/*Table structure for table `kitab_detail` */

DROP TABLE IF EXISTS `kitab_detail`;

CREATE TABLE `kitab_detail` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `id_kitab` int(11) DEFAULT NULL,
  `pasal` int(11) DEFAULT NULL,
  `ayat` int(11) DEFAULT NULL,
  `teks` text,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=10768 DEFAULT CHARSET=latin1;

/*Data for the table `kitab_detail` */

insert  into `kitab_detail`(`id`,`id_kitab`,`pasal`,`ayat`,`teks`) values (1,1,1,1,'Pada mulanya Allah menciptakan langit dan bumi.'),(2,1,1,2,'Bumi belum berbentuk dan kosong; gelap gulita menutupi samudera raya, dan Roh Allah melayang-layang di atas permukaan air.'),(3,1,1,3,'Berfirmanlah Allah: \"Jadilah terang.\" Lalu terang itu jadi.'),(4,1,1,4,'Allah melihat bahwa terang itu baik,\nlalu dipisahkan-Nyalah terang itu dari\ngelap.'),(5,1,1,5,'Dan Allah menamai terang itu siang,\ndan gelap itu malam. Jadilah petang dan\njadilah pagi, itulah hari pertama.'),(6,1,1,6,'Berfirmanlah Allah: \"Jadilah cakrawala\ndi tengah segala air untuk memisahkan\nair dari air.\"'),(7,1,1,7,'Maka Allah menjadikan cakrawala\ndan Ia memisahkan air yang ada di\nbawah cakrawala itu dari air yang ada di\natasnya. Dan jadilah demikian.'),(8,1,1,8,'Lalu Allah menamai cakrawala itu\nlangit. Jadilah petang dan jadilah pagi,\nitulah hari kedua.'),(9,1,1,9,'Berfirmanlah Allah: \"Hendaklah segala\nair yang di bawah langit berkumpul pada\nsatu tempat, sehingga kelihatan yang\nkering.\" Dan jadilah demikian.'),(10,1,1,10,'Lalu Allah menamai yang kering itu\ndarat, dan kumpulan air itu dinamai-Nya\nlaut. Allah melihat bahwa semuanya itu\nbaik.'),(11,1,1,11,'Berfirmanlah Allah: \"Hendaklah\ntanah menumbuhkan tunas-tunas\nmuda, tumbuh-tumbuhan yang berbiji,\nsegala jenis pohon buah-buahan yang\nmenghasilkan buah yang berbiji, supaya\nada tumbuh-tumbuhan di bumi.\" Dan\njadilah demikian.'),(12,1,1,12,'Tanah itu menumbuhkan tunas-tunas\nmuda, segala jenis tumbuh-tumbuhan\nyang berbiji dan segala jenis pohonpohonan yang menghasilkan buah yang\nberbiji. Allah melihat bahwa semuanya\nitu baik.'),(13,1,1,13,'Jadilah petang dan jadilah pagi,\nitulah hari ketiga.'),(14,1,1,14,'Berfirmanlah Allah: \"Jadilah bendabenda penerang pada cakrawala untuk\nmemisahkan siang dari malam. Biarlah\nbenda-benda penerang itu menjadi tanda yang menunjukkan masa-masa yang\ntetap dan hari-hari dan tahun-tahun,'),(15,1,1,15,'dan sebagai penerang pada cakrawala\nbiarlah benda-benda itu menerangi\nbumi.\" Dan jadilah demikian.'),(16,1,1,16,'Maka Allah menjadikan kedua benda\npenerang yang besar itu, yakni yang\nlebih besar untuk menguasai siang dan\nyang lebih kecil untuk menguasai malam,\ndan menjadikan juga bintang-bintang.'),(17,1,1,17,'Allah menaruh semuanya itu di\ncakrawala untuk menerangi bumi,'),(18,1,1,18,'dan untuk menguasai siang dan\nmalam, dan untuk memisahkan terang\ndari gelap. Allah melihat bahwa\nsemuanya itu baik.'),(19,1,1,19,'Jadilah petang dan jadilah pagi,\nitulah hari keempat.'),(20,1,1,20,'Berfirmanlah Allah: \"Hendaklah\ndalam air berkeriapan makhluk\nyang hidup, dan hendaklah burung\nbeterbangan di atas bumi melintasi\ncakrawala.\"'),(21,1,1,21,'Maka Allah menciptakan binatangbinatang laut yang besar dan segala\njenis makhluk hidup yang bergerak, yang\nberkeriapan dalam air, dan segala jenis burung yang bersayap. Allah melihat\nbahwa semuanya itu baik.'),(22,1,1,22,'Lalu Allah memberkati semuanya itu, firman- \ndan bertambah banyaklah serta\npenuhilah air dalam laut, dan hendaklah\nburung-burung di bumi bertambah\nbanyak.\"'),(23,1,1,23,'Jadilah petang dan jadilah pagi,\nitulah hari kelima.'),(24,1,1,24,'Berfirmanlah Allah: \"Hendaklah bumi\nmengeluarkan segala jenis makhluk\nyang hidup, ternak dan binatang melata\ndan segala jenis binatang liar.\" Dan\njadilah demikian.'),(25,1,1,25,'Allah menjadikan segala jenis binatang liar dan segala jenis ternak dan segala jenis binatang melata di muka bumi. Allah melihat bahwa semuanya itu baik.'),(26,1,1,26,'Berfirmanlah Allah: \"Baiklah Kita\nmenjadikan manusia menurut gambar\ndan rupa Kita, supaya mereka berkuasa\natas ikan-ikan di laut dan burung-burung\ndi udara dan atas ternak dan atas\nseluruh bumi dan atas segala binatang\nmelata yang merayap di bumi.\"'),(27,1,1,27,'Maka Allah menciptakan manusia itu\nmenurut gambar-Nya, menurut gambar\nAllah diciptakan-Nya dia; laki-laki dan\nperempuan diciptakan-Nya mereka.'),(28,1,1,28,'Allah memberkati mereka, lalu\nAllah berfirman kepada mereka:\n\"Beranakcuculah dan bertambah banyak;\npenuhilah bumi dan taklukkanlah itu,\nberkuasalah atas ikan-ikan di laut dan\nburung-burung di udara dan atas segala\nbinatang yang merayap di bumi.\"'),(29,1,1,29,'Berfirmanlah Allah: \"Lihatlah, Aku\nmemberikan kepadamu segala tumbuhtumbuhan yang berbiji di seluruh\nbumi dan segala pohon-pohonan yang\nbuahnya berbiji; itulah akan menjadi\nmakananmu.'),(30,1,1,30,'Tetapi kepada segala binatang di\nbumi dan segala burung di udara dan\nsegala yang merayap di bumi, yang\nbernyawa, Kuberikan segala tumbuhtumbuhan hijau menjadi makanannya.\"\nDan jadilah demikian.'),(31,1,1,31,'Maka Allah melihat segala yang\ndijadikan-Nya itu, sungguh amat baik.\nJadilah petang dan jadilah pagi, itulah\nhari keenam.'),(32,1,2,1,'Demikianlah diselesaikan langit dan bumi dan segala isinya.'),(33,1,2,2,'Ketika Allah pada hari ketujuh\ntelah menyelesaikan pekerjaan yang\ndibuat-Nya itu, berhentilah Ia pada hari\nketujuh dari segala pekerjaan yang telah\ndibuat-Nya itu.'),(34,1,2,3,'Lalu Allah memberkati hari ketujuh itu\ndan menguduskannya, karena pada hari\nitulah Ia berhenti dari segala pekerjaan\npenciptaan yang telah dibuat-Nya itu.'),(35,1,2,4,'Demikianlah riwayat langit dan bumi\npada waktu diciptakan. Ketika TUHAN\nAllah menjadikan bumi dan langit, --'),(36,1,2,5,'belum ada semak apapun di bumi,\nbelum timbul tumbuh-tumbuhan apapun\ndi padang, sebab TUHAN Allah belum\nmenurunkan hujan ke bumi, dan belum\nada orang untuk mengusahakan tanah\nitu;'),(37,1,2,6,'ketika itulah TUHAN Allah membentuk\nmanusia itu dari debu tanah dan\nmenghembuskan nafas hidup ke dalam\nhidungnya; demikianlah manusia itu\nmenjadi makhluk yang hidup.'),(38,1,2,7,'Selanjutnya TUHAN Allah membuat\ntaman di Eden, di sebelah timur;\ndisitulah ditempatkan-Nya manusia yang\ndibentuk-Nya itu.'),(39,1,2,8,'Lalu TUHAN Allah menumbuhkan\nberbagai-bagai pohon dari bumi, yang\nmenarik dan yang baik untuk dimakan\nbuahnya; dan pohon kehidupan di\ntengah-tengah taman itu, serta pohon\npengetahuan tentang yang baik dan\nyang jahat.'),(40,1,2,9,'Ada suatu sungai mengalir dari Eden\nuntuk membasahi taman itu, dan dari\nsitu sungai itu terbagi menjadi empat\ncabang.'),(41,1,2,10,'Yang pertama, namanya Pison, yakni\nyang mengalir mengelilingi seluruh\ntanah Hawila, tempat emas ada.'),(42,1,2,11,'Dan emas dari negeri itu baik; di sana\nada damar bedolah dan batu krisopras.'),(43,1,2,12,'Nama sungai yang kedua ialah Gihon,\nyakni yang mengalir mengelilingi seluruh\ntanah Kush.'),(44,1,2,13,'Nama sungai yang ketiga ialah Tigris,\nyakni yang mengalir di sebelah timur\nAsyur. Dan sungai yang keempat ialah\nEfrat.'),(45,1,2,14,'TUHAN Allah mengambil manusia\nitu dan menempatkannya dalam\ntaman Eden untuk mengusahakan dan\nmemelihara taman itu.'),(46,1,2,15,'Lalu TUHAN Allah memberi perintah\nini kepada manusia: \"Semua pohon\ndalam taman ini boleh kaumakan\nbuahnya dengan bebas,'),(47,1,2,16,'tetapi pohon pengetahuan tentang\nyang baik dan yang jahat itu, janganlah\nkaumakan buahnya, sebab pada hari\nengkau memakannya, pastilah engkau\nmati.\"'),(48,1,2,17,'TUHAN Allah berfirman: \"Tidak baik,\nkalau manusia itu seorang diri saja. Aku\nakan menjadikan penolong baginya,\nyang sepadan dengan dia.\"'),(49,1,2,18,'Lalu TUHAN Allah membentuk\ndari tanah segala binatang hutan dan\nsegala burung di udara. Dibawa-Nyalah\nsemuanya kepada manusia itu untuk\nmelihat, bagaimana ia menamainya; dan\nseperti nama yang diberikan manusia itu\nkepada tiap-tiap makhluk yang hidup,\ndemikianlah nanti nama makhluk itu.'),(50,1,2,19,'Manusia itu memberi nama kepada\nsegala ternak, kepada burung-burung\ndi udara dan kepada segala binatang hutan, tetapi baginya sendiri ia tidak\nmenjumpai penolong yang sepadan\ndengan dia.'),(51,1,2,20,'Lalu TUHAN Allah membuat manusia\nitu tidur nyenyak; ketika ia tidur, TUHAN\nAllah mengambil salah satu rusuk dari\npadanya, lalu menutup tempat itu\ndengan daging.'),(52,1,2,21,'Dan dari rusuk yang diambil TUHAN\nAllah dari manusia itu, dibangun-Nyalah\nseorang perempuan, lalu dibawa-Nya\nkepada manusia itu.'),(53,1,2,22,'Lalu berkatalah manusia itu: \"Inilah\ndia, tulang dari tulangku dan daging dari\ndagingku. Ia akan dinamai perempuan,\nsebab ia diambil dari laki-laki.\"'),(54,1,2,23,'Sebab itu seorang laki-laki akan\nmeninggalkan ayahnya dan ibunya dan\nbersatu dengan isterinya, sehingga\nkeduanya menjadi satu daging.'),(55,1,2,24,'Mereka keduanya telanjang, manusia\ndan isterinya itu, tetapi mereka tidak\nmerasa malu.'),(56,1,3,1,'Adapun ular ialah yang paling cerdik dari segala binatang di darat yang dijadikan oleh TUHAN ALLAH. Ular itu berkata kepada perempuan itu: \" tentulah Allah berfirman: Semua pohon dalam taman ini jangan kamu makan buahnya, bukan?\"'),(57,1,3,2,'Lalu sahut perempuan itu kepada ular\nitu: \"Buah pohon-pohonan dalam taman\nini boleh kami makan,'),(58,1,3,3,'tetapi tentang buah pohon yang ada di\ntengah-tengah taman, Allah berfirman:\nJangan kamu makan ataupun raba buah\nitu, nanti kamu mati.\"'),(59,1,3,4,'Tetapi ular itu berkata kepada\nperempuan itu: \"Sekali-kali kamu tidak\nakan mati,'),(60,1,3,5,'tetapi Allah mengetahui, bahwa pada\nwaktu kamu memakannya matamu akan\nterbuka, dan kamu akan menjadi seperti\nAllah, tahu tentang yang baik dan yang\njahat.\"'),(61,1,3,6,'Perempuan itu melihat, bahwa buah\npohon itu baik untuk dimakan dan sedap\nkelihatannya, lagipula pohon itu menarik\nhati karena memberi pengertian.\nLalu ia mengambil dari buahnya dan\ndimakannya dan diberikannya juga\nkepada suaminya yang bersamasama dengan dia, dan suaminyapun\nmemakannya.\ndan\ndimakannya dan diberikannya juga\nkepada suaminya yang bersamasama dengan dia, dan suaminyapun\nmemakannya.'),(62,1,3,7,'Maka terbukalah mata mereka berdua telanjang; lalu mereka menyemat daun\npohon ara dan membuat cawat.\ndan mereka tahu, bahwa mereka'),(63,1,3,8,'Ketika mereka mendengar bunyi\nlangkah TUHAN Allah, yang berjalanjalan dalam taman itu pada waktu hari\nsejuk, bersembunyilah manusia dan\nisterinya itu terhadap TUHAN Allah di\nantara pohon-pohonan dalam taman.'),(64,1,3,9,'Tetapi TUHAN Allah memanggil\nmanusia itu dan berfirman kepadanya:\n\"Di manakah engkau?\"'),(65,1,3,10,'Ia menjawab: \"Ketika aku\nmendengar, bahwa Engkau ada\ndalam taman ini, aku menjadi takut,\nkarena aku telanjang; sebab itu aku\nbersembunyi.\"'),(66,1,3,11,'Firman-Nya: \"Siapakah yang\nmemberitahukan kepadamu, bahwa\nengkau telanjang? Apakah engkau\nmakan dari buah pohon, yang Kularang\nengkau makan itu?\"'),(67,1,3,12,'Manusia itu menjawab: \"Perempuan\nyang Kautempatkan di sisiku, dialah\nyang memberi dari buah pohon itu\nkepadaku, maka kumakan.\"'),(68,1,3,13,'Kemudian berfirmanlah TUHAN Allah\nkepada perempuan itu: \"Apakah yang\ntelah kauperbuat ini?\" Jawab perempuan itu: \"Ular itu yang memperdayakan aku,\nmaka kumakan.\"'),(69,1,3,14,'Lalu berfirmanlah TUHAN Allah\nkepada ular itu: \"Karena engkau berbuat\ndemikian, terkutuklah engkau di antara\nsegala ternak dan di antara segala\nbinatang hutan; dengan perutmulah\nengkau akan menjalar dan debu tanahlah\nakan kaumakan seumur hidupmu.'),(70,1,3,15,'Aku akan mengadakan permusuhan\nantara engkau dan perempuan\nini, antara keturunanmu dan\nketurunannya; keturunannya akan\nmeremukkan kepalamu, dan engkau\nakan meremukkan tumitnya.\"'),(71,1,3,16,'Firman-Nya kepada perempuan itu:\n\"Susah payahmu waktu mengandung\nakan Kubuat sangat banyak; dengan\nkesakitan engkau akan melahirkan\nanakmu; namun engkau akan berahi\nkepada suamimu dan ia akan berkuasa\natasmu.\"'),(72,1,3,17,'Lalu firman-Nya kepada manusia\nitu: \"Karena engkau mendengarkan\nperkataan isterimu dan memakan dari\nbuah pohon, yang telah Kuperintahkan\nkepadamu: Jangan makan dari padanya,\nmaka terkutuklah tanah karena engkau; dengan bersusah payah engkau akan\nmencari rezekimu dari tanah seumur\nhidupmu:'),(73,1,3,18,'semak duri dan rumput duri yang\nakan dihasilkannya bagimu, dan\ntumbuh-tumbuhan di padang akan\nmenjadi makananmu;'),(74,1,3,19,'dengan berpeluh engkau akan\nmencari makananmu, sampai engkau\nkembali lagi menjadi tanah, karena dari\nsitulah engkau diambil; sebab engkau\ndebu dan engkau akan kembali menjadi\ndebu.\"'),(75,1,3,20,'Manusia itu memberi nama Hawa\nkepada isterinya, sebab dialah yang\nmenjadi ibu semua yang hidup.'),(76,1,3,21,'Dan TUHAN Allah membuat pakaian\ndari kulit binatang untuk manusia dan\nuntuk isterinya itu, lalu mengenakannya\nkepada mereka.'),(77,1,3,22,'Berfirmanlah TUHAN Allah:\n\"Sesungguhnya manusia itu telah\nmenjadi seperti salah satu dari Kita,\ntahu tentang yang baik dan yang\njahat; maka sekarang jangan sampai ia\nmengulurkan tangannya dan mengambil\npula dari buah pohon kehidupan itu dan memakannya, sehingga ia hidup untuk\nselama-lamanya.\"'),(78,1,3,23,'Lalu TUHAN Allah mengusir dia dari\ntaman Eden supaya ia mengusahakan\ntanah dari mana ia diambil.'),(79,1,3,24,'Ia menghalau manusia itu dan di\nsebelah timur taman Eden ditempatkanNyalah beberapa kerub dengan pedang\nyang bernyala-nyala dan menyambarnyambar, untuk menjaga jalan ke pohon\nkehidupan.'),(80,1,4,1,'Kemudian manusia itu bersetubuh dengan Hawa, isterinya, dan\nmengandunglah perempuan itu, lalu\nmelahirkan Kain; maka kata perempuan\nitu: \"Aku telah mendapat seorang anak\nlaki-laki dengan pertolongan TUHAN.\"'),(81,1,4,2,'Selanjutnya dilahirkannyalah Habel,\nadik Kain; dan Habel menjadi gembala\nkambing domba, Kain menjadi petani.'),(82,1,4,3,'Setelah beberapa waktu lamanya,\nmaka Kain mempersembahkan sebagian\ndari hasil tanah itu kepada TUHAN\nsebagai korban persembahan;'),(83,1,4,4,'Habel juga mempersembahkan korban\npersembahan dari anak sulung kambing\ndombanya, yakni lemak-lemaknya; maka TUHAN mengindahkan Habel dan\nkorban persembahannya itu,'),(84,1,4,5,'tetapi Kain dan korban\npersembahannya tidak diindahkanNya. Lalu hati Kain menjadi sangat\npanas, dan mukanya muram.'),(85,1,4,6,'Firman TUHAN kepada Kain: \"Mengapa\nhatimu panas dan mukamu muram?'),(86,1,4,7,'Apakah mukamu tidak akan berseri,\njika engkau berbuat baik? Tetapi jika\nengkau tidak berbuat baik, dosa sudah\nmengintip di depan pintu; ia sangat\nmenggoda engkau, tetapi engkau harus\nberkuasa atasnya.\"'),(87,1,4,8,'Kata Kain kepada Habel, adiknya:\n\"Marilah kita pergi ke padang.\" Ketika\nmereka ada di padang, tiba-tiba Kain\nmemukul Habel, adiknya itu, lalu\nmembunuh dia.'),(88,1,4,9,'Firman TUHAN kepada Kain: \"Di mana\nHabel, adikmu itu?\" Jawabnya: \"Aku\ntidak tahu! Apakah aku penjaga adikku?\"'),(89,1,4,10,'Firman-Nya: \"Apakah yang telah\nkauperbuat ini? Darah adikmu itu\nberteriak kepada-Ku dari tanah.'),(90,1,4,11,'Maka sekarang, terkutuklah\nengkau, terbuang jauh dari tanah yang mengangakan mulutnya untuk menerima\ndarah adikmu itu dari tanganmu.'),(91,1,4,12,'Apabila engkau mengusahakan\ntanah itu, maka tanah itu tidak akan\nmemberikan hasil sepenuhnya lagi\nkepadamu; engkau menjadi seorang\npelarian dan pengembara di bumi.\"'),(92,1,4,13,'Kata Kain kepada TUHAN:\n\"Hukumanku itu lebih besar dari\npada yang dapat kutanggung.'),(93,1,4,14,'Engkau menghalau aku sekarang\ndari tanah ini dan aku akan tersembunyi\ndari hadapan-Mu, seorang pelarian dan\npengembara di bumi; maka barangsiapa\nyang akan bertemu dengan aku, tentulah\nakan membunuh aku.\"'),(94,1,4,15,'Firman TUHAN kepadanya: \"Sekalikali tidak! Barangsiapa yang membunuh\nKain akan dibalaskan kepadanya tujuh\nkali lipat.\" Kemudian TUHAN menaruh\ntanda pada Kain, supaya ia jangan\ndibunuh oleh barangsiapapun yang\nbertemu dengan dia.'),(95,1,4,16,'Lalu Kain pergi dari hadapan TUHAN\ndan ia menetap di tanah Nod, di sebelah\ntimur Eden.'),(96,1,4,17,'Kain bersetubuh dengan isterinya\ndan mengandunglah perempuan itu, lalu melahirkan Henokh; kemudian Kain\nmendirikan suatu kota dan dinamainya\nkota itu Henokh, menurut nama\nanaknya.'),(97,1,4,18,'Bagi Henokh lahirlah Irad, dan Irad\nitu memperanakkan Mehuyael dan\nMehuyael memperanakkan Metusael,\ndan Metusael memperanakkan Lamekh.'),(98,1,4,19,'Lamekh mengambil isteri dua orang;\nyang satu namanya Ada, yang lain Zila.'),(99,1,4,20,'Ada itu melahirkan Yabal; dialah yang\nmenjadi bapa orang yang diam dalam\nkemah dan memelihara ternak.'),(100,1,4,21,'Nama adiknya ialah Yubal; dialah\nyang menjadi bapa semua orang yang\nmemainkan kecapi dan suling.'),(101,1,4,22,'Zila juga melahirkan anak, yakni\nTubal-Kain, bapa semua tukang tembaga\ndan tukang besi. Adik perempuan\nTubal-Kain ialah Naama.'),(102,1,4,23,'Berkatalah Lamekh kepada\nkedua isterinya itu: \"Ada dan Zila,\ndengarkanlah suaraku: hai isteri-isteri\nLamekh, pasanglah telingamu kepada\nperkataanku ini: Aku telah membunuh\nseorang laki-laki karena ia melukai aku,\nmembunuh seorang muda karena ia\nmemukul aku sampai bengkak;'),(103,1,4,24,'sebab jika Kain harus dibalaskan\ntujuh kali lipat, maka Lamekh tujuh\npuluh tujuh kali lipat.\"'),(104,1,4,25,'Adam bersetubuh pula dengan\nisterinya, lalu perempuan itu melahirkan\nseorang anak laki-laki dan menamainya\nSet, sebab katanya: \"Allah telah\nmengaruniakan kepadaku anak yang lain\nsebagai ganti Habel; sebab Kain telah\nmembunuhnya.\"'),(105,1,4,26,'Lahirlah seorang anak laki-laki bagi\nSet juga dan anak itu dinamainya Enos.\nWaktu itulah orang mulai memanggil\nnama TUHAN.'),(106,1,5,1,'Inilah daftar keturunan Adam. Pada waktu manusia itu diciptakan oleh\nAllah, dibuat-Nyalah dia menurut rupa\nAllah;'),(107,1,5,2,'laki-laki dan perempuan diciptakanNya mereka. Ia memberkati mereka dan\nmemberikan nama \"Manusia\" kepada\nmereka, pada waktu mereka diciptakan.'),(108,1,5,3,'Setelah Adam hidup seratus tiga\npuluh tahun, ia memperanakkan seorang\nlaki-laki menurut rupa dan gambarnya,\nlalu memberi nama Set kepadanya.'),(109,1,5,4,'Umur Adam, setelah memperanakkan\nSet, delapan ratus tahun, dan ia\nmemperanakkan anak-anak lelaki dan\nperempuan.'),(110,1,5,5,'Jadi Adam mencapai umur sembilan\nratus tiga puluh tahun, lalu ia mati.'),(111,1,5,6,'Setelah Set hidup seratus lima tahun,\nia memperanakkan Enos.'),(112,1,5,7,'Dan Set masih hidup delapan ratus\ntujuh tahun, setelah ia memperanakkan\nEnos, dan ia memperanakkan anak-anak\nlelaki dan perempuan.'),(113,1,5,8,'Jadi Set mencapai umur sembilan\nratus dua belas tahun, lalu ia mati.'),(114,1,5,9,'Setelah Enos hidup sembilan puluh\ntahun, ia memperanakkan Kenan.'),(115,1,5,10,'Dan Enos masih hidup delapan\nratus lima belas tahun, setelah\nia memperanakkan Kenan, dan ia\nmemperanakkan anak-anak lelaki dan\nperempuan.'),(116,1,5,11,'Jadi Enos mencapai umur sembilan\nratus lima tahun, lalu ia mati.'),(117,1,5,12,'Setelah Kenan hidup tujuh puluh\ntahun, ia memperanakkan Mahalaleel.'),(118,1,5,13,'Dan Kenan masih hidup delapan\nratus empat puluh tahun, setelah ia\nmemperanakkan Mahalaleel, dan ia\nmemperanakkan anak-anak lelaki dan\nperempuan.'),(119,1,5,14,'Jadi Kenan mencapai umur sembilan\nratus sepuluh tahun, lalu ia mati.'),(120,1,5,15,'Setelah Mahalaleel hidup enam puluh\nlima tahun, ia memperanakkan Yared.'),(121,1,5,16,'Dan Mahalaleel masih hidup\ndelapan ratus tiga puluh tahun, setelah\nia memperanakkan Yared, dan ia\nmemperanakkan anak-anak lelaki dan\nperempuan.'),(122,1,5,17,'adi Mahalaleel mencapai umur\ndelapan ratus sembilan puluh lima\ntahun, lalu ia mati.'),(123,1,5,18,'Setelah Yared hidup seratus enam\npuluh dua tahun, ia memperanakkan\nHenokh.'),(124,1,5,19,'Dan Yared masih hidup delapan\nratus tahun, setelah ia memperanakkan\nHenokh, dan ia memperanakkan\nanak-anak lelaki dan perempuan.'),(125,1,5,20,'Jadi Yared mencapai umur sembilan\nratus enam puluh dua tahun, lalu ia\nmati.'),(126,1,5,21,'Setelah Henokh hidup enam\npuluh lima tahun, ia memperanakkan\nMetusalah.'),(127,1,5,22,'Dan Henokh hidup bergaul dengan\nAllah selama tiga ratus tahun lagi,\nsetelah ia memperanakkan Metusalah, dan ia memperanakkan anak-anak lelaki\ndan perempuan.'),(128,1,5,23,'Jadi Henokh mencapai umur tiga\nratus enam puluh lima tahun.'),(129,1,5,24,'Dan Henokh hidup bergaul dengan\nAllah, lalu ia tidak ada lagi, sebab ia\ntelah diangkat oleh Allah.'),(130,1,5,25,'Setelah Metusalah hidup seratus\ndelapan puluh tujuh tahun, ia\nmemperanakkan Lamekh.'),(131,1,5,26,'Dan Metusalah masih hidup tujuh\nratus delapan puluh dua tahun, setelah\nia memperanakkan Lamekh, dan ia\nmemperanakkan anak-anak lelaki dan\nperempuan.'),(132,1,5,27,'Jadi Metusalah mencapai umur\nsembilan ratus enam puluh sembilan\ntahun, lalu ia mati.'),(133,1,5,28,'Setelah Lamekh hidup seratus\ndelapan puluh dua tahun, ia\nmemperanakkan seorang anak\nlaki-laki,'),(134,1,5,29,'dan memberi nama Nuh kepadanya,\nkatanya: \"Anak ini akan memberi kepada\nkita penghiburan dalam pekerjaan kita\nyang penuh susah payah di tanah yang\ntelah terkutuk oleh TUHAN.\"'),(135,1,5,30,'Dan Lamekh masih hidup lima\nratus sembilan puluh lima tahun,\nsetelah ia memperanakkan Nuh, dan ia\nmemperanakkan anak-anak lelaki dan\nperempuan.'),(136,1,5,31,'Jadi Lamekh mencapai umur tujuh\nratus tujuh puluh tujuh tahun, lalu ia\nmati.'),(137,1,5,32,'Setelah Nuh berumur lima ratus\ntahun, ia memperanakkan Sem, Ham\ndan Yafet.'),(138,1,6,1,'Ketika manusia itu mulai bertambah banyak jumlahnya di muka bumi, dan \nbagi mereka lahir anak-anak perempuan,'),(139,1,6,2,'maka anak-anak Allah melihat, bahwa\nanak-anak perempuan manusia itu\ncantik-cantik, lalu mereka mengambil\nisteri dari antara perempuan-perempuan\nitu, siapa saja yang disukai mereka.'),(140,1,6,3,'Berfirmanlah TUHAN: \"Roh-Ku tidak\nakan selama-lamanya tinggal di dalam\nmanusia, karena manusia itu adalah\ndaging, tetapi umurnya akan seratus\ndua puluh tahun saja.\"'),(141,1,6,4,'Pada waktu itu orang-orang raksasa\nada di bumi, dan juga pada waktu\nsesudahnya, ketika anak-anak Allah\nmenghampiri anak-anak perempuan manusia, dan perempuan-perempuan\nitu melahirkan anak bagi mereka; inilah\norang-orang yang gagah perkasa di\nzaman purbakala, orang-orang yang\nkenamaan.'),(142,1,6,5,'Ketika dilihat TUHAN, bahwa\nkejahatan manusia besar di bumi\ndan bahwa segala kecenderungan\nhatinya selalu membuahkan kejahatan\nsemata-mata,'),(143,1,6,6,'maka menyesallah TUHAN, bahwa Ia\ntelah menjadikan manusia di bumi, dan\nhal itu memilukan hati-Nya.'),(144,1,6,7,'Berfirmanlah TUHAN: \"Aku akan\nmenghapuskan manusia yang telah\nKuciptakan itu dari muka bumi, baik\nmanusia maupun hewan dan binatangbinatang melata dan burung-burung di\nudara, sebab Aku menyesal, bahwa Aku\ntelah menjadikan mereka.\"'),(145,1,6,8,'Tetapi Nuh mendapat kasih karunia di\nmata TUHAN.'),(146,1,6,9,'Inilah riwayat Nuh: Nuh adalah\nseorang yang benar dan tidak bercela di\nantara orang-orang sezamannya; dan\nNuh itu hidup bergaul dengan Allah.'),(147,1,6,10,'Nuh memperanakkan tiga orang\nlaki-laki: Sem, Ham dan Yafet.'),(148,1,6,11,'Adapun bumi itu telah rusak di\nhadapan Allah dan penuh dengan\nkekerasan.'),(149,1,6,12,'Allah menilik bumi itu dan sungguhlah\nrusak benar, sebab semua manusia\nmenjalankan hidup yang rusak di bumi.'),(150,1,6,13,'Berfirmanlah Allah kepada Nuh: \"Aku\ntelah memutuskan untuk mengakhiri\nhidup segala makhluk, sebab bumi telah\npenuh dengan kekerasan oleh mereka,\njadi Aku akan memusnahkan mereka\nbersama-sama dengan bumi.'),(151,1,6,14,'Buatlah bagimu sebuah bahtera dari\nkayu gofir; bahtera itu harus kaubuat\nberpetak-petak dan harus kaututup\ndengan pakal dari luar dan dari dalam.'),(152,1,6,15,'Beginilah engkau harus membuat\nbahtera itu: tiga ratus hasta panjangnya,\nlima puluh hasta lebarnya dan tiga puluh\nhasta tingginya.'),(153,1,6,16,'Buatlah atap pada bahtera itu dan\nselesaikanlah bahtera itu sampai sehasta\ndari atas, dan pasanglah pintunya\npada lambungnya; buatlah bahtera itu\nbertingkat bawah, tengah dan atas.'),(154,1,6,17,'Sebab sesungguhnya Aku akan\nmendatangkan air bah meliputi bumi\nuntuk memusnahkan segala yang hidup dan bernyawa di kolong langit; segala\nyang ada di bumi akan mati binasa.'),(155,1,6,18,'Tetapi dengan engkau Aku akan\nmengadakan perjanjian-Ku, dan\nengkau akan masuk ke dalam bahtera\nitu: engkau bersama-sama dengan\nanak-anakmu dan isterimu dan isteri\nanak-anakmu.'),(156,1,6,19,'Dan dari segala yang hidup, dari\nsegala makhluk, dari semuanya haruslah\nengkau bawa satu pasang ke dalam\nbahtera itu, supaya terpelihara hidupnya\nbersama-sama dengan engkau; jantan\ndan betina harus kaubawa.'),(157,1,6,20,'Dari segala jenis burung dan dari\nsegala jenis hewan, dari segala jenis\nbinatang melata di muka bumi, dari\nsemuanya itu harus datang satu pasang\nkepadamu, supaya terpelihara hidupnya.'),(158,1,6,21,'Dan engkau, bawalah bagimu segala\napa yang dapat dimakan; kumpulkanlah\nitu padamu untuk menjadi makanan\nbagimu dan bagi mereka.\"'),(159,1,6,22,'Lalu Nuh melakukan semuanya itu;\ntepat seperti yang diperintahkan Allah\nkepadanya, demikianlah dilakukannya.'),(160,1,7,1,'Lalu berfirmanlah TUHAN kepada Nuh: \"Masuklah ke dalam bahtera itu, engkau dan seisi rumahmu, sebab\nengkaulah yang Kulihat benar di\nhadapan-Ku di antara orang zaman ini.'),(161,1,7,2,'Dari segala binatang yang tidak haram\nharuslah kauambil tujuh pasang, jantan\ndan betinanya, tetapi dari binatang\nyang haram satu pasang, jantan dan\nbetinanya;'),(162,1,7,3,'juga dari burung-burung di udara\ntujuh pasang, jantan dan betina, supaya\nterpelihara hidup keturunannya di\nseluruh bumi.'),(163,1,7,4,'Sebab tujuh hari lagi Aku akan\nmenurunkan hujan ke atas bumi empat\npuluh hari empat puluh malam lamanya,\ndan Aku akan menghapuskan dari muka\nbumi segala yang ada, yang Kujadikan\nitu.\"'),(164,1,7,5,'Lalu Nuh melakukan segala yang\ndiperintahkan TUHAN kepadanya.'),(165,1,7,6,'Nuh berumur enam ratus tahun,\nketika air bah datang meliputi bumi.'),(166,1,7,7,'Masuklah Nuh ke dalam bahtera itu\nbersama-sama dengan anak-anaknya\ndan isterinya dan isteri anak-anaknya\nkarena air bah itu.'),(167,1,7,8,'Dari binatang yang tidak haram dan\nyang haram, dari burung-burung dan\ndari segala yang merayap di muka bumi,'),(168,1,7,9,'datanglah sepasang mendapatkan\nNuh ke dalam bahtera itu, jantan dan\nbetina, seperti yang diperintahkan Allah\nkepada Nuh.'),(169,1,7,10,'Setelah tujuh hari datanglah air bah\nmeliputi bumi.'),(170,1,7,11,'Pada waktu umur Nuh enam ratus\ntahun, pada bulan yang kedua, pada hari\nyang ketujuh belas bulan itu, pada hari\nitulah terbelah segala mata air samudera\nraya yang dahsyat dan terbukalah\ntingkap-tingkap di langit.'),(171,1,7,12,'Dan turunlah hujan lebat meliputi\nbumi empat puluh hari empat puluh\nmalam lamanya.'),(172,1,7,13,'Pada hari itu juga masuklah Nuh\nserta Sem, Ham dan Yafet, anak-anak\nNuh, dan isteri Nuh, dan ketiga isteri\nanak-anaknya bersama-sama dengan\ndia, ke dalam bahtera itu,'),(173,1,7,14,'mereka itu dan segala jenis binatang\nliar dan segala jenis ternak dan segala\njenis binatang melata yang merayap di\nbumi dan segala jenis burung, yakni\nsegala yang berbulu bersayap;'),(174,1,7,15,'dari segala yang hidup dan bernyawa\ndatanglah sepasang mendapatkan Nuh\nke dalam bahtera itu.'),(175,1,7,16,'Dan yang masuk itu adalah jantan\ndan betina dari segala yang hidup,\nseperti yang diperintahkan Allah kepada\nNuh; lalu TUHAN menutup pintu bahtera\nitu di belakang Nuh.'),(176,1,7,17,'Empat puluh hari lamanya air bah\nitu meliputi bumi; air itu naik dan\nmengangkat bahtera itu, sehingga\nmelampung tinggi dari bumi.'),(177,1,7,18,'Ketika air itu makin bertambahtambah dan naik dengan hebatnya di\natas bumi, terapung-apunglah bahtera\nitu di muka air.'),(178,1,7,19,'Dan air itu sangat hebatnya\nbertambah-tambah meliputi bumi, dan\nditutupinyalah segala gunung tinggi di\nseluruh kolong langit,'),(179,1,7,20,'sampai lima belas hasta di atasnya\nbertambah-tambah air itu, sehingga\ngunung-gunung ditutupinya.'),(180,1,7,21,'Lalu mati binasalah segala yang\nhidup, yang bergerak di bumi, burungburung, ternak dan binatang liar\ndan segala binatang merayap, yang berkeriapan di bumi, serta semua\nmanusia.'),(181,1,7,22,'Matilah segala yang ada nafas hidup\ndalam hidungnya, segala yang ada di\ndarat.'),(182,1,7,23,'Demikianlah dihapuskan Allah segala\nyang ada, segala yang di muka bumi,\nbaik manusia maupun hewan dan\nbinatang melata dan burung-burung\ndi udara, sehingga semuanya itu\ndihapuskan dari atas bumi; hanya\nNuh yang tinggal hidup dan semua\nyang bersama-sama dengan dia dalam\nbahtera itu.'),(183,1,7,24,'Dan berkuasalah air itu di atas bumi\nseratus lima puluh hari lamanya.'),(184,1,8,1,'Maka Allah mengingat Nuh dan segala binatang ternak, yang bersama-sama dengan dia\ndalam bahtera itu, dan Allah membuat\nangin menghembus melalui bumi,\nsehingga air itu turun.liar dan segala'),(185,1,8,2,'Ditutuplah mata-mata air samudera\nraya serta tingkap-tingkap di langit dan\nberhentilah hujan lebat dari langit,'),(186,1,8,3,'dan makin surutlah air itu dari muka\nbumi. Demikianlah berkurang air itu\nsesudah seratus lima puluh hari.'),(187,1,8,4,'Dalam bulan yang ketujuh, pada\nhari yang ketujuh belas bulan itu,\nterkandaslah bahtera itu pada\npegunungan Ararat.'),(188,1,8,5,'Sampai bulan yang kesepuluh makin\nberkuranglah air itu; dalam bulan yang\nkesepuluh, pada tanggal satu bulan itu,\ntampaklah puncak-puncak gunung.'),(189,1,8,6,'Sesudah lewat empat puluh hari,\nmaka Nuh membuka tingkap yang\ndibuatnya pada bahtera itu.'),(190,1,8,7,'Lalu ia melepaskan seekor burung\ngagak; dan burung itu terbang pulang\npergi, sampai air itu menjadi kering dari\natas bumi.'),(191,1,8,8,'Kemudian dilepaskannya seekor\nburung merpati untuk melihat, apakah\nair itu telah berkurang dari muka bumi.'),(192,1,8,9,'Tetapi burung merpati itu tidak\nmendapat tempat tumpuan kakinya\ndan pulanglah ia kembali mendapatkan\nNuh ke dalam bahtera itu, karena di\nseluruh bumi masih ada air; lalu Nuh\nmengulurkan tangannya, ditangkapnya\nburung itu dan dibawanya masuk ke\ndalam bahtera.'),(193,1,8,10,'Ia menunggu tujuh hari lagi,\nkemudian dilepaskannya pula burung\nmerpati itu dari bahtera;'),(194,1,8,11,'menjelang waktu senja pulanglah\nburung merpati itu mendapatkan Nuh,\ndan pada paruhnya dibawanya sehelai\ndaun zaitun yang segar. Dari situlah\ndiketahui Nuh, bahwa air itu telah\nberkurang dari atas bumi.'),(195,1,8,12,'Selanjutnya ditunggunya pula tujuh\nhari lagi, kemudian dilepaskannya\nburung merpati itu, tetapi burung itu\ntidak kembali lagi kepadanya.'),(196,1,8,13,'Dalam tahun keenam ratus satu,\ndalam bulan pertama, pada tanggal satu\nbulan itu, sudahlah kering air itu dari\natas bumi; kemudian Nuh membuka\ntutup bahtera itu dan melihat-lihat;\nternyatalah muka bumi sudah mulai\nkering.'),(197,1,8,14,'Dalam bulan kedua, pada hari yang\nkedua puluh tujuh bulan itu, bumi telah\nkering.'),(198,1,8,15,'Lalu berfirmanlah Allah kepada Nuh:'),(199,1,8,16,'\"Keluarlah dari bahtera itu, engkau\nbersama-sama dengan isterimu serta\nanak-anakmu dan isteri anak-anakmu;'),(200,1,8,17,'segala binatang yang bersama-sama\ndengan engkau, segala yang hidup:\nburung-burung, hewan dan segala\nbinatang melata yang merayap di\nbumi, suruhlah keluar bersama-sama\ndengan engkau, supaya semuanya itu\nberkeriapan di bumi serta berkembang\nbiak dan bertambah banyak di bumi.\"'),(201,1,8,18,'Lalu keluarlah Nuh bersama-sama\ndengan anak-anaknya dan isterinya dan\nisteri anak-anaknya.'),(202,1,8,19,'Segala binatang liar, segala binatang\nmelata dan segala burung, semuanya\nyang bergerak di bumi, masing-masing\nmenurut jenisnya, keluarlah juga dari\nbahtera itu.'),(203,1,8,20,'Lalu Nuh mendirikan mezbah bagi\nTUHAN; dari segala binatang yang tidak\nharam dan dari segala burung yang tidak\nharam diambilnyalah beberapa ekor, lalu\nia mempersembahkan korban bakaran di\natas mezbah itu.'),(204,1,8,21,'Ketika TUHAN mencium persembahan\nyang harum itu, berfirmanlah TUHAN\ndalam hati-Nya: \"Aku takkan mengutuk\nbumi ini lagi karena manusia, sekalipun\nyang ditimbulkan hatinya adalah jahat\ndari sejak kecilnya, dan Aku takkan membinasakan lagi segala yang hidup\nseperti yang telah Kulakukan.'),(205,1,8,22,'Selama bumi masih ada, takkan\nberhenti-henti musim menabur dan\nmenuai, dingin dan panas, kemarau dan\nhujan, siang dan malam.\"'),(206,1,9,1,'Lalu Allah memberkati Nuh dan\nanak-anaknya serta berfirman\nkepada mereka: \"Beranakcuculah dan\nbertambah banyaklah serta penuhilah\nBumi.'),(207,1,9,2,'Akan takut dan akan gentar kepadamu\nsegala binatang di bumi dan segala\nburung di udara, segala yang bergerak\ndi muka bumi dan segala ikan di laut;\nke dalam tanganmulah semuanya itu\nDiserahkan.'),(208,1,9,3,'Segala yang bergerak, yang hidup,\nakan menjadi makananmu. Aku telah\nmemberikan semuanya itu kepadamu\nseperti juga tumbuh-tumbuhan hijau.'),(209,1,9,4,'Hanya daging yang masih ada\nnyawanya, yakni darahnya, janganlah\nkamu makan.'),(210,1,9,5,'Tetapi mengenai darah kamu, yakni\nnyawa kamu, Aku akan menuntut\nbalasnya; dari segala binatang Aku akan\nmenuntutnya, dan dari setiap manusia Aku akan menuntut nyawa sesama\nManusia.'),(211,1,9,6,'Siapa yang menumpahkan darah\nmanusia, darahnya akan tertumpah oleh\nmanusia, sebab Allah membuat manusia\nitu menurut gambar-Nya sendiri.'),(212,1,9,7,'Dan kamu, beranakcuculah dan\nbertambah banyak, sehingga tak\nterbilang jumlahmu di atas bumi, ya,\nbertambah banyaklah di atasnya.\"'),(213,1,9,8,'Berfirmanlah Allah kepada Nuh\ndan kepada anak-anaknya yang\nbersama-sama dengan dia:'),(214,1,9,9,'\"Sesungguhnya Aku mengadakan\nperjanjian-Ku dengan kamu dan dengan\nKeturunanmu,'),(215,1,9,10,'dan dengan segala makhluk hidup\nyang bersama-sama dengan kamu:\nburung-burung, ternak dan binatangbinatang\nliar di bumi yang bersama-sama\ndengan kamu, segala yang keluar dari\nbahtera itu, segala binatang di bumi.'),(216,1,9,11,'Maka Kuadakan perjanjian-Ku\ndengan kamu, bahwa sejak ini tidak ada\nyang hidup yang akan dilenyapkan oleh\nair bah lagi, dan tidak akan ada lagi air\nbah untuk memusnahkan bumi.\"'),(217,1,9,12,'Dan Allah berfirman: \"Inilah tanda\nperjanjian yang Kuadakan antara\nAku dan kamu serta segala makhluk\nyang hidup, yang bersama-sama\ndengan kamu, turun-temurun, untuk\nSelama-lamanya:'),(218,1,9,13,'Busur-Ku Kutaruh di awan, supaya\nitu menjadi tanda perjanjian antara Aku\ndan bumi.'),(219,1,9,14,'Apabila kemudian Kudatangkan awan\ndi atas bumi dan busur itu tampak di\nAwan,'),(220,1,9,15,'maka Aku akan mengingat\nperjanjian-Ku yang telah ada antara Aku\ndan kamu serta segala makhluk yang\nhidup, segala yang bernyawa, sehingga\nsegenap air tidak lagi menjadi air bah\nuntuk memusnahkan segala yang hidup.'),(221,1,9,16,'Jika busur itu ada di awan, maka\nAku akan melihatnya, sehingga Aku\nmengingat perjanjian-Ku yang kekal\nantara Allah dan segala makhluk yang\nhidup, segala makhluk yang ada di\nbumi.\"'),(222,1,9,17,'Berfirmanlah Allah kepada Nuh:\n\"Inilah tanda perjanjian yang Kuadakan\nantara Aku dan segala makhluk yang\nada di bumi.\"'),(223,1,9,18,'Anak-anak Nuh yang keluar dari\nbahtera ialah Sem, Ham dan Yafet; Ham\nadalah bapa Kanaan.'),(224,1,9,19,'Yang tiga inilah anak-anak Nuh, dan\ndari mereka inilah tersebar penduduk\nseluruh bumi.'),(225,1,9,20,'Nuh menjadi petani; dialah yang\nmula-mula membuat kebun anggur.'),(226,1,9,21,'Setelah ia minum anggur, mabuklah\nia dan ia telanjang dalam kemahnya.'),(227,1,9,22,'Maka Ham, bapa Kanaan itu, melihat\naurat ayahnya, lalu diceritakannya\nkepada kedua saudaranya di luar.'),(228,1,9,23,'Sesudah itu Sem dan Yafet\nmengambil sehelai kain dan\nmembentangkannya pada bahu\nmereka berdua, lalu mereka berjalan\nmundur; mereka menutupi aurat\nayahnya sambil berpaling muka,\nsehingga mereka tidak melihat aurat\nayahnya.'),(229,1,9,24,'Setelah Nuh sadar dari mabuknya\ndan mendengar apa yang dilakukan anak\nbungsunya kepadanya,'),(230,1,9,25,'berkatalah ia: \"Terkutuklah Kanaan,\nhendaklah ia menjadi hamba yang paling\nhina bagi saudara-saudaranya.\"'),(231,1,9,26,'Lagi katanya: \"Terpujilah TUHAN,\nAllah Sem, tetapi hendaklah Kanaan\nmenjadi hamba baginya.'),(232,1,9,27,'Allah meluaskan kiranya tempat\nkediaman Yafet, dan hendaklah ia\ntinggal dalam kemah-kemah Sem, tetapi\nhendaklah Kanaan menjadi hamba\nBaginya.\"'),(233,1,9,28,'Nuh masih hidup tiga ratus lima\npuluh tahun sesudah air bah.'),(234,1,9,29,'Jadi Nuh mencapai umur sembilan\nratus lima puluh tahun, lalu ia mati.'),(235,1,10,1,'Inilah keturunan Sem, Ham dan\nYafet, anak-anak Nuh. Setelah air\nbah itu lahirlah anak-anak lelaki bagi\nMereka.'),(236,1,10,2,'Keturunan Yafet ialah Gomer, Magog,\nMadai, Yawan, Tubal, Mesekh dan Tiras.'),(237,1,10,3,'Keturunan Gomer ialah Askenas, Rifat\ndan Togarma.'),(238,1,10,4,'Keturunan Yawan ialah Elisa, Tarsis,\norang Kitim dan orang Dodanim.'),(239,1,10,5,'Dari mereka inilah berpencar bangsabangsa\ndaerah pesisir. Itulah keturunan\nYafet, masing-masing di tanahnya,\ndengan bahasanya sendiri, menurut\nkaum dan bangsa mereka.'),(240,1,10,6,'Keturunan Ham ialah Kush, Misraim,\nPut dan Kanaan.'),(241,1,10,7,'Keturunan Kush ialah Seba, Hawila,\nSabta, Raema dan Sabtekha; anak-anak\nRaema ialah Syeba dan Dedan.'),(242,1,10,8,'Kush memperanakkan Nimrod; dialah\nyang mula-mula sekali orang yang\nberkuasa di bumi;'),(243,1,10,9,'ia seorang pemburu yang gagah\nperkasa di hadapan TUHAN, sebab\nitu dikatakan orang: \"Seperti Nimrod,\nseorang pemburu yang gagah perkasa di\nhadapan TUHAN.\"'),(244,1,10,10,'Mula-mula kerajaannya terdiri dari\nBabel, Erekh, dan Akad, semuanya di\ntanah Sinear.'),(245,1,10,11,'Dari negeri itu ia pergi ke Asyur, lalu\nmendirikan Niniwe, Rehobot-Ir, Kalah'),(246,1,10,12,'dan Resen di antara Niniwe dan\nKalah; itulah kota besar itu.'),(247,1,10,13,'Misraim memperanakkan orang\nLudim, orang Anamim, orang Lehabim,\norang Naftuhim,'),(248,1,10,14,'orang Patrusim, orang Kasluhim\ndan orang Kaftorim; dari mereka inilah\nberasal orang Filistin.'),(249,1,10,15,'Kanaan memperanakkan Sidon, anak\nsulungnya, dan Het,'),(250,1,10,16,'serta orang Yebusi, orang Amori dan\norang Girgasi;'),(251,1,10,17,'orang Hewi, orang Arki, orang Sini,'),(252,1,10,18,'orang Arwadi, orang Semari dan\norang Hamati; kemudian berseraklah\nkaum-kaum orang Kanaan itu.'),(253,1,10,19,'Daerah orang Kanaan adalah dari\nSidon ke arah Gerar sampai ke Gaza, ke\narah Sodom, Gomora, Adma dan Zeboim\nsampai ke Lasa.'),(254,1,10,20,'Itulah keturunan Ham menurut\nkaum mereka, menurut bahasa mereka,\nmenurut tanah mereka, menurut bangsa\nMereka.'),(255,1,10,21,'Lahirlah juga anak-anak bagi Sem,\nbapa semua anak Eber serta abang\nYafet.'),(256,1,10,22,'Keturunan Sem ialah Elam, Asyur,\nArpakhsad, Lud dan Aram.'),(257,1,10,23,'Keturunan Aram ialah Us, Hul, Geter\ndan Mas.'),(258,1,10,24,'Arpakhsad memperanakkan Selah,\ndan Selah memperanakkan Eber.'),(259,1,10,25,'Bagi Eber lahir dua anak laki-laki;\nnama yang seorang ialah Peleg, sebab\ndalam zamannya bumi terbagi, dan\nnama adiknya ialah Yoktan.'),(260,1,10,26,'Yoktan memperanakkan Almodad,\nSelef, Hazar-Mawet dan Yerah,'),(261,1,10,27,'Hadoram, Uzal dan Dikla,'),(262,1,10,28,'Obal, Abimael dan Syeba,'),(263,1,10,29,'Ofir, Hawila dan Yobab; itulah\nsemuanya keturunan Yoktan.'),(264,1,10,30,'Daerah kediaman mereka terbentang\ndari Mesa ke arah Sefar, yaitu\npegunungan di sebelah timur.'),(265,1,10,31,'Itulah keturunan Sem, menurut\nkaum mereka, menurut bahasa mereka,\nmenurut tanah mereka, menurut bangsa\nMereka.'),(266,1,10,32,'Itulah segala kaum anak-anak Nuh\nmenurut keturunan mereka, menurut\nbangsa mereka. Dan dari mereka itulah\nberpencar bangsa-bangsa di bumi\nsetelah air bah itu.'),(267,1,11,1,'Adapun seluruh bumi, satu\nbahasanya dan satu logatnya.'),(268,1,11,2,'Maka berangkatlah mereka ke sebelah\ntimur dan menjumpai tanah datar di\ntanah Sinear, lalu menetaplah mereka di\nSana.'),(269,1,11,3,'Mereka berkata seorang kepada yang\nlain: \"Marilah kita membuat batu bata\ndan membakarnya baik-baik.\" Lalu bata itulah dipakai mereka sebagai batu dan\nter gala-gala sebagai tanah liat.'),(270,1,11,4,'Juga kata mereka: \"Marilah kita\ndirikan bagi kita sebuah kota dengan\nsebuah menara yang puncaknya sampai\nke langit, dan marilah kita cari nama,\nsupaya kita jangan terserak ke seluruh\nbumi.\"'),(271,1,11,5,'Lalu turunlah TUHAN untuk melihat\nkota dan menara yang didirikan oleh\nanak-anak manusia itu,'),(272,1,11,6,'dan Ia berfirman: \"Mereka ini satu\nbangsa dengan satu bahasa untuk\nsemuanya. Ini barulah permulaan usaha\nmereka; mulai dari sekarang apapun\njuga yang mereka rencanakan, tidak ada\nyang tidak akan dapat terlaksana.'),(273,1,11,7,'Baiklah Kita turun dan\nmengacaubalaukan di sana bahasa\nmereka, sehingga mereka tidak\nmengerti lagi bahasa masing-masing.\"'),(274,1,11,8,'Demikianlah mereka diserakkan\nTUHAN dari situ ke seluruh bumi, dan\nmereka berhenti mendirikan kota itu.'),(275,1,11,9,'Itulah sebabnya sampai sekarang\nnama kota itu disebut Babel, karena di\nsitulah dikacaubalaukan TUHAN bahasa seluruh bumi dan dari situlah mereka\ndiserakkan TUHAN ke seluruh bumi.'),(276,1,11,10,'Inilah keturunan Sem. Setelah\nSem berumur seratus tahun, ia\nmemperanakkan Arpakhsad, dua tahun\nsetelah air bah itu.'),(277,1,11,11,'Sem masih hidup lima ratus tahun,\nsetelah ia memperanakkan Arpakhsad,\ndan ia memperanakkan anak-anak lelaki\ndan perempuan.'),(278,1,11,12,'Setelah Arpakhsad hidup tiga puluh\nlima tahun, ia memperanakkan Selah.'),(279,1,11,13,'Arpakhsad masih hidup empat ratus tiga tahun, setelah ia memperanakkan Selah, dan ia memperanakkan anak-anak lelaki dan perempuan.'),(280,1,11,14,'Setelah Selah hidup tiga puluh tahun,\nia memperanakkan Eber.'),(281,1,11,15,'Selah masih hidup empat ratus tiga\ntahun, setelah ia memperanakkan Eber,\ndan ia memperanakkan anak-anak lelaki\ndan perempuan.'),(282,1,11,16,'Setelah Eber hidup tiga puluh empat\ntahun, ia memperanakkan Peleg.'),(283,1,11,17,'Eber masih hidup empat ratus tiga\npuluh tahun, setelah ia memperanakkan\nPeleg, dan ia memperanakkan anak-anak\nlelaki dan perempuan.'),(284,1,11,18,'Setelah Peleg hidup tiga puluh tahun,\nia memperanakkan Rehu.'),(285,1,11,19,'Peleg masih hidup dua ratus sembilan\ntahun, setelah ia memperanakkan Rehu,\ndan ia memperanakkan anak-anak lelaki\ndan perempuan.'),(286,1,11,20,'Setelah Rehu hidup tiga puluh dua\ntahun, ia memperanakkan Serug.'),(287,1,11,21,'Rehu masih hidup dua ratus tujuh\ntahun, setelah ia memperanakkan Serug,\ndan ia memperanakkan anak-anak lelaki\ndan perempuan.'),(288,1,11,22,'Setelah Serug hidup tiga puluh\ntahun, ia memperanakkan Nahor.'),(289,1,11,23,'Serug masih hidup dua ratus tahun,\nsetelah ia memperanakkan Nahor, dan ia\nmemperanakkan anak-anak lelaki dan\nperempuan.'),(290,1,11,24,'Setelah Nahor hidup dua puluh\nsembilan tahun, ia memperanakkan\nTerah.'),(291,1,11,25,'Nahor masih hidup seratus sembilan\nbelas tahun, setelah ia memperanakkan\nTerah, dan ia memperanakkan anak-anak\nlelaki dan perempuan.'),(292,1,11,26,'Setelah Terah hidup tujuh puluh\ntahun, ia memperanakkan Abram, Nahor\ndan Haran.'),(293,1,11,27,'Inilah keturunan Terah. Terah\nmemperanakkan Abram, Nahor dan\nHaran, dan Haran memperanakkan Lot.'),(294,1,11,28,'Ketika Terah, ayahnya, masih hidup,\nmatilah Haran di negeri kelahirannya, di\nUr-Kasdim.'),(295,1,11,29,'Abram dan Nahor kedua-duanya\nkawin; nama isteri Abram ialah Sarai,\ndan nama isteri Nahor ialah Milka, anak\nHaran ayah Milka dan Yiska.'),(296,1,11,30,'Sarai itu mandul, tidak mempunyai\nanak.'),(297,1,11,31,'Lalu Terah membawa Abram,\nanaknya, serta cucunya, Lot, yaitu\nanak Haran, dan Sarai, menantunya,\nisteri Abram, anaknya; ia berangkat\nbersama-sama dengan mereka dari\nUr-Kasdim untuk pergi ke tanah Kanaan,\nlalu sampailah mereka ke Haran, dan\nmenetap di sana.'),(298,1,11,32,'Umur Terah ada dua ratus lima\ntahun; lalu ia mati di Haran.'),(299,1,12,1,'Berfirmanlah TUHAN kepada Abram: \"Pergilah dari negerimu \ndan dari sanak saudaramu dan dari\nrumah bapamu ini ke negeri yang akan\nKutunjukkan kepadamu;'),(300,1,12,2,'Aku akan membuat engkau menjadi\nbangsa yang besar, dan memberkati\nengkau serta membuat namamu\nmasyhur; dan engkau akan menjadi\nberkat.'),(301,1,12,3,'Aku akan memberkati orang-orang\nyang memberkati engkau, dan mengutuk\norang-orang yang mengutuk engkau,\ndan olehmu semua kaum di muka bumi\nakan mendapat berkat.\"'),(302,1,12,4,'Lalu pergilah Abram seperti yang\ndifirmankan TUHAN kepadanya, dan\nLotpun ikut bersama-sama dengan dia;\nAbram berumur tujuh puluh lima tahun,\nketika ia berangkat dari Haran.'),(303,1,12,5,'Abram membawa Sarai, isterinya,\ndan Lot, anak saudaranya, dan segala\nharta benda yang didapat mereka dan\norang-orang yang diperoleh mereka\ndi Haran; mereka berangkat ke tanah\nKanaan, lalu sampai di situ.'),(304,1,12,6,'Abram berjalan melalui negeri itu\nsampai ke suatu tempat dekat Sikhem,\nyakni pohon tarbantin di More. Waktu itu\norang Kanaan diam di negeri itu.'),(305,1,12,7,'Ketika itu TUHAN menampakkan diri\nkepada Abram dan berfirman: \"Aku\nakan memberikan negeri ini kepada keturunanmu.\" Maka didirikannya di\nsitu mezbah bagi TUHAN yang telah\nmenampakkan diri kepadanya.'),(306,1,12,8,'Kemudian ia pindah dari situ ke\npegunungan di sebelah timur Betel. Ia\nmemasang kemahnya dengan Betel di\nsebelah barat dan Ai di sebelah timur,\nlalu ia mendirikan di situ mezbah bagi\nTUHAN dan memanggil nama TUHAN.'),(307,1,12,9,'Sesudah itu Abram berangkat dan\nmakin jauh ia berjalan ke Tanah Negeb.'),(308,1,12,10,'Ketika kelaparan timbul di negeri itu,\npergilah Abram ke Mesir untuk tinggal di\nsitu sebagai orang asing, sebab hebat\nkelaparan di negeri itu.'),(309,1,12,11,'Pada waktu ia akan masuk ke Mesir,\nberkatalah ia kepada Sarai, isterinya:\n\"Memang aku tahu, bahwa engkau\nadalah seorang perempuan yang cantik\nparasnya.'),(310,1,12,12,'Apabila orang Mesir melihat engkau,\nmereka akan berkata: Itu isterinya.\nJadi mereka akan membunuh aku dan\nmembiarkan engkau hidup.'),(311,1,12,13,'Katakanlah, bahwa engkau adikku,\nsupaya aku diperlakukan mereka dengan\nbaik karena engkau, dan aku dibiarkan\nhidup oleh sebab engkau.\"'),(312,1,12,14,'Sesudah Abram masuk ke Mesir,\norang Mesir itu melihat, bahwa\nperempuan itu sangat cantik,'),(313,1,12,15,'dan ketika punggawa-punggawa\nFiraun melihat Sarai, mereka memujimujinya di hadapan Firaun, sehingga\nperempuan itu dibawa ke istananya.'),(314,1,12,16,'Firaun menyambut Abram dengan\nbaik-baik, karena ia mengingini\nperempuan itu, dan Abram mendapat\nkambing domba, lembu sapi, keledai\njantan, budak laki-laki dan perempuan,\nkeledai betina dan unta.'),(315,1,12,17,'Tetapi TUHAN menimpakan tulah\nyang hebat kepada Firaun, demikian\njuga kepada seisi istananya, karena\nSarai, isteri Abram itu.'),(316,1,12,18,'Lalu Firaun memanggil Abram serta\nberkata: \"Apakah yang kauperbuat\nini terhadap aku? Mengapa tidak\nkauberitahukan, bahwa ia isterimu?'),(317,1,12,19,'Mengapa engkau katakan: dia\nadikku, sehingga aku mengambilnya\nmenjadi isteriku? Sekarang, inilah\nisterimu, ambillah dan pergilah!\"'),(318,1,12,20,'Lalu Firaun memerintahkan beberapa\norang untuk mengantarkan Abram pergi, bersama-sama dengan isterinya dan\nsegala kepunyaannya.'),(319,1,13,1,'Maka pergilah Abram dari Mesir ke Tanah Negeb dengan isterinya dan segala kepunyaannya, dan Lotpun\nbersama-sama dengan dia.'),(320,1,13,2,'Adapun Abram sangat kaya, banyak\nternak, perak dan emasnya.'),(321,1,13,3,'Ia berjalan dari tempat persinggahan\nke tempat persinggahan, dari Tanah\nNegeb sampai dekat Betel, di mana\nkemahnya mula-mula berdiri, antara\nBetel dan Ai,'),(322,1,13,4,'ke tempat mezbah yang dibuatnya\ndahulu di sana; di situlah Abram\nmemanggil nama TUHAN.'),(323,1,13,5,'Juga Lot, yang ikut bersama-sama\ndengan Abram, mempunyai domba dan\nlembu dan kemah.'),(324,1,13,6,'Tetapi negeri itu tidak cukup luas\nbagi mereka untuk diam bersama-sama,\nsebab harta milik mereka amat banyak,\nsehingga mereka tidak dapat diam\nbersama-sama.'),(325,1,13,7,'Karena itu terjadilah perkelahian\nantara para gembala Abram dan para\ngembala Lot. Waktu itu orang Kanaan\ndan orang Feris diam di negeri itu.'),(326,1,13,8,'Maka berkatalah Abram kepada Lot:\n\"Janganlah kiranya ada perkelahian\nantara aku dan engkau, dan antara para\ngembalaku dan para gembalamu, sebab\nkita ini kerabat.'),(327,1,13,9,'Bukankah seluruh negeri ini terbuka\nuntuk engkau? Baiklah pisahkan dirimu\ndari padaku; jika engkau ke kiri, maka\naku ke kanan, jika engkau ke kanan,\nmaka aku ke kiri.\"'),(328,1,13,10,'Lalu Lot melayangkan pandangnya\ndan dilihatnyalah, bahwa seluruh\nLembah Yordan banyak airnya, seperti\ntaman TUHAN, seperti tanah Mesir,\nsampai ke Zoar. --Hal itu terjadi sebelum\nTUHAN memusnahkan Sodom dan\nGomora. --'),(329,1,13,11,'Sebab itu Lot memilih baginya\nseluruh Lembah Yordan itu, lalu ia\nberangkat ke sebelah timur dan mereka\nberpisah.'),(330,1,13,12,'Abram menetap di tanah Kanaan,\ntetapi Lot menetap di kota-kota Lembah\nYordan dan berkemah di dekat Sodom.'),(331,1,13,13,'Adapun orang Sodom sangat jahat\ndan berdosa terhadap TUHAN.'),(332,1,13,14,'Setelah Lot berpisah dari pada\nAbram, berfirmanlah TUHAN kepada Abram: \"Pandanglah sekelilingmu dan\nlihatlah dari tempat engkau berdiri itu ke\ntimur dan barat, utara dan selatan,'),(333,1,13,15,'sebab seluruh negeri yang kaulihat itu\nakan Kuberikan kepadamu dan kepada\nketurunanmu untuk selama-lamanya.'),(334,1,13,16,'Dan Aku akan menjadikan\nketurunanmu seperti debu tanah\nbanyaknya, sehingga, jika seandainya\nada yang dapat menghitung debu tanah,\nketurunanmupun akan dapat dihitung\njuga.'),(335,1,13,17,'Bersiaplah, jalanilah negeri itu\nmenurut panjang dan lebarnya, sebab\nkepadamulah akan Kuberikan negeri\nitu.\"'),(336,1,13,18,'Sesudah itu Abram memindahkan\nkemahnya dan menetap di dekat\npohon-pohon tarbantin di Mamre, dekat\nHebron, lalu didirikannyalah mezbah di\nsitu bagi TUHAN.'),(337,1,14,1,'Pada zaman Amrafel, raja Sinear, Ariokh, raja Elasar, Kedorlaomer, raja Elam, dan Tideal, raja Goyim,\nterjadilah,'),(338,1,14,2,'bahwa raja-raja ini berperang\nmelawan Bera, raja Sodom, Birsya, raja\nGomora, Syinab, raja Adma, Syemeber, raja Zeboim dan raja negeri Bela, yakni\nnegeri Zoar.'),(339,1,14,3,'Raja-raja yang disebut terakhir ini\nsemuanya bersekutu dan datang ke\nlembah Sidim, yakni Laut Asin.'),(340,1,14,4,'Dua belas tahun lamanya mereka\ntakluk kepada Kedorlaomer, tetapi\ndalam tahun yang ketiga belas mereka\nmemberontak.'),(341,1,14,5,'Dalam tahun yang keempat belas\ndatanglah Kedorlaomer serta raja-raja\nyang bersama-sama dengan dia, lalu\nmereka mengalahkan orang Refaim di\nAsyterot-Karnaim, orang Zuzim di Ham,\norang Emim di Syawe-Kiryataim'),(342,1,14,6,'dan orang Hori di pegunungan mereka\nyang bernama Seir, sampai ke El-Paran\ndi tepi padang gurun.'),(343,1,14,7,'Sesudah itu baliklah mereka dan\nsampai ke En-Mispat, yakni Kadesh,\ndan mengalahkan seluruh daerah orang\nAmalek, dan juga orang Amori, yang\ndiam di Hazezon-Tamar.'),(344,1,14,8,'Lalu keluarlah raja negeri Sodom,\nraja negeri Gomora, raja negeri Adma,\nraja negeri Zeboim dan raja negeri Bela,\nyakni negeri Zoar, dan mengatur barisan perangnya melawan mereka di lembah\nSidim,'),(345,1,14,9,'melawan Kedorlaomer, raja Elam,\nTideal, raja Goyim, Amrafel, raja Sinear,\ndan Ariokh, raja Elasar, empat raja lawan\nlima.'),(346,1,14,10,'Di lembah Sidim itu di mana-mana\nada sumur aspal. Ketika raja Sodom dan\nraja Gomora melarikan diri, jatuhlah\nmereka ke dalamnya, dan orang-orang\nyang masih tinggal hidup melarikan diri\nke pegunungan.'),(347,1,14,11,'Segala harta benda Sodom dan\nGomora beserta segala bahan makanan\ndirampas musuh, lalu mereka pergi.'),(348,1,14,12,'Juga Lot, anak saudara Abram,\nbeserta harta bendanya, dibawa musuh,\nlalu mereka pergi--sebab Lot itu diam di\nSodom.'),(349,1,14,13,'Kemudian datanglah seorang pelarian\ndan menceritakan hal ini kepada Abram,\norang Ibrani itu, yang tinggal dekat\npohon-pohon tarbantin kepunyaan\nMamre, orang Amori itu, saudara Eskol\ndan Aner, yakni teman-teman sekutu\nAbram.'),(350,1,14,14,'Ketika Abram mendengar, bahwa\nanak saudaranya tertawan, maka dikerahkannyalah orang-orangnya yang\nterlatih, yakni mereka yang lahir di\nrumahnya, tiga ratus delapan belas\norang banyaknya, lalu mengejar musuh\nsampai ke Dan.'),(351,1,14,15,'Dan pada waktu malam berbagilah\nmereka, ia dan hamba-hambanya\nitu, untuk melawan musuh; mereka\nmengalahkan dan mengejar musuh\nsampai ke Hoba di sebelah utara\nDamsyik.'),(352,1,14,16,'Dibawanyalah kembali segala harta\nbenda itu; juga Lot, anak saudaranya\nitu, serta harta bendanya dibawanya\nkembali, demikian juga perempuanperempuan dan orang-orangnya.'),(353,1,14,17,'Setelah Abram kembali dari\nmengalahkan Kedorlaomer dan para raja\nyang bersama-sama dengan dia, maka\nkeluarlah raja Sodom menyongsong dia\nke lembah Syawe, yakni Lembah Raja.'),(354,1,14,18,'Melkisedek, raja Salem, membawa\nroti dan anggur; ia seorang imam Allah\nYang Mahatinggi.'),(355,1,14,19,'Lalu ia memberkati Abram, katanya:\n\"Diberkatilah kiranya Abram oleh Allah\nYang Mahatinggi, Pencipta langit dan\nbumi,'),(356,1,14,20,'dan terpujilah Allah Yang Mahatinggi,\nyang telah menyerahkan musuhmu ke\ntanganmu.\" Lalu Abram memberikan\nkepadanya sepersepuluh dari semuanya.'),(357,1,14,21,'Berkatalah raja Sodom itu kepada\nAbram: \"Berikanlah kepadaku orangorang itu, dan ambillah untukmu harta\nbenda itu.\"'),(358,1,14,22,'Tetapi kata Abram kepada raja\nnegeri Sodom itu: \"Aku bersumpah demi\nTUHAN, Allah Yang Mahatinggi, Pencipta\nlangit dan bumi:'),(359,1,14,23,'Aku tidak akan mengambil apa-apa\ndari kepunyaanmu itu, sepotong benang\natau tali kasutpun tidak, supaya engkau\njangan dapat berkata: Aku telah\nmembuat Abram menjadi kaya.'),(360,1,14,24,'Kalau aku, jangan sekali-kali!\nHanya apa yang telah dimakan oleh\nbujang-bujang ini dan juga bagian\norang-orang yang pergi bersama-sama\ndengan aku, yakni Aner, Eskol dan\nMamre, biarlah mereka itu mengambil\nbagiannya masing-masing.\"'),(361,1,15,1,'Kemudian datanglah firman TUHAN kepada Abram dalam suatu penglihatan: \"Janganlah takut, Abram, Akulah perisaimu; upahmu akan\nsangat besar.\"'),(362,1,15,2,'Abram menjawab: \"Ya Tuhan ALLAH,\napakah yang akan Engkau berikan\nkepadaku, karena aku akan meninggal\ndengan tidak mempunyai anak, dan\nyang akan mewarisi rumahku ialah\nEliezer, orang Damsyik itu.\"'),(363,1,15,3,'Lagi kata Abram: \"Engkau tidak\nmemberikan kepadaku keturunan,\nsehingga seorang hambaku nanti\nmenjadi ahli warisku.\"'),(364,1,15,4,'Tetapi datanglah firman TUHAN\nkepadanya, demikian: \"Orang ini tidak\nakan menjadi ahli warismu, melainkan\nanak kandungmu, dialah yang akan\nmenjadi ahli warismu.'),(365,1,15,5,'Lalu TUHAN membawa Abram ke\nluar serta berfirman: \"Coba lihat ke\nlangit, hitunglah bintang-bintang, jika\nengkau dapat menghitungnya.\" Maka\nfirman-Nya kepadanya: \"Demikianlah\nbanyaknya nanti keturunanmu.\"'),(366,1,15,6,'Lalu percayalah Abram kepada\nTUHAN, maka TUHAN memperhitungkan\nhal itu kepadanya sebagai kebenaran.'),(367,1,15,7,'Lagi firman TUHAN kepadanya:\n\"Akulah TUHAN, yang membawa engkau keluar dari Ur-Kasdim untuk memberikan\nnegeri ini kepadamu menjadi milikmu.\"'),(368,1,15,8,'Kata Abram: \"Ya Tuhan ALLAH, dari\nmanakah aku tahu, bahwa aku akan\nmemilikinya?\"'),(369,1,15,9,'Firman TUHAN kepadanya: \"Ambillah\nbagi-Ku seekor lembu betina berumur\ntiga tahun, seekor kambing betina\nberumur tiga tahun, seekor domba\njantan berumur tiga tahun, seekor\nburung tekukur dan seekor anak burung\nmerpati.\"'),(370,1,15,10,'Diambilnyalah semuanya itu bagi\nTUHAN, dipotong dua, lalu diletakkannya\nbagian-bagian itu yang satu di samping\nyang lain, tetapi burung-burung itu tidak\ndipotong dua.'),(371,1,15,11,'Ketika burung-burung buas hinggap\npada daging binatang-binatang itu,\nmaka Abram mengusirnya.'),(372,1,15,12,'Menjelang matahari terbenam,\ntertidurlah Abram dengan nyenyak. Lalu\nturunlah meliputinya gelap gulita yang\nmengerikan.'),(373,1,15,13,'Firman TUHAN kepada Abram:\n\"Ketahuilah dengan sesungguhnya\nbahwa keturunanmu akan menjadi orang\nasing dalam suatu negeri, yang bukan kepunyaan mereka, dan bahwa mereka\nakan diperbudak dan dianiaya, empat\nratus tahun lamanya.'),(374,1,15,14,'Tetapi bangsa yang akan\nmemperbudak mereka, akan Kuhukum,\ndan sesudah itu mereka akan keluar\ndengan membawa harta benda yang\nbanyak.'),(375,1,15,15,'Tetapi engkau akan pergi kepada\nnenek moyangmu dengan sejahtera;\nengkau akan dikuburkan pada waktu\ntelah putih rambutmu.'),(376,1,15,16,'Tetapi keturunan yang keempat\nakan kembali ke sini, sebab sebelum\nitu kedurjanaan orang Amori itu belum\ngenap.\"'),(377,1,15,17,'Ketika matahari telah terbenam, dan\nhari menjadi gelap, maka kelihatanlah\nperapian yang berasap beserta\nsuluh yang berapi lewat di antara\npotongan-potongan daging itu.'),(378,1,15,18,'Pada hari itulah TUHAN mengadakan\nperjanjian dengan Abram serta\nberfirman: \"Kepada keturunanmulah\nKuberikan negeri ini, mulai dari sungai\nMesir sampai ke sungai yang besar itu,\nsungai Efrat:'),(379,1,15,19,'yakni tanah orang Keni, orang Kenas,\norang Kadmon,'),(380,1,15,20,'orang Het, orang Feris, orang Refaim,'),(381,1,15,21,'orang Amori, orang Kanaan, orang\nGirgasi dan orang Yebus itu.\"'),(382,1,16,1,'Adapun Sarai, isteri Abram itu, tidak beranak. Ia mempunyai seorang hamba perempuan, orang Mesir,\nHagar namanya.'),(383,1,16,2,'Berkatalah Sarai kepada Abram:\n\"Engkau tahu, TUHAN tidak memberi\naku melahirkan anak. Karena itu baiklah\nhampiri hambaku itu; mungkin oleh\ndialah aku dapat memperoleh seorang\nanak.\" Dan Abram mendengarkan\nperkataan Sarai.'),(384,1,16,3,'Jadi Sarai, isteri Abram itu, mengambil\nHagar, hambanya, orang Mesir itu,\n--yakni ketika Abram telah sepuluh\ntahun tinggal di tanah Kanaan--,lalu\nmemberikannya kepada Abram,\nsuaminya, untuk menjadi isterinya.'),(385,1,16,4,'Abram menghampiri Hagar, lalu\nmengandunglah perempuan itu. Ketika\nHagar tahu, bahwa ia mengandung,\nmaka ia memandang rendah akan\nnyonyanya itu.'),(386,1,16,5,'Lalu berkatalah Sarai kepada Abram:\n\"Penghinaan yang kuderita ini adalah\ntanggung jawabmu; akulah yang\nmemberikan hambaku ke pangkuanmu,\ntetapi baru saja ia tahu, bahwa ia\nmengandung, ia memandang rendah\nakan aku; TUHAN kiranya yang menjadi\nHakim antara aku dan engkau.\"'),(387,1,16,6,'Kata Abram kepada Sarai: \"Hambamu\nitu di bawah kekuasaanmu; perbuatlah\nkepadanya apa yang kaupandang baik.\"\nLalu Sarai menindas Hagar, sehingga ia\nlari meninggalkannya.'),(388,1,16,7,'Lalu Malaikat TUHAN menjumpainya\ndekat suatu mata air di padang gurun,\nyakni dekat mata air di jalan ke Syur.'),(389,1,16,8,'Katanya: \"Hagar, hamba Sarai,\ndari manakah datangmu dan ke\nmanakah pergimu?\" Jawabnya: \"Aku lari\nmeninggalkan Sarai, nyonyaku.\"'),(390,1,16,9,'Lalu kata Malaikat TUHAN itu\nkepadanya: \"Kembalilah kepada\nnyonyamu, biarkanlah engkau ditindas\ndi bawah kekuasaannya.\"'),(391,1,16,10,'Lagi kata Malaikat TUHAN itu\nkepadanya: \"Aku akan membuat sangat\nbanyak keturunanmu, sehingga tidak\ndapat dihitung karena banyaknya.\"'),(392,1,16,11,'Selanjutnya kata Malaikat TUHAN\nitu kepadanya: \"Engkau mengandung\ndan akan melahirkan seorang anak\nlaki-laki dan akan menamainya Ismael,\nsebab TUHAN telah mendengar tentang\npenindasan atasmu itu.'),(393,1,16,12,'Seorang laki-laki yang lakunya\nseperti keledai liar, demikianlah nanti\nanak itu; tangannya akan melawan\ntiap-tiap orang dan tangan tiap-tiap\norang akan melawan dia, dan di tempat\nkediamannya ia akan menentang semua\nsaudaranya.\"'),(394,1,16,13,'Kemudian Hagar menamakan TUHAN\nyang telah berfirman kepadanya itu\ndengan sebutan: \"Engkaulah El-Roi.\"\nSebab katanya: \"Bukankah di sini kulihat\nDia yang telah melihat aku?\"'),(395,1,16,14,'Sebab itu sumur tadi disebutkan\norang: sumur Lahai-Roi; letaknya antara\nKadesh dan Bered.'),(396,1,16,15,'Lalu Hagar melahirkan seorang anak\nlaki-laki bagi Abram dan Abram menamai\nanak yang dilahirkan Hagar itu Ismael.'),(397,1,16,16,'Abram berumur delapan puluh enam\ntahun, ketika Hagar melahirkan Ismael\nbaginya.'),(398,1,17,1,'Ketika Abram berumur sembilan puluh sembilan tahun, maka TUHAN menampakkan diri kepada\nAbram dan berfirman kepadanya:\n\"Akulah Allah Yang Mahakuasa, hiduplah\ndi hadapan-Ku dengan tidak bercela.'),(399,1,17,2,'Aku akan mengadakan perjanjian\nantara Aku dan engkau, dan Aku akan\nmembuat engkau sangat banyak.\"'),(400,1,17,3,'Lalu sujudlah Abram, dan Allah\nberfirman kepadanya:'),(401,1,17,4,'\"Dari pihak-Ku, inilah perjanjian-Ku\ndengan engkau: Engkau akan menjadi\nbapa sejumlah besar bangsa.'),(402,1,17,5,'Karena itu namamu bukan lagi Abram,\nmelainkan Abraham, karena engkau\ntelah Kutetapkan menjadi bapa sejumlah\nbesar bangsa.'),(403,1,17,6,'Aku akan membuat engkau beranak\ncucu sangat banyak; engkau akan\nKubuat menjadi bangsa-bangsa, dan\ndari padamu akan berasal raja-raja.'),(404,1,17,7,'Aku akan mengadakan perjanjian\nantara Aku dan engkau serta\nketurunanmu turun-temurun menjadi\nperjanjian yang kekal, supaya Aku\nmenjadi Allahmu dan Allah keturunanmu.'),(405,1,17,8,'Kepadamu dan kepada keturunanmu\nakan Kuberikan negeri ini yang kaudiami\nsebagai orang asing, yakni seluruh tanah\nKanaan akan Kuberikan menjadi milikmu\nuntuk selama-lamanya; dan Aku akan\nmenjadi Allah mereka.\"'),(406,1,17,9,'Lagi firman Allah kepada Abraham:\n\"Dari pihakmu, engkau harus memegang\nperjanjian-Ku, engkau dan keturunanmu\nturun-temurun.'),(407,1,17,10,'Inilah perjanjian-Ku, yang harus\nkamu pegang, perjanjian antara Aku dan\nkamu serta keturunanmu, yaitu setiap\nlaki-laki di antara kamu harus disunat;'),(408,1,17,11,'haruslah dikerat kulit khatanmu dan\nitulah akan menjadi tanda perjanjian\nantara Aku dan kamu.'),(409,1,17,12,'Anak yang berumur delapan hari\nharuslah disunat, yakni setiap laki-laki di\nantara kamu, turun-temurun: baik yang\nlahir di rumahmu, maupun yang dibeli\ndengan uang dari salah seorang asing,\ntetapi tidak termasuk keturunanmu.'),(410,1,17,13,'Orang yang lahir di rumahmu dan\norang yang engkau beli dengan uang\nharus disunat; maka dalam dagingmulah\nperjanjian-Ku itu menjadi perjanjian\nyang kekal.'),(411,1,17,14,'Dan orang yang tidak disunat,\nyakni laki-laki yang tidak dikerat kulit\nkhatannya, maka orang itu harus\ndilenyapkan dari antara orang-orang\nsebangsanya: ia telah mengingkari\nperjanjian-Ku.\"'),(412,1,17,15,'Selanjutnya Allah berfirman kepada\nAbraham: \"Tentang isterimu Sarai,\njanganlah engkau menyebut dia lagi\nSarai, tetapi Sara, itulah namanya.'),(413,1,17,16,'Aku akan memberkatinya, dan dari\npadanya juga Aku akan memberikan\nkepadamu seorang anak laki-laki,\nbahkan Aku akan memberkatinya,\nsehingga ia menjadi ibu bangsa-bangsa;\nraja-raja bangsa-bangsa akan lahir dari\npadanya.\"'),(414,1,17,17,'Lalu tertunduklah Abraham dan\ntertawa serta berkata dalam hatinya:\n\"Mungkinkah bagi seorang yang berumur\nseratus tahun dilahirkan seorang anak\ndan mungkinkah Sara, yang telah\nberumur sembilan puluh tahun itu\nmelahirkan seorang anak?\"'),(415,1,17,18,'Dan Abraham berkata kepada Allah:\n\"Ah, sekiranya Ismael diperkenankan\nhidup di hadapan-Mu!\"'),(416,1,17,19,'Tetapi Allah berfirman: \"Tidak,\nmelainkan isterimu Saralah yang akan\nmelahirkan anak laki-laki bagimu, dan\nengkau akan menamai dia Ishak, dan\nAku akan mengadakan perjanjian-Ku\ndengan dia menjadi perjanjian yang\nkekal untuk keturunannya.'),(417,1,17,20,'Tentang Ismael, Aku telah\nmendengarkan permintaanmu; ia\nakan Kuberkati, Kubuat beranak\ncucu dan sangat banyak; ia akan\nmemperanakkan dua belas raja, dan Aku\nakan membuatnya menjadi bangsa yang\nbesar.'),(418,1,17,21,'Tetapi perjanjian-Ku akan Kuadakan\ndengan Ishak, yang akan dilahirkan Sara\nbagimu tahun yang akan datang pada\nwaktu seperti ini juga.\"'),(419,1,17,22,'Setelah selesai berfirman kepada\nAbraham, naiklah Allah meninggalkan\nAbraham.'),(420,1,17,23,'Setelah selesai berfirman kepada\nAbraham, naiklah Allah meninggalkan\nAbraham.'),(421,1,17,24,'Setelah itu Abraham memanggil\nIsmael, anaknya, dan semua orang yang\nlahir di rumahnya, juga semua orang\nyang dibelinya dengan uang, yakni\nsetiap laki-laki dari isi rumahnya, lalu ia\nmengerat kulit khatan mereka pada hari itu juga, seperti yang telah difirmankan\nAllah kepadanya.'),(422,1,17,25,'Abraham berumur sembilan puluh\nsembilan tahun ketika dikerat kulit\nkhatannya.'),(423,1,17,26,'Dan Ismael, anaknya, berumur\ntiga belas tahun ketika dikerat kulit\nkhatannya.'),(424,1,17,27,'Pada hari itu juga Abraham dan\nIsmael, anaknya, disunat.'),(425,1,17,28,'Dan semua orang dari isi rumah\nAbraham, baik yang lahir di rumahnya,\nmaupun yang dibeli dengan uang dari\norang asing, disunat bersama-sama\ndengan dia.'),(426,1,18,1,'Kemudian TUHAN menampakkan diri kepada Abraham dekat pohon tarbantin di Mamre, sedang ia duduk di\npintu kemahnya waktu hari panas terik.'),(427,1,18,2,'Ketika ia mengangkat mukanya, ia\nmelihat tiga orang berdiri di depannya.\nSesudah dilihatnya mereka, ia berlari\ndari pintu kemahnya menyongsong\nmereka, lalu sujudlah ia sampai ke\ntanah,'),(428,1,18,3,'serta berkata: \"Tuanku, jika aku\ntelah mendapat kasih tuanku, janganlah\nkiranya lampaui hambamu ini.'),(429,1,18,4,'Biarlah diambil air sedikit, basuhlah\nkakimu dan duduklah beristirahat di\nbawah pohon ini;'),(430,1,18,5,'biarlah kuambil sepotong roti, supaya\ntuan-tuan segar kembali; kemudian\nbolehlah tuan-tuan meneruskan\nperjalanannya; sebab tuan-tuan telah\ndatang ke tempat hambamu ini.\" Jawab\nmereka: \"Perbuatlah seperti yang\nkaukatakan itu.\"'),(431,1,18,6,'Lalu Abraham segera pergi ke kemah\nmendapatkan Sara serta berkata:\n\"Segeralah! Ambil tiga sukat tepung\nyang terbaik! Remaslah itu dan buatlah\nroti bundar!\"'),(432,1,18,7,'Lalu berlarilah Abraham kepada\nlembu sapinya, ia mengambil seekor\nanak lembu yang empuk dan baik\ndagingnya dan memberikannya kepada\nseorang bujangnya, lalu orang ini segera\nmengolahnya.'),(433,1,18,8,'Kemudian diambilnya dadih dan\nsusu serta anak lembu yang telah\ndiolah itu, lalu dihidangkannya di depan\norang-orang itu; dan ia berdiri di dekat\nmereka di bawah pohon itu, sedang\nmereka makan.'),(434,1,18,9,'Lalu kata mereka kepadanya: \"Di\nmanakah Sara, isterimu?\" Jawabnya: \"Di\nsana, di dalam kemah.\"'),(435,1,18,10,'Dan firman-Nya: \"Sesungguhnya Aku\nakan kembali tahun depan mendapatkan\nengkau, pada waktu itulah Sara,\nisterimu, akan mempunyai seorang anak\nlaki-laki.\" Dan Sara mendengarkan pada\npintu kemah yang di belakang-Nya.'),(436,1,18,11,'Adapun Abraham dan Sara telah tua\ndan lanjut umurnya dan Sara telah mati\nhaid.'),(437,1,18,12,'Jadi tertawalah Sara dalam hatinya,\nkatanya: \"Akan berahikah aku, setelah\naku sudah layu, sedangkan tuanku\nsudah tua?\"'),(438,1,18,13,'Lalu berfirmanlah TUHAN kepada\nAbraham: \"Mengapakah Sara tertawa\ndan berkata: Sungguhkah aku akan\nmelahirkan anak, sedangkan aku telah\ntua?'),(439,1,18,14,'Adakah sesuatu apapun yang\nmustahil untuk TUHAN? Pada waktu\nyang telah ditetapkan itu, tahun depan,\nAku akan kembali mendapatkan engkau,\npada waktu itulah Sara mempunyai\nseorang anak laki-laki.\"'),(440,1,18,15,'Lalu Sara menyangkal, katanya: \"Aku\ntidak tertawa,\" sebab ia takut; tetapi\nTUHAN berfirman: \"Tidak, memang\nengkau tertawa!\"'),(441,1,18,16,'Lalu berangkatlah orang-orang\nitu dari situ dan memandang ke\narah Sodom; dan Abraham berjalan\nbersama-sama dengan mereka untuk\nmengantarkan mereka.'),(442,1,18,17,'Berpikirlah TUHAN: \"Apakah Aku\nakan menyembunyikan kepada Abraham\napa yang hendak Kulakukan ini?'),(443,1,18,18,'Bukankah sesungguhnya Abraham\nakan menjadi bangsa yang besar serta\nberkuasa, dan oleh dia segala bangsa di\natas bumi akan mendapat berkat?'),(444,1,18,19,'Sebab Aku telah memilih dia, supaya\ndiperintahkannya kepada anak-anaknya\ndan kepada keturunannya supaya tetap\nhidup menurut jalan yang ditunjukkan\nTUHAN, dengan melakukan kebenaran\ndan keadilan, dan supaya TUHAN\nmemenuhi kepada Abraham apa yang\ndijanjikan-Nya kepadanya.\"'),(445,1,18,20,'Sebab Aku telah memilih dia, supaya\ndiperintahkannya kepada anak-anaknya\ndan kepada keturunannya supaya tetap\nhidup menurut jalan yang ditunjukkan\nTUHAN, dengan melakukan kebenaran\ndan keadilan, dan supaya TUHAN\nmemenuhi kepada Abraham apa yang\ndijanjikan-Nya kepadanya.\"'),(446,1,18,21,'Baiklah Aku turun untuk melihat,\napakah benar-benar mereka telah\nberkelakuan seperti keluh kesah orang\nyang telah sampai kepada-Ku atau tidak;\nAku hendak mengetahuinya.\"'),(447,1,18,22,'Lalu berpalinglah orang-orang itu\ndari situ dan berjalan ke Sodom, tetapi\nAbraham masih tetap berdiri di hadapan\nTUHAN.'),(448,1,18,23,'Abraham datang mendekat dan\nberkata: \"Apakah Engkau akan\nmelenyapkan orang benar bersamasama dengan orang fasik?'),(449,1,18,24,'Bagaimana sekiranya ada lima puluh\norang benar dalam kota itu? Apakah\nEngkau akan melenyapkan tempat itu\ndan tidakkah Engkau mengampuninya\nkarena kelima puluh orang benar yang\nada di dalamnya itu?'),(450,1,18,25,'Jauhlah kiranya dari pada-Mu\nuntuk berbuat demikian, membunuh\norang benar bersama-sama dengan\norang fasik, sehingga orang benar itu\nseolah-olah sama dengan orang fasik!\nJauhlah kiranya yang demikian dari\npada-Mu! Masakan Hakim segenap bumi\ntidak menghukum dengan adil?\"'),(451,1,18,26,'TUHAN berfirman: \"Jika Kudapati\nlima puluh orang benar dalam kota\nSodom, Aku akan mengampuni seluruh\ntempat itu karena mereka.\"'),(452,1,18,27,'Abraham menyahut: \"Sesungguhnya\naku telah memberanikan diri berkata\nkepada Tuhan, walaupun aku debu dan\nabu.'),(453,1,18,28,'Sekiranya kurang lima orang dari\nkelima puluh orang benar itu, apakah\nEngkau akan memusnahkan seluruh kota\nitu karena yang lima itu?\" Firman-Nya:\n\"Aku tidak memusnahkannya, jika\nKudapati empat puluh lima di sana.\"'),(454,1,18,29,'Lagi Abraham melanjutkan\nperkataannya kepada-Nya: \"Sekiranya\nempat puluh didapati di sana?\" FirmanNya: \"Aku tidak akan berbuat demikian\nkarena yang empat puluh itu.\"'),(455,1,18,30,'Katanya: \"Janganlah kiranya Tuhan\nmurka, kalau aku berkata sekali lagi.\nSekiranya tiga puluh didapati di sana?\"\nFirman-Nya: \"Aku tidak akan berbuat\ndemikian, jika Kudapati tiga puluh di\nsana.\"'),(456,1,18,31,'Katanya: \"Sesungguhnya aku telah\nmemberanikan diri berkata kepada\nTuhan. Sekiranya dua puluh didapati di sana?\" Firman-Nya: \"Aku tidak akan\nmemusnahkannya karena yang dua\npuluh itu.\"'),(457,1,18,32,'Katanya: \"Janganlah kiranya Tuhan\nmurka, kalau aku berkata lagi sekali\nini saja. Sekiranya sepuluh didapati di\nsana?\" Firman-Nya: \"Aku tidak akan\nmemusnahkannya karena yang sepuluh\nitu.\"'),(458,1,18,33,'Lalu pergilah TUHAN, setelah Ia\nselesai berfirman kepada Abraham;\ndan kembalilah Abraham ke tempat\ntinggalnya.'),(459,1,19,1,'Kedua malaikat itu tiba di Sodom pada waktu petang. Lot sedang duduk di pintu gerbang Sodom\ndan ketika melihat mereka, bangunlah\nia menyongsong mereka, lalu sujud\ndengan mukanya sampai ke tanah,'),(460,1,19,2,'serta berkata: \"Tuan-tuan, silakanlah\nsinggah ke rumah hambamu ini,\nbermalamlah di sini dan basuhlah\nkakimu, maka besok pagi tuan-tuan\nboleh melanjutkan perjalanannya.\"\nJawab mereka: \"Tidak, kami akan\nbermalam di tanah lapang.\"'),(461,1,19,3,'Tetapi karena ia sangat mendesak\nmereka, singgahlah mereka dan masuk ke dalam rumahnya, kemudian ia\nmenyediakan hidangan bagi mereka, ia\nmembakar roti yang tidak beragi, lalu\nmereka makan.'),(462,1,19,4,'Tetapi sebelum mereka tidur, orangorang lelaki dari kota Sodom itu, dari\nyang muda sampai yang tua, bahkan\nseluruh kota, tidak ada yang terkecuali,\ndatang mengepung rumah itu.'),(463,1,19,5,'Mereka berseru kepada Lot: \"Di\nmanakah orang-orang yang datang\nkepadamu malam ini? Bawalah mereka\nkeluar kepada kami, supaya kami pakai\nmereka.\"'),(464,1,19,6,'Lalu keluarlah Lot menemui mereka,\nke depan pintu, tetapi pintu ditutupnya\ndi belakangnya,'),(465,1,19,7,'dan ia berkata: \"Saudara-saudaraku,\njanganlah kiranya berbuat jahat.'),(466,1,19,8,'Kamu tahu, aku mempunyai dua\norang anak perempuan yang belum\npernah dijamah laki-laki, baiklah mereka\nkubawa ke luar kepadamu; perbuatlah\nkepada mereka seperti yang kamu\npandang baik; hanya jangan kamu apaapakan orang-orang ini, sebab mereka\nmemang datang untuk berlindung di\ndalam rumahku.\"'),(467,1,19,9,'Tetapi mereka berkata: \"Enyahlah!\"\nLagi kata mereka: \"Orang ini datang ke\nsini sebagai orang asing dan dia mau\nmenjadi hakim atas kita! Sekarang kami\nakan menganiaya engkau lebih dari pada\nkedua orang itu!\" Lalu mereka mendesak\norang itu, yaitu Lot, dengan keras, dan\nmereka mendekat untuk mendobrak\npintu.'),(468,1,19,10,'Tetapi kedua orang itu mengulurkan\ntangannya, menarik Lot masuk ke dalam\nrumah, lalu menutup pintu.'),(469,1,19,11,'Dan mereka membutakan mata\norang-orang yang di depan pintu rumah\nitu, dari yang kecil sampai yang besar,\nsehingga percumalah orang-orang itu\nmencari-cari pintu.'),(470,1,19,12,'Lalu kedua orang itu berkata kepada\nLot: \"Siapakah kaummu yang ada di sini\nlagi? Menantu atau anakmu laki-laki,\nanakmu perempuan, atau siapa saja\nkaummu di kota ini, bawalah mereka\nkeluar dari tempat ini,'),(471,1,19,13,'sebab kami akan memusnahkan\ntempat ini, karena banyak keluh kesah\norang tentang kota ini di hadapan\nTUHAN; sebab itulah TUHAN mengutus\nkami untuk memusnahkannya.\"'),(472,1,19,14,'Keluarlah Lot, lalu berbicara dengan\nkedua bakal menantunya, yang\nakan kawin dengan kedua anaknya\nperempuan, katanya: \"Bangunlah,\nkeluarlah dari tempat ini, sebab TUHAN\nakan memusnahkan kota ini.\" Tetapi ia\ndipandang oleh kedua bakal menantunya\nitu sebagai orang yang berolok-olok saja.'),(473,1,19,15,'Ketika fajar telah menyingsing,\nkedua malaikat itu mendesak Lot,\nsupaya bersegera, katanya: \"Bangunlah,\nbawalah isterimu dan kedua anakmu\nyang ada di sini, supaya engkau jangan\nmati lenyap karena kedurjanaan kota\nini.\"'),(474,1,19,16,'Ketika ia berlambat-lambat, maka\ntangannya, tangan isteri dan tangan\nkedua anaknya dipegang oleh kedua\norang itu, sebab TUHAN hendak\nmengasihani dia; lalu kedua orang\nitu menuntunnya ke luar kota dan\nmelepaskannya di sana.'),(475,1,19,17,'Sesudah kedua orang itu menuntun\nmereka sampai ke luar, berkatalah\nseorang: \"Larilah, selamatkanlah\nnyawamu; janganlah menoleh ke\nbelakang, dan janganlah berhenti di\nmanapun juga di Lembah Yordan, larilah ke pegunungan, supaya engkau jangan\nmati lenyap.\"'),(476,1,19,18,'Kata Lot kepada mereka: \"Janganlah\nkiranya demikian, tuanku.'),(477,1,19,19,'Sungguhlah hambamu ini telah\ndikaruniai belas kasihan di hadapanmu,\ndan tuanku telah berbuat kemurahan\nbesar kepadaku dengan memelihara\nhidupku, tetapi jika aku harus lari ke\npegunungan, pastilah aku akan tersusul\noleh bencana itu, sehingga matilah aku.'),(478,1,19,20,'Sungguhlah kota yang di sana itu\ncukup dekat kiranya untuk lari ke sana;\nkota itu kecil; izinkanlah kiranya aku lari\nke sana. Bukankah kota itu kecil? Jika\ndemikian, nyawaku akan terpelihara.\"'),(479,1,19,21,'Sahut malaikat itu kepadanya:\n\"Baiklah, dalam hal inipun permintaanmu\nakan kuterima dengan baik; yakni kota\nyang telah kau sebut itu tidak akan\nkutunggangbalikkan.'),(480,1,19,22,'Cepatlah, larilah ke sana, sebab\naku tidak dapat berbuat apa-apa,\nsebelum engkau sampai ke sana.\" Itulah\nsebabnya nama kota itu disebut Zoar.'),(481,1,19,23,'Matahari telah terbit menyinari bumi,\nketika Lot tiba di Zoar.'),(482,1,19,24,'Kemudian TUHAN menurunkan\nhujan belerang dan api atas Sodom dan\nGomora, berasal dari TUHAN, dari langit;'),(483,1,19,25,'dan ditunggangbalikkan-Nyalah\nkota-kota itu dan Lembah Yordan\ndan semua penduduk kota-kota serta\ntumbuh-tumbuhan di tanah.'),(484,1,19,26,'Tetapi isteri Lot, yang berjalan\nmengikutnya, menoleh ke belakang, lalu\nmenjadi tiang garam.'),(485,1,19,27,'Ketika Abraham pagi-pagi pergi ke\ntempat ia berdiri di hadapan TUHAN itu,'),(486,1,19,28,'dan memandang ke arah Sodom dan\nGomora serta ke seluruh tanah Lembah\nYordan, maka dilihatnyalah asap dari\nbumi membubung ke atas sebagai asap\ndari dapur peleburan.'),(487,1,19,29,'Demikianlah pada waktu Allah\nmemusnahkan kota-kota di Lembah\nYordan dan menunggangbalikkan\nkota-kota kediaman Lot, maka Allah\ningat kepada Abraham, lalu dikeluarkanNyalah Lot dari tengah-tengah tempat\nyang ditunggangbalikkan itu.'),(488,1,19,30,'Pergilah Lot dari Zoar dan ia menetap\nbersama-sama dengan kedua anaknya\nperempuan di pegunungan, sebab ia\ntidak berani tinggal di Zoar, maka diamlah ia dalam suatu gua beserta\nkedua anaknya.'),(489,1,19,31,'Kata kakaknya kepada adiknya:\n\"Ayah kita telah tua, dan tidak ada\nlaki-laki di negeri ini yang dapat\nmenghampiri kita, seperti kebiasaan\nseluruh bumi.'),(490,1,19,32,'Marilah kita beri ayah kita minum\nanggur, lalu kita tidur dengan dia, supaya\nkita menyambung keturunan dari ayah\nkita.\"'),(491,1,19,33,'Pada malam itu mereka memberi\nayah mereka minum anggur, lalu\nmasuklah yang lebih tua untuk tidur\ndengan ayahnya; dan ayahnya itu tidak\nmengetahui ketika anaknya itu tidur dan\nketika ia bangun.'),(492,1,19,34,'Keesokan harinya berkatalah\nkakaknya kepada adiknya: \"Tadi malam\naku telah tidur dengan ayah; baiklah\nmalam ini juga kita beri dia minum\nanggur; masuklah engkau untuk tidur\ndengan dia, supaya kita menyambung\nketurunan dari ayah kita.\"'),(493,1,19,35,'Demikianlah juga pada malam itu\nmereka memberi ayah mereka minum\nanggur, lalu bangunlah yang lebih\nmuda untuk tidur dengan ayahnya; dan ayahnya itu tidak mengetahui ketika\nanaknya itu tidur dan ketika ia bangun.'),(494,1,19,36,'Lalu mengandunglah kedua anak Lot\nitu dari ayah mereka.'),(495,1,19,37,'Yang lebih tua melahirkan seorang\nanak laki-laki, dan menamainya Moab;\ndialah bapa orang Moab yang sekarang.'),(496,1,19,38,'Yang lebih mudapun melahirkan\nseorang anak laki-laki, dan menamainya\nBen-Ami; dialah bapa bani Amon yang\nsekarang.'),(497,1,20,1,'Lalu Abraham berangkat dari situ ke Tanah Negeb dan ia menetap antara Kadesh dan Syur. Ia\ntinggal di Gerar sebagai orang asing.'),(498,1,20,2,'Oleh karena Abraham telah\nmengatakan tentang Sara, isterinya:\n\"Dia saudaraku,\" maka Abimelekh, raja\nGerar, menyuruh mengambil Sara.'),(499,1,20,3,'Tetapi pada waktu malam Allah datang\nkepada Abimelekh dalam suatu mimpi\nserta berfirman kepadanya: \"Engkau\nharus mati oleh karena perempuan\nyang telah kauambil itu; sebab ia sudah\nbersuami.\"'),(500,1,20,4,'Adapun Abimelekh belum\nmenghampiri Sara. Berkatalah ia: \"Tuhan! Apakah Engkau membunuh\nbangsa yang tak bersalah?'),(501,1,20,5,'Bukankah orang itu sendiri\nmengatakan kepadaku: Dia saudaraku?\nDan perempuan itu sendiri telah\nmengatakan: Ia saudaraku. Jadi hal ini\nkulakukan dengan hati yang tulus dan\ndengan tangan yang suci.\"'),(502,1,20,6,'Lalu berfirmanlah Allah kepadanya\ndalam mimpi: \"Aku tahu juga, bahwa\nengkau telah melakukan hal itu dengan\nhati yang tulus, maka Akupun telah\nmencegah engkau untuk berbuat dosa\nterhadap Aku; sebab itu Aku tidak\nmembiarkan engkau menjamah dia.'),(503,1,20,7,'Jadi sekarang, kembalikanlah isteri\norang itu, sebab dia seorang nabi;\nia akan berdoa untuk engkau, maka\nengkau tetap hidup; tetapi jika engkau\ntidak mengembalikan dia, ketahuilah,\nengkau pasti mati, engkau dan semua\norang yang bersama-sama dengan\nengkau.\"'),(504,1,20,8,'Keesokan harinya pagi-pagi Abimelekh\nmemanggil semua hambanya dan\nmemberitahukan seluruh peristiwa itu\nkepada mereka, lalu sangat takutlah\norang-orang itu.'),(505,1,20,9,'Kemudian Abimelekh memanggil\nAbraham dan berkata kepadanya:\n\"Perbuatan apakah yang kaulakukan ini\nterhadap kami, dan kesalahan apakah\nyang kulakukan terhadap engkau,\nsehingga engkau mendatangkan dosa\nbesar atas diriku dan kerajaanku?\nEngkau telah berbuat hal-hal yang tidak\npatut kepadaku.\"'),(506,1,20,10,'Lagi kata Abimelekh kepada\nAbraham: \"Apakah maksudmu, maka\nengkau melakukan hal ini?\"'),(507,1,20,11,'Lalu Abraham berkata: \"Aku berpikir:\nTakut akan Allah tidak ada di tempat\nini; tentulah aku akan dibunuh karena\nisteriku.'),(508,1,20,12,'Lagipula ia benar-benar saudaraku,\nanak ayahku, hanya bukan anak ibuku,\ntetapi kemudian ia menjadi isteriku.'),(509,1,20,13,'Ketika Allah menyuruh aku\nmengembara keluar dari rumah ayahku,\nberkatalah aku kepada isteriku:\nTunjukkanlah kasihmu kepadaku, yakni:\nkatakanlah tentang aku di tiap-tiap\ntempat di mana kita tiba: Ia saudaraku.\"'),(510,1,20,14,'Kemudian Abimelekh mengambil\nkambing domba dan lembu sapi,\nhamba laki-laki dan perempuan, lalu memberikan semuanya itu kepada\nAbraham; Sara, isteri Abraham, juga\ndikembalikannya kepadanya.'),(511,1,20,15,'Dan Abimelekh berkata: \"Negeriku\nini terbuka untuk engkau; menetaplah,\ndi mana engkau suka.\"'),(512,1,20,16,'Lalu katanya kepada Sara: \"Telah\nkuberikan kepada saudaramu seribu\nsyikal perak, itulah bukti kesucianmu\nbagi semua orang yang bersama-sama\ndengan engkau. Maka dalam segala hal\nengkau dibenarkan.\"'),(513,1,20,17,'Lalu Abraham berdoa kepada\nAllah, dan Allah menyembuhkan\nAbimelekh dan isterinya dan budakbudaknya perempuan, sehingga mereka\nmelahirkan anak.'),(514,1,20,18,'Sebab tadinya TUHAN telah menutup\nkandungan setiap perempuan di istana\nAbimelekh karena Sara, isteri Abraham\nitu'),(515,1,21,1,'TUHAN memperhatikan Sara, seperti yang difirmankan-Nya, dan TUHAN melakukan kepada Sara\nseperti yang dijanjikan-Nya.'),(516,1,21,2,'Maka mengandunglah Sara, lalu ia\nmelahirkan seorang anak laki-laki bagi\nAbraham dalam masa tuanya, pada waktu yang telah ditetapkan, sesuai\ndengan firman Allah kepadanya.'),(517,1,21,3,'Abraham menamai anaknya yang\nbaru lahir itu Ishak, yang dilahirkan Sara\nbaginya.'),(518,1,21,4,'Kemudian Abraham menyunat Ishak,\nanaknya itu, ketika berumur delapan\nhari, seperti yang diperintahkan Allah\nkepadanya.'),(519,1,21,5,'Adapun Abraham berumur seratus\ntahun, ketika Ishak, anaknya, lahir\nbaginya.'),(520,1,21,6,'Berkatalah Sara: \"Allah telah\nmembuat aku tertawa; setiap orang\nyang mendengarnya akan tertawa\nkarena aku.\"'),(521,1,21,7,'Lagi katanya: \"Siapakah tadinya yang\ndapat mengatakan kepada Abraham:\nSara menyusui anak? Namun aku\ntelah melahirkan seorang anak laki-laki\nbaginya pada masa tuanya.\"'),(522,1,21,8,'Bertambah besarlah anak itu dan\nia disapih, lalu Abraham mengadakan\nperjamuan besar pada hari Ishak disapih\nitu.'),(523,1,21,9,'Pada waktu itu Sara melihat, bahwa\nanak yang dilahirkan Hagar, perempuan Mesir itu bagi Abraham, sedang main\ndengan Ishak, anaknya sendiri.'),(524,1,21,10,'Berkatalah Sara kepada Abraham:\n\"Usirlah hamba perempuan itu beserta\nanaknya, sebab anak hamba ini tidak\nakan menjadi ahli waris bersama-sama\ndengan anakku Ishak.\"'),(525,1,21,11,'Hal ini sangat menyebalkan Abraham\noleh karena anaknya itu.'),(526,1,21,12,'Tetapi Allah berfirman kepada\nAbraham: \"Janganlah sebal hatimu\nkarena hal anak dan budakmu itu; dalam\nsegala yang dikatakan Sara kepadamu,\nharuslah engkau mendengarkannya,\nsebab yang akan disebut keturunanmu\nialah yang berasal dari Ishak.'),(527,1,21,13,'Tetapi keturunan dari hambamu itu\njuga akan Kubuat menjadi suatu bangsa,\nkarena iapun anakmu.\"'),(528,1,21,14,'Keesokan harinya pagi-pagi Abraham\nmengambil roti serta sekirbat air dan\nmemberikannya kepada Hagar. Ia\nmeletakkan itu beserta anaknya di atas\nbahu Hagar, kemudian disuruhnyalah\nperempuan itu pergi. Maka pergilah\nHagar dan mengembara di padang gurun\nBersyeba.'),(529,1,21,15,'Ketika air yang dikirbat itu habis,\ndibuangnyalah anak itu ke bawah\nsemak-semak,'),(530,1,21,16,'dan ia duduk agak jauh, kira-kira\nsepemanah jauhnya, sebab katanya:\n\"Tidak tahan aku melihat anak itu mati.\"\nSedang ia duduk di situ, menangislah ia\ndengan suara nyaring.'),(531,1,21,17,'Allah mendengar suara anak itu,\nlalu Malaikat Allah berseru dari langit\nkepada Hagar, kata-Nya kepadanya:\n\"Apakah yang engkau susahkan, Hagar?\nJanganlah takut, sebab Allah telah\nmendengar suara anak itu dari tempat ia\nterbaring.'),(532,1,21,18,'Bangunlah, angkatlah anak itu,\ndan bimbinglah dia, sebab Aku akan\nmembuat dia menjadi bangsa yang\nbesar.\"'),(533,1,21,19,'Lalu Allah membuka mata Hagar,\nsehingga ia melihat sebuah sumur; ia\npergi mengisi kirbatnya dengan air,\nkemudian diberinya anak itu minum.'),(534,1,21,20,'Allah menyertai anak itu, sehingga ia\nbertambah besar; ia menetap di padang\ngurun dan menjadi seorang pemanah.'),(535,1,21,21,'Maka tinggallah ia di padang gurun\nParan, dan ibunya mengambil seorang\nisteri baginya dari tanah Mesir.'),(536,1,21,22,'Pada waktu itu Abimelekh, beserta\nPikhol, panglima tentaranya, berkata\nkepada Abraham: \"Allah menyertai\nengkau dalam segala sesuatu yang\nengkau lakukan.'),(537,1,21,23,'Oleh sebab itu, bersumpahlah\nkepadaku di sini demi Allah, bahwa\nengkau tidak akan berlaku curang\nkepadaku, atau kepada anak-anakku,\natau kepada cucu cicitku; sesuai\ndengan persahabatan yang kulakukan\nkepadamu, demikianlah harus engkau\nberlaku kepadaku dan kepada negeri\nyang kautinggali sebagai orang asing.\"'),(538,1,21,24,'Lalu kata Abraham: \"Aku\nbersumpah!\"'),(539,1,21,25,'Tetapi Abraham menyesali Abimelekh\ntentang sebuah sumur yang telah\ndirampas oleh hamba-hamba Abimelekh.'),(540,1,21,26,'Jawab Abimelekh: \"Aku tidak tahu,\nsiapa yang melakukan hal itu; lagi tidak\nkauberitahukan kepadaku, dan sampai\nhari ini belum pula kudengar.\"'),(541,1,21,27,'Lalu Abraham mengambil domba\ndan lembu dan memberikan semuanya itu kepada Abimelekh, kemudian kedua\norang itu mengadakan perjanjian.'),(542,1,21,28,'Tetapi Abraham memisahkan tujuh\nanak domba betina dari domba-domba\nitu.'),(543,1,21,29,'Lalu kata Abimelekh kepada\nAbraham: \"Untuk apakah ketujuh anak\ndomba yang kaupisahkan ini?\"'),(544,1,21,30,'Jawabnya: \"Ketujuh anak domba ini\nharus kauterima dari tanganku untuk\nmenjadi tanda bukti bagiku, bahwa\nakulah yang menggali sumur ini.\"'),(545,1,21,31,'Sebab itu orang menyebutkan tempat\nitu Bersyeba, karena kedua orang itu\ntelah bersumpah di sana.'),(546,1,21,32,'Setelah mereka mengadakan\nperjanjian di Bersyeba, pulanglah\nAbimelekh beserta Pikhol, panglima\ntentaranya, ke negeri orang Filistin.'),(547,1,21,33,'Lalu Abraham menanam sebatang\npohon tamariska di Bersyeba, dan\nmemanggil di sana nama TUHAN, Allah\nyang kekal.'),(548,1,21,34,'Dan masih lama Abraham tinggal\nsebagai orang asing di negeri orang\nFilistin.'),(549,1,22,1,'Setelah semuanya itu Allah mencoba Abraham. Ia berfirman kepadanya: \"Abraham,\" lalu sahutnya:\n\"Ya, Tuhan.\"'),(550,1,22,2,'Firman-Nya: \"Ambillah anakmu yang\ntunggal itu, yang engkau kasihi, yakni\nIshak, pergilah ke tanah Moria dan\npersembahkanlah dia di sana sebagai\nkorban bakaran pada salah satu gunung\nyang akan Kukatakan kepadamu.\"'),(551,1,22,3,'Keesokan harinya pagi-pagi bangunlah\nAbraham, ia memasang pelana\nkeledainya dan memanggil dua orang\nbujangnya beserta Ishak, anaknya;\nia membelah juga kayu untuk korban\nbakaran itu, lalu berangkatlah ia dan\npergi ke tempat yang dikatakan Allah\nkepadanya.'),(552,1,22,4,'Ketika pada hari ketiga Abraham\nmelayangkan pandangnya, kelihatanlah\nkepadanya tempat itu dari jauh.'),(553,1,22,5,'Kata Abraham kepada kedua\nbujangnya itu: \"Tinggallah kamu di\nsini dengan keledai ini; aku beserta\nanak ini akan pergi ke sana; kami akan\nsembahyang, sesudah itu kami kembali\nkepadamu.\"'),(554,1,22,6,'Lalu Abraham mengambil kayu untuk\nkorban bakaran itu dan memikulkannya\nke atas bahu Ishak, anaknya, sedang di tangannya dibawanya api dan\npisau. Demikianlah keduanya berjalan\nbersama-sama.'),(555,1,22,7,'Lalu berkatalah Ishak kepada\nAbraham, ayahnya: \"Bapa.\" Sahut\nAbraham: \"Ya, anakku.\" Bertanyalah ia:\n\"Di sini sudah ada api dan kayu, tetapi\ndi manakah anak domba untuk korban\nbakaran itu?\"'),(556,1,22,8,'Sahut Abraham: \"Allah yang akan\nmenyediakan anak domba untuk korban\nbakaran bagi-Nya, anakku.\" Demikianlah\nkeduanya berjalan bersama-sama.'),(557,1,22,9,'Sampailah mereka ke tempat yang\ndikatakan Allah kepadanya. Lalu\nAbraham mendirikan mezbah di situ,\ndisusunnyalah kayu, diikatnya Ishak,\nanaknya itu, dan diletakkannya di\nmezbah itu, di atas kayu api.'),(558,1,22,10,'Sesudah itu Abraham mengulurkan\ntangannya, lalu mengambil pisau untuk\nmenyembelih anaknya.'),(559,1,22,11,'Tetapi berserulah Malaikat TUHAN\ndari langit kepadanya: \"Abraham,\nAbraham.\" Sahutnya: \"Ya, Tuhan.\"'),(560,1,22,12,'Lalu Ia berfirman: \"Jangan bunuh\nanak itu dan jangan kauapa-apakan dia,\nsebab telah Kuketahui sekarang, bahwa engkau takut akan Allah, dan engkau\ntidak segan-segan untuk menyerahkan\nanakmu yang tunggal kepada-Ku.\"'),(561,1,22,13,'Lalu Abraham menoleh dan melihat\nseekor domba jantan di belakangnya,\nyang tanduknya tersangkut dalam\nbelukar. Abraham mengambil domba itu,\nlalu mengorbankannya sebagai korban\nbakaran pengganti anaknya.'),(562,1,22,14,'Dan Abraham menamai tempat\nitu: \"TUHAN menyediakan\"; sebab itu\nsampai sekarang dikatakan orang: \"Di\natas gunung TUHAN, akan disediakan.\"'),(563,1,22,15,'Untuk kedua kalinya berserulah\nMalaikat TUHAN dari langit kepada\nAbraham,'),(564,1,22,16,'kata-Nya: \"Aku bersumpah demi\ndiri-Ku sendiri--demikianlah firman\nTUHAN--:Karena engkau telah berbuat\ndemikian, dan engkau tidak segan-segan\nuntuk menyerahkan anakmu yang\ntunggal kepada-Ku,'),(565,1,22,17,'maka Aku akan memberkati engkau\nberlimpah-limpah dan membuat\nketurunanmu sangat banyak seperti\nbintang di langit dan seperti pasir di\ntepi laut, dan keturunanmu itu akan\nmenduduki kota-kota musuhnya.'),(566,1,22,18,'Oleh keturunanmulah semua bangsa\ndi bumi akan mendapat berkat, karena\nengkau mendengarkan firman-Ku.\"'),(567,1,22,19,'Kemudian kembalilah Abraham\nkepada kedua bujangnya, dan mereka\nbersama-sama berangkat ke Bersyeba;\ndan Abraham tinggal di Bersyeba.'),(568,1,22,20,'Sesudah itu Abraham mendapat\nkabar: \"Juga Milka telah melahirkan\nanak-anak lelaki bagi Nahor, saudaramu:'),(569,1,22,21,'Us, anak sulung, dan Bus, adiknya,\ndan Kemuel, ayah Aram,'),(570,1,22,22,'juga Kesed, Hazo, Pildash, Yidlaf dan\nBetuel.\"'),(571,1,22,23,'Dan Betuel memperanakkan Ribka.\nKedelapan orang inilah dilahirkan Milka\nbagi Nahor, saudara Abraham itu.'),(572,1,22,24,'Dan gundik Nahor, yang namanya\nReuma, melahirkan anak juga, yakni\nTebah, Gaham, Tahash dan Maakha.'),(573,1,23,1,'Sara hidup seratus dua puluh tujuh tujuh\ntahun lamanya; itulah umur Sara.'),(574,1,23,2,'Kemudian matilah Sara di KiryatArba, yaitu Hebron, di tanah Kanaan,\nlalu Abraham datang meratapi dan\nmenangisinya.'),(575,1,23,3,'Sesudah itu Abraham bangkit dan\nmeninggalkan isterinya yang mati itu,\nlalu berkata kepada bani Het:'),(576,1,23,4,'\"Aku ini orang asing dan pendatang\ndi antara kamu; berikanlah kiranya\nkuburan milik kepadaku di tanah\nkamu ini, supaya kiranya aku dapat\nmengantarkan dan menguburkan\nisteriku yang mati itu.\"'),(577,1,23,5,'Bani Het menjawab Abraham:'),(578,1,23,6,'\"Dengarlah kepada kami, tuanku.\nTuanku ini seorang raja agung di\ntengah-tengah kami; jadi kuburkanlah\nisterimu yang mati itu dalam kuburan\nkami yang terpilih, tidak akan ada\nseorangpun dari kami yang menolak\nmenyediakan kuburannya bagimu untuk\nmenguburkan isterimu yang mati itu.\"'),(579,1,23,7,'Kemudian bangunlah Abraham lalu\nsujud kepada bani Het, penduduk negeri\nitu,'),(580,1,23,8,'serta berkata kepada mereka: \"Jika\nkamu setuju, bahwa aku mengantarkan\ndan menguburkan isteriku yang mati itu,\nmaka dengarkanlah aku dan tolonglah\nmintakan dengan sangat kepada Efron\nbin Zohar,'),(581,1,23,9,'supaya ia memberikan kepadaku\ngua Makhpela miliknya itu, yang\nterletak di ujung ladangnya; baiklah itu\ndiberikannya kepadaku dengan harga\npenuh untuk menjadi kuburan milikku di\ntengah-tengah kamu.\"'),(582,1,23,10,'Pada waktu itu Efron hadir di\ntengah-tengah bani Het. Maka jawab\nEfron, orang Het itu, kepada Abraham\ndengan didengar oleh bani Het, oleh\nsemua orang yang datang di pintu\ngerbang kota:'),(583,1,23,11,'\"Tidak, tuanku, dengarkanlah aku;\nladang itu kuberikan kepadamu dan gua\nyang di sanapun kuberikan kepadamu;\ndi depan mata orang-orang sebangsaku\nkuberikan itu kepadamu; kuburkanlah\nisterimu yang mati itu.\"'),(584,1,23,12,'Lalu sujudlah Abraham di depan\npenduduk negeri itu'),(585,1,23,13,'serta berkata kepada Efron dengan\ndidengar oleh mereka: \"Sesungguhnya,\njika engkau suka, dengarkanlah aku: aku\nmembayar harga ladang itu; terimalah\nitu dari padaku, supaya aku dapat\nmenguburkan isteriku yang mati itu di\nsana.\"'),(586,1,23,14,'Jawab Efron kepada Abraham:'),(587,1,23,15,'\"Tuanku, dengarkanlah aku: sebidang\ntanah dengan harga empat ratus syikal\nperak, apa artinya itu bagi kita?\nKuburkan sajalah isterimu yang mati\nitu.\"'),(588,1,23,16,'Lalu Abraham menerima usul Efron,\nmaka ditimbangnyalah perak untuk\nEfron, sebanyak yang dimintanya dengan\ndidengar oleh bani Het itu, empat ratus\nsyikal perak, seperti yang berlaku di\nantara para saudagar.'),(589,1,23,17,'Demikianlah ladang Efron, yang\nletaknya di Makhpela di sebelah timur\nMamre, ladang dan gua yang di sana,\nserta segala pohon di ladang itu,\nbahkan di seluruh tanah itu sampai ke\ntepi-tepinya,'),(590,1,23,18,'diserahkan kepada Abraham menjadi\ntanah belian, di depan mata bani Het itu,\ndi depan semua orang yang datang di\npintu gerbang kota.'),(591,1,23,19,'Sesudah itu Abraham menguburkan\nSara, isterinya, di dalam gua ladang\nMakhpela itu, di sebelah timur Mamre,\nyaitu Hebron di tanah Kanaan.'),(592,1,23,20,'Demikianlah dari pihak bani Het\nladang dengan gua yang ada di sana diserahkan kepada Abraham menjadi\nkuburan miliknya.'),(593,1,24,1,'Adapun Abraham telah tua dan lanjut umurnya, serta diberkati lanjut umurnya, serta diberkati TUHAN dalam segala hal.'),(594,1,24,2,'Berkatalah Abraham kepada\nhambanya yang paling tua dalam\nrumahnya, yang menjadi kuasa atas\nsegala kepunyaannya, katanya: \"Baiklah\nletakkan tanganmu di bawah pangkal\npahaku,'),(595,1,24,3,'supaya aku mengambil sumpahmu\ndemi TUHAN, Allah yang empunya langit\ndan yang empunya bumi, bahwa engkau\ntidak akan mengambil untuk anakku\nseorang isteri dari antara perempuan\nKanaan yang di antaranya aku diam.'),(596,1,24,4,'Tetapi engkau harus pergi ke negeriku\ndan kepada sanak saudaraku untuk\nmengambil seorang isteri bagi Ishak,\nanakku.\"'),(597,1,24,5,'Lalu berkatalah hambanya itu\nkepadanya: \"Mungkin perempuan itu\ntidak suka mengikuti aku ke negeri ini;\nharuskah aku membawa anakmu itu\nkembali ke negeri dari mana tuanku\nkeluar?\"'),(598,1,24,6,'Tetapi Abraham berkata kepadanya:\n\"Awas, jangan kaubawa anakku itu\nkembali ke sana.'),(599,1,24,7,'TUHAN, Allah yang empunya\nlangit, yang telah memanggil aku\ndari rumah ayahku serta dari negeri\nsanak saudaraku, dan yang telah\nberfirman kepadaku, serta yang\nbersumpah kepadaku, demikian:\nkepada keturunanmulah akan Kuberikan\nnegeri ini--Dialah juga akan mengutus\nmalaikat-Nya berjalan di depanmu,\nsehingga engkau dapat mengambil\nseorang isteri dari sana untuk anakku.'),(600,1,24,8,'Tetapi jika perempuan itu tidak\nmau mengikuti engkau, maka lepaslah\nengkau dari sumpahmu kepadaku\nini; hanya saja, janganlah anakku itu\nkaubawa kembali ke sana.\"'),(601,1,24,9,'Lalu hamba itu meletakkan tangannya\ndi bawah pangkal paha Abraham,\ntuannya, dan bersumpah kepadanya\ntentang hal itu.'),(602,1,24,10,'Kemudian hamba itu mengambil\nsepuluh ekor dari unta tuannya dan\npergi dengan membawa berbagai-bagai\nbarang berharga kepunyaan tuannya; demikianlah ia berangkat menuju\nAram-Mesopotamia ke kota Nahor.'),(603,1,24,11,'Di sana disuruhnyalah unta itu\nberhenti di luar kota dekat suatu\nsumur, pada waktu petang hari, waktu\nperempuan-perempuan keluar untuk\nmenimba air.'),(604,1,24,12,'Lalu berkatalah ia: \"TUHAN, Allah\ntuanku Abraham, buatlah kiranya\ntercapai tujuanku pada hari ini,\ntunjukkanlah kasih setia-Mu kepada\ntuanku Abraham.'),(605,1,24,13,'Di sini aku berdiri di dekat mata air,\ndan anak-anak perempuan penduduk\nkota ini datang keluar untuk menimba\nair.'),(606,1,24,14,'Kiranya terjadilah begini: anak\ngadis, kepada siapa aku berkata:\nTolong miringkan buyungmu itu, supaya\naku minum, dan yang menjawab:\nMinumlah, dan unta-untamu juga akan\nkuberi minum--dialah kiranya yang\nKautentukan bagi hamba-Mu, Ishak;\nmaka dengan begitu akan kuketahui,\nbahwa Engkau telah menunjukkan kasih\nsetia-Mu kepada tuanku itu.\"'),(607,1,24,15,'Sebelum ia selesai berkata, maka\ndatanglah Ribka, yang lahir bagi Betuel, anak laki-laki Milka, isteri Nahor, saudara\nAbraham; buyungnya dibawanya di atas\nbahunya.'),(608,1,24,16,'Anak gadis itu sangat cantik\nparasnya, seorang perawan, belum\npernah bersetubuh dengan laki-laki;\nia turun ke mata air itu dan mengisi\nbuyungnya, lalu kembali naik.'),(609,1,24,17,'Kemudian berlarilah hamba itu\nmendapatkannya serta berkata:\n\"Tolong beri aku minum air sedikit dari\nbuyungmu itu.\"'),(610,1,24,18,'Jawabnya: \"Minumlah, tuan,\" maka\nsegeralah diturunkannya buyungnya\nitu ke tangannya, serta diberinya dia\nminum.'),(611,1,24,19,'Setelah ia selesai memberi hamba itu\nminum, berkatalah ia: \"Baiklah untuk\nunta-untamu juga kutimba air, sampai\nsemuanya puas minum.\"'),(612,1,24,20,'Kemudian segeralah dituangnya\nair yang di buyungnya itu ke dalam\npalungan, lalu berlarilah ia sekali lagi\nke sumur untuk menimba air dan\nditimbanyalah untuk semua unta orang\nitu.'),(613,1,24,21,'Dan orang itu mengamat-amatinya\ndengan berdiam diri untuk mengetahui apakah TUHAN membuat perjalanannya\nberhasil atau tidak.'),(614,1,24,22,'Setelah unta-unta itu puas minum,\nmaka orang itu mengambil anting-anting\nemas yang setengah syikal beratnya,\ndan sepasang gelang tangan yang\nsepuluh syikal emas beratnya,'),(615,1,24,23,'serta berkata: \"Anak siapakah\nengkau? Baiklah katakan kepadaku!\nAdakah di rumah ayahmu tempat\nbermalam bagi kami?\"'),(616,1,24,24,'Lalu jawabnya kepadanya: \"Ayahku\nBetuel, anak Milka, yang melahirkannya\nbagi Nahor.\"'),(617,1,24,25,'Lagi kata gadis itu: \"Baik jerami,\nbaik makanan unta banyak pada kami,\ntempat bermalampun ada.\"'),(618,1,24,26,'Lalu berlututlah orang itu dan sujud\nmenyembah TUHAN,'),(619,1,24,27,'serta berkata: \"Terpujilah TUHAN,\nAllah tuanku Abraham, yang tidak\nmenarik kembali kasih-Nya dan setiaNya dari tuanku itu; dan TUHAN telah\nmenuntun aku di jalan ke rumah\nsaudara-saudara tuanku ini!\"'),(620,1,24,28,'Berlarilah gadis itu pergi\nmenceritakan kejadian itu ke rumah\nibunya.'),(621,1,24,29,'Ribka mempunyai saudara laki-laki,\nnamanya Laban. Laban berlari ke luar\nmendapatkan orang itu, ke mata air tadi,'),(622,1,24,30,'sesudah dilihatnya anting-anting itu\ndan gelang pada tangan saudaranya, dan\nsesudah didengarnya perkataan Ribka,\nsaudaranya, yang bunyinya: \"Begitulah\ndikatakan orang itu kepadaku.\" Ia\nmendapatkan orang itu, yang masih\nberdiri di samping unta-untanya di dekat\nmata air itu,'),(623,1,24,31,'dan berkata: \"Marilah engkau yang\ndiberkati TUHAN, mengapa engkau\nberdiri di luar, padahal telah kusediakan\nrumah bagimu, dan juga tempat untuk\nunta-untamu.\"'),(624,1,24,32,'Masuklah orang itu ke dalam rumah.\nDitanggalkanlah pelana unta-unta,\ndiberikan jerami dan makanan kepada\nunta-unta itu, lalu dibawa air pembasuh\nkaki untuk orang itu dan orang-orang\nyang bersama-sama dengan dia.'),(625,1,24,33,'Masuklah orang itu ke dalam rumah.\nDitanggalkanlah pelana unta-unta,\ndiberikan jerami dan makanan kepada\nunta-unta itu, lalu dibawa air pembasuh\nkaki untuk orang itu dan orang-orang\nyang bersama-sama dengan dia.'),(626,1,24,34,'Lalu berkatalah ia: \"Aku ini hamba\nAbraham.'),(627,1,24,35,'TUHAN sangat memberkati tuanku\nitu, sehingga ia telah menjadi kaya;\nTUHAN telah memberikan kepadanya\nkambing domba dan lembu sapi,\nemas dan perak, budak laki-laki dan\nperempuan, unta dan keledai.'),(628,1,24,36,'Dan Sara, isteri tuanku itu, sesudah\ntua, telah melahirkan anak laki-laki bagi\ntuanku itu; kepada anaknya itu telah\ndiberikan tuanku segala harta miliknya.'),(629,1,24,37,'Tuanku itu telah mengambil\nsumpahku: Engkau tidak akan\nmengambil untuk anakku seorang isteri\ndari antara perempuan Kanaan, yang\nnegerinya kudiami ini,'),(630,1,24,38,'tetapi engkau harus pergi ke rumah\nayahku dan kepada kaumku untuk\nmengambil seorang isteri bagi anakku.'),(631,1,24,39,'Jawabku kepada tuanku itu: Mungkin\nperempuan itu tidak mau mengikut aku.'),(632,1,24,40,'Tetapi katanya kepadaku: TUHAN,\nyang di hadapan-Nya aku hidup, akan\nmengutus malaikat-Nya menyertai\nengkau, dan akan membuat\nperjalananmu berhasil, sehingga\nengkau akan mengambil bagi anakku seorang isteri dari kaumku dan dari\nrumah ayahku.'),(633,1,24,41,'Barulah engkau lepas dari sumpahmu\nkepadaku, jika engkau sampai kepada\nkaumku dan mereka tidak memberikan\nperempuan itu kepadamu; hanya dalam\nhal itulah engkau lepas dari sumpahmu\nkepadaku.'),(634,1,24,42,'Dan hari ini aku sampai ke mata\nair tadi, lalu kataku: TUHAN, Allah\ntuanku Abraham, sudilah kiranya Engkau\nmembuat berhasil perjalanan yang\nkutempuh ini.'),(635,1,24,43,'Di sini aku berdiri di dekat\nmata air ini; kiranya terjadi begini:\nApabila seorang gadis datang ke luar\nuntuk menimba air dan aku berkata\nkepadanya: Tolong berikan aku minum\nair sedikit dari buyungmu itu,'),(636,1,24,44,'dan ia menjawab: Minumlah, dan\nuntuk unta-untamu juga akan kutimba\nair, --dialah kiranya isteri, yang telah\nTUHAN tentukan bagi anak tuanku itu.'),(637,1,24,45,'Belum lagi aku habis berkata dalam\nhatiku, Ribka telah datang membawa\nbuyung di atas bahunya, dan turun ke\nmata air itu, lalu menimba air. Kataku\nkepadanya: Tolong berikan aku minum.'),(638,1,24,46,'Segeralah ia menurunkan buyung\nitu dari atas bahunya serta berkata:\nMinumlah, dan unta-untamu juga akan\nkuberi minum. Lalu aku minum, dan\nunta-unta itu juga diberinya minum.'),(639,1,24,47,'Sesudah itu aku bertanya kepadanya:\nAnak siapakah engkau? Jawabnya:\nAyahku Betuel anak Nahor yang\ndilahirkan Milka. Lalu aku mengenakan\nanting-anting pada hidungnya dan\ngelang pada tangannya.'),(640,1,24,48,'Kemudian berlututlah aku dan sujud\nmenyembah TUHAN, serta memuji\nTUHAN, Allah tuanku Abraham, yang\ntelah menuntun aku di jalan yang benar\nuntuk mengambil anak perempuan\nsaudara tuanku ini bagi anaknya.'),(641,1,24,49,'Jadi sekarang, apabila kamu mau\nmenunjukkan kasih dan setia kepada\ntuanku itu, beritahukanlah kepadaku;\ndan jika tidak, beritahukanlah juga\nkepadaku, supaya aku tahu entah\nberpaling ke kanan atau ke kiri.\"'),(642,1,24,50,'Lalu Laban dan Betuel menjawab:\n\"Semuanya ini datangnya dari TUHAN;\nkami tidak dapat mengatakan kepadamu\nbaiknya atau buruknya.'),(643,1,24,51,'Lihat, Ribka ada di depanmu, bawalah\ndia dan pergilah, supaya ia menjadi isteri\nanak tuanmu, seperti yang difirmankan\nTUHAN.\"'),(644,1,24,52,'Ketika hamba Abraham itu\nmendengar perkataan mereka, sujudlah\nia sampai ke tanah menyembah TUHAN.'),(645,1,24,53,'Kemudian hamba itu mengeluarkan\nperhiasan emas dan perak serta pakaian\nkebesaran, dan memberikan semua itu\nkepada Ribka; juga kepada saudaranya\ndan kepada ibunya diberikannya\npemberian yang indah-indah.'),(646,1,24,54,'Sesudah itu makan dan minumlah\nmereka, ia dan orang-orang yang\nbersama-sama dengan dia, dan mereka\nbermalam di situ. Paginya sesudah\nmereka bangun, berkatalah hamba itu:\n\"Lepaslah aku pulang kepada tuanku.\"'),(647,1,24,55,'Tetapi saudara Ribka berkata, serta\nibunya juga: \"Biarkanlah anak gadis itu\ntinggal pada kami barang sepuluh hari\nlagi, kemudian bolehlah engkau pergi.\"'),(648,1,24,56,'Tetapi jawabnya kepada mereka:\n\"Janganlah tahan aku, sedang TUHAN\ntelah membuat perjalananku berhasil;\nlepaslah aku, supaya aku pulang kepada\ntuanku.\"'),(649,1,24,57,'Kata mereka: \"Baiklah kita panggil\nanak gadis itu dan menanyakan\nkepadanya sendiri.\"'),(650,1,24,58,'Lalu mereka memanggil Ribka dan\nberkata kepadanya: \"Maukah engkau\npergi beserta orang ini?\" Jawabnya:\n\"Mau.\"'),(651,1,24,59,'Maka Ribka, saudara mereka itu,\ndan inang pengasuhnya beserta hamba\nAbraham dan orang-orangnya dibiarkan\nmereka pergi'),(652,1,24,60,'Dan mereka memberkati Ribka, kata\nmereka kepadanya: \"Saudara kami,\nmoga-moga engkau menjadi beribu-ribu\nlaksa, dan moga-moga keturunanmu\nmenduduki kota-kota musuhnya.\"'),(653,1,24,61,'Lalu berkemaslah Ribka beserta\nhamba-hambanya perempuan, dan\nmereka naik unta mengikuti orang itu.\nDemikianlah hamba itu membawa Ribka\nlalu berjalan pulang.'),(654,1,24,62,'Adapun Ishak telah datang dari arah\nsumur Lahai-Roi; ia tinggal di Tanah\nNegeb.'),(655,1,24,63,'Menjelang senja Ishak sedang\nkeluar untuk berjalan-jalan di padang.\nIa melayangkan pandangnya, maka\ndilihatnyalah ada unta-unta datang.'),(656,1,24,64,'Ribka juga melayangkan pandangnya\ndan ketika dilihatnya Ishak, turunlah ia\ndari untanya.'),(657,1,24,65,'Katanya kepada hamba itu: \"Siapakah\nlaki-laki itu yang berjalan di padang ke\narah kita?\" Jawab hamba itu: \"Dialah\ntuanku itu.\" Lalu Ribka mengambil\ntelekungnya dan bertelekunglah ia.'),(658,1,24,66,'Kemudian hamba itu menceritakan\nkepada Ishak segala yang dilakukannya.'),(659,1,24,67,'Lalu Ishak membawa Ribka ke dalam\nkemah Sara, ibunya, dan mengambil dia\nmenjadi isterinya. Ishak mencintainya\ndan demikian ia dihiburkan setelah\nibunya meninggal.'),(660,1,25,1,'Abraham mengambil pula seorang isteri, namanya Ketura.'),(661,1,25,2,'Perempuan itu melahirkan baginya\nZimran, Yoksan, Medan, Midian, Isybak\ndan Suah.'),(662,1,25,3,'Yoksan memperanakkan Syeba dan\nDedan. Keturunan Dedan ialah orang\nAsyur, orang Letush dan orang Leum.'),(663,1,25,4,'Anak-anak Midian ialah Efa, Efer,\nHenokh, Abida dan Eldaa. Itulah\nsemuanya keturunan Ketura.'),(664,1,25,5,'Abraham memberikan segala harta\nmiliknya kepada Ishak,'),(665,1,25,6,'tetapi kepada anak-anaknya yang\ndiperolehnya dari gundik-gundiknya ia\nmemberikan pemberian; kemudian ia\nmenyuruh mereka--masih pada waktu\nia hidup--meninggalkan Ishak, anaknya,\ndan pergi ke sebelah timur, ke Tanah\nTimur.'),(666,1,25,7,'Abraham mencapai umur seratus\ntujuh puluh lima tahun,'),(667,1,25,8,'lalu ia meninggal. Ia mati pada waktu\ntelah putih rambutnya, tua dan suntuk\numur, maka ia dikumpulkan kepada\nkaum leluhurnya.'),(668,1,25,9,'Dan anak-anaknya, Ishak dan Ismael,\nmenguburkan dia dalam gua Makhpela,\ndi padang Efron bin Zohar, orang Het itu,\npadang yang letaknya di sebelah timur\nMamre,'),(669,1,25,10,'yang telah dibeli Abraham dari bani\nHet; di sanalah terkubur Abraham dan\nSara isterinya.'),(670,1,25,11,'Setelah Abraham mati, Allah\nmemberkati Ishak, anaknya itu; dan\nIshak diam dekat sumur Lahai-Roi.'),(671,1,25,12,'Inilah keturunan Ismael, anak\nAbraham, yang telah dilahirkan baginya\noleh Hagar, perempuan Mesir, hamba\nSara itu'),(672,1,25,13,'Inilah nama anak-anak Ismael,\ndisebutkan menurut urutan lahirnya:\nNebayot, anak sulung Ismael,\nselanjutnya Kedar, Adbeel, Mibsam,'),(673,1,25,14,'Misyma, Duma, Masa,'),(674,1,25,15,'Hadad, Tema, Yetur, Nafish dan\nKedma.'),(675,1,25,16,'Itulah anak-anak Ismael, dan\nitulah nama-nama mereka, menurut\nkampung mereka dan menurut\nperkemahan mereka, dua belas orang\nraja, masing-masing dengan sukunya.'),(676,1,25,17,'Umur Ismael ialah seratus tiga puluh\ntujuh tahun. Sesudah itu ia meninggal.\nIa mati dan dikumpulkan kepada kaum\nleluhurnya.'),(677,1,25,18,'Mereka itu mendiami daerah dari\nHawila sampai Syur, yang letaknya\ndi sebelah timur Mesir ke arah Asyur.\nMereka menetap berhadapan dengan\nsemua saudara mereka.'),(678,1,25,19,'Inilah riwayat keturunan Ishak, anak\nAbraham. Abraham memperanakkan\nIshak.'),(679,1,25,20,'Dan Ishak berumur empat puluh\ntahun, ketika Ribka, anak Betuel,\norang Aram dari Padan-Aram, saudara perempuan Laban orang Aram itu,\ndiambilnya menjadi isterinya.'),(680,1,25,21,'Berdoalah Ishak kepada TUHAN untuk\nisterinya, sebab isterinya itu mandul;\nTUHAN mengabulkan doanya, sehingga\nRibka, isterinya itu, mengandung'),(681,1,25,22,'Tetapi anak-anaknya bertolak-tolakan\ndi dalam rahimnya dan ia berkata: \"Jika\ndemikian halnya, mengapa aku hidup?\"\nDan ia pergi meminta petunjuk kepada\nTUHAN.'),(682,1,25,23,'Firman TUHAN kepadanya: \"Dua\nbangsa ada dalam kandunganmu, dan\ndua suku bangsa akan berpencar dari\ndalam rahimmu; suku bangsa yang satu\nakan lebih kuat dari yang lain, dan anak\nyang tua akan menjadi hamba kepada\nanak yang muda.\"'),(683,1,25,24,'Setelah genap harinya untuk bersalin,\nmemang anak kembar yang di dalam\nkandungannya.'),(684,1,25,25,'Keluarlah yang pertama, warnanya\nmerah, seluruh tubuhnya seperti jubah\nberbulu; sebab itu ia dinamai Esau.'),(685,1,25,26,'Sesudah itu keluarlah adiknya;\ntangannya memegang tumit Esau, sebab\nitu ia dinamai Yakub. Ishak berumur enam puluh tahun pada waktu mereka\nlahir.'),(686,1,25,27,'Lalu bertambah besarlah kedua anak\nitu: Esau menjadi seorang yang pandai\nberburu, seorang yang suka tinggal di\npadang, tetapi Yakub adalah seorang\nyang tenang, yang suka tinggal di\nkemah.'),(687,1,25,28,'Ishak sayang kepada Esau, sebab ia\nsuka makan daging buruan, tetapi Ribka\nkasih kepada Yakub.'),(688,1,25,29,'Pada suatu kali Yakub sedang\nmemasak sesuatu, lalu datanglah Esau\ndengan lelah dari padang.'),(689,1,25,30,'Kata Esau kepada Yakub: \"Berikanlah\nkiranya aku menghirup sedikit dari yang\nmerah-merah itu, karena aku lelah.\"\nItulah sebabnya namanya disebutkan\nEdom.'),(690,1,25,31,'Tetapi kata Yakub: \"Juallah dahulu\nkepadaku hak kesulunganmu.\"'),(691,1,25,32,'Sahut Esau: \"Sebentar lagi aku\nakan mati; apakah gunanya bagiku hak\nkesulungan itu?\"'),(692,1,25,33,'Kata Yakub: \"Bersumpahlah dahulu\nkepadaku.\" Maka bersumpahlah ia\nkepada Yakub dan dijualnyalah hak\nkesulungannya kepadanya.'),(693,1,25,34,'Lalu Yakub memberikan roti dan\nmasakan kacang merah itu kepada Esau;\nia makan dan minum, lalu berdiri dan\npergi. Demikianlah Esau memandang\nringan hak kesulungan itu.'),(694,1,26,1,'Maka timbullah kelaparan di negeri itu. --Ini bukan kelaparan yang pertama, yang telah terjadi dalam\nzaman Abraham. Sebab itu Ishak pergi\nke Gerar, kepada Abimelekh, raja orang\nFilistin.'),(695,1,26,2,'Lalu TUHAN menampakkan diri\nkepadanya serta berfirman: \"Janganlah\npergi ke Mesir, diamlah di negeri yang\nakan Kukatakan kepadamu.'),(696,1,26,3,'Tinggallah di negeri ini sebagai\norang asing, maka Aku akan menyertai\nengkau dan memberkati engkau, sebab\nkepadamulah dan kepada keturunanmu\nakan Kuberikan seluruh negeri ini, dan\nAku akan menepati sumpah yang telah\nKuikrarkan kepada Abraham, ayahmu.'),(697,1,26,4,'Aku akan membuat banyak\nketurunanmu seperti bintang di\nlangit; Aku akan memberikan kepada\nketurunanmu seluruh negeri ini, dan\noleh keturunanmu semua bangsa di\nbumi akan mendapat berkat,'),(698,1,26,5,'karena Abraham telah mendengarkan\nfirman-Ku dan memelihara kewajibannya\nkepada-Ku, yaitu segala perintah,\nketetapan dan hukum-Ku.\"'),(699,1,26,6,'Jadi tinggallah Ishak di Gerar.'),(700,1,26,7,'Ketika orang-orang di tempat itu\nbertanya tentang isterinya, berkatalah\nia: \"Dia saudaraku,\" sebab ia takut\nmengatakan: \"Ia isteriku,\" karena\npikirnya: \"Jangan-jangan aku dibunuh\noleh penduduk tempat ini karena Ribka,\nsebab elok parasnya.\"'),(701,1,26,8,'Setelah beberapa lama ia ada di\nsana, pada suatu kali menjenguklah\nAbimelekh, raja orang Filistin itu dari\njendela, maka dilihatnya Ishak sedang\nbercumbu-cumbuan dengan Ribka,\nisterinya.'),(702,1,26,9,'Lalu Abimelekh memanggil Ishak\ndan berkata: \"Sesungguhnya dia\nisterimu, masakan engkau berkata: Dia\nsaudaraku?\" Jawab Ishak kepadanya:\n\"Karena pikirku: Jangan-jangan aku mati\nkarena dia.'),(703,1,26,10,'Tetapi Abimelekh berkata: \"Apakah\njuga yang telah kauperbuat ini terhadap\nkami? Mudah sekali terjadi, salah\nseorang dari bangsa ini tidur dengan isterimu, sehingga dengan demikian\nengkau mendatangkan kesalahan atas\nkami.\"'),(704,1,26,11,'Lalu Abimelekh memberi perintah\nkepada seluruh bangsa itu: \"Siapa yang\nmengganggu orang ini atau isterinya,\npastilah ia akan dihukum mati.\"'),(705,1,26,12,'Maka menaburlah Ishak di tanah itu\ndan dalam tahun itu juga ia mendapat\nhasil seratus kali lipat; sebab ia diberkati\nTUHAN.'),(706,1,26,13,'Dan orang itu menjadi kaya, bahkan\nkian lama kian kaya, sehingga ia menjadi\nsangat kaya.'),(707,1,26,14,'Ia mempunyai kumpulan kambing\ndomba dan lembu sapi serta banyak\nanak buah, sehingga orang Filistin itu\ncemburu kepadanya.'),(708,1,26,15,'Segala sumur, yang digali dalam\nzaman Abraham, ayahnya, oleh hambahamba ayahnya itu, telah ditutup oleh\norang Filistin dan ditimbun dengan\ntanah.'),(709,1,26,16,'Lalu kata Abimelekh kepada Ishak:\n\"Pergilah dari tengah-tengah kami\nsebab engkau telah menjadi jauh lebih\nberkuasa dari pada kami.\"'),(710,1,26,17,'Jadi pergilah Ishak dari situ dan\nberkemahlah ia di lembah Gerar, dan ia\nmenetap di situ.'),(711,1,26,18,'Kemudian Ishak menggali kembali\nsumur-sumur yang digali dalam zaman\nAbraham, ayahnya, dan yang telah\nditutup oleh orang Filistin sesudah\nAbraham mati; disebutkannyalah nama\nsumur-sumur itu menurut nama-nama\nyang telah diberikan oleh ayahnya.'),(712,1,26,19,'Ketika hamba-hamba Ishak menggali\ndi lembah itu, mereka mendapati di situ\nmata air yang berbual-bual airnya.'),(713,1,26,20,'Lalu bertengkarlah para gembala\nGerar dengan para gembala Ishak. Kata\nmereka: \"Air ini kepunyaan kami.\" Dan\nIshak menamai sumur itu Esek, karena\nmereka bertengkar dengan dia di sana.'),(714,1,26,21,'Kemudian mereka menggali sumur\nlain, dan mereka bertengkar juga\ntentang itu. Maka Ishak menamai sumur\nitu Sitna'),(715,1,26,22,'Ia pindah dari situ dan menggali\nsumur yang lain lagi, tetapi tentang\nsumur ini mereka tidak bertengkar.\nSumur ini dinamainya Rehobot, dan\nia berkata: \"Sekarang TUHAN telah\nmemberikan kelonggaran kepada kita, sehingga kita dapat beranak cucu di\nnegeri ini.\"'),(716,1,26,23,'Dari situ ia pergi ke Bersyeba.'),(717,1,26,24,'Lalu pada malam itu TUHAN\nmenampakkan diri kepadanya serta\nberfirman: \"Akulah Allah ayahmu\nAbraham; janganlah takut, sebab\nAku menyertai engkau; Aku akan\nmemberkati engkau dan membuat\nbanyak keturunanmu karena Abraham,\nhamba-Ku itu.\"'),(718,1,26,25,'Sesudah itu Ishak mendirikan\nmezbah di situ dan memanggil nama\nTUHAN. Ia memasang kemahnya di situ,\nlalu hamba-hambanya menggali sumur\ndi situ.'),(719,1,26,26,'Datanglah Abimelekh dari Gerar\nmendapatkannya, bersama-sama\ndengan Ahuzat, sahabatnya, dan Pikhol,\nkepala pasukannya.'),(720,1,26,27,'Tetapi kata Ishak kepada mereka:\n\"Mengapa kamu datang mendapatkan\naku? Bukankah kamu benci kepadaku,\ndan telah menyuruh aku keluar dari\ntanahmu?\"'),(721,1,26,28,'awab mereka: \"Kami telah melihat\nsendiri, bahwa TUHAN menyertai\nengkau; sebab itu kami berkata: baiklah  kita mengadakan sumpah setia, antara\nkami dan engkau; dan baiklah kami\nmengikat perjanjian dengan engkau,'),(722,1,26,29,'bahwa engkau tidak akan berbuat\njahat kepada kami, seperti kami tidak\nmengganggu engkau, dan seperti kami\nsemata-mata berbuat baik kepadamu\ndan membiarkan engkau pergi dengan\ndamai; bukankah engkau sekarang yang\ndiberkati TUHAN.\"'),(723,1,26,30,'Kemudian Ishak mengadakan\nperjamuan bagi mereka, lalu mereka\nmakan dan minum.'),(724,1,26,31,'Keesokan harinya pagi-pagi\nbersumpah-sumpahanlah mereka.\nKemudian Ishak melepas mereka,\ndan mereka meninggalkan dia dengan\ndamai.'),(725,1,26,32,'Pada hari itu datanglah hambahamba Ishak memberitahukan\nkepadanya tentang sumur yang telah\ndigali mereka, serta berkata kepadanya:\n\"Kami telah mendapat air.\"'),(726,1,26,33,'Lalu dinamainyalah sumur itu\nSyeba. Sebab itu nama kota itu adalah\nBersyeba, sampai sekarang.'),(727,1,26,34,'Ketika Esau telah berumur empat\npuluh tahun, ia mengambil Yudit, anak Beeri orang Het, dan Basmat, anak Elon\norang Het, menjadi isterinya.'),(728,1,26,35,'Kedua perempuan itu menimbulkan\nkepedihan hati bagi Ishak dan bagi\nRibka.'),(729,1,27,1,'Ketika Ishak sudah tua, dan matanya telah kabur, sehingga ia tidak dapat melihat lagi, dipanggilnyalah\nEsau, anak sulungnya, serta berkata\nkepadanya: \"Anakku.\" Sahut Esau: \"Ya,\nbapa.\"'),(730,1,27,2,'Berkatalah Ishak: \"Lihat, aku sudah\ntua, aku tidak tahu bila hari kematianku.'),(731,1,27,3,'Maka sekarang, ambillah senjatamu,\ntabung panah dan busurmu, pergilah\nke padang dan burulah bagiku seekor\nbinatang;'),(732,1,27,4,'olahlah bagiku makanan yang enak,\nseperti yang kugemari, sesudah itu\nbawalah kepadaku, supaya kumakan,\nagar aku memberkati engkau, sebelum\naku mati.\"'),(733,1,27,5,'Tetapi Ribka mendengarkannya, ketika\nIshak berkata kepada Esau, anaknya.\nSetelah Esau pergi ke padang memburu\nseekor binatang untuk dibawanya\nkepada ayahnya,'),(734,1,27,6,'berkatalah Ribka kepada Yakub,\nanaknya: \"Telah kudengar ayahmu\nberkata kepada Esau, kakakmu:'),(735,1,27,7,'Bawalah bagiku seekor binatang\nburuan dan olahlah bagiku makanan\nyang enak, supaya kumakan, dan supaya\naku memberkati engkau di hadapan\nTUHAN, sebelum aku mati.'),(736,1,27,8,'Maka sekarang, anakku, dengarkanlah\nperkataanku seperti yang kuperintahkan\nkepadamu.'),(737,1,27,9,'Pergilah ke tempat kambing domba\nkita, ambillah dari sana dua anak\nkambing yang baik, maka aku akan\nmengolahnya menjadi makanan yang\nenak bagi ayahmu, seperti yang\ndigemarinya.'),(738,1,27,10,'Bawalah itu kepada ayahmu, supaya\ndimakannya, agar dia memberkati\nengkau, sebelum ia mati.\"'),(739,1,27,11,'Lalu kata Yakub kepada Ribka,\nibunya: \"Tetapi Esau, kakakku, adalah\nseorang yang berbulu badannya, sedang\naku ini kulitku licin.'),(740,1,27,12,'Mungkin ayahku akan meraba\naku; maka nanti ia akan menyangka\nbahwa aku mau memperolok-olokkan\ndia; dengan demikian aku akan mendatangkan kutuk atas diriku dan\nbukan berkat.\"'),(741,1,27,13,'Tetapi ibunya berkata kepadanya:\n\"Akulah yang menanggung kutuk itu,\nanakku; dengarkan saja perkataanku,\npergilah ambil kambing-kambing itu.\"'),(742,1,27,14,'Lalu ia pergi mengambil kambingkambing itu dan membawanya kepada\nibunya; sesudah itu ibunya mengolah\nmakanan yang enak, seperti yang\ndigemari ayahnya.'),(743,1,27,15,'Kemudian Ribka mengambil pakaian\nyang indah kepunyaan Esau, anak\nsulungnya, pakaian yang disimpannya\ndi rumah, lalu disuruhnyalah dikenakan\noleh Yakub, anak bungsunya.'),(744,1,27,16,'Dan kulit anak kambing itu\ndipalutkannya pada kedua tangan Yakub\ndan pada lehernya yang licin itu.'),(745,1,27,17,'Lalu ia memberikan makanan yang\nenak dan roti yang telah diolahnya itu\nkepada Yakub, anaknya.'),(746,1,27,18,'Demikianlah Yakub masuk ke\ntempat ayahnya serta berkata: \"Bapa!\"\nSahut ayahnya: \"Ya, anakku; siapakah\nengkau?\"'),(747,1,27,19,'Kata Yakub kepada ayahnya: \"Akulah\nEsau, anak sulungmu. Telah kulakukan, seperti yang bapa katakan kepadaku.\nBangunlah, duduklah dan makanlah\ndaging buruan masakanku ini, agar bapa\nmemberkati aku.\"'),(748,1,27,20,'Lalu Ishak berkata kepada anaknya\nitu: \"Lekas juga engkau mendapatnya,\nanakku!\" Jawabnya: \"Karena TUHAN,\nAllahmu, membuat aku mencapai tujuanku.\"'),(749,1,27,21,'Lalu kata Ishak kepada Yakub:\n\"Datanglah mendekat, anakku, supaya\naku meraba engkau, apakah engkau ini\nanakku Esau atau bukan.\"'),(750,1,27,22,'Maka Yakub mendekati Ishak,\nayahnya, dan ayahnya itu merabanya\nserta berkata: \"Kalau suara, suara\nYakub; kalau tangan, tangan Esau.\"'),(751,1,27,23,'Jadi Ishak tidak mengenal dia, karena\ntangannya berbulu seperti tangan Esau,\nkakaknya. Ishak hendak memberkati\ndia,'),(752,1,27,24,'tetapi ia masih bertanya: \"Benarkah\nengkau ini anakku Esau?\" Jawabnya:\n\"Ya!\"'),(753,1,27,25,'Lalu berkatalah Ishak: \"Dekatkanlah\nmakanan itu kepadaku, supaya kumakan\ndaging buruan masakan anakku,\nagar aku memberkati engkau.\" Jadi didekatkannyalah makanan itu kepada\nayahnya, lalu ia makan, dibawanya juga\nanggur kepadanya, lalu ia minum.'),(754,1,27,26,'Berkatalah Ishak, ayahnya,\nkepadanya: \"Datanglah dekat-dekat dan\nciumlah aku, anakku.\"'),(755,1,27,27,'Lalu datanglah Yakub dekatdekat dan diciumnyalah ayahnya.\nKetika Ishak mencium bau pakaian\nYakub, diberkatinyalah dia, katanya:\n\"Sesungguhnya bau anakku adalah\nsebagai bau padang yang diberkati\nTUHAN.'),(756,1,27,28,'Allah akan memberikan kepadamu\nembun yang dari langit dan tanah-tanah\ngemuk di bumi dan gandum serta anggur\nberlimpah-limpah.'),(757,1,27,29,'Bangsa-bangsa akan takluk\nkepadamu, dan suku-suku bangsa akan\nsujud kepadamu; jadilah tuan atas\nsaudara-saudaramu, dan anak-anak\nibumu akan sujud kepadamu. Siapa\nyang mengutuk engkau, terkutuklah ia,\ndan siapa yang memberkati engkau,\ndiberkatilah ia.\"'),(758,1,27,30,'Setelah Ishak selesai memberkati\nYakub, dan baru saja Yakub keluar meninggalkan Ishak, ayahnya, pulanglah\nEsau, kakaknya, dari berburu.'),(759,1,27,31,'Ia juga menyediakan makanan\nyang enak, lalu membawanya kepada\nayahnya. Katanya kepada ayahnya:\n\"Bapa, bangunlah dan makan daging\nburuan masakan anakmu, agar engkau\nmemberkati aku.\"'),(760,1,27,32,'Tetapi kata Ishak, ayahnya,\nkepadanya: \"Siapakah engkau ini?\"\nSahutnya: \"Akulah anakmu, anak\nsulungmu, Esau.\"'),(761,1,27,33,'Lalu terkejutlah Ishak dengan sangat\nserta berkata: \"Siapakah gerangan dia,\nyang memburu binatang itu dan yang\ntelah membawanya kepadaku? Aku telah\nmemakan semuanya, sebelum engkau\ndatang, dan telah memberkati dia; dan\ndia akan tetap orang yang diberkati.\"'),(762,1,27,34,'Sesudah Esau mendengar perkataan\nayahnya itu, meraung-raunglah ia\ndengan sangat keras dalam kepedihan\nhatinya serta berkata kepada ayahnya:\n\"Berkatilah aku ini juga, ya bapa!\"'),(763,1,27,35,'Jawab ayahnya: \"Adikmu telah\ndatang dengan tipu daya dan telah\nmerampas berkat yang untukmu itu.\"'),(764,1,27,36,'Kata Esau: \"Bukankah tepat\nnamanya Yakub, karena ia telah dua kali\nmenipu aku. Hak kesulunganku telah\ndirampasnya, dan sekarang dirampasnya\npula berkat yang untukku.\" Lalu katanya:\n\"Apakah bapa tidak mempunyai berkat\nlain bagiku?\"'),(765,1,27,37,'Lalu Ishak menjawab Esau, katanya:\n\"Sesungguhnya telah kuangkat dia\nmenjadi tuan atas engkau, dan segala\nsaudaranya telah kuberikan kepadanya\nmenjadi hambanya, dan telah kubekali\ndia dengan gandum dan anggur;\nmaka kepadamu, apa lagi yang dapat\nkuperbuat, ya anakku?\"'),(766,1,27,38,'Kata Esau kepada ayahnya: \"Hanya\nberkat yang satu itukah ada padamu, ya\nbapa? Berkatilah aku ini juga, ya bapa!\"\nDan dengan suara keras menangislah\nEsau.'),(767,1,27,39,'Lalu Ishak, ayahnya, menjawabnya:\n\"Sesungguhnya tempat kediamanmu\nakan jauh dari tanah-tanah gemuk di\nbumi dan jauh dari embun dari langit di\natas.'),(768,1,27,40,'Engkau akan hidup dari pedangmu\ndan engkau akan menjadi hamba\nadikmu. Tetapi akan terjadi kelak, apabila engkau berusaha sungguh-sungguh,\nmaka engkau akan melemparkan kuk itu\ndari tengkukmu.\"'),(769,1,27,41,'Esau menaruh dendam kepada Yakub\nkarena berkat yang telah diberikan oleh\nayahnya kepadanya, lalu ia berkata\nkepada dirinya sendiri: \"Hari-hari\nberkabung karena kematian ayahku itu\ntidak akan lama lagi; pada waktu itulah\nYakub, adikku, akan kubunuh.\"'),(770,1,27,42,'Ketika diberitahukan perkataan Esau,\nanak sulungnya itu kepada Ribka, maka\ndisuruhnyalah memanggil Yakub, anak\nbungsunya, lalu berkata kepadanya:\n\"Esau, kakakmu, bermaksud membalas\ndendam membunuh engkau.'),(771,1,27,43,'Jadi sekarang, anakku, dengarkanlah\nperkataanku, bersiaplah engkau dan\nlarilah kepada Laban, saudaraku, ke\nHaran,'),(772,1,27,44,'dan tinggallah padanya beberapa\nwaktu lamanya, sampai kegeraman'),(773,1,27,45,'dan kemarahan kakakmu itu surut\ndari padamu, dan ia lupa apa yang telah\nengkau perbuat kepadanya; kemudian\naku akan menyuruh orang menjemput\nengkau dari situ. Mengapa aku akan kehilangan kamu berdua pada satu hari\njuga?\"'),(774,1,27,46,'Kemudian Ribka berkata kepada\nIshak: \"Aku telah jemu hidup karena\nperempuan-perempuan Het itu; jikalau\nYakub juga mengambil seorang isteri dari\nantara perempuan negeri ini, semacam\nperempuan Het itu, apa gunanya aku\nhidup lagi?\"'),(775,1,28,1,'Kemudian Ishak memanggil Yakub, lalu memberkati dia serta memesankan kepadanya, katanya:\n\"Janganlah mengambil isteri dari\nperempuan Kanaan.'),(776,1,28,2,'Bersiaplah, pergilah ke Padan-Aram,\nke rumah Betuel, ayah ibumu, dan\nambillah dari situ seorang isteri dari\nanak-anak Laban, saudara ibumu.'),(777,1,28,3,'Moga-moga Allah Yang Mahakuasa\nmemberkati engkau, membuat engkau\nberanak cucu dan membuat engkau\nmenjadi banyak, sehingga engkau\nmenjadi sekumpulan bangsa-bangsa.'),(778,1,28,4,'Moga-moga Ia memberikan kepadamu\nberkat yang untuk Abraham, kepadamu\nserta kepada keturunanmu, sehingga\nengkau memiliki negeri ini yang kaudiami sebagai orang asing, yang\ntelah diberikan Allah kepada Abraham.\"'),(779,1,28,5,'Demikianlah Ishak melepas Yakub,\nlalu berangkatlah Yakub ke Padan-Aram,\nkepada Laban anak Betuel, orang Aram\nitu, saudara Ribka ibu Yakub dan Esau.'),(780,1,28,6,'Ketika Esau melihat, bahwa Ishak\ntelah memberkati Yakub dan melepasnya\nke Padan-Aram untuk mengambil isteri\ndari situ--pada waktu ia memberkatinya\nia telah memesankan kepada Yakub:\n\"Janganlah ambil isteri dari antara\nperempuan Kanaan\" --'),(781,1,28,7,'dan bahwa Yakub mendengarkan\nperkataan ayah dan ibunya, dan pergi ke\nPadan-Aram,'),(782,1,28,8,'maka Esaupun menyadari, bahwa\nperempuan Kanaan itu tidak disukai oleh\nIshak, ayahnya.'),(783,1,28,9,'Sebab itu ia pergi kepada Ismael dan\nmengambil Mahalat menjadi isterinya,\ndi samping kedua isterinya yang telah\nada. Mahalat adalah anak Ismael anak\nAbraham, adik Nebayot.'),(784,1,28,10,'Maka Yakub berangkat dari Bersyeba\ndan pergi ke Haran.'),(785,1,28,11,'Ia sampai di suatu tempat, dan\nbermalam di situ, karena matahari telah terbenam. Ia mengambil sebuah\nbatu yang terletak di tempat itu dan\ndipakainya sebagai alas kepala, lalu\nmembaringkan dirinya di tempat itu.'),(786,1,28,12,'Maka bermimpilah ia, di bumi ada\ndidirikan sebuah tangga yang ujungnya\nsampai di langit, dan tampaklah\nmalaikat-malaikat Allah turun naik di\ntangga itu.'),(787,1,28,13,'Berdirilah TUHAN di sampingnya\ndan berfirman: \"Akulah TUHAN, Allah\nAbraham, nenekmu, dan Allah Ishak;\ntanah tempat engkau berbaring ini\nakan Kuberikan kepadamu dan kepada\nketurunanmu.'),(788,1,28,14,'Keturunanmu akan menjadi seperti\ndebu tanah banyaknya, dan engkau akan\nmengembang ke sebelah timur, barat,\nutara dan selatan, dan olehmu serta\nketurunanmu semua kaum di muka bumi\nakan mendapat berkat.'),(789,1,28,15,'Sesungguhnya Aku menyertai engkau\ndan Aku akan melindungi engkau, ke\nmanapun engkau pergi, dan Aku akan\nmembawa engkau kembali ke negeri\nini, sebab Aku tidak akan meninggalkan\nengkau, melainkan tetap melakukan apa\nyang Kujanjikan kepadamu.\"'),(790,1,28,16,'Ketika Yakub bangun dari tidurnya,\nberkatalah ia: \"Sesungguhnya TUHAN\nada di tempat ini, dan aku tidak\nmengetahuinya.\"'),(791,1,28,17,'Ia takut dan berkata: \"Alangkah\ndahsyatnya tempat ini. Ini tidak lain dari\nrumah Allah, ini pintu gerbang sorga.\"'),(792,1,28,18,'Keesokan harinya pagi-pagi Yakub\nmengambil batu yang dipakainya sebagai\nalas kepala dan mendirikan itu menjadi\ntugu dan menuang minyak ke atasnya.'),(793,1,28,19,'Ia menamai tempat itu Betel; dahulu\nnama kota itu Lus.'),(794,1,28,20,'Lalu bernazarlah Yakub: \"Jika Allah\nakan menyertai dan akan melindungi aku\ndi jalan yang kutempuh ini, memberikan\nkepadaku roti untuk dimakan dan\npakaian untuk dipakai,'),(795,1,28,21,'sehingga aku selamat kembali ke\nrumah ayahku, maka TUHAN akan\nmenjadi Allahku.'),(796,1,28,22,'Dan batu yang kudirikan sebagai\ntugu ini akan menjadi rumah Allah. Dari\nsegala sesuatu yang Engkau berikan\nkepadaku akan selalu kupersembahkan\nsepersepuluh kepada-Mu.\"'),(797,1,29,1,'Kemudian berangkatlah Yakub dari situ dan pergi ke negeri Bani Timur.'),(798,1,29,2,'Ketika ia memandang sekelilingnya,\ndilihatnya ada sebuah sumur di padang,\ndan ada tiga kumpulan kambing domba\nberbaring di dekatnya, sebab dari\nsumur itulah orang memberi minum\nkumpulan-kumpulan kambing domba\nitu. Adapun batu penutup sumur itu\nbesar;'),(799,1,29,3,'dan apabila segala kumpulan kambing\ndomba itu digiring berkumpul ke sana,\nmaka gembala-gembala menggulingkan\nbatu itu dari mulut sumur, lalu kambing\ndomba itu diberi minum; kemudian\ndikembalikanlah batu itu lagi ke mulut\nsumur itu.'),(800,1,29,4,'Bertanyalah Yakub kepada mereka:\n\"Saudara-saudara, dari manakah kamu\nini?\" Jawab mereka: \"Kami ini dari\nHaran.\"'),(801,1,29,5,'Lagi katanya kepada mereka:\n\"Kenalkah kamu Laban, cucu Nahor?\"\nJawab mereka: \"Kami kenal.\"'),(802,1,29,6,'Selanjutnya katanya kepada mereka:\n\"Selamatkah ia?\" Jawab mereka:\n\"Selamat! Tetapi lihat, itu datang anaknya perempuan, Rahel, dengan\nkambing dombanya.'),(803,1,29,7,'Lalu kata Yakub: \"Hari masih siang,\nbelum waktunya untuk mengumpulkan\nternak; berilah minum kambing\ndombamu itu, kemudian pergilah\nmenggembalakannya lagi.\"'),(804,1,29,8,'Tetapi jawab mereka: \"Kami tidak\ndapat melakukan itu selama segala\nkumpulan binatang itu belum berkumpul;\nbarulah batu itu digulingkan dari mulut\nsumur dan kami memberi minum\nkambing domba kami.\"'),(805,1,29,9,'Selagi ia berkata-kata dengan\nmereka, datanglah Rahel dengan\nkambing domba ayahnya, sebab dialah\nyang menggembalakannya.'),(806,1,29,10,'Ketika Yakub melihat Rahel, anak\nLaban saudara ibunya, serta kambing\ndomba Laban, ia datang mendekat,\nlalu menggulingkan batu itu dari mulut\nsumur, dan memberi minum kambing\ndomba itu'),(807,1,29,11,'Kemudian Yakub mencium Rahel\nserta menangis dengan suara keras.'),(808,1,29,12,'Lalu Yakub menceritakan kepada\nRahel, bahwa ia sanak saudara ayah Rahel, dan anak Ribka. Maka berlarilah\nRahel menceritakannya kepada ayahnya.'),(809,1,29,13,'Segera sesudah Laban mendengar\nkabar tentang Yakub, anak saudaranya\nitu, berlarilah ia menyongsong dia, lalu\nmendekap dan mencium dia, kemudian\nmembawanya ke rumahnya. Maka\nYakub menceritakan segala hal ihwalnya\nkepada Laban.'),(810,1,29,14,'Kata Laban kepadanya:\n\"Sesungguhnya engkau sedarah\nsedaging dengan aku.\" Maka tinggallah\nYakub padanya genap sebulan lamanya.'),(811,1,29,15,'Kemudian berkatalah Laban kepada\nYakub: \"Masakan karena engkau adalah\nsanak saudaraku, engkau bekerja\npadaku dengan cuma-cuma? Katakanlah\nkepadaku apa yang patut menjadi\nupahmu.\"'),(812,1,29,16,'Laban mempunyai dua anak\nperempuan; yang lebih tua namanya Lea\ndan yang lebih muda namanya Rahel.'),(813,1,29,17,'Lea tidak berseri matanya, tetapi\nRahel itu elok sikapnya dan cantik\nparasnya.'),(814,1,29,18,'Yakub cinta kepada Rahel, sebab itu\nia berkata: \"Aku mau bekerja padamu tujuh tahun lamanya untuk mendapat\nRahel, anakmu yang lebih muda itu.\"'),(815,1,29,19,'Sahut Laban: \"Lebih baiklah ia\nkuberikan kepadamu dari pada kepada\norang lain; maka tinggallah padaku.\"'),(816,1,29,20,'Jadi bekerjalah Yakub tujuh tahun\nlamanya untuk mendapat Rahel itu,\ntetapi yang tujuh tahun itu dianggapnya\nseperti beberapa hari saja, karena\ncintanya kepada Rahel.'),(817,1,29,21,'Sesudah itu berkatalah Yakub kepada\nLaban: \"Berikanlah kepadaku bakal\nisteriku itu, sebab jangka waktuku telah\ngenap, supaya aku akan kawin dengan\ndia.\"'),(818,1,29,22,'Lalu Laban mengundang semua\norang di tempat itu, dan mengadakan\nperjamuan.'),(819,1,29,23,'Tetapi pada waktu malam\ndiambilnyalah Lea, anaknya, lalu\ndibawanya kepada Yakub. Maka\nYakubpun menghampiri dia.'),(820,1,29,24,'Lagipula Laban memberikan Zilpa,\nbudaknya perempuan, kepada Lea,\nanaknya itu, menjadi budaknya.'),(821,1,29,25,'Tetapi pada waktu pagi tampaklah\nbahwa itu Lea! Lalu berkatalah\nYakub kepada Laban: \"Apakah yang kauperbuat terhadap aku ini? Bukankah\nuntuk mendapat Rahel aku bekerja\npadamu? Mengapa engkau menipu aku?\"'),(822,1,29,26,'Jawab Laban: \"Tidak biasa orang\nberbuat demikian di tempat kami ini,\nmengawinkan adiknya lebih dahulu dari\npada kakaknya.'),(823,1,29,27,'Genapilah dahulu tujuh hari\nperkawinanmu dengan anakku ini;\nkemudian anakku yang lainpun akan\ndiberikan kepadamu sebagai upah, asal\nengkau bekerja pula padaku tujuh tahun\nlagi.\"'),(824,1,29,28,'Maka Yakub berbuat demikian; ia\nmenggenapi ketujuh hari perkawinannya\ndengan Lea, kemudian Laban\nmemberikan kepadanya Rahel,\nanaknya itu, menjadi isterinya.'),(825,1,29,29,'Lagipula Laban memberikan Bilha,\nbudaknya perempuan, kepada Rahel,\nanaknya itu, menjadi budaknya.'),(826,1,29,30,'Yakub menghampiri Rahel juga,\nmalah ia lebih cinta kepada Rahel dari\npada kepada Lea. Demikianlah ia bekerja\npula pada Laban tujuh tahun lagi.'),(827,1,29,31,'Ketika TUHAN melihat, bahwa\nLea tidak dicintai, dibuka-Nyalah\nkandungannya, tetapi Rahel mandul.'),(828,1,29,32,'Lea mengandung, lalu melahirkan\nseorang anak laki-laki, dan menamainya\nRuben, sebab katanya: \"Sesungguhnya\nTUHAN telah memperhatikan\nkesengsaraanku; sekarang tentulah aku\nakan dicintai oleh suamiku.\"'),(829,1,29,33,'Mengandung pulalah ia, lalu\nmelahirkan seorang anak laki-laki, maka\nia berkata: \"Sesungguhnya, TUHAN\ntelah mendengar, bahwa aku tidak\ndicintai, lalu diberikan-Nya pula anak ini\nkepadaku.\" Maka ia menamai anak itu\nSimeon.'),(830,1,29,34,'Mengandung pulalah ia, lalu\nmelahirkan seorang anak laki-laki, maka\nia berkata: \"Sekali ini suamiku akan\nlebih erat kepadaku, karena aku telah\nmelahirkan tiga anak laki-laki baginya.\"\nItulah sebabnya ia menamai anak itu\nLewi.'),(831,1,29,35,'Mengandung pulalah ia, lalu\nmelahirkan seorang anak laki-laki,\nmaka ia berkata: \"Sekali ini aku akan\nbersyukur kepada TUHAN.\" Itulah\nsebabnya ia menamai anak itu Yehuda.\nSesudah itu ia tidak melahirkan lagi.'),(832,1,30,1,'Ketika dilihat Rahel, bahwa ia tidak melahirkan anak bagi Yakub, cemburulah ia kepada kakaknya itu,\nlalu berkata kepada Yakub: \"Berikanlah\nkepadaku anak; kalau tidak, aku akan\nmati.\"'),(833,1,30,2,'Maka bangkitlah amarah Yakub\nterhadap Rahel dan ia berkata: \"Akukah\npengganti Allah, yang telah menghalangi\nengkau mengandung?\"'),(834,1,30,3,'Kata Rahel: \"Ini Bilha, budakku\nperempuan, hampirilah dia, supaya ia\nmelahirkan anak di pangkuanku, dan\nsupaya oleh dia akupun mempunyai\nketurunan.\"'),(835,1,30,4,'Maka diberikannyalah Bilha, budaknya\nitu, kepada Yakub menjadi isterinya dan\nYakub menghampiri budak itu.'),(836,1,30,5,'Bilha mengandung, lalu melahirkan\nseorang anak laki-laki bagi Yakub.'),(837,1,30,6,'Berkatalah Rahel: \"Allah telah\nmemberikan keadilan kepadaku, juga\ntelah didengarkan-Nya permohonanku\ndan diberikan-Nya kepadaku seorang\nanak laki-laki.\" Itulah sebabnya ia\nmenamai anak itu Dan.'),(838,1,30,7,'Mengandung pulalah Bilha, budak\nperempuan Rahel, lalu melahirkan anak\nlaki-laki yang kedua bagi Yakub.'),(839,1,30,8,'Berkatalah Rahel: \"Aku telah sangat\nhebat bergulat dengan kakakku, dan\nakupun menang.\" Maka ia menamai\nanak itu Naftali.'),(840,1,30,9,'Ketika dilihat Lea, bahwa ia tidak\nmelahirkan lagi, diambilnyalah Zilpa,\nbudaknya perempuan, dan diberikannya\nkepada Yakub menjadi isterinya.'),(841,1,30,10,'Dan Zilpa, budak perempuan Lea,\nmelahirkan seorang anak laki-laki bagi\nYakub.'),(842,1,30,11,'Berkatalah Lea: \"Mujur telah datang.\"\nMaka ia menamai anak itu Gad.'),(843,1,30,12,'Dan Zilpa, budak perempuan Lea,\nmelahirkan anak laki-laki yang kedua\nbagi Yakub.'),(844,1,30,13,'Berkatalah Lea: \"Aku ini berbahagia!\nTentulah perempuan-perempuan akan\nmenyebutkan aku berbahagia.\" Maka ia\nmenamai anak itu Asyer.'),(845,1,30,14,'Ketika Ruben pada musim\nmenuai gandum pergi berjalanjalan, didapatinyalah di padang buah\ndudaim, lalu dibawanya kepada Lea,\nibunya. Kata Rahel kepada Lea: \"Berilah\naku beberapa buah dudaim yang didapat\noleh anakmu itu.\"'),(846,1,30,15,'Ketika Yakub pada waktu petang\ndatang dari padang, pergilah Lea\nmendapatkannya, sambil berkata:\n\"Engkau harus singgah kepadaku malam\nini, sebab memang engkau telah kusewa\ndengan buah dudaim anakku.\" Sebab itu\ntidurlah Yakub dengan Lea pada malam\nitu.'),(847,1,30,16,'Lalu Allah mendengarkan\npermohonan Lea. Lea mengandung dan\nmelahirkan anak laki-laki yang kelima\nbagi Yakub.'),(848,1,30,17,'Lalu kata Lea: \"Allah telah memberi\nupahku, karena aku telah memberi\nbudakku perempuan kepada suamiku.\"\nMaka ia menamai anak itu Isakhar.'),(849,1,30,18,'Kemudian Lea mengandung pula dan\nmelahirkan anak laki-laki yang keenam\nbagi Yakub.'),(850,1,30,19,'Berkatalah Lea: \"Allah telah\nmemberikan hadiah yang indah kepadaku; sekali ini suamiku akan\ntinggal bersama-sama dengan aku,\nkarena aku telah melahirkan enam\norang anak laki-laki baginya.\" Maka ia\nmenamai anak itu Zebulon.'),(851,1,30,20,'Sesudah itu ia melahirkan seorang\nanak perempuan dan menamai anak itu\nDina.'),(852,1,30,21,'Lalu ingatlah Allah akan Rahel; Allah\nmendengarkan permohonannya serta\nmembuka kandungannya.'),(853,1,30,22,'Maka mengandunglah Rahel\ndan melahirkan seorang anak lakilaki. Berkatalah ia: \"Allah telah\nmenghapuskan aibku.\"'),(854,1,30,23,'Maka ia menamai anak itu Yusuf,\nsambil berkata: \"Mudah-mudahan\nTUHAN menambah seorang anak\nlaki-laki lagi bagiku.\"'),(855,1,30,24,'Setelah Rahel melahirkan Yusuf,\nberkatalah Yakub kepada Laban:\n\"Izinkanlah aku pergi, supaya aku\npulang ke tempat kelahiranku dan ke\nnegeriku.'),(856,1,30,25,'Berikanlah isteri-isteriku dan anakanakku, yang menjadi upahku selama\naku bekerja padamu, supaya aku pulang, sebab engkau tahu, betapa keras aku\nbekerja padamu.\"'),(857,1,30,26,'Tetapi Laban berkata kepadanya:\n\"Sekiranya aku mendapat kasihmu!\nTelah nyata kepadaku, bahwa TUHAN\nmemberkati aku karena engkau.\"'),(858,1,30,27,'Tetapi Laban berkata kepadanya:\n\"Sekiranya aku mendapat kasihmu!\nTelah nyata kepadaku, bahwa TUHAN\nmemberkati aku karena engkau.\"'),(859,1,30,28,'Sahut Yakub kepadanya: \"Engkau\nsendiri tahu, bagaimana aku bekerja\npadamu, dan bagaimana keadaan\nternakmu dalam penjagaanku,'),(860,1,30,29,'sebab harta milikmu tidak begitu\nbanyak sebelum aku datang, tetapi\nsekarang telah berkembang dengan\nsangat, dan TUHAN telah memberkati\nengkau sejak aku berada di sini; jadi,\nbilakah dapat aku bekerja untuk rumah\ntanggaku sendiri?\"'),(861,1,30,30,'Kata Laban: \"Apakah yang\nharus kuberikan kepadamu?\" Jawab\nYakub: \"Tidak usah kauberikan\napa-apa kepadaku; aku mau lagi\nmenggembalakan kambing dombamu\ndan menjaganya, asal engkau\nmengizinkan hal ini kepadaku:'),(862,1,30,31,'Hari ini aku akan lewat dari tengahtengah segala kambing dombamu\ndan akan mengasingkan dari situ\nsetiap binatang yang berbintik-bintik\ndan berbelang-belang; segala domba\nyang hitam dan segala kambing yang\nberbelang-belang dan berbintik-bintik,\nitulah upahku.'),(863,1,30,32,'Dan kejujuranku akan terbukti di\nkemudian hari, apabila engkau datang\nmemeriksa upahku: Segala yang tidak\nberbintik-bintik atau berbelang-belang\ndi antara kambing-kambing dan yang\ntidak hitam di antara domba-domba,\nanggaplah itu tercuri olehku.\"'),(864,1,30,33,'Kemudian kata Laban: \"Baik, jadilah\nseperti perkataanmu itu.\"'),(865,1,30,34,'Lalu diasingkannyalah pada hari\nitu kambing-kambing jantan yang\nbercoreng-coreng dan berbelang-belang\ndan segala kambing yang berbintik-bintik\ndan berbelang-belang, segala yang ada\nwarna putih pada badannya, serta segala\nyang hitam di antara domba-domba, dan\ndiserahkannyalah semuanya itu kepada\nanak-anaknya untuk dijaga.'),(866,1,30,35,'Kemudian Laban menentukan jarak\ntiga hari perjalanan jauhnya antara dia dan Yakub, maka tetaplah Yakub\nmenggembalakan kambing domba yang\ntinggal itu.'),(867,1,30,36,'Lalu Yakub mengambil dahan hijau\ndari pohon hawar, pohon badam dan\npohon berangan, dikupasnyalah dahandahan itu sehingga berbelang-belang,\nsampai yang putihnya kelihatan.'),(868,1,30,37,'Ia meletakkan dahan-dahan yang\ndikupasnya itu dalam palungan, dalam\ntempat minum, ke mana kambing domba\nitu datang minum, sehingga tepat di\ndepan kambing domba itu. Adapun\nkambing domba itu suka berkelamin\npada waktu datang minum.'),(869,1,30,38,'Jika kambing domba itu berkelamin\ndekat dahan-dahan itu, maka anaknya\nbercoreng-coreng, berbintik-bintik dan\nberbelang-belang.'),(870,1,30,39,'Kemudian Yakub memisahkan\ndomba-domba itu, dihadapkannya\nkepala-kepala kambing domba itu\nkepada yang bercoreng-coreng dan\nkepada segala yang hitam di antara\nkambing domba Laban. Demikianlah\nia beroleh kumpulan-kumpulan\nhewan baginya sendiri, dan tidak ditempatkannya pada kambing domba\nLaban.'),(871,1,30,40,'Dan setiap kali, apabila berkelamin\nkambing domba yang kuat, maka\nYakub meletakkan dahan-dahan itu ke\ndalam palungan di depan mata kambing\ndomba itu, supaya berkelamin dekat\ndahan-dahan itu.'),(872,1,30,41,'Tetapi apabila datang kambing\ndomba yang lemah, ia tidak meletakkan\ndahan-dahan itu ke dalamnya. Jadi\nhewan yang lemah untuk Laban dan\nyang kuat untuk Yakub.'),(873,1,30,42,'Maka sangatlah bertambah-tambah\nharta Yakub, dan ia mempunyai banyak\nkambing domba, budak perempuan dan\nlaki-laki, unta dan keledai.'),(874,1,31,1,'Kedengaranlah kepada Yakub anak-anak Laban berkata demikian: \"Yakub telah mengambil\nsegala harta milik ayah kita dan dari\nharta itulah ia membangun segala\nkekayaannya.\"'),(875,1,31,2,'Lagi kelihatan kepada Yakub dari\nmuka Laban, bahwa Laban tidak lagi\nseperti yang sudah-sudah kepadanya.'),(876,1,31,3,'Lalu berfirmanlah TUHAN kepada\nYakub: \"Pulanglah ke negeri nenek moyangmu dan kepada kaummu, dan\nAku akan menyertai engkau.\"'),(877,1,31,4,'Sesudah itu Yakub menyuruh\nmemanggil Rahel dan Lea untuk\ndatang ke padang, ke tempat kambing\ndombanya,'),(878,1,31,5,'lalu ia berkata kepada mereka: \"Telah\nkulihat dari muka ayahmu, bahwa ia\ntidak lagi seperti yang sudah-sudah\nkepadaku, tetapi Allah ayahku menyertai\naku.'),(879,1,31,6,'Juga kamu sendiri tahu, bahwa\naku telah bekerja sekuat-kuatku pada\nayahmu.'),(880,1,31,7,'Tetapi ayahmu telah berlaku curang\nkepadaku dan telah sepuluh kali\nmengubah upahku, tetapi Allah tidak\nmembiarkan dia berbuat jahat kepadaku.'),(881,1,31,8,'Apabila ia berkata: yang berbintikbintiklah akan menjadi upahmu, maka\nsegala kambing domba itu beroleh anak\nyang berbintik-bintik; dan apabila ia\nberkata: yang bercoreng-corenglah\nakan menjadi upahmu, maka segala\nkambing domba itu beroleh anak yang\nbercoreng-coreng.'),(882,1,31,9,'Demikianlah Allah mengambil ternak\nayahmu dan memberikannya kepadaku.'),(883,1,31,10,'Pada suatu kali pada masa kambing\ndomba itu suka berkelamin, maka aku\nbermimpi dan melihat, bahwa jantanjantan yang menjantani kambing domba\nitu bercoreng-coreng, berbintik-bintik\ndan berbelang-belang.'),(884,1,31,11,'Dan Malaikat Allah berfirman\nkepadaku dalam mimpi itu: Yakub!\nJawabku: Ya Tuhan!'),(885,1,31,12,'Lalu Ia berfirman: Angkatlah\nmukamu dan lihatlah, bahwa segala\njantan yang menjantani kambing domba\nitu bercoreng-coreng, berbintik-bintik\ndan berbelang-belang, sebab telah\nKulihat semua yang dilakukan oleh\nLaban itu kepadamu.'),(886,1,31,13,'Akulah Allah yang di Betel itu, di\nmana engkau mengurapi tugu, dan\ndi mana engkau bernazar kepada-Ku;\nmaka sekarang, bersiaplah engkau,\npergilah dari negeri ini dan pulanglah ke\nnegeri sanak saudaramu.\"'),(887,1,31,14,'Lalu Rahel dan Lea menjawab Yakub,\nkatanya: \"Bukankah tidak ada lagi\nbagian atau warisan kami dalam rumah\nayah kami?'),(888,1,31,15,'Bukankah kami ini dianggapnya\nsebagai orang asing, karena ia telah menjual kami? Juga bagian kami telah\ndihabiskannya sama sekali.'),(889,1,31,16,'Tetapi segala kekayaan, yang telah\ndiambil Allah dari ayah kami, adalah\nmilik kami dan anak-anak kami; maka\nsekarang, perbuatlah segala yang\ndifirmankan Allah kepadamu.\"'),(890,1,31,17,'Lalu bersiaplah Yakub, dinaikkannya\nanak-anaknya dan isteri-isterinya ke\natas unta,'),(891,1,31,18,'digiringnya seluruh ternaknya dan\nsegala apa yang telah diperolehnya,\nyakni ternak kepunyaannya, yang telah\ndiperolehnya di Padan-Aram, dengan\nmaksud pergi kepada Ishak, ayahnya,\nke tanah Kanaan.'),(892,1,31,19,'Adapun Laban telah pergi\nmenggunting bulu domba-dombanya.\nKetika itulah Rahel mencuri terafim\nayahnya.'),(893,1,31,20,'Dan Yakub mengakali Laban, orang\nAram itu, dengan tidak memberitahukan\nkepadanya, bahwa ia mau lari.'),(894,1,31,21,'Demikianlah ia lari dengan\nsegala harta miliknya. Ia berangkat,\nmenyeberangi sungai Efrat dan berjalan\nmenuju pegunungan Gilead.'),(895,1,31,22,'Ketika pada hari ketiga dikabarkan\nkepada Laban, bahwa Yakub telah lari,'),(896,1,31,23,'dibawanyalah sanak saudaranya\nbersama-sama, dikejarnya Yakub tujuh\nhari perjalanan jauhnya, lalu ia dapat\nmenyusulnya di pegunungan Gilead.'),(897,1,31,24,'Pada waktu malam datanglah Allah\ndalam suatu mimpi kepada Laban, orang\nAram itu, serta berfirman kepadanya:\n\"Jagalah baik-baik, supaya engkau\njangan mengatai Yakub dengan sepatah\nkatapun.\"'),(898,1,31,25,'Ketika Laban sampai kepada Yakub,\n--Yakub telah memasang kemahnya di\npegunungan, juga Laban dengan sanak\nsaudaranya telah memasang kemahnya\ndi pegunungan Gilead--'),(899,1,31,26,'berkatalah Laban kepada Yakub:\n\"Apakah yang kauperbuat ini, maka\nengkau mengakali aku dan mengangkut\nanak-anakku perempuan sebagai orang\ntawanan?'),(900,1,31,27,'Mengapa engkau lari diam-diam\ndan mengakali aku? Mengapa engkau\ntidak memberitahu kepadaku, supaya\naku menghantarkan engkau dengan\nsukacita dan nyanyian dengan rebana\ndan kecapi?'),(901,1,31,28,'Lagipula engkau tidak memberikan\naku kesempatan untuk mencium\ncucu-cucuku laki-laki dan anakanakku perempuan. Memang bodoh\nperbuatanmu itu.'),(902,1,31,29,'Aku ini berkuasa untuk berbuat jahat\nkepadamu, tetapi Allah ayahmu telah\nberfirman kepadaku tadi malam: Jagalah\nbaik-baik, jangan engkau mengatai\nYakub dengan sepatah katapun.'),(903,1,31,30,'Maka sekarang, kalau memang\nengkau harus pergi, semata-mata\nkarena sangat rindu ke rumah\nayahmu, mengapa engkau mencuri\ndewa-dewaku?\"'),(904,1,31,31,'Lalu Yakub menjawab Laban: \"Aku\ntakut, karena pikirku, jangan-jangan\nengkau merampas anak-anakmu itu dari\npadaku.'),(905,1,31,32,'Tetapi pada siapa engkau menemui\ndewa-dewamu itu, janganlah ia hidup\nlagi. Periksalah di depan saudarasaudara kita segala barang yang ada\npadaku dan ambillah barangmu.\" Sebab\nYakub tidak tahu, bahwa Rahel yang\nmencuri terafim itu.'),(906,1,31,33,'Lalu masuklah Laban ke dalam\nkemah Yakub dan ke dalam kemah Lea dan ke dalam kemah kedua budak\nperempuan itu, tetapi terafim itu tidak\nditemuinya. Setelah keluar dari kemah\nLea, ia masuk ke dalam kemah Rahel.'),(907,1,31,34,'Tetapi Rahel telah mengambil terafim\nitu dan memasukkannya ke dalam\npelana untanya, dan duduk di atasnya.\nLaban menggeledah seluruh kemah itu,\ntetapi terafim itu tidak ditemuinya.'),(908,1,31,35,'Lalu kata Rahel kepada ayahnya:\n\"Janganlah bapa marah, karena aku\ntidak dapat bangun berdiri di depanmu,\nsebab aku sedang haid.\" Dan Laban\nmencari dengan teliti, tetapi ia tidak\nmenemui terafim itu.'),(909,1,31,36,'Lalu hati Yakub panas dan ia\nbertengkar dengan Laban. Ia berkata\nkepada Laban: \"Apakah kesalahanku,\napakah dosaku, maka engkau memburu\naku sehebat itu?'),(910,1,31,37,'Engkau telah menggeledah segala\nbarangku, sekarang apakah yang\nkautemui dari segala barang rumahmu?\nLetakkanlah di sini di depan saudarasaudaraku dan saudara-saudaramu,\nsupaya mereka mengadili antara kita\nberdua.'),(911,1,31,38,'Selama dua puluh tahun ini aku\nbersama-sama dengan engkau; domba\ndan kambing betinamu tidak pernah\nkeguguran dan jantan dari kambing\ndombamu tidak pernah kumakan.'),(912,1,31,39,'Yang diterkam oleh binatang buas\ntidak pernah kubawa kepadamu, aku\nsendiri yang menggantinya; yang dicuri\norang, baik waktu siang, baik waktu\nmalam, selalu engkau tuntut dari\npadaku.'),(913,1,31,40,'Aku dimakan panas hari waktu siang\ndan kedinginan waktu malam, dan\nmataku jauh dari pada tertidur.'),(914,1,31,41,'Selama dua puluh tahun ini aku di\nrumahmu; aku telah bekerja padamu\nempat belas tahun lamanya untuk\nmendapat kedua anakmu dan enam\ntahun untuk mendapat ternakmu, dan\nengkau telah sepuluh kali mengubah\nupahku.'),(915,1,31,42,'Seandainya Allah ayahku, Allah\nAbraham dan Yang Disegani oleh Ishak\ntidak menyertai aku, tentulah engkau\nsekarang membiarkan aku pergi dengan\ntangan hampa; tetapi kesengsaraanku\ndan jerih payahku telah diperhatikan  Allah dan Ia telah menjatuhkan putusan\ntadi malam.\"'),(916,1,31,43,'Lalu Laban menjawab Yakub:\n\"Perempuan-perempuan ini anakku\ndan anak-anak lelaki ini cucuku dan\nternak ini ternakku, bahkan segala yang\nkaulihat di sini adalah milikku; jadi\napakah yang dapat kuperbuat sekarang\nkepada anak-anakku ini atau kepada\nanak-anak yang dilahirkan mereka?'),(917,1,31,44,'Maka sekarang, marilah kita\nmengikat perjanjian, aku dan engkau,\nsupaya itu menjadi kesaksian antara aku\ndan engkau.\"'),(918,1,31,45,'Kemudian Yakub mengambil sebuah\nbatu dan didirikannya menjadi tugu.'),(919,1,31,46,'Selanjutnya berkatalah Yakub kepada\nsanak saudaranya: \"Kumpulkanlah\nbatu.\" Maka mereka mengambil batu\ndan membuat timbunan, lalu makanlah\nmereka di sana di dekat timbunan itu.'),(920,1,31,47,'Laban menamai timbunan batu\nitu Yegar-Sahaduta, tetapi Yakub\nmenamainya Galed.'),(921,1,31,48,'Lalu kata Laban: \"Timbunan batu\ninilah pada hari ini menjadi kesaksian\nantara aku dan engkau.\" Itulah sebabnya\ntimbunan itu dinamainya Galed,'),(922,1,31,49,'dan juga Mizpa, sebab katanya:\n\"TUHAN kiranya berjaga-jaga antara aku\ndan engkau, apabila kita berjauhan.'),(923,1,31,50,'Jika engkau mengaibkan anakanakku, dan jika engkau mengambil\nisteri lain di samping anak-anakku itu,\ningatlah, walaupun tidak ada orang\ndekat kita, Allah juga yang menjadi saksi\nantara aku dan engkau.\"'),(924,1,31,51,'Selanjutnya kata Laban kepada\nYakub: \"Inilah timbunan batu, dan inilah\ntugu yang kudirikan antara aku dan\nengkau--'),(925,1,31,52,'timbunan batu dan tugu inilah\nmenjadi kesaksian, bahwa aku\ntidak akan melewati timbunan\nbatu ini mendapatkan engkau, dan\nbahwa engkaupun tidak akan melewati\ntimbunan batu dan tugu ini mendapatkan\naku, dengan berniat jahat.'),(926,1,31,53,'Allah Abraham dan Allah Nahor, Allah\nayah mereka, kiranya menjadi hakim\nantara kita.\" Lalu Yakub bersumpah demi\nYang Disegani oleh Ishak, ayahnya.'),(927,1,31,54,'Dan Yakub mempersembahkan\nkorban sembelihan di gunung itu. Ia\nmengundang makan sanak saudaranya, lalu mereka makan serta bermalam di\ngunung itu.'),(928,1,31,55,'Keesokan harinya pagi-pagi Laban\nmencium cucu-cucunya dan anakanaknya serta memberkati mereka,\nkemudian pulanglah Laban kembali ke\ntempat tinggalnya.'),(929,1,32,1,'Yakub melanjutkan perjalanannya, lalu bertemulah malaikat-malaikat Allah dengan dia.'),(930,1,32,2,'Ketika Yakub melihat mereka,\nberkatalah ia: \"Ini bala tentara Allah.\"\nSebab itu dinamainyalah tempat itu\nMahanaim.'),(931,1,32,3,'Sesudah itu Yakub menyuruh\nutusannya berjalan lebih dahulu\nmendapatkan Esau, kakaknya, ke tanah\nSeir, daerah Edom.'),(932,1,32,4,'Ia memerintahkan kepada mereka:\n\"Beginilah kamu katakan kepada tuanku,\nkepada Esau: Beginilah kata hambamu\nYakub: Aku telah tinggal pada Laban\nsebagai orang asing dan diam di situ\nselama ini.'),(933,1,32,5,'Aku telah mempunyai lembu sapi,\nkeledai dan kambing domba, budak\nlaki-laki dan perempuan, dan aku\nmenyuruh memberitahukan hal ini kepada tuanku, supaya aku mendapat\nkasihmu.\"'),(934,1,32,6,'Kemudian pulanglah para utusan itu\nkepada Yakub dan berkata: \"Kami telah\nsampai kepada kakakmu, kepada Esau,\ndan iapun sedang di jalan menemui\nengkau, diiringi oleh empat ratus orang.\"'),(935,1,32,7,'Lalu sangat takutlah Yakub dan\nmerasa sesak hati; maka dibaginyalah\norang-orangnya yang bersama-sama\ndengan dia, kambing dombanya, lembu\nsapi dan untanya menjadi dua pasukan.'),(936,1,32,8,'Sebab pikirnya: \"Jika Esau datang\nmenyerang pasukan yang satu, sehingga\nterpukul kalah, maka pasukan yang\ntinggal akan terluput.\"'),(937,1,32,9,'Kemudian berkatalah Yakub: \"Ya Allah\nnenekku Abraham dan Allah ayahku\nIshak, ya TUHAN, yang telah berfirman\nkepadaku: Pulanglah ke negerimu serta\nkepada sanak saudaramu dan Aku akan\nberbuat baik kepadamu--'),(938,1,32,10,'sekali-kali aku tidak layak untuk\nmenerima segala kasih dan kesetiaan\nyang Engkau tunjukkan kepada hambaMu ini, sebab aku membawa hanya\ntongkatku ini waktu aku menyeberangi sungai Yordan ini, tetapi sekarang telah\nmenjadi dua pasukan.'),(939,1,32,11,'Lepaskanlah kiranya aku dari tangan\nkakakku, dari tangan Esau, sebab aku\ntakut kepadanya, jangan-jangan ia\ndatang membunuh aku, juga ibu-ibu\ndengan anak-anaknya.'),(940,1,32,12,'Bukankah Engkau telah berfirman:\nTentu Aku akan berbuat baik kepadamu\ndan menjadikan keturunanmu sebagai\npasir di laut, yang karena banyaknya\ntidak dapat dihitung.\"'),(941,1,32,13,'Lalu bermalamlah ia di sana pada\nmalam itu. Kemudian diambilnyalah\ndari apa yang ada padanya suatu\npersembahan untuk Esau, kakaknya,'),(942,1,32,14,'yaitu dua ratus kambing betina dan\ndua puluh kambing jantan, dua ratus\ndomba betina dan dua puluh domba\njantan,'),(943,1,32,15,'tiga puluh unta yang sedang\nmenyusui beserta anak-anaknya, empat\npuluh lembu betina dan sepuluh lembu\njantan, dua puluh keledai betina dan\nsepuluh keledai jantan.'),(944,1,32,16,'Diserahkannyalah semuanya itu\nkepada budak-budaknya untuk dijaga,\ntiap-tiap kumpulan tersendiri, dan ia berkata kepada mereka: \"Berjalanlah\nkamu lebih dahulu dan jagalah supaya\nada jarak antara kumpulan yang satu\ndengan kumpulan yang lain.\"'),(945,1,32,17,'Diperintahkannyalah kepada yang\npaling di muka: \"Apabila Esau, kakakku,\nbertemu dengan engkau dan bertanya\nkepadamu: Siapakah tuanmu? dan\nke manakah engkau pergi? dan milik\nsiapakah ternak yang di depanmu itu? --'),(946,1,32,18,'jawablah: milik hambamu Yakub;\ninilah persembahan yang dikirim kepada\ntuanku Esau, dan Yakub sendiripun ada\ndi belakang kami.\"'),(947,1,32,19,'Begitulah diperintahkannya baik\nkepada yang kedua maupun kepada yang\nketiga dan kepada sekalian orang yang\nberjalan menggiring kumpulan hewan\nitu, katanya: \"Seperti perkataanku\ntadilah kamu katakan kepada Esau,\napabila kamu berjumpa dengan dia;'),(948,1,32,20,'dan kamu harus mengatakan juga:\nHambamu Yakub sendiri ada di belakang\nkami.\" Sebab pikir Yakub: \"Baiklah\naku mendamaikan hatinya dengan\npersembahan yang diantarkan lebih\ndahulu, kemudian barulah aku akan melihat mukanya; mungkin ia akan\nmenerima aku dengan baik.\"'),(949,1,32,21,'Jadi persembahan itu diantarkan lebih\ndahulu, tetapi ia sendiri bermalam pada\nmalam itu di tempat perkemahannya.'),(950,1,32,22,'Pada malam itu Yakub bangun dan\nia membawa kedua isterinya, kedua\nbudaknya perempuan dan kesebelas\nanaknya, dan menyeberang di tempat\npenyeberangan sungai Yabok.'),(951,1,32,23,'Sesudah ia menyeberangkan mereka,\nia menyeberangkan juga segala miliknya.'),(952,1,32,24,'Lalu tinggallah Yakub seorang diri.\nDan seorang laki-laki bergulat dengan\ndia sampai fajar menyingsing.'),(953,1,32,25,'Ketika orang itu melihat, bahwa\nia tidak dapat mengalahkannya, ia\nmemukul sendi pangkal paha Yakub,\nsehingga sendi pangkal paha itu\nterpelecok, ketika ia bergulat dengan\norang itu.'),(954,1,32,26,'Lalu kata orang itu: \"Biarkanlah aku\npergi, karena fajar telah menyingsing.\"\nSahut Yakub: \"Aku tidak akan\nmembiarkan engkau pergi, jika engkau\ntidak memberkati aku.\"'),(955,1,32,27,'Bertanyalah orang itu kepadanya:\n\"Siapakah namamu?\" Sahutnya:\n\"Yakub.\"'),(956,1,32,28,'Lalu kata orang itu: \"Namamu tidak\nakan disebutkan lagi Yakub, tetapi\nIsrael, sebab engkau telah bergumul\nmelawan Allah dan manusia, dan engkau\nmenang.\"'),(957,1,32,29,'Bertanyalah Yakub: \"Katakanlah juga\nnamamu.\" Tetapi sahutnya: \"Mengapa\nengkau menanyakan namaku?\" Lalu\ndiberkatinyalah Yakub di situ.'),(958,1,32,30,'Yakub menamai tempat itu Pniel,\nsebab katanya: \"Aku telah melihat\nAllah berhadapan muka, tetapi nyawaku\ntertolong!\"'),(959,1,32,31,'Lalu tampaklah kepadanya matahari\nterbit, ketika ia telah melewati Pniel; dan\nYakub pincang karena pangkal pahanya.Lalu tampaklah kepadanya matahari\nterbit, ketika ia telah melewati Pniel; dan\nYakub pincang karena pangkal pahanya.'),(960,1,32,32,'Itulah sebabnya sampai sekarang\norang Israel tidak memakan daging yang\nmenutupi sendi pangkal paha, karena\nDia telah memukul sendi pangkal paha\nYakub, pada otot pangkal pahanya.'),(961,1,33,1,'Yakubpun melayangkan pandangnya, lalu dilihatnyalah Esau datang dengan diiringi oleh empat\nratus orang. Maka diserahkannyalah sebagian dari anak-anak itu kepada Lea\ndan sebagian kepada Rahel serta kepada\nkedua budak perempuan itu.'),(962,1,33,2,'Ia menempatkan budak-budak\nperempuan itu beserta anak-anak\nmereka di muka, Lea beserta anakanaknya di belakang mereka, dan Rahel\nbeserta Yusuf di belakang sekali.'),(963,1,33,3,'Dan ia sendiri berjalan di depan\nmereka dan ia sujud sampai ke tanah\ntujuh kali, hingga ia sampai ke dekat\nkakaknya itu.'),(964,1,33,4,'Tetapi Esau berlari mendapatkan dia,\ndidekapnya dia, dipeluk lehernya dan\ndiciumnya dia, lalu bertangis-tangisanlah\nmereka.'),(965,1,33,5,'Kemudian Esau melayangkan\npandangnya, dilihatnyalah perempuanperempuan dan anak-anak itu, lalu ia\nbertanya: \"Siapakah orang-orang yang\nbeserta engkau itu?\" Jawab Yakub:\n\"Anak-anak yang telah dikaruniakan\nAllah kepada hambamu ini.\"'),(966,1,33,6,'Sesudah itu mendekatlah budak-budak\nperempuan itu beserta anak-anaknya,\nlalu mereka sujud.'),(967,1,33,7,'Mendekat jugalah Lea beserta\nanak-anaknya, dan merekapun sujud. Kemudian mendekatlah Yusuf beserta\nRahel, dan mereka juga sujud.'),(968,1,33,8,'Berkatalah Esau: \"Apakah maksudmu\ndengan seluruh pasukan, yang telah\nbertemu dengan aku tadi?\" Jawabnya:\n\"Untuk mendapat kasih tuanku.\"'),(969,1,33,9,'Tetapi kata Esau: \"Aku mempunyai\nbanyak, adikku; peganglah apa yang\nada padamu.\"'),(970,1,33,10,'Tetapi kata Yakub: \"Janganlah kiranya\ndemikian; jikalau aku telah mendapat\nkasihmu, terimalah persembahanku ini\ndari tanganku, karena memang melihat\nmukamu adalah bagiku serasa melihat\nwajah Allah, dan engkaupun berkenan\nmenyambut aku.'),(971,1,33,11,'Terimalah kiranya pemberian\ntanda salamku ini, yang telah kubawa\nkepadamu, sebab Allah telah memberi\nkarunia kepadaku dan akupun\nmempunyai segala-galanya.\" Lalu\ndibujuk-bujuknyalah Esau, sehingga\nditerimanya.'),(972,1,33,12,'Kata Esau: \"Baiklah kita berangkat\nberjalan terus; aku akan menyertai\nengkau.'),(973,1,33,13,'Tetapi Yakub berkata kepadanya:\n\"Tuanku maklum, bahwa anak-anak ini masih kurang kuat, dan bahwa beserta\naku ada kambing domba dan lembu sapi\nyang masih menyusui, jika diburu-buru,\nsatu hari saja, maka seluruh kumpulan\nbinatang itu akan mati.'),(974,1,33,14,'Biarlah kiranya tuanku berjalan\nlebih dahulu dari hambamu ini dan aku\nmau dengan hati-hati beringsut maju\nmenurut langkah hewan, yang berjalan\ndi depanku dan menurut langkah\nanak-anak, sampai aku tiba pada tuanku\ndi Seir.\"'),(975,1,33,15,'Lalu kata Esau: \"Kalau begitu,\nbaiklah kutinggalkan padamu beberapa\norang dari pengiringku.\" Tetapi Yakub\nberkata: \"Tidak usah demikian! Biarlah\naku mendapat kasih tuanku saja.\"'),(976,1,33,16,'Jadi pulanglah Esau pada hari itu\nberjalan ke Seir.'),(977,1,33,17,'Tetapi Yakub berangkat ke Sukot, lalu\nmendirikan rumah, dan untuk ternaknya\ndibuatnya gubuk-gubuk. Itulah sebabnya\ntempat itu dinamai Sukot.'),(978,1,33,18,'Dalam perjalanannya dari PadanAram sampailah Yakub dengan selamat\nke Sikhem, di tanah Kanaan, lalu ia\nberkemah di sebelah timur kota itu.'),(979,1,33,19,'Kemudian dibelinyalah dari anakanak Hemor, bapa Sikhem, sebidang\ntanah, tempat ia memasang kemahnya,\ndengan harga seratus kesita.'),(980,1,33,20,'Ia mendirikan mezbah di situ dan\ndinamainya itu: \"Allah Israel ialah Allah.\"'),(981,1,34,1,'Pada suatu kali pergilah Dina, anak perempuan Lea yang dilahirkannya bagi Yakub, mengunjungi\nperempuan-perempuan di negeri itu.'),(982,1,34,2,'Ketika itu terlihatlah ia oleh Sikhem,\nanak Hemor, orang Hewi, raja negeri\nitu, lalu Dina itu dilarikannya dan\ndiperkosanya.'),(983,1,34,3,'Tetapi terikatlah hatinya kepada Dina,\nanak Yakub; ia cinta kepada gadis itu,\nlalu menenangkan hati gadis itu.'),(984,1,34,4,'Sebab itu berkatalah Sikhem kepada\nHemor, ayahnya: \"Ambillah bagiku gadis\nini untuk menjadi isteriku.\"'),(985,1,34,5,'Kedengaranlah kepada Yakub,\nbahwa Sikhem mencemari Dina. Tetapi\nanak-anaknya ada di padang menjaga\nternaknya, jadi Yakub mendiamkan soal\nitu sampai mereka pulang.'),(986,1,34,6,'Lalu Hemor ayah Sikhem, pergi\nmendapatkan Yakub untuk berbicara\ndengan dia.'),(987,1,34,7,'Sementara itu anak-anak Yakub\npulang dari padang, dan sesudah\nmendengar peristiwa itu orang-orang\nini sakit hati dan sangat marah karena\nSikhem telah berbuat noda di antara\norang Israel dengan memperkosa anak\nperempuan Yakub, sebab yang demikian\nitu tidak patut dilakukan.'),(988,1,34,8,'Berbicaralah Hemor kepada mereka\nitu: \"Hati Sikhem anakku mengingini\nanakmu; kiranya kamu memberikan dia\nkepadanya menjadi isterinya'),(989,1,34,9,'dan biarlah kita ambil-mengambil:\nberikanlah gadis-gadis kamu kepada\nkami dan ambillah gadis-gadis kami.'),(990,1,34,10,'Tinggallah pada kami: negeri ini\nterbuka untuk kamu; tinggallah di sini,\njalanilah negeri ini dengan bebas, dan\nmenetaplah di sini.\"'),(991,1,34,11,'Lalu Sikhem berkata kepada ayah\nanak itu dan kepada kakak-kakaknya:\n\"Biarlah kiranya aku mendapat kasihmu,\naku akan memberikan kepadamu apa\nyang kamu minta;'),(992,1,34,12,'walaupun kamu bebankan kepadaku\nuang jujuran dan uang mahar seberapa\nbanyakpun, aku akan memberikan apa yang kamu minta; tetapi berilah gadis\nitu kepadaku menjadi isteriku.\"'),(993,1,34,13,'Lalu anak-anak Yakub menjawab\nSikhem dan Hemor, ayahnya, dengan\ntipu muslihat. Karena Sikhem telah\nmencemari Dina, adik mereka itu,'),(994,1,34,14,'berkatalah mereka kepada kedua\norang itu: \"Kami tidak dapat berbuat\ndemikian, memberikan adik kami kepada\nseorang laki-laki yang tidak bersunat,\nsebab hal itu aib bagi kami.'),(995,1,34,15,'Hanyalah dengan syarat ini kami\ndapat menyetujui permintaanmu: kamu\nharus sama seperti kami, yaitu setiap\nlaki-laki di antara kamu harus disunat,'),(996,1,34,16,'barulah kami akan memberikan\ngadis-gadis kami kepada kamu dan\nmengambil gadis-gadis kamu; maka\nkami akan tinggal padamu, dan kita\nakan menjadi satu bangsa.'),(997,1,34,17,'Tetapi jika kamu tidak mendengarkan\nperkataan kami dan kamu tidak disunat,\nmaka kami akan mengambil kembali\nanak itu, lalu pergi.\"'),(998,1,34,18,'Lalu Hemor dan Sikhem, anak Hemor,\nmenyetujui usul mereka.'),(999,1,34,19,'Dan orang muda itu tidak bertangguh\nmelakukannya, sebab ia suka kepada  anak Yakub, lagipula ia seorang yang\npaling dihormati di antara seluruh kaum\nkeluarganya.'),(1000,1,34,20,'Lalu pergilah Hemor dan Sikhem,\nanaknya itu, ke pintu gerbang kota\nmereka dan mereka berbicara kepada\npenduduk kota itu:'),(1001,1,34,21,'\"Orang-orang itu mau hidup damai\ndengan kita, biarlah mereka tinggal di\nnegeri ini dan menjalaninya dengan\nbebas; bukankah negeri ini cukup\nluas untuk mereka? Maka kita dapat\nmengambil gadis-gadis mereka menjadi\nisteri kita dan kita dapat memberikan\ngadis-gadis kita kepada mereka.'),(1002,1,34,22,'Namun hanya dengan syarat ini\norang-orang itu setuju tinggal bersamasama dengan kita, sehingga kita menjadi\nsatu bangsa, yaitu setiap laki-laki di\nantara kita harus disunat seperti mereka\nbersunat.'),(1003,1,34,23,'Ternak mereka, harta benda mereka\ndan segala hewan mereka, bukankah\nsemuanya itu akan menjadi milik\nkita? Hanya biarlah kita menyetujui\npermintaan mereka, sehingga mereka\ntetap tinggal pada kita.\"'),(1004,1,34,24,'Maka usul Hemor dan Sikhem,\nanaknya itu, didengarkan oleh semua\norang yang datang berkumpul di pintu\ngerbang kota itu, lalu disunatlah setiap\nlaki-laki, yakni setiap orang dewasa di\nkota itu.'),(1005,1,34,25,'Pada hari ketiga, ketika mereka\nsedang menderita kesakitan, datanglah\ndua orang anak Yakub, yaitu Simeon\ndan Lewi, kakak-kakak Dina, setelah\nmasing-masing mengambil pedangnya,\nmenyerang kota itu dengan tidak\ntakut-takut serta membunuh setiap\nlaki-laki.'),(1006,1,34,26,'Juga Hemor dan Sikhem, anaknya,\ndibunuh mereka dengan mata pedang,\ndan mereka mengambil Dina dari rumah\nSikhem, lalu pergi.'),(1007,1,34,27,'Kemudian datanglah anak-anak\nYakub merampasi orang-orang yang\nterbunuh itu, lalu menjarah kota itu,\nkarena adik mereka telah dicemari.'),(1008,1,34,28,'Kambing dombanya dan lembu\nsapinya, keledainya dan segala yang\ndi dalam dan di luar kota itu dibawa\nmereka;'),(1009,1,34,29,'segala kekayaannya, semua anaknya\ndan perempuannya ditawan dan dijarah mereka, juga seluruhnya yang ada di\nrumah-rumah.'),(1010,1,34,30,'Yakub berkata kepada Simeon dan\nLewi: \"Kamu telah mencelakakan aku\ndengan membusukkan namaku kepada\npenduduk negeri ini, kepada orang\nKanaan dan orang Feris, padahal kita ini\nhanya sedikit jumlahnya; apabila mereka\nbersekutu melawan kita, tentulah\nmereka akan memukul kita kalah, dan\nkita akan dipunahkan, aku beserta seisi\nrumahku.\"'),(1011,1,34,31,'Tetapi jawab mereka: \"Mengapa adik\nkita diperlakukannya sebagai seorang\nperempuan sundal!\"'),(1012,1,35,1,'Allah berfirman kepada Yakub: \"Bersiaplah, pergilah ke Betel, tinggallah di situ, dan buatlah di\nsitu mezbah bagi Allah, yang telah\nmenampakkan diri kepadamu, ketika\nengkau lari dari Esau, kakakmu.\"'),(1013,1,35,2,'Lalu berkatalah Yakub kepada seisi\nrumahnya dan kepada semua orang\nyang bersama-sama dengan dia:\n\"Jauhkanlah dewa-dewa asing yang ada\ndi tengah-tengah kamu, tahirkanlah\ndirimu dan tukarlah pakaianmu.'),(1014,1,35,3,'Marilah kita bersiap dan pergi ke\nBetel; aku akan membuat mezbah di situ\nbagi Allah, yang telah menjawab aku\npada masa kesesakanku dan yang telah\nmenyertai aku di jalan yang kutempuh.\"'),(1015,1,35,4,'Mereka menyerahkan kepada Yakub\nsegala dewa asing yang dipunyai mereka\ndan anting-anting yang ada pada telinga\nmereka, lalu Yakub menanamnya di\nbawah pohon besar yang dekat Sikhem.'),(1016,1,35,5,'Sesudah itu berangkatlah mereka.\nDan kedahsyatan yang dari Allah\nmeliputi kota-kota sekeliling mereka,\nsehingga anak-anak Yakub tidak dikejar.'),(1017,1,35,6,'Lalu sampailah Yakub ke Lus yang\ndi tanah Kanaan--yaitu Betel--,ia dan\nsemua orang yang bersama-sama\ndengan dia.'),(1018,1,35,7,'Didirikannyalah mezbah di situ, dan\ndinamainyalah tempat itu El-Betel,\nkarena Allah telah menyatakan diri\nkepadanya di situ, ketika ia lari terhadap\nkakaknya.'),(1019,1,35,8,'Ketika Debora, inang pengasuh Ribka,\nmati, dikuburkanlah ia di sebelah hilir\nBetel di bawah pohon besar, yang\ndinamai orang: Pohon Besar Penangisan.'),(1020,1,35,9,'Setelah Yakub datang dari PadanAram, maka Allah menampakkan diri\npula kepadanya dan memberkati dia.'),(1021,1,35,10,'Firman Allah kepadanya: \"Namamu\nYakub; dari sekarang namamu bukan\nlagi Yakub, melainkan Israel, itulah yang\nakan menjadi namamu.\" Maka Allah\nmenamai dia Israel.'),(1022,1,35,11,'Lagi firman Allah kepadanya: \"Akulah\nAllah Yang Mahakuasa. Beranakcuculah\ndan bertambah banyak; satu bangsa,\nbahkan sekumpulan bangsa-bangsa,\nakan terjadi dari padamu dan raja-raja\nakan berasal dari padamu.'),(1023,1,35,12,'Dan negeri ini yang telah Kuberikan\nkepada Abraham dan kepada Ishak,\nakan Kuberikan kepadamu dan juga\nkepada keturunanmu.\"'),(1024,1,35,13,'Lalu naiklah Allah meninggalkan\nYakub dari tempat Ia berfirman\nkepadanya.'),(1025,1,35,14,'Kemudian Yakub mendirikan tugu\ndi tempat itu, yakni tugu batu; ia\nmempersembahkan korban curahan dan\nmenuangkan minyak di atasnya.'),(1026,1,35,15,'Yakub menamai tempat di mana\nAllah telah berfirman kepadanya \"Betel\".'),(1027,1,35,16,'Sesudah itu berangkatlah mereka\ndari Betel. Ketika mereka tidak berapa\njauh lagi dari Efrata, bersalinlah Rahel,\ndan bersalinnya itu sangat sukar.'),(1028,1,35,17,'Sedang ia sangat sukar bersalin,\nberkatalah bidan kepadanya: \"Janganlah\ntakut, sekali inipun anak laki-laki yang\nkaudapat.\"'),(1029,1,35,18,'Dan ketika ia hendak\nmenghembuskan nafas--sebab ia\nmati kemudian--diberikannyalah nama\nBen-oni kepada anak itu, tetapi ayahnya\nmenamainya Benyamin.'),(1030,1,35,19,'Demikianlah Rahel mati, lalu ia\ndikuburkan di sisi jalan ke Efrata, yaitu\nBetlehem.'),(1031,1,35,20,'Yakub mendirikan tugu di atas\nkuburnya; itulah tugu kubur Rahel\nsampai sekarang.'),(1032,1,35,21,'Sesudah itu berangkatlah Israel, lalu\nia memasang kemahnya di seberang\nMigdal-Eder.'),(1033,1,35,22,'Ketika Israel diam di negeri ini,\nterjadilah bahwa Ruben sampai tidur\ndengan Bilha, gundik ayahnya, dan\nkedengaranlah hal itu kepada Israel.\n(35-22b) Adapun anak-anak lelaki Yakub\ndua belas orang jumlahnya.'),(1034,1,35,23,'Anak-anak Lea ialah Ruben, anak\nsulung Yakub, kemudian Simeon, Lewi,\nYehuda, Isakhar dan Zebulon.'),(1035,1,35,24,'Anak-anak Rahel ialah Yusuf dan\nBenyamin.'),(1036,1,35,25,'Dan anak-anak Bilha, budak\nperempuan Rahel ialah Dan serta Naftali.'),(1037,1,35,26,'Dan anak-anak Zilpa, budak\nperempuan Lea ialah Gad dan Asyer.\nItulah anak-anak lelaki Yakub, yang\ndilahirkan baginya di Padan-Aram.'),(1038,1,35,27,'Lalu sampailah Yakub kepada Ishak,\nayahnya, di Mamre dekat Kiryat-Arba-\n-itulah Hebron--tempat Abraham dan\nIshak tinggal sebagai orang asing.'),(1039,1,35,28,'Adapun umur Ishak seratus delapan\npuluh tahun.'),(1040,1,35,29,'Lalu meninggallah Ishak, ia mati dan\ndikumpulkan kepada kaum leluhurnya;\nia tua dan suntuk umur, maka Esau dan\nYakub, anak-anaknya itu, menguburkan\ndia.'),(1041,1,36,1,'Inilah keturunan Esau, yaitu Edom.'),(1042,1,36,2,'Esau mengambil perempuanperempuan Kanaan menjadi isterinya,\nyakni Ada, anak Elon orang Het, dan Oholibama, anak Ana anak Zibeon orang\nHewi,'),(1043,1,36,3,'dan Basmat, anak Ismael, adik\nNebayot.'),(1044,1,36,4,'Ada melahirkan Elifas bagi Esau, dan\nBasmat melahirkan Rehuel,'),(1045,1,36,5,'dan Oholibama melahirkan Yeush,\nYaelam dan Korah. Itulah anak-anak\nEsau, yang lahir baginya di tanah\nKanaan'),(1046,1,36,6,'Esau membawa isteri-isterinya,\nanak-anaknya lelaki dan perempuan dan\nsemua orang yang ada di rumahnya,\nternaknya, segala hewannya dan segala\nharta bendanya yang telah diperolehnya\ndi tanah Kanaan, lalu pergilah ia ke\nnegeri lain dan ia meninggalkan Yakub,\nadiknya itu.'),(1047,1,36,7,'Sebab harta milik mereka terlalu\nbanyak, sehingga mereka tidak dapat\ntinggal bersama-sama, dan negeri\npenumpangan mereka tidak dapat\nmemuat mereka karena banyaknya\nternak mereka itu.'),(1048,1,36,8,'Maka menetaplah Esau di pegunungan\nSeir; Esau itulah Edom.'),(1049,1,36,9,'Inilah keturunan Esau, bapa orang\nEdom, di pegunungan Seir.'),(1050,1,36,10,'Nama anak-anaknya ialah: Elifas,\nanak Ada isteri Esau; Rehuel, anak\nBasmat isteri Esau.'),(1051,1,36,11,'Anak-anak Elifas ialah Teman, Omar,\nZefo, Gaetam dan Kenas.'),(1052,1,36,12,'Timna adalah gundik Elifas anak\nEsau; ia melahirkan Amalek bagi Elifas.\nItulah cucu-cucu Ada isteri Esau.'),(1053,1,36,13,'Inilah anak-anak Rehuel: Nahat,\nZerah, Syama dan Miza. Itulah cucu-cucu\nBasmat isteri Esau.'),(1054,1,36,14,'Inilah anak-anak Oholibama, isteri\nEsau itu, anak Ana anak Zibeon; ia\nmelahirkan bagi Esau: Yeush, Yaelam\ndan Korah.'),(1055,1,36,15,'Inilah kepala-kepala kaum bani Esau:\nketurunan Elifas anak sulung Esau, ialah\nkepala kaum Teman, kepala kaum Omar,\nkepala kaum Zefo, kepala kaum Kenas,'),(1056,1,36,16,'kepala kaum Korah, kepala kaum\nGaetam dan kepala kaum Amalek; itulah\nkepala-kepala kaum Elifas di tanah\nEdom; itulah keturunan Ada.'),(1057,1,36,17,'Inilah keturunan Rehuel anak Esau:\nkepala kaum Nahat, kepala kaum Zerah,\nkepala kaum Syama dan kepala kaum\nMiza; itulah kepala-kepala kaum Rehuel di tanah Edom; itulah keturunan Basmat\nisteri Esau.'),(1058,1,36,18,'Inilah keturunan Oholibama isteri\nEsau: kepala kaum Yeush, kepala kaum\nYaelam, kepala kaum Korah; itulah\nkepala-kepala kaum Oholibama, isteri\nEsau, anak Ana.'),(1059,1,36,19,'Itulah bani Esau, yakni Edom, dan\nitulah kepala-kepala kaum mereka.'),(1060,1,36,20,'Inilah anak-anak Seir, orang Hori,\npenduduk negeri itu: Lotan, Syobal,\nZibeon, Ana,'),(1061,1,36,21,'Disyon, Ezer, Disyan; itulah kepalakepala kaum orang Hori, anak-anak Seir,\ndi tanah Edom.'),(1062,1,36,22,'Anak-anak Lotan ialah Hori dan\nHeman, dan saudara perempuan Lotan\nialah Timna.'),(1063,1,36,23,'Inilah anak-anak Syobal: Alwan,\nManahat, Ebal, Syefo dan Onam.'),(1064,1,36,24,'Inilah anak-anak Zibeon: Aya dan\nAna; Ana inilah yang menemui matamata air panas di padang gurun, ketika\nia sedang menggembalakan keledai\nZibeon ayahnya itu.'),(1065,1,36,25,'Inilah anak-anak Ana: Disyon dan\nOholibama anak perempuan Ana.'),(1066,1,36,26,'Inilah anak-anak Disyon: Hemdan,\nEsyban, Yitran dan Keran.'),(1067,1,36,27,'Inilah anak-anak Ezer: Bilhan,\nZaawan dan Akan.'),(1068,1,36,28,'Inilah anak-anak Disyan: Us dan\nAran.'),(1069,1,36,29,'Itulah kepala-kepala kaum orang\nHori: kepala kaum Lotan, kepala kaum\nSyobal, kepala kaum Zibeon, kepala\nkaum Ana,'),(1070,1,36,30,'kepala kaum Disyon, kepala kaum\nEzer dan kepala kaum Disyan; itulah\nkepala-kepala kaum orang Hori, kaum\ndemi kaum, di tanah Seir.'),(1071,1,36,31,'Inilah raja-raja yang memerintah di\ntanah Edom, sebelum ada seorang raja\nmemerintah atas orang Israel.'),(1072,1,36,32,'Di Edom yang memerintah ialah Bela\nbin Beor dan kotanya bernama Dinhaba.'),(1073,1,36,33,'Setelah Bela mati, Yobab bin Zerah\ndari Bozra menjadi raja menggantikan\ndia.'),(1074,1,36,34,'Setelah Yobab mati, Husyam, dari\nnegeri orang Teman, menjadi raja\nmenggantikan dia.'),(1075,1,36,35,'Setelah Husyam mati, Hadad bin\nBedad menjadi raja menggantikan dia;\ndialah yang memukul kalah orang Midian di daerah Moab, dan kotanya bernama\nAwit.'),(1076,1,36,36,'Setelah Hadad mati, Samla dari\nMasreka menjadi raja menggantikan dia.'),(1077,1,36,37,'Setelah Samla mati, Saul, dari\nRehobot yang di pinggir sungai, menjadi\nraja menggantikan dia.'),(1078,1,36,38,'Setelah Saul mati, Baal-Hanan bin\nAkhbor menjadi raja menggantikan dia.'),(1079,1,36,39,'Setelah Baal-Hanan bin Akhbor mati,\nHadar menjadi raja menggantikan dia;\nkotanya bernama Pahu dan isterinya\nbernama Mehetabeel binti Matred binti\nMezahab.'),(1080,1,36,40,'Inilah nama kepala-kepala kaum\nEsau menurut kaum dan tempat mereka,\ndengan nama mereka masing-masing:\nkepala kaum Timna, kepala kaum Alwa,\nkepala kaum Yetet'),(1081,1,36,41,'kepala kaum Oholibama, kepala\nkaum Ela, kepala kaum Pinon,'),(1082,1,36,42,'kepala kaum Kenas, kepala kaum\nTeman, kepala kaum Mibzar,'),(1083,1,36,43,'kepala kaum Magdiel dan kepala\nkaum Iram; itulah kepala-kepala kaum\nEdom, menurut tempat kediaman\nmereka di tanah milik mereka; Edom\nialah Esau, bapa orang Edom.'),(1084,1,37,1,'Adapun Yakub, ia diam di negeri penumpangan ayahnya, yakni di tanah Kanaan.'),(1085,1,37,2,'Inilah riwayat keturunan Yakub. Yusuf,\ntatkala berumur tujuh belas tahun--jadi\nmasih muda--biasa menggembalakan\nkambing domba, bersama-sama dengan\nsaudara-saudaranya, anak-anak Bilha\ndan Zilpa, kedua isteri ayahnya.\nDan Yusuf menyampaikan kepada\nayahnya kabar tentang kejahatan\nsaudara-saudaranya.'),(1086,1,37,3,'Israel lebih mengasihi Yusuf dari\nsemua anaknya yang lain, sebab Yusuf\nitulah anaknya yang lahir pada masa\ntuanya; dan ia menyuruh membuat\njubah yang maha indah bagi dia.'),(1087,1,37,4,'Setelah dilihat oleh saudarasaudaranya, bahwa ayahnya lebih\nmengasihi Yusuf dari semua saudaranya,\nmaka bencilah mereka itu kepadanya\ndan tidak mau menyapanya dengan\nramah.'),(1088,1,37,5,'Pada suatu kali bermimpilah Yusuf,\nlalu mimpinya itu diceritakannya kepada\nsaudara-saudaranya; sebab itulah\nmereka lebih benci lagi kepadanya.'),(1089,1,37,6,'Karena katanya kepada mereka: \"Coba\ndengarkan mimpi yang kumimpikan ini:'),(1090,1,37,7,'Tampak kita sedang di ladang\nmengikat berkas-berkas gandum, lalu\nbangkitlah berkasku dan tegak berdiri;\nkemudian datanglah berkas-berkas\nkamu sekalian mengelilingi dan sujud\nmenyembah kepada berkasku itu.\"'),(1091,1,37,8,'Lalu saudara-saudaranya berkata\nkepadanya: \"Apakah engkau ingin\nmenjadi raja atas kami? Apakah engkau\ningin berkuasa atas kami?\" Jadi makin\nbencilah mereka kepadanya karena\nmimpinya dan karena perkataannya itu.'),(1092,1,37,9,'Lalu ia memimpikan pula mimpi\nyang lain, yang diceritakannya kepada\nsaudara-saudaranya. Katanya: \"Aku\nbermimpi pula: Tampak matahari, bulan\ndan sebelas bintang sujud menyembah\nkepadaku.\"'),(1093,1,37,10,'Lalu ia memimpikan pula mimpi\nyang lain, yang diceritakannya kepada\nsaudara-saudaranya. Katanya: \"Aku\nbermimpi pula: Tampak matahari, bulan\ndan sebelas bintang sujud menyembah\nkepadaku.\"'),(1094,1,37,11,'Maka iri hatilah saudara-saudaranya\nkepadanya, tetapi ayahnya menyimpan\nhal itu dalam hatinya.'),(1095,1,37,12,'Pada suatu kali pergilah saudarasaudaranya menggembalakan kambing\ndomba ayahnya dekat Sikhem.'),(1096,1,37,13,'Lalu Israel berkata kepada Yusuf:\n\"Bukankah saudara-saudaramu\nmenggembalakan kambing domba dekat\nSikhem? Marilah engkau kusuruh kepada\nmereka.\" Sahut Yusuf: \"Ya bapa.\"'),(1097,1,37,14,'Kata Israel kepadanya: \"Pergilah\nengkau melihat apakah baik keadaan\nsaudara-saudaramu dan keadaan\nkambing domba; dan bawalah kabar\ntentang itu kepadaku.\" Lalu Yakub\nmenyuruh dia dari lembah Hebron, dan\nYusufpun sampailah ke Sikhem.'),(1098,1,37,15,'Ketika Yusuf berjalan ke sana\nke mari di padang, bertemulah ia\ndengan seorang laki-laki, yang bertanya\nkepadanya: \"Apakah yang kaucari?\"'),(1099,1,37,16,'Sahutnya: \"Aku mencari saudarasaudaraku. Tolonglah katakan kepadaku\ndi mana mereka menggembalakan\nkambing domba?\"'),(1100,1,37,17,'Lalu kata orang itu: \"Mereka\ntelah berangkat dari sini, sebab telah kudengar mereka berkata: Marilah\nkita pergi ke Dotan.\" Maka Yusuf\nmenyusul saudara-saudaranya itu dan\ndidapatinyalah mereka di Dotan.'),(1101,1,37,18,'Dari jauh ia telah kelihatan\nkepada mereka. Tetapi sebelum ia\ndekat pada mereka, mereka telah\nbermufakat mencari daya upaya untuk\nmembunuhnya.'),(1102,1,37,19,'Kata mereka seorang kepada yang\nlain: \"Lihat, tukang mimpi kita itu\ndatang!'),(1103,1,37,20,'Sekarang, marilah kita bunuh dia\ndan kita lemparkan ke dalam salah satu\nsumur ini, lalu kita katakan: seekor\nbinatang buas telah menerkamnya. Dan\nkita akan lihat nanti, bagaimana jadinya\nmimpinya itu!\"'),(1104,1,37,21,'Ketika Ruben mendengar hal ini,\nia ingin melepaskan Yusuf dari tangan\nmereka, sebab itu katanya: \"Janganlah\nkita bunuh dia!\"'),(1105,1,37,22,'Lagi kata Ruben kepada mereka:\n\"Janganlah tumpahkan darah,\nlemparkanlah dia ke dalam sumur yang\nada di padang gurun ini, tetapi janganlah\napa-apakan dia\" --maksudnya hendak\nmelepaskan Yusuf dari tangan mereka dan membawanya kembali kepada\nayahnya.'),(1106,1,37,23,'Baru saja Yusuf sampai kepada\nsaudara-saudaranya, merekapun\nmenanggalkan jubah Yusuf, jubah maha\nindah yang dipakainya itu.'),(1107,1,37,24,'Dan mereka membawa dia dan\nmelemparkan dia ke dalam sumur.\nSumur itu kosong, tidak berair.'),(1108,1,37,25,'Kemudian duduklah mereka untuk\nmakan. Ketika mereka mengangkat\nmuka, kelihatanlah kepada mereka\nsuatu kafilah orang Ismael datang\ndari Gilead dengan untanya yang\nmembawa damar, balsam dan damar\nladan, dalam perjalanannya mengangkut\nbarang-barang itu ke Mesir.'),(1109,1,37,26,'Lalu kata Yehuda kepada saudarasaudaranya itu: \"Apakah untungnya\nkalau kita membunuh adik kita itu dan\nmenyembunyikan darahnya?'),(1110,1,37,27,'Marilah kita jual dia kepada orang\nIsmael ini, tetapi janganlah kita apaapakan dia, karena ia saudara kita, darah\ndaging kita.\" Dan saudara-saudaranya\nmendengarkan perkataannya itu.'),(1111,1,37,28,'Ketika ada saudagar-saudagar Midian\nlewat, Yusuf diangkat ke atas dari dalam sumur itu, kemudian dijual kepada orang\nIsmael itu dengan harga dua puluh\nsyikal perak. Lalu Yusuf dibawa mereka\nke Mesir.'),(1112,1,37,29,'Ketika Ruben kembali ke sumur\nitu, ternyata Yusuf tidak ada lagi di\ndalamnya. Lalu dikoyakkannyalah\nbajunya,'),(1113,1,37,30,'dan kembalilah ia kepada saudarasaudaranya, katanya: \"Anak itu tidak\nada lagi, ke manakah aku ini?\"'),(1114,1,37,31,'Kemudian mereka mengambil\njubah Yusuf, dan menyembelih seekor\nkambing, lalu mencelupkan jubah itu ke\ndalam darahnya.'),(1115,1,37,32,'Jubah maha indah itu mereka suruh\nantarkan kepada ayah mereka dengan\npesan: \"Ini kami dapati. Silakanlah bapa\nperiksa apakah jubah ini milik anak bapa\natau tidak?\"'),(1116,1,37,33,'Ketika Yakub memeriksa jubah itu,\nia berkata: \"Ini jubah anakku; binatang\nbuas telah memakannya; tentulah Yusuf\ntelah diterkam.\"'),(1117,1,37,34,'Dan Yakub mengoyakkan jubahnya,\nlalu mengenakan kain kabung pada\npinggangnya dan berkabunglah ia berhari-hari lamanya karena anaknya\nitu.'),(1118,1,37,35,'Sekalian anaknya laki-laki dan\nperempuan berusaha menghiburkan\ndia, tetapi ia menolak dihiburkan, serta\nkatanya: \"Tidak! Aku akan berkabung,\nsampai aku turun mendapatkan anakku,\nke dalam dunia orang mati!\" Demikianlah\nYusuf ditangisi oleh ayahnya.'),(1119,1,37,36,'Adapun Yusuf, ia dijual oleh orang\nMidian itu ke Mesir, kepada Potifar,\nseorang pegawai istana Firaun, kepala\npengawal raja.'),(1120,1,38,1,'Pada waktu itu Yehuda meninggalkan saudara-saudaranya dan menumpang pada\nseorang Adulam, yang namanya Hira.'),(1121,1,38,2,'Di situ Yehuda melihat anak\nperempuan seorang Kanaan; nama\norang itu ialah Syua. Lalu Yehuda\nkawin dengan perempuan itu dan\nmenghampirinya.'),(1122,1,38,3,'Perempuan itu mengandung, lalu\nmelahirkan seorang anak laki-laki dan\nmenamai anak itu Er.'),(1123,1,38,4,'Sesudah itu perempuan itu\nmengandung lagi, lalu melahirkan seorang anak laki-laki dan menamai\nanak itu Onan.'),(1124,1,38,5,'Kemudian perempuan itu melahirkan\nseorang anak laki-laki sekali lagi,\ndan menamai anak itu Syela. Yehuda\nsedang berada di Kezib, ketika anak itu\ndilahirkan.'),(1125,1,38,6,'Sesudah itu Yehuda mengambil bagi\nEr, anak sulungnya, seorang isteri, yang\nbernama Tamar.'),(1126,1,38,7,'Tetapi Er, anak sulung Yehuda itu,\nadalah jahat di mata TUHAN, maka\nTUHAN membunuh dia.'),(1127,1,38,8,'Lalu berkatalah Yehuda kepada Onan:\n\"Hampirilah isteri kakakmu itu, kawinlah\ndengan dia sebagai ganti kakakmu dan\nbangkitkanlah keturunan bagi kakakmu.\"'),(1128,1,38,9,'Tetapi Onan tahu, bahwa bukan ia\nyang empunya keturunannya nanti,\nsebab itu setiap kali ia menghampiri\nisteri kakaknya itu, ia membiarkan\nmaninya terbuang, supaya ia jangan\nmemberi keturunan kepada kakaknya.'),(1129,1,38,10,'Tetapi yang dilakukannya itu adalah\njahat di mata TUHAN, maka TUHAN\nmembunuh dia juga.'),(1130,1,38,11,'Lalu berkatalah Yehuda kepada\nTamar, menantunya itu: \"Tinggallah sebagai janda di rumah ayahmu, sampai\nanakku Syela itu besar,\" sebab pikirnya:\n\"Jangan-jangan ia mati seperti kedua\nkakaknya itu.\" Maka pergilah Tamar dan\ntinggal di rumah ayahnya.'),(1131,1,38,12,'Setelah beberapa lama matilah anak\nSyua, isteri Yehuda. Habis berkabung\npergilah Yehuda ke Timna, kepada\norang-orang yang menggunting bulu\ndomba-dombanya, bersama dengan\nHira, sahabatnya, orang Adulam itu.'),(1132,1,38,13,'Ketika dikabarkan kepada Tamar:\n\"Bapa mertuamu sedang di jalan\nke Timna untuk menggunting bulu\ndomba-dombanya,\"'),(1133,1,38,14,'maka ditanggalkannyalah pakaian\nkejandaannya, ia bertelekung dan\nberselubung, lalu pergi duduk di pintu\nmasuk ke Enaim yang di jalan ke Timna,\nkarena dilihatnya, bahwa Syela telah\nmenjadi besar, dan dia tidak diberikan\njuga kepada Syela itu untuk menjadi\nisterinya.'),(1134,1,38,15,'Ketika Yehuda melihat dia,\ndisangkanyalah dia seorang perempuan\nsundal, karena ia menutupi mukanya.'),(1135,1,38,16,'Lalu berpalinglah Yehuda\nmendapatkan perempuan yang di pinggir jalan itu serta berkata: \"Marilah,\naku mau menghampiri engkau,\" sebab\nia tidak tahu, bahwa perempuan\nitu menantunya. Tanya perempuan\nitu: \"Apakah yang akan kauberikan\nkepadaku, jika engkau menghampiri\naku?\"'),(1136,1,38,17,'Jawabnya: \"Aku akan mengirimkan\nkepadamu seekor anak kambing dari\nkambing dombaku.\" Kata perempuan\nitu: \"Asal engkau memberikan\ntanggungannya, sampai engkau\nmengirimkannya kepadaku.\"'),(1137,1,38,18,'Tanyanya: \"Apakah tanggungan yang\nharus kuberikan kepadamu?\" Jawab\nperempuan itu: \"Cap meteraimu serta\nkalungmu dan tongkat yang ada di\ntanganmu itu.\" Lalu diberikannyalah\nsemuanya itu kepadanya, maka ia\nmenghampirinya. Perempuan itu\nmengandung dari padanya.'),(1138,1,38,19,'Bangunlah perempuan itu, lalu\npergi, ditanggalkannya telekungnya\ndan dikenakannya pula pakaian\nkejandaannya.'),(1139,1,38,20,'Adapun Yehuda, ia mengirimkan\nanak kambing itu dengan perantaraan\nsahabatnya, orang Adulam itu, untuk  mengambil kembali tanggungannya dari\ntangan perempuan itu, tetapi perempuan\nitu tidak dijumpainya lagi.'),(1140,1,38,21,'Ia bertanya-tanya di tempat tinggal\nperempuan itu: \"Di manakah perempuan\njalang, yang duduk tadinya di pinggir\njalan di Enaim itu?\" Jawab mereka:\n\"Tidak ada di sini perempuan jalang.\"'),(1141,1,38,22,'Kembalilah ia kepada Yehuda dan\nberkata: \"Tidak ada kujumpai dia; dan\njuga orang-orang di tempat itu berkata:\nTidak ada perempuan jalang di sini.\"'),(1142,1,38,23,'Lalu berkatalah Yehuda: \"Biarlah\nbarang-barang itu dipegangnya, supaya\nkita jangan menjadi buah olok-olok\norang; sungguhlah anak kambing itu\ntelah kukirimkan, tetapi engkau tidak\nmenjumpai perempuan itu.\"'),(1143,1,38,24,'Sesudah kira-kira tiga bulan\ndikabarkanlah kepada Yehuda: \"Tamar,\nmenantumu, bersundal, bahkan telah\nmengandung dari persundalannya itu.\"\nLalu kata Yehuda: \"Bawalah perempuan\nitu, supaya dibakar.\"'),(1144,1,38,25,'Waktu dibawa, perempuan itu\nmenyuruh orang kepada mertuanya\nmengatakan: \"Dari laki-laki yang\nempunya barang-barang inilah aku mengandung.\" Juga dikatakannya:\n\"Periksalah, siapa yang empunya cap\nmeterai serta kalung dan tongkat ini?\"'),(1145,1,38,26,'Yehuda memeriksa barang-barang\nitu, lalu berkata: \"Bukan aku, tetapi\nperempuan itulah yang benar, karena\nmemang aku tidak memberikan dia\nkepada Syela, anakku.\" Dan ia tidak\nbersetubuh lagi dengan perempuan itu.'),(1146,1,38,27,'Pada waktu perempuan itu hendak\nbersalin, nyatalah ada anak kembar\ndalam kandungannya.'),(1147,1,38,28,'Dan ketika ia bersalin, seorang dari\nanak itu mengeluarkan tangannya, lalu\ndipegang oleh bidan, diikatnya dengan\nbenang kirmizi serta berkata: \"Inilah\nyang lebih dahulu keluar.\"'),(1148,1,38,29,'Ketika anak itu menarik tangannya\nkembali, keluarlah saudaranya laki-laki,\ndan bidan itu berkata: \"Alangkah\nkuatnya engkau menembus ke luar,\"\nmaka anak itu dinamai Peres.'),(1149,1,38,30,'Sesudah itu keluarlah saudaranya\nlaki-laki yang tangannya telah berikat\nbenang kirmizi itu, lalu kepadanya diberi\nnama Zerah.'),(1150,1,39,1,'Adapun Yusuf telah dibawa ke Mesir; dan Potifar, seorang Mesir, pegawai istana Firaun, kepala pengawal\nraja, membeli dia dari tangan orang\nIsmael yang telah membawa dia ke situ.'),(1151,1,39,2,'Tetapi TUHAN menyertai Yusuf,\nsehingga ia menjadi seorang yang selalu\nberhasil dalam pekerjaannya; maka\ntinggallah ia di rumah tuannya, orang\nMesir itu.'),(1152,1,39,3,'Setelah dilihat oleh tuannya, bahwa\nYusuf disertai TUHAN dan bahwa TUHAN\nmembuat berhasil segala sesuatu yang\ndikerjakannya,'),(1153,1,39,4,'maka Yusuf mendapat kasih tuannya,\ndan ia boleh melayani dia; kepada Yusuf\ndiberikannya kuasa atas rumahnya dan\nsegala miliknya diserahkannya pada\nkekuasaan Yusuf.'),(1154,1,39,5,'Sejak ia memberikan kuasa dalam\nrumahnya dan atas segala miliknya\nkepada Yusuf, TUHAN memberkati\nrumah orang Mesir itu karena Yusuf,\nsehingga berkat TUHAN ada atas segala\nmiliknya, baik yang di rumah maupun\nyang di ladang.'),(1155,1,39,6,'Segala miliknya diserahkannya pada\nkekuasaan Yusuf, dan dengan bantuan\nYusuf ia tidak usah lagi mengatur\napa-apapun selain dari makanannya sendiri. Adapun Yusuf itu manis sikapnya\ndan elok parasnya.'),(1156,1,39,7,'Selang beberapa waktu isteri tuannya\nmemandang Yusuf dengan berahi, lalu\nkatanya: \"Marilah tidur dengan aku.\"'),(1157,1,39,8,'Tetapi Yusuf menolak dan berkata\nkepada isteri tuannya itu: \"Dengan\nbantuanku tuanku itu tidak lagi mengatur\napa yang ada di rumah ini dan ia telah\nmenyerahkan segala miliknya pada\nkekuasaanku,'),(1158,1,39,9,'bahkan di rumah ini ia tidak lebih\nbesar kuasanya dari padaku, dan tiada\nyang tidak diserahkannya kepadaku\nselain dari pada engkau, sebab engkau\nisterinya. Bagaimanakah mungkin aku\nmelakukan kejahatan yang besar ini dan\nberbuat dosa terhadap Allah?\"'),(1159,1,39,10,'Walaupun dari hari ke hari\nperempuan itu membujuk Yusuf, Yusuf\ntidak mendengarkan bujukannya itu\nuntuk tidur di sisinya dan bersetubuh\ndengan dia.'),(1160,1,39,11,'Pada suatu hari masuklah Yusuf\nke dalam rumah untuk melakukan\npekerjaannya, sedang dari seisi rumah\nitu seorangpun tidak ada di rumah.'),(1161,1,39,12,'Lalu perempuan itu memegang baju\nYusuf sambil berkata: \"Marilah tidur\ndengan aku.\" Tetapi Yusuf meninggalkan\nbajunya di tangan perempuan itu dan\nlari ke luar.'),(1162,1,39,13,'Ketika dilihat perempuan itu, bahwa\nYusuf meninggalkan bajunya dalam\ntangannya dan telah lari ke luar,'),(1163,1,39,14,'dipanggilnyalah seisi rumah itu,\nlalu katanya kepada mereka: \"Lihat,\ndibawanya ke mari seorang Ibrani,\nsupaya orang ini dapat mempermainkan\nkita. Orang ini mendekati aku untuk tidur\ndengan aku, tetapi aku berteriak-teriak\ndengan suara keras.'),(1164,1,39,15,'Dan ketika didengarnya bahwa\naku berteriak sekeras-kerasnya,\nditinggalkannyalah bajunya padaku, lalu\nia lari ke luar.\"'),(1165,1,39,16,'Juga ditaruhnya baju Yusuf itu di\nsisinya, sampai tuan rumah pulang.'),(1166,1,39,17,'Perkataan itu jugalah yang\ndiceritakan perempuan itu kepada\nPotifar, katanya: \"Hamba orang Ibrani\nyang kaubawa ke mari itu datang\nkepadaku untuk mempermainkan aku.'),(1167,1,39,18,'Tetapi ketika aku berteriak sekeraskerasnya, ditinggalkannya bajunya\npadaku, lalu ia lari ke luar.\"'),(1168,1,39,19,'Baru saja didengar oleh tuannya\nperkataan yang diceritakan isterinya\nkepadanya: begini begitulah aku\ndiperlakukan oleh hambamu itu, maka\nbangkitlah amarahnya.'),(1169,1,39,20,'Lalu Yusuf ditangkap oleh tuannya\ndan dimasukkan ke dalam penjara,\ntempat tahanan-tahanan raja dikurung.\nDemikianlah Yusuf dipenjarakan di sana.'),(1170,1,39,21,'Tetapi TUHAN menyertai Yusuf\ndan melimpahkan kasih setia-Nya\nkepadanya, dan membuat Yusuf\nkesayangan bagi kepala penjara itu.'),(1171,1,39,22,'Sebab itu kepala penjara\nmempercayakan semua tahanan\ndalam penjara itu kepada Yusuf, dan\nsegala pekerjaan yang harus dilakukan\ndi situ, dialah yang mengurusnya.'),(1172,1,39,23,'Dan kepala penjara tidak mencampuri\nsegala yang dipercayakannya kepada\nYusuf, karena TUHAN menyertai dia dan\napa yang dikerjakannya dibuat TUHAN\nberhasil.'),(1173,1,40,1,'Sesudah semuanya itu terjadilah, bahwa juru minuman raja Mesir dan juru rotinya membuat\nkesalahan terhadap tuannya, raja Mesir\nitu,'),(1174,1,40,2,'maka murkalah Firaun kepada kedua\npegawai istananya, kepala juru minuman\ndan kepala juru roti itu.'),(1175,1,40,3,'Ia menahan mereka dalam rumah\nkepala pengawal raja, dalam penjara\ntempat Yusuf dikurung.'),(1176,1,40,4,'Kepala pengawal raja menempatkan\nYusuf bersama-sama dengan mereka\nuntuk melayani mereka. Demikianlah\nmereka ditahan beberapa waktu\nlamanya.'),(1177,1,40,5,'Pada suatu kali bermimpilah mereka\nkeduanya--baik juru minuman maupun\njuru roti raja Mesir, yang ditahan\ndalam penjara itu--masing-masing ada\nmimpinya, pada satu malam juga, dan\nmimpi masing-masing itu ada artinya\nsendiri.'),(1178,1,40,6,'Ketika pada waktu pagi Yusuf datang\nkepada mereka, segera dilihatnya,\nbahwa mereka bersusah hati.'),(1179,1,40,7,'Lalu ia bertanya kepada pegawaipegawai istana Firaun yang ditahan\nbersama-sama dengan dia dalam rumah tuannya itu: \"Mengapakah hari ini\nmukamu semuram itu?\"'),(1180,1,40,8,'Jawab mereka kepadanya: \"Kami\nbermimpi, tetapi tidak ada orang yang\ndapat mengartikannya.\" Lalu kata Yusuf\nkepada mereka: \"Bukankah Allah yang\nmenerangkan arti mimpi? Ceritakanlah\nkiranya mimpimu itu kepadaku.\"'),(1181,1,40,9,'Kemudian juru minuman itu\nmenceritakan mimpinya kepada Yusuf,\nkatanya: \"Dalam mimpiku itu tampak\nada pohon anggur di depanku.'),(1182,1,40,10,'Pohon anggur itu ada tiga carangnya\ndan baru saja pohon itu bertunas,\nbunganya sudah keluar dan tandantandannya penuh buah anggur yang\nranum.'),(1183,1,40,11,'Dan di tanganku ada piala Firaun.\nBuah anggur itu kuambil, lalu kuperas\nke dalam piala Firaun, kemudian\nkusampaikan piala itu ke tangan Firaun.\"'),(1184,1,40,12,'Kata Yusuf kepadanya: \"Beginilah arti\nmimpi itu: ketiga carang itu artinya tiga\nhari;'),(1185,1,40,13,'dalam tiga hari ini Firaun\nakan meninggikan engkau dan\nmengembalikan engkau ke dalam\npangkatmu yang dahulu dan engkau akan menyampaikan piala ke tangan\nFiraun seperti dahulu kala, ketika engkau\njadi juru minumannya.'),(1186,1,40,14,'Tetapi, ingatlah kepadaku,\napabila keadaanmu telah baik\nnanti, tunjukkanlah terima kasihmu\nkepadaku dengan menceritakan hal\nihwalku kepada Firaun dan tolonglah\nkeluarkan aku dari rumah ini.'),(1187,1,40,15,'Sebab aku dicuri diculik begitu\nsaja dari negeri orang Ibrani dan di\nsinipun aku tidak pernah melakukan\napa-apa yang menyebabkan aku layak\ndimasukkan ke dalam liang tutupan ini.\"'),(1188,1,40,16,'Setelah dilihat oleh kepala juru roti,\nbetapa baik arti mimpi itu, berkatalah\nia kepadanya: \"Akupun bermimpi juga.\nTampak aku menjunjung tiga bakul berisi\npenganan.'),(1189,1,40,17,'Dalam bakul atas ada berbagai-bagai\nmakanan untuk Firaun, buatan juru roti,\ntetapi burung-burung memakannya dari\ndalam bakul yang di atas kepalaku.\"'),(1190,1,40,18,'Yusuf menjawab: \"Beginilah arti\nmimpi itu: ketiga bakul itu artinya tiga\nhari;'),(1191,1,40,19,'dalam tiga hari ini Firaun akan\nmeninggikan engkau, tinggi ke atas, dan menggantung engkau pada\nsebuah tiang, dan burung-burung akan\nmemakan dagingmu dari tubuhmu.\"'),(1192,1,40,20,'Dan terjadilah pada hari ketiga,\nhari kelahiran Firaun, maka Firaun\nmengadakan perjamuan untuk semua\npegawainya. Ia meninggikan kepala juru\nminuman dan kepala juru roti itu di\ntengah-tengah para pegawainya:'),(1193,1,40,21,'kepala juru minuman itu\ndikembalikannya ke dalam jabatannya,\nsehingga ia menyampaikan pula piala ke\ntangan Firaun;'),(1194,1,40,22,'tetapi kepala juru roti itu\ndigantungnya, seperti yang ditakbirkan\nYusuf kepada mereka.'),(1195,1,40,23,'Tetapi Yusuf tidaklah diingat oleh\nkepala juru minuman itu, melainkan\ndilupakannya.'),(1196,1,41,1,'Setelah lewat dua tahun lamanya, bermimpilah Firaun, bahwa ia berdiri di tepi sungai Nil.'),(1197,1,41,2,'Tampaklah dari sungai Nil itu keluar\ntujuh ekor lembu yang indah bangunnya\ndan gemuk badannya; lalu memakan\nrumput yang di tepi sungai itu.'),(1198,1,41,3,'Kemudian tampaklah juga tujuh ekor\nlembu yang lain, yang keluar dari dalam sungai Nil itu, buruk bangunnya dan\nkurus badannya, lalu berdiri di samping\nlembu-lembu yang tadi, di tepi sungai\nitu.'),(1199,1,41,4,'Lembu-lembu yang buruk bangunnya\ndan kurus badannya itu memakan\nketujuh ekor lembu yang indah\nbangunnya dan gemuk itu. Lalu\nterjagalah Firaun.'),(1200,1,41,5,'Setelah itu tertidur pulalah ia dan\nbermimpi kedua kalinya: Tampak timbul\ndari satu tangkai tujuh bulir gandum\nyang bernas dan baik.'),(1201,1,41,6,'Tetapi kemudian tampaklah juga\ntumbuh tujuh bulir gandum yang kurus\ndan layu oleh angin timur.'),(1202,1,41,7,'Bulir yang kurus itu menelan ketujuh\nbulir yang bernas dan berisi tadi. Lalu\nterjagalah Firaun. Agaknya ia bermimpi!'),(1203,1,41,8,'Pada waktu pagi gelisahlah hatinya,\nlalu disuruhnyalah memanggil semua\nahli dan semua orang berilmu di Mesir.\nFiraun menceritakan mimpinya kepada\nmereka, tetapi seorangpun tidak ada\nyang dapat mengartikannya kepadanya.'),(1204,1,41,9,'Lalu berkatalah kepala juru minuman\nkepada Firaun: \"Hari ini aku merasa perlu menyebutkan kesalahanku yang\ndahulu.'),(1205,1,41,10,'Waktu itu tuanku Firaun murka\nkepada pegawai-pegawainya, dan\nmenahan aku dalam rumah pengawal\nistana, beserta dengan kepala juru roti.'),(1206,1,41,11,'Pada satu malam juga kami\nbermimpi, aku dan kepala juru roti\nitu; masing-masing mempunyai mimpi\ndengan artinya sendiri.'),(1207,1,41,12,'Bersama-sama dengan kami ada\ndi sana seorang muda Ibrani, hamba\nkepala pengawal istana itu; kami\nmenceritakan mimpi kami kepadanya,\nlalu diartikannya kepada kami mimpi\nkami masing-masing.'),(1208,1,41,13,'Dan seperti yang diartikannya itu\nkepada kami, demikianlah pula terjadi:\naku dikembalikan ke dalam pangkatku,\ndan kepala juru roti itu digantung.\"'),(1209,1,41,14,'Kemudian Firaun menyuruh\nmemanggil Yusuf. Segeralah ia\ndikeluarkan dari tutupan; ia bercukur\ndan berganti pakaian, lalu pergi\nmenghadap Firaun.'),(1210,1,41,15,'Berkatalah Firaun kepada Yusuf: \"Aku\ntelah bermimpi, dan seorangpun tidak\nada yang dapat mengartikannya, tetapi  telah kudengar tentang engkau: hanya\ndengan mendengar mimpi saja engkau\ndapat mengartikannya.\"'),(1211,1,41,16,'Yusuf menyahut Firaun: \"Bukan\nsekali-kali aku, melainkan Allah juga\nyang akan memberitakan kesejahteraan\nkepada tuanku Firaun.\"'),(1212,1,41,17,'Lalu berkatalah Firaun kepada Yusuf:\n\"Dalam mimpiku itu, aku berdiri di tepi\nsungai Nil;'),(1213,1,41,18,'lalu tampaklah dari sungai Nil itu\nkeluar tujuh ekor lembu yang gemuk\nbadannya dan indah bentuknya, dan\nmakan rumput yang di tepi sungai itu.'),(1214,1,41,19,'Tetapi kemudian tampaklah juga\nkeluar tujuh ekor lembu yang lain,\nkulit pemalut tulang, sangat buruk\nbangunnya dan kurus badannya; tidak\npernah kulihat yang seburuk itu di\nseluruh tanah Mesir.'),(1215,1,41,20,'Lembu yang kurus dan buruk itu\nmemakan ketujuh ekor lembu gemuk\nyang mula-mula.'),(1216,1,41,21,'Lembu-lembu ini masuk ke dalam\nperutnya, tetapi walaupun telah masuk\nke dalam perutnya, tidaklah kelihatan\nsedikitpun tandanya: bangunnya tetap sama buruknya seperti semula. Lalu\nterjagalah aku.'),(1217,1,41,22,'Selanjutnya dalam mimpiku itu\nkulihat timbul dari satu tangkai tujuh\nbulir gandum yang berisi dan baik.'),(1218,1,41,23,'Tetapi kemudian tampaklah juga\ntumbuh tujuh bulir yang kering, kurus\ndan layu oleh angin timur.'),(1219,1,41,24,'Bulir yang kurus itu memakan\nketujuh bulir yang baik tadi. Telah\nkuceritakan hal ini kepada semua ahli,\ntetapi seorangpun tidak ada yang dapat\nmenerangkannya kepadaku.\"'),(1220,1,41,25,'Lalu kata Yusuf kepada Firaun:\n\"Kedua mimpi tuanku Firaun itu sama.\nAllah telah memberitahukan kepada\ntuanku Firaun apa yang hendak\ndilakukan-Nya.'),(1221,1,41,26,'Ketujuh ekor lembu yang baik itu\nialah tujuh tahun, dan ketujuh bulir\ngandum yang baik itu ialah tujuh tahun\njuga; kedua mimpi itu sama.'),(1222,1,41,27,'Ketujuh ekor lembu yang kurus\ndan buruk, yang keluar kemudian,\nmaksudnya tujuh tahun, demikian pula\nketujuh bulir gandum yang hampa dan\nlayu oleh angin timur itu; maksudnya\nakan ada tujuh tahun kelaparan.'),(1223,1,41,28,'Inilah maksud perkataanku, ketika\naku berkata kepada tuanku Firaun: Allah\ntelah memperlihatkan kepada tuanku\nFiraun apa yang hendak dilakukan-Nya.'),(1224,1,41,29,'Ketahuilah tuanku, akan datang tujuh\ntahun kelimpahan di seluruh tanah Mesir.'),(1225,1,41,30,'Kemudian akan timbul tujuh tahun\nkelaparan; maka akan dilupakan segala\nkelimpahan itu di tanah Mesir, karena\nkelaparan itu menguruskeringkan negeri\nini.'),(1226,1,41,31,'Sesudah itu akan tidak kelihatan\nlagi bekas-bekas kelimpahan di negeri\nini karena kelaparan itu, sebab sangat\nhebatnya kelaparan itu.'),(1227,1,41,32,'Sampai dua kali mimpi itu diulangi\nbagi tuanku Firaun berarti: hal itu telah\nditetapkan oleh Allah dan Allah akan\nsegera melakukannya.'),(1228,1,41,33,'Oleh sebab itu, baiklah tuanku Firaun\nmencari seorang yang berakal budi dan\nbijaksana, dan mengangkatnya menjadi\nkuasa atas tanah Mesir.'),(1229,1,41,34,'Baiklah juga tuanku Firaun berbuat\nbegini, yakni menempatkan penilikpenilik atas negeri ini dan dalam\nketujuh tahun kelimpahan itu memungut\nseperlima dari hasil tanah Mesir.'),(1230,1,41,35,'Mereka harus mengumpulkan segala\nbahan makanan dalam tahun-tahun baik\nyang akan datang ini dan, di bawah\nkuasa tuanku Firaun, menimbun gandum\ndi kota-kota sebagai bahan makanan,\nserta menyimpannya.'),(1231,1,41,36,'Demikianlah segala bahan makanan\nitu menjadi persediaan untuk negeri\nini dalam ketujuh tahun kelaparan\nyang akan terjadi di tanah Mesir,\nsupaya negeri ini jangan binasa karena\nkelaparan itu.\"'),(1232,1,41,37,'Usul itu dipandang baik oleh Firaun\ndan oleh semua pegawainya.'),(1233,1,41,38,'Lalu berkatalah Firaun kepada\npara pegawainya: \"Mungkinkah kita\nmendapat orang seperti ini, seorang\nyang penuh dengan Roh Allah?\"'),(1234,1,41,39,'Kata Firaun kepada Yusuf: \"Oleh\nkarena Allah telah memberitahukan\nsemuanya ini kepadamu, tidaklah ada\norang yang demikian berakal budi dan\nbijaksana seperti engkau.'),(1235,1,41,40,'Engkaulah menjadi kuasa atas\nistanaku, dan kepada perintahmu\nseluruh rakyatku akan taat; hanya\ntakhta inilah kelebihanku dari padamu.\"'),(1236,1,41,41,'Selanjutnya Firaun berkata kepada\nYusuf: \"Dengan ini aku melantik engkau\nmenjadi kuasa atas seluruh tanah Mesir.\"'),(1237,1,41,42,'Sesudah itu Firaun menanggalkan\ncincin meterainya dari jarinya dan\nmengenakannya pada jari Yusuf;\ndipakaikannyalah kepada Yusuf\npakaian dari pada kain halus dan\ndigantungkannya kalung emas pada\nlehernya.'),(1238,1,41,43,'Lalu Firaun menyuruh menaikkan\nYusuf dalam keretanya yang kedua, dan\nberserulah orang di hadapan Yusuf:\n\"Hormat!\" Demikianlah Yusuf dilantik\noleh Firaun menjadi kuasa atas seluruh\ntanah Mesir.'),(1239,1,41,44,'Berkatalah Firaun kepada Yusuf:\n\"Akulah Firaun, tetapi dengan tidak\nsetahumu, seorangpun tidak boleh\nbergerak di seluruh tanah Mesir.\"'),(1240,1,41,45,'Berkatalah Firaun kepada Yusuf:\n\"Akulah Firaun, tetapi dengan tidak\nsetahumu, seorangpun tidak boleh\nbergerak di seluruh tanah Mesir.\"'),(1241,1,41,46,'Yusuf berumur tiga puluh tahun\nketika ia menghadap Firaun, raja Mesir\nitu. Maka pergilah Yusuf dari depan Firaun, lalu dikelilinginya seluruh tanah\nMesir.'),(1242,1,41,47,'Tanah itu mengeluarkan hasil\nbertumpuk-tumpuk dalam ketujuh tahun\nkelimpahan itu,'),(1243,1,41,48,'maka Yusuf mengumpulkan segala\nbahan makanan ketujuh tahun\nkelimpahan yang ada di tanah Mesir, lalu\ndisimpannya di kota-kota; hasil daerah\nsekitar tiap-tiap kota disimpan di dalam\nkota itu.'),(1244,1,41,49,'Demikianlah Yusuf menimbun\ngandum seperti pasir di laut, sangat\nbanyak, sehingga orang berhenti\nmenghitungnya, karena memang tidak\nterhitung.'),(1245,1,41,50,'Sebelum datang tahun kelaparan\nitu, lahirlah bagi Yusuf dua orang anak\nlaki-laki, yang dilahirkan oleh Asnat,\nanak Potifera, imam di On.'),(1246,1,41,51,'Yusuf memberi nama Manasye\nkepada anak sulungnya itu, sebab\nkatanya: \"Allah telah membuat aku lupa\nsama sekali kepada kesukaranku dan\nkepada rumah bapaku.\"'),(1247,1,41,52,'Dan kepada anaknya yang kedua\ndiberinya nama Efraim, sebab katanya: \"Allah membuat aku mendapat anak\ndalam negeri kesengsaraanku.\"'),(1248,1,41,53,'Setelah lewat ketujuh tahun\nkelimpahan yang ada di tanah Mesir itu,'),(1249,1,41,54,'mulailah datang tujuh tahun\nkelaparan, seperti yang telah dikatakan\nYusuf; dalam segala negeri ada\nkelaparan, tetapi di seluruh negeri Mesir\nada roti.'),(1250,1,41,55,'Ketika seluruh negeri Mesir menderita\nkelaparan, dan rakyat berteriak meminta\nroti kepada Firaun, berkatalah Firaun\nkepada semua orang Mesir: \"Pergilah\nkepada Yusuf, perbuatlah apa yang akan\ndikatakannya kepadamu.\"'),(1251,1,41,56,'Kelaparan itu merajalela di seluruh\nbumi. Maka Yusuf membuka segala\nlumbung dan menjual gandum kepada\norang Mesir, sebab makin hebat\nkelaparan itu di tanah Mesir.'),(1252,1,41,57,'Juga dari seluruh bumi datanglah\norang ke Mesir untuk membeli gandum\ndari Yusuf, sebab hebat kelaparan itu di\nseluruh bumi.'),(1253,1,42,1,'Setelah Yakub mendapat kabar, bahwa ada gandum di Mesir, berkatalah ia kepada anak-anaknya: \"Mengapa kamu berpandang-pandangan\nsaja?\"'),(1254,1,42,2,'Lagi katanya: \"Telah kudengar, bahwa\nada gandum di Mesir; pergilah ke sana\ndan belilah gandum di sana untuk kita,\nsupaya kita tetap hidup dan jangan\nmati.\"'),(1255,1,42,3,'Lalu pergilah sepuluh orang saudara\nYusuf untuk membeli gandum di Mesir.'),(1256,1,42,4,'Tetapi Yakub tidak membiarkan\nBenyamin, adik Yusuf, pergi bersamasama dengan saudara-saudaranya,\nsebab pikirnya: \"Jangan-jangan ia\nditimpa kecelakaan nanti.\"'),(1257,1,42,5,'Jadi di antara orang yang datang\nmembeli gandum terdapatlah juga\nanak-anak Israel, sebab ada kelaparan\ndi tanah Kanaan.'),(1258,1,42,6,'Sementara itu Yusuf telah menjadi\nmangkubumi di negeri itu; dialah yang\nmenjual gandum kepada seluruh rakyat\nnegeri itu. Jadi ketika saudara-saudara\nYusuf datang, kepadanyalah mereka\nmenghadap dan sujud dengan mukanya\nsampai ke tanah.'),(1259,1,42,7,'Ketika Yusuf melihat saudarasaudaranya, segeralah mereka\ndikenalnya, tetapi ia berlaku seolah-olah ia seorang asing kepada mereka; ia\nmenegor mereka dengan membentak,\nkatanya: \"Dari mana kamu?\" Jawab\nmereka: \"Dari tanah Kanaan untuk\nmembeli bahan makanan.\"'),(1260,1,42,8,'Memang Yusuf mengenal saudarasaudaranya itu, tetapi dia tidak dikenal\nmereka.'),(1261,1,42,9,'Lalu teringatlah Yusuf akan mimpimimpinya tentang mereka. Berkatalah\nia kepada mereka: \"Kamu ini pengintai,\nkamu datang untuk melihat-lihat di\nmana negeri ini tidak dijaga.\"'),(1262,1,42,10,'Tetapi jawab mereka: \"Tidak\ntuanku! Hanyalah untuk membeli bahan\nmakanan hamba-hambamu ini datang.'),(1263,1,42,11,'Kami ini sekalian anak dari satu ayah;\nkami ini orang jujur; hamba-hambamu\nini bukanlah pengintai.\"'),(1264,1,42,12,'Tetapi ia berkata kepada mereka:\n\"Tidak! Kamu datang untuk melihat-lihat\ndi mana negeri ini tidak dijaga.\"'),(1265,1,42,13,'Lalu jawab mereka: \"Hambahambamu ini dua belas orang, kami\nbersaudara, anak dari satu ayah di tanah\nKanaan, tetapi yang bungsu sekarang\nada pada ayah kami, dan seorang sudah\ntidak ada lagi.\"'),(1266,1,42,14,'Lalu kata Yusuf kepada mereka:\n\"Sudahlah! Seperti telah kukatakan\nkepadamu tadi: kamu ini pengintai.'),(1267,1,42,15,'Dalam hal ini juga kamu harus diuji:\ndemi hidup Firaun, kamu tidak akan\npergi dari sini, jika saudaramu yang\nbungsu itu tidak datang ke mari.'),(1268,1,42,16,'Suruhlah seorang dari padamu untuk\nmenjemput adikmu itu, tetapi kamu ini\nharus tinggal terkurung di sini. Dengan\ndemikian perkataanmu dapat diuji,\napakah benar, dan jika tidak, demi hidup\nFiraun, sungguh-sungguhlah kamu ini\npengintai.\"'),(1269,1,42,17,'Dan dimasukkannyalah mereka\nbersama-sama ke dalam tahanan tiga\nhari lamanya.'),(1270,1,42,18,'Pada hari yang ketiga berkatalah\nYusuf kepada mereka: \"Buatlah begini,\nmaka kamu akan tetap hidup, aku takut\nakan Allah.'),(1271,1,42,19,'Jika kamu orang jujur, biarkanlah\ndari kamu bersaudara tinggal seorang\nterkurung dalam rumah tahanan, tetapi\npergilah kamu, bawalah gandum untuk\nmeredakan lapar seisi rumahmu.'),(1272,1,42,20,'Tetapi saudaramu yang bungsu\nitu haruslah kamu bawa kepadaku, supaya perkataanmu itu ternyata benar\ndan kamu jangan mati.\" Demikianlah\ndiperbuat mereka.'),(1273,1,42,21,'Mereka berkata seorang kepada yang\nlain: \"Betul-betullah kita menanggung\nakibat dosa kita terhadap adik kita\nitu: bukankah kita melihat bagaimana\nsesak hatinya, ketika ia memohon belas\nkasihan kepada kita, tetapi kita tidak\nmendengarkan permohonannya. Itulah\nsebabnya kesesakan ini menimpa kita.\"'),(1274,1,42,22,'Lalu Ruben menjawab mereka:\n\"Bukankah dahulu kukatakan kepadamu:\nJanganlah kamu berbuat dosa\nterhadap anak itu! Tetapi kamu tidak\nmendengarkan perkataanku. Sekarang\ndarahnya dituntut dari pada kita.\"'),(1275,1,42,23,'Tetapi mereka tidak tahu, bahwa\nYusuf mengerti perkataan mereka, sebab\nmereka memakai seorang juru bahasa.'),(1276,1,42,24,'Maka Yusuf mengundurkan diri dari\nmereka, lalu menangis. Kemudian ia\nkembali kepada mereka dan berkatakata dengan mereka; ia mengambil\nSimeon dari antara mereka; lalu disuruh\nbelenggu di depan mata mereka.'),(1277,1,42,25,'Sesudah itu Yusuf memerintahkan,\nbahwa tempat gandum mereka akan diisi  dengan gandum dan bahwa uang mereka\nmasing-masing akan dikembalikan ke\ndalam karungnya, serta bekal mereka\ndi jalan akan diberikan kepada mereka.\nDemikianlah dilakukan orang kepada\nmereka itu.'),(1278,1,42,26,'Sesudah itu merekapun memuat\ngandum itu ke atas keledai mereka, lalu\nberangkat dari situ.'),(1279,1,42,27,'Ketika seorang membuka karungnya\nuntuk memberi makan keledainya di\ntempat bermalam, dilihatnyalah uangnya\nada di dalam mulut karungnya.'),(1280,1,42,28,'Katanya kepada saudara-saudaranya:\n\"Uangku dikembalikan; lihat, ada dalam\nkarungku!\" Lalu hati mereka menjadi\ntawar dan mereka berpandangpandangan dengan gemetar serta\nberkata: \"Apakah juga yang diperbuat\nAllah terhadap kita!\"'),(1281,1,42,29,'Ketika mereka sampai kepada Yakub,\nayah mereka, di tanah Kanaan, mereka\nmenceritakan segala sesuatu yang\ndialaminya, katanya:'),(1282,1,42,30,'\"Orang itu, yakni yang menjadi tuan\natas negeri itu, telah menegor kami\ndengan membentak dan memperlakukan\nkami sebagai pengintai negeri itu.'),(1283,1,42,31,'Tetapi kata kami kepadanya: Kami\norang jujur, kami bukan pengintai.'),(1284,1,42,32,'Kami dua belas orang bersaudara,\nanak-anak ayah kami; seorang sudah\ntidak ada lagi, dan yang bungsu ada\nsekarang pada ayah kami, di tanah\nKanaan.'),(1285,1,42,33,'Lalu kata orang itu, yakni yang\nmenjadi tuan atas negeri itu, kepada\nkami: Dari hal ini aku akan tahu, apakah\nkamu orang jujur: dari kamu bersaudara\nharuslah kamu tinggalkan seorang\npadaku; kemudian bawalah gandum\nuntuk meredakan lapar seisi rumahmu\ndan pergilah;'),(1286,1,42,34,'lalu bawalah kepadaku saudaramu\nyang bungsu itu, maka aku akan\ntahu, bahwa kamu bukan pengintai,\ntetapi orang jujur; dan aku akan\nmengembalikan saudaramu itu\nkepadamu, dan bolehlah kamu\nmenjalani negeri ini dengan bebas.\"'),(1287,1,42,35,'Ketika mereka mengosongkan\nkarungnya, tampaklah ada pundi-pundi\nuang masing-masing dalam karungnya;\ndan ketika mereka beserta ayah\nmereka melihat pundi-pundi uang itu,\nketakutanlah mereka.'),(1288,1,42,36,'Dan Yakub, ayah mereka, berkata\nkepadanya: \"Kamu membuat aku\nkehilangan anak-anakku: Yusuf tidak\nada lagi, dan Simeon tidak ada lagi,\nsekarang Benyaminpun hendak kamu\nbawa juga. Aku inilah yang menanggung\nsegala-galanya itu!'),(1289,1,42,37,'Lalu berkatalah Ruben kepada\nayahnya: \"Kedua anakku laki-laki boleh\nengkau bunuh, jika ia tidak kubawa\nkepadamu; serahkanlah dia ke dalam\ntanganku, maka dia akan kubawa\nkembali kepadamu.\"'),(1290,1,42,38,'Tetapi jawabnya: \"Anakku itu tidak\nakan pergi ke sana bersama-sama\ndengan kamu, sebab kakaknya telah\nmati dan hanya dialah yang tinggal;\njika dia ditimpa kecelakaan di jalan\nyang akan kamu tempuh, maka tentulah\nkamu akan menyebabkan aku yang\nubanan ini turun ke dunia orang mati\nkarena dukacita.\"'),(1291,1,43,1,'Tetapi hebat sekali kelaparan di negeri itu.'),(1292,1,43,2,'Dan setelah gandum yang dibawa\nmereka dari Mesir habis dimakan,\nberkatalah ayah mereka: \"Pergilah pula  membeli sedikit bahan makanan untuk\nkita.\"'),(1293,1,43,3,'Lalu Yehuda menjawabnya: \"Orang\nitu telah memperingatkan kami dengan\nsungguh-sungguh: Kamu tidak boleh\nmelihat mukaku, jika adikmu itu tidak\nada bersama-sama dengan kamu.'),(1294,1,43,4,'Jika engkau mau membiarkan adik\nkami pergi bersama-sama dengan kami,\nmaka kami mau pergi ke sana dan\nmembeli bahan makanan bagimu.'),(1295,1,43,5,'Tetapi jika engkau tidak mau\nmembiarkan dia pergi, maka kami tidak\nakan pergi ke sana, sebab orang itu\ntelah berkata kepada kami: Kamu tidak\nboleh melihat mukaku, jika adikmu itu\ntidak ada bersama-sama dengan kamu.\"'),(1296,1,43,6,'Lalu berkatalah Israel: \"Mengapa\nkamu mendatangkan malapetaka\nkepadaku dengan memberitahukan\nkepada orang itu, bahwa masih ada\nadikmu seorang?\"'),(1297,1,43,7,'Jawab mereka: \"Orang itu telah\nmenanyai kami dengan seksama\ntentang kami sendiri dan tentang sanak\nsaudara kita: Masih hidupkah ayahmu?\nAdakah adikmu lagi? Dan kami telah\nmemberitahukan semuanya kepadanya seperti yang sebenarnya. Bagaimana\nkami dapat menduga bahwa ia akan\nberkata: Bawalah ke mari adikmu itu.\"'),(1298,1,43,8,'Lalu berkatalah Yehuda kepada Israel,\nayahnya: \"Biarkanlah anak itu pergi\nbersama-sama dengan aku; maka kami\nakan bersiap dan pergi, supaya kita\ntetap hidup dan jangan mati, baik kami\nmaupun engkau dan anak-anak kami.'),(1299,1,43,9,'Akulah yang menanggung dia; engkau\nboleh menuntut dia dari padaku; jika\naku tidak membawa dia kepadamu dan\nmenempatkan dia di depanmu, maka\nakulah yang berdosa terhadap engkau\nuntuk selama-lamanya.'),(1300,1,43,10,'Jika kita tidak berlambat-lambat,\nmaka tentulah kami sekarang sudah dua\nkali pulang.\"'),(1301,1,43,11,'Lalu Israel, ayah mereka, berkata\nkepadanya: \"Jika demikian, perbuatlah\nbegini: Ambillah hasil yang terbaik dari\nnegeri ini dalam tempat gandummu\ndan bawalah kepada orang itu sebagai\npersembahan: sedikit balsam dan sedikit\nmadu, damar dan damar ladan, buah\nkemiri dan buah badam.'),(1302,1,43,12,'Dan bawalah uang dua kali\nlipat banyaknya: uang yang telah dikembalikan ke dalam mulut karungkarungmu itu haruslah kamu bawa\nkembali; mungkin itu suatu kekhilafan.'),(1303,1,43,13,'Bawalah juga adikmu itu, bersiaplah\ndan kembalilah pula kepada orang itu.'),(1304,1,43,14,'Allah Yang Mahakuasa kiranya\nmembuat orang itu menaruh belas\nkasihan kepadamu, supaya ia\nmembiarkan saudaramu yang lain itu\nbeserta Benyamin kembali. Mengenai\naku ini, jika terpaksa aku kehilangan\nanak-anakku, biarlah juga kehilangan!\"'),(1305,1,43,15,'Lalu orang-orang itu mengambil\npersembahan itu dan mengambil uang\ndua kali lipat banyaknya, beserta\nBenyamin juga; mereka bersiap dan\npergi ke Mesir. Kemudian berdirilah\nmereka di depan Yusuf.'),(1306,1,43,16,'Ketika Yusuf melihat Benyamin\nbersama-sama dengan mereka,\nberkatalah ia kepada kepala rumahnya:\n\"Bawalah orang-orang ini ke dalam\nrumah, sembelihlah seekor hewan dan\nsiapkanlah itu, sebab orang-orang ini\nakan makan bersama-sama dengan aku\npada tengah hari ini.\"'),(1307,1,43,17,'Orang itu melakukan seperti yang\ndikatakan Yusuf dan dibawanyalah\norang-orang itu ke dalam rumah Yusuf.'),(1308,1,43,18,'Lalu ketakutanlah orang-orang itu,\nkarena mereka dibawa ke dalam rumah\nYusuf. Kata mereka: \"Yang menjadi\nsebab kita dibawa ke sini, ialah perkara\nuang yang dikembalikan ke dalam\nkarung kita pada mulanya itu, supaya\nkita disergap dan ditangkap dan supaya\nkita dijadikan budak dan keledai kita\ndiambil.\"'),(1309,1,43,19,'Karena itu mereka mendekati kepala\nrumah Yusuf itu, dan berkata kepadanya\ndi depan pintu rumah:'),(1310,1,43,20,'\"Mohon bicara tuan! Kami dahulu\ndatang ke mari untuk membeli bahan\nmakanan,'),(1311,1,43,21,'tetapi ketika kami sampai ke tempat\nbermalam dan membuka karung kami,\ntampaklah uang kami masing-masing\ndengan tidak kurang jumlahnya ada di\ndalam mulut karung. Tetapi sekarang\nkami membawanya kembali.'),(1312,1,43,22,'Uang lain kami bawa juga ke mari\nuntuk membeli bahan makanan; kami\ntidak tahu siapa yang menaruh uang\nkami itu ke dalam karung kami.\"'),(1313,1,43,23,'Tetapi jawabnya: \"Tenang sajalah,\njangan takut; Allahmu dan Allah\nbapamu telah memberikan kepadamu\nharta terpendam dalam karungmu;\nuangmu itu telah kuterima.\" Kemudian\ndikeluarkannyalah Simeon dan\ndibawanya kepada mereka.'),(1314,1,43,24,'Setelah orang itu membawa mereka\nke dalam rumah Yusuf, diberikannyalah\nair, supaya mereka membasuh kaki;\njuga keledai mereka diberinya makan.'),(1315,1,43,25,'Sesudah itu mereka menyiapkan\npersembahannya menantikan Yusuf\ndatang pada waktu tengah hari, sebab\nmereka telah mendengar, bahwa mereka\nakan makan di situ.'),(1316,1,43,26,'Ketika Yusuf telah pulang, mereka\nmembawa persembahan yang ada pada\nmereka itu kepada Yusuf di dalam\nrumah, lalu sujud kepadanya sampai ke\ntanah.'),(1317,1,43,27,'Sesudah itu ia bertanya kepada\nmereka apakah mereka selamat; lagi\nkatanya: \"Apakah ayahmu yang tua\nyang kamu sebutkan itu selamat? Masih\nhidupkah ia?\"'),(1318,1,43,28,'Jawab mereka: \"Hambamu, ayah\nkami, ada selamat; ia masih hidup.\" Sesudah itu berlututlah mereka dan\nsujud.'),(1319,1,43,29,'Ketika Yusuf memandang kepada\nmereka, dilihatnyalah Benyamin,\nadiknya, yang seibu dengan dia, lalu\nkatanya: \"Inikah adikmu yang bungsu\nitu, yang telah kamu sebut-sebut\nkepadaku?\" Lagi katanya: \"Allah kiranya\nmemberikan kasih karunia kepadamu,\nanakku!\"'),(1320,1,43,30,'Lalu segeralah Yusuf pergi dari\nsitu, sebab hatinya sangat terharu\nmerindukan adiknya itu, dan dicarinyalah\ntempat untuk menangis; ia masuk ke\ndalam kamar, lalu menangis di situ.'),(1321,1,43,31,'Sesudah itu dibasuhnyalah mukanya\ndan ia tampil ke luar. Ia menahan hatinya\ndan berkata: \"Hidangkanlah makanan.\"'),(1322,1,43,32,'Lalu dihidangkanlah makanan,\nbagi Yusuf tersendiri, bagi saudarasaudaranya tersendiri dan bagi\norang-orang Mesir yang bersama-sama\nmakan dengan mereka itu tersendiri;\nsebab orang Mesir tidak boleh makan\nbersama-sama dengan orang Ibrani,\nkarena hal itu suatu kekejian bagi orang\nMesir.'),(1323,1,43,33,'Saudara-saudaranya itu duduk\ndi depan Yusuf, dari yang sulung\nsampai yang bungsu, sehingga mereka\nberpandang-pandangan dengan heran.'),(1324,1,43,34,'Lalu disajikan kepada mereka\nhidangan dari meja Yusuf, tetapi yang\nditerima Benyamin adalah lima kali lebih\nbanyak dari pada setiap orang yang lain.\nLalu minumlah mereka dan bersukaria\nbersama-sama dengan dia.'),(1325,1,44,1,'Sesudah itu diperintahkannyalah kepada kepala rumahnya: \"Isilah karung orang-orang itu dengan gandum,\nseberapa yang dapat dibawa mereka,\ndan letakkanlah uang masing-masing di\ndalam mulut karungnya.'),(1326,1,44,2,'Dan pialaku, piala perak itu, taruhlah\ndi dalam mulut karung anak yang bungsu\nserta uang pembayar gandumnya juga.\"\nMaka diperbuatnyalah seperti yang\ndikatakan Yusuf.'),(1327,1,44,3,'Ketika paginya hari terang tanah,\norang melepas mereka beserta keledai\nmereka.'),(1328,1,44,4,'Tetapi baru saja mereka keluar dari\nkota itu, belum lagi jauh jaraknya,\nberkatalah Yusuf kepada kepala\nrumahnya: \"Bersiaplah, kejarlah orang-orang itu, dan apabila engkau sampai\nkepada mereka, katakanlah kepada\nmereka: Mengapa kamu membalas yang\nbaik dengan yang jahat?'),(1329,1,44,5,'Bukankah ini piala yang dipakai\ntuanku untuk minum dan yang biasa\ndipakainya untuk menelaah? Kamu\nberbuat jahat dengan melakukan yang\ndemikian.\"'),(1330,1,44,6,'Ketika sampai kepada mereka,\ndiberitakannyalah kepada mereka\nperkataan Yusuf itu.'),(1331,1,44,7,'Jawab mereka kepadanya: \"Mengapa\ntuanku mengatakan perkataan yang\ndemikian? Jauhlah dari pada hambahambamu ini untuk berbuat begitu!'),(1332,1,44,8,'Bukankah uang yang kami dapati di\ndalam mulut karung kami telah kami\nbawa kembali kepadamu dari tanah\nKanaan? Masakan kami mencuri emas\natau perak dari rumah tuanmu?'),(1333,1,44,9,'Bukankah uang yang kami dapati di\ndalam mulut karung kami telah kami\nbawa kembali kepadamu dari tanah\nKanaan? Masakan kami mencuri emas\natau perak dari rumah tuanmu?'),(1334,1,44,10,'Sesudah itu berkatalah ia: \"Ya,\nusulmu itu baik; tetapi pada siapa\nkedapatan piala itu, hanya dialah yang  akan menjadi budakku dan kamu yang\nlain itu akan bebas dari salah.\"'),(1335,1,44,11,'Lalu segeralah mereka masingmasing menurunkan karungnya ke\ntanah dan masing-masing membuka\nkarungnya.'),(1336,1,44,12,'Dan kepala rumah itu memeriksanya\ndengan teliti; ia mulai dengan yang\nsulung sampai kepada yang bungsu;\nmaka kedapatanlah piala itu dalam\nkarung Benyamin.'),(1337,1,44,13,'Lalu mereka mengoyakkan jubahnya\ndan masing-masing memuati keledainya,\ndan mereka kembali ke kota.'),(1338,1,44,14,'Ketika Yehuda dan saudarasaudaranya sampai ke dalam rumah\nYusuf, Yusuf masih ada di situ, sujudlah\nmereka sampai ke tanah di depannya.'),(1339,1,44,15,'Berkatalah Yusuf kepada mereka:\n\"Perbuatan apakah yang kamu lakukan\nini? Tidakkah kamu tahu, bahwa\nseorang yang seperti aku ini pasti dapat\nmenelaah?\"'),(1340,1,44,16,'Sesudah itu berkatalah Yehuda:\n\"Apakah yang akan kami katakan\nkepada tuanku, apakah yang akan kami\njawab, dan dengan apakah kami akan\nmembenarkan diri kami? Allah telah memperlihatkan kesalahan hambahambamu ini. Maka kami ini, budak\ntuankulah kami, baik kami maupun\norang pada siapa kedapatan piala itu.\"'),(1341,1,44,17,'Tetapi jawabnya: \"Jauhlah dari\npadaku untuk berbuat demikian! Pada\nsiapa kedapatan piala itu, dialah yang\nakan menjadi budakku, tetapi kamu ini,\npergilah kembali dengan selamat kepada\nayahmu.\"'),(1342,1,44,18,'Lalu tampillah Yehuda mendekatinya\ndan berkata: \"Mohon bicara tuanku,\nizinkanlah kiranya hambamu ini\nmengucapkan sepatah kata kepada\ntuanku dan janganlah kiranya bangkit\namarahmu terhadap hambamu ini,\nsebab tuanku adalah seperti Firaun\nsendiri.'),(1343,1,44,19,'Tuanku telah bertanya kepada\nhamba-hambanya ini: Masih adakah\nayah atau saudara kamu?'),(1344,1,44,20,'Dan kami menjawab tuanku: Kami\nmasih mempunyai ayah yang tua dan\nmasih ada anaknya yang muda, yang\nlahir pada masa tuanya; kakaknya\ntelah mati, hanya dia sendirilah yang\ntinggal dari mereka yang seibu, sebab\nitu ayahnya sangat mengasihi dia.'),(1345,1,44,21,'Lalu tuanku berkata kepada hambahambamu ini: Bawalah dia ke mari\nkepadaku, supaya mataku memandang\ndia.'),(1346,1,44,22,'Tetapi jawab kami kepada tuanku:\nAnak itu tidak dapat meninggalkan\nayahnya, sebab jika ia meninggalkan\nayahnya, tentulah ayah ini mati.'),(1347,1,44,23,'Kemudian tuanku berkata kepada\nhamba-hambamu ini: Jika adikmu\nyang bungsu itu tidak datang ke mari\nbersama-sama dengan kamu, kamu\ntidak boleh melihat mukaku lagi.'),(1348,1,44,24,'Setelah kami kembali kepada\nhambamu, ayahku, maka kami\nmemberitahukan kepadanya perkataan\ntuanku itu.'),(1349,1,44,25,'Kemudian ayah kami berkata:\nKembalilah kamu membeli sedikit bahan\nmakanan bagi kita.'),(1350,1,44,26,'Tetapi jawab kami: Kami tidak dapat\npergi ke sana. Jika adik kami yang\nbungsu bersama-sama dengan kami,\nbarulah kami akan pergi ke sana, sebab\nkami tidak boleh melihat muka orang\nitu, apabila adik kami yang bungsu tidak\nbersama-sama dengan kami.'),(1351,1,44,27,'Kemudian berkatalah hambamu,\nayahku, kepada kami: Kamu tahu,\nbahwa isteriku telah melahirkan dua\norang anak bagiku;'),(1352,1,44,28,'yang seorang telah pergi dari padaku,\ndan aku telah berkata: Tentulah ia\nditerkam oleh binatang buas, dan\nsampai sekarang aku tidak melihat dia\nkembali.'),(1353,1,44,29,'Jika anak ini kamu ambil pula dari\npadaku, dan ia ditimpa kecelakaan,\nmaka tentulah kamu akan menyebabkan\naku yang ubanan ini turun ke dunia\norang mati karena nasib celaka.'),(1354,1,44,30,'Maka sekarang, apabila aku datang\nkepada hambamu, ayahku, dan tidak\nada bersama-sama dengan kami anak\nitu, padahal ayahku tidak dapat hidup\ntanpa dia,'),(1355,1,44,31,'tentulah akan terjadi, apabila\ndilihatnya anak itu tidak ada, bahwa ia\nakan mati, dan hamba-hambamu ini\nakan menyebabkan hambamu, ayah\nkami yang ubanan itu, turun ke dunia\norang mati karena dukacita.'),(1356,1,44,32,'Tetapi hambamu ini telah\nmenanggung anak itu terhadap\nayahku dengan perkataan: Jika aku tidak membawanya kembali kepada bapa,\nmaka akulah yang berdosa kepada bapa\nuntuk selama-lamanya.'),(1357,1,44,33,'Oleh sebab itu, baiklah hambamu\nini tinggal menjadi budak tuanku\nmenggantikan anak itu, dan biarlah\nanak itu pulang bersama-sama dengan\nsaudara-saudaranya.'),(1358,1,44,34,'Sebab masakan aku pulang kepada\nayahku, apabila anak itu tidak bersamasama dengan aku? Aku tidak akan\nsanggup melihat nasib celaka yang akan\nmenimpa ayahku.\"'),(1359,1,45,1,'Ketika itu Yusuf tidak dapat menahan hatinya lagi di depan semua orang yang berdiri di dekatnya,\nlalu berserulah ia: \"Suruhlah keluar\nsemua orang dari sini.\" Maka tidak ada\nseorangpun yang tinggal di situ bersamasama Yusuf, ketika ia memperkenalkan\ndirinya kepada saudara-saudaranya.'),(1360,1,45,2,'Setelah itu menangislah ia keraskeras, sehingga kedengaran kepada\norang Mesir dan kepada seisi istana\nFiraun.'),(1361,1,45,3,'Dan Yusuf berkata kepada saudarasaudaranya: \"Akulah Yusuf! Masih\nhidupkah bapa?\" Tetapi saudara-saudaranya tidak dapat menjawabnya,\nsebab mereka takut dan gemetar\nmenghadapi dia.'),(1362,1,45,4,'Lalu kata Yusuf kepada saudarasaudaranya itu: \"Marilah dekat-dekat.\"\nMaka mendekatlah mereka. Katanya\nlagi: \"Akulah Yusuf, saudaramu, yang\nkamu jual ke Mesir.'),(1363,1,45,5,'Tetapi sekarang, janganlah bersusah\nhati dan janganlah menyesali diri,\nkarena kamu menjual aku ke sini, sebab\nuntuk memelihara kehidupanlah Allah\nmenyuruh aku mendahului kamu.'),(1364,1,45,6,'Karena telah dua tahun ada kelaparan\ndalam negeri ini dan selama lima tahun\nlagi orang tidak akan membajak atau\nmenuai.'),(1365,1,45,7,'Maka Allah telah menyuruh aku\nmendahului kamu untuk menjamin\nkelanjutan keturunanmu di bumi ini dan\nuntuk memelihara hidupmu, sehingga\nsebagian besar dari padamu tertolong'),(1366,1,45,8,'Jadi bukanlah kamu yang menyuruh\naku ke sini, tetapi Allah; Dialah yang\ntelah menempatkan aku sebagai bapa\nbagi Firaun dan tuan atas seluruh\nistananya dan sebagai kuasa atas\nseluruh tanah Mesir.'),(1367,1,45,9,'Segeralah kamu kembali kepada bapa\ndan katakanlah kepadanya: Beginilah\nkata Yusuf, anakmu: Allah telah\nmenempatkan aku sebagai tuan atas\nseluruh Mesir; datanglah mendapatkan\naku, janganlah tunggu-tunggu.'),(1368,1,45,10,'Engkau akan tinggal di tanah Gosyen\ndan akan dekat kepadaku, engkau serta\nanak dan cucumu, kambing domba dan\nlembu sapimu dan segala milikmu.'),(1369,1,45,11,'Di sanalah aku memelihara engkau-\n-sebab kelaparan ini masih ada lima\ntahun lagi--supaya engkau jangan jatuh\nmiskin bersama seisi rumahmu dan\nsemua orang yang ikut serta dengan\nengkau.'),(1370,1,45,12,'Dan kamu telah melihat dengan mata\nsendiri, dan saudaraku Benyamin juga,\nbahwa mulutku sendiri mengatakannya\nkepadamu.'),(1371,1,45,13,'Sebab itu ceritakanlah kepada bapa\nsegala kemuliaanku di negeri Mesir\nini, dan segala yang telah kamu lihat,\nkemudian segeralah bawa bapa ke mari.\"'),(1372,1,45,14,'Lalu dipeluknyalah leher Benyamin,\nadiknya itu, dan menangislah ia, dan\nmenangis pulalah Benyamin pada bahu\nYusuf.'),(1373,1,45,15,'Yusuf mencium semua saudaranya itu\ndengan mesra dan ia menangis sambil\nmemeluk mereka. Sesudah itu barulah\nsaudara-saudaranya bercakap-cakap\ndengan dia.'),(1374,1,45,16,'Ketika dalam istana Firaun terdengar\nkabar, bahwa saudara-saudara Yusuf\ndatang, hal itu diterima dengan baik oleh\nFiraun dan pegawai-pegawainya.'),(1375,1,45,17,'Lalu berkatalah Firaun kepada Yusuf:\n\"Katakanlah kepada saudara-saudaramu\nBuatlah begini: muatilah binatangbinatangmu dan pergilah ke tanah\nKanaan,'),(1376,1,45,18,'jemputlah ayahmu dan seisi\nrumahmu dan datanglah mendapatkan\naku, maka aku akan memberikan\nkepadamu apa yang paling baik di tanah\nMesir, sehingga kamu akan mengecap\nkesuburan tanah ini.'),(1377,1,45,19,'Selanjutnya engkau mendapat\nperintah mengatakan kepada mereka:\nBuatlah begini: bawalah kereta dari\ntanah Mesir untuk anak-anakmu dan\nisteri-isterimu, jemputlah ayahmu dari\nsana dan datanglah ke mari.'),(1378,1,45,20,'Janganlah kamu merasa sayang\nmeninggalkan barang-barangmu, sebab apa yang paling baik di seluruh tanah\nMesir ini adalah milikmu.\"'),(1379,1,45,21,'Demikianlah dilakukan oleh anakanak Israel itu. Yusuf memberikan\nkereta kepada mereka menurut perintah\nFiraun; juga diberikan kepada mereka\nbekal di jalan.'),(1380,1,45,22,'Kepada mereka masing-masing\ndiberikannya sepotong pesalin dan\nkepada Benyamin diberikannya tiga\nratus uang perak dan lima potong\npesalin.'),(1381,1,45,23,'Di samping itu kepada ayahnya\ndikirimkannya sepuluh ekor keledai\njantan, dimuati dengan apa yang paling\nbaik di Mesir, lagipula sepuluh ekor\nkeledai betina, dimuati dengan gandum\ndan roti dan makanan untuk ayahnya\ndalam perjalanan.'),(1382,1,45,24,'Kemudian ia melepas saudarasaudaranya serta berkata kepada\nmereka: \"Janganlah berbantah-bantah\ndi jalan.\"'),(1383,1,45,25,'Demikianlah mereka pergi dari tanah\nMesir dan sampai di tanah Kanaan,\nkepada Yakub, ayah mereka.'),(1384,1,45,26,'Mereka menceritakan kepadanya:\n\"Yusuf masih hidup, bahkan dialah yang menjadi kuasa atas seluruh tanah Mesir.\"\nTetapi hati Yakub tetap dingin, sebab ia\ntidak dapat mempercayai mereka.'),(1385,1,45,27,'Tetapi ketika mereka menyampaikan\nkepadanya segala perkataan yang\ndiucapkan Yusuf, dan ketika dilihatnya\nkereta yang dikirim oleh Yusuf untuk\nmenjemputnya, maka bangkitlah\nkembali semangat Yakub, ayah mereka\nitu.'),(1386,1,45,28,'Kata Yakub: \"Cukuplah itu; anakku\nYusuf masih hidup; aku mau pergi\nmelihatnya, sebelum aku mati.\"'),(1387,1,46,1,'Jadi berangkatlah Israel dengan segala miliknya dan ia tiba di Bersyeba, lalu dipersembahkannya\nkorban sembelihan kepada Allah Ishak\nayahnya.'),(1388,1,46,2,'Berfirmanlah Allah kepada Israel\ndalam penglihatan waktu malam:\n\"Yakub, Yakub!\" Sahutnya: \"Ya, Tuhan.\"'),(1389,1,46,3,'Lalu firman-Nya: \"Akulah Allah, Allah\nayahmu, janganlah takut pergi ke Mesir,\nsebab Aku akan membuat engkau\nmenjadi bangsa yang besar di sana.'),(1390,1,46,4,'Aku sendiri akan menyertai engkau\npergi ke Mesir dan tentulah Aku juga\nakan membawa engkau kembali; dan tangan Yusuflah yang akan mengatupkan\nkelopak matamu nanti.\"'),(1391,1,46,5,'Lalu berangkatlah Yakub dari\nBersyeba, dan anak-anak Israel\nmembawa Yakub, ayah mereka, beserta\nanak dan isteri mereka, dan mereka\nmenaiki kereta yang dikirim Firaun untuk\nmenjemputnya.'),(1392,1,46,6,'Mereka membawa juga ternaknya dan\nharta bendanya, yang telah diperoleh\nmereka di tanah Kanaan, lalu tibalah\nmereka di Mesir, yakni Yakub dan seluruh\nketurunannya bersama-sama dengan\ndia.'),(1393,1,46,7,'Anak-anak dan cucu-cucunya laki-laki\ndan perempuan, seluruh keturunannya\ndibawanyalah ke Mesir.'),(1394,1,46,8,'Inilah nama-nama bani Israel yang\ndatang ke Mesir, yakni Yakub beserta\nketurunannya. Anak sulung Yakub ialah\nRuben.'),(1395,1,46,9,'Anak-anak Ruben ialah Henokh, Palu,\nHezron dan Karmi.'),(1396,1,46,10,'Anak-anak Simeon ialah Yemuel,\nYamin, Ohad, Yakhin dan Zohar serta\nSaul, anak seorang perempuan Kanaan.'),(1397,1,46,11,'Anak-anak Lewi ialah Gerson, Kehat\ndan Merari.'),(1398,1,46,12,'Anak-anak Yehuda ialah Er, Onan,\nSyela, Peres dan Zerah; tetapi Er\ndan Onan mati di tanah Kanaan; dan\nanak-anak Peres ialah Hezron dan\nHamul.'),(1399,1,46,13,'Anak-anak Isakhar ialah Tola, Pua,\nAyub dan Simron.'),(1400,1,46,14,'Anak-anak Zebulon ialah Sered, Elon\ndan Yahleel.'),(1401,1,46,15,'Itulah keturunan Lea, yang\nmelahirkan bagi Yakub di PadanAram anak-anak lelaki serta Dina\njuga, anaknya yang perempuan. Jadi\nseluruhnya, laki-laki dan perempuan,\nberjumlah tiga puluh tiga jiwa.'),(1402,1,46,16,'Anak-anak Gad ialah Zifyon, Hagi,\nSyuni, Ezbon, Eri, Arodi dan Areli.'),(1403,1,46,17,'Anak-anak Asyer ialah Yimna, Yiswa,\nYiswi dan Beria; Serah ialah saudara\nperempuan mereka; dan anak-anak\nBeria ialah Heber dan Malkiel.'),(1404,1,46,18,'Itulah keturunan Zilpa, yakni hamba\nperempuan yang telah diberikan Laban\nkepada Lea, anaknya perempuan, dan\nyang melahirkan anak-anak bagi Yakub;\nseluruhnya enam belas jiwa.'),(1405,1,46,19,'Anak-anak Rahel, isteri Yakub, ialah\nYusuf dan Benyamin.'),(1406,1,46,20,'Bagi Yusuf lahir Manasye dan Efraim\ndi tanah Mesir, yang dilahirkan baginya\noleh Asnat, anak perempuan Potifera,\nimam di On.'),(1407,1,46,21,'Anak-anak Benyamin ialah Bela,\nBekher, Asybel, Gera, Naaman, Ehi,\nRosh, Mupim, Hupim dan Ared.'),(1408,1,46,22,'Itulah keturunan Rahel, yang telah\nlahir bagi Yakub, seluruhnya berjumlah\nempat belas jiwa.'),(1409,1,46,23,'Anak Dan ialah Husim.'),(1410,1,46,24,'Anak-anak Naftali ialah Yahzeel,\nGuni, Yezer dan Syilem.'),(1411,1,46,25,'Itulah keturunan Bilha, yakni hamba\nperempuan yang diberikan Laban kepada\nRahel, anaknya yang perempuan dan\nyang melahirkan anak-anak itu bagi\nYakub--seluruhnya berjumlah tujuh jiwa.'),(1412,1,46,26,'Semua orang yang tiba di Mesir\nbersama-sama dengan Yakub, yakni\nanak-anak kandungnya, dengan\ntidak terhitung isteri anak-anaknya,\nseluruhnya berjumlah enam puluh enam\njiwa.'),(1413,1,46,27,'Anak-anak Yusuf yang lahir baginya\ndi Mesir ada dua orang. Jadi keluarga\nYakub yang tiba di Mesir, seluruhnya\nberjumlah tujuh puluh jiwa.'),(1414,1,46,28,'Yakub menyuruh Yehuda berjalan\nlebih dahulu mendapatkan Yusuf, supaya\nYusuf datang ke Gosyen menemui\nayahnya. Sementara itu sampailah\nmereka ke tanah Gosyen.'),(1415,1,46,29,'Lalu Yusuf memasang keretanya dan\npergi ke Gosyen, mendapatkan Israel,\nayahnya. Ketika ia bertemu dengan dia,\ndipeluknyalah leher ayahnya dan lama\nmenangis pada bahunya.'),(1416,1,46,30,'Berkatalah Israel kepada Yusuf:\n\"Sekarang bolehlah aku mati, setelah\naku melihat mukamu dan mengetahui\nbahwa engkau masih hidup.\"'),(1417,1,46,31,'Kemudian berkatalah Yusuf kepada\nsaudara-saudaranya dan kepada\nkeluarga ayahnya itu: \"Aku mau\nmenghadap Firaun dan memberitahukan\nkepadanya: Saudara-saudaraku dan\nkeluarga ayahku, yang tinggal di tanah\nKanaan, telah datang kepadaku;'),(1418,1,46,32,'orang-orang itu gembala kambing\ndomba, sebab mereka itu pemelihara\nternak, dan kambing dombanya, lembu\nsapinya dan segala miliknya telah dibawa\nmereka.'),(1419,1,46,33,'Apabila Firaun memanggil kamu dan\nbertanya: Apakah pekerjaanmu?'),(1420,1,46,34,'maka jawablah: Hamba-hambamu\nini pemelihara ternak, sejak dari kecil\nsampai sekarang, baik kami maupun\nnenek moyang kami--dengan maksud\nsupaya kamu boleh diam di tanah\nGosyen.\" --Sebab segala gembala\nkambing domba adalah suatu kekejian\nbagi orang Mesir.'),(1421,1,47,1,'Kemudian pergilah Yusuf memberitahukan kepada Firaun: \"Ayahku dan saudara-saudaraku beserta\nkambing dombanya, lembu sapinya dan\nsegala miliknya telah datang dari tanah\nKanaan, dan sekarang mereka ada di\ntanah Gosyen.\"'),(1422,1,47,2,'Dari antara saudara-saudaranya\nitu dibawanya lima orang menghadap\nFiraun.'),(1423,1,47,3,'Firaun bertanya kepada saudarasaudara Yusuf itu: \"Apakah\npekerjaanmu?\" Jawab mereka\nkepada Firaun: \"Hamba-hambamu ini\ngembala domba, baik kami maupun\nnenek moyang kami.\"'),(1424,1,47,4,'Lagi kata mereka kepada Firaun:\n\"Kami datang untuk tinggal di negeri ini\nsebagai orang asing, sebab tidak ada lagi\npadang rumput untuk kumpulan ternak  hamba-hambamu ini, karena hebat\nkelaparan itu di tanah Kanaan; maka\nsekarang, izinkanlah hamba-hambamu\nini menetap di tanah Gosyen.\"'),(1425,1,47,5,'Lalu berkatalah Firaun kepada Yusuf:\n\"Ayahmu dan saudara-saudaramu telah\ndatang kepadamu.'),(1426,1,47,6,'Tanah Mesir ini terbuka untukmu.\nTunjukkanlah kepada ayahmu dan\nkepada saudara-saudaramu tempat\nmenetap di tempat yang terbaik dari\nnegeri ini, biarlah mereka diam di\ntanah Gosyen. Dan jika engkau tahu\ndi antara mereka orang-orang yang\ntangkas, tempatkanlah mereka menjadi\npengawas ternakku.\"'),(1427,1,47,7,'usuf membawa juga Yakub,\nayahnya, menghadap Firaun. Lalu Yakub\nmemohonkan berkat bagi Firaun.'),(1428,1,47,8,'Kemudian bertanyalah Firaun kepada\nYakub: \"Sudah berapa tahun umurmu?\"'),(1429,1,47,9,'Jawab Yakub kepada Firaun: \"Tahuntahun pengembaraanku sebagai orang\nasing berjumlah seratus tiga puluh\ntahun. Tahun-tahun hidupku itu sedikit\nsaja dan buruk adanya, tidak mencapai\numur nenek moyangku, yakni jumlah tahun mereka mengembara sebagai\norang asing.\"'),(1430,1,47,10,'Lalu Yakub memohonkan berkat bagi\nFiraun, sesudah itu keluarlah ia dari\ndepan Firaun.'),(1431,1,47,11,'Yusuf menunjukkan kepada ayahnya\ndan saudara-saudaranya tempat untuk\nmenetap dan memberikan kepada\nmereka tanah milik di tanah Mesir,\ndi tempat yang terbaik di negeri\nitu, di tanah Rameses, seperti yang\ndiperintahkan Firaun.'),(1432,1,47,12,'Dan Yusuf memelihara ayahnya,\nsaudara-saudaranya dan seisi rumah\nayahnya dengan makanan, menurut\njumlah anak-anak mereka.'),(1433,1,47,13,'Di seluruh negeri itu tidak ada\nmakanan, sebab kelaparan itu sangat\nhebat, sehingga seisi tanah Mesir\ndan tanah Kanaan lemah lesu karena\nkelaparan itu.'),(1434,1,47,14,'Maka Yusuf mengumpulkan segala\nuang yang terdapat di tanah Mesir dan\ndi tanah Kanaan, yakni uang pembayar\ngandum yang dibeli mereka; dan Yusuf\nmembawa uang itu ke dalam istana\nFiraun.'),(1435,1,47,15,'Setelah habis uang di tanah Mesir\ndan di tanah Kanaan, datanglah semua\norang Mesir menghadap Yusuf serta\nberkata: \"Berilah makanan kepada kami!\nMengapa kami harus mati di depanmu?\nSebab tidak ada lagi uang.\"'),(1436,1,47,16,'Jawab Yusuf: \"Jika tidak ada lagi\nuang, berilah ternakmu, maka aku akan\nmemberi makanan kepadamu sebagai\nganti ternakmu itu.\"'),(1437,1,47,17,'Lalu mereka membawa ternaknya\nkepada Yusuf dan Yusuf memberi\nmakanan kepada mereka ganti\nkuda, kumpulan kambing domba dan\nkumpulan lembu sapi dan keledainya,\njadi disediakannyalah bagi mereka\nmakanan ganti segala ternaknya pada\ntahun itu.'),(1438,1,47,18,'Setelah lewat tahun itu, datanglah\nmereka kepadanya, pada tahun yang\nkedua, serta berkata kepadanya: \"Tidak\nusah kami sembunyikan kepada tuanku,\nbahwa setelah uang kami habis dan\nsetelah kumpulan ternak kami menjadi\nmilik tuanku, tidaklah ada lagi yang\ntinggal yang dapat kami serahkan\nkepada tuanku selain badan kami dan\ntanah kami.'),(1439,1,47,19,'Mengapa kami harus mati di depan\nmatamu, baik kami maupun tanah kami?\nBelilah kami dan tanah kami sebagai\nganti makanan, maka kami dengan\ntanah kami akan menjadi hamba kepada\nFiraun. Berikanlah benih, supaya kami\nhidup dan jangan mati, dan supaya\ntanah itu jangan menjadi tandus.\"'),(1440,1,47,20,'Lalu Yusuf membeli segala tanah\norang Mesir untuk Firaun, sebab orang\nMesir itu masing-masing menjual\nladangnya, karena berat kelaparan itu\nmenimpa mereka. Demikianlah negeri\nitu menjadi milik Firaun.'),(1441,1,47,21,'Dan tentang rakyat itu,\ndiperhambakannyalah mereka di\ndaerah Mesir dari ujung yang satu\nsampai ujung yang lain.'),(1442,1,47,22,'Hanya tanah para imam tidak\ndibelinya, sebab para imam mendapat\ntunjangan tetap dari Firaun, dan mereka\nhidup dari tunjangan itu; itulah sebabnya\nmereka tidak menjual tanahnya.'),(1443,1,47,23,'Berkatalah Yusuf kepada rakyat itu:\n\"Pada hari ini aku telah membeli kamu\ndan tanahmu untuk Firaun; inilah benih\nbagimu, supaya kamu dapat menabur di\ntanah itu.'),(1444,1,47,24,'Mengenai hasilnya, kamu harus\nberikan seperlima bagian kepada Firaun,\ndan yang empat bagian lagi, itulah\nmenjadi benih untuk ladangmu dan\nmenjadi makanan kamu dan mereka\nyang ada di rumahmu, dan menjadi\nmakanan anak-anakmu.\"'),(1445,1,47,25,'Lalu berkatalah mereka: \"Engkau\ntelah memelihara hidup kami; asal\nkiranya kami mendapat kasih tuanku,\nbiarlah kami menjadi hamba kepada\nFiraun.\"'),(1446,1,47,26,'Yusuf membuat hal itu menjadi suatu\nketetapan mengenai tanah di Mesir\nsampai sekarang, yakni bahwa seperlima\ndari hasilnya menjadi milik Firaun; hanya\ntanah para imam tidak menjadi milik\nFiraun.'),(1447,1,47,27,'Maka diamlah Israel di tanah Mesir,\ndi tanah Gosyen, dan mereka menjadi\npenduduk di situ. Mereka beranak cucu\ndan sangat bertambah banyak.'),(1448,1,47,28,'Dan Yakub masih hidup tujuh belas\ntahun di tanah Mesir, maka umur Yakub,\nyakni tahun-tahun hidupnya, menjadi\nseratus empat puluh tujuh tahun.'),(1449,1,47,29,'Ketika hampir waktunya bahwa Israel\nakan mati, dipanggilnyalah anaknya, Yusuf, dan berkata kepadanya: \"Jika aku\nmendapat kasihmu, letakkanlah kiranya\ntanganmu di bawah pangkal pahaku,\ndan bersumpahlah, bahwa engkau akan\nmenunjukkan kasih dan setia kepadaku:\nJanganlah kiranya kuburkan aku di Mesir,'),(1450,1,47,30,'karena aku mau mendapat\nperhentian bersama-sama dengan nenek\nmoyangku. Sebab itu angkutlah aku dari\nMesir dan kuburkanlah aku dalam kubur\nmereka.\" Jawabnya: \"Aku akan berbuat\nseperti katamu itu.\"'),(1451,1,47,31,'Kemudian kata Yakub:\n\"Bersumpahlah kepadaku.\" Maka\nYusufpun bersumpah kepadanya. Lalu\nsujudlah Israel di sebelah kepala tempat\ntidurnya.'),(1452,1,48,1,'Sesudah itu ada orang mengatakan kepada Yusuf: \"Ayahmu sakit!\" Lalu dibawanyalah\nkedua anaknya, Manasye dan Efraim.'),(1453,1,48,2,'Ketika diberitahukan kepada Yakub:\n\"Telah datang anakmu Yusuf kepadamu,\"\nmaka Israel mengumpulkan segenap\nkekuatannya dan duduklah ia di tempat\ntidurnya.'),(1454,1,48,3,'Berkatalah Yakub kepada Yusuf:\n\"Allah, Yang Mahakuasa telah menampakkan diri kepadaku di\nLus di tanah Kanaan dan memberkati\naku'),(1455,1,48,4,'serta berfirman kepadaku: Akulah\nyang membuat engkau beranak cucu,\ndan Aku akan membuat engkau\nbertambah banyak dan menjadi\nsekumpulan bangsa-bangsa; Aku\nakan memberikan negeri ini kepada\nketurunanmu untuk menjadi miliknya\nsampai selama-lamanya.'),(1456,1,48,5,'Maka sekarang kedua anakmu yang\nlahir bagimu di tanah Mesir, sebelum aku\ndatang kepadamu ke Mesir, akulah yang\nempunya mereka; akulah yang akan\nempunya Efraim dan Manasye sama\nseperti Ruben dan Simeon.'),(1457,1,48,6,'Dan keturunanmu yang kauperoleh\nsesudah mereka, engkaulah yang\nempunya, tetapi dalam pembagian\nwarisan nama mereka akan disebutkan\nberdasarkan nama kedua saudaranya\nitu.'),(1458,1,48,7,'Kalau aku, pada waktu perjalananku\ndari Padan, aku kematian Rahel di\ntanah Kanaan di jalan, ketika kami tidak\nberapa jauh lagi dari Efrata, dan aku menguburkannya di sana, di sisi jalan ke\nEfrata\" --yaitu Betlehem.'),(1459,1,48,8,'Ketika Israel melihat anak-anak Yusuf\nitu, bertanyalah ia: \"Siapakah ini?\"'),(1460,1,48,9,'Jawab Yusuf kepada ayahnya: \"Inilah\nanak-anakku yang telah diberikan Allah\nkepadaku di sini.\" Maka kata Yakub:\n\"Dekatkanlah mereka kepadaku, supaya\nkuberkati mereka.\"'),(1461,1,48,10,'Adapun mata Israel telah kabur\nkarena tuanya, jadi ia tidak dapat lagi\nmelihat. Kemudian Yusuf mendekatkan\nmereka kepada ayahnya: dan mereka\ndicium serta didekap oleh ayahnya.'),(1462,1,48,11,'Lalu berkatalah Israel kepada Yusuf:\n\"Tidak kusangka-sangka, bahwa aku\nakan melihat mukamu lagi, tetapi\nsekarang Allah bahkan memberi aku\nmelihat keturunanmu.\"'),(1463,1,48,12,'Lalu Yusuf menarik mereka dari\nantara lutut ayahnya, dan ia sujud\ndengan mukanya sampai ke tanah.'),(1464,1,48,13,'Setelah itu Yusuf memegang mereka\nkeduanya, dengan tangan kanan\ndipegangnya Efraim, yaitu di sebelah kiri\nIsrael, dan dengan tangan kiri Manasye,\nyaitu di sebelah kanan Israel, lalu\ndidekatkannyalah mereka kepadanya.'),(1465,1,48,14,'Tetapi Israel mengulurkan tangan\nkanannya dan meletakkannya di atas\nkepala Efraim, walaupun ia yang bungsu,\ndan tangan kirinya di atas kepala\nManasye--jadi tangannya bersilang,\nwalaupun Manasye yang sulung.'),(1466,1,48,15,'Sesudah itu diberkatinyalah Yusuf,\nkatanya: \"Nenekku dan ayahku,\nAbraham dan Ishak, telah hidup di\nhadapan Allah; Allah itu, sebagai Allah\nyang telah menjadi gembalaku selama\nhidupku sampai sekarang,'),(1467,1,48,16,'dan sebagai Malaikat yang telah\nmelepaskan aku dari segala bahaya,\nDialah kiranya yang memberkati orangorang muda ini, sehingga namaku serta\nnama nenek dan bapaku, Abraham dan\nIshak, termasyhur oleh karena mereka\ndan sehingga mereka bertambahtambah menjadi jumlah yang besar di\nbumi.\"'),(1468,1,48,17,'Ketika Yusuf melihat bahwa ayahnya\nmeletakkan tangan kanannya di atas\nkepala Efraim, hal itu dipandangnya tidak\nbaik; lalu dipegangnya tangan ayahnya\nuntuk memindahkannya dari atas kepala\nEfraim ke atas kepala Manasye.'),(1469,1,48,18,'Katanya kepada ayahnya: \"Janganlah\ndemikian, ayahku, sebab inilah yang\nsulung, letakkanlah tangan kananmu ke\natas kepalanya.\"'),(1470,1,48,19,'Tetapi ayahnya menolak, katanya:\n\"Aku tahu, anakku, aku tahu; ia juga\nakan menjadi suatu bangsa dan ia\njuga akan menjadi besar kuasanya;\nwalaupun begitu, adiknya akan lebih\nbesar kuasanya dari padanya, dan\nketurunan adiknya itu akan menjadi\nsejumlah besar bangsa-bangsa.\"'),(1471,1,48,20,'Lalu diberkatinyalah mereka\npada waktu itu, katanya: \"Dengan\nmenyebutkan namamulah orang Israel\nakan memberkati, demikian: Allah\nkiranya membuat engkau seperti Efraim\ndan seperti Manasye.\" Demikianlah\ndidahulukannya Efraim dari pada\nManasye.'),(1472,1,48,21,'Kemudian berkatalah Israel kepada\nYusuf: \"Tidak lama lagi aku akan mati,\ntetapi Allah akan menyertai kamu dan\nmembawa kamu kembali ke negeri\nnenek moyangmu.'),(1473,1,48,22,'Dan sekarang aku memberikan\nkepadamu sebagai kelebihanmu dari\npada saudara-saudaramu, suatu punggung gunung yang kurebut dengan\npedang dan panahku dari tangan orang\nAmori.\"'),(1474,1,49,1,'Kemudian Yakub memanggil anak-anaknya dan berkata: \"Datanglah berkumpul, supaya\nkuberitahukan kepadamu, apa yang\nakan kamu alami di kemudian hari.'),(1475,1,49,2,'Berhimpunlah kamu dan dengarlah,\nya anak-anak Yakub, dengarlah kepada\nIsrael, ayahmu.'),(1476,1,49,3,'Ruben, engkaulah anak sulungku,\nkekuatanku dan permulaan\nkegagahanku, engkaulah yang terutama\ndalam keluhuran, yang terutama dalam\nkesanggupan.'),(1477,1,49,4,'Engkau yang membual sebagai\nair, tidak lagi engkau yang terutama,\nsebab engkau telah menaiki tempat\ntidur ayahmu; waktu itu engkau telah\nmelanggar kesuciannya. Dia telah\nmenaiki petiduranku!'),(1478,1,49,5,'Simeon dan Lewi bersaudara; senjata\nmereka ialah alat kekerasan.'),(1479,1,49,6,'Janganlah kiranya jiwaku turut dalam\npermupakatan mereka, janganlah\nkiranya rohku bersatu dengan\nperkumpulan mereka, sebab dalam kemarahannya mereka telah membunuh\norang dan dalam keangkaraannya\nmereka telah memotong urat keting\nlembu.'),(1480,1,49,7,'Terkutuklah kemarahan mereka,\nsebab amarahnya keras, terkutuklah\nkeberangan mereka, sebab berangnya\nbengis. Aku akan membagi-bagikan\nmereka di antara anak-anak Yakub\ndan menyerakkan mereka di antara\nanak-anak Israel.'),(1481,1,49,8,'Yehuda, engkau akan dipuji oleh\nsaudara-saudaramu, tanganmu akan\nmenekan tengkuk musuhmu, kepadamu\nakan sujud anak-anak ayahmu.'),(1482,1,49,9,'Yehuda adalah seperti anak singa:\nsetelah menerkam, engkau naik\nke suatu tempat yang tinggi, hai\nanakku; ia meniarap dan berbaring\nseperti singa jantan atau seperti\nsinga betina; siapakah yang berani\nmembangunkannya?'),(1483,1,49,10,'Tongkat kerajaan tidak akan\nberanjak dari Yehuda ataupun lambang\npemerintahan dari antara kakinya,\nsampai dia datang yang berhak\natasnya, maka kepadanya akan takluk\nbangsa-bangsa.'),(1484,1,49,11,'Ia akan menambatkan keledainya\npada pohon anggur dan anak keledainya\npada pohon anggur pilihan; ia akan\nmencuci pakaiannya dengan anggur dan\nbajunya dengan darah buah anggur.'),(1485,1,49,12,'Matanya akan merah karena anggur\ndan giginya akan putih karena susu.'),(1486,1,49,13,'Zebulon akan diam di tepi pantai\nlaut, ia akan menjadi pangkalan kapal,\ndan batasnya akan bersisi dengan Sidon.'),(1487,1,49,14,'Isakhar adalah seperti keledai yang\nkuat tulangnya, yang meniarap diapit\nbebannya,'),(1488,1,49,15,'ketika dilihatnya, bahwa perhentian\nitu baik dan negeri itu permai, maka\ndisendengkannyalah bahunya untuk\nmemikul, lalu menjadi budak rodi.'),(1489,1,49,16,'Adapun Dan, ia akan mengadili\nbangsanya sebagai salah satu suku\nIsrael.'),(1490,1,49,17,'Semoga Dan menjadi seperti ular\ndi jalan, seperti ular beludak di denai\nyang memagut tumit kuda, sehingga\npenunggangnya jatuh ke belakang.'),(1491,1,49,18,'Aku menanti-nantikan keselamatan\nyang dari pada-Mu, ya TUHAN.'),(1492,1,49,19,'Gad, ia akan diserang oleh\ngerombolan, tetapi ia akan menyerang\ntumit mereka.'),(1493,1,49,20,'Asyer, makanannya akan limpah\nmewah dan ia akan memberikan\nsantapan raja-raja.'),(1494,1,49,21,'Naftali adalah seperti rusa betina\nyang terlepas; ia akan melahirkan\nanak-anak indah.'),(1495,1,49,22,'Yusuf adalah seperti pohon\nbuah-buahan yang muda, pohon\nbuah-buahan yang muda pada mata\nair. Dahan-dahannya naik mengatasi\ntembok.'),(1496,1,49,23,'Walaupun pemanah-pemanah\ntelah mengusiknya, memanahnya dan\nmenyerbunya,'),(1497,1,49,24,'namun panahnya tetap kokoh dan\nlengan tangannya tinggal liat, oleh\npertolongan Yang Mahakuat pelindung\nYakub, oleh sebab gembalanya Gunung\nBatu Israel,'),(1498,1,49,25,'oleh Allah ayahmu yang akan\nmenolong engkau, dan oleh Allah Yang\nMahakuasa, yang akan memberkati\nengkau dengan berkat dari langit di\natas, dengan berkat samudera raya yang letaknya di bawah, dengan berkat buah\ndada dan kandungan.'),(1499,1,49,26,'Berkat ayahmu melebihi berkat\ngunung-gunung yang sejak dahulu,\nyakni yang paling sedap di bukit-bukit\nyang berabad-abad; semuanya itu akan\nturun ke atas kepala Yusuf, ke atas batu\nkepala orang yang teristimewa di antara\nsaudara-saudaranya.'),(1500,1,49,27,'Benyamin adalah seperti serigala\nyang menerkam; pada waktu pagi ia\nmemakan mangsanya dan pada waktu\npetang ia membagi-bagi rampasannya.\"'),(1501,1,49,28,'Itulah semuanya suku Israel, dua\nbelas jumlahnya; dan itulah yang\ndikatakan ayahnya kepada mereka,\nketika ia memberkati mereka; tiap-tiap\norang diberkatinya dengan berkat\nyang diuntukkan kepada mereka\nmasing-masing.'),(1502,1,49,29,'Kemudian berpesanlah Yakub kepada\nmereka: \"Apabila aku nanti dikumpulkan\nkepada kaum leluhurku, kuburkanlah\naku di sisi nenek moyangku dalam gua\nyang di ladang Efron, orang Het itu,'),(1503,1,49,30,'dalam gua yang di ladang Makhpela\ndi sebelah timur Mamre di tanah Kanaan,\nladang yang telah dibeli Abraham dari  Efron, orang Het itu, untuk menjadi\nkuburan milik.'),(1504,1,49,31,'Di situlah dikuburkan Abraham\nbeserta Sara, isterinya; di situlah\ndikuburkan Ishak beserta Ribka,\nisterinya,'),(1505,1,49,32,'dan di situlah juga kukuburkan Lea;\nladang dengan gua yang ada di sana\ntelah dibeli dari orang Het.\"'),(1506,1,49,33,'Setelah Yakub selesai berpesan\nkepada anak-anaknya, ditariknyalah\nkakinya ke atas tempat berbaring dan\nmeninggallah ia, maka ia dikumpulkan\nkepada kaum leluhurnya.'),(1507,1,50,1,'Lalu Yusuf merebahkan dirinya  mendekap muka ayahnya serta menangisi dan mencium dia.'),(1508,1,50,2,'Dan Yusuf memerintahkan kepada\ntabib-tabib, yaitu hamba-hambanya,\nuntuk merempah-rempahi mayat\nayahnya; maka tabib-tabib itu\nmerempah-rempahi mayat Israel.'),(1509,1,50,3,'Hal itu memerlukan empat puluh\nhari lamanya, sebab demikianlah\nlamanya waktu yang diperlukan untuk\nmerempah-rempahi, dan orang Mesir\nmenangisi dia tujuh puluh hari lamanya.'),(1510,1,50,4,'Setelah lewat hari-hari penangisan\nitu, berkatalah Yusuf kepada seisi istana\nFiraun: \"Jika kiranya aku mendapat\nkasihmu, katakanlah kepada Firaun,'),(1511,1,50,5,'bahwa ayahku telah menyuruh aku\nbersumpah, katanya: Tidak lama lagi\naku akan mati; dalam kuburku yang\ntelah kugali di tanah Kanaan, di situlah\nkaukuburkan aku. Oleh sebab itu,\nizinkanlah aku pergi ke sana, supaya aku\nmenguburkan ayahku; kemudian aku\nakan kembali.\"'),(1512,1,50,6,'Lalu berkatalah Firaun: \"Pergilah\nke sana dan kuburkanlah ayahmu itu,\nseperti yang telah disuruhnya engkau\nbersumpah.\"'),(1513,1,50,7,'Lalu berjalanlah Yusuf ke sana\nuntuk menguburkan ayahnya, dan\nbersama-sama dengan dia berjalanlah\nsemua pegawai Firaun, para tua-tua dari\nistananya, dan semua tua-tua dari tanah\nMesir,'),(1514,1,50,8,'serta seisi rumah Yusuf juga, saudarasaudaranya dan seisi rumah ayahnya;\nhanya anak-anaknya serta kambing\ndomba dan lembu sapinya ditinggalkan\nmereka di tanah Gosyen.'),(1515,1,50,9,'Baik kereta maupun orang-orang\nberkuda turut pergi ke sana bersamasama dengan dia, sehingga iring-iringan\nitu sangat besar'),(1516,1,50,10,'Setelah mereka sampai ke GorenHaatad, yang di seberang sungai Yordan,\nmaka mereka mengadakan di situ\nratapan yang sangat sedih dan riuh; dan\nYusuf mengadakan perkabungan tujuh\nhari lamanya karena ayahnya itu.'),(1517,1,50,11,'Ketika penduduk negeri itu, orangorang Kanaan, melihat perkabungan di\nGoren-Haatad itu, berkatalah mereka:\n\"Inilah perkabungan orang Mesir yang\namat riuh.\" Itulah sebabnya tempat itu\ndinamai Abel-Mizraim, yang letaknya di\nseberang Yordan.'),(1518,1,50,12,'Anak-anak Yakub melakukan\nkepadanya, seperti yang dipesankannya\nkepada mereka.'),(1519,1,50,13,'Anak-anaknya mengangkut dia ke\ntanah Kanaan, dan mereka menguburkan\ndia dalam gua di ladang Makhpela yang\ntelah dibeli Abraham dari Efron, orang\nHet itu, untuk menjadi kuburan milik,\nyaitu ladang yang di sebelah timur\nMamre.'),(1520,1,50,14,'Setelah ayahnya dikuburkan,\npulanglah Yusuf ke Mesir, dia dan\nsaudara-saudaranya dan semua orang\nyang turut pergi ke sana bersama-sama\ndengan dia untuk menguburkan ayahnya\nitu.'),(1521,1,50,15,'Ketika saudara-saudara Yusuf\nmelihat, bahwa ayah mereka telah\nmati, berkatalah mereka: \"Boleh jadi\nYusuf akan mendendam kita dan\nmembalaskan sepenuhnya kepada kita\nsegala kejahatan yang telah kita lakukan\nkepadanya.\"'),(1522,1,50,16,'Sebab itu mereka menyuruh\nmenyampaikan pesan ini kepada\nYusuf: \"Sebelum ayahmu mati, ia telah\nberpesan:'),(1523,1,50,17,'Beginilah harus kamu katakan\nkepada Yusuf: Ampunilah kiranya\nkesalahan saudara-saudaramu dan dosa\nmereka, sebab mereka telah berbuat\njahat kepadamu. Maka sekarang,\nampunilah kiranya kesalahan yang\ndibuat hamba-hamba Allah ayahmu.\"\nLalu menangislah Yusuf, ketika orang\nberkata demikian kepadanya.'),(1524,1,50,18,'Juga saudara-saudaranya datang\nsendiri dan sujud di depannya serta berkata: \"Kami datang untuk menjadi\nbudakmu.\"'),(1525,1,50,19,'Tetapi Yusuf berkata kepada mereka:\n\"Janganlah takut, sebab aku inikah\npengganti Allah?'),(1526,1,50,20,'Memang kamu telah mereka-rekakan\nyang jahat terhadap aku, tetapi\nAllah telah mereka-rekakannya untuk\nkebaikan, dengan maksud melakukan\nseperti yang terjadi sekarang ini, yakni\nmemelihara hidup suatu bangsa yang\nbesar.'),(1527,1,50,21,'Jadi janganlah takut, aku akan\nmenanggung makanmu dan makan\nanak-anakmu juga.\" Demikianlah\nia menghiburkan mereka dan\nmenenangkan hati mereka dengan\nperkataannya.'),(1528,1,50,22,'Adapun Yusuf, ia tetap tinggal di\nMesir beserta kaum keluarganya; dan\nYusuf hidup seratus sepuluh tahun.'),(1529,1,50,23,'Jadi Yusuf sempat melihat anak cucu\nEfraim sampai keturunan yang ketiga;\njuga anak-anak Makhir, anak Manasye,\nlahir di pangkuan Yusuf.'),(1530,1,50,24,'Berkatalah Yusuf kepada saudarasaudaranya: \"Tidak lama lagi aku akan\nmati; tentu Allah akan memperhatikan kamu dan membawa kamu keluar\ndari negeri ini, ke negeri yang telah\ndijanjikan-Nya dengan sumpah kepada\nAbraham, Ishak dan Yakub.\"'),(1531,1,50,25,'Lalu Yusuf menyuruh anak-anak\nIsrael bersumpah, katanya: \"Tentu\nAllah akan memperhatikan kamu;\npada waktu itu kamu harus membawa\ntulang-tulangku dari sini.\"'),(1532,1,50,26,'Kemudian matilah Yusuf, berumur\nseratus sepuluh tahun. Mayatnya\ndirempah-rempahi, dan ditaruh dalam\npeti mati di Mesir.'),(1533,2,1,1,'Inilah nama para anak Israel yang datang ke Mesir bersama-sama dengan Yakub; mereka datang dengan keluarganya masing-masing:'),(1534,2,1,2,'Ruben, Simeon, Lewi dan Yehuda;'),(1535,2,1,3,'Isakhar, Zebulon dan Benyamin;'),(1536,2,1,4,'Dan serta Naftali, Gad dan Asyer.'),(1537,2,1,5,'Seluruh keturunan yang diperoleh Yakub berjumlah tujuh puluh jiwa. Tetapi Yusuf telah ada di Mesir.'),(1538,2,1,6,'Kemudian matilah Yusuf, serta semua saudara-saudaranya dan semua orang yang seangkatan dengan dia.'),(1539,2,1,7,'Orang-orang Israel beranak cucu dan tak terbilang jumlahnya; mereka bertambah banyak dan dengan dahsyat berlipat ganda, sehingga negeri itu dipenuhi mereka.'),(1540,2,1,8,'Kemudian bangkitlah seorang raja baru memerintah tanah Mesir, yang tidak mengenal Yusuf.'),(1541,2,1,9,'Berkatalah raja itu kepada rakyatnya: \"Bangsa Israel itu sangat banyak dan lebih besar jumlahnya dari pada kita.'),(1542,2,1,10,'Marilah kita bertindak dengan bijaksana terhadap mereka, supaya mereka jangan bertambah banyak lagi dan--jika terjadi peperangan--jangan bersekutu nanti dengan musuh kita dan memerangi kita, lalu pergi dari negeri ini.\"'),(1543,2,1,11,'Sebab itu pengawas-pengawas rodi ditempatkan atas mereka untuk menindas mereka dengan kerja paksa: mereka harus mendirikan bagi Firaun kota-kota perbekalan, yakni Pitom dan Raamses.'),(1544,2,1,12,'Tetapi makin ditindas, makin bertambah banyak dan berkembang mereka, sehingga orang merasa takut kepada orang Israel itu.'),(1545,2,1,13,'Lalu dengan kejam orang Mesir memaksa orang Israel bekerja,'),(1546,2,1,14,'dan memahitkan hidup mereka dengan pekerjaan yang berat, yaitu mengerjakan tanah liat dan batu bata, dan berbagai-bagai pekerjaan di padang, ya segala pekerjaan yang dengan kejam dipaksakan orang Mesir kepada mereka itu.'),(1547,2,1,15,'Raja Mesir juga memerintahkan kepada bidan-bidan yang menolong perempuan Ibrani, seorang bernama Sifra dan yang lain bernama Pua, katanya:'),(1548,2,1,16,'Apabila kamu menolong perempuan Ibrani pada waktu bersalin, kamu harus memperhatikan waktu anak itu lahir: jika anak laki-laki, kamu harus membunuhnya, tetapi jika anak perempuan, bolehlah ia hidup.'),(1549,2,1,17,'Tetapi bidan-bidan itu takut akan Allah dan tidak melakukan seperti yang dikatakan raja Mesir kepada mereka, dan membiarkan bayi-bayi itu hidup.'),(1550,2,1,18,'Lalu raja Mesir memanggil bidan-bidan itu dan bertanya kepada mereka: \"Mengapakah kamu berbuat demikian membiarkan hidup bayi-bayi itu?\"'),(1551,2,1,19,'Jawab bidan-bidan itu kepada Firaun: \"Sebab perempuan Ibrani tidak sama dengan perempuan Mesir; melainkan mereka kuat: sebelum bidan datang, mereka telah bersalin.\"'),(1552,2,1,20,'Maka Allah berbuat baik kepada bidan-bidan itu; bertambah banyaklah bangsa itu dan sangat berlipat ganda.'),(1553,2,1,21,'Dan karena bidan-bidan itu takut akan Allah, maka Ia membuat mereka berumah tangga.'),(1554,2,1,22,'Lalu Firaun memberi perintah kepada seluruh rakyatnya: \"Lemparkanlah segala anak laki-laki yang lahir bagi orang Ibrani ke dalam sungai Nil; tetapi segala anak perempuan biarkanlah hidup.\"'),(1555,2,2,1,'Seorang laki-laki dari keluarga Lewi kawin dengan seorang perempuan Lewi;'),(1556,2,2,2,'lalu mengandunglah ia dan melahirkan seorang anak laki-laki. Ketika dilihatnya, bahwa anak itu cantik, disembunyikannya tiga bulan lamanya.'),(1557,2,2,3,'Tetapi ia tidak dapat menyembunyikannya lebih lama lagi, sebab itu diambilnya sebuah peti pandan, dipakalnya dengan gala-gala dan ter, diletakkannya bayi itu di dalamnya dan ditaruhnya peti itu di tengah-tengah teberau di tepi sungai Nil;'),(1558,2,2,4,'kakaknya perempuan berdiri di tempat yang agak jauh untuk melihat, apakah yang akan terjadi dengan dia.'),(1559,2,2,5,'Maka datanglah puteri Firaun untuk mandi di sungai Nil, sedang dayang-dayangnya berjalan-jalan di tepi sungai Nil, lalu terlihatlah olehnya peti yang di tengah-tengah teberau itu, maka disuruhnya hambanya perempuan untuk mengambilnya.'),(1560,2,2,6,'Ketika dibukanya, dilihatnya bayi itu, dan tampaklah anak itu menangis, sehingga belas kasihanlah ia kepadanya dan berkata: \"Tentulah ini bayi orang Ibrani.\"'),(1561,2,2,7,'Lalu bertanyalah kakak anak itu kepada puteri Firaun: \"Akan kupanggilkah bagi tuan puteri seorang inang penyusu dari perempuan Ibrani untuk menyusukan bayi itu bagi tuan puteri?\"'),(1562,2,2,8,'Sahut puteri Firaun kepadanya: \"Baiklah.\" Lalu pergilah gadis itu memanggil ibu bayi itu.'),(1563,2,2,9,'Maka berkatalah puteri Firaun kepada ibu itu: \"Bawalah bayi ini dan susukanlah dia bagiku, maka aku akan memberi upah kepadamu.\" Kemudian perempuan itu mengambil bayi itu dan menyusuinya.'),(1564,2,2,10,'Ketika anak itu telah besar, dibawanyalah kepada puteri Firaun, yang mengangkatnya menjadi anaknya, dan menamainya Musa, sebab katanya: \"Karena aku telah menariknya dari air.\"'),(1565,2,2,11,'Pada waktu itu, ketika Musa telah dewasa, ia keluar mendapatkan saudara-saudaranya untuk melihat kerja paksa mereka; lalu dilihatnyalah seorang Mesir memukul seorang Ibrani, seorang dari saudara-saudaranya itu.'),(1566,2,2,12,'Ia menoleh ke sana sini dan ketika dilihatnya tidak ada orang, dibunuhnya orang Mesir itu, dan disembunyikannya mayatnya dalam pasir.'),(1567,2,2,13,'Ketika keesokan harinya ia keluar lagi, didapatinya dua orang Ibrani tengah berkelahi. Ia bertanya kepada yang bersalah itu: \"Mengapa engkau pukul temanmu?\"'),(1568,2,2,14,'Tetapi jawabnya: \"Siapakah yang mengangkat engkau menjadi pemimpin dan hakim atas kami? Apakah engkau bermaksud membunuh aku, sama seperti engkau telah membunuh orang Mesir itu?\" Musa menjadi takut, sebab pikirnya: \"Tentulah perkara itu telah ketahuan.\"'),(1569,2,2,15,'Ketika Firaun mendengar tentang perkara itu, dicarinya ikhtiar untuk membunuh Musa. Tetapi Musa melarikan diri dari hadapan Firaun dan tiba di tanah Midian, lalu ia duduk-duduk di tepi sebuah sumur.'),(1570,2,2,16,'Adapun imam di Midian itu mempunyai tujuh anak perempuan. Mereka datang menimba air dan mengisi palungan-palungan untuk memberi minum kambing domba ayahnya.'),(1571,2,2,17,'Maka datanglah gembala-gembala yang mengusir mereka, lalu Musa bangkit menolong mereka dan memberi minum kambing domba mereka.'),(1572,2,2,18,'Ketika mereka sampai kepada Rehuel, ayah mereka, berkatalah ia: \"Mengapa selekas itu kamu pulang hari ini?\"'),(1573,2,2,19,'Jawab mereka: \"Seorang Mesir menolong kami terhadap gembala-gembala, bahkan ia menimba air banyak-banyak untuk kami dan memberi minum kambing domba.\"'),(1574,2,2,20,'Ia berkata kepada anak-anaknya: \"Di manakah ia? Mengapakah kamu tinggalkan orang itu? Panggillah dia makan.\"'),(1575,2,2,21,'Musa bersedia tinggal di rumah itu, lalu diberikan Rehuellah Zipora, anaknya, kepada Musa.'),(1576,2,2,22,'Perempuan itu melahirkan seorang anak laki-laki, maka Musa menamainya Gersom, sebab katanya: \"Aku telah menjadi seorang pendatang di negeri asing.\"'),(1577,2,2,23,'Lama sesudah itu matilah raja Mesir. Tetapi orang Israel masih mengeluh karena perbudakan, dan mereka berseru-seru, sehingga teriak mereka minta tolong karena perbudakan itu sampai kepada Allah.'),(1578,2,2,24,'Allah mendengar mereka mengerang, lalu Ia mengingat kepada perjanjian-Nya dengan Abraham, Ishak dan Yakub.'),(1579,2,2,25,'Maka Allah melihat orang Israel itu, dan Allah memperhatikan mereka.'),(1580,2,3,1,'Adapun Musa, ia biasa menggembalakan kambing domba Yitro, mertuanya, imam di Midian. Sekali, ketika ia menggiring kambing domba itu ke seberang padang gurun, sampailah ia ke gunung Allah, yakni gunung Horeb.'),(1581,2,3,2,'Lalu Malaikat TUHAN menampakkan diri kepadanya di dalam nyala api yang keluar dari semak duri. Lalu ia melihat, dan tampaklah: semak duri itu menyala, tetapi tidak dimakan api.'),(1582,2,3,3,'Musa berkata: \"Baiklah aku menyimpang ke sana untuk memeriksa penglihatan yang hebat itu. Mengapakah tidak terbakar semak duri itu?\"'),(1583,2,3,4,'Ketika dilihat TUHAN, bahwa Musa menyimpang untuk memeriksanya, berserulah Allah dari tengah-tengah semak duri itu kepadanya: \"Musa, Musa!\" dan ia menjawab: \"Ya, Allah.\"'),(1584,2,3,5,'Lalu Ia berfirman: \"Janganlah datang dekat-dekat: tanggalkanlah kasutmu dari kakimu, sebab tempat, di mana engkau berdiri itu, adalah tanah yang kudus.\"'),(1585,2,3,6,'Lagi Ia berfirman: \"Akulah Allah ayahmu, Allah Abraham, Allah Ishak dan Allah Yakub.\" Lalu Musa menutupi mukanya, sebab ia takut memandang Allah.'),(1586,2,3,7,'Dan TUHAN berfirman: \"Aku telah memperhatikan dengan sungguh kesengsaraan umat-Ku di tanah Mesir, dan Aku telah mendengar seruan mereka yang disebabkan oleh pengerah-pengerah mereka, ya, Aku mengetahui penderitaan mereka.'),(1587,2,3,8,'Sebab itu Aku telah turun untuk melepaskan mereka dari tangan orang Mesir dan menuntun mereka keluar dari negeri itu ke suatu negeri yang baik dan luas, suatu negeri yang berlimpah-limpah susu dan madunya, ke tempat orang Kanaan, orang Het, orang Amori, orang Feris, orang Hewi dan orang Yebus.'),(1588,2,3,9,'Sekarang seruan orang Israel telah sampai kepada-Ku; juga telah Kulihat, betapa kerasnya orang Mesir menindas mereka.'),(1589,2,3,10,'Jadi sekarang, pergilah, Aku mengutus engkau kepada Firaun untuk membawa umat-Ku, orang Israel, keluar dari Mesir.\"'),(1590,2,3,11,'Tetapi Musa berkata kepada Allah: \"Siapakah aku ini, maka aku yang akan menghadap Firaun dan membawa orang Israel keluar dari Mesir?\"'),(1591,2,3,12,'Lalu firman-Nya: \"Bukankah Aku akan menyertai engkau? Inilah tanda bagimu, bahwa Aku yang mengutus engkau: apabila engkau telah membawa bangsa itu keluar dari Mesir, maka kamu akan beribadah kepada Allah di gunung ini.\"'),(1592,2,3,13,'Lalu Musa berkata kepada Allah: \"Tetapi apabila aku mendapatkan orang Israel dan berkata kepada mereka: Allah nenek moyangmu telah mengutus aku kepadamu, dan mereka bertanya kepadaku: bagaimana tentang nama-Nya? --apakah yang harus kujawab kepada mereka?\"'),(1593,2,3,14,'Firman Allah kepada Musa: \"AKU ADALAH AKU.\" Lagi firman-Nya: \"Beginilah kaukatakan kepada orang Israel itu: AKULAH AKU telah mengutus aku kepadamu.\"'),(1594,2,3,15,'Selanjutnya berfirmanlah Allah kepada Musa: \"Beginilah kaukatakan kepada orang Israel: TUHAN, Allah nenek moyangmu, Allah Abraham, Allah Ishak dan Allah Yakub, telah mengutus aku kepadamu: itulah nama-Ku untuk selama-lamanya dan itulah sebutan-Ku turun-temurun.'),(1595,2,3,16,'Pergilah, kumpulkanlah para tua-tua Israel dan katakanlah kepada mereka: TUHAN, Allah nenek moyangmu, Allah Abraham, Ishak dan Yakub, telah menampakkan diri kepadaku, serta berfirman: Aku sudah mengindahkan kamu, juga apa yang dilakukan kepadamu di Mesir.'),(1596,2,3,17,'Jadi Aku telah berfirman: Aku akan menuntun kamu keluar dari kesengsaraan di Mesir menuju ke negeri orang Kanaan, orang Het, orang Amori, orang Feris, orang Hewi dan orang Yebus, ke suatu negeri yang berlimpah-limpah susu dan madunya.'),(1597,2,3,18,'Dan bilamana mereka mendengarkan perkataanmu, maka engkau harus beserta para tua-tua Israel pergi kepada raja Mesir, dan kamu harus berkata kepadanya: TUHAN, Allah orang Ibrani, telah menemui kami; oleh sebab itu, izinkanlah kiranya kami pergi ke padang gurun tiga hari perjalanan jauhnya untuk mempersembahkan korban kepada TUHAN, Allah kami.'),(1598,2,3,19,'Tetapi Aku tahu, bahwa raja Mesir tidak akan membiarkan kamu pergi, kecuali dipaksa oleh tangan yang kuat.'),(1599,2,3,20,'Tetapi Aku akan mengacungkan tangan-Ku dan memukul Mesir dengan segala perbuatan yang ajaib, yang akan Kulakukan di tengah-tengahnya; sesudah itu ia akan membiarkan kamu pergi.'),(1600,2,3,21,'Dan Aku akan membuat orang Mesir bermurah hati terhadap bangsa ini, sehingga, apabila kamu pergi, kamu tidak pergi dengan tangan hampa,'),(1601,2,3,22,'tetapi tiap-tiap perempuan harus meminta dari tetangganya dan dari perempuan yang tinggal di rumahnya, barang-barang perak dan emas dan kain-kain, yang akan kamu kenakan kepada anak-anakmu lelaki dan perempuan; demikianlah kamu akan merampasi orang Mesir itu.\"'),(1602,2,4,1,'Lalu sahut Musa: \"Bagaimana jika mereka tidak percaya kepadaku dan tidak mendengarkan perkataanku, melainkan berkata: TUHAN tidak menampakkan diri kepadamu?\"'),(1603,2,4,2,'TUHAN berfirman kepadanya: \"Apakah yang di tanganmu itu?\" Jawab Musa: \"Tongkat.\"'),(1604,2,4,3,'Firman TUHAN: \"Lemparkanlah itu ke tanah.\" Dan ketika dilemparkannya ke tanah, maka tongkat itu menjadi ular, sehingga Musa lari meninggalkannya.'),(1605,2,4,4,'Tetapi firman TUHAN kepada Musa: \"Ulurkanlah tanganmu dan peganglah ekornya\" --Musa mengulurkan tangannya, ditangkapnya ular itu, lalu menjadi tongkat di tangannya'),(1606,2,4,5,'--\"supaya mereka percaya, bahwa TUHAN, Allah nenek moyang mereka, Allah Abraham, Allah Ishak dan Allah Yakub telah menampakkan diri kepadamu.\"'),(1607,2,4,6,'Lagi firman TUHAN kepadanya: \"Masukkanlah tanganmu ke dalam bajumu.\" Dimasukkannya tangannya ke dalam bajunya, dan setelah ditariknya ke luar, maka tangannya kena kusta, putih seperti salju.'),(1608,2,4,7,'Sesudah itu firman-Nya: \"Masukkanlah tanganmu kembali ke dalam bajumu.\" Musa memasukkan tangannya kembali ke dalam bajunya dan setelah ditariknya ke luar, maka tangan itu pulih kembali seperti seluruh badannya.'),(1609,2,4,8,'Jika mereka tidak percaya kepadamu dan tidak mengindahkan tanda mujizat yang pertama, maka mereka akan percaya kepada tanda mujizat yang kedua.'),(1610,2,4,9,'Dan jika mereka tidak juga percaya kepada kedua tanda mujizat ini dan tidak mendengarkan perkataanmu, maka engkau harus mengambil air dari sungai Nil dan harus kaucurahkan di tanah yang kering, lalu air yang kauambil itu akan menjadi darah di tanah yang kering itu.\"'),(1611,2,4,10,'Lalu kata Musa kepada TUHAN: \"Ah, Tuhan, aku ini tidak pandai bicara, dahulupun tidak dan sejak Engkau berfirman kepada hamba-Mupun tidak, sebab aku berat mulut dan berat lidah.\"'),(1612,2,4,11,'Tetapi TUHAN berfirman kepadanya: \"Siapakah yang membuat lidah manusia, siapakah yang membuat orang bisu atau tuli, membuat orang melihat atau buta; bukankah Aku, yakni TUHAN?'),(1613,2,4,12,'Oleh sebab itu, pergilah, Aku akan menyertai lidahmu dan mengajar engkau, apa yang harus kaukatakan.\"'),(1614,2,4,13,'Tetapi Musa berkata: \"Ah, Tuhan, utuslah kiranya siapa saja yang patut Kauutus.\"'),(1615,2,4,14,'Maka bangkitlah murka TUHAN terhadap Musa dan Ia berfirman: \"Bukankah di situ Harun, orang Lewi itu, kakakmu? Aku tahu, bahwa ia pandai bicara; lagipula ia telah berangkat menjumpai engkau, dan apabila ia melihat engkau, ia akan bersukacita dalam hatinya.'),(1616,2,4,15,'Maka engkau harus berbicara kepadanya dan menaruh perkataan itu ke dalam mulutnya; Aku akan menyertai lidahmu dan lidahnya dan mengajarkan kepada kamu apa yang harus kamu lakukan.'),(1617,2,4,16,'Ia harus berbicara bagimu kepada bangsa itu, dengan demikian ia akan menjadi penyambung lidahmu dan engkau akan menjadi seperti Allah baginya.'),(1618,2,4,17,'Dan bawalah tongkat ini di tanganmu, yang harus kaupakai untuk membuat tanda-tanda mujizat.\"'),(1619,2,4,18,'Lalu Musa kembali kepada mertuanya Yitro serta berkata kepadanya: \"Izinkanlah kiranya aku kembali kepada saudara-saudaraku, yang ada di Mesir, untuk melihat apakah mereka masih hidup.\" Yitro berkata kepada Musa: \"Pergilah dengan selamat.\"'),(1620,2,4,19,'Adapun TUHAN sudah berfirman kepada Musa di Midian: \"Kembalilah ke Mesir, sebab semua orang yang ingin mencabut nyawamu telah mati.\"'),(1621,2,4,20,'Kemudian Musa mengajak isteri dan anak-anaknya lelaki, lalu menaikkan mereka ke atas keledai dan ia kembali ke tanah Mesir; dan tongkat Allah itu dipegangnya di tangannya.'),(1622,2,4,21,'Firman TUHAN kepada Musa: \"Pada waktu engkau hendak kembali ini ke Mesir, ingatlah, supaya segala mujizat yang telah Kuserahkan ke dalam tanganmu, kauperbuat di depan Firaun. Tetapi Aku akan mengeraskan hatinya, sehingga ia tidak membiarkan bangsa itu pergi.'),(1623,2,4,22,'Maka engkau harus berkata kepada Firaun: Beginilah firman TUHAN: Israel ialah anak-Ku, anak-Ku yang sulung;'),(1624,2,4,23,'sebab itu Aku berfirman kepadamu: Biarkanlah anak-Ku itu pergi, supaya ia beribadah kepada-Ku; tetapi jika engkau menolak membiarkannya pergi, maka Aku akan membunuh anakmu, anakmu yang sulung.\"'),(1625,2,4,24,'Tetapi di tengah jalan, di suatu tempat bermalam, TUHAN bertemu dengan Musa dan berikhtiar untuk membunuhnya.'),(1626,2,4,25,'Lalu Zipora mengambil pisau batu, dipotongnya kulit khatan anaknya, kemudian disentuhnya dengan kulit itu kaki Musa sambil berkata: \"Sesungguhnya engkau pengantin darah bagiku.\"'),(1627,2,4,26,'Lalu TUHAN membiarkan Musa. \"Pengantin darah,\" kata Zipora waktu itu, karena mengingat sunat itu.'),(1628,2,4,27,'Berfirmanlah TUHAN kepada Harun: \"Pergilah ke padang gurun menjumpai Musa.\" Ia pergi dan bertemu dengan dia di gunung Allah, lalu menciumnya.'),(1629,2,4,28,'Kemudian Musa memberitahukan kepada Harun segala firman TUHAN yang disuruhkan-Nya kepadanya untuk disampaikan dan segala tanda mujizat yang diperintahkan-Nya kepadanya untuk dibuat.'),(1630,2,4,29,'Lalu pergilah Musa beserta Harun dan mereka mengumpulkan semua tua-tua Israel.'),(1631,2,4,30,'Harun mengucapkan segala firman yang telah diucapkan TUHAN kepada Musa, serta membuat di depan bangsa itu tanda-tanda mujizat itu.'),(1632,2,4,31,'Lalu percayalah bangsa itu, dan ketika mereka mendengar, bahwa TUHAN telah mengindahkan orang Israel dan telah melihat kesengsaraan mereka, maka berlututlah mereka dan sujud menyembah.'),(1633,2,5,1,'Kemudian Musa dan Harun pergi menghadap Firaun, lalu berkata kepadanya: \"Beginilah firman TUHAN, Allah Israel: Biarkanlah umat-Ku pergi untuk mengadakan perayaan bagi-Ku di padang gurun.\"'),(1634,2,5,2,'Tetapi Firaun berkata: \"Siapakah TUHAN itu yang harus kudengarkan firman-Nya untuk membiarkan orang Israel pergi? Tidak kenal aku TUHAN itu dan tidak juga aku akan membiarkan orang Israel pergi.\"'),(1635,2,5,3,'Lalu kata mereka: \"Allah orang Ibrani telah menemui kami; izinkanlah kiranya kami pergi ke padang gurun tiga hari perjalanan jauhnya, untuk mempersembahkan korban kepada TUHAN, Allah kami, supaya jangan nanti mendatangkan kepada kami penyakit sampar atau pedang.\"'),(1636,2,5,4,'Tetapi raja Mesir berkata kepada mereka: \"Musa dan Harun, mengapakah kamu bawa-bawa bangsa ini melalaikan pekerjaannya? Pergilah melakukan pekerjaanmu!\"'),(1637,2,5,5,'Lagi kata Firaun: \"Lihat, sekarang telah terlalu banyak bangsamu di negeri ini, masakan kamu hendak menghentikan mereka dari kerja paksanya!\"'),(1638,2,5,6,'Pada hari itu juga Firaun memerintahkan kepada pengerah-pengerah bangsa itu dan kepada mandur-mandur mereka sendiri:'),(1639,2,5,7,'Tidak boleh lagi kamu memberikan jerami kepada bangsa itu untuk membuat batu bata, seperti sampai sekarang; biarlah mereka sendiri yang pergi mengumpulkan jerami,'),(1640,2,5,8,'tetapi jumlah batu bata, yang harus dibuat mereka sampai sekarang, bebankanlah itu juga kepada mereka dan jangan menguranginya, karena mereka pemalas. Itulah sebabnya mereka berteriak-teriak: Izinkanlah kami pergi mempersembahkan korban kepada Allah kami.'),(1641,2,5,9,'Pekerjaan orang-orang ini harus diperberat, sehingga mereka terikat kepada pekerjaannya dan jangan mempedulikan perkataan dusta.\"'),(1642,2,5,10,'Maka para pengerah bangsa itu dan para mandurnya pergi dan berkata kepada mereka: \"Beginilah kata Firaun: Aku tidak memberi jerami lagi kepadamu.'),(1643,2,5,11,'Pergilah kamu sendiri mengambil jerami, di mana saja kamu mendapatnya, tetapi pekerjaanmu sedikitpun tidak boleh kurang.\"'),(1644,2,5,12,'Lalu berseraklah bangsa itu ke seluruh tanah Mesir untuk mengumpulkan tunggul gandum sebagai pengganti jerami.'),(1645,2,5,13,'Dan pengerah-pengerah itu mendesak mereka dengan berkata: \"Selesaikan pekerjaanmu, yaitu tugas sehari, seperti pada waktu ada jerami.\"'),(1646,2,5,14,'Lalu pengerah-pengerah Firaun memukul mandur-mandur Israel, yang mereka angkat, sambil bertanya: \"Mengapakah kamu pada hari ini tidak menyelesaikan jumlah batu bata yang harus kamu buat seperti kemarin?\"'),(1647,2,5,15,'Sesudah itu pergilah para mandur Israel kepada Firaun dan mengadukan halnya kepadanya: \"Mengapakah tuanku berlaku seperti itu terhadap hamba-hambamu ini?'),(1648,2,5,16,'Jerami tidak diberikan lagi kepada hamba-hambamu ini tetapi walaupun begitu, kami diperintahkan: Buatlah batu bata. Dan dalam pada itu hamba-hambamu ini dipukuli, padahal rakyat tuankulah yang bersalah.\"'),(1649,2,5,17,'Tetapi ia berkata: \"Pemalas kamu, pemalas! Itulah sebabnya kamu berkata: Izinkanlah kami pergi mempersembahkan korban kepada TUHAN!'),(1650,2,5,18,'Jadi sekarang, pergilah, bekerja! Jerami tidak akan diberikan lagi kepadamu, tetapi jumlah batu bata yang sama harus kamu serahkan.\"'),(1651,2,5,19,'Maka mengertilah para mandur Israel, bahwa mereka ada dalam keadaan susah, karena dikatakan kepada mereka: \"Kamu tidak boleh mengurangi jumlah batu bata pada tiap-tiap hari.\"'),(1652,2,5,20,'Waktu mereka meninggalkan Firaun berjumpalah mereka dengan Musa dan Harun, yang sedang menantikan mereka,'),(1653,2,5,21,'lalu mereka berkata kepada keduanya: \"Kiranya TUHAN memperhatikan perbuatanmu dan menghukumkan kamu, karena kamu telah membusukkan nama kami kepada Firaun dan hamba-hambanya dan dengan demikian kamu telah memberikan pisau kepada mereka untuk membunuh kami.\"'),(1654,2,5,22,'Lalu Musa kembali menghadap TUHAN, katanya: \"Tuhan, mengapakah Kauperlakukan umat ini begitu bengis? Mengapa pula aku yang Kauutus?'),(1655,2,5,23,'Sebab sejak aku pergi menghadap Firaun untuk berbicara atas nama-Mu, dengan jahat diperlakukannya umat ini, dan Engkau tidak melepaskan umat-Mu sama sekali.\"'),(1656,2,6,1,'(5-24) Tetapi TUHAN berfirman kepada Musa: \"Sekarang engkau akan melihat, apa yang akan Kulakukan kepada Firaun; sebab dipaksa oleh tangan yang kuat ia akan membiarkan mereka pergi, ya dipaksa oleh tangan yang kuat ia akan mengusir mereka dari negerinya.\"'),(1657,2,6,2,'(6-1) Selanjutnya berfirmanlah Allah kepada Musa: \"Akulah TUHAN.'),(1658,2,6,3,'(6-2) Aku telah menampakkan diri kepada Abraham, Ishak dan Yakub sebagai Allah Yang Mahakuasa, tetapi dengan nama-Ku TUHAN Aku belum menyatakan diri.'),(1659,2,6,4,'(6-3) Bukan saja Aku telah mengadakan perjanjian-Ku dengan mereka untuk memberikan kepada mereka tanah Kanaan, tempat mereka tinggal sebagai orang asing,'),(1660,2,6,5,'(6-4) tetapi Aku sudah mendengar juga erang orang Israel yang telah diperbudak oleh orang Mesir, dan Aku ingat kepada perjanjian-Ku.'),(1661,2,6,6,'(6-5) Sebab itu katakanlah kepada orang Israel: Akulah TUHAN, Aku akan membebaskan kamu dari kerja paksa orang Mesir, melepaskan kamu dari perbudakan mereka dan menebus kamu dengan tangan yang teracung dan dengan hukuman-hukuman yang berat.'),(1662,2,6,7,'(6-6) Aku akan mengangkat kamu menjadi umat-Ku dan Aku akan menjadi Allahmu, supaya kamu mengetahui, bahwa Akulah, TUHAN, Allahmu, yang membebaskan kamu dari kerja paksa orang Mesir.'),(1663,2,6,8,'(6-7) Dan Aku akan membawa kamu ke negeri yang dengan sumpah telah Kujanjikan memberikannya kepada Abraham, Ishak dan Yakub, dan Aku akan memberikannya kepadamu untuk menjadi milikmu; Akulah TUHAN.\"'),(1664,2,6,9,'(6-8) Lalu Musa mengatakan demikian kepada orang Israel, tetapi mereka tidak mendengarkan Musa karena mereka putus asa dan karena perbudakan yang berat itu.'),(1665,2,6,10,'(6-9) Kemudian TUHAN berfirman kepada Musa:'),(1666,2,6,11,'(6-10) \"Pergilah menghadap, katakanlah kepada Firaun, raja Mesir, bahwa ia harus membiarkan orang Israel pergi dari negerinya.\"'),(1667,2,6,12,'(6-11) Tetapi Musa berkata di hadapan TUHAN: \"Orang Israel sendiri tidak mendengarkan aku, bagaimanakah mungkin Firaun akan mendengarkan aku, aku seorang yang tidak petah lidahnya!\"'),(1668,2,6,13,'(6-12) Demikianlah TUHAN telah berfirman kepada Musa dan Harun, serta mengutus mereka kepada orang Israel dan kepada Firaun, raja Mesir, dengan membawa perintah supaya orang Israel dibawa keluar dari Mesir.'),(1669,2,6,14,'(6-13) Inilah para kepala kaum keluarga mereka: Anak-anak Ruben anak sulung Israel: Henokh, Palu, Hezron dan Karmi; itulah kaum-kaum Ruben.'),(1670,2,6,15,'(6-14) Anak-anak Simeon: Yemuel, Yamin, Ohad, Yakhin, Zohar, dan Saul, anak seorang perempuan Kanaan; itulah kaum-kaum Simeon.'),(1671,2,6,16,'(6-15) Inilah nama anak-anak Lewi menurut urutan kelahirannya: Gerson, Kehat dan Merari. Umur Lewi seratus tiga puluh tujuh tahun.'),(1672,2,6,17,'(6-16) Anak-anak Gerson: Libni dan Simei, menurut kaum mereka.'),(1673,2,6,18,'(6-17) Anak-anak Kehat: Amram, Yizhar, Hebron dan Uziel. Umur Kehat seratus tiga puluh tiga tahun.'),(1674,2,6,19,'(6-18) Anak-anak Merari: Mahli dan Musi. Itulah kaum-kaum Lewi menurut urutannya.'),(1675,2,6,20,'(6-19) Dan Amram mengambil Yokhebed, saudara ayahnya, menjadi isterinya, dan perempuan ini melahirkan Harun dan Musa baginya. Umur Amram seratus tiga puluh tujuh tahun.'),(1676,2,6,21,'(6-20) Anak-anak Yizhar: Korah, Nefeg dan Zikhri.'),(1677,2,6,22,'(6-21) Anak-anak Uziel: Misael, Elsafan dan Sitri.'),(1678,2,6,23,'(6-22) Dan Harun mengambil Eliseba, anak perempuan Aminadab, saudara perempuan Nahason, menjadi isterinya, dan perempuan ini melahirkan baginya Nadab, Abihu, Eleazar dan Itamar.'),(1679,2,6,24,'(6-23) Anak-anak Korah: Asir, Elkana dan Abiasaf; itulah kaum-kaum orang Korah.'),(1680,2,6,25,'(6-24) Eleazar, anak Harun, mengambil salah seorang anak perempuan Putiel menjadi isterinya dan perempuan ini melahirkan Pinehas baginya. Itulah para kepala kaum keluarga orang Lewi menurut kaum mereka.'),(1681,2,6,26,'(6-25) Itulah Harun dan Musa, yang diperintahkan TUHAN: \"Bawalah orang Israel keluar dari tanah Mesir menurut pasukan mereka.\"'),(1682,2,6,27,'(6-26) Merekalah yang berbicara kepada Firaun, raja Mesir, supaya mereka membawa orang Israel keluar dari Mesir. Itulah Musa dan Harun.'),(1683,2,6,28,'(6-27) Pada waktu TUHAN berfirman kepada Musa di tanah Mesir,'),(1684,2,6,29,'(6-28) TUHAN berfirman kepadanya: \"Akulah TUHAN; katakanlah kepada Firaun, raja Mesir, segala yang Kufirmankan kepadamu.\"'),(1685,2,6,30,'(6-29) Tetapi Musa berkata di hadapan TUHAN: \"Bukankah aku ini seorang yang tidak petah lidahnya, bagaimanakah mungkin Firaun akan mendengarkan aku?\"'),(1686,2,7,1,'Berfirmanlah TUHAN kepada Musa: \"Lihat, Aku mengangkat engkau sebagai Allah bagi Firaun, dan Harun, abangmu, akan menjadi nabimu.'),(1687,2,7,2,'Engkau harus mengatakan segala yang Kuperintahkan kepadamu, dan Harun, abangmu, harus berbicara kepada Firaun, supaya dibiarkannya orang Israel itu pergi dari negerinya.'),(1688,2,7,3,'Tetapi Aku akan mengeraskan hati Firaun, dan Aku akan memperbanyak tanda-tanda dan mujizat-mujizat yang Kubuat di tanah Mesir.'),(1689,2,7,4,'Bilamana Firaun tidak mendengarkan kamu, maka Aku akan mendatangkan tangan-Ku kepada Mesir dan mengeluarkan pasukan-Ku, umat-Ku, orang Israel, dari tanah Mesir dengan hukuman-hukuman yang berat.'),(1690,2,7,5,'Dan orang Mesir itu akan mengetahui, bahwa Akulah TUHAN, apabila Aku mengacungkan tangan-Ku terhadap Mesir dan membawa orang Israel keluar dari tengah-tengah mereka.\"'),(1691,2,7,6,'Demikianlah diperbuat Musa dan Harun; seperti yang diperintahkan TUHAN kepada mereka, demikianlah diperbuat mereka.'),(1692,2,7,7,'Adapun Musa delapan puluh tahun umurnya dan Harun delapan puluh tiga tahun, ketika mereka berbicara kepada Firaun.'),(1693,2,7,8,'Dan TUHAN berfirman kepada Musa dan Harun:'),(1694,2,7,9,'Apabila Firaun berkata kepada kamu: Tunjukkanlah suatu mujizat, maka haruslah kaukatakan kepada Harun: Ambillah tongkatmu dan lemparkanlah itu di depan Firaun. Maka tongkat itu akan menjadi ular.'),(1695,2,7,10,'Musa dan Harun pergi menghadap Firaun, lalu mereka berbuat seperti yang diperintahkan TUHAN; Harun melemparkan tongkatnya di depan Firaun dan para pegawainya, maka tongkat itu menjadi ular.'),(1696,2,7,11,'Kemudian Firaunpun memanggil orang-orang berilmu dan ahli-ahli sihir; dan merekapun, ahli-ahli Mesir itu, membuat yang demikian juga dengan ilmu mantera mereka.'),(1697,2,7,12,'Masing-masing mereka melemparkan tongkatnya, dan tongkat-tongkat itu menjadi ular; tetapi tongkat Harun menelan tongkat-tongkat mereka.'),(1698,2,7,13,'Tetapi hati Firaun berkeras, sehingga tidak mau mendengarkan mereka keduanya--seperti yang telah difirmankan TUHAN.'),(1699,2,7,14,'Berfirmanlah TUHAN kepada Musa: \"Firaun berkeras hati, ia menolak membiarkan bangsa itu pergi.'),(1700,2,7,15,'Pergilah kepada Firaun pada waktu pagi, pada waktu biasanya ia keluar ke sungai; nantikanlah dia di tepi sungai Nil dengan memegang di tanganmu tongkat yang tadinya berubah menjadi ular.'),(1701,2,7,16,'Dan katakanlah kepadanya: TUHAN, Allah orang Ibrani, telah mengutus aku kepadamu untuk mengatakan: Biarkanlah umat-Ku pergi, supaya mereka beribadah kepada-Ku di padang gurun; meskipun begitu sampai sekarang engkau tidak mau mendengarkan.'),(1702,2,7,17,'Sebab itu beginilah firman TUHAN: Dari hal yang berikut akan kauketahui, bahwa Akulah TUHAN. Lihat, dengan tongkat yang di tanganku ini akan kupukul air yang di sungai Nil dan air itu akan berubah menjadi darah,'),(1703,2,7,18,'dan ikan yang dalam sungai Nil akan mati, sehingga sungai Nil akan berbau busuk; maka orang Mesir akan segan meminum air dari sungai Nil ini.\"'),(1704,2,7,19,'TUHAN berfirman kepada Musa: \"Katakanlah kepada Harun: Ambillah tongkatmu, ulurkanlah tanganmu ke atas segala air orang Mesir, ke atas sungai, selokan, kolam dan ke atas segala kumpulan air yang ada pada mereka, supaya semuanya menjadi darah, dan akan ada darah di seluruh tanah Mesir, bahkan dalam wadah kayu dan wadah batu.\"'),(1705,2,7,20,'Demikianlah Musa dan Harun berbuat seperti yang difirmankan TUHAN; diangkatnya tongkat itu dan dipukulkannya kepada air yang di sungai Nil, di depan mata Firaun dan pegawai-pegawainya, maka seluruh air yang di sungai Nil berubah menjadi darah;'),(1706,2,7,21,'matilah ikan di sungai Nil, sehingga sungai Nil itu berbau busuk dan orang Mesir tidak dapat meminum air dari sungai Nil; dan di seluruh tanah Mesir ada darah.'),(1707,2,7,22,'Tetapi para ahli Mesir membuat yang demikian juga dengan ilmu-ilmu mantera mereka, sehingga hati Firaun berkeras dan ia tidak mau mendengarkan mereka keduanya seperti yang telah difirmankan TUHAN.'),(1708,2,7,23,'Firaun berpaling, lalu masuk ke istananya dan tidak mau memperhatikan hal itu juga.'),(1709,2,7,24,'Tetapi semua orang Mesir menggali-gali di sekitar sungai Nil mencari air untuk diminum, sebab mereka tidak dapat meminum air sungai Nil.'),(1710,2,7,25,'Demikianlah genap tujuh hari berlalu setelah TUHAN menulahi sungai Nil.'),(1711,2,8,1,'Berfirmanlah TUHAN kepada Musa: \"Pergilah menghadap Firaun dan katakan kepadanya: Beginilah firman TUHAN: Biarkanlah umat-Ku pergi, supaya mereka beribadah kepada-Ku;'),(1712,2,8,2,'jika engkau menolak membiarkannya pergi, maka Aku akan menulahi seluruh daerahmu dengan katak.'),(1713,2,8,3,'Katak-katak akan mengeriap dalam sungai Nil, lalu naik dan masuk ke dalam istanamu dan kamar tidurmu, ya sampai ke dalam tempat tidurmu, ke dalam rumah pegawai-pegawaimu, dan rakyatmu, bahkan ke dalam pembakaran rotimu serta ke dalam tempat adonanmu.'),(1714,2,8,4,'Katak-katak itu akan naik memanjati engkau, memanjati rakyatmu dan segala pegawaimu.\"'),(1715,2,8,5,'Berfirmanlah TUHAN kepada Musa: \"Katakanlah kepada Harun: Ulurkanlah tanganmu dengan tongkatmu ke atas sungai, ke atas selokan dan ke atas kolam, dan buatlah katak-katak bermunculan meliputi tanah Mesir.\"'),(1716,2,8,6,'Lalu Harun mengulurkan tangannya ke atas segala air di Mesir, maka bermunculanlah katak-katak, lalu menutupi tanah Mesir.'),(1717,2,8,7,'Tetapi para ahli itupun membuat yang demikian juga dengan ilmu-ilmu mantera mereka, sehingga mereka membuat katak-katak bermunculan meliputi tanah Mesir.'),(1718,2,8,8,'Kemudian Firaun memanggil Musa dan Harun serta berkata: \"Berdoalah kepada TUHAN, supaya dijauhkan-Nya katak-katak itu dari padaku dan dari pada rakyatku; maka aku akan membiarkan bangsa itu pergi, supaya mereka mempersembahkan korban kepada TUHAN.\"'),(1719,2,8,9,'Kata Musa kepada Firaun: \"Silakanlah tuanku katakan kepadaku, bila aku akan berdoa untukmu, untuk pegawaimu dan rakyatmu, supaya katak-katak itu dilenyapkan dari padamu dan dari rumah-rumahmu, dan hanya tinggal di sungai Nil saja.\"'),(1720,2,8,10,'Katanya: \"Besok.\" Lalu kata Musa: \"Jadilah seperti katamu itu, supaya tuanku mengetahui, bahwa tidak ada yang seperti TUHAN, Allah kami.'),(1721,2,8,11,'Maka katak-katak itu akan dijauhkan dari padamu, dari rumah-rumahmu, dari pegawai-pegawaimu dan dari rakyatmu; dan hanya akan tinggal di sungai Nil saja.\"'),(1722,2,8,12,'Lalu Musa dan Harun keluar meninggalkan Firaun, dan Musa berseru kepada TUHAN karena katak-katak, yang didatangkan-Nya kepada Firaun.'),(1723,2,8,13,'Dan TUHAN melakukan seperti yang dikatakan Musa, sehingga katak-katak itu mati lenyap dari rumah, dari halaman dan dari ladang.'),(1724,2,8,14,'Dikumpulkan oranglah bangkai-bangkainya bertumpuk-tumpuk, sehingga tanah itu berbau busuk.'),(1725,2,8,15,'Tetapi ketika Firaun melihat, bahwa telah terasa kelegaan, ia tetap berkeras hati, dan tidak mau mendengarkan mereka keduanya--seperti yang telah difirmankan TUHAN.'),(1726,2,8,16,'Berfirmanlah TUHAN kepada Musa: \"Katakanlah kepada Harun: Ulurkanlah tongkatmu dan pukulkanlah itu ke debu tanah, maka debu itu akan menjadi nyamuk di seluruh tanah Mesir.\"'),(1727,2,8,17,'Lalu mereka berbuat demikian; Harun mengulurkan tangannya dengan tongkatnya dan memukulkannya ke debu tanah, maka nyamuk-nyamuk itu hinggap pada manusia dan pada binatang. Segala debu tanah menjadi nyamuk di seluruh tanah Mesir.'),(1728,2,8,18,'Para ahli itupun membuat yang demikian juga dengan ilmu-ilmu manteranya untuk mengadakan nyamuk-nyamuk, tetapi mereka tidak dapat. Demikianlah nyamuk-nyamuk itu hinggap pada manusia dan pada binatang.'),(1729,2,8,19,'Lalu berkatalah para ahli itu kepada Firaun: \"Inilah tangan Allah.\" Tetapi hati Firaun berkeras, dan ia tidak mau mendengarkan mereka--seperti yang telah difirmankan TUHAN.'),(1730,2,8,20,'Berfirmanlah TUHAN kepada Musa: \"Bangunlah pagi-pagi dan berdirilah menantikan Firaun, pada waktu biasanya ia keluar ke sungai, dan katakanlah kepadanya: Beginilah firman TUHAN: Biarkanlah umat-Ku pergi, supaya mereka beribadah kepada-Ku;'),(1731,2,8,21,'sebab jika engkau tidak membiarkan umat-Ku itu pergi, maka Aku akan melepaskan pikat terhadap engkau, terhadap pegawai-pegawaimu, rakyatmu dan rumah-rumahmu, sehingga rumah-rumah orang Mesir, bahkan tanah, di mana mereka berdiri akan penuh dengan pikat.'),(1732,2,8,22,'Tetapi pada hari itu Aku akan mengecualikan tanah Gosyen, di mana umat-Ku tinggal, sehingga di sana tidak ada terdapat pikat, supaya engkau mengetahui, bahwa Aku, TUHAN, ada di negeri ini.'),(1733,2,8,23,'Sebab Aku akan mengadakan perbedaan antara umat-Ku dan bangsamu. Besok tanda mujizat ini akan terjadi.\"'),(1734,2,8,24,'TUHAN berbuat demikian; maka datanglah banyak-banyak pikat ke dalam istana Firaun dan ke dalam rumah pegawai-pegawainya dan ke seluruh tanah Mesir; negeri itu menderita karena pikat itu.'),(1735,2,8,25,'Lalu Firaun memanggil Musa dan Harun serta berkata: \"Pergilah, persembahkanlah korban kepada Allahmu di negeri ini.\"'),(1736,2,8,26,'Tetapi Musa berkata: \"Tidak mungkin kami berbuat demikian, sebab korban yang akan kami persembahkan kepada TUHAN, Allah kami, adalah kekejian bagi orang Mesir. Apabila kami mempersembahkan korban yang menjadi kekejian bagi orang Mesir itu, di depan mata mereka, tidakkah mereka akan melempari kami dengan batu?'),(1737,2,8,27,'Kami harus pergi ke padang gurun tiga hari perjalanan jauhnya untuk mempersembahkan korban kepada TUHAN, Allah kami, seperti yang difirmankan-Nya kepada kami.\"'),(1738,2,8,28,'Lalu kata Firaun: \"Baik, aku akan membiarkan kamu pergi untuk mempersembahkan korban kepada TUHAN, Allahmu, di padang gurun; hanya janganlah kamu pergi terlalu jauh. Berdoalah untuk aku.\"'),(1739,2,8,29,'Lalu kata Musa: \"Sekarang aku keluar meninggalkan tuanku dan akan berdoa kepada TUHAN, maka pikat itu akan dijauhkan besok dari Firaun, dari pegawai-pegawainya dan rakyatnya; hanya janganlah Firaun berlaku curang lagi dengan tidak membiarkan bangsa itu pergi untuk mempersembahkan korban kepada TUHAN.\"'),(1740,2,8,30,'Sesudah itu keluarlah Musa meninggalkan Firaun, lalu berdoa kepada TUHAN.'),(1741,2,8,31,'Dan TUHAN membuat seperti yang dikatakan Musa: pikat itu dijauhkan-Nya dari Firaun, dari pegawai-pegawainya dan rakyatnya; seekorpun tidak ada yang tinggal.'),(1742,2,8,32,'Tetapi sekali inipun Firaun tetap berkeras hati; ia tidak membiarkan bangsa itu pergi.'),(1743,2,9,1,'Berfirmanlah TUHAN kepada Musa: \"Pergilah menghadap Firaun dan berbicaralah kepadanya: Beginilah firman TUHAN, Allah orang Ibrani: Biarkanlah umat-Ku pergi, supaya mereka beribadah kepada-Ku.'),(1744,2,9,2,'Sebab jika engkau menolak membiarkan mereka pergi dan masih menahan mereka,'),(1745,2,9,3,'maka ternakmu, yang ada di padang, kuda, keledai, unta, lembu sapi dan kambing domba, akan kena tulah TUHAN, yakni kena penyakit sampar yang dahsyat.'),(1746,2,9,4,'Dan TUHAN akan membuat perbedaan antara ternak orang Israel dan ternak orang Mesir, sehingga tidak ada yang akan mati seekorpun dari segala ternak orang Israel.\"'),(1747,2,9,5,'Selanjutnya TUHAN menentukan waktunya, firman-Nya: \"Besoklah TUHAN akan melakukan hal itu di negeri ini.\"'),(1748,2,9,6,'Dan TUHAN melakukan hal itu keesokan harinya; segala ternak orang Mesir itu mati, tetapi dari ternak orang Israel tidak ada seekorpun yang mati.'),(1749,2,9,7,'Lalu Firaun menyuruh orang ke sana dan sesungguhnyalah dari ternak orang Israel tidak ada seekorpun yang mati. Tetapi Firaun tetap berkeras hati dan tidak mau membiarkan bangsa itu pergi.'),(1750,2,9,8,'Berfirmanlah TUHAN kepada Musa dan Harun: \"Ambillah jelaga dari dapur peleburan serangkup penuh, dan Musa harus menghamburkannya ke udara di depan mata Firaun.'),(1751,2,9,9,'Maka jelaga itu akan menjadi debu meliputi seluruh tanah Mesir, dan akan menjadikan barah yang memecah sebagai gelembung, pada manusia dan binatang di seluruh tanah Mesir.\"'),(1752,2,9,10,'Lalu mereka mengambil jelaga dari dapur peleburan, dan berdiri di depan Firaun, kemudian Musa menghamburkannya ke udara, maka terjadilah barah, yang memecah sebagai gelembung pada manusia dan binatang,'),(1753,2,9,11,'sehingga ahli-ahli itu tidak dapat tetap berdiri di depan Musa, karena barah-barah itu; sebab ahli-ahli itupun juga kena barah sama seperti semua orang Mesir.'),(1754,2,9,12,'Tetapi TUHAN mengeraskan hati Firaun, sehingga ia tidak mendengarkan mereka--seperti yang telah difirmankan TUHAN kepada Musa.'),(1755,2,9,13,'Berfirmanlah TUHAN kepada Musa: \"Bangunlah pagi-pagi dan berdirilah menantikan Firaun dan katakan kepadanya: Beginilah firman TUHAN, Allah orang Ibrani: Biarkanlah umat-Ku pergi, supaya mereka beribadah kepada-Ku.'),(1756,2,9,14,'Sebab sekali ini Aku akan melepaskan segala tulah-Ku terhadap engkau sendiri, terhadap pegawai-pegawaimu dan terhadap rakyatmu, dengan maksud supaya engkau mengetahui, bahwa tidak ada yang seperti Aku di seluruh bumi.'),(1757,2,9,15,'Bukankah sudah lama Aku dapat mengacungkan tangan-Ku untuk membunuh engkau dan rakyatmu dengan penyakit sampar, sehingga engkau terhapus dari atas bumi;'),(1758,2,9,16,'akan tetapi inilah sebabnya Aku membiarkan engkau hidup, yakni supaya memperlihatkan kepadamu kekuatan-Ku, dan supaya nama-Ku dimasyhurkan di seluruh bumi.'),(1759,2,9,17,'Engkau masih selalu mengalangi umat-Ku, sehingga engkau tidak membiarkan mereka pergi.'),(1760,2,9,18,'Sesungguhnya besok kira-kira waktu ini Aku akan menurunkan hujan es yang sangat dahsyat, seperti yang belum pernah terjadi di Mesir sejak Mesir dijadikan sampai sekarang ini.'),(1761,2,9,19,'Oleh sebab itu, ternakmu dan segala yang kaupunyai di padang, suruhlah dibawa ke tempat yang aman; semua orang dan segala hewan, yang ada di padang dan tidak pulang berkumpul ke rumah, akan ditimpa oleh hujan es itu, sehingga mati.\"'),(1762,2,9,20,'Maka siapa di antara para pegawai Firaun yang takut kepada firman TUHAN, menyuruh hamba-hambanya serta ternaknya lari ke rumah,'),(1763,2,9,21,'tetapi siapa yang tidak mengindahkan firman TUHAN, meninggalkan hamba-hambanya serta ternaknya di padang.'),(1764,2,9,22,'Berfirmanlah TUHAN kepada Musa: \"Ulurkanlah tanganmu ke langit, supaya hujan es turun di seluruh tanah Mesir, menimpa manusia dan binatang dan menimpa tumbuh-tumbuhan di padang di tanah Mesir.\"'),(1765,2,9,23,'Lalu Musa mengulurkan tongkatnya ke langit, maka TUHAN mengadakan guruh dan hujan es, dan apipun menyambar ke bumi, dan TUHAN menurunkan hujan es meliputi tanah Mesir.'),(1766,2,9,24,'Dan turunlah hujan es, beserta api yang berkilat-kilat di tengah-tengah hujan es itu, terlalu dahsyat, seperti yang belum pernah terjadi di seluruh negeri orang Mesir, sejak mereka menjadi suatu bangsa.'),(1767,2,9,25,'Hujan es itu menimpa binasa segala sesuatu yang ada di padang, di seluruh tanah Mesir, dari manusia sampai binatang; juga segala tumbuh-tumbuhan di padang ditimpa binasa oleh hujan itu dan segala pohon di padang ditumbangkannya.'),(1768,2,9,26,'Hanya di tanah Gosyen, tempat kediaman orang Israel, tidak ada turun hujan es.'),(1769,2,9,27,'Lalu Firaun menyuruh memanggil Musa dan Harun serta berkata kepada mereka: \"Aku telah berdosa sekali ini, TUHAN itu yang benar, tetapi aku dan rakyatkulah yang bersalah.'),(1770,2,9,28,'Berdoalah kepada TUHAN; guruh yang sangat dahsyat dan hujan es itu sudah cukup. Maka aku akan membiarkan kamu pergi, tidak usah kamu tinggal lebih lama lagi.\"'),(1771,2,9,29,'Dan berkatalah Musa kepadanya: \"Sekeluar aku dari kota ini, aku akan mengembangkan tanganku kepada TUHAN; guruh akan berhenti dan hujan es tidak akan turun lagi, supaya engkau mengetahui, bahwa bumi adalah milik TUHAN.'),(1772,2,9,30,'Tetapi tentang engkau dan para pegawaimu, aku tahu, bahwa kamu belum takut kepada TUHAN Allah.\"'),(1773,2,9,31,'--Tanaman rami dan jelai telah tertimpa binasa, sebab jelai itu sedang berbulir dan rami itu sedang berbunga.'),(1774,2,9,32,'Tetapi gandum dan sekoi tidak tertimpa binasa, sebab belum lagi musimnya. --'),(1775,2,9,33,'Lalu keluarlah Musa dari kota itu meninggalkan Firaun, dikembangkannyalah tangannya kepada TUHAN, maka berhentilah guruh dan hujan es dan hujan tidak tercurah lagi ke bumi.'),(1776,2,9,34,'Tetapi ketika Firaun melihat, bahwa hujan, hujan es dan guruh telah berhenti, maka teruslah ia berbuat dosa; ia tetap berkeras hati, baik ia maupun para pegawainya.'),(1777,2,9,35,'Berkeraslah hati Firaun, sehingga ia tidak membiarkan orang Israel pergi--seperti yang telah difirmankan TUHAN dengan perantaraan Musa.'),(1778,2,10,1,'Berfirmanlah TUHAN kepada Musa: \"Pergilah menghadap Firaun, sebab Aku telah membuat hatinya dan hati para pegawainya berkeras, supaya Aku mengadakan tanda-tanda mujizat yang Kubuat ini di antara mereka,'),(1779,2,10,2,'dan supaya engkau dapat menceriterakan kepada anak cucumu, bagaimana Aku mempermain-mainkan orang Mesir dan tanda-tanda mujizat mana yang telah Kulakukan di antara mereka, supaya kamu mengetahui, bahwa Akulah TUHAN.\"'),(1780,2,10,3,'Lalu Musa dan Harun pergi menghadap Firaun dan berkata kepadanya: \"Beginilah firman TUHAN, Allah orang Ibrani: Berapa lama lagi engkau menolak untuk merendahkan dirimu di hadapan-Ku? Biarkanlah umat-Ku pergi supaya mereka beribadah kepada-Ku.'),(1781,2,10,4,'Sebab jika engkau menolak membiarkan umat-Ku pergi, maka besok Aku akan mendatangkan belalang-belalang ke dalam daerahmu;'),(1782,2,10,5,'belalang itu akan menutupi permukaan bumi, sehingga orang tidak dapat melihat tanah; belalang itu akan memakan habis sisa yang terluput, yang masih tinggal bagimu dari hujan es itu, bahkan akan memakan habis segala pohonmu yang tumbuh di padang.'),(1783,2,10,6,'Belalang itu akan memenuhi rumahmu, rumah semua pegawaimu, rumah semua orang Mesir seperti yang belum pernah dilihat oleh bapamu dan nenek moyangmu, sejak mereka lahir ke bumi sampai hari ini.\" Lalu berpalinglah ia dan keluar meninggalkan Firaun.'),(1784,2,10,7,'Sesudah itu berkatalah para pegawai Firaun kepadanya: \"Berapa lama lagi orang ini akan menjadi jerat kepada kita? Biarkanlah orang-orang itu pergi supaya mereka beribadah kepada TUHAN, Allah mereka. Belumkah tuanku insaf, bahwa Mesir pasti akan binasa?\"'),(1785,2,10,8,'Lalu Musa dan Harun dibawalah kembali kepada Firaun dan berkatalah Firaun kepada mereka: \"Pergilah, beribadahlah kepada TUHAN, Allahmu. Siapa-siapa sebenarnya yang akan pergi itu?\"'),(1786,2,10,9,'Jawab Musa: \"Kami hendak pergi dengan orang-orang yang muda dan yang tua; dengan anak-anak lelaki kami dan perempuan, dengan kambing domba kami dan lembu sapi kami, sebab kami harus mengadakan perayaan untuk TUHAN.\"'),(1787,2,10,10,'Tetapi Firaun berkata kepada mereka: \"TUHAN boleh menyertai kamu, jika aku membiarkan kamu pergi dengan anak-anakmu! Lihat, jahatlah maksudmu!'),(1788,2,10,11,'Bukan demikian, kamu boleh pergi, tetapi hanya laki-laki, dan beribadahlah kepada TUHAN, sebab itulah yang kamu kehendaki.\" Lalu mereka diusir dari depan Firaun.'),(1789,2,10,12,'Berfirmanlah TUHAN kepada Musa: \"Ulurkanlah tanganmu ke atas tanah Mesir mendatangkan belalang dan belalang akan datang meliputi tanah Mesir dan memakan habis segala tumbuh-tumbuhan di tanah, semuanya yang ditinggalkan oleh hujan es itu.\"'),(1790,2,10,13,'Lalu Musa mengulurkan tongkatnya ke atas tanah Mesir, dan TUHAN mendatangkan angin timur melintasi negeri itu, sehari-harian dan semalam-malaman, dan setelah hari pagi, angin timur membawa belalang.'),(1791,2,10,14,'Datanglah belalang meliputi seluruh tanah Mesir dan hinggap di seluruh daerah Mesir, sangat banyak; sebelum itu tidak pernah ada belalang yang demikian banyaknya dan sesudah itupun tidak akan terjadi lagi yang demikian.'),(1792,2,10,15,'Belalang menutupi seluruh permukaan bumi, sehingga negeri itu menjadi gelap olehnya; belalang memakan habis segala tumbuh-tumbuhan di tanah dan segala buah-buahan pada pohon-pohon yang ditinggalkan oleh hujan es itu, sehingga tidak ada tinggal lagi yang hijau pada pohon atau tumbuh-tumbuhan di padang di seluruh tanah Mesir.'),(1793,2,10,16,'Maka segeralah Firaun memanggil Musa dan Harun serta berkata: \"Aku telah berbuat dosa terhadap TUHAN, Allahmu, dan terhadap kamu.'),(1794,2,10,17,'Oleh sebab itu, ampunilah kiranya dosaku untuk sekali ini saja dan berdoalah kepada TUHAN, Allahmu itu, supaya bahaya maut ini dijauhkan-Nya dari padaku.\"'),(1795,2,10,18,'Lalu keluarlah Musa meninggalkan Firaun dan berdoa kepada TUHAN.'),(1796,2,10,19,'Maka TUHAN membuat angin bertiup dari jurusan sebaliknya, yakni angin barat yang sangat kencang, yang membawa belalang itu dan melemparkannya ke dalam Laut Teberau: tidak ada satu belalangpun yang tinggal di seluruh daerah Mesir.'),(1797,2,10,20,'Tetapi TUHAN mengeraskan hati Firaun, sehingga tidak mau membiarkan orang Israel pergi.'),(1798,2,10,21,'Berfirmanlah TUHAN kepada Musa: \"Ulurkanlah tanganmu ke langit, supaya datang gelap meliputi tanah Mesir, sehingga orang dapat meraba gelap itu.\"'),(1799,2,10,22,'Lalu Musa mengulurkan tangannya ke langit dan datanglah gelap gulita di seluruh tanah Mesir selama tiga hari.'),(1800,2,10,23,'Tidak ada orang yang dapat melihat temannya, juga tidak ada orang yang dapat bangun dari tempatnya selama tiga hari; tetapi pada semua orang Israel ada terang di tempat kediamannya.'),(1801,2,10,24,'Lalu Firaun memanggil Musa serta berkata: \"Pergilah, beribadahlah kepada TUHAN, hanya kambing dombamu dan lembu sapimu harus ditinggalkan, juga anak-anakmu boleh turut beserta kamu.\"'),(1802,2,10,25,'Tetapi Musa berkata: \"Bahkan korban sembelihan dan korban bakaran harus engkau berikan kepada kami, supaya kami menyediakannya untuk TUHAN, Allah kami.'),(1803,2,10,26,'Dan juga ternak kami harus turut beserta kami dan satu kakipun tidak akan tinggal, sebab dari ternak itulah kami harus ambil untuk beribadah kepada TUHAN, Allah kami; dan kami tidak tahu, dengan apa kami harus beribadah kepada TUHAN, sebelum kami sampai di sana.\"'),(1804,2,10,27,'Tetapi TUHAN mengeraskan hati Firaun, sehingga dia tidak mau membiarkan mereka pergi.'),(1805,2,10,28,'Lalu Firaun berkata kepadanya: \"Pergilah dari padaku; awaslah engkau, jangan lihat mukaku lagi, sebab pada waktu engkau melihat mukaku, engkau akan mati.\"'),(1806,2,10,29,'Kemudian Musa berkata: \"Tepat seperti ucapanmu itu! Aku takkan melihat mukamu lagi!\"'),(1807,2,11,1,'Berfirmanlah TUHAN kepada Musa: \"Aku akan mendatangkan satu tulah lagi atas Firaun dan atas Mesir, sesudah itu ia akan membiarkan kamu pergi dari sini; apabila ia membiarkan kamu pergi, ia akan benar-benar mengusir kamu dari sini.'),(1808,2,11,2,'Baiklah katakan kepada bangsa itu, supaya setiap laki-laki meminta barang-barang emas dan perak kepada tetangganya dan setiap perempuan kepada tetangganya pula.\"'),(1809,2,11,3,'Lalu TUHAN membuat orang Mesir bermurah hati terhadap bangsa itu; lagipula Musa adalah seorang yang sangat terpandang di tanah Mesir, di mata pegawai-pegawai Firaun dan di mata rakyat.'),(1810,2,11,4,'Berkatalah Musa: \"Beginilah firman TUHAN: Pada waktu tengah malam Aku akan berjalan dari tengah-tengah Mesir.'),(1811,2,11,5,'Maka tiap-tiap anak sulung di tanah Mesir akan mati, dari anak sulung Firaun yang duduk di takhtanya sampai kepada anak sulung budak perempuan yang menghadapi batu kilangan, juga segala anak sulung hewan.'),(1812,2,11,6,'Dan seruan yang hebat akan terjadi di seluruh tanah Mesir, seperti yang belum pernah terjadi dan seperti yang tidak akan ada lagi.'),(1813,2,11,7,'Tetapi kepada siapa juga dari orang Israel, seekor anjingpun tidak akan berani menggonggong, baik kepada manusia maupun kepada binatang, supaya kamu mengetahui, bahwa TUHAN membuat perbedaan antara orang Mesir dan orang Israel.'),(1814,2,11,8,'Dan semua pegawaimu ini akan datang kepadaku dan sujud kepadaku serta berkata: Keluarlah, engkau dan seluruh rakyat yang mengikut engkau; sesudah itu aku akan keluar.\" Lalu Musa meninggalkan Firaun dengan marah yang bernyala-nyala.'),(1815,2,11,9,'Berfirmanlah TUHAN kepada Musa: \"Firaun tidak akan mendengarkan kamu, supaya mujizat-mujizat yang Kubuat bertambah banyak di tanah Mesir.\"'),(1816,2,11,10,'Musa dan Harun telah melakukan segala mujizat ini di depan Firaun. Tetapi TUHAN mengeraskan hati Firaun, sehingga tidak membiarkan orang Israel pergi dari negerinya.'),(1817,2,12,1,'Berfirmanlah TUHAN kepada Musa dan Harun di tanah Mesir:'),(1818,2,12,2,'Bulan inilah akan menjadi permulaan segala bulan bagimu; itu akan menjadi bulan pertama bagimu tiap-tiap tahun.'),(1819,2,12,3,'Katakanlah kepada segenap jemaah Israel: Pada tanggal sepuluh bulan ini diambillah oleh masing-masing seekor anak domba, menurut kaum keluarga, seekor anak domba untuk tiap-tiap rumah tangga.'),(1820,2,12,4,'Tetapi jika rumah tangga itu terlalu kecil jumlahnya untuk mengambil seekor anak domba, maka ia bersama-sama dengan tetangganya yang terdekat ke rumahnya haruslah mengambil seekor, menurut jumlah jiwa; tentang anak domba itu, kamu buatlah perkiraan menurut keperluan tiap-tiap orang.'),(1821,2,12,5,'Anak dombamu itu harus jantan, tidak bercela, berumur setahun; kamu boleh ambil domba atau kambing.'),(1822,2,12,6,'Kamu harus mengurungnya sampai hari yang keempat belas bulan ini; lalu seluruh jemaah Israel yang berkumpul, harus menyembelihnya pada waktu senja.'),(1823,2,12,7,'Kemudian dari darahnya haruslah diambil sedikit dan dibubuhkan pada kedua tiang pintu dan pada ambang atas, pada rumah-rumah di mana orang memakannya.'),(1824,2,12,8,'Dagingnya harus dimakan mereka pada malam itu juga; yang dipanggang mereka harus makan dengan roti yang tidak beragi beserta sayur pahit.'),(1825,2,12,9,'Janganlah kamu memakannya mentah atau direbus dalam air; hanya dipanggang di api, lengkap dengan kepalanya dan betisnya dan isi perutnya.'),(1826,2,12,10,'Janganlah kamu tinggalkan apa-apa dari daging itu sampai pagi; apa yang tinggal sampai pagi kamu bakarlah habis dengan api.'),(1827,2,12,11,'Dan beginilah kamu memakannya: pinggangmu berikat, kasut pada kakimu dan tongkat di tanganmu; buru-burulah kamu memakannya; itulah Paskah bagi TUHAN.'),(1828,2,12,12,'Sebab pada malam ini Aku akan menjalani tanah Mesir, dan semua anak sulung, dari anak manusia sampai anak binatang, akan Kubunuh, dan kepada semua allah di Mesir akan Kujatuhkan hukuman, Akulah, TUHAN.'),(1829,2,12,13,'Dan darah itu menjadi tanda bagimu pada rumah-rumah di mana kamu tinggal: Apabila Aku melihat darah itu, maka Aku akan lewat dari pada kamu. Jadi tidak akan ada tulah kemusnahan di tengah-tengah kamu, apabila Aku menghukum tanah Mesir.'),(1830,2,12,14,'Hari ini akan menjadi hari peringatan bagimu. Kamu harus merayakannya sebagai hari raya bagi TUHAN turun-temurun. Kamu harus merayakannya sebagai ketetapan untuk selamanya.'),(1831,2,12,15,'Kamu makanlah roti yang tidak beragi tujuh hari lamanya; pada hari pertamapun kamu buanglah segala ragi dari rumahmu, sebab setiap orang yang makan sesuatu yang beragi, dari hari pertama sampai hari ketujuh, orang itu harus dilenyapkan dari antara Israel.'),(1832,2,12,16,'Kamu adakanlah pertemuan yang kudus, baik pada hari yang pertama maupun pada hari yang ketujuh; pada hari-hari itu tidak boleh dilakukan pekerjaan apapun; hanya apa yang perlu dimakan setiap orang, itu sajalah yang boleh kamu sediakan.'),(1833,2,12,17,'Jadi kamu harus tetap merayakan hari raya makan roti yang tidak beragi, sebab tepat pada hari ini juga Aku membawa pasukan-pasukanmu keluar dari tanah Mesir. Maka haruslah kamu rayakan hari ini turun-temurun; itulah suatu ketetapan untuk selamanya.'),(1834,2,12,18,'Dalam bulan pertama, pada hari yang keempat belas bulan itu pada waktu petang, kamu makanlah roti yang tidak beragi, sampai kepada hari yang kedua puluh satu bulan itu, pada waktu petang.'),(1835,2,12,19,'Tujuh hari lamanya tidak boleh ada ragi dalam rumahmu, sebab setiap orang yang makan sesuatu yang beragi, orang itu harus dilenyapkan dari antara jemaah Israel, baik ia orang asing, baik ia orang asli.'),(1836,2,12,20,'Sesuatu apapun yang beragi tidak boleh kamu makan; kamu makanlah roti yang tidak beragi di segala tempat kediamanmu.\"'),(1837,2,12,21,'Lalu Musa memanggil semua tua-tua Israel serta berkata kepada mereka: \"Pergilah, ambillah kambing domba untuk kaummu dan sembelihlah anak domba Paskah.'),(1838,2,12,22,'Kemudian kamu harus mengambil seikat hisop dan mencelupkannya dalam darah yang ada dalam sebuah pasu, dan darah itu kamu harus sapukan pada ambang atas dan pada kedua tiang pintu; seorangpun dari kamu tidak boleh keluar pintu rumahnya sampai pagi.'),(1839,2,12,23,'Dan TUHAN akan menjalani Mesir untuk menulahinya; apabila Ia melihat darah pada ambang atas dan pada kedua tiang pintu itu, maka TUHAN akan melewati pintu itu dan tidak membiarkan pemusnah masuk ke dalam rumahmu untuk menulahi.'),(1840,2,12,24,'Kamu harus memegang ini sebagai ketetapan sampai selama-lamanya bagimu dan bagi anak-anakmu.'),(1841,2,12,25,'Dan apabila kamu tiba di negeri yang akan diberikan TUHAN kepadamu, seperti yang difirmankan-Nya, maka kamu harus pelihara ibadah ini.'),(1842,2,12,26,'Dan apabila anak-anakmu berkata kepadamu: Apakah artinya ibadahmu ini?'),(1843,2,12,27,'maka haruslah kamu berkata: Itulah korban Paskah bagi TUHAN yang melewati rumah-rumah orang Israel di Mesir, ketika Ia menulahi orang Mesir, tetapi menyelamatkan rumah-rumah kita.\" Lalu berlututlah bangsa itu dan sujud menyembah.'),(1844,2,12,28,'Pergilah orang Israel, lalu berbuat demikian; seperti yang diperintahkan TUHAN kepada Musa dan Harun, demikianlah diperbuat mereka.'),(1845,2,12,29,'Maka pada tengah malam TUHAN membunuh tiap-tiap anak sulung di tanah Mesir, dari anak sulung Firaun yang duduk di takhtanya sampai kepada anak sulung orang tawanan, yang ada dalam liang tutupan, beserta segala anak sulung hewan.'),(1846,2,12,30,'Lalu bangunlah Firaun pada malam itu, bersama semua pegawainya dan semua orang Mesir; dan kedengaranlah seruan yang hebat di Mesir, sebab tidak ada rumah yang tidak kematian.'),(1847,2,12,31,'Lalu pada malam itu dipanggilnyalah Musa dan Harun, katanya: \"Bangunlah, keluarlah dari tengah-tengah bangsaku, baik kamu maupun orang Israel; pergilah, beribadahlah kepada TUHAN, seperti katamu itu.'),(1848,2,12,32,'Bawalah juga kambing dombamu dan lembu sapimu, seperti katamu itu, tetapi pergilah! Dan pohonkanlah juga berkat bagiku.\"'),(1849,2,12,33,'Orang Mesir juga mendesak dengan keras kepada bangsa itu, menyuruh bangsa itu pergi dengan segera dari negeri itu, sebab kata mereka: \"Nanti kami mati semuanya.\"'),(1850,2,12,34,'Lalu bangsa itu mengangkat adonannya, sebelum diragi, dengan tempat adonan mereka terbungkus dalam kainnya di atas bahunya.'),(1851,2,12,35,'Orang Israel melakukan juga seperti kata Musa; mereka meminta dari orang Mesir barang-barang emas dan perak serta kain-kain.'),(1852,2,12,36,'Dan TUHAN membuat orang Mesir bermurah hati terhadap bangsa itu, sehingga memenuhi permintaan mereka. Demikianlah mereka merampasi orang Mesir itu.'),(1853,2,12,37,'Kemudian berangkatlah orang Israel dari Raamses ke Sukot, kira-kira enam ratus ribu orang laki-laki berjalan kaki, tidak termasuk anak-anak.'),(1854,2,12,38,'Juga banyak orang dari berbagai-bagai bangsa turut dengan mereka; lagi sangat banyak ternak kambing domba dan lembu sapi.'),(1855,2,12,39,'Adonan yang dibawa mereka dari Mesir dibakarlah menjadi roti bundar yang tidak beragi, sebab adonan itu tidak diragi, karena mereka diusir dari Mesir dan tidak dapat berlambat-lambat, dan mereka tidak pula menyediakan bekal baginya.'),(1856,2,12,40,'Lamanya orang Israel diam di Mesir adalah empat ratus tiga puluh tahun.'),(1857,2,12,41,'Sesudah lewat empat ratus tiga puluh tahun, tepat pada hari itu juga, keluarlah segala pasukan TUHAN dari tanah Mesir.'),(1858,2,12,42,'Malam itulah malam berjaga-jaga bagi TUHAN, untuk membawa mereka keluar dari tanah Mesir. Dan itulah juga malam berjaga-jaga bagi semua orang Israel, turun-temurun, untuk kemuliaan TUHAN.'),(1859,2,12,43,'Berfirmanlah TUHAN kepada Musa dan Harun: \"Inilah ketetapan mengenai Paskah: Tidak seorangpun dari bangsa asing boleh memakannya.'),(1860,2,12,44,'Seorang budak belian barulah boleh memakannya, setelah engkau menyunat dia.'),(1861,2,12,45,'Orang pendatang dan orang upahan tidak boleh memakannya.'),(1862,2,12,46,'Paskah itu harus dimakan dalam satu rumah juga; tidak boleh kaubawa sedikitpun dari daging itu keluar rumah; satu tulangpun tidak boleh kamu patahkan.'),(1863,2,12,47,'Segenap jemaah Israel haruslah merayakannya.'),(1864,2,12,48,'Tetapi apabila seorang asing telah menetap padamu dan mau merayakan Paskah bagi TUHAN, maka setiap laki-laki yang bersama-sama dengan dia, wajiblah disunat; barulah ia boleh mendekat untuk merayakannya; ia akan dianggap sebagai orang asli. Tetapi tidak seorangpun yang tidak bersunat boleh memakannya.'),(1865,2,12,49,'Satu hukum saja akan berlaku untuk orang asli dan untuk orang asing yang menetap di tengah-tengah kamu.\"'),(1866,2,12,50,'Seluruh orang Israel berbuat demikian; seperti yang diperintahkan TUHAN kepada Musa dan Harun, demikianlah diperbuat mereka.'),(1867,2,12,51,'Dan tepat pada hari itu juga TUHAN membawa orang Israel keluar dari tanah Mesir, menurut pasukan mereka.'),(1868,2,13,1,'Berfirmanlah TUHAN kepada Musa:'),(1869,2,13,2,'Kuduskanlah bagi-Ku semua anak sulung, semua yang lahir terdahulu dari kandungan pada orang Israel, baik pada manusia maupun pada hewan; Akulah yang empunya mereka.'),(1870,2,13,3,'Lalu berkatalah Musa kepada bangsa itu: \"Peringatilah hari ini, sebab pada hari ini kamu keluar dari Mesir, dari rumah perbudakan; karena dengan kekuatan tangan-Nya TUHAN telah membawa kamu keluar dari sana. Sebab itu tidak boleh dimakan sesuatupun yang beragi.'),(1871,2,13,4,'Hari ini kamu keluar, dalam bulan Abib.'),(1872,2,13,5,'Apabila TUHAN telah membawa engkau ke negeri orang Kanaan, orang Het, orang Amori, orang Hewi dan orang Yebus, negeri yang telah dijanjikan-Nya dengan sumpah kepada nenek moyangmu untuk memberikannya kepadamu, suatu negeri yang berlimpah-limpah susu dan madunya, maka engkau harus melakukan ibadah ini dalam bulan ini juga.'),(1873,2,13,6,'Makanlah roti yang tidak beragi tujuh hari lamanya dan pada hari yang ketujuh akan diadakan hari raya bagi TUHAN.'),(1874,2,13,7,'Roti yang tidak beragi haruslah dimakan selama tujuh hari itu; sesuatupun yang beragi tidak boleh dilihat padamu, bahkan ragi tidak boleh dilihat padamu di seluruh daerahmu.'),(1875,2,13,8,'Pada hari itu harus kauberitahukan kepada anakmu laki-laki: Ibadah ini adalah karena mengingat apa yang dibuat TUHAN kepadaku pada waktu aku keluar dari Mesir.'),(1876,2,13,9,'Hal itu bagimu harus menjadi tanda pada tanganmu dan menjadi peringatan di dahimu, supaya hukum TUHAN ada di bibirmu; sebab dengan tangan yang kuat TUHAN telah membawa engkau keluar dari Mesir.'),(1877,2,13,10,'Haruslah kaupegang ketetapan ini pada waktunya yang sudah ditentukan, dari tahun ke tahun.'),(1878,2,13,11,'Apabila engkau telah dibawa TUHAN ke negeri orang Kanaan, seperti yang telah dijanjikan-Nya dengan sumpah kepadamu dan kepada nenek moyangmu, dan negeri itu telah diberikan-Nya kepadamu,'),(1879,2,13,12,'maka haruslah kaupersembahkan bagi TUHAN segala yang lahir terdahulu dari kandungan; juga setiap kali ada hewan yang kaupunyai beranak pertama kali, anak jantan yang sulung adalah bagi TUHAN.'),(1880,2,13,13,'Tetapi setiap anak keledai yang lahir terdahulu kautebuslah dengan seekor domba; atau, jika engkau tidak menebusnya, engkau harus mematahkan batang lehernya. Tetapi mengenai manusia, setiap anak sulung di antara anak-anakmu lelaki, haruslah kautebus.'),(1881,2,13,14,'Dan apabila anakmu akan bertanya kepadamu di kemudian hari: Apakah artinya itu? maka haruslah engkau berkata kepadanya: Dengan kekuatan tangan-Nya TUHAN telah membawa kita keluar dari Mesir, dari rumah perbudakan.'),(1882,2,13,15,'Sebab ketika Firaun dengan tegar menolak untuk membiarkan kita pergi, maka TUHAN membunuh semua anak sulung di tanah Mesir, dari anak sulung manusia sampai anak sulung hewan. Itulah sebabnya maka aku biasa mempersembahkan kepada TUHAN segala binatang jantan yang lahir terdahulu dari kandungan, sedang semua anak sulung di antara anak-anakku lelaki kutebus.'),(1883,2,13,16,'Hal itu harus menjadi tanda pada tanganmu dan menjadi lambang di dahimu, sebab dengan kekuatan tangan-Nya TUHAN membawa kita keluar dari Mesir.\"'),(1884,2,13,17,'Setelah Firaun membiarkan bangsa itu pergi, Allah tidak menuntun mereka melalui jalan ke negeri orang Filistin, walaupun jalan ini yang paling dekat; sebab firman Allah: \"Jangan-jangan bangsa itu menyesal, apabila mereka menghadapi peperangan, sehingga mereka kembali ke Mesir.\"'),(1885,2,13,18,'Tetapi Allah menuntun bangsa itu berputar melalui jalan di padang gurun menuju ke Laut Teberau. Dengan siap sedia berperang berjalanlah orang Israel dari tanah Mesir.'),(1886,2,13,19,'Musa membawa tulang-tulang Yusuf, sebab tadinya Yusuf telah menyuruh anak-anak Israel bersumpah dengan sungguh-sungguh: \"Allah tentu akan mengindahkan kamu, maka kamu harus membawa tulang-tulangku dari sini.\"'),(1887,2,13,20,'Demikianlah mereka berangkat dari Sukot dan berkemah di Etam, di tepi padang gurun.'),(1888,2,13,21,'TUHAN berjalan di depan mereka, pada siang hari dalam tiang awan untuk menuntun mereka di jalan, dan pada waktu malam dalam tiang api untuk menerangi mereka, sehingga mereka dapat berjalan siang dan malam.'),(1889,2,13,22,'Dengan tidak beralih tiang awan itu tetap ada pada siang hari dan tiang api pada waktu malam di depan bangsa itu.'),(1890,2,14,1,'Berfirmanlah TUHAN kepada Musa, demikian:'),(1891,2,14,2,'Katakanlah kepada orang Israel, supaya mereka balik kembali dan berkemah di depan Pi-Hahirot, antara Migdol dan laut; tepat di depan Baal-Zefon berkemahlah kamu, di tepi laut.'),(1892,2,14,3,'Maka Firaun akan berkata tentang orang Israel: Mereka telah sesat di negeri ini, padang gurun telah mengurung mereka.'),(1893,2,14,4,'Aku akan mengeraskan hati Firaun, sehingga ia mengejar mereka. Dan terhadap Firaun dan seluruh pasukannya Aku akan menyatakan kemuliaan-Ku, sehingga orang Mesir mengetahui, bahwa Akulah TUHAN.\" Lalu mereka berbuat demikian.'),(1894,2,14,5,'Ketika diberitahukan kepada raja Mesir, bahwa bangsa itu telah lari, maka berubahlah hati Firaun dan pegawai-pegawainya terhadap bangsa itu, dan berkatalah mereka: \"Apakah yang telah kita perbuat ini, bahwa kita membiarkan orang Israel pergi dari perbudakan kita?\"'),(1895,2,14,6,'Kemudian ia memasang keretanya dan membawa rakyatnya serta.'),(1896,2,14,7,'Ia membawa enam ratus kereta yang terpilih, ya, segala kereta Mesir, masing-masing lengkap dengan perwiranya.'),(1897,2,14,8,'Demikianlah TUHAN mengeraskan hati Firaun, raja Mesir itu, sehingga ia mengejar orang Israel. Tetapi orang Israel berjalan terus dipimpin oleh tangan yang dinaikkan.'),(1898,2,14,9,'Adapun orang Mesir, segala kuda dan kereta Firaun, orang-orang berkuda dan pasukannya, mengejar mereka dan mencapai mereka pada waktu mereka berkemah di tepi laut, dekat Pi-Hahirot di depan Baal-Zefon.'),(1899,2,14,10,'Ketika Firaun telah dekat, orang Israel menoleh, maka tampaklah orang Mesir bergerak menyusul mereka. Lalu sangat ketakutanlah orang Israel dan mereka berseru-seru kepada TUHAN,'),(1900,2,14,11,'dan mereka berkata kepada Musa: \"Apakah karena tidak ada kuburan di Mesir, maka engkau membawa kami untuk mati di padang gurun ini? Apakah yang kauperbuat ini terhadap kami dengan membawa kami keluar dari Mesir?'),(1901,2,14,12,'Bukankah ini telah kami katakan kepadamu di Mesir: Janganlah mengganggu kami dan biarlah kami bekerja pada orang Mesir. Sebab lebih baik bagi kami untuk bekerja pada orang Mesir dari pada mati di padang gurun ini.\"'),(1902,2,14,13,'Tetapi berkatalah Musa kepada bangsa itu: \"Janganlah takut, berdirilah tetap dan lihatlah keselamatan dari TUHAN, yang akan diberikan-Nya hari ini kepadamu; sebab orang Mesir yang kamu lihat hari ini, tidak akan kamu lihat lagi untuk selama-lamanya.'),(1903,2,14,14,'TUHAN akan berperang untuk kamu, dan kamu akan diam saja.\"'),(1904,2,14,15,'Berfirmanlah TUHAN kepada Musa: \"Mengapakah engkau berseru-seru demikian kepada-Ku? Katakanlah kepada orang Israel, supaya mereka berangkat.'),(1905,2,14,16,'Dan engkau, angkatlah tongkatmu dan ulurkanlah tanganmu ke atas laut dan belahlah airnya, sehingga orang Israel akan berjalan dari tengah-tengah laut di tempat kering.'),(1906,2,14,17,'Tetapi sungguh Aku akan mengeraskan hati orang Mesir, sehingga mereka menyusul orang Israel, dan terhadap Firaun dan seluruh pasukannya, keretanya dan orangnya yang berkuda, Aku akan menyatakan kemuliaan-Ku.'),(1907,2,14,18,'Maka orang Mesir akan mengetahui, bahwa Akulah TUHAN, apabila Aku memperlihatkan kemuliaan-Ku terhadap Firaun, keretanya dan orangnya yang berkuda.\"'),(1908,2,14,19,'Kemudian bergeraklah Malaikat Allah, yang tadinya berjalan di depan tentara Israel, lalu berjalan di belakang mereka; dan tiang awan itu bergerak dari depan mereka, lalu berdiri di belakang mereka.'),(1909,2,14,20,'Demikianlah tiang itu berdiri di antara tentara orang Mesir dan tentara orang Israel; dan oleh karena awan itu menimbulkan kegelapan, maka malam itu lewat, sehingga yang satu tidak dapat mendekati yang lain, semalam-malaman itu.'),(1910,2,14,21,'Lalu Musa mengulurkan tangannya ke atas laut, dan semalam-malaman itu TUHAN menguakkan air laut dengan perantaraan angin timur yang keras, membuat laut itu menjadi tanah kering; maka terbelahlah air itu.'),(1911,2,14,22,'Demikianlah orang Israel berjalan dari tengah-tengah laut di tempat kering; sedang di kiri dan di kanan mereka air itu sebagai tembok bagi mereka.'),(1912,2,14,23,'Orang Mesir mengejar dan menyusul mereka--segala kuda Firaun, keretanya dan orangnya yang berkuda--sampai ke tengah-tengah laut.'),(1913,2,14,24,'Dan pada waktu jaga pagi, TUHAN yang di dalam tiang api dan awan itu memandang kepada tentara orang Mesir, lalu dikacaukan-Nya tentara orang Mesir itu.'),(1914,2,14,25,'Ia membuat roda keretanya berjalan miring dan maju dengan berat, sehingga orang Mesir berkata: \"Marilah kita lari meninggalkan orang Israel, sebab Tuhanlah yang berperang untuk mereka melawan Mesir.\"'),(1915,2,14,26,'Berfirmanlah TUHAN kepada Musa: \"Ulurkanlah tanganmu ke atas laut, supaya air berbalik meliputi orang Mesir, meliputi kereta mereka dan orang mereka yang berkuda.\"'),(1916,2,14,27,'Musa mengulurkan tangannya ke atas laut, maka menjelang pagi berbaliklah air laut ke tempatnya, sedang orang Mesir lari menuju air itu; demikianlah TUHAN mencampakkan orang Mesir ke tengah-tengah laut.'),(1917,2,14,28,'Berbaliklah segala air itu, lalu menutupi kereta dan orang berkuda dari seluruh pasukan Firaun, yang telah menyusul orang Israel itu ke laut; seorangpun tidak ada yang tinggal dari mereka.'),(1918,2,14,29,'Tetapi orang Israel berjalan di tempat kering dari tengah-tengah laut, sedang di kiri dan di kanan mereka air itu sebagai tembok bagi mereka.'),(1919,2,14,30,'Demikianlah pada hari itu TUHAN menyelamatkan orang Israel dari tangan orang Mesir. Dan orang Israel melihat orang Mesir mati terhantar di pantai laut.'),(1920,2,14,31,'Ketika dilihat oleh orang Israel, betapa besarnya perbuatan yang dilakukan TUHAN terhadap orang Mesir, maka takutlah bangsa itu kepada TUHAN dan mereka percaya kepada TUHAN dan kepada Musa, hamba-Nya itu.'),(1921,2,15,1,'Pada waktu itu Musa bersama-sama dengan orang Israel menyanyikan nyanyian ini bagi TUHAN yang berbunyi: \"Baiklah aku menyanyi bagi TUHAN, sebab Ia tinggi luhur, kuda dan penunggangnya dilemparkan-Nya ke dalam laut.'),(1922,2,15,2,'TUHAN itu kekuatanku dan mazmurku, Ia telah menjadi keselamatanku. Ia Allahku, kupuji Dia, Ia Allah bapaku, kuluhurkan Dia.'),(1923,2,15,3,'TUHAN itu pahlawan perang; TUHAN, itulah nama-Nya.'),(1924,2,15,4,'Kereta Firaun dan pasukannya dibuang-Nya ke dalam laut; para perwiranya yang pilihan dibenamkan ke dalam Laut Teberau.'),(1925,2,15,5,'Samudera raya menutupi mereka; ke air yang dalam mereka tenggelam seperti batu.'),(1926,2,15,6,'Tangan kanan-Mu, TUHAN, mulia karena kekuasaan-Mu, tangan kanan-Mu, TUHAN, menghancurkan musuh.'),(1927,2,15,7,'Dengan keluhuran-Mu yang besar Engkau meruntuhkan siapa yang bangkit menentang Engkau; Engkau melepaskan api murka-Mu, yang memakan mereka sebagai tunggul gandum.'),(1928,2,15,8,'Karena nafas hidung-Mu segala air naik bertimbun-timbun; segala aliran berdiri tegak seperti bendungan; air bah membeku di tengah-tengah laut.'),(1929,2,15,9,'Kata musuh: Aku akan mengejar, akan mencapai mereka, akan membagi-bagi jarahan; nafsuku akan kulampiaskan kepada mereka, akan kuhunus pedangku; tanganku akan melenyapkan mereka!'),(1930,2,15,10,'Engkau meniup dengan taufan-Mu, lautpun menutupi mereka; sebagai timah mereka tenggelam dalam air yang hebat.'),(1931,2,15,11,'Siapakah yang seperti Engkau, di antara para allah, ya TUHAN; siapakah seperti Engkau, mulia karena kekudusan-Mu, menakutkan karena perbuatan-Mu yang masyhur, Engkau pembuat keajaiban?'),(1932,2,15,12,'Engkau mengulurkan tangan kanan-Mu; bumipun menelan mereka.'),(1933,2,15,13,'Dengan kasih setia-Mu Engkau menuntun umat yang telah Kautebus; dengan kekuatan-Mu Engkau membimbingnya ke tempat kediaman-Mu yang kudus.'),(1934,2,15,14,'Bangsa-bangsa mendengarnya, merekapun menggigil; kegentaran menghinggapi penduduk tanah Filistin.'),(1935,2,15,15,'Pada waktu itu gemparlah para kepala kaum di Edom, kedahsyatan menghinggapi orang-orang berkuasa di Moab; semua penduduk tanah Kanaan gemetar.'),(1936,2,15,16,'Ngeri dan takut menimpa mereka, karena kebesaran tangan-Mu mereka kaku seperti batu, sampai umat-Mu menyeberang, ya TUHAN, sampai umat yang Kauperoleh menyeberang.'),(1937,2,15,17,'Engkau membawa mereka dan Kaucangkokkan mereka di atas gunung milik-Mu sendiri; di tempat yang telah Kaubuat kediaman-Mu, ya TUHAN; di tempat kudus, yang didirikan tangan-Mu, ya TUHAN.'),(1938,2,15,18,'TUHAN memerintah kekal selama-lamanya.\"'),(1939,2,15,19,'Ketika kuda Firaun dengan keretanya dan orangnya yang berkuda telah masuk ke laut, maka TUHAN membuat air laut berbalik meliputi mereka, tetapi orang Israel berjalan di tempat kering dari tengah-tengah laut.'),(1940,2,15,20,'Lalu Miryam, nabiah itu, saudara perempuan Harun, mengambil rebana di tangannya, dan tampillah semua perempuan mengikutinya memukul rebana serta menari-nari.'),(1941,2,15,21,'Dan menyanyilah Miryam memimpin mereka: \"Menyanyilah bagi TUHAN, sebab Ia tinggi luhur; kuda dan penunggangnya dilemparkan-Nya ke dalam laut.\"'),(1942,2,15,22,'Musa menyuruh orang Israel berangkat dari Laut Teberau, lalu mereka pergi ke padang gurun Syur; tiga hari lamanya mereka berjalan di padang gurun itu dengan tidak mendapat air.'),(1943,2,15,23,'Sampailah mereka ke Mara, tetapi mereka tidak dapat meminum air yang di Mara itu, karena pahit rasanya. Itulah sebabnya dinamai orang tempat itu Mara.'),(1944,2,15,24,'Lalu bersungut-sungutlah bangsa itu kepada Musa, kata mereka: \"Apakah yang akan kami minum?\"'),(1945,2,15,25,'Musa berseru-seru kepada TUHAN, dan TUHAN menunjukkan kepadanya sepotong kayu; Musa melemparkan kayu itu ke dalam air; lalu air itu menjadi manis. Di sanalah diberikan TUHAN ketetapan-ketetapan dan peraturan-peraturan kepada mereka dan di sanalah TUHAN mencoba mereka,'),(1946,2,15,26,'firman-Nya: \"Jika kamu sungguh-sungguh mendengarkan suara TUHAN, Allahmu, dan melakukan apa yang benar di mata-Nya, dan memasang telingamu kepada perintah-perintah-Nya dan tetap mengikuti segala ketetapan-Nya, maka Aku tidak akan menimpakan kepadamu penyakit manapun, yang telah Kutimpakan kepada orang Mesir; sebab Aku Tuhanlah yang menyembuhkan engkau.\"'),(1947,2,15,27,'Sesudah itu sampailah mereka di Elim; di sana ada dua belas mata air dan tujuh puluh pohon korma, lalu berkemahlah mereka di sana di tepi air itu.'),(1948,2,16,1,'Setelah mereka berangkat dari Elim, tibalah segenap jemaah Israel di padang gurun Sin, yang terletak di antara Elim dan gunung Sinai, pada hari yang kelima belas bulan yang kedua, sejak mereka keluar dari tanah Mesir.'),(1949,2,16,2,'Di padang gurun itu bersungut-sungutlah segenap jemaah Israel kepada Musa dan Harun;'),(1950,2,16,3,'dan berkata kepada mereka: \"Ah, kalau kami mati tadinya di tanah Mesir oleh tangan TUHAN ketika kami duduk menghadapi kuali berisi daging dan makan roti sampai kenyang! Sebab kamu membawa kami keluar ke padang gurun ini untuk membunuh seluruh jemaah ini dengan kelaparan.\"'),(1951,2,16,4,'Lalu berfirmanlah TUHAN kepada Musa: \"Sesungguhnya Aku akan menurunkan dari langit hujan roti bagimu; maka bangsa itu akan keluar dan memungut tiap-tiap hari sebanyak yang perlu untuk sehari, supaya mereka Kucoba, apakah mereka hidup menurut hukum-Ku atau tidak.'),(1952,2,16,5,'Dan pada hari yang keenam, apabila mereka memasak yang dibawa mereka pulang, maka yang dibawa itu akan terdapat dua kali lipat banyaknya dari apa yang dipungut mereka sehari-hari.\"'),(1953,2,16,6,'Sesudah itu berkatalah Musa dan Harun kepada seluruh orang Israel: \"Petang ini kamu akan mengetahui bahwa Tuhanlah yang telah membawa kamu keluar dari tanah Mesir.'),(1954,2,16,7,'Dan besok pagi kamu melihat kemuliaan TUHAN, karena Ia telah mendengar sungut-sungutmu kepada-Nya. Sebab, apalah kami ini maka kamu bersungut-sungut kepada kami?\"'),(1955,2,16,8,'Lagi kata Musa: \"Jika memang TUHAN yang memberi kamu makan daging pada waktu petang dan makan roti sampai kenyang pada waktu pagi, karena TUHAN telah mendengar sungut-sungutmu yang kamu sungut-sungutkan kepada-Nya--apalah kami ini? Bukan kepada kami sungut-sungutmu itu, tetapi kepada TUHAN.\"'),(1956,2,16,9,'Kata Musa kepada Harun: \"Katakanlah kepada segenap jemaah Israel: Marilah dekat ke hadapan TUHAN, sebab Ia telah mendengar sungut-sungutmu.\"'),(1957,2,16,10,'Dan sedang Harun berbicara kepada segenap jemaah Israel, mereka memalingkan mukanya ke arah padang gurun--maka tampaklah kemuliaan TUHAN dalam awan.'),(1958,2,16,11,'Lalu berfirmanlah TUHAN kepada Musa:'),(1959,2,16,12,'Aku telah mendengar sungut-sungut orang Israel; katakanlah kepada mereka: Pada waktu senja kamu akan makan daging dan pada waktu pagi kamu akan kenyang makan roti; maka kamu akan mengetahui, bahwa Akulah TUHAN, Allahmu.'),(1960,2,16,13,'Pada waktu petang datanglah berduyun-duyun burung puyuh yang menutupi perkemahan itu; dan pada waktu pagi terletaklah embun sekeliling perkemahan itu.'),(1961,2,16,14,'Ketika embun itu telah menguap, tampaklah pada permukaan padang gurun sesuatu yang halus, sesuatu yang seperti sisik, halus seperti embun beku di bumi.'),(1962,2,16,15,'Ketika orang Israel melihatnya, berkatalah mereka seorang kepada yang lain: \"Apakah ini?\" Sebab mereka tidak tahu apa itu. Tetapi Musa berkata kepada mereka: \"Inilah roti yang diberikan TUHAN kepadamu menjadi makananmu.'),(1963,2,16,16,'Beginilah perintah TUHAN: Pungutlah itu, tiap-tiap orang menurut keperluannya; masing-masing kamu boleh mengambil untuk seisi kemahnya, segomer seorang, menurut jumlah jiwa.\"'),(1964,2,16,17,'Demikianlah diperbuat orang Israel; mereka mengumpulkan, ada yang banyak, ada yang sedikit.'),(1965,2,16,18,'Ketika mereka menakarnya dengan gomer, maka orang yang mengumpulkan banyak, tidak kelebihan dan orang yang mengumpulkan sedikit, tidak kekurangan. Tiap-tiap orang mengumpulkan menurut keperluannya.'),(1966,2,16,19,'Musa berkata kepada mereka: \"Seorangpun tidak boleh meninggalkan dari padanya sampai pagi.\"'),(1967,2,16,20,'Tetapi ada yang tidak mendengarkan Musa dan meninggalkan dari padanya sampai pagi, lalu berulat dan berbau busuk. Maka Musa menjadi marah kepada mereka.'),(1968,2,16,21,'Setiap pagi mereka memungutnya, tiap-tiap orang menurut keperluannya; tetapi ketika matahari panas, cairlah itu.'),(1969,2,16,22,'Dan pada hari yang keenam mereka memungut roti itu dua kali lipat banyaknya, dua gomer untuk tiap-tiap orang; dan datanglah semua pemimpin jemaah memberitahukannya kepada Musa.'),(1970,2,16,23,'Lalu berkatalah Musa kepada mereka: \"Inilah yang dimaksudkan TUHAN: Besok adalah hari perhentian penuh, sabat yang kudus bagi TUHAN; maka roti yang perlu kamu bakar, bakarlah, dan apa yang perlu kamu masak, masaklah; dan segala kelebihannya biarkanlah di tempatnya untuk disimpan sampai pagi.\"'),(1971,2,16,24,'Mereka membiarkannya di tempatnya sampai keesokan harinya, seperti yang diperintahkan Musa; lalu tidaklah berbau busuk dan tidak ada ulat di dalamnya.'),(1972,2,16,25,'Selanjutnya kata Musa: \"Makanlah itu pada hari ini, sebab hari ini adalah sabat untuk TUHAN, pada hari ini tidaklah kamu mendapatnya di padang.'),(1973,2,16,26,'Enam hari lamanya kamu memungutnya, tetapi pada hari yang ketujuh ada sabat; maka roti itu tidak ada pada hari itu.\"'),(1974,2,16,27,'Tetapi ketika pada hari ketujuh ada dari bangsa itu yang keluar memungutnya, tidaklah mereka mendapatnya.'),(1975,2,16,28,'Sebab itu TUHAN berfirman kepada Musa: \"Berapa lama lagi kamu menolak mengikuti segala perintah-Ku dan hukum-Ku?'),(1976,2,16,29,'Perhatikanlah, TUHAN telah memberikan sabat itu kepadamu; itulah sebabnya pada hari keenam Ia memberikan kepadamu roti untuk dua hari. Tinggallah kamu di tempatmu masing-masing, seorangpun tidak boleh keluar dari tempatnya pada hari ketujuh itu.\"'),(1977,2,16,30,'Lalu beristirahatlah bangsa itu pada hari ketujuh.'),(1978,2,16,31,'Umat Israel menyebutkan namanya: manna; warnanya putih seperti ketumbar dan rasanya seperti rasa kue madu.'),(1979,2,16,32,'Musa berkata: \"Beginilah perintah TUHAN: Ambillah segomer penuh untuk disimpan turun-temurun, supaya keturunan mereka melihat roti yang Kuberi kamu makan di padang gurun, ketika Aku membawa kamu keluar dari tanah Mesir.\"'),(1980,2,16,33,'Sebab itu Musa berkata kepada Harun: \"Ambillah sebuah buli-buli, taruhlah manna di dalamnya segomer penuh, dan tempatkanlah itu di hadapan TUHAN untuk disimpan turun-temurun.\"'),(1981,2,16,34,'Seperti yang diperintahkan TUHAN kepada Musa, demikianlah buli-buli itu ditempatkan Harun di hadapan tabut hukum Allah untuk disimpan.'),(1982,2,16,35,'Orang Israel makan manna empat puluh tahun lamanya, sampai mereka tiba di tanah yang didiami orang; mereka makan manna sampai tiba di perbatasan tanah Kanaan.'),(1983,2,16,36,'Adapun segomer ialah sepersepuluh efa.'),(1984,2,17,1,'Kemudian berangkatlah segenap jemaah Israel dari padang gurun Sin, berjalan dari tempat persinggahan ke tempat persinggahan, sesuai dengan titah TUHAN, lalu berkemahlah mereka di Rafidim, tetapi di sana tidak ada air untuk diminum bangsa itu.'),(1985,2,17,2,'Jadi mulailah mereka itu bertengkar dengan Musa, kata mereka: \"Berikanlah air kepada kami, supaya kami dapat minum.\" Tetapi Musa berkata kepada mereka: \"Mengapakah kamu bertengkar dengan aku? Mengapakah kamu mencobai TUHAN?\"'),(1986,2,17,3,'Hauslah bangsa itu akan air di sana; bersungut-sungutlah bangsa itu kepada Musa dan berkata: \"Mengapa pula engkau memimpin kami keluar dari Mesir, untuk membunuh kami, anak-anak kami dan ternak kami dengan kehausan?\"'),(1987,2,17,4,'Lalu berseru-serulah Musa kepada TUHAN, katanya: \"Apakah yang akan kulakukan kepada bangsa ini? Sebentar lagi mereka akan melempari aku dengan batu!\"'),(1988,2,17,5,'Berfirmanlah TUHAN kepada Musa: \"Berjalanlah di depan bangsa itu dan bawalah beserta engkau beberapa orang dari antara para tua-tua Israel; bawalah juga di tanganmu tongkatmu yang kaupakai memukul sungai Nil dan pergilah.'),(1989,2,17,6,'Maka Aku akan berdiri di sana di depanmu di atas gunung batu di Horeb; haruslah kaupukul gunung batu itu dan dari dalamnya akan keluar air, sehingga bangsa itu dapat minum.\" Demikianlah diperbuat Musa di depan mata tua-tua Israel.'),(1990,2,17,7,'Dinamailah tempat itu Masa dan Meriba, oleh karena orang Israel telah bertengkar dan oleh karena mereka telah mencobai TUHAN dengan mengatakan: \"Adakah TUHAN di tengah-tengah kita atau tidak?\"'),(1991,2,17,8,'Lalu datanglah orang Amalek dan berperang melawan orang Israel di Rafidim.'),(1992,2,17,9,'Musa berkata kepada Yosua: \"Pilihlah orang-orang bagi kita, lalu keluarlah berperang melawan orang Amalek, besok aku akan berdiri di puncak bukit itu dengan memegang tongkat Allah di tanganku.\"'),(1993,2,17,10,'Lalu Yosua melakukan seperti yang dikatakan Musa kepadanya dan berperang melawan orang Amalek; tetapi Musa, Harun dan Hur telah naik ke puncak bukit.'),(1994,2,17,11,'Dan terjadilah, apabila Musa mengangkat tangannya, lebih kuatlah Israel, tetapi apabila ia menurunkan tangannya, lebih kuatlah Amalek.'),(1995,2,17,12,'Maka penatlah tangan Musa, sebab itu mereka mengambil sebuah batu, diletakkanlah di bawahnya, supaya ia duduk di atasnya; Harun dan Hur menopang kedua belah tangannya, seorang di sisi yang satu, seorang di sisi yang lain, sehingga tangannya tidak bergerak sampai matahari terbenam.'),(1996,2,17,13,'Demikianlah Yosua mengalahkan Amalek dan rakyatnya dengan mata pedang.'),(1997,2,17,14,'Kemudian berfirmanlah TUHAN kepada Musa: \"Tuliskanlah semuanya ini dalam sebuah kitab sebagai tanda peringatan, dan ingatkanlah ke telinga Yosua, bahwa Aku akan menghapuskan sama sekali ingatan kepada Amalek dari kolong langit.\"'),(1998,2,17,15,'Lalu Musa mendirikan sebuah mezbah dan menamainya: \"Tuhanlah panji-panjiku!\"'),(1999,2,17,16,'Ia berkata: \"Tangan di atas panji-panji TUHAN! TUHAN berperang melawan Amalek turun-temurun.\"'),(2000,2,18,1,'Kedengaranlah kepada Yitro, imam di Midian, mertua Musa, segala yang dilakukan Allah kepada Musa dan kepada Israel, umat-Nya, yakni bahwa TUHAN telah membawa orang Israel keluar dari Mesir.'),(2001,2,18,2,'Lalu Yitro, mertua Musa, membawa serta Zipora, isteri Musa--yang dahulu disuruh Musa pulang--'),(2002,2,18,3,'dan kedua anak laki-laki Zipora; yang seorang bernama Gersom, sebab kata Musa: \"Aku telah menjadi seorang pendatang di negeri asing,\"'),(2003,2,18,4,'dan yang seorang lagi bernama Eliezer, sebab katanya: \"Allah bapaku adalah penolongku dan telah menyelamatkan aku dari pedang Firaun.\"'),(2004,2,18,5,'Ketika Yitro, mertua Musa, beserta anak-anak dan isteri Musa sampai kepadanya di padang gurun, tempat ia berkemah dekat gunung Allah,'),(2005,2,18,6,'disuruhnyalah mengatakan kepada Musa: \"Aku, mertuamu Yitro, datang kepadamu membawa isterimu beserta kedua anaknya.\"'),(2006,2,18,7,'Lalu keluarlah Musa menyongsong mertuanya itu, sujudlah ia kepadanya dan menciumnya; mereka menanyakan keselamatan masing-masing, lalu masuk ke dalam kemah.'),(2007,2,18,8,'Sesudah itu Musa menceritakan kepada mertuanya segala yang dilakukan TUHAN kepada Firaun dan kepada orang Mesir karena Israel dan segala kesusahan yang mereka alami di jalan dan bagaimana TUHAN menyelamatkan mereka.'),(2008,2,18,9,'Bersukacitalah Yitro tentang segala kebaikan, yang dilakukan TUHAN kepada orang Israel, bahwa Ia telah menyelamatkan mereka dari tangan orang Mesir.'),(2009,2,18,10,'Lalu kata Yitro: \"Terpujilah TUHAN, yang telah menyelamatkan kamu dari tangan orang Mesir dan dari tangan Firaun.'),(2010,2,18,11,'Sekarang aku tahu, bahwa TUHAN lebih besar dari segala allah; sebab Ia telah menyelamatkan bangsa ini dari tangan orang Mesir, karena memang orang-orang ini telah bertindak angkuh terhadap mereka.\"'),(2011,2,18,12,'Dan Yitro, mertua Musa, mempersembahkan korban bakaran dan beberapa korban sembelihan bagi Allah; lalu Harun dan semua tua-tua Israel datang untuk makan bersama-sama dengan mertua Musa di hadapan Allah.'),(2012,2,18,13,'Keesokan harinya duduklah Musa mengadili di antara bangsa itu; dan bangsa itu berdiri di depan Musa, dari pagi sampai petang.'),(2013,2,18,14,'Ketika mertua Musa melihat segala yang dilakukannya kepada bangsa itu, berkatalah ia: \"Apakah ini yang kaulakukan kepada bangsa itu? Mengapakah engkau seorang diri saja yang duduk, sedang seluruh bangsa itu berdiri di depanmu dari pagi sampai petang?\"'),(2014,2,18,15,'Kata Musa kepada mertuanya itu: \"Sebab bangsa ini datang kepadaku untuk menanyakan petunjuk Allah.'),(2015,2,18,16,'Apabila ada perkara di antara mereka, maka mereka datang kepadaku dan aku mengadili antara yang seorang dan yang lain; lagipula aku memberitahukan kepada mereka ketetapan-ketetapan dan keputusan-keputusan Allah.\"'),(2016,2,18,17,'Tetapi mertua Musa menjawabnya: \"Tidak baik seperti yang kaulakukan itu.'),(2017,2,18,18,'Engkau akan menjadi sangat lelah, baik engkau baik bangsa yang beserta engkau ini; sebab pekerjaan ini terlalu berat bagimu, takkan sanggup engkau melakukannya seorang diri saja.'),(2018,2,18,19,'Jadi sekarang dengarkanlah perkataanku, aku akan memberi nasihat kepadamu dan Allah akan menyertai engkau. Adapun engkau, wakililah bangsa itu di hadapan Allah dan kauhadapkanlah perkara-perkara mereka kepada Allah.'),(2019,2,18,20,'Kemudian haruslah engkau mengajarkan kepada mereka ketetapan-ketetapan dan keputusan-keputusan, dan memberitahukan kepada mereka jalan yang harus dijalani, dan pekerjaan yang harus dilakukan.'),(2020,2,18,21,'Di samping itu kaucarilah dari seluruh bangsa itu orang-orang yang cakap dan takut akan Allah, orang-orang yang dapat dipercaya, dan yang benci kepada pengejaran suap; tempatkanlah mereka di antara bangsa itu menjadi pemimpin seribu orang, pemimpin seratus orang, pemimpin lima puluh orang dan pemimpin sepuluh orang.'),(2021,2,18,22,'Dan sewaktu-waktu mereka harus mengadili di antara bangsa; maka segala perkara yang besar haruslah dihadapkan mereka kepadamu, tetapi segala perkara yang kecil diadili mereka sendiri; dengan demikian mereka meringankan pekerjaanmu, dan mereka bersama-sama dengan engkau turut menanggungnya.'),(2022,2,18,23,'Jika engkau berbuat demikian dan Allah memerintahkan hal itu kepadamu, maka engkau akan sanggup menahannya, dan seluruh bangsa ini akan pulang dengan puas senang ke tempatnya.\"'),(2023,2,18,24,'Musa mendengarkan perkataan mertuanya itu dan dilakukannyalah segala yang dikatakannya.'),(2024,2,18,25,'Dari seluruh orang Israel Musa memilih orang-orang cakap dan mengangkat mereka menjadi kepala atas bangsa itu, menjadi pemimpin seribu orang, pemimpin seratus orang, pemimpin lima puluh orang dan pemimpin sepuluh orang.'),(2025,2,18,26,'Mereka ini mengadili di antara bangsa itu sewaktu-waktu; perkara-perkara yang sukar dihadapkan mereka kepada Musa, tetapi perkara-perkara yang kecil diadili mereka sendiri.'),(2026,2,18,27,'Kemudian Musa membiarkan mertuanya itu pergi dan ia pulang ke negerinya.'),(2027,2,19,1,'Pada bulan ketiga setelah orang Israel keluar dari tanah Mesir, mereka tiba di padang gurun Sinai pada hari itu juga.'),(2028,2,19,2,'Setelah mereka berangkat dari Rafidim, tibalah mereka di padang gurun Sinai, lalu mereka berkemah di padang gurun; orang Israel berkemah di sana di depan gunung itu.'),(2029,2,19,3,'Lalu naiklah Musa menghadap Allah, dan TUHAN berseru dari gunung itu kepadanya: \"Beginilah kaukatakan kepada keturunan Yakub dan kauberitakan kepada orang Israel:'),(2030,2,19,4,'Kamu sendiri telah melihat apa yang Kulakukan kepada orang Mesir, dan bagaimana Aku telah mendukung kamu di atas sayap rajawali dan membawa kamu kepada-Ku.'),(2031,2,19,5,'Jadi sekarang, jika kamu sungguh-sungguh mendengarkan firman-Ku dan berpegang pada perjanjian-Ku, maka kamu akan menjadi harta kesayangan-Ku sendiri dari antara segala bangsa, sebab Akulah yang empunya seluruh bumi.'),(2032,2,19,6,'Kamu akan menjadi bagi-Ku kerajaan imam dan bangsa yang kudus. Inilah semuanya firman yang harus kaukatakan kepada orang Israel.\"'),(2033,2,19,7,'Lalu datanglah Musa dan memanggil para tua-tua bangsa itu dan membawa ke depan mereka segala firman yang diperintahkan TUHAN kepadanya.'),(2034,2,19,8,'Seluruh bangsa itu menjawab bersama-sama: \"Segala yang difirmankan TUHAN akan kami lakukan.\" Lalu Musapun menyampaikan jawab bangsa itu kepada TUHAN.'),(2035,2,19,9,'Berfirmanlah TUHAN kepada Musa: \"Sesungguhnya Aku akan datang kepadamu dalam awan yang tebal, dengan maksud supaya dapat didengar oleh bangsa itu apabila Aku berbicara dengan engkau, dan juga supaya mereka senantiasa percaya kepadamu.\" Lalu Musa memberitahukan perkataan bangsa itu kepada TUHAN.'),(2036,2,19,10,'Berfirmanlah TUHAN kepada Musa: \"Pergilah kepada bangsa itu; suruhlah mereka menguduskan diri pada hari ini dan besok, dan mereka harus mencuci pakaiannya.'),(2037,2,19,11,'Menjelang hari ketiga mereka harus bersiap, sebab pada hari ketiga TUHAN akan turun di depan mata seluruh bangsa itu di gunung Sinai.'),(2038,2,19,12,'Sebab itu haruslah engkau memasang batas bagi bangsa itu berkeliling sambil berkata: Jagalah baik-baik, jangan kamu mendaki gunung itu atau kena kepada kakinya, sebab siapapun yang kena kepada gunung itu, pastilah ia dihukum mati.'),(2039,2,19,13,'Tangan seorangpun tidak boleh merabanya, sebab pastilah ia dilempari dengan batu atau dipanahi sampai mati; baik binatang baik manusia, ia tidak akan dibiarkan hidup. Hanya apabila sangkakala berbunyi panjang, barulah mereka boleh mendaki gunung itu.\"'),(2040,2,19,14,'Lalu turunlah Musa dari gunung mendapatkan bangsa itu; disuruhnyalah bangsa itu menguduskan diri dan merekapun mencuci pakaiannya.'),(2041,2,19,15,'Maka kata Musa kepada bangsa itu: \"Bersiaplah menjelang hari yang ketiga, dan janganlah kamu bersetubuh dengan perempuan.\"'),(2042,2,19,16,'Dan terjadilah pada hari ketiga, pada waktu terbit fajar, ada guruh dan kilat dan awan padat di atas gunung dan bunyi sangkakala yang sangat keras, sehingga gemetarlah seluruh bangsa yang ada di perkemahan.'),(2043,2,19,17,'Lalu Musa membawa bangsa itu keluar dari perkemahan untuk menjumpai Allah dan berdirilah mereka pada kaki gunung.'),(2044,2,19,18,'Gunung Sinai ditutupi seluruhnya dengan asap, karena TUHAN turun ke atasnya dalam api; asapnya membubung seperti asap dari dapur, dan seluruh gunung itu gemetar sangat.'),(2045,2,19,19,'Bunyi sangkakala kian lama kian keras. Berbicaralah Musa, lalu Allah menjawabnya dalam guruh.'),(2046,2,19,20,'Lalu turunlah TUHAN ke atas gunung Sinai, ke atas puncak gunung itu, maka TUHAN memanggil Musa ke puncak gunung itu, dan naiklah Musa ke atas.'),(2047,2,19,21,'Kemudian TUHAN berfirman kepada Musa: \"Turunlah, peringatkanlah kepada bangsa itu, supaya mereka jangan menembus mendapatkan TUHAN hendak melihat-lihat; sebab tentulah banyak dari mereka akan binasa.'),(2048,2,19,22,'Juga para imam yang datang mendekat kepada TUHAN haruslah menguduskan dirinya, supaya TUHAN jangan melanda mereka.\"'),(2049,2,19,23,'Lalu berkatalah Musa kepada TUHAN: \"Tidak akan mungkin bangsa itu mendaki gunung Sinai ini, sebab Engkau sendiri telah memperingatkan kepada kami, demikian: Pasanglah batas sekeliling gunung itu dan nyatakanlah itu kudus.\"'),(2050,2,19,24,'Lalu TUHAN berfirman kepadanya: \"Pergilah, turunlah, kemudian naiklah pula, engkau beserta Harun; tetapi para imam dan rakyat tidak boleh menembus untuk mendaki menghadap TUHAN, supaya mereka jangan dilanda-Nya.\"'),(2051,2,19,25,'Lalu turunlah Musa mendapatkan bangsa itu dan menyatakan hal itu kepada mereka.'),(2052,2,20,1,'Lalu Allah mengucapkan segala firman ini:'),(2053,2,20,2,'Akulah TUHAN, Allahmu, yang membawa engkau keluar dari tanah Mesir, dari tempat perbudakan.'),(2054,2,20,3,'Jangan ada padamu allah lain di hadapan-Ku.'),(2055,2,20,4,'Jangan membuat bagimu patung yang menyerupai apapun yang ada di langit di atas, atau yang ada di bumi di bawah, atau yang ada di dalam air di bawah bumi.'),(2056,2,20,5,'Jangan sujud menyembah kepadanya atau beribadah kepadanya, sebab Aku, TUHAN, Allahmu, adalah Allah yang cemburu, yang membalaskan kesalahan bapa kepada anak-anaknya, kepada keturunan yang ketiga dan keempat dari orang-orang yang membenci Aku,'),(2057,2,20,6,'tetapi Aku menunjukkan kasih setia kepada beribu-ribu orang, yaitu mereka yang mengasihi Aku dan yang berpegang pada perintah-perintah-Ku.'),(2058,2,20,7,'Jangan menyebut nama TUHAN, Allahmu, dengan sembarangan, sebab TUHAN akan memandang bersalah orang yang menyebut nama-Nya dengan sembarangan.'),(2059,2,20,8,'Ingatlah dan kuduskanlah hari Sabat:'),(2060,2,20,9,'enam hari lamanya engkau akan bekerja dan melakukan segala pekerjaanmu,'),(2061,2,20,10,'tetapi hari ketujuh adalah hari Sabat TUHAN, Allahmu; maka jangan melakukan sesuatu pekerjaan, engkau atau anakmu laki-laki, atau anakmu perempuan, atau hambamu laki-laki, atau hambamu perempuan, atau hewanmu atau orang asing yang di tempat kediamanmu.'),(2062,2,20,11,'Sebab enam hari lamanya TUHAN menjadikan langit dan bumi, laut dan segala isinya, dan Ia berhenti pada hari ketujuh; itulah sebabnya TUHAN memberkati hari Sabat dan menguduskannya.'),(2063,2,20,12,'Hormatilah ayahmu dan ibumu, supaya lanjut umurmu di tanah yang diberikan TUHAN, Allahmu, kepadamu.'),(2064,2,20,13,'Jangan membunuh.'),(2065,2,20,14,'Jangan berzinah.'),(2066,2,20,15,'Jangan mencuri.'),(2067,2,20,16,'Jangan mengucapkan saksi dusta tentang sesamamu.'),(2068,2,20,17,'Jangan mengingini rumah sesamamu; jangan mengingini isterinya, atau hambanya laki-laki, atau hambanya perempuan, atau lembunya atau keledainya, atau apapun yang dipunyai sesamamu.\"'),(2069,2,20,18,'Seluruh bangsa itu menyaksikan guruh mengguntur, kilat sabung-menyabung, sangkakala berbunyi dan gunung berasap. Maka bangsa itu takut dan gemetar dan mereka berdiri jauh-jauh.'),(2070,2,20,19,'Mereka berkata kepada Musa: \"Engkaulah berbicara dengan kami, maka kami akan mendengarkan; tetapi janganlah Allah berbicara dengan kami, nanti kami mati.\"'),(2071,2,20,20,'Tetapi Musa berkata kepada bangsa itu: \"Janganlah takut, sebab Allah telah datang dengan maksud untuk mencoba kamu dan dengan maksud supaya takut akan Dia ada padamu, agar kamu jangan berbuat dosa.\"'),(2072,2,20,21,'Adapun bangsa itu berdiri jauh-jauh, tetapi Musa pergi mendekati embun yang kelam di mana Allah ada.'),(2073,2,20,22,'Lalu berfirmanlah TUHAN kepada Musa: \"Beginilah kaukatakan kepada orang Israel: Kamu sendiri telah menyaksikan, bahwa Aku berbicara dengan kamu dari langit.'),(2074,2,20,23,'Janganlah kamu membuat di samping-Ku allah perak, juga allah emas janganlah kamu buat bagimu.'),(2075,2,20,24,'Kaubuatlah bagi-Ku mezbah dari tanah dan persembahkanlah di atasnya korban bakaranmu dan korban keselamatanmu, kambing dombamu dan lembu sapimu. Pada setiap tempat yang Kutentukan menjadi tempat peringatan bagi nama-Ku, Aku akan datang kepadamu dan memberkati engkau.'),(2076,2,20,25,'Tetapi jika engkau membuat bagi-Ku mezbah dari batu, maka jangan engkau mendirikannya dari batu pahat, sebab apabila engkau mengerjakannya dengan beliung, maka engkau melanggar kekudusannya.'),(2077,2,20,26,'Juga jangan engkau naik tangga ke atas ke mezbah-Ku, supaya auratmu jangan kelihatan di atasnya.\"'),(2078,2,21,1,'Inilah peraturan-peraturan yang harus kaubawa ke depan mereka.'),(2079,2,21,2,'Apabila engkau membeli seorang budak Ibrani, maka haruslah ia bekerja padamu enam tahun lamanya, tetapi pada tahun yang ketujuh ia diizinkan keluar sebagai orang merdeka, dengan tidak membayar tebusan apa-apa.'),(2080,2,21,3,'Jika ia datang seorang diri saja, maka keluarpun ia seorang diri; jika ia mempunyai isteri, maka isterinya itu diizinkan keluar bersama-sama dengan dia.'),(2081,2,21,4,'Jika tuannya memberikan kepadanya seorang isteri dan perempuan itu melahirkan anak-anak lelaki atau perempuan, maka perempuan itu dengan anak-anaknya tetap menjadi kepunyaan tuannya, dan budak laki-laki itu harus keluar seorang diri.'),(2082,2,21,5,'Tetapi jika budak itu dengan sungguh-sungguh berkata: Aku cinta kepada tuanku, kepada isteriku dan kepada anak-anakku, aku tidak mau keluar sebagai orang merdeka,'),(2083,2,21,6,'maka haruslah tuannya itu membawanya menghadap Allah, lalu membawanya ke pintu atau ke tiang pintu, dan tuannya itu menusuk telinganya dengan penusuk, dan budak itu bekerja pada tuannya untuk seumur hidup.'),(2084,2,21,7,'Apabila ada seorang menjual anaknya yang perempuan sebagai budak, maka perempuan itu tidak boleh keluar seperti cara budak-budak lelaki keluar.'),(2085,2,21,8,'Jika perempuan itu tidak disukai tuannya, yang telah menyediakannya bagi dirinya sendiri, maka haruslah tuannya itu mengizinkan ia ditebus; tuannya itu tidak berhak untuk menjualnya kepada bangsa asing, karena ia memungkiri janjinya kepada perempuan itu.'),(2086,2,21,9,'Jika tuannya itu menyediakannya bagi anaknya laki-laki, maka haruslah tuannya itu memperlakukannya seperti anak-anak perempuan berhak diperlakukan.'),(2087,2,21,10,'Jika tuannya itu mengambil perempuan lain, ia tidak boleh mengurangi makanan perempuan itu, pakaiannya dan persetubuhan dengan dia.'),(2088,2,21,11,'Jika tuannya itu tidak melakukan ketiga hal itu kepadanya, maka perempuan itu harus diizinkan keluar, dengan tidak membayar uang tebusan apa-apa.\"'),(2089,2,21,12,'Siapa yang memukul seseorang, sehingga mati, pastilah ia dihukum mati.'),(2090,2,21,13,'Tetapi jika pembunuhan itu tidak disengaja, melainkan tangannya ditentukan Allah melakukan itu, maka Aku akan menunjukkan bagimu suatu tempat, ke mana ia dapat lari.'),(2091,2,21,14,'Tetapi apabila seseorang berlaku angkara terhadap sesamanya, hingga ia membunuhnya dengan tipu daya, maka engkau harus mengambil orang itu dari mezbah-Ku, supaya ia mati dibunuh.'),(2092,2,21,15,'Siapa yang memukul ayahnya atau ibunya, pastilah ia dihukum mati.'),(2093,2,21,16,'Siapa yang menculik seorang manusia, baik ia telah menjualnya, baik orang itu masih terdapat padanya, ia pasti dihukum mati.'),(2094,2,21,17,'Siapa yang mengutuki ayahnya atau ibunya, ia pasti dihukum mati.'),(2095,2,21,18,'Apabila ada orang bertengkar dan yang seorang memukul yang lain dengan batu atau dengan tinjunya, sehingga yang lain itu memang tidak mati, tetapi terpaksa berbaring di tempat tidur,'),(2096,2,21,19,'maka orang yang memukul itu bebas dari hukuman, jika yang lain itu dapat bangkit lagi dan dapat berjalan di luar dengan memakai tongkat; hanya ia harus membayar kerugian orang yang lain itu, karena terpaksa menganggur, dan menanggung pengobatannya sampai sembuh.'),(2097,2,21,20,'Apabila seseorang memukul budaknya laki-laki atau perempuan dengan tongkat, sehingga mati karena pukulan itu, pastilah budak itu dibalaskan.'),(2098,2,21,21,'Hanya jika budak itu masih hidup sehari dua, maka janganlah dituntut belanya, sebab budak itu adalah miliknya sendiri.'),(2099,2,21,22,'Apabila ada orang berkelahi dan seorang dari mereka tertumbuk kepada seorang perempuan yang sedang mengandung, sehingga keguguran kandungan, tetapi tidak mendapat kecelakaan yang membawa maut, maka pastilah ia didenda sebanyak yang dikenakan oleh suami perempuan itu kepadanya, dan ia harus membayarnya menurut putusan hakim.'),(2100,2,21,23,'Tetapi jika perempuan itu mendapat kecelakaan yang membawa maut, maka engkau harus memberikan nyawa ganti nyawa,'),(2101,2,21,24,'mata ganti mata, gigi ganti gigi, tangan ganti tangan, kaki ganti kaki,'),(2102,2,21,25,'lecur ganti lecur, luka ganti luka, bengkak ganti bengkak.'),(2103,2,21,26,'Apabila seseorang memukul mata budaknya laki-laki atau mata budaknya perempuan dan merusakkannya, maka ia harus melepaskan budak itu sebagai orang merdeka pengganti kerusakan matanya itu.'),(2104,2,21,27,'Dan jika ia menumbuk sampai tanggal gigi budaknya laki-laki atau gigi budaknya perempuan, maka ia harus melepaskan budak itu sebagai orang merdeka pengganti kehilangan giginya itu.'),(2105,2,21,28,'Apabila seekor lembu menanduk seorang laki-laki atau perempuan, sehingga mati, maka pastilah lembu itu dilempari mati dengan batu dan dagingnya tidak boleh dimakan, tetapi pemilik lembu itu bebas dari hukuman.'),(2106,2,21,29,'Tetapi jika lembu itu sejak dahulu telah sering menanduk dan pemiliknya telah diperingatkan, tetapi tidak mau menjaganya, kemudian lembu itu menanduk mati seorang laki-laki atau perempuan, maka lembu itu harus dilempari mati dengan batu, tetapi pemiliknyapun harus dihukum mati.'),(2107,2,21,30,'Jika dibebankan kepadanya uang pendamaian, maka haruslah dibayarnya segala yang dibebankan kepadanya itu sebagai tebusan nyawanya.'),(2108,2,21,31,'Kalau ditanduknya seorang anak laki-laki atau perempuan, maka pemiliknya harus diperlakukan menurut peraturan itu juga.'),(2109,2,21,32,'Tetapi jika lembu itu menanduk seorang budak laki-laki atau perempuan, maka pemiliknya harus membayar tiga puluh syikal perak kepada tuan budak itu, dan lembu itu harus dilempari mati dengan batu.'),(2110,2,21,33,'Apabila seseorang membuka sumur, atau apabila seseorang menggali sumur, dengan tidak menutupnya, dan seekor lembu atau keledai jatuh ke dalamnya,'),(2111,2,21,34,'maka pemilik sumur itu harus membayar ganti kerugian: ia harus mengganti harga binatang itu dengan uang kepada pemiliknya, tetapi binatang yang mati itu menjadi kepunyaannya.'),(2112,2,21,35,'Apabila lembu seseorang menanduk lembu orang lain, sehingga mati, maka lembu yang hidup itu harus dijual, uangnya dibagi dan binatang yang mati itupun harus dibagi juga.'),(2113,2,21,36,'Tetapi jikalau lembu itu terkenal telah sering menanduk sejak dahulu, dan walaupun demikian pemiliknya tidak mau menjaganya, maka ia harus membayar ganti kerugian sepenuhnya: lembu ganti lembu, tetapi binatang yang mati itu menjadi kepunyaannya.\"'),(2114,2,22,1,'Apabila seseorang mencuri seekor lembu atau seekor domba dan membantainya atau menjualnya, maka ia harus membayar gantinya, yakni lima ekor lembu ganti lembu itu dan empat ekor domba ganti domba itu.'),(2115,2,22,2,'Jika seorang pencuri kedapatan waktu membongkar, dan ia dipukul orang sehingga mati, maka si pemukul tidak berhutang darah;'),(2116,2,22,3,'tetapi jika pembunuhan itu terjadi setelah matahari terbit, maka ia berhutang darah. Pencuri itu harus membayar ganti kerugian sepenuhnya; jika ia orang yang tak punya, ia harus dijual ganti apa yang dicurinya itu.'),(2117,2,22,4,'Jika yang dicurinya itu masih terdapat padanya dalam keadaan hidup, baik lembu, keledai atau domba, maka ia harus membayar ganti kerugian dua kali lipat.'),(2118,2,22,5,'Apabila seseorang menggembalakan ternaknya di ladangnya atau di kebun anggurnya dan ternak itu dibiarkannya berjalan lepas, sehingga makan habis ladang orang lain, maka ia harus memberikan hasil yang terbaik dari ladangnya sendiri atau hasil yang terbaik dari kebun anggurnya sebagai ganti kerugian.'),(2119,2,22,6,'Apabila ada api dinyalakan dan api itu menjilat semak duri, tetapi tumpukan gandum atau gandum yang belum dituai atau seluruh ladang itu ikut juga dimakan api, maka orang yang menyebabkan kebakaran itu harus membayar ganti kerugian sepenuhnya.'),(2120,2,22,7,'Apabila seseorang menitipkan kepada temannya uang atau barang, dan itu dicuri dari rumah orang itu, maka jika pencuri itu terdapat, ia harus membayar ganti kerugian dua kali lipat.'),(2121,2,22,8,'Jika pencuri itu tidak terdapat, maka tuan rumah harus pergi menghadap Allah untuk bersumpah, bahwa ia tidak mengulurkan tangannya mengambil harta kepunyaan temannya.'),(2122,2,22,9,'Dalam tiap-tiap perkara pertengkaran harta, baik tentang seekor lembu, tentang seekor keledai, tentang seekor domba, tentang sehelai pakaian, baik tentang barang apapun yang kehilangan, kalau seorang mengatakan: Inilah kepunyaanku--maka perkara kedua orang itu harus dibawa ke hadapan Allah. Siapa yang dipersalahkan oleh Allah haruslah membayar kepada temannya ganti kerugian dua kali lipat.'),(2123,2,22,10,'Apabila seseorang menitipkan kepada temannya seekor keledai atau lembu atau seekor domba atau binatang apapun dan binatang itu mati, atau patah kakinya atau dihalau orang dengan kekerasan, dengan tidak ada orang yang melihatnya,'),(2124,2,22,11,'maka sumpah di hadapan TUHAN harus menentukan di antara kedua orang itu, apakah ia tidak mengulurkan tangannya mengambil harta kepunyaan temannya, dan pemilik harus menerima sumpah itu, dan yang lain itu tidak usah membayar ganti kerugian.'),(2125,2,22,12,'Tetapi jika binatang itu benar-benar dicuri orang dari padanya, maka ia harus membayar ganti kerugian kepada pemilik.'),(2126,2,22,13,'Jika binatang itu benar-benar diterkam oleh binatang buas, maka ia harus membawanya sebagai bukti. Tidak usah ia membayar ganti binatang yang diterkam itu.'),(2127,2,22,14,'Apabila seseorang meminjam seekor binatang dari temannya, dan binatang itu patah kakinya atau mati, ketika pemiliknya tidak ada di situ, maka ia harus membayar ganti kerugian sepenuhnya.'),(2128,2,22,15,'Tetapi jika pemiliknya ada di situ, maka tidak usahlah ia membayar ganti kerugian. Jika binatang itu disewa, maka kerugian itu telah termasuk dalam sewa.'),(2129,2,22,16,'Apabila seseorang membujuk seorang anak perawan yang belum bertunangan, dan tidur dengan dia, maka haruslah ia mengambilnya menjadi isterinya dengan membayar mas kawin.'),(2130,2,22,17,'Jika ayah perempuan itu sungguh-sungguh menolak memberikannya kepadanya, maka ia harus juga membayar perak itu sepenuhnya, sebanyak mas kawin anak perawan.\"'),(2131,2,22,18,'Seorang ahli sihir perempuan janganlah engkau biarkan hidup.'),(2132,2,22,19,'Siapapun yang tidur dengan seekor binatang, pastilah ia dihukum mati.'),(2133,2,22,20,'Siapa yang mempersembahkan korban kepada allah kecuali kepada TUHAN sendiri, haruslah ia ditumpas.\"'),(2134,2,22,21,'Janganlah kautindas atau kautekan seorang orang asing, sebab kamupun dahulu adalah orang asing di tanah Mesir.'),(2135,2,22,22,'Seseorang janda atau anak yatim janganlah kamu tindas.'),(2136,2,22,23,'Jika engkau memang menindas mereka ini, tentulah Aku akan mendengarkan seruan mereka, jika mereka berseru-seru kepada-Ku dengan nyaring.'),(2137,2,22,24,'Maka murka-Ku akan bangkit dan Aku akan membunuh kamu dengan pedang, sehingga isteri-isterimu menjadi janda dan anak-anakmu menjadi yatim.'),(2138,2,22,25,'Jika engkau meminjamkan uang kepada salah seorang dari umat-Ku, orang yang miskin di antaramu, maka janganlah engkau berlaku sebagai seorang penagih hutang terhadap dia: janganlah kamu bebankan bunga uang kepadanya.'),(2139,2,22,26,'Jika engkau sampai mengambil jubah temanmu sebagai gadai, maka haruslah engkau mengembalikannya kepadanya sebelum matahari terbenam,'),(2140,2,22,27,'sebab hanya itu saja penutup tubuhnya, itulah pemalut kulitnya--pakai apakah ia pergi tidur? Maka apabila ia berseru-seru kepada-Ku, Aku akan mendengarkannya, sebab Aku ini pengasih.\"'),(2141,2,22,28,'Janganlah engkau mengutuki Allah dan janganlah engkau menyumpahi seorang pemuka di tengah-tengah bangsamu.'),(2142,2,22,29,'Janganlah lalai mempersembahkan hasil gandummu dan hasil anggurmu. Yang sulung dari anak-anakmu lelaki haruslah kaupersembahkan kepada-Ku.'),(2143,2,22,30,'Demikian juga harus kauperbuat dengan lembu sapimu dan dengan kambing dombamu: tujuh hari lamanya anak-anak binatang itu harus tinggal pada induknya, tetapi pada hari yang kedelapan haruslah kaupersembahkan binatang-binatang itu kepada-Ku.'),(2144,2,22,31,'Haruslah kamu menjadi orang-orang kudus bagi-Ku: daging ternak yang diterkam di padang oleh binatang buas, janganlah kamu makan, tetapi haruslah kamu lemparkan kepada anjing.\"'),(2145,2,23,1,'Janganlah engkau menyebarkan kabar bohong; janganlah engkau membantu orang yang bersalah dengan menjadi saksi yang tidak benar.'),(2146,2,23,2,'Janganlah engkau turut-turut kebanyakan orang melakukan kejahatan, dan dalam memberikan kesaksian mengenai sesuatu perkara janganlah engkau turut-turut kebanyakan orang membelokkan hukum.'),(2147,2,23,3,'Juga janganlah memihak kepada orang miskin dalam perkaranya.'),(2148,2,23,4,'Apabila engkau melihat lembu musuhmu atau keledainya yang sesat, maka segeralah kaukembalikan binatang itu.'),(2149,2,23,5,'Apabila engkau melihat rebah keledai musuhmu karena berat bebannya, maka janganlah engkau enggan menolongnya. Haruslah engkau rela menolong dia dengan membongkar muatan keledainya.'),(2150,2,23,6,'Janganlah engkau memperkosa hak orang miskin di antaramu dalam perkaranya.'),(2151,2,23,7,'Haruslah kaujauhkan dirimu dari perkara dusta. Orang yang tidak bersalah dan orang yang benar tidak boleh kaubunuh, sebab Aku tidak akan membenarkan orang yang bersalah.'),(2152,2,23,8,'Suap janganlah kauterima, sebab suap membuat buta mata orang-orang yang melihat dan memutarbalikkan perkara orang-orang yang benar.'),(2153,2,23,9,'Orang asing janganlah kamu tekan, karena kamu sendiri telah mengenal keadaan jiwa orang asing, sebab kamupun dahulu adalah orang asing di tanah Mesir.'),(2154,2,23,10,'Enam tahunlah lamanya engkau menabur di tanahmu dan mengumpulkan hasilnya,'),(2155,2,23,11,'tetapi pada tahun ketujuh haruslah engkau membiarkannya dan meninggalkannya begitu saja, supaya orang miskin di antara bangsamu dapat makan, dan apa yang ditinggalkan mereka haruslah dibiarkan dimakan binatang hutan. Demikian juga kaulakukan dengan kebun anggurmu dan kebun zaitunmu.'),(2156,2,23,12,'Enam harilah lamanya engkau melakukan pekerjaanmu, tetapi pada hari ketujuh haruslah engkau berhenti, supaya lembu dan keledaimu tidak bekerja dan supaya anak budakmu perempuan dan orang asing melepaskan lelah.'),(2157,2,23,13,'Dalam segala hal yang Kufirmankan kepadamu haruslah kamu berawas-awas; nama allah lain janganlah kamu panggil, janganlah nama itu kedengaran dari mulutmu.\"'),(2158,2,23,14,'Tiga kali setahun haruslah engkau mengadakan perayaan bagi-Ku.'),(2159,2,23,15,'Hari raya Roti Tidak Beragi haruslah kaupelihara; tujuh hari lamanya engkau harus makan roti yang tidak beragi, seperti yang telah Kuperintahkan kepadamu, pada waktu yang ditetapkan dalam bulan Abib, sebab dalam bulan itulah engkau keluar dari Mesir, tetapi janganlah orang menghadap ke hadirat-Ku dengan tangan hampa.'),(2160,2,23,16,'Kaupeliharalah juga hari raya menuai, yakni menuai buah bungaran dari hasil usahamu menabur di ladang; demikian juga hari raya pengumpulan hasil pada akhir tahun, apabila engkau mengumpulkan hasil usahamu dari ladang.'),(2161,2,23,17,'Tiga kali setahun semua orangmu yang laki-laki harus menghadap ke hadirat Tuhanmu TUHAN.'),(2162,2,23,18,'Janganlah kaupersembahkan darah korban sembelihan yang kepada-Ku beserta sesuatu yang beragi, dan janganlah lemak korban hari raya-Ku bermalam sampai pagi.'),(2163,2,23,19,'Yang terbaik dari buah bungaran hasil tanahmu haruslah kaubawa ke dalam rumah TUHAN, Allahmu. Janganlah kaumasak anak kambing dalam susu induknya.\"'),(2164,2,23,20,'Sesungguhnya Aku mengutus seorang malaikat berjalan di depanmu, untuk melindungi engkau di jalan dan untuk membawa engkau ke tempat yang telah Kusediakan.'),(2165,2,23,21,'Jagalah dirimu di hadapannya dan dengarkanlah perkataannya, janganlah engkau mendurhaka kepadanya, sebab pelanggaranmu tidak akan diampuninya, sebab nama-Ku ada di dalam dia.'),(2166,2,23,22,'Tetapi jika engkau sungguh-sungguh mendengarkan perkataannya, dan melakukan segala yang Kufirmankan, maka Aku akan memusuhi musuhmu, dan melawan lawanmu.'),(2167,2,23,23,'Sebab malaikat-Ku akan berjalan di depanmu dan membawa engkau kepada orang Amori, orang Het, orang Feris, orang Kanaan, orang Hewi dan orang Yebus, dan Aku akan melenyapkan mereka.'),(2168,2,23,24,'Janganlah engkau sujud menyembah kepada allah mereka atau beribadah kepadanya, dan janganlah engkau meniru perbuatan mereka, tetapi haruslah engkau memusnahkan sama sekali patung-patung berhala buatan mereka, dan tugu-tugu berhala mereka haruslah kauremukkan sama sekali.'),(2169,2,23,25,'Tetapi kamu harus beribadah kepada TUHAN, Allahmu; maka Ia akan memberkati roti makananmu dan air minumanmu dan Aku akan menjauhkan penyakit dari tengah-tengahmu.'),(2170,2,23,26,'Tidak akan ada di negerimu perempuan yang keguguran atau mandul. Aku akan menggenapkan tahun umurmu.'),(2171,2,23,27,'Kengerian terhadap Aku akan Kukirimkan mendahului engkau: Aku akan mengacaukan semua orang yang kaudatangi, dan Aku akan membuat semua musuhmu lari membelakangi engkau.'),(2172,2,23,28,'Lagi Aku akan melepaskan tabuhan mendahului engkau, sehingga binatang-binatang itu menghalau orang Hewi, orang Kanaan dan orang Het itu dari depanmu.'),(2173,2,23,29,'Aku tidak akan menghalau mereka dari depanmu dalam satu tahun, supaya negeri itu jangan menjadi sepi, dan segala binatang hutan jangan bertambah banyak melebihi engkau.'),(2174,2,23,30,'Sedikit demi sedikit Aku akan menghalau mereka dari depanmu, sampai engkau beranak cucu sedemikian, hingga engkau dapat memiliki negeri itu.'),(2175,2,23,31,'Aku akan menentukan batas daerahmu dari Laut Teberau sampai Laut Filistin dan dari padang gurun sampai sungai Efrat, sebab Aku akan menyerahkan penduduk negeri itu ke dalam tanganmu, sehingga engkau menghalau mereka dari depanmu.'),(2176,2,23,32,'Janganlah mengadakan perjanjian dengan mereka ataupun dengan allah mereka.'),(2177,2,23,33,'Mereka tidak akan tetap diam di negerimu, supaya mereka jangan membuat engkau berdosa kepada-Ku, dengan beribadah kepada allah mereka, sebab tentulah hal itu menjadi jerat bagimu.\"'),(2178,2,24,1,'Berfirmanlah Ia kepada Musa: \"Naiklah menghadap TUHAN, engkau dan Harun, Nadab dan Abihu dan tujuh puluh orang dari para tua-tua Israel dan sujudlah kamu menyembah dari jauh.'),(2179,2,24,2,'Hanya Musa sendirilah yang mendekat kepada TUHAN, tetapi mereka itu tidak boleh mendekat, dan bangsa itu tidak boleh naik bersama-sama dengan dia.\"'),(2180,2,24,3,'Lalu datanglah Musa dan memberitahukan kepada bangsa itu segala firman TUHAN dan segala peraturan itu, maka seluruh bangsa itu menjawab serentak: \"Segala firman yang telah diucapkan TUHAN itu, akan kami lakukan.\"'),(2181,2,24,4,'Lalu Musa menuliskan segala firman TUHAN itu. Keesokan harinya pagi-pagi didirikannyalah mezbah di kaki gunung itu, dengan dua belas tugu sesuai dengan kedua belas suku Israel.'),(2182,2,24,5,'Kemudian disuruhnyalah orang-orang muda dari bangsa Israel, maka mereka mempersembahkan korban bakaran dan menyembelih lembu-lembu jantan sebagai korban keselamatan kepada TUHAN.'),(2183,2,24,6,'Sesudah itu Musa mengambil sebagian dari darah itu, lalu ditaruhnya ke dalam pasu, sebagian lagi dari darah itu disiramkannya pada mezbah itu.'),(2184,2,24,7,'Diambilnyalah kitab perjanjian itu, lalu dibacakannya dengan didengar oleh bangsa itu dan mereka berkata: \"Segala firman TUHAN akan kami lakukan dan akan kami dengarkan.\"'),(2185,2,24,8,'Kemudian Musa mengambil darah itu dan menyiramkannya pada bangsa itu serta berkata: \"Inilah darah perjanjian yang diadakan TUHAN dengan kamu, berdasarkan segala firman ini.\"'),(2186,2,24,9,'Dan naiklah Musa dengan Harun, Nadab dan Abihu dan tujuh puluh orang dari para tua-tua Israel.'),(2187,2,24,10,'Lalu mereka melihat Allah Israel; kaki-Nya berjejak pada sesuatu yang buatannya seperti lantai dari batu nilam dan yang terangnya seperti langit yang cerah.'),(2188,2,24,11,'Tetapi kepada pemuka-pemuka orang Israel itu tidaklah diulurkan-Nya tangan-Nya; mereka memandang Allah, lalu makan dan minum.'),(2189,2,24,12,'TUHAN berfirman kepada Musa: \"Naiklah menghadap Aku, ke atas gunung, dan tinggallah di sana, maka Aku akan memberikan kepadamu loh batu, yakni hukum dan perintah, yang telah Kutuliskan untuk diajarkan kepada mereka.\"'),(2190,2,24,13,'Lalu bangunlah Musa dengan Yosua, abdinya, maka naiklah Musa ke atas gunung Allah itu.'),(2191,2,24,14,'Tetapi kepada para tua-tua itu ia berkata: \"Tinggallah di sini menunggu kami, sampai kami kembali lagi kepadamu; bukankah Harun dan Hur ada bersama-sama dengan kamu, siapa yang ada perkaranya datanglah kepada mereka.\"'),(2192,2,24,15,'Maka Musa mendaki gunung dan awan itu menutupinya.'),(2193,2,24,16,'Kemuliaan TUHAN diam di atas gunung Sinai, dan awan itu menutupinya enam hari lamanya; pada hari ketujuh dipanggil-Nyalah Musa dari tengah-tengah awan itu.'),(2194,2,24,17,'Tampaknya kemuliaan TUHAN sebagai api yang menghanguskan di puncak gunung itu pada pemandangan orang Israel.'),(2195,2,24,18,'Masuklah Musa ke tengah-tengah awan itu dengan mendaki gunung itu. Lalu tinggallah ia di atas gunung itu empat puluh hari dan empat puluh malam lamanya.'),(2196,2,25,1,'Berfirmanlah TUHAN kepada Musa:'),(2197,2,25,2,'Katakanlah kepada orang Israel, supaya mereka memungut bagi-Ku persembahan khusus; dari setiap orang yang terdorong hatinya, haruslah kamu pungut persembahan khusus kepada-Ku itu.'),(2198,2,25,3,'Inilah persembahan khusus yang harus kamu pungut dari mereka: emas, perak, tembaga;'),(2199,2,25,4,'kain ungu tua, kain ungu muda, kain kirmizi, lenan halus, bulu kambing;'),(2200,2,25,5,'kulit domba jantan yang diwarnai merah, kulit lumba-lumba dan kayu penaga;'),(2201,2,25,6,'minyak untuk lampu, rempah-rempah untuk minyak urapan dan untuk ukupan dari wangi-wangian,'),(2202,2,25,7,'permata krisopras dan permata tatahan untuk baju efod dan untuk tutup dada.'),(2203,2,25,8,'Dan mereka harus membuat tempat kudus bagi-Ku, supaya Aku akan diam di tengah-tengah mereka.'),(2204,2,25,9,'Menurut segala apa yang Kutunjukkan kepadamu sebagai contoh Kemah Suci dan sebagai contoh segala perabotannya, demikianlah harus kamu membuatnya.\"'),(2205,2,25,10,'Haruslah mereka membuat tabut dari kayu penaga, dua setengah hasta panjangnya, satu setengah hasta lebarnya dan satu setengah hasta tingginya.'),(2206,2,25,11,'Haruslah engkau menyalutnya dengan emas murni; dari dalam dan dari luar engkau harus menyalutnya dan di atasnya harus kaubuat bingkai emas sekelilingnya.'),(2207,2,25,12,'Haruslah engkau menuang empat gelang emas untuk tabut itu dan pasanglah gelang itu pada keempat penjurunya, yaitu dua gelang pada rusuknya yang satu dan dua gelang pada rusuknya yang kedua.'),(2208,2,25,13,'Engkau harus membuat kayu pengusung dari kayu penaga dan menyalutnya dengan emas.'),(2209,2,25,14,'Haruslah engkau memasukkan kayu pengusung itu ke dalam gelang yang ada pada rusuk tabut itu, supaya dengan itu tabut dapat diangkut.'),(2210,2,25,15,'Kayu pengusung itu haruslah tetap tinggal dalam gelang itu, tidak boleh dicabut dari dalamnya.'),(2211,2,25,16,'Dalam tabut itu haruslah kautaruh loh hukum, yang akan Kuberikan kepadamu.'),(2212,2,25,17,'Juga engkau harus membuat tutup pendamaian dari emas murni, dua setengah hasta panjangnya dan satu setengah hasta lebarnya.'),(2213,2,25,18,'Dan haruslah kaubuat dua kerub dari emas, kaubuatlah itu dari emas tempaan, pada kedua ujung tutup pendamaian itu.'),(2214,2,25,19,'Buatlah satu kerub pada ujung sebelah sini dan satu kerub pada ujung sebelah sana; seiras dengan tutup pendamaian itu kamu buatlah kerub itu di atas kedua ujungnya.'),(2215,2,25,20,'Kerub-kerub itu harus mengembangkan kedua sayapnya ke atas, sedang sayap-sayapnya menudungi tutup pendamaian itu dan mukanya menghadap kepada masing-masing; kepada tutup pendamaian itulah harus menghadap muka kerub-kerub itu.'),(2216,2,25,21,'Haruslah kauletakkan tutup pendamaian itu di atas tabut dan dalam tabut itu engkau harus menaruh loh hukum, yang akan Kuberikan kepadamu.'),(2217,2,25,22,'Dan di sanalah Aku akan bertemu dengan engkau dan dari atas tutup pendamaian itu, dari antara kedua kerub yang di atas tabut hukum itu, Aku akan berbicara dengan engkau tentang segala sesuatu yang akan Kuperintahkan kepadamu untuk disampaikan kepada orang Israel.\"'),(2218,2,25,23,'Lagi haruslah engkau membuat meja dari kayu penaga, dua hasta panjangnya, sehasta lebarnya dan satu setengah hasta tingginya.'),(2219,2,25,24,'Haruslah engkau menyalutnya dengan emas murni dan membuat bingkai emas sekelilingnya.'),(2220,2,25,25,'Haruslah engkau membuat sekelilingnya jalur pinggir yang setapak tangan lebarnya dan kaubuatlah bingkai emas sekeliling jalur pinggirnya itu.'),(2221,2,25,26,'Haruslah engkau membuat untuk meja itu empat gelang emas dan kaupasanglah gelang-gelang itu di keempat penjurunya, pada keempat kakinya.'),(2222,2,25,27,'Gelang itu haruslah dekat ke jalur pinggirnya sebagai tempat memasukkan kayu pengusung, supaya meja itu dapat diangkut.'),(2223,2,25,28,'Haruslah engkau membuat kayu pengusung itu dari kayu penaga dan menyalutnya dengan emas, dan dengan itulah meja harus diangkut.'),(2224,2,25,29,'Haruslah engkau membuat pinggannya, cawannya, kendinya dan pialanya, yang dipakai untuk persembahan curahan; haruslah engkau membuat semuanya itu dari emas murni.'),(2225,2,25,30,'Dan haruslah engkau tetap meletakkan roti sajian di atas meja itu di hadapan-Ku.\"'),(2226,2,25,31,'Haruslah engkau membuat kandil dari emas murni; dari emas tempaan harus kandil itu dibuat, baik kakinya baik batangnya; kelopaknya--dengan tombolnya dan kembangnya--haruslah seiras dengan kandil itu.'),(2227,2,25,32,'Enam cabang harus timbul dari sisinya: tiga cabang kandil itu dari sisi yang satu dan tiga cabang dari sisi yang lain.'),(2228,2,25,33,'Tiga kelopak yang berupa bunga badam pada cabang yang satu--dengan tombol dan kembangnya--dan tiga kelopak yang serupa pada cabang yang lain--dengan tombol dan kembangnya--;demikianlah juga kaubuat keenam cabang yang timbul dari kandil itu.'),(2229,2,25,34,'Pada kandil itu sendiri harus ada empat kelopak berupa bunga badam--dengan tombolnya dan kembangnya.'),(2230,2,25,35,'Juga harus ada satu tombol di bawah sepasang cabang yang pertama, yang timbul dari kandil itu, dan satu tombol di bawah yang kedua, dan satu tombol di bawah yang ketiga; demikianlah juga kaubuat keenam cabang yang timbul dari kandil itu.'),(2231,2,25,36,'Tombol dan cabang itu harus timbul dari kandil itu, dan semuanya itu haruslah dibuat dari sepotong emas tempaan yang murni.'),(2232,2,25,37,'Haruslah kaubuat pada kandil itu tujuh lampu dan lampu-lampu itu haruslah dipasang di atas kandil itu, sehingga diterangi yang di depannya.'),(2233,2,25,38,'Sepitnya dan penadahnya haruslah dari emas murni.'),(2234,2,25,39,'Dari satu talenta emas murni haruslah dibuat kandil itu dengan segala perkakasnya itu.'),(2235,2,25,40,'Dan ingatlah, bahwa engkau membuat semuanya itu menurut contoh yang telah ditunjukkan kepadamu di atas gunung itu.\"'),(2236,2,26,1,'Kemah Suci itu haruslah kaubuat dari sepuluh tenda dari lenan halus yang dipintal benangnya dan dari kain ungu tua, kain ungu muda dan kain kirmizi; dengan ada kerubnya, buatan ahli tenun, haruslah kaubuat semuanya itu.'),(2237,2,26,2,'Panjang tiap-tiap tenda haruslah dua puluh delapan hasta dan lebar tiap-tiap tenda empat hasta: segala tenda itu harus sama ukurannya.'),(2238,2,26,3,'Lima dari tenda itu haruslah dirangkap menjadi satu, dan yang lima lagi juga harus dirangkap menjadi satu.'),(2239,2,26,4,'Pada rangkapan yang pertama, di tepi satu tenda yang di ujung, haruslah engkau membuat sosok-sosok kain ungu tua dan demikian juga di tepi satu tenda yang paling ujung pada rangkapan yang kedua.'),(2240,2,26,5,'Lima puluh sosok harus kaubuat pada tenda yang satu dan lima puluh sosok pada tenda yang di ujung pada rangkapan yang kedua, sehingga sosok-sosok itu tepat berhadapan satu sama lain.'),(2241,2,26,6,'Dan haruslah engkau membuat lima puluh kaitan emas dan menyambung tenda-tenda Kemah Suci yang satu dengan yang lain dengan memakai kaitan itu, sehingga menjadi satu.'),(2242,2,26,7,'Juga haruslah engkau membuat tenda-tenda dari bulu kambing menjadi atap kemah yang menudungi Kemah Suci, sebelas tenda harus kaubuat.'),(2243,2,26,8,'Panjang tiap-tiap tenda harus tiga puluh hasta dan lebar tiap-tiap tenda empat hasta: yang sebelas tenda itu harus sama ukurannya.'),(2244,2,26,9,'Lima dari tenda itu haruslah kausambung dengan tersendiri, dan enam dari tenda itu dengan tersendiri, dan tenda yang keenam haruslah kaulipat dua, di sebelah depan kemah itu.'),(2245,2,26,10,'Haruslah engkau membuat lima puluh sosok pada rangkapan yang pertama di tepi satu tenda yang di ujung dan lima puluh sosok di tepi satu tenda pada rangkapan yang kedua.'),(2246,2,26,11,'Haruslah engkau membuat lima puluh kaitan tembaga dan memasukkan kaitan itu ke dalam sosok-sosok dan menyambung tenda-tenda kemah itu, supaya menjadi satu.'),(2247,2,26,12,'Mengenai bagian yang berjuntai itu, yang berlebih pada tenda kemah itu, haruslah setengah dari tenda yang berlebih itu berjuntai di sebelah belakang Kemah Suci.'),(2248,2,26,13,'Sehasta di sebelah sini dan sehasta di sebelah sana pada bagian yang berlebih pada panjang tenda-tenda kemah itu haruslah berjuntai pada sisi-sisi Kemah Suci, di sebelah sini dan di sebelah sana untuk menudunginya.'),(2249,2,26,14,'Juga haruslah engkau membuat untuk kemah itu tudung dari kulit domba jantan yang diwarnai merah, dan tudung dari kulit lumba-lumba di atasnya lagi.'),(2250,2,26,15,'Haruslah engkau membuat untuk Kemah Suci papan dari kayu penaga yang berdiri tegak,'),(2251,2,26,16,'sepuluh hasta panjangnya satu papan dan satu setengah hasta lebarnya tiap-tiap papan.'),(2252,2,26,17,'Tiap-tiap papan harus ada dua pasaknya yang disengkang satu sama lain; demikianlah harus kauperbuat dengan segala papan Kemah Suci.'),(2253,2,26,18,'Haruslah engkau membuat papan-papan untuk Kemah Suci, dua puluh papan pada sebelah selatan.'),(2254,2,26,19,'Dan haruslah kaubuat empat puluh alas perak di bawah kedua puluh papan itu, dua alas di bawah satu papan untuk kedua pasaknya, dan seterusnya dua alas di bawah setiap papan untuk kedua pasaknya.'),(2255,2,26,20,'Juga untuk sisi yang kedua dari Kemah Suci, pada sebelah utara, kaubuatlah dua puluh papan'),(2256,2,26,21,'dengan empat puluh alas peraknya: dua alas di bawah satu papan dan seterusnya dua alas di bawah setiap papan.'),(2257,2,26,22,'Untuk sisi belakang Kemah Suci, pada sebelah barat, haruslah kaubuat enam papan.'),(2258,2,26,23,'Dua papan haruslah kaubuat untuk sudut Kemah Suci, di sisi belakang.'),(2259,2,26,24,'Kedua papan itu haruslah kembar pasaknya di sebelah bawah dan seperti itu juga kembar pasaknya di sebelah atas, di dekat gelang yang satu itu; demikianlah harus kedua papan itu; haruslah itu merupakan kedua sudutnya.'),(2260,2,26,25,'Jadi harus ada delapan papan dengan alas peraknya: enam belas alas; dua alas di bawah satu papan dan seterusnya dua alas di bawah setiap papan.'),(2261,2,26,26,'Juga haruslah kaubuat kayu lintang dari kayu penaga: lima untuk papan-papan pada sisi yang satu dari Kemah Suci,'),(2262,2,26,27,'lima kayu lintang untuk papan-papan pada sisi yang kedua dari Kemah Suci, dan lima kayu lintang untuk papan-papan pada sisi Kemah Suci yang merupakan sisi belakangnya, pada sebelah barat.'),(2263,2,26,28,'Dan kayu lintang yang di tengah, di tengah-tengah papan-papan itu, haruslah melintang terus dari ujung ke ujung.'),(2264,2,26,29,'Papan-papan itu haruslah kausalut dengan emas, gelang-gelang itu haruslah kaubuat dari emas sebagai tempat memasukkan kayu-kayu lintang itu, dan kayu-kayu lintang itu haruslah kausalut dengan emas.'),(2265,2,26,30,'Kemudian haruslah kaudirikan Kemah Suci sesuai dengan rancangan yang telah ditunjukkan kepadamu di atas gunung itu.'),(2266,2,26,31,'Haruslah kaubuat tabir dari kain ungu tua, dan kain ungu muda, kain kirmizi dan lenan halus yang dipintal benangnya; haruslah dibuat dengan ada kerubnya, buatan ahli tenun.'),(2267,2,26,32,'Haruslah engkau menggantungkannya pada empat tiang dari kayu penaga, yang disalut dengan emas, dengan ada kaitannya dari emas, berdasarkan empat alas perak.'),(2268,2,26,33,'Haruslah tabir itu kaugantungkan pada kaitan penyambung tenda itu dan haruslah kaubawa tabut hukum ke sana, ke belakang tabir itu, sehingga tabir itu menjadi pemisah bagimu antara tempat kudus dan tempat maha kudus.'),(2269,2,26,34,'Tutup pendamaian itu haruslah kauletakkan di atas tabut hukum di dalam tempat maha kudus.'),(2270,2,26,35,'Meja itu haruslah kautaruh di depan tabir itu, dan kandil itu berhadapan dengan meja itu pada sisi selatan dari Kemah Suci, dan meja itu haruslah kautempatkan pada sisi utara.'),(2271,2,26,36,'Juga haruslah kaubuat tirai untuk pintu kemah itu dari kain ungu tua, kain ungu muda, kain kirmizi dan lenan halus yang dipintal benangnya: tenunan yang berwarna-warna.'),(2272,2,26,37,'Haruslah kaubuat lima tiang dari kayu penaga untuk tirai itu dan kausalutlah itu dengan emas, dengan ada kaitannya dari emas, dan untuk itu haruslah kautuang lima alas dari tembaga.\"'),(2273,2,27,1,'Haruslah engkau membuat mezbah dari kayu penaga, lima hasta panjangnya dan lima hasta lebarnya, sehingga mezbah itu empat persegi, tetapi tiga hasta tingginya.'),(2274,2,27,2,'Haruslah engkau membuat tanduk-tanduknya pada keempat sudutnya; tanduk-tanduknya itu haruslah seiras dengan mezbah itu dan haruslah engkau menyalutnya dengan tembaga.'),(2275,2,27,3,'Juga harus engkau membuat kuali-kualinya tempat menaruh abunya, dan sodok-sodoknya dan bokor-bokor penyiramannya, garpu-garpunya dan perbaraan-perbaraannya; semua perkakasnya itu harus kaubuat dari tembaga.'),(2276,2,27,4,'Haruslah engkau membuat untuk itu kisi-kisi, yakni jala-jala tembaga, dan pada jala-jala itu haruslah kaubuat empat gelang tembaga pada keempat ujungnya.'),(2277,2,27,5,'Haruslah engkau memasang jala-jala itu di bawah jalur mezbah itu; mulai dari sebelah bawah, sehingga jala-jala itu sampai setengah tinggi mezbah itu.'),(2278,2,27,6,'Haruslah engkau membuat kayu-kayu pengusung untuk mezbah itu, kayu-kayu pengusung dari kayu penaga dan menyalutnya dengan tembaga.'),(2279,2,27,7,'Kayu-kayu pengusungnya itu haruslah dimasukkan ke dalam gelang-gelang itu dan kayu-kayu pengusung itu haruslah ada pada kedua rusuk mezbah itu waktu mezbah itu diangkut.'),(2280,2,27,8,'Mezbah itu harus kaubuat berongga dan dari papan, seperti yang ditunjukkan kepadamu di atas gunung itu, demikianlah harus dibuat mezbah itu.\"'),(2281,2,27,9,'Haruslah engkau membuat pelataran Kemah Suci; untuk pelataran itu pada sebelah selatan harus dibuat layar dari lenan halus yang dipintal benangnya, seratus hasta panjangnya pada sisi yang satu itu.'),(2282,2,27,10,'Tiang-tiangnya harus ada dua puluh, dan alas-alas tiang itu harus dua puluh, dari tembaga, tetapi kaitan-kaitan tiang itu dan penyambung-penyambungnya harus dari perak.'),(2283,2,27,11,'Demikian juga pada sebelah utara, pada panjangnya, harus ada layar yang seratus hasta panjangnya, tiang-tiangnya harus ada dua puluh dan alas-alas tiang itu harus dua puluh, dari tembaga, tetapi kaitan-kaitan tiang itu dan penyambung-penyambungnya harus dari perak.'),(2284,2,27,12,'Dan pada lebar pelataran itu pada sebelah barat harus ada layar yang lima puluh hasta, dengan sepuluh tiangnya dan sepuluh alas tiang itu.'),(2285,2,27,13,'Lebar pelataran itu, yaitu bagian muka pada sebelah timur harus lima puluh hasta,'),(2286,2,27,14,'yakni lima belas hasta layar untuk sisi yang satu di samping pintu gerbang itu, dengan tiga tiangnya dan tiga alas tiang itu;'),(2287,2,27,15,'dan juga untuk sisi yang kedua di samping pintu gerbang itu lima belas hasta layar, dengan tiga tiangnya dan tiga alas tiang itu;'),(2288,2,27,16,'tetapi untuk pintu gerbang pelataran itu tirai dua puluh hasta dari kain ungu tua dan kain ungu muda, kain kirmizi dan dari lenan halus yang dipintal benangnya--tenunan yang berwarna-warna--dengan empat tiangnya dan empat alas tiang itu.'),(2289,2,27,17,'Segala tiang yang mengelilingi pelataran itu haruslah dihubungkan dengan penyambung-penyambung perak, dan kaitan-kaitannya harus dari perak dan alas-alasnya dari tembaga.'),(2290,2,27,18,'Panjang pelataran itu harus seratus hasta, lebarnya lima puluh hasta dan tingginya lima hasta, dari lenan halus yang dipintal benangnya, dan alas-alasnya harus dari tembaga.'),(2291,2,27,19,'Adapun segala perabotan untuk seluruh perlengkapan Kemah Suci, dan juga segala patoknya dan segala patok pelataran: semuanya harus dari tembaga.\"'),(2292,2,27,20,'Haruslah kauperintahkan kepada orang Israel, supaya mereka membawa kepadamu minyak zaitun tumbuk yang murni untuk lampu, supaya orang dapat memasang lampu agar tetap menyala.'),(2293,2,27,21,'Di dalam Kemah Pertemuan di depan tabir yang menutupi tabut hukum, haruslah Harun dan anak-anaknya mengaturnya dari petang sampai pagi di hadapan TUHAN. Itulah suatu ketetapan yang berlaku untuk selama-lamanya bagi orang Israel turun-temurun.\"'),(2294,2,28,1,'Engkau harus menyuruh abangmu Harun bersama-sama dengan anak-anaknya datang kepadamu, dari tengah-tengah orang Israel, untuk memegang jabatan imam bagi-Ku--Harun dan anak-anak Harun, yakni Nadab, Abihu, Eleazar dan Itamar.'),(2295,2,28,2,'Haruslah engkau membuat pakaian kudus bagi Harun, abangmu, sebagai perhiasan kemuliaan.'),(2296,2,28,3,'Haruslah engkau mengatakan kepada semua orang yang ahli, yang telah Kupenuhi dengan roh keahlian, membuat pakaian Harun, untuk menguduskan dia, supaya dipegangnya jabatan imam bagi-Ku.'),(2297,2,28,4,'Inilah pakaian yang harus dibuat mereka: tutup dada, baju efod, gamis, kemeja yang ada raginya, serban dan ikat pinggang. Demikianlah mereka harus membuat pakaian kudus bagi Harun, abangmu, dan bagi anak-anaknya, supaya ia memegang jabatan imam bagi-Ku.'),(2298,2,28,5,'Untuk itu haruslah mereka mengambil emas, kain ungu tua dan kain ungu muda, kain kirmizi dan lenan halus.'),(2299,2,28,6,'Baju efod itu harus dibuat mereka dari emas, kain ungu tua dan kain ungu muda, kain kirmizi dan lenan halus yang dipintal benangnya: buatan seorang ahli.'),(2300,2,28,7,'Haruslah ada pada baju efod itu dua tutup bahu yang disambung kepadanya, pada kedua ujungnyalah harus baju efod itu disambung.'),(2301,2,28,8,'Sabuk pengikat yang ada pada baju efod itu haruslah sama buatannya dan seiras dengan baju efod itu, yakni dari emas, kain ungu tua, kain ungu muda, kain kirmizi dan lenan halus yang dipintal benangnya.'),(2302,2,28,9,'Haruslah kauambil dua permata krisopras dan mengukirkan nama para anak Israel pada permata itu,'),(2303,2,28,10,'enam dari nama mereka itu pada permata yang pertama dan keenam nama lagi pada permata yang kedua, menurut urutan kelahirannya.'),(2304,2,28,11,'Seperti buatan seorang pengasah permata, diukirkan seperti meterai, demikianlah harus kauukirkan pada kedua permata itu nama para anak Israel; dililit dengan ikat emas harus kaubuat permata itu.'),(2305,2,28,12,'Kemudian haruslah kautaruh kedua permata itu pada kedua tutup bahu baju efod sebagai permata peringatan untuk mengingat orang Israel; maka ke hadapan TUHAN haruslah Harun membawa nama mereka di atas kedua tutup bahunya menjadi tanda peringatan.'),(2306,2,28,13,'Haruslah kaubuat ikat emas'),(2307,2,28,14,'dan dua untai dari emas murni; sebagai utas haruslah kaubuat itu, yang buatannya sebagai tali berjalin dan haruslah kaupasang untai berjalin itu pada ikat itu.'),(2308,2,28,15,'Haruslah engkau membuat tutup dada pernyataan keputusan: buatan seorang ahli. Buatannya sama dengan baju efod, demikianlah harus engkau membuatnya, yakni dari emas, kain ungu tua, kain ungu muda, kain kirmizi dan lenan halus yang dipintal benangnya haruslah engkau membuatnya.'),(2309,2,28,16,'Haruslah itu empat persegi, lipat dua, sejengkal panjangnya dan sejengkal lebarnya.'),(2310,2,28,17,'Haruslah kautatah itu dengan permata tatahan, empat jajar permata: permata yaspis merah, krisolit, malakit, itulah jajar yang pertama;'),(2311,2,28,18,'jajar yang kedua: permata batu darah, lazurit, yaspis hijau;'),(2312,2,28,19,'jajar yang ketiga: permata ambar, akik, kecubung,'),(2313,2,28,20,'jajar yang keempat: permata pirus, krisopras dan nefrit. Dengan berikatkan emas, demikianlah permata-permata itu dalam tatahannya.'),(2314,2,28,21,'Sesuai dengan nama para anak Israel, permata itu haruslah dua belas banyaknya; dan pada tiap-tiap permata haruslah ada, diukirkan seperti meterai, nama salah satu suku dari yang dua belas itu.'),(2315,2,28,22,'Juga haruslah kaubuat untuk tutup dada itu untai berpilin, yang buatannya sebagai tali berjalin, dari emas murni.'),(2316,2,28,23,'Juga haruslah kaubuat untuk tutup dada itu dua gelang emas dan kedua gelang itu harus kaupasang pada kedua ujung tutup dada.'),(2317,2,28,24,'Haruslah kedua untai emas yang berjalin itu kaupasang pada kedua gelang itu, pada ujung tutup dada.'),(2318,2,28,25,'Kedua ujung lain dari kedua untai berjalin itu haruslah kaupasang pada kedua ikat emas itu, demikianlah kaupasang pada tutup bahu baju efod, di sebelah depannya.'),(2319,2,28,26,'Haruslah engkau membuat dua gelang emas dan membubuhnya pada kedua ujung tutup dada itu, pada pinggirnya yang sebelah dalam, yang berhadapan dengan baju efod.'),(2320,2,28,27,'Juga haruslah engkau membuat dua gelang emas dan memasangnya pada kedua tutup bahu baju efod, di sebelah bawah pada bagian depan, dekat ke tempat persambungannya, di sebelah atas sabuk baju efod.'),(2321,2,28,28,'Kemudian haruslah tutup dada itu dengan gelangnya diikatkan kepada gelang baju efod dengan memakai tali ungu tua, sehingga tetap di atas sabuk baju efod, dan tutup dada itu tidak dapat bergeser dari baju efod.'),(2322,2,28,29,'Demikianlah di atas jantungnya harus dibawa Harun nama para anak Israel pada tutup dada pernyataan keputusan itu, apabila ia masuk ke dalam tempat kudus, supaya menjadi tanda peringatan yang tetap di hadapan TUHAN.'),(2323,2,28,30,'Dan di dalam tutup dada pernyataan keputusan itu haruslah kautaruh Urim dan Tumim; haruslah itu di atas jantung Harun, apabila ia masuk menghadap TUHAN, dan Harun harus tetap membawa keputusan bagi orang Israel di atas jantungnya, di hadapan TUHAN.'),(2324,2,28,31,'Haruslah kaubuat gamis baju efod dari kain ungu tua seluruhnya.'),(2325,2,28,32,'Lehernya haruslah di tengah-tengahnya; lehernya itu harus mempunyai pinggir sekelilingnya, buatan tukang tenun, seperti leher baju zirah haruslah lehernya itu, supaya jangan koyak.'),(2326,2,28,33,'Pada ujung gamis itu haruslah kaubuat buah delima dari kain ungu tua, kain ungu muda dan kain kirmizi, pada sekeliling ujung gamis itu, dan di antaranya berselang-seling giring-giring emas,'),(2327,2,28,34,'sehingga satu giring-giring emas dan satu buah delima selalu berselang-seling, pada ujung gamis itu.'),(2328,2,28,35,'Haruslah gamis itu dipakai Harun, apabila ia menyelenggarakan kebaktian, dan bunyinya harus kedengaran, apabila ia masuk ke dalam tempat kudus di hadapan TUHAN dan apabila ia keluar pula, supaya ia jangan mati.'),(2329,2,28,36,'Juga haruslah engkau membuat patam dari emas murni dan pada patam itu kauukirkanlah, diukirkan seperti meterai: Kudus bagi TUHAN.'),(2330,2,28,37,'Haruslah patam itu engkau beri bertali ungu tua, dan haruslah itu dilekatkan pada serban, di sebelah depan serban itu.'),(2331,2,28,38,'Patam itu haruslah ada pada dahi Harun, dan Harun harus menanggung akibat kesalahan terhadap segala yang dikuduskan oleh orang Israel, yakni terhadap segala persembahan kudusnya; maka haruslah patam itu tetap ada pada dahinya, sehingga TUHAN berkenan akan mereka.'),(2332,2,28,39,'Haruslah engkau menenun kemeja dengan ada raginya, dari lenan halus, dan membuat serban dari lenan halus dan haruslah kaubuat ikat pinggang dari tenunan yang berwarna-warna.'),(2333,2,28,40,'Juga bagi anak-anak Harun haruslah kaubuat kemeja-kemeja dan haruslah kaubuat ikat-ikat pinggang bagi mereka, dan destar-destar haruslah kaubuat bagi mereka untuk menjadi perhiasan kemuliaan.'),(2334,2,28,41,'Maka semuanya itu haruslah kaukenakan kepada abangmu Harun bersama-sama dengan anak-anaknya, kemudian engkau harus mengurapi, mentahbiskan dan menguduskan mereka, sehingga mereka dapat memegang jabatan imam bagi-Ku.'),(2335,2,28,42,'Buatlah celana-celana lenan bagi mereka untuk menutupi daging auratnya: celana itu haruslah dari pinggang sampai paha panjangnya.'),(2336,2,28,43,'Harun dan anak-anaknya haruslah memakainya, apabila mereka masuk ke dalam Kemah Pertemuan atau apabila mereka datang ke mezbah untuk menyelenggarakan kebaktian di tempat kudus, supaya mereka jangan membawa kesalahan kepada dirinya, lalu mati. Itulah suatu ketetapan untuk selama-lamanya baginya dan bagi keturunannya.\"'),(2337,2,29,1,'Inilah yang harus kaulakukan kepada mereka, untuk menguduskan mereka, supaya mereka memegang jabatan imam bagi-Ku: Ambillah seekor lembu jantan muda dan dua ekor domba jantan yang tidak bercela,'),(2338,2,29,2,'roti yang tidak beragi dan roti bundar yang tidak beragi, yang diolah dengan minyak, dan roti tipis yang tidak beragi, yang diolesi dengan minyak; dari tepung gandum yang terbaik haruslah kaubuat semuanya itu.'),(2339,2,29,3,'Kautaruhlah semuanya dalam sebuah bakul dan kaupersembahkanlah semuanya dalam bakul itu, demikian juga lembu jantan dan kedua domba jantan itu.'),(2340,2,29,4,'Lalu kausuruhlah Harun dan anak-anaknya datang ke pintu Kemah Pertemuan dan haruslah engkau membasuh mereka dengan air.'),(2341,2,29,5,'Kemudian kauambillah pakaian itu, lalu kaukenakanlah kepada Harun kemeja, gamis baju efod, dan baju efod serta tutup dada; kaukebatkanlah sabuk baju efod kepadanya;'),(2342,2,29,6,'kautaruhlah serban di kepalanya dan jamang yang kudus kaububuh pada serban itu.'),(2343,2,29,7,'Sesudah itu kauambillah minyak urapan dan kautuang ke atas kepalanya, dan kauurapilah dia.'),(2344,2,29,8,'Kausuruhlah anak-anaknya mendekat dan kaukenakanlah kemeja-kemeja itu kepada mereka.'),(2345,2,29,9,'Kauikatkanlah ikat pinggang kepada mereka, kepada Harun dan anak-anaknya, dan kaulilitkanlah destar itu kepada kepala mereka, maka merekalah yang akan memegang jabatan imam; itulah suatu ketetapan untuk selama-lamanya. Demikianlah engkau harus mentahbiskan Harun dan anak-anaknya.'),(2346,2,29,10,'Kemudian haruslah kaubawa lembu jantan itu ke depan Kemah Pertemuan, lalu haruslah Harun dan anak-anaknya meletakkan tangannya ke atas kepala lembu jantan itu.'),(2347,2,29,11,'Haruslah kausembelih lembu jantan itu di hadapan TUHAN di depan pintu Kemah Pertemuan.'),(2348,2,29,12,'Haruslah kauambil sedikit dari darah lembu jantan itu dan kaububuh dengan jarimu pada tanduk-tanduk mezbah, dan segala darah selebihnya haruslah kaucurahkan pada bagian bawah mezbah.'),(2349,2,29,13,'Kemudian kauambillah segala lemak yang menutupi isi perut, umbai hati, kedua buah pinggang dan segala lemak yang melekat padanya, dan kaubakarlah di atas mezbah.'),(2350,2,29,14,'Tetapi daging lembu jantan itu, kulitnya dan kotorannya haruslah kaubakar habis dengan api di luar perkemahan, itulah korban penghapus dosa.'),(2351,2,29,15,'Kemudian haruslah kauambil domba jantan yang satu, lalu haruslah Harun dan anak-anaknya meletakkan tangannya ke atas kepala domba jantan itu.'),(2352,2,29,16,'Haruslah kausembelih domba jantan itu dan kauambillah darahnya dan kausiramkan pada mezbah sekelilingnya.'),(2353,2,29,17,'Haruslah kaupotong-potong domba jantan itu menurut bagian-bagian tertentu, kaubasuhlah isi perutnya dan betis-betisnya dan kautaruh itu di atas potongan-potongannya dan di atas kepalanya.'),(2354,2,29,18,'Kemudian haruslah kaubakar seluruh domba jantan itu di atas mezbah; itulah korban bakaran, suatu persembahan yang harum bagi TUHAN, yakni suatu korban api-apian bagi TUHAN.'),(2355,2,29,19,'Kemudian haruslah kauambil domba jantan yang lain, lalu haruslah Harun dan anak-anaknya meletakkan tangannya ke atas kepala domba jantan itu.'),(2356,2,29,20,'Haruslah kausembelih domba jantan itu, kauambillah sedikit dari darahnya dan kaububuh pada cuping telinga kanan Harun dan pada cuping telinga kanan anak-anaknya, pada ibu jari tangan kanan dan pada ibu jari kaki kanan mereka, dan darah selebihnya kausiramkanlah pada mezbah sekelilingnya.'),(2357,2,29,21,'Haruslah kauambil sedikit dari darah yang ada di atas mezbah dan dari minyak urapan itu dan kaupercikkanlah kepada Harun dan kepada pakaiannya, dan juga kepada anak-anaknya dan pada pakaian anak-anaknya; maka ia akan kudus, ia dan pakaiannya, dan juga anak-anaknya dan pakaian anak-anaknya.'),(2358,2,29,22,'Dari domba jantan itu haruslah kauambil lemaknya, ekornya yang berlemak, lemak yang menutupi isi perutnya, umbai hatinya, kedua buah pinggangnya, lemak yang melekat padanya, paha kanannya--sebab itulah domba jantan persembahan pentahbisan--'),(2359,2,29,23,'kauambillah juga satu keping roti, satu roti bundar yang berminyak dan satu roti tipis dari dalam bakul berisi roti yang tidak beragi, yang ada di hadapan TUHAN.'),(2360,2,29,24,'Haruslah kautaruh seluruhnya ke atas telapak tangan Harun dan ke atas telapak tangan anak-anaknya dan haruslah kaupersembahkan semuanya sebagai persembahan unjukan di hadapan TUHAN.'),(2361,2,29,25,'Kemudian haruslah kauambil semuanya dari tangan mereka dan kaubakar di atas mezbah, yaitu di atas korban bakaran, sebagai persembahan yang harum di hadapan TUHAN; itulah suatu korban api-apian bagi TUHAN.'),(2362,2,29,26,'Selanjutnya haruslah kauambil dada dari domba jantan yang adalah bagi pentahbisan Harun dan kaupersembahkan sebagai persembahan unjukan di hadapan TUHAN, dan itulah bagian untukmu.'),(2363,2,29,27,'Demikianlah harus kaukuduskan dada persembahan unjukan dan paha persembahan khusus yang dipersembahkan dari domba jantan yang adalah bagi pentahbisan Harun dan anak-anaknya.'),(2364,2,29,28,'Itulah yang menjadi bagian untuk Harun dan anak-anaknya menurut ketetapan yang berlaku untuk selama-lamanya bagi orang Israel, sebab inilah suatu persembahan khusus, maka haruslah itu menjadi persembahan khusus dari pihak orang Israel, yang diambil dari korban keselamatan mereka, dan menjadi persembahan khusus mereka bagi TUHAN.'),(2365,2,29,29,'Pakaian kudus kepunyaan Harun itu haruslah turun kepada anak-anaknya yang kemudian, supaya mereka memakainya apabila mereka diurapi dan ditahbiskan.'),(2366,2,29,30,'Tujuh hari lamanya haruslah pakaian itu dikenakan oleh imam penggantinya dari antara anak-anaknya, yang akan masuk ke dalam Kemah Pertemuan untuk menyelenggarakan kebaktian di tempat kudus.'),(2367,2,29,31,'Domba jantan persembahan pentahbisan itu haruslah kauambil dan dagingnya kaumasak pada suatu tempat yang kudus.'),(2368,2,29,32,'Haruslah Harun dan anak-anaknya memakan daging domba jantan itu serta roti yang ada di dalam bakul di depan pintu Kemah Pertemuan.'),(2369,2,29,33,'Haruslah mereka memakan semuanya itu yang dipakai untuk mengadakan pendamaian pada waktu mereka ditahbiskan dan dikuduskan, tetapi orang awam janganlah memakannya, sebab persembahan kudus semuanya itu.'),(2370,2,29,34,'Jika ada yang tinggal dari daging persembahan pentahbisan dan dari roti itu sampai pagi, haruslah kaubakar habis yang tinggal itu dengan api, janganlah dimakan, sebab persembahan kudus semuanya itu.'),(2371,2,29,35,'Maka haruslah kauperbuat demikian kepada Harun dan kepada anak-anaknya, tepat seperti yang Kuperintahkan kepadamu; selama tujuh hari haruslah kautahbiskan mereka.'),(2372,2,29,36,'Tiap-tiap hari haruslah engkau mengolah seekor lembu jantan menjadi korban penghapus dosa untuk mengadakan pendamaian dan haruslah kausucikan mezbah itu, dengan mengadakan pendamaian baginya; haruslah engkau mengurapinya untuk menguduskannya.'),(2373,2,29,37,'Tujuh hari lamanya haruslah engkau mengadakan pendamaian bagi mezbah itu; haruslah engkau menguduskannya, maka mezbah itu akan menjadi maha kudus; setiap orang yang kena kepada mezbah itu akan menjadi kudus.\"'),(2374,2,29,38,'Inilah yang harus kauolah di atas mezbah itu: dua anak domba berumur setahun, tetap tiap-tiap hari.'),(2375,2,29,39,'Domba yang satu haruslah kauolah pada waktu pagi dan domba yang lain kauolah pada waktu senja.'),(2376,2,29,40,'Dan beserta domba yang satu kauolah sepersepuluh efa tepung yang terbaik dengan minyak tumbuk seperempat hin, dan korban curahan dari seperempat hin anggur.'),(2377,2,29,41,'Domba yang lain haruslah kauolah pada waktu senja; sama seperti korban sajian dan korban curahannya pada waktu pagi harus engkau mengolahnya sebagai persembahan yang harum, suatu korban api-apian bagi TUHAN,'),(2378,2,29,42,'suatu korban bakaran yang tetap di antara kamu turun-temurun, di depan pintu Kemah Pertemuan di hadapan TUHAN. Sebab di sana Aku akan bertemu dengan kamu, untuk berfirman kepadamu.'),(2379,2,29,43,'Di sanalah Aku akan bertemu dengan orang Israel, dan tempat itu akan dikuduskan oleh kemuliaan-Ku.'),(2380,2,29,44,'Aku akan menguduskan Kemah Pertemuan dan mezbah itu, lalu Harun dan anak-anaknya akan Kukuduskan supaya mereka memegang jabatan imam bagi-Ku.'),(2381,2,29,45,'Aku akan diam di tengah-tengah orang Israel dan Aku akan menjadi Allah mereka.'),(2382,2,29,46,'Maka mereka akan mengetahui, bahwa Akulah, TUHAN, Allah mereka, yang telah membawa mereka keluar dari tanah Mesir, supaya Aku diam di tengah-tengah mereka; Akulah TUHAN, Allah mereka.\"'),(2383,2,30,1,'Haruslah kaubuat mezbah, tempat pembakaran ukupan; haruslah kaubuat itu dari kayu penaga;'),(2384,2,30,2,'sehasta panjangnya dan sehasta lebarnya, sehingga menjadi empat persegi, tetapi haruslah dua hasta tingginya; tanduk-tanduknya haruslah seiras dengan mezbah itu.'),(2385,2,30,3,'Haruslah kausalut itu dengan emas murni, bidang atasnya dan bidang-bidang sisinya sekelilingnya, serta tanduk-tanduknya. Haruslah kaubuat bingkai emas sekelilingnya.'),(2386,2,30,4,'Haruslah kaubuat dua gelang emas untuk mezbah itu di bawah bingkainya; pada kedua rusuknya haruslah kaubuat gelang itu, pada kedua bidang sisinya, dan haruslah gelang itu menjadi tempat memasukkan kayu pengusung, supaya dengan itu mezbah dapat diangkut.'),(2387,2,30,5,'Haruslah kaubuat kayu pengusung itu dari kayu penaga dan kausalutlah dengan emas.'),(2388,2,30,6,'Haruslah kautaruh tempat pembakaran itu di depan tabir penutup tabut hukum, di depan tutup pendamaian yang di atas loh hukum, di mana Aku akan bertemu dengan engkau.'),(2389,2,30,7,'Di atasnya haruslah Harun membakar ukupan dari wangi-wangian; tiap-tiap pagi, apabila ia membersihkan lampu-lampu, haruslah ia membakarnya.'),(2390,2,30,8,'Juga apabila Harun memasang lampu-lampu itu pada waktu senja, haruslah ia membakarnya sebagai ukupan yang tetap di hadapan TUHAN di antara kamu turun-temurun.'),(2391,2,30,9,'Di atas mezbah itu janganlah kamu persembahkan ukupan yang lain ataupun korban bakaran ataupun korban sajian, juga korban curahan janganlah kamu curahkan di atasnya.'),(2392,2,30,10,'Sekali setahun haruslah Harun mengadakan pendamaian di atas tanduk-tanduknya; dengan darah korban penghapus dosa pembawa pendamaian haruslah ia sekali setahun mengadakan pendamaian bagi mezbah itu di antara kamu turun-temurun; itulah barang maha kudus bagi TUHAN.\"'),(2393,2,30,11,'TUHAN berfirman kepada Musa:'),(2394,2,30,12,'Apabila engkau menghitung jumlah orang Israel pada waktu mereka didaftarkan, maka haruslah mereka masing-masing mempersembahkan kepada TUHAN uang pendamaian karena nyawanya, pada waktu orang mendaftarkan mereka, supaya jangan ada tulah di antara mereka pada waktu pendaftarannya itu.'),(2395,2,30,13,'Inilah yang harus dipersembahkan tiap-tiap orang yang akan termasuk orang-orang yang terdaftar itu: setengah syikal, ditimbang menurut syikal kudus--syikal ini dua puluh gera beratnya--;setengah syikal itulah persembahan khusus kepada TUHAN.'),(2396,2,30,14,'Setiap orang yang akan termasuk orang-orang yang terdaftar itu, yang berumur dua puluh tahun ke atas, haruslah mempersembahkan persembahan khusus itu kepada TUHAN.'),(2397,2,30,15,'Orang kaya janganlah mempersembahkan lebih dan orang miskin janganlah mempersembahkan kurang dari setengah syikal itu pada waktu dipersembahkan persembahan khusus itu kepada TUHAN untuk mengadakan pendamaian bagi nyawa kamu sekalian.'),(2398,2,30,16,'Dan haruslah engkau memungut uang pendamaian itu dari orang Israel dan menggunakannya untuk ibadah dalam Kemah Pertemuan; supaya itu menjadi peringatan di hadapan TUHAN untuk mengingat kepada orang Israel dan untuk mengadakan pendamaian bagi nyawa kamu sekalian.\"'),(2399,2,30,17,'Berfirmanlah TUHAN kepada Musa:'),(2400,2,30,18,'Haruslah engkau membuat bejana dan juga alasnya dari tembaga, untuk pembasuhan, dan kautempatkanlah itu antara Kemah Pertemuan dan mezbah, dan kautaruhlah air ke dalamnya.'),(2401,2,30,19,'Maka Harun dan anak-anaknya haruslah membasuh tangan dan kaki mereka dengan air dari dalamnya.'),(2402,2,30,20,'Apabila mereka masuk ke dalam Kemah Pertemuan, haruslah mereka membasuh tangan dan kaki dengan air, supaya mereka jangan mati. Demikian juga apabila mereka datang ke mezbah itu untuk menyelenggarakan kebaktian dan untuk membakar korban api-apian bagi TUHAN,'),(2403,2,30,21,'haruslah mereka membasuh tangan dan kaki mereka, supaya mereka jangan mati. Itulah yang harus menjadi ketetapan bagi mereka untuk selama-lamanya, bagi dia dan bagi keturunannya turun-temurun.\"'),(2404,2,30,22,'Berfirmanlah TUHAN kepada Musa:'),(2405,2,30,23,'Ambillah rempah-rempah pilihan, mur tetesan lima ratus syikal, dan kayu manis yang harum setengah dari itu, yakni dua ratus lima puluh syikal, dan tebu yang baik dua ratus lima puluh syikal,'),(2406,2,30,24,'dan kayu teja lima ratus syikal, ditimbang menurut syikal kudus, dan minyak zaitun satu hin.'),(2407,2,30,25,'Haruslah kaubuat semuanya itu menjadi minyak urapan yang kudus, suatu campuran rempah-rempah yang dicampur dengan cermat seperti buatan seorang tukang campur rempah-rempah; itulah yang harus menjadi minyak urapan yang kudus.'),(2408,2,30,26,'Haruslah engkau mengurapi dengan itu Kemah Pertemuan dan tabut hukum,'),(2409,2,30,27,'meja dengan segala perkakasnya, kandil dengan perkakasnya, dan mezbah pembakaran ukupan;'),(2410,2,30,28,'mezbah korban bakaran dengan segala perkakasnya, bejana pembasuhan dengan alasnya.'),(2411,2,30,29,'Haruslah kaukuduskan semuanya, sehingga menjadi maha kudus; setiap orang yang kena kepadanya akan menjadi kudus.'),(2412,2,30,30,'Engkau harus juga mengurapi dan menguduskan Harun dan anak-anaknya supaya mereka memegang jabatan imam bagi-Ku.'),(2413,2,30,31,'Dan kepada orang Israel haruslah kaukatakan demikian: Inilah yang harus menjadi minyak urapan yang kudus bagi-Ku di antara kamu turun-temurun.'),(2414,2,30,32,'Kepada badan orang biasa janganlah minyak itu dicurahkan, dan janganlah kaubuat minyak yang semacam itu dengan memakai campuran itu juga: itulah minyak yang kudus, dan haruslah itu kudus bagimu.'),(2415,2,30,33,'Orang yang mencampur rempah-rempah menjadi minyak yang semacam itu atau yang membubuhnya pada badan orang awam, haruslah dilenyapkan dari antara bangsanya.\"'),(2416,2,30,34,'Berfirmanlah TUHAN kepada Musa: \"Ambillah wangi-wangian, yakni getah damar, kulit lokan dan getah rasamala, wangi-wangian itu serta kemenyan yang tulen, masing-masing sama banyaknya.'),(2417,2,30,35,'Semuanya ini haruslah kaubuat menjadi ukupan, suatu campuran rempah-rempah, seperti buatan seorang tukang campur rempah-rempah, digarami, murni, kudus.'),(2418,2,30,36,'Sebagian dari ukupan itu haruslah kaugiling sampai halus, dan sedikit dari padanya kauletakkanlah di hadapan tabut hukum di dalam Kemah Pertemuan, di mana Aku akan bertemu dengan engkau; haruslah itu maha kudus bagimu.'),(2419,2,30,37,'Dan tentang ukupan yang harus kaubuat menurut campuran yang seperti itu juga janganlah kamu buat bagi kamu sendiri; itulah bagian untuk TUHAN, yang kudus bagimu.'),(2420,2,30,38,'Orang yang akan membuat minyak yang semacam itu dengan maksud untuk menghirup baunya, haruslah dilenyapkan dari antara bangsanya.\"'),(2421,2,31,1,'Berfirmanlah TUHAN kepada Musa:'),(2422,2,31,2,'Lihat, telah Kutunjuk Bezaleel bin Uri bin Hur, dari suku Yehuda,'),(2423,2,31,3,'dan telah Kupenuhi dia dengan Roh Allah, dengan keahlian dan pengertian dan pengetahuan, dalam segala macam pekerjaan,'),(2424,2,31,4,'untuk membuat berbagai rancangan supaya dikerjakan dari emas, perak dan tembaga;'),(2425,2,31,5,'untuk mengasah batu permata supaya ditatah; untuk mengukir kayu dan untuk bekerja dalam segala macam pekerjaan.'),(2426,2,31,6,'Juga Aku telah menetapkan di sampingnya Aholiab bin Ahisamakh, dari suku Dan; dalam hati setiap orang ahli telah Kuberikan keahlian. Haruslah mereka membuat segala apa yang telah Kuperintahkan kepadamu:'),(2427,2,31,7,'Kemah Pertemuan, tabut untuk hukum, tutup pendamaian yang terletak di atasnya, dan segala perabotan kemah itu,'),(2428,2,31,8,'yakni meja dengan perkakasnya, kandil dari emas murni dengan segala perkakasnya, mezbah pembakaran ukupan,'),(2429,2,31,9,'mezbah korban bakaran dengan segala perkakasnya, bejana pembasuhan dengan alasnya,'),(2430,2,31,10,'pakaian jabatan, baik pakaian kudus kepunyaan imam Harun, maupun pakaian anak-anaknya, untuk memegang jabatan imam,'),(2431,2,31,11,'minyak urapan dan ukupan dari wangi-wangian untuk tempat kudus; tepat seperti yang telah Kuperintahkan kepadamu haruslah mereka membuat semuanya.\"'),(2432,2,31,12,'Berfirmanlah TUHAN kepada Musa:'),(2433,2,31,13,'Katakanlah kepada orang Israel, demikian: Akan tetapi hari-hari Sabat-Ku harus kamu pelihara, sebab itulah peringatan antara Aku dan kamu, turun-temurun, sehingga kamu mengetahui, bahwa Akulah TUHAN, yang menguduskan kamu.'),(2434,2,31,14,'Haruslah kamu pelihara hari Sabat, sebab itulah hari kudus bagimu; siapa yang melanggar kekudusan hari Sabat itu, pastilah ia dihukum mati, sebab setiap orang yang melakukan pekerjaan pada hari itu, orang itu harus dilenyapkan dari antara bangsanya.'),(2435,2,31,15,'Enam hari lamanya boleh dilakukan pekerjaan, tetapi pada hari yang ketujuh haruslah ada sabat, hari perhentian penuh, hari kudus bagi TUHAN: setiap orang yang melakukan pekerjaan pada hari Sabat, pastilah ia dihukum mati.'),(2436,2,31,16,'Maka haruslah orang Israel memelihara hari Sabat, dengan merayakan sabat, turun-temurun, menjadi perjanjian kekal.'),(2437,2,31,17,'Antara Aku dan orang Israel maka inilah suatu peringatan untuk selama-lamanya, sebab enam hari lamanya TUHAN menjadikan langit dan bumi, dan pada hari yang ketujuh Ia berhenti bekerja untuk beristirahat.\"'),(2438,2,31,18,'Dan TUHAN memberikan kepada Musa, setelah Ia selesai berbicara dengan dia di gunung Sinai, kedua loh hukum Allah, loh batu, yang ditulisi oleh jari Allah.'),(2439,2,32,1,'Ketika bangsa itu melihat, bahwa Musa mengundur-undurkan turun dari gunung itu, maka berkumpullah mereka mengerumuni Harun dan berkata kepadanya: \"Mari, buatlah untuk kami allah, yang akan berjalan di depan kami sebab Musa ini, orang yang telah memimpin kami keluar dari tanah Mesir--kami tidak tahu apa yang telah terjadi dengan dia.\"'),(2440,2,32,2,'Lalu berkatalah Harun kepada mereka: \"Tanggalkanlah anting-anting emas yang ada pada telinga isterimu, anakmu laki-laki dan perempuan, dan bawalah semuanya kepadaku.\"'),(2441,2,32,3,'Lalu seluruh bangsa itu menanggalkan anting-anting emas yang ada pada telinga mereka dan membawanya kepada Harun.'),(2442,2,32,4,'Diterimanyalah itu dari tangan mereka, dibentuknya dengan pahat, dan dibuatnyalah dari padanya anak lembu tuangan. Kemudian berkatalah mereka: \"Hai Israel, inilah Allahmu, yang telah menuntun engkau keluar dari tanah Mesir!\"'),(2443,2,32,5,'Ketika Harun melihat itu, didirikannyalah mezbah di depan anak lembu itu. Berserulah Harun, katanya: \"Besok hari raya bagi TUHAN!\"'),(2444,2,32,6,'Dan keesokan harinya pagi-pagi maka mereka mempersembahkan korban bakaran dan korban keselamatan, sesudah itu duduklah bangsa itu untuk makan dan minum; kemudian bangunlah mereka dan bersukaria.'),(2445,2,32,7,'Berfirmanlah TUHAN kepada Musa: \"Pergilah, turunlah, sebab bangsamu yang kaupimpin keluar dari tanah Mesir telah rusak lakunya.'),(2446,2,32,8,'Segera juga mereka menyimpang dari jalan yang Kuperintahkan kepada mereka; mereka telah membuat anak lembu tuangan, dan kepadanya mereka sujud menyembah dan mempersembahkan korban, sambil berkata: Hai Israel, inilah Allahmu yang telah menuntun engkau keluar dari tanah Mesir.\"'),(2447,2,32,9,'Lagi firman TUHAN kepada Musa: \"Telah Kulihat bangsa ini dan sesungguhnya mereka adalah suatu bangsa yang tegar tengkuk.'),(2448,2,32,10,'Oleh sebab itu biarkanlah Aku, supaya murka-Ku bangkit terhadap mereka dan Aku akan membinasakan mereka, tetapi engkau akan Kubuat menjadi bangsa yang besar.\"'),(2449,2,32,11,'Lalu Musa mencoba melunakkan hati TUHAN, Allahnya, dengan berkata: \"Mengapakah, TUHAN, murka-Mu bangkit terhadap umat-Mu, yang telah Kaubawa keluar dari tanah Mesir dengan kekuatan yang besar dan dengan tangan yang kuat?'),(2450,2,32,12,'Mengapakah orang Mesir akan berkata: Dia membawa mereka keluar dengan maksud menimpakan malapetaka kepada mereka dan membunuh mereka di gunung dan membinasakannya dari muka bumi? Berbaliklah dari murka-Mu yang bernyala-nyala itu dan menyesallah karena malapetaka yang hendak Kaudatangkan kepada umat-Mu.'),(2451,2,32,13,'Ingatlah kepada Abraham, Ishak dan Israel, hamba-hamba-Mu itu, sebab kepada mereka Engkau telah bersumpah demi diri-Mu sendiri dengan berfirman kepada mereka: Aku akan membuat keturunanmu sebanyak bintang di langit, dan seluruh negeri yang telah Kujanjikan ini akan Kuberikan kepada keturunanmu, supaya dimilikinya untuk selama-lamanya.\"'),(2452,2,32,14,'Dan menyesallah TUHAN karena malapetaka yang dirancangkan-Nya atas umat-Nya.'),(2453,2,32,15,'Setelah itu berpalinglah Musa, lalu turun dari gunung dengan kedua loh hukum Allah dalam tangannya, loh-loh yang bertulis pada kedua sisinya; bertulis sebelah-menyebelah.'),(2454,2,32,16,'Kedua loh itu ialah pekerjaan Allah dan tulisan itu ialah tulisan Allah, ditukik pada loh-loh itu.'),(2455,2,32,17,'Ketika Yosua mendengar suara bangsa itu bersorak, berkatalah ia kepada Musa: \"Ada bunyi sorak peperangan kedengaran di perkemahan.\"'),(2456,2,32,18,'Tetapi jawab Musa: \"Bukan bunyi nyanyian kemenangan, bukan bunyi nyanyian kekalahan--bunyi orang menyanyi berbalas-balasan, itulah yang kudengar.\"'),(2457,2,32,19,'Dan ketika ia dekat ke perkemahan itu dan melihat anak lembu dan melihat orang menari-nari, maka bangkitlah amarah Musa; dilemparkannyalah kedua loh itu dari tangannya dan dipecahkannya pada kaki gunung itu.'),(2458,2,32,20,'Sesudah itu diambilnyalah anak lembu yang dibuat mereka itu, dibakarnya dengan api dan digilingnya sampai halus, kemudian ditaburkannya ke atas air dan disuruhnya diminum oleh orang Israel.'),(2459,2,32,21,'Lalu berkatalah Musa kepada Harun: \"Apakah yang dilakukan bangsa ini kepadamu, sehingga engkau mendatangkan dosa yang sebesar itu kepada mereka?\"'),(2460,2,32,22,'Tetapi jawab Harun: \"Janganlah bangkit amarah tuanku; engkau sendiri tahu, bahwa bangsa ini jahat semata-mata.'),(2461,2,32,23,'Mereka berkata kepadaku: Buatlah untuk kami allah, yang akan berjalan di depan kami sebab Musa ini, orang yang telah memimpin kami keluar dari tanah Mesir--kami tidak tahu apa yang telah terjadi dengan dia.'),(2462,2,32,24,'Lalu aku berkata kepada mereka: Siapa yang empunya emas haruslah menanggalkannya. Mereka memberikannya kepadaku dan aku melemparkannya ke dalam api, dan keluarlah anak lembu ini.\"'),(2463,2,32,25,'Ketika Musa melihat, bahwa bangsa itu seperti kuda terlepas dari kandang--sebab Harun telah melepaskannya, sampai menjadi buah cemooh bagi lawan mereka--'),(2464,2,32,26,'maka berdirilah Musa di pintu gerbang perkemahan itu serta berkata: \"Siapa yang memihak kepada TUHAN datanglah kepadaku!\" Lalu berkumpullah kepadanya seluruh bani Lewi.'),(2465,2,32,27,'Berkatalah ia kepada mereka: \"Beginilah firman TUHAN, Allah Israel: Baiklah kamu masing-masing mengikatkan pedangnya pada pinggangnya dan berjalanlah kian ke mari melalui perkemahan itu dari pintu gerbang ke pintu gerbang, dan biarlah masing-masing membunuh saudaranya dan temannya dan tetangganya.\"'),(2466,2,32,28,'Bani Lewi melakukan seperti yang dikatakan Musa dan pada hari itu tewaslah kira-kira tiga ribu orang dari bangsa itu.'),(2467,2,32,29,'Kemudian berkatalah Musa: \"Baktikanlah dirimu mulai hari ini kepada TUHAN, masing-masing dengan membayarkan jiwa anaknya laki-laki dan saudaranya--yakni supaya kamu diberi berkat pada hari ini.\"'),(2468,2,32,30,'Keesokan harinya berkatalah Musa kepada bangsa itu: \"Kamu ini telah berbuat dosa besar, tetapi sekarang aku akan naik menghadap TUHAN, mungkin aku akan dapat mengadakan pendamaian karena dosamu itu.\"'),(2469,2,32,31,'Lalu kembalilah Musa menghadap TUHAN dan berkata: \"Ah, bangsa ini telah berbuat dosa besar, sebab mereka telah membuat allah emas bagi mereka.'),(2470,2,32,32,'Tetapi sekarang, kiranya Engkau mengampuni dosa mereka itu--dan jika tidak, hapuskanlah kiranya namaku dari dalam kitab yang telah Kautulis.\"'),(2471,2,32,33,'Tetapi TUHAN berfirman kepada Musa: \"Siapa yang berdosa kepada-Ku, nama orang itulah yang akan Kuhapuskan dari dalam kitab-Ku.'),(2472,2,32,34,'Tetapi pergilah sekarang, tuntunlah bangsa itu ke tempat yang telah Kusebutkan kepadamu; akan berjalan malaikat-Ku di depanmu, tetapi pada hari pembalasan-Ku itu Aku akan membalaskan dosa mereka kepada mereka.\"'),(2473,2,32,35,'Demikianlah TUHAN menulahi bangsa itu, karena mereka telah menyuruh membuat anak lembu buatan Harun itu.'),(2474,2,33,1,'Berfirmanlah TUHAN kepada Musa: \"Pergilah, berjalanlah dari sini, engkau dan bangsa itu yang telah kaupimpin keluar dari tanah Mesir, ke negeri yang telah Kujanjikan dengan sumpah kepada Abraham, Ishak dan Yakub, demikian: Kepada keturunanmulah akan Kuberikan negeri itu--'),(2475,2,33,2,'Aku akan mengutus seorang malaikat berjalan di depanmu dan akan menghalau orang Kanaan, orang Amori, orang Het, orang Feris, orang Hewi dan orang Yebus--'),(2476,2,33,3,'yakni ke suatu negeri yang berlimpah-limpah susu dan madu. Sebab Aku tidak akan berjalan di tengah-tengahmu, karena engkau ini bangsa yang tegar tengkuk, supaya Aku jangan membinasakan engkau di jalan.\"'),(2477,2,33,4,'Ketika bangsa itu mendengar ancaman yang mengerikan ini, berkabunglah mereka dan seorangpun tidak ada yang memakai perhiasannya.'),(2478,2,33,5,'Berfirmanlah TUHAN kepada Musa: \"Katakanlah kepada orang Israel: Kamu ini bangsa yang tegar tengkuk. Jika Aku berjalan di tengah-tengahmu sesaatpun, tentulah Aku akan membinasakan kamu. Oleh sebab itu, tanggalkanlah perhiasanmu, maka Aku akan melihat, apa yang akan Kulakukan kepadamu.\"'),(2479,2,33,6,'Demikianlah orang Israel tidak memakai perhiasan-perhiasan lagi sejak dari gunung Horeb.'),(2480,2,33,7,'Sesudah itu Musa mengambil kemah dan membentangkannya di luar perkemahan, jauh dari perkemahan, dan menamainya Kemah Pertemuan. Setiap orang yang mencari TUHAN, keluarlah ia pergi ke Kemah Pertemuan yang di luar perkemahan.'),(2481,2,33,8,'Apabila Musa keluar pergi ke kemah itu, bangunlah seluruh bangsa itu dan berdirilah mereka, masing-masing di pintu kemahnya, dan mereka mengikuti Musa dengan matanya, sampai ia masuk ke dalam kemah.'),(2482,2,33,9,'Apabila Musa masuk ke dalam kemah itu, turunlah tiang awan dan berhenti di pintu kemah dan berbicaralah TUHAN dengan Musa di sana.'),(2483,2,33,10,'Setelah seluruh bangsa itu melihat, bahwa tiang awan berhenti di pintu kemah, maka mereka bangun dan sujud menyembah, masing-masing di pintu kemahnya.'),(2484,2,33,11,'Dan TUHAN berbicara kepada Musa dengan berhadapan muka seperti seorang berbicara kepada temannya; kemudian kembalilah ia ke perkemahan. Tetapi abdinya, Yosua bin Nun, seorang yang masih muda, tidaklah meninggalkan kemah itu.'),(2485,2,33,12,'Lalu berkatalah Musa kepada TUHAN: \"Memang Engkau berfirman kepadaku: Suruhlah bangsa ini berangkat, tetapi Engkau tidak memberitahukan kepadaku, siapa yang akan Kauutus bersama-sama dengan aku. Namun demikian Engkau berfirman: Aku mengenal namamu dan juga engkau mendapat kasih karunia di hadapan-Ku.'),(2486,2,33,13,'Maka sekarang, jika aku kiranya mendapat kasih karunia di hadapan-Mu, beritahukanlah kiranya jalan-Mu kepadaku, sehingga aku mengenal Engkau, supaya aku tetap mendapat kasih karunia di hadapan-Mu. Ingatlah, bahwa bangsa ini umat-Mu.\"'),(2487,2,33,14,'Lalu Ia berfirman: \"Aku sendiri hendak membimbing engkau dan memberikan ketenteraman kepadamu.\"'),(2488,2,33,15,'Berkatalah Musa kepada-Nya: \"Jika Engkau sendiri tidak membimbing kami, janganlah suruh kami berangkat dari sini.'),(2489,2,33,16,'Dari manakah gerangan akan diketahui, bahwa aku telah mendapat kasih karunia di hadapan-Mu, yakni aku dengan umat-Mu ini? Bukankah karena Engkau berjalan bersama-sama dengan kami, sehingga kami, aku dengan umat-Mu ini, dibedakan dari segala bangsa yang ada di muka bumi ini?\"'),(2490,2,33,17,'Berfirmanlah TUHAN kepada Musa: \"Juga hal yang telah kaukatakan ini akan Kulakukan, karena engkau telah mendapat kasih karunia di hadapan-Ku dan Aku mengenal engkau.\"'),(2491,2,33,18,'Tetapi jawabnya: \"Perlihatkanlah kiranya kemuliaan-Mu kepadaku.\"'),(2492,2,33,19,'Tetapi firman-Nya: \"Aku akan melewatkan segenap kegemilangan-Ku dari depanmu dan menyerukan nama TUHAN di depanmu: Aku akan memberi kasih karunia kepada siapa yang Kuberi kasih karunia dan mengasihani siapa yang Kukasihani.\"'),(2493,2,33,20,'Lagi firman-Nya: \"Engkau tidak tahan memandang wajah-Ku, sebab tidak ada orang yang memandang Aku dapat hidup.\"'),(2494,2,33,21,'Berfirmanlah TUHAN: \"Ada suatu tempat dekat-Ku, di mana engkau dapat berdiri di atas gunung batu;'),(2495,2,33,22,'apabila kemuliaan-Ku lewat, maka Aku akan menempatkan engkau dalam lekuk gunung itu dan Aku akan menudungi engkau dengan tangan-Ku, sampai Aku berjalan lewat.'),(2496,2,33,23,'Kemudian Aku akan menarik tangan-Ku dan engkau akan melihat belakang-Ku, tetapi wajah-Ku tidak akan kelihatan.\"'),(2497,2,34,1,'Berfirmanlah TUHAN kepada Musa: \"Pahatlah dua loh batu sama dengan yang mula-mula, maka Aku akan menulis pada loh itu segala firman yang ada pada loh yang mula-mula, yang telah kaupecahkan.'),(2498,2,34,2,'Bersiaplah menjelang pagi dan naiklah pada waktu pagi ke atas gunung Sinai; berdirilah di sana menghadap Aku di puncak gunung itu.'),(2499,2,34,3,'Tetapi janganlah ada seorangpun yang naik bersama-sama dengan engkau dan juga seorangpun tidak boleh kelihatan di seluruh gunung itu, bahkan kambing domba dan lembu sapipun tidak boleh makan rumput di sekitar gunung itu.\"'),(2500,2,34,4,'Lalu Musa memahat dua loh batu sama dengan yang mula-mula; bangunlah ia pagi-pagi dan naiklah ia ke atas gunung Sinai, seperti yang diperintahkan TUHAN kepadanya, dan membawa kedua loh batu itu di tangannya.'),(2501,2,34,5,'Turunlah TUHAN dalam awan, lalu berdiri di sana dekat Musa serta menyerukan nama TUHAN.'),(2502,2,34,6,'Berjalanlah TUHAN lewat dari depannya dan berseru: \"TUHAN, TUHAN, Allah penyayang dan pengasih, panjang sabar, berlimpah kasih-Nya dan setia-Nya,'),(2503,2,34,7,'yang meneguhkan kasih setia-Nya kepada beribu-ribu orang, yang mengampuni kesalahan, pelanggaran dan dosa; tetapi tidaklah sekali-kali membebaskan orang yang bersalah dari hukuman, yang membalaskan kesalahan bapa kepada anak-anaknya dan cucunya, kepada keturunan yang ketiga dan keempat.\"'),(2504,2,34,8,'Segeralah Musa berlutut ke tanah, lalu sujud menyembah'),(2505,2,34,9,'serta berkata: \"Jika aku telah mendapat kasih karunia di hadapan-Mu, ya Tuhan, berjalanlah kiranya Tuhan di tengah-tengah kami; sekalipun bangsa ini suatu bangsa yang tegar tengkuk, tetapi ampunilah kesalahan dan dosa kami; ambillah kami menjadi milik-Mu.\"'),(2506,2,34,10,'Firman-Nya: \"Sungguh, Aku mengadakan suatu perjanjian. Di depan seluruh bangsamu ini akan Kulakukan perbuatan-perbuatan yang ajaib, seperti yang belum pernah dijadikan di seluruh bumi di antara segala bangsa; seluruh bangsa, yang di tengah-tengahnya engkau diam, akan melihat perbuatan TUHAN, sebab apa yang akan Kulakukan dengan engkau, sungguh-sungguh dahsyat.'),(2507,2,34,11,'Tetapi engkau, berpeganglah pada yang Kuperintahkan kepadamu pada hari ini. Lihat, Aku akan menghalau dari depanmu orang Amori, orang Kanaan, orang Het, orang Feris, orang Hewi dan orang Yebus.'),(2508,2,34,12,'Berawas-awaslah, janganlah kauadakan perjanjian dengan penduduk negeri yang kaudatangi itu, supaya jangan mereka menjadi jerat bagimu di tengah-tengahmu.'),(2509,2,34,13,'Sebaliknya, mezbah-mezbah mereka haruslah kamu rubuhkan, tugu-tugu berhala mereka kamu remukkan, dan tiang-tiang berhala mereka kamu tebang.'),(2510,2,34,14,'Sebab janganlah engkau sujud menyembah kepada allah lain, karena TUHAN, yang nama-Nya Cemburuan, adalah Allah yang cemburu.'),(2511,2,34,15,'Janganlah engkau sampai mengadakan perjanjian dengan penduduk negeri itu; apabila mereka berzinah dengan mengikuti allah mereka dan mempersembahkan korban kepada allah mereka, maka mereka akan mengundang engkau dan engkau akan ikut makan korban sembelihan mereka.'),(2512,2,34,16,'Apabila engkau mengambil anak-anak perempuan mereka menjadi isteri anak-anakmu dan anak-anak perempuan itu akan berzinah dengan mengikuti allah mereka, maka mereka akan membujuk juga anak-anakmu laki-laki untuk berzinah dengan mengikuti allah mereka.'),(2513,2,34,17,'Janganlah kaubuat bagimu allah tuangan.'),(2514,2,34,18,'Hari raya Roti Tidak Beragi haruslah kaupelihara; tujuh hari lamanya engkau harus makan roti yang tidak beragi, seperti yang Kuperintahkan kepadamu, pada waktu yang ditetapkan dalam bulan Abib, sebab dalam bulan Abib itulah engkau keluar dari Mesir.'),(2515,2,34,19,'Segala apa yang lahir terdahulu dari kandungan, Akulah yang empunya, juga segala ternakmu yang jantan, anak yang lahir terdahulu dari lembu atau domba.'),(2516,2,34,20,'Tetapi anak yang lahir terdahulu dari keledai haruslah kautebus dengan seekor domba; jika tidak kautebus, haruslah kaupatahkan batang lehernya. Setiap yang sulung dari antara anak-anakmu haruslah kautebus, dan janganlah orang menghadap ke hadirat-Ku dengan tangan hampa.'),(2517,2,34,21,'Enam harilah lamanya engkau bekerja, tetapi pada hari yang ketujuh haruslah engkau berhenti, dan dalam musim membajak dan musim menuai haruslah engkau memelihara hari perhentian juga.'),(2518,2,34,22,'Hari raya Tujuh Minggu, yakni hari raya buah bungaran dari penuaian gandum, haruslah kaurayakan, juga hari raya pengumpulan hasil pada pergantian tahun.'),(2519,2,34,23,'Tiga kali setahun segala orangmu yang laki-laki harus menghadap ke hadirat Tuhanmu TUHAN, Allah Israel,'),(2520,2,34,24,'sebab Aku akan menghalau bangsa-bangsa dari depanmu dan meluaskan daerahmu; dan tiada seorangpun yang akan mengingini negerimu, apabila engkau pergi untuk menghadap ke hadirat TUHAN, Allahmu, tiga kali setahun.'),(2521,2,34,25,'Janganlah darah korban sembelihan yang kepada-Ku kaupersembahkan beserta sesuatu yang beragi, dan janganlah ada dari korban sembelihan pada hari raya Paskah bermalam sampai pagi.'),(2522,2,34,26,'Yang terbaik dari buah bungaran hasil tanahmu haruslah kaubawa ke dalam rumah TUHAN, Allahmu. Janganlah engkau masak anak kambing dalam susu induknya.\"'),(2523,2,34,27,'Berfirmanlah TUHAN kepada Musa: \"Tuliskanlah segala firman ini, sebab berdasarkan firman ini telah Kuadakan perjanjian dengan engkau dan dengan Israel.\"'),(2524,2,34,28,'Dan Musa ada di sana bersama-sama dengan TUHAN empat puluh hari empat puluh malam lamanya, tidak makan roti dan tidak minum air, dan ia menuliskan pada loh itu segala perkataan perjanjian, yakni Kesepuluh Firman.'),(2525,2,34,29,'Ketika Musa turun dari gunung Sinai--kedua loh hukum Allah ada di tangan Musa ketika ia turun dari gunung itu--tidaklah ia tahu, bahwa kulit mukanya bercahaya oleh karena ia telah berbicara dengan TUHAN.'),(2526,2,34,30,'Ketika Harun dan segala orang Israel melihat Musa, tampak kulit mukanya bercahaya, maka takutlah mereka mendekati dia.'),(2527,2,34,31,'Tetapi Musa memanggil mereka, maka Harun dan segala pemimpin jemaah itu berbalik kepadanya dan Musa berbicara kepada mereka.'),(2528,2,34,32,'Sesudah itu mendekatlah segala orang Israel, lalu disampaikannyalah kepada mereka segala perintah yang diucapkan TUHAN kepadanya di atas gunung Sinai.'),(2529,2,34,33,'Setelah Musa selesai berbicara dengan mereka, diselubunginyalah mukanya.'),(2530,2,34,34,'Tetapi apabila Musa masuk menghadap TUHAN untuk berbicara dengan Dia, ditanggalkannyalah selubung itu sampai ia keluar; dan apabila ia keluar dikatakannyalah kepada orang Israel apa yang diperintahkan kepadanya.'),(2531,2,34,35,'Apabila orang Israel melihat muka Musa, bahwa kulit muka Musa bercahaya, maka Musa menyelubungi mukanya kembali sampai ia masuk menghadap untuk berbicara dengan TUHAN.'),(2532,2,35,1,'Lalu Musa menyuruh berkumpul segenap jemaah Israel dan berkata kepada mereka: \"Inilah firman yang diperintahkan TUHAN untuk dilakukan.'),(2533,2,35,2,'Enam hari lamanya boleh dilakukan pekerjaan, tetapi pada hari yang ketujuh haruslah ada perhentian kudus bagimu, yakni sabat, hari perhentian penuh bagi TUHAN; setiap orang yang melakukan pekerjaan pada hari itu, haruslah dihukum mati.'),(2534,2,35,3,'Janganlah kamu memasang api di manapun dalam tempat kediamanmu pada hari Sabat.\"'),(2535,2,35,4,'Berkatalah Musa kepada segenap jemaah Israel: \"Inilah firman yang diperintahkan TUHAN, bunyinya:'),(2536,2,35,5,'Ambillah bagi TUHAN persembahan khusus dari barang kepunyaanmu; setiap orang yang terdorong hatinya harus membawanya sebagai persembahan khusus kepada TUHAN: emas, perak, tembaga,'),(2537,2,35,6,'kain ungu tua, kain ungu muda, kain kirmizi, lenan halus, bulu kambing;'),(2538,2,35,7,'kulit domba jantan yang diwarnai merah, kulit-kulit lumba-lumba, kayu penaga,'),(2539,2,35,8,'minyak untuk penerangan, rempah-rempah untuk minyak urapan dan untuk ukupan dari wangi-wangian,'),(2540,2,35,9,'permata krisopras dan permata tatahan untuk baju efod dan untuk tutup dada.'),(2541,2,35,10,'Segala orang yang ahli di antara kamu haruslah datang untuk membuat segala yang diperintahkan TUHAN,'),(2542,2,35,11,'yakni Kemah Suci, atap kemahnya dan tudungnya, kaitannya, dan papannya, kayu lintangnya, tiangnya dan alasnya;'),(2543,2,35,12,'tabut dengan kayu pengusungnya, tutup pendamaian dan tabir penudung;'),(2544,2,35,13,'meja dengan kayu pengusungnya, segala perkakasnya dan roti sajian;'),(2545,2,35,14,'kandil untuk penerangan dengan perkakasnya, lampunya dan minyak untuk penerangan;'),(2546,2,35,15,'mezbah pembakaran ukupan dengan kayu pengusungnya, minyak urapan dan ukupan dari wangi-wangian; tirai pintu untuk pintu Kemah Suci;'),(2547,2,35,16,'mezbah korban bakaran dengan kisi-kisi tembaganya, kayu pengusungnya dan segala perkakasnya, bejana pembasuhan dengan alasnya;'),(2548,2,35,17,'layar pelataran, tiangnya, alasnya dan tirai pintu gerbang pelataran itu;'),(2549,2,35,18,'patok Kemah Suci dan patok pelataran dan talinya;'),(2550,2,35,19,'pakaian jabatan untuk menyelenggarakan kebaktian di tempat kudus, dan pakaian kudus bagi imam Harun, dan pakaian anak-anaknya untuk memegang jabatan imam.\"'),(2551,2,35,20,'Lalu pergilah segenap jemaah Israel dari depan Musa.'),(2552,2,35,21,'Sesudah itu datanglah setiap orang yang tergerak hatinya, setiap orang yang terdorong jiwanya, membawa persembahan khusus kepada TUHAN untuk pekerjaan melengkapi Kemah Pertemuan dan untuk segala ibadah di dalamnya dan untuk pakaian kudus itu.'),(2553,2,35,22,'Maka datanglah mereka, baik laki-laki maupun perempuan, setiap orang yang terdorong hatinya, dengan membawa anting-anting hidung, anting-anting telinga, cincin meterai dan kerongsang, segala macam barang emas; demikian juga setiap orang yang mempersembahkan persembahan unjukan dari emas bagi TUHAN.'),(2554,2,35,23,'Juga setiap orang yang mempunyai kain ungu tua, kain ungu muda, kain kirmizi, lenan halus, bulu kambing, kulit domba jantan yang diwarnai merah dan kulit lumba-lumba, datang membawanya.'),(2555,2,35,24,'Setiap orang yang hendak mempersembahkan persembahan khusus dari perak atau tembaga, membawa persembahan khusus yang kepada TUHAN itu, dan setiap orang yang mempunyai kayu penaga membawanya juga untuk segala pekerjaan mendirikan itu.'),(2556,2,35,25,'Setiap perempuan yang ahli, memintal dengan tangannya sendiri dan membawa yang dipintalnya itu, yakni kain ungu tua, kain ungu muda, kain kirmizi dan lenan halus.'),(2557,2,35,26,'Semua perempuan yang tergerak hatinya oleh karena ia berkeahlian, memintal bulu kambing.'),(2558,2,35,27,'Pemimpin-pemimpin membawa permata krisopras dan permata tatahan untuk baju efod dan untuk tutup dada,'),(2559,2,35,28,'rempah-rempah dan minyak untuk penerangan, untuk minyak urapan dan untuk ukupan dari wangi-wangian.'),(2560,2,35,29,'Semua laki-laki dan perempuan, yang terdorong hatinya akan membawa sesuatu untuk segala pekerjaan yang diperintahkan TUHAN dengan perantaraan Musa untuk dilakukan--mereka itu, yakni orang Israel, membawanya sebagai pemberian sukarela bagi TUHAN.'),(2561,2,35,30,'Berkatalah Musa kepada orang Israel: \"Lihatlah, TUHAN telah menunjuk Bezaleel bin Uri bin Hur, dari suku Yehuda,'),(2562,2,35,31,'dan telah memenuhinya dengan Roh Allah, dengan keahlian, pengertian dan pengetahuan, dalam segala macam pekerjaan,'),(2563,2,35,32,'yakni untuk membuat berbagai rancangan supaya dikerjakan dari emas, perak dan tembaga;'),(2564,2,35,33,'untuk mengasah batu permata supaya ditatah; untuk mengukir kayu dan untuk bekerja dalam segala macam pekerjaan yang dirancang itu.'),(2565,2,35,34,'Dan TUHAN menanam dalam hatinya, dan dalam hati Aholiab bin Ahisamakh dari suku Dan, kepandaian untuk mengajar.'),(2566,2,35,35,'Ia telah memenuhi mereka dengan keahlian, untuk membuat segala macam pekerjaan seorang tukang, pekerjaan seorang ahli, pekerjaan seorang yang membuat tenunan yang berwarna-warna dari kain ungu tua, kain ungu muda, kain kirmizi dan lenan halus, dan pekerjaan seorang tukang tenun, yakni sebagai pelaksana segala macam pekerjaan dan perancang segala sesuatu.'),(2567,2,36,1,'Demikianlah harus bekerja Bezaleel dan Aholiab, dan setiap orang yang ahli, yang telah dikaruniai TUHAN keahlian dan pengertian, sehingga ia tahu melakukan segala macam pekerjaan untuk mendirikan tempat kudus, tepat menurut yang diperintahkan TUHAN.\"'),(2568,2,36,2,'Lalu Musa memanggil Bezaleel dan Aholiab dan setiap orang yang ahli, yang dalam hatinya telah ditanam TUHAN keahlian, setiap orang yang tergerak hatinya untuk datang melakukan pekerjaan itu.'),(2569,2,36,3,'Mereka menerima dari pada Musa seluruh persembahan khusus, yang telah dibawa oleh orang Israel untuk melaksanakan pekerjaan mendirikan tempat kudus. Tetapi orang Israel itu masih terus membawa pemberian sukarela kepada Musa tiap-tiap pagi.'),(2570,2,36,4,'Dan segala orang ahli yang melakukan seluruh pekerjaan untuk tempat kudus itu, datanglah masing-masing dari pekerjaan yang dilakukannya,'),(2571,2,36,5,'dan berkata kepada Musa: \"Rakyat membawa lebih banyak dari yang diperlukan untuk mengerjakan pekerjaan yang diperintahkan TUHAN untuk dilakukan.\"'),(2572,2,36,6,'Lalu Musa memerintahkan, supaya dimaklumkan di mana-mana di perkemahan itu, demikian: \"Tidak usah lagi ada orang laki-laki atau perempuan yang membuat sesuatu menjadi persembahan khusus bagi tempat kudus.\" Demikianlah rakyat itu dicegah membawa persembahan lagi.'),(2573,2,36,7,'Sebab bahan yang diperlukan mereka telah cukup untuk melakukan segala pekerjaan itu, bahkan berlebih.'),(2574,2,36,8,'Lalu semua ahli di antara tukang-tukang itu membuat Kemah Suci dari sepuluh tenda dari lenan halus yang dipintal benangnya dan dari kain ungu tua, kain ungu muda dan kain kirmizi; dengan ada kerubnya, buatan ahli tenun, dibuat orang semuanya itu.'),(2575,2,36,9,'Panjang tiap-tiap tenda dua puluh delapan hasta dan lebar tiap-tiap tenda empat hasta: semua tenda itu sama ukurannya.'),(2576,2,36,10,'Lima dari tenda itu dirangkap menjadi satu, dan yang lima lagi juga dirangkap menjadi satu.'),(2577,2,36,11,'Pada rangkapan yang pertama, di tepi satu tenda yang di ujung, dibuatlah sosok-sosok kain ungu tua dan demikian juga di tepi satu tenda yang paling ujung pada rangkapan yang kedua.'),(2578,2,36,12,'Lima puluh sosok dibuat orang pada tenda yang pertama dan lima puluh sosok pada tenda yang di ujung pada rangkapan yang kedua, sehingga sosok-sosok itu tepat berhadapan satu sama lain.'),(2579,2,36,13,'Dibuatlah lima puluh kaitan emas dan disambunglah tenda-tenda Kemah Suci, yang satu dengan yang lain, dengan memakai kaitan itu, sehingga menjadi satu.'),(2580,2,36,14,'Dibuatlah tenda-tenda dari bulu kambing menjadi atap kemah yang menudungi Kemah Suci, sebelas tenda dibuat orang.'),(2581,2,36,15,'Panjang tiap-tiap tenda tiga puluh hasta dan empat hasta lebarnya tiap-tiap tenda: yang sebelas tenda itu sama ukurannya.'),(2582,2,36,16,'Disambunglah lima dari tenda itu dengan tersendiri dan enam dari tenda itu dengan tersendiri.'),(2583,2,36,17,'Dibuatlah lima puluh sosok pada rangkapan yang pertama di tepi satu tenda yang di ujung, dan dibuat lima puluh sosok di tepi satu tenda pada rangkapan yang kedua.'),(2584,2,36,18,'Dibuat oranglah lima puluh kaitan tembaga untuk menyambung tenda-tenda kemah itu, sehingga menjadi satu.'),(2585,2,36,19,'Juga dibuatlah untuk kemah itu tudung dari kulit domba jantan yang diwarnai merah, dan tudung dari kulit lumba-lumba di atasnya lagi.'),(2586,2,36,20,'Dibuat oranglah untuk Kemah Suci itu papan dari kayu penaga yang berdiri tegak,'),(2587,2,36,21,'sepuluh hasta panjangnya satu papan dan satu setengah hasta lebarnya tiap-tiap papan.'),(2588,2,36,22,'Tiap-tiap papan ada dua pasaknya yang disengkang satu sama lain; demikianlah diperbuat dengan segala papan Kemah Suci.'),(2589,2,36,23,'Dibuat oranglah papan-papan untuk Kemah Suci, dua puluh papan pada sebelah selatan.'),(2590,2,36,24,'Dan empat puluh alas perak dibuat orang di bawah kedua puluh papan itu, dua alas di bawah satu papan untuk kedua pasaknya, dan seterusnya dua alas di bawah setiap papan untuk kedua pasaknya.'),(2591,2,36,25,'Juga dibuat orang untuk sisi yang kedua dari Kemah Suci, pada sebelah utara, dua puluh papan'),(2592,2,36,26,'dengan empat puluh alas peraknya: dua alas di bawah satu papan dan seterusnya dua alas di bawah setiap papan.'),(2593,2,36,27,'Untuk sisi belakang Kemah Suci, pada sebelah barat, dibuat oranglah enam papan.'),(2594,2,36,28,'Dua papan dibuat orang untuk sudut Kemah Suci, di sisi belakang.'),(2595,2,36,29,'Kedua papan itu kembar pasaknya di sebelah bawah dan seperti itu juga kembar pasaknya di sebelah atas, di dekat gelang yang satu itu, demikianlah dibuat orang dengan kedua papan yang untuk kedua sudutnya itu.'),(2596,2,36,30,'Jadi ada delapan papan dengan alas peraknya: enam belas alas; dua-dua alas di bawah satu papan.'),(2597,2,36,31,'Dibuatlah juga kayu lintang dari kayu penaga: lima untuk papan-papan pada sisi yang satu dari Kemah Suci,'),(2598,2,36,32,'lima kayu lintang untuk papan-papan pada sisi yang kedua dari Kemah Suci, dan lima kayu lintang untuk papan-papan Kemah Suci yang merupakan sisi belakangnya, pada sebelah barat.'),(2599,2,36,33,'Dibuat oranglah kayu lintang yang di tengah menjadi melintang terus di tengah-tengah papan-papan itu dari ujung ke ujung.'),(2600,2,36,34,'Papan-papan itu disalut dengan emas, gelang-gelang itu dibuat dari emas sebagai tempat memasukkan kayu-kayu lintang itu, dan kayu-kayu lintang itu disalut dengan emas.'),(2601,2,36,35,'Dibuatlah tabir itu dari kain ungu tua, dan kain ungu muda, kain kirmizi dan lenan halus yang dipintal benangnya; dibuat dengan ada kerubnya, buatan ahli tenun.'),(2602,2,36,36,'Dibuat oranglah untuk itu empat tiang dari kayu penaga dan disalutlah itu dengan emas, dengan ada kaitannya dari emas, lagi dituanglah empat alas perak untuk tiang itu.'),(2603,2,36,37,'Juga dibuat oranglah tirai untuk pintu kemah itu dari kain ungu tua, kain ungu muda, kain kirmizi dan lenan halus yang dipintal benangnya: tenunan yang berwarna-warna;'),(2604,2,36,38,'dan kelima tiangnya dengan kaitan untuk tiang itu; disalutlah ujungnya dan penyambung-penyambungnya dengan emas, dan kelima alasnya itu adalah dari tembaga.'),(2605,2,37,1,'Bezaleel membuat tabut itu dari kayu penaga, dua setengah hasta panjangnya, satu setengah hasta lebarnya, dan satu setengah hasta tingginya.'),(2606,2,37,2,'Disalutnyalah itu dengan emas murni, dari dalam dan dari luar, dan dibuatnyalah bingkai emas sekelilingnya.'),(2607,2,37,3,'Dituangnyalah empat gelang emas untuk tabut itu, pada keempat penjurunya, yaitu dua gelang pada rusuknya yang satu dan dua gelang pada rusuknya yang kedua.'),(2608,2,37,4,'Dibuatnyalah kayu pengusung dari kayu penaga dan disalutnyalah itu dengan emas.'),(2609,2,37,5,'Dan dimasukkannyalah kayu pengusung itu ke dalam gelang yang pada rusuk tabut itu, supaya tabut dapat diangkut.'),(2610,2,37,6,'Dibuatnyalah tutup pendamaian dari emas murni, dua setengah hasta panjangnya dan satu setengah hasta lebarnya.'),(2611,2,37,7,'Dibuatnyalah dua kerub dari emas, dari emas tempaan dibuatnya itu, pada kedua ujung tutup pendamaian itu,'),(2612,2,37,8,'satu kerub pada ujung sebelah sini dan satu kerub pada ujung sebelah sana; seiras dengan tutup pendamaian itu dibuatnya kerub itu pada kedua ujungnya.'),(2613,2,37,9,'Kerub-kerub itu mengembangkan kedua sayapnya ke atas, sayap-sayapnya menudungi tutup pendamaian itu dan mukanya menghadap kepada masing-masing; kepada tutup pendamaian itulah menghadap muka kerub-kerub itu.'),(2614,2,37,10,'Dibuatnyalah meja itu dari kayu penaga, dua hasta panjangnya, sehasta lebarnya dan satu setengah hasta tingginya.'),(2615,2,37,11,'Disalutnyalah itu dengan emas murni dan dibuatnya bingkai emas sekelilingnya.'),(2616,2,37,12,'Dibuatnyalah sekelilingnya jalur pinggir yang setapak tangan lebarnya dan dibuatnya bingkai emas sekeliling jalur pinggirnya itu.'),(2617,2,37,13,'Dituangnyalah untuk meja itu empat gelang emas dan dipasangnyalah gelang-gelang itu di keempat penjurunya, pada keempat kakinya.'),(2618,2,37,14,'Dekat ke jalur pinggirnyalah gelang itu, yakni tempat memasukkan kayu pengusung, supaya meja itu dapat diangkut.'),(2619,2,37,15,'Dibuatnyalah kayu pengusung itu dari kayu penaga dan disalutnya dengan emas, yaitu supaya meja itu dapat diangkut.'),(2620,2,37,16,'Dan dibuatnyalah perkakas yang di atas meja itu, yakni pinggannya, cawannya, piala dan kendinya, yang dipakai untuk persembahan curahan, semuanya dari emas murni.'),(2621,2,37,17,'Dibuatnyalah kandil itu dari emas murni; dari emas tempaan dibuatnya kandil itu, baik kakinya baik batangnya; kelopaknya--dengan tombolnya dan kembangnya--dibuat seiras dengan kandil itu.'),(2622,2,37,18,'Ada enam cabang timbul dari sisinya: tiga cabang kandil itu dari sisi yang satu dan tiga cabang dari sisi yang lain.'),(2623,2,37,19,'Tiga kelopak yang berupa bunga badam pada cabang yang satu--dengan tombol dan kembangnya--dan tiga kelopak yang serupa pada cabang yang lain--dengan tombol dan kembangnya--;demikian juga dibuat keenam cabang yang timbul dari kandil itu.'),(2624,2,37,20,'Pada kandil itu sendiri ada empat kelopak berupa bunga badam--dengan tombolnya dan kembangnya.'),(2625,2,37,21,'Juga ada satu tombol di bawah sepasang cabang yang pertama yang timbul dari kandil itu, dan satu tombol di bawah yang kedua, dan satu tombol di bawah yang ketiga; demikianlah juga dibuat keenam cabang yang timbul dari situ.'),(2626,2,37,22,'Tombol dan cabang itu timbul dari kandil itu, dan semuanya itu dibuat dari sepotong emas tempaan yang murni.'),(2627,2,37,23,'Dibuatnyalah pada kandil itu tujuh lampu dengan sepitnya dan penadahnya dari emas murni.'),(2628,2,37,24,'Dari satu talenta emas murni dibuatnyalah kandil itu dengan segala perkakasnya.'),(2629,2,37,25,'Dibuatnyalah mezbah pembakaran ukupan itu dari kayu penaga, sehasta panjangnya dan sehasta lebarnya, empat persegi, tetapi dua hasta tingginya; tanduk-tanduknya seiras dengan mezbah itu.'),(2630,2,37,26,'Disalutnyalah itu dengan emas murni, bidang atasnya dan bidang-bidang sisinya sekelilingnya, serta tanduk-tanduknya. Dibuatnyalah bingkai emas sekelilingnya.'),(2631,2,37,27,'Dibuatnyalah dua gelang emas untuk mezbah itu di bawah bingkainya, pada kedua rusuknya, pada kedua bidang sisinya, sebagai tempat memasukkan kayu pengusung, supaya dengan itu mezbah dapat diangkut.'),(2632,2,37,28,'Dan dibuatnyalah kayu pengusung itu dari kayu penaga dan disalutnya dengan emas.'),(2633,2,37,29,'Dan dibuatnyalah minyak urapan yang kudus itu dan ukupan murni dari wangi-wangian, seperti buatan seorang tukang campur rempah-rempah.'),(2634,2,38,1,'Dibuatnyalah mezbah korban bakaran itu dari kayu penaga, lima hasta panjangnya dan lima hasta lebarnya, empat persegi, tetapi tiga hasta tingginya.'),(2635,2,38,2,'Dibuatnyalah tanduk-tanduknya pada keempat sudutnya; tanduk-tanduknya itu dibuat seiras dengan mezbah itu dan disalutnya dengan tembaga.'),(2636,2,38,3,'Dibuatnyalah segala perkakas mezbah itu, yakni kuali-kuali, sodok-sodok, bokor-bokor penyiraman, garpu-garpu dan perbaraan-perbaraan, semua perkakasnya itu dibuatnya dari tembaga.'),(2637,2,38,4,'Dibuatnyalah untuk mezbah itu kisi-kisi, yakni jala-jala tembaga, di bawah jalur, mulai dari sebelah bawah sampai setengah tingginya.'),(2638,2,38,5,'Dituangnyalah empat gelang pada keempat ujung kisi-kisi tembaga itu, yakni tempat memasukkan kayu pengusung.'),(2639,2,38,6,'Dibuatnyalah kayu-kayu pengusung itu dari kayu penaga dan disalutnya dengan tembaga.'),(2640,2,38,7,'Dan dimasukkannyalah kayu-kayu pengusung itu ke dalam gelang-gelang yang pada rusuk mezbah itu, supaya dengan itu mezbah dapat diangkut. Mezbah itu dibuatnya berongga dan dari papan.'),(2641,2,38,8,'Dibuatnyalah bejana pembasuhan dan juga alasnya dari tembaga, dari cermin-cermin para pelayan perempuan yang melayani di depan pintu Kemah Pertemuan.'),(2642,2,38,9,'Dibuatnyalah pelataran itu; pada sebelah selatan: layar pelataran itu dari lenan halus yang dipintal benangnya, seratus hasta panjangnya;'),(2643,2,38,10,'kedua puluh tiang layar itu dengan kedua puluh alas tiang itu dari tembaga, tetapi kaitan-kaitan tiang itu dan penyambung-penyambungnya dari perak.'),(2644,2,38,11,'Pada sebelah utara: seratus hasta; kedua puluh tiang layar itu dengan kedua puluh alas tiang itu dari tembaga, tetapi kaitan-kaitan tiang itu dan penyambung-penyambungnya dari perak.'),(2645,2,38,12,'Pada sebelah barat: layar lima puluh hasta; dengan sepuluh tiangnya dan sepuluh alas tiang itu, dan kaitan-kaitan tiang itu serta penyambung-penyambungnya dari perak.'),(2646,2,38,13,'Dan pada sebelah timur: lima puluh hasta,'),(2647,2,38,14,'yakni layar lima belas hasta untuk sisi yang satu di samping pintu gerbang itu, dengan tiga tiangnya dan tiga alas tiang itu;'),(2648,2,38,15,'dan juga untuk sisi yang kedua di samping pintu gerbang itu--sebelah-menyebelah pintu gerbang pelataran itu ada layar--lima belas hasta, dengan tiga tiangnya dan tiga alas tiang itu.'),(2649,2,38,16,'Segala layar yang mengelilingi pelataran, adalah dari lenan halus yang dipintal benangnya.'),(2650,2,38,17,'Alas-alas untuk tiang-tiang itu adalah dari tembaga, tetapi kaitan-kaitan tiang itu dan penyambung-penyambungnya dari perak, juga salut kepalanya dari perak. Dihubungkanlah dengan penyambung-penyambung dari perak segala tiang-tiang pelataran itu.'),(2651,2,38,18,'Tirai pintu gerbang pelataran itu tenunan yang berwarna-warna dari kain ungu tua, kain ungu muda, kain kirmizi dan dari lenan halus yang dipintal benangnya; dua puluh hasta panjangnya, tetapi tingginya--yang juga lebar kain itu--adalah lima hasta, sama dengan tinggi layar pelataran itu.'),(2652,2,38,19,'Keempat tiangnya dan keempat alas tiang itu dari tembaga; tetapi kaitan-kaitannya dari perak, dan juga salut kepalanya, serta penyambung-penyambungnya dari perak.'),(2653,2,38,20,'Segala patok untuk Kemah Suci dan untuk pelataran itu, sekelilingnya, adalah dari tembaga.'),(2654,2,38,21,'Inilah daftar biaya untuk mendirikan Kemah Suci, yakni Kemah Suci, tempat hukum Allah, yang disusun atas perintah Musa, oleh orang Lewi di bawah pimpinan Itamar, anak imam Harun.'),(2655,2,38,22,'Bezaleel bin Uri bin Hur, dari suku Yehuda, membuat segala yang diperintahkan TUHAN kepada Musa,'),(2656,2,38,23,'dan bersama-sama dengan dia turut Aholiab, anak Ahisamakh, dari suku Dan, seorang tukang dan ahli, seorang yang membuat tenunan yang berwarna-warna dari kain ungu tua, dari kain ungu muda, dari kain kirmizi dan dari lenan halus. --'),(2657,2,38,24,'Segala emas yang dipakai untuk segala pekerjaan mendirikan tempat kudus itu, yakni emas dari persembahan unjukan, ada dua puluh sembilan talenta dan tujuh ratus tiga puluh syikal, ditimbang menurut syikal kudus.'),(2658,2,38,25,'Perak persembahan mereka yang didaftarkan dari antara jemaah itu ada seratus talenta dan seribu tujuh ratus tujuh puluh lima syikal, ditimbang menurut syikal kudus:'),(2659,2,38,26,'sebeka seorang, yaitu setengah syikal, ditimbang menurut syikal kudus, untuk setiap orang yang termasuk orang-orang yang terdaftar, yang berumur dua puluh tahun ke atas, sejumlah enam ratus tiga ribu lima ratus lima puluh orang.'),(2660,2,38,27,'Seratus talenta perak dipakai untuk menuang alas-alas tempat kudus dan alas-alas tiang tabir itu, seratus alas sesuai dengan seratus talenta itu, jadi satu talenta untuk satu alas.'),(2661,2,38,28,'Dari yang seribu tujuh ratus tujuh puluh lima syikal itu dibuatnyalah kaitan-kaitan untuk tiang-tiang itu, disalutnyalah kepala tiang itu dan dihubungkannya tiang-tiang itu dengan penyambung-penyambun'),(2662,2,38,29,'Tembaga dari persembahan unjukan itu ada tujuh puluh talenta dan dua ribu empat ratus syikal.'),(2663,2,38,30,'Dari padanya dibuatnyalah alas-alas pintu Kemah Pertemuan, dan mezbah tembaga dengan kisi-kisi tembaganya, segala perkakas mezbah itu,'),(2664,2,38,31,'alas-alas pelataran sekelilingnya, alas-alas pintu gerbang pelataran itu, segala patok Kemah Suci dan segala patok pelataran sekelilingnya.'),(2665,2,39,1,'Dari kain ungu tua, kain ungu muda dan kain kirmizi dibuat merekalah pakaian jabatan yang dipakai apabila diselenggarakan kebaktian di tempat kudus; juga dibuat mereka pakaian-pakaian kudus untuk Harun, seperti yang diperintahkan TUHAN kepada Musa.'),(2666,2,39,2,'Dibuatnyalah baju efod dari emas, kain ungu tua dan kain ungu muda, kain kirmizi dan lenan halus yang dipintal benangnya.'),(2667,2,39,3,'Mereka menempa emas papan dan dipotong-potongnyalah itu menjadi benang emas, untuk dipakankan pada kain ungu tua, pada kain ungu muda, pada kain kirmizi dan pada lenan halus: buatan seorang ahli.'),(2668,2,39,4,'Dibuat merekalah tutup bahu pada baju efod itu, yang disambung kepadanya, dikedua ujungnyalah baju efod itu disambung.'),(2669,2,39,5,'Sabuk pengikat yang ada pada baju efod itu adalah seiras dan sama buatannya dengan baju efod itu, yakni dari emas, kain ungu tua, kain ungu muda, kain kirmizi dan lenan halus yang dipintal benangnya, seperti yang diperintahkan TUHAN kepada Musa.'),(2670,2,39,6,'Dikerjakan merekalah permata krisopras, yakni dililit dengan ikat emas, diukirkan padanya nama para anak Israel, yang diukirkan seperti meterai.'),(2671,2,39,7,'Ditaruhnyalah itu pada kedua tutup bahu baju efod sebagai permata peringatan untuk mengingat orang Israel, seperti yang diperintahkan TUHAN kepada Musa.'),(2672,2,39,8,'Dibuatnyalah tutup dada, buatan seorang ahli. Buatannya sama dengan baju efod, yakni dari emas, kain ungu tua, kain ungu muda, kain kirmizi dan lenan halus yang dipintal benangnya.'),(2673,2,39,9,'Empat persegi dibuatnya itu; lipat dua dibuat mereka tutup dada itu, sejengkal panjangnya dan sejengkal lebarnya.'),(2674,2,39,10,'Ditatah merekalah itu dengan empat jajar permata: permata yaspis merah, krisolit, malakit, itulah jajar yang pertama;'),(2675,2,39,11,'jajar yang kedua: permata batu darah, lazurit, yaspis hijau;'),(2676,2,39,12,'jajar yang ketiga: permata ambar, akik, kecubung;'),(2677,2,39,13,'jajar yang keempat: permata pirus, krisopras dan nefrit. Dililit dengan ikat emas, demikianlah permata-permata itu dalam tatahannya.'),(2678,2,39,14,'Sesuai dengan nama para anak Israel, permata itu adalah dua belas banyaknya; dan pada tiap-tiap permata ada diukirkan seperti meterai, nama salah satu suku dari yang dua belas itu.'),(2679,2,39,15,'Juga dibuat merekalah untuk tutup dada itu untai berpilin, yang buatannya sebagai tali berjalin, dari emas murni.'),(2680,2,39,16,'Dibuat merekalah dua ikat emas dan dua gelang emas dan kedua gelang itu dipasang pada kedua ujung tutup dada.'),(2681,2,39,17,'Dipasang merekalah kedua untai emas yang berjalin itu pada kedua gelang itu di ujung tutup dada.'),(2682,2,39,18,'Kedua ujung lain dari kedua untai berjalin itu dipasang merekalah pada kedua ikat emas itu, demikianlah dipasang pada tutup bahu baju efod, di sebelah depannya.'),(2683,2,39,19,'Dibuat merekalah dua gelang emas dan dibubuh pada kedua ujung tutup dada itu, pada pinggirnya yang sebelah dalam, yang berhadapan dengan baju efod.'),(2684,2,39,20,'Juga dibuat merekalah dua gelang emas dan dipasang pada kedua tutup bahu baju efod, di sebelah bawah pada bagian depan, dekat ke tempat persambungannya, di sebelah atas sabuk baju efod.'),(2685,2,39,21,'Kemudian diikatkan merekalah tutup dada itu dengan gelangnya kepada gelang baju efod dengan memakai tali ungu tua, sehingga tetap di atas sabuk baju efod, dan tutup dada itu tidak dapat bergeser dari baju efod--seperti yang diperintahkan TUHAN kepada Musa.'),(2686,2,39,22,'Dibuatnyalah gamis baju efod, buatan tukang tenun, dari kain ungu tua seluruhnya.'),(2687,2,39,23,'Leher gamis itu di tengah-tengahnya seperti leher baju zirah, lehernya itu mempunyai pinggir sekelilingnya, supaya jangan koyak.'),(2688,2,39,24,'Dibuat merekalah pada ujung gamis itu buah delima dari kain ungu tua, kain ungu muda dan kain kirmizi, yang dipintal benangnya.'),(2689,2,39,25,'Dibuat merekalah giring-giring dari emas murni dan ditaruhlah giring-giring itu di antara buah delima, pada ujung gamis itu, berselang-seling di antara buah delima itu,'),(2690,2,39,26,'sehingga satu giring-giring dan satu buah delima selalu berselang-seling, pada sekeliling ujung gamis, yang dipakai apabila diselenggarakan kebaktian seperti yang diperintahkan TUHAN kepada Musa.'),(2691,2,39,27,'Dibuat merekalah kemeja dari lenan halus, buatan tukang tenun, untuk Harun dan anak-anaknya,'),(2692,2,39,28,'serban dari lenan halus, destar yang indah dari lenan halus, celana lenan dari lenan halus yang dipintal benangnya,'),(2693,2,39,29,'dan ikat pinggang dari lenan halus yang dipintal benangnya, kain ungu tua, kain ungu muda dan kain kirmizi, dari tenunan yang berwarna-warna--seperti yang diperintahkan TUHAN kepada Musa.'),(2694,2,39,30,'Dibuat merekalah patam, jamang yang kudus dari emas murni, dan pada jamang itu dituliskan tulisan, diukirkan seperti meterai: Kudus bagi TUHAN.'),(2695,2,39,31,'Dipasang merekalah pada patam itu tali ungu tua untuk mengikatkan patam itu pada serbannya, di sebelah atas--seperti yang diperintahkan TUHAN kepada Musa.'),(2696,2,39,32,'Demikianlah diselesaikan segala pekerjaan melengkapi Kemah Suci, yakni Kemah Pertemuan itu. Orang Israel telah melakukannya tepat seperti yang diperintahkan TUHAN kepada Musa, demikianlah mereka melakukannya.'),(2697,2,39,33,'Dibawa merekalah Kemah Suci itu kepada Musa, yakni kemah dengan segala perabotannya: kaitannya, papannya, kayu lintangnya, tiangnya dan alasnya,'),(2698,2,39,34,'tudung dari kulit domba jantan yang diwarnai merah, tudung dari kulit lumba-lumba, tabir penudung,'),(2699,2,39,35,'tabut hukum Allah dengan kayu-kayu pengusungnya dan tutup pendamaian,'),(2700,2,39,36,'meja, segala perkakasnya dan roti sajian,'),(2701,2,39,37,'kandil dari emas murni, lampu-lampunya--lampu yang harus teratur di atasnya--dan segala perkakasnya, minyak untuk penerangan,'),(2702,2,39,38,'mezbah dari emas, minyak urapan, ukupan dari wangi-wangian, tirai pintu kemah,'),(2703,2,39,39,'mezbah tembaga dengan kisi-kisi tembaganya, kayu-kayu pengusungnya dan segala perkakasnya, bejana pembasuhan dengan alasnya,'),(2704,2,39,40,'layar pelataran, tiangnya dan alasnya, dan tirai untuk pintu gerbang pelataran, talinya dan patoknya, segala perkakas untuk pekerjaan mendirikan Kemah Suci, yakni Kemah Pertemuan itu;'),(2705,2,39,41,'pakaian jabatan yang dipakai apabila diselenggarakan kebaktian di tempat kudus, pakaian kudus untuk imam Harun, dan pakaian anak-anaknya untuk memegang jabatan imam.'),(2706,2,39,42,'Tepat seperti yang diperintahkan TUHAN kepada Musa, demikianlah dilakukan orang Israel segala pekerjaan melengkapi itu.'),(2707,2,39,43,'Dan Musa melihat segala pekerjaan itu, dan sesungguhnyalah, mereka telah melakukannya seperti yang diperintahkan TUHAN, demikianlah mereka melakukannya. Lalu Musa memberkati mereka.'),(2708,2,40,1,'Berfirmanlah TUHAN kepada Musa:'),(2709,2,40,2,'Pada hari yang pertama dari bulan yang pertama haruslah engkau mendirikan Kemah Suci, yakni Kemah Pertemuan itu.'),(2710,2,40,3,'Kautempatkanlah di dalamnya tabut hukum dan kaupasanglah tabir sebagai penudung di depan tabut itu.'),(2711,2,40,4,'Kaubawalah ke dalamnya meja dan taruhlah di atasnya perkakas menurut susunannya; kaubawalah ke dalamnya kandil dan kaupasang lampu-lampunya di atasnya.'),(2712,2,40,5,'Kautaruhlah mezbah emas untuk membakar ukupan di depan tabut hukum. Kaugantungkanlah tirai pintu Kemah Suci.'),(2713,2,40,6,'Kautaruhlah mezbah korban bakaran di depan pintu Kemah Suci, yakni Kemah Pertemuan itu.'),(2714,2,40,7,'Kautaruhlah bejana pembasuhan di antara Kemah Pertemuan dan mezbah itu, lalu kautaruhlah air ke dalamnya.'),(2715,2,40,8,'Haruslah kaubuat pelataran keliling dan kaugantungkanlah tirai pintu gerbang pelataran itu.'),(2716,2,40,9,'Kemudian kauambillah minyak urapan dan kauurapilah Kemah Suci dengan segala yang ada di dalamnya; demikianlah harus engkau menguduskannya, dengan segala perabotannya, sehingga menjadi kudus.'),(2717,2,40,10,'Juga kauurapilah mezbah korban bakaran itu dengan segala perkakasnya; demikianlah engkau harus menguduskan mezbah itu, sehingga mezbah itu maha kudus.'),(2718,2,40,11,'Juga kauurapilah bejana pembasuhan itu dengan alasnya, dan demikianlah engkau harus menguduskannya.'),(2719,2,40,12,'Kemudian kausuruhlah Harun dan anak-anaknya datang ke pintu Kemah Pertemuan dan kaubasuhlah mereka dengan air.'),(2720,2,40,13,'Kaukenakanlah pakaian yang kudus kepada Harun, kauurapi dan kaukuduskanlah dia supaya ia memegang jabatan imam bagi-Ku.'),(2721,2,40,14,'Juga anak-anaknya kausuruhlah mendekat dan kaukenakanlah kemeja kepada mereka.'),(2722,2,40,15,'Urapilah mereka, seperti engkau mengurapi ayah mereka, supaya mereka memegang jabatan imam bagi-Ku; dan ini terjadi, supaya berdasarkan pengurapan itu mereka memegang jabatan imam untuk selama-lamanya turun-temurun.\"'),(2723,2,40,16,'Dan Musa melakukan semuanya itu tepat seperti yang diperintahkan TUHAN kepadanya, demikianlah dilakukannya.'),(2724,2,40,17,'Dan terjadilah dalam bulan yang pertama tahun yang kedua, pada tanggal satu bulan itu, maka didirikanlah Kemah Suci.'),(2725,2,40,18,'Musa mendirikan Kemah Suci itu, dipasangnyalah alas-alasnya, ditaruhnya papan-papannya, dipasangnya kayu-kayu lintangnya dan didirikannya tiang-tiangnya.'),(2726,2,40,19,'Dikembangkannyalah atap kemah yang menudungi Kemah Suci dan diletakkannyalah tudung kemah di atasnya--seperti yang diperintahkan TUHAN kepada Musa.'),(2727,2,40,20,'Diambilnyalah loh hukum Allah dan ditaruhnya ke dalam tabut, dikenakannyalah kayu pengusung pada tabut itu dan diletakkannya tutup pendamaian di atas tabut itu.'),(2728,2,40,21,'Dibawanyalah tabut itu ke dalam Kemah Suci, digantungkannyalah tabir penudung dan dipasangnya sebagai penudung di depan tabut hukum Allah--seperti yang diperintahkan TUHAN kepada Musa.'),(2729,2,40,22,'Ditaruhnyalah meja di dalam Kemah Pertemuan pada sisi Kemah Suci sebelah utara, di depan tabir itu.'),(2730,2,40,23,'Diletakkannyalah di atasnya roti sajian menurut susunannya, di hadapan TUHAN--seperti yang diperintahkan TUHAN kepada Musa.'),(2731,2,40,24,'Ditempatkannyalah kandil di dalam Kemah Pertemuan berhadapan dengan meja itu, pada sisi Kemah Suci sebelah selatan.'),(2732,2,40,25,'Dipasangnyalah lampu-lampu di atasnya di hadapan TUHAN--seperti yang diperintahkan TUHAN kepada Musa.'),(2733,2,40,26,'Ditempatkannyalah mezbah emas di dalam Kemah Pertemuan di depan tabir itu.'),(2734,2,40,27,'Dibakarnyalah di atasnya ukupan dari wangi-wangian seperti yang diperintahkan TUHAN kepada Musa.'),(2735,2,40,28,'Digantungkannyalah tirai pintu Kemah Suci.'),(2736,2,40,29,'Mezbah korban bakaran ditempatkannyalah di depan pintu Kemah Suci, yakni Kemah Pertemuan itu, dan dipersembahkannyalah di atasnya korban bakaran dan korban sajian--seperti yang diperintahkan TUHAN kepada Musa.'),(2737,2,40,30,'Ditempatkannyalah bejana pembasuhan di antara Kemah Pertemuan dan mezbah itu, lalu ditaruhnyalah air ke dalamnya untuk pembasuhan.'),(2738,2,40,31,'Musa dan Harun serta anak-anaknya membasuh tangan dan kaki mereka dengan air dari dalamnya.'),(2739,2,40,32,'Apabila mereka masuk ke dalam Kemah Pertemuan dan apabila mereka datang mendekat kepada mezbah itu, maka mereka membasuh kaki dan tangan--seperti yang diperintahkan TUHAN kepada Musa.'),(2740,2,40,33,'Didirikannyalah tiang-tiang pelataran sekeliling Kemah Suci dan mezbah itu, dan digantungkannyalah tirai pintu gerbang pelataran itu. Demikianlah diselesaikan Musa pekerjaan itu.'),(2741,2,40,34,'Lalu awan itu menutupi Kemah Pertemuan, dan kemuliaan TUHAN memenuhi Kemah Suci,'),(2742,2,40,35,'sehingga Musa tidak dapat memasuki Kemah Pertemuan, sebab awan itu hinggap di atas kemah itu, dan kemuliaan TUHAN memenuhi Kemah Suci.'),(2743,2,40,36,'Apabila awan itu naik dari atas Kemah Suci, berangkatlah orang Israel dari setiap tempat mereka berkemah.'),(2744,2,40,37,'Tetapi jika awan itu tidak naik, maka merekapun tidak berangkat sampai hari awan itu naik.'),(2745,2,40,38,'Sebab awan TUHAN itu ada di atas Kemah Suci pada siang hari, dan pada malam hari ada api di dalamnya, di depan mata seluruh umat Israel pada setiap tempat mereka berkemah.'),(2746,3,1,1,'TUHAN memanggil Musa dan berfirman kepadanya dari dalam Kemah Pertemuan:'),(2747,3,1,2,'Berbicaralah kepada orang Israel dan katakan kepada mereka: Apabila seseorang di antaramu hendak mempersembahkan persembahan kepada TUHAN, haruslah persembahanmu yang kamu persembahkan itu dari ternak, yakni dari lembu sapi atau dari kambing domba.'),(2748,3,1,3,'Jikalau persembahannya merupakan korban bakaran dari lembu, haruslah ia mempersembahkan seekor jantan yang tidak bercela. Ia harus membawanya ke pintu Kemah Pertemuan, supaya TUHAN berkenan akan dia.'),(2749,3,1,4,'Lalu ia harus meletakkan tangannya ke atas kepala korban bakaran itu, sehingga baginya persembahan itu diperkenan untuk mengadakan pendamaian baginya.'),(2750,3,1,5,'Kemudian haruslah ia menyembelih lembu itu di hadapan TUHAN, dan anak-anak Harun, imam-imam itu, harus mempersembahkan darah lembu itu dan menyiramkannya pada sekeliling mezbah yang di depan pintu Kemah Pertemuan.'),(2751,3,1,6,'Kemudian haruslah ia menguliti korban bakaran itu dan memotong-motongnya menurut bagian-bagian tertentu.'),(2752,3,1,7,'Anak-anak imam Harun haruslah menaruh api di atas mezbah dan menyusun kayu di atas api itu.'),(2753,3,1,8,'Dan mereka harus mengatur potongan-potongan korban itu dan kepala serta lemaknya di atas kayu yang sedang menyala di atas mezbah.'),(2754,3,1,9,'Tetapi isi perutnya dan betisnya haruslah dibasuh dengan air dan seluruhnya itu harus dibakar oleh imam di atas mezbah sebagai korban bakaran, sebagai korban api-apian yang baunya menyenangkan bagi TUHAN.'),(2755,3,1,10,'Jikalau persembahannya untuk korban bakaran adalah dari kambing domba, baik dari domba, maupun dari kambing, haruslah ia mempersembahkan seekor jantan yang tidak bercela.'),(2756,3,1,11,'Haruslah ia menyembelihnya pada sisi mezbah sebelah utara di hadapan TUHAN, lalu haruslah anak-anak Harun, imam-imam itu, menyiramkan darahnya pada mezbah sekelilingnya.'),(2757,3,1,12,'Kemudian haruslah ia memotong-motongnya menurut bagian-bagian tertentu, dan bersama-sama kepalanya dan lemaknya diaturlah semuanya itu oleh imam di atas kayu yang sedang menyala di atas mezbah.'),(2758,3,1,13,'Isi perut dan betisnya haruslah dibasuhnya dengan air, dan seluruhnya itu haruslah dipersembahkan oleh imam dan dibakar di atas mezbah: itulah korban bakaran, suatu korban api-apian yang baunya menyenangkan bagi TUHAN.'),(2759,3,1,14,'Jikalau persembahannya kepada TUHAN merupakan korban bakaran dari burung, haruslah ia mempersembahkan korbannya itu dari burung tekukur atau dari anak burung merpati.'),(2760,3,1,15,'Imam harus membawanya ke mezbah, lalu memulas kepalanya dan membakarnya di atas mezbah. Darahnya harus ditekan ke luar pada dinding mezbah.'),(2761,3,1,16,'Temboloknya serta dengan bulunya haruslah disisihkan dan dibuang ke samping mezbah sebelah timur, ke tempat abu.'),(2762,3,1,17,'Dan ia harus mencabik burung itu pada pangkal sayapnya, tetapi tidak sampai terpisah; lalu imam harus membakarnya di atas mezbah, di atas kayu yang sedang terbakar; itulah korban bakaran, suatu korban api-apian yang baunya menyenangkan bagi TUHAN.\"'),(2763,3,2,1,'Apabila seseorang hendak mempersembahkan persembahan berupa korban sajian kepada TUHAN, hendaklah persembahannya itu tepung yang terbaik dan ia harus menuangkan minyak serta membubuhkan kemenyan ke atasnya.'),(2764,3,2,2,'Lalu korban itu harus dibawanya kepada anak-anak Harun, imam-imam itu. Setelah diambil dari korban itu tepung segenggam dengan minyak beserta seluruh kemenyannya, maka imam haruslah membakar semuanya itu di atas mezbah sebagai bagian ingat-ingatan korban itu, sebagai korban api-apian yang baunya menyenangkan bagi TUHAN.'),(2765,3,2,3,'Korban sajian selebihnya adalah teruntuk bagi Harun dan anak-anaknya, yakni bagian maha kudus dari segala korban api-apian TUHAN.'),(2766,3,2,4,'Apabila engkau hendak mempersembahkan persembahan berupa korban sajian dari apa yang dibakar di dalam pembakaran roti, haruslah itu dari tepung yang terbaik, berupa roti bundar yang tidak beragi, yang diolah dengan minyak, atau roti tipis yang tidak beragi, yang diolesi dengan minyak.'),(2767,3,2,5,'Jikalau persembahanmu merupakan korban sajian dari yang dipanggang di atas panggangan, haruslah itu dari tepung yang terbaik, diolah dengan minyak, berupa roti yang tidak beragi.'),(2768,3,2,6,'Korban itu harus dipotong-potong, lalu kautuangkanlah minyak ke atasnya; itulah korban sajian.'),(2769,3,2,7,'Jikalau persembahanmu merupakan korban sajian dari yang dimasak di dalam wajan, haruslah itu diolah dari tepung yang terbaik bersama-sama minyak.'),(2770,3,2,8,'Maka korban sajian yang diolah menurut salah satu cara itu haruslah kaupersembahkan kepada TUHAN, yakni harus disampaikan kepada imam, yang membawanya ke mezbah.'),(2771,3,2,9,'Kemudian imam harus mengkhususkan dari korban sajian itu bagian ingat-ingatannya lalu membakarnya di atas mezbah sebagai korban api-apian yang baunya menyenangkan bagi TUHAN.'),(2772,3,2,10,'Korban sajian selebihnya adalah bagian Harun dan anak-anaknya, yakni bagian maha kudus dari segala korban api-apian TUHAN!'),(2773,3,2,11,'Suatu korban sajian yang kamu persembahkan kepada TUHAN janganlah diolah beragi, karena dari ragi atau dari madu tidak boleh kamu membakar sesuatupun sebagai korban api-apian bagi TUHAN.'),(2774,3,2,12,'Tetapi sebagai persembahan dari hasil pertama boleh kamu mempersembahkannya kepada TUHAN, hanya janganlah dibawa ke atas mezbah menjadi bau yang menyenangkan.'),(2775,3,2,13,'Dan tiap-tiap persembahanmu yang berupa korban sajian haruslah kaububuhi garam, janganlah kaulalaikan garam perjanjian Allahmu dari korban sajianmu; beserta segala persembahanmu haruslah kaupersembahkan garam.'),(2776,3,2,14,'Jikalau engkau hendak mempersembahkan korban sajian dari hulu hasil kepada TUHAN, haruslah engkau mempersembahkan bulir gandum yang dipanggang di atas api, emping gandum baru, sebagai korban sajian dari hulu hasil gandummu.'),(2777,3,2,15,'Haruslah kaububuh minyak dan kautaruh kemenyan ke atasnya; itulah korban sajian.'),(2778,3,2,16,'Haruslah imam membakar sebagai ingat-ingatannya, sebagian dari emping gandumnya dan minyaknya beserta seluruh kemenyannya sebagai korban api-apian bagi TUHAN.\"'),(2779,3,3,1,'Jikalau persembahannya merupakan korban keselamatan, maka jikalau yang dipersembahkannya itu dari lembu, seekor jantan atau seekor betina, haruslah ia membawa yang tidak bercela ke hadapan TUHAN.'),(2780,3,3,2,'Lalu ia harus meletakkan tangannya di atas kepala persembahannya itu, dan menyembelihnya di depan pintu Kemah Pertemuan, lalu anak-anak Harun, imam-imam itu haruslah menyiramkan darahnya pada mezbah sekelilingnya.'),(2781,3,3,3,'Kemudian dari korban keselamatan itu ia harus mempersembahkan lemak yang menyelubungi isi perut, dan segala lemak yang melekat pada isi perut itu sebagai korban api-apian bagi TUHAN,'),(2782,3,3,4,'dan lagi kedua buah pinggang dan lemak yang melekat padanya, yang ada pada pinggang dan umbai hati yang harus dipisahkannya beserta buah pinggang itu.'),(2783,3,3,5,'Anak-anak Harun harus membakarnya di atas mezbah, yakni di atas korban bakaran yang sedang dibakar di atas api, sebagai korban api-apian yang baunya menyenangkan bagi TUHAN.'),(2784,3,3,6,'Jikalau persembahannya untuk korban keselamatan bagi TUHAN adalah dari kambing domba, seekor jantan atau seekor betina, haruslah ia mempersembahkan yang tidak bercela.'),(2785,3,3,7,'Jikalau ia mempersembahkan seekor domba sebagai persembahannya, ia harus membawanya ke hadapan TUHAN.'),(2786,3,3,8,'Lalu ia harus meletakkan tangannya ke atas kepala persembahannya itu dan menyembelihnya di depan Kemah Pertemuan, lalu anak-anak Harun harus menyiramkan darahnya pada mezbah sekelilingnya.'),(2787,3,3,9,'Kemudian dari korban keselamatan itu ia harus mempersembahkan lemaknya sebagai korban api-apian bagi TUHAN, yakni segenap ekornya yang berlemak yang harus dipotong dekat pada tulang belakang, dan lemak yang menyelubungi isi perut, dan segala lemak yang melekat pada isi perut itu,'),(2788,3,3,10,'dan lagi kedua buah pinggang dan lemak yang melekat padanya, yang ada pada pinggang, dan umbai hati yang harus dipisahkannya beserta buah pinggang itu.'),(2789,3,3,11,'Imam harus membakarnya di atas mezbah sebagai santapan berupa korban api-apian bagi TUHAN.'),(2790,3,3,12,'Jikalau persembahannya seekor kambing, ia harus membawanya ke hadapan TUHAN.'),(2791,3,3,13,'Lalu ia harus meletakkan tangannya di atas kepala kambing itu dan menyembelihnya di depan Kemah Pertemuan, lalu anak-anak Harun harus menyiramkan darahnya pada mezbah sekelilingnya.'),(2792,3,3,14,'Kemudian dari kambing itu ia harus mempersembahkan lemak yang menyelubungi isi perut, dan segala lemak yang melekat pada isi perut itu sebagai persembahannya berupa korban api-apian bagi TUHAN,'),(2793,3,3,15,'dan lagi kedua buah pinggang dan lemak yang melekat padanya, yang ada pada pinggang dan umbai hati yang harus dipisahkannya beserta buah pinggang itu.'),(2794,3,3,16,'Imam harus membakar semuanya itu di atas mezbah sebagai santapan berupa korban api-apian menjadi bau yang menyenangkan. Segala lemak adalah kepunyaan TUHAN.'),(2795,3,3,17,'Inilah suatu ketetapan untuk selamanya bagi kamu turun-temurun di segala tempat kediamanmu: janganlah sekali-kali kamu makan lemak dan darah.\"'),(2796,3,4,1,'TUHAN berfirman kepada Musa:'),(2797,3,4,2,'Katakanlah kepada orang Israel: Apabila seseorang tidak dengan sengaja berbuat dosa dalam sesuatu hal yang dilarang TUHAN dan ia memang melakukan salah satu dari padanya,'),(2798,3,4,3,'maka jikalau yang berbuat dosa itu imam yang diurapi, sehingga bangsanya turut bersalah, haruslah ia mempersembahkan kepada TUHAN karena dosa yang telah diperbuatnya itu, seekor lembu jantan muda yang tidak bercela sebagai korban penghapus dosa.'),(2799,3,4,4,'Ia harus membawa lembu itu ke pintu Kemah Pertemuan, ke hadapan TUHAN, lalu ia harus meletakkan tangannya ke atas kepala lembu itu, dan menyembelih lembu itu di hadapan TUHAN.'),(2800,3,4,5,'Imam yang diurapi itu harus mengambil sebagian dari darah lembu itu, lalu membawanya ke dalam Kemah Pertemuan.'),(2801,3,4,6,'Imam harus mencelupkan jarinya ke dalam darah itu, dan memercikkan sedikit dari darah itu, tujuh kali di hadapan TUHAN, di depan tabir penyekat tempat kudus.'),(2802,3,4,7,'Kemudian imam itu harus membubuh sedikit dari darah itu pada tanduk-tanduk mezbah pembakaran ukupan dari wangi-wangian, yang ada di hadapan TUHAN di dalam Kemah Pertemuan, dan semua darah selebihnya harus dicurahkannya kepada bagian bawah mezbah korban bakaran yang di depan pintu Kemah Pertemuan.'),(2803,3,4,8,'Segala lemak lembu jantan korban penghapus dosa itu harus dikhususkannya dari lembu itu, yakni lemak yang menyelubungi isi perut dan segala lemak yang melekat pada isi perut itu,'),(2804,3,4,9,'dan lagi kedua buah pinggang dan lemak yang melekat padanya, yang ada pada pinggang, dan umbai hati yang harus dipisahkannya beserta buah pinggang itu,'),(2805,3,4,10,'sama seperti yang dikhususkan dari lembu korban keselamatan. Imam harus membakar semuanya di atas mezbah korban bakaran.'),(2806,3,4,11,'Adapun kulit lembu jantan itu dan segala dagingnya, beserta kepala dan betisnya dan isi perutnya dan kotorannya,'),(2807,3,4,12,'jadi lembu jantan itu seluruhnya harus dibawanya ke luar perkemahan, ke suatu tempat yang tahir, ke tempat pembuangan abu, dan lembu itu harus dibakarnya sampai habis di atas kayu api di tempat pembuangan abu.'),(2808,3,4,13,'Jikalau yang berbuat dosa dengan tak sengaja itu segenap umat Israel, dan jemaah tidak menyadarinya, sehingga mereka melakukan salah satu hal yang dilarang TUHAN, dan mereka bersalah,'),(2809,3,4,14,'maka apabila dosa yang diperbuat mereka itu ketahuan, haruslah jemaah itu mempersembahkan seekor lembu jantan yang muda sebagai korban penghapus dosa. Lembu itu harus dibawa mereka ke depan Kemah Pertemuan.'),(2810,3,4,15,'Lalu para tua-tua umat itu harus meletakkan tangan mereka di atas kepala lembu jantan itu di hadapan TUHAN, dan lembu itu harus disembelih di hadapan TUHAN.'),(2811,3,4,16,'Imam yang diurapi harus membawa sebagian dari darah lembu itu ke dalam Kemah Pertemuan.'),(2812,3,4,17,'Imam harus mencelupkan jarinya ke dalam darah itu dan memercikkannya tujuh kali di hadapan TUHAN, di depan tabir.'),(2813,3,4,18,'Kemudian dari darah itu harus dibubuhnya sedikit pada tanduk-tanduk mezbah yang di hadapan TUHAN di dalam Kemah Pertemuan, dan semua darah selebihnya harus dicurahkannya kepada bagian bawah mezbah korban bakaran yang di depan pintu Kemah Pertemuan.'),(2814,3,4,19,'Segala lemak harus dikhususkannya dari lembu itu dan dibakarnya di atas mezbah.'),(2815,3,4,20,'Beginilah harus diperbuatnya dengan lembu jantan itu: seperti yang diperbuatnya dengan lembu jantan korban penghapus dosa, demikianlah harus diperbuatnya dengan lembu itu. Dengan demikian imam itu mengadakan pendamaian bagi mereka, sehingga mereka menerima pengampunan.'),(2816,3,4,21,'Dan haruslah ia membawa lembu jantan itu ke luar perkemahan, lalu membakarnya sampai habis seperti ia membakar habis lembu jantan yang pertama. Itulah korban penghapus dosa untuk jemaah.'),(2817,3,4,22,'Jikalau yang berbuat dosa itu seorang pemuka yang tidak dengan sengaja melakukan salah satu hal yang dilarang TUHAN, Allahnya, sehingga ia bersalah,'),(2818,3,4,23,'maka jikalau dosa yang telah diperbuatnya itu diberitahukan kepadanya, haruslah ia membawa sebagai persembahannya seekor kambing jantan yang tidak bercela.'),(2819,3,4,24,'Lalu haruslah ia meletakkan tangannya ke atas kepala kambing itu dan menyembelihnya di tempat yang biasa orang menyembelih korban bakaran di hadapan TUHAN; itulah korban penghapus dosa.'),(2820,3,4,25,'Kemudian haruslah imam mengambil dengan jarinya sedikit dari darah korban penghapus dosa itu, lalu membubuhnya pada tanduk-tanduk mezbah korban bakaran. Darah selebihnya haruslah dicurahkannya kepada bagian bawah mezbah korban bakaran.'),(2821,3,4,26,'Tetapi segala lemak harus dibakarnya di atas mezbah, seperti juga lemak korban keselamatan. Dengan demikian imam mengadakan pendamaian bagi orang itu karena dosanya, sehingga ia menerima pengampunan.'),(2822,3,4,27,'Jikalau yang berbuat dosa dengan tak sengaja itu seorang dari rakyat jelata, dan ia melakukan salah satu hal yang dilarang TUHAN, sehingga ia bersalah,'),(2823,3,4,28,'maka jikalau dosa yang telah diperbuatnya itu diberitahukan kepadanya, haruslah ia membawa sebagai persembahannya karena dosa yang telah diperbuatnya itu seekor kambing betina yang tidak bercela.'),(2824,3,4,29,'Lalu haruslah ia meletakkan tangannya ke atas kepala korban penghapus dosa dan menyembelih korban itu di tempat korban bakaran.'),(2825,3,4,30,'Kemudian imam harus mengambil dengan jarinya sedikit dari darah korban itu, lalu membubuhnya pada tanduk-tanduk mezbah korban bakaran. Semua darah selebihnya haruslah dicurahkannya kepada bagian bawah mezbah.'),(2826,3,4,31,'Tetapi segala lemak haruslah dipisahkannya, seperti juga lemak korban keselamatan dipisahkan, lalu haruslah dibakar oleh imam di atas mezbah menjadi bau yang menyenangkan bagi TUHAN. Dengan demikian imam mengadakan pendamaian bagi orang itu sehingga ia menerima pengampunan.'),(2827,3,4,32,'Jika ia membawa seekor domba sebagai persembahannya menjadi korban penghapus dosa, haruslah ia membawa seekor betina yang tidak bercela.'),(2828,3,4,33,'Lalu haruslah ia meletakkan tangannya ke atas kepala korban penghapus dosa itu, dan menyembelihnya menjadi korban penghapus dosa di tempat yang biasa orang menyembelih korban bakaran.'),(2829,3,4,34,'Kemudian imam harus mengambil dengan jarinya sedikit dari darah korban penghapus dosa itu, lalu membubuhnya pada tanduk-tanduk mezbah korban bakaran. Semua darah selebihnya haruslah dicurahkannya kepada bagian bawah mezbah.'),(2830,3,4,35,'Tetapi segala lemak haruslah dipisahkannya, seperti juga lemak domba korban keselamatan dipisahkan, lalu imam harus membakar semuanya itu di atas mezbah di atas segala korban api-apian TUHAN. Dengan demikian imam mengadakan pendamaian bagi orang itu karena dosa yang telah diperbuatnya, sehingga ia menerima pengampunan.'),(2831,3,5,1,'Apabila seseorang berbuat dosa, yakni jika ia mendengar seorang mengutuki, dan ia dapat naik saksi karena ia melihat atau mengetahuinya, tetapi ia tidak mau memberi keterangan, maka ia harus menanggung kesalahannya sendiri.'),(2832,3,5,2,'Atau bila seseorang kena kepada sesuatu yang najis, baik bangkai binatang liar yang najis, atau bangkai hewan yang najis, atau bangkai binatang yang mengeriap yang najis, tanpa menyadari hal itu, maka ia menjadi najis dan bersalah.'),(2833,3,5,3,'Atau apabila ia kena kepada kenajisan berasal dari manusia, dengan kenajisan apapun juga ia menjadi najis, tanpa menyadari hal itu, tetapi kemudian ia mengetahuinya, maka ia bersalah.'),(2834,3,5,4,'Atau apabila seseorang bersumpah teledor dengan bibirnya hendak berbuat yang buruk atau yang baik, sumpah apapun juga yang diucapkan orang dengan teledor, tanpa menyadari hal itu, tetapi kemudian ia mengetahuinya, maka ia bersalah dalam salah satu perkara itu.'),(2835,3,5,5,'Jadi apabila ia bersalah dalam salah satu perkara itu, haruslah ia mengakui dosa yang telah diperbuatnya itu,'),(2836,3,5,6,'dan haruslah ia mempersembahkan kepada TUHAN sebagai tebusan salah karena dosa itu seekor betina dari domba atau kambing, menjadi korban penghapus dosa. Dengan demikian imam mengadakan pendamaian bagi orang itu karena dosanya.'),(2837,3,5,7,'Tetapi jikalau ia tidak mampu untuk menyediakan kambing atau domba, maka sebagai tebusan salah karena dosa yang telah diperbuatnya itu, haruslah ia mempersembahkan kepada TUHAN dua ekor burung tekukur atau dua ekor anak burung merpati, yang seekor menjadi korban penghapus dosa dan yang seekor lagi menjadi korban bakaran.'),(2838,3,5,8,'Haruslah ia membawanya kepada imam, dan imam itu haruslah lebih dahulu mempersembahkan burung untuk korban penghapus dosa itu. Dan haruslah ia memulas kepalanya pada pangkal tengkuknya, tetapi tidak sampai terpisah.'),(2839,3,5,9,'Sedikit dari darah korban penghapus dosa itu haruslah dipercikkannya ke dinding mezbah, tetapi darah selebihnya haruslah ditekan ke luar pada bagian bawah mezbah; itulah korban penghapus dosa.'),(2840,3,5,10,'Yang kedua haruslah diolahnya menjadi korban bakaran, sesuai dengan peraturan. Dengan demikian imam mengadakan pendamaian bagi orang itu karena dosa yang telah diperbuatnya, sehingga ia menerima pengampunan.'),(2841,3,5,11,'Tetapi jikalau ia tidak mampu menyediakan dua ekor burung tekukur atau dua ekor anak burung merpati, maka haruslah ia membawa sebagai persembahannya karena dosanya itu sepersepuluh efa tepung yang terbaik menjadi korban penghapus dosa. Tidak boleh ditaruhnya minyak dan dibubuhnya kemenyan di atasnya, karena itulah korban penghapus dosa.'),(2842,3,5,12,'Lalu haruslah itu dibawanya kepada imam dan imam itu haruslah mengambil dari padanya segenggam sebagai bagian ingat-ingatannya, lalu membakarnya di atas mezbah di atas segala korban.'),(2843,3,5,13,'Dengan demikian imam mengadakan pendamaian bagi orang itu karena dosanya dalam salah satu perkara itu, sehingga ia menerima pengampunan. Selebihnya adalah bagian imam, sama seperti korban sajian.\"'),(2844,3,5,14,'TUHAN berfirman kepada Musa:'),(2845,3,5,15,'Apabila seseorang berubah setia dan tidak sengaja berbuat dosa dalam sesuatu hal kudus yang dipersembahkan kepada TUHAN, maka haruslah ia mempersembahkan kepada TUHAN sebagai tebusan salahnya seekor domba jantan yang tidak bercela dari kambing domba, dinilai menurut syikal perak, yakni menurut syikal kudus, menjadi korban penebus salah.'),(2846,3,5,16,'Hal kudus yang menyebabkan orang itu berdosa, haruslah dibayar gantinya dengan menambah seperlima, lalu menyerahkannya kepada imam. Imam harus mengadakan pendamaian bagi orang itu dengan domba jantan korban penebus salah itu, sehingga ia menerima pengampunan.'),(2847,3,5,17,'Jikalau seseorang berbuat dosa dengan melakukan salah satu hal yang dilarang TUHAN tanpa mengetahuinya, maka ia bersalah dan harus menanggung kesalahannya sendiri.'),(2848,3,5,18,'Haruslah ia membawa kepada imam seekor domba jantan yang tidak bercela dari kambing domba, yang sudah dinilai, sebagai korban penebus salah. Imam itu haruslah mengadakan pendamaian bagi orang itu karena perbuatan yang tidak disengajanya dan yang tidak diketahuinya itu, sehingga ia menerima pengampunan.'),(2849,3,5,19,'Itulah korban penebus salah; orang itu sungguh bersalah terhadap TUHAN.\"'),(2850,3,6,1,'TUHAN berfirman kepada Musa:'),(2851,3,6,2,'Apabila seseorang berbuat dosa dan berubah setia terhadap TUHAN, dan memungkiri terhadap sesamanya barang yang dipercayakan kepadanya, atau barang yang diserahkan kepadanya atau barang yang dirampasnya, atau apabila ia telah melakukan pemerasan atas sesamanya,'),(2852,3,6,3,'atau bila ia menemui barang hilang, dan memungkirinya, dan ia bersumpah dusta--dalam perkara apapun yang diperbuat seseorang, sehingga ia berdosa--'),(2853,3,6,4,'apabila dengan demikian ia berbuat dosa dan bersalah, maka haruslah ia memulangkan barang yang telah dirampasnya atau yang telah diperasnya atau yang telah dipercayakan kepadanya atau barang hilang yang ditemuinya itu,'),(2854,3,6,5,'atau segala sesuatu yang dimungkirinya dengan bersumpah dusta. Haruslah ia membayar gantinya sepenuhnya dengan menambah seperlima; haruslah ia menyerahkannya kepada pemiliknya pada hari ia mempersembahkan korban penebus salahnya.'),(2855,3,6,6,'Sebagai korban penebus salahnya haruslah ia mempersembahkan kepada TUHAN seekor domba jantan yang tidak bercela dari kambing domba, yang sudah dinilai, menjadi korban penebus salah, dengan menyerahkannya kepada imam.'),(2856,3,6,7,'Imam harus mengadakan pendamaian bagi orang itu di hadapan TUHAN, sehingga ia menerima pengampunan atas perkara apapun yang diperbuatnya sehingga ia bersalah.\"'),(2857,3,6,8,'TUHAN berfirman kepada Musa:'),(2858,3,6,9,'Perintahkanlah kepada Harun dan anak-anaknya: Inilah hukum tentang korban bakaran. Korban bakaran itu haruslah tinggal di atas perapian di atas mezbah semalam-malaman sampai pagi, dan api mezbah haruslah dipelihara menyala di atasnya.'),(2859,3,6,10,'Imam haruslah mengenakan pakaian lenannya, dan mengenakan celana lenan untuk menutup auratnya. Lalu ia harus mengangkat abu yang ada di atas mezbah sesudah korban bakaran habis dimakan api, dan haruslah ia membuangnya di samping mezbah.'),(2860,3,6,11,'Kemudian haruslah ia menanggalkan pakaiannya dan mengenakan pakaian lain, lalu membawa abu itu ke luar perkemahan ke suatu tempat yang tahir.'),(2861,3,6,12,'Api yang di atas mezbah itu harus dijaga supaya terus menyala, jangan dibiarkan padam. Tiap-tiap pagi imam harus menaruh kayu di atas mezbah, mengatur korban bakaran di atasnya dan membakar segala lemak korban keselamatan di sana.'),(2862,3,6,13,'Harus dijaga supaya api tetap menyala di atas mezbah, janganlah dibiarkan padam.\"'),(2863,3,6,14,'Inilah hukum tentang korban sajian. Anak-anak Harun haruslah membawanya ke hadapan TUHAN ke depan mezbah.'),(2864,3,6,15,'Setelah dikhususkan dari korban sajian itu segenggam tepung yang terbaik dengan minyak, serta seluruh kemenyan yang di atas korban sajian itu, maka haruslah semuanya dibakar di atas mezbah sehingga baunya menyenangkan sebagai bagian ingat-ingatannya bagi TUHAN.'),(2865,3,6,16,'Selebihnya haruslah dimakan oleh Harun dan anak-anaknya; haruslah itu dimakan sebagai roti yang tidak beragi di suatu tempat yang kudus, haruslah mereka memakannya di pelataran Kemah Pertemuan.'),(2866,3,6,17,'Janganlah itu dibakar beragi. Telah Kuberikan itu sebagai bagian mereka dari pada segala korban api-apian-Ku; itulah bagian maha kudus, sama seperti korban penghapus dosa dan korban penghapus salah.'),(2867,3,6,18,'Setiap laki-laki di antara anak-anak Harun haruslah memakannya; itulah suatu ketetapan untuk selamanya bagi kamu turun-temurun; itulah bagianmu dari segala korban api-apian TUHAN. Setiap orang yang kena kepada korban-korban itu menjadi kudus.\"'),(2868,3,6,19,'TUHAN berfirman kepada Musa:'),(2869,3,6,20,'Inilah persembahan Harun dan anak-anaknya, yang harus dipersembahkan oleh mereka kepada TUHAN pada hari ia diurapi: sepersepuluh efa tepung yang terbaik sebagai korban sajian yang tetap, setengahnya pada waktu pagi dan setengahnya pada waktu petang.'),(2870,3,6,21,'Haruslah itu diolah di atas panggangan bersama-sama minyak, setelah teraduk haruslah engkau membawanya dan mempersembahkannya sebagai korban sajian, sesudah dibakar dan berpotong-potong sebagai bau yang menyenangkan bagi TUHAN.'),(2871,3,6,22,'Dan imam dari antara anak-anaknya yang diurapi sebagai penggantinya, haruslah mengolahnya; itulah suatu ketetapan untuk selamanya. Seluruhnya haruslah dibakar bagi TUHAN.'),(2872,3,6,23,'Tiap-tiap korban sajian dari seorang imam itu haruslah menjadi korban yang terbakar seluruhnya, janganlah dimakan.\"'),(2873,3,6,24,'TUHAN berfirman kepada Musa, demikian:'),(2874,3,6,25,'Katakanlah kepada Harun dan anak-anaknya: Inilah hukum tentang korban penghapus dosa. Di tempat korban bakaran disembelih, di situlah harus disembelih korban penghapus dosa di hadapan TUHAN. Itulah persembahan maha kudus.'),(2875,3,6,26,'Imam yang mempersembahkan korban penghapus dosa itulah yang harus memakannya; haruslah itu dimakan di suatu tempat yang kudus, di pelataran Kemah Pertemuan.'),(2876,3,6,27,'Setiap orang yang kena kepada daging korban itu menjadi kudus, dan bila darahnya ada yang tepercik kepada sesuatu pakaian, haruslah engkau mencuci pakaian itu di suatu tempat yang kudus.'),(2877,3,6,28,'Dan belanga tanah, tempat korban itu dimasak, haruslah dipecahkan, dan jikalau dimasak di dalam belanga tembaga, haruslah belanga itu digosok dan dibasuh dengan air.'),(2878,3,6,29,'Setiap laki-laki di antara para imam haruslah memakannya; itulah persembahan maha kudus.'),(2879,3,6,30,'Tetapi setiap korban penghapus dosa, yang dari darahnya dibawa sebagian ke dalam Kemah Pertemuan untuk mengadakan pendamaian di dalam tempat kudus, janganlah dimakan, melainkan dibakar habis dengan api.\"'),(2880,3,7,1,'Inilah hukum tentang korban penebus salah. Korban itu ialah persembahan maha kudus.'),(2881,3,7,2,'Di tempat orang menyembelih korban bakaran, di situlah harus disembelih korban penebus salah, dan darahnya haruslah disiramkan pada mezbah itu sekelilingnya.'),(2882,3,7,3,'Segala lemak dari korban itu haruslah dipersembahkan, yakni ekornya yang berlemak dan lemak yang menutupi isi perut,'),(2883,3,7,4,'dan lagi kedua buah pinggang dan lemak yang melekat padanya, yang ada pada pinggang, dan umbai hati yang harus dipisahkan beserta buah pinggang itu.'),(2884,3,7,5,'Haruslah imam membakar semuanya itu di atas mezbah sebagai korban api-apian bagi TUHAN; itulah korban penebus salah.'),(2885,3,7,6,'Setiap laki-laki di antara para imam haruslah memakannya; haruslah itu dimakan di suatu tempat yang kudus; itulah bagian maha kudus.'),(2886,3,7,7,'Seperti halnya dengan korban penghapus dosa, demikian juga halnya dengan korban penebus salah; satu hukum berlaku atas keduanya: imam yang mengadakan pendamaian dengan korban itu, bagi dialah korban itu.'),(2887,3,7,8,'Imam yang mempersembahkan korban bakaran seseorang, bagi dia juga kulit korban bakaran yang dipersembahkannya itu.'),(2888,3,7,9,'Tiap-tiap korban sajian yang dibakar di dalam pembakaran roti, dan segala yang diolah di dalam wajan dan di atas panggangan adalah bagi imam yang mempersembahkannya.'),(2889,3,7,10,'Tiap-tiap korban sajian yang diolah dengan minyak atau yang kering adalah bagi semua anak-anak Harun, semuanya dapat bagian.\"'),(2890,3,7,11,'Inilah hukum tentang korban keselamatan, yang harus dipersembahkan orang kepada TUHAN.'),(2891,3,7,12,'Jikalau ia mempersembahkannya untuk memberi syukur, haruslah beserta korban syukur itu dipersembahkannya roti bundar yang tidak beragi yang diolah dengan minyak, dan roti tipis yang tidak beragi yang diolesi dengan minyak, serta roti bundar dari tepung yang terbaik yang teraduk, yang diolah dengan minyak.'),(2892,3,7,13,'Ia harus mempersembahkan persembahannya itu beserta dengan roti bundar yang beragi, di samping korban syukur yang menjadi korban keselamatannya.'),(2893,3,7,14,'Dan dari padanya, yakni dari setiap bagian persembahan itu haruslah dipersembahkannya satu roti sebagai persembahan khusus bagi TUHAN. Persembahan itu adalah bagian imam yang menyiramkan darah korban keselamatan.'),(2894,3,7,15,'Dan daging korban syukur yang menjadi korban keselamatannya itu haruslah dimakan pada hari dipersembahkannya itu. Sedikitpun dari padanya janganlah ditinggalkan sampai pagi.'),(2895,3,7,16,'Jikalau korban sembelihan yang dipersembahkan itu merupakan korban nazar atau korban sukarela, haruslah itu dimakan pada hari mempersembahkannya dan yang selebihnya boleh juga dimakan pada keesokan harinya.'),(2896,3,7,17,'Tetapi apa yang masih tinggal dari daging korban sembelihan itu sampai hari yang ketiga, haruslah dibakar habis dengan api.'),(2897,3,7,18,'Karena jikalau pada hari yang ketiga masih dimakan dari daging korban keselamatan itu, maka TUHAN tidak berkenan akan orang yang mempersembahkannya dan korban itu dianggap batal baginya, bahkan menjadi sesuatu yang jijik, dan orang yang memakannya harus menanggung kesalahannya sendiri.'),(2898,3,7,19,'Bila daging itu kena kepada sesuatu yang najis, janganlah dimakan, tetapi haruslah dibakar habis dengan api. Tiap-tiap orang yang tahir boleh memakan dari daging korban itu.'),(2899,3,7,20,'Tetapi seseorang yang memakan daging dari korban keselamatan yang untuk TUHAN, sedang ia dalam keadaan najis, haruslah nyawa orang itu dilenyapkan dari antara bangsanya.'),(2900,3,7,21,'Dan apabila seseorang kena kepada sesuatu yang najis, yakni kepada kenajisan berasal dari manusia, atau kepada hewan yang najis atau kepada setiap binatang yang merayap yang najis, lalu memakan dari pada daging korban keselamatan yang untuk TUHAN, maka haruslah nyawa orang itu dilenyapkan dari antara bangsanya.\"'),(2901,3,7,22,'TUHAN berfirman kepada Musa:'),(2902,3,7,23,'Katakanlah kepada orang Israel: Segala lemak dari lembu, domba ataupun kambing janganlah kamu makan.'),(2903,3,7,24,'Lemak bangkai atau lemak binatang yang mati diterkam boleh dipergunakan untuk segala keperluan, tetapi jangan sekali-kali kamu memakannya.'),(2904,3,7,25,'Karena setiap orang yang memakan lemak dari hewan yang dipergunakan untuk mempersembahkan korban api-apian bagi TUHAN, nyawa orang yang memakan itu, haruslah dilenyapkan dari antara bangsanya.'),(2905,3,7,26,'Demikian juga janganlah kamu memakan darah apapun di segala tempat kediamanmu, baik darah burung-burung ataupun darah hewan.'),(2906,3,7,27,'Setiap orang yang memakan darah apapun, nyawa orang itu haruslah dilenyapkan dari antara bangsanya.\"'),(2907,3,7,28,'TUHAN berfirman kepada Musa, demikian:'),(2908,3,7,29,'Katakanlah kepada orang Israel: Orang yang mempersembahkan korban keselamatannya kepada TUHAN, haruslah membawa kepada TUHAN sebagian dari korban keselamatannya itu sebagai persembahannya.'),(2909,3,7,30,'Dengan tangannya sendirilah harus ia membawa segala korban api-apian TUHAN; adapun lemaknya, haruslah dibawanya beserta dadanya, supaya dadanya itu diunjukkan sebagai persembahan unjukan di hadapan TUHAN.'),(2910,3,7,31,'Lalu haruslah imam membakar lemaknya di atas mezbah, tetapi dadanya itu adalah bagian Harun dan anak-anaknya.'),(2911,3,7,32,'Paha kanannya harus kamu serahkan kepada imam sebagai persembahan khusus dari segala korban keselamatanmu.'),(2912,3,7,33,'Siapa dari antara anak-anak Harun yang mempersembahkan darah dan lemak korban keselamatan, maka dialah yang harus mendapat paha kanan itu sebagai bagiannya.'),(2913,3,7,34,'Karena dada persembahan unjukan dan paha persembahan khusus telah Kuambil dari orang Israel dari segala korban keselamatan mereka dan telah Kuberikan kepada imam Harun, dan kepada anak-anaknya; itulah suatu ketetapan yang berlaku bagi orang Israel untuk selamanya.\"'),(2914,3,7,35,'Itulah bagian Harun dan bagian anak-anaknya dari segala korban api-apian TUHAN pada hari mereka itu disuruh datang untuk memegang jabatan imam bagi TUHAN;'),(2915,3,7,36,'itulah yang harus diserahkan menurut perintah TUHAN dari pihak Israel kepada mereka pada hari mereka itu diurapi-Nya; itulah suatu ketetapan untuk selamanya bagi mereka turun-temurun.'),(2916,3,7,37,'Itulah hukum tentang korban bakaran, korban sajian, korban penghapus dosa, korban penebus salah, persembahan pentahbisan dan korban keselamatan,'),(2917,3,7,38,'yang diperintahkan TUHAN kepada Musa di atas gunung Sinai pada hari TUHAN memerintahkan kepada orang Israel mempersembahkan persembahan mereka kepada TUHAN di padang gurun Sinai.'),(2918,3,8,1,'TUHAN berfirman kepada Musa:'),(2919,3,8,2,'Panggillah Harun dan anak-anaknya bersama-sama dengan dia, dan ambillah pakaian-pakaian, minyak urapan, dan lembu jantan korban penghapus dosa, dua domba jantan dan bakul berisi roti yang tidak beragi,'),(2920,3,8,3,'lalu suruhlah berkumpul segenap umat ke depan pintu Kemah Pertemuan.\"'),(2921,3,8,4,'Musa melakukan seperti yang diperintahkan TUHAN kepadanya, lalu berkumpullah umat itu di depan Kemah Pertemuan.'),(2922,3,8,5,'Berkatalah Musa kepada umat itu: \"Inilah firman yang diperintahkan TUHAN untuk dilakukan.\"'),(2923,3,8,6,'Lalu Musa menyuruh Harun dan anak-anaknya mendekat, dan dibasuhnyalah mereka dengan air.'),(2924,3,8,7,'Sesudah itu dikenakannyalah kemeja kepadanya, diikatkannya ikat pinggang, dikenakannya gamis, dikenakannya baju efod, diikatkannya sabuk baju efod dan dikebatkannya sabuk itu kepadanya.'),(2925,3,8,8,'Dikenakannyalah tutup dada kepadanya dan dibubuhnya di dalam tutup dada itu Urim dan Tumim.'),(2926,3,8,9,'Kemudian ditaruhnyalah serban di kepalanya, dan di atas serban itu di sebelah depan ditaruhnyalah patam emas, yakni jamang yang kudus, seperti yang diperintahkan TUHAN kepada Musa.'),(2927,3,8,10,'Musa mengambil minyak urapan, lalu diurapinyalah Kemah Suci serta segala yang ada di dalamnya dan dikuduskannya semuanya itu.'),(2928,3,8,11,'Dipercikkannyalah sedikit dari minyak itu ke mezbah tujuh kali dan diurapinya mezbah itu serta segala perkakasnya, dan juga bejana pembasuhan serta alasnya untuk menguduskannya.'),(2929,3,8,12,'Kemudian dituangkannya sedikit dari minyak urapan itu ke atas kepala Harun dan diurapinyalah dia untuk menguduskannya.'),(2930,3,8,13,'Musa menyuruh anak-anak Harun mendekat, lalu dikenakannyalah kemeja kepada mereka, diikatkannya ikat pinggang dan dililitkannya destar, seperti yang diperintahkan TUHAN kepada Musa.'),(2931,3,8,14,'Disuruhnyalah membawa lembu jantan korban penghapus dosa, lalu Harun dan anak-anaknya meletakkan tangannya ke atas kepala lembu jantan korban penghapus dosa itu.'),(2932,3,8,15,'Lembu itu disembelih, lalu Musa mengambil darahnya, kemudian dengan jarinya dibubuhnyalah darah itu pada tanduk-tanduk mezbah sekelilingnya, dan dengan demikian disucikannyalah mezbah itu dari dosa; darah selebihnya dituangkannya pada bagian bawah mezbah. Dengan demikian dikuduskannya mezbah itu dan diadakannya pendamaian baginya.'),(2933,3,8,16,'Diambillah segala lemak yang melekat pada isi perut, umbai hati, kedua buah pinggang serta lemaknya, lalu Musa membakarnya di atas mezbah.'),(2934,3,8,17,'Tetapi lembu jantan itu dengan kulit, daging dan kotorannya dibakarnya habis di luar perkemahan, seperti yang diperintahkan TUHAN kepada Musa.'),(2935,3,8,18,'Kemudian disuruhnya membawa domba jantan korban bakaran, lalu Harun dan anak-anaknya meletakkan tangannya ke atas kepala domba jantan itu.'),(2936,3,8,19,'Domba itu disembelih, lalu Musa menyiramkan darahnya pada mezbah sekelilingnya.'),(2937,3,8,20,'Domba itu dipotong-potong menurut bagian-bagian tertentu, lalu Musa membakar kepalanya dan bagian-bagiannya dan lemaknya.'),(2938,3,8,21,'Tetapi isi perut dan betisnya dibasuh dengan air, lalu Musa membakar seluruh domba jantan itu di atas mezbah. Itulah korban bakaran, yang baunya menyenangkan; yakni suatu korban api-apian bagi TUHAN, seperti yang diperintahkan TUHAN kepada Musa.'),(2939,3,8,22,'Kemudian disuruhnya membawa domba jantan yang lain, yakni domba persembahan pentahbisan, lalu Harun dan anak-anaknya meletakkan tangannya ke atas kepala domba jantan itu.'),(2940,3,8,23,'Domba jantan itu disembelih, lalu Musa mengambil sedikit dari darahnya dan membubuhnya pada cuping telinga kanan Harun, pada ibu jari tangan kanan dan pada ibu jari kaki kanannya.'),(2941,3,8,24,'Musa menyuruh anak-anak Harun mendekat, lalu membubuh sedikit dari darah itu pada cuping telinga kanan mereka, pada ibu jari tangan kanan dan pada ibu jari kaki kanan mereka, lalu Musa menyiramkan darah selebihnya pada mezbah sekelilingnya.'),(2942,3,8,25,'Diambilnyalah lemaknya, ekornya yang berlemak, segala lemaknya yang melekat pada isi perut, umbai hatinya, kedua buah pinggang serta lemaknya dan paha kanannya.'),(2943,3,8,26,'Dan dari dalam bakul berisi roti yang tidak beragi, yang ada di hadapan TUHAN, diambilnyalah satu roti bundar yang tidak beragi, satu roti bundar yang diolah dengan minyak dan satu roti tipis, lalu diletakkannya di atas segala lemak dan di atas paha kanan itu,'),(2944,3,8,27,'dan ditaruhnya seluruhnya di telapak tangan Harun dan di telapak tangan anak-anaknya, dan dipersembahkannya semuanya sebagai persembahan unjukan di hadapan TUHAN.'),(2945,3,8,28,'Kemudian Musa mengambil semuanya dari telapak tangan mereka, lalu dibakarnya di atas mezbah, yaitu di atas korban bakaran. Itulah persembahan pentahbisan untuk menjadi bau yang menyenangkan; itulah suatu korban api-apian bagi TUHAN.'),(2946,3,8,29,'Musa mengambil dada domba itu, dan mempersembahkannya sebagai persembahan unjukan di hadapan TUHAN. Itulah yang didapat Musa sebagai bagiannya dari domba jantan persembahan pentahbisan itu, seperti yang diperintahkan TUHAN kepada Musa.'),(2947,3,8,30,'Dan lagi Musa mengambil sedikit dari minyak urapan dan dari darah yang di atas mezbah itu, lalu dipercikkannya kepada Harun, ke pakaiannya, dan juga kepada anak-anaknya dan ke pakaian anak-anaknya. Dengan demikian ditahbiskannyalah Harun, pakaiannya, dan juga anak-anaknya dan pakaian anak-anaknya.'),(2948,3,8,31,'Berkatalah Musa kepada Harun dan kepada anak-anaknya: \"Masaklah daging itu di depan pintu Kemah Pertemuan; di sanalah harus kamu memakannya dengan roti yang ada di dalam bakul untuk persembahan pentahbisan, seperti yang telah kuperintahkan dengan berkata: Harun dan anak-anaknya haruslah memakannya.'),(2949,3,8,32,'Dan apa yang tinggal dari daging dan roti itu haruslah kamu bakar habis dengan api.'),(2950,3,8,33,'Janganlah kamu pergi dari depan pintu Kemah Pertemuan selama tujuh hari, sampai kepada genapnya perayaan pentahbisan, karena perayaan pentahbisan akan berlangsung tujuh hari lamanya.'),(2951,3,8,34,'Seperti yang diperbuat pada hari ini, demikian juga diperintahkan TUHAN kamu perbuat kelak untuk mengadakan pendamaian bagimu.'),(2952,3,8,35,'Di depan pintu Kemah Pertemuan haruslah kamu tinggal siang malam tujuh hari lamanya, dan kamu harus lakukan kewajibanmu terhadap TUHAN dengan setia, supaya janganlah kamu mati, karena demikianlah diperintahkan kepadaku.\"'),(2953,3,8,36,'Maka Harun dan anak-anaknya melakukan segala firman yang diperintahkan TUHAN dengan perantaraan Musa.'),(2954,3,9,1,'Pada hari yang kedelapan Musa memanggil Harun serta anak-anaknya dan para tua-tua Israel,'),(2955,3,9,2,'lalu berkatalah ia kepada Harun: \"Ambillah bagimu sendiri seekor lembu muda, untuk korban penghapus dosa, dan seekor domba jantan untuk korban bakaran, kedua-duanya yang tidak bercela, kemudian persembahkanlah itu di hadapan TUHAN.'),(2956,3,9,3,'Dan kepada orang Israel haruslah engkau berkata, begini: Ambillah seekor kambing jantan untuk korban penghapus dosa, dan seekor anak lembu dan seekor domba, masing-masing berumur setahun dan yang tidak bercela, untuk korban bakaran.'),(2957,3,9,4,'Dan lagi seekor lembu dan seekor domba jantan untuk korban keselamatan, supaya dikorbankan di hadapan TUHAN, dan korban sajian yang diolah dengan minyak, karena pada hari ini TUHAN akan menampakkan diri kepadamu.\"'),(2958,3,9,5,'Kemudian dibawa merekalah apa yang diperintahkan Musa ke Kemah Pertemuan, lalu mendekatlah segenap umat itu dan berdiri di hadapan TUHAN.'),(2959,3,9,6,'Kata Musa: \"Inilah firman yang diperintahkan TUHAN kamu perbuat, agar kemuliaan TUHAN tampak kepadamu.\"'),(2960,3,9,7,'Kata Musa kepada Harun: \"Datanglah mendekat kepada mezbah, olahlah korban penghapus dosa dan korban bakaranmu, dan adakanlah pendamaian bagimu sendiri dan bagi bangsa itu; sesudah itu olahlah persembahan bangsa itu dan adakanlah pendamaian bagi mereka, seperti yang diperintahkan TUHAN.\"'),(2961,3,9,8,'Maka mendekatlah Harun kepada mezbah, dan disembelihnyalah anak lembu yang akan menjadi korban penghapus dosa baginya sendiri.'),(2962,3,9,9,'Anak-anak Harun menyampaikan darah lembu itu kepadanya, dan Harun mencelupkan jarinya ke dalam darah itu dan membubuhnya pada tanduk-tanduk mezbah. Darah selebihnya dituangkannya pada bagian bawah mezbah.'),(2963,3,9,10,'Lemak, buah pinggang dan umbai hati dari korban penghapus dosa itu dibakarnya di atas mezbah, seperti yang diperintahkan TUHAN kepada Musa.'),(2964,3,9,11,'Tetapi daging dan kulitnya dibakarnya habis di luar perkemahan.'),(2965,3,9,12,'Kemudian ia menyembelih korban bakaran, lalu anak-anak Harun menyerahkan darah korban itu kepadanya, maka Harun menyiramkannya pada mezbah sekelilingnya.'),(2966,3,9,13,'Juga diserahkan merekalah kepadanya korban bakaran itu menurut bagian-bagian tertentu beserta dengan kepalanya, lalu dibakarnya di atas mezbah.'),(2967,3,9,14,'Isi perut dan betisnya dibasuhnya dan dibakarnya dengan korban bakaran di atas mezbah.'),(2968,3,9,15,'Sesudah itu dibawanya persembahan bangsa ke mezbah; diambilnyalah kambing jantan yang akan menjadi korban penghapus dosa yang bagi bangsa itu, lalu disembelihnya dan dipersembahkannya sebagai korban penghapus dosa seperti yang pertama.'),(2969,3,9,16,'Kemudian dibawanyalah korban bakaran ke mezbah, dan diolahnya sesuai dengan peraturan.'),(2970,3,9,17,'Selanjutnya dibawanyalah korban sajian dan diambilnya segenggam dari padanya, lalu dibakarnya di atas mezbah, di samping korban bakaran pada waktu pagi.'),(2971,3,9,18,'Ia menyembelih juga lembu dan domba jantan yang akan menjadi korban keselamatan bagi bangsa itu, lalu anak-anak Harun menyerahkan darah korban itu kepadanya, maka Harun menyiramkannya pada mezbah sekelilingnya.'),(2972,3,9,19,'Tetapi segala lemak dari lembu dan dari domba jantan itu, yakni ekor yang berlemak, lemak yang menutupi isi perut, buah pinggang dan umbai hati,'),(2973,3,9,20,'segala lemak itu diletakkan mereka di atas dada kedua korban itu, lalu Harun membakar segala lemak itu di atas mezbah.'),(2974,3,9,21,'Dada dan paha kanan itu dipersembahkan Harun sebagai persembahan unjukan di hadapan TUHAN, seperti yang diperintahkan Musa.'),(2975,3,9,22,'Harun mengangkat kedua tangannya atas bangsa itu, lalu memberkati mereka, kemudian turunlah ia, setelah mempersembahkan korban penghapus dosa, korban bakaran dan korban keselamatan.'),(2976,3,9,23,'Masuklah Musa dan Harun ke dalam Kemah Pertemuan. Setelah keluar, mereka memberkati bangsa itu, lalu tampaklah kemuliaan TUHAN kepada segenap bangsa itu.'),(2977,3,9,24,'Dan keluarlah api dari hadapan TUHAN, lalu menghanguskan korban bakaran dan segala lemak di atas mezbah. Tatkala seluruh bangsa itu melihatnya, bersorak-sorailah mereka, lalu sujud menyembah.'),(2978,3,10,1,'Kemudian anak-anak Harun, Nadab dan Abihu, masing-masing mengambil perbaraannya, membubuh api ke dalamnya serta menaruh ukupan di atas api itu. Dengan demikian mereka mempersembahkan ke hadapan TUHAN api yang asing yang tidak diperintahkan-Nya kepada mereka.'),(2979,3,10,2,'Maka keluarlah api dari hadapan TUHAN, lalu menghanguskan keduanya, sehingga mati di hadapan TUHAN.'),(2980,3,10,3,'Berkatalah Musa kepada Harun: \"Inilah yang difirmankan TUHAN: Kepada orang yang karib kepada-Ku Kunyatakan kekudusan-Ku, dan di muka seluruh bangsa itu akan Kuperlihatkan kemuliaan-Ku.\" Dan Harun berdiam diri.'),(2981,3,10,4,'Kemudian Musa memanggil Misael dan Elsafan, anak-anak Uziel, paman Harun, lalu berkatalah ia kepada mereka: \"Datang ke mari, angkatlah saudara-saudaramu ini dari depan tempat kudus ke luar perkemahan.\"'),(2982,3,10,5,'Mereka datang, dan mengangkat mayat keduanya, masih berpakaian kemeja, ke luar perkemahan, seperti yang dikatakan Musa.'),(2983,3,10,6,'Kemudian berkatalah Musa kepada Harun dan kepada Eleazar dan Itamar, anak-anak Harun: \"Janganlah kamu berkabung dan janganlah kamu berdukacita, supaya jangan kamu mati dan jangan TUHAN memurkai segenap umat ini, tetapi saudara-saudaramu, yaitu seluruh bangsa Israel, merekalah yang harus menangis karena api yang dinyalakan TUHAN itu.'),(2984,3,10,7,'Janganlah kamu pergi dari depan pintu Kemah Pertemuan, supaya jangan kamu mati, karena minyak urapan TUHAN ada di atasmu.\" Mereka melakukan sesuai dengan perkataan Musa.'),(2985,3,10,8,'TUHAN berfirman kepada Harun:'),(2986,3,10,9,'Janganlah engkau minum anggur atau minuman keras, engkau serta anak-anakmu, bila kamu masuk ke dalam Kemah Pertemuan, supaya jangan kamu mati. Itulah suatu ketetapan untuk selamanya bagi kamu turun-temurun.'),(2987,3,10,10,'Haruslah kamu dapat membedakan antara yang kudus dengan yang tidak kudus, antara yang najis dengan yang tidak najis,'),(2988,3,10,11,'dan haruslah kamu dapat mengajarkan kepada orang Israel segala ketetapan yang telah difirmankan TUHAN kepada mereka dengan perantaraan Musa.\"'),(2989,3,10,12,'Kemudian berkatalah Musa kepada Harun dan kepada Eleazar dan Itamar, anak-anak Harun yang tinggal itu: \"Ambillah korban sajian yang tinggal dari segala korban api-apian TUHAN, dan makanlah itu sebagai roti yang tidak beragi di samping mezbah, karena itulah bagian maha kudus.'),(2990,3,10,13,'Haruslah kamu memakannya di suatu tempat yang kudus, karena itulah ketetapan bagimu dan anak-anakmu dari segala korban api-apian TUHAN, sebab demikianlah diperintahkan kepadaku.'),(2991,3,10,14,'Dada persembahan unjukan dan paha persembahan khusus itu haruslah kamu makan di suatu tempat yang tahir, engkau ini serta anak-anakmu laki-laki dan perempuan, karena semuanya diberikan sebagai ketetapan bagimu dan anak-anakmu dari segala korban keselamatan orang Israel.'),(2992,3,10,15,'Paha persembahan khusus dan dada persembahan unjukan itu haruslah dibawa mereka ke tempat segala korban api-apian yang dari lemak itu, supaya dipersembahkan sebagai persembahan unjukan di hadapan TUHAN. Itulah suatu ketetapan untuk selamanya bagimu serta bagi anak-anakmu seperti yang diperintahkan TUHAN.\"'),(2993,3,10,16,'Kemudian Musa mencari dengan teliti kambing jantan korban penghapus dosa itu, tetapi ternyata kambing itu sudah habis dibakar. Sebab itu dimarahinyalah Eleazar dan Itamar, anak-anak Harun yang tinggal itu, katanya:'),(2994,3,10,17,'Mengapa tidak kamu makan korban penghapus dosa itu di tempat yang kudus? Bukankah itu sesuatu bagian maha kudus dan TUHAN memberikannya kepadamu, supaya kamu mengangkut kesalahan umat itu dan mengadakan pendamaian bagi mereka di hadapan TUHAN?'),(2995,3,10,18,'Lihat, darahnya itu tidak dibawa masuk ke dalam tempat kudus; bukankah seharusnya kamu memakannya di tempat kudus, seperti yang telah kuperintahkan?\"'),(2996,3,10,19,'Lalu berkatalah Harun kepada Musa: \"Memang benar, pada hari ini mereka telah mempersembahkan korban penghapus dosa dan korban bakaran mereka ke hadapan TUHAN, tetapi hal-hal seperti tadilah yang kualami. Jikalau pada hari ini aku memakan juga korban penghapus dosa, mungkinkah hal itu disetujui oleh TUHAN?\"'),(2997,3,10,20,'Ketika Musa mendengar itu, ia menyetujuinya.'),(2998,3,11,1,'Lalu TUHAN berfirman kepada Musa dan Harun, kata-Nya kepada mereka:'),(2999,3,11,2,'Katakanlah kepada orang Israel, begini: Inilah binatang-binatang yang boleh kamu makan dari segala binatang berkaki empat yang ada di atas bumi:'),(3000,3,11,3,'setiap binatang yang berkuku belah, yaitu yang kukunya bersela panjang, dan yang memamah biak boleh kamu makan.'),(3001,3,11,4,'Tetapi inilah yang tidak boleh kamu makan dari yang memamah biak atau dari yang berkuku belah: unta, karena memang memamah biak, tetapi tidak berkuku belah; haram itu bagimu.'),(3002,3,11,5,'Juga pelanduk, karena memang memamah biak, tetapi tidak berkuku belah; haram itu bagimu.'),(3003,3,11,6,'Juga kelinci, karena memang memamah biak, tetapi tidak berkuku belah, haram itu bagimu.'),(3004,3,11,7,'Demikian juga babi hutan, karena memang berkuku belah, yaitu kukunya bersela panjang, tetapi tidak memamah biak; haram itu bagimu.'),(3005,3,11,8,'Daging binatang-binatang itu janganlah kamu makan dan bangkainya janganlah kamu sentuh; haram semuanya itu bagimu.'),(3006,3,11,9,'Inilah yang boleh kamu makan dari segala yang hidup di dalam air: segala yang bersirip dan bersisik di dalam air, di dalam lautan, dan di dalam sungai, itulah semuanya yang boleh kamu makan.'),(3007,3,11,10,'Tetapi segala yang tidak bersirip atau bersisik di dalam lautan dan di dalam sungai, dari segala yang berkeriapan di dalam air dan dari segala makhluk hidup yang ada di dalam air, semuanya itu kejijikan bagimu.'),(3008,3,11,11,'Sesungguhnya haruslah semuanya itu kejijikan bagimu; dagingnya janganlah kamu makan, dan bangkainya haruslah kamu jijikkan.'),(3009,3,11,12,'Segala yang tidak bersirip dan tidak bersisik di dalam air, adalah kejijikan bagimu.'),(3010,3,11,13,'Inilah yang harus kamu jijikkan dari burung-burung, janganlah dimakan, karena semuanya itu adalah kejijikan: burung rajawali, ering janggut dan elang laut;'),(3011,3,11,14,'elang merah dan elang hitam menurut jenisnya;'),(3012,3,11,15,'setiap burung gagak menurut jenisnya;'),(3013,3,11,16,'burung unta, burung hantu, camar dan elang sikap menurut jenisnya;'),(3014,3,11,17,'burung pungguk, burung dendang air dan burung hantu besar;'),(3015,3,11,18,'burung hantu putih, burung undan, burung ering;'),(3016,3,11,19,'burung ranggung, bangau menurut jenisnya, meragai dan kelelawar.'),(3017,3,11,20,'Segala binatang yang merayap dan bersayap dan berjalan dengan keempat kakinya adalah kejijikan bagimu.'),(3018,3,11,21,'Tetapi inilah yang boleh kamu makan dari segala binatang yang merayap dan bersayap dan yang berjalan dengan keempat kakinya, yaitu yang mempunyai paha di sebelah atas kakinya untuk melompat di atas tanah.'),(3019,3,11,22,'Inilah yang boleh kamu makan dari antaranya: belalang-belalang menurut jenisnya, yaitu belalang-belalang gambar menurut jenisnya, belalang-belalang kunyit menurut jenisnya, dan belalang-belalang padi menurut jenisnya.'),(3020,3,11,23,'Selainnya segala binatang yang merayap dan bersayap dan yang berkaki empat adalah kejijikan bagimu.'),(3021,3,11,24,'Semua yang berikut akan menajiskan kamu--setiap orang yang kena kepada bangkainya, menjadi najis sampai matahari terbenam,'),(3022,3,11,25,'dan setiap orang yang ada membawa dari bangkainya haruslah mencuci pakaiannya, dan ia menjadi najis sampai matahari terbenam--,'),(3023,3,11,26,'yakni segala binatang yang berkuku belah, tetapi tidak bersela panjang, dan yang tidak memamah biak; haram semuanya itu bagimu dan setiap orang yang kena kepadanya, menjadi najis.'),(3024,3,11,27,'Demikian juga segala yang berjalan dengan telapak kakinya di antara segala binatang yang berjalan dengan keempat kakinya, semuanya itu haram bagimu; setiap orang yang kena kepada bangkainya, menjadi najis sampai matahari terbenam.'),(3025,3,11,28,'Dan siapa yang membawa bangkainya, haruslah mencuci pakaiannya dan ia menjadi najis sampai matahari terbenam. Haram semuanya itu bagimu.'),(3026,3,11,29,'Inilah yang haram bagimu di antara segala binatang yang merayap dan berkeriapan di atas bumi: tikus buta, tikus, dan katak menurut jenisnya'),(3027,3,11,30,'dan landak, biawak, dan bengkarung, siput dan bunglon.'),(3028,3,11,31,'Itulah semuanya yang haram bagimu di antara segala binatang yang mengeriap. Setiap orang yang kena kepada binatang-binatang itu sesudah binatang-binatang itu mati, menjadi najis sampai matahari terbenam.'),(3029,3,11,32,'Dan segala sesuatu menjadi najis, kalau seekor yang mati dari binatang-binatang itu jatuh ke atasnya: perkakas kayu apa saja atau pakaian atau kulit atau karung, setiap barang yang dipergunakan untuk sesuatu apapun, haruslah dimasukkan ke dalam air dan menjadi najis sampai matahari terbenam, kemudian menjadi tahir pula.'),(3030,3,11,33,'Kalau seekor dari binatang-binatang itu jatuh ke dalam sesuatu belanga tanah, maka segala yang ada di dalamnya menjadi najis dan belanga itu harus kamu pecahkan.'),(3031,3,11,34,'Dalam hal itu segala makanan yang boleh dimakan, kalau kena air dari belanga itu, menjadi najis, dan segala minuman yang boleh diminum dalam belanga seperti itu, menjadi najis.'),(3032,3,11,35,'Kalau bangkai seekor dari binatang-binatang itu jatuh ke atas sesuatu benda, itu menjadi najis; pembakaran roti dan anglo haruslah diremukkan, karena semuanya itu najis dan haruslah najis juga bagimu;'),(3033,3,11,36,'tetapi mata air atau sumur yang memuat air, tetap tahir, sedangkan siapa yang kena kepada bangkai binatang-binatang itu menjadi najis.'),(3034,3,11,37,'Apabila bangkai seekor dari binatang-binatang itu jatuh ke atas benih apapun yang akan ditaburkan, maka benih itu tetap tahir.'),(3035,3,11,38,'Tetapi apabila benih itu telah dibubuhi air, lalu ke atasnya jatuh bangkai seekor dari binatang-binatang itu, maka najislah benih itu bagimu.'),(3036,3,11,39,'Apabila mati salah seekor binatang yang menjadi makanan bagimu, maka siapa yang kena kepada bangkainya menjadi najis sampai matahari terbenam.'),(3037,3,11,40,'Dan siapa yang makan dari bangkainya itu, haruslah mencuci pakaiannya, dan ia menjadi najis sampai matahari terbenam; demikian juga siapa yang membawa bangkainya haruslah mencuci pakaiannya, dan ia menjadi najis sampai matahari terbenam.'),(3038,3,11,41,'Segala binatang yang merayap dan berkeriapan di atas bumi, adalah kejijikan, janganlah dimakan.'),(3039,3,11,42,'Segala yang merayap dengan perutnya dan segala yang berjalan dengan keempat kakinya, atau segala yang berkaki banyak, semua yang termasuk binatang yang merayap dan berkeriapan di atas bumi, janganlah kamu makan, karena semuanya itu adalah kejijikan.'),(3040,3,11,43,'Janganlah kamu membuat dirimu jijik oleh setiap binatang yang merayap dan berkeriapan dan janganlah kamu menajiskan dirimu dengan semuanya itu, sehingga kamu menjadi najis karenanya.'),(3041,3,11,44,'Sebab Akulah TUHAN, Allahmu, maka haruslah kamu menguduskan dirimu dan haruslah kamu kudus, sebab Aku ini kudus, dan janganlah kamu menajiskan dirimu dengan setiap binatang yang mengeriap dan merayap di atas bumi.'),(3042,3,11,45,'Sebab Akulah TUHAN yang telah menuntun kamu keluar dari tanah Mesir, supaya menjadi Allahmu; jadilah kudus, sebab Aku ini kudus.'),(3043,3,11,46,'Itulah hukum tentang binatang berkaki empat, burung-burung dan segala makhluk hidup yang bergerak di dalam air dan segala makhluk yang mengeriap di atas bumi,'),(3044,3,11,47,'yakni untuk membedakan antara yang najis dengan yang tahir, antara binatang yang boleh dimakan dengan binatang yang tidak boleh dimakan.\"'),(3045,3,12,1,'TUHAN berfirman kepada Musa, demikian:'),(3046,3,12,2,'Katakanlah kepada orang Israel: Apabila seorang perempuan bersalin dan melahirkan anak laki-laki, maka najislah ia selama tujuh hari. Sama seperti pada hari-hari ia bercemar kain ia najis.'),(3047,3,12,3,'Dan pada hari yang kedelapan haruslah dikerat daging kulit khatan anak itu.'),(3048,3,12,4,'Selanjutnya tiga puluh tiga hari lamanya perempuan itu harus tinggal menantikan pentahiran dari darah nifas, tidak boleh ia kena kepada sesuatu apapun yang kudus dan tidak boleh ia masuk ke tempat kudus, sampai sudah genap hari-hari pentahirannya.'),(3049,3,12,5,'Tetapi jikalau ia melahirkan anak perempuan, maka najislah ia selama dua minggu, sama seperti pada waktu ia bercemar kain; selanjutnya enam puluh enam hari lamanya ia harus tinggal menantikan pentahiran dari darah nifas.'),(3050,3,12,6,'Bila sudah genap hari-hari pentahirannya, maka untuk anak laki-laki atau anak perempuan haruslah dibawanya seekor domba berumur setahun sebagai korban bakaran dan seekor anak burung merpati atau burung tekukur sebagai korban penghapus dosa ke pintu Kemah Pertemuan, dengan menyerahkannya kepada imam.'),(3051,3,12,7,'Imam itu harus mempersembahkannya ke hadapan TUHAN dan mengadakan pendamaian bagi perempuan itu. Demikianlah perempuan itu ditahirkan dari leleran darahnya. Itulah hukum tentang perempuan yang melahirkan anak laki-laki atau anak perempuan.'),(3052,3,12,8,'Tetapi jikalau ia tidak mampu untuk menyediakan seekor kambing atau domba, maka haruslah ia mengambil dua ekor burung tekukur atau dua ekor anak burung merpati, yang seekor sebagai korban bakaran dan yang seekor lagi sebagai korban penghapus dosa, dan imam itu harus mengadakan pendamaian bagi perempuan itu, maka tahirlah ia.\"'),(3053,3,13,1,'TUHAN berfirman kepada Musa dan Harun:'),(3054,3,13,2,'Apabila pada kulit badan seseorang ada bengkak atau bintil-bintil atau panau, yang mungkin menjadi penyakit kusta pada kulitnya, ia harus dibawa kepada imam Harun, atau kepada salah seorang dari antara anak-anaknya, imam-imam itu.'),(3055,3,13,3,'Imam haruslah memeriksa penyakit pada kulit itu, dan kalau bulu di tempat penyakit itu sudah berubah menjadi putih, dan penyakit itu kelihatan lebih dalam dari kulit, maka itu penyakit kusta; kalau imam melihat hal itu, haruslah ia menyatakan orang itu najis.'),(3056,3,13,4,'Tetapi jikalau yang ada pada kulitnya itu hanya panau putih dan tidak kelihatan lebih dalam dari kulit, dan bulunya tidak berubah menjadi putih, imam harus mengurung orang itu tujuh hari lamanya.'),(3057,3,13,5,'Pada hari yang ketujuh haruslah imam memeriksa dia; bila menurut penglihatannya penyakit itu masih tetap dan tidak meluas pada kulit, imam harus mengurung dia tujuh hari lagi untuk kedua kalinya.'),(3058,3,13,6,'Kemudian pada hari yang ketujuh haruslah imam memeriksa dia untuk kedua kalinya; bila penyakit itu menjadi pudar dan tidak meluas pada kulit, imam harus menyatakan dia tahir; itu hanya bintil-bintil. Orang itu harus mencuci pakaiannya dan ia menjadi tahir.'),(3059,3,13,7,'Tetapi jikalau bintil-bintil itu memang meluas pada kulit, sesudah ia minta diperiksa oleh imam untuk dinyatakan tahir, haruslah ia minta diperiksa untuk kedua kalinya.'),(3060,3,13,8,'Kalau menurut pemeriksaan imam bintil-bintil itu meluas pada kulit, imam harus menyatakan dia najis; itu penyakit kusta.'),(3061,3,13,9,'Apabila seseorang kena kusta, ia harus dibawa kepada imam.'),(3062,3,13,10,'Kalau menurut pemeriksaan imam pada kulitnya ada bengkak yang putih, yang mengubah bulunya menjadi putih, dan ada daging liar timbul pada bengkak itu,'),(3063,3,13,11,'maka kusta idapanlah yang ada pada kulitnya. Imam harus menyatakan dia najis dengan tidak usah mengurung dia, karena orang itu memang sudah najis.'),(3064,3,13,12,'Jikalau kusta itu timbul di mana-mana pada kulit, sehingga menutupi seluruh kulit orang sakit itu, dari kepala sampai kakinya, seberapa dapat dilihat oleh imam,'),(3065,3,13,13,'dan kalau menurut pemeriksaannya kusta itu menutupi seluruh tubuh orang itu, maka ia harus dinyatakan tahir oleh imam; ia seluruhnya telah berubah menjadi putih, jadi ia tahir.'),(3066,3,13,14,'Tetapi pada waktu ada tampak daging liar padanya, najislah ia.'),(3067,3,13,15,'Kalau daging liar itu dilihat oleh imam, ia harus menyatakan orang itu najis, karena daging liar itu najis, dan itu penyakit kusta.'),(3068,3,13,16,'Atau apabila daging liar itu susut dan berubah menjadi putih, haruslah orang itu datang kepada imam.'),(3069,3,13,17,'Kalau menurut pemeriksaannya penyakit itu telah berubah menjadi putih, haruslah imam menyatakan orang itu tahir; memang ia tahir.'),(3070,3,13,18,'Apabila pada kulit seseorang ada barah yang telah sembuh,'),(3071,3,13,19,'tetapi di tempat barah itu timbul bengkak yang putih atau panau yang putih kemerah-merahan, haruslah orang itu minta diperiksa oleh imam.'),(3072,3,13,20,'Kalau menurut pemeriksaannya panau itu kelihatan lebih dalam dari pada kulit dan bulunya telah berubah menjadi putih, maka imam harus menyatakan orang itu najis, karena penyakit kustalah yang timbul di dalam barah itu.'),(3073,3,13,21,'Tetapi jikalau panau itu diperiksa oleh imam dan ternyata tidak ada bulu yang putih padanya, dan tidak lebih dalam dari pada kulit, malahan pudar, imam harus mengurung orang itu tujuh hari lamanya.'),(3074,3,13,22,'Dan jikalau panau itu memang meluas pada kulit, imam harus menyatakan dia najis; itu penyakit kusta.'),(3075,3,13,23,'Tetapi jikalau panau itu masih tetap dan tidak meluas, maka itu bekas barah, dan imam harus menyatakan orang itu tahir.'),(3076,3,13,24,'Atau apabila pada kulit seseorang ada lecur karena api dan daging liar yang timbul pada lecur itu menjadi panau yang putih kemerah-merahan atau putih,'),(3077,3,13,25,'maka imam harus memeriksa panau itu; bila ternyata bulu pada panau itu berubah menjadi putih dan panau itu kelihatan lebih dalam dari kulit, maka yang timbul di dalam lecur itu adalah penyakit kusta, dan imam harus menyatakan orang itu najis; itu penyakit kusta.'),(3078,3,13,26,'Tetapi jikalau menurut pemeriksaannya tidak ada pada panau itu bulu yang putih dan panau itu tidak lebih dalam dari pada kulit, malahan pudar, imam harus mengurung orang itu tujuh hari lamanya.'),(3079,3,13,27,'Pada hari yang ketujuh imam harus memeriksa lagi dia; jikalau panau itu memang meluas pada kulit, maka haruslah imam menyatakan dia najis, itu penyakit kusta.'),(3080,3,13,28,'Tetapi jikalau panau itu masih tetap dan tidak meluas pada kulit, malahan pudar, maka itu bengkak lecur dan imam harus menyatakan dia tahir, sebab itu bekas lecur.'),(3081,3,13,29,'Apabila seorang laki-laki atau perempuan mendapat penyakit pada kepala atau pada janggut,'),(3082,3,13,30,'imam harus memeriksa penyakit itu; bila itu kelihatan lebih dalam dari kulit, dan ada padanya rambut halus yang kuning, maka imam harus menyatakan orang itu najis, karena itu kudis kepala, yakni kusta kepala atau kusta janggut.'),(3083,3,13,31,'Dan apabila menurut pemeriksaannya penyakit kudis itu tidak kelihatan lebih dalam dari kulit dan tidak ada padanya rambut yang hitam, maka imam harus mengurung orang yang kena penyakit kudis itu tujuh hari lamanya.'),(3084,3,13,32,'Pada hari yang ketujuh imam harus memeriksa penyakit itu; bila ternyata kudis itu tidak meluas dan tidak ada rambut yang kuning padanya, dan kudis itu tidak kelihatan lebih dalam dari kulit,'),(3085,3,13,33,'maka orang itu harus bercukur, hanya tempat kudis itu tidak boleh dicukurnya. Lalu imam harus mengurung orang yang kena kudis itu untuk kedua kalinya tujuh hari lagi.'),(3086,3,13,34,'Kemudian pada hari yang ketujuh imam harus memeriksa lagi kudis itu; bila ternyata, kudis itu tidak meluas pada kulit, dan tidak kelihatan lebih dalam dari kulit, maka imam harus menyatakan orang itu tahir, dan ia harus mencuci pakaiannya dan ia menjadi tahir.'),(3087,3,13,35,'Tetapi jikalau kudis itu memang meluas pada kulit, sesudah ia dinyatakan tahir,'),(3088,3,13,36,'dan menurut pemeriksaan imam kudis itu meluas pada kulit, maka imam tidak usah lagi mencari rambut yang kuning, memang orang itu najis.'),(3089,3,13,37,'Tetapi jikalau menurut penglihatan imam kudis itu masih tetap, dan ada rambut hitam tumbuh pada kudis itu, maka kudis itu sudah sembuh, dan orang itu tahir, dan imam harus menyatakan dia tahir.'),(3090,3,13,38,'Apabila pada kulit seorang laki-laki atau perempuan ada panau-panau, yakni panau-panau yang putih,'),(3091,3,13,39,'imam harus melakukan pemeriksaan; bila ternyata pada kulitnya ada panau-panau pudar dan putih, maka hanya kuraplah yang timbul pada kulitnya dan orang itu tahir.'),(3092,3,13,40,'Apabila rambut kepala seorang laki-laki meluruh, dan ia hanya menjadi botak, ia tahir.'),(3093,3,13,41,'Jikalau rambutnya meluruh pada sebelah mukanya, dan ia menjadi botak sebelah depan, ia tahir.'),(3094,3,13,42,'Tetapi apabila pada kepala yang botak itu, sebelah atas atau sebelah depan, ada penyakit yang putih kemerah-merahan, maka penyakit kustalah yang timbul pada bagian kepala yang botak itu.'),(3095,3,13,43,'Lalu imam harus memeriksa dia; bila ternyata bahwa bengkak pada bagian kepala yang botak itu putih kemerah-merahan, dan kelihatannya seperti kusta pada kulit,'),(3096,3,13,44,'maka orang itu sakit kusta, dan ia najis, dan imam harus menyatakan dia najis, karena penyakit yang di kepalanya itu.'),(3097,3,13,45,'Orang yang sakit kusta harus berpakaian yang cabik-cabik, rambutnya terurai dan lagi ia harus menutupi mukanya sambil berseru-seru: Najis! Najis!'),(3098,3,13,46,'Selama ia kena penyakit itu, ia tetap najis; memang ia najis; ia harus tinggal terasing, di luar perkemahan itulah tempat kediamannya.'),(3099,3,13,47,'Apabila pada pakaian ada tanda kusta, pada pakaian bulu domba atau pakaian lenan,'),(3100,3,13,48,'entah pada benang lungsin atau benang pakannya, entah pada kulit atau sesuatu barang kulit,'),(3101,3,13,49,'--kalau tanda pada barang-barang itu sudah kemerah-merahan warnanya, maka itu kusta--hal itu harus diperiksakan kepada imam.'),(3102,3,13,50,'Kalau tanda itu telah diperiksa oleh imam, ia harus mengasingkan yang mempunyai tanda itu tujuh hari lamanya.'),(3103,3,13,51,'Pada hari yang ketujuh ia harus memeriksa tanda itu lagi; apabila tanda itu meluas pada pakaian atau benang lungsin atau benang pakan atau pada kulit, entah untuk barang apapun kulit itu dipakai, maka itu adalah kusta yang jahat sekali, dan barang itu najis.'),(3104,3,13,52,'Ia harus membakar barang-barang yang mempunyai tanda itu, karena itu kusta yang jahat sekali; barang-barang itu harus dibakar habis.'),(3105,3,13,53,'Tetapi jikalau menurut pemeriksaan imam tanda itu tidak meluas pada barang-barang itu,'),(3106,3,13,54,'maka imam harus memerintahkan orang mencuci barang yang mempunyai tanda itu, lalu ia harus mengasingkannya tujuh hari lagi untuk kedua kalinya.'),(3107,3,13,55,'Kemudian sesudah barang itu dicuci, imam harus memeriksa tanda itu lagi; bila ternyata rupa tanda itu tidak berubah, biarpun itu tidak meluas, maka barang itu najis, dan engkau harus membakarnya habis, karena tanda itu semakin mendalam pada sebelah belakang atau sebelah muka.'),(3108,3,13,56,'Dan jikalau menurut pemeriksaan imam tanda itu menjadi pudar sesudah dicuci, maka ia harus mengoyakkannya dari barang-barang itu.'),(3109,3,13,57,'Tetapi jikalau tanda itu tampak pula pada barang-barang itu, maka itu kusta yang sedang timbul; barang yang mempunyai tanda itu, haruslah kaubakar habis.'),(3110,3,13,58,'Tetapi barang-barang yang telah kaucuci, sehingga tanda itu lenyap dari padanya, haruslah dicuci untuk kedua kalinya, barulah menjadi tahir.'),(3111,3,13,59,'Itulah hukum tentang kusta yang ada pada pakaian bulu domba atau lenan atau pada benang lungsin atau pada benang pakan atau pada setiap barang kulit, untuk menyatakan tahir atau najisnya.\"'),(3112,3,14,1,'TUHAN berfirman kepada Musa:'),(3113,3,14,2,'Inilah yang harus menjadi hukum tentang orang yang sakit kusta pada hari pentahirannya: ia harus dibawa kepada imam,'),(3114,3,14,3,'dan imam harus pergi ke luar perkemahan; kalau menurut pemeriksaan imam penyakit kusta itu telah sembuh dari padanya,'),(3115,3,14,4,'maka imam harus memerintahkan, supaya bagi orang yang akan ditahirkan itu diambil dua ekor burung yang hidup dan yang tidak haram, juga kayu aras, kain kirmizi dan hisop.'),(3116,3,14,5,'Imam harus memerintahkan supaya burung yang seekor disembelih di atas belanga tanah berisi air mengalir.'),(3117,3,14,6,'Tetapi burung yang masih hidup haruslah diambilnya bersama-sama dengan kayu aras, kain kirmizi dan hisop, lalu bersama-sama dengan burung itu semuanya harus dicelupkannya ke dalam darah burung yang sudah disembelih di atas air mengalir itu.'),(3118,3,14,7,'Kemudian ia harus memercik tujuh kali kepada orang yang akan ditahirkan dari kusta itu dan dengan demikian mentahirkan dia, lalu burung yang hidup itu haruslah dilepaskannya ke padang.'),(3119,3,14,8,'Orang yang akan ditahirkan itu haruslah mencuci pakaiannya, mencukur seluruh rambutnya dan membasuh tubuhnya dengan air, maka ia menjadi tahir. Sesudah itu ia boleh masuk ke dalam perkemahan, tetapi harus tinggal di luar kemahnya sendiri tujuh hari lamanya.'),(3120,3,14,9,'Maka pada hari yang ketujuh ia harus mencukur seluruh rambutnya: rambut kepala, janggut, alis, bahkan segala bulunya harus dicukur, pakaiannya dicuci, dan tubuhnya dibasuh dengan air; maka ia menjadi tahir.'),(3121,3,14,10,'Pada hari yang kedelapan ia harus mengambil dua ekor domba jantan yang tidak bercela dan seekor domba betina berumur setahun yang tidak bercela dan tiga persepuluh efa tepung yang terbaik diolah dengan minyak sebagai korban sajian, serta satu log minyak.'),(3122,3,14,11,'Imam yang melakukan pentahiran itu harus menempatkan orang yang akan ditahirkan bersama-sama dengan persembahannya di hadapan TUHAN di depan pintu Kemah Pertemuan.'),(3123,3,14,12,'Dan ia harus mengambil domba jantan yang seekor dan mempersembahkannya sebagai tebusan salah bersama-sama dengan minyak yang satu log itu, dan ia harus mempersembahkannya sebagai persembahan unjukan di hadapan TUHAN.'),(3124,3,14,13,'Domba jantan itu harus disembelihnya di tempat orang menyembelih korban penghapus dosa dan korban bakaran, di tempat kudus, karena korban penebus salah, begitu juga korban penghapus dosa, adalah bagian imam; itulah bagian maha kudus.'),(3125,3,14,14,'Imam harus mengambil sedikit dari darah tebusan salah itu dan harus membubuhnya pada cuping telinga kanan dari orang yang akan ditahirkan dan pada ibu jari tangan kanan dan pada ibu jari kaki kanannya.'),(3126,3,14,15,'Imam harus mengambil sedikit dari minyak yang satu log itu dan menuangnya ke telapak tangan kiri imam sendiri;'),(3127,3,14,16,'ia harus mencelupkan jari kanannya ke dalam minyak yang di telapak tangan kirinya itu dan sedikit dari minyak itu haruslah dipercikkannya dengan jarinya tujuh kali di hadapan TUHAN.'),(3128,3,14,17,'Dari minyak selebihnya imam harus membubuh sedikit pada cuping telinga kanan orang itu, pada ibu jari tangan kanannya dan pada ibu jari kaki kanannya, di tempat mana darah tebusan salah dibubuhkan.'),(3129,3,14,18,'Dan apa yang tinggal dari minyak itu haruslah dibubuhnya pada kepala orang yang akan ditahirkan. Dengan demikian imam mengadakan pendamaian bagi orang itu di hadapan TUHAN.'),(3130,3,14,19,'Imam harus mempersembahkan korban penghapus dosa dan dengan demikian mengadakan pendamaian bagi orang yang akan ditahirkan dari kenajisannya, dan sesudah itu ia harus menyembelih korban bakaran.'),(3131,3,14,20,'Kemudian imam harus mempersembahkan korban bakaran dan korban sajian di atas mezbah. Dengan demikian imam mengadakan pendamaian bagi orang itu, maka ia menjadi tahir.'),(3132,3,14,21,'Tetapi jikalau orang itu miskin dan tidak mampu, ia harus mengambil domba jantan seekor saja sebagai tebusan salah untuk persembahan unjukan, supaya diadakan pendamaian bagi orang itu, juga sepersepuluh efa tepung yang terbaik diolah dengan minyak untuk korban sajian, dan satu log minyak.'),(3133,3,14,22,'Dan lagi dua ekor burung tekukur atau dua ekor burung merpati sekadar kemampuannya, yang seekor harus menjadi korban penghapus dosa dan yang seekor lagi menjadi korban bakaran.'),(3134,3,14,23,'Pada hari yang kedelapan ia harus membawa semuanya untuk pentahirannya kepada imam, ke depan pintu Kemah Pertemuan di hadapan TUHAN.'),(3135,3,14,24,'Kemudian imam harus mengambil domba tebusan salah dan minyak yang satu log itu, lalu imam harus mempersembahkan semuanya sebagai persembahan unjukan di hadapan TUHAN.'),(3136,3,14,25,'Ia harus menyembelih domba tebusan salah dan imam harus mengambil sedikit dari darah tebusan salah itu dan membubuhnya pada cuping telinga kanan orang itu dan pada ibu jari tangan kanan dan ibu jari kaki kanannya.'),(3137,3,14,26,'Dan imam harus menuang sedikit dari minyak itu ke telapak tangan kirinya sendiri,'),(3138,3,14,27,'lalu sedikit dari minyak itu haruslah dipercikkan oleh imam dengan jari kanannya tujuh kali di hadapan TUHAN.'),(3139,3,14,28,'Kemudian imam harus membubuh sedikit dari minyak itu pada cuping telinga kanan dari orang yang akan ditahirkan, pada ibu jari tangan kanannya dan pada ibu jari kaki kanannya, di tempat mana dibubuhi darah tebusan salah itu.'),(3140,3,14,29,'Dan minyak selebihnya haruslah dibubuhnya pada kepala orang yang akan ditahirkan, supaya diadakan pendamaian bagi orang itu di hadapan TUHAN.'),(3141,3,14,30,'Lalu ia harus mempersembahkan seekor dari kedua burung tekukur atau anak burung merpati, yang dibawa orang itu sekadar kemampuannya,'),(3142,3,14,31,'yang seekor sebagai korban penghapus dosa, dan yang seekor lagi sebagai korban bakaran, di samping korban sajian. Dengan demikian imam mengadakan pendamaian bagi orang yang akan ditahirkan di hadapan TUHAN.'),(3143,3,14,32,'Itulah hukum tentang pentahiran seorang yang kena kusta yang tidak mampu.\"'),(3144,3,14,33,'TUHAN berfirman kepada Musa dan Harun:'),(3145,3,14,34,'Apabila kamu masuk ke tanah Kanaan yang akan Kuberikan kepadamu menjadi milikmu dan Aku mendatangkan tanda kusta di sebuah rumah di negeri milikmu itu,'),(3146,3,14,35,'maka pemilik rumah itu harus datang memberitahukannya kepada imam: Ada kelihatan seperti tanda kusta di rumahku.'),(3147,3,14,36,'Maka imam harus memerintahkan supaya rumah itu dikosongkan, sebelum ia datang memeriksa tanda kusta itu, supaya jangan menjadi najis segala yang ada di dalam rumah itu, dan sesudah itu barulah imam datang untuk memeriksanya.'),(3148,3,14,37,'Kalau menurut pemeriksaannya tanda pada dinding rumah itu merupakan lekuk-lekuk yang kehijau-hijauan atau kemerah-merahan warnanya, yang kelihatan lebih dalam dari permukaan dinding itu,'),(3149,3,14,38,'imam harus keluar dari rumah itu, lalu berdiri di depan pintu rumah, dan menutup rumah itu tujuh hari lamanya.'),(3150,3,14,39,'Pada hari yang ketujuh imam harus datang kembali; kalau menurut pemeriksaannya tanda kusta itu meluas pada dinding rumah,'),(3151,3,14,40,'maka imam harus memerintahkan supaya orang mengungkit batu-batu yang kena tanda itu dan membuangnya ke luar kota ke suatu tempat yang najis.'),(3152,3,14,41,'Dan ia harus mengikis rumah itu sebelah dalam berkeliling, dan kikisan lepa itu haruslah ditumpahkan ke luar kota ke suatu tempat yang najis.'),(3153,3,14,42,'Dan orang harus mengambil batu-batu lain, lalu memasangnya sebagai pengganti batu-batu tadi, dan harus mengambil lepa lain dan melepa rumah itu.'),(3154,3,14,43,'Tetapi jikalau tanda itu timbul lagi di dalam rumah itu, sesudah batu-batunya diungkit dan sesudah rumah itu dikikis, bahkan sesudah dilepa lagi,'),(3155,3,14,44,'dan kalau imam datang dan menurut pemeriksaannya tanda itu meluas di dalam rumah, maka kusta ganaslah yang di dalam rumah itu, dan rumah itu najis.'),(3156,3,14,45,'Rumah itu haruslah dirombak, yakni batunya, kayunya dan segala lepa rumah itu, lalu dibawa semuanya ke luar kota ke suatu tempat yang najis.'),(3157,3,14,46,'Dan orang yang masuk ke dalam rumah itu selama rumah itu ditutup, menjadi najis sampai matahari terbenam.'),(3158,3,14,47,'Dan orang yang tidur di dalam rumah itu haruslah mencuci pakaiannya; demikian juga orang yang makan di dalam rumah itu haruslah mencuci pakaiannya.'),(3159,3,14,48,'Tetapi jikalau imam datang dan menurut pemeriksaannya tanda itu tidak meluas di dalam rumah itu, sesudah dilepa, maka imam harus menyatakan rumah itu tahir, karena tanda itu telah hilang.'),(3160,3,14,49,'Kemudian, untuk menyucikan rumah itu, haruslah ia mengambil dua ekor burung, kayu aras, kain kirmizi dan hisop.'),(3161,3,14,50,'Burung yang seekor haruslah disembelihnya di atas belanga tanah berisi air mengalir.'),(3162,3,14,51,'Lalu ia harus mengambil kayu aras dan hisop, kain kirmizi dan burung yang masih hidup itu, dan mencelupkan semuanya ke dalam darah burung yang sudah disembelih dan ke dalam air mengalir itu, kemudian ia harus memercik kepada rumah itu tujuh kali.'),(3163,3,14,52,'Dengan demikian ia harus menyucikan rumah itu dengan darah burung, air mengalir, burung yang hidup, kayu aras, hisop, dan kain kirmizi.'),(3164,3,14,53,'Dan burung yang hidup itu harus dilepaskannya ke luar kota ke padang. Dengan demikian ia mengadakan pendamaian bagi rumah itu, maka rumah itu menjadi tahir.'),(3165,3,14,54,'Itulah hukum tentang setiap penyakit kusta, kudis kepala,'),(3166,3,14,55,'tentang kusta pada pakaian dan rumah,'),(3167,3,14,56,'tentang bengkak, bintil-bintil dan panau,'),(3168,3,14,57,'untuk memberi petunjuk dalam hal najis atau dalam hal tahir; itulah hukum tentang kusta.\"'),(3169,3,15,1,'TUHAN berfirman kepada Musa dan Harun:'),(3170,3,15,2,'Berbicaralah kepada orang Israel dan katakan kepada mereka: Apabila aurat seorang laki-laki mengeluarkan lelehan, maka najislah ia karena lelehannya itu.'),(3171,3,15,3,'Beginilah kenajisannya berhubung dengan lelehannya itu: bila auratnya membiarkan lelehan itu mengalir, atau bila auratnya menahannya, sehingga tidak mengeluarkan lelehan, maka itulah kenajisannya.'),(3172,3,15,4,'Setiap tempat tidur, yang ditiduri orang yang mengeluarkan lelehan itu menjadi najis, dan setiap barang yang didudukinya menjadi najis juga.'),(3173,3,15,5,'Setiap orang yang kena kepada tempat tidurnya haruslah mencuci pakaiannya, membasuh tubuhnya dengan air dan ia menjadi najis sampai matahari terbenam.'),(3174,3,15,6,'Siapa yang duduk di atas barang yang telah diduduki oleh orang yang demikian haruslah mencuci pakaiannya, membasuh tubuhnya dengan air, dan ia menjadi najis sampai matahari terbenam.'),(3175,3,15,7,'Siapa yang kena kepada tubuh orang yang demikian, haruslah mencuci pakaiannya, membasuh tubuhnya dengan air dan ia menjadi najis sampai matahari terbenam.'),(3176,3,15,8,'Apabila orang yang demikian meludahi orang yang tahir, haruslah orang ini mencuci pakaiannya, membasuh tubuhnya dengan air dan ia menjadi najis sampai matahari terbenam.'),(3177,3,15,9,'Dan setiap pelana yang diduduki orang yang demikian menjadi najis.'),(3178,3,15,10,'Setiap orang yang kena kepada sesuatu bekas tempat orang tadi menjadi najis sampai matahari terbenam. Siapa yang mengangkatnya, haruslah mencuci pakaiannya, membasuh tubuhnya dengan air dan ia menjadi najis sampai matahari terbenam.'),(3179,3,15,11,'Dan setiap orang yang kena pada orang yang demikian, sedang orang ini tidak mencuci tangan dahulu dengan air, haruslah mencuci pakaiannya, membasuh tubuhnya dengan air dan ia menjadi najis sampai matahari terbenam.'),(3180,3,15,12,'Kalau orang itu kena pada belanga tanah, itu haruslah dipecahkan, dan setiap perkakas kayu haruslah dicuci dengan air.'),(3181,3,15,13,'Apabila orang yang demikian sudah bersih dari lelehannya, ia harus menghitung tujuh hari lagi untuk dapat dinyatakan tahir, lalu mencuci pakaiannya, membasuh tubuhnya dengan air mengalir, maka ia menjadi tahir.'),(3182,3,15,14,'Pada hari yang kedelapan ia harus mengambil dua ekor burung tekukur atau dua ekor anak burung merpati, dan datang ke hadapan TUHAN, ke pintu Kemah Pertemuan, dan menyerahkan burung-burung itu kepada imam.'),(3183,3,15,15,'Lalu imam harus mempersembahkannya, yang seekor sebagai korban penghapus dosa dan yang seekor lagi sebagai korban bakaran. Dengan demikian imam mengadakan pendamaian bagi orang itu di hadapan TUHAN karena lelehannya.'),(3184,3,15,16,'Apabila seorang laki-laki tertumpah maninya, ia harus membasuh seluruh tubuhnya dengan air dan ia menjadi najis sampai matahari terbenam.'),(3185,3,15,17,'Setiap pakaian dan setiap kulit, yang kena tumpahan mani itu, haruslah dicuci dengan air dan menjadi najis sampai matahari terbenam.'),(3186,3,15,18,'Juga seorang perempuan, kalau seorang laki-laki tidur dengan dia dengan ada tumpahan mani, maka keduanya harus membasuh tubuhnya dengan air dan mereka menjadi najis sampai matahari terbenam.'),(3187,3,15,19,'Apabila seorang perempuan mengeluarkan lelehan, dan lelehannya itu adalah darah dari auratnya, ia harus tujuh hari lamanya dalam cemar kainnya, dan setiap orang yang kena kepadanya, menjadi najis sampai matahari terbenam.'),(3188,3,15,20,'Segala sesuatu yang ditidurinya selama ia cemar kain menjadi najis. Dan segala sesuatu yang didudukinya menjadi najis juga.'),(3189,3,15,21,'Setiap orang yang kena kepada tempat tidur perempuan itu haruslah mencuci pakaiannya, membasuh tubuhnya dengan air dan ia menjadi najis sampai matahari terbenam.'),(3190,3,15,22,'Setiap orang yang kena kepada sesuatu barang yang diduduki perempuan itu haruslah mencuci pakaiannya, membasuh diri dengan air dan ia menjadi najis sampai matahari terbenam.'),(3191,3,15,23,'Juga pada waktu ia kena kepada sesuatu yang ada di tempat tidur atau di atas barang yang diduduki perempuan itu, ia menjadi najis sampai matahari terbenam.'),(3192,3,15,24,'Jikalau seorang laki-laki tidur dengan perempuan itu, dan ia kena cemar kain perempuan itu, maka ia menjadi najis selama tujuh hari, dan setiap tempat tidur yang ditidurinya menjadi najis juga.'),(3193,3,15,25,'Apabila seorang perempuan berhari-hari lamanya mengeluarkan lelehan, yakni lelehan darah yang bukan pada waktu cemar kainnya, atau apabila ia mengeluarkan lelehan lebih lama dari waktu cemar kainnya, maka selama lelehannya yang najis itu perempuan itu adalah seperti pada hari-hari cemar kainnya, yakni ia najis.'),(3194,3,15,26,'Setiap tempat tidur yang ditidurinya, selama ia mengeluarkan lelehan, haruslah baginya seperti tempat tidur pada waktu cemar kainnya dan setiap barang yang didudukinya menjadi najis sama seperti kenajisan cemar kainnya.'),(3195,3,15,27,'Setiap orang yang kena kepada barang-barang itu menjadi najis, dan ia harus mencuci pakaiannya, membasuh tubuhnya dengan air, dan ia menjadi najis sampai matahari terbenam.'),(3196,3,15,28,'Tetapi jikalau perempuan itu sudah tahir dari lelehannya, ia harus menghitung tujuh hari lagi, sesudah itu barulah ia menjadi tahir.'),(3197,3,15,29,'Pada hari yang kedelapan ia harus mengambil dua ekor burung tekukur atau dua ekor anak burung merpati dan membawanya kepada imam ke pintu Kemah Pertemuan.'),(3198,3,15,30,'Imam harus mempersembahkan yang seekor sebagai korban penghapus dosa dan yang seekor lagi sebagai korban bakaran. Dengan demikian imam mengadakan pendamaian bagi orang itu di hadapan TUHAN, karena lelehannya yang najis itu.'),(3199,3,15,31,'Begitulah kamu harus menghindarkan orang Israel dari kenajisannya, supaya mereka jangan mati di dalam kenajisannya, bila mereka menajiskan Kemah Suci-Ku yang ada di tengah-tengah mereka itu.\"'),(3200,3,15,32,'Itulah hukum tentang seorang laki-laki yang mengeluarkan lelehan atau yang tertumpah maninya yang menyebabkan dia najis,'),(3201,3,15,33,'dan tentang seorang perempuan yang bercemar kain dan tentang seseorang, baik laki-laki maupun perempuan, yang mengeluarkan lelehan, dan tentang laki-laki yang tidur dengan perempuan yang najis.'),(3202,3,16,1,'Sesudah kedua anak Harun mati, yang terjadi pada waktu mereka mendekat ke hadapan TUHAN, berfirmanlah TUHAN kepada Musa.'),(3203,3,16,2,'Firman TUHAN kepadanya: \"Katakanlah kepada Harun, kakakmu, supaya ia jangan sembarang waktu masuk ke dalam tempat kudus di belakang tabir, ke depan tutup pendamaian yang di atas tabut supaya jangan ia mati; karena Aku menampakkan diri dalam awan di atas tutup pendamaian.'),(3204,3,16,3,'Beginilah caranya Harun masuk ke dalam tempat kudus itu, yakni dengan membawa seekor lembu jantan muda untuk korban penghapus dosa dan seekor domba jantan untuk korban bakaran.'),(3205,3,16,4,'Ia harus mengenakan kemeja lenan yang kudus dan ia harus menutupi auratnya dengan celana lenan dan ia harus memakai ikat pinggang lenan dan berlilitkan serban lenan; itulah pakaian kudus yang harus dikenakannya, sesudah ia membasuh tubuhnya dengan air.'),(3206,3,16,5,'Dari umat Israel ia harus mengambil dua ekor kambing jantan untuk korban penghapus dosa dan seekor domba jantan untuk korban bakaran.'),(3207,3,16,6,'Kemudian Harun harus mempersembahkan lembu jantan yang akan menjadi korban penghapus dosa baginya sendiri dan dengan demikian mengadakan pendamaian baginya dan bagi keluarganya.'),(3208,3,16,7,'Ia harus mengambil kedua ekor kambing jantan itu dan menempatkannya di hadapan TUHAN di depan pintu Kemah Pertemuan,'),(3209,3,16,8,'dan harus membuang undi atas kedua kambing jantan itu, sebuah undi bagi TUHAN dan sebuah bagi Azazel.'),(3210,3,16,9,'Lalu Harun harus mempersembahkan kambing jantan yang kena undi bagi TUHAN itu dan mengolahnya sebagai korban penghapus dosa.'),(3211,3,16,10,'Tetapi kambing jantan yang kena undi bagi Azazel haruslah ditempatkan hidup-hidup di hadapan TUHAN untuk mengadakan pendamaian, lalu dilepaskan bagi Azazel ke padang gurun.'),(3212,3,16,11,'Harun harus mempersembahkan lembu jantan yang akan menjadi korban penghapus dosa baginya sendiri dan mengadakan pendamaian baginya dan bagi keluarganya; ia harus menyembelih lembu jantan itu.'),(3213,3,16,12,'Dan ia harus mengambil perbaraan berisi penuh bara api dari atas mezbah yang di hadapan TUHAN, serta serangkup penuh ukupan dari wangi-wangian yang digiling sampai halus, lalu membawanya masuk ke belakang tabir.'),(3214,3,16,13,'Kemudian ia harus meletakkan ukupan itu di atas api yang di hadapan TUHAN, sehingga asap ukupan itu menutupi tutup pendamaian yang di atas hukum Allah, supaya ia jangan mati.'),(3215,3,16,14,'Lalu ia harus mengambil sedikit dari darah lembu jantan itu dan memercikkannya dengan jarinya ke atas tutup pendamaian di bagian muka, dan ke depan tutup pendamaian itu ia harus memercikkan sedikit dari darah itu dengan jarinya tujuh kali.'),(3216,3,16,15,'Lalu ia harus menyembelih domba jantan yang akan menjadi korban penghapus dosa bagi bangsa itu dan membawa darahnya masuk ke belakang tabir, kemudian haruslah diperbuatnya dengan darah itu seperti yang diperbuatnya dengan darah lembu jantan, yakni ia harus memercikkannya ke atas tutup pendamaian dan ke depan tutup pendamaian itu.'),(3217,3,16,16,'Dengan demikian ia mengadakan pendamaian bagi tempat kudus itu karena segala kenajisan orang Israel dan karena segala pelanggaran mereka, apapun juga dosa mereka. Demikianlah harus diperbuatnya dengan Kemah Pertemuan yang tetap diam di antara mereka di tengah-tengah segala kenajisan mereka.'),(3218,3,16,17,'Seorangpun tidak boleh hadir di dalam Kemah Pertemuan, bila Harun masuk untuk mengadakan pendamaian di tempat kudus, sampai ia keluar, setelah mengadakan pendamaian baginya sendiri, bagi keluarganya dan bagi seluruh jemaah orang Israel.'),(3219,3,16,18,'Kemudian haruslah ia pergi ke luar ke mezbah yang ada di hadapan TUHAN, dan mengadakan pendamaian bagi mezbah itu. Ia harus mengambil sedikit dari darah lembu jantan dan dari darah domba jantan itu dan membubuhnya pada tanduk-tanduk mezbah sekelilingnya.'),(3220,3,16,19,'Kemudian ia harus memercikkan sedikit dari darah itu ke mezbah itu dengan jarinya tujuh kali dan mentahirkan serta menguduskannya dari segala kenajisan orang Israel.'),(3221,3,16,20,'Setelah selesai mengadakan pendamaian bagi tempat kudus dan Kemah Pertemuan serta mezbah, ia harus mempersembahkan kambing jantan yang masih hidup itu,'),(3222,3,16,21,'dan Harun harus meletakkan kedua tangannya ke atas kepala kambing jantan yang hidup itu dan mengakui di atas kepala kambing itu segala kesalahan orang Israel dan segala pelanggaran mereka, apapun juga dosa mereka; ia harus menanggungkan semuanya itu ke atas kepala kambing jantan itu dan kemudian melepaskannya ke padang gurun dengan perantaraan seseorang yang sudah siap sedia untuk itu.'),(3223,3,16,22,'Demikianlah kambing jantan itu harus mengangkut segala kesalahan Israel ke tanah yang tandus, dan kambing itu harus dilepaskan di padang gurun.'),(3224,3,16,23,'Sesudah itu Harun harus masuk ke dalam Kemah Pertemuan dan menanggalkan pakaian lenan, yang dikenakannya ketika ia masuk ke dalam tempat kudus dan harus meninggalkannya di sana.'),(3225,3,16,24,'Ia harus membasuh tubuhnya dengan air di suatu tempat yang kudus dan mengenakan pakaiannya sendiri, lalu ia harus keluar dan mempersembahkan korban bakarannya sendiri dan korban bakaran bangsa itu; dengan demikian ia mengadakan pendamaian baginya sendiri dan bagi bangsa itu.'),(3226,3,16,25,'Kemudian ia harus membakar lemak korban penghapus dosa di atas mezbah.'),(3227,3,16,26,'Maka orang yang melepaskan kambing jantan bagi Azazel itu harus mencuci pakaiannya, membasuh tubuhnya dengan air dan sesudah itu barulah boleh masuk ke perkemahan.'),(3228,3,16,27,'Lembu jantan dan kambing jantan korban penghapus dosa, yang darahnya telah dibawa masuk untuk mengadakan pendamaian di dalam tempat kudus, harus dibawa keluar dari perkemahan, dan kulitnya, dagingnya dan kotorannya harus dibakar habis.'),(3229,3,16,28,'Siapa yang membakar semuanya itu, harus mencuci pakaiannya, membasuh tubuhnya dengan air dan sesudah itu barulah boleh masuk ke perkemahan.'),(3230,3,16,29,'Inilah yang harus menjadi ketetapan untuk selama-lamanya bagi kamu, yakni pada bulan yang ketujuh, pada tanggal sepuluh bulan itu kamu harus merendahkan diri dengan berpuasa dan janganlah kamu melakukan sesuatu pekerjaan, baik orang Israel asli maupun orang asing yang tinggal di tengah-tengahmu.'),(3231,3,16,30,'Karena pada hari itu harus diadakan pendamaian bagimu untuk mentahirkan kamu. Kamu akan ditahirkan dari segala dosamu di hadapan TUHAN.'),(3232,3,16,31,'Hari itu harus menjadi sabat, hari perhentian penuh, bagimu dan kamu harus merendahkan diri dengan berpuasa. Itulah suatu ketetapan untuk selama-lamanya.'),(3233,3,16,32,'Dan pendamaian harus diadakan oleh imam yang telah diurapi dan telah ditahbiskan untuk memegang jabatan imam menggantikan ayahnya; ia harus mengenakan pakaian lenan, yakni pakaian kudus.'),(3234,3,16,33,'Ia harus mengadakan pendamaian bagi tempat maha kudus, bagi Kemah Pertemuan dan bagi mezbah, juga bagi para imam dan bagi seluruh bangsa itu, yakni jemaah itu.'),(3235,3,16,34,'Itulah yang harus menjadi ketetapan untuk selama-lamanya bagimu, supaya sekali setahun diadakan pendamaian bagi orang Israel karena segala dosa mereka.\" Maka Harun melakukan seperti yang diperintahkan TUHAN kepada Musa.'),(3236,3,17,1,'TUHAN berfirman kepada Musa:'),(3237,3,17,2,'Berbicaralah kepada Harun dan kepada anak-anaknya dan kepada seluruh orang Israel, dan katakan kepada mereka: Inilah firman yang diperintahkan TUHAN:'),(3238,3,17,3,'Setiap orang dari kaum Israel yang menyembelih lembu atau domba atau kambing di dalam perkemahan atau di luarnya,'),(3239,3,17,4,'tetapi tidak membawanya ke pintu Kemah Pertemuan, untuk dipersembahkan sebagai persembahan kepada TUHAN di depan Kemah Suci TUHAN, hal itu harus dihitungkan kepada orang itu sebagai hutang darah, karena ia telah menumpahkan darah, dan orang itu haruslah dilenyapkan dari tengah-tengah bangsanya.'),(3240,3,17,5,'Maksudnya supaya orang Israel membawa korban sembelihan mereka, yang biasa dipersembahkan mereka di padang, kepada TUHAN ke pintu Kemah Pertemuan dengan menyerahkannya kepada imam, untuk dipersembahkan kepada TUHAN sebagai korban keselamatan.'),(3241,3,17,6,'Imam harus menyiramkan darahnya pada mezbah TUHAN di depan pintu Kemah Pertemuan dan membakar lemaknya menjadi bau yang menyenangkan bagi TUHAN.'),(3242,3,17,7,'Janganlah mereka mempersembahkan lagi korban mereka kepada jin-jin, sebab menyembah jin-jin itu adalah zinah. Itulah yang harus menjadi ketetapan untuk selama-lamanya bagi mereka turun-temurun.'),(3243,3,17,8,'Dan haruslah kaukatakan kepada mereka: Setiap orang dari kaum Israel atau dari orang asing yang tinggal di tengah-tengah mereka, yang mempersembahkan korban bakaran atau korban sembelihan,'),(3244,3,17,9,'tetapi tidak membawanya ke pintu Kemah Pertemuan supaya dipersembahkan kepada TUHAN, maka orang itu haruslah dilenyapkan dari antara orang-orang sebangsanya.\"'),(3245,3,17,10,'Setiap orang dari bangsa Israel dan dari orang asing yang tinggal di tengah-tengah mereka, yang makan darah apapun juga Aku sendiri akan menentang dia dan melenyapkan dia dari tengah-tengah bangsanya.'),(3246,3,17,11,'Karena nyawa makhluk ada di dalam darahnya dan Aku telah memberikan darah itu kepadamu di atas mezbah untuk mengadakan pendamaian bagi nyawamu, karena darah mengadakan pendamaian dengan perantaraan nyawa.'),(3247,3,17,12,'Itulah sebabnya Aku berfirman kepada orang Israel: Seorangpun di antaramu janganlah makan darah. Demikian juga orang asing yang tinggal di tengah-tengahmu tidak boleh makan darah.'),(3248,3,17,13,'Setiap orang dari orang Israel dan dari orang asing yang tinggal di tengah-tengahmu, yang menangkap dalam perburuan seekor binatang atau burung yang boleh dimakan, haruslah mencurahkan darahnya, lalu menimbunnya dengan tanah.'),(3249,3,17,14,'Karena darah itulah nyawa segala makhluk. Sebab itu Aku telah berfirman kepada orang Israel: Darah makhluk apapun janganlah kamu makan, karena darah itulah nyawa segala makhluk: setiap orang yang memakannya haruslah dilenyapkan.'),(3250,3,17,15,'Dan setiap orang yang makan bangkai atau sisa mangsa binatang buas, baik ia orang Israel asli maupun orang asing, haruslah mencuci pakaiannya, membasuh tubuhnya dengan air dan ia menjadi najis sampai matahari terbenam, barulah ia menjadi tahir.'),(3251,3,17,16,'Tetapi jikalau ia tidak mencuci pakaiannya dan tidak membasuh tubuhnya, ia akan menanggung kesalahannya sendiri.\"'),(3252,3,18,1,'TUHAN berfirman kepada Musa:'),(3253,3,18,2,'Berbicaralah kepada orang Israel dan katakan kepada mereka: Akulah TUHAN, Allahmu.'),(3254,3,18,3,'Janganlah kamu berbuat seperti yang diperbuat orang di tanah Mesir, di mana kamu diam dahulu; juga janganlah kamu berbuat seperti yang diperbuat orang di tanah Kanaan, ke mana Aku membawa kamu; janganlah kamu hidup menurut kebiasaan mereka.'),(3255,3,18,4,'Kamu harus lakukan peraturan-Ku dan harus berpegang pada ketetapan-Ku dengan hidup menurut semuanya itu; Akulah TUHAN, Allahmu.'),(3256,3,18,5,'Sesungguhnya kamu harus berpegang pada ketetapan-Ku dan peraturan-Ku. Orang yang melakukannya, akan hidup karenanya; Akulah TUHAN.'),(3257,3,18,6,'Siapapun di antaramu janganlah menghampiri seorang kerabatnya yang terdekat untuk menyingkapkan auratnya; Akulah TUHAN.'),(3258,3,18,7,'Janganlah kausingkapkan aurat isteri ayahmu, karena ia hak ayahmu; dia ibumu, jadi janganlah singkapkan auratnya.'),(3259,3,18,8,'Janganlah kausingkapkan aurat seorang isteri ayahmu, karena ia hak ayahmu.'),(3260,3,18,9,'Mengenai aurat saudaramu perempuan, anak ayahmu atau anak ibumu, baik yang lahir di rumah ayahmu maupun yang lahir di luar, janganlah kausingkapkan auratnya.'),(3261,3,18,10,'Mengenai aurat anak perempuan dari anakmu laki-laki atau anakmu perempuan, janganlah kausingkapkan auratnya, karena dengan begitu engkau menodai keturunanmu.'),(3262,3,18,11,'Mengenai aurat anak perempuan dari seorang isteri ayahmu, yang lahir pada ayahmu sendiri, janganlah kausingkapkan auratnya, karena ia saudaramu perempuan.'),(3263,3,18,12,'Janganlah kausingkapkan aurat saudara perempuan ayahmu, karena ia kerabat ayahmu.'),(3264,3,18,13,'Janganlah kausingkapkan aurat saudara perempuan ibumu, karena ia kerabat ibumu.'),(3265,3,18,14,'Janganlah kausingkapkan aurat isteri saudara laki-laki ayahmu, janganlah kauhampiri isterinya, karena ia isteri saudara ayahmu.'),(3266,3,18,15,'Janganlah kausingkapkan aurat menantumu perempuan, karena ia isteri anakmu laki-laki, maka janganlah kausingkapkan auratnya.'),(3267,3,18,16,'Janganlah kausingkapkan aurat isteri saudaramu laki-laki, karena itu hak saudaramu laki-laki.'),(3268,3,18,17,'Janganlah kausingkapkan aurat seorang perempuan dan anaknya perempuan. Janganlah kauambil anak perempuan dari anaknya laki-laki atau dari anaknya perempuan untuk menyingkapkan auratnya, karena mereka adalah kerabatmu; itulah perbuatan mesum.'),(3269,3,18,18,'Janganlah kauambil seorang perempuan sebagai madu kakaknya untuk menyingkapkan auratnya di samping kakaknya selama kakaknya itu masih hidup.'),(3270,3,18,19,'Janganlah kauhampiri seorang perempuan pada waktu cemar kainnya yang menajiskan untuk menyingkapkan auratnya.'),(3271,3,18,20,'Dan janganlah engkau bersetubuh dengan isteri sesamamu, sehingga engkau menjadi najis dengan dia.'),(3272,3,18,21,'Janganlah kauserahkan seorang dari anak-anakmu untuk dipersembahkan kepada Molokh, supaya jangan engkau melanggar kekudusan nama Allahmu; Akulah TUHAN.'),(3273,3,18,22,'Janganlah engkau tidur dengan laki-laki secara orang bersetubuh dengan perempuan, karena itu suatu kekejian.'),(3274,3,18,23,'Janganlah engkau berkelamin dengan binatang apapun, sehingga engkau menjadi najis dengan binatang itu. Seorang perempuan janganlah berdiri di depan seekor binatang untuk berkelamin, karena itu suatu perbuatan keji.'),(3275,3,18,24,'Janganlah kamu menajiskan dirimu dengan semuanya itu, sebab dengan semuanya itu bangsa-bangsa yang akan Kuhalaukan dari depanmu telah menjadi najis.'),(3276,3,18,25,'Negeri itu telah menjadi najis dan Aku telah membalaskan kesalahannya kepadanya, sehingga negeri itu memuntahkan penduduknya.'),(3277,3,18,26,'Tetapi kamu ini haruslah tetap berpegang pada ketetapan-Ku dan peraturan-Ku dan jangan melakukan sesuatupun dari segala kekejian itu, baik orang Israel asli maupun orang asing yang tinggal di tengah-tengahmu,'),(3278,3,18,27,'--karena segala kekejian itu telah dilakukan oleh penghuni negeri yang sebelum kamu, sehingga negeri itu sudah menjadi najis--'),(3279,3,18,28,'supaya kamu jangan dimuntahkan oleh negeri itu, apabila kamu menajiskannya, seperti telah dimuntahkannya bangsa yang sebelum kamu.'),(3280,3,18,29,'Karena setiap orang yang melakukan sesuatupun dari segala kekejian itu, orang itu harus dilenyapkan dari tengah-tengah bangsanya.'),(3281,3,18,30,'Dengan demikian kamu harus tetap berpegang pada kewajibanmu terhadap Aku, dan jangan kamu melakukan sesuatu dari kebiasaan yang keji itu, yang dilakukan sebelum kamu, dan janganlah kamu menajiskan dirimu dengan semuanya itu; Akulah TUHAN, Allahmu.\"'),(3282,3,19,1,'TUHAN berfirman kepada Musa:'),(3283,3,19,2,'Berbicaralah kepada segenap jemaah Israel dan katakan kepada mereka: Kuduslah kamu, sebab Aku, TUHAN, Allahmu, kudus.'),(3284,3,19,3,'Setiap orang di antara kamu haruslah menyegani ibunya dan ayahnya dan memelihara hari-hari sabat-Ku; Akulah TUHAN, Allahmu.'),(3285,3,19,4,'Janganlah kamu berpaling kepada berhala-berhala dan janganlah kamu membuat bagimu dewa tuangan; Akulah TUHAN, Allahmu.'),(3286,3,19,5,'Apabila kamu mempersembahkan korban keselamatan kepada TUHAN, kamu harus mempersembahkannya sedemikian, hingga TUHAN berkenan akan kamu.'),(3287,3,19,6,'Dan haruslah itu dimakan pada hari mempersembahkannya atau boleh juga pada keesokan harinya, tetapi apa yang tinggal sampai hari yang ketiga haruslah dibakar habis.'),(3288,3,19,7,'Jikalau dimakan juga pada hari yang ketiga, maka itu menjadi sesuatu yang jijik dan TUHAN tidak berkenan akan orang itu.'),(3289,3,19,8,'Siapa yang memakannya, akan menanggung kesalahannya sendiri, karena ia telah melanggar kekudusan persembahan kudus yang kepada TUHAN. Nyawa orang itu haruslah dilenyapkan dari antara orang-orang sebangsanya.'),(3290,3,19,9,'Pada waktu kamu menuai hasil tanahmu, janganlah kausabit ladangmu habis-habis sampai ke tepinya, dan janganlah kaupungut apa yang ketinggalan dari penuaianmu.'),(3291,3,19,10,'Juga sisa-sisa buah anggurmu janganlah kaupetik untuk kedua kalinya dan buah yang berjatuhan di kebun anggurmu janganlah kaupungut, tetapi semuanya itu harus kautinggalkan bagi orang miskin dan bagi orang asing; Akulah TUHAN, Allahmu.'),(3292,3,19,11,'Janganlah kamu mencuri, janganlah kamu berbohong dan janganlah kamu berdusta seorang kepada sesamanya.'),(3293,3,19,12,'Janganlah kamu bersumpah dusta demi nama-Ku, supaya engkau jangan melanggar kekudusan nama Allahmu; Akulah TUHAN.'),(3294,3,19,13,'Janganlah engkau memeras sesamamu manusia dan janganlah engkau merampas; janganlah kautahan upah seorang pekerja harian sampai besok harinya.'),(3295,3,19,14,'Janganlah kaukutuki orang tuli dan di depan orang buta janganlah kautaruh batu sandungan, tetapi engkau harus takut akan Allahmu; Akulah TUHAN.'),(3296,3,19,15,'Janganlah kamu berbuat curang dalam peradilan; janganlah engkau membela orang kecil dengan tidak sewajarnya dan janganlah engkau terpengaruh oleh orang-orang besar, tetapi engkau harus mengadili orang sesamamu dengan kebenaran.'),(3297,3,19,16,'Janganlah engkau pergi kian ke mari menyebarkan fitnah di antara orang-orang sebangsamu; janganlah engkau mengancam hidup sesamamu manusia; Akulah TUHAN.'),(3298,3,19,17,'Janganlah engkau membenci saudaramu di dalam hatimu, tetapi engkau harus berterus terang menegor orang sesamamu dan janganlah engkau mendatangkan dosa kepada dirimu karena dia.'),(3299,3,19,18,'Janganlah engkau menuntut balas, dan janganlah menaruh dendam terhadap orang-orang sebangsamu, melainkan kasihilah sesamamu manusia seperti dirimu sendiri; Akulah TUHAN.'),(3300,3,19,19,'Kamu harus berpegang kepada ketetapan-Ku. Janganlah kawinkan dua jenis ternak dan janganlah taburi ladangmu dengan dua jenis benih, dan janganlah pakai pakaian yang dibuat dari pada dua jenis bahan.'),(3301,3,19,20,'Apabila seorang laki-laki bersetubuh dengan seorang perempuan, yakni seorang budak perempuan yang ada di bawah kuasa laki-laki lain, tetapi yang tidak pernah ditebus dan tidak juga diberi surat tanda merdeka, maka perbuatan itu haruslah dihukum; tetapi janganlah keduanya dihukum mati, karena perempuan itu belum dimerdekakan.'),(3302,3,19,21,'Laki-laki itu harus membawa tebusan salahnya kepada TUHAN ke pintu Kemah Pertemuan, yakni seekor domba jantan sebagai korban penebus salah.'),(3303,3,19,22,'Imam harus mengadakan pendamaian bagi orang itu dengan domba jantan korban penebus salah di hadapan TUHAN, karena dosa yang telah diperbuatnya, sehingga ia beroleh pengampunan dari dosanya itu.'),(3304,3,19,23,'Apabila kamu sudah masuk ke negeri itu dan menanam bermacam-macam pohon buah-buahan, janganlah kamu memetik buahnya selama tiga tahun dan jangan memakannya.'),(3305,3,19,24,'Tetapi pada tahun yang keempat haruslah segala buahnya menjadi persembahan kudus sebagai puji-pujian bagi TUHAN.'),(3306,3,19,25,'Barulah pada tahun yang kelima kamu boleh memakan buahnya, supaya hasilnya ditambah bagimu; Akulah TUHAN, Allahmu.'),(3307,3,19,26,'Janganlah kamu makan sesuatu yang darahnya masih ada. Janganlah kamu melakukan telaah atau ramalan.'),(3308,3,19,27,'Janganlah kamu mencukur tepi rambut kepalamu berkeliling dan janganlah engkau merusakkan tepi janggutmu.'),(3309,3,19,28,'Janganlah kamu menggoresi tubuhmu karena orang mati dan janganlah merajah tanda-tanda pada kulitmu; Akulah TUHAN.'),(3310,3,19,29,'Janganlah engkau merusak kesucian anakmu perempuan dengan menjadikan dia perempuan sundal, supaya negeri itu jangan melakukan persundalan, sehingga negeri itu penuh dengan perbuatan mesum.'),(3311,3,19,30,'Kamu harus memelihara hari-hari sabat-Ku dan menghormati tempat kudus-Ku; Akulah TUHAN.'),(3312,3,19,31,'Janganlah kamu berpaling kepada arwah atau kepada roh-roh peramal; janganlah kamu mencari mereka dan dengan demikian menjadi najis karena mereka; Akulah TUHAN, Allahmu.'),(3313,3,19,32,'Engkau harus bangun berdiri di hadapan orang ubanan dan engkau harus menaruh hormat kepada orang yang tua dan engkau harus takut akan Allahmu; Akulah TUHAN.'),(3314,3,19,33,'Apabila seorang asing tinggal padamu di negerimu, janganlah kamu menindas dia.'),(3315,3,19,34,'Orang asing yang tinggal padamu harus sama bagimu seperti orang Israel asli dari antaramu, kasihilah dia seperti dirimu sendiri, karena kamu juga orang asing dahulu di tanah Mesir; Akulah TUHAN, Allahmu.'),(3316,3,19,35,'Janganlah kamu berbuat curang dalam peradilan, mengenai ukuran, timbangan dan sukatan.'),(3317,3,19,36,'Neraca yang betul, batu timbangan yang betul, efa yang betul dan hin yang betul haruslah kamu pakai; Akulah TUHAN, Allahmu yang membawa kamu keluar dari tanah Mesir.'),(3318,3,19,37,'Demikianlah kamu harus berpegang pada segala ketetapan-Ku dan segala peraturan-Ku serta melakukan semuanya itu; Akulah TUHAN.\"'),(3319,3,20,1,'TUHAN berfirman kepada Musa:'),(3320,3,20,2,'Engkau harus berkata kepada orang Israel: Setiap orang, baik dari antara orang Israel maupun dari antara orang asing yang tinggal di tengah-tengah orang Israel, yang menyerahkan seorang dari anak-anaknya kepada Molokh, pastilah ia dihukum mati, yakni rakyat negeri harus melontari dia dengan batu.'),(3321,3,20,3,'Aku sendiri akan menentang orang itu dan akan melenyapkan dia dari tengah-tengah bangsanya, karena ia menyerahkan seorang dari anak-anaknya kepada Molokh, dengan maksud menajiskan tempat kudus-Ku dan melanggar kekudusan nama-Ku yang kudus.'),(3322,3,20,4,'Tetapi jikalau rakyat negeri menutup mata terhadap orang itu, ketika ia menyerahkan seorang dari anak-anaknya kepada Molokh, dan tidak menghukum dia mati,'),(3323,3,20,5,'maka Aku sendiri akan menentang orang itu serta kaumnya dan akan melenyapkan dia dari tengah-tengah bangsanya dan semua orang yang turut berzinah mengikuti dia, yakni berzinah dengan menyembah Molokh.'),(3324,3,20,6,'Orang yang berpaling kepada arwah atau kepada roh-roh peramal, yakni yang berzinah dengan bertanya kepada mereka, Aku sendiri akan menentang orang itu dan melenyapkan dia dari tengah-tengah bangsanya.'),(3325,3,20,7,'Maka kamu harus menguduskan dirimu, dan kuduslah kamu, sebab Akulah TUHAN, Allahmu.'),(3326,3,20,8,'Demikianlah kamu harus berpegang pada ketetapan-Ku dan melakukannya; Akulah TUHAN yang menguduskan kamu.'),(3327,3,20,9,'Apabila ada seseorang yang mengutuki ayahnya atau ibunya, pastilah ia dihukum mati; ia telah mengutuki ayahnya atau ibunya, maka darahnya tertimpa kepadanya sendiri.'),(3328,3,20,10,'Bila seorang laki-laki berzinah dengan isteri orang lain, yakni berzinah dengan isteri sesamanya manusia, pastilah keduanya dihukum mati, baik laki-laki maupun perempuan yang berzinah itu.'),(3329,3,20,11,'Bila seorang laki-laki tidur dengan seorang isteri ayahnya, jadi ia melanggar hak ayahnya, pastilah keduanya dihukum mati, dan darah mereka tertimpa kepada mereka sendiri.'),(3330,3,20,12,'Bila seorang laki-laki tidur dengan menantunya perempuan, pastilah keduanya dihukum mati; mereka telah melakukan suatu perbuatan keji, maka darah mereka tertimpa kepada mereka sendiri.'),(3331,3,20,13,'Bila seorang laki-laki tidur dengan laki-laki secara orang bersetubuh dengan perempuan, jadi keduanya melakukan suatu kekejian, pastilah mereka dihukum mati dan darah mereka tertimpa kepada mereka sendiri.'),(3332,3,20,14,'Bila seorang laki-laki mengambil seorang perempuan dan ibunya, itu suatu perbuatan mesum; ia dan kedua perempuan itu harus dibakar, supaya jangan ada perbuatan mesum di tengah-tengah kamu.'),(3333,3,20,15,'Bila seorang laki-laki berkelamin dengan seekor binatang, pastilah ia dihukum mati, dan binatang itupun harus kamu bunuh juga.'),(3334,3,20,16,'Bila seorang perempuan menghampiri binatang apapun untuk berkelamin, haruslah kaubunuh perempuan dan binatang itu; mereka pasti dihukum mati dan darah mereka tertimpa kepada mereka sendiri.'),(3335,3,20,17,'Bila seorang laki-laki mengambil saudaranya perempuan, anak ayahnya atau anak ibunya, dan mereka bersetubuh, maka itu suatu perbuatan sumbang, dan mereka harus dilenyapkan di depan orang-orang sebangsanya; orang itu telah menyingkapkan aurat saudaranya perempuan, maka ia harus menanggung kesalahannya sendiri.'),(3336,3,20,18,'Bila seorang laki-laki tidur dengan seorang perempuan yang bercemar kain, jadi ia menyingkapkan aurat perempuan itu dan membuka tutup lelerannya sedang perempuan itupun membiarkan tutup leleran darahnya itu disingkapkan, keduanya harus dilenyapkan dari tengah-tengah bangsanya.'),(3337,3,20,19,'Janganlah kausingkapkan aurat saudara perempuan ibumu atau saudara perempuan ayahmu, karena aurat seorang kerabatnya sendirilah yang dibuka, dan mereka harus menanggung kesalahannya sendiri.'),(3338,3,20,20,'Bila seorang laki-laki tidur dengan isteri saudara ayahnya, jadi ia melanggar hak saudara ayahnya, mereka mendatangkan dosa kepada dirinya, dan mereka akan mati dengan tidak beranak.'),(3339,3,20,21,'Bila seorang laki-laki mengambil isteri saudaranya, itu suatu kecemaran, karena ia melanggar hak saudaranya laki-laki, dan mereka akan tidak beranak.'),(3340,3,20,22,'Demikianlah kamu harus berpegang pada segala ketetapan-Ku dan segala peraturan-Ku serta melakukan semuanya itu, supaya jangan kamu dimuntahkan oleh negeri ke mana Aku membawa kamu untuk diam di sana.'),(3341,3,20,23,'Janganlah kamu hidup menurut kebiasaan bangsa yang akan Kuhalau dari depanmu: karena semuanya itu telah dilakukan mereka, sehingga Aku muak melihat mereka.'),(3342,3,20,24,'Tetapi kepadamu Aku telah berfirman: Kamulah yang akan menduduki tanah mereka dan Akulah yang akan memberikannya kepadamu menjadi milikmu, suatu negeri yang berlimpah-limpah susu dan madunya; Akulah TUHAN, Allahmu, yang memisahkan kamu dari bangsa-bangsa lain.'),(3343,3,20,25,'Kamu harus membedakan binatang yang tidak haram dari yang haram, dan burung-burung yang haram dari yang tidak haram, supaya kamu jangan membuat dirimu jijik oleh binatang berkaki empat dan burung-burung dan oleh segala yang merayap di muka bumi, yang telah Kupisahkan supaya kamu haramkan.'),(3344,3,20,26,'Kuduslah kamu bagi-Ku, sebab Aku ini, TUHAN, kudus dan Aku telah memisahkan kamu dari bangsa-bangsa lain, supaya kamu menjadi milik-Ku.'),(3345,3,20,27,'Apabila seorang laki-laki atau perempuan dirasuk arwah atau roh peramal, pastilah mereka dihukum mati, yakni mereka harus dilontari dengan batu dan darah mereka tertimpa kepada mereka sendiri.\"'),(3346,3,21,1,'TUHAN berfirman kepada Musa: \"Berbicaralah kepada para imam, anak-anak Harun, dan katakan kepada mereka: Seorang imam janganlah menajiskan diri dengan orang mati di antara orang-orang sebangsanya,'),(3347,3,21,2,'kecuali kalau yang mati itu adalah kerabatnya yang terdekat, yakni: ibunya, ayahnya, anaknya laki-laki atau perempuan, saudaranya laki-laki,'),(3348,3,21,3,'saudaranya perempuan, yang masih perawan dan dekat kepadanya karena belum mempunyai suami, dengan mereka itu bolehlah ia menajiskan diri.'),(3349,3,21,4,'Sebagai suami janganlah ia menajiskan diri di antara orang-orang sebangsanya dan dengan demikian melanggar kekudusannya.'),(3350,3,21,5,'Janganlah mereka menggundul sebagian kepalanya, dan janganlah mereka mencukur tepi janggutnya, dan janganlah mereka menggoresi kulit tubuhnya.'),(3351,3,21,6,'Mereka itu harus kudus bagi Allahnya dan janganlah mereka melanggar kekudusan nama Allahnya, karena merekalah yang mempersembahkan segala korban api-apian TUHAN, santapan Allah mereka, dan karena itu haruslah mereka kudus.'),(3352,3,21,7,'Janganlah mereka mengambil seorang perempuan sundal atau perempuan yang sudah dirusak kesuciannya atau seorang perempuan yang telah diceraikan oleh suaminya, karena imam itu kudus bagi Allahnya.'),(3353,3,21,8,'Dan kamu harus menganggap dia kudus, karena dialah yang mempersembahkan santapan Allahmu. Ia harus kudus bagimu, sebab Aku, TUHAN, yang menguduskan kamu adalah kudus.'),(3354,3,21,9,'Apabila anak perempuan seorang imam membiarkan kehormatannya dilanggar dengan bersundal, maka ia melanggar kekudusan ayahnya, dan ia harus dibakar dengan api.'),(3355,3,21,10,'Imam yang terbesar di antara saudara-saudaranya, yang sudah diurapi dengan menuangkan minyak urapan di atas kepalanya dan yang ditahbiskan dengan mengenakan kepadanya segala pakaian kudus, janganlah membiarkan rambutnya terurai dan janganlah ia mencabik pakaiannya.'),(3356,3,21,11,'Janganlah ia dekat kepada semua mayat, bahkan janganlah ia menajiskan diri dengan mayat ayahnya atau ibunya.'),(3357,3,21,12,'Janganlah ia keluar dari tempat kudus, supaya jangan dilanggarnya kekudusan tempat kudus Allahnya, karena minyak urapan Allahnya, yang menandakan bahwa ia telah dikhususkan, ada di atas kepalanya; Akulah TUHAN.'),(3358,3,21,13,'Ia harus mengambil seorang perempuan yang masih perawan.'),(3359,3,21,14,'Seorang janda atau perempuan yang telah diceraikan atau yang dirusak kesuciannya atau perempuan sundal, janganlah diambil, melainkan harus seorang perawan dari antara orang-orang sebangsanya,'),(3360,3,21,15,'supaya jangan ia melanggar kekudusan keturunannya di antara orang-orang sebangsanya, sebab Akulah TUHAN, yang menguduskan dia.\"'),(3361,3,21,16,'TUHAN berfirman kepada Musa:'),(3362,3,21,17,'Katakanlah kepada Harun, begini: Setiap orang dari antara keturunanmu turun-temurun yang bercacat badannya, janganlah datang mendekat untuk mempersembahkan santapan Allahnya,'),(3363,3,21,18,'karena setiap orang yang bercacat badannya tidak boleh datang mendekat: orang buta, orang timpang, orang yang bercacat mukanya, orang yang terlalu panjang anggotanya,'),(3364,3,21,19,'orang yang patah kakinya atau tangannya,'),(3365,3,21,20,'orang yang berbongkol atau yang kerdil badannya atau yang bular matanya, orang yang berkedal atau berkurap atau yang rusak buah pelirnya.'),(3366,3,21,21,'Setiap orang dari keturunan imam Harun, yang bercacat badannya, janganlah datang untuk mempersembahkan segala korban api-apian TUHAN; karena badannya bercacat janganlah ia datang dekat untuk mempersembahkan santapan Allahnya.'),(3367,3,21,22,'Mengenai santapan Allahnya, baik persembahan-persembahan maha kudus maupun persembahan-persembahan kudus boleh dimakannya.'),(3368,3,21,23,'Hanya janganlah ia datang sampai ke tabir dan janganlah ia datang ke mezbah, karena badannya bercacat, supaya jangan dilanggarnya kekudusan seluruh tempat kudus-Ku, sebab Akulah TUHAN, yang menguduskan mereka.\"'),(3369,3,21,24,'Demikianlah Musa menyampaikan firman itu kepada Harun serta anak-anaknya dan kepada semua orang Israel.'),(3370,3,22,1,'TUHAN berfirman kepada Musa:'),(3371,3,22,2,'Katakanlah kepada Harun dan anak-anaknya, supaya mereka berlaku hati-hati terhadap persembahan-persembahan kudus yang dikuduskan orang Israel bagi-Ku, agar jangan mereka melanggar kekudusan nama-Ku yang kudus; Akulah TUHAN.'),(3372,3,22,3,'Katakanlah kepada mereka: Setiap orang di antara kamu turun-temurun, yakni dari antara segala keturunanmu yang datang mendekat kepada persembahan-persembahan kudus yang dikuduskan orang Israel bagi TUHAN, sedang ia dalam keadaan najis, maka orang itu akan dilenyapkan dari hadapan-Ku; Akulah TUHAN.'),(3373,3,22,4,'Seseorang dari keturunan Harun yang sakit kusta atau yang mengeluarkan lelehan, janganlah memakan persembahan-persembahan kudus, sebelum ia menjadi tahir; dan orang yang kena kepada sesuatu yang najis karena orang mati atau orang yang tertumpah maninya'),(3374,3,22,5,'atau orang yang kena kepada seekor binatang yang merayap yang menajiskan dia atau kepada salah seorang manusia yang menajiskan dia, dengan kenajisan apapun ia menjadi najis,'),(3375,3,22,6,'orang yang kena kepada yang demikian itu menjadi najis sampai matahari terbenam dan janganlah ia makan dari persembahan-persembahan kudus, sebelum ia membasuh tubuhnya dengan air.'),(3376,3,22,7,'Sesudah matahari terbenam, barulah ia menjadi tahir dan sesudah itu bolehlah ia makan dari persembahan-persembahan kudus itu, karena itulah yang menjadi makanannya.'),(3377,3,22,8,'Janganlah ia makan bangkai atau sisa mangsa binatang buas, supaya jangan ia menjadi najis karenanya; Akulah TUHAN.'),(3378,3,22,9,'Dan mereka harus tetap berpegang pada kewajibannya terhadap Aku, supaya dalam hal itu jangan mereka mendatangkan dosa kepada dirinya dan mati oleh karenanya, karena mereka telah melanggar kekudusan kewajiban itu; Akulah TUHAN, yang menguduskan mereka.'),(3379,3,22,10,'Setiap orang awam janganlah memakan persembahan kudus; demikian juga pendatang yang tinggal pada imam ataupun orang upahan.'),(3380,3,22,11,'Tetapi apabila seseorang telah dibeli oleh imam dengan uangnya menjadi budak beliannya, maka orang itu boleh turut memakannya, demikian juga mereka yang lahir di rumahnya.'),(3381,3,22,12,'Apabila anak perempuan imam bersuamikan orang awam, janganlah ia makan persembahan khusus dari persembahan-persembahan kudus.'),(3382,3,22,13,'Tetapi apabila perempuan itu menjadi janda atau diceraikan, dan ia tidak mempunyai anak, dan telah kembali ke rumah ayahnya seperti waktu ia masih gadis, maka ia boleh makan dari makanan ayahnya; tetapi setiap orang awam janganlah memakannya.'),(3383,3,22,14,'Apabila seseorang dengan tidak sengaja memakan persembahan kudus, ia harus memberi gantinya kepada imam dengan menambah seperlima.'),(3384,3,22,15,'Janganlah pada imam melanggar kekudusan persembahan-persembahan kudus orang Israel yang telah dikhususkan bagi TUHAN,'),(3385,3,22,16,'karena dengan demikian mereka mendatangkan kepada orang Israel kesalahan yang harus ditebus, apabila mereka memakan persembahan-persembahan kudus mereka, sebab Akulah TUHAN, yang menguduskan mereka.\"'),(3386,3,22,17,'TUHAN berfirman kepada Musa:'),(3387,3,22,18,'Berbicaralah kepada Harun serta anak-anaknya dan kepada semua orang Israel dan katakan kepada mereka: Siapapun dari umat Israel dan dari orang asing di antara orang Israel yang mempersembahkan persembahannya, baik berupa sesuatu persembahan nazar maupun berupa sesuatu persembahan sukarela, yang hendak dipersembahkan mereka kepada TUHAN sebagai korban bakaran,'),(3388,3,22,19,'maka supaya TUHAN berkenan akan kamu, haruslah persembahan itu tidak bercela dari lembu jantan, domba atau kambing.'),(3389,3,22,20,'Segala yang bercacat badannya janganlah kamu persembahkan, karena dengan itu TUHAN tidak berkenan akan kamu.'),(3390,3,22,21,'Juga apabila seseorang mempersembahkan kepada TUHAN korban keselamatan sebagai pembayar nazar khusus atau sebagai korban sukarela dari lembu atau kambing domba, maka korban itu haruslah yang tidak bercela, supaya TUHAN berkenan akan dia, janganlah badannya bercacat sedikitpun.'),(3391,3,22,22,'Binatang yang buta atau yang patah tulang, yang luka atau yang berbisul, yang berkedal atau yang berkurap, semuanya itu janganlah kamu persembahkan kepada TUHAN dan binatang yang demikian janganlah kamu taruh sebagai korban api-apian bagi TUHAN ke atas mezbah.'),(3392,3,22,23,'Tetapi seekor lembu atau domba yang terlalu panjang atau terlalu pendek anggotanya bolehlah kaupersembahkan sebagai korban sukarela, tetapi sebagai korban nazar TUHAN tidak akan berkenan akan binatang itu.'),(3393,3,22,24,'Tetapi binatang yang buah pelirnya terjepit, ditumbuk, direnggut atau dikerat, janganlah kamu persembahkan kepada TUHAN; janganlah kamu berbuat demikian di negerimu.'),(3394,3,22,25,'Juga dari tangan orang asing janganlah kamu persembahkan sesuatu dari semuanya itu sebagai santapan Allahmu, karena semuanya itu telah rusak dan bercacat badannya; TUHAN tidak akan berkenan akan kamu karena persembahan-persembahan itu.\"'),(3395,3,22,26,'TUHAN berfirman kepada Musa:'),(3396,3,22,27,'Apabila seekor anak lembu atau anak domba atau anak kambing dilahirkan, maka haruslah itu tinggal tujuh hari lamanya dengan induknya, tetapi sejak hari kedelapan dan seterusnya TUHAN berkenan akan binatang itu kalau dipersembahkan berupa korban api-apian bagi-Nya.'),(3397,3,22,28,'Seekor lembu atau kambing atau domba janganlah kamu sembelih bersama dengan anaknya pada satu hari juga.'),(3398,3,22,29,'Dan apabila kamu menyembelih korban syukur bagi TUHAN, kamu harus menyembelihnya sedemikian, hingga TUHAN berkenan akan kamu.'),(3399,3,22,30,'Pada hari itu juga korban itu harus dimakan; janganlah kamu tinggalkan apa-apa dari padanya sampai pagi; Akulah TUHAN.'),(3400,3,22,31,'Dengan demikian kamu harus berpegang pada perintah-Ku dan melakukannya; Akulah TUHAN.'),(3401,3,22,32,'Janganlah melanggar kekudusan nama-Ku yang kudus, supaya Aku dikuduskan di tengah-tengah orang Israel, sebab Akulah TUHAN, yang menguduskan kamu,'),(3402,3,22,33,'yang membawa kamu keluar dari tanah Mesir, supaya Aku menjadi Allahmu; Akulah TUHAN.\"'),(3403,3,23,1,'TUHAN berfirman kepada Musa:'),(3404,3,23,2,'Berbicaralah kepada orang Israel dan katakan kepada mereka: Hari-hari raya yang ditetapkan TUHAN yang harus kamu maklumkan sebagai waktu pertemuan kudus, waktu perayaan yang Kutetapkan, adalah yang berikut.'),(3405,3,23,3,'Enam hari lamanya boleh dilakukan pekerjaan, tetapi pada hari yang ketujuh haruslah ada sabat, hari perhentian penuh, yakni hari pertemuan kudus; janganlah kamu melakukan sesuatu pekerjaan; itulah sabat bagi TUHAN di segala tempat kediamanmu.'),(3406,3,23,4,'Inilah hari-hari raya yang ditetapkan TUHAN, hari-hari pertemuan kudus, yang harus kamu maklumkan masing-masing pada waktunya yang tetap.'),(3407,3,23,5,'Dalam bulan yang pertama, pada tanggal empat belas bulan itu, pada waktu senja, ada Paskah bagi TUHAN.'),(3408,3,23,6,'Dan pada hari yang kelima belas bulan itu ada hari raya Roti Tidak Beragi bagi TUHAN; tujuh hari lamanya kamu harus makan roti yang tidak beragi.'),(3409,3,23,7,'Pada hari yang pertama kamu harus mengadakan pertemuan kudus, janganlah kamu melakukan sesuatu pekerjaan berat.'),(3410,3,23,8,'Kamu harus mempersembahkan korban api-apian kepada TUHAN tujuh hari lamanya; pada hari yang ketujuh haruslah ada pertemuan kudus, janganlah kamu melakukan sesuatu pekerjaan berat.\"'),(3411,3,23,9,'TUHAN berfirman kepada Musa:'),(3412,3,23,10,'Berbicaralah kepada orang Israel dan katakan kepada mereka: Apabila kamu sampai ke negeri yang akan Kuberikan kepadamu, dan kamu menuai hasilnya, maka kamu harus membawa seberkas hasil pertama dari penuaianmu kepada imam,'),(3413,3,23,11,'dan imam itu haruslah mengunjukkan berkas itu di hadapan TUHAN, supaya TUHAN berkenan akan kamu. Imam harus mengunjukkannya pada hari sesudah sabat itu.'),(3414,3,23,12,'Pada hari kamu mengunjukkan berkas itu kamu harus mempersembahkan seekor domba berumur setahun yang tidak bercela, sebagai korban bakaran bagi TUHAN,'),(3415,3,23,13,'serta dengan korban sajiannya dari dua persepuluh efa tepung yang terbaik, diolah dengan minyak, sebagai korban api-apian bagi TUHAN yakni bau yang menyenangkan, serta dengan korban curahannya dari seperempat hin anggur.'),(3416,3,23,14,'Sampai pada hari itu juga janganlah kamu makan roti, atau bertih gandum atau gandum baru, sampai kamu telah membawa persembahan Allahmu; itulah suatu ketetapan untuk selama-lamanya bagi kamu turun-temurun di segala tempat kediamanmu.'),(3417,3,23,15,'Kemudian kamu harus menghitung, mulai dari hari sesudah sabat itu, yaitu waktu kamu membawa berkas persembahan unjukan, harus ada genap tujuh minggu;'),(3418,3,23,16,'sampai pada hari sesudah sabat yang ketujuh kamu harus hitung lima puluh hari; lalu kamu harus mempersembahkan korban sajian yang baru kepada TUHAN.'),(3419,3,23,17,'Dari tempat kediamanmu kamu harus membawa dua buah roti unjukan yang harus dibuat dari dua persepuluh efa tepung yang terbaik dan yang dibakar sesudah dicampur dengan ragi sebagai hulu hasil bagi TUHAN.'),(3420,3,23,18,'Beserta roti itu kamu harus mempersembahkan tujuh ekor domba berumur setahun yang tidak bercela dan seekor lembu jantan muda dan dua ekor domba jantan; semuanya itu haruslah menjadi korban bakaran bagi TUHAN, serta dengan korban sajiannya dan korban-korban curahannya, suatu korban api-apian yang baunya menyenangkan bagi TUHAN.'),(3421,3,23,19,'Kemudian kamu harus mempersembahkan seekor kambing jantan sebagai korban penghapus dosa, dan dua ekor domba yang berumur setahun sebagai korban keselamatan.'),(3422,3,23,20,'Imam harus mengunjukkan semuanya beserta roti hulu hasil itu sebagai persembahan unjukan di hadapan TUHAN, beserta kedua ekor domba itu. Semuanya itu haruslah menjadi persembahan kudus bagi TUHAN dan adalah bagian imam.'),(3423,3,23,21,'Pada hari itu juga kamu harus mengumumkan hari raya dan kamu harus mengadakan pertemuan kudus, janganlah kamu melakukan sesuatu pekerjaan berat; itulah suatu ketetapan untuk selama-lamanya di segala tempat kediamanmu turun-temurun.'),(3424,3,23,22,'Pada waktu kamu menuai hasil tanahmu, janganlah kausabit ladangmu habis-habis sampai ke tepinya dan janganlah kaupungut apa yang ketinggalan dari penuaianmu, semuanya itu harus kautinggalkan bagi orang miskin dan bagi orang asing; Akulah TUHAN, Allahmu.\"'),(3425,3,23,23,'TUHAN berfirman kepada Musa:'),(3426,3,23,24,'Katakanlah kepada orang Israel, begini: Dalam bulan yang ketujuh, pada tanggal satu bulan itu, kamu harus mengadakan hari perhentian penuh yang diperingati dengan meniup serunai, yakni hari pertemuan kudus.'),(3427,3,23,25,'Janganlah kamu melakukan sesuatu pekerjaan berat dan kamu harus mempersembahkan korban api-apian kepada TUHAN.\"'),(3428,3,23,26,'TUHAN berfirman kepada Musa:'),(3429,3,23,27,'Akan tetapi pada tanggal sepuluh bulan yang ketujuh itu ada hari Pendamaian; kamu harus mengadakan pertemuan kudus dan harus merendahkan diri dengan berpuasa dan mempersembahkan korban api-apian kepada TUHAN.'),(3430,3,23,28,'Pada hari itu janganlah kamu melakukan sesuatu pekerjaan; itulah hari Pendamaian untuk mengadakan pendamaian bagimu di hadapan TUHAN, Allahmu.'),(3431,3,23,29,'Karena setiap orang yang pada hari itu tidak merendahkan diri dengan berpuasa, haruslah dilenyapkan dari antara orang-orang sebangsanya.'),(3432,3,23,30,'Setiap orang yang melakukan sesuatu pekerjaan pada hari itu, orang itu akan Kubinasakan dari tengah-tengah bangsanya.'),(3433,3,23,31,'Janganlah kamu melakukan sesuatu pekerjaan; itulah suatu ketetapan untuk selama-lamanya bagimu turun-temurun di segala tempat kediamanmu.'),(3434,3,23,32,'Itu harus menjadi suatu sabat, hari perhentian penuh bagimu, dan kamu harus merendahkan diri dengan berpuasa. Mulai pada malam tanggal sembilan bulan itu, dari matahari terbenam sampai matahari terbenam, kamu harus merayakan sabatmu.\"'),(3435,3,23,33,'TUHAN berfirman kepada Musa:'),(3436,3,23,34,'Katakanlah kepada orang Israel, begini: Pada hari yang kelima belas bulan yang ketujuh itu ada hari raya Pondok Daun bagi TUHAN tujuh hari lamanya.'),(3437,3,23,35,'Pada hari yang pertama haruslah ada pertemuan kudus, janganlah kamu melakukan sesuatu pekerjaan berat.'),(3438,3,23,36,'Tujuh hari lamanya kamu harus mempersembahkan korban api-apian kepada TUHAN, dan pada hari yang kedelapan kamu harus mengadakan pertemuan kudus dan mempersembahkan korban api-apian kepada TUHAN. Itulah hari raya perkumpulan, janganlah kamu melakukan sesuatu pekerjaan berat.'),(3439,3,23,37,'Itulah hari-hari raya yang ditetapkan TUHAN, yang harus kamu maklumkan sebagai hari pertemuan kudus untuk mempersembahkan korban api-apian kepada TUHAN, yaitu korban bakaran dan korban sajian, korban sembelihan dan korban-korban curahan, setiap hari sebanyak yang ditetapkan untuk hari itu,'),(3440,3,23,38,'belum termasuk hari-hari Sabat TUHAN dan belum termasuk persembahan-persembahan atau segala korban nazarmu atau segala korban sukarelamu, yang kamu hendak persembahkan kepada TUHAN.'),(3441,3,23,39,'Akan tetapi pada hari yang kelima belas bulan yang ketujuh itu pada waktu mengumpulkan hasil tanahmu, kamu harus mengadakan perayaan bagi TUHAN tujuh hari lamanya; pada hari yang pertama haruslah ada perhentian penuh dan juga pada hari yang kedelapan harus ada perhentian penuh.'),(3442,3,23,40,'Pada hari yang pertama kamu harus mengambil buah-buah dari pohon-pohon yang elok, pelepah-pelepah pohon-pohon korma, ranting-ranting dari pohon-pohon yang rimbun dan dari pohon-pohon gandarusa dan kamu harus bersukaria di hadapan TUHAN, Allahmu, tujuh hari lamanya.'),(3443,3,23,41,'Kamu harus merayakannya sebagai perayaan bagi TUHAN tujuh hari lamanya dalam setahun; itulah suatu ketetapan untuk selama-lamanya bagimu turun-temurun. Dalam bulan yang ketujuh kamu harus merayakannya.'),(3444,3,23,42,'Di dalam pondok-pondok daun kamu harus tinggal tujuh hari lamanya, setiap orang asli di Israel haruslah tinggal di dalam pondok-pondok daun,'),(3445,3,23,43,'supaya diketahui oleh keturunanmu, bahwa Aku telah menyuruh orang Israel tinggal di dalam pondok-pondok selama Aku menuntun mereka sesudah keluar dari tanah Mesir, Akulah TUHAN, Allahmu.\"'),(3446,3,23,44,'Demikianlah Musa menyampaikan kepada orang Israel firman tentang hari-hari raya yang ditetapkan TUHAN.'),(3447,3,24,1,'TUHAN berfirman kepada Musa:'),(3448,3,24,2,'Perintahkanlah kepada orang Israel, supaya mereka membawa kepadamu minyak zaitun tumbuk yang tulen untuk lampu, supaya lampu dapat dipasang dan tetap menyala.'),(3449,3,24,3,'Harun harus tetap mengatur lampu-lampu itu di depan tabir yang menutupi tabut hukum, di dalam Kemah Pertemuan, dari petang sampai pagi, di hadapan TUHAN. Itulah suatu ketetapan untuk selama-lamanya bagimu turun-temurun.'),(3450,3,24,4,'Di atas kandil dari emas murni haruslah tetap diaturnya lampu-lampu itu di hadapan TUHAN.\"'),(3451,3,24,5,'Engkau harus mengambil tepung yang terbaik dan membakar dua belas roti bundar dari padanya, setiap roti bundar harus dibuat dari dua persepuluh efa;'),(3452,3,24,6,'engkau harus mengaturnya menjadi dua susun, enam buah sesusun, di atas meja dari emas murni itu, di hadapan TUHAN.'),(3453,3,24,7,'Engkau harus membubuh kemenyan tulen di atas tiap-tiap susun; kemenyan itulah yang harus menjadi bagian ingat-ingatan roti itu, yakni suatu korban api-apian bagi TUHAN.'),(3454,3,24,8,'Setiap hari Sabat ia harus tetap mengaturnya di hadapan TUHAN; itulah dari pihak orang Israel suatu kewajiban perjanjian untuk selama-lamanya.'),(3455,3,24,9,'Roti itu teruntuk bagi Harun serta anak-anaknya dan mereka harus memakannya di suatu tempat yang kudus; itulah bagian maha kudus baginya dari segala korban api-apian TUHAN; itulah suatu ketetapan untuk selama-lamanya.\"'),(3456,3,24,10,'Pada suatu hari datanglah seorang laki-laki, ibunya seorang Israel sedang ayahnya seorang Mesir, di tengah-tengah perkemahan orang Israel; dan orang itu berkelahi dengan seorang Israel di perkemahan.'),(3457,3,24,11,'Anak perempuan Israel itu menghujat nama TUHAN dengan mengutuk, lalu dibawalah ia kepada Musa. Nama ibunya ialah Selomit binti Dibri dari suku Dan.'),(3458,3,24,12,'Ia dimasukkan dalam tahanan untuk menantikan keputusan sesuai dengan firman TUHAN.'),(3459,3,24,13,'Lalu berfirmanlah TUHAN kepada Musa:'),(3460,3,24,14,'Bawalah orang yang mengutuk itu ke luar perkemahan dan semua orang yang mendengar haruslah meletakkan tangannya ke atas kepala orang itu, sesudahnya haruslah seluruh jemaah itu melontari dia dengan batu.'),(3461,3,24,15,'Engkau harus mengatakan kepada orang Israel, begini: Setiap orang yang mengutuki Allah harus menanggung kesalahannya sendiri.'),(3462,3,24,16,'Siapa yang menghujat nama TUHAN, pastilah ia dihukum mati dan dilontari dengan batu oleh seluruh jemaah itu. Baik orang asing maupun orang Israel asli, bila ia menghujat nama TUHAN, haruslah dihukum mati.'),(3463,3,24,17,'Juga apabila seseorang membunuh seorang manusia, pastilah ia dihukum mati.'),(3464,3,24,18,'Tetapi siapa yang memukul mati seekor ternak, harus membayar gantinya, seekor ganti seekor.'),(3465,3,24,19,'Apabila seseorang membuat orang sesamanya bercacat, maka seperti yang telah dilakukannya, begitulah harus dilakukan kepadanya:'),(3466,3,24,20,'patah ganti patah, mata ganti mata, gigi ganti gigi; seperti dibuatnya orang lain bercacat, begitulah harus dibuat kepadanya.'),(3467,3,24,21,'Siapa yang memukul mati seekor ternak, ia harus membayar gantinya, tetapi siapa yang membunuh seorang manusia, ia harus dihukum mati.'),(3468,3,24,22,'Satu hukum berlaku bagi kamu, baik bagi orang asing maupun bagi orang Israel asli, sebab Akulah TUHAN, Allahmu.\"'),(3469,3,24,23,'Demikianlah Musa menyampaikan firman itu kepada orang Israel, lalu dibawalah orang yang mengutuk itu ke luar perkemahan, dan dilontarilah dia dengan batu. Maka orang Israel melakukan seperti yang diperintahkan TUHAN kepada Musa.'),(3470,3,25,1,'TUHAN berfirman kepada Musa di gunung Sinai:'),(3471,3,25,2,'Berbicaralah kepada orang Israel dan katakan kepada mereka: Apabila kamu telah masuk ke negeri yang akan Kuberikan kepadamu, maka tanah itu harus mendapat perhentian sebagai sabat bagi TUHAN.'),(3472,3,25,3,'Enam tahun lamanya engkau harus menaburi ladangmu, dan enam tahun lamanya engkau harus merantingi kebun anggurmu dan mengumpulkan hasil tanah itu,'),(3473,3,25,4,'tetapi pada tahun yang ketujuh haruslah ada bagi tanah itu suatu sabat, masa perhentian penuh, suatu sabat bagi TUHAN. Ladangmu janganlah kautaburi dan kebun anggurmu janganlah kaurantingi.'),(3474,3,25,5,'Dan apa yang tumbuh sendiri dari penuaianmu itu, janganlah kautuai dan buah anggur dari pokok anggurmu yang tidak dirantingi, janganlah kaupetik. Tahun itu harus menjadi tahun perhentian penuh bagi tanah itu.'),(3475,3,25,6,'Hasil tanah selama sabat itu haruslah menjadi makanan bagimu, yakni bagimu sendiri, bagi budakmu laki-laki, bagi budakmu perempuan, bagi orang upahan dan bagi orang asing di antaramu, yang semuanya tinggal padamu.'),(3476,3,25,7,'Juga bagi ternakmu, dan bagi binatang liar yang ada di tanahmu, segala hasil tanah itu menjadi makanannya.'),(3477,3,25,8,'Selanjutnya engkau harus menghitung tujuh tahun sabat, yakni tujuh kali tujuh tahun; sehingga masa tujuh tahun sabat itu sama dengan empat puluh sembilan tahun.'),(3478,3,25,9,'Lalu engkau harus memperdengarkan bunyi sangkakala di mana-mana dalam bulan yang ketujuh pada tanggal sepuluh bulan itu; pada hari raya Pendamaian kamu harus memperdengarkan bunyi sangkakala itu di mana-mana di seluruh negerimu.'),(3479,3,25,10,'Kamu harus menguduskan tahun yang kelima puluh, dan memaklumkan kebebasan di negeri itu bagi segenap penduduknya. Itu harus menjadi tahun Yobel bagimu, dan kamu harus masing-masing pulang ke tanah miliknya dan kepada kaumnya.'),(3480,3,25,11,'Tahun yang kelima puluh itu harus menjadi tahun Yobel bagimu, jangan kamu menabur, dan apa yang tumbuh sendiri dalam tahun itu jangan kamu tuai, dan pokok anggur yang tidak dirantingi jangan kamu petik buahnya.'),(3481,3,25,12,'Karena tahun itu adalah tahun Yobel, haruslah itu kudus bagimu; hasil tahun itu yang hendak kamu makan harus diambil dari ladang.'),(3482,3,25,13,'Dalam tahun Yobel itu kamu harus masing-masing pulang ke tanah miliknya.'),(3483,3,25,14,'Apabila kamu menjual sesuatu kepada sesamamu atau membeli dari padanya, janganlah kamu merugikan satu sama lain.'),(3484,3,25,15,'Apabila engkau membeli dari sesamamu haruslah menurut jumlah tahun sesudah tahun Yobel, dan apabila ia menjual kepadamu haruslah menurut jumlah tahun panen.'),(3485,3,25,16,'Makin besar jumlah tahun itu, makin besarlah pembeliannya, dan makin kecil jumlah tahun itu, makin kecillah pembeliannya, karena jumlah panenlah yang dijualnya kepadamu.'),(3486,3,25,17,'Janganlah kamu merugikan satu sama lain, tetapi engkau harus takut akan Allahmu, sebab Akulah TUHAN, Allahmu.'),(3487,3,25,18,'Demikianlah kamu harus melakukan ketetapan-Ku dan tetap berpegang pada peraturan-Ku serta melakukannya, maka kamu akan diam di tanahmu dengan aman tenteram.'),(3488,3,25,19,'Tanah itu akan memberi hasilnya, dan kamu akan makan sampai kenyang dan diam di sana dengan aman tenteram.'),(3489,3,25,20,'Apabila kamu bertanya: Apakah yang akan kami makan dalam tahun yang ketujuh itu, bukankah kami tidak boleh menabur dan tidak boleh mengumpulkan hasil tanah kami?'),(3490,3,25,21,'Maka Aku akan memerintahkan berkat-Ku kepadamu dalam tahun yang keenam, supaya diberinya hasil untuk tiga tahun.'),(3491,3,25,22,'Dalam tahun yang kedelapan kamu akan menabur, tetapi kamu akan makan dari hasil yang lama sampai kepada tahun yang kesembilan, sampai masuk hasilnya, kamu akan memakan yang lama.\"'),(3492,3,25,23,'Tanah jangan dijual mutlak, karena Akulah pemilik tanah itu, sedang kamu adalah orang asing dan pendatang bagi-Ku.'),(3493,3,25,24,'Di seluruh tanah milikmu haruslah kamu memberi hak menebus tanah.'),(3494,3,25,25,'Apabila saudaramu jatuh miskin, sehingga harus menjual sebagian dari miliknya, maka seorang kaumnya yang berhak menebus, yakni kaumnya yang terdekat harus datang dan menebus yang telah dijual saudaranya itu.'),(3495,3,25,26,'Apabila seseorang tidak mempunyai penebus, tetapi kemudian ia mampu, sehingga didapatnya yang perlu untuk menebus miliknya itu,'),(3496,3,25,27,'maka ia harus memasukkan tahun-tahun sesudah penjualannya itu dalam perhitungan, dan kelebihannya haruslah dikembalikannya kepada orang yang membeli dari padanya, supaya ia boleh pulang ke tanah miliknya.'),(3497,3,25,28,'Tetapi jikalau ia tidak mampu untuk mengembalikannya kepadanya, maka yang telah dijualnya itu tetap di tangan orang yang membelinya sampai kepada tahun Yobel; dalam tahun Yobel tanah itu akan bebas, dan orang itu boleh pulang ke tanah miliknya.\"'),(3498,3,25,29,'Apabila seseorang menjual rumah tempat tinggal di suatu kota yang berpagar tembok, maka hak menebus hanya berlaku selama setahun mulai dari hari penjualannya; hak menebus berlaku hanya satu tahun.'),(3499,3,25,30,'Tetapi jikalau rumah itu tidak ditebus dalam jangka waktu setahun itu, rumah itu secara mutlak menjadi milik si pembeli turun temurun; dalam tahun Yobel rumah itu tidaklah bebas.'),(3500,3,25,31,'Tetapi rumah-rumah di desa-desa yang tidak dikelilingi pagar tembok haruslah dianggap sama dengan ladang-ladang di negeri itu, atasnya harus ada hak menebus dan dalam tahun Yobel rumah itu harus bebas.'),(3501,3,25,32,'Mengenai rumah-rumah di kota-kota orang Lewi, hak menebus rumah-rumah itu ada pada orang-orang Lewi untuk selama-lamanya.'),(3502,3,25,33,'Sekalipun dari antara orang Lewi yang melakukan penebusan, tetapi rumah yang terjual di kota miliknya itu haruslah bebas dalam tahun Yobel, karena segala rumah di kota-kota orang Lewi adalah milik mereka masing-masing di tengah-tengah orang Israel.'),(3503,3,25,34,'Dan padang penggembalaan sekitar kota-kota mereka janganlah dijual, karena itu milik mereka untuk selama-lamanya.\"'),(3504,3,25,35,'Apabila saudaramu jatuh miskin, sehingga tidak sanggup bertahan di antaramu, maka engkau harus menyokong dia sebagai orang asing dan pendatang, supaya ia dapat hidup di antaramu.'),(3505,3,25,36,'Janganlah engkau mengambil bunga uang atau riba dari padanya, melainkan engkau harus takut akan Allahmu, supaya saudaramu dapat hidup di antaramu.'),(3506,3,25,37,'Janganlah engkau memberi uangmu kepadanya dengan meminta bunga, juga makananmu janganlah kauberikan dengan meminta riba.'),(3507,3,25,38,'Akulah TUHAN, Allahmu, yang membawa kamu keluar dari tanah Mesir, untuk memberikan kepadamu tanah Kanaan, supaya Aku menjadi Allahmu.'),(3508,3,25,39,'Apabila saudaramu jatuh miskin di antaramu, sehingga menyerahkan dirinya kepadamu, maka janganlah memperbudak dia.'),(3509,3,25,40,'Sebagai orang upahan dan sebagai pendatang ia harus tinggal di antaramu; sampai kepada tahun Yobel ia harus bekerja padamu.'),(3510,3,25,41,'Kemudian ia harus diizinkan keluar dari padamu, ia bersama-sama anak-anaknya, lalu pulang kembali kepada kaumnya dan ia boleh pulang ke tanah milik nenek moyangnya.'),(3511,3,25,42,'Karena mereka itu hamba-hamba-Ku yang Kubawa keluar dari tanah Mesir, janganlah mereka itu dijual, secara orang menjual budak.'),(3512,3,25,43,'Janganlah engkau memerintah dia dengan kejam, melainkan engkau harus takut akan Allahmu.'),(3513,3,25,44,'Tetapi budakmu laki-laki atau perempuan yang boleh kaumiliki adalah dari antara bangsa-bangsa yang di sekelilingmu; hanya dari antara merekalah kamu boleh membeli budak laki-laki dan perempuan.'),(3514,3,25,45,'Juga dari antara anak-anak pendatang yang tinggal di antaramu boleh kamu membelinya dan dari antara kaum mereka yang tinggal di antaramu, yang dilahirkan di negerimu. Orang-orang itu boleh menjadi milikmu.'),(3515,3,25,46,'Kamu harus membagikan mereka sebagai milik pusaka kepada anak-anakmu yang kemudian, supaya diwarisi sebagai milik; kamu harus memperbudakkan mereka untuk selama-lamanya, tetapi atas saudara-saudaramu orang-orang Israel, janganlah memerintah dengan kejam yang satu sama yang lain.'),(3516,3,25,47,'Apabila seorang asing atau seorang pendatang di antaramu telah menjadi mampu, sedangkan saudaramu yang tinggal padanya jatuh miskin, sehingga menyerahkan dirinya kepada orang asing atau pendatang yang di antaramu itu atau kepada seorang yang berasal dari kaum orang asing,'),(3517,3,25,48,'maka sesudah ia menyerahkan dirinya, ia berhak ditebus, yakni seorang dari antara saudara-saudaranya boleh menebus dia,'),(3518,3,25,49,'atau saudara ayahnya atau anak laki-laki saudara ayahnya atau seorang kerabatnya yang terdekat dari kaumnya atau kalau ia telah mampu, ia sendiri berhak menebus dirinya.'),(3519,3,25,50,'Bersama-sama dengan si pembelinya ia harus membuat perhitungan, mulai dari tahun ia menyerahkan dirinya kepada orang itu sampai kepada tahun Yobel, dan harga penjualan dirinya haruslah ditentukan menurut jumlah tahun-tahun itu; masa ia tinggal pada orang itu haruslah dihitung seperti masa kerja orang upahan.'),(3520,3,25,51,'Jikalau jumlah tahun itu masih besar, maka dari harga pembeliannya harus dikembalikan sebagai penebus dirinya menurut jumlah tahun itu.'),(3521,3,25,52,'Jika waktu yang masih tinggal sampai kepada tahun Yobel sedikit lagi saja, maka ia harus membuat perhitungan dengan orang itu; menurut jumlah tahun itulah ia harus membayar uang tebusan dirinya.'),(3522,3,25,53,'Demikianlah ia harus tinggal padanya sebagai orang upahan dari tahun ke tahun. Janganlah ia diperintah dengan kejam oleh orang itu di depan matamu.'),(3523,3,25,54,'Tetapi jikalau ia tidak ditebus dengan cara demikian, maka ia harus diizinkan keluar dalam tahun Yobel, ia bersama-sama anak-anaknya.'),(3524,3,25,55,'Karena pada-Kulah orang Israel menjadi hamba; mereka itu adalah hamba-hamba-Ku yang Kubawa keluar dari tanah Mesir; Akulah TUHAN, Allahmu.\"'),(3525,3,26,1,'Janganlah kamu membuat berhala bagimu, dan patung atau tugu berhala janganlah kamu dirikan bagimu; juga batu berukir janganlah kamu tempatkan di negerimu untuk sujud menyembah kepadanya, sebab Akulah TUHAN, Allahmu.'),(3526,3,26,2,'Kamu harus memelihara hari-hari Sabat-Ku dan menghormati tempat kudus-Ku, Akulah TUHAN.'),(3527,3,26,3,'Jikalau kamu hidup menurut ketetapan-Ku dan tetap berpegang pada perintah-Ku serta melakukannya,'),(3528,3,26,4,'maka Aku akan memberi kamu hujan pada masanya, sehingga tanah itu memberi hasilnya dan pohon-pohonan di ladangmu akan memberi buahnya.'),(3529,3,26,5,'Lamanya musim mengirik bagimu akan sampai kepada musim memetik buah anggur dan lamanya musim memetik buah anggur akan sampai kepada musim menabur. Kamu akan makan makananmu sampai kenyang dan diam di negerimu dengan aman tenteram.'),(3530,3,26,6,'Dan Aku akan memberi damai sejahtera di dalam negeri itu, sehingga kamu akan berbaring dengan tidak dikejutkan oleh apapun; Aku akan melenyapkan binatang buas dari negeri itu, dan pedang tidak akan melintas di negerimu.'),(3531,3,26,7,'Kamu akan mengejar musuhmu, dan mereka akan tewas di hadapanmu oleh pedang.'),(3532,3,26,8,'Lima orang dari antaramu akan mengejar seratus, dan seratus orang dari antaramu akan mengejar selaksa dan semua musuhmu akan tewas di hadapanmu oleh pedang.'),(3533,3,26,9,'Dan Aku akan berpaling kepadamu dan akan membuat kamu beranak cucu serta bertambah banyak dan Aku akan meneguhkan perjanjian-Ku dengan kamu.'),(3534,3,26,10,'Kamu masih akan makan hasil lama dari panen yang lampau, dan hasil lama itu akan kamu keluarkan untuk menyimpan yang baru.'),(3535,3,26,11,'Aku akan menempatkan Kemah Suci-Ku di tengah-tengahmu dan hati-Ku tidak akan muak melihat kamu.'),(3536,3,26,12,'Tetapi Aku akan hadir di tengah-tengahmu dan Aku akan menjadi Allahmu dan kamu akan menjadi umat-Ku.'),(3537,3,26,13,'Akulah TUHAN, Allahmu, yang membawa kamu keluar dari tanah Mesir, supaya kamu jangan lagi menjadi budak mereka. Aku telah mematahkan kayu kuk yang di atasmu dan membuat kamu berjalan tegak.\"'),(3538,3,26,14,'Tetapi jikalau kamu tidak mendengarkan Daku, dan tidak melakukan segala perintah itu,'),(3539,3,26,15,'jikalau kamu menolak ketetapan-Ku dan hatimu muak mendengar peraturan-Ku, sehingga kamu tidak melakukan segala perintah-Ku dan kamu mengingkari perjanjian-Ku,'),(3540,3,26,16,'maka Akupun akan berbuat begini kepadamu, yakni Aku akan mendatangkan kekejutan atasmu, batuk kering serta demam, yang membuat mata rusak dan jiwa merana; kamu akan sia-sia menabur benihmu, karena hasilnya akan habis dimakan musuhmu.'),(3541,3,26,17,'Aku sendiri akan menentang kamu, sehingga kamu akan dikalahkan oleh musuhmu, dan mereka yang membenci kamu akan menguasai kamu, dan kamu akan lari, sungguhpun tidak ada orang mengejar kamu.'),(3542,3,26,18,'Dan jikalau kamu dalam keadaan yang demikianpun tidak mendengarkan Daku, maka Aku akan lebih keras menghajar kamu sampai tujuh kali lipat karena dosamu,'),(3543,3,26,19,'dan Aku akan mematahkan kekuasaanmu yang kaubanggakan dan akan membuat langit di atasmu sebagai besi dan tanahmu sebagai tembaga.'),(3544,3,26,20,'Maka tenagamu akan habis dengan sia-sia, tanahmu tidak akan memberi hasilnya dan pohon-pohonan di tanah itu tidak akan memberi buahnya.'),(3545,3,26,21,'Jikalau hidupmu tetap bertentangan dengan Daku dan kamu tidak mau mendengarkan Daku, maka Aku akan makin menambah hukuman atasmu sampai tujuh kali lipat setimpal dengan dosamu.'),(3546,3,26,22,'Aku akan melepaskan kepadamu binatang liar yang akan memunahkan anak-anakmu dan yang akan melenyapkan ternakmu, serta membuat kamu menjadi sedikit, sehingga jalan-jalanmu menjadi sunyi.'),(3547,3,26,23,'Jikalau kamu dalam keadaan yang demikianpun tidak mau Kuajar, dan hidupmu tetap bertentangan dengan Daku,'),(3548,3,26,24,'maka Akupun akan bertindak melawan kamu dan Aku sendiri akan menghukum kamu tujuh kali lipat karena dosamu,'),(3549,3,26,25,'dan Aku akan mendatangkan ke atasmu suatu pedang, yang akan melakukan pembalasan oleh karena perjanjian itu; bila kamu berkumpul kelak di kota-kotamu, maka Aku akan melepas penyakit sampar ke tengah-tengahmu dan kamu akan diserahkan ke dalam tangan musuh.'),(3550,3,26,26,'Jika Aku memusnahkan persediaan makananmu, maka sepuluh perempuan akan membakar roti di dalam satu pembakaran. Mereka akan mengembalikan rotimu menurut timbangan tertentu, dan kamu akan makan, tetapi tidak menjadi kenyang.'),(3551,3,26,27,'Dan jikalau kamu dalam keadaan yang demikianpun tidak mendengarkan Daku, dan hidupmu tetap bertentangan dengan Daku,'),(3552,3,26,28,'maka Akupun akan bertindak keras melawan kamu dan Aku sendiri akan menghajar kamu tujuh kali lipat karena dosamu,'),(3553,3,26,29,'dan kamu akan memakan daging anak-anakmu lelaki dan anak-anakmu perempuan.'),(3554,3,26,30,'Dan bukit-bukit pengorbananmu akan Kupunahkan, dan segala pedupaanmu akan Kulenyapkan. Aku akan melemparkan bangkai-bangkaimu ke atas bangkai-bangkai berhalamu dan hati-Ku akan muak melihat kamu.'),(3555,3,26,31,'Kota-kotamu akan Kubuat menjadi reruntuhan dan tempat-tempat kudusmu akan Kurusakkan dan Aku tidak mau lagi menghirup bau persembahanmu yang menyenangkan.'),(3556,3,26,32,'Aku sendiri akan merusakkan negeri itu, sehingga musuhmu yang tinggal di situ akan tercengang karenanya.'),(3557,3,26,33,'Tetapi kamu akan Kuserakkan di antara bangsa-bangsa lain dan Aku akan menghunus pedang di belakang kamu, dan tanahmu akan menjadi tempat tandus dan kota-kotamu akan menjadi reruntuhan.'),(3558,3,26,34,'Pada waktu itulah tanah itu pulih dari dilalaikannya tahun-tahun sabatnya selama tanah itu tandus dan selama kamu tinggal di negeri musuh-musuhmu; pada waktu itulah tanah itu akan menjalani sabatnya dan dipulihkan tahun-tahun sabat yang belum didapatnya.'),(3559,3,26,35,'Selama ketandusannya tanah itu akan menjalani sabat yang belum dijalaninya pada tiap-tiap tahun sabatmu, ketika kamu masih diam di situ.'),(3560,3,26,36,'Dan mengenai mereka yang masih tinggal hidup dari antaramu, Aku akan mendatangkan kecemasan ke dalam hati mereka di dalam negeri-negeri musuh mereka, sehingga bunyi daun yang ditiupkan anginpun akan mengejar mereka, dan mereka akan lari seperti orang lari menjauhi pedang, dan mereka akan rebah, sungguhpun tidak ada orang yang mengejar.'),(3561,3,26,37,'Dan mereka akan jatuh tersandung seorang kepada seorang seolah-olah hendak menjauhi pedang, sungguhpun yang mengejar tidak ada, dan kamu tidak akan dapat bertahan di hadapan musuh-musuhmu.'),(3562,3,26,38,'Dan kamu akan binasa di antara bangsa-bangsa lain, dan negeri musuhmu akan memusnahkan kamu.'),(3563,3,26,39,'Dan siapa yang masih tinggal hidup dari antaramu, mereka akan hancur lebur dalam hukumannya di negeri-negeri musuh mereka, dan karena kesalahan nenek moyang mereka juga mereka akan hancur lebur sama seperti nenek moyangnya.'),(3564,3,26,40,'Tetapi bila mereka mengakui kesalahan mereka dan kesalahan nenek moyang mereka dalam hal berubah setia yang dilakukan mereka terhadap Aku dan mengakui juga bahwa hidup mereka bertentangan dengan Daku'),(3565,3,26,41,'--Akupun bertindak melawan mereka dan membawa mereka ke negeri musuh mereka--atau bila kemudian hati mereka yang tidak bersunat itu telah tunduk dan mereka telah membayar pulih kesalahan mereka,'),(3566,3,26,42,'maka Aku akan mengingat perjanjian-Ku dengan Yakub; juga perjanjian dengan Ishak dan perjanjian-Ku dengan Abrahampun akan Kuingat dan negeri itu akan Kuingat juga.'),(3567,3,26,43,'Jadi tanah itu akan ditinggalkan mereka dan akan pulih dari akibat tahun-tahun sabat yang dilalaikan selama tanah itu tandus, oleh karena ditinggalkan mereka, dan mereka akan membayar pulih kesalahan mereka, tak lain dan tak bukan karena mereka menolak peraturan-Ku dan hati mereka muak mendengarkan ketetapan-Ku.'),(3568,3,26,44,'Namun demikian, apabila mereka ada di negeri musuh mereka, Aku tidak akan menolak mereka dan tidak akan muak melihat mereka, sehingga Aku membinasakan mereka dan membatalkan perjanjian-Ku dengan mereka, sebab Akulah TUHAN, Allah mereka.'),(3569,3,26,45,'Untuk keselamatan mereka Aku akan mengingat perjanjian dengan orang-orang dahulu yang Kubawa keluar dari tanah Mesir di depan mata bangsa-bangsa lain, supaya Aku menjadi Allah mereka; Akulah TUHAN.\"'),(3570,3,26,46,'Itulah ketetapan-ketetapan dan peraturan-peraturan serta hukum-hukum yang diberikan TUHAN, berlaku di antara Dia dengan orang Israel, di gunung Sinai, dengan perantaraan Musa.'),(3571,3,27,1,'TUHAN berfirman kepada Musa:'),(3572,3,27,2,'Berbicaralah kepada orang Israel dan katakan kepada mereka: Apabila seorang mengucapkan nazar khusus kepada TUHAN mengenai orang menurut penilaian yang berlaku untuk itu,'),(3573,3,27,3,'maka tentang nilai bagi orang laki-laki dari yang berumur dua puluh tahun sampai yang berumur enam puluh tahun, nilai itu harus lima puluh syikal perak, ditimbang menurut syikal kudus.'),(3574,3,27,4,'Tetapi jikalau itu seorang perempuan, maka nilai itu harus tiga puluh syikal.'),(3575,3,27,5,'Jikalau itu mengenai seorang dari yang berumur lima tahun sampai yang berumur dua puluh tahun, maka bagi laki-laki nilai itu harus dua puluh syikal dan bagi perempuan sepuluh syikal.'),(3576,3,27,6,'Jikalau itu mengenai seorang dari yang berumur satu bulan sampai yang berumur lima tahun, maka bagi laki-laki nilai itu harus lima syikal perak, dan bagi perempuan tiga syikal perak.'),(3577,3,27,7,'Jikalau itu mengenai seorang yang berumur enam puluh tahun atau lebih, jikalau itu mengenai laki-laki, maka nilai itu harus lima belas syikal dan bagi perempuan sepuluh syikal.'),(3578,3,27,8,'Tetapi jikalau orang itu terlalu miskin untuk membayar nilai itu, maka haruslah dihadapkannya orang yang dinazarkannya itu kepada imam, dan imam harus menilainya; sesuai dengan kemampuan orang yang bernazar itu imam harus menentukan nilainya.'),(3579,3,27,9,'Jikalau itu termasuk hewan yang boleh dipersembahkan sebagai persembahan kepada TUHAN, maka apapun dari pada hewan itu yang dipersembahkan orang itu kepada TUHAN haruslah kudus.'),(3580,3,27,10,'Janganlah ia menggantinya dan janganlah ia menukarnya, yang baik dengan yang buruk atau yang buruk dengan yang baik. Tetapi jikalau ia menukar juga seekor hewan dengan seekor hewan lain, maka baik hewan itu maupun tukarnya haruslah kudus.'),(3581,3,27,11,'Jikalau itu barang seekor dari antara hewan haram yang tidak boleh dipersembahkan sebagai persembahan kepada TUHAN, maka hewan itu harus dihadapkannya kepada imam,'),(3582,3,27,12,'dan imam harus menetapkan nilainya menurut baik atau buruknya, dan seperti penilaian imam demikianlah jadinya.'),(3583,3,27,13,'Dan jikalau orang itu mau menebusnya juga, ia harus menambahkan seperlima kepada nilai itu.'),(3584,3,27,14,'Apabila seorang menguduskan rumahnya sebagai persembahan kudus bagi TUHAN, maka imam harus menetapkan nilainya menurut baik atau buruknya, dan seperti nilai yang ditetapkan imam demikianlah harus dipegang teguh.'),(3585,3,27,15,'Tetapi jikalau orang yang menguduskan itu mau menebus rumahnya, maka ia harus menambah harganya dengan seperlima dari uang nilainya dan rumah itu menjadi kepunyaannya pula.'),(3586,3,27,16,'Jikalau seseorang menguduskan sebagian dari ladang miliknya bagi TUHAN, maka nilainya haruslah sesuai dengan taburannya, yakni sehomer taburan benih jelai berharga lima puluh syikal perak.'),(3587,3,27,17,'Jikalau ia menguduskan ladangnya mulai dari tahun Yobel, maka nilainya haruslah dipegang teguh.'),(3588,3,27,18,'Tetapi jikalau ia menguduskan ladangnya sesudah tahun Yobel, maka imam harus menghitung harganya bagi orang itu sesuai dengan tahun-tahun yang masih tinggal sampai kepada tahun Yobel, dan harga itu harus dikurangkan dari nilainya.'),(3589,3,27,19,'Dan jikalau orang yang menguduskannya benar-benar mau menebus ladang itu, maka ia harus menambah harganya dengan seperlima dari uang nilainya dan ladang itu tetap dimilikinya.'),(3590,3,27,20,'Tetapi jikalau ia tidak menebus ladang itu, malahan ladang itu telah dijualnya kepada orang lain, maka tidak dapat ditebus lagi.'),(3591,3,27,21,'Tetapi pada waktu bebas dalam tahun Yobel, ladang itu haruslah kudus bagi TUHAN, sama seperti ladang yang dikhususkan bagi TUHAN. Imamlah yang harus memilikinya.'),(3592,3,27,22,'Dan jikalau ia menguduskan bagi TUHAN ladang yang telah dibelinya dan yang tidak termasuk ladang miliknya dahulu,'),(3593,3,27,23,'maka imam harus menghitung baginya harga nilainya sampai kepada tahun Yobel dan orang itu haruslah mempersembahkan nilai itu pada hari itu juga sebagai persembahan kudus bagi TUHAN.'),(3594,3,27,24,'Dalam tahun Yobel ladang itu harus dipulangkan kepada orang yang menjualnya kepadanya, yakni kepada orang yang mula-mula memiliki tanah itu.'),(3595,3,27,25,'Dan segala nilai harus menurut syikal kudus, syikal itu harus dua puluh gera beratnya.'),(3596,3,27,26,'Akan tetapi anak sulung, yang sebagai anak sulung menjadi hak TUHAN dari antara hewan, tidak boleh dikuduskan oleh siapapun, baik seekor lembu maupun seekor kambing atau domba, itu milik TUHAN.'),(3597,3,27,27,'Tetapi jikalau itu dari antara hewan yang haram, maka haruslah orang menebusnya menurut nilainya dengan menambah seperlima dan jikalau tidak ditebus, haruslah dijual menurut nilainya.'),(3598,3,27,28,'Akan tetapi segala yang sudah dikhususkan oleh seseorang bagi TUHAN dari segala miliknya, baik manusia atau hewan, maupun ladang miliknya, tidak boleh dijual dan tidak boleh ditebus, karena segala yang dikhususkan adalah maha kudus bagi TUHAN.'),(3599,3,27,29,'Setiap orang yang dikhususkan, yang harus ditumpas di antara manusia, tidak boleh ditebus, pastilah ia dihukum mati.'),(3600,3,27,30,'Demikian juga segala persembahan persepuluhan dari tanah, baik dari hasil benih di tanah maupun dari buah pohon-pohonan, adalah milik TUHAN; itulah persembahan kudus bagi TUHAN.'),(3601,3,27,31,'Tetapi jikalau seseorang mau menebus juga sebagian dari persembahan persepuluhannya itu, maka ia harus menambah seperlima.'),(3602,3,27,32,'Mengenai segala persembahan persepuluhan dari lembu sapi atau kambing domba, maka dari segala yang lewat dari bawah tongkat gembala waktu dihitung, setiap yang kesepuluh harus menjadi persembahan kudus bagi TUHAN.'),(3603,3,27,33,'Janganlah dipilih-pilih mana yang baik dan mana yang buruk, dan janganlah ditukar; jikalau orang menukarnya juga, maka baik hewan itu maupun tukarnya haruslah kudus dan tidak boleh ditebus.\"'),(3604,3,27,34,'Itulah perintah-perintah yang diperintahkan TUHAN kepada Musa di gunung Sinai untuk disampaikan kepada orang Israel.'),(3605,3,1,1,'TUHAN berfirman kepada Musa di padang gurun Sinai, dalam Kemah Pertemuan, pada tanggal satu bulan yang kedua dalam tahun yang kedua sesudah mereka keluar dari tanah Mesir:'),(3606,3,1,2,'Hitunglah jumlah segenap umat Israel menurut kaum-kaum yang ada dalam setiap suku mereka, dan catatlah nama semua laki-laki di Israel'),(3607,3,1,3,'yang berumur dua puluh tahun ke atas dan yang sanggup berperang, orang demi orang. Engkau ini beserta Harun harus mencatat mereka menurut pasukannya masing-masing.'),(3608,3,1,4,'Dari tiap-tiap suku harus ada satu orang yang mendampingi kamu, yakni orang yang menjadi kepala dari suku yang diwakilinya itu.'),(3609,3,1,5,'Dan inilah nama semua orang yang harus mendampingi kamu. Dari suku Ruben: Elizur bin Syedeur;'),(3610,3,1,6,'dari suku Simeon: Selumiel bin Zurisyadai;'),(3611,3,1,7,'dari suku Yehuda: Nahason bin Aminadab;'),(3612,3,1,8,'dari suku Isakhar: Netaneel bin Zuar;'),(3613,3,1,9,'dari suku Zebulon: Eliab bin Helon;'),(3614,3,1,10,'dari keturunan Yusuf: Elisama bin Amihud dari suku Efraim, dan Gamaliel bin Pedazur dari suku Manasye;'),(3615,3,1,11,'dari suku Benyamin: Abidan bin Gideoni;'),(3616,3,1,12,'dari suku Dan: Ahiezer bin Amisyadai;'),(3617,3,1,13,'dari suku Asyer: Pagiel bin Okhran;'),(3618,3,1,14,'dari suku Gad: Elyasaf bin Rehuel;'),(3619,3,1,15,'dari suku Naftali: Ahira bin Enan.\"'),(3620,3,1,16,'Itulah orang-orang yang dipilih dari umat itu, masing-masing sebagai pemimpin dari suku bapa leluhurnya; mereka inilah kepala-kepala pasukan Israel.'),(3621,3,1,17,'Lalu Musa dan Harun memanggil orang-orang yang tertunjuk namanya itu,'),(3622,3,1,18,'dan pada tanggal satu bulan yang kedua mereka menyuruh segenap umat berkumpul. Kemudian silsilah orang-orang Israel disusun menurut kaum-kaum yang ada dalam setiap suku mereka, sedang nama-nama mereka yang berumur dua puluh tahun ke atas dicatat orang demi orang,'),(3623,3,1,19,'seperti yang diperintahkan TUHAN kepada Musa. Demikianlah Musa mencatat mereka di padang gurun Sinai.'),(3624,3,1,20,'Ketika silsilah bani Ruben, yaitu keturunan anak sulung Israel, disusun menurut kaum-kaum yang ada dalam suku mereka, maka dicatatlah nama semua laki-laki yang berumur dua puluh tahun ke atas, semua orang yang sanggup berperang, orang demi orang.'),(3625,3,1,21,'Jumlah yang dicatat dari suku Ruben ada empat puluh enam ribu lima ratus orang.'),(3626,3,1,22,'Ketika silsilah bani Simeon disusun menurut kaum-kaum yang ada dalam suku mereka, maka dicatatlah nama semua laki-laki yang berumur dua puluh tahun ke atas, semua orang yang sanggup berperang, orang demi orang.'),(3627,3,1,23,'Jumlah yang dicatat dari suku Simeon ada lima puluh sembilan ribu tiga ratus orang.'),(3628,3,1,24,'Ketika silsilah bani Gad disusun menurut kaum-kaum yang ada dalam suku mereka, maka dicatatlah nama orang-orang yang berumur dua puluh tahun ke atas, semua orang yang sanggup berperang.'),(3629,3,1,25,'Jumlah yang dicatat dari suku Gad ada empat puluh lima ribu enam ratus lima puluh orang.'),(3630,3,1,26,'Ketika silsilah bani Yehuda disusun menurut kaum-kaum yang ada dalam suku mereka, maka dicatatlah nama orang-orang yang berumur dua puluh tahun ke atas, semua orang yang sanggup berperang.'),(3631,3,1,27,'Jumlah yang dicatat dari suku Yehuda ada tujuh puluh empat ribu enam ratus orang.'),(3632,3,1,28,'Ketika silsilah bani Isakhar disusun menurut kaum-kaum yang ada dalam suku mereka, maka dicatatlah nama orang-orang yang berumur dua puluh tahun ke atas, semua orang yang sanggup berperang.'),(3633,3,1,29,'Jumlah yang dicatat dari suku Isakhar ada lima puluh empat ribu empat ratus orang.'),(3634,3,1,30,'Ketika silsilah bani Zebulon disusun menurut kaum-kaum yang ada dalam suku mereka, maka dicatatlah nama orang-orang yang berumur dua puluh tahun ke atas, semua orang yang sanggup berperang.'),(3635,3,1,31,'Jumlah yang dicatat dari suku Zebulon ada lima puluh tujuh ribu empat ratus orang.'),(3636,3,1,32,'Mengenai keturunan Yusuf: Ketika silsilah bani Efraim disusun menurut kaum-kaum yang ada dalam suku mereka, maka dicatatlah nama orang-orang yang berumur dua puluh tahun ke atas, semua orang yang sanggup berperang.'),(3637,3,1,33,'Jumlah yang dicatat dari suku Efraim ada empat puluh ribu lima ratus orang.'),(3638,3,1,34,'Dan ketika silsilah bani Manasye disusun menurut kaum-kaum yang ada dalam suku mereka, maka dicatatlah nama orang-orang yang berumur dua puluh tahun ke atas, semua orang yang sanggup berperang.'),(3639,3,1,35,'Jumlah yang dicatat dari suku Manasye ada tiga puluh dua ribu dua ratus orang.'),(3640,3,1,36,'Ketika silsilah bani Benyamin disusun menurut kaum-kaum yang ada dalam suku mereka, maka dicatatlah nama orang-orang yang berumur dua puluh tahun ke atas, semua orang yang sanggup berperang.'),(3641,3,1,37,'Jumlah yang dicatat dari suku Benyamin ada tiga puluh lima ribu empat ratus orang.'),(3642,3,1,38,'Ketika silsilah bani Dan disusun menurut kaum-kaum yang ada dalam suku mereka, maka dicatatlah nama orang-orang yang berumur dua puluh tahun ke atas, semua orang yang sanggup berperang.'),(3643,3,1,39,'Jumlah yang dicatat dari suku Dan ada enam puluh dua ribu tujuh ratus orang.'),(3644,3,1,40,'Ketika silsilah bani Asyer disusun menurut kaum-kaum yang ada dalam suku mereka, maka dicatatlah nama orang-orang yang berumur dua puluh tahun ke atas, semua orang yang sanggup berperang.'),(3645,3,1,41,'Jumlah yang dicatat dari suku Asyer ada empat puluh satu ribu lima ratus orang.'),(3646,3,1,42,'Ketika silsilah bani Naftali disusun menurut kaum-kaum yang ada dalam suku mereka, maka dicatatlah nama orang-orang yang berumur dua puluh tahun ke atas, semua orang yang sanggup berperang.'),(3647,3,1,43,'Jumlah yang dicatat dari suku Naftali ada lima puluh tiga ribu empat ratus orang.'),(3648,3,1,44,'Itulah jumlah orang-orang yang dicatat oleh Musa dengan Harun dan dengan kedua belas pemimpin Israel yang masing-masing mewakili sukunya.'),(3649,3,1,45,'Jadi semua orang Israel yang dicatat menurut suku-suku mereka, yaitu orang-orang yang berumur dua puluh tahun ke atas dan yang sanggup berperang di antara orang Israel,'),(3650,3,1,46,'berjumlah enam ratus tiga ribu lima ratus lima puluh orang.'),(3651,3,1,47,'Tetapi mereka yang menurut suku bapa leluhurnya termasuk orang Lewi, tidak turut dicatat bersama-sama dengan mereka itu.'),(3652,3,1,48,'Sebab TUHAN telah berfirman kepada Musa:'),(3653,3,1,49,'Hanya suku Lewi janganlah kaucatat dan janganlah kauhitung jumlahnya bersama-sama dengan orang Israel,'),(3654,3,1,50,'tetapi tugaskanlah mereka untuk mengawasi Kemah Suci, tempat hukum Allah dengan segala perabotan dan perlengkapannya; mereka harus mengangkat Kemah Suci dengan segala perabotannya; mereka harus mengurusnya dan harus berkemah di sekelilingnya.'),(3655,3,1,51,'Apabila berangkat, Kemah Suci harus dibongkar oleh orang Lewi, dan apabila berkemah, Kemah Suci harus dipasang oleh mereka; sedang orang awam yang mendekat harus dihukum mati.'),(3656,3,1,52,'Orang Israel haruslah berkemah masing-masing di tempat perkemahannya dan masing-masing dekat panji-panjinya, menurut pasukan mereka,'),(3657,3,1,53,'tetapi orang Lewi haruslah berkemah di sekeliling Kemah Suci, tempat hukum Allah supaya umat Israel jangan kena murka; orang Lewi haruslah memelihara Kemah Suci, tempat hukum itu.\"'),(3658,3,1,54,'Maka orang Israel berbuat demikian; tepat seperti yang diperintahkan TUHAN kepada Musa, demikianlah diperbuat mereka.'),(3659,3,2,1,'TUHAN berfirman kepada Musa dan Harun:'),(3660,3,2,2,'Orang Israel harus berkemah masing-masing dekat panji-panjinya, menurut lambang suku-sukunya. Mereka harus berkemah di sekeliling Kemah Pertemuan, agak jauh dari padanya.'),(3661,3,2,3,'Yang berkemah di sebelah timur dekat panji-panjinya, ialah laskar Yehuda, menurut pasukan-pasukan mereka. Pemimpin bani Yehuda ialah Nahason bin Aminadab.'),(3662,3,2,4,'Pasukannya terdiri dari tujuh puluh empat ribu enam ratus orang yang dicatat.'),(3663,3,2,5,'Yang berkemah di dekatnya ialah suku Isakhar. Pemimpin bani Isakhar ialah Netaneel bin Zuar.'),(3664,3,2,6,'Pasukannya terdiri dari lima puluh empat ribu empat ratus orang yang dicatat.'),(3665,3,2,7,'Kemudian suku Zebulon. Pemimpin bani Zebulon ialah Eliab bin Helon.'),(3666,3,2,8,'Pasukannya terdiri dari lima puluh tujuh ribu empat ratus orang yang dicatat.'),(3667,3,2,9,'Jumlah orang yang dicatat dalam laskar Yehuda menurut pasukan-pasukan mereka ada seratus delapan puluh enam ribu empat ratus orang. Merekalah yang terdahulu berangkat.'),(3668,3,2,10,'Panji-panji laskar Ruben adalah di sebelah selatan, menurut pasukan-pasukan mereka. Pemimpin bani Ruben ialah Elizur bin Syedeur.'),(3669,3,2,11,'Pasukannya terdiri dari empat puluh enam ribu lima ratus orang yang dicatat.'),(3670,3,2,12,'Yang berkemah di dekatnya ialah suku Simeon. Pemimpin bani Simeon ialah Selumiel bin Zurisyadai.'),(3671,3,2,13,'Pasukannya terdiri dari lima puluh sembilan ribu tiga ratus orang yang dicatat.'),(3672,3,2,14,'Kemudian suku Gad. Pemimpin bani Gad ialah Elyasaf bin Rehuel.'),(3673,3,2,15,'Pasukannya terdiri dari empat puluh lima ribu enam ratus lima puluh orang yang dicatat.'),(3674,3,2,16,'Jumlah orang yang dicatat dalam laskar Ruben menurut pasukan-pasukan mereka ada seratus lima puluh satu ribu empat ratus lima puluh orang. Merekalah yang nomor dua berangkat.'),(3675,3,2,17,'Sesudah itu berangkatlah Kemah Pertemuan dengan laskar orang Lewi, di tengah-tengah laskar yang lain itu. Sama seperti mereka berkemah, demikianlah juga mereka berangkat, masing-masing di tempatnya menurut panji-panji mereka.'),(3676,3,2,18,'Panji-panji laskar Efraim, menurut pasukan-pasukan mereka, adalah di sebelah barat. Pemimpin bani Efraim ialah Elisama bin Amihud.'),(3677,3,2,19,'Pasukannya terdiri dari empat puluh ribu lima ratus orang yang dicatat.'),(3678,3,2,20,'Di dekatnya ialah suku Manasye. Pemimpin bani Manasye ialah Gamaliel bin Pedazur.'),(3679,3,2,21,'Pasukannya terdiri dari tiga puluh dua ribu dua ratus orang yang dicatat.'),(3680,3,2,22,'Kemudian suku Benyamin. Pemimpin bani Benyamin ialah Abidan bin Gideoni.'),(3681,3,2,23,'Pasukannya terdiri dari tiga puluh lima ribu empat ratus orang yang dicatat.'),(3682,3,2,24,'Jumlah orang yang dicatat dalam laskar Efraim menurut pasukan-pasukan mereka ada seratus delapan ribu seratus orang. Merekalah yang nomor tiga berangkat.'),(3683,3,2,25,'Panji-panji laskar Dan adalah di sebelah utara, menurut pasukan-pasukan mereka. Pemimpin bani Dan ialah Ahiezer bin Amisyadai.'),(3684,3,2,26,'Pasukannya terdiri dari enam puluh dua ribu tujuh ratus orang yang dicatat.'),(3685,3,2,27,'Yang berkemah di dekatnya ialah suku Asyer. Pemimpin bani Asyer ialah Pagiel bin Okhran.'),(3686,3,2,28,'Pasukannya terdiri dari empat puluh satu ribu lima ratus orang yang dicatat.'),(3687,3,2,29,'Kemudian suku Naftali. Pemimpin bani Naftali ialah Ahira bin Enan.'),(3688,3,2,30,'Pasukannya terdiri dari lima puluh tiga ribu empat ratus orang yang dicatat.'),(3689,3,2,31,'Jumlah orang yang dicatat dalam laskar Dan ada seratus lima puluh tujuh ribu enam ratus orang. Merekalah yang terkemudian berangkat, menurut panji-panji mereka.\"'),(3690,3,2,32,'Itulah pencatatan orang Israel menurut suku-suku mereka. Jumlah orang yang dicatat dalam laskar-laskar dengan pasukan-pasukannya ada enam ratus tiga ribu lima ratus lima puluh orang.'),(3691,3,2,33,'Tetapi orang Lewi tidak turut dicatat bersama-sama dengan orang Israel, seperti yang diperintahkan TUHAN kepada Musa.'),(3692,3,2,34,'Maka orang Israel berbuat demikian; tepat seperti yang diperintahkan TUHAN kepada Musa, demikianlah mereka berkemah menurut panji-panji mereka, dan demikianlah mereka berangkat, masing-masing menurut kaumnya dan sukunya.'),(3693,3,3,1,'Inilah keturunan Harun dan Musa pada waktu TUHAN berfirman kepada Musa di gunung Sinai.'),(3694,3,3,2,'Nama anak-anak Harun, ialah: yang sulung Nadab, kemudian Abihu, Eleazar dan Itamar.'),(3695,3,3,3,'Itulah nama anak-anak Harun, imam-imam yang diurapi, yang telah ditahbiskan untuk memegang jabatan imam.'),(3696,3,3,4,'Tetapi Nadab dan Abihu sudah mati di hadapan TUHAN di padang gurun Sinai, ketika mereka mempersembahkan api yang asing ke hadapan TUHAN. Mereka tidak mempunyai anak. Jadi ketika Harun, ayah mereka, masih hidup, yang memegang jabatan imam ialah Eleazar dan Itamar.'),(3697,3,3,5,'TUHAN berfirman kepada Musa:'),(3698,3,3,6,'Suruhlah suku Lewi mendekat dan menghadap imam Harun, supaya mereka melayani dia.'),(3699,3,3,7,'Mereka harus mengerjakan tugas-tugas bagi Harun dan bagi segenap umat Israel di depan Kemah Pertemuan dan dengan demikian melakukan pekerjaan jabatannya pada Kemah Suci.'),(3700,3,3,8,'Mereka harus memelihara segala perabotan Kemah Pertemuan, dan mengerjakan tugas-tugas bagi orang Israel dan dengan demikian melakukan pekerjaan jabatannya pada Kemah Suci.'),(3701,3,3,9,'Orang Lewi harus kauserahkan kepada Harun dan anak-anaknya; dari antara orang Israel haruslah orang-orang itu diserahkan kepadanya dengan sepenuhnya.'),(3702,3,3,10,'Tetapi Harun dan anak-anaknya haruslah kautugaskan untuk memegang jabatannya sebagai imam, sedang orang awam yang mendekat harus dihukum mati.\"'),(3703,3,3,11,'TUHAN berfirman kepada Musa:'),(3704,3,3,12,'Sesungguhnya, Aku mengambil orang Lewi dari antara orang Israel ganti semua anak sulung mereka, yang terdahulu lahir dari kandungan, supaya orang Lewi menjadi kepunyaan-Ku,'),(3705,3,3,13,'sebab Akulah yang punya semua anak sulung. Pada waktu Aku membunuh semua anak sulung di tanah Mesir, maka Aku menguduskan bagi-Ku semua anak sulung yang ada pada orang Israel, baik dari manusia maupun dari hewan; semuanya itu kepunyaan-Ku; Akulah TUHAN.\"'),(3706,3,3,14,'TUHAN berfirman kepada Musa di padang gurun Sinai:'),(3707,3,3,15,'Catatlah bani Lewi menurut puak-puak dan kaum-kaum mereka; semua laki-laki yang berumur satu bulan ke atas harus kaucatat.'),(3708,3,3,16,'Lalu Musa mencatat mereka sesuai dengan titah TUHAN, seperti yang diperintahkan kepadanya.'),(3709,3,3,17,'Inilah anak-anak Lewi dengan nama mereka: Gerson, Kehat dan Merari.'),(3710,3,3,18,'Inilah nama anak-anak Gerson dan kaum-kaum mereka: Libni dan Simei.'),(3711,3,3,19,'Anak-anak Kehat dan kaum-kaum mereka ialah Amram, Yizhar, Hebron dan Uziel.'),(3712,3,3,20,'Anak-anak Merari dan kaum-kaum mereka ialah Mahli dan Musi. Inilah kaum-kaum orang Lewi yang ada dalam puak-puak mereka.'),(3713,3,3,21,'Puak Gerson terdiri dari kaum Libni dan kaum Simei; itulah kaum-kaum Gerson.'),(3714,3,3,22,'Jumlah pencatatan mereka ketika semua laki-laki yang berumur satu bulan ke atas dicatat ada tujuh ribu lima ratus orang.'),(3715,3,3,23,'Kaum-kaum Gerson ini berkemah di belakang Kemah Suci di sebelah barat.'),(3716,3,3,24,'Pemimpin puak Gerson ialah Elyasaf bin Lael.'),(3717,3,3,25,'Yang harus dipelihara oleh bani Gerson dalam Kemah Pertemuan ialah Kemah Suci dan Kemah dengan tudungnya, tirai pintu Kemah Pertemuan,'),(3718,3,3,26,'layar pelataran dan tirai pintu pelataran yang ada sekeliling Kemah Suci dan mezbah, dan talinya, termasuk segala pekerjaan yang berhubungan dengan semuanya itu.'),(3719,3,3,27,'Puak Kehat terdiri dari kaum Amram, kaum Yizhar, kaum Hebron dan kaum Uziel; itulah kaum-kaum Kehat.'),(3720,3,3,28,'Jumlah pencatatan mereka ketika semua laki-laki yang berumur satu bulan ke atas dicatat ada delapan ribu enam ratus orang, yakni mereka yang memelihara barang-barang kudus.'),(3721,3,3,29,'Kaum-kaum bani Kehat ini berkemah pada sisi Kemah Suci sebelah selatan.'),(3722,3,3,30,'Pemimpin puak Kehat dan kaum-kaumnya ialah Elisafan bin Uziel.'),(3723,3,3,31,'Yang harus dipelihara mereka ialah tabut, meja, kandil, mezbah-mezbah, perkakas tempat kudus yang dipakai untuk menyelenggarakan ibadah, juga tirai, termasuk segala pekerjaan yang berhubungan dengan semuanya itu.'),(3724,3,3,32,'Adapun pemimpin tertinggi orang Lewi ialah Eleazar, anak imam Harun, yang mengawasi mereka yang memelihara barang-barang kudus.'),(3725,3,3,33,'Puak Merari terdiri dari kaum Mahli dan kaum Musi; itulah kaum-kaum Merari.'),(3726,3,3,34,'Jumlah pencatatan mereka ketika semua laki-laki yang berumur satu bulan ke atas dicatat ada enam ribu dua ratus orang.'),(3727,3,3,35,'Pemimpin puak Merari dan kaum-kaumnya ialah Zuriel bin Abihail. Mereka berkemah pada sisi Kemah Suci sebelah utara.'),(3728,3,3,36,'Yang ditugaskan kepada bani Merari untuk dipelihara ialah papan Kemah Suci, kayu lintangnya, tiang-tiangnya, alasnya, segala perabotannya, termasuk segala pekerjaan yang berhubungan dengan semuanya itu,'),(3729,3,3,37,'juga tiang pelataran sekelilingnya, alas, patok dan talinya.'),(3730,3,3,38,'Yang berkemah di depan Kemah Suci di sebelah timur, di depan Kemah Pertemuan, ialah Musa, dan Harun serta anak-anaknya, yang mengerjakan tugas pemeliharaan tempat kudus bagi orang Israel; tetapi orang awam yang mendekat, haruslah dihukum mati.'),(3731,3,3,39,'Jumlah orang Lewi yang sesuai dengan titah TUHAN dicatat oleh Musa dan Harun, menurut kaum-kaum mereka, yakni semua laki-laki yang berumur satu bulan ke atas, ada dua puluh dua ribu orang.'),(3732,3,3,40,'Lalu berfirmanlah TUHAN kepada Musa: \"Catatlah semua anak sulung laki-laki Israel yang berumur satu bulan ke atas, lalu hitunglah jumlah mereka,'),(3733,3,3,41,'dan ambillah orang-orang Lewi bagi-Ku--Akulah TUHAN--sebagai ganti semua anak sulung yang ada pada orang Israel, juga hewan orang Lewi ganti semua anak sulung di antara hewan orang Israel.\"'),(3734,3,3,42,'Maka Musa mencatat semua anak sulung yang ada pada orang Israel, seperti yang diperintahkan TUHAN kepadanya.'),(3735,3,3,43,'Semua anak sulung laki-laki yang dicatat namanya dalam pencatatan itu, yakni yang berumur satu bulan ke atas, ada dua puluh dua ribu dua ratus tujuh puluh tiga orang.'),(3736,3,3,44,'Lalu berfirmanlah TUHAN kepada Musa:'),(3737,3,3,45,'Ambillah orang Lewi ganti semua anak sulung yang ada pada orang Israel, juga hewan orang Lewi ganti hewan mereka, supaya orang Lewi itu menjadi kepunyaan-Ku; Akulah TUHAN.'),(3738,3,3,46,'Sebagai uang tebusan untuk kedua ratus tujuh puluh tiga anak sulung Israel yang melebihi jumlah orang Lewi itu,'),(3739,3,3,47,'haruslah engkau mengambil lima syikal seorang; engkau harus mengambilnya menurut syikal kudus--syikal ini dua puluh gera beratnya--.'),(3740,3,3,48,'Berikanlah perak itu kepada Harun dan anak-anaknya sebagai uang tebusan untuk orang-orang yang kelebihan itu.\"'),(3741,3,3,49,'Lalu Musa mengambil uang tebusan untuk orang-orang yang melebihi jumlah mereka yang telah ditebus oleh orang Lewi itu;'),(3742,3,3,50,'dari pada anak-anak sulung Israel diambilnya perak itu, seribu tiga ratus enam puluh lima syikal, ditimbang menurut syikal kudus,'),(3743,3,3,51,'maka Musa memberikan uang tebusan itu kepada Harun dan anak-anaknya sesuai dengan titah TUHAN, seperti yang diperintahkan TUHAN kepada Musa.'),(3744,3,4,1,'TUHAN berfirman kepada Musa dan Harun:'),(3745,3,4,2,'Hitunglah jumlah bani Kehat sebagai suatu golongan tersendiri di antara bani Lewi, menurut kaum-kaum yang ada dalam puak mereka.'),(3746,3,4,3,'Hitunglah yang berumur tiga puluh tahun ke atas sampai yang berumur lima puluh tahun, semua orang yang kena wajib tugas, supaya mereka melakukan pekerjaan di Kemah Pertemuan.'),(3747,3,4,4,'Pekerjaan jabatan orang Kehat di Kemah Pertemuan ialah mengurus barang-barang yang maha kudus.'),(3748,3,4,5,'Kalau perkemahan akan berangkat, haruslah Harun dan anak-anaknya masuk ke dalam untuk menurunkan tabir penudung, dan menudungkannya kepada tabut hukum.'),(3749,3,4,6,'Di atasnya mereka harus meletakkan tutup dari kulit lumba-lumba, dan di atasnya lagi mereka harus membentangkan sehelai kain yang seluruhnya ungu tua, kemudian mereka harus memasang kayu-kayu pengusung tabut itu.'),(3750,3,4,7,'Lagipula di atas meja roti sajian mereka harus membentangkan sehelai kain ungu tua, dan di atasnya mereka harus meletakkan pinggan, cawan, piala dan kendi korban curahan; juga roti sajian harus tetap ada di atasnya.'),(3751,3,4,8,'Di atas semuanya itu mereka harus membentangkan sehelai kain kirmizi, lalu menudungnya dengan tudung dari kulit lumba-lumba, kemudian mereka harus memasang kayu-kayu pengusung meja itu.'),(3752,3,4,9,'Lalu mereka harus mengambil sehelai kain ungu tua dan menudungkannya kepada kandil untuk penerangan dengan lampu-lampunya, sepit-sepit dan penadah-penadahnya, dan segala perkakas minyaknya yang dipakai untuk mengurus kandil itu.'),(3753,3,4,10,'Dan mereka harus meletakkannya dengan segala perkakasnya ke atas tudung dengan dari kulit lumba-lumba dan meletakkannya di atas usungan.'),(3754,3,4,11,'Di atas mezbah dari emas itu mereka harus membentangkan sehelai kain ungu tua dan menudunginya dengan tudung dari kulit lumba-lumba, kemudian mereka harus memasang kayu-kayu pengusung mezbah itu.'),(3755,3,4,12,'Lalu mereka harus mengambil segala perkakas yang dipakai untuk menyelenggarakan kebaktian di tempat kudus, meletakkannya di atas sehelai kain ungu tua dan menudunginya dengan tudung dari kulit lumba-lumba, kemudian meletakkannya di atas usungan.'),(3756,3,4,13,'Dan mereka harus membersihkan mezbah itu dari abu, lalu membentangkan sehelai kain ungu muda di atasnya,'),(3757,3,4,14,'sesudah itu meletakkan di atasnya segala perkakasnya yang dipakai untuk mengurusnya, yakni perbaraan, garpu, penyodok, bokor penyiraman, segala perkakas mezbah itu, dan di atasnya mereka harus membentangkan tutup dari kulit lumba-lumba, kemudian mereka harus memasang kayu-kayu pengusung mezbah itu.'),(3758,3,4,15,'Setelah Harun dan anak-anaknya selesai menudungi barang-barang kudus dan segala perkakas tempat kudus, pada waktu perkemahan akan berangkat, barulah orang Kehat boleh masuk ke dalam untuk mengangkat barang-barang itu; tetapi janganlah mereka kena kepada barang-barang kudus itu, nanti mereka mati. Jadi itulah barang-barang di Kemah Pertemuan yang harus diangkat bani Kehat.'),(3759,3,4,16,'Tetapi Eleazar, anak imam Harun, bertanggung jawab atas minyak untuk penerangan, ukupan dari wangi-wangian, korban sajian yang tetap dan minyak urapan; ia bertanggung jawab atas segenap Kemah Suci dan segala isinya, yakni barang-barang kudus dan perabotannya.\"'),(3760,3,4,17,'TUHAN berfirman kepada Musa dan Harun:'),(3761,3,4,18,'Perhatikanlah supaya puak Kehat dan kaum-kaumnya jangan musnah binasa dari tengah-tengah orang Lewi.'),(3762,3,4,19,'Inilah yang harus kamu lakukan bagi mereka, supaya mereka tinggal hidup dan jangan mati, apabila mereka mendekat ke barang-barang maha kudus: Harun dan anak-anaknya haruslah masuk ke dalam dan menempatkan mereka masing-masing di tempat tugasnya dekat barang yang harus diangkat.'),(3763,3,4,20,'Tetapi janganlah orang Kehat masuk ke dalam untuk melihat barang-barang kudus itu walau sesaatpun, nanti mereka mati.\"'),(3764,3,4,21,'TUHAN berfirman kepada Musa:'),(3765,3,4,22,'Hitunglah juga jumlah bani Gerson menurut puak dan kaum-kaum mereka.'),(3766,3,4,23,'Catatlah mereka yang berumur tiga puluh tahun ke atas sampai yang berumur lima puluh tahun, yakni setiap orang yang wajib tugas, supaya mereka melakukan pekerjaan jabatan di Kemah Pertemuan.'),(3767,3,4,24,'Inilah tugas kaum-kaum Gerson dalam hal pekerjaan jabatan dan pengangkatan barang itu:'),(3768,3,4,25,'mereka harus mengangkat tenda-tenda Kemah Suci, dan Kemah Pertemuan tudungnya dan tudung dari kulit lumba-lumba yang ada di atasnya, tirai pintu Kemah Pertemuan,'),(3769,3,4,26,'layar-layar pelataran dan tirai pintu gerbang pelataran yang ada sekeliling Kemah Suci dan mezbah, dengan talinya dan segala perkakas untuk pekerjaan jabatan mereka; dan mereka harus melakukan segala tugas yang perlu berkenaan dengan semuanya itu.'),(3770,3,4,27,'Seluruh pekerjaan jabatan bani Gerson harus dilakukan sesuai dengan perintah Harun dan anak-anaknya, yakni segala tugas pengangkatan barang dan pekerjaan jabatan itu; kamu harus membuat mereka penanggung jawab atas segala yang harus diangkat mereka.'),(3771,3,4,28,'Itulah tugas kaum-kaum bani Gerson di Kemah Pertemuan. Mereka harus mengerjakan di bawah pimpinan Itamar, anak imam Harun itu.'),(3772,3,4,29,'Orang Merari haruslah kaucatat menurut kaum-kaum yang ada dalam puak mereka.'),(3773,3,4,30,'Catatlah mereka yang berumur tiga puluh tahun ke atas sampai yang berumur lima puluh tahun, yakni setiap orang yang kena wajib tugas, supaya mereka melakukan pekerjaan jabatan pada Kemah Pertemuan.'),(3774,3,4,31,'Inilah yang wajib diangkat mereka berhubung dengan seluruh pekerjaan jabatan mereka di Kemah Pertemuan: papan-papan Kemah Suci, kayu-kayu lintangnya, tiang-tiangnya, alas-alasnya,'),(3775,3,4,32,'tiang-tiang pelataran sekelilingnya, alas-alasnya, patok-patok dan tali-talinya, serta segala perkakasnya; semuanya termasuk tugas mereka. Dengan terperinci haruslah kamu tunjuk perkakas yang wajib diangkat mereka itu.'),(3776,3,4,33,'Itulah tugas kaum-kaum bani Merari, yakni seluruh pekerjaan jabatan mereka di Kemah Pertemuan, yang harus dilakukan di bawah pimpinan Itamar, anak imam Harun.\"'),(3777,3,4,34,'Demikianlah Musa dan Harun dengan para pemimpin umat Israel mencatat bani Kehat menurut kaum-kaum yang ada dalam puak mereka,'),(3778,3,4,35,'yakni orang-orang yang berumur tiga puluh tahun ke atas sampai yang berumur lima puluh tahun, setiap orang yang kena wajib tugas berhubung dengan pekerjaan jabatan di Kemah Pertemuan.'),(3779,3,4,36,'Maka jumlah pencatatan mereka menurut kaum-kaum mereka ada dua ribu tujuh ratus lima puluh orang.'),(3780,3,4,37,'Itulah orang-orang yang dicatat dari kaum-kaum Kehat, semua orang yang melakukan pekerjaan jabatan di Kemah Pertemuan, yakni mereka yang dicatat oleh Musa dan Harun, sesuai dengan titah TUHAN dengan perantaraan Musa.'),(3781,3,4,38,'Bani Gerson yang dicatat menurut kaum-kaum yang ada dalam puak mereka,'),(3782,3,4,39,'yaitu orang-orang yang berumur tiga puluh tahun ke atas sampai yang berumur lima puluh tahun, setiap orang yang kena wajib tugas berhubung dengan pekerjaan jabatan di Kemah Pertemuan,'),(3783,3,4,40,'jadi mereka yang dicatat menurut kaum-kaum yang ada dalam puak mereka, berjumlah dua ribu enam ratus tiga puluh orang.'),(3784,3,4,41,'Itulah jumlah pencatatan kaum-kaum bani Gerson, semua orang yang melakukan pekerjaan jabatan di Kemah Pertemuan, yakni mereka yang dicatat oleh Musa dan Harun sesuai dengan titah TUHAN.'),(3785,3,4,42,'Bani Merari yang dicatat menurut kaum-kaum yang ada dalam puak mereka,'),(3786,3,4,43,'yaitu orang-orang yang berumur tiga puluh tahun ke atas sampai yang berumur lima puluh tahun, setiap orang yang kena wajib tugas berhubung dengan pekerjaan jabatan di Kemah Pertemuan,'),(3787,3,4,44,'jadi mereka yang dicatat menurut kaum-kaum mereka, berjumlah tiga ribu dua ratus orang.'),(3788,3,4,45,'Itulah jumlah pencatatan kaum-kaum bani Merari, yakni mereka yang dicatat oleh Musa dan Harun, sesuai dengan titah TUHAN dengan perantaraan Musa.'),(3789,3,4,46,'Semua orang Lewi yang dicatat oleh Musa dan Harun dengan para pemimpin Israel menurut kaum-kaum yang ada dalam puak-puak mereka,'),(3790,3,4,47,'yakni orang-orang yang berumur tiga puluh tahun ke atas sampai yang berumur lima puluh tahun, setiap orang yang wajib melakukan pekerjaan jabatan di Kemah Pertemuan dan pekerjaan pengangkatan barang,'),(3791,3,4,48,'jadi mereka yang dicatat, berjumlah delapan ribu lima ratus delapan puluh orang.'),(3792,3,4,49,'Sesuai dengan titah TUHAN dengan perantaraan Musa, maka mereka masing-masing dibuat penanggung jawab atas apa yang harus dikerjakan dan diangkatnya. Demikianlah mereka dicatat, seperti yang diperintahkan TUHAN kepada Musa.'),(3793,3,5,1,'TUHAN berfirman kepada Musa:'),(3794,3,5,2,'Perintahkanlah kepada orang Israel, supaya semua orang yang sakit kusta, semua orang yang mengeluarkan lelehan, dan semua orang yang najis oleh mayat disuruh meninggalkan tempat perkemahan;'),(3795,3,5,3,'baik laki-laki maupun perempuan haruslah kausuruh pergi; ke luar tempat perkemahan haruslah mereka kausuruh pergi, supaya mereka jangan menajiskan tempat perkemahan di mana Aku diam di tengah-tengah mereka.\"'),(3796,3,5,4,'Maka orang Israel berbuat demikian, mereka menyuruh orang-orang itu meninggalkan tempat perkemahan; seperti yang difirmankan TUHAN kepada Musa, demikianlah diperbuat orang Israel.'),(3797,3,5,5,'TUHAN berfirman kepada Musa:'),(3798,3,5,6,'Berbicaralah kepada orang Israel: Apabila seseorang, laki-laki atau perempuan, melakukan sesuatu dosa terhadap sesamanya manusia, dan oleh karena itu berubah setia terhadap TUHAN, sehingga orang itu menjadi bersalah,'),(3799,3,5,7,'maka haruslah ia mengakui dosa yang telah dilakukannya itu; kemudian membayar tebusan sepenuhnya dengan menambah seperlima, lalu menyerahkannya kepada orang terhadap siapa ia bersalah.'),(3800,3,5,8,'Tetapi apabila orang itu tidak ada kaumnya, kepada siapa dapat dibayar tebusan salah itu, maka tebusan salah yang harus dibayar itu menjadi kepunyaan TUHAN, dan adalah bagian imam, belum terhitung domba jantan pendamaian yang dipakai untuk mengadakan pendamaian bagi orang itu.'),(3801,3,5,9,'Dari persembahan-persembahan kudus yang disampaikan orang Israel kepada imam, persembahan khususnya adalah bagian imam.'),(3802,3,5,10,'Sedang persembahan-persembahan kudus yang dibawa oleh seseorang adalah bagian orang itu sendiri; hanya apa yang diserahkannya kepada seorang imam adalah bagian imam itu.\"'),(3803,3,5,11,'TUHAN berfirman kepada Musa:'),(3804,3,5,12,'Berbicaralah kepada orang Israel dan katakanlah kepada mereka: Apabila isteri seseorang berbuat serong dan tidak setia terhadap suaminya,'),(3805,3,5,13,'dan laki-laki lain tidur dan bersetubuh dengan perempuan itu, dengan tidak diketahui suaminya, karena tinggal rahasia bahwa perempuan itu mencemarkan dirinya, tidak ada saksi terhadap dia, dia tidak kedapatan,'),(3806,3,5,14,'dan apabila kemudian roh cemburu menguasai suami itu, sehingga ia menjadi cemburu terhadap isterinya, dan perempuan itu memang telah mencemarkan dirinya, atau apabila roh cemburu menguasai suami itu, sehingga ia menjadi cemburu terhadap isterinya, walaupun perempuan itu tidak mencemarkan dirinya,'),(3807,3,5,15,'maka haruslah orang itu membawa isterinya kepada imam. Dan orang itu harus membawa persembahan karena perempuan itu sebanyak sepersepuluh efa tepung jelai, yang ke atasnya tidak dituangkannya minyak dan yang tidak dibubuhinya kemenyan, karena korban itu ialah korban sajian cemburuan, suatu korban peringatan yang mengingatkan kepada kedurjanaan.'),(3808,3,5,16,'Maka haruslah imam menyuruh perempuan itu mendekat dan menghadapkannya kepada TUHAN.'),(3809,3,5,17,'Lalu imam harus membawa air kudus dalam suatu tempayan tanah, kemudian harus memungut debu yang ada di lantai Kemah Suci dan membubuhnya ke dalam air itu.'),(3810,3,5,18,'Apabila imam sudah menghadapkan perempuan itu kepada TUHAN, haruslah ia menguraikan rambut perempuan itu, lalu meletakkan korban peringatan, yakni korban sajian cemburuan, ke atas telapak tangan perempuan itu, sedang di tangan imam haruslah ada air pahit yang mendatangkan kutuk.'),(3811,3,5,19,'Maka haruslah imam menyumpah perempuan itu dengan berkata kepadanya: Jika tidak benar ada laki-laki yang tidur dengan engkau, dan jika tidak engkau berbuat serong kepada kecemaran, padahal engkau di bawah kuasa suamimu, maka luputlah engkau dari air pahit yang mendatangkan kutuk ini;'),(3812,3,5,20,'tetapi jika engkau, padahal engkau di bawah kuasa suamimu, berbuat serong dan mencemarkan dirimu, oleh karena orang lain dari suamimu sendiri bersetubuh dengan engkau--'),(3813,3,5,21,'dalam hal ini haruslah imam menyumpah perempuan itu dengan sumpah kutuk, dan haruslah imam berkata kepada perempuan itu--maka TUHAN kiranya membuat engkau menjadi sumpah kutuk di tengah-tengah bangsamu dengan mengempiskan pahamu dan mengembungkan perutmu,'),(3814,3,5,22,'sebab air yang mendatangkan kutuk ini akan masuk ke dalam tubuhmu untuk mengembungkan perutmu dan mengempiskan pahamu. Dan haruslah perempuan itu berkata: Amin, amin.'),(3815,3,5,23,'Lalu imam harus menuliskan kutuk itu pada sehelai kertas dan menghapusnya dengan air pahit itu,'),(3816,3,5,24,'dan ia harus memberi perempuan itu minum air pahit yang mendatangkan kutuk itu, dan air itu akan masuk ke dalam badannya dan menyebabkan sakit yang pedih.'),(3817,3,5,25,'Maka haruslah imam mengambil korban sajian cemburuan dari tangan perempuan itu lalu mengunjukkannya ke hadapan TUHAN, dan membawanya ke mezbah.'),(3818,3,5,26,'Sesudah itu haruslah imam mengambil segenggam dari korban sajian itu sebagai bagian ingat-ingatannya dan membakarnya di atas mezbah, kemudian memberi perempuan itu minum air itu.'),(3819,3,5,27,'Setelah terjadi demikian, apabila perempuan itu memang mencemarkan dirinya dan berubah setia terhadap suaminya, air yang mendatangkan sumpah serapah itu akan masuk ke badannya dan menyebabkan sakit yang pedih, sehingga perutnya mengembung dan pahanya mengempis, dan perempuan itu akan menjadi sumpah kutuk di antara bangsanya.'),(3820,3,5,28,'Tetapi apabila perempuan itu tidak mencemarkan dirinya, melainkan ia suci, maka ia akan bebas dan akan dapat beranak.\"'),(3821,3,5,29,'Itulah hukum tentang perkara cemburuan, kalau seorang perempuan telah berbuat serong dan mencemarkan dirinya, padahal ia di bawah kuasa suaminya,'),(3822,3,5,30,'atau kalau roh cemburu menguasai seorang laki-laki, sehingga ia cemburu terhadap isterinya; ia harus menghadapkan perempuan itu kepada TUHAN dan imam haruslah melaksanakan seluruh hukum ini kepada perempuan itu.'),(3823,3,5,31,'Laki-laki itu akan bebas dari pada salah, tetapi perempuan itu haruslah menanggung akibat kesalahannya.'),(3824,3,6,1,'TUHAN berfirman kepada Musa:'),(3825,3,6,2,'Berbicaralah kepada orang Israel dan katakanlah kepada mereka: Apabila seseorang, laki-laki atau perempuan, mengucapkan nazar khusus, yakni nazar orang nazir, untuk mengkhususkan dirinya bagi TUHAN,'),(3826,3,6,3,'maka haruslah ia menjauhkan dirinya dari anggur dan minuman yang memabukkan, jangan meminum cuka anggur atau cuka minuman yang memabukkan dan jangan meminum sesuatu minuman yang dibuat dari buah anggur, dan jangan memakan buah anggur, baik yang segar maupun yang kering.'),(3827,3,6,4,'Selama waktu kenazirannya janganlah ia makan sesuatu apapun yang berasal dari pohon anggur, dari bijinya sampai kepada pucuk rantingnya.'),(3828,3,6,5,'Selama waktu nazarnya sebagai orang nazir janganlah pisau cukur lalu di kepalanya; sampai genap waktunya ia mengkhususkan dirinya bagi TUHAN, haruslah ia tetap kudus dan membiarkan rambutnya tumbuh panjang.'),(3829,3,6,6,'Selama waktunya ia mengkhususkan dirinya bagi TUHAN, janganlah ia dekat kepada mayat orang;'),(3830,3,6,7,'bahkan apabila mati ayahnya ataupun ibunya, saudaranya laki-laki ataupun saudaranya perempuan, janganlah ia menajiskan dirinya kepada mereka, sebab tanda kenaziran bagi Allahnya ada di atas kepalanya.'),(3831,3,6,8,'Selama waktu kenazirannya ia kudus bagi TUHAN.'),(3832,3,6,9,'Tetapi apabila seseorang mati di dekatnya dengan sangat tiba-tiba, sehingga ia menajiskan rambut kenazirannya, maka haruslah ia mencukur rambutnya pada hari pentahirannya, yaitu pada hari yang ketujuh haruslah ia mencukurnya.'),(3833,3,6,10,'Pada hari yang kedelapan haruslah ia membawa dua ekor burung tekukur atau dua ekor anak burung merpati kepada imam, ke pintu Kemah Pertemuan.'),(3834,3,6,11,'Maka haruslah imam mengolah yang seekor menjadi korban penghapus dosa dan yang lain menjadi korban bakaran, dan mengadakan pendamaian bagi dia, oleh karena dia telah berdosa dengan berada dekat mayat. Pada hari itu juga ia harus menguduskan kepalanya'),(3835,3,6,12,'dan mengkhususkan waktu kenazirannya bagi TUHAN. Ia harus membawa seekor domba jantan berumur setahun menjadi korban penebus salah. Hari-hari yang sudah lewat dianggap batal, karena rambut kenazirannya telah menjadi najis.'),(3836,3,6,13,'Dan inilah hukum tentang seorang nazir. Apabila waktu kenazirannya genap, ia harus dibawa ke pintu Kemah Pertemuan,'),(3837,3,6,14,'dan ia harus mempersembahkan sebagai persembahannya kepada TUHAN seekor domba jantan berumur setahun yang tidak bercela untuk korban bakaran dan seekor domba betina berumur setahun yang tidak bercela untuk korban penghapus dosa dan seekor domba jantan yang tidak bercela untuk korban keselamatan,'),(3838,3,6,15,'juga sebakul roti yang tidak beragi, yakni roti bundar dari tepung yang terbaik, yang diolah dengan minyak, dan roti tipis yang tidak beragi diolesi dengan minyak, serta dengan korban sajian dan korban-korban curahannya.'),(3839,3,6,16,'Lalu haruslah imam membawa semuanya itu ke hadapan TUHAN dan mengolah korban penghapus dosa dan korban bakarannya;'),(3840,3,6,17,'domba jantan itu haruslah diolahnya sebagai korban keselamatan bagi TUHAN, beserta sebakul roti yang tidak beragi itu; juga haruslah imam mengolah korban sajian dan korban curahannya.'),(3841,3,6,18,'Maka haruslah orang nazir itu mencukur rambut kenazirannya di depan pintu Kemah Pertemuan, lalu mengambil rambut kenazirannya itu dan melemparkannya ke dalam api yang di bawah korban keselamatan.'),(3842,3,6,19,'Imam haruslah mengambil paha depan domba jantan itu, sesudah dimasak, dan satu roti bundar yang tidak beragi dari dalam bakul, dengan satu roti tipis yang tidak beragi, lalu meletakkannya ke atas telapak tangan orang nazir itu, setelah orang ini mencukur rambut kenazirannya;'),(3843,3,6,20,'kemudian haruslah imam mengunjukkan semuanya itu ke hadapan TUHAN sebagai persembahan unjukan; semuanya itu menjadi bagian kudus bagi imam, beserta dada persembahan unjukan dan beserta paha persembahan khusus. Sesudah itu barulah boleh orang nazir itu minum anggur.\"'),(3844,3,6,21,'Itulah hukum tentang orang nazir yang menazarkan persembahannya kepada TUHAN berdasarkan kenazirannya, belum dihitung apa yang ia mampu mempersembahkan di samping itu. Sesuai dengan bunyi nazar yang diikrarkannya, demikianlah harus dilakukannya berdasarkan hukum tentang kenazirannya.'),(3845,3,6,22,'TUHAN berfirman kepada Musa:'),(3846,3,6,23,'Berbicaralah kepada Harun dan anak-anaknya: Beginilah harus kamu memberkati orang Israel, katakanlah kepada mereka:'),(3847,3,6,24,'TUHAN memberkati engkau dan melindungi engkau;'),(3848,3,6,25,'TUHAN menyinari engkau dengan wajah-Nya dan memberi engkau kasih karunia;'),(3849,3,6,26,'TUHAN menghadapkan wajah-Nya kepadamu dan memberi engkau damai sejahtera.'),(3850,3,6,27,'Demikianlah harus mereka meletakkan nama-Ku atas orang Israel, maka Aku akan memberkati mereka.\"'),(3851,3,7,1,'Pada waktu Musa selesai mendirikan Kemah Suci, diurapinya dan dikuduskannyalah itu dengan segala perabotannya, juga mezbah dengan segala perkakasnya; dan setelah diurapi dan dikuduskannya semuanya itu,'),(3852,3,7,2,'maka para pemimpin Israel, para kepala suku mereka, mempersembahkan persembahan. Mereka itu ialah para pemimpin suku yang bertanggung jawab atas pencatatan itu.'),(3853,3,7,3,'Sebagai persembahan dibawa mereka ke hadapan TUHAN: enam kereta beratap dengan dua belas ekor lembu; satu kereta untuk dua orang pemimpin dan satu ekor lembu untuk satu orang pemimpin, lalu mereka membawa semuanya itu ke depan Kemah Suci.'),(3854,3,7,4,'Kemudian TUHAN berfirman kepada Musa:'),(3855,3,7,5,'Terimalah semuanya itu dari mereka, supaya dipergunakan untuk pekerjaan pada Kemah Pertemuan; berikanlah semuanya itu kepada orang Lewi, sesuai dengan keperluan pekerjaan masing-masing.'),(3856,3,7,6,'Lalu Musa menerima kereta-kereta dan lembu-lembu itu dan memberikannya kepada orang Lewi;'),(3857,3,7,7,'dua kereta dengan empat ekor lembu diberikannya kepada bani Gerson, sesuai dengan keperluan pekerjaan mereka,'),(3858,3,7,8,'dan empat kereta dengan delapan ekor lembu diberikannya kepada bani Merari, sesuai dengan keperluan pekerjaan mereka di bawah pimpinan Itamar, anak imam Harun itu.'),(3859,3,7,9,'Tetapi kepada bani Kehat tidak diberikannya apa-apa, karena pekerjaan mereka ialah mengurus barang-barang kudus, yang harus diangkat di atas bahunya.'),(3860,3,7,10,'Lagi para pemimpin mempersembahkan persembahan pentahbisan mezbah, pada hari mezbah itu diurapi; para pemimpin membawa persembahan mereka ke depan mezbah itu.'),(3861,3,7,11,'TUHAN berfirman kepada Musa: \"Satu pemimpin setiap hari haruslah mempersembahkan persembahannya untuk mentahbiskan mezbah itu.\"'),(3862,3,7,12,'Yang mempersembahkan persembahannya pada hari pertama ialah Nahason bin Aminadab, dari suku Yehuda.'),(3863,3,7,13,'Persembahannya ialah satu pinggan perak, seratus tiga puluh syikal timbangannya, dan satu bokor penyiraman dari perak tujuh puluh syikal beratnya, ditimbang menurut syikal kudus, keduanya berisi tepung yang terbaik, diolah dengan minyak, untuk korban sajian;'),(3864,3,7,14,'satu cawan sepuluh syikal emas beratnya, berisi ukupan;'),(3865,3,7,15,'seekor lembu jantan muda, seekor domba jantan dan seekor domba berumur setahun, untuk korban bakaran;'),(3866,3,7,16,'seekor kambing jantan, untuk korban penghapus dosa;'),(3867,3,7,17,'dan untuk korban keselamatan dua ekor lembu, lima ekor domba jantan, lima ekor kambing jantan dan lima ekor domba berumur setahun. Itulah persembahan Nahason bin Aminadab.'),(3868,3,7,18,'Pada hari kedua Netaneel bin Zuar, pemimpin suku Isakhar, mempersembahkan persembahan.'),(3869,3,7,19,'Sebagai persembahannya dipersembahkannya satu pinggan perak, seratus tiga puluh syikal timbangannya, dan satu bokor penyiraman dari perak, tujuh puluh syikal beratnya, ditimbang menurut syikal kudus, keduanya berisi tepung yang terbaik, diolah dengan minyak, untuk korban sajian;'),(3870,3,7,20,'satu cawan, sepuluh syikal emas beratnya, berisi ukupan;'),(3871,3,7,21,'seekor lembu jantan muda, seekor domba jantan dan seekor domba berumur setahun, untuk korban bakaran;'),(3872,3,7,22,'seekor kambing jantan untuk korban penghapus dosa;'),(3873,3,7,23,'dan untuk korban keselamatan dua ekor lembu, lima ekor domba jantan, lima ekor kambing jantan dan lima ekor domba berumur setahun. Itulah persembahan Netaneel bin Zuar.'),(3874,3,7,24,'Pada hari ketiga: pemimpin bani Zebulon, Eliab bin Helon.'),(3875,3,7,25,'Persembahannya ialah satu pinggan perak, seratus tiga puluh syikal timbangannya, dan satu bokor penyiraman dari perak tujuh puluh syikal beratnya, ditimbang menurut syikal kudus, keduanya berisi tepung yang terbaik, diolah dengan minyak, untuk korban sajian;'),(3876,3,7,26,'satu cawan, sepuluh syikal emas beratnya, berisi ukupan;'),(3877,3,7,27,'seekor lembu jantan muda, seekor domba jantan dan seekor domba berumur setahun, untuk korban bakaran;'),(3878,3,7,28,'seekor kambing jantan untuk korban penghapus dosa;'),(3879,3,7,29,'dan untuk korban keselamatan dua ekor lembu, lima ekor domba jantan, lima ekor kambing jantan dan lima ekor domba berumur setahun. Itulah persembahan Eliab bin Helon.'),(3880,3,7,30,'Pada hari keempat: pemimpin bani Ruben, Elizur bin Syedeur.'),(3881,3,7,31,'Persembahannya ialah satu pinggan perak, seratus tiga puluh syikal timbangannya, dan satu bokor penyiraman dari perak, tujuh puluh syikal beratnya, ditimbang menurut syikal kudus, keduanya berisi tepung yang terbaik diolah dengan minyak, untuk korban sajian;'),(3882,3,7,32,'satu cawan, sepuluh syikal emas beratnya, berisi ukupan;'),(3883,3,7,33,'seekor lembu jantan muda, seekor domba jantan dan seekor domba berumur setahun, untuk korban bakaran;'),(3884,3,7,34,'seekor kambing jantan untuk korban penghapus dosa;'),(3885,3,7,35,'dan untuk korban keselamatan dua ekor lembu, lima ekor domba jantan, lima ekor kambing jantan dan lima ekor domba berumur setahun. Itulah persembahan Elizur bin Syedeur.'),(3886,3,7,36,'Pada hari kelima: pemimpin bani Simeon, Selumiel bin Zurisyadai.'),(3887,3,7,37,'Persembahannya ialah satu pinggan perak, seratus tiga puluh syikal timbangannya, dan satu bokor penyiraman dari perak, tujuh puluh syikal beratnya, ditimbang menurut syikal kudus, keduanya berisi tepung yang terbaik, diolah dengan minyak, untuk korban sajian;'),(3888,3,7,38,'satu cawan, sepuluh syikal emas beratnya, berisi ukupan;'),(3889,3,7,39,'seekor lembu jantan muda, seekor domba jantan dan seekor domba berumur setahun, untuk korban bakaran;'),(3890,3,7,40,'seekor kambing jantan untuk korban penghapus dosa;'),(3891,3,7,41,'dan untuk korban keselamatan dua ekor lembu, lima ekor domba jantan, lima ekor kambing jantan dan lima ekor domba berumur setahun. Itulah persembahan Selumiel bin Zurisyadai.'),(3892,3,7,42,'Pada hari keenam: pemimpin bani Gad, Elyasaf bin Rehuel.'),(3893,3,7,43,'Persembahannya ialah satu pinggan perak, seratus tiga puluh syikal timbangannya, dan satu bokor penyiraman dari perak, tujuh puluh syikal beratnya, ditimbang menurut syikal kudus, keduanya berisi tepung yang terbaik, diolah dengan minyak, untuk korban sajian;'),(3894,3,7,44,'satu cawan, sepuluh syikal emas beratnya, berisi ukupan;'),(3895,3,7,45,'seekor lembu jantan muda, seekor domba jantan dan seekor domba berumur setahun, untuk korban bakaran;'),(3896,3,7,46,'seekor kambing jantan untuk korban penghapus dosa;'),(3897,3,7,47,'dan untuk korban keselamatan dua ekor lembu, lima ekor domba jantan, lima ekor kambing jantan dan lima ekor domba berumur setahun. Itulah persembahan Elyasaf bin Rehuel.'),(3898,3,7,48,'Pada hari ketujuh: pemimpin bani Efraim, Elisama bin Amihud.'),(3899,3,7,49,'Persembahannya ialah satu pinggan perak, seratus tiga puluh syikal timbangannya, dan satu bokor penyiraman dari perak, tujuh puluh syikal beratnya, ditimbang menurut syikal kudus, keduanya berisi tepung yang terbaik, diolah dengan minyak, untuk korban sajian;'),(3900,3,7,50,'satu cawan, sepuluh syikal emas beratnya, berisi ukupan;'),(3901,3,7,51,'seekor lembu jantan muda, seekor domba jantan dan seekor domba berumur setahun, untuk korban bakaran;'),(3902,3,7,52,'seekor kambing jantan untuk korban penghapus dosa;'),(3903,3,7,53,'dan untuk korban keselamatan dua ekor lembu, lima ekor domba jantan, lima ekor kambing jantan dan lima ekor domba berumur setahun. Itulah persembahan Elisama bin Amihud.'),(3904,3,7,54,'Pada hari kedelapan: pemimpin bani Manasye, Gamaliel bin Pedazur.'),(3905,3,7,55,'Persembahannya ialah satu pinggan perak, seratus tiga puluh syikal timbangannya, dan satu bokor penyiraman dari perak, tujuh puluh syikal beratnya, ditimbang menurut syikal kudus, keduanya berisi tepung yang terbaik, diolah dengan minyak, untuk korban sajian;'),(3906,3,7,56,'satu cawan, sepuluh syikal emas beratnya, berisi ukupan;'),(3907,3,7,57,'seekor lembu jantan muda, seekor domba jantan dan seekor domba berumur setahun, untuk korban bakaran;'),(3908,3,7,58,'seekor kambing jantan untuk korban penghapus dosa;'),(3909,3,7,59,'dan untuk korban keselamatan dua ekor lembu, lima ekor domba jantan, lima ekor kambing jantan dan lima ekor domba berumur setahun. Itulah persembahan Gamaliel bin Pedazur.'),(3910,3,7,60,'Pada hari kesembilan: pemimpin bani Benyamin, Abidan bin Gideoni.'),(3911,3,7,61,'Persembahannya ialah satu pinggan perak, seratus tiga puluh syikal timbangannya, dan satu bokor penyiraman dari perak, tujuh puluh syikal beratnya, ditimbang menurut syikal kudus, keduanya berisi tepung yang terbaik, diolah dengan minyak, untuk korban sajian;'),(3912,3,7,62,'satu cawan, sepuluh syikal emas beratnya, berisi ukupan;'),(3913,3,7,63,'seekor lembu jantan muda, seekor domba jantan dan seekor domba berumur setahun, untuk korban bakaran;'),(3914,3,7,64,'seekor kambing jantan untuk korban penghapus dosa;'),(3915,3,7,65,'dan untuk korban keselamatan dua ekor lembu, lima ekor domba jantan, lima ekor kambing jantan dan lima ekor domba berumur setahun. Itulah persembahan Abidan bin Gideoni.'),(3916,3,7,66,'Pada hari kesepuluh: pemimpin bani Dan, Ahiezer bin Amisyadai.'),(3917,3,7,67,'Persembahannya ialah satu pinggan perak, seratus tiga puluh syikal timbangannya, dan satu bokor penyiraman dari perak, tujuh puluh syikal beratnya, ditimbang menurut syikal kudus, keduanya berisi tepung yang terbaik, diolah dengan minyak, untuk korban sajian;'),(3918,3,7,68,'satu cawan, sepuluh syikal emas beratnya, berisi ukupan;'),(3919,3,7,69,'seekor lembu jantan muda, seekor domba jantan dan seekor domba berumur setahun, untuk korban bakaran;'),(3920,3,7,70,'seekor kambing jantan untuk korban penghapus dosa;'),(3921,3,7,71,'dan untuk korban keselamatan dua ekor lembu, lima ekor domba jantan, lima ekor kambing jantan dan lima ekor domba berumur setahun. Itulah persembahan Ahiezer bin Amisyadai.'),(3922,3,7,72,'Pada hari kesebelas: pemimpin bani Asyer, Pagiel bin Okhran.'),(3923,3,7,73,'Persembahannya ialah satu pinggan perak, seratus tiga puluh syikal timbangannya, dan satu bokor penyiraman dari perak, tujuh puluh syikal beratnya, ditimbang menurut syikal kudus, keduanya berisi tepung yang terbaik, diolah dengan minyak, untuk korban sajian;'),(3924,3,7,74,'satu cawan, sepuluh syikal emas beratnya, berisi ukupan;'),(3925,3,7,75,'seekor lembu jantan muda, seekor domba jantan dan seekor domba berumur setahun, untuk korban bakaran;'),(3926,3,7,76,'seekor kambing jantan untuk korban penghapus dosa;'),(3927,3,7,77,'dan untuk korban keselamatan dua ekor lembu, lima ekor domba jantan, lima ekor kambing jantan dan lima ekor domba berumur setahun. Itulah persembahan Pagiel bin Okhran.'),(3928,3,7,78,'Pada hari kedua belas: pemimpin bani Naftali, Ahira bin Enan.'),(3929,3,7,79,'Persembahannya ialah satu pinggan perak, seratus tiga puluh syikal timbangannya, dan satu bokor penyiraman dari perak, tujuh puluh syikal beratnya, ditimbang menurut syikal kudus, keduanya berisi tepung yang terbaik, diolah dengan minyak, untuk korban sajian;'),(3930,3,7,80,'satu cawan, sepuluh syikal emas beratnya, berisi ukupan;'),(3931,3,7,81,'seekor lembu jantan muda, seekor domba jantan dan seekor domba berumur setahun, untuk korban bakaran;'),(3932,3,7,82,'seekor kambing jantan untuk korban penghapus dosa;'),(3933,3,7,83,'dan untuk korban keselamatan dua ekor lembu, lima ekor domba jantan, lima ekor kambing jantan dan lima ekor domba berumur setahun. Itulah persembahan Ahira bin Enan.'),(3934,3,7,84,'Itulah persembahan pentahbisan mezbah pada hari mezbah itu diurapi, dari pihak para pemimpin Israel, yaitu dua belas pinggan perak, dua belas bokor penyiraman dari perak, dua belas cawan emas.'),(3935,3,7,85,'Beratnya tiap-tiap pinggan adalah seratus tiga puluh syikal perak, dan beratnya tiap-tiap bokor penyiraman adalah tujuh puluh syikal; segala perak perkakas-perkakas itu ada dua ribu empat ratus syikal beratnya, ditimbang menurut syikal kudus.'),(3936,3,7,86,'Selanjutnya dua belas cawan emas berisi ukupan, tiap-tiap cawan sepuluh syikal beratnya, ditimbang menurut syikal kudus; segala emas cawan itu ada seratus dua puluh syikal beratnya.'),(3937,3,7,87,'Segala hewan untuk korban bakaran itu ialah dua belas ekor lembu jantan, dua belas ekor domba jantan, dua belas ekor domba berumur setahun, dengan korban sajiannya; juga dua belas ekor kambing jantan untuk korban penghapus dosa.'),(3938,3,7,88,'Segala hewan korban keselamatan itu ialah dua puluh empat ekor lembu jantan, enam puluh ekor domba jantan, enam puluh ekor kambing jantan, enam puluh ekor domba berumur setahun. Itulah persembahan pentahbisan mezbah, sesudah mezbah itu diurapi.'),(3939,3,7,89,'Apabila Musa masuk ke dalam Kemah Pertemuan untuk berbicara dengan Dia, maka ia mendengar suara yang berfirman kepadanya dari atas tutup pendamaian, yang di atas tabut hukum Allah, dari antara kedua kerub itu; demikianlah Ia berfirman kepadanya.'),(3940,3,8,1,'TUHAN berfirman kepada Musa:'),(3941,3,8,2,'Berbicaralah kepada Harun dan katakanlah kepadanya: Apabila engkau memasang lampu-lampu itu, haruslah ketujuh lampu itu menerangi yang di sebelah depan kandil.'),(3942,3,8,3,'Demikianlah diperbuat Harun. Di sebelah depan kandil dipasangnyalah lampu-lampunya, seperti yang diperintahkan TUHAN kepada Musa.'),(3943,3,8,4,'Dan beginilah kandil itu dibuat: dari emas tempaan; kandil itu tempaan, baik kakinya maupun kembangnya; sesuai dengan apa yang telah diperlihatkan TUHAN kepada Musa, demikianlah kandil itu dibuatnya.'),(3944,3,8,5,'TUHAN berfirman kepada Musa:'),(3945,3,8,6,'Ambillah orang Lewi dari tengah-tengah orang Israel dan tahirkanlah mereka.'),(3946,3,8,7,'Beginilah harus kaulakukan kepada mereka untuk mentahirkan mereka: percikkanlah kepada mereka air penghapus dosa, kemudian haruslah mereka mencukur seluruh tubuhnya dan mencuci pakaiannya dan dengan demikian mentahirkan dirinya.'),(3947,3,8,8,'Sesudah itu haruslah mereka mengambil seekor lembu jantan muda dengan korban sajiannya dari tepung yang terbaik, diolah dengan minyak, juga seekor lembu jantan muda yang lain haruslah kauambil untuk korban penghapus dosa.'),(3948,3,8,9,'Selanjutnya haruslah kausuruh orang Lewi mendekat ke depan Kemah Pertemuan, dan kaupanggil berkumpul segenap umat Israel.'),(3949,3,8,10,'Apabila engkau telah menyuruh orang Lewi mendekat ke hadapan TUHAN, maka haruslah orang Israel meletakkan tangannya atas orang Lewi itu,'),(3950,3,8,11,'dan Harun harus mengunjukkan orang Lewi itu sebagai persembahan unjukan dari antara orang Israel di hadapan TUHAN, dan demikianlah mereka diuntukkan melakukan pekerjaan jabatannya bagi TUHAN.'),(3951,3,8,12,'Setelah orang Lewi meletakkan tangannya atas kepala lembu-lembu jantan muda itu, maka haruslah yang seekor diolah sebagai korban penghapus dosa dan yang lain sebagai korban bakaran bagi TUHAN untuk mengadakan pendamaian bagi orang Lewi.'),(3952,3,8,13,'Maka haruslah engkau menghadapkan orang Lewi kepada Harun dengan anak-anaknya dan mengunjukkan mereka sebagai persembahan unjukan bagi TUHAN.'),(3953,3,8,14,'Demikianlah harus engkau mentahirkan mereka dari tengah-tengah orang Israel, supaya orang Lewi itu menjadi kepunyaan-Ku.'),(3954,3,8,15,'Barulah sesudah itu orang Lewi boleh masuk untuk melakukan pekerjaan jabatannya pada Kemah Pertemuan, sesudah engkau mentahirkan mereka dan mengunjukkan mereka sebagai persembahan unjukan.'),(3955,3,8,16,'Sebab mereka harus diserahkan dengan sepenuhnya kepada-Ku dari tengah-tengah orang Israel; ganti semua yang terdahulu lahir dari kandungan, yakni semua anak sulung yang ada pada orang Israel, telah Kuambil mereka bagi-Ku.'),(3956,3,8,17,'Sebab semua anak sulung yang ada pada orang Israel, baik dari manusia maupun dari hewan, adalah kepunyaan-Ku; pada waktu Aku membunuh semua anak sulung di tanah Mesir, Aku telah menguduskan semuanya bagi-Ku.'),(3957,3,8,18,'Maka Aku mengambil orang Lewi ganti semua anak sulung yang ada pada orang Israel,'),(3958,3,8,19,'dan Aku menyerahkan orang Lewi dari tengah-tengah orang Israel sebagai pemberian kepada Harun dan anak-anaknya untuk melakukan segala pekerjaan jabatan bagi orang Israel di Kemah Pertemuan, dan untuk mengadakan pendamaian bagi orang Israel, supaya orang Israel jangan kena tulah apabila mereka mendekat ke tempat kudus.\"'),(3959,3,8,20,'Lalu Musa, Harun dan segenap umat Israel melakukan yang demikian kepada orang Lewi; tepat seperti yang diperintahkan TUHAN kepada Musa mengenai orang Lewi, demikianlah dilakukan orang Israel kepada mereka.'),(3960,3,8,21,'Orang Lewi itu menghapus dosa dari dirinya dan mencuci pakaian mereka, kemudian Harun mengunjukkan mereka sebagai persembahan unjukan di hadapan TUHAN, dan mengadakan pendamaian bagi mereka sambil mentahirkan mereka.'),(3961,3,8,22,'Sesudah itu masuklah orang Lewi untuk melakukan pekerjaan jabatan mereka di Kemah Pertemuan, di bawah pengawasan Harun dan anak-anaknya. Seperti yang diperintahkan TUHAN kepada Musa mengenai orang Lewi, demikianlah dilakukan kepada mereka.'),(3962,3,8,23,'TUHAN berfirman kepada Musa:'),(3963,3,8,24,'Inilah yang berlaku bagi orang Lewi: setiap orang yang berumur dua puluh lima tahun ke atas wajib bertugas, supaya ia bekerja pada Kemah Pertemuan,'),(3964,3,8,25,'tetapi jika ia berumur lima puluh tahun haruslah ia dibebaskan dari pekerjaan itu, sehingga tak usah ia bekerja lebih lama lagi.'),(3965,3,8,26,'Ia boleh membantu saudara-saudaranya di Kemah Pertemuan dalam menjalankan tugas mereka, tetapi tidak usah lagi ia menjabat pekerjaan itu. Demikianlah harus kaulakukan kepada orang Lewi mengenai tugas mereka.\"'),(3966,3,9,1,'TUHAN berfirman kepada Musa di padang gurun Sinai, pada bulan yang pertama tahun yang kedua sesudah mereka keluar dari tanah Mesir:'),(3967,3,9,2,'Orang Israel harus merayakan Paskah pada waktunya;'),(3968,3,9,3,'pada hari yang keempat belas bulan ini, pada waktu senja, haruslah kamu merayakannya pada waktu yang ditetapkan, menurut segala ketetapan dan peraturannya haruslah kamu merayakannya.\"'),(3969,3,9,4,'Lalu Musa menyuruh orang Israel merayakan Paskah.'),(3970,3,9,5,'Maka mereka merayakan Paskah pada bulan yang pertama, pada hari yang keempat belas bulan itu, pada waktu senja, di padang gurun Sinai; tepat seperti yang diperintahkan TUHAN kepada Musa, demikianlah dilakukan orang Israel.'),(3971,3,9,6,'Tetapi ada beberapa orang yang najis oleh karena mayat, sehingga tidak dapat merayakan Paskah pada hari itu. Mereka datang menghadap Musa dan Harun pada hari itu juga,'),(3972,3,9,7,'lalu berkata kepadanya: \"Sungguhpun kami najis oleh karena mayat, dengan dasar apakah kami dicegah mempersembahkan persembahan bagi TUHAN di tengah-tengah orang Israel pada waktu yang ditetapkan?\"'),(3973,3,9,8,'Lalu jawab Musa kepada mereka: \"Tunggulah dahulu, aku hendak mendengar apa yang akan diperintahkan TUHAN mengenai kamu.\"'),(3974,3,9,9,'Lalu berfirmanlah TUHAN kepada Musa:'),(3975,3,9,10,'Katakanlah kepada orang Israel: Apabila salah seorang di antara kamu atau keturunanmu najis oleh karena mayat, atau berada dalam perjalanan jauh, maka ia harus juga merayakan Paskah bagi TUHAN.'),(3976,3,9,11,'Pada bulan yang kedua, pada hari yang keempat belas, pada waktu senja, haruslah orang-orang itu merayakannya; beserta roti yang tidak beragi dan sayur pahit haruslah mereka memakannya.'),(3977,3,9,12,'Janganlah mereka meninggalkan sebagian dari padanya sampai pagi, dan satu tulangpun tidak boleh dipatahkan mereka. Menurut segala ketetapan Paskah haruslah mereka merayakannya.'),(3978,3,9,13,'Sebaliknya orang yang tidak najis, dan tidak dalam perjalanan, tetapi lalai merayakan Paskah, orang itu harus dilenyapkan dari antara orang-orang sebangsanya, sebab ia tidak mempersembahkan persembahan yang kepada TUHAN pada waktunya; orang itu akan menanggung akibat dosanya.'),(3979,3,9,14,'Apabila seorang asing yang telah menetap padamu hendak merayakan Paskah bagi TUHAN, maka haruslah ia merayakannya menurut segala ketetapan dan peraturan Paskah. Satu ketetapan harus berlaku bagi kamu, baik bagi orang asing maupun bagi orang Israel asli.\"'),(3980,3,9,15,'Pada hari didirikan Kemah Suci, maka awan itu menutupi Kemah Suci, kemah hukum Allah; dan pada waktu malam sampai pagi awan itu ada di atas Kemah Suci, kelihatan seperti api.'),(3981,3,9,16,'Demikianlah selalu terjadi: awan itu menutupi Kemah, dan pada waktu malam kelihatan seperti api.'),(3982,3,9,17,'Dan setiap kali awan itu naik dari atas Kemah, maka orang Israelpun berangkatlah, dan di tempat awan itu diam, di sanalah orang Israel berkemah.'),(3983,3,9,18,'Atas titah TUHAN orang Israel berangkat dan atas titah TUHAN juga mereka berkemah; selama awan itu diam di atas Kemah Suci, mereka tetap berkemah.'),(3984,3,9,19,'Apabila awan itu lama tinggal di atas Kemah Suci, maka orang Israel memelihara kewajibannya kepada TUHAN, dan tidaklah mereka berangkat.'),(3985,3,9,20,'Ada kalanya awan itu hanya tinggal beberapa hari di atas Kemah Suci; maka atas titah TUHAN mereka berkemah dan atas titah TUHAN juga mereka berangkat.'),(3986,3,9,21,'Ada kalanya awan itu tinggal dari petang sampai pagi; ketika awan itu naik pada waktu pagi, merekapun berangkatlah; baik pada waktu siang baik pada waktu malam, apabila awan itu naik, merekapun berangkatlah.'),(3987,3,9,22,'Berapa lamapun juga awan itu diam di atas Kemah Suci, baik dua hari, baik sebulan atau lebih lama, maka orang Israel tetap berkemah dan tidak berangkat; tetapi apabila awan itu naik, barulah mereka berangkat.'),(3988,3,9,23,'Atas titah TUHAN mereka berkemah dan atas titah TUHAN juga mereka berangkat; mereka memelihara kewajibannya kepada TUHAN, menurut titah TUHAN dengan perantaraan Musa.'),(3989,3,10,1,'TUHAN berfirman kepada Musa:'),(3990,3,10,2,'Buatlah dua nafiri dari perak. Dari perak tempaan harus kaubuat itu, supaya dipergunakan untuk memanggil umat Israel dan untuk menyuruh laskar-laskarnya berangkat.'),(3991,3,10,3,'Apabila kedua nafiri itu ditiup, segenap umat itu harus berkumpul kepadamu di depan pintu Kemah Pertemuan.'),(3992,3,10,4,'Jikalau hanya satu saja ditiup, maka para pemimpin, para kepala pasukan Israel harus berkumpul kepadamu.'),(3993,3,10,5,'Apabila kamu meniup tanda semboyan, maka haruslah berangkat laskar-laskar yang berkemah di sebelah timur;'),(3994,3,10,6,'apabila kamu meniup tanda semboyan kedua kalinya, maka haruslah berangkat laskar-laskar yang berkemah di sebelah selatan. Jadi tanda semboyan harus ditiup untuk menyuruh mereka berangkat;'),(3995,3,10,7,'tetapi untuk menyuruh jemaah itu berkumpul kamu harus meniup saja tanpa memberi tanda semboyan.'),(3996,3,10,8,'Nafiri-nafiri itu harus ditiup oleh anak-anak imam Harun; itulah yang harus menjadi ketetapan untuk selama-lamanya bagimu turun-temurun.'),(3997,3,10,9,'Dan apabila kamu maju berperang di negerimu melawan musuh yang menyesakkan kamu, kamu harus memberi tanda semboyan dengan nafiri, supaya kamu diingat di hadapan TUHAN, Allahmu, dan diselamatkan dari pada musuhmu.'),(3998,3,10,10,'Juga pada hari-hari kamu bersukaria, pada perayaan-perayaanmu dan pada bulan-bulan barumu haruslah kamu meniup nafiri itu pada waktu mempersembahkan korban-korban bakaranmu dan korban-korban keselamatanmu; maksudnya supaya kamu diingat di hadapan Allahmu; Akulah TUHAN, Allahmu.\"'),(3999,3,10,11,'Pada tahun yang kedua, pada bulan yang kedua, pada tanggal dua puluh bulan itu, naiklah awan itu dari atas Kemah Suci, tempat hukum Allah.'),(4000,3,10,12,'Lalu berangkatlah orang Israel dari padang gurun Sinai menurut aturan keberangkatan mereka, kemudian diamlah awan itu di padang gurun Paran.'),(4001,3,10,13,'Itulah pertama kali mereka berangkat menurut titah TUHAN dengan perantaraan Musa.'),(4002,3,10,14,'Terdahulu berangkatlah laskar yang di bawah panji-panji bani Yehuda menurut pasukan mereka; yang mengepalai laskar itu ialah Nahason bin Aminadab;'),(4003,3,10,15,'yang mengepalai laskar suku bani Isakhar ialah Netaneel bin Zuar;'),(4004,3,10,16,'yang mengepalai laskar suku bani Zebulon ialah Eliab bin Helon.'),(4005,3,10,17,'Sesudah itu Kemah Suci dibongkar, dan berangkatlah bani Gerson dan bani Merari yang mengangkat Kemah Suci itu.'),(4006,3,10,18,'Kemudian berangkatlah laskar yang di bawah panji-panji Ruben menurut pasukan mereka; yang mengepalai laskar itu ialah Elizur bin Syedeur;'),(4007,3,10,19,'yang mengepalai laskar suku bani Simeon ialah Selumiel bin Zurisyadai;'),(4008,3,10,20,'yang mengepalai laskar suku bani Gad ialah Elyasaf bin Rehuel.'),(4009,3,10,21,'Sesudah itu berangkatlah orang Kehat, yang mengangkat barang-barang tempat kudus; Kemah Suci sudah dipasang sebelum mereka datang.'),(4010,3,10,22,'Kemudian berangkatlah laskar yang di bawah panji-panji bani Efraim menurut pasukan mereka;'),(4011,3,10,23,'yang mengepalai laskar itu ialah Elisama bin Amihud; yang mengepalai laskar suku bani Manasye ialah Gamaliel bin Pedazur;'),(4012,3,10,24,'yang mengepalai laskar suku bani Benyamin ialah Abidan bin Gideoni.'),(4013,3,10,25,'Sebagai barisan penutup semua laskar itu berangkatlah laskar yang di bawah panji-panji bani Dan menurut pasukan mereka; yang mengepalai laskar itu ialah Ahiezer bin Amisyadai;'),(4014,3,10,26,'yang mengepalai laskar suku bani Asyer ialah Pagiel bin Okhran;'),(4015,3,10,27,'yang mengepalai laskar suku bani Naftali ialah Ahira bin Enan.'),(4016,3,10,28,'Itulah aturan keberangkatan orang Israel menurut pasukan mereka, ketika mereka berangkat.'),(4017,3,10,29,'Lalu berkatalah Musa kepada Hobab anak Rehuel orang Midian, mertua Musa: \"Kami berangkat ke tempat yang dimaksud TUHAN ketika Ia berfirman: Aku akan memberikannya kepadamu. Sebab itu ikutlah bersama-sama dengan kami, maka kami akan berbuat baik kepadamu, sebab TUHAN telah menjanjikan yang baik tentang Israel.\"'),(4018,3,10,30,'Tetapi jawabnya kepada Musa: \"Aku tidak ikut, melainkan aku hendak pergi ke negeriku dan kepada sanak saudaraku.\"'),(4019,3,10,31,'Kata Musa: \"Janganlah kiranya tinggalkan kami, sebab engkaulah yang tahu, bagaimana kami berkemah di padang gurun, maka engkau dapat menjadi penunjuk jalan bagi kami.'),(4020,3,10,32,'Jika engkau ikut bersama-sama dengan kami, maka kebaikan yang akan dilakukan TUHAN kepada kami akan kami lakukan juga kepadamu.\"'),(4021,3,10,33,'Lalu berangkatlah mereka dari gunung TUHAN dan berjalan tiga hari perjalanan jauhnya, sedang tabut perjanjian TUHAN berangkat di depan mereka dan berjalan tiga hari perjalanan jauhnya untuk mencari tempat perhentian bagi mereka.'),(4022,3,10,34,'Dan awan TUHAN ada di atas mereka pada siang hari, apabila mereka berangkat dari tempat perkemahan.'),(4023,3,10,35,'Apabila tabut itu berangkat, berkatalah Musa: \"Bangkitlah, TUHAN, supaya musuh-Mu berserak dan orang-orang yang membenci Engkau melarikan diri dari hadapan-Mu.\"'),(4024,3,10,36,'Dan apabila tabut itu berhenti, berkatalah ia: \"Kembalilah, TUHAN, kepada umat Israel yang beribu-ribu laksa ini.\"'),(4025,3,11,1,'Pada suatu kali bangsa itu bersungut-sungut di hadapan TUHAN tentang nasib buruk mereka, dan ketika TUHAN mendengarnya bangkitlah murka-Nya, kemudian menyalalah api TUHAN di antara mereka dan merajalela di tepi tempat perkemahan.'),(4026,3,11,2,'Lalu berteriaklah bangsa itu kepada Musa, dan Musa berdoa kepada TUHAN; maka padamlah api itu.'),(4027,3,11,3,'Sebab itu orang menamai tempat itu Tabera, karena telah menyala api TUHAN di antara mereka.'),(4028,3,11,4,'Orang-orang bajingan yang ada di antara mereka kemasukan nafsu rakus; dan orang Israelpun menangislah pula serta berkata: \"Siapakah yang akan memberi kita makan daging?'),(4029,3,11,5,'Kita teringat kepada ikan yang kita makan di Mesir dengan tidak bayar apa-apa, kepada mentimun dan semangka, bawang prei, bawang merah dan bawang putih.'),(4030,3,11,6,'Tetapi sekarang kita kurus kering, tidak ada sesuatu apapun, kecuali manna ini saja yang kita lihat.\"'),(4031,3,11,7,'Adapun manna itu seperti ketumbar dan kelihatannya seperti damar bedolah.'),(4032,3,11,8,'Bangsa itu berlari kian ke mari untuk memungutnya, lalu menggilingnya dengan batu kilangan atau menumbuknya dalam lumpang. Mereka memasaknya dalam periuk dan membuatnya menjadi roti bundar; rasanya seperti rasa panganan yang digoreng.'),(4033,3,11,9,'Dan apabila embun turun di tempat perkemahan pada waktu malam, maka turunlah juga manna di situ.'),(4034,3,11,10,'Ketika Musa mendengar bangsa itu, yaitu orang-orang dari setiap kaum, menangis di depan pintu kemahnya, bangkitlah murka TUHAN dengan sangat, dan hal itu dipandang jahat oleh Musa.'),(4035,3,11,11,'Lalu berkatalah Musa kepada TUHAN: \"Mengapa Kauperlakukan hamba-Mu ini dengan buruk dan mengapa aku tidak mendapat kasih karunia di mata-Mu, sehingga Engkau membebankan kepadaku tanggung jawab atas seluruh bangsa ini?'),(4036,3,11,12,'Akukah yang mengandung seluruh bangsa ini atau akukah yang melahirkannya, sehingga Engkau berkata kepadaku: Pangkulah dia seperti pak pengasuh memangku anak yang menyusu, berjalan ke tanah yang Kaujanjikan dengan bersumpah kepada nenek moyangnya?'),(4037,3,11,13,'Dari manakah aku mengambil daging untuk diberikan kepada seluruh bangsa ini? Sebab mereka menangis kepadaku dengan berkata: Berilah kami daging untuk dimakan.'),(4038,3,11,14,'Aku seorang diri tidak dapat memikul tanggung jawab atas seluruh bangsa ini, sebab terlalu berat bagiku.'),(4039,3,11,15,'Jika Engkau berlaku demikian kepadaku, sebaiknya Engkau membunuh aku saja, jika aku mendapat kasih karunia di mata-Mu, supaya aku tidak harus melihat celakaku.\"'),(4040,3,11,16,'Lalu berfirmanlah TUHAN kepada Musa: \"Kumpulkanlah di hadapan-Ku dari antara para tua-tua Israel tujuh puluh orang, yang kauketahui menjadi tua-tua bangsa dan pengatur pasukannya, kemudian bawalah mereka ke Kemah Pertemuan, supaya mereka berdiri di sana bersama-sama dengan engkau.'),(4041,3,11,17,'Maka Aku akan turun dan berbicara dengan engkau di sana, lalu sebagian dari Roh yang hinggap padamu itu akan Kuambil dan Kutaruh atas mereka, maka mereka bersama-sama dengan engkau akan memikul tanggung jawab atas bangsa itu, jadi tidak usah lagi engkau seorang diri memikulnya.'),(4042,3,11,18,'Tetapi kepada bangsa itu haruslah kaukatakan: Kuduskanlah dirimu untuk besok, maka kamu akan makan daging; sebab kamu telah menangis di hadapan TUHAN dengan berkata: Siapakah yang akan memberi kami makan daging? Begitu baik keadaan kita di Mesir, bukan? --TUHAN akan memberi kamu daging untuk dimakan.'),(4043,3,11,19,'Bukan hanya satu hari kamu akan memakannya, bukan dua hari, bukan lima hari, bukan sepuluh hari, bukan dua puluh hari,'),(4044,3,11,20,'tetapi genap sebulan lamanya, sampai keluar dari dalam hidungmu dan sampai kamu muak--karena kamu telah menolak TUHAN yang ada di tengah-tengah kamu dan menangis di hadapan-Nya dengan berkata: Untuk apakah kita keluar dari Mesir?\"'),(4045,3,11,21,'Tetapi kata Musa: \"Bangsa yang ada bersama aku ini berjumlah enam ratus ribu orang berjalan kaki, namun Engkau berfirman: Daging akan Kuberikan kepada mereka, dan genap sebulan lamanya mereka akan memakannya!'),(4046,3,11,22,'Dapatkah sekian banyak kambing domba dan lembu sapi disembelih bagi mereka, sehingga mereka mendapat cukup? Atau dapatkah ditangkap segala ikan di laut bagi mereka, sehingga mereka mendapat cukup?\"'),(4047,3,11,23,'Tetapi TUHAN menjawab Musa: \"Masakan kuasa TUHAN akan kurang untuk melakukan itu? Sekarang engkau akan melihat apakah firman-Ku terjadi kepadamu atau tidak!\"'),(4048,3,11,24,'Setelah Musa datang ke luar, disampaikannya firman TUHAN itu kepada bangsa itu. Ia mengumpulkan tujuh puluh orang dari para tua-tua bangsa itu dan menyuruh mereka berdiri di sekeliling kemah.'),(4049,3,11,25,'Lalu turunlah TUHAN dalam awan dan berbicara kepada Musa, kemudian diambil-Nya sebagian dari Roh yang hinggap padanya, dan ditaruh-Nya atas ketujuh puluh tua-tua itu; ketika Roh itu hinggap pada mereka, kepenuhanlah mereka seperti nabi, tetapi sesudah itu tidak lagi.'),(4050,3,11,26,'Masih ada dua orang tinggal di tempat perkemahan; yang seorang bernama Eldad, yang lain bernama Medad. Ketika Roh itu hinggap pada mereka--mereka itu termasuk orang-orang yang dicatat, tetapi tidak turut pergi ke kemah--maka kepenuhanlah mereka seperti nabi di tempat perkemahan.'),(4051,3,11,27,'Lalu berlarilah seorang muda memberitahukan kepada Musa: \"Eldad dan Medad kepenuhan seperti nabi di tempat perkemahan.\"'),(4052,3,11,28,'Maka menjawablah Yosua bin Nun, yang sejak mudanya menjadi abdi Musa: \"Tuanku Musa, cegahlah mereka!\"'),(4053,3,11,29,'Tetapi Musa berkata kepadanya: \"Apakah engkau begitu giat mendukung diriku? Ah, kalau seluruh umat TUHAN menjadi nabi, oleh karena TUHAN memberi Roh-Nya hinggap kepada mereka!\"'),(4054,3,11,30,'Kemudian kembalilah Musa ke tempat perkemahan, dia dan para tua-tua Israel.'),(4055,3,11,31,'Lalu bertiuplah angin yang dari TUHAN asalnya; dibawanyalah burung-burung puyuh dari sebelah laut, dan dihamburkannya ke atas tempat perkemahan dan di sekelilingnya, kira-kira sehari perjalanan jauhnya ke segala penjuru, dan kira-kira dua hasta tingginya dari atas muka bumi.'),(4056,3,11,32,'Lalu sepanjang hari dan sepanjang malam itu dan sepanjang hari esoknya bangkitlah bangsa itu mengumpulkan burung-burung puyuh itu--setiap orang sedikit-dikitnya mengumpulkan sepuluh homer--,kemudian mereka menyebarkannya lebar-lebar sekeliling tempat perkemahan.'),(4057,3,11,33,'Selagi daging itu ada di mulut mereka, sebelum dikunyah, maka bangkitlah murka TUHAN terhadap bangsa itu dan TUHAN memukul bangsa itu dengan suatu tulah yang sangat besar.'),(4058,3,11,34,'Sebab itu dinamailah tempat itu Kibrot-Taawa, karena di sanalah dikuburkan orang-orang yang bernafsu rakus.'),(4059,3,11,35,'Dari Kibrot-Taawa berangkatlah bangsa itu ke Hazerot dan mereka tinggal di situ.'),(4060,3,12,1,'Miryam serta Harun mengatai Musa berkenaan dengan perempuan Kush yang diambilnya, sebab memang ia telah mengambil seorang perempuan Kush.'),(4061,3,12,2,'Kata mereka: \"Sungguhkah TUHAN berfirman dengan perantaraan Musa saja? Bukankah dengan perantaraan kita juga Ia berfirman?\" Dan kedengaranlah hal itu kepada TUHAN.'),(4062,3,12,3,'Adapun Musa ialah seorang yang sangat lembut hatinya, lebih dari setiap manusia yang di atas muka bumi.'),(4063,3,12,4,'Lalu berfirmanlah TUHAN dengan tiba-tiba kepada Musa, Harun dan Miryam: \"Keluarlah kamu bertiga ke Kemah Pertemuan.\" Maka keluarlah mereka bertiga.'),(4064,3,12,5,'Lalu turunlah TUHAN dalam tiang awan, dan berdiri di pintu kemah itu, lalu memanggil Harun dan Miryam; maka tampillah mereka keduanya.'),(4065,3,12,6,'Lalu berfirmanlah Ia: \"Dengarlah firman-Ku ini. Jika di antara kamu ada seorang nabi, maka Aku, TUHAN menyatakan diri-Ku kepadanya dalam penglihatan, Aku berbicara dengan dia dalam mimpi.'),(4066,3,12,7,'Bukan demikian hamba-Ku Musa, seorang yang setia dalam segenap rumah-Ku.'),(4067,3,12,8,'Berhadap-hadapan Aku berbicara dengan dia, terus terang, bukan dengan teka-teki, dan ia memandang rupa TUHAN. Mengapakah kamu tidak takut mengatai hamba-Ku Musa?\"'),(4068,3,12,9,'Sebab itu bangkitlah murka TUHAN terhadap mereka, lalu pergilah Ia.'),(4069,3,12,10,'Dan ketika awan telah naik dari atas kemah, maka tampaklah Miryam kena kusta, putih seperti salju; ketika Harun berpaling kepada Miryam, maka dilihatnya, bahwa dia kena kusta!'),(4070,3,12,11,'Lalu kata Harun kepada Musa: \"Ah tuanku, janganlah kiranya timpakan kepada kami dosa ini, yang kami perbuat dalam kebodohan kami.'),(4071,3,12,12,'Janganlah kiranya dibiarkan dia sebagai anak gugur, yang pada waktu keluar dari kandungan ibunya sudah setengah busuk dagingnya.\"'),(4072,3,12,13,'Lalu berserulah Musa kepada TUHAN: \"Ya Allah, sembuhkanlah kiranya dia.\"'),(4073,3,12,14,'Kemudian berfirmanlah TUHAN kepada Musa: \"Sekiranya ayahnya meludahi mukanya, tidakkah ia mendapat malu selama tujuh hari? Biarlah dia selama tujuh hari dikucilkan ke luar tempat perkemahan, kemudian bolehlah ia diterima kembali.\"'),(4074,3,12,15,'Jadi dikucilkanlah Miryam ke luar tempat perkemahan tujuh hari lamanya, dan bangsa itu tidak berangkat sebelum Miryam diterima kembali.'),(4075,3,12,16,'Kemudian berangkatlah mereka dari Hazerot dan berkemah di padang gurun Paran.'),(4076,3,13,1,'TUHAN berfirman kepada Musa:'),(4077,3,13,2,'Suruhlah beberapa orang mengintai tanah Kanaan, yang akan Kuberikan kepada orang Israel; dari setiap suku nenek moyang mereka haruslah kausuruh seorang, semuanya pemimpin-pemimpin di antara mereka.'),(4078,3,13,3,'Lalu Musa menyuruh mereka dari padang gurun Paran, sesuai dengan titah TUHAN; semua orang itu adalah kepala-kepala di antara orang Israel.'),(4079,3,13,4,'Dan inilah nama-nama mereka: Dari suku Ruben: Syamua bin Zakur;'),(4080,3,13,5,'dari suku Simeon: Safat bin Hori;'),(4081,3,13,6,'dari suku Yehuda: Kaleb bin Yefune;'),(4082,3,13,7,'dari suku Isakhar: Yigal bin Yusuf;'),(4083,3,13,8,'dari suku Efraim: Hosea bin Nun;'),(4084,3,13,9,'dari suku Benyamin: Palti bin Rafu;'),(4085,3,13,10,'dari suku Zebulon: Gadiel bin Sodi;'),(4086,3,13,11,'dari suku Yusuf, yakni dari suku Manasye: Gadi bin Susi;'),(4087,3,13,12,'dari suku Dan: Amiel bin Gemali;'),(4088,3,13,13,'dari suku Asyer: Setur bin Mikhael;'),(4089,3,13,14,'dari suku Naftali: Nahbi bin Wofsi;'),(4090,3,13,15,'dari suku Gad: Guel bin Makhi.'),(4091,3,13,16,'Itulah nama orang-orang yang disuruh Musa untuk mengintai negeri itu; dan Musa menamai Hosea bin Nun itu Yosua.'),(4092,3,13,17,'Maka Musa menyuruh mereka untuk mengintai tanah Kanaan, katanya kepada mereka: \"Pergilah dari sini ke Tanah Negeb dan naiklah ke pegunungan,'),(4093,3,13,18,'dan amat-amatilah bagaimana keadaan negeri itu, apakah bangsa yang mendiaminya kuat atau lemah, apakah mereka sedikit atau banyak;'),(4094,3,13,19,'dan bagaimana negeri yang didiaminya, apakah baik atau buruk, bagaimana kota-kota yang didiaminya, apakah mereka diam di tempat-tempat yang terbuka atau di tempat-tempat yang berkubu,'),(4095,3,13,20,'dan bagaimana tanah itu, apakah gemuk atau kurus, apakah ada di sana pohon-pohonan atau tidak. Tabahkanlah hatimu dan bawalah sedikit dari hasil negeri itu.\" Waktu itu ialah musim hulu hasil anggur.'),(4096,3,13,21,'Mereka pergi ke sana, lalu mengintai negeri itu mulai dari padang gurun Zin sampai ke Rehob, ke jalan yang menuju ke Hamat.'),(4097,3,13,22,'Mereka berjalan melalui Tanah Negeb, lalu sampai ke Hebron; di sana ada Ahiman, Sesai dan Talmai, keturunan Enak. Hebron didirikan tujuh tahun lebih dahulu dari Soan di Mesir.'),(4098,3,13,23,'Ketika mereka sampai ke lembah Eskol, dipotong merekalah di sana suatu cabang dengan setandan buah anggurnya, lalu berdualah mereka menggandarnya; juga mereka membawa beberapa buah delima dan buah ara.'),(4099,3,13,24,'Tempat itu dinamai orang lembah Eskol, karena tandan buah anggur yang dipotong orang Israel di sana.'),(4100,3,13,25,'Sesudah lewat empat puluh hari pulanglah mereka dari pengintaian negeri itu,'),(4101,3,13,26,'dan langsung datang kepada Musa, Harun dan segenap umat Israel di Kadesh, di padang gurun Paran. Mereka membawa pulang kabar kepada keduanya dan kepada segenap umat itu dan memperlihatkan kepada sekaliannya hasil negeri itu.'),(4102,3,13,27,'Mereka menceritakan kepadanya: \"Kami sudah masuk ke negeri, ke mana kausuruh kami, dan memang negeri itu berlimpah-limpah susu dan madunya, dan inilah hasilnya.'),(4103,3,13,28,'Hanya, bangsa yang diam di negeri itu kuat-kuat dan kota-kotanya berkubu dan sangat besar, juga keturunan Enak telah kami lihat di sana.'),(4104,3,13,29,'Orang Amalek diam di Tanah Negeb, orang Het, orang Yebus dan orang Amori diam di pegunungan, orang Kanaan diam sepanjang laut dan sepanjang tepi sungai Yordan.\"'),(4105,3,13,30,'Kemudian Kaleb mencoba menenteramkan hati bangsa itu di hadapan Musa, katanya: \"Tidak! Kita akan maju dan menduduki negeri itu, sebab kita pasti akan mengalahkannya!\"'),(4106,3,13,31,'Tetapi orang-orang yang pergi ke sana bersama-sama dengan dia berkata: \"Kita tidak dapat maju menyerang bangsa itu, karena mereka lebih kuat dari pada kita.\"'),(4107,3,13,32,'Juga mereka menyampaikan kepada orang Israel kabar busuk tentang negeri yang diintai mereka, dengan berkata: \"Negeri yang telah kami lalui untuk diintai adalah suatu negeri yang memakan penduduknya, dan semua orang yang kami lihat di sana adalah orang-orang yang tinggi-tinggi perawakannya.'),(4108,3,13,33,'Juga kami lihat di sana orang-orang raksasa, orang Enak yang berasal dari orang-orang raksasa, dan kami lihat diri kami seperti belalang, dan demikian juga mereka terhadap kami.\"'),(4109,3,14,1,'Lalu segenap umat itu mengeluarkan suara nyaring dan bangsa itu menangis pada malam itu.'),(4110,3,14,2,'Bersungut-sungutlah semua orang Israel kepada Musa dan Harun; dan segenap umat itu berkata kepada mereka: \"Ah, sekiranya kami mati di tanah Mesir, atau di padang gurun ini!'),(4111,3,14,3,'Mengapakah TUHAN membawa kami ke negeri ini, supaya kami tewas oleh pedang, dan isteri serta anak-anak kami menjadi tawanan? Bukankah lebih baik kami pulang ke Mesir?\"'),(4112,3,14,4,'Dan mereka berkata seorang kepada yang lain: \"Baiklah kita mengangkat seorang pemimpin, lalu pulang ke Mesir.\"'),(4113,3,14,5,'Lalu sujudlah Musa dan Harun di depan mata seluruh jemaah Israel yang berkumpul di situ.'),(4114,3,14,6,'Tetapi Yosua bin Nun dan Kaleb bin Yefune, yang termasuk orang-orang yang telah mengintai negeri itu, mengoyakkan pakaiannya,'),(4115,3,14,7,'dan berkata kepada segenap umat Israel: \"Negeri yang kami lalui untuk diintai itu adalah luar biasa baiknya.'),(4116,3,14,8,'Jika TUHAN berkenan kepada kita, maka Ia akan membawa kita masuk ke negeri itu dan akan memberikannya kepada kita, suatu negeri yang berlimpah-limpah susu dan madunya.'),(4117,3,14,9,'Hanya, janganlah memberontak kepada TUHAN, dan janganlah takut kepada bangsa negeri itu, sebab mereka akan kita telan habis. Yang melindungi mereka sudah meninggalkan mereka, sedang TUHAN menyertai kita; janganlah takut kepada mereka.\"'),(4118,3,14,10,'Lalu segenap umat itu mengancam hendak melontari kedua orang itu dengan batu. Tetapi tampaklah kemuliaan TUHAN di Kemah Pertemuan kepada semua orang Israel.'),(4119,3,14,11,'TUHAN berfirman kepada Musa: \"Berapa lama lagi bangsa ini menista Aku, dan berapa lama lagi mereka tidak mau percaya kepada-Ku, sekalipun sudah ada segala tanda mujizat yang Kulakukan di tengah-tengah mereka!'),(4120,3,14,12,'Aku akan memukul mereka dengan penyakit sampar dan melenyapkan mereka, tetapi engkau akan Kubuat menjadi bangsa yang lebih besar dan lebih kuat dari pada mereka.\"'),(4121,3,14,13,'Lalu berkatalah Musa kepada TUHAN: \"Jikalau hal itu kedengaran kepada orang Mesir, padahal Engkau telah menuntun bangsa ini dengan kekuatan-Mu dari tengah-tengah mereka,'),(4122,3,14,14,'mereka akan berceritera kepada penduduk negeri ini, yang telah mendengar bahwa Engkau, TUHAN, ada di tengah-tengah bangsa ini, dan bahwa Engkau, TUHAN, menampakkan diri-Mu kepada mereka dengan berhadapan muka, waktu awan-Mu berdiri di atas mereka dan waktu Engkau berjalan mendahului mereka di dalam tiang awan pada waktu siang dan di dalam tiang api pada waktu malam.'),(4123,3,14,15,'Jadi jikalau Engkau membunuh bangsa ini sampai habis, maka bangsa-bangsa yang mendengar kabar tentang Engkau itu nanti berkata:'),(4124,3,14,16,'Oleh karena TUHAN tidak berkuasa membawa bangsa ini masuk ke negeri yang dijanjikan-Nya dengan bersumpah kepada mereka, maka Ia menyembelih mereka di padang gurun.'),(4125,3,14,17,'Jadi sekarang, biarlah kiranya kekuatan TUHAN itu nyata kebesarannya, seperti yang Kaufirmankan:'),(4126,3,14,18,'TUHAN itu berpanjangan sabar dan kasih setia-Nya berlimpah-limpah, Ia mengampuni kesalahan dan pelanggaran, tetapi sekali-kali tidak membebaskan orang yang bersalah dari hukuman, bahkan Ia membalaskan kesalahan bapa kepada anak-anaknya, kepada keturunan yang ketiga dan keempat.'),(4127,3,14,19,'Ampunilah kiranya kesalahan bangsa ini sesuai dengan kebesaran kasih setia-Mu, seperti Engkau telah mengampuni bangsa ini mulai dari Mesir sampai ke mari.\"'),(4128,3,14,20,'Berfirmanlah TUHAN: \"Aku mengampuninya sesuai dengan permintaanmu.'),(4129,3,14,21,'Hanya, demi Aku yang hidup dan kemuliaan TUHAN memenuhi seluruh bumi:'),(4130,3,14,22,'Semua orang yang telah melihat kemuliaan-Ku dan tanda-tanda mujizat yang Kuperbuat di Mesir dan di padang gurun, namun telah sepuluh kali mencobai Aku dan tidak mau mendengarkan suara-Ku,'),(4131,3,14,23,'pastilah tidak akan melihat negeri yang Kujanjikan dengan bersumpah kepada nenek moyang mereka! Semua yang menista Aku ini tidak akan melihatnya.'),(4132,3,14,24,'Tetapi hamba-Ku Kaleb, karena lain jiwa yang ada padanya dan ia mengikut Aku dengan sepenuhnya, akan Kubawa masuk ke negeri yang telah dimasukinya itu, dan keturunannya akan memilikinya.'),(4133,3,14,25,'Orang Amalek dan orang Kanaan diam di lembah. Sebab itu berpalinglah besok dan berangkatlah ke padang gurun, ke arah Laut Teberau.\"'),(4134,3,14,26,'Lagi berfirmanlah TUHAN kepada Musa dan Harun:'),(4135,3,14,27,'Berapa lama lagi umat yang jahat ini akan bersungut-sungut kepada-Ku? Segala sesuatu yang disungut-sungutkan orang Israel kepada-Ku telah Kudengar.'),(4136,3,14,28,'Katakanlah kepada mereka: Demi Aku yang hidup, demikianlah firman TUHAN, bahwasanya seperti yang kamu katakan di hadapan-Ku, demikianlah akan Kulakukan kepadamu.'),(4137,3,14,29,'Di padang gurun ini bangkai-bangkaimu akan berhantaran, yakni semua orang di antara kamu yang dicatat, semua tanpa terkecuali yang berumur dua puluh tahun ke atas, karena kamu telah bersungut-sungut kepada-Ku.'),(4138,3,14,30,'Bahwasanya kamu ini tidak akan masuk ke negeri yang dengan mengangkat sumpah telah Kujanjikan akan Kuberi kamu diami, kecuali Kaleb bin Yefune dan Yosua bin Nun!'),(4139,3,14,31,'Tentang anak-anakmu yang telah kamu katakan: Mereka akan menjadi tawanan, merekalah yang akan Kubawa masuk, supaya mereka mengenal negeri yang telah kamu hinakan itu.'),(4140,3,14,32,'Tetapi mengenai kamu, bangkai-bangkaimu akan berhantaran di padang gurun ini,'),(4141,3,14,33,'dan anak-anakmu akan mengembara sebagai penggembala di padang gurun empat puluh tahun lamanya dan akan menanggung akibat ketidaksetiaan, sampai bangkai-bangkaimu habis di padang gurun.'),(4142,3,14,34,'Sesuai dengan jumlah hari yang kamu mengintai negeri itu, yakni empat puluh hari, satu hari dihitung satu tahun, jadi empat puluh tahun lamanya kamu harus menanggung akibat kesalahanmu, supaya kamu tahu rasanya, jika Aku berbalik dari padamu:'),(4143,3,14,35,'Aku, TUHAN, yang berkata demikian. Sesungguhnya Aku akan melakukan semuanya itu kepada segenap umat yang jahat ini yang telah bersepakat melawan Aku. Di padang gurun ini mereka akan habis dan di sinilah mereka akan mati.\"'),(4144,3,14,36,'Adapun orang-orang yang telah disuruh Musa untuk mengintai negeri itu, yang sudah pulang dan menyebabkan segenap umat itu bersungut-sungut kepada Musa dengan menyampaikan kabar busuk tentang negeri itu,'),(4145,3,14,37,'orang-orang itu mati, kena tulah di hadapan TUHAN.'),(4146,3,14,38,'Tetapi yang tinggal hidup dari orang-orang yang telah pergi mengintai negeri itu hanyalah Yosua bin Nun dan Kaleb bin Yefune.'),(4147,3,14,39,'Setelah Musa menyampaikan perkataan ini kepada semua orang Israel, maka berkabunglah bangsa itu dengan sangat.'),(4148,3,14,40,'Dan keesokan harinya bangunlah mereka pagi-pagi hendak naik ke puncak gunung sambil berkata: \"Sekarang kita hendak maju ke negeri yang difirmankan TUHAN itu; memang kita telah berbuat dosa.\"'),(4149,3,14,41,'Tetapi kata Musa: \"Mengapakah kamu hendak melanggar titah TUHAN? Hal itu tidak akan berhasil.'),(4150,3,14,42,'Janganlah maju, sebab TUHAN tidak ada di tengah-tengahmu, supaya jangan kamu dikalahkan oleh musuhmu,');
insert  into `kitab_detail`(`id`,`id_kitab`,`pasal`,`ayat`,`teks`) values (4151,3,14,43,'sebab orang Amalek dan orang Kanaan ada di sana di depanmu dan kamu akan tewas oleh pedang; dari sebab kamu berbalik membelakangi TUHAN, maka TUHAN tidak akan menyertai kamu.\"'),(4152,3,14,44,'Meskipun demikian, mereka nekat naik ke puncak gunung itu, tetapi tabut perjanjian TUHAN dan Musa juga tidaklah meninggalkan tempat perkemahan.'),(4153,3,14,45,'Lalu turunlah orang Amalek dan orang Kanaan yang mendiami pegunungan itu dan menyerang mereka; kemudian orang-orang itu mencerai-beraikan mereka sampai ke Horma.'),(4154,3,15,1,'TUHAN berfirman kepada Musa:'),(4155,3,15,2,'Berbicaralah kepada orang Israel dan katakanlah kepada mereka: Apabila kamu masuk ke negeri yang akan Kuberikan kepadamu menjadi tempat kediamanmu,'),(4156,3,15,3,'dan kamu hendak mempersembahkan korban api-apian bagi TUHAN, dari lembu sapi atau kambing domba, baik korban bakaran atau korban sembelihan, baik untuk membayar suatu nazar khusus, atau sebagai persembahan sukarela atau pada waktu perayaan-perayaanmu, dan dengan demikian menyediakan bau yang menyenangkan bagi TUHAN,'),(4157,3,15,4,'maka orang yang mempersembahkan persembahannya itu kepada TUHAN, haruslah mempersembahkan sebagai korban sajian sepersepuluh efa tepung yang terbaik, diolah dengan seperempat hin minyak.'),(4158,3,15,5,'Dan beserta korban bakaran atau korban sembelihan itu engkau harus juga mempersembahkan seperempat hin anggur sebagai korban curahan, untuk setiap ekor domba yang dipersembahkan.'),(4159,3,15,6,'Tetapi jikalau persembahanmu itu seekor domba jantan, engkau harus mempersembahkan sebagai korban sajian dua persepuluh efa tepung yang terbaik, diolah dengan sepertiga hin minyak,'),(4160,3,15,7,'dan sebagai korban curahan haruslah kaupersembahkan sepertiga hin anggur, menjadi bau yang menyenangkan bagi TUHAN.'),(4161,3,15,8,'Dan apabila engkau mengolah seekor lembu, sebagai korban bakaran atau sebagai korban sembelihan, baik untuk membayar suatu nazar khusus maupun sebagai korban keselamatan bagi TUHAN,'),(4162,3,15,9,'maka beserta lembu itu haruslah dipersembahkan sebagai korban sajian tiga persepuluh efa tepung yang terbaik, diolah dengan setengah hin minyak,'),(4163,3,15,10,'dan sebagai korban curahan haruslah kaupersembahkan setengah hin anggur. Itulah korban api-apian yang baunya menyenangkan bagi TUHAN.'),(4164,3,15,11,'Demikianlah harus diperbuat untuk setiap ekor lembu dan untuk setiap ekor domba jantan dan untuk setiap ekor domba atau kambing.'),(4165,3,15,12,'Berapapun jumlah hewan yang kamu olah, untuk setiap hewan itu harus kamu perbuat demikian juga.'),(4166,3,15,13,'Setiap orang Israel asli haruslah berbuat demikian, apabila ia mempersembahkan korban api-apian yang baunya menyenangkan bagi TUHAN.'),(4167,3,15,14,'Dan apabila seorang asing telah menetap padamu, atau seorang lain yang tinggal di antara kamu atau di antara keturunanmu kelak, hendak mempersembahkan korban api-apian yang baunya menyenangkan bagi TUHAN, maka seperti yang kamu perbuat, demikianlah harus diperbuatnya.'),(4168,3,15,15,'Mengenai jemaah itu, haruslah ada satu ketetapan bagi kamu dan bagi orang asing yang tinggal padamu; itulah suatu ketetapan untuk selama-lamanya bagi kamu turun-temurun: kamu dan orang asing haruslah sama di hadapan TUHAN.'),(4169,3,15,16,'Satu hukum dan satu peraturan berlaku bagi kamu dan bagi orang asing yang tinggal padamu.\"'),(4170,3,15,17,'Lagi berfirmanlah TUHAN kepada Musa:'),(4171,3,15,18,'Berbicaralah kepada orang Israel dan katakanlah kepada mereka: Apabila kamu masuk ke negeri, ke mana kamu akan Kubawa,'),(4172,3,15,19,'maka apabila kamu makan roti hasil negeri itu haruslah kamu mempersembahkan persembahan khusus bagi TUHAN.'),(4173,3,15,20,'Tepung jelaimu yang mula-mula haruslah kamu persembahkan sebagai persembahan khusus berupa roti bundar; sama seperti persembahan khusus dari hasil tempat pengirikanmu, demikianlah harus kamu mempersembahkanny'),(4174,3,15,21,'Dari tepung jelaimu yang mula-mula haruslah kamu menyerahkan persembahan khusus kepada TUHAN, turun-temurun.\"'),(4175,3,15,22,'Apabila kamu dengan tidak sengaja melalaikan salah satu dari segala perintah ini, yang telah difirmankan TUHAN kepada Musa,'),(4176,3,15,23,'yakni dari segala yang diperintahkan TUHAN kepadamu dengan perantaraan Musa, mulai dari hari TUHAN memberikan perintah-perintah-Nya dan seterusnya turun-temurun,'),(4177,3,15,24,'dan apabila hal itu diperbuat di luar pengetahuan umat ini, tidak dengan sengaja, maka haruslah segenap umat mengolah seekor lembu jantan muda sebagai korban bakaran menjadi bau yang menyenangkan bagi TUHAN, serta dengan korban sajiannya dan korban curahannya, sesuai dengan peraturan; juga seekor kambing jantan sebagai korban penghapus dosa.'),(4178,3,15,25,'Maka haruslah imam mengadakan pendamaian bagi segenap umat Israel, sehingga mereka beroleh pengampunan, sebab hal itu terjadi tidak dengan sengaja, dan karena mereka telah membawa persembahan-persembahan mereka sebagai korban api-apian bagi TUHAN, juga korban penghapus dosa mereka di hadapan TUHAN, karena hal yang tidak disengaja itu.'),(4179,3,15,26,'Segenap umat Israel akan beroleh pengampunan, juga orang asing yang tinggal di tengah-tengahmu, karena hal itu dilakukan oleh seluruh bangsa itu dengan tidak sengaja.'),(4180,3,15,27,'Apabila satu orang saja berbuat dosa dengan tidak sengaja, maka haruslah ia mempersembahkan kambing betina berumur setahun sebagai korban penghapus dosa;'),(4181,3,15,28,'dan imam haruslah mengadakan pendamaian di hadapan TUHAN bagi orang yang dengan tidak sengaja berbuat dosa itu, sehingga orang itu beroleh pengampunan karena telah diadakan pendamaian baginya.'),(4182,3,15,29,'Baik bagi orang Israel asli maupun bagi orang asing yang tinggal di tengah-tengah kamu, satu hukum saja berlaku bagi mereka berkenaan dengan orang yang berbuat dosa dengan tidak sengaja.'),(4183,3,15,30,'Tetapi orang yang berbuat sesuatu dengan sengaja, baik orang Israel asli, baik orang asing, orang itu menjadi penista TUHAN, ia harus dilenyapkan dari tengah-tengah bangsanya,'),(4184,3,15,31,'sebab ia telah memandang hina terhadap firman TUHAN dan merombak perintah-Nya; pastilah orang itu dilenyapkan, kesalahannya akan tertimpa atasnya.\"'),(4185,3,15,32,'Ketika orang Israel ada di padang gurun, didapati merekalah seorang yang mengumpulkan kayu api pada hari Sabat.'),(4186,3,15,33,'Lalu orang-orang yang mendapati dia sedang mengumpulkan kayu api itu, menghadapkan dia kepada Musa dan Harun dan segenap umat itu.'),(4187,3,15,34,'Orang itu dimasukkan dalam tahanan, oleh karena belum ditentukan apa yang harus dilakukan kepadanya.'),(4188,3,15,35,'Lalu berfirmanlah TUHAN kepada Musa: \"Orang itu pastilah dihukum mati; segenap umat Israel harus melontari dia dengan batu di luar tempat perkemahan.\"'),(4189,3,15,36,'Lalu segenap umat menggiring dia ke luar tempat perkemahan, kemudian dia dilontari dengan batu, sehingga ia mati, seperti yang difirmankan TUHAN kepada Musa.'),(4190,3,15,37,'TUHAN berfirman kepada Musa:'),(4191,3,15,38,'Berbicaralah kepada orang Israel dan katakanlah kepada mereka, bahwa mereka harus membuat jumbai-jumbai pada punca baju mereka, turun-temurun, dan dalam jumbai-jumbai punca itu haruslah dibubuh benang ungu kebiru-biruan.'),(4192,3,15,39,'Maka jumbai itu akan mengingatkan kamu, apabila kamu melihatnya, kepada segala perintah TUHAN, sehingga kamu melakukannya dan tidak lagi menuruti hatimu atau matamu sendiri, seperti biasa kamu perbuat dalam ketidaksetiaanmu terhadap TUHAN.'),(4193,3,15,40,'Maksudnya supaya kamu mengingat dan melakukan segala perintah-Ku dan menjadi kudus bagi Allahmu.'),(4194,3,15,41,'Akulah TUHAN, Allahmu, yang telah membawa kamu keluar dari tanah Mesir, supaya Aku menjadi Allah bagimu; Akulah TUHAN, Allahmu.\"'),(4195,3,16,1,'Korah bin Yizhar bin Kehat bin Lewi, beserta Datan dan Abiram, anak-anak Eliab, dan On bin Pelet, ketiganya orang Ruben, mengajak orang-orang'),(4196,3,16,2,'untuk memberontak melawan Musa, beserta dua ratus lima puluh orang Israel, pemimpin-pemimpin umat itu, yaitu orang-orang yang dipilih oleh rapat, semuanya orang-orang yang kenamaan.'),(4197,3,16,3,'Maka mereka berkumpul mengerumuni Musa dan Harun, serta berkata kepada keduanya: \"Sekarang cukuplah itu! Segenap umat itu adalah orang-orang kudus, dan TUHAN ada di tengah-tengah mereka. Mengapakah kamu meninggi-ninggikan diri di atas jemaah TUHAN?\"'),(4198,3,16,4,'Ketika Musa mendengar hal itu, sujudlah ia.'),(4199,3,16,5,'Dan ia berkata kepada Korah dan segenap kumpulannya: \"Besok pagi TUHAN akan memberitahukan, siapa kepunyaan-Nya, dan siapa yang kudus, dan Ia akan memperbolehkan orang itu mendekat kepada-Nya; orang yang akan dipilih-Nya akan diperbolehkan-Nya mendekat kepada-Nya.'),(4200,3,16,6,'Perbuatlah begini: ambillah perbaraan-perbaraan, hai Korah, dan kamu segenap kumpulannya,'),(4201,3,16,7,'bubuhlah api ke dalamnya dan taruhlah ukupan di atasnya, di hadapan TUHAN pada esok hari, dan orang yang akan dipilih TUHAN, dialah yang kudus. Cukuplah itu, hai orang-orang Lewi!\"'),(4202,3,16,8,'Lalu berkatalah Musa kepada Korah: \"Cobalah dengar, hai orang-orang Lewi!'),(4203,3,16,9,'Belum cukupkah bagimu, bahwa kamu dipisahkan oleh Allah Israel dari umat Israel dan diperbolehkan mendekat kepada-Nya, supaya kamu melakukan pekerjaan pada Kemah Suci TUHAN dan bertugas bagi umat itu untuk melayani mereka,'),(4204,3,16,10,'dan bahwa engkau diperbolehkan mendekat bersama-sama dengan semua saudaramu bani Lewi? Dan sekarang mau pula kamu menuntut pangkat imam lagi?'),(4205,3,16,11,'Sebab itu, engkau ini dengan segenap kumpulanmu, kamu bersepakat melawan TUHAN. Karena siapakah Harun, sehingga kamu bersungut-sungut kepadanya?\"'),(4206,3,16,12,'Adapun Musa telah menyuruh orang untuk memanggil Datan dan Abiram, anak-anak Eliab, tetapi jawab mereka: \"Kami tidak mau datang.'),(4207,3,16,13,'Belum cukupkah, bahwa engkau memimpin kami keluar dari suatu negeri yang berlimpah-limpah susu dan madunya untuk membiarkan kami mati di padang gurun, sehingga masih juga engkau menjadikan dirimu tuan atas kami?'),(4208,3,16,14,'Sungguh, engkau tidak membawa kami ke negeri yang berlimpah-limpah susu dan madunya, ataupun memberikan kepada kami ladang-ladang dan kebun-kebun anggur sebagai milik pusaka. Masakan engkau dapat mengelabui mata orang-orang ini? Kami tidak mau datang.\"'),(4209,3,16,15,'Lalu sangat marahlah Musa dan ia berkata kepada TUHAN: \"Janganlah perhatikan segala persembahan mereka. Belum pernah kuambil satu ekor keledaipun dari mereka, dan belum pernah kulakukan yang jahat kepada seseorangpun dari mereka.\"'),(4210,3,16,16,'Lalu berkatalah Musa kepada Korah: \"Engkau ini dengan segenap kumpulanmu harus menghadap TUHAN, engkau dan mereka dan Harun, pada esok hari.'),(4211,3,16,17,'Baiklah kamu masing-masing membawa perbaraannya membubuh ukupan di atasnya, lalu kamu mempersembahkan masing-masing perbaraannya ke hadapan TUHAN, dua ratus lima puluh perbaraan; juga engkau ini dan Harun masing-masing harus membawa perbaraannya.\"'),(4212,3,16,18,'Maka mereka masing-masing membawa perbaraannya, membubuh api ke dalamnya, menaruh ukupan di atasnya, lalu berdirilah mereka di depan pintu Kemah Pertemuan, juga Musa dan Harun.'),(4213,3,16,19,'Ketika Korah mengumpulkan segenap umat itu melawan mereka berdua di depan pintu Kemah Pertemuan, tampaklah kemuliaan TUHAN kepada segenap umat itu.'),(4214,3,16,20,'Lalu berfirmanlah TUHAN kepada Musa dan Harun:'),(4215,3,16,21,'Pisahkanlah dirimu dari tengah-tengah umat ini, supaya Kuhancurkan mereka dalam sekejap mata.'),(4216,3,16,22,'Tetapi sujudlah mereka berdua dan berkata: \"Ya Allah, Allah dari roh segala makhluk! Satu orang saja berdosa, masakan Engkau murka terhadap segenap perkumpulan ini?\"'),(4217,3,16,23,'Maka berfirmanlah TUHAN kepada Musa:'),(4218,3,16,24,'Katakanlah kepada umat itu: Pergilah dari sekeliling tempat kediaman Korah, Datan dan Abiram.'),(4219,3,16,25,'Lalu pergilah Musa kepada Datan dan Abiram, dan para tua-tua Israel mengikuti dia.'),(4220,3,16,26,'Berkatalah ia kepada umat itu: \"Baiklah kamu menjauh dari kemah orang-orang fasik ini dan janganlah kamu kena kepada sesuatu apapun dari kepunyaan mereka, supaya kamu jangan mati lenyap oleh karena segala dosa mereka.\"'),(4221,3,16,27,'Maka pergilah mereka dari sekeliling tempat kediaman Korah, Datan dan Abiram. Keluarlah Datan dan Abiram, lalu berdiri di depan pintu kemah mereka bersama-sama dengan isterinya, para anaknya dan anak-anak yang kecil.'),(4222,3,16,28,'Sesudah itu berkatalah Musa: \"Dari hal inilah kamu akan tahu, bahwa aku diutus TUHAN untuk melakukan segala perbuatan ini, dan hal itu bukanlah dari hatiku sendiri:'),(4223,3,16,29,'jika orang-orang ini nanti mati seperti matinya setiap manusia, dan mereka mengalami yang dialami setiap manusia, maka aku tidak diutus TUHAN.'),(4224,3,16,30,'Tetapi, jika TUHAN akan menjadikan sesuatu yang belum pernah terjadi, dan tanah mengangakan mulutnya dan menelan mereka beserta segala kepunyaan mereka, sehingga mereka hidup-hidup turun ke dunia orang mati, maka kamu akan tahu, bahwa orang-orang ini telah menista TUHAN.\"'),(4225,3,16,31,'Baru saja ia selesai mengucapkan segala perkataan itu, maka terbelahlah tanah yang di bawah mereka,'),(4226,3,16,32,'dan bumi membuka mulutnya dan menelan mereka dengan seisi rumahnya dan dengan semua orang yang ada pada Korah dan dengan segala harta milik mereka.'),(4227,3,16,33,'Demikianlah mereka dengan semua orang yang ada pada mereka turun hidup-hidup ke dunia orang mati; dan bumi menutupi mereka, sehingga mereka binasa dari tengah-tengah jemaah itu.'),(4228,3,16,34,'Dan semua orang Israel yang di sekeliling mereka berlarian mendengar teriak mereka, sebab kata mereka: \"Jangan-jangan bumi menelan kita juga!\"'),(4229,3,16,35,'Lagi keluarlah api, berasal dari pada TUHAN, lalu memakan habis kedua ratus lima puluh orang yang mempersembahkan ukupan itu.'),(4230,3,16,36,'TUHAN berfirman kepada Musa:'),(4231,3,16,37,'Katakanlah kepada Eleazar, anak imam Harun, supaya ia mengangkat perbaraan-perbaraan dari antara kebakaran itu, lalu hamburkanlah api itu jauh-jauh, karena semuanya itu kudus,'),(4232,3,16,38,'yakni perbaraan orang-orang berdosa yang telah membayarkan nyawanya, kemudian semuanya itu harus ditempa tipis-tipis menjadi salut mezbah, sebab telah dibawa ke hadapan TUHAN oleh orang-orang itu, jadi semuanya itu kudus; dengan demikian hal itu menjadi tanda bagi orang Israel.\"'),(4233,3,16,39,'Maka imam Eleazar mengambil perbaraan-perbaraan tembaga yang telah dibawa oleh orang-orang yang terbakar itu, lalu ditempa menjadi salut mezbah.'),(4234,3,16,40,'Itu menjadi suatu peringatan bagi orang Israel, supaya jangan tampil orang awam yang bukan dari keturunan Harun untuk membakar ukupan di hadapan TUHAN, dan jangan ia menjadi seperti Korah dan kumpulannya--seperti yang difirmankan TUHAN kepadanya dengan perantaraan Musa.'),(4235,3,16,41,'Tetapi pada keesokan harinya bersungut-sungutlah segenap umat Israel kepada Musa dan Harun, kata mereka: \"Kamu telah membunuh umat TUHAN.\"'),(4236,3,16,42,'Ketika umat itu berkumpul melawan Musa dan Harun, dan mereka memalingkan mukanya ke arah Kemah Pertemuan, maka kelihatanlah awan itu menutupinya dan tampaklah kemuliaan TUHAN.'),(4237,3,16,43,'Lalu pergilah Musa dan Harun ke depan Kemah Pertemuan.'),(4238,3,16,44,'Maka berfirmanlah TUHAN kepada Musa:'),(4239,3,16,45,'Pergilah dari tengah-tengah umat ini, supaya Kuhancurkan mereka dalam sekejap mata.'),(4240,3,16,46,'Berkatalah Musa kepada Harun: \"Ambillah perbaraan, bubuhlah api ke dalamnya dari atas mezbah, dan taruhlah ukupan, dan pergilah dengan segera kepada umat itu dan adakanlah pendamaian bagi mereka, sebab murka TUHAN telah berkobar, dan tulah sedang mulai.\"'),(4241,3,16,47,'Maka Harun mengambil perbaraan, seperti yang dikatakan Musa, dan berlarilah ia ke tengah-tengah jemaah itu, dan tampaklah tulah telah mulai di antara bangsa itu; lalu dibubuhnyalah ukupan dan diadakannyalah pendamaian bagi bangsa itu.'),(4242,3,16,48,'Ketika ia berdiri di antara orang-orang mati dan orang-orang hidup, berhentilah tulah itu.'),(4243,3,16,49,'Dan mereka yang mati kena tulah itu ada empat belas ribu tujuh ratus orang banyaknya, belum terhitung orang-orang yang mati karena perkara Korah.'),(4244,3,16,50,'Ketika Harun kembali kepada Musa di depan pintu Kemah Pertemuan, tulah itu telah berhenti.'),(4245,3,17,1,'TUHAN berfirman kepada Musa:'),(4246,3,17,2,'Katakanlah kepada orang Israel dan suruhlah mereka memberikan kepadamu satu tongkat untuk setiap suku. Semua pemimpin mereka harus memberikannya, suku demi suku, seluruhnya dua belas tongkat. Lalu tuliskanlah nama setiap pemimpin pada tongkatnya.'),(4247,3,17,3,'Pada tongkat Lewi harus kautuliskan nama Harun. Bagi setiap kepala suku harus ada satu tongkat.'),(4248,3,17,4,'Kemudian haruslah kauletakkan semuanya itu di dalam Kemah Pertemuan di hadapan tabut hukum, tempat Aku biasa bertemu dengan kamu.'),(4249,3,17,5,'Dan orang yang Kupilih, tongkat orang itulah akan bertunas; demikianlah Aku hendak meredakan sungut-sungut yang diucapkan mereka kepada kamu, sehingga tidak usah Kudengar lagi.\"'),(4250,3,17,6,'Setelah Musa berbicara kepada orang Israel, maka semua pemimpin mereka memberikan kepadanya satu tongkat dari setiap pemimpin, menurut suku-suku mereka, dua belas tongkat, dan tongkat Harun ada di antara tongkat-tongkat itu.'),(4251,3,17,7,'Musa meletakkan tongkat-tongkat itu di hadapan TUHAN dalam kemah hukum Allah.'),(4252,3,17,8,'Ketika Musa keesokan harinya masuk ke dalam kemah hukum itu, maka tampaklah tongkat Harun dari keturunan Lewi telah bertunas, mengeluarkan kuntum, mengembangkan bunga dan berbuahkan buah badam.'),(4253,3,17,9,'Kemudian Musa membawa semua tongkat itu keluar dari hadapan TUHAN kepada seluruh orang Israel; mereka melihatnya lalu mengambil tongkatnya masing-masing.'),(4254,3,17,10,'TUHAN berfirman kepada Musa: \"Kembalikanlah tongkat Harun ke hadapan tabut hukum untuk disimpan menjadi tanda bagi orang-orang durhaka, sehingga engkau mengakhiri sungut-sungut mereka dan tidak Kudengar lagi, supaya mereka jangan mati.\"'),(4255,3,17,11,'Dan Musa berbuat demikian; seperti yang diperintahkan TUHAN kepadanya, demikianlah diperbuatnya.'),(4256,3,17,12,'Tetapi orang Israel berkata kepada Musa: \"Sesungguhnya kami akan mati, kami akan binasa, kami semuanya akan binasa.'),(4257,3,17,13,'Siapapun juga yang mendekat ke Kemah Suci TUHAN, niscayalah ia akan mati. Haruskah kami habis binasa?\"'),(4258,3,18,1,'TUHAN berfirman kepada Harun: \"Engkau ini dan anak-anakmu beserta seluruh sukumu haruslah menanggung akibat setiap kesalahan terhadap tempat kudus; sedang hanya engkau beserta anak-anakmulah yang harus menanggung akibat setiap kesalahan yang dilakukan dalam jabatanmu sebagai imam.'),(4259,3,18,2,'Suruhlah juga saudara-saudaramu, suku Lewi, suku bapa leluhurmu, mendekat bersama-sama dengan engkau, supaya mereka menggabungkan diri kepadamu dan melayani engkau, apabila engkau ini beserta anak-anakmu ada di depan kemah hukum.'),(4260,3,18,3,'Mereka harus melakukan kewajiban mereka kepadamu, dan kewajiban mereka mengenai kemah seluruhnya; hanya kepada perkakas tempat kudus dan kepada mezbah janganlah mereka mendekat, nanti mereka mati, baik mereka maupun kamu.'),(4261,3,18,4,'Mereka harus menggabungkan diri kepadamu dan melakukan kewajiban mereka mengenai Kemah Pertemuan sesuai dengan segala pekerjaan pada kemah itu; tetapi orang awam jangan mendekat kepadamu.'),(4262,3,18,5,'Dan kamu ini haruslah melakukan kewajibanmu mengenai tempat kudus dan kewajibanmu mengenai mezbah, supaya orang Israel jangan lagi tertimpa oleh murka.'),(4263,3,18,6,'Sesungguhnya Aku ini telah mengambil saudara-saudaramu, orang Lewi, dari tengah-tengah orang Israel sebagai pemberian kepadamu, sebagai orang-orang yang diserahkan kepada TUHAN, untuk melakukan pekerjaan pada Kemah Pertemuan;'),(4264,3,18,7,'tetapi engkau ini beserta anak-anakmu harus memegang jabatanmu sebagai imam dalam segala hal yang berkenaan dengan mezbah dan dengan segala sesuatu yang ada di belakang tabir, dan kamu harus mengerjakannya; sebagai suatu jabatan pemberian Aku memberikan kepadamu jabatanmu sebagai imam itu; tetapi orang awam yang mendekat harus dihukum mati.\"'),(4265,3,18,8,'Lagi berfirmanlah TUHAN kepada Harun: \"Sesungguhnya Aku ini telah menyerahkan kepadamu pemeliharaan persembahan-persembahan khusus yang kepada-Ku; semua persembahan kudus orang Israel Kuberikan kepadamu dan kepada anak-anakmu sebagai bagianmu; itulah suatu ketetapan untuk selama-lamanya.'),(4266,3,18,9,'Inilah bagianmu dari segala persembahan-persembahan yang maha kudus itu, yaitu dari bagian yang tidak harus dibakar: segala persembahan mereka yang berupa korban sajian, korban penghapus dosa dan korban penebus salah, yang dibayar mereka kepada-Ku; itulah bagian maha kudus yang menjadi bagianmu dan bagian anak-anakmu.'),(4267,3,18,10,'Sebagai bagian maha kudus haruslah kamu memakannya; semua orang laki-laki boleh memakannya; haruslah itu bagian kudus bagimu.'),(4268,3,18,11,'Dan inipun adalah bagianmu: persembahan khusus dari pemberian mereka yang lain, termasuk segala persembahan unjukan orang Israel; semuanya itu Kuberikan kepadamu dan kepada anak-anakmu laki-laki dan perempuan bersama-sama dengan engkau; itulah suatu ketetapan untuk selama-lamanya. Setiap orang yang tahir dari seisi rumahmu boleh memakannya.'),(4269,3,18,12,'Segala yang terbaik dari minyak dan segala yang terbaik dari anggur dan dari gandum, yakni yang sebagai hasil pertamanya dipersembahkan mereka kepada TUHAN, Aku berikan kepadamu.'),(4270,3,18,13,'Hulu hasil dari segala yang tumbuh di tanahnya yang dipersembahkan mereka kepada TUHAN adalah juga bagianmu; setiap orang yang tahir dari seisi rumahmu boleh memakannya.'),(4271,3,18,14,'Semua yang dikhususkan bagi TUHAN di antara orang Israel menjadi bagianmu.'),(4272,3,18,15,'Semua yang terdahulu lahir dari kandungan segala yang hidup, yang dipersembahkan mereka kepada TUHAN, baik dari manusia maupun dari binatang, adalah bagianmu; hanya haruslah kamu menebus anak sulung manusia, juga anak sulung binatang yang najis haruslah kamu tebus.'),(4273,3,18,16,'Mengenai uang tebusannya, dari sejak berumur satu bulan haruslah kautebus menurut nilainya, yakni lima syikal perak ditimbang menurut syikal kudus; syikal ini dua puluh gera beratnya.'),(4274,3,18,17,'Tetapi anak sulung lembu, domba atau kambing janganlah kautebus; semuanya itu kudus; darahnya haruslah kausiramkan pada mezbah dan lemaknya kaubakar sebagai korban api-apian menjadi bau yang menyenangkan bagi TUHAN;'),(4275,3,18,18,'tetapi dagingnya adalah bagianmu sama seperti dada persembahan dan paha kanan.'),(4276,3,18,19,'Segala persembahan khusus, yakni persembahan kudus yang dipersembahkan orang Israel kepada TUHAN, Aku berikan kepadamu dan kepada anak-anakmu laki-laki dan perempuan bersama-sama dengan engkau; itulah suatu ketetapan untuk selama-lamanya; itulah suatu perjanjian garam untuk selama-lamanya di hadapan TUHAN bagimu serta bagi keturunanmu.\"'),(4277,3,18,20,'TUHAN berfirman kepada Harun: \"Di negeri mereka engkau tidak akan mendapat milik pusaka dan tidak akan beroleh bagian di tengah-tengah mereka; Akulah bagianmu dan milik pusakamu di tengah-tengah orang Israel.'),(4278,3,18,21,'Mengenai bani Lewi, sesungguhnya Aku berikan kepada mereka segala persembahan persepuluhan di antara orang Israel sebagai milik pusakanya, untuk membalas pekerjaan yang dilakukan mereka, pekerjaan pada Kemah Pertemuan.'),(4279,3,18,22,'Maka janganlah lagi orang Israel mendekat kepada Kemah Pertemuan, sehingga mereka mendatangkan dosa kepada dirinya, lalu mati;'),(4280,3,18,23,'tetapi orang Lewi, merekalah yang harus melakukan pekerjaan pada Kemah Pertemuan dan mereka harus menanggung akibat kesalahan mereka; itulah suatu ketetapan untuk selama-lamanya bagimu turun-temurun. Mereka tidak akan mendapat milik pusaka di tengah-tengah orang Israel,'),(4281,3,18,24,'sebab persembahan persepuluhan yang dipersembahkan orang Israel kepada TUHAN sebagai persembahan khusus Kuberikan kepada orang Lewi sebagai milik pusakanya; itulah sebabnya Aku telah berfirman tentang mereka: Mereka tidak akan mendapat milik pusaka di tengah-tengah orang Israel.\"'),(4282,3,18,25,'TUHAN berfirman kepada Musa:'),(4283,3,18,26,'Lagi haruslah engkau berbicara kepada orang Lewi dan berkata kepada mereka: Apabila kamu menerima dari pihak orang Israel persembahan persepuluhan yang Kuberikan kepadamu dari pihak mereka sebagai milik pusakamu, maka haruslah kamu mempersembahkan sebagian dari padanya sebagai persembahan khusus kepada TUHAN, yakni persembahan persepuluhanmu dari persembahan persepuluhan itu,'),(4284,3,18,27,'dan persembahan itu akan diperhitungkan sebagai persembahan khususmu, sama seperti gandum dari tempat pengirikan dan sama seperti hasil dari tempat pemerasan anggur.'),(4285,3,18,28,'Secara demikian kamupun harus mempersembahkan sebagai persembahan khusus kepada TUHAN sebagian dari segala persembahan persepuluhan yang kamu terima dari pihak orang Israel. Dan yang dipersembahkan dari padanya sebagai persembahan khusus kepada TUHAN haruslah kamu serahkan kepada imam Harun.'),(4286,3,18,29,'Dari segala yang diserahkan kepadamu, yakni dari segala yang terbaik di antaranya, haruslah kamu mempersembahkan seluruh persembahan khusus kepada TUHAN, sebagai bagian kudus dari padanya.'),(4287,3,18,30,'Lagi haruslah engkau berkata kepada mereka: Apabila kamu mengkhususkan yang terbaik dari padanya, maka bagi orang Lewi haruslah hal itu dihitungkan sebagai hasil tempat pengirikan dan hasil tempat pemerasan anggur;'),(4288,3,18,31,'kamu boleh memakannya di setiap tempat, kamu dan seisi rumahmu, sebab upahmulah itu, untuk membalas pekerjaanmu di Kemah Pertemuan.'),(4289,3,18,32,'Dan dalam hal itu kamu tidak akan mendatangkan dosa kepada dirimu, asal kamu mengkhususkan yang terbaik dari padanya; demikianlah kamu tidak akan melanggar kekudusan persembahan-persembahan kudus orang Israel, dan kamu tidak akan mati.\"'),(4290,3,19,1,'TUHAN berfirman kepada Musa dan Harun:'),(4291,3,19,2,'Inilah ketetapan hukum yang diperintahkan TUHAN dengan berfirman: Katakanlah kepada orang Israel, supaya mereka membawa kepadamu seekor lembu betina merah yang tidak bercela, yang tidak ada cacatnya dan yang belum pernah kena kuk.'),(4292,3,19,3,'Dan haruslah kamu memberikannya kepada imam Eleazar, maka lembu itu harus dibawa ke luar tempat perkemahan, lalu disembelih di depan imam.'),(4293,3,19,4,'Kemudian imam Eleazar harus mengambil dengan jarinya sedikit dari darah lembu itu, lalu haruslah ia memercikkan sedikit ke arah sebelah depan Kemah Pertemuan sampai tujuh kali.'),(4294,3,19,5,'Sesudah itu haruslah lembu itu dibakar habis di depan mata imam; kulitnya, dagingnya dan darahnya haruslah dibakar habis bersama-sama dengan kotorannya.'),(4295,3,19,6,'Dan imam haruslah mengambil kayu aras, hisop dan kain kirmizi dan melemparkannya ke tengah-tengah api yang membakar habis lembu itu.'),(4296,3,19,7,'Kemudian haruslah imam mencuci pakaiannya dan membasuh tubuhnya dengan air, sesudah itu masuk ke tempat perkemahan, dan imam itu najis sampai matahari terbenam.'),(4297,3,19,8,'Orang yang membakar habis lembu itu haruslah mencuci pakaiannya dengan air dan membasuh tubuhnya dengan air, dan ia najis sampai matahari terbenam.'),(4298,3,19,9,'Maka seorang yang tahir haruslah mengumpulkan abu lembu itu dan menaruhnya pada suatu tempat yang tahir di luar tempat perkemahan, supaya semuanya itu tinggal tersimpan bagi umat Israel untuk membuat air pentahiran; itulah penghapus dosa.'),(4299,3,19,10,'Dan orang yang mengumpulkan abu lembu itu haruslah mencuci pakaiannya, dan ia najis sampai matahari terbenam. Itulah suatu ketetapan untuk selama-lamanya bagi orang Israel dan bagi orang asing yang tinggal di tengah-tengahmu.'),(4300,3,19,11,'Orang yang kena kepada mayat, ia najis tujuh hari lamanya.'),(4301,3,19,12,'Ia harus menghapus dosa dari dirinya dengan air itu pada hari yang ketiga, dan pada hari yang ketujuh ia tahir. Tetapi jika pada hari yang ketiga ia tidak menghapus dosa dari dirinya, maka tidaklah ia tahir pada hari yang ketujuh.'),(4302,3,19,13,'Setiap orang yang kena kepada mayat, yaitu tubuh manusia yang telah mati, dan tidak menghapus dosa dari dirinya, ia menajiskan Kemah Suci TUHAN, dan orang itu haruslah dilenyapkan dari Israel; karena air pentahiran tidak disiramkan kepadanya, maka ia najis; kenajisannya masih melekat padanya.'),(4303,3,19,14,'Inilah hukumnya, apabila seseorang mati dalam suatu kemah: setiap orang yang masuk ke dalam kemah itu dan segala yang di dalam kemah itu najis tujuh hari lamanya;'),(4304,3,19,15,'setiap bejana yang terbuka yang tidak ada kain penutup terikat di atasnya adalah najis.'),(4305,3,19,16,'Juga setiap orang yang di padang, yang kena kepada seorang yang mati terbunuh oleh pedang, atau kepada mayat, atau kepada tulang-tulang seorang manusia, atau kepada kubur, orang itu najis tujuh hari lamanya.'),(4306,3,19,17,'Bagi orang yang najis haruslah diambil sedikit abu dari korban penghapus dosa yang dibakar habis, lalu di dalam bejana abu itu dibubuhi air mengalir.'),(4307,3,19,18,'Kemudian seorang yang tahir haruslah mengambil hisop, mencelupkannya ke dalam air itu dan memercikkannya ke atas kemah dan ke atas segala bejana dan ke atas orang-orang yang ada di sana, dan ke atas orang yang telah kena kepada tulang-tulang, atau kepada orang yang mati terbunuh, atau kepada mayat, atau kepada kubur itu;'),(4308,3,19,19,'orang yang tahir itu haruslah memercik kepada orang yang najis itu pada hari yang ketiga dan pada hari yang ketujuh, dan pada hari yang ketujuh itu haruslah ia menghapus dosa orang itu; dan orang yang najis itu haruslah mencuci pakaiannya dan membasuh badannya dengan air, lalu ia tahir pada waktu matahari terbenam.'),(4309,3,19,20,'Tetapi orang yang telah najis, dan tidak menghapus dosa dari dirinya, orang itu harus dilenyapkan dari tengah-tengah jemaah itu, karena ia telah menajiskan tempat kudus TUHAN; air pentahiran tidak ada disiramkan kepadanya, jadi ia tetap najis.'),(4310,3,19,21,'Itulah yang harus menjadi ketetapan bagi mereka untuk selama-lamanya. Orang yang menyiramkan air penyuci itu, ia harus mencuci pakaiannya, dan orang yang kena kepada air penyuci itu, ia menjadi najis sampai matahari terbenam.'),(4311,3,19,22,'Segala yang diraba orang yang najis itu menjadi najis dan orang yang kena kepadanya menjadi najis juga sampai matahari terbenam.\"'),(4312,3,20,1,'Kemudian sampailah orang Israel, yakni segenap umat itu, ke padang gurun Zin, dalam bulan pertama, lalu tinggallah bangsa itu di Kadesh. Matilah Miryam di situ dan dikuburkan di situ.'),(4313,3,20,2,'Pada suatu kali, ketika tidak ada air bagi umat itu, berkumpullah mereka mengerumuni Musa dan Harun,'),(4314,3,20,3,'dan bertengkarlah bangsa itu dengan Musa, katanya: \"Sekiranya kami mati binasa pada waktu saudara-saudara kami mati binasa di hadapan TUHAN!'),(4315,3,20,4,'Mengapa kamu membawa jemaah TUHAN ke padang gurun ini, supaya kami dan ternak kami mati di situ?'),(4316,3,20,5,'Mengapa kamu memimpin kami keluar dari Mesir, untuk membawa kami ke tempat celaka ini, yang bukan tempat menabur, tanpa pohon ara, anggur dan delima, bahkan air minumpun tidak ada?\"'),(4317,3,20,6,'Maka pergilah Musa dan Harun dari umat itu ke pintu Kemah Pertemuan, lalu sujud. Kemudian tampaklah kemuliaan TUHAN kepada mereka.'),(4318,3,20,7,'TUHAN berfirman kepada Musa:'),(4319,3,20,8,'Ambillah tongkatmu itu dan engkau dan Harun, kakakmu, harus menyuruh umat itu berkumpul; katakanlah di depan mata mereka kepada bukit batu itu supaya diberi airnya; demikianlah engkau mengeluarkan air dari bukit batu itu bagi mereka dan memberi minum umat itu serta ternaknya.'),(4320,3,20,9,'Lalu Musa mengambil tongkat itu dari hadapan TUHAN, seperti yang diperintahkan-Nya kepadanya.'),(4321,3,20,10,'Ketika Musa dan Harun telah mengumpulkan jemaah itu di depan bukit batu itu, berkatalah ia kepada mereka: \"Dengarlah kepadaku, hai orang-orang durhaka, apakah kami harus mengeluarkan air bagimu dari bukit batu ini?\"'),(4322,3,20,11,'Sesudah itu Musa mengangkat tangannya, lalu memukul bukit batu itu dengan tongkatnya dua kali, maka keluarlah banyak air, sehingga umat itu dan ternak mereka dapat minum.'),(4323,3,20,12,'Tetapi TUHAN berfirman kepada Musa dan Harun: \"Karena kamu tidak percaya kepada-Ku dan tidak menghormati kekudusan-Ku di depan mata orang Israel, itulah sebabnya kamu tidak akan membawa jemaah ini masuk ke negeri yang akan Kuberikan kepada mereka.\"'),(4324,3,20,13,'Itulah mata air Meriba, tempat orang Israel bertengkar dengan TUHAN dan Ia menunjukkan kekudusan-Nya di antara mereka.'),(4325,3,20,14,'Kemudian Musa mengirim utusan dari Kadesh kepada raja Edom dengan pesan: \"Beginilah perkataan saudaramu Israel: Engkau tahu segala kesusahan yang telah menimpa kami,'),(4326,3,20,15,'bahwa nenek moyang kami pergi ke Mesir, dan kami lama diam di Mesir dan kami dan nenek moyang kami diperlakukan dengan jahat oleh orang Mesir;'),(4327,3,20,16,'bahwa kami berteriak kepada TUHAN, dan Ia mendengarkan suara kami, mengutus seorang malaikat dan menuntun kami keluar dari Mesir. Sekarang ini kami ada di Kadesh, sebuah kota di tepi perbatasanmu.'),(4328,3,20,17,'Izinkanlah kiranya kami melalui negerimu; kami tidak akan berjalan melalui ladang-ladang dan kebun-kebun anggurmu dan kami tidak akan minum air sumurmu; jalan besar saja akan kami jalani dengan tidak menyimpang ke kanan atau ke kiri, sampai kami melalui batas daerahmu.\"'),(4329,3,20,18,'Tetapi orang Edom berkata kepada mereka: \"Tidak boleh kamu melalui daerah kami, nanti kami keluar menjumpai kamu dengan pedang!\"'),(4330,3,20,19,'Lalu berkatalah orang Israel kepadanya: \"Kami akan berjalan melalui jalan raya, dan jika kami dan ternak kami minum airmu, maka kami akan membayar uangnya, asal kami diizinkan lalu dengan berjalan kaki, hanya itu saja.\"'),(4331,3,20,20,'Tetapi jawab mereka: \"Tidak boleh kamu lalu.\" Maka keluarlah orang Edom menghadapi mereka dengan banyak rakyatnya dan dengan tentara yang kuat.'),(4332,3,20,21,'Ketika orang Edom tidak mau mengizinkan orang Israel lalu dari daerahnya, maka orang Israel menyimpang meninggalkannya.'),(4333,3,20,22,'Setelah mereka berangkat dari Kadesh, sampailah segenap umat Israel ke gunung Hor.'),(4334,3,20,23,'Lalu berkatalah TUHAN kepada Musa dan Harun dekat gunung Hor, di perbatasan tanah Edom:'),(4335,3,20,24,'Harun akan dikumpulkan kepada kaum leluhurnya, sebab ia tidak akan masuk ke negeri yang Kuberikan kepada orang Israel, karena kamu berdua telah mendurhaka kepada titah-Ku dekat mata air Meriba.'),(4336,3,20,25,'Panggillah Harun dan Eleazar, anaknya, dan bawalah mereka naik ke gunung Hor;'),(4337,3,20,26,'tanggalkanlah pakaian Harun dan kenakanlah itu kepada Eleazar, anaknya, kemudian Harun akan dikumpulkan kepada kaum leluhurnya dan mati di sana.\"'),(4338,3,20,27,'Lalu Musa melakukan seperti yang diperintahkan TUHAN. Mereka naik ke gunung Hor sedang segenap umat itu memandangnya.'),(4339,3,20,28,'Musa menanggalkan pakaian Harun dan mengenakannya kepada Eleazar, anaknya. Lalu matilah Harun di puncak gunung itu, kemudian Musa dengan Eleazar turun dari gunung itu.'),(4340,3,20,29,'Ketika segenap umat itu melihat, bahwa Harun telah mati, maka seluruh orang Israel menangisi Harun tiga puluh hari lamanya.'),(4341,3,21,1,'Raja negeri Arad, orang Kanaan yang tinggal di Tanah Negeb, mendengar, bahwa Israel datang dari jalan Atarim, lalu ia berperang melawan Israel, dan diangkutnya beberapa orang tawanan dari pada mereka.'),(4342,3,21,2,'Maka bernazarlah orang Israel kepada TUHAN, katanya: \"Jika Engkau serahkan bangsa ini sama sekali ke dalam tangan kami, kami akan menumpas kota-kota mereka sampai binasa.\"'),(4343,3,21,3,'TUHAN mendengarkan permintaan orang Israel, lalu menyerahkan orang Kanaan itu; kemudian orang-orang itu dan kota-kotanya ditumpas sampai binasa. Itulah sebabnya tempat itu dinamai Horma.'),(4344,3,21,4,'Setelah mereka berangkat dari gunung Hor, berjalan ke arah Laut Teberau untuk mengelilingi tanah Edom, maka bangsa itu tidak dapat lagi menahan hati di tengah jalan.'),(4345,3,21,5,'Lalu mereka berkata-kata melawan Allah dan Musa: \"Mengapa kamu memimpin kami keluar dari Mesir? Supaya kami mati di padang gurun ini? Sebab di sini tidak ada roti dan tidak ada air, dan akan makanan hambar ini kami telah muak.\"'),(4346,3,21,6,'Lalu TUHAN menyuruh ular-ular tedung ke antara bangsa itu, yang memagut mereka, sehingga banyak dari orang Israel yang mati.'),(4347,3,21,7,'Kemudian datanglah bangsa itu mendapatkan Musa dan berkata: \"Kami telah berdosa, sebab kami berkata-kata melawan TUHAN dan engkau; berdoalah kepada TUHAN, supaya dijauhkan-Nya ular-ular ini dari pada kami.\" Lalu Musa berdoa untuk bangsa itu.'),(4348,3,21,8,'Maka berfirmanlah TUHAN kepada Musa: \"Buatlah ular tedung dan taruhlah itu pada sebuah tiang; maka setiap orang yang terpagut, jika ia melihatnya, akan tetap hidup.\"'),(4349,3,21,9,'Lalu Musa membuat ular tembaga dan menaruhnya pada sebuah tiang; maka jika seseorang dipagut ular, dan ia memandang kepada ular tembaga itu, tetaplah ia hidup.'),(4350,3,21,10,'Kemudian berangkatlah orang Israel, lalu berkemah di Obot.'),(4351,3,21,11,'Berangkatlah mereka dari Obot, lalu berkemah dekat reruntuhan di Abarim, di padang gurun yang di sebelah timur Moab.'),(4352,3,21,12,'Dari situ berangkatlah mereka, lalu berkemah di lembah Zered.'),(4353,3,21,13,'Dari situ berangkatlah mereka, lalu berkemah di seberang sungai Arnon yang di padang gurun dan yang keluar dari daerah orang Amori, sebab sungai Arnon ialah batas Moab, di antara orang Moab dan orang Amori.'),(4354,3,21,14,'Itulah sebabnya dikatakan dalam kitab peperangan TUHAN: \"Waheb di Sufa dan lembah-lembah ke sungai Arnon,'),(4355,3,21,15,'dan lereng lembah-lembah; lereng itu terbentang ke tempat di mana terletak kota Ar, dan bersandar pada batas daerah Moab.\"'),(4356,3,21,16,'Dari sana mereka ke Beer. Inilah sumur di mana TUHAN berfirman kepada Musa: \"Kumpulkanlah bangsa itu, maka Aku akan memberikan air kepada mereka.\"'),(4357,3,21,17,'Pada waktu itu orang Israel menyanyikan nyanyian ini: \"Berbual-buallah, hai sumur! Mari kita bernyanyi-nyanyi berbalas-balasan karena sumur yang digali oleh raja-raja,'),(4358,3,21,18,'yang dikorek oleh kaum bangsawan di antara bangsa itu dengan tongkat-tongkat kerajaan, dengan tongkat-tongkat mereka.\" Dan dari padang gurun mereka ke Matana;'),(4359,3,21,19,'dari Matana ke Nahaliel; dari Nahaliel ke Bamot;'),(4360,3,21,20,'dari Bamot ke lembah yang di daerah Moab, dekat puncak gunung Pisga yang menghadap Padang Belantara.'),(4361,3,21,21,'Kemudian orang Israel mengirim utusan kepada Sihon, raja orang Amori, dengan pesan:'),(4362,3,21,22,'Izinkanlah kami melalui negerimu; kami tidak akan menyimpang masuk ke ladang-ladang dan kebun-kebun anggurmu, kami tidak akan minum air sumurmu, di jalan besar saja kami akan berjalan, sampai kami melalui batas daerahmu.'),(4363,3,21,23,'Tetapi Sihon tidak mengizinkan orang Israel berjalan melalui daerahnya, bahkan ia mengumpulkan seluruh laskarnya, lalu keluar ke padang gurun menghadapi orang Israel, dan sesampainya di Yahas berperanglah ia melawan orang Israel.'),(4364,3,21,24,'Tetapi orang Israel mengalahkan dia dengan mata pedang dan menduduki negerinya dari sungai Arnon sampai ke sungai Yabok, sampai kepada bani Amon, sebab batas daerah bani Amon itu kuat.'),(4365,3,21,25,'Dan orang Israel merebut segala kota itu, lalu menetaplah mereka di segala kota orang Amori, di Hesybon dan segala anak kotanya.'),(4366,3,21,26,'Sebab Hesybon ialah kota kediaman Sihon, raja orang Amori; raja ini tadinya berperang melawan raja Moab yang lalu, dan merebut dari tangannya seluruh negerinya sampai ke sungai Arnon.'),(4367,3,21,27,'Itulah sebabnya penyair-penyair berkata: \"Datanglah ke Hesybon, baiklah dibangun dan baiklah diperkuat kota kediaman Sihon itu!'),(4368,3,21,28,'Sebab api keluar dari Hesybon, nyala dari kota kediaman Sihon, yang memakan habis Ar-Moab, yang berkuasa atas bukit-bukit di sepanjang sungai Arnon.'),(4369,3,21,29,'Celakalah engkau, ya Moab; binasa engkau, ya bangsa Kamos! Ia membuat anak-anaknya lelaki menjadi orang-orang pelarian, dan anak-anaknya perempuan menjadi tawanan kepada Sihon, raja orang Amori.'),(4370,3,21,30,'Kita telah menembaki mereka, Hesybon binasa sampai ke Dibon, dan kita menanduskannya sampai ke Nofah, yang terbentang sampai ke Medeba.\"'),(4371,3,21,31,'Demikianlah orang Israel diam di negeri orang Amori.'),(4372,3,21,32,'Setelah Musa menyuruh orang mengintai kota Yaezer, mereka merebut segala anak kota Yaezer dan menghalau orang-orang Amori yang ada di situ.'),(4373,3,21,33,'Kemudian berpalinglah mereka dan maju ke arah Basan. Lalu Og, raja Basan, beserta segala rakyatnya maju ke Edrei menjumpai mereka untuk berperang.'),(4374,3,21,34,'Tetapi TUHAN berfirman kepada Musa: \"Janganlah takut kepadanya, sebab Aku menyerahkan dia dengan seluruh rakyatnya dan negerinya ke dalam tanganmu, dan haruslah kaulakukan kepadanya seperti yang kaulakukan kepada Sihon, raja orang Amori, yang diam di Hesybon.\"'),(4375,3,21,35,'Maka mereka mengalahkan dia dan anak-anaknya dan seluruh rakyatnya, sehingga seorangpun dari mereka tidak ada yang dibiarkan terlepas; lalu mereka menduduki negerinya.'),(4376,3,22,1,'Kemudian berangkatlah orang Israel, dan berkemah di dataran Moab, di daerah seberang sungai Yordan dekat Yerikho.'),(4377,3,22,2,'Balak bin Zipor melihat segala yang dilakukan Israel kepada orang Amori.'),(4378,3,22,3,'Maka sangat gentarlah orang Moab terhadap bangsa itu, karena jumlahnya banyak, lalu muak dan takutlah orang Moab karena orang Israel.'),(4379,3,22,4,'Lalu berkatalah orang Moab kepada para tua-tua Midian: \"Tentu saja laskar besar itu akan membabat habis segala sesuatu yang di sekeliling kita, seperti lembu membabat habis tumbuh-tumbuhan hijau di padang.\" Adapun pada waktu itu Balak bin Zipor menjadi raja Moab.'),(4380,3,22,5,'Raja ini mengirim utusan kepada Bileam bin Beor, ke Petor yang di tepi sungai Efrat, ke negeri teman-teman sebangsanya, untuk memanggil dia, dengan pesan: \"Ketahuilah, ada suatu bangsa keluar dari Mesir; sungguh, sampai tertutup permukaan bumi olehnya, dan mereka sedang berkemah di depanku.'),(4381,3,22,6,'Karena itu, datanglah dan kutuk bangsa itu bagiku, sebab mereka lebih kuat dari padaku; mungkin aku sanggup mengalahkannya dan menghalaunya dari negeri ini, sebab aku tahu: siapa yang kauberkati, dia beroleh berkat, dan siapa yang kaukutuk, dia kena kutuk.\"'),(4382,3,22,7,'Lalu berangkatlah para tua-tua Moab dan para tua-tua Midian dengan membawa di tangannya upah penenung; setelah mereka sampai kepada Bileam, disampaikanlah kepadanya pesan Balak.'),(4383,3,22,8,'Lalu berkatalah Bileam kepada mereka: \"Bermalamlah di sini pada malam ini, maka aku akan memberi jawab kepadamu, sesuai dengan apa yang akan difirmankan TUHAN kepadaku.\" Maka tinggallah pemuka-pemuka Moab itu pada Bileam.'),(4384,3,22,9,'Kemudian datanglah Allah kepada Bileam serta berfirman: \"Siapakah orang-orang yang bersama-sama dengan engkau itu?\"'),(4385,3,22,10,'Dan berkatalah Bileam kepada Allah: \"Balak bin Zipor, raja Moab, mengutus orang kepadaku dengan pesan:'),(4386,3,22,11,'Ketahuilah, ada bangsa yang keluar dari Mesir, dan permukaan bumi tertutup olehnya; karena itu, datanglah, serapahlah mereka bagiku, mungkin aku akan sanggup berperang melawan mereka dan menghalau mereka.\"'),(4387,3,22,12,'Lalu berfirmanlah Allah kepada Bileam: \"Janganlah engkau pergi bersama-sama dengan mereka, janganlah engkau mengutuk bangsa itu, sebab mereka telah diberkati.\"'),(4388,3,22,13,'Bangunlah Bileam pada waktu pagi, lalu berkata kepada pemuka-pemuka Balak: \"Pulanglah ke negerimu, sebab TUHAN tidak mengizinkan aku pergi bersama-sama dengan kamu.\"'),(4389,3,22,14,'Lalu berangkatlah pemuka-pemuka Moab itu dan setelah mereka sampai kepada Balak, berkatalah mereka: \"Bileam menolak datang bersama-sama dengan kami.\"'),(4390,3,22,15,'Tetapi Balak mengutus pula pemuka-pemuka lebih banyak dan lebih terhormat dari yang pertama.'),(4391,3,22,16,'Setelah mereka sampai kepada Bileam, berkatalah mereka kepadanya: \"Beginilah kata Balak bin Zipor: Janganlah biarkan dirimu terhalang-halang untuk datang kepadaku,'),(4392,3,22,17,'sebab aku akan memberi upahmu sangat banyak, dan apapun yang kauminta dari padaku, aku akan mengabulkannya. Datanglah, dan serapahlah bangsa itu bagiku.\"'),(4393,3,22,18,'Tetapi Bileam menjawab kepada pegawai-pegawai Balak: \"Sekalipun Balak memberikan kepadaku emas dan perak seistana penuh, aku tidak akan sanggup berbuat sesuatu, yang kecil atau yang besar, yang melanggar titah TUHAN, Allahku.'),(4394,3,22,19,'Oleh sebab itu, baiklah kamupun tinggal di sini pada malam ini, supaya aku tahu, apakah pula yang akan difirmankan TUHAN kepadaku.\"'),(4395,3,22,20,'Datanglah Allah kepada Bileam pada waktu malam serta berfirman kepadanya: \"Jikalau orang-orang itu memang sudah datang untuk memanggil engkau, bangunlah, pergilah bersama-sama dengan mereka, tetapi hanya apa yang akan Kufirmankan kepadamu harus kaulakukan.\"'),(4396,3,22,21,'Lalu bangunlah Bileam pada waktu pagi, dipelanainyalah keledainya yang betina, dan pergi bersama-sama dengan pemuka-pemuka Moab.'),(4397,3,22,22,'Tetapi bangkitlah murka Allah ketika ia pergi, dan berdirilah Malaikat TUHAN di jalan sebagai lawannya. Bileam mengendarai keledainya yang betina dan dua orang bujangnya ada bersama-sama dengan dia.'),(4398,3,22,23,'Ketika keledai itu melihat Malaikat TUHAN berdiri di jalan, dengan pedang terhunus di tangan-Nya, menyimpanglah keledai itu dari jalan dan masuk ke ladang. Maka Bileam memukul keledai itu untuk memalingkannya kembali ke jalan.'),(4399,3,22,24,'Kemudian pergilah Malaikat TUHAN berdiri pada jalan yang sempit di antara kebun-kebun anggur dengan tembok sebelah-menyebelah.'),(4400,3,22,25,'Ketika keledai itu melihat Malaikat TUHAN, ditekankannyalah dirinya kepada tembok, sehingga kaki Bileam terhimpit kepada tembok. Maka ia memukulnya pula.'),(4401,3,22,26,'Berjalanlah pula Malaikat TUHAN terus dan berdirilah Ia pada suatu tempat yang sempit, yang tidak ada jalan untuk menyimpang ke kanan atau ke kiri.'),(4402,3,22,27,'Melihat Malaikat TUHAN meniaraplah keledai itu dengan Bileam masih di atasnya. Maka bangkitlah amarah Bileam, lalu dipukulnyalah keledai itu dengan tongkat.'),(4403,3,22,28,'Ketika itu TUHAN membuka mulut keledai itu, sehingga ia berkata kepada Bileam: \"Apakah yang kulakukan kepadamu, sampai engkau memukul aku tiga kali?\"'),(4404,3,22,29,'Jawab Bileam kepada keledai itu: \"Karena engkau mempermain-mainkan aku; seandainya ada pedang di tanganku, tentulah engkau kubunuh sekarang.\"'),(4405,3,22,30,'Tetapi keledai itu berkata kepada Bileam: \"Bukankah aku ini keledaimu yang kautunggangi selama hidupmu sampai sekarang? Pernahkah aku berbuat demikian kepadamu?\" Jawabnya: \"Tidak.\"'),(4406,3,22,31,'Kemudian TUHAN menyingkapkan mata Bileam; dilihatnyalah Malaikat TUHAN dengan pedang terhunus di tangan-Nya berdiri di jalan, lalu berlututlah ia dan sujud.'),(4407,3,22,32,'Berfirmanlah Malaikat TUHAN kepadanya: \"Apakah sebabnya engkau memukul keledaimu sampai tiga kali? Lihat, Aku keluar sebagai lawanmu, sebab jalan ini pada pemandangan-Ku menuju kepada kebinasaan.'),(4408,3,22,33,'Ketika keledai ini melihat Aku, telah tiga kali ia menyimpang dari hadapan-Ku; jika ia tidak menyimpang dari hadapan-Ku, tentulah engkau yang Kubunuh pada waktu itu juga dan dia Kubiarkan hidup.\"'),(4409,3,22,34,'Lalu berkatalah Bileam kepada Malaikat TUHAN: \"Aku telah berdosa, karena aku tidak mengetahui, bahwa Engkau ini berdiri di jalan menentang aku. Maka sekarang, jika hal itu jahat di mata-Mu, aku mau pulang.\"'),(4410,3,22,35,'Tetapi Malaikat TUHAN berfirman kepada Bileam: \"Pergilah bersama-sama dengan orang-orang itu, tetapi hanyalah perkataan yang akan Kukatakan kepadamu harus kaukatakan.\" Sesudah itu pergilah Bileam bersama-sama dengan pemuka-pemuka Balak itu.'),(4411,3,22,36,'Ketika Balak mendengar, bahwa Bileam datang, keluarlah ia menyongsong dia sampai ke Kota Moab di perbatasan sungai Arnon, pada ujung perbatasan itu.'),(4412,3,22,37,'Dan berkatalah Balak kepada Bileam: \"Bukankah aku sudah mengutus orang memanggil engkau? Mengapakah engkau tidak hendak datang kepadaku? Sungguhkah tidak sanggup aku memberi upahmu?\"'),(4413,3,22,38,'Tetapi berkatalah Bileam kepada Balak: \"Ini aku sudah datang kepadamu sekarang; tetapi akan mungkinkah aku dapat mengatakan apa-apa? Perkataan yang akan ditaruh Allah ke dalam mulutku, itulah yang akan kukatakan.\"'),(4414,3,22,39,'Lalu pergilah Bileam bersama-sama dengan Balak dan sampailah mereka ke Kiryat-Huzot.'),(4415,3,22,40,'Balak mengorbankan beberapa ekor lembu sapi dan kambing domba dan mengirimkan sebagian kepada Bileam dan kepada pemuka-pemuka yang bersama-sama dengan dia.'),(4416,3,22,41,'Keesokan harinya Balak mengambil Bileam dan membawa dia mendaki bukit Baal. Dari situ dilihatnyalah bagian yang paling ujung dari bangsa Israel.'),(4417,3,23,1,'Lalu berkatalah Bileam kepada Balak: \"Dirikanlah bagiku di sini tujuh mezbah dan siapkanlah bagiku di sini tujuh ekor lembu jantan dan tujuh ekor domba jantan.\"'),(4418,3,23,2,'Balak melakukan seperti yang dikatakan Bileam, maka Balak dan Bileam mempersembahkan seekor lembu jantan dan seekor domba jantan di atas setiap mezbah itu.'),(4419,3,23,3,'Sesudah itu berkatalah Bileam kepada Balak: \"Berdirilah di samping korban bakaranmu, tetapi aku ini hendak pergi; mungkin TUHAN akan datang menemui aku, dan perkataan apapun yang dinyatakan-Nya kepadaku, akan kuberitahukan kepadamu.\" Lalu pergilah ia ke atas sebuah bukit yang gundul.'),(4420,3,23,4,'Maka Allah menemui Bileam, lalu Bileam berkata kepada-Nya: \"Ketujuh mezbah itu telah kuatur, dan kupersembahkan seekor lembu jantan dan seekor domba jantan di atas setiap mezbah.\"'),(4421,3,23,5,'Kemudian TUHAN menaruh perkataan ke dalam mulut Bileam dan berfirman: \"Kembalilah kepada Balak dan katakanlah demikian.\"'),(4422,3,23,6,'Ketika ia kembali, maka Balak masih berdiri di situ di samping korban bakarannya, bersama dengan semua pemuka Moab.'),(4423,3,23,7,'Lalu Bileam mengucapkan sanjaknya, katanya: \"Dari Aram aku disuruh datang oleh Balak, raja Moab, dari gunung-gunung sebelah timur: Datanglah, katanya, kutuklah bagiku Yakub, dan datanglah, kutuklah Israel.'),(4424,3,23,8,'Bagaimanakah aku menyerapah yang tidak diserapah Allah? Bagaimanakah aku mengutuk yang tidak dikutuk TUHAN?'),(4425,3,23,9,'Sebab dari puncak gunung-gunung batu aku melihat mereka, dari bukit-bukit aku memandang mereka. Lihat, suatu bangsa yang diam tersendiri dan tidak mau dihitung di antara bangsa-bangsa kafir.'),(4426,3,23,10,'Siapakah yang menghitung debu Yakub dan siapakah yang membilang bondongan-bondongan Israel? Sekiranya aku mati seperti matinya orang-orang jujur dan sekiranya ajalku seperti ajal mereka!\"'),(4427,3,23,11,'Lalu berkatalah Balak kepada Bileam: \"Apakah yang kaulakukan kepadaku ini? Untuk menyerapah musuhkulah aku menjemput engkau, tetapi sebaliknya engkau memberkati mereka.\"'),(4428,3,23,12,'Tetapi ia menjawab: \"Bukankah aku harus berawas-awas, supaya mengatakan apa yang ditaruh TUHAN ke dalam mulutku?\"'),(4429,3,23,13,'Lalu Balak berkata kepadanya: \"Baiklah pergi bersama-sama dengan aku ke tempat lain, dan dari sana engkau dapat melihat bangsa itu; engkau akan melihat hanya bagiannya yang paling ujung, tetapi seluruhnya tidak akan kaulihat; serapahlah mereka dari situ bagiku.\"'),(4430,3,23,14,'Lalu dibawanyalah dia ke Padang Pengintai, ke puncak gunung Pisga; ia mendirikan tujuh mezbah dan mempersembahkan seekor lembu jantan dan seekor domba jantan di atas setiap mezbah itu.'),(4431,3,23,15,'Kemudian berkatalah ia kepada Balak: \"Berdirilah di sini di samping korban bakaranmu, sedang aku hendak bertemu dengan TUHAN di situ.\"'),(4432,3,23,16,'Lalu TUHAN menemui Bileam dan menaruh perkataan ke dalam mulutnya, dan berfirman: \"Kembalilah kepada Balak dan katakanlah demikian.\"'),(4433,3,23,17,'Ketika ia sampai kepadanya, Balak masih berdiri di samping korban bakarannya bersama-sama dengan pemuka-pemuka Moab. Berkatalah Balak kepadanya: \"Apakah yang difirmankan TUHAN?\"'),(4434,3,23,18,'Lalu diucapkannyalah sanjaknya, katanya: \"Bangunlah, hai Balak, dan dengarlah; pasanglah telingamu mendengarkan aku, ya anak Zipor.'),(4435,3,23,19,'Allah bukanlah manusia, sehingga Ia berdusta bukan anak manusia, sehingga Ia menyesal. Masakan Ia berfirman dan tidak melakukannya, atau berbicara dan tidak menepatinya?'),(4436,3,23,20,'Ketahuilah, aku mendapat perintah untuk memberkati, dan apabila Dia memberkati, maka aku tidak dapat membalikkannya.'),(4437,3,23,21,'Tidak ada ditengok kepincangan di antara keturunan Yakub, dan tidak ada dilihat kesukaran di antara orang Israel. TUHAN, Allah mereka, menyertai mereka, dan sorak-sorak karena Raja ada di antara mereka.'),(4438,3,23,22,'Allah, yang membawa mereka keluar dari Mesir, adalah bagi mereka seperti tanduk kekuatan lembu hutan,'),(4439,3,23,23,'sebab tidak ada mantera yang mempan terhadap Yakub, ataupun tenungan yang mempan terhadap Israel. Pada waktunya akan dikatakan kepada Yakub, begitu juga kepada Israel, keajaiban yang diperbuat Allah:'),(4440,3,23,24,'Lihat, suatu bangsa, yang bangkit seperti singa betina, dan yang berdiri tegak seperti singa jantan, yang tidak membaringkan dirinya, sebelum ia memakan mangsanya dan meminum darah dari yang mati dibunuhnya.\"'),(4441,3,23,25,'Lalu berkatalah Balak kepada Bileam: \"Jika sekali-kali tidak mau engkau menyerapah mereka, janganlah sekali-kali memberkatinya.\"'),(4442,3,23,26,'Tetapi Bileam menjawab Balak: \"Bukankah telah kukatakan kepadamu: Segala yang akan difirmankan TUHAN, itulah yang akan kulakukan.\"'),(4443,3,23,27,'Kemudian berkatalah Balak kepada Bileam: \"Marilah aku akan membawa engkau ke tempat lain; mungkin benar di mata Allah bahwa engkau menyerapah mereka bagiku dari tempat itu.\"'),(4444,3,23,28,'Lalu Balak membawa Bileam ke puncak gunung Peor, yang menghadap Padang Belantara.'),(4445,3,23,29,'Berkatalah Bileam kepada Balak: \"Dirikanlah bagiku di sini tujuh mezbah dan siapkanlah di sini bagiku tujuh ekor lembu jantan dan tujuh ekor domba jantan.\"'),(4446,3,23,30,'Lalu Balak melakukan seperti yang dikatakan Bileam, maka ia mempersembahkan seekor lembu jantan dan seekor domba jantan di atas setiap mezbah itu.'),(4447,3,24,1,'Ketika dilihat Bileam, bahwa baik di mata TUHAN untuk memberkati Israel, ia tidak mencarikan pertanda lagi seperti yang sudah-sudah, tetapi ia menghadapkan mukanya ke arah padang gurun.'),(4448,3,24,2,'Ketika Bileam memandang ke depan dan melihat orang Israel berkemah menurut suku mereka, maka Roh Allah menghinggapi dia.'),(4449,3,24,3,'Lalu diucapkannyalah sanjaknya, katanya: \"Tutur kata Bileam bin Beor, tutur kata orang yang terbuka matanya;'),(4450,3,24,4,'tutur kata orang yang mendengar firman Allah, yang melihat penglihatan dari Yang Mahakuasa sambil rebah, namun dengan mata tersingkap.'),(4451,3,24,5,'Alangkah indahnya kemah-kemahmu, hai Yakub, dan tempat-tempat kediamanmu, hai Israel!'),(4452,3,24,6,'Sebagai lembah yang membentang semuanya; sebagai taman di tepi sungai; sebagai pohon gaharu yang ditanam TUHAN; sebagai pohon aras di tepi air.'),(4453,3,24,7,'Air mengalir dari timbanya, dan benihnya mendapat air banyak-banyak. Rajanya akan naik tinggi melebihi Agag, dan kerajaannya akan dimuliakan.'),(4454,3,24,8,'Allah, yang membawa mereka keluar dari Mesir, adalah bagi mereka seperti tanduk kekuatan lembu hutan. Bangsa-bangsa yang menjadi lawannya akan ditelannya habis, dan tulang-tulang mereka akan dihancurkannya dan akan ditembaknya tembus dengan panah-panahnya.'),(4455,3,24,9,'Ia meniarap dan merebahkan diri sebagai singa jantan, dan sebagai singa betina; siapakah yang berani membangunkannya? Diberkatilah orang yang memberkati engkau, dan terkutuklah orang yang mengutuk engkau!\"'),(4456,3,24,10,'Lalu bangkitlah amarah Balak terhadap Bileam dan dengan meremas-remas jarinya berkatalah ia kepada Bileam: \"Untuk menyerapah musuhku aku memanggil engkau, tetapi sebaliknya sampai tiga kali engkau memberkati mereka.'),(4457,3,24,11,'Oleh sebab itu, enyahlah engkau ke tempat kediamanmu; aku telah berkata kepadamu aku telah bermaksud memberi banyak upah kepadamu, tetapi TUHAN telah mencegah engkau memperolehnya.\"'),(4458,3,24,12,'Tetapi berkatalah Bileam kepada Balak: \"Bukankah telah kukatakan juga kepada utusan-utusan yang kaukirim kepadaku:'),(4459,3,24,13,'Sekalipun Balak memberikan kepadaku emas dan perak seistana penuh, aku tidak akan sanggup melanggar titah TUHAN dengan berbuat baik atau jahat atas kemauanku sendiri; apa yang akan difirmankan TUHAN, itulah yang akan kukatakan.'),(4460,3,24,14,'Dan sekarang, aku ini sudah hendak pergi kepada bangsaku; marilah kuberitahukan kepadamu apa yang akan dilakukan bangsa itu kepada bangsamu di kemudian hari.\"'),(4461,3,24,15,'Lalu diucapkannyalah sanjaknya, katanya: \"Tutur kata Bileam bin Beor, tutur kata orang yang terbuka matanya;'),(4462,3,24,16,'tutur kata orang yang mendengar firman Allah, dan yang beroleh pengenalan akan Yang Mahatinggi, yang melihat penglihatan dari Yang Mahakuasa, sambil rebah, namun dengan mata tersingkap.'),(4463,3,24,17,'Aku melihat dia, tetapi bukan sekarang; aku memandang dia, tetapi bukan dari dekat; bintang terbit dari Yakub, tongkat kerajaan timbul dari Israel, dan meremukkan pelipis-pelipis Moab, dan menghancurkan semua anak Set.'),(4464,3,24,18,'Maka Edom akan menjadi tanah pendudukan dan Seir akan menjadi tanah pendudukan--musuh-musuhnya itu. Tetapi Israel akan melakukan perbuatan-perbuatan yang gagah perkasa,'),(4465,3,24,19,'dan dari Yakub akan timbul seorang penguasa, yang akan membinasakan orang-orang yang melarikan diri dari kota.\"'),(4466,3,24,20,'Ketika ia melihat orang Amalek, diucapkannyalah sanjaknya, katanya: \"Yang pertama di antara bangsa-bangsa ialah Amalek, tetapi akhirnya ia akan sampai kepada kebinasaan.\"'),(4467,3,24,21,'Ketika ia melihat orang Keni, diucapkannyalah sanjaknya, katanya: \"Kokoh tempat kediamanmu, tertaruh di atas bukit batu sarangmu,'),(4468,3,24,22,'namun orang Keni akan hapus; berapa lama lagi maka Asyur akan menawan engkau?\"'),(4469,3,24,23,'Diucapkannyalah juga sanjaknya, katanya: \"Celaka! Siapakah yang akan hidup, apabila Allah melakukan hal itu?'),(4470,3,24,24,'Tetapi kapal-kapal akan datang dari pantai orang Kitim, mereka akan menindas Asyur dan menindas Heber, lalu iapun juga akan sampai kepada kebinasaan.\"'),(4471,3,24,25,'Lalu bersiaplah Bileam dan pulang ke tempat kediamannya; dan Balakpun pergilah juga.'),(4472,3,25,1,'Sementara Israel tinggal di Sitim, mulailah bangsa itu berzinah dengan perempuan-perempuan Moab.'),(4473,3,25,2,'Perempuan-perempuan ini mengajak bangsa itu ke korban sembelihan bagi allah mereka, lalu bangsa itu turut makan dari korban itu dan menyembah allah orang-orang itu.'),(4474,3,25,3,'Ketika Israel berpasangan dengan Baal-Peor, bangkitlah murka TUHAN terhadap Israel;'),(4475,3,25,4,'lalu berfirmanlah TUHAN kepada Musa: \"Tangkaplah semua orang yang mengepalai bangsa itu dan gantunglah mereka di hadapan TUHAN di tempat terang, supaya murka TUHAN yang bernyala-nyala itu surut dari pada Israel.\"'),(4476,3,25,5,'Lalu berkatalah Musa kepada hakim-hakim Israel: \"Baiklah masing-masing kamu membunuh orang-orangnya yang telah berpasangan dengan Baal-Peor.\"'),(4477,3,25,6,'Kebetulan datanglah salah seorang Israel membawa seorang perempuan Midian kepada sanak saudaranya dengan dilihat Musa dan segenap umat Israel yang sedang bertangis-tangisan di depan pintu Kemah Pertemuan.'),(4478,3,25,7,'Ketika hal itu dilihat oleh Pinehas, anak Eleazar, anak imam Harun, bangunlah ia dari tengah-tengah umat itu dan mengambil sebuah tombak di tangannya,'),(4479,3,25,8,'mengejar orang Israel itu sampai ke ruang tengah, dan menikam mereka berdua, yakni orang Israel dan perempuan itu, pada perutnya. Maka berhentilah tulah itu menimpa orang Israel.'),(4480,3,25,9,'Orang yang mati karena tulah itu ada dua puluh empat ribu orang banyaknya.'),(4481,3,25,10,'TUHAN berfirman kepada Musa:'),(4482,3,25,11,'Pinehas, anak Eleazar, anak imam Harun, telah menyurutkan murka-Ku dari pada orang Israel, oleh karena ia begitu giat membela kehormatan-Ku di tengah-tengah mereka, sehingga tidaklah Kuhabisi orang Israel dalam cemburu-Ku.'),(4483,3,25,12,'Sebab itu katakanlah: Sesungguhnya Aku berikan kepadanya perjanjian keselamatan yang dari pada-Ku'),(4484,3,25,13,'untuk menjadi perjanjian mengenai keimaman selama-lamanya bagi dia dan bagi keturunannya, karena ia telah begitu giat membela Allahnya dan telah mengadakan pendamaian bagi orang Israel.\"'),(4485,3,25,14,'Nama orang Israel yang mati terbunuh bersama-sama dengan perempuan Midian itu ialah Zimri bin Salu, pemimpin salah satu puak orang Simeon,'),(4486,3,25,15,'dan nama perempuan Midian yang mati terbunuh itu ialah Kozbi binti Zur; Zur itu adalah seorang kepala kaum--yaitu puak--di Midian.'),(4487,3,25,16,'Kemudian berfirmanlah TUHAN kepada Musa:'),(4488,3,25,17,'Lawanlah orang Midian itu, dan tewaskanlah mereka,'),(4489,3,25,18,'sebab mereka telah melawan kamu dengan daya upaya yang dirancang mereka terhadap kamu dalam hal Peor dan dalam hal Kozbi, saudara mereka, yakni anak perempuan seorang pemimpin Midian; Kozbi itu mati terbunuh pada waktu turunnya tulah karena Peor itu.\"'),(4490,3,26,1,'Sesudah tulah itu berfirmanlah TUHAN kepada Musa dan kepada Eleazar, anak imam Harun:'),(4491,3,26,2,'Hitunglah jumlah segenap umat Israel, yang berumur dua puluh tahun ke atas menurut suku mereka, semua orang yang sanggup berperang di antara orang Israel.'),(4492,3,26,3,'Lalu berkatalah Musa dan imam Eleazar kepada mereka di dataran Moab, di tepi sungai Yordan dekat Yerikho:'),(4493,3,26,4,'Hitunglah jumlah semua orang yang berumur dua puluh tahun ke atas!'),(4494,3,26,5,'Ruben ialah anak sulung Israel; bani Ruben ialah dari Henokh kaum orang Henokh; dari Palu kaum orang Palu;'),(4495,3,26,6,'dari Hezron kaum orang Hezron dan dari Karmi kaum orang Karmi.'),(4496,3,26,7,'Itulah kaum-kaum orang Ruben, dan orang-orang yang dicatat dari mereka berjumlah empat puluh tiga ribu tujuh ratus tiga puluh orang.'),(4497,3,26,8,'Adapun anak Palu ialah Eliab,'),(4498,3,26,9,'dan anak-anak Eliab ialah Nemuel, Datan dan Abiram. Datan dan Abiram, orang-orang yang dipilih oleh umat itu, ialah orang-orang yang telah membantah Musa dan Harun dalam kumpulan Korah, ketika mereka membantah TUHAN,'),(4499,3,26,10,'tetapi bumi membuka mulutnya dan menelan mereka bersama-sama dengan Korah, ketika kumpulan itu mati, ketika kedua ratus lima puluh orang itu dimakan api, sehingga mereka menjadi peringatan.'),(4500,3,26,11,'Tetapi anak-anak Korah tidaklah mati.'),(4501,3,26,12,'Bani Simeon, menurut kaum mereka, ialah: dari Nemuel kaum orang Nemuel; dari Yamin kaum orang Yamin; dari Yakhin kaum orang Yakhin;'),(4502,3,26,13,'dari Zerah kaum orang Zerah dan dari Saul kaum orang Saul.'),(4503,3,26,14,'Itulah kaum-kaum orang Simeon, dua puluh dua ribu dua ratus orang banyaknya.'),(4504,3,26,15,'Bani Gad, menurut kaum mereka, ialah: dari Zefon kaum orang Zefon; dari Hagi kaum orang Hagi; dari Syuni kaum orang Syuni;'),(4505,3,26,16,'dari Ozni kaum orang Ozni, dari Eri kaum orang Eri;'),(4506,3,26,17,'dari Arod kaum orang Arod dan dari Areli kaum orang Areli.'),(4507,3,26,18,'Itulah kaum-kaum bani Gad, dan orang-orang yang dicatat dari mereka berjumlah empat puluh ribu lima ratus orang.'),(4508,3,26,19,'Anak-anak Yehuda ialah: Er dan Onan; tetapi Er dan Onan itu mati di tanah Kanaan.'),(4509,3,26,20,'Bani Yehuda, menurut kaum mereka, ialah: dari Syela kaum orang Syela; dari Peres kaum orang Peres dan dari Zerah kaum orang Zerah.'),(4510,3,26,21,'Bani Peres ialah: dari Hezron kaum orang Hezron dan dari Hamul kaum orang Hamul.'),(4511,3,26,22,'Itulah kaum-kaum Yehuda, dan orang-orang yang dicatat dari mereka berjumlah tujuh puluh enam ribu lima ratus orang.'),(4512,3,26,23,'Bani Isakhar, menurut kaum mereka, ialah: dari Tola kaum orang Tola; dari Pua kaum orang Pua;'),(4513,3,26,24,'dari Yasub kaum orang Yasub dan dari Simron kaum orang Simron.'),(4514,3,26,25,'Itulah kaum-kaum Isakhar, dan orang-orang yang dicatat dari mereka, berjumlah enam puluh empat ribu tiga ratus orang.'),(4515,3,26,26,'Bani Zebulon, menurut kaum mereka, ialah: dari Sered kaum orang Sered; dari Elon kaum orang Elon dan dari Yahleel kaum orang Yahleel.'),(4516,3,26,27,'Itulah kaum-kaum orang Zebulon, dan orang-orang yang dicatat dari mereka berjumlah enam puluh ribu lima ratus orang.'),(4517,3,26,28,'Keturunan Yusuf, menurut kaum mereka, ialah orang Manasye dan orang Efraim.'),(4518,3,26,29,'Bani Manasye ialah: dari Makhir kaum orang Makhir; dan Makhir beranakkan Gilead; dari Gilead kaum orang Gilead.'),(4519,3,26,30,'Inilah bani Gilead: dari Iezer kaum orang Iezer; dari Helek kaum orang Helek;'),(4520,3,26,31,'dari Asriel kaum orang Asriel; dari Sekhem kaum orang Sekhem;'),(4521,3,26,32,'dari Semida kaum orang Semida dan dari Hefer kaum orang Hefer.'),(4522,3,26,33,'Adapun Zelafehad bin Hefer tidak mempunyai anak laki-laki, tetapi anak perempuan saja, dan nama anak-anak perempuan Zelafehad ialah Mahla, Noa, Hogla, Milka dan Tirza.'),(4523,3,26,34,'Itulah kaum-kaum Manasye, dan orang-orang yang dicatat dari mereka berjumlah lima puluh dua ribu tujuh ratus orang.'),(4524,3,26,35,'Inilah bani Efraim, menurut kaum mereka: dari Sutelah kaum orang Sutelah; dari Bekher kaum orang Bekher dan dari Tahan kaum orang Tahan.'),(4525,3,26,36,'Dan inilah bani Sutelah: dari Eran kaum orang Eran.'),(4526,3,26,37,'Itulah kaum-kaum bani Efraim, dan orang-orang yang dicatat dari mereka berjumlah tiga puluh dua ribu lima ratus orang banyaknya. Itulah bani Yusuf menurut kaum mereka.'),(4527,3,26,38,'Bani Benyamin, menurut kaum mereka, ialah: dari Bela kaum orang Bela; dari Asybel kaum orang Asybel; dari Ahiram kaum orang Ahiram;'),(4528,3,26,39,'dari Sefufam kaum orang Sefufam dan dari Hufam kaum orang Hufam.'),(4529,3,26,40,'Dan anak-anak Bela ialah Ared dan Naaman; dari Ared kaum orang Ared dan dari Naaman kaum orang Naaman.'),(4530,3,26,41,'Itulah bani Benyamin menurut kaum mereka, dan orang-orang yang dicatat dari mereka berjumlah empat puluh lima ribu enam ratus orang.'),(4531,3,26,42,'Inilah bani Dan, menurut kaum mereka: dari Suham kaum orang Suham. Itulah kaum-kaum Dan menurut kaum mereka.'),(4532,3,26,43,'Segala kaum orang Suham, orang-orang yang dicatat dari mereka berjumlah enam puluh empat ribu empat ratus orang.'),(4533,3,26,44,'Bani Asyer, menurut kaum mereka, ialah: dari Yimna kaum orang Yimna; dari Yiswi kaum orang Yiswi dan dari Beria kaum orang Beria.'),(4534,3,26,45,'Mengenai bani Beria: dari Heber kaum orang Heber dan dari Malkiel kaum orang Malkiel.'),(4535,3,26,46,'Dan nama anak perempuan Asyer ialah Serah.'),(4536,3,26,47,'Itulah kaum-kaum bani Asyer, dan orang-orang yang dicatat dari mereka berjumlah lima puluh tiga ribu empat ratus orang.'),(4537,3,26,48,'Bani Naftali, menurut kaum mereka, ialah: dari Yahzeel kaum orang Yahzeel; dari Guni kaum orang Guni;'),(4538,3,26,49,'dari Yezer kaum orang Yezer dan dari Syilem kaum orang Syilem.'),(4539,3,26,50,'Itulah kaum-kaum Naftali menurut kaum mereka, dan orang-orang yang dicatat dari mereka berjumlah empat puluh lima ribu empat ratus orang.'),(4540,3,26,51,'Itulah orang-orang yang dicatat dari orang Israel, enam ratus satu ribu tujuh ratus tiga puluh orang banyaknya.'),(4541,3,26,52,'TUHAN berfirman kepada Musa:'),(4542,3,26,53,'Kepada suku-suku itulah harus dibagikan tanah itu menjadi milik pusaka menurut nama-nama yang dicatat;'),(4543,3,26,54,'kepada yang besar jumlahnya haruslah engkau memberikan milik pusaka yang besar dan kepada yang kecil jumlahnya haruslah engkau memberikan milik pusaka yang kecil; kepada setiap suku sesuai dengan jumlah orang-orangnya yang dicatat haruslah diberikan milik pusaka.'),(4544,3,26,55,'Tetapi tanah itu harus dibagikan dengan membuang undi; menurut nama suku-suku nenek moyang mereka haruslah mereka mendapat milik pusaka;'),(4545,3,26,56,'seperti yang ditunjukkan undian haruslah dibagikan milik pusaka setiap suku, di antara yang besar dan yang kecil jumlahnya.\"'),(4546,3,26,57,'Inilah orang-orang yang dicatat dari orang Lewi, menurut kaum mereka: dari Gerson kaum orang Gerson; dari Kehat kaum orang Kehat dan dari Merari kaum orang Merari.'),(4547,3,26,58,'Inilah kaum-kaum Lewi: kaum orang Libni, kaum orang Hebron, kaum orang Mahli, kaum orang Musi dan kaum orang Korah. Adapun Kehat beranakkan Amram.'),(4548,3,26,59,'Dan nama isteri Amram ialah Yokhebed, anak perempuan Lewi, yang dilahirkan bagi Lewi di Mesir; dan bagi Amram perempuan itu melahirkan Harun dan Musa dan Miryam, saudara mereka yang perempuan.'),(4549,3,26,60,'Pada Harun lahir Nadab dan Abihu, Eleazar dan Itamar.'),(4550,3,26,61,'Tetapi Nadab dan Abihu mati, ketika mereka mempersembahkan persembahan api yang asing ke hadapan TUHAN.'),(4551,3,26,62,'Dan orang-orang yang dicatat dari mereka berjumlah dua puluh tiga ribu orang, semuanya laki-laki, yang berumur satu bulan ke atas, sebab mereka tidak dicatat bersama-sama dengan orang Israel, karena mereka tidak diberikan milik pusaka di tengah-tengah orang Israel.'),(4552,3,26,63,'Itulah orang-orang yang dicatat oleh Musa dan imam Eleazar, ketika keduanya mencatat orang-orang Israel di dataran Moab di tepi sungai Yordan dekat Yerikho.'),(4553,3,26,64,'Di antara mereka tidak ada terdapat seorangpun yang dicatat Musa dan imam Harun, ketika keduanya mencatat orang Israel di padang gurun Sinai'),(4554,3,26,65,'sebab TUHAN telah berfirman tentang mereka: \"Pastilah mereka mati di padang gurun.\" Dari mereka itu tidak ada seorangpun yang masih tinggal hidup selain dari Kaleb bin Yefune dan Yosua bin Nun.'),(4555,3,27,1,'Kemudian mendekatlah anak-anak perempuan Zelafehad bin Hefer bin Gilead bin Makhir bin Manasye dari kaum Manasye bin Yusuf--nama anak-anaknya itu adalah: Mahla, Noa, Hogla, Milka dan Tirza--'),(4556,3,27,2,'dan berdiri di depan Musa dan imam Eleazar, dan di depan para pemimpin dan segenap umat itu dekat pintu Kemah Pertemuan, serta berkata:'),(4557,3,27,3,'Ayah kami telah mati di padang gurun, walaupun ia tidak termasuk ke dalam kumpulan yang bersepakat melawan TUHAN, ke dalam kumpulan Korah, tetapi ia telah mati karena dosanya sendiri, dan ia tidak mempunyai anak laki-laki.'),(4558,3,27,4,'Mengapa nama ayah kami harus hapus dari tengah-tengah kaumnya, oleh karena ia tidak mempunyai anak laki-laki? Berilah kami tanah milik di antara saudara-saudara ayah kami.\"'),(4559,3,27,5,'Lalu Musa menyampaikan perkara mereka itu ke hadapan TUHAN.'),(4560,3,27,6,'Maka berfirmanlah TUHAN kepada Musa:'),(4561,3,27,7,'Perkataan anak-anak perempuan Zelafehad itu benar; memang engkau harus memberikan tanah milik pusaka kepadanya di tengah-tengah saudara-saudara ayahnya; engkau harus memindahkan kepadanya hak atas milik pusaka ayahnya.'),(4562,3,27,8,'Dan kepada orang Israel engkau harus berkata: Apabila seseorang mati dengan tidak mempunyai anak laki-laki, maka haruslah kamu memindahkan hak atas milik pusakanya kepada anaknya yang perempuan.'),(4563,3,27,9,'Apabila ia tidak mempunyai anak perempuan, maka haruslah kamu memberikan milik pusakanya itu kepada saudara-saudaranya yang laki-laki.'),(4564,3,27,10,'Dan apabila ia tidak mempunyai saudara-saudara lelaki, maka haruslah kamu memberikan milik pusakanya itu kepada saudara-saudara lelaki ayahnya.'),(4565,3,27,11,'Dan apabila ayahnya tidak mempunyai saudara-saudara lelaki, maka haruslah kamu memberikan milik pusakanya itu kepada kerabatnya yang terdekat dari antara kaumnya, supaya dimilikinya.\" Itulah yang harus menjadi ketetapan hukum bagi orang Israel, seperti yang diperintahkan TUHAN kepada Musa.'),(4566,3,27,12,'TUHAN berfirman kepada Musa: \"Naiklah ke gunung Abarim ini, dan pandanglah negeri yang Kuberikan kepada orang Israel.'),(4567,3,27,13,'Sesudah engkau memandangnya, maka engkaupun juga akan dikumpulkan kepada kaum leluhurmu, sama seperti Harun, abangmu, dahulu.'),(4568,3,27,14,'Karena pada waktu pembantahan umat itu di padang gurun Zin, kamu berdua telah memberontak terhadap titah-Ku untuk menyatakan kekudusan-Ku di depan mata mereka dengan air itu.\" Itulah mata air Meriba dekat Kadesh di padang gurun Zin.'),(4569,3,27,15,'Lalu berkatalah Musa kepada TUHAN:'),(4570,3,27,16,'Biarlah TUHAN, Allah dari roh segala makhluk, mengangkat atas umat ini seorang'),(4571,3,27,17,'yang mengepalai mereka waktu keluar dan masuk, dan membawa mereka keluar dan masuk, supaya umat TUHAN jangan hendaknya seperti domba-domba yang tidak mempunyai gembala.\"'),(4572,3,27,18,'Lalu TUHAN berfirman kepada Musa: \"Ambillah Yosua bin Nun, seorang yang penuh roh, letakkanlah tanganmu atasnya,'),(4573,3,27,19,'suruhlah ia berdiri di depan imam Eleazar dan di depan segenap umat, lalu berikanlah kepadanya perintahmu di depan mata mereka itu'),(4574,3,27,20,'dan berilah dia sebagian dari kewibawaanmu, supaya segenap umat Israel mendengarkan dia.'),(4575,3,27,21,'Ia harus berdiri di depan imam Eleazar, supaya Eleazar menanyakan keputusan Urim bagi dia di hadapan TUHAN; atas titahnya mereka akan keluar dan atas titahnya mereka akan masuk, ia beserta semua orang Israel, segenap umat itu.\"'),(4576,3,27,22,'Maka Musa melakukan seperti yang diperintahkan TUHAN kepadanya. Ia memanggil Yosua dan menyuruh dia berdiri di depan imam Eleazar dan di depan segenap umat itu,'),(4577,3,27,23,'lalu ia meletakkan tangannya atas Yosua dan memberikan kepadanya perintahnya, seperti yang difirmankan TUHAN dengan perantaraan Musa.'),(4578,3,28,1,'TUHAN berfirman kepada Musa:'),(4579,3,28,2,'Perintahkanlah kepada orang Israel dan katakanlah kepada mereka: Dengan setia dan pada waktu yang ditetapkan haruslah kamu mempersembahkan persembahan-persembahan kepada-Ku sebagai santapan-Ku, berupa korban api-apian yang baunya menyenangkan bagi-Ku.'),(4580,3,28,3,'Katakanlah kepada mereka: Inilah korban api-apian yang harus kamu persembahkan kepada TUHAN: dua ekor domba berumur setahun yang tidak bercela setiap hari sebagai korban bakaran yang tetap;'),(4581,3,28,4,'domba yang satu haruslah kauolah pada waktu pagi, domba yang lain haruslah kauolah pada waktu senja.'),(4582,3,28,5,'Juga sepersepuluh efa tepung yang terbaik untuk korban sajian, diolah dengan seperempat hin minyak tumbuk.'),(4583,3,28,6,'Itulah korban bakaran yang tetap yang diolah pertama kali di atas gunung Sinai menjadi bau yang menyenangkan, suatu korban api-apian bagi TUHAN.'),(4584,3,28,7,'Dan korban curahannya ialah seperempat hin untuk setiap domba; curahkanlah minuman yang memabukkan sebagai korban curahan bagi TUHAN di tempat kudus.'),(4585,3,28,8,'Dan domba yang lain haruslah kauolah pada waktu senja; sama seperti korban sajian pada waktu pagi dan sama seperti korban curahannya haruslah engkau mengolahnya sebagai korban api-apian yang baunya menyenangkan bagi TUHAN.\"'),(4586,3,28,9,'Pada hari Sabat: dua ekor domba berumur setahun yang tidak bercela, dan dua persepuluh efa tepung yang terbaik sebagai korban sajian, diolah dengan minyak, serta dengan korban curahannya.'),(4587,3,28,10,'Itulah korban bakaran Sabat pada tiap-tiap Sabat, di samping korban bakaran yang tetap dan korban curahannya.'),(4588,3,28,11,'Pada bulan barumu haruslah kamu mempersembahkan sebagai korban bakaran kepada TUHAN: dua ekor lembu jantan muda, seekor domba jantan, tujuh ekor domba berumur setahun yang tidak bercela,'),(4589,3,28,12,'dan juga tiga persepuluh efa tepung yang terbaik sebagai korban sajian, diolah dengan minyak, untuk tiap-tiap lembu jantan, serta dua persepuluh efa tepung yang terbaik sebagai korban sajian, diolah dengan minyak, untuk domba jantan yang seekor itu,'),(4590,3,28,13,'serta sepersepuluh efa tepung yang terbaik sebagai korban sajian, diolah dengan minyak, untuk tiap-tiap domba; itulah suatu korban bakaran, bau yang menyenangkan, suatu korban api-apian bagi TUHAN.'),(4591,3,28,14,'Dan korban-korban curahannya haruslah untuk seekor lembu jantan setengah hin anggur, untuk seekor domba jantan sepertiga hin dan untuk seekor domba seperempat hin. Itulah korban bakaran pada setiap bulan baru dalam setahun.'),(4592,3,28,15,'Dan seekor kambing jantan haruslah diolah menjadi korban penghapus dosa bagi TUHAN, serta dengan korban curahannya, di samping korban bakaran yang tetap.\"'),(4593,3,28,16,'Dalam bulan yang pertama, pada hari yang keempat belas bulan itu, ada Paskah bagi TUHAN.'),(4594,3,28,17,'Pada hari yang kelima belas bulan itu ada hari raya; tujuh hari lamanya harus dimakan roti yang tidak beragi.'),(4595,3,28,18,'Pada hari yang pertama ada pertemuan kudus, maka tidak boleh kamu melakukan sesuatu pekerjaan berat,'),(4596,3,28,19,'dan haruslah kamu mempersembahkan kepada TUHAN sebagai korban api-apian, sebagai korban bakaran: dua ekor lembu jantan muda, seekor domba jantan dan tujuh ekor domba berumur setahun; haruslah kamu ambil yang tidak bercela.'),(4597,3,28,20,'Sebagai korban sajiannya haruslah kamu olah tepung yang terbaik diolah dengan minyak, yakni tiga persepuluh efa untuk seekor lembu jantan dan dua persepuluh efa untuk seekor domba jantan;'),(4598,3,28,21,'sepersepuluh efa harus kauolah untuk setiap domba dari ketujuh ekor domba itu.'),(4599,3,28,22,'Selanjutnya seekor kambing jantan sebagai korban penghapus dosa untuk mengadakan pendamaian bagimu;'),(4600,3,28,23,'selain dari korban bakaran pagi yang termasuk korban bakaran yang tetap haruslah kamu mengolah semuanya itu.'),(4601,3,28,24,'Secara demikian haruslah setiap hari selama tujuh hari kamu olah santapan berupa korban api-apian yang baunya menyenangkan bagi TUHAN; di samping korban bakaran yang tetap haruslah itu diolah, serta dengan korban curahannya.'),(4602,3,28,25,'Dan pada hari yang ketujuh haruslah kamu mengadakan pertemuan yang kudus, maka tidak boleh kamu melakukan sesuatu pekerjaan berat.'),(4603,3,28,26,'Pada hari hulu hasil, pada waktu kamu mempersembahkan korban sajian baru kepada TUHAN, pada hari raya lepas tujuh minggu, haruslah kamu mengadakan pertemuan kudus, maka tidak boleh kamu melakukan sesuatu pekerjaan berat.'),(4604,3,28,27,'Pada waktu itu haruslah kamu mempersembahkan sebagai korban bakaran menjadi bau yang menyenangkan bagi TUHAN: dua ekor lembu jantan muda, seekor domba jantan, tujuh ekor domba berumur setahun;'),(4605,3,28,28,'juga sebagai korban sajiannya: tepung yang terbaik, diolah dengan minyak, yakni tiga persepuluh efa untuk setiap ekor lembu jantan, dua persepuluh efa untuk domba jantan yang seekor itu,'),(4606,3,28,29,'sepersepuluh efa untuk setiap domba dari ketujuh ekor domba itu;'),(4607,3,28,30,'seekor kambing jantan untuk mengadakan pendamaian bagimu.'),(4608,3,28,31,'Selain dari korban bakaran yang tetap dan korban sajiannya, haruslah kamu mengolah semuanya itu, serta dengan korban-korban curahannya. Haruslah kamu ambil yang tidak bercela.'),(4609,3,29,1,'Pada bulan yang ketujuh, pada tanggal satu bulan itu, haruslah kamu mengadakan pertemuan yang kudus, maka tidak boleh kamu melakukan sesuatu pekerjaan berat; itulah hari peniupan serunai bagimu.'),(4610,3,29,2,'Pada waktu itu haruslah kamu mengolah sebagai korban bakaran menjadi bau yang menyenangkan bagi TUHAN: seekor lembu jantan muda, seekor domba jantan, tujuh ekor domba berumur setahun yang tidak bercela;'),(4611,3,29,3,'juga sebagai korban sajiannya: tepung yang terbaik, diolah dengan minyak, yakni tiga persepuluh efa untuk lembu jantan itu, dua persepuluh efa untuk domba jantan itu'),(4612,3,29,4,'dan sepersepuluh efa untuk setiap domba dari ketujuh ekor domba itu;'),(4613,3,29,5,'dan seekor kambing jantan sebagai korban penghapus dosa untuk mengadakan pendamaian bagimu,'),(4614,3,29,6,'selain dari korban bakaran bulan baru serta dengan korban sajiannya, dan korban bakaran yang tetap serta dengan korban sajiannya dan korban-korban curahannya, sesuai dengan peraturannya, menjadi bau yang menyenangkan, suatu korban api-apian bagi TUHAN.'),(4615,3,29,7,'Pada hari yang kesepuluh bulan yang ketujuh itu haruslah kamu mengadakan pertemuan yang kudus dan merendahkan dirimu dengan berpuasa, maka tidak boleh kamu melakukan sesuatu pekerjaan.'),(4616,3,29,8,'Pada waktu itu haruslah kamu mempersembahkan sebagai korban bakaran kepada TUHAN, sebagai bau yang menyenangkan: seekor lembu jantan muda, seekor domba jantan, tujuh ekor domba berumur setahun; haruslah tidak bercela semuanya itu;'),(4617,3,29,9,'juga sebagai korban sajiannya: tepung yang terbaik, diolah dengan minyak, yakni tiga persepuluh efa untuk lembu jantan itu, dua persepuluh efa untuk domba jantan yang seekor itu,'),(4618,3,29,10,'sepersepuluh efa untuk setiap domba dari ketujuh ekor domba itu;'),(4619,3,29,11,'dan seekor kambing jantan sebagai korban penghapus dosa, selain dari pada korban penghapus dosa pembawa pendamaian dan korban bakaran yang tetap serta dengan korban sajiannya dan korban-korban curahannya.'),(4620,3,29,12,'Pada hari yang kelima belas bulan yang ketujuh itu haruslah kamu mengadakan pertemuan yang kudus, maka tidak boleh kamu melakukan sesuatu pekerjaan berat; haruslah kamu mengadakan perayaan bagi TUHAN, tujuh hari lamanya.'),(4621,3,29,13,'Pada waktu itu haruslah kamu mempersembahkan sebagai korban bakaran, sebagai korban api-apian yang baunya menyenangkan bagi TUHAN: tiga belas ekor lembu jantan muda, dua ekor domba jantan, empat belas ekor domba berumur setahun; haruslah tidak bercela semuanya itu;'),(4622,3,29,14,'juga sebagai korban sajiannya: tepung yang terbaik, diolah dengan minyak, yakni tiga persepuluh efa untuk setiap lembu dari ketiga belas ekor lembu jantan itu, dua persepuluh efa untuk setiap domba dari kedua ekor domba jantan itu,'),(4623,3,29,15,'dan sepersepuluh efa untuk setiap domba dari keempat belas ekor domba itu;'),(4624,3,29,16,'dan seekor kambing jantan sebagai korban penghapus dosa, selain dari korban bakaran yang tetap dengan korban sajiannya dan korban curahannya.'),(4625,3,29,17,'Pada hari yang kedua: dua belas ekor lembu jantan muda, dua ekor domba jantan, empat belas ekor domba berumur setahun yang tidak bercela,'),(4626,3,29,18,'serta dengan korban sajiannya dan korban-korban curahannya, yakni untuk lembu-lembu jantan, untuk domba-domba jantan dan untuk domba-domba muda itu, menurut jumlah yang sesuai dengan peraturan;'),(4627,3,29,19,'dan seekor kambing jantan sebagai korban penghapus dosa, selain dari korban bakaran yang tetap serta dengan korban sajiannya dan korban-korban curahannya.'),(4628,3,29,20,'Pada hari yang ketiga: sebelas ekor lembu jantan, dua ekor domba jantan, empat belas ekor domba berumur setahun yang tidak bercela,'),(4629,3,29,21,'serta dengan korban sajiannya dan korban-korban curahannya, yakni untuk lembu-lembu jantan, untuk domba-domba jantan dan untuk domba-domba muda itu, menurut jumlah yang sesuai dengan peraturan;'),(4630,3,29,22,'dan seekor kambing jantan sebagai korban penghapus dosa, selain dari korban bakaran yang tetap serta dengan korban sajiannya dan korban curahannya.'),(4631,3,29,23,'Pada hari yang keempat: sepuluh ekor lembu jantan, dua ekor domba jantan, empat belas ekor domba berumur setahun yang tidak bercela,'),(4632,3,29,24,'serta dengan korban sajiannya dan korban-korban curahannya, yakni untuk lembu-lembu jantan, untuk domba-domba jantan dan untuk domba-domba muda itu, menurut jumlah yang sesuai dengan peraturan;'),(4633,3,29,25,'dan seekor kambing jantan sebagai korban penghapus dosa, selain dari korban bakaran yang tetap dengan korban sajiannya dan korban curahannya.'),(4634,3,29,26,'Pada hari yang kelima: sembilan ekor lembu jantan, dua ekor domba jantan, empat belas ekor domba berumur setahun yang tidak bercela,'),(4635,3,29,27,'serta dengan korban sajiannya dan korban-korban curahannya, yakni untuk lembu-lembu jantan, untuk domba-domba jantan dan untuk domba-domba muda itu, menurut jumlah yang sesuai dengan peraturan;'),(4636,3,29,28,'dan seekor kambing jantan sebagai korban penghapus dosa, selain dari korban bakaran yang tetap serta dengan korban sajiannya dan korban curahannya.'),(4637,3,29,29,'Pada hari yang keenam: delapan ekor lembu jantan, dua ekor domba jantan, empat belas ekor domba berumur setahun yang tidak bercela,'),(4638,3,29,30,'serta dengan korban sajiannya dan korban-korban curahannya, yakni untuk lembu-lembu jantan, untuk domba-domba jantan dan untuk domba-domba muda itu, menurut jumlah yang sesuai dengan peraturan;'),(4639,3,29,31,'dan seekor kambing jantan sebagai korban penghapus dosa, selain dari korban bakaran yang tetap dengan korban sajiannya dan korban-korban curahannya.'),(4640,3,29,32,'Pada hari yang ketujuh: tujuh ekor lembu jantan, dua ekor domba jantan, empat belas ekor domba berumur setahun yang tidak bercela,'),(4641,3,29,33,'serta dengan korban sajiannya, dan korban-korban curahannya, yakni untuk lembu-lembu jantan, untuk domba-domba jantan dan untuk domba-domba muda itu, menurut jumlah yang sesuai dengan peraturannya;'),(4642,3,29,34,'dan seekor kambing jantan sebagai korban penghapus dosa, selain dari korban bakaran yang tetap dengan korban sajiannya dan korban curahannya.'),(4643,3,29,35,'Pada hari yang kedelapan haruslah kamu mengadakan perkumpulan raya, maka tidak boleh kamu melakukan sesuatu pekerjaan berat.'),(4644,3,29,36,'Pada waktu itu haruslah kamu mempersembahkan sebagai korban bakaran, sebagai korban api-apian yang baunya menyenangkan bagi TUHAN: seekor lembu jantan, seekor domba jantan, tujuh ekor domba berumur setahun yang tidak bercela,'),(4645,3,29,37,'dengan korban sajiannya dan korban-korban curahannya, yakni untuk lembu-lembu jantan, untuk domba-domba jantan dan untuk domba-domba muda itu, menurut jumlah yang sesuai dengan peraturan;'),(4646,3,29,38,'dan seekor kambing jantan sebagai korban penghapus dosa, selain dari korban bakaran yang tetap serta dengan korban sajiannya dan korban curahannya.'),(4647,3,29,39,'Itulah semuanya yang harus kamu olah bagi TUHAN pada hari-hari rayamu sebagai korban-korban bakaranmu, korban-korban sajianmu, korban-korban curahanmu dan korban-korban keselamatanmu, selain dari korban-korban nazarmu dan korban-korban sukarelamu.\"'),(4648,3,29,40,'Lalu berbicaralah Musa kepada orang Israel sesuai dengan segala yang diperintahkan TUHAN kepada Musa.'),(4649,3,30,1,'Musa berkata kepada kepala-kepala suku Israel, demikian: \"Inilah yang diperintahkan TUHAN.'),(4650,3,30,2,'Apabila seorang laki-laki bernazar atau bersumpah kepada TUHAN, sehingga ia mengikat dirinya kepada suatu janji, maka janganlah ia melanggar perkataannya itu; haruslah ia berbuat tepat seperti yang diucapkannya.'),(4651,3,30,3,'Tetapi apabila seorang perempuan bernazar kepada TUHAN dan mengikat dirinya kepada suatu janji di rumah ayahnya, yakni pada waktu ia masih gadis,'),(4652,3,30,4,'dan ayahnya mendengar nazar dan janji yang mengikat diri anaknya itu, tetapi ayahnya tidak berkata apa-apa kepadanya, maka segala nazarnya itu akan tetap berlaku dan setiap janji mengikat dirinya akan tetap berlaku juga.'),(4653,3,30,5,'Tetapi jika ayahnya melarang dia pada waktu mendengar itu, maka segala nazar dan janji yang mengikat diri anaknya itu tidak akan berlaku; dan TUHAN akan mengampuni perempuan itu, sebab ayahnya telah melarang dia.'),(4654,3,30,6,'Tetapi jika perempuan itu bersuami, dan ia masih berhutang karena salah satu nazar atau salah satu janji yang diucapkan begitu saja dan yang mengikat dirinya,'),(4655,3,30,7,'dan suaminya mendengar tentang hal itu, tetapi tidak berkata apa-apa kepadanya pada waktu mendengarnya, maka nazarnya itu akan tetap berlaku dan janji yang mengikat dirinya akan tetap berlaku juga.'),(4656,3,30,8,'Tetapi apabila suaminya itu, pada waktu mendengarnya, melarang dia, maka ia telah membatalkan nazar yang menjadi hutang isterinya dan janji yang diucapkan begitu saja dan yang mengikat isterinya; dan TUHAN akan mengampuni isterinya itu.'),(4657,3,30,9,'Mengenai nazar seorang janda atau seorang perempuan yang diceraikan, segala apa yang mengikat dirinya akan tetap berlaku baginya.'),(4658,3,30,10,'Jika seorang perempuan di rumah suaminya bernazar atau mengikat dirinya kepada suatu janji dengan bersumpah,'),(4659,3,30,11,'dan suaminya mendengarnya, tetapi tidak berkata apa-apa kepadanya dan tidak melarang dia, maka segala nazar perempuan itu akan tetap berlaku, dan setiap janji yang mengikat diri perempuan itu akan tetap berlaku juga.'),(4660,3,30,12,'Tetapi jika suaminya itu membatalkannya dengan tegas pada waktu mendengarnya, maka ucapan apapun yang keluar dari mulutnya, baik nazar maupun janji, tidak akan berlaku; suaminya telah membatalkannya, dan TUHAN akan mengampuni isterinya itu.'),(4661,3,30,13,'Setiap nazar dan setiap janji sumpah perempuan itu untuk merendahkan diri dengan berpuasa, dapat dinyatakan berlaku oleh suaminya atau dapat dibatalkan oleh suaminya.'),(4662,3,30,14,'Tetapi apabila suaminya sama sekali tidak berkata apa-apa kepadanya dari hari ke hari, maka dengan demikian ia telah menyatakan berlaku segala nazar isterinya atau segala ikatan janji yang menjadi hutang isterinya; ia telah menyatakannya berlaku, karena ia tidak berkata apa-apa kepadanya pada waktu mendengarnya.'),(4663,3,30,15,'Tetapi jika ia baru membatalkannya beberapa lama setelah didengarnya, maka ia akan menanggung akibat kesalahan isterinya.\"'),(4664,3,30,16,'Itulah ketetapan-ketetapan yang diperintahkan TUHAN kepada Musa, yakni antara seorang suami dengan isterinya, dan antara seorang ayah dengan anaknya perempuan pada waktu ia masih gadis di rumah ayahnya.'),(4665,3,31,1,'TUHAN berfirman kepada Musa:'),(4666,3,31,2,'Lakukanlah pembalasan orang Israel kepada orang Midian; kemudian engkau akan dikumpulkan kepada kaum leluhurmu.'),(4667,3,31,3,'Lalu berkatalah Musa kepada bangsa itu: \"Baiklah sejumlah orang dari antaramu mempersenjatai diri untuk berperang, supaya mereka melawan Midian untuk menjalankan pembalasan TUHAN terhadap Midian.'),(4668,3,31,4,'Dari setiap suku di antara segala suku Israel haruslah kamu menyuruh seribu orang untuk berperang.\"'),(4669,3,31,5,'Demikianlah diserahkan dari kaum-kaum Israel seribu orang dari tiap-tiap suku, jadi dua belas ribu orang bersenjata untuk berperang.'),(4670,3,31,6,'Lalu Musa menyuruh mereka untuk berperang, seribu orang dari tiap-tiap suku, bersama-sama dengan Pinehas, anak imam Eleazar, untuk berperang, dengan membawa perkakas tempat kudus dan nafiri-nafiri pemberi tanda semboyan.'),(4671,3,31,7,'Kemudian berperanglah mereka melawan Midian, seperti yang diperintahkan TUHAN kepada Musa, lalu membunuh semua laki-laki mereka.'),(4672,3,31,8,'Selain dari orang-orang yang mati terbunuh itu, merekapun membunuh juga raja-raja Midian, yakni Ewi, Rekem, Zur, Hur dan Reba, kelima raja Midian, juga Bileam bin Beor dibunuh mereka dengan pedang.'),(4673,3,31,9,'Kemudian Israel menawan perempuan-perempuan Midian dan anak-anak mereka; juga segala hewan, segala ternak dan segenap kekayaan mereka dijarah,'),(4674,3,31,10,'dan segala kota kediaman serta segala tempat perkemahan mereka dibakar.'),(4675,3,31,11,'Kemudian diambillah seluruh jarahan dan seluruh rampasan berupa manusia dan hewan itu,'),(4676,3,31,12,'dan dibawalah orang-orang tawanan, rampasan dan jarahan itu kepada Musa dan imam Eleazar dan kepada umat Israel, ke tempat perkemahan di dataran Moab yang di tepi sungai Yordan dekat Yerikho.'),(4677,3,31,13,'Lalu pergilah Musa dan imam Eleazar dan semua pemimpin umat itu sampai ke luar tempat perkemahan untuk menyongsong mereka.'),(4678,3,31,14,'Maka gusarlah Musa kepada para pemimpin tentara itu, kepada para kepala pasukan seribu dan para kepala pasukan seratus, yang pulang dari peperangan,'),(4679,3,31,15,'dan Musa berkata kepada mereka: \"Kamu biarkankah semua perempuan hidup?'),(4680,3,31,16,'Bukankah perempuan-perempuan ini, atas nasihat Bileam, menjadi sebabnya orang Israel berubah setia terhadap TUHAN dalam hal Peor, sehingga tulah turun ke antara umat TUHAN.'),(4681,3,31,17,'Maka sekarang bunuhlah semua laki-laki di antara anak-anak mereka, dan juga semua perempuan yang pernah bersetubuh dengan laki-laki haruslah kamu bunuh.'),(4682,3,31,18,'Tetapi semua orang muda di antara perempuan yang belum pernah bersetubuh dengan laki-laki haruslah kamu biarkan hidup bagimu.'),(4683,3,31,19,'Tetapi kamu ini, berkemahlah tujuh hari lamanya di luar tempat perkemahan; setiap orang yang telah membunuh orang dan setiap orang yang kena kepada orang yang mati terbunuh haruslah menghapus dosa dari dirinya pada hari yang ketiga dan pada hari yang ketujuh, kamu sendiri dan orang-orang tawananmu;'),(4684,3,31,20,'juga setiap pakaian dan setiap barang kulit dan setiap barang yang dibuat dari bulu kambing dan setiap barang kayu haruslah disucikan.\"'),(4685,3,31,21,'Lalu berkatalah imam Eleazar kepada para prajurit, yang telah pergi bertempur itu: \"Inilah ketetapan hukum yang diperintahkan TUHAN kepada Musa.'),(4686,3,31,22,'Hanya emas dan perak, tembaga, besi, timah putih dan timah hitam,'),(4687,3,31,23,'segala yang tahan api, haruslah kamu lakukan dari api, supaya menjadi tahir; tetapi semuanya itu haruslah juga disucikan dengan air penyuci; dan segala yang tidak tahan api haruslah kamu lalukan dari air.'),(4688,3,31,24,'Lagipula kamu harus mencuci pakaianmu pada hari yang ketujuh, supaya kamu tahir, dan kemudian bolehlah kamu masuk ke tempat perkemahan.\"'),(4689,3,31,25,'TUHAN berfirman kepada Musa:'),(4690,3,31,26,'Hitunglah jumlah rampasan yang telah diangkut, yang berupa manusia dan hewan--engkau ini dan imam Eleazar serta kepala-kepala puak umat itu.'),(4691,3,31,27,'Lalu bagi dualah rampasan itu, kepada pasukan bersenjata yang telah keluar berperang, dan kepada segenap umat yang lain.'),(4692,3,31,28,'Dan engkau harus mengkhususkan upeti bagi TUHAN dari para prajurit yang keluar bertempur itu, yakni satu dari setiap lima ratus, baik dari manusia, baik dari lembu, dari keledai dan dari kambing domba;'),(4693,3,31,29,'dari yang setengah yang telah didapat mereka haruslah engkau mengambilnya, lalu menyerahkannya kepada imam Eleazar, sebagai persembahan khusus bagi TUHAN.'),(4694,3,31,30,'Tetapi dari yang setengah lagi yang untuk orang Israel lain haruslah engkau mengambil satu ambilan dari setiap lima puluh, baik dari manusia, baik dari lembu, dari keledai dan dari kambing domba, jadi dari segala hewan, lalu menyerahkan semuanya kepada orang Lewi yang memelihara Kemah Suci TUHAN.\"'),(4695,3,31,31,'Kemudian Musa dan imam Eleazar melakukan seperti yang diperintahkan TUHAN kepada Musa.'),(4696,3,31,32,'Adapun rampasan, yakni yang masih tinggal dari apa yang telah dijarah laskar itu berjumlah: enam ratus tujuh puluh lima ribu ekor kambing domba'),(4697,3,31,33,'dan tujuh puluh dua ribu ekor lembu,'),(4698,3,31,34,'dan enam puluh satu ribu ekor keledai,'),(4699,3,31,35,'selanjutnya orang-orang, yaitu perempuan-perempuan yang belum pernah bersetubuh dengan laki-laki, seluruhnya tiga puluh dua ribu orang.'),(4700,3,31,36,'Yang setengah yang menjadi bagian orang-orang yang telah keluar berperang itu jumlahnya tiga ratus tiga puluh tujuh ribu lima ratus ekor kambing domba,'),(4701,3,31,37,'jadi upeti bagi TUHAN dari kambing domba itu ada enam ratus tujuh puluh lima ekor;'),(4702,3,31,38,'lembu-lembu tiga puluh enam ribu ekor, jadi upetinya bagi TUHAN ada tujuh puluh dua ekor;'),(4703,3,31,39,'keledai-keledai tiga puluh ribu lima ratus ekor, jadi upetinya bagi TUHAN ada enam puluh satu ekor;'),(4704,3,31,40,'dan orang-orang enam belas ribu orang, jadi upetinya bagi TUHAN tiga puluh dua orang.'),(4705,3,31,41,'Lalu Musa menyerahkan upeti yang dikhususkan bagi TUHAN itu kepada imam Eleazar, seperti yang diperintahkan TUHAN kepada Musa.'),(4706,3,31,42,'Yang setengah lagi yang menjadi bagian orang Israel lain, yang dipisahkan Musa dari bagian orang-orang yang telah berperang itu,'),(4707,3,31,43,'yaitu yang setengah yang menjadi bagian umat yang lain itu: domba-domba tiga ratus tiga puluh tujuh ribu lima ratus ekor,'),(4708,3,31,44,'lembu-lembu tiga puluh enam ribu ekor,'),(4709,3,31,45,'keledai-keledai tiga puluh ribu lima ratus ekor,'),(4710,3,31,46,'dan orang-orang enam belas ribu orang.'),(4711,3,31,47,'Lalu Musa mengambil dari yang setengah yang menjadi bagian orang Israel lain itu satu ambilan dari setiap lima puluh, baik dari manusia baik dari hewan, kemudian menyerahkan semuanya kepada orang Lewi yang memelihara Kemah Suci, seperti yang diperintahkan TUHAN kepada Musa.'),(4712,3,31,48,'Lalu mendekatlah para pemimpin tentara, yakni kepala-kepala pasukan seribu dan kepala-kepala pasukan seratus, kepada Musa'),(4713,3,31,49,'serta berkata kepadanya: \"Hamba-hambamu ini telah menghitung jumlah prajurit yang ada di bawah kuasa kami dan dari mereka tidak ada seorangpun yang hilang.'),(4714,3,31,50,'Sebab itu kami mempersembahkan sebagai persembahan kepada TUHAN apa yang didapat masing-masing, yakni barang-barang emas, gelang kaki, gelang tangan, cincin meterai, anting-anting dan kerongsang untuk mengadakan pendamaian bagi nyawa kami di hadapan TUHAN.\"'),(4715,3,31,51,'Maka Musa dan imam Eleazar menerima dari mereka emas itu, semuanya barang-barang tempaan.'),(4716,3,31,52,'Dan segala emas persembahan khusus yang dipersembahkan mereka kepada TUHAN, yakni yang dari pihak kepala-kepala pasukan seribu dan kepala-kepala pasukan seratus, ada enam belas ribu tujuh ratus lima puluh syikal beratnya.'),(4717,3,31,53,'Tetapi prajurit-prajurit itu masing-masing telah mengambil jarahan bagi dirinya sendiri.'),(4718,3,31,54,'Setelah Musa dan imam Eleazar menerima emas itu dari pihak kepala-kepala pasukan seribu dan kepala-kepala pasukan seratus, maka mereka membawanya ke dalam Kemah Pertemuan sebagai peringatan di hadapan TUHAN untuk mengingat orang Israel.'),(4719,3,32,1,'Adapun bani Ruben dan bani Gad ternaknya banyak, bahkan sangat banyak sekali. Ketika mereka melihat tanah Yaezer dan tanah Gilead, tampaklah tempat itu tempat yang baik untuk peternakan.'),(4720,3,32,2,'Lalu datanglah bani Gad dan bani Ruben dan berkata kepada Musa, imam Eleazar dan para pemimpin umat itu:'),(4721,3,32,3,'Atarot, Dibon, Yaezer, Nimra, Hesybon, Eleale, Sebam, Nebo dan Beon,'),(4722,3,32,4,'negeri yang telah dikalahkan oleh TUHAN untuk umat Israel, itulah suatu negeri yang baik untuk peternakan dan hamba-hambamu ini memang ada ternaknya.\"'),(4723,3,32,5,'Lagi kata mereka: \"Jika kami mendapat kasihmu, biarlah negeri ini diberikan kepada hamba-hambamu ini sebagai milik; janganlah kami harus pindah ke seberang sungai Yordan.\"'),(4724,3,32,6,'Jawab Musa kepada bani Gad dan bani Ruben itu: \"Masakan saudara-saudaramu pergi berperang dan kamu tinggal di sini?'),(4725,3,32,7,'Mengapa kamu hendak membuat enggan hati orang Israel untuk menyeberang ke negeri yang diberikan TUHAN kepada mereka?'),(4726,3,32,8,'Demikian juga dilakukan bapa-bapamu, ketika aku menyuruh mereka dari Kadesh-Barnea untuk melihat-lihat negeri itu;'),(4727,3,32,9,'mereka berjalan sampai ke lembah Eskol, melihat-lihat negeri dan membuat enggan hati orang Israel, sehingga mereka tidak mau pergi ke negeri yang diberikan TUHAN kepada mereka.'),(4728,3,32,10,'Maka bangkitlah murka TUHAN pada waktu itu dan Ia bersumpah:'),(4729,3,32,11,'Bahwasanya orang-orang yang telah berjalan dari Mesir, yang berumur dua puluh tahun ke atas, tidak akan melihat negeri yang Kujanjikan dengan bersumpah kepada Abraham, Ishak dan Yakub, oleh karena mereka tidak mengikut Aku dengan sepenuh hatinya,'),(4730,3,32,12,'kecuali Kaleb bin Yefune, orang Kenas itu, dan Yosua bin Nun, sebab keduanya mengikut TUHAN dengan sepenuh hatinya.'),(4731,3,32,13,'Sebab itu bangkitlah murka TUHAN kepada orang Israel, sehingga Ia membuat mereka mengembara di padang gurun empat puluh tahun lamanya, sampai habis mati segenap angkatan yang telah berbuat jahat di mata TUHAN.'),(4732,3,32,14,'Dan sekarang kamu bangkit ganti bapa-bapamu, suatu kawanan orang-orang berdosa, untuk menambah lagi murka TUHAN yang menyala-nyala kepada orang Israel itu.'),(4733,3,32,15,'Jika kamu berbalik membelakangi Dia, maka kamu akan lebih lama lagi dibiarkan-Nya tinggal di padang gurun dan kamu akan membawa kemusnahan atas seluruh bangsa ini.\"'),(4734,3,32,16,'Tetapi mendekatlah mereka kepadanya serta berkata: \"Kami hendak mendirikan kandang-kandang kambing domba di sini untuk ternak kami dan kota-kota untuk anak-anak kami,'),(4735,3,32,17,'tetapi kami sendiri akan mempersenjatai diri dan dengan bersegera kami akan berjalan di depan orang Israel, sampai kami membawa mereka ke tempatnya; sementara itu anak-anak kami akan tinggal dalam kota-kota yang berkubu oleh karena penduduk negeri ini;'),(4736,3,32,18,'kami tidak akan pulang ke rumah kami, sampai setiap orang Israel memperoleh milik pusakanya;'),(4737,3,32,19,'sebab kami tidak mau menerima milik pusaka di seberang sungai Yordan sana dan seterusnya, apabila kami mendapat milik pusaka di seberang sungai Yordan sini, di sebelah timur.\"'),(4738,3,32,20,'Lalu berkatalah Musa kepada mereka: \"Jika kamu hendak berbuat demikian, jika kamu hendak mempersenjatai diri untuk berperang di hadapan TUHAN,'),(4739,3,32,21,'dan setiap orang dari kamu yang telah bersenjata hendak menyeberangi sungai Yordan di hadapan TUHAN, sampai Ia menghalau musuh-musuh-Nya dari hadapan-Nya,'),(4740,3,32,22,'sehingga negeri itu takluk ke hadapan TUHAN, dan jika kemudian kamu pulang, maka akan bebaslah kamu dari kewajibanmu kepada TUHAN dan kepada Israel, dan negeri inipun akan menjadi milikmu di hadapan TUHAN.'),(4741,3,32,23,'Tetapi jika kamu tidak berbuat demikian, sesungguhnya kamu berdosa kepada TUHAN, dan kamu akan mengalami, bahwa dosamu itu akan menimpa kamu.'),(4742,3,32,24,'Dirikanlah kota-kota bagi anak-anakmu dan kandang-kandang bagi kambing dombamu, dan perbuatlah apa yang telah kamu ucapkan.\"'),(4743,3,32,25,'Maka berkatalah bani Gad dan bani Ruben itu kepada Musa: \"Hamba-hambamu ini akan berbuat seperti yang diperintahkan tuanku.'),(4744,3,32,26,'Anak-anak dan isteri-isteri kami, ternak dan hewan kami akan tinggal di sini di kota-kota Gilead,'),(4745,3,32,27,'tetapi hamba-hambamu ini akan menyeberang di hadapan TUHAN untuk bertempur, yakni setiap orang yang bersenjata untuk berperang, seperti yang dikatakan tuanku.\"'),(4746,3,32,28,'Lalu Musa memberi perintah mengenai mereka kepada imam Eleazar dan kepada Yosua bin Nun, dan kepada kepala-kepala puak dari suku-suku Israel,'),(4747,3,32,29,'kata Musa kepada mereka: \"Jika bani Gad dan bani Ruben itu telah menyeberangi sungai Yordan bersama-sama dengan kamu untuk berperang di hadapan TUHAN, yakni semuanya orang yang bersenjata untuk berperang, dan jika negeri itu telah takluk kepadamu, maka haruslah kamu memberikan tanah Gilead kepada mereka sebagai milik.'),(4748,3,32,30,'Tetapi jika mereka tidak menyeberang dengan bersenjata bersama-sama dengan kamu, maka haruslah mereka menerima tanah miliknya di tengah-tengahmu di tanah Kanaan.\"'),(4749,3,32,31,'Lalu bani Gad dan bani Ruben itu menjawab: \"Apa yang difirmankan TUHAN kepada hamba-hambamu ini akan kami lakukan.'),(4750,3,32,32,'Kami sendiri akan menyeberang dengan bersenjata di hadapan TUHAN ke tanah Kanaan, tetapi bagi kami tetaplah tanah milik pusaka kami di seberang sungai Yordan sini.\"'),(4751,3,32,33,'Lalu Musa memberikan kepada mereka, kepada bani Gad, kepada bani Ruben dan kepada setengah suku Manasye bin Yusuf: kerajaan Sihon, raja orang Amori, dan kerajaan Og, raja Basan, yakni negeri mereka beserta kota-kotanya di seluruh negeri itu, dengan daerah-daerah setiap kota itu.'),(4752,3,32,34,'Maka bani Gad membangun kota-kota Dibon, Atarot, Aroer,'),(4753,3,32,35,'Atarot-Sofan, Yaezer, Yogbeha,'),(4754,3,32,36,'Bet-Nimra, Bet-Haran, sebagai kota-kota yang berkubu dan sebagai tempat kandang-kandang kambing domba.'),(4755,3,32,37,'Dan bani Ruben membangun kota-kota Hesybon, Eleale, Kiryataim,'),(4756,3,32,38,'Nebo, Baal-Meon, --dengan mengganti nama-namanya--dan Sibma; dan mereka memberi nama lain kepada kota-kota yang dibangun mereka itu.'),(4757,3,32,39,'Bani Makhir bin Manasye pergi ke Gilead; mereka merebutnya dan menghalaukan orang Amori yang ada di sana.'),(4758,3,32,40,'Lalu Musa memberikan Gilead kepada Makhir bin Manasye dan diamlah ia di sana.'),(4759,3,32,41,'Yair, anak Manasye, pergi merebut dusun-dusunnya dan menamainya Hawot-Yair.'),(4760,3,32,42,'Nobah pergi merebut Kenat dengan segala anak kotanya dan menamainya Nobah menurut namanya sendiri.'),(4761,3,33,1,'Inilah tempat-tempat persinggahan orang Israel, setelah mereka keluar dari tanah Mesir, pasukan demi pasukan, di bawah pimpinan Musa dan Harun;'),(4762,3,33,2,'Musa menuliskan perjalanan mereka dari tempat persinggahan ke tempat persinggahan sesuai dengan titah TUHAN; dan inilah tempat-tempat persinggahan mereka dalam perjalanan mereka:'),(4763,3,33,3,'Mereka berangkat dari Rameses pada bulan yang pertama, pada hari yang kelima belas bulan yang pertama itu; pada hari sesudah Paskah berjalanlah orang Israel keluar, oleh tangan yang dinaikkan, di depan mata semua orang Mesir,'),(4764,3,33,4,'sementara orang Mesir sedang menguburkan orang-orang yang telah dibunuh TUHAN di antara mereka, yakni semua anak sulung; sebab TUHAN telah menjatuhkan hukuman-hukuman kepada para allah mereka.'),(4765,3,33,5,'Berangkatlah orang Israel dari Rameses, lalu berkemah di Sukot.'),(4766,3,33,6,'Mereka berangkat dari Sukot, lalu berkemah di Etam yang di tepi padang gurun.'),(4767,3,33,7,'Mereka berangkat dari Etam, lalu balik kembali ke Pi-Hahirot yang di depan Baal-Zefon, kemudian berkemah di tentangan Migdol.'),(4768,3,33,8,'Mereka berangkat dari Pi-Hahirot dan lewat dari tengah-tengah laut ke padang gurun, lalu mereka berjalan tiga hari perjalanan jauhnya di padang gurun Etam, kemudian mereka berkemah di Mara.'),(4769,3,33,9,'Mereka berangkat dari Mara, lalu sampai ke Elim; di Elim ada dua belas mata air dan tujuh puluh pohon korma; di sanalah mereka berkemah.'),(4770,3,33,10,'Mereka berangkat dari Elim, lalu berkemah di tepi Laut Teberau.'),(4771,3,33,11,'Mereka berangkat dari Laut Teberau, lalu berkemah di padang gurun Sin.'),(4772,3,33,12,'Mereka berangkat dari padang gurun Sin, lalu berkemah di Dofka.'),(4773,3,33,13,'Mereka berangkat dari Dofka, lalu berkemah di Alus.'),(4774,3,33,14,'Mereka berangkat dari Alus, lalu berkemah di Rafidim, dan di sana tidak ada air minum untuk bangsa itu.'),(4775,3,33,15,'Mereka berangkat dari Rafidim, lalu berkemah di padang gurun Sinai.'),(4776,3,33,16,'Mereka berangkat dari padang gurun Sinai, lalu berkemah di Kibrot-Taawa.'),(4777,3,33,17,'Mereka berangkat dari Kibrot-Taawa, lalu berkemah di Hazerot.'),(4778,3,33,18,'Mereka berangkat dari Hazerot, lalu berkemah di Ritma.'),(4779,3,33,19,'Mereka berangkat dari Ritma, lalu berkemah di Rimon-Peros.'),(4780,3,33,20,'Mereka berangkat dari Rimon-Peros, lalu berkemah di Libna.'),(4781,3,33,21,'Mereka berangkat dari Libna, lalu berkemah di Risa.'),(4782,3,33,22,'Mereka berangkat dari Risa, lalu berkemah di Kehelata.'),(4783,3,33,23,'Mereka berangkat dari Kehelata, lalu berkemah di Har-Syafer.'),(4784,3,33,24,'Mereka berangkat dari Har-Syafer, lalu berkemah di Harada.'),(4785,3,33,25,'Mereka berangkat dari Harada, lalu berkemah di Makhelot.'),(4786,3,33,26,'Mereka berangkat dari Makhelot, lalu berkemah di Tahat.'),(4787,3,33,27,'Mereka berangkat dari Tahat, lalu berkemah di Tarah.'),(4788,3,33,28,'Mereka berangkat dari Tarah, lalu berkemah di Mitka.'),(4789,3,33,29,'Mereka berangkat dari Mitka, lalu berkemah di Hasmona.'),(4790,3,33,30,'Mereka berangkat dari Hasmona, lalu berkemah di Moserot.'),(4791,3,33,31,'Mereka berangkat dari Moserot, lalu berkemah di Bene-Yaakan.'),(4792,3,33,32,'Mereka berangkat dari Bene-Yaakan, lalu berkemah di Hor-Gidgad.'),(4793,3,33,33,'Mereka berangkat dari Hor-Gidgad, lalu berkemah di Yotbata.'),(4794,3,33,34,'Mereka berangkat dari Yotbata, lalu berkemah di Abrona.'),(4795,3,33,35,'Mereka berangkat dari Abrona, lalu berkemah di Ezion-Geber.'),(4796,3,33,36,'Mereka berangkat dari Ezion-Geber, lalu berkemah di padang gurun Zin, yaitu Kadesh.'),(4797,3,33,37,'Mereka berangkat dari Kadesh, lalu berkemah di gunung Hor, di perbatasan tanah Edom.'),(4798,3,33,38,'Ketika itu imam Harun naik ke gunung Hor sesuai dengan titah TUHAN, dan di situ ia mati pada tahun keempat puluh sesudah orang Israel keluar dari tanah Mesir, pada bulan yang kelima, pada tanggal satu bulan itu;'),(4799,3,33,39,'Harun berumur seratus dua puluh tiga tahun, ketika ia mati di gunung Hor.'),(4800,3,33,40,'Pada waktu itu raja negeri Arad, orang Kanaan itu, yang tinggal di Tanah Negeb di tanah Kanaan, mendengar kabar tentang kedatangan orang Israel.'),(4801,3,33,41,'Berangkatlah mereka dari gunung Hor, lalu berkemah di Zalmona.'),(4802,3,33,42,'Mereka berangkat dari Zalmona, lalu berkemah di Funon.'),(4803,3,33,43,'Mereka berangkat dari Funon, lalu berkemah di Obot.'),(4804,3,33,44,'Mereka berangkat dari Obot, lalu berkemah dekat reruntuhan di Abarim di daerah Moab.'),(4805,3,33,45,'Mereka berangkat dari reruntuhan itu, lalu berkemah di Dibon-Gad.'),(4806,3,33,46,'Mereka berangkat dari Dibon-Gad, lalu berkemah di Almon-Diblataim.'),(4807,3,33,47,'Mereka berangkat dari Almon-Diblataim, lalu berkemah di pegunungan Abarim di depan Nebo.'),(4808,3,33,48,'Mereka berangkat dari pegunungan Abarim, lalu berkemah di dataran Moab di tepi sungai Yordan dekat Yerikho.'),(4809,3,33,49,'Mereka berkemah di tepi sungai Yordan, dari Bet-Yesimot sampai ke Abel-Sitim di dataran Moab.'),(4810,3,33,50,'TUHAN berfirman kepada Musa di dataran Moab di tepi sungai Yordan dekat Yerikho:'),(4811,3,33,51,'Berbicaralah kepada orang Israel dan katakanlah kepada mereka: Apabila kamu menyeberangi sungai Yordan ke tanah Kanaan,'),(4812,3,33,52,'maka haruslah kamu menghalau semua penduduk negeri itu dari depanmu dan membinasakan segala batu berukir kepunyaan mereka; juga haruslah kamu membinasakan segala patung tuangan mereka dan memusnahkan segala bukit pengorbanan mereka.'),(4813,3,33,53,'Haruslah kamu menduduki negeri itu dan diam di sana, sebab kepadamulah Kuberikan negeri itu untuk diduduki.'),(4814,3,33,54,'Maka haruslah kamu membagi negeri itu sebagai milik pusaka dengan membuang undi menurut kaummu: kepada yang besar jumlahnya haruslah kamu memberikan milik pusaka yang besar, dan kepada yang kecil jumlahnya haruslah kamu memberikan milik pusaka yang kecil; yang ditunjuk oleh undi bagi masing-masing, itulah bagian undiannya; menurut suku nenek moyangmu haruslah kamu membagi milik pusaka itu.'),(4815,3,33,55,'Tetapi jika kamu tidak menghalau penduduk negeri itu dari depanmu, maka orang-orang yang kamu tinggalkan hidup dari mereka akan menjadi seperti selumbar di matamu dan seperti duri yang menusuk lambungmu, dan mereka akan menyesatkan kamu di negeri yang kamu diami itu.'),(4816,3,33,56,'Maka akan Kulakukan kepadamu seperti yang Kurancang melakukan kepada mereka.\"'),(4817,3,34,1,'TUHAN berfirman kepada Musa:'),(4818,3,34,2,'Perintahkanlah kepada orang Israel dan katakanlah kepada mereka: Apabila kamu masuk ke negeri Kanaan, maka inilah negeri yang akan jatuh kepadamu sebagai milik pusaka, yakni tanah Kanaan menurut batas-batasnya.'),(4819,3,34,3,'Adapun sisi selatanmu ialah dari padang gurun Zin menyusur Edom, maka batas selatanmu mulai dari ujung Laut Asin di sebelah timur.'),(4820,3,34,4,'Lalu batasmu membelok di selatan pendakian Akrabim, terus ke Zin dan berakhir di sebelah selatan Kadesh-Barnea. Sesudah itu ia mencapai Hazar-Adar, dan terus ke Azmon.'),(4821,3,34,5,'Kemudian batas itu membelok dari Azmon ke sungai Mesir dan berakhir ke laut.'),(4822,3,34,6,'Batas baratmu ialah laut besar dan pantainya; itulah batas baratmu.'),(4823,3,34,7,'Inilah batas utaramu: mulai dari laut besar haruslah kamu buat tanda batas ke gunung Hor,'),(4824,3,34,8,'dari gunung Hor harus kamu buat tanda batas ke jalan yang menuju ke Hamat, lalu batas itu mencapai Zedad.'),(4825,3,34,9,'Kemudian batas itu mencapai Zifron dan berakhir di Hazar-Enan; itulah batas utaramu.'),(4826,3,34,10,'Sebagai batasmu di sebelah timur haruslah kamu membuat tanda batas dari Hazar-Enan ke Sefam.'),(4827,3,34,11,'Dari Sefam batas itu turun ke Ribla, di sebelah timur Ain; kemudian batas itu turun lagi dan mencapai tebing danau Kineret di sebelah timur.'),(4828,3,34,12,'Lalu batas itu turun ke sungai Yordan dan berakhir di Laut Asin. Itulah negerimu menurut batas-batasnya sekeliling.\"'),(4829,3,34,13,'Musa memerintahkan kepada orang Israel: \"Itulah negeri yang akan kamu bagi sebagai milik pusaka dengan membuang undi, yang diperintahkan TUHAN untuk diberikan kepada suku yang sembilan setengah itu.'),(4830,3,34,14,'Sebab suku bani Ruben menurut puak-puak mereka dan suku bani Gad menurut puak-puak mereka telah menerima milik pusakanya; juga suku Manasye yang setengah itu telah menerimanya.'),(4831,3,34,15,'Dua setengah suku itu telah menerima milik pusaka mereka di seberang sungai Yordan di dekat Yerikho, ke sebelah timur.\"'),(4832,3,34,16,'TUHAN berfirman kepada Musa:'),(4833,3,34,17,'Inilah nama orang-orang yang harus membagikan tanah itu kepadamu sebagai milik pusaka: imam Eleazar dan Yosua bin Nun.'),(4834,3,34,18,'Lagi haruslah kamu mengambil seorang pemimpin dari setiap suku untuk membagikan tanah itu sebagai milik pusaka.'),(4835,3,34,19,'Inilah nama orang-orang itu: dari suku Yehuda: Kaleb bin Yefune;'),(4836,3,34,20,'dari suku bani Simeon: Samuel bin Amihud;'),(4837,3,34,21,'dari suku Benyamin: Elidad bin Kislon;'),(4838,3,34,22,'dari suku bani Dan seorang pemimpin: Buki bin Yogli;'),(4839,3,34,23,'dari anak-anak Yusuf, yakni dari suku bani Manasye seorang pemimpin Haniel bin Efod;'),(4840,3,34,24,'dan dari suku bani Efraim seorang pemimpin: Kemuel bin Siftan;'),(4841,3,34,25,'dari suku bani Zebulon seorang pemimpin: Elisafan bin Parnah;'),(4842,3,34,26,'dari suku bani Isakhar seorang pemimpin: Paltiel bin Azan;'),(4843,3,34,27,'dari suku bani Asyer seorang pemimpin: Ahihud bin Selomi;'),(4844,3,34,28,'dari suku bani Naftali seorang pemimpin: Pedael bin Amihud.'),(4845,3,34,29,'Itulah orang-orang yang diperintahkan TUHAN untuk membagikan milik pusaka kepada orang Israel di tanah Kanaan.\"'),(4846,3,35,1,'TUHAN berfirman kepada Musa di dataran Moab di tepi sungai Yordan di dekat Yerikho:'),(4847,3,35,2,'Perintahkanlah kepada orang Israel, supaya dari milik pusaka kepunyaannya diberikan mereka kota-kota kepada orang Lewi untuk didiami; juga haruslah kamu memberikan kepada orang Lewi tanah-tanah penggembalaan yang di sekeliling kota-kota itu.'),(4848,3,35,3,'Kota-kota itu akan menjadi kepunyaan mereka untuk didiami dan tanah-tanah penggembalaannya untuk hewan yang mereka miliki dan untuk segala ternak mereka yang lain.'),(4849,3,35,4,'Tanah-tanah penggembalaan kota-kota yang harus kamu berikan kepada orang Lewi itu ialah dari tembok kota ke luar seribu hasta berkeliling.'),(4850,3,35,5,'Di luar kota itu haruslah kamu mengukur dua ribu hasta di sisi timur dan dua ribu hasta di sisi selatan dan dua ribu hasta di sisi barat dan dua ribu hasta di sisi utara, sehingga kota itu berada di tengah-tengah; itulah bagi mereka tanah-tanah penggembalaan kota-kota.'),(4851,3,35,6,'Mengenai kota-kota yang harus kamu berikan kepada orang Lewi itu, ialah enam kota perlindungan yang harus kamu berikan, supaya orang pembunuh dapat melarikan diri ke sana; di samping itu haruslah kamu memberikan empat puluh dua kota.'),(4852,3,35,7,'Segala kota yang harus kamu berikan kepada orang Lewi itu berjumlah empat puluh delapan kota, semuanya dengan tanah-tanah penggembalaannya.'),(4853,3,35,8,'Mengenai kota-kota yang akan kamu berikan dari tanah milik orang Israel, dari suku yang banyak jumlahnya haruslah kamu ambil banyak, dan dari suku yang sedikit jumlahnya haruslah kamu ambil sedikit. Setiap suku harus memberikan dari kota-kotanya kepada orang Lewi sekadar milik pusaka yang dibagikan kepadanya.\"'),(4854,3,35,9,'TUHAN berfirman kepada Musa:'),(4855,3,35,10,'Berbicaralah kepada orang Israel dan katakanlah kepada mereka: Apabila kamu menyeberangi sungai Yordan ke tanah Kanaan,'),(4856,3,35,11,'maka haruslah kamu memilih beberapa kota yang menjadi kota-kota perlindungan bagimu, supaya orang pembunuh yang telah membunuh seseorang dengan tidak sengaja dapat melarikan diri ke sana.'),(4857,3,35,12,'Kota-kota itu akan menjadi tempat perlindungan bagimu terhadap penuntut balas, supaya pembunuh jangan mati, sebelum ia dihadapkan kepada rapat umat untuk diadili.'),(4858,3,35,13,'Dan kota-kota yang kamu tentukan itu haruslah enam buah kota perlindungan bagimu.'),(4859,3,35,14,'Tiga kota harus kamu tentukan di seberang sungai Yordan sini dan tiga kota harus kamu tentukan di tanah Kanaan; semuanya kota-kota perlindungan.'),(4860,3,35,15,'Keenam kota itu haruslah menjadi tempat perlindungan bagi orang Israel dan bagi orang asing dan pendatang di tengah-tengahmu, supaya setiap orang yang telah membunuh seseorang dengan tidak sengaja dapat melarikan diri ke sana.'),(4861,3,35,16,'Tetapi jika ia membunuh orang itu dengan benda besi, sehingga orang itu mati, maka ia seorang pembunuh; pastilah pembunuh itu dibunuh.'),(4862,3,35,17,'Dan jika ia membunuh orang itu dengan batu di tangan yang mungkin menyebabkan matinya seseorang, sehingga orang itu mati, maka ia seorang pembunuh; pastilah pembunuh itu dibunuh.'),(4863,3,35,18,'Atau jika ia membunuh orang itu dengan benda kayu di tangan yang mungkin menyebabkan matinya seseorang, sehingga orang itu mati, maka ia seorang pembunuh; pastilah pembunuh itu dibunuh.'),(4864,3,35,19,'Penuntut darahlah yang harus membunuh pembunuh itu; pada waktu bertemu dengan dia ia harus membunuh dia.'),(4865,3,35,20,'Juga jika ia menumbuk orang itu karena benci atau melempar dia dengan sengaja, sehingga orang itu mati,'),(4866,3,35,21,'atau jika ia memukul dia dengan tangannya karena perasaan permusuhan, sehingga orang itu mati, maka pastilah si pemukul itu dibunuh; ia seorang pembunuh; penuntut darah harus membunuh pembunuh itu, pada waktu bertemu dengan dia.'),(4867,3,35,22,'Tetapi jika ia sekonyong-konyong menumbuk orang itu dengan tidak ada perasaan permusuhan, atau dengan tidak sengaja melemparkan sesuatu benda kepadanya,'),(4868,3,35,23,'atau dengan kurang ingat menjatuhkan kepada orang itu sesuatu batu yang mungkin menyebabkan matinya seseorang, sehingga orang itu mati, sedangkan dia tidak merasa bermusuh dengan orang itu dan juga tidak mengikhtiarkan celakanya,'),(4869,3,35,24,'maka haruslah rapat umat mengadili antara orang yang membunuh itu dan penuntut darah, menurut hukum-hukum ini,'),(4870,3,35,25,'dan haruslah rapat umat membebaskan pembunuh dari tangan penuntut darah, dan haruslah rapat umat mengembalikan dia ke kota perlindungan, ke tempat ia telah melarikan diri; di situlah ia harus tinggal sampai matinya imam besar yang telah diurapi dengan minyak yang kudus.'),(4871,3,35,26,'Tetapi jika terjadi bahwa pembunuh itu keluar dari batas kota perlindungan, tempat ia melarikan diri,'),(4872,3,35,27,'dan penuntut darah mendapat dia di luar batas kota perlindungannya, dan penuntut darah membunuh pembunuh itu, maka tidaklah ia berhutang darah,'),(4873,3,35,28,'sebab pembunuh itu wajib tinggal di kota perlindungan sampai matinya imam besar, tetapi sesudah matinya imam besar bolehlah pembunuh itu kembali ke tanah kepunyaannya sendiri.'),(4874,3,35,29,'Itulah semuanya yang harus menjadi ketetapan hukum bagimu turun-temurun di segala tempat kediamanmu.'),(4875,3,35,30,'Setiap orang yang telah membunuh seseorang haruslah dibunuh sebagai pembunuh menurut keterangan saksi-saksi, tetapi kalau hanya satu orang saksi saja tidak cukup untuk memberi keterangan terhadap seseorang dalam perkara hukuman mati.'),(4876,3,35,31,'Janganlah kamu menerima uang tebusan karena nyawa seorang pembunuh yang kesalahannya setimpal dengan hukuman mati, tetapi pastilah ia dibunuh.'),(4877,3,35,32,'Juga janganlah kamu menerima uang tebusan karena seseorang yang telah melarikan diri ke kota perlindungannya, supaya ia boleh kembali untuk diam di tanahnya sebelum matinya imam besar.'),(4878,3,35,33,'Jadi janganlah kamu mencemarkan negeri tempat tinggalmu, sebab darah itulah yang mencemarkan negeri itu, maka bagi negeri itu tidak dapat diadakan pendamaian oleh karena darah yang tertumpah di sana, kecuali dengan darah orang yang telah menumpahkannya.'),(4879,3,35,34,'Maka janganlah najiskan negeri tempat kedudukanmu, yang di tengah-tengahnya Aku diam, sebab Aku, TUHAN, diam di tengah-tengah orang Israel.\"'),(4880,3,36,1,'Mendekatlah kepala-kepala puak dari kaum bani Gilead bin Makhir bin Manasye, salah satu dari kaum-kaum keturunan Yusuf, dan berbicara di depan Musa dan pemimpin-pemimpin, kepala-kepala suku orang Israel,'),(4881,3,36,2,'kata mereka: \"TUHAN telah memerintahkan tuanku untuk memberikan tanah itu kepada orang Israel sebagai milik pusaka dengan membuang undi, dan oleh TUHAN telah diperintahkan kepada tuanku untuk memberikan milik pusaka Zelafehad, saudara kami, kepada anak-anaknya yang perempuan.'),(4882,3,36,3,'Tetapi seandainya mereka kawin dengan salah seorang anak laki-laki dari suku lain di antara orang Israel, maka milik pusaka perempuan itu akan dikurangkan dari milik pusaka bapa-bapa kami dan akan ditambahkan kepada milik pusaka suku yang akan dimasukinya, jadi akan dikurangkan dari milik pusaka yang diundikan kepada kami.'),(4883,3,36,4,'Maka apabila tiba tahun Yobel bagi orang Israel, milik pusaka perempuan itu akan ditambahkan kepada milik pusaka suku yang akan dimasukinya dan akan dikurangkan dari milik pusaka suku nenek moyang kami.\"'),(4884,3,36,5,'Lalu Musa memerintahkan kepada orang Israel sesuai dengan titah TUHAN: \"Perkataan suku keturunan Yusuf itu benar.'),(4885,3,36,6,'Inilah firman yang diperintahkan TUHAN mengenai anak-anak perempuan Zelafehad, bunyinya: Mereka boleh kawin dengan siapa saja yang suka kepada mereka, asal mereka kawin di lingkungan salah satu kaum dari suku ayah mereka.'),(4886,3,36,7,'Sebab milik pusaka orang Israel tidak boleh beralih dari suku ke suku, tetapi orang Israel haruslah masing-masing memegang milik pusaka suku nenek moyangnya.'),(4887,3,36,8,'Jadi setiap anak perempuan di antara suku-suku orang Israel yang telah mewarisi milik pusaka, haruslah kawin dengan seorang dari salah satu kaum yang termasuk suku ayahnya, supaya setiap orang Israel mewarisi milik pusaka nenek moyangnya.'),(4888,3,36,9,'Sebab milik pusaka itu tidak boleh beralih dari suku ke suku, tetapi suku-suku orang Israel haruslah masing-masing memegang milik pusakanya sendiri.\"'),(4889,3,36,10,'Seperti yang diperintahkan TUHAN kepada Musa, demikianlah diperbuat anak-anak perempuan Zelafehad.'),(4890,3,36,11,'Maka Mahla, Tirza, Hogla, Milka dan Noa, anak-anak perempuan Zelafehad, kawin dengan anak-anak lelaki dari pihak saudara-saudara ayah mereka;'),(4891,3,36,12,'mereka kawin dengan laki-laki dari kaum-kaum bani Manasye bin Yusuf, sehingga milik pusaka mereka tetap tinggal pada suku kaum ayahnya.'),(4892,3,36,13,'Itulah perintah dan peraturan yang diperintahkan TUHAN kepada orang Israel dengan perantaraan Musa di dataran Moab di tepi sungai Yordan dekat Yerikho.'),(4893,4,1,1,'Inilah perkataan-perkataan yang diucapkan Musa kepada seluruh orang Israel di seberang sungai Yordan, di padang gurun, di Araba-Yordan, di tentangan Suf, antara Paran dengan Tofel, Laban, Hazerot dan Di-Zahab.'),(4894,4,1,2,'Sebelas hari perjalanan jauhnya dari Horeb sampai Kadesh-Barnea, melalui jalan pegunungan Seir.'),(4895,4,1,3,'Pada tanggal satu bulan sebelas tahun keempat puluh berbicaralah Musa kepada orang Israel sesuai dengan segala yang diperintahkan TUHAN kepadanya demi mereka,'),(4896,4,1,4,'setelah ia memukul kalah Sihon, raja orang Amori, yang diam di Hesybon, dan Og, raja negeri Basan, yang diam di Asytarot, dekat Edrei.'),(4897,4,1,5,'Di seberang sungai Yordan, di tanah Moab, mulailah Musa menguraikan hukum Taurat ini, katanya:'),(4898,4,1,6,'TUHAN, Allah kita, telah berfirman kepada kita di Horeb, demikian: Telah cukup lama kamu tinggal di gunung ini.'),(4899,4,1,7,'Majulah, berangkatlah, pergilah ke pegunungan orang Amori dan kepada semua tetangga mereka di Araba-Yordan, di Pegunungan, di Daerah Bukit, di Tanah Negeb dan di tepi pantai laut, yakni negeri orang Kanaan, dan ke gunung Libanon sampai Efrat, sungai besar itu.'),(4900,4,1,8,'Ketahuilah, Aku telah menyerahkan negeri itu kepadamu; masukilah, dudukilah negeri yang dijanjikan TUHAN dengan sumpah kepada nenek moyangmu, yakni Abraham, Ishak dan Yakub, untuk memberikannya kepada mereka dan kepada keturunannya.\"'),(4901,4,1,9,'Pada waktu itu aku berkata kepadamu, demikian: Seorang diri aku tidak dapat memikul tanggung jawab atas kamu.'),(4902,4,1,10,'TUHAN, Allahmu, telah membuat kamu banyak dan sesungguhnya, sekarang kamu sudah seperti bintang-bintang di langit banyaknya.'),(4903,4,1,11,'TUHAN, Allah nenek moyangmu, kiranya menambahi kamu seribu kali lagi dari jumlahmu sekarang dan memberkati kamu seperti yang dijanjikan-Nya kepadamu.'),(4904,4,1,12,'Tetapi bagaimana seorang diri aku dapat memikul tanggung jawab atas kesusahanmu, atas bebanmu dan perkaramu?'),(4905,4,1,13,'Kemukakanlah dari suku-sukumu orang-orang yang bijaksana, berakal budi dan berpengalaman, maka aku akan mengangkat mereka menjadi kepala atas kamu.'),(4906,4,1,14,'Lalu kamu menjawab aku: Memang baik apa yang kauanjurkan untuk dilakukan itu.'),(4907,4,1,15,'Kemudian aku mengambil kepala-kepala sukumu, yakni orang-orang yang bijaksana dan berpengalaman, lalu aku mengangkat mereka menjadi pemimpin atas kamu, yakni sebagai kepala pasukan seribu, kepala pasukan seratus, kepala pasukan lima puluh dan kepala pasukan sepuluh dan sebagai pengatur pasukan bagi suku-sukumu.'),(4908,4,1,16,'Dan pada waktu itu aku memerintahkan kepada para hakimmu, demikian: Berilah perhatian kepada perkara-perkara di antara saudara-saudaramu dan berilah keputusan yang adil di dalam perkara-perkara antara seseorang dengan saudaranya atau dengan orang asing yang ada padanya.'),(4909,4,1,17,'Dalam mengadili jangan pandang bulu. Baik perkara orang kecil maupun perkara orang besar harus kamu dengarkan. Jangan gentar terhadap siapapun, sebab pengadilan adalah kepunyaan Allah. Tetapi perkara yang terlalu sukar bagimu, harus kamu hadapkan kepadaku, supaya aku mendengarnya.'),(4910,4,1,18,'Demikianlah aku pada waktu itu memerintahkan kepadamu segala hal yang harus kamu lakukan.\"'),(4911,4,1,19,'Kemudian kita berangkat dari Horeb dan berjalan melalui segenap padang gurun yang besar dan dahsyat yang telah kamu lihat itu, ke arah pegunungan orang Amori, seperti yang diperintahkan kepada kita oleh TUHAN, Allah kita; lalu kita sampai ke Kadesh-Barnea.'),(4912,4,1,20,'Ketika itu aku berkata kepadamu: Kamu sudah sampai ke pegunungan orang Amori, yang diberikan kepada kita oleh TUHAN, Allah kita.'),(4913,4,1,21,'Ketahuilah, TUHAN, Allahmu, telah menyerahkan negeri itu kepadamu. Majulah, dudukilah, seperti yang difirmankan kepadamu oleh TUHAN, Allah nenek moyangmu. Janganlah takut dan janganlah patah hati.'),(4914,4,1,22,'Lalu kamu sekalian mendekati aku dan berkata: Marilah kita menyuruh beberapa orang mendahului kita untuk menyelidiki negeri itu bagi kita dan membawa kabar kepada kita tentang jalan yang akan kita lalui, dan tentang kota-kota yang akan kita datangi.'),(4915,4,1,23,'Hal itu kupandang baik. Jadi aku memilih dari padamu dua belas orang, dari tiap-tiap suku seorang.'),(4916,4,1,24,'Mereka pergi dan berjalan ke arah pegunungan, lalu sampai ke lembah Eskol, kemudian menyelidiki negeri itu.'),(4917,4,1,25,'Maka mereka mengambil buah-buahan negeri itu dan membawanya kepada kita. Pula mereka membawa kabar kepada kita, demikian: Negeri yang diberikan TUHAN, Allah kita, kepada kita itu baik.'),(4918,4,1,26,'Tetapi kamu tidak mau berjalan ke sana, kamu menentang titah TUHAN, Allahmu.'),(4919,4,1,27,'Kamu menggerutu di dalam kemahmu serta berkata: Karena TUHAN membenci kita, maka Ia membawa kita keluar dari tanah Mesir untuk menyerahkan kita ke dalam tangan orang Amori, supaya dimusnahkan.'),(4920,4,1,28,'Ke manakah pula kita maju? Saudara-saudara kita telah membuat hati kita tawar dengan mengatakan: Orang-orang itu lebih besar dan lebih tinggi dari pada kita, kota-kota di sana besar dan kubu-kubunya sampai ke langit, lagipula kami melihat orang-orang Enak di sana.'),(4921,4,1,29,'Ketika itu aku berkata kepadamu: Janganlah gemetar, janganlah takut kepada mereka;'),(4922,4,1,30,'TUHAN, Allahmu, yang berjalan di depanmu, Dialah yang akan berperang untukmu sama seperti yang dilakukan-Nya bagimu di Mesir, di depan matamu,'),(4923,4,1,31,'dan di padang gurun, di mana engkau melihat bahwa TUHAN, Allahmu, mendukung engkau, seperti seseorang mendukung anaknya, sepanjang jalan yang kamu tempuh, sampai kamu tiba di tempat ini.'),(4924,4,1,32,'Tetapi walaupun demikian, kamu tidak percaya kepada TUHAN, Allahmu,'),(4925,4,1,33,'yang berjalan di depanmu di perjalanan untuk mencari tempat bagimu, di mana kamu dapat berkemah: dengan api pada waktu malam dan dengan awan pada waktu siang, untuk memperlihatkan kepadamu jalan yang harus kamu tempuh.\"'),(4926,4,1,34,'Ketika TUHAN mendengar gerutumu itu, Ia menjadi murka dan bersumpah:'),(4927,4,1,35,'Tidak seorangpun dari orang-orang ini, angkatan yang jahat ini, akan melihat negeri yang baik, yang dengan sumpah Kujanjikan untuk memberikannya kepada nenek moyangmu,'),(4928,4,1,36,'kecuali Kaleb bin Yefune. Dialah yang akan melihat negeri itu dan kepadanya dan kepada anak-anaknya akan Kuberikan negeri yang diinjaknya itu, karena dengan sepenuh hati ia mengikuti TUHAN.'),(4929,4,1,37,'Juga kepadaku TUHAN murka oleh karena kamu, dan Ia berfirman: Juga engkau tidak akan masuk ke sana.'),(4930,4,1,38,'Yosua bin Nun, pelayanmu, dialah yang akan masuk ke sana. Berilah kepadanya semangat, sebab dialah yang akan memimpin orang Israel sampai mereka memiliki negeri itu.'),(4931,4,1,39,'Dan anak-anakmu yang kecil, yang kamu katakan akan menjadi rampasan, dan anak-anakmu yang sekarang ini yang belum mengetahui tentang yang baik dan yang jahat, merekalah yang akan masuk ke sana dan kepada merekalah Aku akan memberikannya, dan merekalah yang akan memilikinya.'),(4932,4,1,40,'Tetapi kamu ini, baliklah, berangkatlah ke padang gurun, ke arah Laut Teberau.\"'),(4933,4,1,41,'Lalu kamu menjawab, katamu kepadaku: Kami berbuat dosa kepada TUHAN. Kami mau maju berperang, menurut segala yang diperintahkan kepada kami oleh TUHAN, Allah kita. Dan setiap orang dari padamu menyandang senjata perangnya, sebab kamu menganggap mudah untuk berjalan maju ke arah pegunungan.'),(4934,4,1,42,'Tetapi TUHAN berfirman kepadaku: Katakanlah kepada mereka: Janganlah kamu maju dan janganlah kamu berperang, sebab Aku tidak ada di tengah-tengahmu, nanti kamu terpukul kalah oleh musuhmu.'),(4935,4,1,43,'Dan aku berbicara kepadamu tetapi kamu tidak mendengarkan, kamu menentang titah TUHAN; kamu berlaku terlalu berani dan maju ke arah pegunungan.'),(4936,4,1,44,'Kemudian orang Amori yang diam di pegunungan itu keluar menyerbu kamu, dan mereka mengejar kamu seperti lebah dan mengalahkan kamu dari Seir sampai Horma.'),(4937,4,1,45,'Lalu kamu pulang dan menangis di hadapan TUHAN; tetapi TUHAN tidak mendengarkan tangisanmu dan tidak memberi telinga kepada suaramu.'),(4938,4,1,46,'Demikianlah kamu lama tinggal di Kadesh, yakni sepanjang waktu kamu tinggal di sana.\"'),(4939,4,2,1,'Kemudian kita balik dan berangkat ke padang gurun, ke arah Laut Teberau, seperti yang difirmankan TUHAN kepadaku. Lama kita berjalan keliling pegunungan Seir.'),(4940,4,2,2,'Lalu berfirmanlah TUHAN kepadaku, demikian:'),(4941,4,2,3,'Telah cukup lamanya kamu berjalan keliling pegunungan ini, beloklah sekarang ke utara.'),(4942,4,2,4,'Perintahkanlah kepada bangsa itu, demikian: Sebentar lagi kamu akan berjalan melalui daerah saudara-saudaramu, bani Esau, yang diam di Seir; mereka akan takut kepadamu. Tetapi hati-hatilah sekali;'),(4943,4,2,5,'janganlah menyerang mereka, sebab Aku tidak akan memberikan kepadamu setapak kaki dari negeri mereka, karena kepada Esau telah Kuberikan pegunungan Seir menjadi miliknya.'),(4944,4,2,6,'Makanan haruslah kamu beli dari mereka dengan uang, supaya kamu dapat makan; juga air haruslah kamu beli dari mereka dengan uang, supaya kamu dapat minum.'),(4945,4,2,7,'Sebab TUHAN, Allahmu, memberkati engkau dalam segala pekerjaan tanganmu. Ia memperhatikan perjalananmu melalui padang gurun yang besar ini; keempat puluh tahun ini TUHAN, Allahmu, menyertai engkau, dan engkau tidak kekurangan apapun.'),(4946,4,2,8,'Kemudian kita berjalan terus, meninggalkan daerah saudara-saudara kita, bani Esau yang diam di Seir, meninggalkan jalan dari Araba-Yordan, yakni dari Elat dan Ezion-Geber. Sesudah itu kita belok dan berjalan terus ke arah padang gurun Moab.'),(4947,4,2,9,'Lalu berfirmanlah TUHAN kepadaku: Janganlah melawan Moab dan janganlah menyerang mereka, sebab Aku tidak akan memberikan kepadamu apapun dari negerinya menjadi milikmu, karena Ar telah Kuberikan kepada bani Lot menjadi miliknya.'),(4948,4,2,10,'Dahulu orang Emim diam di sana, suatu bangsa yang besar dan banyak jumlahnya, tinggi seperti orang Enak.'),(4949,4,2,11,'Mereka itupun dikira orang Refaim, seperti juga orang Enak, tetapi orang Moab menyebut mereka orang Emim.'),(4950,4,2,12,'Dan dahulu di Seir diam orang Hori, tetapi bani Esau telah menduduki daerah mereka, memunahkan mereka dari hadapannya, lalu menetap di sana menggantikan mereka, seperti yang dilakukan orang Israel dengan negeri miliknya yang diberikan TUHAN kepada mereka.'),(4951,4,2,13,'Jadi sekarang bersiaplah kamu dan seberangilah sungai Zered. Lalu kita menyeberangi sungai Zered.'),(4952,4,2,14,'Lamanya kita berjalan sejak dari Kadesh-Barnea sampai kita ada di seberang sungai Zered, ada tiga puluh delapan tahun, sampai seluruh angkatan itu, yakni prajurit, habis binasa dari perkemahan, seperti yang dijanjikan TUHAN dengan sumpah kepada mereka;'),(4953,4,2,15,'dan tangan TUHAN juga melawan mereka untuk menghamburkan mereka dari perkemahan, sampai mereka habis binasa.'),(4954,4,2,16,'Maka ketika dari bangsa itu telah habis binasa semua prajurit,'),(4955,4,2,17,'berfirmanlah TUHAN kepadaku:'),(4956,4,2,18,'Pada hari ini engkau akan berjalan melintasi perbatasan Moab, yakni Ar,'),(4957,4,2,19,'maka engkau sampai ke dekat bani Amon. Janganlah melawan mereka dan janganlah menyerang mereka, sebab Aku tidak akan memberikan kepadamu apapun dari negeri bani Amon itu menjadi milikmu, karena Aku telah memberikannya kepada bani Lot menjadi miliknya.'),(4958,4,2,20,'--Negeri inipun dikira orang negeri orang Refaim. Dahulu orang Refaim diam di sana, tetapi orang Amon menyebut mereka orang Zamzumim,'),(4959,4,2,21,'suatu bangsa yang besar dan banyak jumlahnya, tinggi seperti orang Enak, tetapi TUHAN telah memunahkan mereka dari hadapan bani Amon, sehingga orang-orang ini menduduki daerah mereka dan menetap di sana menggantikan mereka;'),(4960,4,2,22,'seperti yang dilakukan TUHAN bagi bani Esau yang diam di Seir, ketika Ia memunahkan orang Hori dari hadapan mereka, sehingga mereka menduduki daerah orang Hori itu dan menetap di sana menggantikan orang-orang itu sampai sekarang.'),(4961,4,2,23,'Juga orang Awi yang diam di kampung-kampung sampai Gaza, dipunahkan oleh orang Kaftor yang berasal dari Kaftor, lalu orang Kaftor itu menetap di sana menggantikan mereka. --'),(4962,4,2,24,'Bersiaplah kamu, berangkatlah dan seberangilah sungai Arnon. Ketahuilah, Aku menyerahkan Sihon, raja Hesybon, orang Amori itu, beserta negerinya ke dalam tanganmu; mulailah menduduki negerinya dan seranglah Sihon.'),(4963,4,2,25,'Pada hari ini Aku mulai mendatangkan ke atas bangsa-bangsa di seluruh kolong langit keseganan dan ketakutan terhadap kamu, sehingga mereka menggigil dan gemetar karena engkau, apabila mereka mendengar tentang kamu.\"'),(4964,4,2,26,'Kemudian aku menyuruh utusan dari padang gurun Kedemot kepada Sihon, raja Hesybon, menyampaikan pesan perdamaian, bunyinya:'),(4965,4,2,27,'Izinkanlah aku berjalan melalui negerimu. Aku akan tetap berjalan mengikuti jalan raya, dengan tidak menyimpang ke kanan atau ke kiri.'),(4966,4,2,28,'Juallah makanan kepadaku dengan bayaran uang, supaya aku dapat makan, dan berikanlah air kepadaku ganti uang, supaya aku dapat minum; hanya izinkanlah aku lewat dengan berjalan kaki--'),(4967,4,2,29,'seperti yang diperbuat kepadaku oleh bani Esau yang diam di Seir dan oleh orang Moab yang diam di Ar--sampai aku menyeberangi sungai Yordan pergi ke negeri yang diberikan kepada kami oleh TUHAN, Allah kami.'),(4968,4,2,30,'Tetapi Sihon, raja Hesybon, tidak mau memberi kita berjalan melalui daerahnya, sebab TUHAN, Allahmu, membuat dia keras kepala dan tegar hati, dengan maksud menyerahkan dia ke dalam tanganmu, seperti yang terjadi sekarang ini.'),(4969,4,2,31,'Lalu TUHAN berfirman kepadaku: Ketahuilah, Aku mulai menyerahkan Sihon dan negerinya kepadamu. Mulailah menduduki negerinya supaya menjadi milikmu.'),(4970,4,2,32,'Kemudian Sihon dan seluruh tentaranya maju mendatangi kita, untuk berperang dekat Yahas,'),(4971,4,2,33,'tetapi TUHAN, Allah kita, menyerahkan dia kepada kita, sehingga kita mengalahkan dia dengan anak-anaknya dan seluruh tentaranya.'),(4972,4,2,34,'Pada waktu itu kita merebut segala kotanya dan menumpas penduduk setiap kota: laki-laki dan perempuan serta anak-anak. Tidak ada seorangpun yang kita biarkan terluput;'),(4973,4,2,35,'hanya hewan kita rampas bagi kita sendiri, seperti juga jarahan dari kota-kota yang telah kita rebut.'),(4974,4,2,36,'Mulai dari Aroer, di tepi sungai Arnon, dan kota di lembah itu, sampai Gilead tidak ada kota yang bentengnya terlalu kuat bagi kita; sebab TUHAN, Allah kita, menyerahkan semuanya kepada kita.'),(4975,4,2,37,'Hanya negeri bani Amon tidak engkau dekati, baik sungai Yabok sepanjang tepinya maupun kota-kota di pegunungan, tepat seperti yang dilarang TUHAN, Allah kita.\"'),(4976,4,3,1,'Kemudian beloklah kita dan maju ke arah Basan. Dan Og, raja Basan, dengan seluruh tentaranya maju mendatangi kita, untuk berperang di Edrei.'),(4977,4,3,2,'Tetapi TUHAN berfirman kepadaku: Janganlah takut kepadanya, sebab Aku menyerahkan dia ke dalam tanganmu beserta seluruh tentaranya dan negerinya, dan perlakukanlah dia seperti yang kaulakukan terhadap Sihon, raja orang Amori, yang diam di Hesybon.'),(4978,4,3,3,'Dan TUHAN, Allah kita, menyerahkan juga Og, raja Basan, beserta seluruh tentaranya ke dalam tangan kita dan kita mengalahkan dia, sehingga tidak seorangpun luput.'),(4979,4,3,4,'Pada waktu itu kita merebut segala kotanya; tidak ada kota yang tidak kita rampas dari pada mereka: enam puluh kota, seluruh wilayah Argob, kerajaan Og di Basan.'),(4980,4,3,5,'Semuanya itu adalah kota berkubu, dengan tembok yang tinggi-tinggi, dengan pintu-pintu gerbang dan palang-palangnya; lain dari pada itu sangat banyak kota yang tidak berkubu.'),(4981,4,3,6,'Kita menumpas seluruh penduduknya, seperti yang kita lakukan terhadap Sihon, raja Hesybon, dengan menumpas penduduk setiap kota: laki-laki, perempuan dan anak-anak.'),(4982,4,3,7,'Tetapi segala hewan dan jarahan dari kota-kota itu kita rampas bagi kita sendiri.'),(4983,4,3,8,'Jadi pada waktu itu dari tangan kedua raja orang Amori itu kita merampas negeri yang di seberang sungai Yordan, mulai dari sungai Arnon sampai gunung Hermon'),(4984,4,3,9,'--orang Sidon menyebut Hermon itu Siryon dan orang Amori menyebutnya Senir--,'),(4985,4,3,10,'segala kota di dataran tinggi, seluruh Gilead dan seluruh Basan sampai Salkha dan Edrei, kota-kota kerajaan Og di Basan.'),(4986,4,3,11,'Hanya Og, raja Basan, yang tinggal hidup dari sisa-sisa orang Refaim. Sesungguhnya, ranjangnya adalah ranjang dari besi; bukankah itu masih ada di kota Raba bani Amon? Sembilan hasta panjangnya dan empat hasta lebarnya, menurut hasta biasa.\"'),(4987,4,3,12,'Adapun negeri itu telah kita duduki pada waktu itu; mulai dari Aroer yang di tepi sungai Arnon, beserta setengah dari pegunungan Gilead dengan kota-kotanya aku berikan kepada orang Ruben dan orang Gad;'),(4988,4,3,13,'dan yang masih tinggal dari Gilead beserta seluruh Basan, kerajaan Og, yakni seluruh wilayah Argob, aku berikan kepada suku Manasye yang setengah itu. --Seluruh Basan ini disebut negeri orang Refaim. --'),(4989,4,3,14,'Yair, anak Manasye, mengambil seluruh wilayah Argob sampai daerah orang Gesur dan orang Maakha, dan menamai daerah itu, yakni Basan, menurut namanya sendiri: Hawot-Yair, sampai sekarang.'),(4990,4,3,15,'Kepada Makhir kuberikan Gilead.'),(4991,4,3,16,'Kepada orang Ruben dan kepada orang Gad kuberikan sebagian dari Gilead, sebelah sini sampai sungai Arnon, yakni setengah dari sungai itu dengan daerah pinggirnya, dan sebelah sana sampai sungai Yabok, batas daerah bani Amon;'),(4992,4,3,17,'selanjutnya Araba-Yordan dan sungai Yordan dengan daerah pinggirnya, mulai dari Kineret sampai ke Laut Araba, yakni Laut Asin di kaki lereng gunung Pisga ke arah timur.'),(4993,4,3,18,'Pada waktu itu aku memerintahkan kepadamu, demikian: TUHAN, Allahmu, telah memberikan negeri ini kepadamu untuk dimiliki; namun kamu, yakni semua orang yang gagah perkasa, harus menyeberang dengan bersenjata di depan saudara-saudaramu, orang Israel.'),(4994,4,3,19,'Hanya isteri dan anak-anakmu serta ternak-ternakmu--aku tahu ada banyak ternak padamu--boleh tinggal di kota-kota yang telah kuberikan kepadamu,'),(4995,4,3,20,'sampai TUHAN mengaruniakan keamanan kepada saudara-saudaramu seperti kepadamu, dan merekapun memiliki negeri, yang diberikan TUHAN, Allahmu, kepada mereka di seberang sungai Yordan. Sesudah itu bolehlah kamu pulang, masing-masing ke tanah miliknya yang telah kuberikan kepadamu.'),(4996,4,3,21,'Dan kepada Yosua kuperintahkan pada waktu itu, demikian: Matamu sendirilah yang melihat segala yang dilakukan TUHAN, Allahmu, terhadap kedua raja itu. Demikianlah akan dilakukan TUHAN terhadap segala kerajaan, ke mana engkau pergi.'),(4997,4,3,22,'Janganlah takut kepada mereka, sebab TUHAN, Allahmu, Dialah yang berperang untukmu.\"'),(4998,4,3,23,'Juga pada waktu itu aku mohon kasih karunia dari pada TUHAN, demikian:'),(4999,4,3,24,'Ya, Tuhan ALLAH, Engkau telah mulai memperlihatkan kepada hamba-Mu ini kebesaran-Mu dan tangan-Mu yang kuat; sebab allah manakah di langit dan di bumi, yang dapat melakukan perbuatan perkasa seperti Engkau?'),(5000,4,3,25,'Biarlah aku menyeberang dan melihat negeri yang baik yang di seberang sungai Yordan, tanah pegunungan yang baik itu, dan gunung Libanon.'),(5001,4,3,26,'Tetapi TUHAN murka terhadap aku oleh karena kamu dan tidaklah mendengarkan permohonanku. TUHAN berfirman kepadaku: Cukup! Jangan lagi bicarakan perkara itu dengan Aku.'),(5002,4,3,27,'Naiklah ke puncak gunung Pisga dan layangkanlah pandangmu ke barat, ke utara, ke selatan dan ke timur dan lihatlah baik-baik, sebab sungai Yordan ini tidak akan kauseberangi.'),(5003,4,3,28,'Dan berilah perintah kepada Yosua, kuatkan dan teguhkanlah hatinya, sebab dialah yang akan menyeberang di depan bangsa ini dan dialah yang akan memimpin mereka sampai mereka memiliki negeri yang akan kaulihat itu.'),(5004,4,3,29,'Demikianlah kita tinggal di lembah di tentangan Bet-Peor.\"'),(5005,4,4,1,'Maka sekarang, hai orang Israel, dengarlah ketetapan dan peraturan yang kuajarkan kepadamu untuk dilakukan, supaya kamu hidup dan memasuki serta menduduki negeri yang diberikan kepadamu oleh TUHAN, Allah nenek moyangmu.'),(5006,4,4,2,'Janganlah kamu menambahi apa yang kuperintahkan kepadamu dan janganlah kamu menguranginya, dengan demikian kamu berpegang pada perintah TUHAN, Allahmu, yang kusampaikan kepadamu.'),(5007,4,4,3,'Matamu sendiri telah melihat apa yang diperbuat TUHAN mengenai Baal-Peor, sebab TUHAN, Allahmu, telah memunahkan dari tengah-tengahmu semua orang yang mengikuti Baal-Peor,'),(5008,4,4,4,'sedangkan kamu sekalian yang berpaut pada TUHAN, Allahmu, masih hidup pada hari ini.'),(5009,4,4,5,'Ingatlah, aku telah mengajarkan ketetapan dan peraturan kepadamu, seperti yang diperintahkan kepadaku oleh TUHAN, Allahku, supaya kamu melakukan yang demikian di dalam negeri, yang akan kamu masuki untuk mendudukinya.'),(5010,4,4,6,'Lakukanlah itu dengan setia, sebab itulah yang akan menjadi kebijaksanaanmu dan akal budimu di mata bangsa-bangsa yang pada waktu mendengar segala ketetapan ini akan berkata: Memang bangsa yang besar ini adalah umat yang bijaksana dan berakal budi.'),(5011,4,4,7,'Sebab bangsa besar manakah yang mempunyai allah yang demikian dekat kepadanya seperti TUHAN, Allah kita, setiap kali kita memanggil kepada-Nya?'),(5012,4,4,8,'Dan bangsa besar manakah yang mempunyai ketetapan dan peraturan demikian adil seperti seluruh hukum ini, yang kubentangkan kepadamu pada hari ini?'),(5013,4,4,9,'Tetapi waspadalah dan berhati-hatilah, supaya jangan engkau melupakan hal-hal yang dilihat oleh matamu sendiri itu, dan supaya jangan semuanya itu hilang dari ingatanmu seumur hidupmu. Beritahukanlah kepada anak-anakmu dan kepada cucu cicitmu semuanya itu,'),(5014,4,4,10,'yakni hari itu ketika engkau berdiri di hadapan TUHAN, Allahmu, di Horeb, waktu TUHAN berfirman kepadaku: Suruhlah bangsa itu berkumpul kepada-Ku, maka Aku akan memberi mereka mendengar segala perkataan-Ku, sehingga mereka takut kepada-Ku selama mereka hidup di muka bumi dan mengajarkan demikian kepada anak-anak mereka.'),(5015,4,4,11,'Lalu kamu mendekat dan berdiri di kaki gunung itu, sedang gunung itu menyala sampai ke pusar langit dalam gelap gulita, awan dan kegelapan.'),(5016,4,4,12,'Lalu berfirmanlah TUHAN kepadamu dari tengah-tengah api; suara kata-kata kamu dengar, tetapi suatu rupa tidak kamu lihat, hanya ada suara.'),(5017,4,4,13,'Dan Ia memberitahukan kepadamu perjanjian, yang diperintahkan-Nya kepadamu untuk dilakukan, yakni Kesepuluh Firman dan Ia menuliskannya pada dua loh batu.'),(5018,4,4,14,'Dan pada waktu itu aku diperintahkan TUHAN untuk mengajarkan kepadamu ketetapan dan peraturan, supaya kamu melakukannya di negeri, ke mana kamu pergi untuk mendudukinya.'),(5019,4,4,15,'Hati-hatilah sekali--sebab kamu tidak melihat sesuatu rupa pada hari TUHAN berfirman kepadamu di Horeb dari tengah-tengah api--'),(5020,4,4,16,'supaya jangan kamu berlaku busuk dengan membuat bagimu patung yang menyerupai berhala apapun: yang berbentuk laki-laki atau perempuan;'),(5021,4,4,17,'yang berbentuk binatang yang di bumi, atau berbentuk burung bersayap yang terbang di udara,'),(5022,4,4,18,'atau berbentuk binatang yang merayap di muka bumi, atau berbentuk ikan yang ada di dalam air di bawah bumi;'),(5023,4,4,19,'dan juga supaya jangan engkau mengarahkan matamu ke langit, sehingga apabila engkau melihat matahari, bulan dan bintang, segenap tentara langit, engkau disesatkan untuk sujud menyembah dan beribadah kepada sekaliannya itu, yang justru diberikan TUHAN, Allahmu, kepada segala bangsa di seluruh kolong langit sebagai bagian mereka,'),(5024,4,4,20,'sedangkan TUHAN telah mengambil kamu dan membawa kamu keluar dari dapur peleburan besi, dari Mesir, untuk menjadi umat milik-Nya sendiri, seperti yang terjadi sekarang ini.'),(5025,4,4,21,'Tetapi TUHAN menjadi murka terhadap aku oleh karena kamu, dan Ia bersumpah, bahwa aku tidak akan menyeberangi sungai Yordan dan tidak akan masuk ke dalam negeri yang baik, yang diberikan TUHAN, Allahmu, kepadamu menjadi milik pusakamu.'),(5026,4,4,22,'Sebab aku akan mati di negeri ini dan tidak akan menyeberangi sungai Yordan, tetapi kamu akan menyeberanginya dan menduduki negeri yang baik itu.'),(5027,4,4,23,'Hati-hatilah, supaya jangan kamu melupakan perjanjian TUHAN, Allahmu, yang telah diikat-Nya dengan kamu dan membuat bagimu patung yang menyerupai apapun yang oleh TUHAN, Allahmu, dilarang kauperbuat.'),(5028,4,4,24,'Sebab TUHAN, Allahmu, adalah api yang menghanguskan, Allah yang cemburu.'),(5029,4,4,25,'Apabila kamu beranak cucu dan kamu telah tua di negeri itu lalu kamu berlaku busuk dengan membuat patung yang menyerupai apapun juga, dan melakukan apa yang jahat di mata TUHAN, Allahmu, sehingga kamu menimbulkan sakit hati-Nya,'),(5030,4,4,26,'maka aku memanggil langit dan bumi menjadi saksi terhadap kamu pada hari ini, bahwa pastilah kamu habis binasa dengan segera dari negeri ke mana kamu menyeberangi sungai Yordan untuk mendudukinya; tidak akan lanjut umurmu di sana, tetapi pastilah kamu punah.'),(5031,4,4,27,'TUHAN akan menyerakkan kamu di antara bangsa-bangsa dan hanya dengan jumlah yang sedikit kamu akan tinggal di antara bangsa-bangsa, ke mana TUHAN akan menyingkirkan kamu.'),(5032,4,4,28,'Maka di sana kamu akan beribadah kepada allah, buatan tangan manusia, dari kayu dan batu, yang tidak dapat melihat, tidak dapat mendengar, tidak dapat makan dan tidak dapat mencium.'),(5033,4,4,29,'Dan baru di sana engkau mencari TUHAN, Allahmu, dan menemukan-Nya, asal engkau menanyakan Dia dengan segenap hatimu dan dengan segenap jiwamu.'),(5034,4,4,30,'Apabila engkau dalam keadaan terdesak dan segala hal ini menimpa engkau di kemudian hari, maka engkau akan kembali kepada TUHAN, Allahmu, dan mendengarkan suara-Nya.'),(5035,4,4,31,'Sebab TUHAN, Allahmu, adalah Allah Penyayang, Ia tidak akan meninggalkan atau memusnahkan engkau dan Ia tidak akan melupakan perjanjian yang diikrarkan-Nya dengan sumpah kepada nenek moyangmu.'),(5036,4,4,32,'Sebab cobalah tanyakan, dari ujung langit ke ujung langit, tentang zaman dahulu, yang ada sebelum engkau, sejak waktu Allah menciptakan manusia di atas bumi, apakah ada pernah terjadi sesuatu hal yang demikian besar atau apakah ada pernah terdengar sesuatu seperti itu.'),(5037,4,4,33,'Pernahkah suatu bangsa mendengar suara ilahi, yang berbicara dari tengah-tengah api, seperti yang kaudengar dan tetap hidup?'),(5038,4,4,34,'Atau pernahkah suatu allah mencoba datang untuk mengambil baginya suatu bangsa dari tengah-tengah bangsa yang lain, dengan cobaan-cobaan, tanda-tanda serta mujizat-mujizat dan peperangan, dengan tangan yang kuat dan lengan yang teracung dan dengan kedahsyatan-kedahsyatan yang besar, seperti yang dilakukan TUHAN, Allahmu, bagimu di Mesir, di depan matamu?'),(5039,4,4,35,'Engkau diberi melihatnya untuk mengetahui, bahwa Tuhanlah Allah, tidak ada yang lain kecuali Dia.'),(5040,4,4,36,'Dari langit Ia membiarkan engkau mendengar suara-Nya untuk mengajari engkau, di bumi Ia membiarkan engkau melihat api-Nya yang besar, dan segala perkataan-Nya kaudengar dari tengah-tengah api.'),(5041,4,4,37,'Karena Ia mengasihi nenek moyangmu dan memilih keturunan mereka, maka Ia sendiri telah membawa engkau keluar dari Mesir dengan kekuatan-Nya yang besar,'),(5042,4,4,38,'untuk menghalau dari hadapanmu bangsa-bangsa yang lebih besar dan lebih kuat dari padamu, untuk membawa engkau masuk ke dalam negeri mereka dan memberikannya kepadamu menjadi milik pusakamu, seperti yang terjadi sekarang ini.'),(5043,4,4,39,'Sebab itu ketahuilah pada hari ini dan camkanlah, bahwa Tuhanlah Allah yang di langit di atas dan di bumi di bawah, tidak ada yang lain.'),(5044,4,4,40,'Berpeganglah pada ketetapan dan perintah-Nya yang kusampaikan kepadamu pada hari ini, supaya baik keadaanmu dan keadaan anak-anakmu yang kemudian, dan supaya lanjut umurmu di tanah yang diberikan TUHAN, Allahmu, kepadamu untuk selamanya.\"'),(5045,4,4,41,'Lalu Musa mengkhususkan tiga kota di seberang sungai Yordan, di sebelah timur,'),(5046,4,4,42,'supaya orang yang membunuh sesamanya manusia dengan tidak sengaja dan dengan tidak memusuhinya lebih dahulu, dapat melarikan diri ke sana, sehingga ia, apabila melarikan diri ke salah satu kota itu, dapat tetap hidup.'),(5047,4,4,43,'Kota-kota itu adalah: Bezer di padang gurun, di daerah dataran tinggi, untuk orang Ruben; Ramot di Gilead untuk orang Gad dan Golan di Basan untuk orang Manasye.'),(5048,4,4,44,'Inilah hukum Taurat yang dipaparkan Musa kepada orang Israel.'),(5049,4,4,45,'Inilah peringatan, ketetapan dan peraturan, yang dikatakan Musa kepada orang Israel, dalam perjalanan mereka keluar dari Mesir,'),(5050,4,4,46,'di seberang sungai Yordan, di lembah di tentangan Bet-Peor, di negeri Sihon, raja orang Amori, yang diam di Hesybon, yang dipukul kalah oleh Musa dan orang Israel dalam perjalanan mereka keluar dari Mesir.'),(5051,4,4,47,'Negerinya diduduki mereka, dan juga negeri Og, raja negeri Basan: kedua-duanya raja orang Amori, yang diam di seberang sungai Yordan, di sebelah timur,'),(5052,4,4,48,'mulai dari Aroer, di tepi sungai Arnon, sampai gunung Siryon--itulah gunung Hermon--'),(5053,4,4,49,'serta seluruh dataran di seberang sungai Yordan, di sebelah timur, sampai Laut Araba, di kaki lereng gunung Pisga.'),(5054,4,5,1,'Musa memanggil seluruh orang Israel berkumpul dan berkata kepada mereka: \"Dengarlah, hai orang Israel, ketetapan dan peraturan, yang pada hari ini kuperdengarkan kepadamu, supaya kamu mempelajarinya dan melakukannya dengan setia.'),(5055,4,5,2,'TUHAN, Allah kita, telah mengikat perjanjian dengan kita di Horeb.'),(5056,4,5,3,'Bukan dengan nenek moyang kita TUHAN mengikat perjanjian itu, tetapi dengan kita, kita yang ada di sini pada hari ini, kita semuanya yang masih hidup.'),(5057,4,5,4,'TUHAN telah bicara dengan berhadapan muka dengan kamu di gunung dan di tengah-tengah api--'),(5058,4,5,5,'aku pada waktu itu berdiri antara TUHAN dan kamu untuk memberitahukan firman TUHAN kepadamu, sebab kamu takut kepada api dan kamu tidak naik ke gunung--dan Ia berfirman:'),(5059,4,5,6,'Akulah TUHAN, Allahmu, yang membawa engkau keluar dari tanah Mesir, dari tempat perbudakan.'),(5060,4,5,7,'Jangan ada padamu allah lain di hadapan-Ku.'),(5061,4,5,8,'Jangan membuat bagimu patung yang menyerupai apapun yang ada di langit di atas, atau yang ada di bumi di bawah, atau yang ada di dalam air di bawah bumi.'),(5062,4,5,9,'Jangan sujud menyembah kepadanya atau beribadah kepadanya, sebab Aku, TUHAN Allahmu, adalah Allah yang cemburu, yang membalaskan kesalahan bapa kepada anak-anaknya dan kepada keturunan yang ketiga dan keempat dari orang-orang yang membenci Aku,'),(5063,4,5,10,'tetapi Aku menunjukkan kasih setia kepada beribu-ribu orang, yaitu mereka yang mengasihi Aku dan yang berpegang pada perintah-perintah-Ku.'),(5064,4,5,11,'Jangan menyebut nama TUHAN, Allahmu, dengan sembarangan, sebab TUHAN akan memandang bersalah orang yang menyebut nama-Nya dengan sembarangan.'),(5065,4,5,12,'Tetaplah ingat dan kuduskanlah hari Sabat, seperti yang diperintahkan kepadamu oleh TUHAN, Allahmu.'),(5066,4,5,13,'Enam hari lamanya engkau akan bekerja dan melakukan segala pekerjaanmu,'),(5067,4,5,14,'tetapi hari ketujuh adalah hari Sabat TUHAN, Allahmu; maka jangan melakukan sesuatu pekerjaan, engkau atau anakmu laki-laki, atau anakmu perempuan, atau hambamu laki-laki, atau hambamu perempuan, atau lembumu, atau keledaimu, atau hewanmu yang manapun, atau orang asing yang di tempat kediamanmu, supaya hambamu laki-laki dan hambamu perempuan berhenti seperti engkau juga.'),(5068,4,5,15,'Sebab haruslah kauingat, bahwa engkaupun dahulu budak di tanah Mesir dan engkau dibawa keluar dari sana oleh TUHAN, Allahmu dengan tangan yang kuat dan lengan yang teracung; itulah sebabnya TUHAN, Allahmu, memerintahkan engkau merayakan hari Sabat.'),(5069,4,5,16,'Hormatilah ayahmu dan ibumu, seperti yang diperintahkan kepadamu oleh TUHAN, Allahmu, supaya lanjut umurmu dan baik keadaanmu di tanah yang diberikan TUHAN, Allahmu, kepadamu.'),(5070,4,5,17,'Jangan membunuh.'),(5071,4,5,18,'Jangan berzinah.'),(5072,4,5,19,'Jangan mencuri.'),(5073,4,5,20,'Jangan mengucapkan saksi dusta tentang sesamamu.'),(5074,4,5,21,'Jangan mengingini isteri sesamamu, dan jangan menghasratkan rumahnya, atau ladangnya, atau hambanya laki-laki, atau hambanya perempuan, atau lembunya, atau keledainya, atau apapun yang dipunyai sesamamu.'),(5075,4,5,22,'Firman itulah yang diucapkan TUHAN kepada seluruh jemaahmu dengan suara nyaring di gunung, dari tengah-tengah api, awan dan kegelapan, dan tidak ditambahkan-Nya apa-apa lagi. Ditulis-Nya semuanya pada dua loh batu, lalu diberikan-Nya kepadaku.\"'),(5076,4,5,23,'Ketika kamu mendengar suara itu dari tengah-tengah gelap gulita, sementara gunung itu menyala, maka kamu, yakni semua kepala sukumu dan para tua-tuamu, mendekati aku,'),(5077,4,5,24,'dan berkata: Sesungguhnya, TUHAN, Allah kita, telah memperlihatkan kepada kita kemuliaan dan kebesaran-Nya, dan suara-Nya telah kita dengar dari tengah-tengah api. Pada hari ini telah kami lihat, bahwa Allah berbicara dengan manusia dan manusia itu tetap hidup.'),(5078,4,5,25,'Tetapi sekarang, mengapa kami harus mati? Sebab api yang besar ini akan menghanguskan kami. Apabila kami lebih lama lagi mendengar suara TUHAN, Allah kita, kami akan mati.'),(5079,4,5,26,'Sebab makhluk manakah yang telah mendengar suara dari Allah yang hidup yang berbicara dari tengah-tengah api, seperti kami dan tetap hidup?'),(5080,4,5,27,'Mendekatlah engkau dan dengarkanlah segala yang difirmankan TUHAN, Allah kita, dan engkaulah yang mengatakan kepada kami segala yang difirmankan kepadamu oleh TUHAN, Allah kita, maka kami akan mendengar dan melakukannya.'),(5081,4,5,28,'Ketika TUHAN mendengar perkataanmu itu, sedang kamu mengatakannya kepadaku, maka berfirmanlah TUHAN kepadaku: Telah Kudengar perkataan bangsa ini yang dikatakan mereka kepadamu. Segala yang dikatakan mereka itu baik.'),(5082,4,5,29,'Kiranya hati mereka selalu begitu, yakni takut akan Daku dan berpegang pada segala perintah-Ku, supaya baik keadaan mereka dan anak-anak mereka untuk selama-lamanya!'),(5083,4,5,30,'Pergilah, katakanlah kepada mereka: Kembalilah ke kemahmu.'),(5084,4,5,31,'Tetapi engkau, berdirilah di sini bersama-sama dengan Aku, maka Aku hendak mengatakan kepadamu segenap perintah, yakni ketetapan dan peraturan, yang harus kauajarkan kepada mereka, supaya mereka melakukannya di negeri yang Kuberikan kepada mereka untuk dimiliki.'),(5085,4,5,32,'Maka lakukanlah semuanya itu dengan setia, seperti yang diperintahkan kepadamu oleh TUHAN, Allahmu. Janganlah menyimpang ke kanan atau ke kiri.'),(5086,4,5,33,'Segenap jalan, yang diperintahkan kepadamu oleh TUHAN, Allahmu, haruslah kamu jalani, supaya kamu hidup, dan baik keadaanmu serta lanjut umurmu di negeri yang akan kamu duduki.\"'),(5087,4,6,1,'Inilah perintah, yakni ketetapan dan peraturan, yang aku ajarkan kepadamu atas perintah TUHAN, Allahmu, untuk dilakukan di negeri, ke mana kamu pergi untuk mendudukinya,'),(5088,4,6,2,'supaya seumur hidupmu engkau dan anak cucumu takut akan TUHAN, Allahmu, dan berpegang pada segala ketetapan dan perintah-Nya yang kusampaikan kepadamu, dan supaya lanjut umurmu.'),(5089,4,6,3,'Maka dengarlah, hai orang Israel! Lakukanlah itu dengan setia, supaya baik keadaanmu, dan supaya kamu menjadi sangat banyak, seperti yang dijanjikan TUHAN, Allah nenek moyangmu, kepadamu di suatu negeri yang berlimpah-limpah susu dan madunya.'),(5090,4,6,4,'Dengarlah, hai orang Israel: TUHAN itu Allah kita, TUHAN itu esa!'),(5091,4,6,5,'Kasihilah TUHAN, Allahmu, dengan segenap hatimu dan dengan segenap jiwamu dan dengan segenap kekuatanmu.'),(5092,4,6,6,'Apa yang kuperintahkan kepadamu pada hari ini haruslah engkau perhatikan,'),(5093,4,6,7,'haruslah engkau mengajarkannya berulang-ulang kepada anak-anakmu dan membicarakannya apabila engkau duduk di rumahmu, apabila engkau sedang dalam perjalanan, apabila engkau berbaring dan apabila engkau bangun.'),(5094,4,6,8,'Haruslah juga engkau mengikatkannya sebagai tanda pada tanganmu dan haruslah itu menjadi lambang di dahimu,'),(5095,4,6,9,'dan haruslah engkau menuliskannya pada tiang pintu rumahmu dan pada pintu gerbangmu.'),(5096,4,6,10,'Maka apabila TUHAN, Allahmu, telah membawa engkau masuk ke negeri yang dijanjikan-Nya dengan sumpah kepada nenek moyangmu, yakni Abraham, Ishak dan Yakub, untuk memberikannya kepadamu--kota-kota yang besar dan baik, yang tidak kaudirikan;'),(5097,4,6,11,'rumah-rumah, penuh berisi berbagai-bagai barang baik, yang tidak kauisi; sumur-sumur yang tidak kaugali; kebun-kebun anggur dan kebun-kebun zaitun, yang tidak kautanami--dan apabila engkau sudah makan dan menjadi kenyang,'),(5098,4,6,12,'maka berhati-hatilah, supaya jangan engkau melupakan TUHAN, yang telah membawa kamu keluar dari tanah Mesir, dari rumah perbudakan.'),(5099,4,6,13,'Engkau harus takut akan TUHAN, Allahmu; kepada Dia haruslah engkau beribadah dan demi nama-Nya haruslah engkau bersumpah.'),(5100,4,6,14,'Janganlah kamu mengikuti allah lain, dari antara allah bangsa-bangsa sekelilingmu,'),(5101,4,6,15,'sebab TUHAN, Allahmu, adalah Allah yang cemburu di tengah-tengahmu, supaya jangan bangkit murka TUHAN, Allahmu, terhadap engkau, sehingga Ia memunahkan engkau dari muka bumi.'),(5102,4,6,16,'Janganlah kamu mencobai TUHAN, Allahmu, seperti kamu mencobai Dia di Masa.'),(5103,4,6,17,'Haruslah kamu berpegang pada perintah, peringatan dan ketetapan TUHAN, Allahmu, yang diperintahkan-Nya kepadamu;'),(5104,4,6,18,'haruslah engkau melakukan apa yang benar dan baik di mata TUHAN, supaya baik keadaanmu dan engkau memasuki dan menduduki negeri yang baik, yang dijanjikan TUHAN dengan sumpah kepada nenek moyangmu,'),(5105,4,6,19,'dengan mengusir semua musuhmu dari hadapanmu, seperti yang difirmankan TUHAN.'),(5106,4,6,20,'Apabila di kemudian hari anakmu bertanya kepadamu: Apakah peringatan, ketetapan dan peraturan itu, yang diperintahkan kepadamu oleh TUHAN Allah kita?'),(5107,4,6,21,'maka haruslah engkau menjawab anakmu itu: Kita dahulu adalah budak Firaun di Mesir, tetapi TUHAN membawa kita keluar dari Mesir dengan tangan yang kuat.'),(5108,4,6,22,'TUHAN membuat tanda-tanda dan mujizat-mujizat, yang besar dan yang mencelakakan, terhadap Mesir, terhadap Firaun dan seisi rumahnya, di depan mata kita;'),(5109,4,6,23,'tetapi kita dibawa-Nya keluar dari sana, supaya kita dapat dibawa-Nya masuk untuk memberikan kepada kita negeri yang telah dijanjikan-Nya dengan sumpah kepada nenek moyang kita.'),(5110,4,6,24,'TUHAN, Allah kita, memerintahkan kepada kita untuk melakukan segala ketetapan itu dan untuk takut akan TUHAN, Allah kita, supaya senantiasa baik keadaan kita dan supaya Ia membiarkan kita hidup, seperti sekarang ini.'),(5111,4,6,25,'Dan kita akan menjadi benar, apabila kita melakukan segenap perintah itu dengan setia di hadapan TUHAN, Allah kita, seperti yang diperintahkan-Nya kepada kita.\"'),(5112,4,7,1,'Apabila TUHAN, Allahmu, telah membawa engkau ke dalam negeri, ke mana engkau masuk untuk mendudukinya, dan Ia telah menghalau banyak bangsa dari depanmu, yakni orang Het, orang Girgasi, orang Amori, orang Kanaan, orang Feris, orang Hewi dan orang Yebus, tujuh bangsa, yang lebih banyak dan lebih kuat dari padamu,'),(5113,4,7,2,'dan TUHAN, Allahmu, telah menyerahkan mereka kepadamu, sehingga engkau memukul mereka kalah, maka haruslah kamu menumpas mereka sama sekali. Janganlah engkau mengadakan perjanjian dengan mereka dan janganlah engkau mengasihani mereka.'),(5114,4,7,3,'Janganlah juga engkau kawin-mengawin dengan mereka: anakmu perempuan janganlah kauberikan kepada anak laki-laki mereka, ataupun anak perempuan mereka jangan kauambil bagi anakmu laki-laki;'),(5115,4,7,4,'sebab mereka akan membuat anakmu laki-laki menyimpang dari pada-Ku, sehingga mereka beribadah kepada allah lain. Maka murka TUHAN akan bangkit terhadap kamu dan Ia akan memunahkan engkau dengan segera.'),(5116,4,7,5,'Tetapi beginilah kamu lakukan terhadap mereka: mezbah-mezbah mereka haruslah kamu robohkan, tugu-tugu berhala mereka kamu remukkan, tiang-tiang berhala mereka kamu hancurkan dan patung-patung mereka kamu bakar habis.'),(5117,4,7,6,'Sebab engkaulah umat yang kudus bagi TUHAN, Allahmu; engkaulah yang dipilih oleh TUHAN, Allahmu, dari segala bangsa di atas muka bumi untuk menjadi umat kesayangan-Nya.'),(5118,4,7,7,'Bukan karena lebih banyak jumlahmu dari bangsa manapun juga, maka hati TUHAN terpikat olehmu dan memilih kamu--bukankah kamu ini yang paling kecil dari segala bangsa? --'),(5119,4,7,8,'tetapi karena TUHAN mengasihi kamu dan memegang sumpah-Nya yang telah diikrarkan-Nya kepada nenek moyangmu, maka TUHAN telah membawa kamu keluar dengan tangan yang kuat dan menebus engkau dari rumah perbudakan, dari tangan Firaun, raja Mesir.'),(5120,4,7,9,'Sebab itu haruslah kauketahui, bahwa TUHAN, Allahmu, Dialah Allah, Allah yang setia, yang memegang perjanjian dan kasih setia-Nya terhadap orang yang kasih kepada-Nya dan berpegang pada perintah-Nya, sampai kepada beribu-ribu keturunan,'),(5121,4,7,10,'tetapi terhadap diri setiap orang dari mereka yang membenci Dia, Ia melakukan pembalasan dengan membinasakan orang itu. Ia tidak bertangguh terhadap orang yang membenci Dia. Ia langsung mengadakan pembalasan terhadap orang itu.'),(5122,4,7,11,'Jadi berpeganglah pada perintah, yakni ketetapan dan peraturan yang kusampaikan kepadamu pada hari ini untuk dilakukan.\"'),(5123,4,7,12,'Dan akan terjadi, karena kamu mendengarkan peraturan-peraturan itu serta melakukannya dengan setia, maka terhadap engkau TUHAN, Allahmu, akan memegang perjanjian dan kasih setia-Nya yang diikrarkan-Nya dengan sumpah kepada nenek moyangmu.'),(5124,4,7,13,'Ia akan mengasihi engkau, memberkati engkau dan membuat engkau banyak; Ia akan memberkati buah kandunganmu dan hasil bumimu, gandum dan anggur serta minyakmu, anak lembu sapimu dan anak kambing dombamu, di tanah yang dijanjikan-Nya dengan sumpah kepada nenek moyangmu untuk memberikannya kepadamu.'),(5125,4,7,14,'Engkau akan diberkati lebih dari pada segala bangsa: tidak akan ada laki-laki atau perempuan yang mandul di antaramu, ataupun di antara hewanmu.'),(5126,4,7,15,'TUHAN akan menjauhkan segala penyakit dari padamu, dan tidak ada satu dari wabah celaka yang kaukenal di Mesir itu akan ditimpakan-Nya kepadamu, tetapi Ia akan mendatangkannya kepada semua orang yang membenci engkau.'),(5127,4,7,16,'Engkau harus melenyapkan segala bangsa yang diserahkan kepadamu oleh TUHAN, Allahmu; janganlah engkau merasa sayang kepada mereka dan janganlah beribadah kepada allah mereka, sebab hal itu akan menjadi jerat bagimu.'),(5128,4,7,17,'Jika sekiranya engkau berkata dalam hatimu: Bangsa-bangsa ini lebih banyak dari padaku, bagaimanakah aku dapat menghalaukan mereka?'),(5129,4,7,18,'maka janganlah engkau takut kepada mereka; ingatlah selalu apa yang dilakukan TUHAN, Allahmu, terhadap Firaun dan seluruh Mesir,'),(5130,4,7,19,'yakni cobaan-cobaan besar, yang kaulihat dengan matamu sendiri, tanda-tanda dan mujizat-mujizat, tangan yang kuat dan lengan yang teracung, yang dipakai TUHAN, Allahmu, untuk membawa engkau keluar. Demikianlah juga akan dilakukan TUHAN, Allahmu, terhadap segala bangsa yang engkau takuti.'),(5131,4,7,20,'Lagipula TUHAN, Allahmu, akan melepaskan tabuhan menyerbu mereka, sampai habis binasa orang-orang yang masih tinggal dan yang menyembunyikan diri terhadap engkau.'),(5132,4,7,21,'Janganlah gemetar karena mereka, sebab TUHAN, Allahmu, ada di tengah-tengahmu, Allah yang besar dan dahsyat.'),(5133,4,7,22,'TUHAN, Allahmu, akan menghalau bangsa-bangsa ini dari hadapanmu sedikit demi sedikit; engkau tidak boleh membinasakan mereka dengan segera, supaya jangan binatang hutan menjadi terlalu banyak melebihi engkau.'),(5134,4,7,23,'Demikianlah TUHAN, Allahmu, akan menyerahkan mereka kepadamu dan akan mengacaukan mereka sama sekali, sampai mereka punah.'),(5135,4,7,24,'Raja-raja mereka akan diserahkan-Nya ke dalam tanganmu, sehingga engkau menghapuskan nama mereka dari kolong langit; tidak akan ada yang dapat bertahan menghadapi engkau, sampai engkau memunahkan mereka.'),(5136,4,7,25,'Patung-patung allah mereka haruslah kamu bakar habis; perak dan emas yang ada pada mereka janganlah kauingini dan kauambil bagi dirimu sendiri, supaya jangan engkau terjerat karenanya, sebab hal itu adalah kekejian bagi TUHAN, Allahmu.'),(5137,4,7,26,'Dan janganlah engkau membawa sesuatu kekejian masuk ke dalam rumahmu, sehingga engkaupun ditumpas seperti itu; haruslah engkau benar-benar merasa jijik dan keji terhadap hal itu, sebab semuanya itu dikhususkan untuk dimusnahkan.\"'),(5138,4,8,1,'Segenap perintah, yang kusampaikan kepadamu pada hari ini, haruslah kamu lakukan dengan setia, supaya kamu hidup dan bertambah banyak dan kamu memasuki serta menduduki negeri yang dijanjikan TUHAN dengan sumpah kepada nenek moyangmu.'),(5139,4,8,2,'Ingatlah kepada seluruh perjalanan yang kaulakukan atas kehendak TUHAN, Allahmu, di padang gurun selama empat puluh tahun ini dengan maksud merendahkan hatimu dan mencobai engkau untuk mengetahui apa yang ada dalam hatimu, yakni, apakah engkau berpegang pada perintah-Nya atau tidak.'),(5140,4,8,3,'Jadi Ia merendahkan hatimu, membiarkan engkau lapar dan memberi engkau makan manna, yang tidak kaukenal dan yang juga tidak dikenal oleh nenek moyangmu, untuk membuat engkau mengerti, bahwa manusia hidup bukan dari roti saja, tetapi manusia hidup dari segala yang diucapkan TUHAN.'),(5141,4,8,4,'Pakaianmu tidaklah menjadi buruk di tubuhmu dan kakimu tidaklah menjadi bengkak selama empat puluh tahun ini.'),(5142,4,8,5,'Maka haruslah engkau insaf, bahwa TUHAN, Allahmu, mengajari engkau seperti seseorang mengajari anaknya.'),(5143,4,8,6,'Oleh sebab itu haruslah engkau berpegang pada perintah TUHAN, Allahmu, dengan hidup menurut jalan yang ditunjukkan-Nya dan dengan takut akan Dia.'),(5144,4,8,7,'Sebab TUHAN, Allahmu, membawa engkau masuk ke dalam negeri yang baik, suatu negeri dengan sungai, mata air dan danau, yang keluar dari lembah-lembah dan gunung-gunung;'),(5145,4,8,8,'suatu negeri dengan gandum dan jelainya, dengan pohon anggur, pohon ara dan pohon delimanya; suatu negeri dengan pohon zaitun dan madunya;'),(5146,4,8,9,'suatu negeri, di mana engkau akan makan roti dengan tidak usah berhemat, di mana engkau tidak akan kekurangan apapun; suatu negeri, yang batunya mengandung besi dan dari gunungnya akan kaugali tembaga.'),(5147,4,8,10,'Dan engkau akan makan dan akan kenyang, maka engkau akan memuji TUHAN, Allahmu, karena negeri yang baik yang diberikan-Nya kepadamu itu.'),(5148,4,8,11,'Hati-hatilah, supaya jangan engkau melupakan TUHAN, Allahmu, dengan tidak berpegang pada perintah, peraturan dan ketetapan-Nya, yang kusampaikan kepadamu pada hari ini;'),(5149,4,8,12,'dan supaya, apabila engkau sudah makan dan kenyang, mendirikan rumah-rumah yang baik serta mendiaminya,'),(5150,4,8,13,'dan apabila lembu sapimu dan kambing dombamu bertambah banyak dan emas serta perakmu bertambah banyak, dan segala yang ada padamu bertambah banyak,'),(5151,4,8,14,'jangan engkau tinggi hati, sehingga engkau melupakan TUHAN, Allahmu, yang membawa engkau keluar dari tanah Mesir, dari rumah perbudakan,'),(5152,4,8,15,'dan yang memimpin engkau melalui padang gurun yang besar dan dahsyat itu, dengan ular-ular yang ganas serta kalajengkingnya dan tanahnya yang gersang, yang tidak ada air. Dia yang membuat air keluar bagimu dari gunung batu yang keras,'),(5153,4,8,16,'dan yang di padang gurun memberi engkau makan manna, yang tidak dikenal oleh nenek moyangmu, supaya direndahkan-Nya hatimu dan dicobai-Nya engkau, hanya untuk berbuat baik kepadamu akhirnya.'),(5154,4,8,17,'Maka janganlah kaukatakan dalam hatimu: Kekuasaanku dan kekuatan tangankulah yang membuat aku memperoleh kekayaan ini.'),(5155,4,8,18,'Tetapi haruslah engkau ingat kepada TUHAN, Allahmu, sebab Dialah yang memberikan kepadamu kekuatan untuk memperoleh kekayaan, dengan maksud meneguhkan perjanjian yang diikrarkan-Nya dengan sumpah kepada nenek moyangmu, seperti sekarang ini.'),(5156,4,8,19,'Tetapi jika engkau sama sekali melupakan TUHAN, Allahmu, dan mengikuti allah lain, beribadah kepadanya dan sujud menyembah kepadanya, aku memperingatkan kepadamu hari ini, bahwa kamu pasti binasa;'),(5157,4,8,20,'seperti bangsa-bangsa, yang dibinasakan TUHAN di hadapanmu, kamupun akan binasa, sebab kamu tidak mau mendengarkan suara TUHAN, Allahmu.\"'),(5158,4,9,1,'Dengarlah, hai orang Israel! Engkau akan menyeberangi sungai Yordan pada hari ini untuk memasuki serta menduduki daerah bangsa-bangsa yang lebih besar dan lebih kuat dari padamu, yakni kota-kota besar yang kubu-kubunya sampai ke langit--'),(5159,4,9,2,'suatu bangsa yang besar dan tinggi, orang Enak, yang kaukenal dan yang tentangnya kaudengar orang berkata: Siapakah yang dapat bertahan menghadapi orang Enak?'),(5160,4,9,3,'Maka ketahuilah pada hari ini, bahwa TUHAN, Allahmu, Dialah yang berjalan di depanmu laksana api yang menghanguskan; Dia akan memunahkan mereka dan Dia akan menundukkan mereka di hadapanmu. Demikianlah engkau akan menghalau dan membinasakan mereka dengan segera, seperti yang dijanjikan kepadamu oleh TUHAN.'),(5161,4,9,4,'Janganlah engkau berkata dalam hatimu, apabila TUHAN, Allahmu, telah mengusir mereka dari hadapanmu: Karena jasa-jasakulah TUHAN membawa aku masuk menduduki negeri ini; padahal karena kefasikan bangsa-bangsa itulah TUHAN menghalau mereka dari hadapanmu.'),(5162,4,9,5,'Bukan karena jasa-jasamu atau karena kebenaran hatimu engkau masuk menduduki negeri mereka, tetapi karena kefasikan bangsa-bangsa itulah, TUHAN, Allahmu, menghalau mereka dari hadapanmu, dan supaya TUHAN menepati janji yang diikrarkan-Nya dengan sumpah kepada nenek moyangmu, yakni Abraham, Ishak dan Yakub.'),(5163,4,9,6,'Jadi ketahuilah, bahwa bukan karena jasa-jasamu TUHAN, Allahmu, memberikan kepadamu negeri yang baik itu untuk diduduki. Sesungguhnya engkau bangsa yang tegar tengkuk!\"'),(5164,4,9,7,'Ingatlah, janganlah lupa, bahwa engkau sudah membuat TUHAN, Allahmu, gusar di padang gurun. Sejak engkau keluar dari tanah Mesir sampai kamu tiba di tempat ini, kamu menentang TUHAN.'),(5165,4,9,8,'Di Horeb kamu sudah membuat TUHAN gusar, bahkan TUHAN begitu murka kepadamu, hingga Ia mau memunahkan kamu.'),(5166,4,9,9,'Setelah aku mendaki gunung untuk menerima loh-loh batu, loh-loh perjanjian yang diikat TUHAN dengan kamu, maka aku tinggal empat puluh hari empat puluh malam lamanya di gunung itu; roti tidak kumakan dan air tidak kuminum.'),(5167,4,9,10,'TUHAN memberikan kepadaku kedua loh batu, yang ditulisi jari Allah, di mana ada segala firman yang diucapkan TUHAN kepadamu di gunung itu dari tengah-tengah api, pada hari perkumpulan.'),(5168,4,9,11,'Sesudah lewat empat puluh hari empat puluh malam itu, maka TUHAN memberikan kepadaku kedua loh batu, loh-loh perjanjian itu.'),(5169,4,9,12,'Lalu berfirmanlah TUHAN kepadaku: Bangunlah, turunlah dengan segera dari sini, sebab bangsamu, yang kaubawa keluar dari Mesir, telah berlaku busuk; mereka segera menyimpang dari jalan yang Kuperintahkan kepada mereka; mereka telah membuat patung tuangan.'),(5170,4,9,13,'Lagi TUHAN berfirman kepadaku: Telah Kulihat bangsa ini dan sesungguhnya mereka adalah bangsa yang tegar tengkuk.'),(5171,4,9,14,'Biarkanlah Aku, maka Aku akan memunahkan mereka dan menghapuskan nama mereka dari kolong langit; tetapi dari padamu akan Kubuat suatu bangsa yang lebih berkuasa dan lebih banyak dari pada bangsa ini.'),(5172,4,9,15,'Setelah itu berpalinglah aku, lalu turun dari gunung yang sedang menyala itu dengan kedua loh perjanjian di kedua tanganku.'),(5173,4,9,16,'Lalu aku menyaksikan, bahwa sesungguhnya kamu telah berbuat dosa terhadap TUHAN, Allahmu: kamu telah membuat suatu anak lembu tuangan, kamu telah segera menyimpang dari jalan yang diperintahkan TUHAN kepadamu.'),(5174,4,9,17,'Maka kupeganglah kuat-kuat kedua loh itu, kulemparkan dari kedua tanganku, kupecahkan di depan matamu.'),(5175,4,9,18,'Sesudah itu aku sujud di hadapan TUHAN, empat puluh hari empat puluh malam lamanya, seperti yang pertama kali--roti tidak kumakan dan air tidak kuminum--karena segala dosa yang telah kamu perbuat, yakni kamu melakukan apa yang jahat di mata TUHAN, sehingga kamu menimbulkan sakit hati-Nya.'),(5176,4,9,19,'Sebab aku gentar karena murka dan kepanasan amarah yang ditimpakan TUHAN kepadamu, sampai Ia mau memunahkan kamu. Tetapi sekali inipun TUHAN mendengarkan aku.'),(5177,4,9,20,'Juga kepada Harun TUHAN begitu murka, hingga Ia mau membinasakannya; maka pada waktu itu aku berdoa untuk Harun juga.'),(5178,4,9,21,'Tetapi hasil perbuatanmu yang berdosa, yakni anak lembu itu, kuambil, kubakar, kuhancurkan dan kugiling baik-baik sampai halus, menjadi abu, lalu abunya kulemparkan ke dalam sungai yang mengalir turun dari gunung.'),(5179,4,9,22,'Juga di Tabera, di Masa dan di Kibrot-Taawa, kamu selalu membuat TUHAN gusar.'),(5180,4,9,23,'Dan ketika TUHAN menyuruh kamu pergi dari Kadesh-Barnea dengan berfirman: Majulah dan dudukilah negeri yang Kuberikan kepadamu itu, maka kamu menentang titah TUHAN, Allahmu; kamu tidak percaya kepada-Nya dan tidak mendengarkan suara-Nya.'),(5181,4,9,24,'Bahkan kamu menentang TUHAN, sejak aku mengenal kamu.'),(5182,4,9,25,'Maka aku sujud di hadapan TUHAN--empat puluh hari empat puluh malam lamanya aku sujud--,karena TUHAN telah berfirman akan memunahkan kamu,'),(5183,4,9,26,'dan aku berdoa kepada TUHAN, kataku: Ya, Tuhan ALLAH, janganlah musnahkan umat milik-Mu sendiri, yang Kautebus dengan kebesaran-Mu, dan yang Kaubawa keluar dari Mesir dengan tangan yang kuat.'),(5184,4,9,27,'Ingatlah kepada hamba-hamba-Mu, kepada Abraham, Ishak dan Yakub; janganlah perhatikan ketegaran bangsa ini ataupun kefasikannya dan dosanya,'),(5185,4,9,28,'supaya negeri, dari mana Engkau membawa kami keluar, jangan berkata: Sebab TUHAN tidak dapat membawa mereka masuk ke negeri yang dijanjikan-Nya kepada mereka, dan sebab benci-Nya kepada mereka, maka Ia membawa mereka keluar untuk membunuh mereka di padang gurun.'),(5186,4,9,29,'Bukankah mereka itu umat milik-Mu sendiri, yang Kaubawa keluar dengan kekuatan-Mu yang besar dan dengan lengan-Mu yang teracung?\"'),(5187,4,10,1,'Pada waktu itu berfirmanlah TUHAN kepadaku: Pahatlah dua loh batu yang serupa dengan yang mula-mula, naiklah kepada-Ku ke atas gunung, dan buatlah sebuah tabut dari kayu;'),(5188,4,10,2,'maka Aku akan menuliskan pada loh itu firman-firman yang ada pada loh yang mula-mula yang telah kaupecahkan itu, kemudian letakkanlah kedua loh ke dalam tabut itu.'),(5189,4,10,3,'Maka aku membuat sebuah tabut dari kayu penaga dan memahat dua loh batu yang serupa dengan yang mula-mula; kemudian aku mendaki gunung dengan kedua loh itu di tanganku.'),(5190,4,10,4,'Dan pada loh itu Ia menuliskan, sama dengan tulisan yang mula-mula, Kesepuluh Firman yang telah diucapkan TUHAN kepadamu di atas gunung dari tengah-tengah api pada hari kamu berkumpul; sesudah itu TUHAN memberikannya kepadaku.'),(5191,4,10,5,'Lalu aku turun kembali dari atas gunung, dan aku meletakkan loh-loh itu ke dalam tabut yang telah kubuat; dan di situlah tempatnya, seperti yang diperintahkan TUHAN kepadaku.'),(5192,4,10,6,'Maka orang Israel berangkat dari Beerot Bene-Yaakan ke Mosera; di sanalah Harun mati dan dikuburkan; lalu Eleazar, anaknya, menjadi imam menggantikan dia.'),(5193,4,10,7,'Dari sana mereka berangkat ke Gudgod, dan dari Gudgod ke Yotbata, suatu daerah yang banyak sungainya.'),(5194,4,10,8,'Pada waktu itu TUHAN menunjuk suku Lewi untuk mengangkut tabut perjanjian TUHAN, untuk bertugas melayani TUHAN dan untuk memberi berkat demi nama-Nya, sampai sekarang.'),(5195,4,10,9,'Sebab itu suku Lewi tidak mempunyai bagian milik pusaka bersama-sama dengan saudara-saudaranya; Tuhanlah milik pusakanya, seperti yang difirmankan kepadanya oleh TUHAN, Allahmu.'),(5196,4,10,10,'Maka aku ini berdiri di atas gunung seperti yang pertama kali, empat puluh hari empat puluh malam lamanya, dan sekali inipun TUHAN mendengarkan aku: TUHAN tidak mau memusnahkan engkau.'),(5197,4,10,11,'Lalu berfirmanlah TUHAN kepadaku: Bersiaplah, pergilah berjalan di depan bangsa itu, supaya mereka memasuki dan menduduki negeri yang Kujanjikan dengan sumpah kepada nenek moyang mereka untuk memberikannya kepada mereka.\"'),(5198,4,10,12,'Maka sekarang, hai orang Israel, apakah yang dimintakan dari padamu oleh TUHAN, Allahmu, selain dari takut akan TUHAN, Allahmu, hidup menurut segala jalan yang ditunjukkan-Nya, mengasihi Dia, beribadah kepada TUHAN, Allahmu, dengan segenap hatimu dan dengan segenap jiwamu,'),(5199,4,10,13,'berpegang pada perintah dan ketetapan TUHAN yang kusampaikan kepadamu pada hari ini, supaya baik keadaanmu.'),(5200,4,10,14,'Sesungguhnya, TUHAN, Allahmulah yang empunya langit, bahkan langit yang mengatasi segala langit, dan bumi dengan segala isinya;'),(5201,4,10,15,'tetapi hanya oleh nenek moyangmulah hati TUHAN terpikat sehingga Ia mengasihi mereka, dan keturunan merekalah, yakni kamu, yang dipilih-Nya dari segala bangsa, seperti sekarang ini.'),(5202,4,10,16,'Sebab itu sunatlah hatimu dan janganlah lagi kamu tegar tengkuk.'),(5203,4,10,17,'Sebab TUHAN, Allahmulah Allah segala allah dan Tuhan segala tuhan, Allah yang besar, kuat dan dahsyat, yang tidak memandang bulu ataupun menerima suap;'),(5204,4,10,18,'yang membela hak anak yatim dan janda dan menunjukkan kasih-Nya kepada orang asing dengan memberikan kepadanya makanan dan pakaian.'),(5205,4,10,19,'Sebab itu haruslah kamu menunjukkan kasihmu kepada orang asing, sebab kamupun dahulu adalah orang asing di tanah Mesir.'),(5206,4,10,20,'Engkau harus takut akan TUHAN, Allahmu, kepada-Nya haruslah engkau beribadah dan berpaut, dan demi nama-Nya haruslah engkau bersumpah.'),(5207,4,10,21,'Dialah pokok puji-pujianmu dan Dialah Allahmu, yang telah melakukan di antaramu perbuatan-perbuatan yang besar dan dahsyat, yang telah kaulihat dengan matamu sendiri.'),(5208,4,10,22,'Dengan tujuh puluh orang nenek moyangmu pergi ke Mesir, tetapi sekarang ini TUHAN, Allahmu, telah membuat engkau banyak seperti bintang-bintang di langit.\"'),(5209,4,11,1,'Haruslah engkau mengasihi TUHAN, Allahmu, dan melakukan dengan setia kewajibanmu terhadap Dia dengan senantiasa berpegang pada segala ketetapan-Nya, peraturan-Nya dan perintah-Nya.'),(5210,4,11,2,'Kamu tahu sekarang--kukatakan bukan kepada anak-anakmu, yang tidak mengenal dan tidak melihat hajaran TUHAN, Allahmu--kebesaran-Nya, tangan-Nya yang kuat dan lengan-Nya yang teracung,'),(5211,4,11,3,'tanda-tanda dan perbuatan-perbuatan yang dilakukan-Nya di Mesir terhadap Firaun, raja Mesir, dan terhadap seluruh negerinya;'),(5212,4,11,4,'juga apa yang dilakukan-Nya terhadap pasukan Mesir, dengan kuda-kudanya dan kereta-keretanya, yakni bagaimana Ia membuat air Laut Teberau meluap meliputi mereka, ketika mereka mengejar kamu, sehingga TUHAN membinasakan mereka untuk selamanya;'),(5213,4,11,5,'dan apa yang dilakukan-Nya terhadapmu di padang gurun, sampai kamu tiba di tempat ini;'),(5214,4,11,6,'pula apa yang dilakukan-Nya terhadap Datan dan Abiram, anak-anak Eliab, anak Ruben, yakni ketika tanah mengangakan mulutnya dan menelan mereka dengan seisi rumahnya, kemah-kemah dan segala yang mengikuti mereka, di tengah-tengah seluruh orang Israel.'),(5215,4,11,7,'Sebab matamu sendirilah yang telah melihat segala perbuatan besar yang dilakukan TUHAN.\"'),(5216,4,11,8,'Jadi kamu harus berpegang pada seluruh perintah yang kusampaikan kepadamu pada hari ini, supaya kamu kuat untuk memasuki serta menduduki negeri, ke mana kamu pergi mendudukinya,'),(5217,4,11,9,'dan supaya lanjut umurmu di tanah yang dijanjikan TUHAN dengan sumpah kepada nenek moyangmu untuk memberikannya kepada mereka dan kepada keturunan mereka, suatu negeri yang berlimpah-limpah susu dan madunya.'),(5218,4,11,10,'Sebab negeri, ke mana engkau masuk untuk mendudukinya, bukanlah negeri seperti tanah Mesir, dari mana kamu keluar, yang setelah ditabur dengan benih harus kauairi dengan jerih payah, seakan-akan kebun sayur.'),(5219,4,11,11,'Tetapi negeri, ke mana kamu pergi untuk mendudukinya, ialah negeri yang bergunung-gunung dan berlembah-lembah, yang mendapat air sebanyak hujan yang turun dari langit;'),(5220,4,11,12,'suatu negeri yang dipelihara oleh TUHAN, Allahmu: mata TUHAN, Allahmu, tetap mengawasinya dari awal sampai akhir tahun.'),(5221,4,11,13,'Jika kamu dengan sungguh-sungguh mendengarkan perintah yang kusampaikan kepadamu pada hari ini, sehingga kamu mengasihi TUHAN, Allahmu, dan beribadah kepada-Nya dengan segenap hatimu dan dengan segenap jiwamu,'),(5222,4,11,14,'maka Ia akan memberikan hujan untuk tanahmu pada masanya, hujan awal dan hujan akhir, sehingga engkau dapat mengumpulkan gandummu, anggurmu dan minyakmu,'),(5223,4,11,15,'dan Dia akan memberi rumput di padangmu untuk hewanmu, sehingga engkau dapat makan dan menjadi kenyang.'),(5224,4,11,16,'Hati-hatilah, supaya jangan hatimu terbujuk, sehingga kamu menyimpang dengan beribadah kepada allah lain dan sujud menyembah kepadanya.'),(5225,4,11,17,'Jika demikian, maka akan bangkitlah murka TUHAN terhadap kamu dan Ia akan menutup langit, sehingga tidak ada hujan dan tanah tidak mengeluarkan hasil, lalu kamu lenyap dengan cepat dari negeri yang baik yang diberikan TUHAN kepadamu.'),(5226,4,11,18,'Tetapi kamu harus menaruh perkataanku ini dalam hatimu dan dalam jiwamu; kamu harus mengikatkannya sebagai tanda pada tanganmu dan haruslah itu menjadi lambang di dahimu.'),(5227,4,11,19,'Kamu harus mengajarkannya kepada anak-anakmu dengan membicarakannya, apabila engkau duduk di rumahmu dan apabila engkau sedang dalam perjalanan, apabila engkau berbaring dan apabila engkau bangun;'),(5228,4,11,20,'engkau harus menuliskannya pada tiang pintu rumahmu dan pada pintu gerbangmu,'),(5229,4,11,21,'supaya panjang umurmu dan umur anak-anakmu di tanah yang dijanjikan TUHAN dengan sumpah kepada nenek moyangmu untuk memberikannya kepada mereka, selama ada langit di atas bumi.'),(5230,4,11,22,'Sebab jika kamu sungguh-sungguh berpegang pada perintah yang kusampaikan kepadamu untuk dilakukan, dengan mengasihi TUHAN, Allahmu, dengan hidup menurut segala jalan yang ditunjukkan-Nya dan dengan berpaut pada-Nya,'),(5231,4,11,23,'maka TUHAN akan menghalau segala bangsa ini dari hadapanmu, sehingga kamu menduduki daerah bangsa-bangsa yang lebih besar dan lebih kuat dari padamu.'),(5232,4,11,24,'Setiap tempat yang diinjak oleh telapak kakimu, kamulah yang akan memilikinya: mulai dari padang gurun sampai gunung Libanon, dan dari sungai itu, yakni sungai Efrat, sampai laut sebelah barat, akan menjadi daerahmu.'),(5233,4,11,25,'Tidak ada yang akan dapat bertahan menghadapi kamu: TUHAN, Allahmu, akan membuat seluruh negeri yang kauinjak itu menjadi gemetar dan takut kepadamu, seperti yang dijanjikan TUHAN kepadamu.'),(5234,4,11,26,'Lihatlah, aku memperhadapkan kepadamu pada hari ini berkat dan kutuk:'),(5235,4,11,27,'berkat, apabila kamu mendengarkan perintah TUHAN, Allahmu, yang kusampaikan kepadamu pada hari ini;'),(5236,4,11,28,'dan kutuk, jika kamu tidak mendengarkan perintah TUHAN, Allahmu, dan menyimpang dari jalan yang kuperintahkan kepadamu pada hari ini, dengan mengikuti allah lain yang tidak kamu kenal.'),(5237,4,11,29,'Jadi apabila TUHAN, Allahmu, telah membawa engkau ke negeri, yang engkau masuki untuk mendudukinya, maka haruslah engkau mengucapkan berkat di atas gunung Gerizim dan kutuk di atas gunung Ebal.'),(5238,4,11,30,'Bukankah keduanya terletak di sebelah barat sungai Yordan, di belakang jalan raya sebelah matahari terbenam, di negeri orang Kanaan yang diam di Araba-Yordan, di tentangan Gilgal dekat pohon-pohon tarbantin di More?'),(5239,4,11,31,'Sebab kamu ini sebentar lagi hendak menyeberangi sungai Yordan untuk memasuki dan menduduki negeri yang diberikan kepadamu oleh TUHAN, Allahmu; dan bila kamu akan menduduki dan mendiaminya,'),(5240,4,11,32,'maka haruslah kamu melakukan dengan setia segala ketetapan dan peraturan yang kupaparkan kepadamu pada hari ini.\"'),(5241,4,12,1,'Inilah ketetapan dan peraturan yang harus kamu lakukan dengan setia di negeri yang diberikan TUHAN, Allah nenek moyangmu, kepadamu untuk memilikinya, selama kamu hidup di muka bumi.'),(5242,4,12,2,'Kamu harus memusnahkan sama sekali segala tempat, di mana bangsa-bangsa yang daerahnya kamu duduki itu beribadah kepada allah mereka, yakni di gunung-gunung yang tinggi, di bukit-bukit dan di bawah setiap pohon yang rimbun.'),(5243,4,12,3,'Mezbah mereka kamu harus robohkan, tugu-tugu berhala mereka kamu remukkan, tiang-tiang berhala mereka kamu bakar habis, patung-patung allah mereka kamu hancurkan, dan nama mereka kamu hapuskan dari tempat itu.'),(5244,4,12,4,'Jangan kamu berbuat seperti itu terhadap TUHAN, Allahmu.'),(5245,4,12,5,'Tetapi tempat yang akan dipilih TUHAN, Allahmu, dari segala sukumu sebagai kediaman-Nya untuk menegakkan nama-Nya di sana, tempat itulah harus kamu cari dan ke sanalah harus kamu pergi.'),(5246,4,12,6,'Ke sanalah harus kamu bawa korban bakaran dan korban sembelihanmu, persembahan persepuluhanmu dan persembahan khususmu, korban nazarmu dan korban sukarelamu, anak-anak sulung lembu sapimu dan kambing dombamu.'),(5247,4,12,7,'Di sanalah kamu makan di hadapan TUHAN, Allahmu, dan bersukaria, kamu dan seisi rumahmu, karena dalam segala usahamu engkau diberkati oleh TUHAN, Allahmu.'),(5248,4,12,8,'Jangan kamu melakukan apapun yang kita lakukan di sini sekarang, yakni masing-masing berbuat segala sesuatu yang dipandangnya benar.'),(5249,4,12,9,'Sebab hingga sekarang kamu belum sampai ke tempat perhentian dan ke milik pusaka yang diberikan kepadamu oleh TUHAN, Allahmu.'),(5250,4,12,10,'Tetapi apabila nanti sudah kamu seberangi sungai Yordan dan kamu diam di negeri yang diberikan TUHAN, Allahmu, kepadamu untuk dimiliki, dan apabila Ia mengaruniakan kepadamu keamanan dari segala musuhmu di sekelilingmu, dan kamu diam dengan tenteram,'),(5251,4,12,11,'maka ke tempat yang dipilih TUHAN, Allahmu, untuk membuat nama-Nya diam di sana, haruslah kamu bawa semuanya yang kuperintahkan kepadamu, yakni korban bakaran dan korban sembelihanmu, persembahan persepuluhanmu dan persembahan khususmu dan segala korban nazarmu yang terpilih, yang kamu nazarkan kepada TUHAN.'),(5252,4,12,12,'Kamu harus bersukaria di hadapan TUHAN, Allahmu, kamu ini, anakmu laki-laki dan anakmu perempuan, hambamu laki-laki dan hambamu perempuan, dan orang Lewi yang di dalam tempatmu, sebab orang Lewi tidak mendapat bagian milik pusaka bersama-sama kamu.'),(5253,4,12,13,'Hati-hatilah, supaya jangan engkau mempersembahkan korban-korban bakaranmu di sembarang tempat yang kaulihat;'),(5254,4,12,14,'tetapi di tempat yang akan dipilih TUHAN di daerah salah satu sukumu, di sanalah harus kaupersembahkan korban bakaranmu, dan di sanalah harus kaulakukan segala yang kuperintahkan kepadamu.'),(5255,4,12,15,'Tetapi engkau boleh menyembelih dan memakan daging sesuka hatimu, sesuai dengan berkat TUHAN, Allahmu, yang diberikan-Nya kepadamu di segala tempatmu. Orang najis ataupun orang tahir boleh memakannya, seperti juga daging kijang atau daging rusa;'),(5256,4,12,16,'hanya darahnya janganlah kaumakan, tetapi harus kaucurahkan ke bumi seperti air.'),(5257,4,12,17,'Di dalam tempatmu tidak boleh kaumakan persembahan persepuluhan dari gandummu, dari anggurmu dan minyakmu, ataupun dari anak-anak sulung lembu sapimu dan kambing dombamu, ataupun sesuatu dari korban yang akan kaunazarkan, ataupun dari korban sukarelamu, ataupun persembahan khususmu.'),(5258,4,12,18,'Tetapi di hadapan TUHAN, Allahmu, haruslah engkau memakannya, di tempat yang akan dipilih TUHAN, Allahmu, engkau ini, anakmu laki-laki dan anakmu perempuan, hambamu laki-laki dan hambamu perempuan, dan orang Lewi yang di dalam tempatmu, dan haruslah engkau bersukaria di hadapan TUHAN, Allahmu, karena segala usahamu.'),(5259,4,12,19,'Hati-hatilah, supaya jangan engkau melalaikan orang Lewi, selama engkau ada di tanahmu.'),(5260,4,12,20,'Apabila TUHAN, Allahmu, telah meluaskan daerahmu nanti, seperti yang dijanjikan-Nya kepadamu dan engkau berpikir: Aku mau makan daging, karena engkau ingin makan daging, maka bolehlah engkau makan daging sesuka hatimu.'),(5261,4,12,21,'Apabila tempat yang akan dipilih TUHAN, Allahmu, untuk menegakkan nama-Nya di sana, terlalu jauh dari tempatmu, maka engkau boleh menyembelih dari lembu sapimu dan kambing dombamu yang diberikan TUHAN kepadamu, seperti yang kuperintahkan kepadamu, dan memakan dagingnya di tempatmu sesuka hatimu.'),(5262,4,12,22,'Tetapi engkau harus memakan dagingnya, seperti memakan daging kijang atau daging rusa; baik orang najis maupun orang tahir boleh memakannya.'),(5263,4,12,23,'Tetapi jagalah baik-baik, supaya jangan engkau memakan darahnya, sebab darah ialah nyawa, maka janganlah engkau memakan nyawa bersama-sama dengan daging.'),(5264,4,12,24,'Janganlah engkau memakannya; engkau harus mencurahkannya ke bumi seperti air.'),(5265,4,12,25,'Janganlah engkau memakannya, supaya baik keadaanmu dan keadaan anak-anakmu yang kemudian, apabila engkau melakukan apa yang benar di mata TUHAN.'),(5266,4,12,26,'Tetapi persembahan kudusmu yang ada padamu dan korban nazarmu haruslah kaubawa ke tempat yang akan dipilih TUHAN;'),(5267,4,12,27,'engkau harus mengolah korban bakaranmu, daging dan darahnya, di atas mezbah TUHAN, Allahmu, dan darah korban sembelihanmu haruslah dicurahkan ke atas mezbah TUHAN, Allahmu, tetapi dagingnya boleh kaumakan.'),(5268,4,12,28,'Dengarkanlah baik-baik segala yang kuperintahkan kepadamu, supaya baik keadaanmu dan keadaan anak-anakmu yang kemudian untuk selama-lamanya, apabila engkau melakukan apa yang baik dan benar di mata TUHAN, Allahmu.\"'),(5269,4,12,29,'Apabila TUHAN, Allahmu, telah melenyapkan dari hadapanmu bangsa-bangsa yang daerahnya kaumasuki untuk mendudukinya, dan apabila engkau sudah menduduki daerahnya dan diam di negerinya,'),(5270,4,12,30,'maka hati-hatilah, supaya jangan engkau kena jerat dan mengikuti mereka, setelah mereka dipunahkan dari hadapanmu, dan supaya jangan engkau menanya-nanya tentang allah mereka dengan berkata: Bagaimana bangsa-bangsa ini beribadah kepada allah mereka? Akupun mau berlaku begitu.'),(5271,4,12,31,'Jangan engkau berbuat seperti itu terhadap TUHAN, Allahmu; sebab segala yang menjadi kekejian bagi TUHAN, apa yang dibenci-Nya, itulah yang dilakukan mereka bagi allah mereka; bahkan anak-anaknya lelaki dan anak-anaknya perempuan dibakar mereka dengan api bagi allah mereka.'),(5272,4,12,32,'Segala yang kuperintahkan kepadamu haruslah kamu lakukan dengan setia, janganlah engkau menambahinya ataupun menguranginya.'),(5273,4,13,1,'Apabila di tengah-tengahmu muncul seorang nabi atau seorang pemimpi, dan ia memberitahukan kepadamu suatu tanda atau mujizat,'),(5274,4,13,2,'dan apabila tanda atau mujizat yang dikatakannya kepadamu itu terjadi, dan ia membujuk: Mari kita mengikuti allah lain, yang tidak kaukenal, dan mari kita berbakti kepadanya,'),(5275,4,13,3,'maka janganlah engkau mendengarkan perkataan nabi atau pemimpi itu; sebab TUHAN, Allahmu, mencoba kamu untuk mengetahui, apakah kamu sungguh-sungguh mengasihi TUHAN, Allahmu, dengan segenap hatimu dan dengan segenap jiwamu.'),(5276,4,13,4,'TUHAN, Allahmu, harus kamu ikuti, kamu harus takut akan Dia, kamu harus berpegang pada perintah-Nya, suara-Nya harus kamu dengarkan, kepada-Nya harus kamu berbakti dan berpaut.'),(5277,4,13,5,'Nabi atau pemimpi itu haruslah dihukum mati, karena ia telah mengajak murtad terhadap TUHAN, Allahmu, yang telah membawa kamu keluar dari tanah Mesir dan yang menebus engkau dari rumah perbudakan--dengan maksud untuk menyesatkan engkau dari jalan yang diperintahkan TUHAN, Allahmu, kepadamu untuk dijalani. Demikianlah harus kauhapuskan yang jahat itu dari tengah-tengahmu.'),(5278,4,13,6,'Apabila saudaramu laki-laki, anak ibumu, atau anakmu laki-laki atau anakmu perempuan atau isterimu sendiri atau sahabat karibmu membujuk engkau diam-diam, katanya: Mari kita berbakti kepada allah lain yang tidak dikenal olehmu ataupun oleh nenek moyangmu,'),(5279,4,13,7,'salah satu allah bangsa-bangsa sekelilingmu, baik yang dekat kepadamu maupun yang jauh dari padamu, dari ujung bumi ke ujung bumi,'),(5280,4,13,8,'maka janganlah engkau mengalah kepadanya dan janganlah mendengarkan dia. Janganlah engkau merasa sayang kepadanya, janganlah mengasihani dia dan janganlah menutupi salahnya,'),(5281,4,13,9,'tetapi bunuhlah dia! Pertama-tama tanganmu sendirilah yang bergerak untuk membunuh dia, kemudian seluruh rakyat.'),(5282,4,13,10,'Engkau harus melempari dia dengan batu, sehingga mati, karena ia telah berikhtiar menyesatkan engkau dari pada TUHAN, Allahmu, yang telah membawa engkau keluar dari tanah Mesir, dari rumah perbudakan.'),(5283,4,13,11,'Maka seluruh orang Israel akan mendengar dan menjadi takut, sehingga mereka tidak akan melakukan lagi perbuatan jahat seperti itu di tengah-tengahmu.'),(5284,4,13,12,'Apabila di salah satu kota yang diberikan TUHAN, Allahmu, kepadamu untuk diam di sana, kaudengar orang berkata:'),(5285,4,13,13,'Ada orang-orang dursila tampil dari tengah-tengahmu, yang telah menyesatkan penduduk kota mereka dengan berkata: Mari kita berbakti kepada allah lain yang tidak kamu kenal,'),(5286,4,13,14,'maka haruslah engkau memeriksa, menyelidiki dan menanyakan baik-baik. Jikalau ternyata benar dan sudah pasti, bahwa kekejian itu dilakukan di tengah-tengahmu,'),(5287,4,13,15,'maka bunuhlah dengan mata pedang penduduk kota itu, dan tumpaslah dengan mata pedang kota itu serta segala isinya dan hewannya.'),(5288,4,13,16,'Seluruh jarahan harus kaukumpulkan di tengah-tengah lapangan dan harus kaubakar habis kota dengan seluruh jarahan itu sebagai korban bakaran yang lengkap bagi TUHAN, Allahmu. Semuanya itu akan tetap menjadi timbunan puing untuk selamanya dan tidak akan dibangun kembali.'),(5289,4,13,17,'Dari barang-barang yang dikhususkan itu janganlah apapun melekat pada tanganmu, supaya TUHAN berhenti dari murka-Nya yang bernyala-nyala itu, menunjukkan belas kasihan-Nya kepadamu, mengasihani engkau dan membuat jumlahmu banyak, seperti yang dijanjikan-Nya dengan sumpah kepada nenek moyangmu.'),(5290,4,13,18,'Sebab dengan demikian engkau mendengarkan suara TUHAN, Allahmu, untuk berpegang pada segala perintah-Nya, yang kusampaikan kepadamu pada hari ini, dengan melakukan apa yang benar di mata TUHAN, Allahmu.\"'),(5291,4,14,1,'Kamulah anak-anak TUHAN, Allahmu; janganlah kamu menoreh-noreh dirimu ataupun menggundul rambut di atas dahimu karena kematian seseorang;'),(5292,4,14,2,'sebab engkaulah umat yang kudus bagi TUHAN, Allahmu, dan engkau dipilih TUHAN untuk menjadi umat kesayangan-Nya dari antara segala bangsa yang di atas muka bumi.\"'),(5293,4,14,3,'Janganlah engkau memakan sesuatu yang merupakan kekejian.'),(5294,4,14,4,'Inilah binatang-binatang berkaki empat yang boleh kamu makan: lembu, domba dan kambing;'),(5295,4,14,5,'rusa, kijang, rusa dandi, kambing hutan, kijang gunung, lembu hutan dan domba hutan.'),(5296,4,14,6,'Setiap binatang berkaki empat yang berkuku belah--yaitu yang kukunya bersela panjang menjadi dua--dan yang memamah biak di antara binatang-binatang berkaki empat, itu boleh kamu makan.'),(5297,4,14,7,'Tetapi inilah yang tidak boleh kamu makan dari antara yang memamah biak atau dari antara yang berbelah dan bersela kukunya: unta, kelinci hutan dan marmot, karena semuanya itu memang memamah biak, tetapi tidak berkuku belah; haram semuanya itu bagimu.'),(5298,4,14,8,'Juga babi hutan, karena memang berkuku belah, tetapi tidak memamah biak; haram itu bagimu. Daging binatang-binatang itu janganlah kamu makan dan janganlah kamu terkena bangkainya.'),(5299,4,14,9,'Inilah yang boleh kamu makan dari segala yang hidup di dalam air; segala yang bersirip dan bersisik boleh kamu makan,'),(5300,4,14,10,'tetapi segala yang tidak bersirip atau bersisik janganlah kamu makan; haram semuanya itu bagimu.'),(5301,4,14,11,'Setiap burung yang tidak haram boleh kamu makan.'),(5302,4,14,12,'Tetapi yang berikut janganlah kamu makan: burung rajawali, ering janggut dan elang laut;'),(5303,4,14,13,'elang merah, elang hitam dan burung dendang menurut jenisnya;'),(5304,4,14,14,'setiap burung gagak menurut jenisnya;'),(5305,4,14,15,'burung unta, burung hantu, camar dan elang sikap menurut jenisnya;'),(5306,4,14,16,'burung pungguk, burung hantu besar, burung hantu putih;'),(5307,4,14,17,'burung undan, burung ering dan burung dendang air;'),(5308,4,14,18,'burung ranggung, dan bangau menurut jenisnya, meragai dan kelelawar.'),(5309,4,14,19,'Juga segala binatang mengeriap yang bersayap, itupun haram bagimu, jangan dimakan.'),(5310,4,14,20,'Segala burung yang tidak haram boleh kamu makan.'),(5311,4,14,21,'Janganlah kamu memakan bangkai apapun, tetapi boleh kauberikan kepada pendatang yang di dalam tempatmu untuk dimakan, atau boleh kaujual kepada orang asing; sebab engkaulah umat yang kudus bagi TUHAN, Allahmu. Janganlah kaumasak anak kambing dalam air susu induknya.\"'),(5312,4,14,22,'Haruslah engkau benar-benar mempersembahkan sepersepuluh dari seluruh hasil benih yang tumbuh di ladangmu, tahun demi tahun.'),(5313,4,14,23,'Di hadapan TUHAN, Allahmu, di tempat yang akan dipilih-Nya untuk membuat nama-Nya diam di sana, haruslah engkau memakan persembahan persepuluhan dari gandummu, dari anggurmu dan minyakmu, ataupun dari anak-anak sulung lembu sapimu dan kambing dombamu, supaya engkau belajar untuk selalu takut akan TUHAN, Allahmu.'),(5314,4,14,24,'Apabila, dalam hal engkau diberkati TUHAN, Allahmu, jalan itu terlalu jauh bagimu, sehingga engkau tidak dapat mengangkutnya, karena tempat yang akan dipilih TUHAN untuk menegakkan nama-Nya di sana terlalu jauh dari tempatmu,'),(5315,4,14,25,'maka haruslah engkau menguangkannya dan membawa uang itu dalam bungkusan dan pergi ke tempat yang akan dipilih TUHAN, Allahmu,'),(5316,4,14,26,'dan haruslah engkau membelanjakan uang itu untuk segala yang disukai hatimu, untuk lembu sapi atau kambing domba, untuk anggur atau minuman yang memabukkan, atau apapun yang diingini hatimu, dan haruslah engkau makan di sana di hadapan TUHAN, Allahmu dan bersukaria, engkau dan seisi rumahmu.'),(5317,4,14,27,'Juga orang Lewi yang diam di dalam tempatmu janganlah kauabaikan, sebab ia tidak mendapat bagian milik pusaka bersama-sama engkau.'),(5318,4,14,28,'Pada akhir tiga tahun engkau harus mengeluarkan segala persembahan persepuluhan dari hasil tanahmu dalam tahun itu dan menaruhnya di dalam kotamu;'),(5319,4,14,29,'maka orang Lewi, karena ia tidak mendapat bagian milik pusaka bersama-sama engkau, dan orang asing, anak yatim dan janda yang di dalam tempatmu, akan datang makan dan menjadi kenyang, supaya TUHAN, Allahmu, memberkati engkau di dalam segala usaha yang dikerjakan tanganmu.\"'),(5320,4,15,1,'Pada akhir tujuh tahun engkau harus mengadakan penghapusan hutang.'),(5321,4,15,2,'Inilah cara penghapusan itu: setiap orang yang berpiutang harus menghapuskan apa yang dipinjamkannya kepada sesamanya; janganlah ia menagih dari sesamanya atau saudaranya, karena telah dimaklumkan penghapusan hutang demi TUHAN.'),(5322,4,15,3,'Dari seorang asing boleh kautagih, tetapi piutangmu kepada saudaramu haruslah kauhapuskan.'),(5323,4,15,4,'Maka tidak akan ada orang miskin di antaramu, sebab sungguh TUHAN akan memberkati engkau di negeri yang diberikan TUHAN, Allahmu, kepadamu untuk menjadi milik pusaka,'),(5324,4,15,5,'asal saja engkau mendengarkan baik-baik suara TUHAN, Allahmu, dan melakukan dengan setia segenap perintah yang kusampaikan kepadamu pada hari ini.'),(5325,4,15,6,'Apabila TUHAN, Allahmu, memberkati engkau, seperti yang dijanjikan-Nya kepadamu, maka engkau akan memberi pinjaman kepada banyak bangsa, tetapi engkau sendiri tidak akan meminta pinjaman; engkau akan menguasai banyak bangsa, tetapi mereka tidak akan menguasai engkau.'),(5326,4,15,7,'Jika sekiranya ada di antaramu seorang miskin, salah seorang saudaramu di dalam salah satu tempatmu, di negeri yang diberikan kepadamu oleh TUHAN, Allahmu, maka janganlah engkau menegarkan hati ataupun menggenggam tangan terhadap saudaramu yang miskin itu,'),(5327,4,15,8,'tetapi engkau harus membuka tangan lebar-lebar baginya dan memberi pinjaman kepadanya dengan limpahnya, cukup untuk keperluannya, seberapa ia perlukan.'),(5328,4,15,9,'Hati-hatilah, supaya jangan timbul di dalam hatimu pikiran dursila, demikian: Sudah dekat tahun ketujuh, tahun penghapusan hutang, dan engkau menjadi kesal terhadap saudaramu yang miskin itu dan engkau tidak memberikan apa-apa kepadanya, maka ia berseru kepada TUHAN tentang engkau, dan hal itu menjadi dosa bagimu.'),(5329,4,15,10,'Engkau harus memberi kepadanya dengan limpahnya dan janganlah hatimu berdukacita, apabila engkau memberi kepadanya, sebab oleh karena hal itulah TUHAN, Allahmu, akan memberkati engkau dalam segala pekerjaanmu dan dalam segala usahamu.'),(5330,4,15,11,'Sebab orang-orang miskin tidak hentinya akan ada di dalam negeri itu; itulah sebabnya aku memberi perintah kepadamu, demikian: Haruslah engkau membuka tangan lebar-lebar bagi saudaramu, yang tertindas dan yang miskin di negerimu.\"'),(5331,4,15,12,'Apabila seorang saudaramu menjual dirinya kepadamu, baik seorang laki-laki Ibrani ataupun seorang perempuan Ibrani, maka ia akan bekerja padamu enam tahun lamanya, tetapi pada tahun yang ketujuh engkau harus melepaskan dia sebagai orang merdeka.'),(5332,4,15,13,'Dan apabila engkau melepaskan dia sebagai orang merdeka, maka janganlah engkau melepaskan dia dengan tangan hampa,'),(5333,4,15,14,'engkau harus dengan limpahnya memberi bekal kepadanya dari kambing dombamu, dari tempat pengirikanmu dan dari tempat pemerasanmu, sesuai dengan berkat yang diberikan kepadamu oleh TUHAN, Allahmu, haruslah kauberikan kepadanya.'),(5334,4,15,15,'Haruslah kauingat, bahwa engkaupun dahulu budak di tanah Mesir dan engkau ditebus TUHAN, Allahmu; itulah sebabnya aku memberi perintah itu kepadamu pada hari ini.'),(5335,4,15,16,'Tetapi apabila dia berkata kepadamu: Aku tidak mau keluar meninggalkan engkau, karena ia mengasihi engkau dan keluargamu, sebab baik keadaannya padamu,'),(5336,4,15,17,'maka engkau harus mengambil sebuah penusuk dan menindik telinganya pada pintu, sehingga ia menjadi budakmu untuk selama-lamanya. Demikian juga kauperbuat kepada budakmu perempuan.'),(5337,4,15,18,'Janganlah merasa susah, apabila engkau melepaskan dia sebagai orang merdeka, sebab enam tahun lamanya ia telah bekerja padamu dengan jasa dua kali upah seorang pekerja harian. Maka TUHAN, Allahmu, akan memberkati engkau dalam segala sesuatu yang kaukerjakan.\"'),(5338,4,15,19,'Segala anak sulung jantan yang lahir di antara lembu sapimu dan kambing dombamu, haruslah kaukuduskan bagi TUHAN, Allahmu; janganlah engkau memakai anak sulung lembumu, dan janganlah engkau menggunting bulu anak sulung dombamu.'),(5339,4,15,20,'Di hadapan TUHAN, Allahmu, engkau harus memakan dagingnya tahun demi tahun di tempat yang akan dipilih TUHAN, engkau ini dan seisi rumahmu.'),(5340,4,15,21,'Tetapi apabila ada cacatnya, jika timpang atau buta, bahkan cacat apapun yang buruk, maka janganlah engkau menyembelihnya bagi TUHAN, Allahmu.'),(5341,4,15,22,'Di dalam tempatmu boleh engkau, baik orang najis maupun orang tahir, memakan dagingnya, seperti daging kijang atau daging rusa.'),(5342,4,15,23,'Hanya darahnya janganlah kaumakan; haruslah kaucurahkan ke tanah seperti air.\"'),(5343,4,16,1,'Ingatlah akan bulan Abib dan rayakanlah Paskah bagi TUHAN, Allahmu, sebab dalam bulan Abib itulah TUHAN, Allahmu, membawa engkau keluar dari Mesir pada waktu malam.'),(5344,4,16,2,'Maka engkau harus menyembelih kambing domba dan lembu sapi sebagai korban Paskah bagi TUHAN, Allahmu, di tempat yang akan dipilih TUHAN untuk membuat nama-Nya diam di sana.'),(5345,4,16,3,'Janganlah engkau makan sesuatu yang beragi besertanya; tujuh hari lamanya engkau harus makan roti yang tidak beragi besertanya, yakni roti penderitaan, sebab dengan buru-buru engkau keluar dari tanah Mesir. Maksudnya supaya seumur hidupmu engkau teringat akan hari engkau keluar dari tanah Mesir.'),(5346,4,16,4,'Janganlah terdapat padamu ragi di seluruh daerahmu, tujuh hari lamanya; dan dari daging hewan yang kausembelih pada waktu petang pada hari pertama, janganlah ada yang bermalam sampai pagi.'),(5347,4,16,5,'Engkau tidak boleh mempersembahkan korban Paskah di salah satu tempat yang diberikan kepadamu oleh TUHAN, Allahmu.'),(5348,4,16,6,'Tetapi di tempat yang akan dipilih TUHAN, Allahmu, untuk membuat nama-Nya diam di sana, engkau harus mempersembahkan korban Paskah itu pada waktu senja, ketika matahari terbenam, bertepatan dengan saat engkau keluar dari Mesir.'),(5349,4,16,7,'Engkau harus memasaknya dan memakannya di tempat yang akan dipilih TUHAN, Allahmu; kemudian paginya engkau harus pulang kembali ke kemahmu.'),(5350,4,16,8,'Enam hari lamanya engkau harus makan roti yang tidak beragi dan pada hari yang ketujuh harus ada perkumpulan raya bagi TUHAN, Allahmu; maka janganlah engkau melakukan pekerjaan.'),(5351,4,16,9,'Tujuh minggu harus kauhitung: pada waktu orang mulai menyabit gandum yang belum dituai, haruslah engkau mulai menghitung tujuh minggu itu.'),(5352,4,16,10,'Kemudian haruslah engkau merayakan hari raya Tujuh Minggu bagi TUHAN, Allahmu, sekedar persembahan sukarela yang akan kauberikan, sesuai dengan berkat yang diberikan kepadamu oleh TUHAN, Allahmu.'),(5353,4,16,11,'Haruslah engkau bersukaria di hadapan TUHAN, Allahmu, engkau ini dan anakmu laki-laki serta anakmu perempuan, hambamu laki-laki dan hambamu perempuan, dan orang Lewi yang di dalam tempatmu, dan orang asing, anak yatim dan janda, yang di tengah-tengahmu, di tempat yang akan dipilih TUHAN, Allahmu, untuk membuat nama-Nya diam di sana.'),(5354,4,16,12,'Haruslah kauingat, bahwa engkaupun dahulu budak di Mesir, dan haruslah engkau melakukan ketetapan ini dengan setia.'),(5355,4,16,13,'Hari raya Pondok Daun haruslah kaurayakan tujuh hari lamanya, apabila engkau selesai mengumpulkan hasil tempat pengirikanmu dan tempat pemerasanmu.'),(5356,4,16,14,'Haruslah engkau bersukaria pada hari rayamu itu, engkau ini dan anakmu laki-laki serta anakmu perempuan, hambamu laki-laki dan hambamu perempuan, dan orang Lewi, orang asing, anak yatim dan janda yang di dalam tempatmu.'),(5357,4,16,15,'Tujuh hari lamanya harus engkau mengadakan perayaan bagi TUHAN, Allahmu, di tempat yang akan dipilih TUHAN; sebab TUHAN, Allahmu, akan memberkati engkau dalam segala hasil tanahmu dan dalam segala usahamu, sehingga engkau dapat bersukaria dengan sungguh-sungguh.'),(5358,4,16,16,'Tiga kali setahun setiap orang laki-laki di antaramu harus menghadap hadirat TUHAN, Allahmu, ke tempat yang akan dipilih-Nya, yakni pada hari raya Roti Tidak Beragi, pada hari raya Tujuh Minggu dan pada hari raya Pondok Daun. Janganlah ia menghadap hadirat TUHAN dengan tangan hampa,'),(5359,4,16,17,'tetapi masing-masing dengan sekedar persembahan, sesuai dengan berkat yang diberikan kepadamu oleh TUHAN, Allahmu.\"'),(5360,4,16,18,'Hakim-hakim dan petugas-petugas haruslah kauangkat di segala tempat yang diberikan TUHAN, Allahmu, kepadamu, menurut suku-sukumu; mereka harus menghakimi bangsa itu dengan pengadilan yang adil.'),(5361,4,16,19,'Janganlah memutarbalikkan keadilan, janganlah memandang bulu dan janganlah menerima suap, sebab suap membuat buta mata orang-orang bijaksana dan memutarbalikkan perkataan orang-orang yang benar.'),(5362,4,16,20,'Semata-mata keadilan, itulah yang harus kaukejar, supaya engkau hidup dan memiliki negeri yang diberikan kepadamu oleh TUHAN, Allahmu.\"'),(5363,4,16,21,'Janganlah engkau menanam sesuatu pohon sebagai tiang berhala di samping mezbah TUHAN, Allahmu, mezbah yang akan kaubuat bagimu.'),(5364,4,16,22,'Janganlah juga kaudirikan bagimu tugu berhala, yang dibenci oleh TUHAN, Allahmu.'),(5365,4,17,1,'Janganlah engkau mempersembahkan bagi TUHAN, Allahmu, lembu atau domba, yang ada cacatnya, atau sesuatu yang buruk; sebab yang demikian adalah kekejian bagi TUHAN, Allahmu.\"'),(5366,4,17,2,'Apabila di tengah-tengahmu di salah satu tempatmu yang diberikan kepadamu oleh TUHAN, Allahmu, ada terdapat seorang laki-laki atau perempuan yang melakukan apa yang jahat di mata TUHAN, Allahmu, dengan melangkahi perjanjian-Nya,'),(5367,4,17,3,'dan yang pergi beribadah kepada allah lain dan sujud menyembah kepadanya, atau kepada matahari atau bulan atau segenap tentara langit, hal yang telah Kularang itu;'),(5368,4,17,4,'dan apabila hal itu diberitahukan atau terdengar kepadamu, maka engkau harus memeriksanya baik-baik. Jikalau ternyata benar dan sudah pasti, bahwa kekejian itu dilakukan di antara orang Israel,'),(5369,4,17,5,'maka engkau harus membawa laki-laki atau perempuan yang telah melakukan perbuatan jahat itu ke luar ke pintu gerbang, kemudian laki-laki atau perempuan itu harus kaulempari dengan batu sampai mati.'),(5370,4,17,6,'Atas keterangan dua atau tiga orang saksi haruslah mati dibunuh orang yang dihukum mati; atas keterangan satu orang saksi saja janganlah ia dihukum mati.'),(5371,4,17,7,'Saksi-saksi itulah yang pertama-tama menggerakkan tangan mereka untuk membunuh dia, kemudian seluruh rakyat. Demikianlah harus kauhapuskan yang jahat itu dari tengah-tengahmu.\"'),(5372,4,17,8,'Apabila sesuatu perkara terlalu sukar bagimu untuk diputuskan, misalnya bunuh-membunuh, tuntut-menuntut, atau luka-melukai--perkara pendakwaan di dalam tempatmu--maka haruslah engkau pergi menghadap ke tempat yang akan dipilih TUHAN, Allahmu;'),(5373,4,17,9,'haruslah engkau pergi kepada imam-imam orang Lewi dan kepada hakim yang ada pada waktu itu, dan meminta putusan. Mereka akan memberitahukan kepadamu keputusan hakim.'),(5374,4,17,10,'Dan engkau harus berbuat menurut keputusan yang diberitahukan mereka kepadamu dari tempat yang akan dipilih TUHAN; engkau harus melakukan dengan setia segala yang ditunjukkan mereka kepadamu.'),(5375,4,17,11,'Menurut petunjuk yang diberikan mereka kepadamu dan menurut keputusan yang dikatakan mereka kepadamu haruslah engkau berbuat; janganlah engkau menyimpang ke kanan atau ke kiri dari keputusan yang diberitahukan mereka kepadamu.'),(5376,4,17,12,'Orang yang berlaku terlalu berani dengan tidak mendengarkan perkataan imam yang berdiri di sana sebagai pelayan TUHAN, Allahmu, ataupun perkataan hakim, maka orang itu harus mati. Demikianlah harus kauhapuskan yang jahat itu dari antara orang Israel.'),(5377,4,17,13,'Maka seluruh bangsa itu akan mendengar dan menjadi takut dan tidak lagi berlaku terlalu berani.\"'),(5378,4,17,14,'Apabila engkau telah masuk ke negeri yang diberikan kepadamu oleh TUHAN, Allahmu, dan telah mendudukinya dan diam di sana, kemudian engkau berkata: Aku mau mengangkat raja atasku, seperti segala bangsa yang di sekelilingku,'),(5379,4,17,15,'maka hanyalah raja yang dipilih TUHAN, Allahmu, yang harus kauangkat atasmu. Dari tengah-tengah saudara-saudaramu haruslah engkau mengangkat seorang raja atasmu; seorang asing yang bukan saudaramu tidaklah boleh kauangkat atasmu.'),(5380,4,17,16,'Hanya, janganlah ia memelihara banyak kuda dan janganlah ia mengembalikan bangsa ini ke Mesir untuk mendapat banyak kuda, sebab TUHAN telah berfirman kepadamu: Janganlah sekali-kali kamu kembali melalui jalan ini lagi.'),(5381,4,17,17,'Juga janganlah ia mempunyai banyak isteri, supaya hatinya jangan menyimpang; emas dan perakpun janganlah ia kumpulkan terlalu banyak.'),(5382,4,17,18,'Apabila ia duduk di atas takhta kerajaan, maka haruslah ia menyuruh menulis baginya salinan hukum ini menurut kitab yang ada pada imam-imam orang Lewi.'),(5383,4,17,19,'Itulah yang harus ada di sampingnya dan haruslah ia membacanya seumur hidupnya untuk belajar takut akan TUHAN, Allahnya, dengan berpegang pada segala isi hukum dan ketetapan ini untuk dilakukannya,'),(5384,4,17,20,'supaya jangan ia tinggi hati terhadap saudara-saudaranya, supaya jangan ia menyimpang dari perintah itu ke kanan atau ke kiri, agar lama ia memerintah, ia dan anak-anaknya di tengah-tengah orang Israel.\"'),(5385,4,18,1,'Imam-imam orang Lewi, seluruh suku Lewi, janganlah mendapat bagian milik pusaka bersama-sama orang Israel; dari korban api-apian kepada TUHAN dan apa yang menjadi milik-Nya harus mereka mendapat rezeki.'),(5386,4,18,2,'Janganlah ia mempunyai milik pusaka di tengah-tengah saudara-saudaranya; Tuhanlah milik pusakanya, seperti yang dijanjikan-Nya kepadanya.'),(5387,4,18,3,'Inilah hak imam terhadap kaum awam, terhadap mereka yang mempersembahkan korban sembelihan, baik lembu maupun domba: kepada imam haruslah diberikan paha depan, kedua rahang dan perut besar.'),(5388,4,18,4,'Hasil pertama dari gandummu, dari anggurmu dan minyakmu, dan bulu guntingan pertama dari dombamu haruslah kauberikan kepadanya.'),(5389,4,18,5,'Sebab dialah yang dipilih oleh TUHAN, Allahmu, dari segala sukumu, supaya ia senantiasa melayani TUHAN dan menyelenggarakan kebaktian demi nama-Nya, ia dan anak-anaknya.'),(5390,4,18,6,'Apabila seorang Lewi datang dari tempat manapun di Israel, di mana ia tinggal sebagai pendatang, dan dengan sepenuh hati masuk ke tempat yang akan dipilih TUHAN,'),(5391,4,18,7,'dan menyelenggarakan kebaktian demi nama TUHAN, Allahnya, sama seperti semua saudaranya, orang-orang Lewi, yang melayani TUHAN di sana,'),(5392,4,18,8,'maka haruslah mereka mendapat rezeki yang sama, dengan tidak terhitung apa yang ia peroleh dengan menjual harta nenek moyangnya.\"'),(5393,4,18,9,'Apabila engkau sudah masuk ke negeri yang diberikan kepadamu oleh TUHAN, Allahmu, maka janganlah engkau belajar berlaku sesuai dengan kekejian yang dilakukan bangsa-bangsa itu.'),(5394,4,18,10,'Di antaramu janganlah didapati seorangpun yang mempersembahkan anaknya laki-laki atau anaknya perempuan sebagai korban dalam api, ataupun seorang yang menjadi petenung, seorang peramal, seorang penelaah, seorang penyihir,'),(5395,4,18,11,'seorang pemantera, ataupun seorang yang bertanya kepada arwah atau kepada roh peramal atau yang meminta petunjuk kepada orang-orang mati.'),(5396,4,18,12,'Sebab setiap orang yang melakukan hal-hal ini adalah kekejian bagi TUHAN, dan oleh karena kekejian-kekejian inilah TUHAN, Allahmu, menghalau mereka dari hadapanmu.'),(5397,4,18,13,'Haruslah engkau hidup dengan tidak bercela di hadapan TUHAN, Allahmu.'),(5398,4,18,14,'Sebab bangsa-bangsa yang daerahnya akan kaududuki ini mendengarkan kepada peramal atau petenung, tetapi engkau ini tidak diizinkan TUHAN, Allahmu, melakukan yang demikian.'),(5399,4,18,15,'Seorang nabi dari tengah-tengahmu, dari antara saudara-saudaramu, sama seperti aku, akan dibangkitkan bagimu oleh TUHAN, Allahmu; dialah yang harus kamu dengarkan.'),(5400,4,18,16,'Tepat seperti yang kamu minta dahulu kepada TUHAN, Allahmu, di gunung Horeb, pada hari perkumpulan, dengan berkata: Tidak mau aku mendengar lagi suara TUHAN, Allahku, dan api yang besar ini tidak mau aku melihatnya lagi, supaya jangan aku mati.'),(5401,4,18,17,'Lalu berkatalah TUHAN kepadaku: Apa yang dikatakan mereka itu baik;'),(5402,4,18,18,'seorang nabi akan Kubangkitkan bagi mereka dari antara saudara mereka, seperti engkau ini; Aku akan menaruh firman-Ku dalam mulutnya, dan ia akan mengatakan kepada mereka segala yang Kuperintahkan kepadanya.'),(5403,4,18,19,'Orang yang tidak mendengarkan segala firman-Ku yang akan diucapkan nabi itu demi nama-Ku, dari padanya akan Kutuntut pertanggungjawaban.'),(5404,4,18,20,'Tetapi seorang nabi, yang terlalu berani untuk mengucapkan demi nama-Ku perkataan yang tidak Kuperintahkan untuk dikatakan olehnya, atau yang berkata demi nama allah lain, nabi itu harus mati.'),(5405,4,18,21,'Jika sekiranya kamu berkata dalam hatimu: Bagaimanakah kami mengetahui perkataan yang tidak difirmankan TUHAN? --'),(5406,4,18,22,'apabila seorang nabi berkata demi nama TUHAN dan perkataannya itu tidak terjadi dan tidak sampai, maka itulah perkataan yang tidak difirmankan TUHAN; dengan terlalu berani nabi itu telah mengatakannya, maka janganlah gentar kepadanya.\"'),(5407,4,19,1,'Apabila TUHAN, Allahmu, sudah melenyapkan bangsa-bangsa yang negerinya diberikan kepadamu oleh TUHAN, Allahmu, dan engkau sudah menduduki daerah mereka dan diam di kota-kota dan rumah-rumah mereka,'),(5408,4,19,2,'maka engkau harus mengkhususkan tiga kota di dalam negeri yang diberikan TUHAN, Allahmu, kepadamu untuk diduduki.'),(5409,4,19,3,'Engkau harus menetapkan jauhnya jalan, dan membagi dalam tiga bagian wilayah negeri yang diberikan TUHAN, Allahmu, untuk dimiliki olehmu, supaya setiap pembunuh dapat melarikan diri ke sana.'),(5410,4,19,4,'Inilah ketentuan mengenai pembunuh yang melarikan diri ke sana dan boleh tinggal hidup: apabila ia membunuh sesamanya manusia dengan tidak sengaja dan dengan tidak membenci dia sebelumnya,'),(5411,4,19,5,'misalnya apabila seseorang pergi ke hutan dengan temannya untuk membelah kayu, ketika tangannya mengayunkan kapak untuk menebang pohon kayu, mata kapak terlucut dari gagangnya, lalu mengenai temannya sehingga mati, maka ia boleh melarikan diri ke salah satu kota itu dan tinggal hidup.'),(5412,4,19,6,'Maksudnya supaya jangan penuntut tebusan darah sementara hatinya panas dapat mengejar pembunuh itu, karena jauhnya perjalanan, menangkapnya dan membunuhnya, padahal pembunuh itu tidak patut mendapat hukuman mati, karena ia tidak membenci dia sebelumnya.'),(5413,4,19,7,'Itulah sebabnya aku memberi perintah kepadamu, demikian: tiga kota haruslah kaukhususkan.'),(5414,4,19,8,'Dan jika TUHAN, Allahmu, sudah meluaskan daerahmu nanti, seperti yang dijanjikan-Nya dengan sumpah kepada nenek moyangmu, dan sudah memberikan kepadamu seluruh negeri yang dikatakan-Nya akan diberikan kepada nenek moyangmu,'),(5415,4,19,9,'--apabila engkau melakukan dengan setia perintah ini, yang kusampaikan kepadamu pada hari ini, dengan mengasihi TUHAN, Allahmu, dan dengan senantiasa hidup menurut jalan yang ditunjukkan-Nya--maka haruslah engkau menambah tiga kota lagi kepada yang tiga itu,'),(5416,4,19,10,'supaya jangan tercurah darah orang yang tidak bersalah di negeri yang diberikan TUHAN, Allahmu, kepadamu menjadi milikmu dan hutang darah melekat kepadamu.'),(5417,4,19,11,'Tetapi apabila seseorang membenci sesamanya manusia, dan dengan bersembunyi menantikan dia, lalu bangun menyerang dan memukul dia, sehingga mati, kemudian melarikan diri ke salah satu kota itu,'),(5418,4,19,12,'maka haruslah para tua-tua kotanya menyuruh mengambil dia dari sana dan menyerahkan dia kepada penuntut tebusan darah, supaya ia mati dibunuh.'),(5419,4,19,13,'Janganlah engkau merasa sayang kepadanya. Demikianlah harus kauhapuskan darah orang yang tidak bersalah dari antara orang Israel, supaya baik keadaanmu.\"'),(5420,4,19,14,'Janganlah menggeser batas tanah sesamamu yang telah ditetapkan oleh orang-orang dahulu di dalam milik pusaka yang akan kaumiliki di negeri yang diberikan TUHAN, Allahmu, kepadamu untuk menjadi milikmu.'),(5421,4,19,15,'Satu orang saksi saja tidak dapat menggugat seseorang mengenai perkara kesalahan apapun atau dosa apapun yang mungkin dilakukannya; baru atas keterangan dua atau tiga orang saksi perkara itu tidak disangsikan.'),(5422,4,19,16,'Apabila seorang saksi jahat menggugat seseorang untuk menuduh dia mengenai suatu pelanggaran,'),(5423,4,19,17,'maka kedua orang yang mempunyai perkara itu haruslah berdiri di hadapan TUHAN, di hadapan imam-imam dan hakim-hakim yang ada pada waktu itu.'),(5424,4,19,18,'Maka hakim-hakim itu harus memeriksanya baik-baik, dan apabila ternyata, bahwa saksi itu seorang saksi dusta dan bahwa ia telah memberi tuduhan dusta terhadap saudaranya,'),(5425,4,19,19,'maka kamu harus memperlakukannya sebagaimana ia bermaksud memperlakukan saudaranya. Demikianlah harus kauhapuskan yang jahat itu dari tengah-tengahmu.'),(5426,4,19,20,'Maka orang-orang lain akan mendengar dan menjadi takut, sehingga mereka tidak akan melakukan lagi perbuatan jahat seperti itu di tengah-tengahmu.'),(5427,4,19,21,'Janganlah engkau merasa sayang kepadanya, sebab berlaku: nyawa ganti nyawa, mata ganti mata, gigi ganti gigi, tangan ganti tangan, kaki ganti kaki.\"'),(5428,4,20,1,'Apabila engkau keluar berperang melawan musuhmu, dan engkau melihat kuda dan kereta, yakni tentara yang lebih banyak dari padamu, maka janganlah engkau takut kepadanya, sebab TUHAN, Allahmu, yang telah menuntun engkau keluar dari tanah Mesir, menyertai engkau.'),(5429,4,20,2,'Apabila kamu menghadapi pertempuran, maka seorang imam harus tampil ke depan dan berbicara kepada rakyat,'),(5430,4,20,3,'dengan berkata kepada mereka: Dengarlah, hai orang Israel! Kamu sekarang menghadapi pertempuran melawan musuhmu; janganlah lemah hatimu, janganlah takut, janganlah gentar dan janganlah gemetar karena mereka,'),(5431,4,20,4,'sebab TUHAN, Allahmu, Dialah yang berjalan menyertai kamu untuk berperang bagimu melawan musuhmu, dengan maksud memberikan kemenangan kepadamu.'),(5432,4,20,5,'Para pengatur pasukan haruslah berbicara kepada tentara, demikian: Siapakah orang yang telah mendirikan rumah baru, tetapi belum menempatinya? Ia boleh pergi dan pulang ke rumahnya, supaya jangan ia mati dalam pertempuran dan orang lain yang menempatinya.'),(5433,4,20,6,'Dan siapa telah membuat kebun anggur, tetapi belum mengecap hasilnya? Ia boleh pergi dan pulang ke rumahnya, supaya jangan ia mati dalam pertempuran dan orang lain yang mengecap hasilnya.'),(5434,4,20,7,'Dan siapa telah bertunangan dengan seorang perempuan, tetapi belum mengawininya? Ia boleh pergi dan pulang ke rumahnya, supaya jangan ia mati dalam pertempuran dan orang lain yang mengawininya.'),(5435,4,20,8,'Lagi para pengatur pasukan itu harus berbicara kepada tentara demikian: Siapa takut dan lemah hati? Ia boleh pergi dan pulang ke rumahnya, supaya hati saudara-saudaranya jangan tawar seperti hatinya.'),(5436,4,20,9,'Apabila para pengatur pasukan selesai berbicara kepada tentara, maka haruslah ditunjuk kepala-kepala pasukan untuk mengepalai tentara.'),(5437,4,20,10,'Apabila engkau mendekati suatu kota untuk berperang melawannya, maka haruslah engkau menawarkan perdamaian kepadanya.'),(5438,4,20,11,'Apabila kota itu menerima tawaran perdamaian itu dan dibukanya pintu gerbang bagimu, maka haruslah semua orang yang terdapat di situ melakukan pekerjaan rodi bagimu dan menjadi hamba kepadamu.'),(5439,4,20,12,'Tetapi apabila kota itu tidak mau berdamai dengan engkau, melainkan mengadakan pertempuran melawan engkau, maka haruslah engkau mengepungnya;'),(5440,4,20,13,'dan setelah TUHAN, Allahmu, menyerahkannya ke dalam tanganmu, maka haruslah engkau membunuh seluruh penduduknya yang laki-laki dengan mata pedang.'),(5441,4,20,14,'Hanya perempuan, anak-anak, hewan dan segala yang ada di kota itu, yakni seluruh jarahan itu, boleh kaurampas bagimu sendiri, dan jarahan yang dari musuhmu ini, yang diberikan kepadamu oleh TUHAN, Allahmu, boleh kaupergunakan.'),(5442,4,20,15,'Demikianlah harus kaulakukan terhadap segala kota yang sangat jauh letaknya dari tempatmu, yang tidak termasuk kota-kota bangsa-bangsa di sini.'),(5443,4,20,16,'Tetapi dari kota-kota bangsa-bangsa itu yang diberikan TUHAN, Allahmu, kepadamu menjadi milik pusakamu, janganlah kaubiarkan hidup apapun yang bernafas,'),(5444,4,20,17,'melainkan kautumpas sama sekali, yakni orang Het, orang Amori, orang Kanaan, orang Feris, orang Hewi, dan orang Yebus, seperti yang diperintahkan kepadamu oleh TUHAN, Allahmu,'),(5445,4,20,18,'supaya mereka jangan mengajar kamu berbuat sesuai dengan segala kekejian, yang dilakukan mereka bagi allah mereka, sehingga kamu berbuat dosa kepada TUHAN, Allahmu.'),(5446,4,20,19,'Apabila dalam memerangi suatu kota, engkau lama mengepungnya untuk direbut, maka tidak boleh engkau merusakkan pohon-pohon sekelilingnya dengan mengayunkan kapak kepadanya; buahnya boleh kaumakan, tetapi batangnya janganlah kautebang; sebab, pohon yang di padang itu bukan manusia, jadi tidak patut ikut kaukepung.'),(5447,4,20,20,'Hanya pohon-pohon, yang engkau tahu tidak menghasilkan makanan, boleh kaurusakkan dan kautebang untuk mendirikan pagar pengepungan terhadap kota yang berperang melawan engkau, sampai kota itu jatuh.\"'),(5448,4,21,1,'Apabila di tanah yang diberikan TUHAN, Allahmu, kepadamu untuk menjadi milikmu, terdapat seorang yang mati terbunuh di padang, dengan tidak diketahui siapa yang membunuhnya,'),(5449,4,21,2,'maka haruslah para tua-tuamu dan para hakimmu keluar mengukur jarak ke kota-kota yang di sekeliling orang yang terbunuh itu.'),(5450,4,21,3,'Kota yang ternyata paling dekat dengan tempat orang yang terbunuh itu, para tua-tua kota itulah harus mengambil seekor lembu betina yang muda, yang belum pernah dipakai, yang belum pernah menghela dengan kuk.'),(5451,4,21,4,'Para tua-tua kota itu haruslah membawa lembu muda itu ke suatu lembah yang selalu berair dan yang belum pernah dikerjakan atau ditaburi, dan di sana di lembah itu haruslah mereka mematahkan batang leher lembu muda itu.'),(5452,4,21,5,'Imam-imam bani Lewi haruslah tampil ke depan, sebab merekalah yang dipilih TUHAN, Allahmu, untuk melayani Dia dan untuk memberi berkat demi nama TUHAN; menurut putusan merekalah setiap perkara dan setiap hal luka-melukai harus diselesaikan.'),(5453,4,21,6,'Dan semua tua-tua dari kota yang paling dekat dengan tempat orang yang terbunuh itu, haruslah membasuh tangannya di atas lembu muda yang batang lehernya dipatahkan di lembah itu,'),(5454,4,21,7,'dan mereka harus memberi pernyataan dengan mengatakan: Tangan kami tidak mencurahkan darah ini dan mata kami tidak melihatnya.'),(5455,4,21,8,'Adakanlah pendamaian bagi umat-Mu Israel yang telah Kautebus itu, TUHAN, dan janganlah timpakan darah orang yang tidak bersalah ke tengah-tengah umat-Mu Israel. Maka karena darah itu telah diadakan pendamaian bagi mereka.'),(5456,4,21,9,'Demikianlah engkau harus menghapuskan darah orang yang tidak bersalah itu dari tengah-tengahmu, sebab dengan demikian engkau melakukan apa yang benar di mata TUHAN.\"'),(5457,4,21,10,'Apabila engkau keluar berperang melawan musuhmu, dan TUHAN, Allahmu, menyerahkan mereka ke dalam tanganmu dan engkau menjadikan mereka tawanan,'),(5458,4,21,11,'dan engkau melihat di antara tawanan itu seorang perempuan yang elok, sehingga hatimu mengingini dia dan engkau mau mengambil dia menjadi isterimu,'),(5459,4,21,12,'maka haruslah engkau membawa dia ke dalam rumahmu. Perempuan itu harus mencukur rambutnya, memotong kukunya,'),(5460,4,21,13,'menanggalkan pakaian yang dipakainya pada waktu ditawan, dan tinggal di rumahmu untuk menangisi ibu bapanya sebulan lamanya. Sesudah demikian, bolehlah engkau menghampiri dia dan menjadi suaminya, sehingga ia menjadi isterimu.'),(5461,4,21,14,'Apabila engkau tidak suka lagi kepadanya, maka haruslah engkau membiarkan dia pergi sesuka hatinya; tidak boleh sekali-kali engkau menjual dia dengan bayaran uang; tidak boleh engkau memperlakukan dia sebagai budak, sebab engkau telah memaksa dia.\"'),(5462,4,21,15,'Apabila seorang mempunyai dua orang isteri, yang seorang dicintai dan yang lain tidak dicintainya, dan mereka melahirkan anak-anak lelaki baginya, baik isteri yang dicintai maupun isteri yang tidak dicintai, dan anak sulung adalah dari isteri yang tidak dicintai,'),(5463,4,21,16,'maka pada waktu ia membagi warisan harta kepunyaannya kepada anak-anaknya itu, tidaklah boleh ia memberikan bagian anak sulung kepada anak dari isteri yang dicintai merugikan anak dari isteri yang tidak dicintai, yang adalah anak sulung.'),(5464,4,21,17,'Tetapi ia harus mengakui anak yang sulung, anak dari isteri yang tidak dicintai itu, dengan memberikan kepadanya dua bagian dari segala kepunyaannya, sebab dialah kegagahannya yang pertama-tama: dialah yang empunya hak kesulungan.\"'),(5465,4,21,18,'Apabila seseorang mempunyai anak laki-laki yang degil dan membangkang, yang tidak mau mendengarkan perkataan ayahnya dan ibunya, dan walaupun mereka menghajar dia, tidak juga ia mendengarkan mereka,'),(5466,4,21,19,'maka haruslah ayahnya dan ibunya memegang dia dan membawa dia keluar kepada para tua-tua kotanya di pintu gerbang tempat kediamannya,'),(5467,4,21,20,'dan harus berkata kepada para tua-tua kotanya: Anak kami ini degil dan membangkang, ia tidak mau mendengarkan perkataan kami, ia seorang pelahap dan peminum.'),(5468,4,21,21,'Maka haruslah semua orang sekotanya melempari anak itu dengan batu, sehingga ia mati. Demikianlah harus kauhapuskan yang jahat itu dari tengah-tengahmu; dan seluruh orang Israel akan mendengar dan menjadi takut.\"'),(5469,4,21,22,'Apabila seseorang berbuat dosa yang sepadan dengan hukuman mati, lalu ia dihukum mati, kemudian kaugantung dia pada sebuah tiang,'),(5470,4,21,23,'maka janganlah mayatnya dibiarkan semalam-malaman pada tiang itu, tetapi haruslah engkau menguburkan dia pada hari itu juga, sebab seorang yang digantung terkutuk oleh Allah; janganlah engkau menajiskan tanah yang diberikan TUHAN, Allahmu, kepadamu menjadi milik pusakamu.\"'),(5471,4,22,1,'Apabila engkau melihat, bahwa lembu atau domba saudaramu tersesat, janganlah engkau pura-pura tidak tahu; haruslah engkau benar-benar mengembalikannya kepada saudaramu itu.'),(5472,4,22,2,'Dan apabila saudaramu itu tidak tinggal dekat denganmu dan engkau tidak mengenalnya, maka haruslah engkau membawa hewan itu ke dalam rumahmu dan haruslah itu tinggal padamu, sampai saudaramu itu datang mencarinya; engkau harus mengembalikannya kepadanya.'),(5473,4,22,3,'Demikianlah harus kauperbuat dengan keledainya, demikianlah kauperbuat dengan pakaiannya, demikianlah kauperbuat dengan setiap barang yang hilang dari saudaramu dan yang kautemui; tidak boleh engkau pura-pura tidak tahu.'),(5474,4,22,4,'Apabila engkau melihat keledai saudaramu atau lembunya rebah di jalan, janganlah engkau pura-pura tidak tahu; engkau harus benar-benar menolong membangunkannya bersama-sama dengan saudaramu itu.\"'),(5475,4,22,5,'Seorang perempuan janganlah memakai pakaian laki-laki dan seorang laki-laki janganlah mengenakan pakaian perempuan, sebab setiap orang yang melakukan hal ini adalah kekejian bagi TUHAN, Allahmu.'),(5476,4,22,6,'Apabila engkau menemui di jalan sarang burung di salah satu pohon atau di tanah dengan anak-anak burung atau telur-telur di dalamnya, dan induknya sedang duduk mendekap anak-anak atau telur-telur itu, maka janganlah engkau mengambil induk itu bersama-sama dengan anak-anaknya.'),(5477,4,22,7,'Setidak-tidaknya induk itu haruslah kaulepaskan, tetapi anak-anaknya boleh kauambil. Maksudnya supaya baik keadaanmu dan lanjut umurmu.'),(5478,4,22,8,'Apabila engkau mendirikan rumah yang baru, maka haruslah engkau memagari sotoh rumahmu, supaya jangan kaudatangkan hutang darah kepada rumahmu itu, apabila ada seorang jatuh dari atasnya.'),(5479,4,22,9,'Janganlah kautaburi kebun anggurmu dengan dua jenis benih, supaya seluruh hasil benih yang kautaburkan dan hasil kebun anggurmu jangan menjadi milik tempat kudus.'),(5480,4,22,10,'Janganlah engkau membajak dengan lembu dan keledai bersama-sama.'),(5481,4,22,11,'Janganlah engkau memakai pakaian yang dua jenis bahannya, yakni bulu domba dan lenan bersama-sama.'),(5482,4,22,12,'Haruslah engkau membuat tali yang terpilin pada keempat punca kain penutup tubuhmu.\"'),(5483,4,22,13,'Apabila seseorang mengambil isteri dan setelah menghampiri perempuan itu, menjadi benci kepadanya,'),(5484,4,22,14,'menuduhkan kepadanya perbuatan yang kurang senonoh dan membusukkan namanya dengan berkata: Perempuan ini kuambil menjadi isteriku, tetapi ketika ia kuhampiri, tidak ada kudapati padanya tanda-tanda keperawanan--'),(5485,4,22,15,'maka haruslah ayah dan ibu gadis itu memperlihatkan tanda-tanda keperawanan gadis itu kepada para tua-tua kota di pintu gerbang.'),(5486,4,22,16,'Dan ayah si gadis haruslah berkata kepada para tua-tua itu: Aku telah memberikan anakku kepada laki-laki ini menjadi isterinya, lalu ia menjadi benci kepadanya,'),(5487,4,22,17,'dan ketahuilah, ia menuduhkan perbuatan yang kurang senonoh dengan berkata: Tidak ada kudapati tanda-tanda keperawanan pada anakmu. Tetapi inilah tanda-tanda keperawanan anakku itu. Lalu haruslah mereka membentangkan kain itu di depan para tua-tua kota.'),(5488,4,22,18,'Maka haruslah para tua-tua kota itu mengambil laki-laki itu, menghajar dia,'),(5489,4,22,19,'mendenda dia seratus syikal perak dan memberikan perak itu kepada ayah si gadis--karena laki-laki itu telah membusukkan nama seorang perawan Israel. Perempuan itu haruslah tetap menjadi isterinya; selama hidupnya tidak boleh laki-laki itu menyuruh dia pergi.'),(5490,4,22,20,'Tetapi jika tuduhan itu benar dan tidak didapati tanda-tanda keperawanan pada si gadis,'),(5491,4,22,21,'maka haruslah si gadis dibawa ke luar ke depan pintu rumah ayahnya, dan orang-orang sekotanya haruslah melempari dia dengan batu, sehingga mati--sebab dia telah menodai orang Israel dengan bersundal di rumah ayahnya. Demikianlah harus kauhapuskan yang jahat itu dari tengah-tengahmu.'),(5492,4,22,22,'Apabila seseorang kedapatan tidur dengan seorang perempuan yang bersuami, maka haruslah keduanya dibunuh mati: laki-laki yang telah tidur dengan perempuan itu dan perempuan itu juga. Demikianlah harus kauhapuskan yang jahat itu dari antara orang Israel.'),(5493,4,22,23,'Apabila ada seorang gadis yang masih perawan dan yang sudah bertunangan--jika seorang laki-laki bertemu dengan dia di kota dan tidur dengan dia,'),(5494,4,22,24,'maka haruslah mereka keduanya kamu bawa ke luar ke pintu gerbang kota dan kamu lempari dengan batu, sehingga mati: gadis itu, karena walaupun di kota, ia tidak berteriak-teriak, dan laki-laki itu, karena ia telah memperkosa isteri sesamanya manusia. Demikianlah harus kauhapuskan yang jahat itu dari tengah-tengahmu.'),(5495,4,22,25,'Tetapi jikalau di padang laki-laki itu bertemu dengan gadis yang telah bertunangan itu, memaksa gadis itu tidur dengan dia, maka hanyalah laki-laki yang tidur dengan gadis itu yang harus mati,'),(5496,4,22,26,'tetapi gadis itu janganlah kauapa-apakan. Gadis itu tidak ada dosanya yang sepadan dengan hukuman mati, sebab perkara ini sama dengan perkara seseorang yang menyerang sesamanya manusia dan membunuhnya.'),(5497,4,22,27,'Sebab laki-laki itu bertemu dengan dia di padang; walaupun gadis yang bertunangan itu berteriak-teriak, tetapi tidak ada yang datang menolongnya.'),(5498,4,22,28,'Apabila seseorang bertemu dengan seorang gadis, yang masih perawan dan belum bertunangan, memaksa gadis itu tidur dengan dia, dan keduanya kedapatan--'),(5499,4,22,29,'maka haruslah laki-laki yang sudah tidur dengan gadis itu memberikan lima puluh syikal perak kepada ayah gadis itu, dan gadis itu haruslah menjadi isterinya, sebab laki-laki itu telah memperkosa dia; selama hidupnya tidak boleh laki-laki itu menyuruh dia pergi.'),(5500,4,22,30,'Seorang laki-laki janganlah mengambil isteri ayahnya dan jangan menyingkapkan punca kain ayahnya.\"'),(5501,4,23,1,'Orang yang hancur buah pelirnya atau yang terpotong kemaluannya, janganlah masuk jemaah TUHAN.'),(5502,4,23,2,'Seorang anak haram janganlah masuk jemaah TUHAN, bahkan keturunannya yang kesepuluhpun tidak boleh masuk jemaah TUHAN.'),(5503,4,23,3,'Seorang Amon atau seorang Moab janganlah masuk jemaah TUHAN, bahkan keturunannya yang kesepuluhpun tidak boleh masuk jemaah TUHAN sampai selama-lamanya,'),(5504,4,23,4,'karena mereka tidak menyongsong kamu dengan roti dan air pada waktu perjalananmu keluar dari Mesir, dan karena mereka mengupah Bileam bin Beor dari Petor di Aram-Mesopotamia melawan engkau, supaya dikutukinya engkau.'),(5505,4,23,5,'Tetapi TUHAN, Allahmu, tidak mau mendengarkan Bileam dan TUHAN, Allahmu, telah mengubah kutuk itu menjadi berkat bagimu, karena TUHAN, Allahmu, mengasihi engkau.'),(5506,4,23,6,'Selama engkau hidup, janganlah engkau mengikhtiarkan kesejahteraan dan kebahagiaan mereka sampai selama-lamanya.'),(5507,4,23,7,'Janganlah engkau menganggap keji orang Edom, sebab dia saudaramu. Janganlah engkau menganggap keji orang Mesir, sebab engkaupun dahulu adalah orang asing di negerinya.'),(5508,4,23,8,'Anak-anak yang lahir bagi mereka dalam keturunan yang ketiga, boleh masuk jemaah TUHAN.\"'),(5509,4,23,9,'Apabila engkau maju dengan tentaramu melawan musuhmu, maka haruslah engkau menjaga diri terhadap segala yang jahat.'),(5510,4,23,10,'Apabila ada di antaramu seorang laki-laki yang tidak tahir disebabkan oleh sesuatu yang terjadi atasnya pada malam hari, maka haruslah ia pergi ke luar perkemahan, janganlah ia masuk ke dalam perkemahan.'),(5511,4,23,11,'Kemudian menjelang senja haruslah ia mandi dengan air, dan pada waktu matahari terbenam, ia boleh masuk kembali ke dalam perkemahan.'),(5512,4,23,12,'Di luar perkemahan itu haruslah ada bagimu suatu tempat ke mana engkau pergi untuk kada hajat.'),(5513,4,23,13,'Di antara perlengkapanmu haruslah ada padamu sekop kecil dan apabila engkau jongkok kada hajat, haruslah engkau menggali lobang dengan itu dan menimbuni kotoranmu.'),(5514,4,23,14,'Sebab TUHAN, Allahmu, berjalan dari tengah-tengah perkemahanmu untuk melepaskan engkau dan menyerahkan musuhmu kepadamu; sebab itu haruslah perkemahanmu itu kudus, supaya jangan Ia melihat sesuatu yang tidak senonoh di antaramu, lalu berbalik dari padamu.\"'),(5515,4,23,15,'Janganlah kauserahkan kepada tuannya seorang budak yang melarikan diri dari tuannya kepadamu.'),(5516,4,23,16,'Bersama-sama engkau ia boleh tinggal, di tengah-tengahmu, di tempat yang dipilihnya di salah satu tempatmu, yang dirasanya baik; janganlah engkau menindas dia.\"'),(5517,4,23,17,'Di antara anak-anak perempuan Israel janganlah ada pelacur bakti, dan di antara anak-anak lelaki Israel janganlah ada semburit bakti.'),(5518,4,23,18,'Janganlah kaubawa upah sundal atau uang semburit ke dalam rumah TUHAN, Allahmu, untuk menepati salah satu nazar, sebab keduanya itu adalah kekejian bagi TUHAN, Allahmu.\"'),(5519,4,23,19,'Janganlah engkau membungakan kepada saudaramu, baik uang maupun bahan makanan atau apapun yang dapat dibungakan.'),(5520,4,23,20,'Dari orang asing boleh engkau memungut bunga, tetapi dari saudaramu janganlah engkau memungut bunga--supaya TUHAN, Allahmu, memberkati engkau dalam segala usahamu di negeri yang engkau masuki untuk mendudukinya.\"'),(5521,4,23,21,'Apabila engkau bernazar kepada TUHAN, Allahmu, janganlah engkau menunda-nunda memenuhinya, sebab tentulah TUHAN, Allahmu, akan menuntutnya dari padamu, sehingga hal itu menjadi dosa bagimu.'),(5522,4,23,22,'Tetapi apabila engkau tidak bernazar, maka hal itu bukan menjadi dosa bagimu.'),(5523,4,23,23,'Apa yang keluar dari bibirmu haruslah kaulakukan dengan setia, sebab dengan sukarela kaunazarkan kepada TUHAN, Allahmu, sesuatu yang kaukatakan dengan mulutmu sendiri.\"'),(5524,4,23,24,'Apabila engkau melalui kebun anggur sesamamu, engkau boleh makan buah anggur sepuas-puas hatimu, tetapi tidak boleh kaumasukkan ke dalam bungkusanmu.'),(5525,4,23,25,'Apabila engkau melalui ladang gandum sesamamu yang belum dituai, engkau boleh memetik bulir-bulirnya dengan tanganmu, tetapi sabit tidak boleh kauayunkan kepada gandum sesamamu itu.\"'),(5526,4,24,1,'Apabila seseorang mengambil seorang perempuan dan menjadi suaminya, dan jika kemudian ia tidak menyukai lagi perempuan itu, sebab didapatinya yang tidak senonoh padanya, lalu ia menulis surat cerai dan menyerahkannya ke tangan perempuan itu, sesudah itu menyuruh dia pergi dari rumahnya,'),(5527,4,24,2,'dan jika perempuan itu keluar dari rumahnya dan pergi dari sana, lalu menjadi isteri orang lain,'),(5528,4,24,3,'dan jika laki-laki yang kemudian ini tidak cinta lagi kepadanya, lalu menulis surat cerai dan menyerahkannya ke tangan perempuan itu serta menyuruh dia pergi dari rumahnya, atau jika laki-laki yang kemudian mengambil dia menjadi isterinya itu mati,'),(5529,4,24,4,'maka suaminya yang pertama, yang telah menyuruh dia pergi itu, tidak boleh mengambil dia kembali menjadi isterinya, setelah perempuan itu dicemari; sebab hal itu adalah kekejian di hadapan TUHAN. Janganlah engkau mendatangkan dosa atas negeri yang diberikan TUHAN, Allahmu, kepadamu menjadi milik pusakamu.'),(5530,4,24,5,'Apabila baru saja seseorang mengambil isteri, janganlah ia keluar bersama-sama dengan tentara maju berperang atau dibebankan sesuatu pekerjaan; satu tahun lamanya ia harus dibebaskan untuk keperluan rumah tangganya dan menyukakan hati perempuan yang telah diambilnya menjadi isterinya.\"'),(5531,4,24,6,'Janganlah mengambil kilangan atau batu kilangan atas sebagai gadai, karena yang demikian itu mengambil nyawa orang sebagai gadai.'),(5532,4,24,7,'Apabila seseorang kedapatan sedang menculik orang, salah seorang saudaranya, dari antara orang Israel, lalu memperlakukan dia sebagai budak dan menjual dia, maka haruslah penculik itu mati. Demikianlah harus kauhapuskan yang jahat itu dari tengah-tengahmu.'),(5533,4,24,8,'Hati-hatilah dalam hal penyakit kusta dan lakukanlah dengan tepat segala yang diajarkan imam-imam orang Lewi kepadamu; apa yang kuperintahkan kepada mereka haruslah kamu lakukan dengan setia.'),(5534,4,24,9,'Ingatlah apa yang dilakukan TUHAN, Allahmu, kepada Miryam pada waktu perjalananmu keluar dari Mesir.'),(5535,4,24,10,'Apabila engkau meminjamkan sesuatu kepada sesamamu, janganlah engkau masuk ke rumahnya untuk mengambil gadai dari padanya.'),(5536,4,24,11,'Haruslah engkau tinggal berdiri di luar, dan orang yang kauberi pinjaman itu haruslah membawa gadai itu ke luar kepadamu.'),(5537,4,24,12,'Jika ia seorang miskin, janganlah engkau tidur dengan barang gadaiannya;'),(5538,4,24,13,'kembalikanlah gadaian itu kepadanya pada waktu matahari terbenam, supaya ia dapat tidur dengan memakai kainnya sendiri dan memberkati engkau. Maka engkau akan menjadi benar di hadapan TUHAN, Allahmu.'),(5539,4,24,14,'Janganlah engkau memeras pekerja harian yang miskin dan menderita, baik ia saudaramu maupun seorang asing yang ada di negerimu, di dalam tempatmu.'),(5540,4,24,15,'Pada hari itu juga haruslah engkau membayar upahnya sebelum matahari terbenam; ia mengharapkannya, karena ia orang miskin; supaya ia jangan berseru kepada TUHAN mengenai engkau dan hal itu menjadi dosa bagimu.'),(5541,4,24,16,'Janganlah ayah dihukum mati karena anaknya, janganlah juga anak dihukum mati karena ayahnya; setiap orang harus dihukum mati karena dosanya sendiri.'),(5542,4,24,17,'Janganlah engkau memperkosa hak orang asing dan anak yatim; juga janganlah engkau mengambil pakaian seorang janda menjadi gadai.'),(5543,4,24,18,'Haruslah kauingat, bahwa engkaupun dahulu budak di Mesir dan engkau ditebus TUHAN, Allahmu, dari sana; itulah sebabnya aku memerintahkan engkau melakukan hal ini.'),(5544,4,24,19,'Apabila engkau menuai di ladangmu, lalu terlupa seberkas di ladang, maka janganlah engkau kembali untuk mengambilnya; itulah bagian orang asing, anak yatim dan janda--supaya TUHAN, Allahmu, memberkati engkau dalam segala pekerjaanmu.'),(5545,4,24,20,'Apabila engkau memetik hasil pohon zaitunmu dengan memukul-mukulnya, janganlah engkau memeriksa dahan-dahannya sekali lagi; itulah bagian orang asing, anak yatim dan janda.'),(5546,4,24,21,'Apabila engkau mengumpulkan hasil kebun anggurmu, janganlah engkau mengadakan pemetikan sekali lagi; itulah bagian orang asing, anak yatim dan janda.'),(5547,4,24,22,'Haruslah kauingat, bahwa engkaupun dahulu budak di tanah Mesir; itulah sebabnya aku memerintahkan engkau melakukan hal ini.\"'),(5548,4,25,1,'Apabila ada perselisihan di antara beberapa orang, lalu mereka pergi ke pengadilan, dan mereka diadili dengan dinyatakannya siapa yang benar dan siapa yang salah,'),(5549,4,25,2,'maka jika orang yang bersalah itu layak dipukul, haruslah hakim menyuruh dia meniarap dan menyuruh orang memukuli dia di depannya dengan sejumlah dera setimpal dengan kesalahannya.'),(5550,4,25,3,'Empat puluh kali harus orang itu dipukuli, jangan lebih; supaya jangan saudaramu menjadi rendah di matamu, apabila ia dipukul lebih banyak lagi.'),(5551,4,25,4,'Janganlah engkau memberangus mulut lembu yang sedang mengirik.\"'),(5552,4,25,5,'Apabila orang-orang yang bersaudara tinggal bersama-sama dan seorang dari pada mereka mati dengan tidak meninggalkan anak laki-laki, maka janganlah isteri orang yang mati itu kawin dengan orang di luar lingkungan keluarganya; saudara suaminya haruslah menghampiri dia dan mengambil dia menjadi isterinya dan dengan demikian melakukan kewajiban perkawinan ipar.'),(5553,4,25,6,'Maka anak sulung yang nanti dilahirkan perempuan itu haruslah dianggap sebagai anak saudara yang sudah mati itu, supaya nama itu jangan terhapus dari antara orang Israel.'),(5554,4,25,7,'Tetapi jika orang itu tidak suka mengambil isteri saudaranya, maka haruslah isteri saudaranya itu pergi ke pintu gerbang menghadap para tua-tua serta berkata: Iparku menolak menegakkan nama saudaranya di antara orang Israel, ia tidak mau melakukan kewajiban perkawinan ipar dengan aku.'),(5555,4,25,8,'Kemudian para tua-tua kotanya haruslah memanggil orang itu dan berbicara dengan dia. Jika ia tetap berpendirian dengan mengatakan: Aku tidak suka mengambil dia sebagai isteri--'),(5556,4,25,9,'maka haruslah isteri saudaranya itu datang kepadanya di hadapan para tua-tua, menanggalkan kasut orang itu dari kakinya, meludahi mukanya sambil menyatakan: Beginilah harus dilakukan kepada orang yang tidak mau membangun keturunan saudaranya.'),(5557,4,25,10,'Dan di antara orang Israel namanya haruslah disebut: Kaum yang kasutnya ditanggalkan orang.\"'),(5558,4,25,11,'Apabila dua orang berkelahi dan isteri yang seorang datang mendekat untuk menolong suaminya dari tangan orang yang memukulnya, dan perempuan itu mengulurkan tangannya dan menangkap kemaluan orang itu,'),(5559,4,25,12,'maka haruslah kaupotong tangan perempuan itu; janganlah engkau merasa sayang kepadanya.\"'),(5560,4,25,13,'Janganlah ada di dalam pundi-pundimu dua macam batu timbangan, yang besar dan yang kecil.'),(5561,4,25,14,'Janganlah ada di dalam rumahmu dua macam efa, yang besar dan yang kecil.'),(5562,4,25,15,'Haruslah ada padamu batu timbangan yang utuh dan tepat; haruslah ada padamu efa yang utuh dan tepat--supaya lanjut umurmu di tanah yang diberikan kepadamu oleh TUHAN, Allahmu.'),(5563,4,25,16,'Sebab setiap orang yang melakukan hal yang demikian, setiap orang yang berbuat curang, adalah kekejian bagi TUHAN, Allahmu.\"'),(5564,4,25,17,'Ingatlah apa yang dilakukan orang Amalek kepadamu pada waktu perjalananmu keluar dari Mesir;'),(5565,4,25,18,'bahwa engkau didatangi mereka di jalan dan semua orang lemah pada barisan belakangmu dihantam mereka, sedang engkau lelah dan lesu. Mereka tidak takut akan Allah.'),(5566,4,25,19,'Maka apabila TUHAN, Allahmu, sudah mengaruniakan keamanan kepadamu dari pada segala musuhmu di sekeliling, di negeri yang diberikan TUHAN, Allahmu, kepadamu untuk dimiliki sebagai milik pusaka, maka haruslah engkau menghapuskan ingatan kepada Amalek dari kolong langit. Janganlah lupa!\"'),(5567,4,26,1,'Apabila engkau telah masuk ke negeri yang diberikan TUHAN, Allahmu, kepadamu menjadi milik pusakamu, dan engkau telah mendudukinya dan diam di sana,'),(5568,4,26,2,'maka haruslah engkau membawa hasil pertama dari bumi yang telah kaukumpulkan dari tanahmu yang diberikan kepadamu oleh TUHAN, Allahmu, dan haruslah engkau menaruhnya dalam bakul, kemudian pergi ke tempat yang akan dipilih TUHAN, Allahmu, untuk membuat nama-Nya diam di sana.'),(5569,4,26,3,'Dan sesampainya kepada imam yang ada pada waktu itu, haruslah engkau berkata kepadanya: Aku memberitahukan pada hari ini kepada TUHAN, Allahmu, bahwa aku telah masuk ke negeri yang dijanjikan TUHAN dengan sumpah kepada nenek moyang kita untuk memberikannya kepada kita.'),(5570,4,26,4,'Maka imam harus menerima bakul itu dari tanganmu dan meletakkannya di depan mezbah TUHAN, Allahmu.'),(5571,4,26,5,'Kemudian engkau harus menyatakan di hadapan TUHAN, Allahmu, demikian: Bapaku dahulu seorang Aram, seorang pengembara. Ia pergi ke Mesir dengan sedikit orang saja dan tinggal di sana sebagai orang asing, tetapi di sana ia menjadi suatu bangsa yang besar, kuat dan banyak jumlahnya.'),(5572,4,26,6,'Ketika orang Mesir menganiaya dan menindas kami dan menyuruh kami melakukan pekerjaan yang berat,'),(5573,4,26,7,'maka kami berseru kepada TUHAN, Allah nenek moyang kami, lalu TUHAN mendengar suara kami dan melihat kesengsaraan dan kesukaran kami dan penindasan terhadap kami.'),(5574,4,26,8,'Lalu TUHAN membawa kami keluar dari Mesir dengan tangan yang kuat dan lengan yang teracung, dengan kedahsyatan yang besar dan dengan tanda-tanda serta mujizat-mujizat.'),(5575,4,26,9,'Ia membawa kami ke tempat ini, dan memberikan kepada kami negeri ini, suatu negeri yang berlimpah-limpah susu dan madunya.'),(5576,4,26,10,'Oleh sebab itu, di sini aku membawa hasil pertama dari bumi yang telah Kauberikan kepadaku, ya TUHAN. Kemudian engkau harus meletakkannya di hadapan TUHAN, Allahmu; engkau harus sujud di hadapan TUHAN, Allahmu,'),(5577,4,26,11,'dan haruslah engkau, orang Lewi dan orang asing yang ada di tengah-tengahmu bersukaria karena segala yang baik yang diberikan TUHAN, Allahmu, kepadamu dan kepada seisi rumahmu.\"'),(5578,4,26,12,'Apabila dalam tahun yang ketiga, tahun persembahan persepuluhan, engkau sudah selesai mengambil segala persembahan persepuluhan dari hasil tanahmu, maka haruslah engkau memberikannya kepada orang Lewi, orang asing, anak yatim dan kepada janda, supaya mereka dapat makan di dalam tempatmu dan menjadi kenyang.'),(5579,4,26,13,'Dan haruslah engkau berkata di hadapan TUHAN, Allahmu: Telah kupindahkan persembahan kudus itu dari rumahku, juga telah kuberikan kepada orang Lewi, dan kepada orang asing, anak yatim dan kepada janda, tepat seperti perintah yang telah Kauberikan kepadaku. Tidak kulangkahi atau kulupakan sesuatu dari perintah-Mu itu.'),(5580,4,26,14,'Pada waktu aku berkabung sesuatu tidak kumakan dari persembahan kudus itu, pada waktu aku najis sesuatu tidak kujauhkan dari padanya, juga sesuatu tidak kupersembahkan dari padanya kepada orang mati, tetapi aku mendengarkan suara TUHAN, Allahku, aku berbuat sesuai dengan segala yang Kauperintahkan kepadaku.'),(5581,4,26,15,'Jenguklah dari tempat kediaman-Mu yang kudus, dari dalam sorga, dan berkatilah umat-Mu Israel, dan tanah yang telah Kauberikan kepada kami, seperti yang telah Kaujanjikan dengan sumpah kepada nenek moyang kami--suatu negeri yang berlimpah-limpah susu dan madunya.\"'),(5582,4,26,16,'Pada hari ini TUHAN, Allahmu, memerintahkan engkau melakukan ketetapan dan peraturan ini; lakukanlah semuanya itu dengan setia, dengan segenap hatimu dan segenap jiwamu.'),(5583,4,26,17,'Engkau telah menerima janji dari pada TUHAN pada hari ini, bahwa Ia akan menjadi Allahmu, dan engkaupun akan hidup menurut jalan yang ditunjukkan-Nya dan berpegang pada ketetapan, perintah serta peraturan-Nya, dan mendengarkan suara-Nya.'),(5584,4,26,18,'Dan TUHAN telah menerima janji dari padamu pada hari ini, bahwa engkau akan menjadi umat kesayangan-Nya, seperti yang dijanjikan-Nya kepadamu, dan bahwa engkau akan berpegang pada segala perintah-Nya,'),(5585,4,26,19,'dan Iapun akan mengangkat engkau di atas segala bangsa yang telah dijadikan-Nya, untuk menjadi terpuji, ternama dan terhormat. Maka engkau akan menjadi umat yang kudus bagi TUHAN, Allahmu, seperti yang dijanjikan-Nya.\"'),(5586,4,27,1,'Lagi Musa dan para tua-tua Israel memerintahkan kepada bangsa itu: \"Berpeganglah pada segenap perintah yang kusampaikan kepadamu pada hari ini.'),(5587,4,27,2,'Dan pada hari kamu menyeberangi sungai Yordan ke negeri yang diberikan kepadamu oleh TUHAN, Allahmu, maka haruslah engkau menegakkan batu-batu besar, dan mengapurnya,'),(5588,4,27,3,'lalu pada batu itu haruslah kautuliskan segala perkataan hukum Taurat ini, sesudah engkau menyeberang, supaya engkau masuk ke negeri yang diberikan kepadamu oleh TUHAN, Allahmu, suatu negeri yang berlimpah-limpah susu dan madunya, seperti yang dijanjikan kepadamu oleh TUHAN, Allah nenek moyangmu.'),(5589,4,27,4,'Dan sesudah kamu menyeberangi sungai Yordan, maka haruslah batu-batu itu, yang telah kuperintahkan kepadamu pada hari ini, kamu tegakkan di gunung Ebal dan kaukapuri.'),(5590,4,27,5,'Juga haruslah kaudirikan di sana mezbah bagi TUHAN, Allahmu, suatu mezbah dari batu yang tidak boleh kauolah dengan perkakas besi.'),(5591,4,27,6,'Dari batu yang tidak dipahat haruslah kaudirikan mezbah TUHAN, Allahmu, itu dan di atasnya haruslah kaupersembahkan korban bakaran kepada TUHAN, Allahmu.'),(5592,4,27,7,'Juga haruslah engkau mempersembahkan korban keselamatan, memakannya di sana dan bersukaria di hadapan TUHAN, Allahmu.'),(5593,4,27,8,'Selanjutnya haruslah engkau menuliskan pada batu-batu itu segala perkataan hukum Taurat ini dengan jelas dan terang.\"'),(5594,4,27,9,'Juga berbicaralah Musa dan imam-imam orang Lewi kepada seluruh orang Israel: \"Diamlah dan dengarlah, hai orang Israel. Pada hari ini engkau telah menjadi umat TUHAN, Allahmu.'),(5595,4,27,10,'Sebab itu engkau harus mendengarkan suara TUHAN, Allahmu, dan melakukan perintah dan ketetapan-Nya, yang kusampaikan kepadamu pada hari ini.\"'),(5596,4,27,11,'Pada hari itu Musa memberi perintah kepada bangsa itu:'),(5597,4,27,12,'Sesudah kamu menyeberangi sungai Yordan, maka mereka inilah yang harus berdiri di gunung Gerizim untuk memberkati bangsa itu, yakni suku Simeon, Lewi, Yehuda, Isakhar, Yusuf dan Benyamin.'),(5598,4,27,13,'Dan mereka inilah yang harus berdiri di gunung Ebal untuk mengutuki, yakni suku Ruben, Gad, Asyer, Zebulon, Dan serta Naftali.'),(5599,4,27,14,'Maka haruslah orang-orang Lewi mulai bicara dan mengatakan kepada seluruh orang Israel dengan suara nyaring:'),(5600,4,27,15,'Terkutuklah orang yang membuat patung pahatan atau patung tuangan, suatu kekejian bagi TUHAN, buatan tangan seorang tukang, dan yang mendirikannya dengan tersembunyi. Dan seluruh bangsa itu haruslah menjawab: Amin!'),(5601,4,27,16,'Terkutuklah orang yang memandang rendah ibu dan bapanya. Dan seluruh bangsa itu haruslah berkata: Amin!'),(5602,4,27,17,'Terkutuklah orang yang menggeser batas tanah sesamanya manusia. Dan seluruh bangsa itu haruslah berkata: Amin!'),(5603,4,27,18,'Terkutuklah orang yang membawa seorang buta ke jalan yang sesat. Dan seluruh bangsa itu haruslah berkata: Amin!'),(5604,4,27,19,'Terkutuklah orang yang memperkosa hak orang asing, anak yatim dan janda. Dan seluruh bangsa itu haruslah berkata: Amin!'),(5605,4,27,20,'Terkutuklah orang yang tidur dengan isteri ayahnya, sebab ia telah menyingkapkan punca kain ayahnya. Dan seluruh bangsa itu haruslah berkata: Amin!'),(5606,4,27,21,'Terkutuklah orang yang tidur dengan binatang apapun. Dan seluruh bangsa itu haruslah berkata: Amin!'),(5607,4,27,22,'Terkutuklah orang yang tidur dengan saudaranya perempuan, anak ayah atau anak ibunya. Dan seluruh bangsa itu haruslah berkata: Amin!'),(5608,4,27,23,'Terkutuklah orang yang tidur dengan mertuanya perempuan. Dan seluruh bangsa itu haruslah berkata: Amin!'),(5609,4,27,24,'Terkutuklah orang yang membunuh sesamanya manusia dengan tersembunyi. Dan seluruh bangsa itu haruslah berkata: Amin!'),(5610,4,27,25,'Terkutuklah orang yang menerima suap untuk membunuh seseorang yang tidak bersalah. Dan seluruh bangsa itu harus berkata: Amin!'),(5611,4,27,26,'Terkutuklah orang yang tidak menepati perkataan hukum Taurat ini dengan perbuatan. Dan seluruh bangsa itu haruslah berkata: Amin!\"'),(5612,4,28,1,'Jika engkau baik-baik mendengarkan suara TUHAN, Allahmu, dan melakukan dengan setia segala perintah-Nya yang kusampaikan kepadamu pada hari ini, maka TUHAN, Allahmu, akan mengangkat engkau di atas segala bangsa di bumi.'),(5613,4,28,2,'Segala berkat ini akan datang kepadamu dan menjadi bagianmu, jika engkau mendengarkan suara TUHAN, Allahmu:'),(5614,4,28,3,'Diberkatilah engkau di kota dan diberkatilah engkau di ladang.'),(5615,4,28,4,'Diberkatilah buah kandunganmu, hasil bumimu dan hasil ternakmu, yakni anak lembu sapimu dan kandungan kambing dombamu.'),(5616,4,28,5,'Diberkatilah bakulmu dan tempat adonanmu.'),(5617,4,28,6,'Diberkatilah engkau pada waktu masuk dan diberkatilah engkau pada waktu keluar.'),(5618,4,28,7,'TUHAN akan membiarkan musuhmu yang maju berperang melawan engkau, terpukul kalah olehmu. Bersatu jalan mereka akan menyerangi engkau, tetapi bertujuh jalan mereka akan lari dari depanmu.'),(5619,4,28,8,'TUHAN akan memerintahkan berkat ke atasmu di dalam lumbungmu dan di dalam segala usahamu; Ia akan memberkati engkau di negeri yang diberikan kepadamu oleh TUHAN, Allahmu.'),(5620,4,28,9,'TUHAN akan menetapkan engkau sebagai umat-Nya yang kudus, seperti yang dijanjikan-Nya dengan sumpah kepadamu, jika engkau berpegang pada perintah TUHAN, Allahmu, dan hidup menurut jalan yang ditunjukkan-Nya.'),(5621,4,28,10,'Maka segala bangsa di bumi akan melihat, bahwa nama TUHAN telah disebut atasmu, dan mereka akan takut kepadamu.'),(5622,4,28,11,'Juga TUHAN akan melimpahi engkau dengan kebaikan dalam buah kandunganmu, dalam hasil ternakmu dan dalam hasil bumimu--di tanah yang dijanjikan TUHAN dengan sumpah kepada nenek moyangmu untuk memberikannya kepadamu.'),(5623,4,28,12,'TUHAN akan membuka bagimu perbendaharaan-Nya yang melimpah, yakni langit, untuk memberi hujan bagi tanahmu pada masanya dan memberkati segala pekerjaanmu, sehingga engkau memberi pinjaman kepada banyak bangsa, tetapi engkau sendiri tidak meminta pinjaman.'),(5624,4,28,13,'TUHAN akan mengangkat engkau menjadi kepala dan bukan menjadi ekor, engkau akan tetap naik dan bukan turun, apabila engkau mendengarkan perintah TUHAN, Allahmu, yang kusampaikan pada hari ini kaulakukan dengan setia,'),(5625,4,28,14,'dan apabila engkau tidak menyimpang ke kanan atau ke kiri dari segala perintah yang kuberikan kepadamu pada hari ini, dengan mengikuti allah lain dan beribadah kepadanya.\"'),(5626,4,28,15,'Tetapi jika engkau tidak mendengarkan suara TUHAN, Allahmu, dan tidak melakukan dengan setia segala perintah dan ketetapan-Nya, yang kusampaikan kepadamu pada hari ini, maka segala kutuk ini akan datang kepadamu dan mencapai engkau:'),(5627,4,28,16,'Terkutuklah engkau di kota dan terkutuklah engkau di ladang.'),(5628,4,28,17,'Terkutuklah bakulmu dan tempat adonanmu.'),(5629,4,28,18,'Terkutuklah buah kandunganmu, hasil bumimu, anak lembu sapimu dan kandungan kambing dombamu.'),(5630,4,28,19,'Terkutuklah engkau pada waktu masuk dan terkutuklah engkau pada waktu keluar.'),(5631,4,28,20,'TUHAN akan mendatangkan kutuk, huru-hara dan penghajaran ke antaramu dalam segala usaha yang kaukerjakan, sampai engkau punah dan binasa dengan segera karena jahat perbuatanmu, sebab engkau telah meninggalkan Aku.'),(5632,4,28,21,'TUHAN akan melekatkan penyakit sampar kepadamu, sampai dihabiskannya engkau dari tanah, ke mana engkau pergi untuk mendudukinya.'),(5633,4,28,22,'TUHAN akan menghajar engkau dengan batuk kering, demam, demam kepialu, sakit radang, kekeringan, hama dan penyakit gandum; semuanya itu akan memburu engkau sampai engkau binasa.'),(5634,4,28,23,'Juga langit yang di atas kepalamu akan menjadi tembaga dan tanah yang di bawahpun menjadi besi.'),(5635,4,28,24,'TUHAN akan menurunkan hujan abu dan debu ke atas negerimu; dari langit akan turun semuanya itu ke atasmu, sampai engkau punah.'),(5636,4,28,25,'TUHAN akan membiarkan engkau terpukul kalah oleh musuhmu. Bersatu jalan engkau akan keluar menyerang mereka, tetapi bertujuh jalan engkau akan lari dari depan mereka, sehingga engkau menjadi kengerian bagi segala kerajaan di bumi.'),(5637,4,28,26,'Mayatmu akan menjadi makanan segala burung di udara serta binatang-binatang di bumi, dengan tidak ada yang mengganggunya.'),(5638,4,28,27,'TUHAN akan menghajar engkau dengan barah Mesir, dengan borok, dengan kedal dan kudis, yang dari padanya engkau tidak dapat sembuh.'),(5639,4,28,28,'TUHAN akan menghajar engkau dengan kegilaan, kebutaan dan kehilangan akal,'),(5640,4,28,29,'sehingga engkau meraba-raba pada waktu tengah hari, seperti seorang buta meraba-raba di dalam gelap; perjalananmu tidak akan beruntung, tetapi engkau selalu diperas dan dirampasi, dengan tidak ada seorang yang datang menolong.'),(5641,4,28,30,'Engkau akan bertunangan dengan seorang perempuan, tetapi orang lain akan menidurinya. Engkau akan mendirikan rumah, tetapi tidak akan mendiaminya. Engkau akan membuat kebun anggur, tetapi tidak akan mengecap hasilnya.'),(5642,4,28,31,'Lembumu akan disembelih orang di depan matamu, tetapi engkau tidak akan memakan dagingnya. Keledaimu akan dirampas dari depanmu, dan tidak akan dikembalikan kepadamu. Kambing dombamu akan diberikan kepada musuhmu dengan tidak ada orang yang datang menolong engkau.'),(5643,4,28,32,'Anak-anakmu lelaki dan anak-anakmu perempuan akan diserahkan kepada bangsa lain, sedang engkau melihatnya dengan matamu sendiri, dan sehari-harian engkau rindu kepada mereka, dengan tidak dapat berbuat apa-apa.'),(5644,4,28,33,'Suatu bangsa yang tidak kaukenal akan memakan hasil bumimu dan segala hasil jerih payahmu; engkau akan selalu ditindas dan diinjak.'),(5645,4,28,34,'Engkau akan menjadi gila karena apa yang dilihat matamu.'),(5646,4,28,35,'TUHAN akan menghajar engkau dengan barah jahat, yang dari padanya engkau tidak dapat sembuh, pada lutut dan pahamu, bahkan dari telapak kakimu sampai kepada batu kepalamu.'),(5647,4,28,36,'TUHAN akan membawa engkau dengan raja yang kauangkat atasmu itu kepada suatu bangsa yang tidak dikenal olehmu ataupun oleh nenek moyangmu; di sanalah engkau akan beribadah kepada allah lain, kepada kayu dan batu.'),(5648,4,28,37,'Engkau akan menjadi kedahsyatan, kiasan dan sindiran di antara segala bangsa, ke mana TUHAN akan menyingkirkan engkau.'),(5649,4,28,38,'Banyak benih yang akan kaubawa ke ladang, tetapi sedikit hasil yang akan kaukumpulkan, sebab belalang akan menghabiskannya.'),(5650,4,28,39,'Kebun-kebun anggur akan kaubuat dan kauusahakan, tetapi engkau tidak akan meminum atau menyimpan anggur, sebab ulat akan memakannya.'),(5651,4,28,40,'Pohon-pohon zaitun akan kaupunyai di seluruh daerahmu, tetapi engkau tidak akan berurap dengan minyaknya; sebab buah zaitunmu akan gugur.'),(5652,4,28,41,'Engkau akan mendapat anak-anak lelaki dan anak-anak perempuan, tetapi mereka bukan bagi dirimu, sebab mereka akan menjadi tawanan.'),(5653,4,28,42,'Segala pohon-pohonmu dan hasil bumimu akan diduduki oleh kawanan belalang.'),(5654,4,28,43,'Orang asing yang ada di tengah-tengahmu akan menjadi makin tinggi mengatasi engkau, tetapi engkau menjadi makin rendah.'),(5655,4,28,44,'Ia akan memberi pinjaman kepadamu, tetapi engkau tidak akan memberi pinjaman kepadanya; ia akan menjadi kepala, tetapi engkau akan menjadi ekor.'),(5656,4,28,45,'Segala kutuk itu akan datang ke atasmu, memburu engkau dan mencapai engkau, sampai engkau punah, karena engkau tidak mendengarkan suara TUHAN, Allahmu dan tidak berpegang pada perintah dan ketetapan yang diperintahkan-Nya kepadamu;'),(5657,4,28,46,'semuanya itu akan menjadi tanda dan mujizat di antaramu dan di antara keturunanmu untuk selamanya.\"'),(5658,4,28,47,'Karena engkau tidak mau menjadi hamba kepada TUHAN, Allahmu, dengan sukacita dan gembira hati walaupun kelimpahan akan segala-galanya,'),(5659,4,28,48,'maka dengan menanggung lapar dan haus, dengan telanjang dan kekurangan akan segala-galanya engkau akan menjadi hamba kepada musuh yang akan disuruh TUHAN melawan engkau. Ia akan membebankan kuk besi ke atas tengkukmu, sampai engkau dipunahkan-Nya.'),(5660,4,28,49,'TUHAN akan mendatangkan kepadamu suatu bangsa dari jauh, dari ujung bumi, seperti rajawali yang datang menyambar; suatu bangsa yang bahasanya engkau tidak mengerti,'),(5661,4,28,50,'suatu bangsa yang garang mukanya, yang tidak menghiraukan orang tua-tua dan tidak merasa kasihan kepada anak-anak;'),(5662,4,28,51,'yang akan memakan habis hasil ternakmu dan hasil bumimu, sampai engkau punah; yang tidak akan meninggalkan bagimu gandum, air anggur atau minyak, ataupun anak lembu sapimu atau anak kambing dombamu, sampai engkau dibinasakannya.'),(5663,4,28,52,'Engkau akan ditekannya di segala tempatmu, sampai runtuh tembok-tembokmu yang tinggi dan berkubu, yang kaupercayai itu di seluruh negerimu, bahkan engkau akan ditekan di dalam segala tempatmu, di seluruh negeri yang telah diberikan kepadamu oleh TUHAN, Allahmu.'),(5664,4,28,53,'Dan engkau akan memakan buah kandunganmu, yakni daging anak-anakmu lelaki dan anak-anakmu perempuan yang diberikan kepadamu oleh TUHAN, Allahmu, --dalam keadaan susah dan sulit yang ditimbulkan musuhmu kepadamu.'),(5665,4,28,54,'Dan orang laki-laki yang paling lemah dan paling manja di antaramu akan kesal terhadap saudaranya atau terhadap isterinya sendiri atau terhadap anak-anaknya yang masih tinggal padanya,'),(5666,4,28,55,'sehingga kepada salah seorang dari mereka itu ia tidak mau memberikan sedikitpun dari daging anak-anaknya yang dimakannya, karena tidak ada lagi sesuatu yang ditinggalkan baginya, dalam keadaan susah dan sulit yang ditimbulkan musuhmu kepadamu di segala tempatmu.'),(5667,4,28,56,'Perempuan yang lemah dan manja di antaramu, yang tidak pernah mencoba menjejakkan telapak kakinya ke tanah karena sifatnya yang manja dan lemah itu, akan kesal terhadap suaminya sendiri atau terhadap anaknya laki-laki atau anaknya perempuan,'),(5668,4,28,57,'karena uri yang keluar dari kandungannya ataupun karena anak-anak yang dilahirkannya; sebab karena kekurangan segala-galanya ia akan memakannya dengan sembunyi-sembunyi, dalam keadaan susah dan sulit yang ditimbulkan musuhmu kepadamu di dalam tempatmu.'),(5669,4,28,58,'Jika engkau tidak melakukan dengan setia segala perkataan hukum Taurat yang tertulis dalam kitab ini, dan engkau tidak takut akan Nama yang mulia dan dahsyat ini, yakni akan TUHAN, Allahmu,'),(5670,4,28,59,'maka TUHAN akan menimpakan pukulan-pukulan yang ajaib kepadamu, dan kepada keturunanmu, yakni pukulan-pukulan yang keras lagi lama dan penyakit-penyakit yang jahat lagi lama.'),(5671,4,28,60,'Ia akan mendatangkan pula segala wabah Mesir yang kautakuti itu kepadamu, sehingga semuanya itu melekat padamu.'),(5672,4,28,61,'Juga berbagai-bagai penyakit dan pukulan, yang tidak tertulis dalam kitab Taurat ini, akan ditimbulkan TUHAN menimpa engkau, sampai engkau punah.'),(5673,4,28,62,'Dari pada kamu hanya sedikit orang yang tertinggal, padahal kamu dahulu seperti bintang-bintang di langit banyaknya--karena engkau tidak mendengarkan suara TUHAN, Allahmu.'),(5674,4,28,63,'Seperti TUHAN bergirang karena kamu untuk berbuat baik kepadamu dan membuat kamu banyak, demikianlah TUHAN akan bergirang karena kamu untuk membinasakan dan memunahkan kamu, dan kamu akan dicabut dari tanah, ke mana engkau pergi untuk mendudukinya.'),(5675,4,28,64,'TUHAN akan menyerakkan engkau ke antara segala bangsa dari ujung bumi ke ujung bumi; di sanalah engkau akan beribadah kepada allah lain yang tidak dikenal olehmu ataupun oleh nenek moyangmu, yakni kepada kayu dan batu.'),(5676,4,28,65,'Engkau tidak akan mendapat ketenteraman di antara bangsa-bangsa itu dan tidak akan ada tempat berjejak bagi telapak kakimu; TUHAN akan memberikan di sana kepadamu hati yang gelisah, mata yang penuh rindu dan jiwa yang merana.'),(5677,4,28,66,'Hidupmu akan terkatung-katung, siang dan malam engkau akan terkejut dan kuatir akan hidupmu.'),(5678,4,28,67,'Pada waktu pagi engkau akan berkata: Ah, kalau malam sekarang! dan pada waktu malam engkau akan berkata: Ah, kalau pagi sekarang! karena kejut memenuhi hatimu, dan karena apa yang dilihat matamu.'),(5679,4,28,68,'TUHAN akan membawa engkau kembali ke Mesir dengan kapal, melalui jalan yang telah Kukatakan kepadamu: Engkau tidak akan melihatnya lagi, dan di sana kamu akan menawarkan diri kepada musuhmu sebagai budak lelaki dan budak perempuan, tetapi tidak ada pembeli.\"'),(5680,4,29,1,'Inilah perkataan perjanjian yang diikat Musa dengan orang Israel di tanah Moab sesuai dengan perintah TUHAN, selain perjanjian yang telah diikat-Nya dengan mereka di gunung Horeb.'),(5681,4,29,2,'Musa memanggil seluruh orang Israel berkumpul, lalu berkata kepada mereka: \"Sudah kamu lihat segala yang dilakukan TUHAN di tanah Mesir di depan matamu terhadap Firaun dan terhadap semua pegawainya dan terhadap seluruh negerinya:'),(5682,4,29,3,'cobaan-cobaan yang besar yang telah dilihat oleh matamu sendiri, tanda-tanda dan mujizat-mujizat yang besar itu.'),(5683,4,29,4,'Tetapi sampai sekarang ini TUHAN tidak memberi kamu akal budi untuk mengerti atau mata untuk melihat atau telinga untuk mendengar.'),(5684,4,29,5,'Empat puluh tahun lamanya Aku memimpin kamu berjalan melalui padang gurun; pakaianmu tidak menjadi rusak di tubuhmu, dan kasutmu tidak menjadi rusak di kakimu.'),(5685,4,29,6,'Roti tidak kamu makan, anggur atau minuman yang memabukkan tidak kamu minum--supaya kamu tahu bahwa Akulah TUHAN, Allahmu.'),(5686,4,29,7,'Ketika kamu sudah sampai ke tempat ini, maka keluarlah Sihon, raja Hesybon, dan Og, raja Basan, mendatangi kita untuk berperang, dan kita memukul mereka kalah,'),(5687,4,29,8,'merebut negeri mereka dan memberikannya kepada orang Ruben, kepada orang Gad, dan kepada suku Manasye yang setengah itu menjadi milik pusaka mereka.'),(5688,4,29,9,'Sebab itu lakukanlah perkataan perjanjian ini dengan setia, supaya kamu beruntung dalam segala yang kamu lakukan.'),(5689,4,29,10,'Kamu sekalian pada hari ini berdiri di hadapan TUHAN, Allahmu: para kepala sukumu, para tua-tuamu dan para pengatur pasukanmu, semua laki-laki Israel,'),(5690,4,29,11,'anak-anakmu, perempuan-perempuanmu dan orang-orang asing dalam perkemahanmu, bahkan tukang-tukang belah kayu dan tukang-tukang timba air di antaramu,'),(5691,4,29,12,'untuk masuk ke dalam perjanjian TUHAN, Allahmu, yakni sumpah janji-Nya, yang diikat TUHAN, Allahmu, dengan engkau pada hari ini,'),(5692,4,29,13,'supaya Ia mengangkat engkau sebagai umat-Nya pada hari ini dan supaya Ia menjadi Allahmu, seperti yang difirmankan-Nya kepadamu dan seperti yang dijanjikan-Nya dengan sumpah kepada nenek moyangmu, yakni kepada Abraham, Ishak dan Yakub.'),(5693,4,29,14,'Bukan hanya dengan kamu saja aku mengikat perjanjian dan sumpah janji ini,'),(5694,4,29,15,'tetapi dengan setiap orang yang ada di sini pada hari ini bersama-sama dengan kita, yang berdiri di hadapan TUHAN, Allah kita, dan juga dengan setiap orang yang tidak ada di sini pada hari ini bersama-sama dengan kita.'),(5695,4,29,16,'Sebab kamu ini tahu, bagaimana kita diam di tanah Mesir dan bagaimana kita berjalan dari tengah-tengah segala bangsa yang negerinya kamu lalui,'),(5696,4,29,17,'dan kamu sudah melihat dewa kejijikan dan berhala mereka, yakni kayu dan batu, emas dan perak itu, yang ada terdapat pada mereka.'),(5697,4,29,18,'Sebab itu janganlah di antaramu ada laki-laki atau perempuan, kaum keluarga atau suku yang hatinya pada hari ini berpaling meninggalkan TUHAN, Allah kita, untuk pergi berbakti kepada allah bangsa-bangsa itu; janganlah di antaramu ada akar yang menghasilkan racun atau ipuh.'),(5698,4,29,19,'Tetapi apabila seseorang pada waktu mendengar perkataan sumpah serapah ini menyangka dirinya tetap diberkati, dengan berkata: Aku akan selamat, walaupun aku berlaku degil--dengan demikian dilenyapkannya baik tanah yang kegenangan maupun yang kekeringan--'),(5699,4,29,20,'maka TUHAN tidak akan mau mengampuni orang itu, tetapi murka dan cemburu TUHAN akan menyala atasnya pada waktu itu; segenap sumpah serapah yang tertulis dalam kitab ini akan menghinggapi dia, dan TUHAN akan menghapuskan namanya dari kolong langit.'),(5700,4,29,21,'TUHAN akan memisahkan orang itu dari segala suku Israel supaya dia mendapat celaka, sesuai dengan segala sumpah serapah perjanjian yang tertulis dalam kitab hukum Taurat ini.'),(5701,4,29,22,'Maka keturunan yang akan datang, yakni anak-anakmu yang bangkit sesudah kamu, dan orang asing yang datang dari negeri jauh akan berkata--apabila mereka melihat hajaran dan penyakit yang dijatuhkan TUHAN ke negeri itu,'),(5702,4,29,23,'seluruh tanahnya yang telah hangus oleh belerang dan garam, yang tidak ditaburi, tidak menumbuhkan apa-apa dan tidak ada tumbuh-tumbuhan apapun yang timbul dari padanya, seperti pada waktu ditunggangbalikkan-Nya Sodom, Gomora, Adma dan Zeboim, yakni yang ditunggangbalikkan TUHAN dalam murka dan kepanasan amarah-Nya--'),(5703,4,29,24,'bahkan segala bangsa akan berkata: Apakah sebabnya TUHAN berbuat demikian kepada negeri ini? Apakah artinya murka yang hebat bernyala-nyala ini?'),(5704,4,29,25,'Maka orang akan menjawab: Sebab mereka itu telah melalaikan perjanjian TUHAN, Allah nenek moyang mereka, yakni perjanjian yang diikat-Nya dengan mereka ketika mereka dibawa-Nya keluar dari tanah Mesir,'),(5705,4,29,26,'dan sebab mereka itu sudah pergi berbakti kepada allah lain dan sujud menyembah kepadanya, yakni allah yang tidak dikenal mereka dan yang tidak diberikan TUHAN kepada mereka sebagai bagiannya.'),(5706,4,29,27,'Itulah sebabnya murka TUHAN bangkit terhadap negeri ini, sehingga didatangkan ke atasnya segala kutuk yang tertulis dalam kitab ini:'),(5707,4,29,28,'TUHAN telah menyentakkan mereka dari tanah mereka dalam murka dan kepanasan amarah dan gusar-Nya yang hebat, lalu melemparkan mereka ke negeri lain, seperti yang terjadi sekarang ini.'),(5708,4,29,29,'Hal-hal yang tersembunyi ialah bagi TUHAN, Allah kita, tetapi hal-hal yang dinyatakan ialah bagi kita dan bagi anak-anak kita sampai selama-lamanya, supaya kita melakukan segala perkataan hukum Taurat ini.\"'),(5709,4,30,1,'Maka apabila segala hal ini berlaku atasmu, yakni berkat dan kutuk yang telah kuperhadapkan kepadamu itu, dan engkau menjadi sadar dalam hatimu di tengah-tengah segala bangsa, ke mana TUHAN, Allahmu, menghalau engkau,'),(5710,4,30,2,'dan apabila engkau berbalik kepada TUHAN, Allahmu, dan mendengarkan suara-Nya sesuai dengan segala yang kuperintahkan kepadamu pada hari ini, baik engkau maupun anak-anakmu, dengan segenap hatimu dan dengan segenap jiwamu,'),(5711,4,30,3,'maka TUHAN, Allahmu, akan memulihkan keadaanmu dan akan menyayangi engkau. Ia akan mengumpulkan engkau kembali dari segala bangsa, ke mana TUHAN, Allahmu, telah menyerakkan engkau.'),(5712,4,30,4,'Sekalipun orang-orang yang terhalau dari padamu ada di ujung langit, dari sanapun TUHAN, Allahmu, akan mengumpulkan engkau kembali dan dari sanapun Ia akan mengambil engkau.'),(5713,4,30,5,'TUHAN, Allahmu, akan membawa engkau masuk ke negeri yang sudah dimiliki nenek moyangmu, dan engkaupun akan memilikinya pula. Ia akan berbuat baik kepadamu dan membuat engkau banyak melebihi nenek moyangmu.'),(5714,4,30,6,'Dan TUHAN, Allahmu, akan menyunat hatimu dan hati keturunanmu, sehingga engkau mengasihi TUHAN, Allahmu, dengan segenap hatimu dan dengan segenap jiwamu, supaya engkau hidup.'),(5715,4,30,7,'TUHAN, Allahmu, akan menjatuhkan segala sumpah serapah itu kepada musuhmu dan pembencimu, yang telah mengejar engkau.'),(5716,4,30,8,'Engkau akan mendengarkan kembali suara TUHAN dan melakukan segala perintah-Nya yang kusampaikan kepadamu pada hari ini.'),(5717,4,30,9,'TUHAN, Allahmu, akan melimpahi engkau dengan kebaikan dalam segala pekerjaanmu, dalam buah kandunganmu, dalam hasil ternakmu dan dalam hasil bumimu, sebab TUHAN, Allahmu, akan bergirang kembali karena engkau dalam keberuntunganmu, seperti Ia bergirang karena nenek moyangmu dahulu--'),(5718,4,30,10,'apabila engkau mendengarkan suara TUHAN, Allahmu, dengan berpegang pada perintah dan ketetapan-Nya, yang tertulis dalam kitab Taurat ini dan apabila engkau berbalik kepada TUHAN, Allahmu, dengan segenap hatimu dan dengan segenap jiwamu.\"'),(5719,4,30,11,'Sebab perintah ini, yang kusampaikan kepadamu pada hari ini, tidaklah terlalu sukar bagimu dan tidak pula terlalu jauh.'),(5720,4,30,12,'Tidak di langit tempatnya, sehingga engkau berkata: Siapakah yang akan naik ke langit untuk mengambilnya bagi kita dan memperdengarkannya kepada kita, supaya kita melakukannya?'),(5721,4,30,13,'Juga tidak di seberang laut tempatnya, sehingga engkau berkata: Siapakah yang akan menyeberang ke seberang laut untuk mengambilnya bagi kita dan memperdengarkannya kepada kita, supaya kita melakukannya?'),(5722,4,30,14,'Tetapi firman ini sangat dekat kepadamu, yakni di dalam mulutmu dan di dalam hatimu, untuk dilakukan.'),(5723,4,30,15,'Ingatlah, aku menghadapkan kepadamu pada hari ini kehidupan dan keberuntungan, kematian dan kecelakaan,'),(5724,4,30,16,'karena pada hari ini aku memerintahkan kepadamu untuk mengasihi TUHAN, Allahmu, dengan hidup menurut jalan yang ditunjukkan-Nya dan berpegang pada perintah, ketetapan dan peraturan-Nya, supaya engkau hidup dan bertambah banyak dan diberkati oleh TUHAN, Allahmu, di negeri ke mana engkau masuk untuk mendudukinya.'),(5725,4,30,17,'Tetapi jika hatimu berpaling dan engkau tidak mau mendengar, bahkan engkau mau disesatkan untuk sujud menyembah kepada allah lain dan beribadah kepadanya,'),(5726,4,30,18,'maka aku memberitahukan kepadamu pada hari ini, bahwa pastilah kamu akan binasa; tidak akan lanjut umurmu di tanah, ke mana engkau pergi, menyeberangi sungai Yordan untuk mendudukinya.'),(5727,4,30,19,'Aku memanggil langit dan bumi menjadi saksi terhadap kamu pada hari ini: kepadamu kuperhadapkan kehidupan dan kematian, berkat dan kutuk. Pilihlah kehidupan, supaya engkau hidup, baik engkau maupun keturunanmu,'),(5728,4,30,20,'dengan mengasihi TUHAN, Allahmu, mendengarkan suara-Nya dan berpaut pada-Nya, sebab hal itu berarti hidupmu dan lanjut umurmu untuk tinggal di tanah yang dijanjikan TUHAN dengan sumpah kepada nenek moyangmu, yakni kepada Abraham, Ishak dan Yakub, untuk memberikannya kepada mereka.\"'),(5729,4,31,1,'Kemudian pergilah Musa, lalu mengatakan segala perkataan ini kepada seluruh orang Israel.'),(5730,4,31,2,'Berkatalah ia kepada mereka: \"Aku sekarang berumur seratus dua puluh tahun; aku tidak dapat giat lagi, dan TUHAN telah berfirman kepadaku: Sungai Yordan ini tidak akan kauseberangi.'),(5731,4,31,3,'TUHAN, Allahmu, Dialah yang akan menyeberang di depanmu; Dialah yang akan memunahkan bangsa-bangsa itu dari hadapanmu, sehingga engkau dapat memiliki negeri mereka; Yosua, dialah yang akan menyeberang di depanmu, seperti yang difirmankan TUHAN.'),(5732,4,31,4,'Dan TUHAN akan melakukan terhadap mereka seperti yang dilakukan-Nya terhadap Sihon dan Og, raja-raja orang Amori, yang telah dipunahkan-Nya itu, dan terhadap negeri mereka.'),(5733,4,31,5,'TUHAN akan menyerahkan mereka kepadamu dan haruslah kamu melakukan kepada mereka tepat seperti perintah yang kusampaikan kepadamu.'),(5734,4,31,6,'Kuatkan dan teguhkanlah hatimu, janganlah takut dan jangan gemetar karena mereka, sebab TUHAN, Allahmu, Dialah yang berjalan menyertai engkau; Ia tidak akan membiarkan engkau dan tidak akan meninggalkan engkau.\"'),(5735,4,31,7,'Lalu Musa memanggil Yosua dan berkata kepadanya di depan seluruh orang Israel: \"Kuatkan dan teguhkanlah hatimu, sebab engkau akan masuk bersama-sama dengan bangsa ini ke negeri yang dijanjikan TUHAN dengan sumpah kepada nenek moyang mereka untuk memberikannya kepada mereka, dan engkau akan memimpin mereka sampai mereka memilikinya.'),(5736,4,31,8,'Sebab TUHAN, Dia sendiri akan berjalan di depanmu, Dia sendiri akan menyertai engkau, Dia tidak akan membiarkan engkau dan tidak akan meninggalkan engkau; janganlah takut dan janganlah patah hati.\"'),(5737,4,31,9,'Setelah hukum Taurat itu dituliskan Musa, maka diberikannyalah kepada imam-imam bani Lewi, yang mengangkut tabut perjanjian TUHAN, dan kepada segala tua-tua Israel.'),(5738,4,31,10,'Dan Musa memerintahkan kepada mereka, demikian: \"Pada akhir tujuh tahun, pada waktu yang telah ditetapkan dalam tahun penghapusan hutang, yakni hari raya Pondok Daun,'),(5739,4,31,11,'apabila seluruh orang Israel datang menghadap hadirat TUHAN, Allahmu, di tempat yang akan dipilih-Nya, maka haruslah engkau membacakan hukum Taurat ini di depan seluruh orang Israel.'),(5740,4,31,12,'Seluruh bangsa itu berkumpul, laki-laki, perempuan dan anak-anak, dan orang asing yang diam di dalam tempatmu, supaya mereka mendengarnya dan belajar takut akan TUHAN, Allahmu, dan mereka melakukan dengan setia segala perkataan hukum Taurat ini,'),(5741,4,31,13,'dan supaya anak-anak mereka, yang tidak mengetahuinya, dapat mendengarnya dan belajar takut akan TUHAN, Allahmu, --selama kamu hidup di tanah, ke mana kamu pergi, menyeberangi sungai Yordan untuk mendudukinya.\"'),(5742,4,31,14,'Kemudian berfirmanlah TUHAN kepada Musa: \"Sesungguhnya sudah dekat waktunya bahwa engkau akan mati; maka panggillah Yosua dan berdirilah bersama-sama dengan dia dalam Kemah Pertemuan, supaya Aku memberi perintah kepadanya.\" Lalu pergilah Musa dan Yosua berdiri dalam Kemah Pertemuan.'),(5743,4,31,15,'Dan TUHAN menampakkan diri di kemah itu dalam tiang awan, dan tiang awan itu berdiri pada pintu kemah.'),(5744,4,31,16,'TUHAN berfirman kepada Musa: \"Ketahuilah, engkau akan mendapat perhentian bersama-sama dengan nenek moyangmu dan bangsa ini akan bangkit dan berzinah dengan mengikuti allah asing yang ada di negeri, ke mana mereka akan masuk; mereka akan meninggalkan Aku dan mengingkari perjanjian-Ku yang Kuikat dengan mereka.'),(5745,4,31,17,'Pada waktu itu murka-Ku akan bernyala-nyala terhadap mereka, Aku akan meninggalkan mereka dan menyembunyikan wajah-Ku terhadap mereka, sehingga mereka termakan habis dan banyak kali ditimpa malapetaka serta kesusahan. Maka pada waktu itu mereka akan berkata: Bukankah malapetaka itu menimpa kita, oleh sebab Allah kita tidak ada di tengah-tengah kita?'),(5746,4,31,18,'Tetapi Aku akan menyembunyikan wajah-Ku sama sekali pada waktu itu, karena segala kejahatan yang telah dilakukan mereka: yakni mereka telah berpaling kepada allah lain.'),(5747,4,31,19,'Oleh sebab itu tuliskanlah nyanyian ini dan ajarkanlah kepada orang Israel, letakkanlah di dalam mulut mereka, supaya nyanyian ini menjadi saksi bagi-Ku terhadap orang Israel.'),(5748,4,31,20,'Sebab Aku akan membawa mereka ke tanah yang Kujanjikan dengan sumpah kepada nenek moyang mereka, yakni tanah yang berlimpah-limpah susu dan madunya; mereka akan makan dan kenyang dan menjadi gemuk, tetapi mereka akan berpaling kepada allah lain dan beribadah kepadanya. Aku ini akan dinista mereka dan perjanjian-Ku akan diingkari mereka.'),(5749,4,31,21,'Maka apabila banyak kali mereka ditimpa malapetaka serta kesusahan, maka nyanyian ini akan menjadi kesaksian terhadap mereka, sebab nyanyian ini akan tetap melekat pada bibir keturunan mereka. Sebab Aku tahu niat yang dikandung mereka pada hari ini, sebelum Aku membawa mereka ke negeri yang Kujanjikan dengan sumpah kepada mereka.\"'),(5750,4,31,22,'Maka Musa menuliskan nyanyian ini dan mengajarkannya kepada orang Israel.'),(5751,4,31,23,'Kepada Yosua bin Nun diberi-Nya perintah, firman-Nya: \"Kuatkan dan teguhkanlah hatimu, sebab engkau akan membawa orang Israel ke negeri yang Kujanjikan dengan sumpah kepada mereka, dan Aku akan menyertai engkau.\"'),(5752,4,31,24,'Ketika Musa selesai menuliskan perkataan hukum Taurat itu dalam sebuah kitab sampai perkataan yang penghabisan,'),(5753,4,31,25,'maka Musa memerintahkan kepada orang-orang Lewi pengangkut tabut perjanjian TUHAN, demikian:'),(5754,4,31,26,'Ambillah kitab Taurat ini dan letakkanlah di samping tabut perjanjian TUHAN, Allahmu, supaya menjadi saksi di situ terhadap engkau.'),(5755,4,31,27,'Sebab aku mengenal kedegilan dan tegar tengkukmu. Sedangkan sekarang, selagi aku hidup bersama-sama dengan kamu, kamu sudah menunjukkan kedegilanmu terhadap TUHAN, terlebih lagi nanti sesudah aku mati.'),(5756,4,31,28,'Suruhlah berkumpul kepadaku segala tua-tua sukumu dan para pengatur pasukanmu, maka aku akan mengatakan hal yang berikut kepada mereka dan memanggil langit dan bumi menjadi saksi terhadap mereka.'),(5757,4,31,29,'Sebab aku tahu, bahwa sesudah aku mati, kamu akan berlaku sangat busuk dan akan menyimpang dari jalan yang telah kuperintahkan kepadamu. Sebab itu di kemudian hari malapetaka akan menimpa kamu, apabila kamu berbuat yang jahat di mata TUHAN, dan menimbulkan sakit hati-Nya dengan perbuatan tanganmu.\"'),(5758,4,31,30,'Lalu Musa menyampaikan ke telinga seluruh jemaah Israel nyanyian ini sampai perkataan yang penghabisan.'),(5759,4,32,1,'Pasanglah telingamu, hai langit, aku mau berbicara, dan baiklah bumi mendengarkan ucapan mulutku.'),(5760,4,32,2,'Mudah-mudahan pengajaranku menitik laksana hujan, perkataanku menetes laksana embun, laksana hujan renai ke atas tunas muda, dan laksana dirus hujan ke atas tumbuh-tumbuhan.'),(5761,4,32,3,'Sebab nama TUHAN akan kuserukan: Berilah hormat kepada Allah kita,'),(5762,4,32,4,'Gunung Batu, yang pekerjaan-Nya sempurna, karena segala jalan-Nya adil, Allah yang setia, dengan tiada kecurangan, adil dan benar Dia.'),(5763,4,32,5,'Berlaku busuk terhadap Dia, mereka yang bukan lagi anak-anak-Nya, yang merupakan noda, suatu angkatan yang bengkok dan belat-belit.'),(5764,4,32,6,'Demikianlah engkau mengadakan pembalasan terhadap TUHAN, hai bangsa yang bebal dan tidak bijaksana? Bukankah Ia Bapamu yang mencipta engkau, yang menjadikan dan menegakkan engkau?'),(5765,4,32,7,'Ingatlah kepada zaman dahulu kala, perhatikanlah tahun-tahun keturunan yang lalu, tanyakanlah kepada ayahmu, maka ia memberitahukannya kepadamu, kepada para tua-tuamu, maka mereka mengatakannya kepadamu.'),(5766,4,32,8,'Ketika Sang Mahatinggi membagi-bagikan milik pusaka kepada bangsa-bangsa, ketika Ia memisah-misah anak-anak manusia, maka Ia menetapkan wilayah bangsa-bangsa menurut bilangan anak-anak Israel.'),(5767,4,32,9,'Tetapi bagian TUHAN ialah umat-Nya, Yakub ialah milik yang ditetapkan bagi-Nya.'),(5768,4,32,10,'Didapati-Nya dia di suatu negeri, di padang gurun, di tengah-tengah ketandusan dan auman padang belantara. Dikelilingi-Nya dia dan diawasi-Nya, dijaga-Nya sebagai biji mata-Nya.'),(5769,4,32,11,'Laksana rajawali menggoyangbangkitkan isi sarangnya, melayang-layang di atas anak-anaknya, mengembangkan sayapnya, menampung seekor, dan mendukungnya di atas kepaknya,'),(5770,4,32,12,'demikianlah TUHAN sendiri menuntun dia, dan tidak ada allah asing menyertai dia.'),(5771,4,32,13,'Dibuat-Nya dia berkendaraan mengatasi bukit-bukit di bumi, dan memakan hasil dari ladang; dibuat-Nya dia mengisap madu dari bukit batu, dan minyak dari gunung batu yang keras,'),(5772,4,32,14,'dadih dari lembu sapi dan susu kambing domba, dengan lemak anak-anak domba; dan domba-domba jantan dari Basan dan kambing-kambing jantan, dengan gandum yang terbaik; juga darah buah anggur yang berbuih engkau minum.'),(5773,4,32,15,'Lalu menjadi gemuklah Yesyurun, dan menendang ke belakang, --bertambah gemuk engkau, gendut dan tambun--dan ia meninggalkan Allah yang telah menjadikan dia, ia memandang rendah gunung batu keselamatannya.'),(5774,4,32,16,'Mereka membangkitkan cemburu-Nya dengan allah asing, mereka menimbulkan sakit hati-Nya dengan dewa kekejian,'),(5775,4,32,17,'mereka mempersembahkan korban kepada roh-roh jahat yang bukan Allah, kepada allah yang tidak mereka kenal, allah baru yang belum lama timbul, yang kepadanya nenek moyangmu tidak gentar.'),(5776,4,32,18,'Gunung batu yang memperanakkan engkau, telah kaulalaikan, dan telah kaulupakan Allah yang melahirkan engkau.'),(5777,4,32,19,'Ketika TUHAN melihat hal itu, maka Ia menolak mereka, karena Ia sakit hati oleh anak-anaknya lelaki dan perempuan.'),(5778,4,32,20,'Ia berfirman: Aku hendak menyembunyikan wajah-Ku terhadap mereka, dan melihat bagaimana kesudahan mereka, sebab mereka itu suatu angkatan yang bengkok, anak-anak yang tidak mempunyai kesetiaan.'),(5779,4,32,21,'Mereka membangkitkan cemburu-Ku dengan yang bukan Allah, mereka menimbulkan sakit hati-Ku dengan berhala mereka. Sebab itu Aku akan membangkitkan cemburu mereka dengan yang bukan umat, dan akan menyakiti hati mereka dengan bangsa yang bebal.'),(5780,4,32,22,'Sebab api telah dinyalakan oleh murka-Ku, dan bernyala-nyala sampai ke bagian dunia orang mati yang paling bawah; api itu memakan bumi dengan hasilnya, dan menghanguskan dasar gunung-gunung.'),(5781,4,32,23,'Aku akan menimbun malapetaka ke atas mereka, seluruh anak panah-Ku akan Kutembakkan kepada mereka.'),(5782,4,32,24,'Apabila mereka sudah lemas karena lapar dan merana oleh demam yang membara, dan oleh penyakit sampar, maka Aku akan melepaskan taring binatang buas kepada mereka, dengan racun binatang yang menjalar di dalam debu.'),(5783,4,32,25,'Pedang di luar rumah dan kengerian di dalam kamar akan melenyapkan teruna maupun dara, anak menyusu serta orang ubanan.'),(5784,4,32,26,'Seharusnya Aku berfirman: Aku meniupkan mereka, melenyapkan ingatan kepada mereka dari antara manusia,'),(5785,4,32,27,'tetapi Aku kuatir disakiti hati-Ku oleh musuh, jangan-jangan lawan mereka salah mengerti, jangan-jangan mereka berkata: Tangan kami jaya, bukanlah TUHAN yang melakukan semuanya ini.'),(5786,4,32,28,'Sebab mereka itu suatu bangsa yang tidak punya pertimbangan, dan tidak ada pengertian pada mereka.'),(5787,4,32,29,'Sekiranya mereka bijaksana, tentulah mereka mengerti hal ini, dan memperhatikan kesudahan mereka.'),(5788,4,32,30,'Bagaimana mungkin satu orang dapat mengejar seribu orang, dan dua orang dapat membuat lari sepuluh ribu orang, kalau tidak gunung batu mereka telah menjual mereka, dan TUHAN telah menyerahkan mereka!'),(5789,4,32,31,'Sebab bukanlah seperti gunung batu kita gunung batu orang-orang itu; musuh kita boleh menjadi hakim!'),(5790,4,32,32,'Sesungguhnya, pohon anggur mereka berasal dari pohon anggur Sodom, dan dari kebun-kebun Gomora; buah anggur mereka adalah buah anggur yang beracun, pahit gugusan-gugusannya.'),(5791,4,32,33,'Air anggur mereka adalah racun ular, dan bisa ular tedung yang keras ganas.'),(5792,4,32,34,'Bukankah hal itu tersimpan pada-Ku, termeterai dalam perbendaharaan-Ku?'),(5793,4,32,35,'Hak-Kulah dendam dan pembalasan, pada waktu kaki mereka goyang, sebab hari bencana bagi mereka telah dekat, akan segera datang apa yang telah disediakan bagi mereka.'),(5794,4,32,36,'Sebab TUHAN akan memberi keadilan kepada umat-Nya, dan akan merasa sayang kepada hamba-hamba-Nya; apabila dilihat-Nya, bahwa kekuatan mereka sudah lenyap, dan baik hamba maupun orang merdeka sudah tiada.'),(5795,4,32,37,'Maka Ia akan berfirman: Di manakah allah mereka, --gunung batu tempat mereka berlindung--'),(5796,4,32,38,'yang memakan lemak dari korban sembelihan mereka, meminum anggur dari korban curahan mereka? Biarlah mereka bangkit untuk menolong kamu, sehingga kamu mendapat perlindungan.'),(5797,4,32,39,'Lihatlah sekarang, bahwa Aku, Akulah Dia. Tidak ada Allah kecuali Aku. Akulah yang mematikan dan yang menghidupkan, Aku telah meremukkan, tetapi Akulah yang menyembuhkan, dan seorangpun tidak ada yang dapat melepaskan dari tangan-Ku.'),(5798,4,32,40,'Sesungguhnya, Aku mengangkat tangan-Ku ke langit, dan berfirman: Demi Aku yang hidup selama-lamanya,'),(5799,4,32,41,'apabila Aku mengasah pedang-Ku yang berkilat-kilat, dan tangan-Ku memegang penghukuman, maka Aku membalas dendam kepada lawan-Ku, dan mengadakan pembalasan kepada yang membenci Aku.'),(5800,4,32,42,'Aku akan memabukkan anak panah-Ku dengan darah, dan pedang-Ku akan memakan daging: darah orang-orang yang mati tertikam dan orang-orang yang tertawan, dari kepala-kepala musuh yang berambut panjang.'),(5801,4,32,43,'Bersorak-sorailah, hai bangsa-bangsa karena umat-Nya, sebab Ia membalaskan darah hamba-hamba-Nya, Ia membalas dendam kepada lawan-Nya, dan mengadakan pendamaian bagi tanah umat-Nya.\"'),(5802,4,32,44,'Lalu datanglah Musa bersama-sama dengan Yosua bin Nun dan menyampaikan ke telinga bangsa itu segala perkataan nyanyian tadi.'),(5803,4,32,45,'Setelah Musa selesai menyampaikan segala perkataan itu kepada seluruh orang Israel,'),(5804,4,32,46,'berkatalah ia kepada mereka: \"Perhatikanlah segala perkataan yang kuperingatkan kepadamu pada hari ini, supaya kamu memerintahkannya kepada anak-anakmu untuk melakukan dengan setia segala perkataan hukum Taurat ini.'),(5805,4,32,47,'Sebab perkataan ini bukanlah perkataan hampa bagimu, tetapi itulah hidupmu, dan dengan perkataan ini akan lanjut umurmu di tanah, ke mana kamu pergi, menyeberangi sungai Yordan untuk mendudukinya.\"'),(5806,4,32,48,'Pada hari itulah juga TUHAN berfirman kepada Musa:'),(5807,4,32,49,'Naiklah ke atas pegunungan Abarim, ke atas gunung Nebo, yang di tanah Moab, di tentangan Yerikho, dan pandanglah tanah Kanaan yang Kuberikan kepada orang Israel menjadi miliknya,'),(5808,4,32,50,'kemudian engkau akan mati di atas gunung yang akan kaunaiki itu, supaya engkau dikumpulkan kepada kaum leluhurmu, sama seperti Harun, kakakmu, sudah meninggal di gunung Hor dan dikumpulkan kepada kaum leluhurnya--'),(5809,4,32,51,'oleh sebab kamu telah berubah setia terhadap Aku di tengah-tengah orang Israel, dekat mata air Meriba di Kadesh di padang gurun Zin, dan oleh sebab kamu tidak menghormati kekudusan-Ku di tengah-tengah orang Israel.'),(5810,4,32,52,'Engkau boleh melihat negeri itu terbentang di depanmu, tetapi tidak boleh masuk ke sana, ke negeri yang Kuberikan kepada orang Israel.\"'),(5811,4,33,1,'Inilah berkat yang diberikan Musa, abdi Allah itu, kepada orang Israel sebelum ia mati.'),(5812,4,33,2,'Berkatalah ia: \"TUHAN datang dari Sinai dan terbit kepada mereka dari Seir; Ia tampak bersinar dari pegunungan Paran dan datang dari tengah-tengah puluhan ribu orang yang kudus; di sebelah kanan-Nya tampak kepada mereka api yang menyala.'),(5813,4,33,3,'Sungguh Ia mengasihi umat-Nya; semua orang-Nya yang kudus--di dalam tangan-Mulah mereka, pada kaki-Mulah mereka duduk, menangkap sesuatu dari firman-Mu.'),(5814,4,33,4,'Musa telah memerintahkan hukum Taurat kepada kita, suatu milik bagi jemaah Yakub.'),(5815,4,33,5,'Ia menjadi raja di Yesyurun, ketika kepala-kepala bangsa datang berkumpul, yakni segala suku Israel bersama-sama.'),(5816,4,33,6,'Biarlah Ruben hidup dan jangan mati, tetapi biarlah orang-orangnya sedikit jumlahnya.\"'),(5817,4,33,7,'Dan inilah tentang Yehuda. Katanya: \"Dengarlah, ya TUHAN, suara Yehuda dan bawalah dia kepada bangsanya. Berjuanglah baginya dengan tangan-Mu, dan jadilah Engkau penolongnya melawan musuhnya.\"'),(5818,4,33,8,'Tentang Lewi ia berkata: \"Biarlah Tumim dan Urim-Mu menjadi kepunyaan orang yang Kaukasihi, yang telah Kaucoba di Masa, dengan siapa Engkau berbantah dekat mata air Meriba;'),(5819,4,33,9,'yang berkata tentang ayahnya dan tentang ibunya: aku tidak mengindahkan mereka; ia yang tidak mau kenal saudara-saudaranya dan acuh tak acuh terhadap anak-anaknya. Sebab orang-orang Lewi itu berpegang pada firman-Mu dan menjaga perjanjian-Mu;'),(5820,4,33,10,'mereka mengajarkan peraturan-peraturan-Mu kepada Yakub, hukum-Mu kepada Israel; mereka menaruh ukupan wangi-wangian di depan-Mu dan korban yang terbakar seluruhnya di atas mezbah-Mu.'),(5821,4,33,11,'Berkatilah, ya TUHAN, kekuatannya dan berkenanlah kepada pekerjaannya. Remukkanlah pinggang orang yang melawan dia dan yang membenci dia, sehingga mereka tidak dapat bangkit.\"'),(5822,4,33,12,'Tentang Benyamin ia berkata: \"Kekasih TUHAN yang diam pada-Nya dengan tenteram! TUHAN melindungi dia setiap waktu dan diam di antara lereng-lereng gunungnya.\"'),(5823,4,33,13,'Tentang Yusuf ia berkata: \"Kiranya negerinya diberkati oleh TUHAN dengan yang terbaik dari langit, dengan air embun, dan dengan air samudera raya yang ada di bawah;'),(5824,4,33,14,'dengan yang terbaik dari yang dihasilkan matahari, dan dengan yang terbaik dari yang ditumbuhkan bulan;'),(5825,4,33,15,'dengan yang terutama dari gunung-gunung yang sejak dahulu, dan dengan yang terbaik dari bukit-bukit yang berabad-abad,'),(5826,4,33,16,'dan dengan yang terbaik dari bumi serta segala isinya; dengan perkenanan Dia yang diam dalam semak duri. Biarlah itu semuanya turun ke atas kepala Yusuf, ke atas batu kepala orang yang teristimewa di antara saudara-saudaranya.'),(5827,4,33,17,'Anak sulung lembu sapinya adalah kegemilangannya dan tanduk-tanduknya seperti tanduk-tanduk lembu hutan; dengan itu ia akan menanduk bangsa-bangsa, seluruh bumi, dari ujung ke ujung. Itulah orang Efraim yang puluhan ribu, dan itulah orang Manasye yang ribuan.\"'),(5828,4,33,18,'Tentang Zebulon ia berkata: \"Bersukacitalah, hai Zebulon, atas perjalanan-perjalananmu, dan engkaupun, hai Isakhar, atas kemah-kemahmu.'),(5829,4,33,19,'Bangsa-bangsa akan dipanggil mereka datang ke gunung; di sanalah mereka akan mempersembahkan korban sembelihan yang benar, sebab mereka akan mengisap kelimpahan laut dan harta yang terpendam di dalam pasir.\"'),(5830,4,33,20,'Tentang Gad ia berkata: \"Terpujilah Dia yang memberi kelapangan kepada Gad. Seperti singa betina ia diam dan menerkam lengan, bahkan batu kepala.'),(5831,4,33,21,'Ia memilih bagian yang terutama, sebab di sanalah tersimpan bagian panglima; ia datang kepada para kepala bangsa itu; dilakukannya kebenaran TUHAN serta penghukuman-penghukuman-Nya bersama-sama dengan orang Israel.\"'),(5832,4,33,22,'Tentang Dan ia berkata: \"Adapun Dan ialah anak singa yang melompat keluar dari Basan.\"'),(5833,4,33,23,'Tentang Naftali ia berkata: \"Naftali kenyang dengan perkenanan dan penuh dengan berkat TUHAN; milikilah tasik dan wilayah sebelah selatan.\"'),(5834,4,33,24,'Tentang Asyer ia berkata: \"Diberkatilah Asyer di antara anak-anak lelaki; biarlah ia disukai oleh saudara-saudaranya, dan biarlah ia mencelupkan kakinya ke dalam minyak.'),(5835,4,33,25,'Biarlah dari besi dan dari tembaga palang pintumu, selama umurmu kiranya kekuatanmu.'),(5836,4,33,26,'Tidak ada yang seperti Allah, hai Yesyurun. Ia berkendaraan melintasi langit sebagai penolongmu dan dalam kejayaan-Nya melintasi awan-awan.'),(5837,4,33,27,'Allah yang abadi adalah tempat perlindunganmu, dan di bawahmu ada lengan-lengan yang kekal. Ia mengusir musuh dari depanmu dan berfirman: Punahkanlah!'),(5838,4,33,28,'Maka Israel diam dengan tenteram dan sumber Yakub diam tidak terganggu di dalam suatu negeri yang ada gandum dan anggur; bahkan langitnya menitikkan embun.'),(5839,4,33,29,'Berbahagialah engkau, hai Israel; siapakah yang sama dengan engkau? Suatu bangsa yang diselamatkan oleh TUHAN, perisai pertolongan dan pedang kejayaanmu. Sebab itu musuhmu akan tunduk menjilat kepadamu, dan engkau akan berjejak di bukit-bukit mereka.\"'),(5840,4,34,1,'Kemudian naiklah Musa dari dataran Moab ke atas gunung Nebo, yakni ke atas puncak Pisga, yang di tentangan Yerikho, lalu TUHAN memperlihatkan kepadanya seluruh negeri itu: daerah Gilead sampai ke kota Dan,'),(5841,4,34,2,'seluruh Naftali, tanah Efraim dan Manasye, seluruh tanah Yehuda sampai laut sebelah barat,'),(5842,4,34,3,'Tanah Negeb dan lembah Yordan, lembah Yerikho, kota pohon korma itu, sampai Zoar.'),(5843,4,34,4,'Dan berfirmanlah TUHAN kepadanya: \"Inilah negeri yang Kujanjikan dengan sumpah kepada Abraham, Ishak dan Yakub; demikian: Kepada keturunanmulah akan Kuberikan negeri itu. Aku mengizinkan engkau melihatnya dengan matamu sendiri, tetapi engkau tidak akan menyeberang ke sana.\"'),(5844,4,34,5,'Lalu matilah Musa, hamba TUHAN itu, di sana di tanah Moab, sesuai dengan firman TUHAN.'),(5845,4,34,6,'Dan dikuburkan-Nyalah dia di suatu lembah di tanah Moab, di tentangan Bet-Peor, dan tidak ada orang yang tahu kuburnya sampai hari ini.'),(5846,4,34,7,'Musa berumur seratus dua puluh tahun, ketika ia mati; matanya belum kabur dan kekuatannya belum hilang.'),(5847,4,34,8,'Orang Israel menangisi Musa di dataran Moab tiga puluh hari lamanya. Maka berakhirlah hari-hari tangis perkabungan karena Musa itu.'),(5848,4,34,9,'Dan Yosua bin Nun penuh dengan roh kebijaksanaan, sebab Musa telah meletakkan tangannya ke atasnya. Sebab itu orang Israel mendengarkan dia dan melakukan seperti yang diperintahkan TUHAN kepada Musa.'),(5849,4,34,10,'Seperti Musa yang dikenal TUHAN dengan berhadapan muka, tidak ada lagi nabi yang bangkit di antara orang Israel,'),(5850,4,34,11,'dalam hal segala tanda dan mujizat, yang dilakukannya atas perintah TUHAN di tanah Mesir terhadap Firaun dan terhadap semua pegawainya dan seluruh negerinya,'),(5851,4,34,12,'dan dalam hal segala perbuatan kekuasaan dan segala kedahsyatan yang besar yang dilakukan Musa di depan seluruh orang Israel.'),(5852,5,1,1,'Sesudah Musa hamba TUHAN itu mati, berfirmanlah TUHAN kepada Yosua bin Nun, abdi Musa itu, demikian:'),(5853,5,1,2,'Hamba-Ku Musa telah mati; sebab itu bersiaplah sekarang, seberangilah sungai Yordan ini, engkau dan seluruh bangsa ini, menuju negeri yang akan Kuberikan kepada mereka, kepada orang Israel itu.'),(5854,5,1,3,'Setiap tempat yang akan diinjak oleh telapak kakimu Kuberikan kepada kamu, seperti yang telah Kujanjikan kepada Musa.'),(5855,5,1,4,'Dari padang gurun dan gunung Libanon yang sebelah sana itu sampai ke sungai besar, yakni sungai Efrat, seluruh tanah orang Het, sampai ke Laut Besar di sebelah matahari terbenam, semuanya itu akan menjadi daerahmu.'),(5856,5,1,5,'Seorangpun tidak akan dapat bertahan menghadapi engkau seumur hidupmu; seperti Aku menyertai Musa, demikianlah Aku akan menyertai engkau; Aku tidak akan membiarkan engkau dan tidak akan meninggalkan engkau.'),(5857,5,1,6,'Kuatkan dan teguhkanlah hatimu, sebab engkaulah yang akan memimpin bangsa ini memiliki negeri yang Kujanjikan dengan bersumpah kepada nenek moyang mereka untuk diberikan kepada mereka.'),(5858,5,1,7,'Hanya, kuatkan dan teguhkanlah hatimu dengan sungguh-sungguh, bertindaklah hati-hati sesuai dengan seluruh hukum yang telah diperintahkan kepadamu oleh hamba-Ku Musa; janganlah menyimpang ke kanan atau ke kiri, supaya engkau beruntung, ke manapun engkau pergi.'),(5859,5,1,8,'Janganlah engkau lupa memperkatakan kitab Taurat ini, tetapi renungkanlah itu siang dan malam, supaya engkau bertindak hati-hati sesuai dengan segala yang tertulis di dalamnya, sebab dengan demikian perjalananmu akan berhasil dan engkau akan beruntung.'),(5860,5,1,9,'Bukankah telah Kuperintahkan kepadamu: kuatkan dan teguhkanlah hatimu? Janganlah kecut dan tawar hati, sebab TUHAN, Allahmu, menyertai engkau, ke manapun engkau pergi.\"'),(5861,5,1,10,'Lalu Yosua memberi perintah kepada pengatur-pengatur pasukan bangsa itu, katanya:'),(5862,5,1,11,'Jalanilah seluruh perkemahan dan perintahkanlah kepada bangsa itu, demikian: Sediakanlah bekalmu, sebab dalam tiga hari kamu akan menyeberangi sungai Yordan ini untuk pergi menduduki negeri yang akan diberikan TUHAN, Allahmu, kepadamu untuk diduduki.'),(5863,5,1,12,'Kepada orang Ruben, kepada orang Gad dan kepada suku Manasye yang setengah itu berkatalah Yosua, demikian:'),(5864,5,1,13,'Ingatlah kepada perkataan yang dipesankan Musa, hamba TUHAN itu, kepadamu, yakni: TUHAN, Allahmu, mengaruniakan keamanan kepadamu dan memberikan kepadamu negeri ini;'),(5865,5,1,14,'perempuan-perempuan dan anak-anak di antara kamu dan ternakmu boleh tinggal di negeri yang diberikan Musa kepadamu di seberang sungai Yordan, tetapi kamu, semua pahlawan yang gagah perkasa, haruslah menyeberang di depan saudara-saudaramu dengan bersenjata, dan haruslah menolong mereka,'),(5866,5,1,15,'sampai TUHAN mengaruniakan keamanan kepada saudara-saudaramu seperti kepada kamu juga, dan mereka juga menduduki negeri yang akan diberikan kepada mereka oleh TUHAN, Allahmu. Kemudian bolehlah kamu pulang kembali ke negerimu sendiri dan menduduki negeri yang diberikan Musa, hamba TUHAN itu, kepadamu di seberang sungai Yordan, di sebelah matahari terbit.\"'),(5867,5,1,16,'Lalu mereka menjawab Yosua, katanya: \"Segala yang kauperintahkan kepada kami akan kami lakukan dan ke manapun kami akan kausuruh, kami akan pergi;'),(5868,5,1,17,'sama seperti kami mendengarkan perintah Musa, demikianlah kami akan mendengarkan perintahmu. Hanya, TUHAN, Allahmu, kiranya menyertai engkau, seperti Ia menyertai Musa.'),(5869,5,1,18,'Setiap orang yang menentang perintahmu dan tidak mendengarkan perkataanmu, apapun yang kauperintahkan kepadanya, dia akan dihukum mati. Hanya, kuatkan dan teguhkanlah hatimu!\"'),(5870,5,2,1,'Yosua bin Nun dengan diam-diam melepas dari Sitim dua orang pengintai, katanya: \"Pergilah, amat-amatilah negeri itu dan kota Yerikho.\" Maka pergilah mereka dan sampailah mereka ke rumah seorang perempuan sundal, yang bernama Rahab, lalu tidur di situ.'),(5871,5,2,2,'Kemudian diberitahukanlah kepada raja Yerikho, demikian: \"Tadi malam ada orang datang ke mari dari orang Israel untuk menyelidik negeri ini.\"'),(5872,5,2,3,'Maka raja Yerikho menyuruh orang kepada Rahab, mengatakan: \"Bawalah ke luar orang-orang yang datang kepadamu itu, yang telah masuk ke dalam rumahmu, sebab mereka datang untuk menyelidik seluruh negeri ini.\"'),(5873,5,2,4,'Tetapi perempuan itu telah membawa dan menyembunyikan kedua orang itu. Berkatalah ia: \"Memang, orang-orang itu telah datang kepadaku, tetapi aku tidak tahu dari mana mereka,'),(5874,5,2,5,'dan ketika pintu gerbang hendak ditutup menjelang malam, maka keluarlah orang-orang itu; aku tidak tahu, ke mana orang-orang itu pergi. Segeralah kejar mereka, tentulah kamu dapat menyusul mereka.\"'),(5875,5,2,6,'Tetapi perempuan itu telah menyuruh keduanya naik ke sotoh rumah dan menyembunyikan mereka di bawah timbunan batang rami, yang ditebarkan di atas sotoh itu.'),(5876,5,2,7,'Maka pergilah orang-orang itu, mengejar mereka ke arah sungai Yordan, ke tempat-tempat penyeberangan, dan ditutuplah pintu gerbang, segera sesudah pengejar-pengejar itu keluar.'),(5877,5,2,8,'Tetapi sebelum kedua orang itu tidur, naiklah perempuan itu mendapatkan mereka di atas sotoh'),(5878,5,2,9,'dan berkata kepada orang-orang itu: \"Aku tahu, bahwa TUHAN telah memberikan negeri ini kepada kamu dan bahwa kengerian terhadap kamu telah menghinggapi kami dan segala penduduk negeri ini gemetar menghadapi kamu.'),(5879,5,2,10,'Sebab kami mendengar, bahwa TUHAN telah mengeringkan air Laut Teberau di depan kamu, ketika kamu berjalan keluar dari Mesir, dan apa yang kamu lakukan kepada kedua raja orang Amori yang di seberang sungai Yordan itu, yakni kepada Sihon dan Og, yang telah kamu tumpas.'),(5880,5,2,11,'Ketika kami mendengar itu, tawarlah hati kami dan jatuhlah semangat setiap orang menghadapi kamu, sebab TUHAN, Allahmu, ialah Allah di langit di atas dan di bumi di bawah.'),(5881,5,2,12,'Maka sekarang, bersumpahlah kiranya demi TUHAN, bahwa karena aku telah berlaku ramah terhadapmu, kamu juga akan berlaku ramah terhadap kaum keluargaku; dan berikanlah kepadaku suatu tanda yang dapat dipercaya,'),(5882,5,2,13,'bahwa kamu akan membiarkan hidup ayah dan ibuku, saudara-saudaraku yang laki-laki dan yang perempuan dan semua orang-orang mereka dan bahwa kamu akan menyelamatkan nyawa kami dari maut.\"'),(5883,5,2,14,'Lalu jawab kedua orang itu kepadanya: \"Nyawa kamilah jaminan bagi kamu, asal jangan kaukabarkan perkara kami ini; apabila TUHAN nanti memberikan negeri ini kepada kami, maka kami akan menunjukkan terima kasih dan setia kami kepadamu.\"'),(5884,5,2,15,'Kemudian perempuan itu menurunkan mereka dengan tali melalui jendela, sebab rumahnya itu letaknya pada tembok kota, jadi pada tembok itulah ia diam.'),(5885,5,2,16,'Berkatalah ia kepada mereka: \"Pergilah ke pegunungan, supaya pengejar-pengejar itu jangan menemui kamu, dan bersembunyilah di sana tiga hari lamanya, sampai pengejar-pengejar itu pulang; kemudian bolehlah kamu melanjutkan perjalananmu.\"'),(5886,5,2,17,'Kedua orang itu berkata kepadanya: \"Kami akan bebas dari sumpah kami ini kepadamu, yang telah kausuruh kami ikrarkan--'),(5887,5,2,18,'sesungguhnya, apabila kami memasuki negeri ini, haruslah tali dari benang kirmizi ini kauikatkan pada jendela tempat engkau menurunkan kami, dan ayahmu serta ibumu, saudara-saudaramu serta seluruh kaum keluargamu kaukumpulkan di rumahmu.'),(5888,5,2,19,'Setiap orang yang keluar nanti dari pintu rumahmu, harus sendiri menanggung akibatnya, kalau darahnya tertumpah, dan kami tidak bersalah; tetapi siapapun juga yang ada di dalam rumahmu, jika ada orang yang menciderainya, kamilah yang menanggung akibat pertumpahan darahnya.'),(5889,5,2,20,'Tetapi jika engkau mengabarkan perkara kami ini, maka bebaslah kami dari sumpah kepadamu itu, yang telah kausuruh kami ikrarkan.\"'),(5890,5,2,21,'Perempuan itupun berkata: \"Seperti yang telah kamu katakan, demikianlah akan terjadi.\" Sesudah itu dilepasnyalah orang-orang itu pergi, maka berangkatlah mereka. Kemudian perempuan itu mengikatkan tali kirmizi itu pada jendela.'),(5891,5,2,22,'Merekapun pergilah dan tiba di pegunungan. Mereka tinggal di sana tiga hari lamanya, sampai pengejar-pengejar itu pulang. Pengejar-pengejar itu telah mencari di mana-mana sepanjang jalan tanpa menemukan mereka.'),(5892,5,2,23,'Maka pulanglah kedua orang itu, mereka turun dari pegunungan, lalu menyeberang dan sampai kepada Yosua bin Nun, kemudian mereka ceritakan segala pengalaman mereka.'),(5893,5,2,24,'Kata mereka kepada Yosua: \"TUHAN telah menyerahkan seluruh negeri ini ke dalam tangan kita, bahkan seluruh penduduk negeri itu gemetar menghadapi kita.\"'),(5894,5,3,1,'Yosua bangun pagi-pagi, lalu ia dan semua orang Israel berangkat dari Sitim, dan sampailah mereka ke sungai Yordan, maka bermalamlah mereka di sana, sebelum menyeberang.'),(5895,5,3,2,'Setelah lewat tiga hari, para pengatur pasukan menjalani seluruh perkemahan,'),(5896,5,3,3,'dan memberi perintah kepada bangsa itu, katanya: \"Segera sesudah kamu melihat tabut perjanjian TUHAN, Allahmu, yang diangkat para imam, yang memang suku Lewi, maka kamu harus juga berangkat dari tempatmu dan mengikutinya--'),(5897,5,3,4,'hanya antara kamu dan tabut itu harus ada jarak kira-kira dua ribu hasta panjangnya, janganlah mendekatinya--maksudnya supaya kamu mengetahui jalan yang harus kamu tempuh, sebab jalan itu belum pernah kamu lalui dahulu.\"'),(5898,5,3,5,'Berkatalah Yosua kepada bangsa itu: \"Kuduskanlah dirimu, sebab besok TUHAN akan melakukan perbuatan yang ajaib di antara kamu.\"'),(5899,5,3,6,'Dan kepada para imam itu Yosua berkata, demikian: \"Angkatlah tabut perjanjian dan menyeberanglah di depan bangsa itu.\" Maka mereka mengangkat tabut perjanjian dan berjalan di depan bangsa itu.'),(5900,5,3,7,'Dan TUHAN berfirman kepada Yosua: \"Pada hari inilah Aku mulai membesarkan namamu di mata seluruh orang Israel, supaya mereka tahu, bahwa seperti dahulu Aku menyertai Musa, demikianlah Aku akan menyertai engkau.'),(5901,5,3,8,'Maka kauperintahkanlah kepada para imam pengangkat tabut perjanjian itu, demikian: Setelah kamu sampai ke tepi air sungai Yordan, haruslah kamu tetap berdiri di sungai Yordan itu.\"'),(5902,5,3,9,'Lalu berkatalah Yosua kepada orang Israel: \"Datanglah dekat dan dengarkanlah firman TUHAN, Allahmu.\"'),(5903,5,3,10,'Lagi kata Yosua: \"Dari hal inilah akan kamu ketahui, bahwa Allah yang hidup ada di tengah-tengah kamu dan bahwa sungguh-sungguh akan dihalau-Nya orang Kanaan, orang Het, orang Hewi, orang Feris, orang Girgasi, orang Amori dan orang Yebus itu dari depan kamu:'),(5904,5,3,11,'sesungguhnya, tabut perjanjian Tuhan semesta bumi berjalan menyeberang di depan kamu, masuk ke sungai Yordan.'),(5905,5,3,12,'Maka sekarang, pilihlah dua belas orang dari suku-suku Israel, seorang dari tiap-tiap suku.'),(5906,5,3,13,'Segera sesudah kaki para imam pengangkat tabut TUHAN, Tuhan semesta bumi, berhenti di dalam air sungai Yordan, maka air sungai Yordan itu akan terputus; air yang turun dari hulu akan berhenti mengalir menjadi bendungan.\"'),(5907,5,3,14,'Ketika bangsa itu berangkat dari tempat perkemahan mereka untuk menyeberangi sungai Yordan, para imam pengangkat tabut perjanjian itu berjalan di depan bangsa itu.'),(5908,5,3,15,'Segera sesudah para pengangkat tabut itu sampai ke sungai Yordan, dan para imam pengangkat tabut itu mencelupkan kakinya ke dalam air di tepi sungai itu--sungai Yordan itu sebak sampai meluap sepanjang tepinya selama musim menuai--'),(5909,5,3,16,'maka berhentilah air itu mengalir. Air yang turun dari hulu melonjak menjadi bendungan, jauh sekali, di dekat Adam, kota yang terletak di sebelah Sartan, sedang air yang turun ke Laut Araba itu, yakni Laut Asin, terputus sama sekali. Lalu menyeberanglah bangsa itu, di tentangan Yerikho.'),(5910,5,3,17,'Tetapi para imam pengangkat tabut perjanjian TUHAN itu tetap berdiri di tanah yang kering, di tengah-tengah sungai Yordan, sedang seluruh bangsa Israel menyeberang di tanah yang kering, sampai seluruh bangsa itu selesai menyeberangi sungai Yordan.'),(5911,5,4,1,'Setelah seluruh bangsa itu selesai menyeberangi sungai Yordan, berfirmanlah TUHAN kepada Yosua, demikian:'),(5912,5,4,2,'Pilihlah dari bangsa itu dua belas orang, seorang dari tiap-tiap suku,'),(5913,5,4,3,'dan perintahkanlah kepada mereka, demikian: Angkatlah dua belas batu dari sini, dari tengah-tengah sungai Yordan ini, dari tempat berjejak kaki para imam itu, bawalah semuanya itu ke seberang dan letakkanlah di tempat kamu akan bermalam nanti malam.\"'),(5914,5,4,4,'Lalu Yosua memanggil kedua belas orang yang ditetapkannya dari orang Israel itu, seorang dari tiap-tiap suku,'),(5915,5,4,5,'dan Yosua berkata kepada mereka: \"Menyeberanglah di depan tabut TUHAN, Allahmu, ke tengah-tengah sungai Yordan, dan angkatlah masing-masing sebuah batu ke atas bahumu, menurut bilangan suku orang Israel,'),(5916,5,4,6,'supaya ini menjadi tanda di tengah-tengah kamu. Jika anak-anakmu bertanya di kemudian hari: Apakah artinya batu-batu ini bagi kamu?'),(5917,5,4,7,'maka haruslah kamu katakan kepada mereka: Bahwa air sungai Yordan terputus di depan tabut perjanjian TUHAN; ketika tabut itu menyeberangi sungai Yordan, air sungai Yordan itu terputus. Sebab itu batu-batu ini akan menjadi tanda peringatan bagi orang Israel untuk selama-lamanya.\"'),(5918,5,4,8,'Maka orang Israel itu melakukan seperti yang diperintahkan Yosua. Mereka mengangkat dua belas batu dari tengah-tengah sungai Yordan, seperti yang difirmankan TUHAN kepada Yosua, menurut jumlah suku Israel. Semuanya itu dibawa merekalah ke seberang, ke tempat bermalam, dan diletakkan di situ.'),(5919,5,4,9,'Pula Yosua menegakkan dua belas batu di tengah-tengah sungai Yordan itu, di tempat bekas berjejak kaki para imam pengangkat tabut perjanjian itu. Batu-batu itu masih ada di sana sampai sekarang.'),(5920,5,4,10,'Para imam pengangkat tabut itu tinggal berdiri di tengah-tengah sungai Yordan, sampai selesai dilakukan segala yang diperintahkan TUHAN kepada Yosua untuk disampaikan kepada bangsa itu, sesuai dengan segala yang diperintahkan Musa kepada Yosua. Maka menyeberanglah bangsa itu dengan cepat-cepat.'),(5921,5,4,11,'Ketika seluruh bangsa itu selesai menyeberang, maka menyeberanglah tabut TUHAN itu serta para imam di depan mata bangsa itu.'),(5922,5,4,12,'Juga bani Ruben, bani Gad dan suku Manasye yang setengah itu menyeberang, dengan bersenjata, di depan orang Israel itu, seperti yang dikatakan Musa kepada mereka.'),(5923,5,4,13,'Kira-kira empat puluh ribu orang yang siap untuk berperang menyeberang di hadapan TUHAN ke dataran Yerikho untuk berperang.'),(5924,5,4,14,'Pada waktu itulah TUHAN membesarkan nama Yosua di mata seluruh orang Israel, sehingga mereka takut kepadanya, seperti mereka takut kepada Musa seumur hidupnya.'),(5925,5,4,15,'Berfirmanlah TUHAN kepada Yosua, demikian:'),(5926,5,4,16,'Perintahkanlah para imam pengangkat tabut hukum Allah itu supaya naik dari sungai Yordan.'),(5927,5,4,17,'Maka Yosua memerintahkan kepada para imam itu, demikian: \"Keluarlah dari sungai Yordan.\"'),(5928,5,4,18,'Ketika para imam, pengangkat tabut perjanjian TUHAN itu, keluar dari tengah-tengah sungai Yordan, dan baru saja kaki para imam itu dijejakkan di tanah yang kering, maka berbaliklah air sungai Yordan itu ke tempatnya dan mengalir seperti dahulu dengan meluap sepanjang tepinya.'),(5929,5,4,19,'Bangsa itu telah keluar dari sungai Yordan pada tanggal sepuluh bulan pertama dan mereka berkemah di Gilgal, di batas timur Yerikho.'),(5930,5,4,20,'Kedua belas batu yang diambil dari sungai Yordan itu ditegakkan oleh Yosua di Gilgal.'),(5931,5,4,21,'Dan berkatalah ia kepada orang Israel, demikian: \"Apabila di kemudian hari anak-anakmu bertanya kepada ayahnya: Apakah arti batu-batu ini?'),(5932,5,4,22,'maka haruslah kamu beritahukan kepada anak-anakmu, begini: Israel telah menyeberangi sungai Yordan ini di tanah yang kering! --'),(5933,5,4,23,'sebab TUHAN, Allahmu, telah mengeringkan di depan kamu air sungai Yordan, sampai kamu dapat menyeberang seperti yang telah dilakukan TUHAN, Allahmu, dengan Laut Teberau, yang telah dikeringkan-Nya di depan kita, sampai kita dapat menyeberang,'),(5934,5,4,24,'supaya semua bangsa di bumi tahu, bahwa kuat tangan TUHAN, dan supaya mereka selalu takut kepada TUHAN, Allahmu.\"'),(5935,5,5,1,'Ketika semua raja orang Amori di sebelah barat sungai Yordan dan semua raja orang Kanaan di tepi laut mendengar, bahwa TUHAN telah mengeringkan air sungai Yordan di depan orang Israel, sampai mereka dapat menyeberang, tawarlah hati mereka dan hilanglah semangat mereka menghadapi orang Israel itu.'),(5936,5,5,2,'Pada waktu itu berfirmanlah TUHAN kepada Yosua: \"Buatlah pisau dari batu dan sunatlah lagi orang Israel itu, untuk kedua kalinya.\"'),(5937,5,5,3,'Lalu Yosua membuat pisau dari batu dan disunatnyalah orang Israel itu di Bukit Kulit Khatan.'),(5938,5,5,4,'Inilah sebabnya Yosua menyunat mereka: semua orang yang keluar dari Mesir, yakni yang laki-laki, semua prajurit, telah mati di padang gurun di tengah jalan, setelah mereka keluar dari Mesir.'),(5939,5,5,5,'Sebab, semua orang yang keluar dari Mesir itu telah bersunat, tetapi semua orang yang lahir di padang gurun dalam perjalanan sejak keluar dari Mesir, belum disunat.'),(5940,5,5,6,'Sebab empat puluh tahun lamanya orang Israel itu berjalan melalui padang gurun, sampai habis mati seluruh bangsa itu, yakni prajurit yang keluar dari Mesir, yang tidak mendengarkan firman TUHAN. Kepada mereka itu TUHAN telah bersumpah, bahwa Ia tidak akan mengizinkan mereka melihat negeri yang dijanjikan TUHAN dengan bersumpah kepada nenek moyang mereka akan memberikannya kepada kita, suatu negeri yang berlimpah-limpah susu dan madunya.'),(5941,5,5,7,'Tetapi anak-anak mereka yang telah dijadikan-Nya ganti mereka, mereka itulah yang disunat Yosua, sebab mereka belum bersunat, karena mereka tidak disunat dalam perjalanan.'),(5942,5,5,8,'Setelah seluruh bangsa itu selesai disunat, maka tinggallah mereka di tempatnya masing-masing di perkemahan itu, sampai mereka sembuh.'),(5943,5,5,9,'Dan berfirmanlah TUHAN kepada Yosua: \"Hari ini telah Kuhapuskan cela Mesir itu dari padamu.\" Itulah sebabnya nama tempat itu disebut Gilgal sampai sekarang.'),(5944,5,5,10,'Sementara berkemah di Gilgal, orang Israel itu merayakan Paskah pada hari yang keempat belas bulan itu, pada waktu petang, di dataran Yerikho.'),(5945,5,5,11,'Lalu pada hari sesudah Paskah mereka makan hasil negeri itu, yakni roti yang tidak beragi dan bertih gandum, pada hari itu juga.'),(5946,5,5,12,'Lalu berhentilah manna itu, pada keesokan harinya setelah mereka makan hasil negeri itu. Jadi orang Israel tidak beroleh manna lagi, tetapi dalam tahun itu mereka makan yang dihasilkan tanah Kanaan.'),(5947,5,5,13,'Ketika Yosua dekat Yerikho, ia melayangkan pandangnya, dilihatnya seorang laki-laki berdiri di depannya dengan pedang terhunus di tangannya. Yosua mendekatinya dan bertanya kepadanya: \"Kawankah engkau atau lawan?\"'),(5948,5,5,14,'Jawabnya: \"Bukan, tetapi akulah Panglima Balatentara TUHAN. Sekarang aku datang.\" Lalu sujudlah Yosua dengan mukanya ke tanah, menyembah dan berkata kepadanya: \"Apakah yang akan dikatakan tuanku kepada hambanya ini?\"'),(5949,5,5,15,'Dan Panglima Balatentara TUHAN itu berkata kepada Yosua: \"Tanggalkanlah kasutmu dari kakimu, sebab tempat engkau berdiri itu kudus.\" Dan Yosua berbuat demikian.'),(5950,5,6,1,'Dalam pada itu Yerikho telah menutup pintu gerbangnya; telah tertutup kota itu karena orang Israel; tidak ada orang keluar atau masuk.'),(5951,5,6,2,'Berfirmanlah TUHAN kepada Yosua: \"Ketahuilah, Aku serahkan ke tanganmu Yerikho ini beserta rajanya dan pahlawan-pahlawannya yang gagah perkasa.'),(5952,5,6,3,'Haruslah kamu mengelilingi kota itu, yakni semua prajurit harus mengedari kota itu sekali saja; demikianlah harus engkau perbuat enam hari lamanya,'),(5953,5,6,4,'dan tujuh orang imam harus membawa tujuh sangkakala tanduk domba di depan tabut. Tetapi pada hari yang ketujuh, tujuh kali kamu harus mengelilingi kota itu sedang para imam meniup sangkakala.'),(5954,5,6,5,'Apabila sangkakala tanduk domba itu panjang bunyinya dan kamu mendengar bunyi sangkakala itu, maka haruslah seluruh bangsa bersorak dengan sorak yang nyaring, maka tembok kota itu akan runtuh, lalu bangsa itu harus memanjatnya, masing-masing langsung ke depan.\"'),(5955,5,6,6,'Kemudian Yosua bin Nun memanggil para imam dan berkata kepada mereka: \"Angkatlah tabut perjanjian itu dan tujuh orang imam harus membawa tujuh sangkakala tanduk domba di depan tabut TUHAN.\"'),(5956,5,6,7,'Dan kepada bangsa itu dikatakannya: \"Majulah, kelilingilah kota itu, dan orang-orang bersenjata harus berjalan di depan tabut TUHAN.\"'),(5957,5,6,8,'Segera sesudah Yosua berkata kepada bangsa itu, maka berjalanlah maju ketujuh orang imam, yang membawa ketujuh sangkakala tanduk domba itu di hadapan TUHAN, lalu mereka meniup sangkakala, sedang tabut perjanjian TUHAN mengikut mereka.'),(5958,5,6,9,'Dan orang-orang bersenjata berjalan di depan para imam yang meniup sangkakala dan barisan penutup mengikut tabut itu, sedang sangkakala terus-menerus ditiup.'),(5959,5,6,10,'Tetapi Yosua telah memerintahkan kepada bangsa itu, demikian: \"Janganlah bersorak dan janganlah perdengarkan suaramu, sepatah katapun janganlah keluar dari mulutmu sampai pada hari aku mengatakan kepadamu: Bersoraklah! --maka kamu harus bersorak.\"'),(5960,5,6,11,'Demikianlah tabut TUHAN mengelilingi kota itu, mengedarinya sekali saja. Kemudian kembalilah mereka ke tempat perkemahan dan bermalam di tempat perkemahan itu.'),(5961,5,6,12,'Keesokan harinya Yosua bangun pagi-pagi, lalu para imam mengangkat tabut TUHAN.'),(5962,5,6,13,'Maka berjalanlah juga ketujuh orang imam, yang membawa ketujuh sangkakala tanduk domba itu di depan tabut TUHAN, sambil berjalan mereka meniup sangkakala, sedang orang-orang bersenjata berjalan di depan mereka dan barisan penutup mengikut tabut TUHAN, sementara sangkakala terus-menerus ditiup.'),(5963,5,6,14,'Demikianlah pada hari kedua mereka mengelilingi kota itu sekali saja, lalu pulang ke tempat perkemahan. Dan begitulah dilakukan mereka enam hari lamanya.'),(5964,5,6,15,'Tetapi pada hari yang ketujuh mereka bangun pagi-pagi, ketika fajar menyingsing, dan mengelilingi kota tujuh kali dengan cara yang sama; hanya pada hari itu mereka mengelilingi kota itu tujuh kali.'),(5965,5,6,16,'Lalu pada ketujuh kalinya, ketika para imam meniup sangkakala, berkatalah Yosua kepada bangsa itu: \"Bersoraklah, sebab TUHAN telah menyerahkan kota ini kepadamu!'),(5966,5,6,17,'Dan kota itu dengan segala isinya akan dikhususkan bagi TUHAN untuk dimusnahkan; hanya Rahab, perempuan sundal itu, akan tetap hidup, ia dengan semua orang yang bersama-sama dengan dia dalam rumah itu, karena ia telah menyembunyikan orang suruhan yang kita suruh.'),(5967,5,6,18,'Tetapi kamu ini, jagalah dirimu terhadap barang-barang yang dikhususkan untuk dimusnahkan, supaya jangan kamu mengambil sesuatu dari barang-barang yang dikhususkan itu setelah mengkhususkannya dan dengan demikian membawa kemusnahan atas perkemahan orang Israel dan mencelakakannya.'),(5968,5,6,19,'Segala emas dan perak serta barang-barang tembaga dan besi adalah kudus bagi TUHAN; semuanya itu akan dimasukkan ke dalam perbendaharaan TUHAN.\"'),(5969,5,6,20,'Lalu bersoraklah bangsa itu, sedang sangkakala ditiup; segera sesudah bangsa itu mendengar bunyi sangkakala, bersoraklah mereka dengan sorak yang nyaring. Maka runtuhlah tembok itu, lalu mereka memanjat masuk ke dalam kota, masing-masing langsung ke depan, dan merebut kota itu.'),(5970,5,6,21,'Mereka menumpas dengan mata pedang segala sesuatu yang di dalam kota itu, baik laki-laki maupun perempuan, baik tua maupun muda, sampai kepada lembu, domba dan keledai.'),(5971,5,6,22,'Tetapi kepada kedua orang pengintai negeri itu Yosua berkata: \"Masuklah ke dalam rumah perempuan sundal itu dan bawalah ke luar perempuan itu dan semua orang yang bersama-sama dengan dia, seperti yang telah kamu janjikan dengan bersumpah kepadanya.\"'),(5972,5,6,23,'Lalu masuklah kedua pengintai muda itu dan membawa ke luar Rahab dan ayahnya, ibunya, saudara-saudaranya dan semua orang yang bersama-sama dengan dia, bahkan seluruh kaumnya dibawa mereka ke luar, lalu mereka menunjukkan kepadanya tempat tinggal di luar perkemahan orang Israel.'),(5973,5,6,24,'Tetapi kota itu dan segala sesuatu yang ada di dalamnya dibakar mereka dengan api; hanya emas dan perak, barang-barang tembaga dan besi ditaruh mereka di dalam perbendaharaan rumah TUHAN.'),(5974,5,6,25,'Demikianlah Rahab, perempuan sundal itu dan keluarganya serta semua orang yang bersama-sama dengan dia dibiarkan hidup oleh Yosua. Maka diamlah perempuan itu di tengah-tengah orang Israel sampai sekarang, karena ia telah menyembunyikan orang suruhan yang disuruh Yosua mengintai Yerikho.'),(5975,5,6,26,'Pada waktu itu bersumpahlah Yosua, katanya: \"Terkutuklah di hadapan TUHAN orang yang bangkit untuk membangun kembali kota Yerikho ini; dengan membayarkan nyawa anaknya yang sulung ia akan meletakkan dasar kota itu dan dengan membayarkan nyawa anaknya yang bungsu ia akan memasang pintu gerbangnya!\"'),(5976,5,6,27,'Dan TUHAN menyertai Yosua dan terdengarlah kabar tentang dia di seluruh negeri itu.'),(5977,5,7,1,'Tetapi orang Israel berubah setia dengan mengambil barang-barang yang dikhususkan itu, karena Akhan bin Karmi bin Zabdi bin Zerah, dari suku Yehuda, mengambil sesuatu dari barang-barang yang dikhususkan itu. Lalu bangkitlah murka TUHAN terhadap orang Israel.'),(5978,5,7,2,'Yosua menyuruh orang dari Yerikho ke Ai, yang letaknya dekat Bet-Awen, di sebelah timur Betel, dan berkata kepada mereka, demikian: \"Pergilah ke sana dan intailah negeri itu.\" Maka pergilah orang-orang itu ke sana dan mengintai kota Ai.'),(5979,5,7,3,'Kemudian kembalilah mereka kepada Yosua dan berkata kepadanya: \"Tidak usah seluruh bangsa itu pergi, biarlah hanya kira-kira dua atau tiga ribu orang pergi untuk menggempur Ai itu; janganlah kaususahkan seluruh bangsa itu dengan berjalan ke sana, sebab orang-orang di sana sedikit saja.\"'),(5980,5,7,4,'Maka berangkatlah kira-kira tiga ribu orang dari bangsa itu ke sana; tetapi mereka melarikan diri di depan orang-orang Ai.'),(5981,5,7,5,'Sebab orang-orang Ai menewaskan kira-kira tiga puluh enam orang dari mereka; orang-orang Israel itu dikejar dari depan pintu gerbang kota itu sampai ke Syebarim dan dipukul kalah di lereng. Lalu tawarlah hati bangsa itu amat sangat.'),(5982,5,7,6,'Yosuapun mengoyakkan jubahnya dan sujudlah ia dengan mukanya sampai ke tanah di depan tabut TUHAN hingga petang, bersama dengan para tua-tua orang Israel, sambil menaburkan debu di atas kepalanya.'),(5983,5,7,7,'Dan berkatalah Yosua: \"Ah, Tuhanku ALLAH, mengapa Engkau menyuruh bangsa ini menyeberangi sungai Yordan? supaya kami diserahkan kepada orang Amori untuk dibinasakan? Lebih baik kalau kami putuskan tadinya untuk tinggal di seberang sungai Yordan itu!'),(5984,5,7,8,'O Tuhan, apakah yang akan kukatakan, setelah orang Israel lari membelakangi musuhnya?'),(5985,5,7,9,'Apabila hal itu terdengar oleh orang Kanaan dan seluruh penduduk negeri ini, maka mereka akan mengepung kami dan melenyapkan nama kami dari bumi ini. Dan apakah yang akan Kaulakukan untuk memulihkan nama-Mu yang besar itu?\"'),(5986,5,7,10,'Lalu berfirmanlah TUHAN kepada Yosua: \"Bangunlah! Mengapa engkau sujud demikian?'),(5987,5,7,11,'Orang Israel telah berbuat dosa, mereka melanggar perjanjian-Ku yang Kuperintahkan kepada mereka, mereka mengambil sesuatu dari barang-barang yang dikhususkan itu, mereka mencurinya, mereka menyembunyikannya dan mereka menaruhnya di antara barang-barangnya.'),(5988,5,7,12,'Sebab itu orang Israel tidak dapat bertahan menghadapi musuhnya. Mereka membelakangi musuhnya, sebab mereka itupun dikhususkan untuk ditumpas. Aku tidak akan menyertai kamu lagi jika barang-barang yang dikhususkan itu tidak kamu punahkan dari tengah-tengahmu.'),(5989,5,7,13,'Bangunlah, kuduskanlah bangsa itu dan katakan: Kuduskanlah dirimu untuk esok hari, sebab, demikianlah firman TUHAN, Allah Israel: Hai, orang Israel ada barang-barang yang dikhususkan di tengah-tengahmu; kamu tidak akan dapat bertahan menghadapi musuhmu, sebelum barang-barang yang dikhususkan itu kamu jauhkan dari tengah-tengah kamu.'),(5990,5,7,14,'Besok pagi kamu harus tampil ke muka suku demi suku dan suku yang ditunjuk oleh TUHAN harus tampil ke muka kaum demi kaum, dan kaum yang ditunjuk oleh TUHAN harus tampil ke muka keluarga demi keluarga dan keluarga yang ditunjuk oleh TUHAN harus tampil ke muka seorang demi seorang.'),(5991,5,7,15,'Dan siapa yang didapati menyimpan barang-barang yang dikhususkan itu, akan dibakar dengan api, ia dan segala sesuatu yang ada padanya, sebab ia telah melanggar perjanjian TUHAN dan berbuat noda di antara orang Israel.\"'),(5992,5,7,16,'Keesokan harinya bangunlah Yosua pagi-pagi, lalu menyuruh orang Israel tampil ke muka suku demi suku, maka didapatilah suku Yehuda.'),(5993,5,7,17,'Ketika disuruhnya tampil ke muka kaum-kaum Yehuda, maka didapatinya kaum Zerah. Ketika disuruhnya tampil ke muka kaum Zerah, seorang demi seorang, maka didapatilah Zabdi.'),(5994,5,7,18,'Ketika disuruhnya keluarga orang itu tampil ke muka, seorang demi seorang, maka didapatilah Akhan bin Karmi bin Zabdi bin Zerah, dari suku Yehuda.'),(5995,5,7,19,'Berkatalah Yosua kepada Akhan: \"Anakku, hormatilah TUHAN, Allah Israel, dan mengakulah di hadapan-Nya; katakanlah kepadaku apa yang kauperbuat, jangan sembunyikan kepadaku.\"'),(5996,5,7,20,'Lalu Akhan menjawab Yosua, katanya: \"Benar, akulah yang berbuat dosa terhadap TUHAN, Allah Israel, sebab beginilah perbuatanku:'),(5997,5,7,21,'aku melihat di antara barang-barang jarahan itu jubah yang indah, buatan Sinear, dan dua ratus syikal perak dan sebatang emas yang lima puluh syikal beratnya; aku mengingininya, maka kuambil; semuanya itu disembunyikan di dalam kemahku dalam tanah, dan perak itu di bawah sekali.\"'),(5998,5,7,22,'Lalu Yosua menyuruh orang segera pergi ke kemah itu, dan sesungguhnya, semuanya itu disembunyikan dalam kemah Akhan, dan perak itu ada di bawah sekali.'),(5999,5,7,23,'Maka mereka mengambil semuanya itu dari dalam kemah, lalu membawanya kepada Yosua dan kepada semua orang Israel, dan mencurahkannya di hadapan TUHAN.'),(6000,5,7,24,'Kemudian Yosua, beserta seluruh Israel mengambil Akhan bin Zerah, dan perak, jubah dan emas sebatang itu, anak-anaknya yang laki-laki dan perempuan, lembunya, keledainya dan kambing dombanya, kemahnya dan segala kepunyaannya, lalu semuanya itu dibawa ke lembah Akhor.'),(6001,5,7,25,'Berkatalah Yosua: \"Seperti engkau mencelakakan kami, maka TUHAN pun mencelakakan engkau pada hari ini.\" Lalu seluruh Israel melontari dia dengan batu, semuanya itu dibakar dengan api dan dilempari dengan batu.'),(6002,5,7,26,'Sesudah itu didirikanlah di atasnya suatu timbunan batu yang besar, yang masih ada sampai sekarang. Lalu surutlah murka TUHAN yang bernyala-nyala itu. Oleh sebab itu nama tempat itu sampai sekarang disebutkan lembah Akhor.'),(6003,5,8,1,'Kemudian berfirmanlah TUHAN kepada Yosua: \"Janganlah takut dan janganlah tawar hati; bawalah seluruh tentara dan bersiaplah, majulah ke Ai. Ketahuilah, Aku serahkan kepadamu raja negeri Ai, rakyatnya, kotanya dan negerinya,'),(6004,5,8,2,'dan haruslah kaulakukan kepada Ai dan rajanya, seperti yang kaulakukan kepada Yerikho dan rajanya; hanya barang-barangnya dan ternaknya boleh kamu jarah. Suruhlah orang bersembunyi di belakang kota itu.\"'),(6005,5,8,3,'Lalu bersiaplah Yosua beserta seluruh tentara untuk pergi ke Ai. Yosua memilih tiga puluh ribu orang, pahlawan-pahlawan yang gagah perkasa, mereka disuruhnya pergi pada waktu malam'),(6006,5,8,4,'dan kepada mereka diperintahkannya, katanya: \"Ketahuilah, kamu harus bersembunyi di belakang kota itu untuk menyerangnya, janganlah terlalu jauh dari kota itu, dan bersiap-siaplah kamu sekalian.'),(6007,5,8,5,'Aku dan semua orang yang bersama-sama dengan aku akan mendekati kota itu; apabila mereka keluar menyerbu kami, seperti yang pertama kali, maka kami akan melarikan diri dari hadapan mereka.'),(6008,5,8,6,'Jadi mereka akan keluar menyusul kami, sehingga kami memancing mereka jauh dari kota itu, sebab mereka akan berkata: orang-orang itu melarikan diri dari hadapan kita seperti yang pertama kali. Jika kami melarikan diri dari hadapan mereka,'),(6009,5,8,7,'maka kamu harus bangun dari tempat persembunyianmu itu untuk menduduki kota itu, dan TUHAN, Allahmu, akan menyerahkannya ke dalam tanganmu.'),(6010,5,8,8,'Segera setelah kamu merebut kota itu, haruslah kamu membakarnya; sesuai dengan firman TUHAN kamu harus melakukan semuanya itu; ingatlah, itulah perintahku kepadamu.\"'),(6011,5,8,9,'Demikianlah Yosua menyuruh mereka pergi, lalu berjalanlah mereka ke tempat persembunyian dan tinggal di antara Betel dan Ai, di sebelah barat Ai. Tetapi Yosua bermalam di tengah-tengah rakyat pada malam itu.'),(6012,5,8,10,'Keesokan harinya Yosua bangun pagi-pagi, lalu diperiksanyalah barisan bangsa itu dan berjalanlah ia maju beserta para tua-tua orang Israel di depan bangsa itu ke Ai.'),(6013,5,8,11,'Juga seluruh tentara yang bersama-sama dengan dia berjalan maju; mereka maju mendekat, lalu sampai ke tentangan kota itu, kemudian berkemahlah mereka di sebelah utara Ai, sehingga lembah itu ada di antara mereka dan Ai.'),(6014,5,8,12,'Yosua telah mengambil kira-kira lima ribu orang, lalu disuruhnya mereka bersembunyi di antara Betel dan Ai, di sebelah barat kota itu.'),(6015,5,8,13,'Beginilah rakyat itu diatur: seluruh tentara itu di sebelah utara kota dengan barisan belakang di sebelah barat kota. Pada malam itu berjalanlah Yosua melalui lembah itu.'),(6016,5,8,14,'Pagi-pagi, ketika raja negeri Ai melihat hal itu, maka ia dan seluruh rakyatnya, orang-orang kota itu, segera keluar berperang, menyerbu orang Israel, ke lereng di seberang dataran itu; raja itu tidak tahu, bahwa ada orang bersembunyi di belakang kota.'),(6017,5,8,15,'Yosua dan seluruh orang Israel itu berlaku seolah-olah dipukul mundur oleh mereka, lalu melarikan diri ke arah padang gurun.'),(6018,5,8,16,'Sebab itu semua orang yang ada di kota dikerahkan untuk mengejar orang Israel. Maka mereka mengejar Yosua, sehingga makin jauhlah mereka terpancing dari kota.'),(6019,5,8,17,'Seorangpun tidak tertinggal lagi di Ai dan Betel yang tidak keluar memburu orang Israel. Mereka meninggalkan kota itu terbuka, karena mereka mengejar orang Israel.'),(6020,5,8,18,'Lalu berfirmanlah TUHAN kepada Yosua: \"Acungkanlah lembing yang ada di tanganmu ke arah Ai, sebab Aku menyerahkan kota itu ke dalam tanganmu.\" Maka Yosua mengacungkan lembing yang di tangannya ke arah kota itu.'),(6021,5,8,19,'Ketika diacungkannya tangannya, maka segeralah bangun orang-orang yang bersembunyi itu dari tempatnya, mereka berlari memasuki kota, merebutnya, lalu segera membakar kota itu.'),(6022,5,8,20,'Ketika orang Ai berpaling menoleh ke belakang, tampaklah asap kota itu naik membubung ke langit; mereka tidak sempat melarikan diri ke manapun juga, sebab rakyat yang tadinya lari ke padang gurun, berbalik melawan pengejar-pengejarnya.'),(6023,5,8,21,'Ketika Yosua dan seluruh Israel melihat, bahwa orang-orang yang bersembunyi itu telah merebut kota dan bahwa asap kota itu naik membubung, berbaliklah mereka, lalu menewaskan orang-orang Ai.'),(6024,5,8,22,'Sementara itu juga keluar orang-orang Israel yang lain dari dalam kota menyerbu orang-orang Ai, sehingga terjepit di tengah-tengah orang Israel itu, yang ini dari sini dan yang itu dari sana; orang-orang Ai ditewaskan, sehingga seorangpun dari mereka tidak ada yang dibiarkan terlepas atau luput.'),(6025,5,8,23,'Tetapi raja Ai ditangkap mereka hidup-hidup dan dihadapkan kepada Yosua.'),(6026,5,8,24,'Segera sesudah orang Israel selesai membunuh seluruh penduduk kota Ai di padang terbuka ke mana orang Israel mengejar mereka, dan orang-orang ini semuanya tewas oleh mata pedang sampai orang yang penghabisan, maka seluruh Israel kembali ke Ai dan memukul kota itu dengan mata pedang.'),(6027,5,8,25,'Jumlah semua orang yang tewas pada hari itu, baik laki-laki maupun perempuan, ada dua belas ribu orang, semuanya orang Ai.'),(6028,5,8,26,'Dan Yosua tidak menarik tangannya yang mengacungkan lembing itu, sebelum seluruh penduduk kota Ai ditumpasnya.'),(6029,5,8,27,'Hanya ternak dan barang-barang kota itu dijarah oleh orang Israel, sesuai dengan firman TUHAN, yang diperintahkan-Nya kepada Yosua.'),(6030,5,8,28,'Yosua membakar Ai dan membuatnya menjadi timbunan puing untuk selama-lamanya, menjadi tempat yang tandus sampai sekarang.'),(6031,5,8,29,'Dan raja Ai digantungnya pada sebuah tiang sampai petang. Ketika matahari terbenam, Yosua memerintahkan orang menurunkan mayat itu dari tiang, lalu dilemparkan di depan pintu gerbang kota, kemudian didirikan oranglah di atasnya suatu timbunan batu yang besar, yang masih ada sampai sekarang.'),(6032,5,8,30,'Pada waktu itulah Yosua mendirikan mezbah di gunung Ebal bagi TUHAN, Allah Israel,'),(6033,5,8,31,'seperti yang diperintahkan Musa, hamba TUHAN, kepada orang Israel, menurut apa yang tertulis dalam kitab hukum Musa: suatu mezbah dari batu-batu yang tidak dipahat, yang tidak diolah dengan perkakas besi apapun. Di atasnyalah mereka mempersembahkan korban bakaran kepada TUHAN dan mengorbankan korban keselamatan.'),(6034,5,8,32,'Dan di sanalah di atas batu-batu itu, dituliskan Yosua salinan hukum Musa, yang dituliskannya di depan orang Israel.'),(6035,5,8,33,'Seluruh orang Israel, para tua-tuanya, para pengatur pasukannya dan para hakimnya berdiri sebelah-menyebelah tabut, berhadapan dengan para imam yang memang suku Lewi, para pengangkat tabut perjanjian TUHAN itu, baik pendatang maupun anak negeri, setengahnya menghadap ke gunung Gerizim dan setengahnya lagi menghadap ke gunung Ebal, seperti yang dahulu diperintahkan oleh Musa, hamba TUHAN, apabila orang memberkati bangsa Israel.'),(6036,5,8,34,'Sesudah itu dibacakannyalah segala perkataan hukum Taurat, berkatnya dan kutuknya, sesuai dengan segala apa yang tertulis dalam kitab hukum.'),(6037,5,8,35,'Tidak ada sepatah katapun dari segala apa yang diperintahkan Musa yang tidak dibacakan oleh Yosua kepada seluruh jemaah Israel dan kepada perempuan-perempuan dan anak-anak dan kepada pendatang yang ikut serta.'),(6038,5,9,1,'Ketika terdengar oleh raja-raja di sebelah barat sungai Yordan, di Pegunungan, di Daerah Bukit dan sepanjang tepi pantai Laut Besar sampai ke seberang gunung Libanon, yakni raja-raja orang Het, orang Amori, orang Kanaan, orang Feris, orang Hewi dan orang Yebus,'),(6039,5,9,2,'bergabunglah mereka dengan seia sekata untuk memerangi Yosua dan orang Israel.'),(6040,5,9,3,'Tetapi ketika terdengar kepada penduduk negeri Gibeon apa yang dilakukan Yosua terhadap Yerikho dan Ai,'),(6041,5,9,4,'maka merekapun bertindak dengan memakai akal: mereka pergi menyediakan bekal, mengambil karung yang buruk-buruk untuk dimuatkan ke atas keledai mereka dan kirbat anggur yang buruk-buruk, yang robek dan dijahit kembali,'),(6042,5,9,5,'dan kasut yang buruk-buruk dan ditambal untuk dikenakan pada kaki mereka dan pakaian yang buruk-buruk untuk dikenakan oleh mereka, sedang segala roti bekal mereka telah kering, tinggal remah-remah belaka.'),(6043,5,9,6,'Demikianlah mereka pergi kepada Yosua, ke tempat perkemahan di Gilgal. Berkatalah mereka kepadanya dan kepada orang-orang Israel itu: \"Kami ini datang dari negeri jauh; maka sekarang ikatlah perjanjian dengan kami.\"'),(6044,5,9,7,'Tetapi berkatalah orang-orang Israel kepada orang-orang Hewi itu: \"Barangkali kamu ini diam di tengah-tengah kami, bagaimana mungkin kami mengikat perjanjian dengan kamu?\"'),(6045,5,9,8,'Lalu kata mereka kepada Yosua: \"Kami ini hamba-hambamu.\" Tanya Yosua: \"Siapakah kamu ini dan dari manakah kamu datang?\"'),(6046,5,9,9,'Jawab mereka kepadanya: \"Dari negeri yang sangat jauh hamba-hambamu ini datang karena nama TUHAN, Allahmu, sebab kami telah mendengar kabar tentang Dia, yakni segala yang dilakukan-Nya di Mesir,'),(6047,5,9,10,'dan segala yang dilakukan-Nya terhadap kedua raja orang Amori itu di seberang sungai Yordan, Sihon, raja Hesybon, dan Og, raja Basan, yang diam di Asytarot.'),(6048,5,9,11,'Sebab itu para tua-tua kami dan seluruh penduduk negeri kami berkata kepada kami, demikian: Bawalah bekal untuk di jalan dan pergilah menemui mereka dan berkatalah kepada mereka: Kami ini hamba-hambamu, maka sekarang ikatlah perjanjian dengan kami.'),(6049,5,9,12,'Inilah roti kami: masih panas ketika kami bawa sebagai bekal dari rumah pada hari kami berangkat berjalan mendapatkan kamu, tetapi sekarang, lihatlah, telah kering dan tinggal remah-remah belaka.'),(6050,5,9,13,'Inilah kirbat-kirbat anggur, yang masih baru ketika kami mengisinya, tetapi lihatlah, telah robek; dan inilah pakaian dan kasut kami, semuanya telah buruk-buruk karena perjalanan yang sangat jauh itu.\"'),(6051,5,9,14,'Lalu orang-orang Israel mengambil bekal orang-orang itu, tetapi tidak meminta keputusan TUHAN.'),(6052,5,9,15,'Maka Yosua mengadakan persahabatan dengan mereka dan mengikat perjanjian dengan mereka, bahwa ia akan membiarkan mereka hidup; dan para pemimpin umat itu bersumpah kepada mereka.'),(6053,5,9,16,'Tetapi setelah lewat tiga hari, sesudah orang Israel mengikat perjanjian dengan orang-orang itu, terdengarlah oleh mereka, bahwa orang-orang itu tinggal dekat mereka, bahkan diam di tengah-tengah mereka.'),(6054,5,9,17,'Sebab orang Israel berangkat pergi dan pada hari ketiga sampai ke kota-kota orang-orang itu; adapun kota-kota itu ialah Gibeon, Kefira, Beerot dan Kiryat-Yearim.'),(6055,5,9,18,'Orang Israel tidak menewaskan, sebab para pemimpin umat telah bersumpah kepada mereka demi TUHAN, Allah Israel. Lalu bersungut-sungutlah segenap umat kepada para pemimpin.'),(6056,5,9,19,'Berkatalah pemimpin-pemimpin itu kepada seluruh umat: \"Kami telah bersumpah kepada mereka demi TUHAN, Allah Israel; oleh sebab itu kita tidak dapat mengusik mereka.'),(6057,5,9,20,'Beginilah akan kita perlakukan mereka: membiarkan mereka hidup, supaya kita jangan tertimpa murka karena sumpah yang telah kita ikrarkan itu kepada mereka.\"'),(6058,5,9,21,'Lagi kata para pemimpin kepada mereka: \"Biarlah mereka hidup.\" Maka merekapun dijadikan tukang belah kayu dan tukang timba air untuk segenap umat, seperti yang ditetapkan oleh para pemimpin mengenai mereka.'),(6059,5,9,22,'Lalu Yosua memanggil mereka dan berkata kepada mereka, demikian: \"Mengapa kamu menipu kami dengan berkata: Kami ini tinggal sangat jauh dari pada kamu, padahal kamu diam di tengah-tengah kami?'),(6060,5,9,23,'Oleh sebab itu, terkutuklah kamu dan tak putus-putusnya kamu menjadi hamba, tukang belah kayu dan tukang timba air untuk rumah Allahku.\"'),(6061,5,9,24,'Jawab mereka kepada Yosua, katanya: \"Sebab telah dikabarkan dengan sungguh-sungguh kepada hamba-hambamu ini, bahwa TUHAN, Allahmu, memerintahkan kepada Musa hamba-Nya, memberikan seluruh negeri itu kepadamu dan memunahkan seluruh penduduk negeri itu dari depan kamu, maka sangatlah kami takut kehilangan nyawa, menghadapi kamu; itulah sebabnya kami melakukan yang demikian.'),(6062,5,9,25,'Maka sekarang, kami ini dalam tanganmu; perlakukanlah kami seperti yang kaupandang baik dan benar untuk dilakukan kepada kami.\"'),(6063,5,9,26,'Demikianlah dilakukannya kepada mereka. Dilepaskannyalah mereka dari tangan orang-orang Israel, sehingga mereka tidak dibunuh.'),(6064,5,9,27,'Dan pada waktu itu Yosua menjadikan mereka tukang belah kayu dan tukang timba air untuk umat itu dan untuk mezbah TUHAN, sampai sekarang, di tempat yang akan dipilih-Nya.'),(6065,5,10,1,'Setelah terdengar oleh Adoni-Zedek, raja Yerusalem, bahwa Yosua telah merebut Ai dan telah menumpasnya--seperti yang dilakukannya terhadap Yerikho dan terhadap rajanya, demikianlah juga dilakukannya terhadap Ai dan terhadap rajanya--dan bahwa penduduk kota Gibeon telah mengadakan ikatan persahabatan dengan orang Israel dan diam di tengah-tengah mereka,'),(6066,5,10,2,'maka sangat takutlah orang, sebab Gibeon itu kota yang besar, seperti salah satu kota kerajaan, bahkan lebih besar dari Ai, dan semua orangnya adalah pahlawan.'),(6067,5,10,3,'Sebab itu Adoni-Zedek, raja Yerusalem, menyuruh orang kepada Hoham, raja Hebron, kepada Piream, raja Yarmut, kepada Yafia, raja Lakhis, dan kepada Debir, raja Eglon, mengatakan:'),(6068,5,10,4,'Datanglah kepadaku dan bantulah aku, supaya kita menggempur Gibeon, karena telah mengadakan ikatan persahabatan dengan Yosua dan orang Israel.'),(6069,5,10,5,'Lalu kelima raja orang Amori itu berkumpul dan bergerak maju: raja Yerusalem, raja Hebron, raja Yarmut, raja Lakhis dan raja Eglon, mereka beserta seluruh tentara mereka. Mereka berkemah mengepung Gibeon dan berperang melawannya.'),(6070,5,10,6,'Lalu orang-orang Gibeon itu menyuruh orang kepada Yosua, ke tempat perkemahan di Gilgal, mengatakan: \"Jangan menarik tanganmu dari pada hamba-hambamu ini. Datanglah dengan segera kepada kami, lepaskanlah kami dan bantulah kami, sebab semua raja orang Amori, yang diam di pegunungan, telah bergabung melawan kami.\"'),(6071,5,10,7,'Lalu Yosua bergerak maju dari Gilgal, dia dan seluruh tentara yang bersama-sama dengan dia, semuanya pahlawan yang gagah perkasa.'),(6072,5,10,8,'Berfirmanlah TUHAN kepada Yosua: \"Janganlah takut kepada mereka, sebab Aku menyerahkan mereka kepadamu. Tidak seorangpun dari mereka yang akan dapat bertahan menghadapi engkau.\"'),(6073,5,10,9,'Lalu Yosua menyerang mereka dengan tiba-tiba, setelah semalam-malaman bergerak maju dari Gilgal.'),(6074,5,10,10,'Dan TUHAN mengacaukan mereka di depan orang Israel, sehingga Yosua menimbulkan kekalahan yang besar di antara mereka dekat Gibeon, mengejar mereka ke arah pendakian Bet-Horon dan memukul mereka mundur sampai dekat Azeka dan Makeda.'),(6075,5,10,11,'Sedang mereka melarikan diri di depan orang Israel dan baru di lereng Bet-Horon, maka TUHAN melempari mereka dengan batu-batu besar dari langit, sampai ke Azeka, sehingga mereka mati. Yang mati kena hujan batu itu ada lebih banyak dari yang dibunuh oleh orang Israel dengan pedang.'),(6076,5,10,12,'Lalu Yosua berbicara kepada TUHAN pada hari TUHAN menyerahkan orang Amori itu kepada orang Israel; ia berkata di hadapan orang Israel: \"Matahari, berhentilah di atas Gibeon dan engkau, bulan, di atas lembah Ayalon!\"'),(6077,5,10,13,'Maka berhentilah matahari dan bulanpun tidak bergerak, sampai bangsa itu membalaskan dendamnya kepada musuhnya. Bukankah hal itu telah tertulis dalam Kitab Orang Jujur? Matahari tidak bergerak di tengah langit dan lambat-lambat terbenam kira-kira sehari penuh.'),(6078,5,10,14,'Belum pernah ada hari seperti itu, baik dahulu maupun kemudian, bahwa TUHAN mendengarkan permohonan seorang manusia secara demikian, sebab yang berperang untuk orang Israel ialah TUHAN.'),(6079,5,10,15,'Kemudian Yosua dan seluruh orang Israel yang menyertainya pulang kembali ke tempat perkemahan di Gilgal.'),(6080,5,10,16,'Kelima raja itu melarikan diri dan bersembunyi di dalam gua di Makeda.'),(6081,5,10,17,'Kepada Yosua dikabarkan, demikian: \"Kelima raja itu telah ditemukan bersembunyi di dalam gua di Makeda.\"'),(6082,5,10,18,'Lalu berkatalah Yosua: \"Gulingkanlah batu-batu yang besar ke mulut gua itu dan tempatkanlah di sana orang untuk menjaga mereka.'),(6083,5,10,19,'Tetapi kamu, janganlah kamu berhenti, kejarlah musuhmu dan hantamlah barisan belakangnya; janganlah biarkan mereka masuk ke dalam kota-kota mereka, sebab TUHAN, Allahmu, menyerahkan mereka kepadamu!\"'),(6084,5,10,20,'Setelah Yosua dan orang Israel selesai menimbulkan kekalahan yang besar sekali di antara mereka, sampai mereka dihancurkan sama sekali--beberapa orang dari mereka dapat lolos dan masuk ke kota-kota yang diperkuat--'),(6085,5,10,21,'pulanglah seluruh bangsa itu dengan selamat kepada Yosua ke tempat perkemahan, di Makeda. Tidak ada seorangpun yang berani melemparkan kata-kata ancaman terhadap orang Israel.'),(6086,5,10,22,'Kemudian berkatalah Yosua: \"Bukalah mulut gua dan keluarkanlah kelima raja itu dari dalam dan bawa kepadaku.\"'),(6087,5,10,23,'Dilakukan oranglah demikian, kelima raja itu dikeluarkan dari gua itu dan dibawa kepadanya: raja Yerusalem, raja Hebron, raja Yarmut, raja Lakhis dan raja Eglon.'),(6088,5,10,24,'Setelah raja-raja itu dikeluarkan dan dibawa kepada Yosua, maka Yosuapun memanggil semua orang Israel berkumpul dan berkata kepada para panglima tentara, yang ikut berperang bersama-sama dengan dia: \"Marilah dekat, taruhlah kakimu ke atas tengkuk raja-raja ini.\" Maka datanglah mereka dekat dan menaruh kakinya ke atas tengkuk raja-raja itu.'),(6089,5,10,25,'Lalu berkatalah Yosua kepada mereka: \"Janganlah takut dan janganlah tawar hati, kuatkan dan teguhkanlah hatimu, sebab secara itulah akan dilakukan TUHAN kepada semua musuhmu, yang kamu perangi.\"'),(6090,5,10,26,'Sesudah itu Yosua membunuh raja-raja itu, dan menggantung mereka pada lima tiang, dan mereka tinggal tergantung pada tiang-tiang itu sampai matahari terbenam.'),(6091,5,10,27,'Tetapi menjelang matahari terbenam, atas perintah Yosua mayat mereka diturunkan dari tiang-tiang itu, dan dilemparkan ke dalam gua, tempat mereka bersembunyi. Lalu mulut gua itu ditutupi orang dengan batu-batu besar, yang masih ada sampai sekarang.'),(6092,5,10,28,'Pada hari itu Yosua merebut Makeda dan kota itu dipukulnya dengan mata pedang, juga rajanya; kota itu dan semua makhluk yang ada di dalamnya ditumpasnya, tidak ada seorangpun yang dibiarkannya lolos, dan raja Makeda, diperlakukannya seperti telah diperlakukannya raja Yerikho.'),(6093,5,10,29,'Kemudian Yosua dengan seluruh Israel berjalan terus dari Makeda ke Libna, lalu memerangi Libna.'),(6094,5,10,30,'Dan TUHAN menyerahkan kota itu juga kepada orang Israel, beserta rajanya. Yosua memukul kota itu dan semua makhluk yang ada di dalamnya dengan mata pedang, tidak ada seorangpun di dalamnya yang dibiarkannya lolos, dan rajanya itu, diperlakukannya seperti telah diperlakukannya raja Yerikho.'),(6095,5,10,31,'Kemudian Yosua dengan seluruh Israel berjalan terus dari Libna ke Lakhis, lalu berkemah mengepung kota itu dan berperang melawannya.'),(6096,5,10,32,'Dan TUHAN menyerahkan Lakhis kepada orang Israel. Yosua merebut kota itu pada hari yang kedua. Kota itu dan semua makhluk yang ada di dalamnya dipukulnya dengan mata pedang, tepat seperti yang dilakukannya terhadap Libna.'),(6097,5,10,33,'Lalu Horam, raja Gezer, maju untuk membantu Lakhis, tetapi Yosua menewaskan dia dan rakyatnya, sehingga tidak ada seorangpun padanya yang dibiarkannya lolos.'),(6098,5,10,34,'Kemudian Yosua dengan seluruh Israel berjalan terus dari Lakhis ke Eglon, lalu mereka berkemah mengepung kota itu dan berperang melawannya.'),(6099,5,10,35,'Kota itu direbut mereka pada hari itu juga dan dipukul dengan mata pedang. Semua makhluk yang ada di dalamnya ditumpasnya pada hari itu, tepat seperti yang dilakukan terhadap Lakhis.'),(6100,5,10,36,'Kemudian Yosua dengan seluruh Israel bergerak maju dari Eglon ke Hebron, lalu berperang melawannya.'),(6101,5,10,37,'Negeri itu direbut mereka dan dipukul dengan mata pedang, juga rajanya dan segala kotanya dan semua makhluk yang ada di dalamnya, tidak seorangpun yang dibiarkannya lolos, tepat seperti yang dilakukannya terhadap Eglon. Kota itu dan semua makhluk yang ada di dalamnya ditumpasnya.'),(6102,5,10,38,'Kemudian Yosua dengan seluruh Israel kembali ke Debir, lalu berperang melawannya.'),(6103,5,10,39,'Negeri itu beserta rajanya dan segala kotanya direbutnya, dan dipukul dengan mata pedang. Semua makhluk yang ada di dalamnya ditumpas mereka, tidak seorangpun yang dibiarkannya lolos; seperti yang dilakukannya terhadap Hebron, demikianlah dilakukan terhadap Debir beserta rajanya, sama seperti yang dilakukannya terhadap Libna beserta rajanya.'),(6104,5,10,40,'Demikianlah Yosua mengalahkan seluruh negeri itu, Pegunungan, Tanah Negeb, Daerah Bukit dan Lereng Gunung, beserta semua raja mereka. Tidak seorangpun yang dibiarkannya lolos, tetapi ditumpasnya semua yang bernafas, seperti yang diperintahkan TUHAN, Allah Israel.'),(6105,5,10,41,'Yosua menewaskan mereka dari Kadesh-Barnea sampai Gaza, juga seluruh tanah Gosyen sampai Gibeon.'),(6106,5,10,42,'Semua raja ini dan negeri mereka telah dikalahkan Yosua sekaligus, sebab yang berperang untuk orang Israel ialah TUHAN, Allah Israel.'),(6107,5,10,43,'Kemudian Yosua dengan seluruh Israel pulang kembali ke tempat perkemahan di Gilgal.'),(6108,5,11,1,'Setelah hal itu terdengar kepada Yabin, raja Hazor, diutusnyalah orang kepada Yobab, raja Madon, dan kepada raja negeri Simron, kepada raja negeri Akhsaf,'),(6109,5,11,2,'serta kepada raja-raja yang di sebelah utara, di Pegunungan, di Araba-Yordan di sebelah selatan Kinerot, di Daerah Bukit dan di tanah bukit Dor di sebelah barat,'),(6110,5,11,3,'yakni raja-raja orang Kanaan di sebelah timur dan di sebelah barat, orang Amori, orang Het, orang Feris, orang Yebus di pegunungan dan orang Hewi di kaki gunung Hermon, di tanah Mizpa.'),(6111,5,11,4,'Kemudian keluarlah raja-raja ini bersama-sama semua tentaranya, amat banyak rakyat, seperti pasir di tepi laut banyaknya, beserta sangat banyak kuda dan kereta.'),(6112,5,11,5,'Raja-raja ini bersekutu dan datang berkemah bersama-sama dekat mata air Merom untuk memerangi orang Israel.'),(6113,5,11,6,'Lalu TUHAN berkata kepada Yosua: \"Janganlah takut menghadapi mereka, sebab besok kira-kira waktu ini Aku menyerahkan mereka mati terbunuh semuanya kepada orang Israel. Kuda mereka haruslah kamu lumpuhkan dan kereta mereka haruslah kamu bakar dengan api.\"'),(6114,5,11,7,'Lalu Yosua dengan seluruh tentaranya mendatangi mereka dengan tiba-tiba dekat mata air Merom, dan menyerbu mereka.'),(6115,5,11,8,'Dan TUHAN menyerahkan mereka kepada orang Israel. Mereka dikalahkan dan dikejar sampai Sidon-Besar dan sampai Misrefot-Maim, dan sampai lembah Mizpa yang di sebelah timur. Demikianlah mereka dihancurkan, sehingga tidak seorangpun dari mereka yang dibiarkan lolos.'),(6116,5,11,9,'Yosua melakukan terhadap mereka seperti yang difirmankan TUHAN kepadanya: kuda mereka dilumpuhkan dan kereta mereka dibakar dengan api.'),(6117,5,11,10,'Pada waktu itu Yosua kembali, direbutnya Hazor, dan rajanya dibunuhnya dengan mata pedang. Sebab Hazor pada waktu dahulu adalah yang terutama di antara segala kerajaan itu.'),(6118,5,11,11,'Semua makhluk yang ada di dalamnya dibunuhnya dengan mata pedang, sambil menumpas orang-orang itu. Tidak ada yang tinggal hidup dari semua yang bernafas dan Hazor dibakarnya.'),(6119,5,11,12,'Selanjutnya segala kota kepunyaan raja-raja itu dan semua rajanya dikalahkan Yosua dan dibunuhnya dengan mata pedang. Mereka ditumpasnya seperti yang diperintahkan Musa, hamba TUHAN itu.'),(6120,5,11,13,'Tetapi kota-kota yang letaknya di atas bukit-bukit puing tidaklah dibakar oleh orang Israel, hanya Hazor saja yang dibakar oleh Yosua.'),(6121,5,11,14,'Segala barang dari kota-kota itu serta ternaknya telah dijarah orang Israel. Tetapi manusia semuanya dibunuh mereka dengan mata pedang, sehingga orang-orang itu dipunahkan mereka. Tidak ada yang ditinggalkan hidup dari semua yang bernafas.'),(6122,5,11,15,'Seperti yang diperintahkan TUHAN kepada Musa, hamba-Nya itu, demikianlah diperintahkan Musa kepada Yosua dan seperti itulah dilakukan Yosua: tidak ada sesuatu yang diabaikannya dari segala yang diperintahkan TUHAN kepada Musa.'),(6123,5,11,16,'Demikianlah Yosua merebut seluruh negeri itu, pegunungan, seluruh Tanah Negeb, seluruh tanah Gosyen, Daerah Bukit, serta Araba-Yordan, dan Pegunungan Israel dengan tanah rendahnya;'),(6124,5,11,17,'mulai dari Pegunungan Gundul, yang mendaki ke arah Seir, sampai ke Baal-Gad di lembah gunung Libanon, di kaki gunung Hermon. Semua rajanya ditangkapnya, dan dibunuhnya.'),(6125,5,11,18,'Lama Yosua melakukan perang melawan semua raja itu.'),(6126,5,11,19,'Tidak ada satu kotapun yang mengadakan ikatan persahabatan dengan orang Israel, selain dari pada orang Hewi yang diam di Gibeon itu, semuanya telah direbut mereka dengan berperang.'),(6127,5,11,20,'Karena TUHAN yang menyebabkan hati orang-orang itu menjadi keras, sehingga mereka berperang melawan orang Israel, supaya mereka ditumpas, dan jangan dikasihani, tetapi dipunahkan, seperti yang diperintahkan TUHAN kepada Musa.'),(6128,5,11,21,'Pada waktu itu Yosua datang dan melenyapkan orang Enak dari pegunungan, dari Hebron, Debir dan Anab, dari seluruh pegunungan Yehuda dan dari seluruh pegunungan Israel. Mereka dan kota-kota mereka ditumpas oleh Yosua.'),(6129,5,11,22,'Tidak ada lagi orang Enak ditinggalkan hidup di negeri orang Israel; hanya di Gaza, di Gat dan di Asdod masih ada yang tertinggal.'),(6130,5,11,23,'Demikianlah Yosua merebut seluruh negeri itu sesuai dengan segala yang difirmankan TUHAN kepada Musa. Dan Yosuapun memberikan negeri itu kepada orang Israel menjadi milik pusaka mereka, menurut pembagian suku mereka. Lalu amanlah negeri itu, berhenti dari berperang.'),(6131,5,12,1,'Inilah raja-raja negeri yang dikalahkan oleh orang Israel dan yang negerinya diduduki mereka di seberang Yordan ke arah matahari terbit, dari sungai Arnon sampai gunung Hermon, serta seluruh Araba-Yordan ke arah timur:'),(6132,5,12,2,'yakni Sihon, raja orang Amori, yang diam di Hesybon, yang memerintah atas suatu daerah dari Aroer, yang letaknya di tepi sungai Arnon, pada pertengahan sungai itu, ditambah setengah Gilead, sampai sungai Yabok, batas daerah bani Amon,'),(6133,5,12,3,'dan atas Araba-Yordan sampai sebelah timur danau Kinerot dan sampai sebelah timur Laut Araba, yakni Laut Asin, ke arah Bet-Yesimot dan ke arah selatan sampai kaki lereng-lereng gunung Pisga.'),(6134,5,12,4,'Selanjutnya daerah Og, raja Basan, seorang dari sisa-sisa orang Refaim. Raja ini diam di Asytarot dan di Edrei,'),(6135,5,12,5,'dan memerintah atas gunung Hermon, atas Salkha dan atas seluruh Basan sampai daerah orang Gesur dan orang Maakha dan atas setengah Gilead, sampai daerah Sihon, raja Hesybon.'),(6136,5,12,6,'Musa, hamba TUHAN itu, beserta orang Israel, telah mengalahkan mereka, dan Musa, hamba TUHAN itu, telah memberikan daerah itu kepada orang Ruben, orang Gad dan suku Manasye yang setengah itu, menjadi milik mereka.'),(6137,5,12,7,'Inilah raja negeri yang dikalahkan oleh Yosua dan oleh orang Israel di sebelah barat sungai Yordan, dari Baal-Gad di lembah gunung Libanon sampai Pegunungan Gundul, yang mendaki ke arah Seir--negeri ini diberikan Yosua kepada suku-suku Israel menjadi miliknya, menurut pembagiannya,'),(6138,5,12,8,'di Pegunungan, di Daerah Bukit, di Araba-Yordan, di Lereng Gunung, di Padang Gurun dan di Tanah Negeb, yakni di negeri orang Het, orang Amori, orang Kanaan, orang Feris, orang Hewi dan orang Yebus--:'),(6139,5,12,9,'Raja negeri Yerikho, satu; raja negeri Ai, di sebelah Betel, satu;'),(6140,5,12,10,'raja negeri Yerusalem, satu; raja negeri Hebron, satu;'),(6141,5,12,11,'raja negeri Yarmut, satu; raja negeri Lakhis, satu;'),(6142,5,12,12,'raja negeri Eglon, satu; raja negeri Gezer, satu;'),(6143,5,12,13,'raja negeri Debir, satu; raja negeri Geder, satu;'),(6144,5,12,14,'raja negeri Horma, satu; raja negeri Arad, satu;'),(6145,5,12,15,'raja negeri Libna, satu; raja negeri Adulam, satu;'),(6146,5,12,16,'raja negeri Makeda, satu; raja negeri Betel, satu;'),(6147,5,12,17,'raja negeri Tapuah, satu; raja negeri Hefer, satu;'),(6148,5,12,18,'raja negeri Afek, satu; raja negeri Lasaron, satu;'),(6149,5,12,19,'raja negeri Madon, satu; raja negeri Hazor, satu;'),(6150,5,12,20,'raja negeri Simron Meron, satu; raja negeri Akhsaf, satu;'),(6151,5,12,21,'raja negeri Taanakh, satu; raja negeri Megido, satu;'),(6152,5,12,22,'raja negeri Kedesh, satu; raja negeri Yokneam di dekat gunung Karmel, satu;'),(6153,5,12,23,'raja negeri Dor di tanah bukit Dor, satu; raja negeri Goyim di Galilea, satu;'),(6154,5,12,24,'raja negeri Tirza, satu; jadi jumlah semua raja itu, tiga puluh satu orang.'),(6155,5,13,1,'Setelah Yosua menjadi tua dan lanjut umurnya, berfirmanlah TUHAN kepadanya: \"Engkau telah tua dan lanjut umur, dan dari negeri ini masih amat banyak yang belum diduduki.'),(6156,5,13,2,'Inilah negeri yang tertinggal: segenap wilayah orang Filistin dan seluruh negeri orang Gesur,'),(6157,5,13,3,'mulai dari sungai Sikhor di sebelah timur Mesir sampai ke daerah Ekron ke arah utara--semuanya itu terhitung tanah orang Kanaan--;ada lima raja kota orang Filistin, yakni di Gaza, di Asdod, di Askelon, di Gat dan di Ekron--;dan orang Awi'),(6158,5,13,4,'di sebelah selatan; seluruh negeri orang Kanaan dan Meara, kepunyaan orang Sidon, sampai ke Afek, sampai ke daerah orang Amori;'),(6159,5,13,5,'selanjutnya negeri orang Gebal dan seluruh gunung Libanon di sebelah matahari terbit, mulai dari Baal-Gad di kaki gunung Hermon sampai ke jalan yang menuju ke Hamat;'),(6160,5,13,6,'semua orang yang diam di pegunungan, mulai dari gunung Libanon sampai ke Misrefot-Maim; semua orang Sidon. Aku sendiri akan menghalau mereka dari depan orang Israel; hanya undikanlah dahulu negeri itu di antara orang Israel menjadi milik pusaka mereka, seperti yang Kuperintahkan kepadamu.'),(6161,5,13,7,'Oleh sebab itu, bagikanlah negeri ini kepada suku yang sembilan itu dan kepada suku Manasye yang setengah itu menjadi milik pusaka mereka.\"'),(6162,5,13,8,'Bersama-sama dengan suku Manasye yang setengah lagi, orang Ruben dan orang Gad telah menerima milik pusaka mereka, yang telah diberikan Musa kepada mereka di sebelah timur sungai Yordan, seperti yang ditentukan Musa, hamba TUHAN itu, kepada mereka,'),(6163,5,13,9,'yakni mulai dari Aroer, yang di tepi sungai Arnon, kota pada pertengahan sungai itu, dan seluruh dataran tinggi, dari Medeba sampai ke Dibon;'),(6164,5,13,10,'dan segala kota Sihon, raja orang Amori, yang berkerajaan di Hesybon, sampai ke daerah orang Amon;'),(6165,5,13,11,'selanjutnya Gilead dan daerah orang Gesur dan orang Maakha, serta seluruh gunung Hermon dan seluruh tanah Basan sampai ke Salkha,'),(6166,5,13,12,'seluruh kerajaan Og di Basan, yang memerintah di Asytarot dan Edrei, dialah yang tinggal hidup dari sisa-sisa orang Refaim yang telah dikalahkan dan dihalau oleh Musa.'),(6167,5,13,13,'Tetapi orang Israel tidak menghalau orang Gesur dan orang Maakha itu, sehingga Gesur dan Maakha masih tetap diam di tengah-tengah Israel sampai sekarang.'),(6168,5,13,14,'Hanya kepada suku Lewi tidak diberikan milik pusaka: yang menjadi milik pusakanya ialah TUHAN, Allah Israel, seperti yang dijanjikan-Nya kepada mereka.'),(6169,5,13,15,'Kepada suku bani Ruben, menurut kaum-kaum mereka, telah diberikan Musa bagian ini:'),(6170,5,13,16,'daerah kepunyaan mereka mulai dari Aroer yang di tepi sungai Arnon, kota pada pertengahan sungai itu, dan seluruh dataran tinggi dekat Medeba;'),(6171,5,13,17,'Hesybon dan segala kotanya, yang di dataran tinggi, yakni Dibon, Bamot-Baal, Bet-Baal-Meon,'),(6172,5,13,18,'Yahas, Kedemot, Mefaat,'),(6173,5,13,19,'Kiryataim, Sibma, Zeret-Hasahar di atas gunung di lembah itu,'),(6174,5,13,20,'Bet-Peor, lereng-lereng gunung Pisga, Bet-Yesimot,'),(6175,5,13,21,'selanjutnya segala kota di dataran tinggi itu dan seluruh kerajaan Sihon, raja orang Amori, yang memerintah di Hesybon, yang dikalahkan oleh Musa; bersama-sama dia juga dikalahkannya raja-raja Midian, yakni Ewi, Rekem, Zur, Hur dan Reba, raja-raja bawahan Sihon, penduduk negeri itu.'),(6176,5,13,22,'Juga Bileam bin Beor, juru tenung itu, telah dibunuh oleh orang Israel dengan pedang, beserta orang-orang yang telah mati tertikam oleh mereka.'),(6177,5,13,23,'Jadi batas daerah bani Ruben ialah sungai Yordan dengan daerah pinggirnya. Itulah milik pusaka bani Ruben menurut kaum-kaum mereka, yakni kota-kota dengan desa-desanya.'),(6178,5,13,24,'Kepada suku Gad, yakni kepada bani Gad menurut kaum-kaum mereka, telah diberikan Musa bagian ini:'),(6179,5,13,25,'daerah kepunyaan mereka ialah Yaezer dan segala kota negeri Gilead, serta setengah negeri orang Amon sampai ke Aroer, yang di sebelah timur Raba,'),(6180,5,13,26,'yakni mulai dari Hesybon sampai ke Ramat-Mizpa dan Betonim, dan dari Mahanaim sampai ke daerah Debir;'),(6181,5,13,27,'dan di lembah: Bet-Haram, Bet-Nimra, Sukot dan Zafon, sisa kerajaan Sihon, raja Hesybon; sungai Yordan dengan daerah pinggirnya, sampai ke ujung danau Kineret, di sebelah timur sungai Yordan.'),(6182,5,13,28,'Itulah milik pusaka bani Gad menurut kaum-kaum mereka, yakni kota-kota dengan desa-desanya.'),(6183,5,13,29,'Kepada suku Manasye yang setengah itu, yakni kepada puak bani Manasye yang setengah itu, menurut kaum-kaum mereka, telah diberikan Musa bagian ini:'),(6184,5,13,30,'daerah mereka mulai dari Mahanaim: seluruh Basan, seluruh kerajaan Og, raja Basan, dan segenap Hawot-Yair, yang di Basan itu, enam puluh kota banyaknya;'),(6185,5,13,31,'selanjutnya setengah Gilead, Asytarot dan Edrei, kota-kota kerajaan Og di Basan, menjadi bagian bani Makhir, anak Manasye, yaitu bagian setengah bani Makhir menurut kaum-kaum mereka.'),(6186,5,13,32,'Itulah semuanya yang dibagikan Musa sebagai milik pusaka di dataran Moab di sebelah timur sungai Yordan dekat Yerikho.'),(6187,5,13,33,'Tetapi kepada suku Lewi Musa tidak memberikan milik pusaka: TUHAN, Allah Israel, Dialah yang menjadi milik pusaka mereka, seperti yang dijanjikan-Nya kepada mereka.'),(6188,5,14,1,'Inilah semuanya yang diterima oleh orang Israel sebagai milik pusaka di tanah Kanaan, yang telah dibagikan kepada orang Israel oleh imam Eleazar, dan Yosua bin Nun dan para kepala kaum keluarga dari suku-suku mereka,'),(6189,5,14,2,'dengan mengundi milik pusaka itu, seperti yang diperintahkan TUHAN dengan perantaraan Musa mengenai suku-suku yang sembilan setengah itu.'),(6190,5,14,3,'Sebab kepada suku-suku yang dua setengah lagi telah diberikan Musa milik pusaka di seberang sungai Yordan, tetapi kepada orang Lewi tidak diberikannya milik pusaka di tengah-tengah mereka.'),(6191,5,14,4,'Sebab bani Yusuf merupakan dua suku, Manasye dan Efraim. Maka kepada orang Lewi tidak diberikan bagiannya di negeri itu, selain dari kota-kota untuk didiami, dengan tanah penggembalaannya untuk ternak dan hewan mereka.'),(6192,5,14,5,'Seperti yang diperintahkan TUHAN kepada Musa, demikianlah diperbuat oleh orang Israel dan dibagi-bagi merekalah negeri itu.'),(6193,5,14,6,'Bani Yehuda datang menghadap Yosua di Gilgal. Pada waktu itu berkatalah Kaleb bin Yefune, orang Kenas itu, kepadanya: \"Engkau tahu firman yang diucapkan TUHAN kepada Musa, abdi Allah itu, tentang aku dan tentang engkau di Kadesh-Barnea.'),(6194,5,14,7,'Aku berumur empat puluh tahun, ketika aku disuruh Musa, hamba TUHAN itu, dari Kadesh-Barnea untuk mengintai negeri ini; dan aku pulang membawa kabar kepadanya yang sejujur-jujurnya.'),(6195,5,14,8,'Sedang saudara-saudaraku, yang bersama-sama pergi ke sana dengan aku, membuat tawar hati bangsa itu, aku tetap mengikuti TUHAN, Allahku, dengan sepenuh hati.'),(6196,5,14,9,'Pada waktu itu Musa bersumpah, katanya: Sesungguhnya tanah yang diinjak oleh kakimu itu akan menjadi milik pusakamu dan anak-anakmu sampai selama-lamanya, sebab engkau tetap mengikuti TUHAN, Allahku, dengan sepenuh hati.'),(6197,5,14,10,'Jadi sekarang, sesungguhnya TUHAN telah memelihara hidupku, seperti yang dijanjikan-Nya. Kini sudah empat puluh lima tahun lamanya, sejak diucapkan TUHAN firman itu kepada Musa, dan selama itu orang Israel mengembara di padang gurun. Jadi sekarang, telah berumur delapan puluh lima tahun aku hari ini;'),(6198,5,14,11,'pada waktu ini aku masih sama kuat seperti pada waktu aku disuruh Musa; seperti kekuatanku pada waktu itu demikianlah kekuatanku sekarang untuk berperang dan untuk keluar masuk.'),(6199,5,14,12,'Oleh sebab itu, berikanlah kepadaku pegunungan, yang dijanjikan TUHAN pada waktu itu, sebab engkau sendiri mendengar pada waktu itu, bahwa di sana ada orang Enak dengan kota-kota yang besar dan berkubu. Mungkin TUHAN menyertai aku, sehingga aku menghalau mereka, seperti yang difirmankan TUHAN.\"'),(6200,5,14,13,'Lalu Yosua memberkati Kaleb bin Yefune, dan diberikannyalah Hebron kepadanya menjadi milik pusakanya.'),(6201,5,14,14,'Itulah sebabnya Hebron menjadi milik pusaka Kaleb bin Yefune, orang Kenas itu, sampai sekarang ini, karena ia tetap mengikuti TUHAN, Allah Israel, dengan sepenuh hati.'),(6202,5,14,15,'Nama Hebron dahulu ialah Kiryat-Arba; Arba ialah orang yang paling besar di antara orang Enak. Dan amanlah negeri itu, berhenti berperang.'),(6203,5,15,1,'Bagian yang diundikan kepada suku bani Yehuda menurut kaum-kaum mereka adalah sampai ke batas tanah Edom, sampai ke padang gurun Zin ke selatan, di ujung selatan.'),(6204,5,15,2,'Batas selatan bagi mereka mulai dari ujung Laut Asin, dari teluk yang menghadap ke selatan;'),(6205,5,15,3,'lalu keluar pada sebelah selatan pendakian Akrabim, terus ke Zin, naik ke sebelah selatan Kadesh-Barnea, terus ke Hezron, naik ke Adar, membelok ke Karka,'),(6206,5,15,4,'terus ke Azmon dan keluar pada sungai tanah Mesir, sehingga batas itu berakhir ke laut. Itulah bagi kamu batas selatan.'),(6207,5,15,5,'Batas timur ialah Laut Asin sampai ke muara sungai Yordan. Batas pada sisi utara mulai dari teluk di muara sungai Yordan itu:'),(6208,5,15,6,'batas itu naik ke Bet-Hogla, melalui sebelah utara Bet-Araba, kemudian batas itu naik ke batu Bohan bin Ruben;'),(6209,5,15,7,'lalu dari lembah Akhor batas itu naik ke Debir, dan menuju ke utara ke Gilgal di seberang pendakian Adumim, yang di sebelah selatan sungai. Kemudian batas itu terus ke mata air En-Semes dan keluar ke En-Rogel.'),(6210,5,15,8,'Kemudian batas itu naik ke lembah Ben-Hinom, di sebelah selatan sepanjang lereng gunung Yebus, itulah Yerusalem; kemudian batas itu naik ke puncak gunung yang di seberang lembah Hinom, di sebelah barat, di ujung utara lembah orang Refaim.'),(6211,5,15,9,'Kemudian batas itu melengkung dari puncak gunung itu ke mata air Me-Neftoah, lalu keluar ke kota-kota pegunungan Efron; selanjutnya batas itu melengkung ke Baala, itulah Kiryat-Yearim.'),(6212,5,15,10,'Kemudian batas itu membelok dari Baala ke barat ke pegunungan Seir, terus ke utara ke lereng gunung Yearim, itulah Kesalon, turun ke Bet-Semes dan terus ke Timna.'),(6213,5,15,11,'Kemudian batas itu keluar ke utara ke lereng gunung Ekron; kemudian batas itu melengkung ke Sikron, terus ke gunung Baala dan keluar dekat Yabneel, sehingga batas itu berakhir di laut.'),(6214,5,15,12,'Batas barat ialah Laut Besar dan pantainya. Itulah, ke segala penjuru, batas-batas daerah bani Yehuda menurut kaum-kaum mereka.'),(6215,5,15,13,'Tetapi kepada Kaleb bin Yefune diberikan Yosua sebagian di tengah-tengah bani Yehuda itu, yakni Kiryat-Arba, seperti yang dititahkan TUHAN kepadanya; Arba ialah bapa Enak. Itulah Hebron.'),(6216,5,15,14,'Dan Kaleb menghalau dari sana ketiga orang Enak, yakni Sesai, Ahiman dan Talmai, anak-anak Enak.'),(6217,5,15,15,'Dari sana ia maju menyerang penduduk Debir. Nama Debir itu dahulu ialah Kiryat-Sefer.'),(6218,5,15,16,'Lalu berkatalah Kaleb: \"Siapa yang menggempur Kiryat-Sefer dan merebutnya, kepadanya akan kuberikan Akhsa, anakku, menjadi isterinya.\"'),(6219,5,15,17,'Dan Otniel, anak Kenas saudara Kaleb, merebut kota itu; lalu Kaleb memberikan kepadanya Akhsa, anaknya, menjadi isterinya.'),(6220,5,15,18,'Ketika perempuan itu tiba, dibujuknya suaminya untuk meminta ladang kepada ayahnya. Maka turunlah perempuan itu dari keledainya, lalu berkatalah Kaleb kepadanya: \"Ada apa?\"'),(6221,5,15,19,'Jawabnya: \"Berikanlah kepadaku hadiah; telah kauberikan kepadaku tanah yang gersang, berikanlah juga kepadaku mata air.\" Lalu diberikannyalah kepadanya mata air yang di hulu dan mata air yang di hilir.'),(6222,5,15,20,'Inilah milik pusaka suku bani Yehuda menurut kaum-kaum mereka.'),(6223,5,15,21,'Kota-kota suku bani Yehuda yang paling ujung, dekat batas tanah Edom di Tanah Negeb, ialah Kabzeel, Eder, Yagur,'),(6224,5,15,22,'Kina, Dimona, Adada,'),(6225,5,15,23,'Kedesh, Hazor, Yitnan,'),(6226,5,15,24,'Zif, Telem, Bealot,'),(6227,5,15,25,'Hazor-Hadata, Keriot-Hezron, itulah Hazor;'),(6228,5,15,26,'Amam, Sema, Molada,'),(6229,5,15,27,'Hazar-Gada, Hesmon, Bet-Pelet,'),(6230,5,15,28,'Hazar-Sual, Bersyeba dan segala anak kotanya,'),(6231,5,15,29,'Baala, Iyim, Ezem,'),(6232,5,15,30,'Eltolad, Kesil, Horma,'),(6233,5,15,31,'Ziklag, Madmana, Sansana,'),(6234,5,15,32,'Lebaot, Silhim, Ain dan Rimon; seluruhnya dua puluh sembilan kota dengan desa-desanya.'),(6235,5,15,33,'Di Daerah Bukit: Esytaol, Zora, Asna,'),(6236,5,15,34,'Zanoah, En-Ganim, Tapuah, Enam,'),(6237,5,15,35,'Yarmut, Adulam, Sokho, Azeka,'),(6238,5,15,36,'Saaraim, Aditaim, Gedera dan Gederotaim; empat belas kota dengan desa-desanya.'),(6239,5,15,37,'Zenan, Hadasa, Migdal-Gad,'),(6240,5,15,38,'Dilean, Mizpa, Yokteel,'),(6241,5,15,39,'Lakhis, Bozkat, Eglon,'),(6242,5,15,40,'Kabon, Lahmas, Kitlis,'),(6243,5,15,41,'Gederot, Bet-Dagon, Naama dan Makeda; enam belas kota dengan desa-desanya.'),(6244,5,15,42,'Libna, Eter, Asan,'),(6245,5,15,43,'Yiftah, Asna, Nezib,'),(6246,5,15,44,'Kehila, Akhzib dan Maresa; sembilan kota dengan desa-desanya.'),(6247,5,15,45,'Ekron dengan segala anak kota dan desanya.'),(6248,5,15,46,'Mulai dari Ekron sampai ke laut, segala yang di sisi Asdod dan desa-desanya,'),(6249,5,15,47,'Asdod dengan segala anak kota dan desa-desanya, Gaza dengan segala anak kota dan desa-desanya, sampai ke sungai tanah Mesir, Laut Besar dan pantainya.'),(6250,5,15,48,'Lagi di Pegunungan: Samir, Yatir, Sokho,'),(6251,5,15,49,'Dana, Kiryat-Sana, itulah Debir;'),(6252,5,15,50,'Anab, Estemo, Anim,'),(6253,5,15,51,'Gosyen, Holon dan Gilo; sebelas kota dengan desa-desanya;'),(6254,5,15,52,'Arab, Duma, Esan,'),(6255,5,15,53,'Yanum, Bet-Tapuah, Afeka,'),(6256,5,15,54,'Humta, Kiryat-Arba, itulah Hebron, dan Zior; sembilan kota dengan desa-desanya.'),(6257,5,15,55,'Maon, Karmel, Zif, Yuta,'),(6258,5,15,56,'Yizreel, Yokdeam, Zanoah,'),(6259,5,15,57,'Kain, Gibea dan Timna; sepuluh kota dengan desa-desanya.'),(6260,5,15,58,'Halhul, Bet-Zur, Gedor,'),(6261,5,15,59,'Maarat, Bet-Anot dan Eltekon; enam kota dengan desa-desanya.'),(6262,5,15,60,'Kiryat-Baal, itulah Kiryat-Yearim, dan Raba; dua kota dengan desa-desanya.'),(6263,5,15,61,'Di padang gurun: Bet-Araba, Midin, Sekhakha,'),(6264,5,15,62,'Nibsan, Kota Garam dan En-Gedi; enam kota dengan desa-desanya.'),(6265,5,15,63,'Tetapi orang Yebus, penduduk kota Yerusalem, tidak dapat dihalau oleh bani Yehuda. Jadi orang Yebus itu masih tetap diam bersama-sama dengan bani Yehuda di Yerusalem sampai sekarang.'),(6266,5,16,1,'Kemudian keluarlah undian bagi bani Yusuf. Batas mereka mulai dari sungai Yordan dekat Yerikho, di sebelah timur mata-mata air Yerikho, melalui padang gurun, yang naik dari Yerikho ke pegunungan, ke Betel,'),(6267,5,16,2,'keluar dari Betel menuju ke Lus, terus ke daerah orang Arki, ke Atarot,'),(6268,5,16,3,'turun ke barat ke daerah orang Yaflet, sampai daerah Bet-Horon Hilir dan selanjutnya sampai Gezer dan berakhir di laut.'),(6269,5,16,4,'Demikianlah bani Yusuf, yakni suku Manasye dan suku Efraim, menerima milik pusaka.'),(6270,5,16,5,'Daerah bani Efraim menurut kaum-kaum mereka ialah demikian: batas milik pusaka mereka ke timur ialah Atarot-Adar sampai Bet-Horon Hulu.'),(6271,5,16,6,'Ke barat batas itu keluar di sebelah utara Mikhmetat; kemudian batas itu membelok ke timur ke Taanat-Silo, dan dari sana terus sampai ke sebelah timur Yanoah,'),(6272,5,16,7,'turun dari Yanoah ke Atarot dan Naharat, menyinggung daerah Yerikho dan keluar ke sungai Yordan.'),(6273,5,16,8,'Dari Tapuah batas itu terus ke barat ke sungai Kana dan berakhir ke laut. Itulah milik pusaka suku bani Efraim menurut kaum-kaum mereka.'),(6274,5,16,9,'Selanjutnya bagi bani Efraim ditentukan kota-kota terpisah di tengah-tengah milik pusaka bani Manasye, segala kota itu dengan desa-desanya.'),(6275,5,16,10,'Tetapi orang Kanaan yang diam di Gezer tidaklah dihalau mereka. Jadi orang Kanaan itu masih tetap tinggal di tengah-tengah suku Efraim sampai sekarang, tetapi menjadi budak rodi.'),(6276,5,17,1,'Bagian undian yang ditentukan bagi suku Manasye, sebab dialah anak sulung Yusuf, ialah demikian: Kepada Makhir, anak sulung Manasye, bapa Gilead, telah diberikan Gilead dan Basan, karena ia seorang tentara.'),(6277,5,17,2,'Jadi bagian itulah yang ditentukan bagi anak-anak Manasye yang lain, menurut kaum-kaum mereka, yakni bagi bani Abiezer, bani Helek, bani Asriel, bani Sekhem, bani Hefer dan bani Semida; itulah keturunan yang laki-laki dari Manasye bin Yusuf, menurut kaum-kaum mereka.'),(6278,5,17,3,'Tetapi Zelafehad bin Hefer bin Gilead bin Makhir bin Manasye tidak mempunyai anak laki-laki, tetapi hanya anak perempuan. Inilah nama anak-anaknya itu: Mahla, Noa, Hogla, Milka dan Tirza.'),(6279,5,17,4,'Mereka datang menghadap imam Eleazar, dan menghadap Yosua bin Nun, serta para pemimpin, dan berkata: \"TUHAN telah memerintahkan kepada Musa untuk memberikan milik pusaka kepada kami di tengah-tengah saudara-saudara kami.\" Sebab itu diberikannya kepada mereka, milik pusaka di tengah-tengah saudara-saudara ayah mereka, sesuai dengan titah TUHAN.'),(6280,5,17,5,'Demikianlah kepada suku Manasye jatuh sepuluh bidang tanah, selain dari tanah Gilead dan Basan yang di seberang Yordan,'),(6281,5,17,6,'sebab anak-anak perempuan Manasye telah menerima milik pusaka di tengah-tengah anak-anaknya lelaki, sedang tanah Gilead ditentukan bagi anak-anak lelaki Manasye yang lain.'),(6282,5,17,7,'Jadi batas daerah Manasye ialah dari Asyer ke Mikhmetat yang di sebelah timur Sikhem; kemudian batas itu terus ke selatan ke daerah penduduk En-Tapuah.'),(6283,5,17,8,'Tanah Tapuah adalah kepunyaan suku Manasye, tetapi kota Tapuah, dekat batas daerah Manasye, adalah kepunyaan bani Efraim.'),(6284,5,17,9,'Kemudian batas itu turun ke sungai Kana, ke sebelah selatan sungai itu. Kota-kota di sana adalah kepunyaan suku Efraim, sekalipun di tengah-tengah kota-kota suku Manasye. Selanjutnya batas daerah Manasye itu lewat dari sebelah utara sungai itu dan berakhir ke laut.'),(6285,5,17,10,'Ke selatan daerah itu kepunyaan suku Efraim dan ke utara kepunyaan suku Manasye. Batasnya ialah laut; di sebelah utara batas daerah itu menyinggung daerah Asyer, di sebelah timur menyinggung daerah Isakhar.'),(6286,5,17,11,'Tetapi di daerah Isakhar dan Asyer ada juga kepunyaan suku Manasye, yakni Bet-Sean dengan segala anak kotanya, Yibleam dengan segala anak kotanya, penduduk Dor dengan segala anak kotanya, penduduk En-Dor dengan segala anak kotanya, penduduk Taanakh dengan segala anak kotanya, dan penduduk Megido dengan segala anak kotanya, yakni ketiga daerah bukit itu.'),(6287,5,17,12,'Tetapi bani Manasye tidak dapat menduduki kota-kota itu, sebab orang Kanaan berhasil untuk tetap diam di negeri itu.'),(6288,5,17,13,'Setelah orang Israel menjadi kuat, orang Kanaan itu dibuatnya menjadi orang rodi, tetapi tidaklah sama sekali mereka itu dihalaunya.'),(6289,5,17,14,'Berkatalah bani Yusuf kepada Yosua, demikian: \"Mengapa engkau memberikan kepadaku hanya satu bagian undian dan satu bidang tanah saja menjadi milik pusaka, padahal aku ini bangsa yang banyak jumlahnya, karena TUHAN sampai sekarang memberkati aku?\"'),(6290,5,17,15,'Jawab Yosua kepada mereka: \"Kalau engkau bangsa yang banyak jumlahnya, pergilah ke hutan dan bukalah tanah bagimu di sana di negeri orang Feris dan orang Refaim, jika pegunungan Efraim terlalu sesak bagimu.\"'),(6291,5,17,16,'Kemudian berkatalah bani Yusuf: \"Pegunungan itu tidak cukup bagi kami, dan semua orang Kanaan yang diam di dataran itu mempunyai kereta besi, baik yang diam di Bet-Sean dengan segala anak kotanya maupun yang diam di lembah Yizreel.\"'),(6292,5,17,17,'Lalu berkatalah Yosua kepada keturunan Yusuf, kepada suku Efraim dan suku Manasye: \"Engkau ini bangsa yang banyak jumlahnya dan mempunyai kekuatan yang besar; tidak hanya satu bagian undian ditentukan bagimu,'),(6293,5,17,18,'tetapi pegunungan itu akan ditentukan bagimu juga, dan karena tanah itu hutan, haruslah kamu membukanya; kamu akan memilikinya sampai kepada ujung-ujungnya, sebab kamu akan menghalau orang Kanaan itu, sekalipun mereka mempunyai kereta besi dan sekalipun mereka kuat.\"'),(6294,5,18,1,'Maka berkumpullah segenap umat Israel di Silo, lalu mereka menempatkan Kemah Pertemuan di sana, karena negeri itu telah takluk kepada mereka.'),(6295,5,18,2,'Pada waktu itu masih tinggal tujuh suku di antara orang Israel, yang belum mendapat bagian milik pusaka.'),(6296,5,18,3,'Sebab itu berkatalah Yosua kepada orang Israel: \"Berapa lama lagi kamu bermalas-malas, sehingga tidak pergi menduduki negeri yang telah diberikan kepadamu oleh TUHAN, Allah nenek moyangmu?'),(6297,5,18,4,'Ajukanlah tiga orang dari tiap-tiap suku; maka aku akan menyuruh mereka, supaya mereka bersiap untuk menjelajahi negeri itu, mencatat keadaannya, sekadar milik pusaka masing-masing, kemudian kembali kepadaku.'),(6298,5,18,5,'Sesudah itu mereka akan membaginya di antara mereka menjadi tujuh bagian. Suku Yehuda akan tetap tinggal dalam daerahnya di sebelah selatan dan keturunan Yusuf akan tetap tinggal dalam daerahnya di sebelah utara.'),(6299,5,18,6,'Kamu catat keadaan negeri itu dalam tujuh bagian dan kamu bawa ke mari kepadaku; lalu aku akan membuang undi di sini bagi kamu di hadapan TUHAN, Allah kita.'),(6300,5,18,7,'Sebab orang Lewi tidak mendapat bagian di tengah-tengah kamu, karena jabatan sebagai imam TUHAN ialah milik pusaka mereka, sedang suku Gad, suku Ruben dan suku Manasye yang setengah itu telah menerima milik pusaka di sebelah timur sungai Yordan, yang diberikan kepada mereka oleh Musa, hamba TUHAN.\"'),(6301,5,18,8,'Kemudian bersiaplah orang-orang itu, lalu pergi, sedang Yosua memerintahkan kepada mereka, pada waktu mereka berangkat, supaya mereka mencatat keadaan negeri itu, katanya: \"Pergilah, jelajahilah negeri itu, catatkanlah keadaannya, kemudian kembalilah kepadaku; maka di sini, di Silo, aku akan membuang undi bagi kamu di hadapan TUHAN.\"'),(6302,5,18,9,'Orang-orang itu pergi dan berjalan melalui negeri itu; mereka mencatat keadaannya dalam suatu daftar, kota demi kota, dalam tujuh bagian, lalu kembali kepada Yosua ke tempat perkemahan di Silo.'),(6303,5,18,10,'Lalu Yosua membuang undi bagi mereka di Silo, di hadapan TUHAN, dan di sanalah Yosua membagikan negeri itu kepada orang Israel, sesuai dengan pembagian mereka.'),(6304,5,18,11,'Maka keluarlah undian suku bani Benyamin menurut kaum-kaum mereka, dan daerah yang diundikan kepada mereka terdapat antara daerah bani Yehuda dan daerah bani Yusuf.'),(6305,5,18,12,'Batas pada sisi utara bagi mereka mulai dari sungai Yordan; kemudian batas itu naik ke lereng gunung di sebelah utara Yerikho, naik ke barat ke pegunungan, dan menuju ke padang gurun Bet-Awen.'),(6306,5,18,13,'Dari sana batas itu terus ke Lus, ke selatan, ke arah lereng gunung dekat Lus, itulah Betel; kemudian batas itu turun ke Atarot-Adar di pegunungan yang di sebelah selatan Bet-Horon Hilir.'),(6307,5,18,14,'Kemudian batas itu melengkung, lalu membelok dari pegunungan yang di seberang Bet-Horon di sebelah selatan menuju ke sisi barat daya, dan berakhir di Kiryat-Baal, itulah Kiryat-Yearim, suatu kota bani Yehuda. Itulah sisi barat.'),(6308,5,18,15,'Sisi selatan mulai dari ujung Kiryat-Yearim, kemudian batas itu menuju ke barat, dan menuju pula ke mata air Me-Neftoah.'),(6309,5,18,16,'Selanjutnya batas itu turun ke ujung pegunungan yang di tentangan lebak Ben-Hinom, di sebelah utara lembah orang Refaim; kemudian turun ke lebak Hinom, sepanjang lereng gunung Yebus, ke selatan, kemudian turun ke En-Rogel.'),(6310,5,18,17,'Kemudian melengkung ke utara, menuju ke En-Semes dan menuju pula ke Gelilot di seberang pendakian Adumim, turun ke batu Bohan bin Ruben,'),(6311,5,18,18,'terus ke lereng gunung di seberang Bet-Araba ke utara, dan turun ke Araba-Yordan.'),(6312,5,18,19,'Kemudian batas itu terus ke lereng gunung dekat Bet-Hogla ke utara dan batas itu berakhir ke teluk utara Laut Asin, ke muara sungai Yordan di sebelah selatan. Itulah batas selatan.'),(6313,5,18,20,'Sungai Yordan ialah batasnya pada sisi timur. Itulah milik pusaka bani Benyamin dengan batas-batasnya ke segala penjuru menurut kaum-kaum mereka.'),(6314,5,18,21,'Kota-kota suku bani Benyamin menurut kaum-kaum mereka ialah Yerikho, Bet-Hogla, Emek-Kezis,'),(6315,5,18,22,'Bet-Araba, Zemaraim, Betel,'),(6316,5,18,23,'Haawim, Para, Ofra,'),(6317,5,18,24,'Kefar-Haamonai, Ofni dan Geba; dua belas kota dengan desa-desanya.'),(6318,5,18,25,'Gibeon, Rama, Beerot,'),(6319,5,18,26,'Mizpa, Kefira, Moza,'),(6320,5,18,27,'Rekem, Yirpeel, Tarala,'),(6321,5,18,28,'Zela, Elef dan Yebus, ialah Yerusalem, Gibeat dan Kiryat; empat belas kota dengan desa-desanya. Itulah milik pusaka bani Benyamin menurut kaum-kaum mereka.'),(6322,5,19,1,'Undian yang keduapun keluarlah bagi suku Simeon, untuk suku bani Simeon menurut kaum-kaum mereka. Milik pusaka mereka ada di tengah-tengah milik pusaka bani Yehuda.'),(6323,5,19,2,'Sebagai milik warisan mereka menerima: Bersyeba, Syeba, Molada,'),(6324,5,19,3,'Hazar-Sual, Bala, Ezem,'),(6325,5,19,4,'Eltolad, Betul, Horma,'),(6326,5,19,5,'Ziklag, Bet-Hamarkabot, Hazar-Susa,'),(6327,5,19,6,'Bet-Lebaot dan Saruhen: tiga belas kota dengan desa-desanya.'),(6328,5,19,7,'En-Rimon, Eter, dan Asan: empat kota dengan desa-desanya;'),(6329,5,19,8,'juga segala desa di sekeliling kota-kota tadi, sampai ke Baalat-Beer, yakni Rama yang di Tanah Selatan. Itulah milik pusaka suku bani Simeon menurut kaum-kaum mereka.'),(6330,5,19,9,'Milik pusaka bani Simeon diambil dari bagian bani Yehuda. Karena bagian bani Yehuda itu terlalu besar bagi mereka, maka bani Simeon menerima milik pusaka di tengah-tengah mereka.'),(6331,5,19,10,'Undian yang ketigapun keluarlah bagi bani Zebulon menurut kaum-kaum mereka. Batas milik pusaka mereka sampai ke Sarid.'),(6332,5,19,11,'Ke sebelah barat batas mereka itu naik ke Marala, menyinggung Dabeset, kemudian menyinggung sungai yang mengalir lewat Yokneam.'),(6333,5,19,12,'Dari Sarid batas itu berbalik ke timur, ke arah matahari terbit, melalui daerah Kislot-Tabor, menuju Dobrat, naik ke Yafia;'),(6334,5,19,13,'dari sana terus ke timur, ke arah matahari terbit, ke Gat-Hefer, ke Et-Kazin, menuju ke Rimon, dan melengkung ke Nea.'),(6335,5,19,14,'Kemudian batas itu membelok mengelilinginya di sebelah utara Hanaton, dan berakhir di lembah Yiftah-El.'),(6336,5,19,15,'Lagi Katat, Nahalal, Simron, Yidala dan Betlehem; dua belas kota dengan desa-desanya.'),(6337,5,19,16,'Itulah milik pusaka bani Zebulon menurut kaum-kaum mereka; kota-kota tadi dengan desa-desanya.'),(6338,5,19,17,'Bagi suku Isakhar keluarlah undian yang keempat, yakni bagi bani Isakhar menurut kaum-kaum mereka.'),(6339,5,19,18,'Daerah mereka ialah Yizreel, Kesulot, Sunem,'),(6340,5,19,19,'Hafaraim, Sion, Anaharat,'),(6341,5,19,20,'Rabit, Kisyon, Ebes,'),(6342,5,19,21,'Remet, En-Ganim, En-Hada dan Bet-Pazes.'),(6343,5,19,22,'Batas daerah itu menyinggung Tabor, Sahazima dan Bet-Semes; dan batas daerah mereka berakhir di sungai Yordan; enam belas kota dengan desa-desanya.'),(6344,5,19,23,'Itulah milik pusaka suku bani Isakhar menurut kaum-kaum mereka, kota-kota itu dengan desa-desanya.'),(6345,5,19,24,'Undian yang kelimapun keluarlah bagi suku bani Asyer menurut kaum-kaum mereka.'),(6346,5,19,25,'Daerah mereka ialah Helkat, Hali, Beten, Akhsaf,'),(6347,5,19,26,'Alamelekh, Amad dan Misal; dan batasnya menyinggung gunung Karmel dan sungai Libnat di sebelah barat;'),(6348,5,19,27,'kemudian berbalik ke arah matahari terbit, ke Bet-Dagon; menyinggung daerah Zebulon dan lembah Yiftah-El di sebelah utara, Bet-Emek dan Nehiel, dan menuju ke Kabul di sebelah utara,'),(6349,5,19,28,'dan ke Ebron, Rehob, Hamon dan Kana sampai ke Sidon Besar.'),(6350,5,19,29,'Kemudian batas itu berbalik ke Rama dan sampai ke kota yang berkubu Tirus, kemudian batas itu berbalik ke Hosa dan berakhir di laut. Lagipula Mahalab, Akhzib,'),(6351,5,19,30,'Uma, Afek dan Rehob; dua puluh dua kota dengan desa-desanya.'),(6352,5,19,31,'Itulah milik pusaka suku bani Asyer menurut kaum-kaum mereka; kota-kota tadi dengan desa-desanya.'),(6353,5,19,32,'Bagi bani Naftali keluarlah undian yang keenam, yakni bagi bani Naftali menurut kaum-kaum mereka.'),(6354,5,19,33,'Daerah mereka mulai dari Helef, dari pohon tarbantin di Zaananim, Adami-Nekeb dan Yabneel, sampai ke Lakum, dan berakhir di sungai Yordan.'),(6355,5,19,34,'Kemudian batas itu berbalik ke barat ke Aznot-Tabor, dari sana menuju ke Hukok, menyinggung daerah Zebulon di sebelah selatan, menyinggung daerah Asyer di sebelah barat dan daerah Yehuda pada sungai Yordan, di sebelah matahari terbit.'),(6356,5,19,35,'Kota-kota yang berkubu ialah Zidim, Zer, Hamat, Rakat, Kineret,'),(6357,5,19,36,'Adama, Rama, Hazor,'),(6358,5,19,37,'Kedesh, Edrei, En-Hazor,'),(6359,5,19,38,'Yiron, Migdal-El, Horem, Bet-Anat dan Bet-Semes; sembilan belas kota dengan desa-desanya.'),(6360,5,19,39,'Itulah milik pusaka suku bani Naftali menurut kaum-kaum mereka; kota-kota itu dengan desa-desanya.'),(6361,5,19,40,'Bagi suku bani Dan menurut kaum-kaum mereka keluarlah undian yang ketujuh.'),(6362,5,19,41,'Daerah milik pusaka mereka ialah Zora, Esytaol, Ir-Semes,'),(6363,5,19,42,'Saalabin, Ayalon, Yitla,'),(6364,5,19,43,'Elon, Timna, Ekron,'),(6365,5,19,44,'Elteke, Gibeton, Baalat,'),(6366,5,19,45,'Yehud, Bene-Berak, Gat-Rimon,'),(6367,5,19,46,'Me-Yarkon dan Rakon, bersama-sama dengan daerah di seberang Yafo.'),(6368,5,19,47,'Karena daerah bani Dan telah menjadi terlalu sempit untuk mereka, maka berjalanlah bani Dan itu maju dan berperang melawan kota Lesem. Mereka merebutnya, memarang penduduknya dengan mata pedang dan mendudukinya. Lalu menetaplah mereka di sana dan menamai Lesem itu Dan menurut nama Dan, bapa leluhur mereka.'),(6369,5,19,48,'Itulah milik pusaka suku bani Dan menurut kaum-kaum mereka; kota-kota tadi dengan desa-desanya.'),(6370,5,19,49,'Setelah orang Israel selesai membagikan negeri itu menjadi milik pusaka mereka menurut daerah-daerahnya, maka kepada Yosua bin Nun diberikanlah milik pusaka di tengah-tengah mereka.'),(6371,5,19,50,'Sesuai dengan titah TUHAN, mereka memberikan kepadanya kota yang dimintanya, yakni Timnat-Serah di pegunungan Efraim. Kota itu dibangunnya dan menetaplah ia di sana.'),(6372,5,19,51,'Itulah milik pusaka yang diperundikan di antara suku-suku orang Israel di Silo oleh imam Eleazar, oleh Yosua bin Nun dan oleh para kepala kaum keluarga di hadapan TUHAN di depan pintu Kemah Pertemuan. Demikianlah diselesaikan mereka pembagian negeri itu.'),(6373,5,20,1,'Berfirmanlah TUHAN kepada Yosua, demikian:'),(6374,5,20,2,'Katakanlah kepada orang Israel, begini: Tentukanlah bagimu kota-kota perlindungan, yang telah Kusebutkan kepadamu dengan perantaraan Musa,'),(6375,5,20,3,'supaya siapa yang membunuh seseorang dengan tidak sengaja, dengan tidak ada niat lebih dahulu, dapat melarikan diri ke sana, sehingga kota-kota itu menjadi tempat perlindungan bagimu terhadap penuntut tebusan darah.'),(6376,5,20,4,'Apabila ia melarikan diri ke salah satu kota tadi, maka haruslah ia tinggal berdiri di depan pintu gerbang kota dan memberitahukan perkaranya kepada para tua-tua kota. Mereka harus menerima dia dalam kota itu dan memberikan tempat kepadanya, dan ia akan diam pada mereka.'),(6377,5,20,5,'Apabila penuntut tebusan darah itu mengejar dia, pembunuh itu tidak akan diserahkan mereka ke dalam tangannya, sebab ia telah membunuh sesamanya manusia dengan tidak ada niat lebih dahulu, dan dengan tidak menaruh benci kepadanya lebih dahulu.'),(6378,5,20,6,'Ia harus tetap diam di kota itu sampai ia dihadapkan kepada rapat jemaah untuk diadili, sampai imam besar yang ada pada waktu itu mati. Maka barulah pembunuh itu boleh pulang ke kotanya dan ke rumahnya, ke kota dari mana ia melarikan diri.\"'),(6379,5,20,7,'Lalu orang Israel mengkhususkan sebagai kota perlindungan: Kedesh di Galilea, di pegunungan Naftali dan Sikhem, di pegunungan Efraim, dan Kiryat-Arba, itulah Hebron, di pegunungan Yehuda.'),(6380,5,20,8,'Dan di seberang sungai Yordan, di sebelah timur Yerikho, mereka menentukan Bezer, di padang gurun, di dataran tinggi, dari suku Ruben; dan Ramot di Gilead dari suku Gad, dan Golan di Basan dari suku Manasye.'),(6381,5,20,9,'Itulah kota-kota yang ditetapkan bagi semua orang Israel dan bagi pendatang-pendatang yang ada di tengah-tengah mereka, supaya setiap orang yang membunuh seseorang dengan tidak sengaja dapat melarikan diri ke sana dan jangan mati dibunuh oleh tangan penuntut tebusan darah, sebelum ia dihadapkan kepada rapat jemaah.'),(6382,5,21,1,'Kemudian datanglah para kepala kaum keluarga orang Lewi menghadap imam Eleazar, menghadap Yosua bin Nun, dan menghadap para kepala kaum keluarga di antara suku-suku orang Israel'),(6383,5,21,2,'dan berkata kepada mereka di Silo di tanah Kanaan, demikian: \"TUHAN telah memerintahkan dengan perantaraan Musa, supaya diberikan kepada kami kota-kota untuk didiami dan tanah-tanah penggembalaannya untuk ternak kami.\"'),(6384,5,21,3,'Lalu orang Israel memberikan dari milik pusaka mereka kota-kota yang berikut dengan tanah-tanah penggembalaannya kepada orang Lewi, seperti yang dititahkan TUHAN.'),(6385,5,21,4,'Lalu keluarlah undian bagi kaum-kaum orang Kehat. Maka di antara orang Lewi anak-anak imam Harun, mendapat dengan undian tiga belas kota dari suku Yehuda, dari suku Simeon dan dari suku Benyamin.'),(6386,5,21,5,'Kaum-kaum yang lain di antara keturunan Kehat mendapat dengan undian sepuluh kota dari kaum-kaum suku Efraim, dari suku Dan dan dari suku Manasye yang setengah itu.'),(6387,5,21,6,'Keturunan Gerson mendapat dengan undian tiga belas kota dari kaum-kaum suku Isakhar, dari suku Asyer, dari suku Naftali dan dari setengah suku Manasye yang di Basan itu.'),(6388,5,21,7,'Keturunan Merari mendapat dengan undian dua belas kota menurut kaum-kaum mereka, dari suku Ruben, dari suku Gad dan dari suku Zebulon.'),(6389,5,21,8,'Demikianlah diberikan orang Israel kota-kota tadi dengan tanah-tanah penggembalaannya kepada orang Lewi dengan undian seperti yang diperintahkan TUHAN dengan perantaraan Musa.'),(6390,5,21,9,'Dari suku bani Yehuda dan dari suku bani Simeon diberikan mereka kota-kota yang berikut, yang disebutkan namanya di sini:'),(6391,5,21,10,'kepada anak-anak Harun yang dari kaum-kaum orang Kehat yang termasuk bani Lewi, karena bagi merekalah undian yang pertama,'),(6392,5,21,11,'kepada mereka diberikan Kiryat-Arba, itulah Hebron--Arba inilah bapa suku Enak--di pegunungan Yehuda, dan tanah-tanah penggembalaan di sekelilingnya;'),(6393,5,21,12,'tetapi tanah ladang kota tadi dengan desa-desanya telah diberikan mereka kepada Kaleb bin Yefune menjadi miliknya.'),(6394,5,21,13,'Kepada anak-anak imam Harun, diberikan mereka Hebron, kota perlindungan untuk pembunuh, dan tanah-tanah penggembalaan kota itu, Libna dengan tanah-tanah penggembalaannya,'),(6395,5,21,14,'Yatir dengan tanah-tanah penggembalaannya, Estemoa dengan tanah-tanah penggembalaannya,'),(6396,5,21,15,'Holon dengan tanah-tanah penggembalaannya, Debir dengan tanah-tanah penggembalaannya,'),(6397,5,21,16,'Ain dengan tanah-tanah penggembalaannya, Yuta dengan tanah-tanah penggembalaannya dan Bet-Semes dengan tanah-tanah penggembalaannya: sembilan kota dari kedua suku itu.'),(6398,5,21,17,'Dan dari suku Benyamin: Gibeon dengan tanah-tanah penggembalaannya, Geba dengan tanah-tanah penggembalaannya,'),(6399,5,21,18,'Anatot dengan tanah-tanah penggembalaannya dan Almon dengan tanah-tanah penggembalaannya: empat kota.'),(6400,5,21,19,'Seluruhnya kota-kota kepunyaan anak-anak Harun, para imam itu, ada tiga belas kota dengan tanah-tanah penggembalaannya.'),(6401,5,21,20,'Kaum-kaum keturunan Kehat, yakni orang Lewi yang masih tinggal dari antara keturunan Kehat, mendapat kota-kota yang dengan undian kepada mereka dari suku Efraim.'),(6402,5,21,21,'Kepada mereka diberikan Sikhem, kota perlindungan untuk pembunuh, dengan tanah-tanah penggembalaannya, di pegunungan Efraim, Gezer dengan tanah-tanah penggembalaannya,'),(6403,5,21,22,'Kibzaim dengan tanah-tanah penggembalaannya dan Bet-Horon dengan tanah-tanah penggembalaannya: empat kota.'),(6404,5,21,23,'Dan dari suku Dan: Elteke dengan tanah-tanah penggembalaannya, Gibeton dengan tanah-tanah penggembalaannya,'),(6405,5,21,24,'Ayalon dengan tanah-tanah penggembalaannya dan Gat-Rimon dengan tanah-tanah penggembalaannya: empat kota.'),(6406,5,21,25,'Dan dari suku Manasye yang setengah itu: Taanakh dengan tanah-tanah penggembalaannya dan Gat-Rimon dengan tanah-tanah penggembalaannya: dua kota.'),(6407,5,21,26,'Seluruhnya kota-kota itu ada sepuluh, dengan tanah-tanah penggembalaannya, ditentukan bagi kaum-kaum yang masih tinggal dari antara keturunan Kehat.'),(6408,5,21,27,'Bani Gerson dari kaum-kaum orang Lewi mendapat dari suku Manasye yang setengah lagi: Golan, kota perlindungan untuk pembunuh, di Basan, dengan tanah-tanah penggembalaannya, dan Beestera dengan tanah-tanah penggembalaannya: dua kota.'),(6409,5,21,28,'Dan dari suku Isakhar: Kisyon dengan tanah-tanah penggembalaannya, Dobrat dengan tanah-tanah penggembalaannya,'),(6410,5,21,29,'Yarmut dengan tanah-tanah penggembalaannya, dan En-Ganim dengan tanah-tanah penggembalaannya: empat kota.'),(6411,5,21,30,'Dan dari suku Asyer: Misal dengan tanah-tanah penggembalaannya, Abdon dengan tanah-tanah penggembalaannya,'),(6412,5,21,31,'Helkat dengan tanah-tanah penggembalaannya dan Rehob dengan tanah-tanah penggembalaannya: empat kota.'),(6413,5,21,32,'Dan dari suku Naftali: Kedesh, kota perlindungan untuk pembunuh, di Galilea, dengan tanah-tanah penggembalaannya, Hamot-Dor dengan tanah-tanah penggembalaannya dan Kartan dengan tanah-tanah penggembalaannya: tiga kota.'),(6414,5,21,33,'Seluruhnya kota-kota kepunyaan orang Gerson menurut kaum-kaum mereka ada tiga belas kota dengan tanah-tanah penggembalaannya.'),(6415,5,21,34,'Kaum-kaum keturunan Merari, orang Lewi yang masih tinggal, mendapat dari suku Zebulon: Yokneam dengan tanah-tanah penggembalaannya, Karta dengan tanah-tanah penggembalaannya,'),(6416,5,21,35,'Dimna dengan tanah-tanah penggembalaannya dan Nahalal dengan tanah-tanah penggembalaannya: empat kota.'),(6417,5,21,36,'Dan dari suku Ruben: Bezer dengan tanah-tanah penggembalaannya, Yahas dengan tanah-tanah penggembalaannya,'),(6418,5,21,37,'Kedemot dengan tanah-tanah penggembalaannya dan Mefaat dengan tanah-tanah penggembalaannya: empat kota.'),(6419,5,21,38,'Dan dari suku Gad: Ramot, kota perlindungan untuk pembunuh, di Gilead, dengan tanah-tanah penggembalaannya, Mahanaim dengan tanah-tanah penggembalaannya,'),(6420,5,21,39,'Hesybon dengan tanah-tanah penggembalaannya dan Yaezer dengan tanah-tanah penggembalaannya: seluruhnya kota-kota itu ada empat.'),(6421,5,21,40,'Seluruhnya kota-kota itu ditentukan bagi keturunan Merari menurut kaum-kaum mereka, yakni yang masih tinggal dari kaum-kaum orang Lewi. Bagian undian mereka ada dua belas kota.'),(6422,5,21,41,'Seluruhnya kota-kota orang Lewi di tengah-tengah milik orang Israel ada empat puluh delapan kota dengan tanah-tanah penggembalaannya.'),(6423,5,21,42,'Kota-kota itu masing-masing ada tanah-tanah penggembalaannya di sekelilingnya, demikianlah kota-kota tadi seluruhnya.'),(6424,5,21,43,'Jadi seluruh negeri itu diberikan TUHAN kepada orang Israel, yakni negeri yang dijanjikan-Nya dengan bersumpah untuk diberikan kepada nenek moyang mereka. Mereka menduduki negeri itu dan menetap di sana.'),(6425,5,21,44,'Dan TUHAN mengaruniakan kepada mereka keamanan ke segala penjuru, tepat seperti yang dijanjikan-Nya dengan bersumpah kepada nenek moyang mereka. Tidak ada seorangpun dari semua musuhnya yang tahan berdiri menghadapi mereka; semua musuhnya diserahkan TUHAN kepada mereka.'),(6426,5,21,45,'Dari segala yang baik yang dijanjikan TUHAN kepada kaum Israel, tidak ada yang tidak dipenuhi; semuanya terpenuhi.'),(6427,5,22,1,'Lalu Yosua memanggil orang Ruben, orang Gad dan suku Manasye yang setengah itu,'),(6428,5,22,2,'dan berkata kepada mereka: \"Kamu telah memelihara segala yang diperintahkan kepadamu oleh Musa, hamba TUHAN itu, dan telah mendengarkan perkataanku dalam segala yang kuperintahkan kepadamu.'),(6429,5,22,3,'Kamu tidak meninggalkan saudara-saudaramu selama waktu ini, sampai sekarang, tetapi kamu setia memelihara perintah TUHAN, Allahmu, kepadamu.'),(6430,5,22,4,'Tetapi sekarang TUHAN, Allahmu, telah mengaruniakan keamanan kepada saudara-saudaramu, seperti yang dijanjikan-Nya kepada mereka. Oleh sebab itu, pulanglah ke kemahmu, ke tanah milikmu, yang telah diberikan kepadamu oleh Musa, hamba TUHAN itu, di seberang Yordan.'),(6431,5,22,5,'Hanya, lakukanlah dengan sangat setia perintah dan hukum, yang diperintahkan kepadamu oleh Musa, hamba TUHAN itu, yakni mengasihi TUHAN, Allahmu, hidup menurut segala jalan yang ditunjukkan-Nya, tetap mengikuti perintah-Nya, berpaut pada-Nya dan berbakti kepada-Nya dengan segenap hatimu dan dengan segenap jiwamu.\"'),(6432,5,22,6,'Lalu Yosua memberkati mereka dan melepas mereka pergi. Maka pulanglah mereka ke kemah mereka.'),(6433,5,22,7,'Kepada suku Manasye yang setengah telah diberikan Musa bagian mereka di Basan; kepada suku yang setengah lagi telah diberikan Yosua bagian mereka di antara saudara-saudara mereka di sebelah barat sungai Yordan. Lagipun ketika Yosua melepas mereka pergi ke kemah mereka, setelah memberkati mereka,'),(6434,5,22,8,'maka ia berkata kepada mereka, demikian: \"Pulanglah ke kemahmu dengan kekayaan yang banyak dan dengan sangat banyak ternak, dengan perak, emas, tembaga, besi dan dengan pakaian yang sangat banyak. Bagilah dengan saudara-saudaramu jarahan yang dari musuhmu itu.\"'),(6435,5,22,9,'Maka pulanglah bani Ruben, bani Gad dan suku Manasye yang setengah itu dan mereka pergi meninggalkan orang Israel, keluar dari Silo di tanah Kanaan untuk pergi ke tanah Gilead, tanah milik mereka yang didiami mereka sesuai dengan titah TUHAN dengan perantaraan Musa.'),(6436,5,22,10,'Ketika mereka sampai ke Gelilot pada sungai Yordan, yang di tanah Kanaan, maka bani Ruben, bani Gad dan suku Manasye yang setengah itu mendirikan mezbah di sana di tepi sungai Yordan, mezbah yang besar bangunannya.'),(6437,5,22,11,'Lalu terdengarlah oleh orang Israel itu cakap orang: \"Telah didirikan mezbah oleh bani Ruben, bani Gad dan suku Manasye yang setengah itu, mezbah menghadap ke tanah Kanaan, di Gelilot pada sungai Yordan, di sebelah wilayah orang Israel.\"'),(6438,5,22,12,'Ketika hal itu terdengar oleh orang Israel, berkumpullah segenap umat Israel di Silo, untuk maju memerangi mereka.'),(6439,5,22,13,'Kemudian orang Israel mengutus kepada bani Ruben, kepada bani Gad dan kepada suku Manasye yang setengah itu, ke tanah Gilead, imam Pinehas bin Eleazar,'),(6440,5,22,14,'dan bersama-sama dengan dia sepuluh pemimpin, yakni seorang pemimpin kaum keluarga sebagai wakil tiap-tiap suku Israel. Masing-masing mereka itu kepala kaum keluarganya di antara kaum-kaum orang Israel.'),(6441,5,22,15,'Setelah mereka sampai kepada bani Ruben, kepada bani Gad dan kepada suku Manasye yang setengah itu di tanah Gilead, berkatalah mereka kepada orang-orang itu, demikian:'),(6442,5,22,16,'Beginilah kata segenap umat TUHAN: Apa macam perbuatanmu yang tidak setia ini terhadap Allah Israel, dengan sekarang berbalik dari pada TUHAN dan mendirikan mezbah bagimu, dengan demikian memberontak terhadap TUHAN pada hari ini?'),(6443,5,22,17,'Belum cukupkah bagi kita noda yang di Peor itu, yang dari padanya kita belum mentahirkan diri sampai hari ini dan yang menyebabkan umat TUHAN kena tulah,'),(6444,5,22,18,'sehingga kamu berbalik pula sekarang ini membelakangi TUHAN? Jika kamu hari ini memberontak terhadap TUHAN, maka besok Ia akan murka kepada segenap umat Israel.'),(6445,5,22,19,'Akan tetapi, jika sekiranya tanah milikmu itu najis, marilah menyeberang ke tanah milik TUHAN, tempat kedudukan Kemah Suci TUHAN, dan menetaplah di tengah-tengah kami. Tetapi janganlah memberontak terhadap TUHAN dan janganlah memberontak terhadap kami, dengan mendirikan mezbah bagimu sendiri, selain dari mezbah TUHAN, Allah kita.'),(6446,5,22,20,'Ketika Akhan bin Zerah berubah setia dengan mengambil barang-barang yang dikhususkan, bukankah segenap umat Israel kena murka? Bukan orang itu saja yang mati karena dosanya.\"'),(6447,5,22,21,'Lalu jawab bani Ruben, bani Gad dan suku Manasye yang setengah itu, katanya kepada para kepala kaum-kaum orang Israel:'),(6448,5,22,22,'Allah segala allah, TUHAN, Allah segala allah, TUHAN, Dialah yang mengetahui, dan patutlah orang Israel mengetahuinya juga! Jika sekiranya hal ini terjadi dengan maksud memberontak atau dengan maksud berubah setia terhadap TUHAN--biarlah jangan TUHAN selamatkan kami pada hari ini.'),(6449,5,22,23,'Jika sekiranya kami mendirikan mezbah untuk berbalik dari pada TUHAN, untuk mempersembahkan korban bakaran dan korban sajian di atasnya serta korban keselamatan di atasnya, biarlah TUHAN sendiri yang menuntut balas terhadap kami.'),(6450,5,22,24,'Tetapi sesungguhnya, kami telah melakukannya karena cemas. Sebab pikir kami: Di kemudian hari anak-anak kamu mungkin berkata kepada anak-anak kami, demikian: Apakah sangkut pautmu dengan TUHAN, Allah Israel?'),(6451,5,22,25,'Bukankah TUHAN telah menentukan sungai Yordan sebagai batas antara kami dan kamu, hai orang bani Ruben dan bani Gad! Kamu tidak mempunyai bagian akan TUHAN. Demikianlah mungkin anak-anak kamu membuat anak-anak kami berhenti dari pada takut akan TUHAN.'),(6452,5,22,26,'Sebab itu kata kami: Biarlah kita mendirikan mezbah itu bagi kita! Bukanlah untuk korban bakaran dan bukanlah untuk korban sembelihan,'),(6453,5,22,27,'tetapi supaya mezbah itu menjadi saksi antara kami dan kamu, dan antara keturunan kita kemudian, bahwa kami tetap beribadah kepada TUHAN di hadapan-Nya dengan korban bakaran, korban sembelihan dan korban keselamatan kami. Jadi tidaklah mungkin anak-anak kamu di kemudian hari berkata kepada anak-anak kami: Kamu tidak mempunyai bagian pada TUHAN.'),(6454,5,22,28,'Lagi kata kami: Apabila di kemudian hari demikian dikatakan mereka kepada kita dan kepada keturunan kita, maka kita akan berkata: Tengoklah bangunan tiruan mezbah TUHAN itu, yang telah dibuat oleh nenek moyang kami. Bukan untuk korban bakaran dan bukan untuk korban sembelihan, tetapi mezbah itu menjadi saksi antara kami dan kamu.'),(6455,5,22,29,'Jauhlah dari pada kami untuk memberontak terhadap TUHAN, dan untuk berbalik dari pada TUHAN pada hari ini dengan mendirikan mezbah untuk korban bakaran, korban sajian atau korban sembelihan, mezbah yang bukan mezbah TUHAN, Allah kita, yang ada di depan Kemah Suci-Nya!\"'),(6456,5,22,30,'Setelah imam Pinehas dan para pemimpin umat serta para kepala kaum-kaum orang Israel yang bersama-sama dengan dia, mendengar perkataan yang dikatakan oleh bani Ruben, bani Gad dan bani Manasye itu, maka mereka menganggap hal itu baik.'),(6457,5,22,31,'Kemudian berkatalah imam Pinehas bin Eleazar kepada bani Ruben, bani Gad dan bani Manasye: \"Sekarang tahulah kami bahwa TUHAN ada di tengah-tengah kita, sebab tidaklah kamu berubah setia terhadap TUHAN. Dengan demikian kamu telah melepaskan orang Israel dari hukuman TUHAN.\"'),(6458,5,22,32,'Sesudah itu imam Pinehas bin Eleazar serta para pemimpin itu meninggalkan bani Ruben dan bani Gad di tanah Gilead, pulang ke Kanaan kepada orang Israel, lalu disampaikanlah berita itu kepada mereka.'),(6459,5,22,33,'Hal itu dipandang baik oleh orang Israel, sehingga orang Israel memuji Allah dan tidak lagi berkata hendak maju memerangi mereka untuk memusnahkan negeri yang didiami bani Ruben dan bani Gad itu.'),(6460,5,22,34,'Dan bani Ruben dan bani Gad menamai mezbah itu: Saksi, karena inilah saksi antara kita, bahwa TUHAN itulah Allah.'),(6461,5,23,1,'Lama setelah TUHAN mengaruniakan keamanan kepada orang Israel ke segala penjuru terhadap semua musuhnya, dan ketika Yosua telah tua dan lanjut umur,'),(6462,5,23,2,'dipanggilnya seluruh orang Israel, para tua-tuanya, para kepalanya, para hakimnya dan para pengatur pasukannya dan berkata kepada mereka: \"Aku telah tua dan sangat lanjut umur,'),(6463,5,23,3,'dan kamu ini telah melihat segala yang dilakukan TUHAN, Allahmu, kepada semua bangsa di sini demi kamu, sebab TUHAN, Allahmu, Dialah yang telah berperang bagi kamu.'),(6464,5,23,4,'Ingatlah, aku telah membagikan dengan membuang undi bangsa-bangsa yang masih tinggal ini kepada suku-sukumu menjadi milik pusakamu, seperti juga semua bangsa yang telah kulenyapkan hari itu, mulai dari sungai Yordan sampai ke Laut Besar di sebelah matahari terbenam.'),(6465,5,23,5,'Dan TUHAN, Allahmu, Dialah yang akan mengusir dan menghalau mereka dari depanmu, sehingga kamu menduduki negeri mereka, seperti yang dijanjikan kepadamu oleh TUHAN, Allahmu.'),(6466,5,23,6,'Kuatkanlah benar-benar hatimu dalam memelihara dan melakukan segala yang tertulis dalam kitab hukum Musa, supaya kamu jangan menyimpang ke kanan atau ke kiri,'),(6467,5,23,7,'dan supaya kamu jangan bergaul dengan bangsa-bangsa yang masih tinggal di antaramu itu, serta mengakui nama allah mereka dan bersumpah demi nama itu, dan beribadah atau sujud menyembah kepada mereka.'),(6468,5,23,8,'Tetapi kamu harus berpaut pada TUHAN, Allahmu, seperti yang kamu lakukan sampai sekarang.'),(6469,5,23,9,'Bukankah TUHAN telah menghalau bangsa-bangsa yang besar dan kuat dari depanmu, dan akan kamu ini, seorangpun tidak ada yang tahan menghadapi kamu sampai sekarang.'),(6470,5,23,10,'Satu orang saja dari pada kamu dapat mengejar seribu orang, sebab TUHAN Allahmu, Dialah yang berperang bagi kamu, seperti yang dijanjikan-Nya kepadamu.'),(6471,5,23,11,'Maka demi nyawamu, bertekunlah mengasihi TUHAN, Allahmu.'),(6472,5,23,12,'Sebab jika kamu berbalik dan berpaut kepada sisa bangsa-bangsa ini yang masih tinggal di antara kamu, kawin-mengawin dengan mereka serta bergaul dengan mereka dan mereka dengan kamu,'),(6473,5,23,13,'maka ketahuilah dengan sesungguhnya, bahwa TUHAN, Allahmu, tidak akan menghalau lagi bangsa-bangsa itu dari depanmu. Tetapi mereka akan menjadi perangkap dan jerat bagimu, menjadi cambuk pada lambungmu dan duri di matamu, sampai kamu binasa dari tanah yang baik ini, yang telah diberikan kepadamu oleh TUHAN, Allahmu.'),(6474,5,23,14,'Maka sekarang, sebentar lagi aku akan menempuh jalan segala yang fana. Sebab itu insaflah dengan segenap hatimu dan segenap jiwamu, bahwa satupun dari segala yang baik yang telah dijanjikan kepadamu oleh TUHAN, Allahmu, tidak ada yang tidak dipenuhi. Semuanya telah digenapi bagimu. Tidak ada satupun yang tidak dipenuhi.'),(6475,5,23,15,'Tetapi seperti telah datang atas kamu segala yang baik, yang telah dijanjikan kepadamu oleh TUHAN, Allahmu, demikianlah TUHAN akan mendatangkan atas kamu segala yang tidak baik sampai Ia telah memusnahkan kamu dari tanah yang baik ini, yang diberikan kepadamu oleh TUHAN, Allahmu,'),(6476,5,23,16,'apabila kamu melangkahi perjanjian, yang telah diperintahkan kepadamu oleh TUHAN, Allahmu, dan pergi beribadah kepada allah lain dan sujud menyembah kepada mereka. Maka murka TUHAN akan bangkit terhadap kamu, sehingga kamu segera binasa dari negeri yang baik, yang telah diberikan-Nya kepadamu.\"'),(6477,5,24,1,'Kemudian Yosua mengumpulkan semua suku orang Israel di Sikhem. Dipanggilnya para tua-tua orang Israel, para kepalanya, para hakimnya dan para pengatur pasukannya, lalu mereka berdiri di hadapan Allah.'),(6478,5,24,2,'Berkatalah Yosua kepada seluruh bangsa itu: \"Beginilah firman TUHAN, Allah Israel: Dahulu kala di seberang sungai Efrat, di situlah diam nenek moyangmu, yakni Terah, ayah Abraham dan ayah Nahor, dan mereka beribadah kepada allah lain.'),(6479,5,24,3,'Tetapi Aku mengambil Abraham, bapamu itu, dari seberang sungai Efrat, dan menyuruh dia menjelajahi seluruh tanah Kanaan. Aku membuat banyak keturunannya dan memberikan Ishak kepadanya.'),(6480,5,24,4,'Kepada Ishak Kuberikan Yakub dan Esau. Kepada Esau Kuberikan pegunungan Seir menjadi miliknya, sedang Yakub serta anak-anaknya pergi ke Mesir.'),(6481,5,24,5,'Lalu Aku mengutus Musa serta Harun dan menulahi Mesir, seperti yang Kulakukan di tengah-tengah mereka, kemudian Aku membawa kamu keluar.'),(6482,5,24,6,'Setelah Aku membawa nenek moyangmu keluar dari Mesir dan kamu sampai ke laut, lalu orang Mesir mengejar nenek moyangmu dengan kereta dan orang berkuda ke Laut Teberau.'),(6483,5,24,7,'Sebab itu berteriak-teriaklah mereka kepada TUHAN, maka diadakan-Nya gelap antara kamu dan orang Mesir itu dan didatangkan-Nya air laut atas mereka, sehingga mereka diliputi. Dan matamu sendiri telah melihat, apa yang Kulakukan terhadap Mesir. Sesudah itu lama kamu diam di padang gurun.'),(6484,5,24,8,'Aku membawa kamu ke negeri orang Amori yang diam di seberang sungai Yordan, dan ketika mereka berperang melawan kamu, mereka Kuserahkan ke dalam tanganmu, sehingga kamu menduduki negerinya, sedang mereka Kupunahkan dari depan kamu.'),(6485,5,24,9,'Ketika itu Balak bin Zipor, raja Moab, bangkit berperang melawan orang Israel. Disuruhnya memanggil Bileam bin Beor untuk mengutuki kamu.'),(6486,5,24,10,'Tetapi Aku tidak mau mendengarkan Bileam, sehingga iapun memberkati kamu. Demikianlah Aku melepaskan kamu dari tangannya.'),(6487,5,24,11,'Setelah kamu menyeberangi sungai Yordan dan sampai ke Yerikho, berperanglah melawan kamu warga-warga kota Yerikho, orang Amori, orang Feris, orang Kanaan, orang Het, orang Girgasi, orang Hewi dan orang Yebus, tetapi mereka itu Kuserahkan ke dalam tanganmu.'),(6488,5,24,12,'Kemudian Aku melepaskan tabuhan mendahului kamu dan binatang-binatang ini menghalau mereka dari depanmu, seperti kedua raja orang Amori itu. Sesungguhnya, bukan oleh pedangmu dan bukan pula oleh panahmu.'),(6489,5,24,13,'Demikianlah Kuberikan kepadamu negeri yang kamu peroleh tanpa bersusah-susah dan kota-kota yang tidak kamu dirikan, tetapi kamulah yang diam di dalamnya; juga kebun-kebun anggur dan kebun-kebun zaitun yang tidak kamu tanami, kamulah yang makan hasilnya.'),(6490,5,24,14,'Oleh sebab itu, takutlah akan TUHAN dan beribadahlah kepada-Nya dengan tulus ikhlas dan setia. Jauhkanlah allah yang kepadanya nenek moyangmu telah beribadah di seberang sungai Efrat dan di Mesir, dan beribadahlah kepada TUHAN.'),(6491,5,24,15,'Tetapi jika kamu anggap tidak baik untuk beribadah kepada TUHAN, pilihlah pada hari ini kepada siapa kamu akan beribadah; allah yang kepadanya nenek moyangmu beribadah di seberang sungai Efrat, atau allah orang Amori yang negerinya kamu diami ini. Tetapi aku dan seisi rumahku, kami akan beribadah kepada TUHAN!\"'),(6492,5,24,16,'Lalu bangsa itu menjawab: \"Jauhlah dari pada kami meninggalkan TUHAN untuk beribadah kepada allah lain!'),(6493,5,24,17,'Sebab TUHAN, Allah kita, Dialah yang telah menuntun kita dan nenek moyang kita dari tanah Mesir, dari rumah perbudakan, dan yang telah melakukan tanda-tanda mujizat yang besar ini di depan mata kita sendiri, dan yang telah melindungi kita sepanjang jalan yang kita tempuh, dan di antara semua bangsa yang kita lalui,'),(6494,5,24,18,'TUHAN menghalau semua bangsa dan orang Amori, penduduk negeri ini, dari depan kita. Kamipun akan beribadah kepada TUHAN, sebab Dialah Allah kita.\"'),(6495,5,24,19,'Tetapi Yosua berkata kepada bangsa itu: \"Tidaklah kamu sanggup beribadah kepada TUHAN, sebab Dialah Allah yang kudus, Dialah Allah yang cemburu. Ia tidak akan mengampuni kesalahan dan dosamu.'),(6496,5,24,20,'Apabila kamu meninggalkan TUHAN dan beribadah kepada allah asing, maka Ia akan berbalik dari padamu dan melakukan yang tidak baik kepada kamu serta membinasakan kamu, setelah Ia melakukan yang baik kepada kamu dahulu.\"'),(6497,5,24,21,'Tetapi bangsa itu berkata kepada Yosua: \"Tidak, hanya kepada TUHAN saja kami akan beribadah.\"'),(6498,5,24,22,'Kemudian berkatalah Yosua kepada bangsa itu: \"Kamulah saksi terhadap kamu sendiri, bahwa kamu telah memilih TUHAN untuk beribadah kepada-Nya.\" Jawab mereka: \"Kamilah saksi!\"'),(6499,5,24,23,'Ia berkata: \"Maka sekarang, jauhkanlah allah asing yang ada di tengah-tengah kamu dan condongkanlah hatimu kepada TUHAN, Allah Israel.\"'),(6500,5,24,24,'Lalu jawab bangsa itu kepada Yosua: \"Kepada TUHAN, Allah kita, kami akan beribadah, dan firman-Nya akan kami dengarkan.\"'),(6501,5,24,25,'Pada hari itu juga Yosua mengikat perjanjian dengan bangsa itu dan membuat ketetapan dan peraturan bagi mereka di Sikhem.'),(6502,5,24,26,'Yosua menuliskan semuanya itu dalam kitab hukum Allah, lalu ia mengambil batu yang besar dan mendirikannya di sana, di bawah pohon besar, di tempat kudus TUHAN.'),(6503,5,24,27,'Kata Yosua kepada seluruh bangsa itu: \"Sesungguhnya batu inilah akan menjadi saksi terhadap kita, sebab telah didengarnya segala firman TUHAN yang diucapkan-Nya kepada kita. Sebab itu batu ini akan menjadi saksi terhadap kamu, supaya kamu jangan menyangkal Allahmu.\"'),(6504,5,24,28,'Sesudah itu Yosua melepas bangsa itu pergi, masing-masing ke milik pusakanya.'),(6505,5,24,29,'Dan sesudah peristiwa-peristiwa ini, maka matilah Yosua bin Nun, hamba TUHAN itu, ketika berumur seratus sepuluh tahun.'),(6506,5,24,30,'Lalu ia dikuburkan di daerah milik pusakanya, di Timnat-Serah yang di pegunungan Efraim, di sebelah utara gunung Gaas.'),(6507,5,24,31,'Orang Israel beribadah kepada TUHAN sepanjang zaman Yosua dan sepanjang zaman para tua-tua yang hidup lebih lama dari pada Yosua, dan yang mengenal segenap perbuatan yang dilakukan TUHAN bagi orang Israel.'),(6508,5,24,32,'Tulang-tulang Yusuf, yang dibawa orang Israel dari Mesir, dikuburkan mereka di Sikhem, di tanah milik yang dibeli Yakub dengan harga seratus kesita dari anak-anak Hemor, bapa Sikhem, dan yang ditentukan bagi bani Yusuf menjadi milik pusaka mereka.'),(6509,5,24,33,'Juga Eleazar bin Harun mati, dan dia dikuburkan di bukit yang diberikan kepada Pinehas, anaknya itu, di pegunungan Efraim.'),(6510,6,1,1,'Sesudah Yosua mati, orang Israel bertanya kepada TUHAN: \"Siapakah dari pada kami yang harus lebih dahulu maju menghadapi orang Kanaan untuk berperang melawan mereka?\"'),(6511,6,1,2,'Firman TUHAN: \"Suku Yehudalah yang harus maju; sesungguhnya telah Kuserahkan negeri itu ke dalam tangannya.\"'),(6512,6,1,3,'Lalu berkatalah Yehuda kepada Simeon, saudaranya itu: \"Majulah bersama-sama dengan aku ke bagian yang telah diundikan kepadaku dan baiklah kita berperang melawan orang Kanaan, maka akupun akan maju bersama-sama dengan engkau ke bagian yang telah diundikan kepadamu.\" Lalu Simeon maju bersama-sama dengan dia.'),(6513,6,1,4,'Maka majulah suku Yehuda, lalu TUHAN menyerahkan orang Kanaan dan orang Feris ke dalam tangan mereka, dan mereka memukul kalah orang-orang itu dekat Bezek, sepuluh ribu orang banyaknya.'),(6514,6,1,5,'Di Bezek mereka menjumpai Adoni-Bezek dan berperang melawan dia, dan mereka memukul kalah orang Kanaan dan orang Feris.'),(6515,6,1,6,'Tetapi Adoni-Bezek melarikan diri, lalu mereka mengejarnya, menangkapnya dan memotong ibu jari dari tangannya dan dari kakinya.'),(6516,6,1,7,'Kata Adoni-Bezek: \"Ada tujuh puluh raja dengan terpotong ibu jari tangan dan kakinya memungut sisa-sisa makanan di bawah mejaku; sesuai dengan yang kulakukan itu, demikianlah dibalaskan Allah kepadaku.\" Kemudian ia dibawa ke Yerusalem dan mati di sana.'),(6517,6,1,8,'Sesudah itu bani Yehuda berperang melawan Yerusalem, merebutnya lalu memukulnya dengan mata pedang dan memusnahkan kota itu dengan api.'),(6518,6,1,9,'Kemudian bani Yehuda maju berperang melawan orang Kanaan, yang diam di pegunungan, di Tanah Negeb dan di Daerah Bukit.'),(6519,6,1,10,'Lalu suku Yehuda bergerak menyerang orang Kanaan yang diam di Hebron--nama Hebron dahulu adalah Kiryat-Arba--dan memukul kalah Sesai, Ahiman dan Talmai.'),(6520,6,1,11,'Dari sana mereka bergerak menyerang penduduk Debir. Nama Debir dahulu adalah Kiryat-Sefer.'),(6521,6,1,12,'Berkatalah Kaleb: \"Siapa yang mengalahkan dan merebut Kiryat-Sefer, kepadanya akan kuberikan Akhsa, anakku, menjadi isterinya.\"'),(6522,6,1,13,'Dan Otniel, anak Kenas adik Kaleb, merebut kota itu; lalu Kaleb memberikan Akhsa, anaknya, kepadanya menjadi isterinya.'),(6523,6,1,14,'Ketika perempuan itu tiba, dibujuknya suaminya untuk meminta sebidang ladang kepada ayahnya. Maka turunlah perempuan itu dari keledainya, lalu berkatalah Kaleb kepadanya: \"Ada apa?\"'),(6524,6,1,15,'Jawabnya kepadanya: \"Berikanlah kepadaku suatu hadiah; telah kauberikan kepadaku tanah yang gersang, berikanlah juga kepadaku mata air.\" Lalu Kaleb memberikan kepadanya mata air yang di hulu dan mata air yang di hilir.'),(6525,6,1,16,'Keturunan Hobab, ipar Musa, orang Keni itu, maju bersama-sama dengan bani Yehuda dari kota pohon korma ke padang gurun Yehuda di Tanah Negeb dekat Arad; lalu mereka menetap di antara penduduk di sana.'),(6526,6,1,17,'Yehuda maju bersama-sama dengan Simeon, saudaranya itu, lalu mereka memukul kalah orang Kanaan, penduduk Zefat; mereka menumpas kota itu. Sebab itu kota itu dinamai Horma.'),(6527,6,1,18,'Selanjutnya suku Yehuda merebut Gaza dengan daerahnya, Askelon dengan daerahnya dan Ekron dengan daerahnya.'),(6528,6,1,19,'Dan TUHAN menyertai suku Yehuda, sehingga mereka menduduki pegunungan itu; tetapi mereka tidak dapat menghalau penduduk yang di lembah, sebab orang-orang ini mempunyai kereta-kereta besi.'),(6529,6,1,20,'Kepada Kaleb telah diberikan Hebron, seperti yang dikatakan Musa dahulu, dan dari sana telah dihalaukannya anak Enak yang tiga itu.'),(6530,6,1,21,'Tetapi orang Yebus, penduduk kota Yerusalem, tidak dihalau oleh bani Benyamin, jadi orang Yebus itu masih diam bersama-sama dengan bani Benyamin di Yerusalem sampai sekarang.'),(6531,6,1,22,'Keturunan Yusuf juga maju menyerang Betel, dan TUHAN menyertai mereka.'),(6532,6,1,23,'Keturunan Yusuf menyuruh orang mengintai Betel itu--nama kota itu dahulu adalah Lus.'),(6533,6,1,24,'Ketika pengintai-pengintai itu melihat seorang keluar dari kota itu, maka berkatalah mereka kepadanya: \"Tolong tunjukkan bagaimana kami dapat memasuki kota ini, maka kami akan memperlakukan engkau sebagai sahabat.\"'),(6534,6,1,25,'Lalu ditunjukkannyalah kepada mereka bagaimana mereka dapat memasuki kota itu, dan mereka memukul kota itu dengan mata pedang, tetapi orang itu dengan seluruh kaumnya dibiarkan mereka pergi.'),(6535,6,1,26,'Orang itu pergi ke negeri orang Het dan mendirikan di sana sebuah kota yang dinamainya Lus. Demikianlah nama kota itu sampai sekarang.'),(6536,6,1,27,'Suku Manasye tidak menghalau penduduk Bet-Sean dan penduduk segala anak kotanya, penduduk Taanakh dengan segala anak kotanya, penduduk Dor dengan segala anak kotanya, penduduk Yibleam dengan segala anak kotanya, dan penduduk Megido dengan segala anak kotanya, sebab orang Kanaan itu berkeras untuk tetap diam di negeri itu.'),(6537,6,1,28,'Setelah orang Israel menjadi kuat, mereka membuat orang Kanaan itu menjadi orang rodi dan tidak menghalau mereka sama sekali.'),(6538,6,1,29,'Suku Efraimpun tidak menghalau orang Kanaan yang diam di Gezer, sehingga orang Kanaan itu tetap diam di tengah-tengah mereka di Gezer.'),(6539,6,1,30,'Suku Zebulon tidak menghalau penduduk Kitron dan penduduk Nahalol, sehingga orang Kanaan itu tetap diam di tengah-tengah mereka, walaupun sebagai orang rodi.'),(6540,6,1,31,'Suku Asyer tidak menghalau penduduk Ako, penduduk Sidon serta Ahlab, Akhzib, Helba, Afek dan Rehob,'),(6541,6,1,32,'sehingga orang Asyer itu diam di tengah-tengah orang Kanaan, penduduk asli di negeri itu, sebab orang-orang itu tidak dihalaunya.'),(6542,6,1,33,'Suku Naftali tidak menghalau penduduk Bet-Semes dan penduduk Bet-Anat, sehingga mereka diam di tengah-tengah orang Kanaan, penduduk asli di negeri itu; tetapi penduduk Bet-Semes dan Bet-Anat itu menjadi orang rodi bagi mereka.'),(6543,6,1,34,'Orang Amori mendesak bani Dan ke sebelah pegunungan dan tidak membiarkan mereka turun ke lembah,'),(6544,6,1,35,'dan orang Amori itu berkeras untuk tetap diam di Har-Heres, di Ayalon dan di Saalbim, walaupun mereka mendapat tekanan berat dari keturunan Yusuf, sebab mereka menjadi orang rodi.'),(6545,6,1,36,'Daerah orang Amori itu mulai dari pendakian Akrabim, dari Sela, terus ke atas.'),(6546,6,2,1,'Lalu Malaikat TUHAN pergi dari Gilgal ke Bokhim dan berfirman: \"Telah Kutuntun kamu keluar dari Mesir dan Kubawa ke negeri yang Kujanjikan dengan bersumpah kepada nenek moyangmu, dan Aku telah berfirman: Aku tidak akan membatalkan perjanjian-Ku dengan kamu untuk selama-lamanya,'),(6547,6,2,2,'tetapi janganlah kamu mengikat perjanjian dengan penduduk negeri ini; mezbah mereka haruslah kamu robohkan. Tetapi kamu tidak mendengarkan firman-Ku. Mengapa kamu perbuat demikian?'),(6548,6,2,3,'Lagi Aku telah berfirman: Aku tidak akan menghalau orang-orang itu dari depanmu, tetapi mereka akan menjadi musuhmu dan segala allah mereka akan menjadi jerat bagimu.\"'),(6549,6,2,4,'Setelah Malaikat TUHAN mengucapkan firman itu kepada seluruh Israel, menangislah bangsa itu dengan keras.'),(6550,6,2,5,'Maka tempat itu dinamai Bokhim. Lalu mereka mempersembahkan korban di sana kepada TUHAN.'),(6551,6,2,6,'Setelah Yosua melepas bangsa itu pergi, maka pergilah orang Israel itu, masing-masing ke milik pusakanya, untuk memiliki negeri itu.'),(6552,6,2,7,'Dan bangsa itu beribadah kepada TUHAN sepanjang zaman Yosua dan sepanjang zaman para tua-tua yang hidup lebih lama dari pada Yosua, dan yang telah melihat segenap perbuatan yang besar, yang dilakukan TUHAN bagi orang Israel.'),(6553,6,2,8,'Dan Yosua bin Nun, hamba TUHAN itu, mati pada umur seratus sepuluh tahun;'),(6554,6,2,9,'ia dikuburkan di daerah milik pusakanya di Timnat-Heres, di pegunungan Efraim, di sebelah utara gunung Gaas.'),(6555,6,2,10,'Setelah seluruh angkatan itu dikumpulkan kepada nenek moyangnya, bangkitlah sesudah mereka itu angkatan yang lain, yang tidak mengenal TUHAN ataupun perbuatan yang dilakukan-Nya bagi orang Israel.'),(6556,6,2,11,'Lalu orang Israel melakukan apa yang jahat di mata TUHAN dan mereka beribadah kepada para Baal.'),(6557,6,2,12,'Mereka meninggalkan TUHAN, Allah nenek moyang mereka yang telah membawa mereka keluar dari tanah Mesir, lalu mengikuti allah lain, dari antara allah bangsa-bangsa di sekeliling mereka, dan sujud menyembah kepadanya, sehingga mereka menyakiti hati TUHAN.'),(6558,6,2,13,'Demikianlah mereka meninggalkan TUHAN dan beribadah kepada Baal dan para Asytoret.'),(6559,6,2,14,'Maka bangkitlah murka TUHAN terhadap orang Israel. Ia menyerahkan mereka ke dalam tangan perampok dan menjual mereka kepada musuh di sekeliling mereka, sehingga mereka tidak sanggup lagi menghadapi musuh mereka.'),(6560,6,2,15,'Setiap kali mereka maju, tangan TUHAN melawan mereka dan mendatangkan malapetaka kepada mereka, sesuai dengan apa yang telah diperingatkan kepada mereka oleh TUHAN dengan sumpah, sehingga mereka sangat terdesak.'),(6561,6,2,16,'Maka TUHAN membangkitkan hakim-hakim, yang menyelamatkan mereka dari tangan perampok itu.'),(6562,6,2,17,'Tetapi juga para hakim itu tidak mereka hiraukan, karena mereka berzinah dengan mengikuti allah lain dan sujud menyembah kepadanya. Mereka segera menyimpang dari jalan yang ditempuh oleh nenek moyangnya yang mendengarkan perintah TUHAN; mereka melakukan yang tidak patut.'),(6563,6,2,18,'Setiap kali apabila TUHAN membangkitkan seorang hakim bagi mereka, maka TUHAN menyertai hakim itu dan menyelamatkan mereka dari tangan musuh mereka selama hakim itu hidup; sebab TUHAN berbelas kasihan mendengar rintihan mereka karena orang-orang yang mendesak dan menindas mereka.'),(6564,6,2,19,'Tetapi apabila hakim itu mati, kembalilah mereka berlaku jahat, lebih jahat dari nenek moyang mereka, dengan mengikuti allah lain, beribadah kepadanya dan sujud menyembah kepadanya; dalam hal apapun mereka tidak berhenti dengan perbuatan dan kelakuan mereka yang tegar itu.'),(6565,6,2,20,'Apabila murka TUHAN bangkit terhadap orang Israel, berfirmanlah Ia: \"Karena bangsa ini melanggar perjanjian yang telah Kuperintahkan kepada nenek moyang mereka, dan tidak mendengarkan firman-Ku,'),(6566,6,2,21,'maka Akupun tidak mau menghalau lagi dari depan mereka satupun dari bangsa-bangsa yang ditinggalkan Yosua pada waktu matinya,'),(6567,6,2,22,'supaya dengan perantaraan bangsa-bangsa itu Aku mencobai orang Israel, apakah mereka tetap hidup menurut jalan yang ditunjukkan TUHAN, seperti yang dilakukan oleh nenek moyang mereka, atau tidak.\"'),(6568,6,2,23,'Demikianlah TUHAN membiarkan bangsa-bangsa itu tinggal dengan tidak segera menghalau mereka; mereka tidak diserahkan-Nya ke dalam tangan Yosua.'),(6569,6,3,1,'Inilah bangsa-bangsa yang dibiarkan TUHAN tinggal untuk mencobai orang Israel itu dengan perantaraan mereka, yakni semua orang Israel yang tidak mengenal perang Kanaan.'),(6570,6,3,2,'--Maksudnya hanyalah, supaya keturunan-keturunan orang Israel yang tidak mengenal perang yang sudah-sudah, dilatih berperang oleh TUHAN.'),(6571,6,3,3,'Yang tinggal ialah kelima raja kota orang Filistin dan semua orang Kanaan, orang Sidon dan orang Hewi, yang mendiami pegunungan Libanon, dari gunung Baal-Hermon sampai ke jalan yang menuju ke Hamat.'),(6572,6,3,4,'Mereka itu ada di sana, supaya Ia mencobai orang Israel dengan perantaraan mereka untuk mengetahui, apakah mereka mendengarkan perintah yang diberikan TUHAN kepada nenek moyang mereka dengan perantaraan Musa.'),(6573,6,3,5,'Demikianlah orang Israel itu diam di tengah-tengah orang Kanaan, orang Het, orang Amori, orang Feris, orang Hewi dan orang Yebus.'),(6574,6,3,6,'Mereka mengambil anak-anak perempuan, orang-orang itu menjadi isteri mereka dan memberikan anak-anak perempuan mereka kepada anak-anak lelaki orang-orang itu, serta beribadah kepada allah orang-orang itu.'),(6575,6,3,7,'Orang Israel melakukan apa yang jahat di mata TUHAN, mereka melupakan TUHAN, Allah mereka, dan beribadah kepada para Baal dan para Asyera.'),(6576,6,3,8,'Lalu bangkitlah murka TUHAN terhadap orang Israel, sehingga Ia menjual mereka kepada Kusyan-Risyataim, raja Aram-Mesopotamia dan orang Israel menjadi takluk kepada Kusyan-Risyataim delapan tahun lamanya.'),(6577,6,3,9,'Lalu berserulah orang Israel kepada TUHAN, maka TUHAN membangkitkan seorang penyelamat bagi orang Israel, yakni Otniel, anak Kenas adik Kaleb.'),(6578,6,3,10,'Roh TUHAN menghinggapi dia dan ia menghakimi orang Israel. Ia maju berperang, lalu TUHAN menyerahkan Kusyan-Risyataim, raja Aram, ke dalam tangannya, sehingga ia mengalahkan Kusyan-Risyataim.'),(6579,6,3,11,'Lalu amanlah negeri itu empat puluh tahun lamanya. Kemudian matilah Otniel anak Kenas.'),(6580,6,3,12,'Tetapi orang Israel melakukan pula apa yang jahat di mata TUHAN; lalu Eglon, raja Moab, diberi TUHAN kuasa atas orang Israel, oleh sebab mereka telah melakukan apa yang jahat di mata TUHAN.'),(6581,6,3,13,'Raja ini mengajak bani Amon dan bani Amalek menjadi sekutunya. Lalu majulah ia dan memukul orang Israel kalah. Kota pohon korma diduduki mereka.'),(6582,6,3,14,'Delapan belas tahun lamanya orang Israel menjadi takluk kepada Eglon, raja Moab.'),(6583,6,3,15,'Lalu orang Israel berseru kepada TUHAN, maka TUHAN membangkitkan bagi mereka seorang penyelamat yakni Ehud, anak Gera, orang Benyamin, seorang yang kidal. Dengan perantaraannya orang Israel biasa mengirimkan upeti kepada Eglon, raja Moab.'),(6584,6,3,16,'Dan Ehud membuat pedang yang bermata dua, yang panjangnya hampir sehasta, disandangnyalah itu di bawah pakaiannya, pada pangkal paha kanannya.'),(6585,6,3,17,'Kemudian ia menyampaikan upeti kepada Eglon, raja Moab. Adapun Eglon itu seorang yang sangat gendut.'),(6586,6,3,18,'Setelah Ehud selesai menyampaikan upeti itu, disuruhnya pembawa-pembawa upeti itu pulang,'),(6587,6,3,19,'tetapi ia sendiri berhenti pada batu-batu berpahat yang di dekat Gilgal, dan kembali menghadap raja. Berkatalah ia: \"Ada pesan rahasia yang kubawa untuk tuanku, ya raja.\" Kata Eglon: \"Diamlah dahulu!\" Maka semua orang yang berdiri di depannya itu pergi ke luar.'),(6588,6,3,20,'Lalu Ehud masuk mendapatkan dia, sedang ia duduk sendirian di kamar atas di rumah peranginannya. Berkatalah Ehud: \"Ada firman Allah yang kubawa untuk tuanku.\" Lalu bangunlah ia berdiri dari tempat duduknya.'),(6589,6,3,21,'Kemudian Ehud mengulurkan tangan kirinya, dihunusnya pedang itu dari pangkal paha kanannya dan ditikamkannya ke perut raja,'),(6590,6,3,22,'sehingga hulunya beserta mata pedang itu masuk. Lemak menutupi mata pedang itu, sebab pedang itu tidak dicabutnya dari perut raja. Lalu keluarlah ia melalui pintu belakang.'),(6591,6,3,23,'Demikianlah Ehud sampai ke serambi; pintu kamar atas itu ditutup dan dikuncinya setelah ia keluar.'),(6592,6,3,24,'Baru saja ia keluar, datanglah hamba-hamba raja melihat, tetapi pintu kamar atas itu terkunci. Lalu berkatalah mereka: \"Tentulah ia membuang air di kamar rumah peranginan itu.\"'),(6593,6,3,25,'Lalu mereka menunggu-nunggu sampai menjadi bingung, tetapi raja tidak membuka pintu kamar atas itu. Kemudian mereka mengambil kunci, membuka pintu, maka tampaklah tuan mereka mati tergeletak di lantai.'),(6594,6,3,26,'Sedang mereka berlambat-lambat, Ehud meloloskan diri; ia lewat dari batu-batu berpahat dan meloloskan diri ke arah Seira.'),(6595,6,3,27,'Setelah ia sampai ke sana, ditiupnyalah sangkakala di pegunungan Efraim, lalu turunlah orang Israel bersama-sama dengan dia dari pegunungan itu, dan ia sendiri di depan.'),(6596,6,3,28,'Berkatalah ia kepada mereka: \"Ikutlah aku, sebab TUHAN telah menyerahkan musuhmu, orang-orang Moab itu, ke dalam tanganmu.\" Maka turunlah mereka mengikuti dia, lalu mereka merebut tempat penyeberangan sungai Yordan ke Moab dan tidak seorangpun dibiarkan mereka menyeberang.'),(6597,6,3,29,'Pada waktu itu mereka menewaskan kira-kira sepuluh ribu orang dari Moab, semuanya orang yang tegap dan tangkas, seorangpun tidak ada yang lolos.'),(6598,6,3,30,'Demikianlah pada hari itu Moab ditundukkan oleh Israel, maka amanlah tanah itu, delapan puluh tahun lamanya.'),(6599,6,3,31,'Sesudah dia, bangkitlah Samgar bin Anat; ia menewaskan orang Filistin dengan tongkat penghalau lembu, enam ratus orang banyaknya. Demikianlah ia juga menyelamatkan orang Israel.'),(6600,6,4,1,'Setelah Ehud mati, orang Israel melakukan pula apa yang jahat di mata TUHAN.'),(6601,6,4,2,'Lalu TUHAN menyerahkan mereka ke dalam tangan Yabin, raja Kanaan, yang memerintah di Hazor. Panglima tentaranya ialah Sisera yang diam di Haroset-Hagoyim.'),(6602,6,4,3,'Lalu orang Israel berseru kepada TUHAN, sebab Sisera mempunyai sembilan ratus kereta besi dan dua puluh tahun lamanya ia menindas orang Israel dengan keras.'),(6603,6,4,4,'Pada waktu itu Debora, seorang nabiah, isteri Lapidot, memerintah sebagai hakim atas orang Israel.'),(6604,6,4,5,'Ia biasa duduk di bawah pohon korma Debora antara Rama dan Betel di pegunungan Efraim, dan orang Israel menghadap dia untuk berhakim kepadanya.'),(6605,6,4,6,'Ia menyuruh memanggil Barak bin Abinoam dari Kedesh di daerah Naftali, lalu berkata kepadanya: \"Bukankah TUHAN, Allah Israel, memerintahkan demikian: Majulah, bergeraklah menuju gunung Tabor dengan membawa sepuluh ribu orang bani Naftali dan bani Zebulon bersama-sama dengan engkau,'),(6606,6,4,7,'dan Aku akan menggerakkan Sisera, panglima tentara Yabin, dengan kereta-keretanya dan pasukan-pasukannya menuju engkau ke sungai Kison dan Aku akan menyerahkan dia ke dalam tanganmu.\"'),(6607,6,4,8,'Jawab Barak kepada Debora: \"Jika engkau turut maju akupun maju, tetapi jika engkau tidak turut maju akupun tidak maju.\"'),(6608,6,4,9,'Kata Debora: \"Baik, aku turut! Hanya, engkau tidak akan mendapat kehormatan dalam perjalanan yang engkau lakukan ini, sebab TUHAN akan menyerahkan Sisera ke dalam tangan seorang perempuan.\" Lalu Debora bangun berdiri dan pergi bersama-sama dengan Barak ke Kedesh.'),(6609,6,4,10,'Barak mengerahkan suku Zebulon dan suku Naftali ke Kedesh, maka sepuluh ribu orang maju mengikuti dia; juga Debora maju bersama-sama dengan dia.'),(6610,6,4,11,'Adapun Heber, orang Keni itu, telah memisahkan diri dari suku Keni, dari anak-anak Hobab ipar Musa, dan telah berpindah-pindah memasang kemahnya sampai ke pohon tarbantin di Zaanaim yang dekat Kedesh.'),(6611,6,4,12,'Setelah dikabarkan kepada Sisera, bahwa Barak bin Abinoam telah maju ke gunung Tabor,'),(6612,6,4,13,'dikerahkannyalah segala keretanya, sembilan ratus kereta besi, dan seluruh rakyat yang bersama-sama dengan dia, dari Haroset-Hagoyim ke sungai Kison.'),(6613,6,4,14,'Lalu berkatalah Debora kepada Barak: \"Bersiaplah, sebab inilah harinya TUHAN menyerahkan Sisera ke dalam tanganmu. Bukankah TUHAN telah maju di depan engkau?\" Lalu turunlah Barak dari gunung Tabor dan sepuluh ribu orang mengikuti dia,'),(6614,6,4,15,'dan TUHAN mengacaukan Sisera serta segala keretanya dan seluruh tentaranya oleh mata pedang di depan Barak, sehingga Sisera turun dari keretanya dan melarikan diri dengan berjalan kaki.'),(6615,6,4,16,'Lalu Barak mengejar kereta-kereta dan tentara itu sampai ke Haroset-Hagoyim, dan seluruh tentara Sisera tewas oleh mata pedang; tidak ada seorangpun yang tinggal hidup.'),(6616,6,4,17,'Tetapi Sisera dengan berjalan kaki melarikan diri ke kemah Yael, isteri Heber, orang Keni itu, sebab ada perhubungan baik antara Yabin, raja Hazor, dengan keluarga Heber, orang Keni itu.'),(6617,6,4,18,'Yael itupun keluar mendapatkan Sisera, dan berkata kepadanya: \"Singgahlah, tuanku, silakan masuk. Jangan takut.\" Lalu singgahlah ia ke dalam kemah perempuan itu dan perempuan itu menutupi dia dengan selimut.'),(6618,6,4,19,'Kemudian berkatalah ia kepada perempuan itu: \"Berilah kiranya aku minum air sedikit, aku haus.\" Lalu perempuan itu membuka kirbat susu, diberinyalah dia minum dan diselimutinya pula.'),(6619,6,4,20,'Lagi katanya kepada perempuan itu: \"Berdirilah di depan pintu kemah dan apabila ada orang datang dan bertanya kepadamu: Ada orang di sini?, maka jawablah: Tidak ada.\"'),(6620,6,4,21,'Tetapi Yael, isteri Heber, mengambil patok kemah, diambilnya pula palu, mendekatinya diam-diam, lalu dilantaknyalah patok itu masuk ke dalam pelipisnya sampai tembus ke tanah--sebab ia telah tidur nyenyak karena lelahnya--maka matilah orang itu.'),(6621,6,4,22,'Pada waktu itu muncullah Barak yang mengejar Sisera. Keluarlah Yael mendapatkan dia dan berkata kepadanya: \"Mari, aku akan menunjukkan kepadamu orang yang kaucari itu.\" Lalu masuklah Barak ke dalam dan tampaklah Sisera mati tergeletak dengan patok dalam pelipisnya.'),(6622,6,4,23,'Demikianlah Allah pada hari itu menundukkan Yabin, raja Kanaan, di depan orang Israel.'),(6623,6,4,24,'Dan kekuasaan orang Israel kian keras menekan Yabin, raja Kanaan, sampai mereka melenyapkan Yabin, raja Kanaan itu.'),(6624,6,5,1,'Pada hari itu bernyanyilah Debora dan Barak bin Abinoam, demikian:'),(6625,6,5,2,'Karena pahlawan-pahlawan di Israel siap berperang, karena bangsa itu menawarkan dirinya dengan sukarela, pujilah TUHAN!'),(6626,6,5,3,'Dengarlah, ya raja-raja! Pasanglah telingamu, ya pemuka-pemuka! Kalau aku, aku mau bernyanyi bagi TUHAN, bermazmur bagi TUHAN, Allah Israel.'),(6627,6,5,4,'TUHAN, ketika Engkau bergerak dari Seir, ketika Engkau melangkah maju dari daerah Edom, bergoncanglah bumi, tirislah juga langit, juga awan tiris airnya;'),(6628,6,5,5,'gunung-gunung--yakni Sinai--bergoyang di hadapan TUHAN, di hadapan TUHAN, Allah Israel.'),(6629,6,5,6,'Dalam zaman Samgar bin Anat, dalam zaman Yael, kafilah tidak ada lagi dan orang-orang yang dalam perjalanan terpaksa menempuh jalan yang berbelit-belit.'),(6630,6,5,7,'Penduduk pedusunan diam-diam saja di Israel, ya mereka diam-diam, sampai engkau bangkit, Debora, bangkit sebagai ibu di Israel.'),(6631,6,5,8,'Ketika orang memilih allah baru, maka terjadilah perang di pintu gerbang. Sesungguhnya, perisai ataupun tombak tidak terlihat di antara empat puluh ribu orang di Israel.'),(6632,6,5,9,'Hatiku tertuju kepada para panglima Israel, kepada mereka yang menawarkan dirinya dengan sukarela di antara bangsa itu. Pujilah TUHAN!'),(6633,6,5,10,'Kamu, yang menunggang keledai betina putih, kamu, yang duduk di atas permadani, kamu, yang berjalan di jalan, ceriterakanlah hal itu!'),(6634,6,5,11,'Di tempat-tempat penimbaan air, menurut suara orang-orang yang berdendang, di sanalah orang menyanyikan perbuatan TUHAN yang adil, perbuatan-Nya yang adil terhadap orang-orang-Nya di pedusunan di Israel. Pada waktu itu turunlah umat TUHAN ke pintu gerbang.'),(6635,6,5,12,'Bangunlah, bangunlah, Debora! Bangunlah, bangunlah, nyanyikanlah suatu nyanyian! Bangkitlah, Barak! dan giringlah tawananmu, hai anak Abinoam!'),(6636,6,5,13,'Lalu turunlah para bangsawan yang terluput, umat TUHAN turun bagi-Nya sebagai pahlawan.'),(6637,6,5,14,'Dari suku Efraim mereka datang ke lembah, mengikuti engkau, ya suku Benyamin, dengan laskarmu; dari suku Makhir turunlah para panglima dan dari suku Zebulon orang-orang pembawa tongkat pengerah.'),(6638,6,5,15,'Juga para pemimpin suku Isakhar menyertai Debora, dan seperti Isakhar, demikianlah Naftali menyertai Barak. Mereka menyusul dia dan menyerbu masuk lembah. Tetapi pihak pasukan-pasukan suku Ruben ada banyak pertimbangan.'),(6639,6,5,16,'Mengapa engkau tinggal duduk di antara kandang-kandang sambil mendengarkan seruling pemanggil kawanan? Di pihak pasukan-pasukan suku Ruben ada banyak pertimbangan!'),(6640,6,5,17,'Orang Gilead tinggal diam di seberang sungai Yordan; dan suku Dan, mengapa mereka tinggal dekat kapal-kapal? Suku Asyer duduk di tepi pantai laut, tinggal diam di teluk-teluknya.'),(6641,6,5,18,'Tetapi suku Zebulon ialah bangsa yang berani mempertaruhkan nyawanya, demikian juga suku Naftali, di tempat-tempat tinggi di padang.'),(6642,6,5,19,'Raja-raja datang dan berperang, pada waktu itu raja-raja Kanaan berperang dekat Taanakh, pada mata air di Megido, tetapi perak sebagai rampasan tidak diperoleh mereka.'),(6643,6,5,20,'Dari langit berperang bintang-bintang, dari peredarannya mereka memerangi Sisera.'),(6644,6,5,21,'Sungai Kison menghanyutkan musuh, Kison, sungai yang terkenal dari dahulu kala itu. --Majulah sekuat tenaga, hai jiwaku! --'),(6645,6,5,22,'Ketika itu menderaplah telapak kuda, karena berpacu lari kuda-kudanya.'),(6646,6,5,23,'Kutukilah kota Meros!'),(6647,6,5,24,'Diberkatilah Yael, isteri Heber, orang Keni itu, melebihi perempuan-perempuan lain, diberkatilah ia, melebihi perempuan-perempuan yang di dalam kemah.'),(6648,6,5,25,'Air diminta orang itu, tetapi susu diberikannya; dalam cawan yang indah disuguhkannya dadih.'),(6649,6,5,26,'Tangannya diulurkannya mengambil patok, tangan kanannya mengambil tukul tukang, ditukulnya Sisera, dihancurkannya kepalanya, diremukkan dan ditembusnya pelipisnya.'),(6650,6,5,27,'Dekat kakinya orang itu rebah, tewas tergeletak, dekat kakinya orang itu rebah dan tewas, di tempat ia rebah, di sanalah orang itu tewas, digagahi.'),(6651,6,5,28,'Dari jendela ibu Sisera menjenguk dan berseru dari tingkap: \"Mengapa keretanya tak kunjung datang? Mengapa kereta-keretanya belum kedengaran?\"'),(6652,6,5,29,'Yang paling bijak di antara dayang-dayangnya menjawabnya, dan ia sendiri juga membalas perkataannya itu:'),(6653,6,5,30,'Bukankah mereka mendapat jarahan dan membagi-baginya, gadis seorang dua untuk setiap orang jarahan kain berwarna sehelai dua untuk Sisera, jarahan kain sulaman aneka warna sehelai dua untuk leherku?'),(6654,6,5,31,'Demikianlah akan binasa segala musuh-Mu, ya TUHAN! Tetapi orang yang mengasihi-Nya bagaikan matahari terbit dalam kemegahannya. Lalu amanlah negeri itu empat puluh tahun lamanya.'),(6655,6,6,1,'Tetapi orang Israel melakukan apa yang jahat di mata TUHAN; sebab itu TUHAN menyerahkan mereka ke dalam tangan orang Midian, tujuh tahun lamanya,'),(6656,6,6,2,'dan selama itu orang Midian berkuasa atas orang Israel. Karena takutnya kepada orang Midian itu, maka orang Israel membuat tempat-tempat perlindungan di pegunungan, yakni gua-gua dan kubu-kubu.'),(6657,6,6,3,'Setiap kali orang Israel selesai menabur, datanglah orang Midian, orang Amalek dan orang-orang dari sebelah timur, lalu maju mendatangi mereka;'),(6658,6,6,4,'berkemahlah orang-orang itu di daerah mereka, dan memusnahkan hasil tanah itu sampai ke dekat Gaza, dan tidak meninggalkan bahan makanan apapun di Israel, juga domba, atau lembu atau keledaipun tidak.'),(6659,6,6,5,'Sebab orang-orang itu datang maju dengan ternaknya dan kemahnya, dan datangnya itu berbanyak-banyak seperti belalang. Orang-orangnya dan unta-untanya tidak terhitung banyaknya, sekaliannya datang ke negeri itu untuk memusnahkannya,'),(6660,6,6,6,'sehingga orang Israel menjadi sangat melarat oleh perbuatan orang Midian itu. Lalu berserulah orang Israel kepada TUHAN.'),(6661,6,6,7,'Ketika orang Israel berseru kepada TUHAN karena orang Midian itu,'),(6662,6,6,8,'maka TUHAN mengutus seorang nabi kepada orang Israel, yang berkata kepada mereka: \"Beginilah firman TUHAN, Allah Israel: Akulah yang menuntun kamu keluar dari Mesir dan yang membawa kamu keluar dari rumah perbudakan.'),(6663,6,6,9,'Aku melepaskan kamu dari tangan orang Mesir dan dari tangan semua orang yang menindas kamu, bahkan Aku menghalau mereka dari depanmu dan negeri mereka Kuberikan kepadamu.'),(6664,6,6,10,'Dan Aku telah berfirman kepadamu: Akulah TUHAN, Allahmu, maka janganlah kamu menyembah allah orang Amori, yang negerinya kamu diami ini. Tetapi kamu tidak mendengarkan firman-Ku itu.\"'),(6665,6,6,11,'Kemudian datanglah Malaikat TUHAN dan duduk di bawah pohon tarbantin di Ofra, kepunyaan Yoas, orang Abiezer itu, sedang Gideon, anaknya, mengirik gandum dalam tempat pemerasan anggur agar tersembunyi bagi orang Midian.'),(6666,6,6,12,'Malaikat TUHAN menampakkan diri kepadanya dan berfirman kepadanya, demikian: \"TUHAN menyertai engkau, ya pahlawan yang gagah berani.\"'),(6667,6,6,13,'Jawab Gideon kepada-Nya: \"Ah, tuanku, jika TUHAN menyertai kami, mengapa semuanya ini menimpa kami? Di manakah segala perbuatan-perbuatan-yang ajaib yang diceritakan oleh nenek moyang kami kepada kami, ketika mereka berkata: Bukankah TUHAN telah menuntun kita keluar dari Mesir? Tetapi sekarang TUHAN membuang kami dan menyerahkan kami ke dalam cengkeraman orang Midian.\"'),(6668,6,6,14,'Lalu berpalinglah TUHAN kepadanya dan berfirman: \"Pergilah dengan kekuatanmu ini dan selamatkanlah orang Israel dari cengkeraman orang Midian. Bukankah Aku mengutus engkau!\"'),(6669,6,6,15,'Tetapi jawabnya kepada-Nya: \"Ah Tuhanku, dengan apakah akan kuselamatkan orang Israel? Ketahuilah, kaumku adalah yang paling kecil di antara suku Manasye dan akupun seorang yang paling muda di antara kaum keluargaku.\"'),(6670,6,6,16,'Berfirmanlah TUHAN kepadanya: \"Tetapi Akulah yang menyertai engkau, sebab itu engkau akan memukul kalah orang Midian itu sampai habis.\"'),(6671,6,6,17,'Maka jawabnya kepada-Nya: \"Jika sekiranya aku mendapat kasih karunia di mata-Mu, maka berikanlah kepadaku tanda, bahwa Engkau sendirilah yang berfirman kepadaku.'),(6672,6,6,18,'Janganlah kiranya pergi dari sini, sampai aku datang kepada-Mu membawa persembahanku dan meletakkannya di hadapan-Mu.\" Firman-Nya: \"Aku akan tinggal, sampai engkau kembali.\"'),(6673,6,6,19,'Masuklah Gideon ke dalam, lalu mengolah seekor anak kambing dan roti yang tidak beragi dari seefa tepung; ditaruhnya daging itu ke dalam bakul dan kuahnya ke dalam periuk, dibawanya itu kepada-Nya ke bawah pohon tarbantin, lalu disuguhkannya.'),(6674,6,6,20,'Berfirmanlah Malaikat Allah kepadanya: \"Ambillah daging dan roti yang tidak beragi itu, letakkanlah ke atas batu ini, dan curahkan kuahnya.\" Maka diperbuatnya demikian.'),(6675,6,6,21,'Dan Malaikat TUHAN mengulurkan tongkat yang ada di tangan-Nya; dengan ujungnya disinggung-Nya daging dan roti itu; maka timbullah api dari batu itu dan memakan habis daging dan roti itu. Kemudian hilanglah Malaikat TUHAN dari pandangannya.'),(6676,6,6,22,'Maka tahulah Gideon, bahwa itulah Malaikat TUHAN, lalu katanya: \"Celakalah aku, Tuhanku ALLAH! sebab memang telah kulihat Malaikat TUHAN dengan berhadapan muka.\"'),(6677,6,6,23,'Tetapi berfirmanlah TUHAN kepadanya: \"Selamatlah engkau! Jangan takut, engkau tidak akan mati.\"'),(6678,6,6,24,'Lalu Gideon mendirikan mezbah di sana bagi TUHAN dan menamainya: TUHAN itu keselamatan. Mezbah itu masih ada sampai sekarang di Ofra, kota orang Abiezer.'),(6679,6,6,25,'Pada malam itu juga TUHAN berfirman kepadanya: \"Ambillah seekor lembu jantan kepunyaan ayahmu, yakni lembu jantan yang kedua, berumur tujuh tahun, runtuhkanlah mezbah Baal kepunyaan ayahmu dan tebanglah tiang berhala yang di dekatnya.'),(6680,6,6,26,'Kemudian dirikanlah mezbah bagi TUHAN, Allahmu, di atas kubu pertahanan ini dengan disusun baik, lalu ambillah lembu jantan yang kedua dan persembahkanlah korban bakaran dengan kayu tiang berhala yang akan kautebang itu.\"'),(6681,6,6,27,'Kemudian Gideon membawa sepuluh orang hambanya dan diperbuatnyalah seperti yang difirmankan TUHAN kepadanya. Tetapi karena ia takut kepada kaum keluarganya dan kepada orang-orang kota itu untuk melakukan hal itu pada waktu siang, maka dilakukannyalah pada waktu malam.'),(6682,6,6,28,'Ketika orang-orang kota itu bangun pagi-pagi, tampaklah telah dirobohkan mezbah Baal itu, telah ditebang tiang berhala yang di dekatnya dan telah dikorbankan lembu jantan yang kedua di atas mezbah yang didirikan itu.'),(6683,6,6,29,'Berkatalah mereka seorang kepada yang lain: \"Siapakah yang melakukan hal itu?\" Setelah diperiksa dan ditanya-tanya, maka kata orang: \"Gideon bin Yoas, dialah yang melakukan hal itu.\"'),(6684,6,6,30,'Sesudah itu berkatalah orang-orang kota itu kepada Yoas: \"Bawalah anakmu itu ke luar; dia harus mati, karena ia telah merobohkan mezbah Baal dan karena ia telah menebang tiang berhala yang di dekatnya.\"'),(6685,6,6,31,'Tetapi jawab Yoas kepada semua orang yang mengerumuninya itu: \"Kamu mau berjuang membela Baal? Atau kamu mau menolong dia? Siapa yang berjuang membela Baal akan dihukum mati sebelum pagi. Jika Baal itu allah, biarlah ia berjuang membela dirinya sendiri, setelah mezbahnya dirobohkan orang.\"'),(6686,6,6,32,'Dan pada hari itu diberikan oranglah nama Yerubaal kepada Gideon, karena kata orang: \"Biarlah Baal berjuang dengan dia, setelah dirobohkannya mezbahnya itu.\"'),(6687,6,6,33,'Seluruh orang Midian dan orang Amalek dan orang-orang dari sebelah timur telah berkumpul bersama-sama; mereka telah menyeberang dan berkemah di lembah Yizreel.'),(6688,6,6,34,'Pada waktu itu Roh TUHAN menguasai Gideon; ditiupnyalah sangkakala dan orang-orang Abiezer dikerahkan untuk mengikuti dia.'),(6689,6,6,35,'Juga dikirimnya pesan kepada seluruh suku Manasye dan orang-orang inipun dikerahkan untuk mengikuti dia. Dikirimnya pula pesan kepada suku Asyer, Zebulon dan Naftali, dan orang-orang inipun maju untuk menggabungkan diri dengan mereka.'),(6690,6,6,36,'Kemudian berkatalah Gideon kepada Allah: \"Jika Engkau mau menyelamatkan orang Israel dengan perantaraanku, seperti yang Kaufirmankan itu,'),(6691,6,6,37,'maka aku membentangkan guntingan bulu domba di tempat pengirikan; apabila hanya di atas guntingan bulu itu ada embun, tetapi seluruh tanah di situ tinggal kering, maka tahulah aku, bahwa Engkau mau menyelamatkan orang Israel dengan perantaraanku, seperti yang Kaufirmankan.\"'),(6692,6,6,38,'Dan demikianlah terjadi; sebab keesokan harinya pagi-pagi ia bangun, dipulasnya guntingan bulu itu dan diperasnya air embun dari guntingan bulu itu, secawan penuh air.'),(6693,6,6,39,'Lalu berkatalah Gideon kepada Allah: \"Janganlah kiranya murka-Mu bangkit terhadap aku, apabila aku berkata lagi, sekali ini saja; biarkanlah aku satu kali lagi saja mengambil percobaan dengan guntingan bulu itu: sekiranya yang kering hanya guntingan bulu itu, dan di atas seluruh tanah itu ada embun.\"'),(6694,6,6,40,'Dan demikianlah diperbuat Allah pada malam itu, sebab hanya guntingan bulu itu yang kering, dan di atas seluruh tanah itu ada embun.'),(6695,6,7,1,'Adapun Yerubaal--itulah Gideon--bangun pagi-pagi dengan segala rakyat yang bersama-sama dengan dia, lalu mereka berkemah dekat mata air Harod; perkemahan orang Midian itu ada di sebelah utaranya, dekat bukit More, di lembah.'),(6696,6,7,2,'Berfirmanlah TUHAN kepada Gideon: \"Terlalu banyak rakyat yang bersama-sama dengan engkau itu dari pada yang Kuhendaki untuk menyerahkan orang Midian ke dalam tangan mereka, jangan-jangan orang Israel memegah-megahkan diri terhadap Aku, sambil berkata: Tanganku sendirilah yang menyelamatkan aku.'),(6697,6,7,3,'Maka sekarang, serukanlah kepada rakyat itu, demikian: Siapa yang takut dan gentar, biarlah ia pulang, enyah dari pegunungan Gilead.\" Lalu pulanglah dua puluh dua ribu orang dari rakyat itu dan tinggallah sepuluh ribu orang.'),(6698,6,7,4,'Tetapi TUHAN berfirman kepada Gideon: \"Masih terlalu banyak rakyat; suruhlah mereka turun minum air, maka Aku akan menyaring mereka bagimu di sana. Siapa yang Kufirmankan kepadamu: Inilah orang yang akan pergi bersama-sama dengan engkau, dialah yang akan pergi bersama-sama dengan engkau, tetapi barangsiapa yang Kufirmankan kepadamu: Inilah orang yang tidak akan pergi bersama-sama dengan engkau, dialah yang tidak akan pergi.\"'),(6699,6,7,5,'Lalu Gideon menyuruh rakyat itu turun minum air, dan berfirmanlah TUHAN kepadanya: \"Barangsiapa yang menghirup air dengan lidahnya seperti anjing menjilat, haruslah kaukumpulkan tersendiri, demikian juga semua orang yang berlutut untuk minum.\"'),(6700,6,7,6,'Jumlah orang yang menghirup dengan membawa tangannya ke mulutnya, ada tiga ratus orang, tetapi yang lain dari rakyat itu semuanya berlutut minum air.'),(6701,6,7,7,'Lalu berfirmanlah TUHAN kepada Gideon: \"Dengan ketiga ratus orang yang menghirup itu akan Kuselamatkan kamu: Aku akan menyerahkan orang Midian ke dalam tanganmu; tetapi yang lain dari rakyat itu semuanya boleh pergi, masing-masing ke tempat kediamannya.\"'),(6702,6,7,8,'Dari rakyat itu mereka mengambil bekal dan sangkakala; demikianlah seluruh orang Israel disuruhnya pergi, masing-masing ke kemahnya, tetapi ketiga ratus orang itu ditahannya. Adapun perkemahan orang Midian ada di bawahnya, di lembah.'),(6703,6,7,9,'Pada malam itu berfirmanlah TUHAN kepadanya: \"Bangunlah, turunlah menyerbu perkemahan itu, sebab telah Kuserahkan itu ke dalam tanganmu.'),(6704,6,7,10,'Tetapi jika engkau takut untuk turun menyerbu, turunlah bersama dengan Pura, bujangmu, ke perkemahan itu;'),(6705,6,7,11,'maka kaudengarlah apa yang mereka katakan; kemudian engkau akan mendapat keberanian untuk turun menyerbu perkemahan itu.\" Lalu turunlah ia bersama dengan Pura, bujangnya itu, sampai kepada penjagaan terdepan laskar di perkemahan itu.'),(6706,6,7,12,'Adapun orang Midian dan orang Amalek dan semua orang dari sebelah timur itu bergelimpangan di lembah itu, seperti belalang banyaknya, dan unta mereka tidak terhitung, seperti pasir di tepi laut banyaknya.'),(6707,6,7,13,'Ketika Gideon sampai ke situ, kebetulan ada seorang menceritakan mimpinya kepada temannya, katanya: \"Aku bermimpi: tampak sekeping roti jelai terguling masuk ke perkemahan orang Midian; setelah sampai ke kemah ini, dilanggarnyalah kemah ini, sehingga roboh, dan dibongkar-bangkirkannya, demikianlah kemah ini habis runtuh.\"'),(6708,6,7,14,'Lalu temannya menjawab: \"Ini tidak lain dari pedang Gideon bin Yoas, orang Israel itu; Allah telah menyerahkan orang Midian dan seluruh perkemahan ini ke dalam tangannya.\"'),(6709,6,7,15,'Segera sesudah Gideon mendengar mimpi itu diceritakan dengan maknanya, sujudlah ia menyembah. Kemudian pulanglah ia ke perkemahan orang Israel, lalu berkata: \"Bangunlah, sebab TUHAN telah menyerahkan perkemahan orang Midian ke dalam tanganmu.\"'),(6710,6,7,16,'Sesudah itu dibaginyalah ketiga ratus orang itu dalam tiga pasukan dan ke tangan mereka semuanya diberikannya sangkakala dan buyung kosong dengan suluh di dalam buyung itu.'),(6711,6,7,17,'Dan berkatalah ia kepada mereka: \"Perhatikanlah aku dan lakukanlah seperti yang kulakukan. Maka apabila aku sampai ke ujung perkemahan itu, haruslah kamu lakukan seperti yang kulakukan.'),(6712,6,7,19,'Lalu Gideon dan keseratus orang yang bersama-sama dengan dia sampai ke ujung perkemahan itu pada waktu permulaan giliran jaga tengah malam, ketika penjaga-penjaga baru saja ditempatkan. Lalu mereka meniup sangkakala sambil memecahkan buyung yang di tangan mereka.'),(6713,6,7,20,'Demikianlah ketiga pasukan itu bersama-sama meniup sangkakala, dan memecahkan buyung dengan memegang obor di tangan kirinya dan sangkakala di tangan kanannya untuk ditiup, serta berseru: \"Pedang demi TUHAN dan demi Gideon!\"'),(6714,6,7,21,'Sementara itu tinggallah mereka berdiri, masing-masing di tempatnya, sekeliling perkemahan itu, tetapi seluruh tentara musuh menjadi kacau balau, berteriak-teriak dan melarikan diri.'),(6715,6,7,22,'Sedang ketiga ratus orang itu meniup sangkakala, maka di perkemahan itu TUHAN membuat pedang yang seorang diarahkan kepada yang lain, lalu larilah tentara itu sampai ke Bet-Sita ke arah Zerera sampai ke pinggir Abel-Mehola dekat Tabat.'),(6716,6,7,23,'Kemudian dikerahkanlah orang-orang Israel dari suku Naftali dan dari suku Asyer dan dari segenap suku Manasye, lalu mereka mengejar orang Midian itu.'),(6717,6,7,24,'Gideon menyuruh juga orang ke seluruh pegunungan Efraim dengan pesan: \"Turunlah menghadapi orang Midian, dan dudukilah segala batang air sampai ke Bet-Bara, dan juga sungai Yordan.\" Maka semua orang Efraim dikerahkan, lalu mereka menduduki segala batang air sampai ke Bet-Bara, juga sungai Yordan.'),(6718,6,7,25,'Mereka berhasil menawan dua raja Midian, yakni Oreb dan Zeeb. Oreb dibunuh di gunung batu Oreb dan Zeeb dibunuh dalam tempat pemerasan anggur Zeeb. Mereka mengejar orang Midian itu, lalu mereka membawa kepala Oreb dan kepala Zeeb kepada Gideon di seberang sungai Yordan.'),(6719,6,8,1,'Lalu berkatalah orang-orang Efraim kepada Gideon: \"Apa macam perbuatanmu ini terhadap kami! Mengapa engkau tidak memanggil kami, ketika engkau pergi berperang melawan orang Midian?\" Lalu mereka menyesali dia dengan sangat.'),(6720,6,8,2,'Jawabnya kepada mereka: \"Apa perbuatanku dalam hal ini, jika dibandingkan dengan kamu? Bukankah pemetikan susulan oleh suku Efraim lebih baik hasilnya dari panen buah anggur kaum Abiezer?'),(6721,6,8,3,'Allah telah menyerahkan kedua raja Midian itu, yakni Oreb dan Zeeb, ke dalam tanganmu; apa yang telah dapat kucapai, jika dibandingkan dengan kamu?\" Setelah ia berkata demikian, maka redalah marah mereka terhadap dia.'),(6722,6,8,4,'Ketika Gideon sampai ke sungai Yordan, menyeberanglah ia dan ketiga ratus orang yang bersama-sama dengan dia, meskipun masih lelah, namun mengejar juga.'),(6723,6,8,5,'Dan berkatalah ia kepada orang-orang Sukot: \"Tolong berikan beberapa roti untuk rakyat yang mengikuti aku ini, sebab mereka telah lelah, dan aku sedang mengejar Zebah dan Salmuna, raja-raja Midian.\"'),(6724,6,8,6,'Tetapi jawab para pemuka di Sukot itu: \"Sudahkah Zebah dan Salmuna itu ada dalam tanganmu, sehingga kami harus memberikan roti kepada tentaramu?\"'),(6725,6,8,7,'Lalu kata Gideon: \"Kalau begitu, apabila TUHAN menyerahkan Zebah dan Salmuna ke dalam tanganku, aku akan menggaruk tubuhmu dengan duri padang gurun dan onak.\"'),(6726,6,8,8,'Maka berjalanlah ia dari sana ke Pnuel, dan berkata demikian juga kepada orang-orang Pnuel, tetapi orang-orang inipun menjawabnya seperti orang-orang Sukot.'),(6727,6,8,9,'Lalu berkatalah ia juga kepada orang-orang Pnuel: \"Apabila aku kembali dengan selamat, maka aku akan merobohkan menara ini.\"'),(6728,6,8,10,'Sementara itu Zebah dan Salmuna ada di Karkor bersama-sama dengan tentara mereka, kira-kira lima belas ribu orang banyaknya, yakni semua orang yang masih tinggal hidup dari seluruh tentara orang-orang dari sebelah timur; banyaknya yang tewas ada seratus dua puluh ribu orang yang bersenjatakan pedang.'),(6729,6,8,11,'Gideon maju melalui jalan orang-orang yang diam di dalam kemah di sebelah timur Nobah dan Yogbeha, lalu memukul kalah tentara itu, ketika tentara itu menyangka dirinya aman.'),(6730,6,8,12,'Zebah dan Salmuna melarikan diri, tetapi Gideon mengejar mereka dan menawan kedua raja Midian itu, yakni Zebah dan Salmuna, sedang seluruh tentara itu diceraiberaikannya.'),(6731,6,8,13,'Kemudian kembalilah Gideon bin Yoas dari peperangan dengan melalui pendakian Heres;'),(6732,6,8,14,'ditangkapnyalah seorang muda dari penduduk Sukot. Setelah ditanyai, orang itu menuliskan nama para pemuka dan para tua-tua di Sukot untuk Gideon, tujuh puluh tujuh orang banyaknya.'),(6733,6,8,15,'Lalu pergilah Gideon kepada orang-orang Sukot sambil berkata: \"Inilah Zebah dan Salmuna yang karenanya kamu telah mencela aku dengan berkata: Sudahkah Zebah dan Salmuna itu ada dalam tanganmu, sehingga kami harus memberikan roti kepada orang-orangmu yang lelah itu?\"'),(6734,6,8,16,'Lalu ia mengumpulkan para tua-tua kota itu, ia mengambil duri padang gurun dan onak, dan menghajar orang-orang Sukot dengan itu.'),(6735,6,8,17,'Juga menara Pnuel dirobohkannya dan dibunuhnya orang-orang kota itu.'),(6736,6,8,18,'Kemudian bertanyalah ia kepada Zebah dan Salmuna: \"Di manakah orang-orang yang telah kamu bunuh di Tabor itu?\" Jawab mereka: \"Mereka itu serupa dengan engkau, sikap mereka masing-masing seperti anak raja.\"'),(6737,6,8,19,'Lalu kata Gideon: \"Saudara-saudarakulah itu, anak-anak ibuku! Demi TUHAN yang hidup, seandainya kamu membiarkan mereka hidup, aku tidak akan membunuh kamu.\"'),(6738,6,8,20,'Katanya kepada Yeter, anak sulungnya: \"Bangunlah, bunuhlah mereka.\" Tetapi orang muda itu tidak menghunus pedangnya, karena ia takut, sebab ia masih muda.'),(6739,6,8,21,'Lalu kata Zebah dan Salmuna: \"Bangunlah engkau sendiri dan paranglah kami, sebab seperti orangnya, demikian pula kekuatannya.\" Maka bangunlah Gideon, dibunuhnya Zebah dan Salmuna, kemudian diambilnya bulan-bulanan yang ada pada leher unta mereka.'),(6740,6,8,22,'Kemudian berkatalah orang Israel kepada Gideon: \"Biarlah engkau memerintah kami, baik engkau baik anakmu maupun cucumu, sebab engkaulah yang telah menyelamatkan kami dari tangan orang Midian.\"'),(6741,6,8,23,'Jawab Gideon kepada mereka: \"Aku tidak akan memerintah kamu dan juga anakku tidak akan memerintah kamu tetapi TUHAN yang memerintah kamu.\"'),(6742,6,8,24,'Selanjutnya kata Gideon kepada mereka: \"Satu hal saja yang kuminta kepadamu: Baiklah kamu masing-masing memberikan anting-anting dari jarahannya.\" --Karena musuh itu beranting-anting mas, sebab mereka orang Ismael.'),(6743,6,8,25,'Jawab mereka: \"Kami mau memberikannya dengan suka hati.\" Dan setelah dihamparkan sehelai kain, maka masing-masing melemparkan anting-anting dari jarahannya ke atas kain itu.'),(6744,6,8,26,'Adapun berat anting-anting emas yang dimintanya itu ada seribu tujuh ratus syikal emas, belum terhitung bulan-bulanan, perhiasan telinga dan pakaian kain ungu muda yang dipakai oleh raja-raja Midian, dan belum terhitung kalung rantai yang ada pada leher unta mereka.'),(6745,6,8,27,'Kemudian Gideon membuat efod dari semuanya itu dan menempatkannya di kotanya, di Ofra. Di sanalah orang Israel berlaku serong dengan menyembah efod itu; inilah yang menjadi jerat bagi Gideon dan seisi rumahnya.'),(6746,6,8,28,'Demikianlah orang Midian tunduk kepada orang Israel dan tidak dapat menegakkan kepalanya lagi; maka amanlah negeri itu empat puluh tahun lamanya pada zaman Gideon.'),(6747,6,8,29,'Lalu Yerubaal bin Yoas pergilah dan diam di rumahnya sendiri.'),(6748,6,8,30,'Gideon mempunyai tujuh puluh anak laki-laki, semuanya anak kandungnya, sebab ia beristeri banyak;'),(6749,6,8,31,'juga gundiknya yang tinggal di Sikhem melahirkan seorang anak laki-laki baginya, lalu ia memberikan nama Abimelekh kepada anak itu.'),(6750,6,8,32,'Gideon bin Yoas mati pada waktu rambutnya telah putih, lalu dikuburkan dalam kubur Yoas, ayahnya, di Ofra kota orang Abiezer.'),(6751,6,8,33,'Setelah Gideon mati, kembalilah orang Israel berjalan serong dengan mengikuti para Baal dan membuat Baal-Berit menjadi allah mereka;'),(6752,6,8,34,'orang Israel tidak ingat kepada TUHAN, Allah mereka, yang telah melepaskan mereka dari tangan semua musuhnya di sekelilingnya,'),(6753,6,8,35,'juga tidak menunjukkan terima kasihnya kepada keturunan Yerubaal-Gideon seimbang dengan segala yang baik yang telah dilakukannya kepada orang Israel.'),(6754,6,9,1,'Adapun Abimelekh bin Yerubaal pergi ke Sikhem kepada saudara-saudara ibunya dan berkata kepada mereka dan kepada seluruh kaum dari pihak keluarga ibunya:'),(6755,6,9,2,'Tolong katakan kepada seluruh warga kota Sikhem: Manakah yang lebih baik bagimu: tujuh puluh orang memerintah kamu, yaitu semua anak Yerubaal, atau satu orang? Dan ingat juga, bahwa aku darah dagingmu.'),(6756,6,9,3,'Lalu saudara-saudara ibunya mengatakan hal ihwalnya kepada seluruh warga kota Sikhem, maka condonglah hati orang-orang itu untuk mengikuti Abimelekh, sebab kata mereka: \"Memang ia saudara kita.\"'),(6757,6,9,4,'Sesudah itu mereka memberikan kepadanya tujuh puluh uang perak dari kuil Baal-Berit, lalu Abimelekh memberi perak itu sebagai upah kepada petualang-petualang dan orang-orang nekat supaya mengikuti dia.'),(6758,6,9,5,'Ia pergi ke rumah ayahnya di Ofra, lalu membunuh saudara-saudaranya, anak-anak Yerubaal, tujuh puluh orang, di atas satu batu. Tetapi Yotam, anak bungsu Yerubaal tinggal hidup, karena ia menyembunyikan diri.'),(6759,6,9,6,'Kemudian berkumpullah seluruh warga kota Sikhem dan seluruh Bet-Milo; mereka pergi menobatkan Abimelekh menjadi raja dekat pohon tarbantin di tugu peringatan yang di Sikhem.'),(6760,6,9,7,'Setelah hal itu dikabarkan kepada Yotam, pergilah ia ke gunung Gerizim dan berdiri di atasnya, lalu berserulah ia dengan suara nyaring kepada mereka: \"Dengarkanlah aku, kamu warga kota Sikhem, maka Allah akan mendengarkan kamu juga.'),(6761,6,9,8,'Sekali peristiwa pohon-pohon pergi mengurapi yang akan menjadi raja atas mereka. Kata mereka kepada pohon zaitun: Jadilah raja atas kami!'),(6762,6,9,9,'Tetapi jawab pohon zaitun itu kepada mereka: Masakan aku meninggalkan minyakku yang dipakai untuk menghormati Allah dan manusia, dan pergi melayang di atas pohon-pohon?'),(6763,6,9,10,'Lalu kata pohon-pohon itu kepada pohon ara: Marilah, jadilah raja atas kami!'),(6764,6,9,11,'Tetapi jawab pohon ara itu kepada mereka: Masakan aku meninggalkan manisanku dan buah-buahku yang baik, dan pergi melayang di atas pohon-pohon?'),(6765,6,9,12,'Lalu kata pohon-pohon itu kepada pohon anggur: Marilah, jadilah raja atas kami!'),(6766,6,9,13,'Tetapi jawab pohon anggur itu kepada mereka: Masakan aku meninggalkan air buah anggurku, yang menyukakan hati Allah dan manusia, dan pergi melayang di atas pohon-pohon?'),(6767,6,9,14,'Lalu kata segala pohon itu kepada semak duri: Marilah, jadilah raja atas kami!'),(6768,6,9,15,'Jawab semak duri itu kepada pohon-pohon itu: Jika kamu sungguh-sungguh mau mengurapi aku menjadi raja atas kamu, datanglah berlindung di bawah naunganku; tetapi jika tidak, biarlah api keluar dari semak duri dan memakan habis pohon-pohon aras yang di gunung Libanon.'),(6769,6,9,16,'Maka sekarang, jika kamu berlaku setia dan tulus ikhlas dengan membuat Abimelekh menjadi raja, dan jika kamu berbuat yang baik kepada Yerubaal dan kepada keturunannya dan jika kamu membalaskan kepadanya seimbang dengan jasanya--'),(6770,6,9,17,'bukankah ayahku telah berperang membela kamu dan menyabung nyawanya, dan telah melepaskan kamu dari tangan orang Midian,'),(6771,6,9,18,'padahal kamu sekarang memberontak terhadap keturunan ayahku dan membunuh anak-anaknya, tujuh puluh orang banyaknya, di atas satu batu, serta membuat Abimelekh anak seorang budaknya perempuan menjadi raja atas warga kota Sikhem, karena ia saudaramu--'),(6772,6,9,19,'jadi jika kamu pada hari ini berlaku setia dan tulus ikhlas kepada Yerubaal dan keturunannya, maka silakanlah kamu bersukacita atas Abimelekh dan silakanlah ia bersukacita atas kamu.'),(6773,6,9,20,'Tetapi jika tidak demikian, maka biarlah api keluar dari pada Abimelekh dan memakan habis warga kota Sikhem dan juga Bet-Milo, dan biarlah api keluar dari pada warga kota Sikhem dan juga dari Bet-Milo dan memakan habis Abimelekh.\"'),(6774,6,9,21,'Kemudian larilah Yotam; ia melarikan diri ke Beer, dan tinggal di sana karena takut kepada Abimelekh, saudaranya itu.'),(6775,6,9,22,'Setelah tiga tahun lamanya Abimelekh memerintah atas orang Israel,'),(6776,6,9,23,'maka Allah membangkitkan semangat jahat di antara Abimelekh dan warga kota Sikhem, sehingga warga kota Sikhem itu menjadi tidak setia kepada Abimelekh,'),(6777,6,9,24,'supaya kekerasan terhadap ketujuh puluh anak Yerubaal dibalaskan dan darah mereka ditimpakan kepada Abimelekh, saudara mereka yang telah membunuh mereka dan kepada warga kota Sikhem yang membantu dia membunuh saudara-saudaranya itu.'),(6778,6,9,25,'Sebab warga kota Sikhem itu menempatkan orang untuk menghadang dia di puncak gunung dan merampas setiap orang yang melewati mereka melalui jalan itu. Hal itu dikabarkan kepada Abimelekh.'),(6779,6,9,26,'Sementara itu Gaal bin Ebed beserta saudara-saudaranya telah datang dan pindah ke kota Sikhem. Warga kota Sikhem percaya kepadanya,'),(6780,6,9,27,'jadi pergilah mereka ke ladang; mereka mengumpulkan hasil kebun anggur mereka, dan mengirik memerasnya, lalu mengadakan perayaan. Mereka masuk ke kuil allah mereka dan makan minum sambil mengutuki Abimelekh.'),(6781,6,9,28,'Berkatalah Gaal bin Ebed: \"Siapa itu Abimelekh dan siapa kita orang Sikhem, maka kita menjadi hambanya? Bukankah anak Yerubaal dan Zebul, wakilnya, menjadi hamba orang-orang Hemor, ayah Sikhem, jadi mengapakah kita menjadi hambanya?'),(6782,6,9,29,'Sekiranya orang-orang kota ini ada di dalam tanganku, maka tentulah aku mengenyahkan Abimelekh.\" Lalu berkatalah ia ke arah Abimelekh: \"Perkuatlah tentaramu dan majulah!\"'),(6783,6,9,30,'Ketika Zebul, penguasa kota itu mendengar perkataan Gaal bin Ebed, bangkitlah amarahnya.'),(6784,6,9,31,'Ia mengirim utusan kepada Abimelekh di Aruma dengan pesan: \"Gaal bin Ebed dan saudara-saudaranya telah datang ke Sikhem dan ketahuilah mereka menghasut kota itu melawan engkau.'),(6785,6,9,32,'Oleh sebab itu, berangkatlah pada waktu malam, engkau dan rakyat yang bersama-sama dengan engkau itu, dan adakanlah penghadangan di padang.'),(6786,6,9,33,'Esoknya pagi-pagi, pada waktu matahari terbit, haruslah engkau menyerbu kota itu. Dan jika ia dan orang-orangnya keluar melawan engkau, maka engkau dapat berbuat kepadanya sesuai dengan keadaan yang kaudapati.\"'),(6787,6,9,34,'Sebab itu berangkatlah Abimelekh pada waktu malam beserta segala rakyat yang bersama-sama dengan dia, lalu mereka mengadakan penghadangan dalam empat pasukan untuk melawan Sikhem.'),(6788,6,9,35,'Ketika Gaal bin Ebed pergi ke luar dan berdiri di depan pintu gerbang kota itu, Abimelekh kebetulan bangun dari tempat penghadangannya beserta rakyat yang bersama-sama dengan dia.'),(6789,6,9,36,'Ketika Gaal melihat rakyat itu, berkatalah ia kepada Zebul: \"Lihat, ada orang banyak turun dari puncak gunung.\" Jawab Zebul kepadanya: \"Itu bayang-bayang gunung, yang kausangka manusia.\"'),(6790,6,9,37,'Kata Gaal sekali lagi: \"Lihat, ada orang banyak turun dari gunung Pusat Tanah dan satu kelompok datang dari jalan Pohon Tarbantin Peramal.\"'),(6791,6,9,38,'Jawab Zebul kepadanya: \"Di manakah mulutmu itu yang mengatakan: Siapa itu Abimelekh, maka kita menjadi hambanya? Bukankah ini orang-orang yang telah kauhina itu? Majulah sekarang untuk memerangi mereka.\"'),(6792,6,9,39,'Maka majulah Gaal dengan dipandangi oleh warga kota Sikhem, lalu berperang melawan Abimelekh.'),(6793,6,9,40,'Tetapi Abimelekh mengejar dia, dan ia melarikan diri dari depannya, dan banyaklah orang tewas sampai di depan pintu gerbang.'),(6794,6,9,41,'Adapun Abimelekh tinggal di Aruma, tetapi Zebul mengusir Gaal dan saudara-saudaranya, sehingga mereka ini tidak dapat tinggal di Sikhem.'),(6795,6,9,42,'Keesokan harinya orang-orang kota itu pergi ke ladang. Setelah hal ini dikabarkan kepada Abimelekh,'),(6796,6,9,43,'dibawanyalah rakyatnya, dibaginya dalam tiga pasukan, lalu mereka mengadakan penghadangan di padang. Ketika dilihatnya, bahwa orang-orang kota itu keluar dari dalam kota, bangunlah ia menyerang mereka serta menewaskan mereka.'),(6797,6,9,44,'Abimelekh dan pasukan yang bersama-sama dengan dia menyerbu dan menduduki pintu gerbang kota, sedang kedua pasukan lain itu menyerbu dan menewaskan semua orang yang ada di padang.'),(6798,6,9,45,'Sehari-harian itu Abimelekh berperang melawan kota itu; ia merebut kota itu dan membunuh orang-orang yang di dalamnya; kemudian dirobohkannya kota itu dan ditaburinya dengan garam.'),(6799,6,9,46,'Mendengar itu masuklah seluruh warga kota Menara-Sikhem ke dalam liang di bawah kuil El-Berit.'),(6800,6,9,47,'Dikabarkanlah kepada Abimelekh, bahwa seluruh warga kota Menara-Sikhem telah berhimpun di sana.'),(6801,6,9,48,'Lalu Abimelekh dan seluruh rakyat yang bersama-sama dengan dia naik ke gunung Zalmon. Abimelekh mengambil kapak, lalu memotong dahan-dahan kayu, mengangkatnya dan meletakkannya ke atas bahunya sambil berkata kepada rakyatnya yang bersama-sama dengan dia: \"Turutilah dengan segera perbuatanku yang kamu lihat ini.\"'),(6802,6,9,49,'Kemudian rakyat itu juga masing-masing memotong dahan-dahan, lalu mengikuti Abimelekh, meletakkan dahan-dahan itu di atas liang dan membakar liang itu di atas kepala orang-orang itu. Demikianlah semua penduduk kota Menara-Sikhem juga mati, kira-kira seribu orang laki-laki dan perempuan.'),(6803,6,9,50,'Selanjutnya Abimelekh pergi ke Tebes; ia mengepung Tebes, lalu merebutnya.'),(6804,6,9,51,'Tetapi ada sebuah menara yang kuat di tengah-tengah kota, dan semua laki-laki dan perempuan, seluruh warga kota itu, melarikan diri ke situ; mereka menutup pintu di belakangnya dan naik ke atas sotoh menara itu.'),(6805,6,9,52,'Lalu sampailah Abimelekh ke menara itu, menyerangnya, dan dapat menerobos sampai ke pintu menara itu untuk membakarnya.'),(6806,6,9,53,'Tetapi seorang perempuan menimpakan sebuah batu kilangan kepada kepala Abimelekh dan memecahkan batu kepalanya.'),(6807,6,9,54,'Dengan segera dipanggilnya bujang pembawa senjatanya dan berkata kepadanya: \"Hunuslah pedangmu dan bunuhlah aku, supaya jangan orang berkata tentang aku: Seorang perempuan membunuh dia.\" Lalu bujangnya itu menikam dia, sehingga mati.'),(6808,6,9,55,'Setelah dilihat oleh orang Israel, bahwa Abimelekh telah mati, pergilah mereka, masing-masing ke tempat kediamannya.'),(6809,6,9,56,'Demikianlah Allah membalaskan kejahatan yang dilakukan oleh Abimelekh kepada ayahnya, yaitu pembunuhan atas ketujuh puluh saudaranya;'),(6810,6,9,57,'juga segala kejahatan orang-orang Sikhem ditimpakan kembali oleh Allah kepada kepala mereka sendiri. Demikianlah kutuk Yotam bin Yerubaal mengenai mereka.'),(6811,6,10,1,'Sesudah Abimelekh, bangkitlah Tola bin Pua bin Dodo, seorang Isakhar, untuk menyelamatkan orang Israel. Ia diam di Samir, di pegunungan Efraim'),(6812,6,10,2,'dan ia memerintah sebagai hakim atas orang Israel dua puluh tiga tahun lamanya; kemudian matilah ia, lalu dikuburkan di Samir.'),(6813,6,10,3,'Sesudah dia, bangkitlah Yair, orang Gilead, yang memerintah sebagai hakim atas orang Israel dua puluh dua tahun lamanya.'),(6814,6,10,4,'Ia mempunyai tiga puluh anak laki-laki, yang mengendarai tiga puluh ekor keledai jantan, dan mereka mempunyai tiga puluh kota, yang sampai sekarang disebutkan orang Hawot-Yair, di tanah Gilead.'),(6815,6,10,5,'Lalu matilah Yair dan dikuburkan di Kamon.'),(6816,6,10,6,'Orang Israel itu melakukan pula apa yang jahat di mata TUHAN; mereka beribadah kepada para Baal dan para Asytoret, kepada para allah orang Aram, para allah orang Sidon, para allah orang Moab, para allah bani Amon dan para allah orang Filistin, tetapi TUHAN ditinggalkan mereka dan kepada Dia mereka tidak beribadah.'),(6817,6,10,7,'Lalu bangkitlah murka TUHAN terhadap orang Israel, dan Ia menyerahkan mereka ke dalam tangan orang Filistin dan bani Amon.'),(6818,6,10,8,'Dalam tahun itu juga orang Israel ditindas dan diinjak mereka; delapan belas tahun lamanya mereka memperlakukan demikian semua orang Israel yang di seberang sungai Yordan di tanah orang Amori yang di Gilead.'),(6819,6,10,9,'Dan bani Amonpun menyeberangi sungai Yordan untuk berperang melawan suku Yehuda, suku Benyamin dan keturunan Efraim, sehingga orang Israel sangat terdesak.'),(6820,6,10,10,'Lalu berserulah orang Israel kepada TUHAN, katanya: \"Kami telah berbuat dosa terhadap Engkau, sebab kami telah meninggalkan Allah kami lalu beribadah kepada para Baal.\"'),(6821,6,10,11,'Tetapi firman TUHAN kepada orang Israel: \"Bukankah Aku yang telah menyelamatkan kamu dari tangan orang Mesir, orang Amori, bani Amon, orang Filistin,'),(6822,6,10,12,'orang Sidon, suku Amalek dan suku Maon yang menindas kamu, ketika kamu berseru kepada-Ku?'),(6823,6,10,13,'Tetapi kamu telah meninggalkan Aku dan beribadah kepada allah lain; sebab itu Aku tidak akan menyelamatkan kamu lagi.'),(6824,6,10,14,'Pergi sajalah berseru kepada para allah yang telah kamu pilih itu; biar merekalah yang menyelamatkan kamu, pada waktu kamu terdesak.\"'),(6825,6,10,15,'Kata orang Israel kepada TUHAN: \"Kami telah berbuat dosa. Lakukanlah kepada kami segala yang baik di mata-Mu. Hanya tolonglah kiranya kami sekarang ini!\"'),(6826,6,10,16,'Dan mereka menjauhkan para allah asing dari tengah-tengah mereka, lalu mereka beribadah kepada TUHAN. Maka TUHAN tidak dapat lagi menahan hati-Nya melihat kesukaran mereka.'),(6827,6,10,17,'Kemudian bani Amon dikerahkan dan berkemah di Gilead, sedang orang Israel berkumpul dan berkemah di Mizpa.'),(6828,6,10,18,'Maka para pemimpin bangsa di Gilead berkata seorang kepada yang lain: \"Siapakah orang yang berani memulai peperangan melawan bani Amon itu? Dialah yang harus menjadi kepala atas seluruh penduduk Gilead.\"'),(6829,6,11,1,'Adapun Yefta, orang Gilead itu, adalah seorang pahlawan yang gagah perkasa, tetapi ia anak seorang perempuan sundal; ayah Yefta ialah Gilead.'),(6830,6,11,2,'Juga isteri Gilead melahirkan anak-anak lelaki baginya. Setelah besar anak-anak isterinya ini, maka mereka mengusir Yefta, katanya kepadanya: \"Engkau tidak mendapat milik pusaka dalam keluarga kami, sebab engkau anak dari perempuan lain.\"'),(6831,6,11,3,'Maka larilah Yefta dari saudara-saudaranya itu dan diam di tanah Tob; di sana berkumpullah kepadanya petualang-petualang yang pergi merampok bersama-sama dengan dia.'),(6832,6,11,4,'Beberapa waktu kemudian bani Amon berperang melawan orang Israel.'),(6833,6,11,5,'Dan ketika bani Amon itu berperang melawan orang Israel, pergilah para tua-tua Gilead menjemput Yefta dari tanah Tob.'),(6834,6,11,6,'Kata mereka kepada Yefta: \"Mari, jadilah panglima kami dan biarlah kita berperang melawan bani Amon.\"'),(6835,6,11,7,'Tetapi kata Yefta kepada para tua-tua Gilead itu: \"Bukankah kamu sendiri membenci aku dan mengusir aku dari keluargaku? Mengapa kamu datang sekarang kepadaku, pada waktu kamu terdesak?\"'),(6836,6,11,8,'Kemudian berkatalah para tua-tua Gilead kepada Yefta: \"Memang, kami datang kembali sekarang kepadamu, ikutilah kami dan berperanglah melawan bani Amon, maka engkau akan menjadi kepala atas kami, atas seluruh penduduk Gilead.\"'),(6837,6,11,9,'Kata Yefta kepada para tua-tua Gilead: \"Jadi, jika kamu membawa aku kembali untuk berperang melawan bani Amon, dan TUHAN menyerahkan mereka kepadaku, maka akulah yang akan menjadi kepala atas kamu?\"'),(6838,6,11,10,'Lalu kata para tua-tua Gilead kepada Yefta: \"Demi TUHAN yang mendengarkannya sebagai saksi antara kita: Kami akan berbuat seperti katamu itu.\"'),(6839,6,11,11,'Maka Yefta ikut dengan para tua-tua Gilead, lalu bangsa itu mengangkat dia menjadi kepala dan panglima mereka. Tetapi Yefta membawa seluruh perkaranya itu ke hadapan TUHAN, di Mizpa.'),(6840,6,11,12,'Kemudian Yefta mengirim utusan kepada raja bani Amon dengan pesan: \"Apakah urusanmu dengan aku, sehingga engkau mendatangi aku untuk memerangi negeriku?\"'),(6841,6,11,13,'Jawab raja bani Amon kepada utusan Yefta: \"Orang Israel, ketika berjalan keluar dari Mesir, telah merampas tanahku, dari sungai Arnon sampai ke sungai Yabok dan sampai ke sungai Yordan. Maka sekarang, kembalikanlah semuanya itu dengan jalan damai.\"'),(6842,6,11,14,'Lalu Yefta mengirim pula utusan kepada raja bani Amon'),(6843,6,11,15,'dengan pesan: \"Beginilah kata Yefta: orang Israel tidak merampas tanah orang Moab atau tanah bani Amon.'),(6844,6,11,16,'Sebab ketika berjalan keluar dari Mesir, orang Israel melalui padang gurun sampai ke Laut Teberau dan tiba di Kadesh.'),(6845,6,11,17,'Ketika itu orang Israel mengirim utusan kepada raja negeri Edom dengan permintaan: Izinkanlah kiranya kami berjalan melalui negerimu ini. Tetapi raja negeri Edom tidak mau mendengar. Mereka mengirim juga utusan kepada raja negeri Moab, tetapi raja ini menolak. Maka orang Israel tinggal di Kadesh.'),(6846,6,11,18,'Kemudian mereka berjalan melalui padang gurun, menempuh jalan keliling tanah Edom dan tanah Moab, lalu sampai ke sebelah timur tanah Moab, maka berkemahlah mereka di seberang sungai Arnon, dengan tidak masuk daerah Moab, sebab sungai Arnon itulah batas daerah Moab.'),(6847,6,11,19,'Lalu orang Israel mengirim utusan kepada Sihon, raja orang Amori, raja di Hesybon, dan orang Israel meminta kepadanya: Izinkanlah kiranya kami berjalan melalui negerimu ini sampai ke tempat yang kami tuju.'),(6848,6,11,20,'Tetapi Sihon tidak percaya kepada orang Israel yang hendak berjalan melalui daerahnya itu, maka dikumpulkannyalah seluruh rakyatnya. Ia berkemah di Yahas, lalu berperang melawan orang Israel.'),(6849,6,11,21,'Tetapi TUHAN, Allah Israel, menyerahkan Sihon dengan seluruh rakyatnya ke dalam tangan orang Israel, dan mereka dikalahkan, sehingga orang Israel menduduki seluruh negeri kepunyaan orang Amori, penduduk negeri itu.'),(6850,6,11,22,'Demikianlah dimiliki orang Israel seluruh daerah orang Amori itu, dari sungai Arnon sampai ke sungai Yabok dan dari padang gurun sampai ke sungai Yordan.'),(6851,6,11,23,'Maka sekarang TUHAN, Allah Israel, telah merebut milik orang Amori, bagi Israel, umat-Nya. Apakah engkau hendak memiliki pula tanah mereka itu?'),(6852,6,11,24,'Bukankah engkau akan memiliki apa yang diberi oleh Kamos, allahmu? Demikianlah kami memiliki segala yang direbut bagi kami oleh TUHAN, Allah kami.'),(6853,6,11,25,'Lagipula, apakah engkau lebih baik dari Balak bin Zipor, raja Moab? Pernahkah ia menuntut hak kepada orang Israel atau pernahkah ia berperang melawan mereka?'),(6854,6,11,26,'Ketika orang Israel diam di Hesybon dengan segala anak kotanya, di Aroer dengan segala anak kotanya, dan di segala kota sepanjang kedua tepi sungai Arnon selama tiga ratus tahun, mengapa pada waktu itu engkau tidak melepaskan kota-kota itu?'),(6855,6,11,27,'Jadi aku tidak bersalah terhadap engkau, tetapi engkau berbuat jahat terhadap aku dengan berperang melawan aku. TUHAN, Hakim itu, Dialah yang menjadi hakim pada hari ini antara orang Israel dan bani Amon.\"'),(6856,6,11,28,'Tetapi raja bani Amon tidak mendengarkan perkataan yang disampaikan kepadanya oleh utusan-utusan Yefta.'),(6857,6,11,29,'Lalu Roh TUHAN menghinggapi Yefta; ia berjalan melalui daerah Gilead dan daerah Manasye, kemudian melalui Mizpa di Gilead, dan dari Mizpa di Gilead ia berjalan terus ke daerah bani Amon.'),(6858,6,11,30,'Lalu bernazarlah Yefta kepada TUHAN, katanya: \"Jika Engkau sungguh-sungguh menyerahkan bani Amon itu ke dalam tanganku,'),(6859,6,11,31,'maka apa yang keluar dari pintu rumahku untuk menemui aku, pada waktu aku kembali dengan selamat dari bani Amon, itu akan menjadi kepunyaan TUHAN, dan aku akan mempersembahkannya sebagai korban bakaran.\"'),(6860,6,11,32,'Kemudian Yefta berjalan terus untuk berperang melawan bani Amon, dan TUHAN menyerahkan mereka ke dalam tangannya.'),(6861,6,11,33,'Ia menimbulkan kekalahan yang amat besar di antara mereka, mulai dari Aroer sampai dekat Minit--dua puluh kota banyaknya--dan sampai ke Abel-Keramim, sehingga bani Amon itu ditundukkan di depan orang Israel.'),(6862,6,11,34,'Ketika Yefta pulang ke Mizpa ke rumahnya, tampaklah anaknya perempuan keluar menyongsong dia dengan memukul rebana serta menari-nari. Dialah anaknya yang tunggal; selain dari dia tidak ada anaknya laki-laki atau perempuan.'),(6863,6,11,35,'Demi dilihatnya dia, dikoyakkannyalah bajunya, sambil berkata: \"Ah, anakku, engkau membuat hatiku hancur luluh dan engkaulah yang mencelakakan aku; aku telah membuka mulutku bernazar kepada TUHAN, dan tidak dapat aku mundur.\"'),(6864,6,11,36,'Tetapi jawabnya kepadanya: \"Bapa, jika engkau telah membuka mulutmu bernazar kepada TUHAN, maka perbuatlah kepadaku sesuai dengan nazar yang kauucapkan itu, karena TUHAN telah mengadakan bagimu pembalasan terhadap musuhmu, yakni bani Amon itu.\"'),(6865,6,11,37,'Lagi katanya kepada ayahnya: \"Hanya izinkanlah aku melakukan hal ini: berilah keluasan kepadaku dua bulan lamanya, supaya aku pergi mengembara ke pegunungan dan menangisi kegadisanku bersama-sama dengan teman-temanku.\"'),(6866,6,11,38,'Jawab Yefta: \"Pergilah,\" dan ia membiarkan dia pergi dua bulan lamanya. Maka pergilah gadis itu bersama-sama dengan teman-temannya menangisi kegadisannya di pegunungan.'),(6867,6,11,39,'Setelah lewat kedua bulan itu, kembalilah ia kepada ayahnya, dan ayahnya melakukan kepadanya apa yang telah dinazarkannya itu; jadi gadis itu tidak pernah kenal laki-laki. Dan telah menjadi adat di Israel,'),(6868,6,11,40,'bahwa dari tahun ke tahun anak-anak perempuan orang Israel selama empat hari setahun meratapi anak perempuan Yefta, orang Gilead itu.'),(6869,6,12,1,'Dikerahkanlah orang Efraim, lalu mereka bergerak ke Zafon. Dan mereka berkata kepada Yefta: \"Mengapa engkau bergerak untuk memerangi bani Amon dengan tidak memanggil kami untuk maju bersama-sama dengan engkau? Sebab itu kami akan membakar rumahmu bersama-sama kamu!\"'),(6870,6,12,2,'Tetapi jawab Yefta kepada mereka: \"Aku dan rakyatku telah terlibat dalam peperangan yang hebat dengan bani Amon; lalu aku memanggil kamu, tetapi kamu tidak datang menyelamatkan aku dari tangan mereka.'),(6871,6,12,3,'Ketika kulihat, bahwa tidak ada yang datang menyelamatkan aku, maka aku mempertaruhkan nyawaku dan aku pergi melawan bani Amon itu, dan TUHAN menyerahkan mereka ke dalam tanganku. Mengapa pada hari ini kamu mendatangi aku untuk berperang melawan aku?\"'),(6872,6,12,4,'Kemudian Yefta mengumpulkan semua orang Gilead, lalu mereka berperang melawan suku Efraim. Dan orang-orang Gilead mengalahkan suku Efraim itu. Sebab orang-orang itu mengatakan: \"Kamulah orang-orang yang telah lari dari suku Efraim!\" --kaum Gilead itu ada di tengah-tengah suku Efraim dan suku Manasye--.'),(6873,6,12,5,'Untuk menghadapi suku Efraim itu, maka orang Gilead menduduki tempat-tempat penyeberangan sungai Yordan. Apabila dari suku Efraim ada yang lari dan berkata: \"Biarkanlah aku menyeberang,\" maka orang Gilead berkata kepadanya: \"Orang Efraimkah engkau?\" Dan jika ia menjawab: \"Bukan,\"'),(6874,6,12,6,'maka mereka berkata kepadanya: \"Coba katakan dahulu: syibolet.\" Jika ia berkata: sibolet, jadi tidak dapat mengucapkannya dengan tepat, maka mereka menangkap dia dan menyembelihnya dekat tempat-tempat penyeberangan sungai Yordan itu. Pada waktu itu tewaslah dari suku Efraim empat puluh dua ribu orang.'),(6875,6,12,7,'Yefta memerintah sebagai hakim atas orang Israel enam tahun lamanya. Kemudian matilah Yefta, orang Gilead itu, lalu dikuburkan di sebuah kota di daerah Gilead.'),(6876,6,12,8,'Sesudah dia, maka Ebzan dari Betlehem memerintah sebagai hakim atas orang Israel.'),(6877,6,12,9,'Ia mempunyai tiga puluh anak laki-laki. Tiga puluh anaknya perempuan dikawinkannya ke luar kaumnya dan tiga puluh anak perempuan diambilnya dari luar kaumnya untuk anak-anaknya lelaki itu. Ia memerintah atas orang Israel tujuh tahun lamanya.'),(6878,6,12,10,'Kemudian matilah Ebzan, lalu dikuburkan di Betlehem.'),(6879,6,12,11,'Sesudah dia, maka Elon orang Zebulon memerintah sebagai hakim atas orang Israel. Ia memerintah atas orang Israel sepuluh tahun lamanya.'),(6880,6,12,12,'Kemudian matilah Elon, orang Zebulon itu, lalu dikuburkan di Ayalon, di tanah Zebulon.'),(6881,6,12,13,'Sesudah dia, maka Abdon bin Hilel, orang Piraton, memerintah sebagai hakim atas orang Israel.'),(6882,6,12,14,'Ia mempunyai empat puluh anak laki-laki dan tiga puluh cucu laki-laki, yang mengendarai tujuh puluh ekor keledai jantan. Ia memerintah atas orang Israel delapan tahun lamanya.'),(6883,6,12,15,'Kemudian matilah Abdon bin Hilel, orang Piraton itu, lalu dikuburkan di Piraton, di tanah Efraim, di pegunungan orang Amalek.'),(6884,6,13,1,'Orang Israel melakukan pula apa yang jahat di mata TUHAN; sebab itu TUHAN menyerahkan mereka ke dalam tangan orang Filistin empat puluh tahun lamanya.'),(6885,6,13,2,'Pada waktu itu ada seorang dari Zora, dari keturunan orang Dan, namanya Manoah; isterinya mandul, tidak beranak.'),(6886,6,13,3,'Dan Malaikat TUHAN menampakkan diri kepada perempuan itu dan berfirman kepadanya, demikian: \"Memang engkau mandul, tidak beranak, tetapi engkau akan mengandung dan melahirkan seorang anak laki-laki.'),(6887,6,13,4,'Oleh sebab itu, peliharalah dirimu, jangan minum anggur atau minuman yang memabukkan dan jangan makan sesuatu yang haram.'),(6888,6,13,5,'Sebab engkau akan mengandung dan melahirkan seorang anak laki-laki; kepalanya takkan kena pisau cukur, sebab sejak dari kandungan ibunya anak itu akan menjadi seorang nazir Allah dan dengan dia akan mulai penyelamatan orang Israel dari tangan orang Filistin.\"'),(6889,6,13,6,'Kemudian perempuan itu datang kepada suaminya dan berkata: \"Telah datang kepadaku seorang abdi Allah, yang rupanya sebagai rupa malaikat Allah, amat menakutkan. Tidak kutanyakan dari mana datangnya, dan tidak juga diberitahukannya namanya kepadaku.'),(6890,6,13,7,'Tetapi ia berkata kepadaku: Engkau akan mengandung dan melahirkan seorang anak laki-laki; oleh sebab itu janganlah minum anggur atau minuman yang memabukkan dan janganlah makan sesuatu yang haram, sebab sejak dari kandungan ibunya sampai pada hari matinya, anak itu akan menjadi seorang nazir Allah.\"'),(6891,6,13,8,'Lalu Manoah memohon kepada TUHAN, katanya: \"Ya Tuhan, berilah kiranya abdi Allah, yang Kauutus itu, datang pula kepada kami dan mengajar kami, apa yang harus kami perbuat kepada anak yang akan lahir itu.\"'),(6892,6,13,9,'Maka Allah mendengarkan permohonan Manoah, sehingga Malaikat Allah datang pula kepada perempuan itu, ketika ia duduk di padang dan ketika Manoah, suaminya itu, tidak ada bersama-sama dengan dia.'),(6893,6,13,10,'Kemudian perempuan itu segera berlari memberitahukan kepada suaminya, katanya kepadanya: \"Orang yang datang kepadaku baru-baru ini menampakkan diri pula kepadaku.\"'),(6894,6,13,11,'Lalu bangunlah Manoah dan mengikuti isterinya. Setelah sampai kepada orang itu, berkatalah ia kepadanya: \"Engkaukah orang yang telah berbicara kepada perempuan ini?\" Jawabnya: \"Benar!\"'),(6895,6,13,12,'Lalu kata Manoah: \"Dan apabila terjadi yang Kaukatakan itu, bagaimanakah nanti cara hidup anak itu dan tingkah lakunya?\"'),(6896,6,13,13,'Jawab Malaikat TUHAN itu kepada Manoah: \"Perempuan itu harus memelihara diri terhadap semua yang Kukatakan kepadanya.'),(6897,6,13,14,'Janganlah ia makan sesuatu yang berasal dari pohon anggur; anggur atau minuman yang memabukkan tidak boleh diminumnya dan sesuatu yang haram tidak boleh dimakannya. Ia harus berpegang pada segala yang Kuperintahkan kepadanya.\"'),(6898,6,13,15,'Kata Manoah kepada Malaikat TUHAN itu: \"Perkenankanlah kami menahan Engkau di sini, supaya kami mengolah anak kambing bagi-Mu.\"'),(6899,6,13,16,'Tetapi jawab Malaikat TUHAN itu kepada Manoah: \"Sekalipun engkau menahan Aku di sini, hidanganmu itu tidak akan Kumakan. Tetapi jika engkau hendak mengolahnya menjadi korban bakaran, persembahkanlah itu kepada TUHAN.\" Sebab Manoah tidak mengetahui, bahwa Dia itu Malaikat TUHAN.'),(6900,6,13,17,'Kemudian berkatalah Manoah kepada Malaikat TUHAN itu: \"Siapakah nama-Mu, sebab apabila terjadi yang Kaukatakan itu, maka kami hendak memuliakan Engkau.\"'),(6901,6,13,18,'Tetapi jawab malaikat TUHAN itu kepadanya: \"Mengapa engkau juga menanyakan nama-Ku? Bukankah nama itu ajaib?\"'),(6902,6,13,19,'Sesudah itu Manoah mengambil seekor anak kambing dan korban sajian, lalu mempersembahkannya kepada TUHAN di atas batu. Lalu diperbuat-Nya keajaiban, sementara Manoah dan isterinya memandanginya.'),(6903,6,13,20,'Sedang nyala api itu naik ke langit dari mezbah, maka naiklah Malaikat TUHAN dalam nyala api mezbah itu. Ketika Manoah dan isterinya melihat hal ini, sujudlah mereka dengan mukanya sampai ke tanah.'),(6904,6,13,21,'Sejak itu Malaikat TUHAN tidak lagi menampakkan diri kepada Manoah dan isterinya. Maka tahulah Manoah, bahwa Dia itu Malaikat TUHAN.'),(6905,6,13,22,'Berkatalah Manoah kepada isterinya: \"Kita pasti mati, sebab kita telah melihat Allah.\"'),(6906,6,13,23,'Tetapi jawab isterinya kepadanya: \"Seandainya TUHAN hendak membunuh kita, maka tidaklah Ia menerima korban bakaran dan korban sajian dari tangan kita dan tidaklah Ia memperlihatkan semuanya itu kepada kita dan tidaklah Ia memperdengarkan hal-hal yang demikian kepada kita pada waktu sekarang ini.\"'),(6907,6,13,24,'Lalu perempuan itu melahirkan seorang anak laki-laki dan memberi nama Simson kepadanya. Anak itu menjadi besar dan TUHAN memberkati dia.'),(6908,6,13,25,'Mulailah hatinya digerakkan oleh Roh TUHAN di Mahane-Dan yang terletak di antara Zora dan Esytaol.'),(6909,6,14,1,'Simson pergi ke Timna dan di situ ia melihat seorang gadis Filistin.'),(6910,6,14,2,'Ia pulang dan memberitahukan kepada ayahnya dan ibunya: \"Di Timna aku melihat seorang gadis Filistin. Tolong, ambillah dia menjadi isteriku.\"'),(6911,6,14,3,'Tetapi ayahnya dan ibunya berkata kepadanya: \"Tidak adakah di antara anak-anak perempuan sanak saudaramu atau di antara seluruh bangsa kita seorang perempuan, sehingga engkau pergi mengambil isteri dari orang Filistin, orang-orang yang tidak bersunat itu?\" Tetapi jawab Simson kepada ayahnya: \"Ambillah dia bagiku, sebab dia kusukai.\"'),(6912,6,14,4,'Tetapi ayahnya dan ibunya tidak tahu bahwa hal itu dari pada TUHAN asalnya: sebab memang Simson harus mencari gara-gara terhadap orang Filistin. Karena pada masa itu orang Filistin menguasai orang Israel.'),(6913,6,14,5,'Lalu pergilah Simson beserta ayahnya dan ibunya ke Timna. Ketika mereka sampai ke kebun-kebun anggur di Timna, maka seekor singa muda mendatangi Simson dengan mengaum.'),(6914,6,14,6,'Pada waktu itu berkuasalah Roh TUHAN atas dia, sehingga singa itu dicabiknya seperti orang mencabik anak kambing--tanpa apa-apa di tangannya. Tetapi tidak diceriterakannya kepada ayahnya atau ibunya apa yang dilakukannya itu.'),(6915,6,14,7,'Maka pergilah ia ke sana, lalu bercakap-cakap dengan perempuan itu, sebab Simson suka kepadanya.'),(6916,6,14,8,'Setelah beberapa waktu kembalilah ia ke sana untuk kawin dengan perempuan itu; dan ketika ia menyimpang dari jalan untuk melihat bangkai singa itu, tampaklah ada kawanan lebah pada kerangka singa itu dan juga madu.'),(6917,6,14,9,'Dikeruknya madu itu ke dalam tangannya dan sambil memakannya ia berjalan terus, kemudian pergilah ia kepada ayahnya dan ibunya, dan memberikannya juga kepada mereka, lalu mereka memakannya. Tetapi tidak diceriterakannya kepada mereka, bahwa madu itu dikeruknya dari kerangka singa.'),(6918,6,14,10,'Setelah ayahnya pergi kepada perempuan itu, Simson mengadakan perjamuan di sana, sebab demikianlah biasanya dilakukan orang-orang muda.'),(6919,6,14,11,'Ketika mereka melihat dia, dipilihlah tiga puluh orang kawan untuk menemani dia.'),(6920,6,14,12,'Kata Simson kepada mereka: \"Aku mau mengatakan suatu teka-teki kepada kamu. Jika kamu dapat memberi jawabnya yang tepat kepadaku dalam tujuh hari selama perjamuan ini berlangsung dan menebaknya, maka aku akan memberikan kepadamu tiga puluh pakaian lenan dan tiga puluh pakaian kebesaran.'),(6921,6,14,13,'Tetapi jika kamu tidak dapat memberi jawabnya kepadaku, maka kamulah yang harus memberikan tiga puluh pakaian dalam dan tiga puluh pakaian kebesaran kepadaku.\" Kata mereka kepadanya: \"Katakanlah teka-tekimu itu, supaya kami dengar.\"'),(6922,6,14,14,'Lalu katanya kepada mereka: \"Dari yang makan keluar makanan, dari yang kuat keluar manisan.\" Ada tiga hari lamanya mereka tidak dapat memberi jawab teka-teki itu.'),(6923,6,14,15,'Pada hari ketujuh berkatalah mereka kepada isteri Simson: \"Bujuklah suamimu, supaya diberitahukannya kepada kami jawab teka-teki itu; kalau tidak, kami akan membakar engkau beserta seisi rumah ayahmu. Apakah engkau mengundang kami untuk membuat kami menjadi miskin? Tidak, bukan?\"'),(6924,6,14,16,'Lalu menangislah isteri Simson itu sambil memeluk Simson, katanya: \"Engkau benci saja kepadaku, dan tidak cinta kepadaku; suatu teka-teki kaukatakan kepada orang-orang sebangsaku, tetapi jawabnya tidak kauberitahukan kepadaku.\" Sahutnya kepadanya: \"Sedangkan kepada ayahku dan ibuku tidak kuberitahukan, masakan kepada engkau akan kuberitahukan?\"'),(6925,6,14,17,'Tetapi isterinya itu menangis di sampingnya selama ketujuh hari mereka mengadakan perjamuan itu. Pada hari yang ketujuh diberitahukannyala kepadanya, karena ia merengek-rengek kepadanya, kemudian perempuan itu memberitahukan jawab teka-teki itu kepada orang-orang sebangsanya.'),(6926,6,14,18,'Lalu pada hari yang ketujuh itu, sebelum matahari terbenam, berkatalah orang-orang kota itu kepadanya: \"Apakah yang lebih manis dari pada madu? Apakah yang lebih kuat dari pada singa?\" Sahutnya kepada mereka: \"Kalau kamu tidak membajak dengan lembu betinaku, pasti kamu tidak menebak teka-tekiku.\"'),(6927,6,14,19,'Maka berkuasalah Roh TUHAN atas dia, lalu pergilah ia ke Askelon dan dibunuhnya tiga puluh orang di sana, diambilnya pakaian mereka dan diberikannya pakaian-pakaian kebesaran itu kepada orang-orang yang dapat memberi jawab teka-teki itu. Tetapi amarahnya masih juga bernyala-nyala, lalu pulanglah ia ke rumah ayahnya.'),(6928,6,14,20,'Maka diberikanlah isteri Simson itu kepada kawannya, bekas pengiringnya.'),(6929,6,15,1,'Beberapa waktu kemudian, dalam musim menuai gandum, pergilah Simson mengunjungi isterinya, dengan membawa seekor anak kambing, serta berkata: \"Aku mau ke kamar mendapatkan isteriku.\" Tetapi ayah perempuan itu tidak membiarkan dia masuk.'),(6930,6,15,2,'Kata ayah perempuan itu: \"Aku telah menyangka, bahwa engkau benci sama sekali kepadanya, sebab itu aku memberikannya kepada kawanmu. Bukankah adiknya lebih cantik dari padanya? Baiklah kauambil itu bagimu sebagai gantinya.\"'),(6931,6,15,3,'Lalu kata Simson kepadanya: \"Sekali ini aku tidak bersalah terhadap orang Filistin, apabila aku mendatangkan celaka kepada mereka.\"'),(6932,6,15,4,'Maka pergilah Simson, ditangkapnya tiga ratus anjing hutan, diambilnya obor, diikatnya ekor dengan ekor dan ditaruhnya sebuah obor di antara tiap-tiap dua ekor.'),(6933,6,15,5,'Kemudian dinyalakannyalah obor itu dan dilepaskannya anjing-anjing hutan itu ke gandum yang belum dituai kepunyaan orang Filistin, sehingga terbakarlah tumpukan-tumpukan gandum dan gandum yang belum dituai dan kebun-kebun pohon zaitun.'),(6934,6,15,6,'Berkatalah orang Filistin: \"Siapakah yang melakukan ini?\" Orang menjawab: \"Simson, menantu orang Timna itu, sebab orang itu telah mengambil isteri Simson dan memberikannya kepada kawannya.\" Kemudian pergilah orang Filistin ke sana dan membakar perempuan itu beserta ayahnya.'),(6935,6,15,7,'Lalu berkatalah Simson kepada mereka: \"Jika kamu berbuat demikian, sesungguhnya aku takkan berhenti sebelum aku membalaskannya kepada kamu.\"'),(6936,6,15,8,'Dan dengan pukulan yang hebat ia meremukkan tulang-tulang mereka. Lalu pergilah ia dan tinggal dalam gua di bukit batu Etam.'),(6937,6,15,9,'Lalu majulah orang Filistin dan berkemah di daerah Yehuda serta memencar ke Lehi.'),(6938,6,15,10,'Berkatalah orang-orang Yehuda: \"Mengapa kamu maju menyerang kami?\" Lalu jawab mereka: \"Kami maju untuk mengikat Simson dan memperlakukan dia seperti dia memperlakukan kami.\"'),(6939,6,15,11,'Kemudian turunlah tiga ribu orang dari suku Yehuda ke gua di gunung batu Etam dan berkata kepada Simson: \"Tidakkah kauketahui, bahwa orang Filistin berkuasa atas kita? Apakah juga yang telah kauperbuat terhadap kami?\" Tetapi jawabnya kepada mereka: \"Seperti mereka memperlakukan aku, demikianlah aku memperlakukan mereka.\"'),(6940,6,15,12,'Kata mereka kepadanya: \"Kami datang ke sini untuk mengikat dan menyerahkan engkau ke dalam tangan orang Filistin.\" Tetapi jawab Simson kepada mereka: \"Bersumpahlah kepadaku, bahwa kamu sendiri tidak akan menyerang aku.\"'),(6941,6,15,13,'Lalu kata mereka kepadanya: \"Tidak, kami hanya mau mengikat engkau dan menyerahkan engkau ke dalam tangan mereka, tetapi membunuh engkau kami tidak mau.\" Maka mereka mengikat dia dengan dua tali baru dan membawa dia dari bukit batu itu.'),(6942,6,15,14,'Setelah ia sampai ke Lehi dan orang-orang Filistin mendatangi dia dengan bersorak-sorak, maka berkuasalah Roh TUHAN atas dia dan tali-tali pada tangannya menjadi seperti batang rami yang telah habis dimakan api dan segala pengikatnya hancur tanggal dari tangannya.'),(6943,6,15,15,'Kemudian ia menemui sebuah tulang rahang keledai yang masih baru, diulurkannya tangannya, dipungutnya dan dipukulnya mati seribu orang dengan tulang itu.'),(6944,6,15,16,'Berkatalah Simson: \"Dengan rahang keledai bangsa keledai itu kuhajar, dengan rahang keledai seribu orang kupukul.\"'),(6945,6,15,17,'Setelah berkata demikian, dilemparnya tulang rahang itu dari tangannya. Kemudian dinamailah tempat itu Ramat Lehi.'),(6946,6,15,18,'Ketika ia sangat haus, berserulah ia kepada TUHAN: \"Oleh tangan hamba-Mu ini telah Kauberikan kemenangan yang besar itu, masakan sekarang aku akan mati kehausan dan jatuh ke dalam tangan orang-orang yang tidak bersunat itu!\"'),(6947,6,15,19,'Kemudian Allah membelah liang batu yang di Lehi itu, dan keluarlah air dari situ. Ia minum, lalu menjadi kuat dan segar kembali. Sebab itu dinamailah mata air itu Mata Air Penyeru, yang sampai sekarang masih ada di Lehi.'),(6948,6,15,20,'Ia memerintah sebagai hakim atas orang Israel dalam zaman orang Filistin, dua puluh tahun lamanya.'),(6949,6,16,1,'Pada suatu kali, ketika Simson pergi ke Gaza, dilihatnya di sana seorang perempuan sundal, lalu menghampiri dia.'),(6950,6,16,2,'Ketika diberitahukan kepada orang-orang Gaza: \"Simson telah datang ke sini,\" maka mereka mengepung tempat itu dan siap menghadang dia semalam-malaman itu di pintu gerbang kota, tetapi semalam-malaman itu mereka tidak berbuat apa-apa, karena pikirnya: \"Nanti pada waktu fajar kita akan membunuh dia.\"'),(6951,6,16,3,'Tetapi Simson tidur di situ sampai tengah malam. Pada waktu tengah malam bangunlah ia, dipegangnya kedua daun pintu gerbang kota itu dan kedua tiang pintu, dicabutnyalah semuanya beserta palangnya, diletakkannya di atas kedua bahunya, lalu semuanya itu diangkatnya ke puncak gunung yang berhadapan dengan Hebron.'),(6952,6,16,4,'Sesudah itu Simson jatuh cinta kepada seorang perempuan dari lembah Sorek yang namanya Delila.'),(6953,6,16,5,'Lalu datanglah raja-raja kota orang Filistin kepada perempuan itu sambil berkata: \"Cobalah bujuk dia untuk mengetahui karena apakah kekuatannya demikian besar, dan dengan apakah kami dapat mengalahkan dia dan mengikat dia untuk menundukkannya. Maka kami masing-masing akan memberikan seribu seratus uang perak kepadamu.\"'),(6954,6,16,6,'Lalu berkatalah Delila kepada Simson: \"Ceritakanlah kiranya kepadaku, karena apakah kekuatanmu demikian besar, dan dengan apakah engkau harus diikat untuk ditundukkan?\"'),(6955,6,16,7,'Jawab Simson kepadanya: \"Jika aku diikat dengan tujuh tali busur yang baru, yang belum kering, maka aku akan menjadi lemah dan menjadi seperti orang lain manapun juga.\"'),(6956,6,16,8,'Lalu raja-raja kota orang Filistin membawa tujuh tali busur yang baru yang belum kering kepada perempuan itu dan ia mengikat Simson dengan tali-tali itu,'),(6957,6,16,9,'sedang di kamarnya ada orang bersiap-siap. Kemudian berserulah perempuan itu kepadanya: \"Orang-orang Filistin menyergap engkau, Simson!\" Tetapi ia memutuskan tali-tali busur itu seperti tali rami yang terbakar putus, apabila kena api. Dan tidaklah ketahuan di mana duduk kekuatannya itu.'),(6958,6,16,10,'Kemudian berkatalah Delila kepada Simson: \"Sesungguhnya engkau telah mempermain-mainkan dan membohongi aku. Sekarang ceritakanlah kiranya kepadaku dengan apa engkau dapat diikat.\"'),(6959,6,16,11,'Jawabnya kepadanya: \"Jika aku diikat erat-erat dengan tali baru, yang belum terpakai untuk pekerjaan apapun, maka aku akan menjadi lemah dan menjadi seperti orang lain manapun juga.\"'),(6960,6,16,12,'Kemudian Delila mengambil tali baru, diikatnyalah dia dengan tali-tali itu dan berseru kepadanya: \"Orang-orang Filistin menyergap engkau, Simson!\" --di kamar ada orang bersiap-siap--tetapi tali-tali itu diputuskannya tanggal dari tangannya seperti benang saja.'),(6961,6,16,13,'Berkatalah Delila kepada Simson: \"Sampai sekarang engkau telah mempermain-mainkan dan membohongi aku. Ceritakanlah kepadaku dengan apakah engkau dapat diikat.\" Jawabnya kepadanya: \"Kalau engkau menenun ketujuh rambut jalinku bersama-sama dengan lungsin lalu mengokohkannya dengan patok, maka aku akan menjadi lemah dan menjadi seperti orang lain manapun juga.\"'),(6962,6,16,14,'Kemudian perempuan itu mengokohkan lagi tenunan itu dengan patok, lalu berserulah ia kepadanya: \"Orang-orang Filistin menyergap engkau, Simson.\" Tetapi ketika ia terjaga dari tidurnya, disentaknya lepas patok tenunan dan lungsin itu.'),(6963,6,16,15,'Berkatalah perempuan itu kepadanya: \"Bagaimana mungkin engkau berkata: Aku cinta kepadamu, padahal hatimu tidak tertuju kepadaku? Sekarang telah tiga kali engkau mempermain-mainkan aku dan tidak mau menceritakan kepadaku, karena apakah kekuatanmu demikian besar.\"'),(6964,6,16,16,'Lalu setelah perempuan itu berhari-hari merengek-rengek kepadanya dan terus mendesak-desak dia, ia tidak dapat lagi menahan hati, sehingga ia mau mati rasanya.'),(6965,6,16,17,'Maka diceritakannyalah kepadanya segala isi hatinya, katanya: \"Kepalaku tidak pernah kena pisau cukur, sebab sejak dari kandungan ibuku aku ini seorang nazir Allah. Jika kepalaku dicukur, maka kekuatanku akan lenyap dari padaku, dan aku menjadi lemah dan sama seperti orang-orang lain.\"'),(6966,6,16,18,'Ketika dilihat Delila, bahwa segala isi hatinya telah diceritakannya kepadanya, disuruhnyalah memanggil raja-raja kota orang Filistin, katanya: \"Sekali ini lagi datanglah ke mari, sebab ia telah menceritakan segala isi hatinya kepadaku.\" Lalu datanglah raja-raja kota orang Filistin itu kepadanya sambil membawa uang itu.'),(6967,6,16,19,'Sesudah itu dibujuknya Simson tidur di pangkuannya, lalu dipanggilnya seorang dan disuruhnya mencukur ketujuh rambut jalinnya, sehingga mulailah Simson ditundukkan oleh perempuan itu, sebab kekuatannya telah lenyap dari padanya.'),(6968,6,16,20,'Lalu berserulah perempuan itu: \"Orang Filistin menyergap engkau, Simson!\" Maka terjagalah ia dari tidurnya serta katanya: \"Seperti yang sudah-sudah, aku akan bebas dan akan meronta lepas.\" Tetapi tidaklah diketahuinya, bahwa TUHAN telah meninggalkan dia.'),(6969,6,16,21,'Orang Filistin itu menangkap dia, mencungkil kedua matanya dan membawanya ke Gaza. Di situ ia dibelenggu dengan dua rantai tembaga dan pekerjaannya di penjara ialah menggiling.'),(6970,6,16,22,'Tetapi rambutnya mulai tumbuh pula sesudah dicukur.'),(6971,6,16,23,'Sesudah itu berkumpullah raja-raja kota orang Filistin untuk mengadakan perayaan korban sembelihan yang besar kepada Dagon, allah mereka, dan untuk bersukaria; kata mereka: \"Telah diserahkan oleh allah kita ke dalam tangan kita Simson, musuh kita.\"'),(6972,6,16,24,'Dan ketika orang banyak melihat Simson, mereka memuji allah mereka, sambil berseru: \"Telah diserahkan oleh allah kita ke dalam tangan kita musuh kita, perusak tanah kita, dan yang membunuh banyak teman kita.\"'),(6973,6,16,25,'Ketika hati mereka riang gembira, berkatalah mereka: \"Panggillah Simson untuk melawak bagi kita.\" Simson dipanggil dari penjara, lalu ia melawak di depan mereka, kemudian mereka menyuruh dia berdiri di antara tiang-tiang.'),(6974,6,16,26,'Berkatalah Simson kepada anak yang menuntun dia: \"Lepaskan aku dan biarkanlah aku meraba-raba tiang-tiang penyangga rumah ini, supaya aku dapat bersandar padanya.\"'),(6975,6,16,27,'Adapun gedung itu penuh dengan laki-laki dan perempuan; segala raja kota orang Filistin ada di sana, dan di atas sotoh ada kira-kira tiga ribu orang laki-laki dan perempuan, yang menonton lawak Simson itu.'),(6976,6,16,28,'Berserulah Simson kepada TUHAN, katanya: \"Ya Tuhan ALLAH, ingatlah kiranya kepadaku dan buatlah aku kuat, sekali ini saja, ya Allah, supaya dengan satu pembalasan juga kubalaskan kedua mataku itu kepada orang Filistin.\"'),(6977,6,16,29,'Kemudian Simson merangkul kedua tiang yang paling tengah, penyangga rumah itu, lalu bertopang kepada tiang yang satu dengan tangan kanannya dan kepada tiang yang lain dengan tangan kirinya.'),(6978,6,16,30,'Berkatalah Simson: \"Biarlah kiranya aku mati bersama-sama orang Filistin ini.\" Lalu membungkuklah ia sekuat-kuatnya, maka rubuhlah rumah itu menimpa raja-raja kota itu dan seluruh orang banyak yang ada di dalamnya. Yang mati dibunuhnya pada waktu matinya itu lebih banyak dari pada yang dibunuhnya pada waktu hidupnya.'),(6979,6,16,31,'Sesudah itu datanglah ke sana saudara-saudaranya dan seluruh keluarganya, mereka mengangkat dia dan membawanya dari sana, lalu menguburkannya di antara Zora dan Esytaol di dalam kubur Manoah, ayahnya. Dia memerintah sebagai hakim atas orang Israel dua puluh tahun lamanya.'),(6980,6,17,1,'Ada seorang dari pegunungan Efraim, Mikha namanya.'),(6981,6,17,2,'Berkatalah ia kepada ibunya: \"Uang perak yang seribu seratus itu, yang diambil orang dari padamu dan yang karena itu kauucapkan kutuk--aku sendiri mendengar ucapanmu itu--memang uang itu ada padaku, akulah yang mengambilnya.\" Lalu kata ibunya: \"Diberkatilah kiranya anakku oleh TUHAN.\"'),(6982,6,17,3,'Sesudah itu dikembalikannyalah uang perak yang seribu seratus itu kepada ibunya. Tetapi ibunya berkata: \"Aku mau menguduskan uang itu bagi TUHAN, aku menyerahkannya untuk anakku, supaya dibuat patung pahatan dan patung tuangan dari pada uang itu. Maka sekarang, uang itu kukembalikan kepadamu.\"'),(6983,6,17,4,'Tetapi orang itu mengembalikan uang itu kepada ibunya, lalu perempuan itu mengambil dua ratus uang perak dan memberikannya kepada tukang perak, yang membuat patung pahatan dan patung tuangan dari pada uang itu; lalu patung itu ditaruh di rumah Mikha.'),(6984,6,17,5,'Mikha ini mempunyai kuil. Dibuatnyalah efod dan terafim, ditahbiskannya salah seorang anaknya laki-laki, yang menjadi imamnya.'),(6985,6,17,6,'Pada zaman itu tidak ada raja di antara orang Israel; setiap orang berbuat apa yang benar menurut pandangannya sendiri.'),(6986,6,17,7,'Maka ada seorang muda dari Betlehem-Yehuda, dari kaum Yehuda; ia seorang Lewi dan tinggal di sana sebagai pendatang.'),(6987,6,17,8,'Lalu orang itu keluar dari kota Betlehem-Yehuda untuk menetap sebagai pendatang di mana saja ia mendapat tempat; dan dalam perjalanannya itu sampailah ia ke pegunungan Efraim di rumah Mikha.'),(6988,6,17,9,'Bertanyalah Mikha kepadanya: \"Engkau dari mana?\" Jawabnya kepadanya: \"Aku orang Lewi dari Betlehem-Yehuda, dan aku pergi untuk menetap sebagai pendatang di mana saja aku mendapat tempat.\"'),(6989,6,17,10,'Lalu kata Mikha kepadanya: \"Tinggallah padaku dan jadilah bapak dan imam bagiku; maka setiap tahun aku akan memberikan kepadamu sepuluh uang perak, sepasang pakaian serta makananmu.\"'),(6990,6,17,11,'Orang Lewi itu setuju untuk tinggal padanya. Maka orang muda itu menjadi seperti salah seorang anaknya sendiri.'),(6991,6,17,12,'Mikha mentahbiskan orang Lewi itu; orang muda itu menjadi imamnya dan diam di rumah Mikha.'),(6992,6,17,13,'Lalu kata Mikha: \"Sekarang tahulah aku, bahwa TUHAN akan berbuat baik kepadaku, karena ada seorang Lewi menjadi imamku.\"'),(6993,6,18,1,'Pada zaman itu tidak ada raja di antara orang Israel dan pada zaman itu suku Dan sedang mencari milik pusaka untuk menetap, sebab sampai hari itu mereka belum juga mendapat bagian milik pusaka di tengah-tengah suku-suku Israel.'),(6994,6,18,2,'Sebab itu bani Dan menyuruh dari kaumnya lima orang dari seluruh jumlah mereka, semuanya orang-orang yang gagah perkasa, yang berasal dari Zora dan Esytaol, untuk mengintai negeri itu dan menyelidikinya, serta berkata kepada mereka: \"Pergilah menyelidiki negeri itu.\" Ketika orang-orang itu sampai ke pegunungan Efraim di rumah Mikha, bermalamlah mereka di sana.'),(6995,6,18,3,'Ketika mereka ada dekat rumah Mikha itu, dikenal merekalah logat orang muda suku Lewi itu, lalu singgahlah mereka ke sana dan berkata kepadanya: \"Siapakah yang membawa engkau ke mari? Apakah pekerjaanmu dan urusanmu di sini?\"'),(6996,6,18,4,'Katanya kepada mereka: \"Begini begitulah dilakukan Mikha kepadaku; ia menggaji aku dan aku menjadi imamnya.\"'),(6997,6,18,5,'Kata mereka kepadanya: \"Tanyakanlah kiranya kepada Allah, supaya kami ketahui apakah perjalanan yang kami tempuh ini akan berhasil.\"'),(6998,6,18,6,'Kata imam itu kepada mereka: \"Pergilah dengan selamat! Perjalanan yang kamu tempuh itu dipandang baik oleh TUHAN.\"'),(6999,6,18,7,'Sesudah itu pergilah kelima orang itu, lalu sampailah mereka ke Lais. Dilihat merekalah, bahwa rakyat yang diam di sana hidup dengan tenteram, menurut adat orang Sidon, aman dan tenteram. Orang-orang itu tidak kekurangan apapun yang ada di muka bumi, malah kaya harta. Mereka tinggal jauh dari orang Sidon dan tidak bergaul dengan siapapun juga.'),(7000,6,18,8,'Setelah mereka kembali kepada saudara-saudara sesukunya di Zora dan Esytaol, berkatalah saudara-saudara sesukunya kepada mereka: \"Apakah yang kamu dapati di sana?\"'),(7001,6,18,9,'Jawab mereka: \"Bersiaplah, marilah kita maju menyerang mereka, sebab kami telah melihat negeri itu, dan memang sangat baik. Masakan kamu tinggal diam! Janganlah bermalas-malas untuk pergi memasuki dan menduduki negeri itu.'),(7002,6,18,10,'Apabila kamu memasukinya kamu mendapati rakyat yang hidup dengan tenteram, dan negeri itu luas ke sebelah kiri dan ke sebelah kanan. Sesungguhnya, Allah telah menyerahkannya ke dalam tanganmu; itulah tempat yang tidak kekurangan apapun yang ada di muka bumi.\"'),(7003,6,18,11,'Lalu berangkatlah dari sana, dari Zora dan Esytaol, enam ratus orang dari kaum suku Dan, diperlengkapi dengan senjata.'),(7004,6,18,12,'Mereka maju, lalu berkemah di Kiryat-Yearim di Yehuda. Itulah sebabnya tempat itu disebut Mahane-Dan sampai sekarang; letaknya di sebelah barat Kiryat-Yearim.'),(7005,6,18,13,'Dari sana mereka bergerak terus ke pegunungan Efraim dan sampai di rumah Mikha.'),(7006,6,18,14,'Lalu berbicaralah kelima orang yang telah pergi mengintai daerah Lais itu, katanya kepada saudara-saudara sesukunya: \"Tahukah kamu, bahwa dalam rumah-rumah ini ada efod, terafim, patung pahatan dan patung tuangan? Oleh sebab itu, insafilah apa yang akan kamu perbuat!\"'),(7007,6,18,15,'Kemudian mereka menuju ke tempat itu, lalu sampai di rumah orang muda suku Lewi itu, di rumah Mikha, dan menanyakan apakah ia selamat.'),(7008,6,18,16,'Sementara keenam ratus orang dari bani Dan yang diperlengkapi dengan senjata itu tinggal berdiri di pintu gerbang,'),(7009,6,18,17,'maka kelima orang yang telah pergi mengintai negeri itu berjalan terus, masuk ke dalam lalu mengambil patung pahatan, efod, terafim dan patung tuangan itu. Adapun imam itu berdiri di pintu gerbang bersama-sama dengan keenam ratus orang yang diperlengkapi dengan senjata itu.'),(7010,6,18,18,'Tetapi, setelah yang lain-lain itu masuk ke dalam rumah Mikha dan mengambil patung pahatan, efod, terafim dan patung tuangan itu, berkatalah imam itu kepada mereka: \"Berbuat apakah kamu ini?\"'),(7011,6,18,19,'Tetapi jawab mereka kepadanya: \"Diamlah, tutup mulut, ikutlah kami dan jadilah bapak dan imam kami. Apakah yang lebih baik bagimu: menjadi imam untuk seisi rumah satu orang atau menjadi imam untuk suatu suku dan kaum di antara orang Israel?\"'),(7012,6,18,20,'Maka gembiralah hati imam itu, diambilnyalah efod, terafim dan patung pahatan itu, lalu masuk ke tengah-tengah orang banyak.'),(7013,6,18,21,'Kemudian berbaliklah mereka dan pergi, dengan anak-anak, ternak dan barang-barang yang berharga ditempatkan di depan mereka.'),(7014,6,18,22,'Ketika mereka telah jauh dari rumah Mikha, dikerahkanlah orang-orang dari rumah-rumah yang di dekat rumah Mikha dan orang-orang itu mengejar bani Dan itu.'),(7015,6,18,23,'Mereka memanggil-manggil bani Dan, maka berbaliklah orang-orang itu dan berkata kepada Mikha: \"Mau apa engkau dengan mengerahkan orang?\" Lalu jawabnya:'),(7016,6,18,24,'Allahku yang kubuat serta imam juga kamu ambil, lalu kamu pergi. Apakah lagi yang masih tinggal padaku? Bagaimana perkataanmu itu kepadaku: Mau apa engkau?'),(7017,6,18,25,'Berkatalah bani Dan kepadanya: \"Janganlah suaramu kedengaran lagi kepada kami, nanti ada orang yang menyerang engkau karena sakit hati dan dengan demikian engkau serta seisi rumahmu kehilangan nyawa.\"'),(7018,6,18,26,'Lalu bani Dan melanjutkan perjalanannya, dan Mikha, setelah dilihatnya mereka itu lebih kuat dari padanya, berpalinglah ia pulang ke rumahnya.'),(7019,6,18,27,'Lalu bani Dan, dengan membawa barang-barang yang dibuat Mikha, juga imamnya, mendatangi Lais, yakni rakyat yang hidup dengan aman dan tenteram. Mereka memukul orang-orang itu dengan mata pedang dan kotanya dibakar.'),(7020,6,18,28,'Tidak ada orang yang datang menolong, sebab kota itu jauh dari Sidon dan orang-orang kota itu tidak bergaul dengan siapapun juga. Letak kota itu di lembah Bet-Rehob. Kemudian bani Dan membangun kota itu kembali dan diam di sana.'),(7021,6,18,29,'Mereka menamai kota itu Dan, menurut nama bapa leluhur mereka, yakni Dan, yang lahir bagi Israel, tetapi nama kota itu dahulu adalah Lais.'),(7022,6,18,30,'Bani Dan menegakkan bagi mereka sendiri patung pahatan itu, lalu Yonatan bin Gersom bin Musa bersama-sama dengan anak-anaknya menjadi imam bagi suku Dan, sampai penduduk negeri itu diangkut sebagai orang buangan.'),(7023,6,18,31,'Demikianlah mereka menempatkan bagi mereka sendiri patung pahatan yang telah dibuat Mikha itu, dan patung itu ada di sana selama rumah Allah ada di Silo.'),(7024,6,19,1,'Terjadilah pada zaman itu, ketika tidak ada raja di Israel, bahwa di balik pegunungan Efraim ada seorang Lewi tinggal sebagai pendatang. Ia mengambil seorang gundik dari Betlehem-Yehuda.'),(7025,6,19,2,'Tetapi gundiknya itu berlaku serong terhadap dia dan pergi dari padanya ke rumah ayahnya di Betlehem-Yehuda, lalu tinggal di sana empat bulan lamanya.'),(7026,6,19,3,'Berkemaslah suaminya itu, lalu pergi menyusul perempuan itu untuk membujuk dia dan membawanya kembali; bersama-sama dia bujangnya dan sepasang keledai. Ketika perempuan muda itu membawa dia masuk ke rumah ayahnya, dan ketika ayah itu melihat dia, maka bersukacitalah ia mendapatkannya.'),(7027,6,19,4,'Mertuanya, ayah perempuan muda itu, tidak membiarkan dia pergi, sehingga ia tinggal tiga hari lamanya pada ayah itu; mereka makan, minum dan bermalam di sana.'),(7028,6,19,5,'Tetapi pada hari yang keempat, ketika mereka bangun pagi-pagi dan ketika orang Lewi itu berkemas untuk pergi, berkatalah ayah perempuan muda itu kepada menantunya: \"Segarkanlah dirimu dahulu dengan sekerat roti, kemudian bolehlah kamu pergi.\"'),(7029,6,19,6,'Jadi duduklah mereka, lalu makan dan minumlah keduanya bersama-sama. Kata ayah perempuan muda itu kepada laki-laki itu: \"Baiklah putuskan untuk tinggal bermalam dan biarlah hatimu gembira.\"'),(7030,6,19,7,'Tetapi ketika orang itu bangun untuk pergi juga, mertuanya itu mendesaknya, sehingga ia tinggal pula di sana bermalam.'),(7031,6,19,8,'Pada hari yang kelima, ketika ia bangun pagi-pagi untuk pergi, berkatalah ayah perempuan muda itu: \"Mari, segarkanlah dirimu dahulu, dan tinggallah sebentar lagi, sampai matahari surut.\" Lalu makanlah mereka keduanya.'),(7032,6,19,9,'Ketika orang itu bangun untuk pergi, bersama dengan gundiknya dan bujangnya, berkatalah mertuanya, ayah perempuan muda itu, kepadanya: \"Lihatlah, matahari telah mulai turun menjelang petang; baiklah tinggal bermalam, lihat, matahari hampir terbenam, tinggallah di sini bermalam dan biarlah hatimu gembira; maka besok kamu dapat bangun pagi-pagi untuk berjalan dan pulang ke rumahmu.\"'),(7033,6,19,10,'Tetapi orang itu tidak mau tinggal bermalam; ia berkemas, lalu pergi. Demikian sampailah ia di daerah yang berhadapan dengan Yebus--itulah Yerusalem--;bersama-sama dengan dia ada sepasang keledai yang berpelana dan gundiknya juga.'),(7034,6,19,11,'Ketika mereka dekat ke Yebus dan ketika matahari telah sangat rendah, berkatalah bujang itu kepada tuannya: \"Marilah kita singgah di kota orang Yebus ini dan bermalam di situ.\"'),(7035,6,19,12,'Tetapi tuannya menjawabnya: \"Kita tidak akan singgah di kota asing yang bukan kepunyaan orang Israel, tetapi kita akan berjalan terus sampai ke Gibea.\"'),(7036,6,19,13,'Lagi katanya kepada bujangnya: \"Marilah kita berjalan sampai ke salah satu tempat yang di sana dan bermalam di Gibea atau di Rama.\"'),(7037,6,19,14,'Lalu berjalanlah mereka melanjutkan perjalanannya, dan matahari terbenam, ketika mereka dekat Gibea kepunyaan suku Benyamin.'),(7038,6,19,15,'Sebab itu singgahlah mereka di Gibea, lalu masuk untuk bermalam di situ, dan setelah sampai, duduklah mereka di tanah lapang kota. Tetapi tidak ada seorangpun yang mengajak mereka ke rumah untuk bermalam.'),(7039,6,19,16,'Tetapi datanglah pada malam itu seorang tua, yang pulang dari pekerjaannya di ladang. Orang itu berasal dari pegunungan Efraim dan tinggal di Gibea sebagai pendatang, tetapi penduduk tempat itu adalah orang Benyamin.'),(7040,6,19,17,'Ketika ia mengangkat mukanya dan melihat orang yang dalam perjalanan itu di tanah lapang kota, berkatalah orang tua itu: \"Ke manakah engkau pergi dan dari manakah engkau datang?\"'),(7041,6,19,18,'Jawabnya kepadanya: \"Kami sedang dalam perjalanan dari Betlehem-Yehuda ke balik pegunungan Efraim. Dari sanalah aku berasal; aku tadinya pergi ke Betlehem-Yehuda dan sekarang sedang berjalan pulang ke rumah. Tetapi tidak ada orang yang mengajak aku ke rumahnya,'),(7042,6,19,19,'walaupun ada padaku jerami dan makanan untuk keledai kami, pula roti dan anggur untuk aku sendiri, untuk hambamu perempuan ini dan untuk bujang yang bersama-sama dengan hambamu ini; kami tidak kekurangan sesuatu.\"'),(7043,6,19,20,'Lalu berkatalah orang tua itu: \"Jangan kuatir! Segala yang engkau perlukan biarlah aku yang menanggung, tetapi janganlah engkau bermalam di tanah lapang kota ini.\"'),(7044,6,19,21,'Sesudah itu dibawanyalah dia masuk ke rumahnya, lalu keledai-keledai diberinya makan; maka merekapun membasuh kaki, makan dan minum.'),(7045,6,19,22,'Tetapi sementara mereka menggembirakan hatinya, datanglah orang-orang kota itu, orang-orang dursila, mengepung rumah itu. Mereka menggedor-gedor pintu sambil berkata kepada orang tua, pemilik rumah itu: \"Bawalah ke luar orang yang datang ke rumahmu itu, supaya kami pakai dia.\"'),(7046,6,19,23,'Lalu keluarlah pemilik rumah itu menemui mereka dan berkata kepada mereka: \"Tidak, saudara-saudaraku, janganlah kiranya berbuat jahat; karena orang ini telah masuk ke rumahku, janganlah kamu berbuat noda.'),(7047,6,19,24,'Tetapi ada anakku perempuan, yang masih perawan, dan juga gundik orang itu, baiklah kubawa keduanya ke luar; perkosalah mereka dan perbuatlah dengan mereka apa yang kamu pandang baik, tetapi terhadap orang ini janganlah kamu berbuat noda.\"'),(7048,6,19,25,'Tetapi orang-orang itu tidak mau mendengarkan perkataannya. Lalu orang Lewi itu menangkap gundiknya dan membawanya kepada mereka ke luar, kemudian mereka bersetubuh dengan perempuan itu dan semalam-malaman itu mereka mempermainkannya, sampai pagi. Barulah pada waktu fajar menyingsing mereka melepaskan perempuan itu.'),(7049,6,19,26,'Menjelang pagi perempuan itu datang kembali, tetapi ia jatuh rebah di depan pintu rumah orang itu, tempat tuannya bermalam, dan ia tergeletak di sana sampai fajar.'),(7050,6,19,27,'Pada waktu tuannya bangun pagi-pagi, dibukanya pintu rumah dan pergi ke luar untuk melanjutkan perjalanannya, tetapi tampaklah perempuan itu, gundiknya, tergeletak di depan pintu rumah dengan tangannya pada ambang pintu.'),(7051,6,19,28,'Berkatalah ia kepada perempuan itu: \"Bangunlah, marilah kita pergi.\" Tetapi tidak ada jawabnya. Lalu diangkatnyalah mayat itu ke atas keledai, berkemaslah ia, kemudian pergi ke tempat kediamannya.'),(7052,6,19,29,'Sesampai di rumah, diambilnyalah pisau, dipegangnyalah mayat gundiknya, dipotong-potongnya menurut tulang-tulangnya menjadi dua belas potongan, lalu dikirimnya ke seluruh daerah orang Israel.'),(7053,6,19,30,'Dan setiap orang yang melihatnya, berkata: \"Hal yang demikian belum pernah terjadi dan belum pernah terlihat, sejak orang Israel berangkat keluar dari tanah Mesir sampai sekarang. Perhatikanlah itu, pertimbangkanlah, lalu berbicaralah!\"'),(7054,6,20,1,'Lalu majulah semua orang Israel; dari Dan sampai Bersyeba dan juga dari tanah Gilead berkumpullah umat itu secara serentak menghadap TUHAN di Mizpa.'),(7055,6,20,2,'Maka berdirilah para pemuka dari seluruh bangsa itu, dari segala suku orang Israel, memimpin jemaah umat Allah yang jumlahnya empat ratus ribu orang berjalan kaki, yang bersenjatakan pedang.'),(7056,6,20,3,'Kedengaranlah kepada bani Benyamin, bahwa orang Israel telah maju ke Mizpa. Berkatalah orang Israel: \"Ceritakan bagaimana kejahatan itu terjadi.\"'),(7057,6,20,4,'Lalu orang Lewi, suami perempuan yang terbunuh itu, menjawab: \"Aku sampai dengan gundikku di Gibea kepunyaan suku Benyamin untuk bermalam di sana.'),(7058,6,20,5,'Lalu warga-warga kota Gibea itu mendatangi aku dan mengepung rumah itu pada malam hari untuk menyerang aku. Mereka bermaksud membunuh aku, tetapi gundikku diperkosa mereka, sehingga mati.'),(7059,6,20,6,'Maka kuambillah mayat gundikku, kupotong-potong dia dan kukirimkan ke seluruh daerah milik pusaka orang Israel, sebab orang-orang itu telah berbuat mesum dan berbuat noda di antara orang Israel.'),(7060,6,20,7,'Sekarang kamu sekalian, orang Israel, telah ada di sini. Berikanlah di sini pertimbanganmu dan nasihatmu.\"'),(7061,6,20,8,'Kemudian bangunlah seluruh bangsa itu dengan serentak, sambil berkata: \"Seorangpun dari pada kita takkan pergi ke kemahnya, seorangpun dari pada kita takkan pulang ke rumahnya.'),(7062,6,20,9,'Inilah yang akan kita lakukan kepada Gibea; memeranginya, dengan membuang undi!'),(7063,6,20,10,'Kita akan memilih dari seluruh suku Israel sepuluh orang dari tiap-tiap seratus, seratus orang dari tiap-tiap seribu, seribu orang dari tiap-tiap sepuluh ribu, untuk mengambil bekal bagi laskar ini, supaya sesudah mereka datang, dilakukan kepada Gibea-Benyamin setimpal dengan segala perbuatan noda yang telah diperbuat mereka di antara orang Israel.\"'),(7064,6,20,11,'Demikianlah orang Israel berkumpul melawan kota itu, semuanya bersekutu dengan serentak.'),(7065,6,20,12,'Kemudian suku-suku Israel mengirim orang kepada seluruh suku Benyamin dengan pesan: \"Apa macam kejahatan yang terjadi di antara kamu itu!'),(7066,6,20,13,'Maka sekarang, serahkanlah orang-orang itu, yakni orang-orang dursila yang di Gibea itu, supaya kami menghukum mati mereka dan dengan demikian menghapuskan yang jahat itu dari antara orang Israel.\" Tetapi bani Benyamin tidak mau mendengarkan perkataan saudara-saudaranya, orang Israel itu.'),(7067,6,20,14,'Sebaliknya, bani Benyamin dari kota-kota lain berkumpul di Gibea untuk maju berperang melawan orang Israel.'),(7068,6,20,15,'Pada hari itu dihitunglah jumlah bani Benyamin dari kota-kota lain itu: dua puluh enam ribu orang yang bersenjatakan pedang, belum termasuk penduduk Gibea, yang terhitung tujuh ratus orang pilihan banyaknya.'),(7069,6,20,16,'Dari segala laskar ini ada tujuh ratus orang pilihan yang kidal, dan setiap orang dari mereka dapat mengumban dengan tidak pernah meleset sampai sehelai rambutpun.'),(7070,6,20,17,'Juga orang-orang Israel dihitung jumlahnya; dengan tidak termasuk suku Benyamin ada empat ratus ribu orang yang bersenjatakan pedang; semuanya itu prajurit.'),(7071,6,20,18,'Lalu orang Israel berangkat dan maju ke Betel. Di sana mereka bertanya kepada Allah: \"Siapakah dari kami yang lebih dahulu maju berperang melawan bani Benyamin?\" Jawab TUHAN: \"Suku Yehudalah lebih dahulu.\"'),(7072,6,20,19,'Lalu orang-orang Israel bangun pagi-pagi dan berkemah mengepung Gibea.'),(7073,6,20,20,'Kemudian majulah orang-orang Israel berperang melawan suku Benyamin; orang-orang Israel mengatur barisan perangnya melawan mereka dekat Gibea.'),(7074,6,20,21,'Juga bani Benyamin maju menyerang dari Gibea dan menggugurkan ke bumi dua puluh dua ribu orang dari antara orang Israel pada hari itu.'),(7075,6,20,22,'Tetapi laskar orang Israel mengumpulkan segenap kekuatannya, lalu mengatur pula barisan perangnya di tempat mereka mengatur barisannya semula.'),(7076,6,20,23,'Kemudian pergilah orang-orang Israel, lalu menangis di hadapan TUHAN sampai petang, sesudah itu mereka bertanya kepada TUHAN: \"Akan pergi pulakah kami berperang melawan bani Benyamin, saudara kami itu?\" Jawab TUHAN: \"Majulah melawan mereka.\"'),(7077,6,20,24,'Tetapi ketika orang-orang Israel pada hari kedua sampai di dekat bani Benyamin,'),(7078,6,20,25,'maka pada hari kedua itu majulah suku Benyamin dari Gibea menyerbu mereka, dan digugurkannya pula ke bumi delapan belas ribu orang di antara orang-orang Israel; semuanya orang-orang yang bersenjatakan pedang.'),(7079,6,20,26,'Kemudian pergilah semua orang Israel, yakni seluruh bangsa itu, lalu sampai di Betel; di sana mereka tinggal menangis di hadapan TUHAN, berpuasa sampai senja pada hari itu dan mempersembahkan korban bakaran dan korban keselamatan di hadapan TUHAN.'),(7080,6,20,27,'Dan orang-orang Israel bertanya kepada TUHAN--pada waktu itu ada di sana tabut perjanjian Allah,'),(7081,6,20,28,'dan Pinehas bin Eleazar bin Harun menjadi imam Allah pada waktu itu--kata mereka: \"Haruskah kami maju sekali lagi untuk berperang melawan bani Benyamin, saudara kami itu, atau haruskah kami hentikan itu?\" Jawab TUHAN: \"Majulah, sebab besok Aku akan menyerahkan mereka ke dalam tanganmu.\"'),(7082,6,20,29,'Lalu orang Israel menempatkan penghadang-penghadang sekeliling Gibea.'),(7083,6,20,30,'Pada hari ketiga majulah orang-orang Israel melawan bani Benyamin dan mengatur barisannya melawan Gibea seperti yang sudah-sudah.'),(7084,6,20,31,'Maka majulah bani Benyamin menyerbu laskar itu; mereka terpancing dari kota, dan seperti yang sudah-sudah, mereka mulai menyerang laskar itu pada kedua jalan raya--yang satu menuju ke Betel, dan yang lain ke Gibea melalui padang--sehingga terbunuh beberapa orang, kira-kira tiga puluh orang di antara orang Israel.'),(7085,6,20,32,'Maka kata bani Benyamin: \"Orang-orang itu telah terpukul kalah oleh kita seperti semula.\" Tetapi orang-orang Israel telah bermupakat lebih dahulu: \"Marilah kita lari dan memancing mereka dari kota ke jalan-jalan raya.\"'),(7086,6,20,33,'Jadi orang Israel bangun dari tempatnya dan mengatur barisannya di Baal-Tamar, sedang orang Israel yang menghadang itu tiba-tiba keluar dari tempatnya, yakni tempat terbuka dekat Geba,'),(7087,6,20,34,'dan sampai di depan Gibea, sebanyak sepuluh ribu orang pilihan dari seluruh Israel. Pertempuran itu dahsyat, tetapi bani Benyamin tidak tahu bahwa malapetaka datang menimpa mereka.'),(7088,6,20,35,'TUHAN membuat suku Benyamin terpukul kalah oleh orang Israel, dan pada hari itu orang-orang Israel memusnahkan dari antara suku Benyamin dua puluh lima ribu seratus orang, semuanya orang-orang yang bersenjatakan pedang.'),(7089,6,20,36,'Bani Benyamin melihat, bahwa mereka telah terpukul kalah. Sementara orang-orang Israel agak mundur di depan suku Benyamin--sebab mereka mempercayai penghadang-penghadang yang ditempatkan mereka untuk menyerang Gibea--'),(7090,6,20,37,'maka segeralah penghadang-penghadang itu menyerbu Gibea. Mereka bergerak maju dan memukul seluruh kota itu dengan mata pedang.'),(7091,6,20,38,'Tetapi orang-orang Israel telah bermupakat dengan penghadang-penghadang itu untuk menaikkan gumpalan asap tebal dari kota itu.'),(7092,6,20,39,'Ketika orang-orang Israel mundur dalam pertempuran itu, maka suku Benyamin mulai menyerang orang Israel, sehingga terbunuh kira-kira tiga puluh orang, karena pikir mereka: \"Tentulah orang-orang itu terpukul kalah sama sekali oleh kita seperti dalam pertempuran yang dahulu.\"'),(7093,6,20,40,'Tetapi pada waktu itu mulailah gumpalan asap naik dari kota itu seperti tiang asap. Suku Benyamin menoleh ke belakang dan tampaklah kota itu seluruhnya terbakar, apinya naik ke langit.'),(7094,6,20,41,'Lagipula orang-orang Israel maju lagi. Maka gemetarlah orang-orang Benyamin itu, sebab mereka melihat, bahwa malapetaka datang menimpa mereka.'),(7095,6,20,42,'Jadi larilah mereka dari depan orang-orang Israel itu, ke arah padang gurun, tetapi pertempuran itu tidak dapat dihindari mereka, lalu orang-orang dari kota-kota menghabisi mereka di tengah-tengahnya.'),(7096,6,20,43,'Mereka mengepung suku Benyamin itu, mengejarnya dengan tak henti-hentinya dan melandanya sampai di depan Gibea, di sebelah timur.'),(7097,6,20,44,'Dari bani Benyamin ada tewas delapan belas ribu orang, semuanya orang-orang gagah perkasa.'),(7098,6,20,45,'Yang lain berpaling lari ke padang gurun, ke bukit batu Rimon. Tetapi di jalan-jalan raya masih diadakan penyabitan susulan di antara mereka: lima ribu orang; mereka diburu sampai ke Gideom dan dipukul mati dua ribu orang dari mereka.'),(7099,6,20,46,'Maka yang tewas dari suku Benyamin pada hari itu seluruhnya berjumlah dua puluh lima ribu orang yang bersenjatakan pedang, semuanya orang-orang gagah perkasa.'),(7100,6,20,47,'Tetapi enam ratus orang berpaling lari ke padang gurun, ke bukit batu Rimon, dan tinggal empat bulan lamanya di bukit batu itu.'),(7101,6,20,48,'Tetapi orang-orang Israel kembali kepada bani Benyamin dan memukul mereka dengan mata pedang, baik manusia baik hewan dan segala sesuatu yang terdapat di sana. Juga segala kota yang terdapat di sana mereka musnahkan dengan api.'),(7102,6,21,1,'Orang-orang Israel telah bersumpah di Mizpa, demikian: \"Seorangpun dari kita takkan memberikan anaknya perempuan kepada seorang Benyamin menjadi isterinya.\"'),(7103,6,21,2,'Ketika bangsa itu datang ke Betel dan tinggal di situ di hadapan Allah sampai petang, maka merekapun menyaringkan suaranya menangis dengan sangat keras,'),(7104,6,21,3,'katanya: \"Mengapa, ya TUHAN, Allah Israel, terjadi hal yang begini di antara orang Israel, yakni bahwa hari ini satu suku dari antara orang Israel hilang?\"'),(7105,6,21,4,'Keesokan harinya pagi-pagi maka bangsa itu mendirikan mezbah di situ, lalu mempersembahkan korban bakaran dan korban keselamatan.'),(7106,6,21,5,'Pada waktu itu berkatalah orang-orang Israel: \"Siapakah dari seluruh suku Israel yang tidak ikut datang dengan jemaah ini untuk menghadap TUHAN?\" Sebab mereka telah bersumpah dengan sungguh-sungguh mengenai orang yang tidak datang menghadap TUHAN di Mizpa, demikian: \"Pastilah ia dihukum mati.\"'),(7107,6,21,6,'Orang-orang Israel merasa kasihan terhadap suku Benyamin, saudaranya itu, maka kata mereka: \"Hari ini ada satu suku terputus dari orang Israel.'),(7108,6,21,7,'Apakah yang dapat kita lakukan kepada orang-orang yang tinggal itu dalam hal mencarikan isteri, karena kitalah yang bersumpah demi TUHAN untuk tidak memberikan seorangpun dari anak-anak perempuan kita kepada mereka menjadi isterinya?\"'),(7109,6,21,8,'Sebab itu berkatalah mereka: \"Dari suku-suku Israel adakah satu yang tidak datang menghadap TUHAN di Mizpa?\" Lalu tampaklah, bahwa dari Yabesh-Gilead tidak ada seorangpun yang datang ke perkemahan jemaah itu.'),(7110,6,21,9,'Lalu diperiksa jumlah bangsa itu, dan tampaklah tidak hadir seorangpun dari penduduk Yabesh-Gilead.'),(7111,6,21,10,'Maka perkumpulan itu menyuruh ke situ dua belas ribu orang dari orang-orang gagah perkasa dengan memerintahkan kepada mereka, demikian: \"Pergilah, pukullah penduduk Yabesh-Gilead dengan mata pedang, juga perempuan-perempuan dan anak-anak.'),(7112,6,21,11,'Tetapi perbuatlah begini: hanya semua laki-laki sajalah dan semua perempuan yang telah pernah tidur dengan laki-laki harus kamu tumpas.\"'),(7113,6,21,12,'Mereka menjumpai di antara penduduk Yabesh-Gilead empat ratus orang anak gadis, perawan yang belum pernah tidur dengan orang laki-laki, lalu gadis-gadis itu dibawa mereka ke perkemahan di Silo, di tanah Kanaan.'),(7114,6,21,13,'Sesudah itu segenap umat itu menyuruh orang membawa pesan kepada bani Benyamin yang ada di bukit batu Rimon, lalu memaklumkan damai kepada mereka.'),(7115,6,21,14,'Pada waktu itu kembalilah suku Benyamin, dan kepada mereka diberikan perempuan-perempuan yang telah dibiarkan hidup dari antara perempuan Yabesh-Gilead; tetapi belum cukup juga jumlahnya bagi mereka.'),(7116,6,21,15,'Maka bangsa itu merasa kasihan kepada suku Benyamin, karena TUHAN telah membuat keretakan di antara suku-suku Israel.'),(7117,6,21,16,'Kemudian berkatalah para tua-tua umat itu: \"Apakah yang dapat kita lakukan kepada yang tinggal ini dalam hal mencarikan isteri? Sebab perempuan-perempuan telah punah dari antara suku Benyamin.\"'),(7118,6,21,17,'Lagi kata mereka: \"Warisan orang-orang yang terluput itu haruslah tetap tinggal pada suku Benyamin, supaya jangan ada suku yang terhapus dari antara orang Israel.'),(7119,6,21,18,'Tetapi kita ini tidak dapat memberikan isteri kepada mereka dari anak-anak perempuan kita.\" Sebab orang-orang Israel telah bersumpah, demikian: \"Terkutuklah orang yang memberikan isteri kepada suku Benyamin!\"'),(7120,6,21,19,'Lalu kata mereka pula: \"Setiap tahun ada perayaan bagi TUHAN di Silo yang letaknya di sebelah utara Betel, di sebelah timur jalan raya yang menuju dari Betel ke Sikhem dan di sebelah selatan Lebona.\"'),(7121,6,21,20,'Maka mereka berpesan kepada bani Benyamin, demikian: \"Pergilah menghadang di kebun-kebun anggur.'),(7122,6,21,21,'Perhatikanlah baik-baik; maka apabila anak-anak perempuan Silo keluar untuk menari-nari, baiklah kamu keluar dari kebun-kebun anggur itu, dan masing-masing melarikan seorang dari anak-anak perempuan Silo itu menjadi isterinya dan pergi ke tanah Benyamin.'),(7123,6,21,22,'Apabila ayah atau saudaranya laki-laki datang untuk menuntutnya kepada kami, maka kami akan berkata kepada mereka: Serahkanlah mereka itu kepada kami dengan rela hati, sebab dalam pertempuran kita tidak dapat menangkap seorang perempuan untuk menjadi isteri mereka masing-masing. Memang kamu ini tidak memberikan anak-anak gadis itu kepada mereka; sebab seandainya demikian, kamu bersalah.\"'),(7124,6,21,23,'Jadi bani Benyamin berbuat demikian; dari gadis-gadis yang menari-nari yang dirampas itu mereka mengambil perempuan, jumlahnya sama dengan jumlah mereka, kemudian pulanglah mereka ke milik pusakanya lalu membangun kota-kotanya kembali dan diam di sana.'),(7125,6,21,24,'Pada waktu itu pergilah orang Israel dari sana, masing-masing menurut suku dan kaumnya; mereka masing-masing berangkat dari sana ke milik pusakanya.'),(7126,6,21,25,'Pada zaman itu tidak ada raja di antara orang Israel; setiap orang berbuat apa yang benar menurut pandangannya sendiri.'),(7127,7,1,1,'Pada zaman para hakim memerintah ada kelaparan di tanah Israel. Lalu pergilah seorang dari Betlehem-Yehuda beserta isterinya dan kedua anaknya laki-laki ke daerah Moab untuk menetap di sana sebagai orang asing.'),(7128,7,1,2,'Nama orang itu ialah Elimelekh, nama isterinya Naomi dan nama kedua anaknya Mahlon dan Kilyon, semuanya orang-orang Efrata dari Betlehem-Yehuda; dan setelah sampai ke daerah Moab, diamlah mereka di sana.'),(7129,7,1,3,'Kemudian matilah Elimelekh, suami Naomi, sehingga perempuan itu tertinggal dengan kedua anaknya.'),(7130,7,1,4,'Keduanya mengambil perempuan Moab: yang pertama bernama Orpa, yang kedua bernama Rut; dan mereka diam di situ kira-kira sepuluh tahun lamanya.'),(7131,7,1,5,'Lalu matilah juga keduanya, yakni Mahlon dan Kilyon, sehingga perempuan itu kehilangan kedua anaknya dan suaminya.'),(7132,7,1,6,'Kemudian berkemaslah ia dengan kedua menantunya dan ia pulang dari daerah Moab, sebab di daerah Moab ia mendengar bahwa TUHAN telah memperhatikan umat-Nya dan memberikan makanan kepada mereka.'),(7133,7,1,7,'Maka berangkatlah ia dari tempat tinggalnya itu, bersama-sama dengan kedua menantunya. Ketika mereka sedang di jalan untuk pulang ke tanah Yehuda,'),(7134,7,1,8,'berkatalah Naomi kepada kedua menantunya itu: \"Pergilah, pulanglah masing-masing ke rumah ibunya; TUHAN kiranya menunjukkan kasih-Nya kepadamu, seperti yang kamu tunjukkan kepada orang-orang yang telah mati itu dan kepadaku;'),(7135,7,1,9,'kiranya atas karunia TUHAN kamu mendapat tempat perlindungan, masing-masing di rumah suaminya.\" Lalu diciumnyalah mereka, tetapi mereka menangis dengan suara keras'),(7136,7,1,10,'dan berkata kepadanya: \"Tidak, kami ikut dengan engkau pulang kepada bangsamu.\"'),(7137,7,1,11,'Tetapi Naomi berkata: \"Pulanglah, anak-anakku, mengapakah kamu turut dengan aku? Bukankah tidak akan ada lagi anak laki-laki yang kulahirkan untuk dijadikan suamimu nanti?'),(7138,7,1,12,'Pulanglah, anak-anakku, pergilah, sebab sudah terlalu tua aku untuk bersuami. Seandainya pikirku: Ada harapan bagiku, dan sekalipun malam ini aku bersuami, bahkan sekalipun aku masih melahirkan anak laki-laki,'),(7139,7,1,13,'masakan kamu menanti sampai mereka dewasa? Masakan karena itu kamu harus menahan diri dan tidak bersuami? Janganlah kiranya demikian, anak-anakku, bukankah jauh lebih pahit yang aku alami dari pada kamu, sebab tangan TUHAN teracung terhadap aku?\"'),(7140,7,1,14,'Menangis pula mereka dengan suara keras, lalu Orpa mencium mertuanya itu minta diri, tetapi Rut tetap berpaut padanya.'),(7141,7,1,15,'Berkatalah Naomi: \"Telah pulang iparmu kepada bangsanya dan kepada para allahnya; pulanglah mengikuti iparmu itu.\"'),(7142,7,1,16,'Tetapi kata Rut: \"Janganlah desak aku meninggalkan engkau dan pulang dengan tidak mengikuti engkau; sebab ke mana engkau pergi, ke situ jugalah aku pergi, dan di mana engkau bermalam, di situ jugalah aku bermalam: bangsamulah bangsaku dan Allahmulah Allahku;'),(7143,7,1,17,'di mana engkau mati, akupun mati di sana, dan di sanalah aku dikuburkan. Beginilah kiranya TUHAN menghukum aku, bahkan lebih lagi dari pada itu, jikalau sesuatu apapun memisahkan aku dari engkau, selain dari pada maut!\"'),(7144,7,1,18,'Ketika Naomi melihat, bahwa Rut berkeras untuk ikut bersama-sama dengan dia, berhentilah ia berkata-kata kepadanya.'),(7145,7,1,19,'Dan berjalanlah keduanya sampai mereka tiba di Betlehem. Ketika mereka masuk ke Betlehem, gemparlah seluruh kota itu karena mereka, dan perempuan-perempuan berkata: \"Naomikah itu?\"'),(7146,7,1,20,'Tetapi ia berkata kepada mereka: \"Janganlah sebutkan aku Naomi; sebutkanlah aku Mara, sebab Yang Mahakuasa telah melakukan banyak yang pahit kepadaku.'),(7147,7,1,21,'Dengan tangan yang penuh aku pergi, tetapi dengan tangan yang kosong TUHAN memulangkan aku. Mengapakah kamu menyebutkan aku Naomi, karena TUHAN telah naik saksi menentang aku dan Yang Mahakuasa telah mendatangkan malapetaka kepadaku.\"'),(7148,7,1,22,'Demikianlah Naomi pulang bersama-sama dengan Rut, perempuan Moab itu, menantunya, yang turut pulang dari daerah Moab. Dan sampailah mereka ke Betlehem pada permulaan musim menuai jelai.'),(7149,7,2,1,'Naomi itu mempunyai seorang sanak dari pihak suaminya, seorang yang kaya raya dari kaum Elimelekh, namanya Boas.'),(7150,7,2,2,'Maka Rut, perempuan Moab itu, berkata kepada Naomi: \"Biarkanlah aku pergi ke ladang memungut bulir-bulir jelai di belakang orang yang murah hati kepadaku.\" Dan sahut Naomi kepadanya: \"Pergilah, anakku.\"'),(7151,7,2,3,'Pergilah ia, lalu sampai di ladang dan memungut jelai di belakang penyabit-penyabit; kebetulan ia berada di tanah milik Boas, yang berasal dari kaum Elimelekh.'),(7152,7,2,4,'Lalu datanglah Boas dari Betlehem. Ia berkata kepada penyabit-penyabit itu: \"TUHAN kiranya menyertai kamu.\" Jawab mereka kepadanya: \"TUHAN kiranya memberkati tuan!\"'),(7153,7,2,5,'Lalu kata Boas kepada bujangnya yang mengawasi penyabit-penyabit itu: \"Dari manakah perempuan ini?\"'),(7154,7,2,6,'Bujang yang mengawasi penyabit-penyabit itu menjawab: \"Dia adalah seorang perempuan Moab, dia pulang bersama-sama dengan Naomi dari daerah Moab.'),(7155,7,2,7,'Tadi ia berkata: Izinkanlah kiranya aku memungut dan mengumpulkan jelai dari antara berkas-berkas jelai ini di belakang penyabit-penyabit. Begitulah ia datang dan terus sibuk dari pagi sampai sekarang dan seketikapun ia tidak berhenti.\"'),(7156,7,2,8,'Sesudah itu berkatalah Boas kepada Rut: \"Dengarlah dahulu, anakku! Tidak usah engkau pergi memungut jelai ke ladang lain dan tidak usah juga engkau pergi dari sini, tetapi tetaplah dekat pengerja-pengerja perempuan.'),(7157,7,2,9,'Lihat saja ke ladang yang sedang disabit orang itu. Ikutilah perempuan-perempuan itu dari belakang. Sebab aku telah memesankan kepada pengerja-pengerja lelaki jangan mengganggu engkau. Jika engkau haus, pergilah ke tempayan-tempayan dan minumlah air yang dicedok oleh pengerja-pengerja itu.\"'),(7158,7,2,10,'Lalu sujudlah Rut menyembah dengan mukanya sampai ke tanah dan berkata kepadanya: \"Mengapakah aku mendapat belas kasihan dari padamu, sehingga tuan memperhatikan aku, padahal aku ini seorang asing?\"'),(7159,7,2,11,'Boas menjawab: \"Telah dikabarkan orang kepadaku dengan lengkap segala sesuatu yang engkau lakukan kepada mertuamu sesudah suamimu mati, dan bagaimana engkau meninggalkan ibu bapamu dan tanah kelahiranmu serta pergi kepada suatu bangsa yang dahulu tidak engkau kenal.'),(7160,7,2,12,'TUHAN kiranya membalas perbuatanmu itu, dan kepadamu kiranya dikaruniakan upahmu sepenuhnya oleh TUHAN, Allah Israel, yang di bawah sayap-Nya engkau datang berlindung.\"'),(7161,7,2,13,'Kemudian berkatalah Rut: \"Memang aku mendapat belas kasihan dari padamu, ya tuanku, sebab tuan telah menghiburkan aku dan telah menenangkan hati hambamu ini, walaupun aku tidak sama seperti salah seorang hamba-hambamu perempuan.\"'),(7162,7,2,14,'Ketika sudah waktu makan, berkatalah Boas kepadanya: \"Datanglah ke mari, makanlah roti ini dan celupkanlah suapmu ke dalam cuka ini.\" Lalu duduklah ia di sisi penyabit-penyabit itu, dan Boas mengunjukkan bertih gandum kepadanya; makanlah Rut sampai kenyang, bahkan ada sisanya.'),(7163,7,2,15,'Setelah ia bangun untuk memungut pula, maka Boas memerintahkan kepada pengerja-pengerjanya: \"Dari antara berkas-berkas itupun ia boleh memungut, janganlah ia diganggu;'),(7164,7,2,16,'bahkan haruslah kamu dengan sengaja menarik sedikit-sedikit dari onggokan jelai itu untuk dia dan meninggalkannya, supaya dipungutnya; janganlah berlaku kasar terhadap dia.\"'),(7165,7,2,17,'Maka ia memungut di ladang sampai petang; lalu ia mengirik yang dipungutnya itu, dan ada kira-kira seefa jelai banyaknya.'),(7166,7,2,18,'Diangkatnyalah itu, lalu masuklah ia ke kota. Ketika mertuanya melihat apa yang dipungutnya itu, dan ketika dikeluarkannya dan diberikannya kepada mertuanya sisa yang ada setelah kenyang itu,'),(7167,7,2,19,'maka berkatalah mertuanya kepadanya: \"Di mana engkau memungut dan di mana engkau bekerja hari ini? Diberkatilah kiranya orang yang telah memperhatikan engkau itu!\" Lalu diceritakannyalah kepada mertuanya itu pada siapa ia bekerja, katanya: \"Nama orang pada siapa aku bekerja hari ini ialah Boas.\"'),(7168,7,2,20,'Sesudah itu berkatalah Naomi kepada menantunya: \"Diberkatilah kiranya orang itu oleh TUHAN yang rela mengaruniakan kasih setia-Nya kepada orang-orang yang hidup dan yang mati.\" Lagi kata Naomi kepadanya: \"Orang itu kaum kerabat kita, dialah salah seorang yang wajib menebus kita.\"'),(7169,7,2,21,'Lalu kata Rut, perempuan Moab itu: \"Lagipula ia berkata kepadaku: Tetaplah dekat pengerja-pengerjaku sampai mereka menyelesaikan seluruh penyabitan ladangku.\"'),(7170,7,2,22,'Lalu berkatalah Naomi kepada Rut, menantunya itu: \"Ya anakku, sebaiknya engkau keluar bersama-sama dengan pengerja-pengerjanya perempuan, supaya engkau jangan disusahi orang di ladang lain.\"'),(7171,7,2,23,'Demikianlah Rut tetap dekat pada pengerja-pengerja perempuan Boas untuk memungut, sampai musim menuai jelai dan musim menuai gandum telah berakhir. Dan selama itu ia tinggal pada mertuanya.'),(7172,7,3,1,'Lalu Naomi, mertuanya itu, berkata kepadanya: \"Anakku, apakah tidak ada baiknya jika aku mencari tempat perlindungan bagimu supaya engkau berbahagia?'),(7173,7,3,2,'Maka sekarang, bukankah Boas, yang pengerja-pengerjanya perempuan telah kautemani itu, adalah sanak kita? Dia pada malam ini menampi jelai di tempat pengirikan;'),(7174,7,3,3,'maka mandilah dan beruraplah, pakailah pakaian bagusmu dan pergilah ke tempat pengirikan itu. Tetapi janganlah engkau ketahuan kepada orang itu, sebelum ia selesai makan dan minum.'),(7175,7,3,4,'Jika ia membaringkan diri tidur, haruslah engkau perhatikan baik-baik tempat ia berbaring; kemudian datanglah dekat, singkapkanlah selimut dari kakinya dan berbaringlah di sana. Maka ia akan memberitahukan kepadamu apa yang harus kaulakukan.\"'),(7176,7,3,5,'Lalu kata Rut kepadanya: \"Segala yang engkau katakan itu akan kulakukan.\"'),(7177,7,3,6,'Sesudah itu pergilah ia ke tempat pengirikan dan dilakukannyalah tepat seperti yang diperintahkan mertuanya kepadanya.'),(7178,7,3,7,'Setelah Boas habis makan dan minum dan hatinya gembira, datanglah ia untuk membaringkan diri tidur pada ujung timbunan jelai itu. Kemudian datanglah perempuan itu dekat dengan diam-diam, disingkapkannyalah selimut dari kaki Boas dan berbaringlah ia di situ.'),(7179,7,3,8,'Pada waktu tengah malam dengan terkejut terjagalah orang itu, lalu meraba-raba ke sekelilingnya, dan ternyata ada seorang perempuan berbaring di sebelah kakinya.'),(7180,7,3,9,'Bertanyalah ia: \"Siapakah engkau ini?\" Jawabnya: \"Aku Rut, hambamu: kembangkanlah kiranya sayapmu melindungi hambamu ini, sebab engkaulah seorang kaum yang wajib menebus kami.\"'),(7181,7,3,10,'Lalu katanya: \"Diberkatilah kiranya engkau oleh TUHAN, ya anakku! Sekarang engkau menunjukkan kasihmu lebih nyata lagi dari pada yang pertama kali itu, karena engkau tidak mengejar-ngejar orang-orang muda, baik yang miskin maupun yang kaya.'),(7182,7,3,11,'Oleh sebab itu, anakku, janganlah takut; segala yang kaukatakan itu akan kulakukan kepadamu; sebab setiap orang dalam kota kami tahu, bahwa engkau seorang perempuan baik-baik.'),(7183,7,3,12,'Maka sekarang, memang aku seorang kaum yang wajib menebus, tetapi walaupun demikian masih ada lagi seorang penebus, yang lebih dekat dari padaku.'),(7184,7,3,13,'Tinggallah di sini malam ini; dan besok pagi, jika ia mau menebus engkau, baik, biarlah ia menebus; tetapi jika ia tidak suka menebus engkau, maka akulah yang akan menebus engkau, demi TUHAN yang hidup. Berbaring sajalah tidur sampai pagi.\"'),(7185,7,3,14,'Jadi berbaringlah ia tidur di sebelah kakinya sampai pagi; lalu bangunlah ia, sebelum orang dapat kenal-mengenal, sebab kata Boas: \"Janganlah diketahui orang, bahwa seorang perempuan datang ke tempat pengirikan.\"'),(7186,7,3,15,'Lagi katanya: \"Berikanlah selendang yang engkau pakai itu dan tadahkanlah itu.\" Lalu ditadahkannya selendang itu. Kemudian ditakarnyalah enam takar jelai ke dalam selendang itu. Sesudah itu pergilah Boas ke kota.'),(7187,7,3,16,'Setelah perempuan itu sampai kepada mertuanya, berkatalah mertuanya itu: \"Bagaimana, anakku?\" Lalu diceritakannyalah semua yang dilakukan orang itu kepadanya'),(7188,7,3,17,'serta berkata: \"Yang enam takar jelai ini diberikannya kepadaku, sebab katanya: Engkau tidak boleh pulang kepada mertuamu dengan tangan hampa.\"'),(7189,7,3,18,'Lalu kata mertuanya itu: \"Duduk sajalah menanti, anakku, sampai engkau mengetahui, bagaimana kesudahan perkara itu; sebab orang itu tidak akan berhenti, sebelum diselesaikannya perkara itu pada hari ini juga.\"'),(7190,7,4,1,'Boas telah pergi ke pintu gerbang dan duduk di sana. Kebetulan lewatlah penebus yang disebutkan Boas itu. Lalu berkatalah Boas: \"Hai saudara, datanglah dahulu ke mari, duduklah di sini.\" Maka datanglah ia, lalu duduk.'),(7191,7,4,2,'Kemudian dipilihnyalah sepuluh orang dari para tua-tua kota itu, dan berkata: \"Duduklah kamu di sini.\" Maka duduklah mereka.'),(7192,7,4,3,'Lalu berkatalah ia kepada penebus itu: \"Tanah milik kepunyaan saudara kita Elimelekh hendak dijual oleh Naomi, yang telah pulang dari daerah Moab.'),(7193,7,4,4,'Jadi pikirku: baik juga hal itu kusampaikan kepadamu sebagai berikut: Belilah tanah itu di depan orang-orang yang duduk di sini dan di depan para tua-tua bangsa kita. Jika engkau mau menebusnya, tebuslah; tetapi jika engkau tidak mau menebusnya, beritahukanlah kepadaku, supaya aku tahu, sebab tidak ada orang yang dapat menebusnya kecuali engkau, dan sesudah engkau: aku.\" Lalu berkatalah ia: \"Aku akan menebusnya.\"'),(7194,7,4,5,'Tetapi kata Boas: \"Pada waktu engkau membeli tanah itu dari tangan Naomi, engkau memperoleh Rut juga, perempuan Moab, isteri orang yang telah mati itu, untuk menegakkan nama orang itu di atas milik pusakanya.\"'),(7195,7,4,6,'Lalu berkatalah penebus itu: \"Jika demikian, aku ini tidak dapat menebusnya, sebab aku akan merusakkan milik pusakaku sendiri. Aku mengharap engkau menebus apa yang seharusnya aku tebus, sebab aku tidak dapat menebusnya.\"'),(7196,7,4,7,'Beginilah kebiasaan dahulu di Israel dalam hal menebus dan menukar: setiap kali orang hendak menguatkan sesuatu perkara, maka yang seorang menanggalkan kasutnya sebelah dan memberikannya kepada yang lain. Demikianlah caranya orang mensahkan perkara di Israel.'),(7197,7,4,8,'Lalu penebus itu berkata kepada Boas: \"Engkau saja yang membelinya.\" Dan ditanggalkannyalah kasutnya.'),(7198,7,4,9,'Kemudian berkatalah Boas kepada para tua-tua dan kepada semua orang di situ: \"Kamulah pada hari ini menjadi saksi, bahwa segala milik Elimelekh dan segala milik Kilyon dan Mahlon, aku beli dari tangan Naomi;'),(7199,7,4,10,'juga Rut, perempuan Moab itu, isteri Mahlon, aku peroleh menjadi isteriku untuk menegakkan nama orang yang telah mati itu di atas milik pusakanya. Demikianlah nama orang itu tidak akan lenyap dari antara saudara-saudaranya dan dari antara warga kota. Kamulah pada hari ini menjadi saksi.\"'),(7200,7,4,11,'Dan seluruh orang banyak yang hadir di pintu gerbang, dan para tua-tua berkata: \"Kamilah menjadi saksi. TUHAN kiranya membuat perempuan yang akan masuk ke rumahmu itu sama seperti Rahel dan Lea, yang keduanya telah membangunkan umat Israel. Biarlah engkau menjadi makmur di Efrata dan biarlah namamu termasyhur di Betlehem,'),(7201,7,4,12,'keturunanmu kiranya menjadi seperti keturunan Peres yang dilahirkan Tamar bagi Yehuda oleh karena anak-anak yang akan diberikan TUHAN kepadamu dari perempuan muda ini!\"'),(7202,7,4,13,'Lalu Boas mengambil Rut dan perempuan itu menjadi isterinya dan dihampirinyalah dia. Maka atas karunia TUHAN perempuan itu mengandung, lalu melahirkan seorang anak laki-laki.'),(7203,7,4,14,'Sebab itu perempuan-perempuan berkata kepada Naomi: \"Terpujilah TUHAN, yang telah rela menolong engkau pada hari ini dengan seorang penebus. Termasyhurlah kiranya nama anak itu di Israel.'),(7204,7,4,15,'Dan dialah yang akan menyegarkan jiwamu dan memelihara engkau pada waktu rambutmu telah putih; sebab menantumu yang mengasihi engkau telah melahirkannya, perempuan yang lebih berharga bagimu dari tujuh anak laki-laki.\"'),(7205,7,4,16,'Dan Naomi mengambil anak itu serta meletakkannya pada pangkuannya dan dialah yang mengasuhnya.'),(7206,7,4,17,'Dan tetangga-tetangga perempuan memberi nama kepada anak itu, katanya: \"Pada Naomi telah lahir seorang anak laki-laki\"; lalu mereka menyebutkan namanya Obed. Dialah ayah Isai, ayah Daud.'),(7207,7,4,18,'Inilah keturunan Peres: Peres memperanakkan Hezron,'),(7208,7,4,19,'Hezron memperanakkan Ram, Ram memperanakkan Aminadab,'),(7209,7,4,20,'Aminadab memperanakkan Nahason, Nahason memperanakkan Salmon,'),(7210,7,4,21,'Salmon memperanakkan Boas, Boas memperanakkan Obed,'),(7211,7,4,22,'Obed memperanakkan Isai dan Isai memperanakkan Daud.'),(7212,8,1,1,'Ada seorang laki-laki dari Ramataim-Zofim, dari pegunungan Efraim, namanya Elkana bin Yeroham bin Elihu bin Tohu bin Zuf, seorang Efraim.'),(7213,9,1,2,'Orang ini mempunyai dua isteri: yang seorang bernama Hana dan yang lain bernama Penina; Penina mempunyai anak, tetapi Hana tidak.'),(7214,9,1,3,'Orang itu dari tahun ke tahun pergi meninggalkan kotanya untuk sujud menyembah dan mempersembahkan korban kepada TUHAN semesta alam di Silo. Di sana yang menjadi imam TUHAN ialah kedua anak Eli, Hofni dan Pinehas.'),(7215,9,1,4,'Pada hari Elkana mempersembahkan korban, diberikannyalah kepada Penina, isterinya, dan kepada semua anaknya yang laki-laki dan perempuan masing-masing sebagian.'),(7216,9,1,5,'Meskipun ia mengasihi Hana, ia memberikan kepada Hana hanya satu bagian, sebab TUHAN telah menutup kandungannya.'),(7217,9,1,6,'Tetapi madunya selalu menyakiti hatinya supaya ia gusar, karena TUHAN telah menutup kandungannya.'),(7218,9,1,7,'Demikianlah terjadi dari tahun ke tahun; setiap kali Hana pergi ke rumah TUHAN, Penina menyakiti hati Hana, sehingga ia menangis dan tidak mau makan.'),(7219,9,1,8,'Lalu Elkana, suaminya, berkata kepadanya: \"Hana, mengapa engkau menangis dan mengapa engkau tidak mau makan? Mengapa hatimu sedih? Bukankah aku lebih berharga bagimu dari pada sepuluh anak laki-laki?\"'),(7220,9,1,9,'Pada suatu kali, setelah mereka habis makan dan minum di Silo, berdirilah Hana, sedang imam Eli duduk di kursi dekat tiang pintu bait suci TUHAN,'),(7221,9,1,10,'dan dengan hati pedih ia berdoa kepada TUHAN sambil menangis tersedu-sedu.'),(7222,9,1,11,'Kemudian bernazarlah ia, katanya: \"TUHAN semesta alam, jika sungguh-sungguh Engkau memperhatikan sengsara hamba-Mu ini dan mengingat kepadaku dan tidak melupakan hamba-Mu ini, tetapi memberikan kepada hamba-Mu ini seorang anak laki-laki, maka aku akan memberikan dia kepada TUHAN untuk seumur hidupnya dan pisau cukur tidak akan menyentuh kepalanya.\"'),(7223,9,1,12,'Ketika perempuan itu terus-menerus berdoa di hadapan TUHAN, maka Eli mengamat-amati mulut perempuan itu;'),(7224,9,1,13,'dan karena Hana berkata-kata dalam hatinya dan hanya bibirnya saja bergerak-gerak, tetapi suaranya tidak kedengaran, maka Eli menyangka perempuan itu mabuk.'),(7225,9,1,14,'Lalu kata Eli kepadanya: \"Berapa lama lagi engkau berlaku sebagai orang mabuk? Lepaskanlah dirimu dari pada mabukmu.\"'),(7226,9,1,15,'Tetapi Hana menjawab: \"Bukan, tuanku, aku seorang perempuan yang sangat bersusah hati; anggur ataupun minuman yang memabukkan tidak kuminum, melainkan aku mencurahkan isi hatiku di hadapan TUHAN.'),(7227,9,1,16,'Janganlah anggap hambamu ini seorang perempuan dursila; sebab karena besarnya cemas dan sakit hati aku berbicara demikian lama.\"'),(7228,9,1,17,'Jawab Eli: \"Pergilah dengan selamat, dan Allah Israel akan memberikan kepadamu apa yang engkau minta dari pada-Nya.\"'),(7229,9,1,18,'Sesudah itu berkatalah perempuan itu: \"Biarlah hambamu ini mendapat belas kasihan dari padamu.\" Lalu keluarlah perempuan itu, ia mau makan dan mukanya tidak muram lagi.'),(7230,9,1,19,'Keesokan harinya bangunlah mereka itu pagi-pagi, lalu sujud menyembah di hadapan TUHAN; kemudian pulanglah mereka ke rumahnya di Rama. Ketika Elkana bersetubuh dengan Hana, isterinya, TUHAN ingat kepadanya.'),(7231,9,1,20,'Maka setahun kemudian mengandunglah Hana dan melahirkan seorang anak laki-laki. Ia menamai anak itu Samuel, sebab katanya: \"Aku telah memintanya dari pada TUHAN.\"'),(7232,9,1,21,'Elkana, laki-laki itu, pergi dengan seisi rumahnya mempersembahkan korban sembelihan tahunan dan korban nazarnya kepada TUHAN.'),(7233,9,1,22,'Tetapi Hana tidak ikut pergi, sebab katanya kepada suaminya: \"Nanti apabila anak itu cerai susu, aku akan mengantarkan dia, maka ia akan menghadap ke hadirat TUHAN dan tinggal di sana seumur hidupnya.\"'),(7234,9,1,23,'Kemudian Elkana, suaminya itu, berkata kepadanya: \"Perbuatlah apa yang kaupandang baik; tinggallah sampai engkau menyapih dia; hanya, TUHAN kiranya menepati janji-Nya.\" Jadi tinggallah perempuan itu dan menyusui anaknya sampai disapihnya.'),(7235,9,1,24,'Setelah perempuan itu menyapih anaknya, dibawanyalah dia, dengan seekor lembu jantan yang berumur tiga tahun, satu efa tepung dan sebuyung anggur, lalu diantarkannya ke dalam rumah TUHAN di Silo. Waktu itu masih kecil betul kanak-kanak itu.'),(7236,9,1,25,'Setelah mereka menyembelih lembu, mereka mengantarkan kanak-kanak itu kepada Eli;'),(7237,9,1,26,'lalu kata perempuan itu: \"Mohon bicara tuanku, demi tuanku hidup, akulah perempuan yang dahulu berdiri di sini dekat tuanku untuk berdoa kepada TUHAN.'),(7238,9,1,27,'Untuk mendapat anak inilah aku berdoa, dan TUHAN telah memberikan kepadaku, apa yang kuminta dari pada-Nya.'),(7239,9,1,28,'Maka akupun menyerahkannya kepada TUHAN; seumur hidup terserahlah ia kiranya kepada TUHAN.\" Lalu sujudlah mereka di sana menyembah kepada TUHAN.'),(7240,9,2,1,'Lalu berdoalah Hana, katanya: \"Hatiku bersukaria karena TUHAN, tanduk kekuatanku ditinggikan oleh TUHAN; mulutku mencemoohkan musuhku, sebab aku bersukacita karena pertolongan-Mu.'),(7241,9,2,2,'Tidak ada yang kudus seperti TUHAN, sebab tidak ada yang lain kecuali Engkau dan tidak ada gunung batu seperti Allah kita.'),(7242,9,2,3,'Janganlah kamu selalu berkata sombong, janganlah caci maki keluar dari mulutmu. Karena TUHAN itu Allah yang mahatahu, dan oleh Dia perbuatan-perbuatan diuji.'),(7243,9,2,4,'Busur pada pahlawan telah patah, tetapi orang-orang yang terhuyung-huyung, pinggangnya berikatkan kekuatan.'),(7244,9,2,5,'Siapa yang kenyang dahulu, sekarang menyewakan dirinya karena makanan, tetapi orang yang lapar dahulu, sekarang boleh beristirahat. Bahkan orang yang mandul melahirkan tujuh anak, tetapi orang yang banyak anaknya, menjadi layu.'),(7245,9,2,6,'TUHAN mematikan dan menghidupkan, Ia menurunkan ke dalam dunia orang mati dan mengangkat dari sana.'),(7246,9,2,7,'TUHAN membuat miskin dan membuat kaya; Ia merendahkan, dan meninggikan juga.'),(7247,9,2,8,'Ia menegakkan orang yang hina dari dalam debu, dan mengangkat orang yang miskin dari lumpur, untuk mendudukkan dia bersama-sama dengan para bangsawan, dan membuat dia memiliki kursi kehormatan. Sebab TUHAN mempunyai alas bumi; dan di atasnya Ia menaruh daratan.'),(7248,9,2,9,'Langkah kaki orang-orang yang dikasihi-Nya dilindungi-Nya, tetapi orang-orang fasik akan mati binasa dalam kegelapan, sebab bukan oleh karena kekuatannya sendiri seseorang berkuasa.'),(7249,9,2,10,'Orang yang berbantah dengan TUHAN akan dihancurkan; atas mereka Ia mengguntur di langit. TUHAN mengadili bumi sampai ke ujung-ujungnya; Ia memberi kekuatan kepada raja yang diangkat-Nya dan meninggikan tanduk kekuatan orang yang diurapi-Nya.\"'),(7250,9,2,11,'Lalu pulanglah Elkana ke Rama tetapi anak itu menjadi pelayan TUHAN di bawah pengawasan imam Eli.'),(7251,9,2,12,'Adapun anak-anak lelaki Eli adalah orang-orang dursila; mereka tidak mengindahkan TUHAN,'),(7252,9,2,13,'ataupun batas hak para imam terhadap bangsa itu. Setiap kali seseorang mempersembahkan korban sembelihan, sementara daging itu dimasak, datanglah bujang imam membawa garpu bergigi tiga di tangannya'),(7253,9,2,14,'dan dicucukkannya ke dalam bejana atau ke dalam kuali atau ke dalam belanga atau ke dalam periuk. Segala yang ditarik dengan garpu itu ke atas, diambil imam itu untuk dirinya sendiri. Demikianlah mereka memperlakukan semua orang Israel yang datang ke sana, ke Silo.'),(7254,9,2,15,'Bahkan sebelum lemaknya dibakar, bujang imam itu datang, lalu berkata kepada orang yang mempersembahkan korban itu: \"Berikanlah daging kepada imam untuk dipanggang, sebab ia tidak mau menerima dari padamu daging yang dimasak, hanya yang mentah saja.\"'),(7255,9,2,16,'Apabila orang itu menjawabnya: \"Bukankah lemak itu harus dibakar dahulu, kemudian barulah ambil bagimu sesuka hatimu,\" maka berkatalah ia kepada orang itu: \"Sekarang juga harus kauberikan, kalau tidak, aku akan mengambilnya dengan kekerasan.\"'),(7256,9,2,17,'Dengan demikian sangat besarlah dosa kedua orang muda itu di hadapan TUHAN, sebab mereka memandang rendah korban untuk TUHAN.'),(7257,9,2,18,'Adapun Samuel menjadi pelayan di hadapan TUHAN; ia masih anak-anak, yang tubuhnya berlilitkan baju efod dari kain lenan.'),(7258,9,2,19,'Setiap tahun ibunya membuatkan dia jubah kecil dan membawa jubah itu kepadanya, apabila ia bersama-sama suaminya pergi mempersembahkan korban sembelihan tahunan.'),(7259,9,2,20,'Lalu Eli memberkati Elkana dan isterinya, katanya: \"TUHAN kiranya memberikan keturunan kepadamu dari perempuan ini pengganti yang telah diserahkannya kepada TUHAN.\" Sesudah itu pulanglah mereka ke tempat kediamannya.'),(7260,9,2,21,'Dan TUHAN mengindahkan Hana, sehingga dia mengandung dan melahirkan tiga anak laki-laki dan dua anak perempuan lagi. Sementara itu makin besarlah Samuel yang muda itu di hadapan TUHAN.'),(7261,9,2,22,'Eli telah sangat tua. Apabila didengarnya segala sesuatu yang dilakukan anak-anaknya terhadap semua orang Israel dan bahwa mereka itu tidur dengan perempuan-perempuan yang melayani di depan pintu Kemah Pertemuan,'),(7262,9,2,23,'berkatalah ia kepada mereka: \"Mengapa kamu melakukan hal-hal yang begitu, sehingga kudengar dari segenap bangsa ini tentang perbuatan-perbuatanmu yang jahat itu?'),(7263,9,2,24,'Janganlah begitu, anak-anakku. Bukan kabar baik yang kudengar itu bahwa kamu menyebabkan umat TUHAN melakukan pelanggaran.'),(7264,9,2,25,'Jika seseorang berdosa terhadap seorang yang lain, maka Allah yang akan mengadili; tetapi jika seseorang berdosa terhadap TUHAN, siapakah yang menjadi perantara baginya?\" Tetapi tidaklah didengarkan mereka perkataan ayahnya itu, sebab TUHAN hendak mematikan mereka.'),(7265,9,2,26,'Tetapi Samuel yang muda itu, semakin besar dan semakin disukai, baik di hadapan TUHAN maupun di hadapan manusia.'),(7266,9,2,27,'Seorang abdi Allah datang kepada Eli dan berkata kepadanya: \"Beginilah firman TUHAN: Bukankah dengan nyata Aku menyatakan diri-Ku kepada nenek moyangmu, ketika mereka masih di Mesir dan takluk kepada keturunan Firaun?'),(7267,9,2,28,'Dan Aku telah memilihnya dari segala suku Israel menjadi imam bagi-Ku, supaya ia mempersembahkan korban di atas mezbah-Ku, membakar ukupan dan memakai baju efod di hadapan-Ku; kepada kaummu telah Kuserahkan segala korban api-apian orang Israel.'),(7268,9,2,29,'Mengapa engkau memandang dengan loba kepada korban sembelihan-Ku dan korban sajian-Ku, yang telah Kuperintahkan, dan mengapa engkau menghormati anak-anakmu lebih dari pada-Ku, sambil kamu menggemukkan dirimu dengan bagian yang terbaik dari setiap korban sajian umat-Ku Israel?'),(7269,9,2,30,'Sebab itu--demikianlah firman TUHAN, Allah Israel--sesungguhnya Aku telah berjanji: Keluargamu dan kaummu akan hidup di hadapan-Ku selamanya, tetapi sekarang--demikianlah firman TUHAN--:Jauhlah hal itu dari pada-Ku! Sebab siapa yang menghormati Aku, akan Kuhormati, tetapi siapa yang menghina Aku, akan dipandang rendah.'),(7270,9,2,31,'Sesungguhnya akan datang waktunya, bahwa Aku akan mematahkan tangan kekuatanmu dan tangan kekuatan kaummu, sehingga tidak ada seorang kakek dalam keluargamu.'),(7271,9,2,32,'Maka engkau akan memandang dengan mata bermusuhan kepada segala kebaikan yang akan Kulakukan kepada Israel dan dalam keluargamu takkan ada seorang kakek untuk selamanya.'),(7272,9,2,33,'Tetapi seorang dari padamu yang tidak Kulenyapkan dari lingkungan mezbah-Ku akan membuat matamu rusak dan jiwamu merana; segala tambahan keluargamu akan mati oleh pedang lawan.'),(7273,9,2,34,'Inilah yang akan menjadi tanda bagimu, yakni apa yang akan terjadi kepada kedua anakmu itu, Hofni dan Pinehas: pada hari yang sama keduanya akan mati.'),(7274,9,2,35,'Dan Aku akan mengangkat bagi-Ku seorang imam kepercayaan, yang berlaku sesuai dengan hati-Ku dan jiwa-Ku, dan Aku akan membangunkan baginya keturunan yang teguh setia, sehingga ia selalu hidup di hadapan orang yang Kuurapi.'),(7275,9,2,36,'Kemudian siapa yang masih tinggal hidup dari keturunanmu akan datang sujud menyembah kepadanya meminta sekeping uang perak atau sepotong roti, dan akan berkata: Tempatkanlah kiranya aku dalam salah satu golongan imam itu, supaya aku dapat makan sekerat roti.\"'),(7276,9,3,1,'Samuel yang muda itu menjadi pelayan TUHAN di bawah pengawasan Eli. Pada masa itu firman TUHAN jarang; penglihatan-penglihatanpun tidak sering.'),(7277,9,3,2,'Pada suatu hari Eli, yang matanya mulai kabur dan tidak dapat melihat dengan baik, sedang berbaring di tempat tidurnya.'),(7278,9,3,3,'Lampu rumah Allah belum lagi padam. Samuel telah tidur di dalam bait suci TUHAN, tempat tabut Allah.'),(7279,9,3,4,'Lalu TUHAN memanggil: \"Samuel! Samuel!\", dan ia menjawab: \"Ya, bapa.\"'),(7280,9,3,5,'Lalu berlarilah ia kepada Eli, serta katanya: \"Ya, bapa, bukankah bapa memanggil aku?\" Tetapi Eli berkata: \"Aku tidak memanggil; tidurlah kembali.\" Lalu pergilah ia tidur.'),(7281,9,3,6,'Dan TUHAN memanggil Samuel sekali lagi. Samuelpun bangunlah, lalu pergi mendapatkan Eli serta berkata: \"Ya, bapa, bukankah bapa memanggil aku?\" Tetapi Eli berkata: \"Aku tidak memanggil, anakku; tidurlah kembali.\"'),(7282,9,3,7,'Samuel belum mengenal TUHAN; firman TUHAN belum pernah dinyatakan kepadanya.'),(7283,9,3,8,'Dan TUHAN memanggil Samuel sekali lagi, untuk ketiga kalinya. Iapun bangunlah, lalu pergi mendapatkan Eli serta katanya: \"Ya, bapa, bukankah bapa memanggil aku?\" Lalu mengertilah Eli, bahwa Tuhanlah yang memanggil anak itu.'),(7284,9,3,9,'Sebab itu berkatalah Eli kepada Samuel: \"Pergilah tidur dan apabila Ia memanggil engkau, katakanlah: Berbicaralah, TUHAN, sebab hamba-Mu ini mendengar.\" Maka pergilah Samuel dan tidurlah ia di tempat tidurnya.'),(7285,9,3,10,'Lalu datanglah TUHAN, berdiri di sana dan memanggil seperti yang sudah-sudah: \"Samuel! Samuel!\" Dan Samuel menjawab: \"Berbicaralah, sebab hamba-Mu ini mendengar.\"'),(7286,9,3,11,'Lalu berfirmanlah TUHAN kepada Samuel: \"Ketahuilah, Aku akan melakukan sesuatu di Israel, sehingga setiap orang yang mendengarnya, akan bising kedua telinganya.'),(7287,9,3,12,'Pada waktu itu Aku akan menepati kepada Eli segala yang telah Kufirmankan tentang keluarganya, dari mula sampai akhir.'),(7288,9,3,13,'Sebab telah Kuberitahukan kepadanya, bahwa Aku akan menghukum keluarganya untuk selamanya karena dosa yang telah diketahuinya, yakni bahwa anak-anaknya telah menghujat Allah, tetapi ia tidak memarahi mereka!'),(7289,9,3,14,'Sebab itu Aku telah bersumpah kepada keluarga Eli, bahwa dosa keluarga Eli takkan dihapuskan dengan korban sembelihan atau dengan korban sajian untuk selamanya.\"'),(7290,9,3,15,'Samuel tidur sampai pagi; kemudian dibukanya pintu rumah TUHAN. Samuel segan memberitahukan penglihatan itu kepada Eli.'),(7291,9,3,16,'Tetapi Eli memanggil Samuel, katanya: \"Samuel, anakku.\" Jawab Samuel: \"Ya, bapa.\"'),(7292,9,3,17,'Kata Eli: \"Apakah yang disampaikan-Nya kepadamu? Janganlah kausembunyikan kepadaku. Kiranya beginilah Allah menghukum engkau, bahkan lebih lagi dari pada itu, jika engkau menyembunyikan sepatah katapun kepadaku dari apa yang disampaikan-Nya kepadamu itu.\"'),(7293,9,3,18,'Lalu Samuel memberitahukan semuanya itu kepadanya dengan tidak menyembunyikan sesuatupun. Kemudian Eli berkata: \"Dia TUHAN, biarlah diperbuat-Nya apa yang dipandang-Nya baik.\"'),(7294,9,3,19,'Dan Samuel makin besar dan TUHAN menyertai dia dan tidak ada satupun dari firman-Nya itu yang dibiarkan-Nya gugur.'),(7295,9,3,20,'Maka tahulah seluruh Israel dari Dan sampai Bersyeba, bahwa kepada Samuel telah dipercayakan jabatan nabi TUHAN.'),(7296,9,3,21,'Dan TUHAN selanjutnya menampakkan diri di Silo, sebab Ia menyatakan diri di Silo kepada Samuel dengan perantaraan firman-Nya.'),(7297,9,4,1,'Dan perkataan Samuel sampai ke seluruh Israel. (4-1b) Orang Israel maju berperang melawan orang Filistin dan berkemah dekat Eben-Haezer, sedang orang Filistin berkemah di Afek.'),(7298,9,4,2,'Orang Filistin mengatur barisannya berhadapan dengan orang Israel. Ketika pertempuran menghebat, terpukullah kalah orang Israel oleh orang Filistin, yang menewaskan kira-kira empat ribu orang di medan pertempuran itu.'),(7299,9,4,3,'Ketika tentara itu kembali ke perkemahan, berkatalah para tua-tua Israel: \"Mengapa TUHAN membuat kita terpukul kalah oleh orang Filistin pada hari ini? Marilah kita mengambil dari Silo tabut perjanjian TUHAN, supaya Ia datang ke tengah-tengah kita dan melepaskan kita dari tangan musuh kita.\"'),(7300,9,4,4,'Kemudian bangsa itu menyuruh orang ke Silo, lalu mereka mengangkat dari sana tabut perjanjian TUHAN semesta alam, yang bersemayam di atas para kerub; kedua anak Eli, Hofni dan Pinehas, ada di sana dekat tabut perjanjian Allah itu.'),(7301,9,4,5,'Segera sesudah tabut perjanjian TUHAN sampai ke perkemahan, bersoraklah seluruh orang Israel dengan nyaring, sehingga bumi bergetar.'),(7302,9,4,6,'Dan orang Filistin yang mendengar bunyi sorak itu berkata: \"Apakah bunyi sorak yang nyaring di perkemahan orang Ibrani itu?\" Ketika diketahui mereka, bahwa tabut TUHAN telah sampai ke perkemahan itu,'),(7303,9,4,7,'ketakutanlah orang Filistin, sebab kata mereka: \"Allah mereka telah datang ke perkemahan itu,\" dan mereka berkata: \"Celakalah kita, sebab seperti itu belum pernah terjadi dahulu.'),(7304,9,4,8,'Celakalah kita! Siapakah yang menolong kita dari tangan Allah yang maha dahsyat ini? Inilah juga Allah, yang telah menghajar orang Mesir dengan berbagai-bagai tulah di padang gurun.'),(7305,9,4,9,'Kuatkanlah hatimu dan berlakulah seperti laki-laki, hai orang Filistin, supaya kamu jangan menjadi budak orang Ibrani itu, seperti mereka dahulu menjadi budakmu. Berlakulah seperti laki-laki dan berperanglah!\"'),(7306,9,4,10,'Lalu berperanglah orang Filistin, sehingga orang Israel terpukul kalah. Mereka melarikan diri masing-masing ke kemahnya. Amatlah besar kekalahan itu: dari pihak Israel gugur tiga puluh ribu orang pasukan berjalan kaki.'),(7307,9,4,11,'Lagipula tabut Allah dirampas dan kedua anak Eli, Hofni dan Pinehas, tewas.'),(7308,9,4,12,'Seorang dari suku Benyamin lari dari barisan pertempuran dan pada hari itu juga ia sampai ke Silo dengan pakaian terkoyak-koyak dan dengan tanah di kepalanya.'),(7309,9,4,13,'Ketika ia sampai, Eli sedang duduk di kursi di tepi jalan menunggu-nunggu, sebab hatinya berdebar-debar karena tabut Allah itu. Ketika orang itu masuk ke kota dan menceritakan kabar itu, berteriaklah seluruh kota itu.'),(7310,9,4,14,'Ketika Eli mendengar bunyi teriakan itu, bertanyalah ia: \"Keributan apakah itu?\" Lalu bersegeralah orang itu mendapatkan Eli dan memberitahukannya kepadanya.'),(7311,9,4,15,'Eli sudah sembilan puluh delapan tahun umurnya dan matanya sudah bular, sehingga ia tidak dapat melihat lagi.'),(7312,9,4,16,'Kata orang itu kepada Eli: \"Aku datang dari medan pertempuran; baru hari ini aku melarikan diri dari medan pertempuran.\" Kata Eli: \"Bagaimana keadaannya, anakku?\"'),(7313,9,4,17,'Jawab pembawa kabar itu: \"Orang Israel melarikan diri dari hadapan orang Filistin; kekalahan yang besar telah diderita oleh rakyat; lagipula kedua anakmu, Hofni dan Pinehas, telah tewas, dan tabut Allah sudah dirampas.\"'),(7314,9,4,18,'Ketika disebutnya tabut Allah itu, jatuhlah Eli telentang dari kursi di sebelah pintu gerbang, batang lehernya patah dan ia mati. Sebab telah tua dan gemuk orangnya. Empat puluh tahun lamanya ia memerintah sebagai hakim atas orang Israel.'),(7315,9,4,19,'Adapun menantunya perempuan, isteri Pinehas, sudah hamil tua. Ketika didengarnya kabar itu, bahwa tabut Allah telah dirampas dan mertuanya laki-laki serta suaminya telah mati, duduklah ia berlutut, lalu bersalin, sebab ia kedatangan sakit beranak.'),(7316,9,4,20,'Ketika ia hampir mati, berkatalah perempuan-perempuan yang berdiri di dekatnya: \"Janganlah takut, sebab engkau telah melahirkan seorang anak laki-laki.\" Tetapi ia tidak menjawab dan tidak memperhatikannya.'),(7317,9,4,21,'Ia menamai anak itu Ikabod, katanya: \"Telah lenyap kemuliaan dari Israel\" --karena tabut Allah sudah dirampas dan karena mertuanya dan suaminya.'),(7318,9,4,22,'Katanya: \"Telah lenyap kemuliaan dari Israel, sebab tabut Allah telah dirampas.\"'),(7319,9,5,1,'Sesudah orang Filistin merampas tabut Allah, maka mereka membawanya dari Eben-Haezer ke Asdod.'),(7320,9,5,2,'Orang Filistin mengambil tabut Allah itu, dibawanya masuk ke kuil Dagon dan diletakkannya di sisi Dagon.'),(7321,9,5,3,'Ketika orang-orang Asdod bangun pagi-pagi pada keesokan harinya, tampaklah Dagon terjatuh dengan mukanya ke tanah di hadapan tabut TUHAN; lalu mereka mengambil Dagon dan mengembalikannya ke tempatnya.'),(7322,9,5,4,'Tetapi ketika keesokan harinya mereka bangun pagi-pagi, tampaklah Dagon terjatuh dengan mukanya ke tanah di hadapan tabut TUHAN, tetapi kepala Dagon dan kedua belah tangannya terpenggal dan terpelanting ke ambang pintu, hanya badan Dagon itu yang masih tinggal.'),(7323,9,5,5,'Itulah sebabnya para imam Dagon dan semua orang yang masuk ke dalam kuil Dagon tidak menginjak ambang pintu rumah Dagon yang di Asdod, sampai hari ini.'),(7324,9,5,6,'Tangan TUHAN menekan orang-orang Asdod itu dengan berat dan Ia membingungkan mereka: Ia menghajar mereka dengan borok-borok, baik Asdod maupun daerahnya.'),(7325,9,5,7,'Ketika dilihat orang-orang Asdod, bahwa demikian halnya, berkatalah mereka: \"Tabut Allah Israel tidak boleh tinggal pada kita, sebab tangan-Nya keras melawan kita dan melawan Dagon, allah kita.\"'),(7326,9,5,8,'Sebab itu mereka memanggil berkumpul kepadanya semua raja kota orang Filistin dan berkata: \"Apakah yang akan kita lakukan dengan tabut Allah Israel itu?\" Lalu kata mereka: \"Tabut Allah Israel harus dipindahkan ke Gat.\" Jadi mereka memindahkan tabut Allah Israel itu ke sana.'),(7327,9,5,9,'Tetapi setelah mereka memindahkannya, maka tangan TUHAN mendatangkan kegemparan yang sangat besar atas kota itu; Ia menghajar orang-orang kota itu, anak-anak dan orang dewasa, sehingga timbul borok-borok pada mereka.'),(7328,9,5,10,'Lalu mereka mengantarkan tabut Allah itu ke Ekron. Tetapi sesampai tabut Allah itu di Ekron, berteriaklah orang Ekron itu, demikian: \"Mereka memindahkan tabut Allah Israel itu kepada kita untuk mematikan kita dan bangsa kita.\"'),(7329,9,5,11,'Sebab itu mereka memanggil berkumpul semua raja kota orang Filistin itu dan berkata: \"Antarkanlah tabut Allah Israel itu; biarlah itu kembali ke tempatnya, supaya jangan dimatikannya kita dan bangsa kita.\" Sebab di seluruh kota itu ada kegemparan maut; tangan Allah menekan orang-orang di sana dengan sangat berat:'),(7330,9,5,12,'orang-orang yang tidak mati, dihajar dengan borok-borok, sehingga teriakan kota itu naik ke langit.'),(7331,9,6,1,'Setelah tujuh bulan lamanya tabut TUHAN itu ada di daerah orang Filistin,'),(7332,9,6,2,'maka orang Filistin itu memanggil para imam dan para petenung, lalu berkata kepada mereka: \"Apakah yang harus kami lakukan dengan tabut TUHAN itu? Beritahukanlah kepada kami, bagaimana kami harus mengantarkannya kembali ke tempatnya.\"'),(7333,9,6,3,'Lalu kata mereka: \"Apabila kamu mengantarkan tabut Allah Israel itu, maka janganlah kamu mengantarkannya dengan tangan hampa, melainkan haruslah kamu membayar tebusan salah kepada-Nya; maka kamu akan menjadi sembuh dan kamu akan mengetahui, mengapa tangan-Nya tidak undur dari padamu.\"'),(7334,9,6,4,'Sesudah itu bertanyalah mereka: \"Apakah tebusan salah yang harus kami bayar kepada-Nya?\" Jawab mereka: \"Menurut jumlah raja-raja kota orang Filistin, lima borok emas dan lima tikus emas, sebab tulah yang sama menimpa kamu sekalian dan raja-raja kotamu.'),(7335,9,6,5,'Jadi buatlah gambar borok-borokmu dan gambar tikus yang merusak tanahmu, dan sampaikanlah hormatmu kepada Allah Israel. Mungkin Ia akan mengangkat dari padamu, dari pada allahmu dan dari pada tanahmu tangan-Nya yang menekan dengan berat.'),(7336,9,6,6,'Mengapa kamu berkeras hati, sama seperti orang Mesir dan Firaun berkeras hati? Bukankah mereka membiarkan bangsa itu pergi, ketika Ia mempermain-mainkan mereka?'),(7337,9,6,7,'Oleh sebab itu ambillah dan siapkanlah sebuah kereta baru dengan dua ekor lembu yang menyusui, yang belum pernah kena kuk, pasanglah kedua lembu itu pada kereta, tetapi bawalah anak-anaknya kembali ke rumah, supaya jangan mengikutinya lagi.'),(7338,9,6,8,'Kemudian ambillah tabut TUHAN, muatkanlah itu ke atas kereta dan letakkanlah benda-benda emas, yang harus kamu bayar kepada-Nya sebagai tebusan salah, ke dalam suatu peti di sisinya. Dan biarkanlah tabut itu pergi.'),(7339,9,6,9,'Perhatikanlah: apabila tabut itu mengambil jalan ke daerahnya, ke Bet-Semes, maka Dialah itu yang telah mendatangkan malapetaka yang hebat ini kepada kita. Dan jika tidak, maka kita mengetahui, bahwa bukanlah tangan-Nya yang telah menimpa kita; kebetulan saja hal itu terjadi kepada kita.\"'),(7340,9,6,10,'Demikianlah diperbuat orang-orang itu. Mereka mengambil dua ekor lembu yang menyusui, dipasangnya pada kereta, tetapi anak-anaknya ditahan di rumah.'),(7341,9,6,11,'Mereka meletakkan tabut TUHAN ke atas kereta, juga peti berisi tikus-tikus emas dan gambar benjol-benjol mereka.'),(7342,9,6,12,'Lembu-lembu itu langsung mengikuti jalan yang ke Bet-Semes; melalui satu jalan raya, sambil menguak dengan tidak menyimpang ke kanan atau ke kiri, sedang raja-raja kota orang Filistin itu berjalan di belakangnya sampai ke daerah Bet-Semes.'),(7343,9,6,13,'Orang-orang Bet-Semes sedang menuai gandum di lembah. Ketika mereka mengangkat muka, maka tampaklah kepada mereka tabut itu, lalu bersukacitalah mereka melihatnya.'),(7344,9,6,14,'Kereta itu sampai ke ladang Yosua, orang Bet-Semes itu, dan berhenti di sana. Di sana ada batu besar. Mereka membelah kayu kereta itu dan mereka mempersembahkan lembu-lembu sebagai korban bakaran kepada TUHAN.'),(7345,9,6,15,'Orang-orang suku Lewi menurunkan tabut TUHAN dengan peti yang ada di sebelahnya, yang di dalamnya ada benda-benda emas itu, lalu menaruhnya di atas batu besar itu, dan pada hari itu orang-orang Bet-Semes mempersembahkan korban bakaran dan korban sembelihan kepada TUHAN.'),(7346,9,6,16,'Ketika kelima raja kota orang Filistin melihat hal itu, pulanglah mereka ke Ekron pada hari itu juga.'),(7347,9,6,17,'Inilah benjol-benjol emas yang dibayar orang Filistin kepada TUHAN sebagai tebusan salah: dari Asdod satu, dari Gaza satu, dari Askelon satu, dari Gat satu, dari Ekron satu;'),(7348,9,6,18,'lagi tikus-tikus emas menurut jumlah segala kota orang Filistin kepunyaan kelima raja kota itu, baik kota-kota yang berkubu maupun dusun-dusun sekitarnya; dan batu besar, yang di atasnya diletakkan mereka tabut TUHAN, di ladang Yosua, orang Bet-Semes itu, adalah saksi sampai hari ini.'),(7349,9,6,19,'Dan Ia membunuh beberapa orang Bet-Semes, karena mereka melihat ke dalam tabut TUHAN; Ia membunuh tujuh puluh orang dari rakyat itu. Rakyat itu berkabung, karena TUHAN telah menghajar mereka dengan dahsyatnya.'),(7350,9,6,20,'Dan orang-orang Bet-Semes berkata: \"Siapakah yang tahan berdiri di hadapan TUHAN, Allah yang kudus ini? Kepada siapakah Ia akan berangkat meninggalkan kita?\"'),(7351,9,6,21,'Lalu mereka mengirim utusan kepada penduduk Kiryat-Yearim dengan pesan: \"Orang Filistin telah mengembalikan tabut TUHAN; datanglah dan angkutlah itu kepadamu.\"'),(7352,9,7,1,'Lalu orang-orang Kiryat-Yearim datang, mereka mengangkut tabut TUHAN itu dan membawanya ke dalam rumah Abinadab yang di atas bukit. Dan Eleazar, anaknya, mereka kuduskan untuk menjaga tabut TUHAN itu.'),(7353,9,7,2,'Sejak saat tabut itu tinggal di Kiryat-Yearim berlalulah waktu yang cukup lama, yakni dua puluh tahun, dan seluruh kaum Israel mengeluh kepada TUHAN.'),(7354,9,7,3,'Lalu berkatalah Samuel kepada seluruh kaum Israel demikian: \"Jika kamu berbalik kepada TUHAN dengan segenap hati, maka jauhkanlah para allah asing dan para Asytoret dari tengah-tengahmu dan tujukan hatimu kepada TUHAN dan beribadahlah hanya kepada-Nya; maka Ia akan melepaskan kamu dari tangan orang Filistin.\"'),(7355,9,7,4,'Kemudian orang-orang Israel menjauhkan para Baal dan para Asytoret dan beribadah hanya kepada TUHAN.'),(7356,9,7,5,'Lalu berkatalah Samuel: \"Kumpulkanlah segenap orang Israel ke Mizpa; maka aku akan berdoa untuk kamu kepada TUHAN.\"'),(7357,9,7,6,'Setelah berkumpul di Mizpa, mereka menimba air dan mencurahkannya di hadapan TUHAN. Mereka juga berpuasa pada hari itu dan berkata di sana: \"Kami telah berdosa kepada TUHAN.\" Dan Samuel menghakimi orang Israel di Mizpa.'),(7358,9,7,7,'Ketika didengar orang Filistin, bahwa orang Israel telah berkumpul di Mizpa, majulah raja-raja kota orang Filistin mendatangi orang Israel. Serta didengar orang Israel demikian, maka ketakutanlah mereka terhadap orang Filistin.'),(7359,9,7,8,'Lalu kata orang Israel kepada Samuel: \"Janganlah berhenti berseru bagi kami kepada TUHAN, Allah kita, supaya Ia menyelamatkan kami dari tangan orang Filistin itu.\"'),(7360,9,7,9,'Sesudah itu Samuel mengambil seekor anak domba yang menyusu, lalu mempersembahkan seluruhnya kepada TUHAN sebagai korban bakaran. Dan ketika Samuel berseru kepada TUHAN bagi orang Israel, maka TUHAN menjawab dia.'),(7361,9,7,10,'Sedang Samuel mempersembahkan korban bakaran itu, majulah orang Filistin berperang melawan orang Israel. Tetapi pada hari itu TUHAN mengguntur dengan bunyi yang hebat ke atas orang Filistin dan mengacaukan mereka, sehingga mereka terpukul kalah oleh orang Israel.'),(7362,9,7,11,'Keluarlah orang-orang Israel dari Mizpa, mengejar orang Filistin itu dan memukul mereka kalah sampai hilir Bet-Kar.'),(7363,9,7,12,'Kemudian Samuel mengambil sebuah batu dan mendirikannya antara Mizpa dan Yesana; ia menamainya Eben-Haezer, katanya: \"Sampai di sini TUHAN menolong kita.\"'),(7364,9,7,13,'Demikianlah orang Filistin itu ditundukkan dan tidak lagi memasuki daerah Israel. Tangan TUHAN melawan orang Filistin seumur hidup Samuel,'),(7365,9,7,14,'dan kota-kota yang diambil orang Filistin dari pada Israel, kembali pula kepada Israel, mulai dari Ekron sampai Gat; dan orang Israel merebut daerah sekitarnya dari tangan orang Filistin. Antara orang Israel dan orang Amori ada damai.'),(7366,9,7,15,'Samuel memerintah sebagai hakim atas orang Israel seumur hidupnya.'),(7367,9,7,16,'Dari tahun ke tahun ia berkeliling ke Betel, Gilgal dan Mizpa, dan memerintah atas orang Israel di segala tempat itu,'),(7368,9,7,17,'lalu ia kembali ke Rama, sebab di sanalah rumahnya dan di sanalah ia memerintah atas orang Israel; dan di sana ia mendirikan mezbah bagi TUHAN.'),(7369,9,8,1,'Setelah Samuel menjadi tua, diangkatnyalah anak-anaknya laki-laki menjadi hakim atas orang Israel.'),(7370,9,8,2,'Nama anaknya yang sulung ialah Yoel, dan nama anaknya yang kedua ialah Abia; keduanya menjadi hakim di Bersyeba.'),(7371,9,8,3,'Tetapi anak-anaknya itu tidak hidup seperti ayahnya; mereka mengejar laba, menerima suap dan memutarbalikkan keadilan.'),(7372,9,8,4,'Sebab itu berkumpullah semua tua-tua Israel; mereka datang kepada Samuel di Rama'),(7373,9,8,5,'dan berkata kepadanya: \"Engkau sudah tua dan anak-anakmu tidak hidup seperti engkau; maka angkatlah sekarang seorang raja atas kami untuk memerintah kami, seperti pada segala bangsa-bangsa lain.\"'),(7374,9,8,6,'Waktu mereka berkata: \"Berikanlah kepada kami seorang raja untuk memerintah kami,\" perkataan itu mengesalkan Samuel, maka berdoalah Samuel kepada TUHAN.'),(7375,9,8,7,'TUHAN berfirman kepada Samuel: \"Dengarkanlah perkataan bangsa itu dalam segala hal yang dikatakan mereka kepadamu, sebab bukan engkau yang mereka tolak, tetapi Akulah yang mereka tolak, supaya jangan Aku menjadi raja atas mereka.'),(7376,9,8,8,'Tepat seperti yang dilakukan mereka kepada-Ku sejak hari Aku menuntun mereka keluar dari Mesir sampai hari ini, yakni meninggalkan Daku dan beribadah kepada allah lain, demikianlah juga dilakukan mereka kepadamu.'),(7377,9,8,9,'Oleh sebab itu dengarkanlah permintaan mereka, hanya peringatkanlah mereka dengan sungguh-sungguh dan beritahukanlah kepada mereka apa yang menjadi hak raja yang akan memerintah mereka.\"'),(7378,9,8,10,'Dan Samuel menyampaikan segala firman TUHAN kepada bangsa itu, yang meminta seorang raja kepadanya,'),(7379,9,8,11,'katanya: \"Inilah yang menjadi hak raja yang akan memerintah kamu itu: anak-anakmu laki-laki akan diambilnya dan dipekerjakannya pada keretanya dan pada kudanya, dan mereka akan berlari di depan keretanya;'),(7380,9,8,12,'ia akan menjadikan mereka kepala pasukan seribu dan kepala pasukan lima puluh; mereka akan membajak ladangnya dan mengerjakan penuaian baginya; senjata-senjatanya dan perkakas keretanya akan dibuat mereka.'),(7381,9,8,13,'Anak-anakmu perempuan akan diambilnya sebagai juru campur rempah-rempah, juru masak dan juru makanan.'),(7382,9,8,14,'Selanjutnya dari ladangmu, kebun anggurmu dan kebun zaitunmu akan diambilnya yang paling baik dan akan diberikannya kepada pegawai-pegawainya'),(7383,9,8,15,'dari gandummu dan hasil kebun anggurmu akan diambilnya sepersepuluh dan akan diberikannya kepada pegawai-pegawai istananya dan kepada pegawai-pegawainya yang lain.'),(7384,9,8,16,'Budak-budakmu laki-laki dan budak-budakmu perempuan, ternakmu yang terbaik dan keledai-keledaimu akan diambilnya dan dipakainya untuk pekerjaannya.'),(7385,9,8,17,'Dari kambing dombamu akan diambilnya sepersepuluh, dan kamu sendiri akan menjadi budaknya.'),(7386,9,8,18,'Pada waktu itu kamu akan berteriak karena rajamu yang kamu pilih itu, tetapi TUHAN tidak akan menjawab kamu pada waktu itu.\"'),(7387,9,8,19,'Tetapi bangsa itu menolak mendengarkan perkataan Samuel dan mereka berkata: \"Tidak, harus ada raja atas kami;'),(7388,9,8,20,'maka kamipun akan sama seperti segala bangsa-bangsa lain; raja kami akan menghakimi kami dan memimpin kami dalam perang.\"'),(7389,9,8,21,'Samuel mendengar segala perkataan bangsa itu, dan menyampaikannya kepada TUHAN.'),(7390,9,8,22,'TUHAN berfirman kepada Samuel: \"Dengarkanlah permintaan mereka dan angkatlah seorang raja bagi mereka.\" Kemudian berkatalah Samuel kepada orang-orang Israel itu: \"Pergilah, masing-masing ke kotanya.\"'),(7391,9,9,1,'Ada seorang dari daerah Benyamin, namanya Kish bin Abiel, bin Zeror, bin Bekhorat, bin Afiah, seorang suku Benyamin, seorang yang berada.'),(7392,9,9,2,'Orang ini ada anaknya laki-laki, namanya Saul, seorang muda yang elok rupanya; tidak ada seorangpun dari antara orang Israel yang lebih elok dari padanya: dari bahu ke atas ia lebih tinggi dari pada setiap orang sebangsanya.'),(7393,9,9,3,'Kish, ayah Saul itu, kehilangan keledai-keledai betinanya. Sebab itu berkatalah Kish kepada Saul, anaknya: \"Ambillah salah seorang bujang, bersiaplah dan pergilah mencari keledai-keledai itu.\"'),(7394,9,9,4,'Lalu mereka berjalan melalui pegunungan Efraim; juga mereka berjalan melalui tanah Salisa, tetapi tidak menemuinya. Kemudian mereka berjalan melalui tanah Sahalim, tetapi keledai-keledai itu tidak ada; kemudian mereka berjalan melalui tanah Benyamin, tetapi tidak menemuinya.'),(7395,9,9,5,'Ketika mereka sampai ke tanah Zuf, berkatalah Saul kepada bujangnya yang bersama-sama dengan dia: \"Mari, kita pulang. Nanti ayahku tidak lagi memikirkan keledai-keledai itu, tetapi kuatir mengenai kita.\"'),(7396,9,9,6,'Tetapi orang ini berkata kepadanya: \"Tunggu, di kota ini ada seorang abdi Allah, seorang yang terhormat; segala yang dikatakannya pasti terjadi. Marilah kita pergi ke sana sekarang juga, mungkin ia dapat memberitahukan kepada kita tentang perjalanan yang kita tempuh ini.\"'),(7397,9,9,7,'Jawab Saul kepada bujangnya itu: \"Tetapi kalau kita pergi, apakah yang kita bawa kepada orang itu? Sebab roti di kantong kita telah habis, dan tidak ada pemberian untuk dibawa kepada abdi Allah itu. Apakah yang ada pada kita?\"'),(7398,9,9,8,'Jawab bujang itu pula kepada Saul: \"Masih ada padaku seperempat syikal perak; itu dapat aku berikan kepada abdi Allah itu, maka ia akan memberitahukan kepada kita tentang perjalanan kita.\"'),(7399,9,9,9,'--Dahulu di antara orang Israel, apabila seseorang pergi menanyakan petunjuk Allah, ia berkata begini: \"Mari kita pergi kepada pelihat,\" sebab nabi yang sekarang ini disebutkan dahulu pelihat. --'),(7400,9,9,10,'Kemudian berkatalah Saul kepada bujangnya itu: \"Pikiranmu itu baik. Mari kita pergi.\" Maka pergilah mereka ke kota, ke tempat abdi Allah itu.'),(7401,9,9,11,'Ketika mereka naik jalan pendakian ke kota itu, mereka bertemu dengan gadis-gadis yang keluar hendak menimba air. Mereka bertanya kepada gadis-gadis itu: \"Pelihat ada di sini?\"'),(7402,9,9,12,'Jawab gadis-gadis itu kepada mereka: \"Ya, ada, baru saja ia mendahului kamu, cepat-cepatlah sekarang. Ia datang ke kota hari ini, karena ada perjamuan korban untuk orang banyak di bukit pada hari ini.'),(7403,9,9,13,'Apabila kamu masuk ke kota, kamu akan segera menjumpainya, sebelum ia naik ke bukit untuk makan. Sebab orang banyak tidak akan makan, sebelum ia datang; karena dialah yang memberkati korban, kemudian barulah para undangan makan. Pergilah sekarang, sebab kamu akan menjumpainya dengan segera.\"'),(7404,9,9,14,'Maka naiklah mereka ke kota, dan ketika mereka masuk kota, Samuel yang berjalan keluar untuk naik ke bukit, berpapasan dengan mereka.'),(7405,9,9,15,'Tetapi TUHAN telah menyatakan kepada Samuel, sehari sebelum kedatangan Saul, demikian:'),(7406,9,9,16,'Besok kira-kira waktu ini Aku akan menyuruh kepadamu seorang laki-laki dari tanah Benyamin; engkau akan mengurapi dia menjadi raja atas umat-Ku Israel dan ia akan menyelamatkan umat-Ku dari tangan orang Filistin. Sebab Aku telah memperhatikan sengsara umat-Ku itu, karena teriakannya telah sampai kepada-Ku.'),(7407,9,9,17,'Ketika Samuel melihat Saul, maka berfirmanlah TUHAN kepadanya: \"Inilah orang yang Kusebutkan kepadamu itu; orang ini akan memegang tampuk pemerintahan atas umat-Ku.\"'),(7408,9,9,18,'Dalam pada itu Saul, datang mendekati Samuel di tengah pintu gerbang dan berkata: \"Maaf, di mana rumah pelihat itu?\"'),(7409,9,9,19,'Jawab Samuel kepada Saul, katanya: \"Akulah pelihat itu. Naiklah mendahului aku ke bukit. Hari ini kamu makan bersama-sama dengan daku; besok pagi aku membiarkan engkau pergi dan aku akan memberitahukan kepadamu segala sesuatu yang ada dalam hatimu.'),(7410,9,9,20,'Adapun keledai-keledaimu, yang telah hilang tiga hari lamanya sampai sekarang, janganlah engkau kuatir, sebab telah diketemukan. Tetapi siapakah yang memiliki segala yang diingini orang Israel? Bukankah itu ada padamu dan pada seluruh kaum keluargamu?\"'),(7411,9,9,21,'Tetapi jawab Saul: \"Bukankah aku seorang suku Benyamin, suku yang terkecil di Israel? Dan bukankah kaumku yang paling hina dari segala kaum suku Benyamin? Mengapa bapa berkata demikian kepadaku?\"'),(7412,9,9,22,'Sesudah itu Samuel mengajak Saul dan bujangnya, dibawanya ke pendopo dan diberikannya kepada mereka tempat utama di depan para undangan, yang banyaknya kira-kira tiga puluh orang.'),(7413,9,9,23,'Berkatalah Samuel kepada juru masak: \"Berikanlah sekarang bagian yang kuberikan kepadamu tadi, dengan pesan: Simpanlah ini dahulu.\"'),(7414,9,9,24,'Lalu juru masak itu menghidangkan paha dan apa yang termasuk ke situ dan meletakkannya ke depan Saul. Dan Samuel berkata: \"Lihat, yang tinggal ini diletakkan ke depanmu; makanlah, sebab telah disimpan bagimu untuk perayaan ini, ketika aku berkata: Aku telah mengundang orang banyak.\" Demikianlah pada hari itu Saul makan bersama-sama dengan Samuel.'),(7415,9,9,25,'Sesudah itu turunlah mereka dari bukit ke kota. Dan Samuel bercakap-cakap dengan Saul di atas sotoh.'),(7416,9,9,26,'Mereka bangun pagi-pagi, dan ketika fajar menyingsing, Samuel memanggil Saul yang ada di atas sotoh itu, katanya: \"Bangunlah, aku akan mengantarkan engkau.\" Lalu Saul bangun dan mereka keduanya pergi ke luar, yakni ia dan Samuel.'),(7417,9,9,27,'Ketika mereka turun sampai ke ujung kota, berkatalah Samuel kepada Saul: \"Katakanlah kepada bujang itu, supaya ia pergi mendahului kita, tetapi berhentilah engkau sebentar, maka aku akan memberitahukan kepadamu firman Allah.\"'),(7418,9,10,1,'Lalu Samuel mengambil buli-buli berisi minyak, dituangnyalah ke atas kepala Saul, diciumnyalah dia sambil berkata: \"Bukankah TUHAN telah mengurapi engkau menjadi raja atas umat-Nya Israel? Engkau akan memegang tampuk pemerintahan atas umat TUHAN, dan engkau akan menyelamatkannya dari tangan musuh-musuh di sekitarnya. Inilah tandanya bagimu, bahwa TUHAN telah mengurapi engkau menjadi raja atas milik-Nya sendiri:'),(7419,9,10,2,'Apabila engkau pada hari ini pergi meninggalkan aku, maka engkau akan bertemu dengan dua orang laki-laki di dekat kubur Rahel, di daerah Benyamin, di Zelzah. Mereka akan berkata kepadamu: Keledai-keledai yang engkau cari itu telah diketemukan; dan ayahmu tidak memikirkan keledai-keledai itu lagi, tetapi ia kuatir mengenai kamu, katanya: Apakah yang akan kuperbuat untuk anakku itu?'),(7420,9,10,3,'Dari sana engkau akan berjalan terus lagi dan sampai ke pohon tarbantin Tabor, maka di sana engkau akan ditemui oleh tiga orang laki-laki yang naik menghadap Allah di Betel; seorang membawa tiga ekor anak kambing, seorang membawa tiga ketul roti dan yang lain lagi sebuyung anggur.'),(7421,9,10,4,'Mereka akan memberi salam kepadamu dan memberikan kepadamu dua ketul roti yang akan kauterima dari mereka.'),(7422,9,10,5,'Sesudah itu engkau akan sampai ke Gibea Allah, tempat kedudukan pasukan orang Filistin. Dan apabila engkau masuk kota, engkau akan berjumpa di sana dengan serombongan nabi, yang turun dari bukit pengorbanan dengan gambus, rebana, suling dan kecapi di depan mereka; mereka sendiri akan kepenuhan seperti nabi.'),(7423,9,10,6,'Maka Roh TUHAN akan berkuasa atasmu; engkau akan kepenuhan bersama-sama dengan mereka dan berubah menjadi manusia lain.'),(7424,9,10,7,'Apabila tanda-tanda ini terjadi kepadamu, lakukanlah apa saja yang didapat oleh tanganmu, sebab Allah menyertai engkau.'),(7425,9,10,8,'Engkau harus pergi ke Gilgal mendahului aku, dan camkanlah, aku akan datang kepadamu untuk mempersembahkan korban bakaran dan korban keselamatan. Engkau harus menunggu tujuh hari lamanya, sampai aku datang kepadamu dan memberitahukan kepadamu apa yang harus kaulakukan.\"'),(7426,9,10,9,'Sedang ia berpaling untuk pergi meninggalkan Samuel, maka Allah mengubah hatinya menjadi lain. Dan segala tanda-tanda yang tersebut itu terjadi pada hari itu juga.'),(7427,9,10,10,'Ketika mereka sampai di Gibea dari sana, maka bertemulah ia dengan serombongan nabi; Roh Allah berkuasa atasnya dan Saul turut kepenuhan seperti nabi di tengah-tengah mereka.'),(7428,9,10,11,'Dan semua orang yang mengenalnya dari dahulu melihat dengan heran, bahwa ia bernubuat bersama-sama dengan nabi-nabi itu; lalu berkatalah orang banyak yang satu kepada yang lain: \"Apakah gerangan yang terjadi dengan anak Kish itu? Apa Saul juga termasuk golongan nabi?\"'),(7429,9,10,12,'Lalu seorang dari tempat itu menjawab: \"Siapakah bapa mereka?\" --Itulah sebabnya menjadi peribahasa: Apa Saul juga termasuk golongan nabi?'),(7430,9,10,13,'Setelah habis ia kepenuhan seperti nabi, pulanglah ia.'),(7431,9,10,14,'Dan paman Saul berkata kepadanya dan bujangnya: \"Dari mana kamu?\" Jawabnya: \"Mencari keledai-keledai itu, tetapi ketika tidak terlihat, maka kami pergi kepada Samuel.\"'),(7432,9,10,15,'Kemudian paman Saul itu berkata: \"Coba ceritakan kepadaku apa yang dikatakan Samuel kepada kamu.\"'),(7433,9,10,16,'Kata Saul kepada pamannya itu: \"Terus saja diberitahukannya kepada kami, bahwa keledai-keledai itu telah diketemukan.\" Tetapi perihal menjadi raja yang telah dikatakan Samuel kepadanya, tidak diceritakan kepadanya.'),(7434,9,10,17,'Kemudian Samuel mengerahkan bangsa itu ke hadapan TUHAN di Mizpa'),(7435,9,10,18,'dan ia berkata kepada orang Israel itu: \"Beginilah firman TUHAN, Allah Israel: Aku telah menuntun orang Israel keluar dari Mesir dan telah melepaskan kamu dari tangan orang Mesir dan dari tangan segala kerajaan yang menindas kamu.'),(7436,9,10,19,'Tetapi sekarang kamu menolak Allahmu yang menyelamatkan kamu dari segala malapetaka dan kesusahanmu, dengan berkata: Tidak, angkatlah seorang raja atas kami. Maka sebab itu, berdirilah kamu di hadapan TUHAN, menurut sukumu dan menurut kaummu.\"'),(7437,9,10,20,'Lalu Samuel menyuruh segala suku Israel tampil ke muka, maka didapati suku Benyamin.'),(7438,9,10,21,'Sesudah itu disuruhnyalah suku Benyamin tampil ke muka menurut kaum keluarganya, maka didapati kaum keluarga Matri. Akhirnya disuruhnyalah kaum keluarga Matri tampil ke muka seorang demi seorang, maka didapati Saul bin Kish. Tetapi ketika ia dicari, ia tidak diketemukan.'),(7439,9,10,22,'Sebab itu ditanyakan pulalah kepada TUHAN: \"Apa orang itu juga datang ke mari?\" TUHAN menjawab: \"Sesungguhnya ia bersembunyi di antara barang-barang.\"'),(7440,9,10,23,'Berlarilah orang ke sana dan mengambilnya dari sana, dan ketika ia berdiri di tengah-tengah orang-orang sebangsanya, ternyata ia dari bahu ke atas lebih tinggi dari pada setiap orang sebangsanya.'),(7441,9,10,24,'Dan Samuel berkata kepada seluruh bangsa itu: \"Kamu lihatkah orang yang dipilih TUHAN itu? Sebab tidak ada seorangpun yang sama seperti dia di antara seluruh bangsa itu.\" Lalu bersoraklah seluruh bangsa itu, demikian: \"Hidup raja!\"'),(7442,9,10,25,'Kemudian Samuel menguraikan kepada bangsa itu tentang hak-hak kerajaan, menuliskannya pada suatu piagam dan meletakkannya di hadapan TUHAN; sesudah itu Samuel menyuruh seluruh bangsa itu pulang, masing-masing ke rumahnya.'),(7443,9,10,26,'Saulpun pulang ke rumahnya, ke Gibea, dan bersama-sama dengan dia ikut pergi orang-orang gagah perkasa yang hatinya telah digerakkan Allah.'),(7444,9,10,27,'Tetapi orang-orang dursila berkata: \"Masakan orang ini dapat menyelamatkan kita!\" Mereka menghina dia dan tidak membawa persembahan kepadanya. Tetapi ia pura-pura tuli.'),(7445,9,11,1,'Maka Nahas, orang Amon itu, bergerak maju dan berkemah mengepung Yabesh-Gilead. Lalu berkatalah semua orang Yabesh itu kepada Nahas: \"Adakanlah perjanjian dengan kami, maka kami akan takluk kepadamu.\"'),(7446,9,11,2,'Tetapi Nahas, orang Amon itu, berkata kepada mereka: \"Dengan syarat inilah aku akan mengadakan perjanjian dengan kamu, bahwa tiap mata kananmu akan kucungkil; dengan demikian aku mendatangkan malu kepada segenap orang Israel.\"'),(7447,9,11,3,'Para tua-tua Yabesh berkata kepadanya: \"Berilah kelonggaran kepada kami tujuh hari lamanya, supaya kami mengirim utusan ke seluruh daerah Israel; dan jika tidak ada seorangpun yang menyelamatkan kami, maka kami akan keluar menyerahkan diri kepadamu.\"'),(7448,9,11,4,'Ketika para utusan itu sampai di Gibea-Saul, dan menyampaikan hal itu kepada bangsa itu, menangislah bangsa itu dengan suara nyaring.'),(7449,9,11,5,'Saul baru saja datang dari padang dengan berjalan di belakang lembunya, dan ia bertanya: \"Ada apa dengan orang-orang itu, sehingga mereka menangis?\" Mereka menceritakan kepadanya kabar orang-orang Yabesh itu.'),(7450,9,11,6,'Ketika Saul mendengar kabar itu, maka berkuasalah Roh Allah atas dia, dan menyala-nyalalah amarahnya dengan sangat.'),(7451,9,11,7,'Diambilnyalah sepasang lembu, dipotong-potongnya, lalu potongan-potongan itu dikirimkannya ke seluruh daerah Israel dengan perantaraan utusan, pesannya: \"Siapa yang tidak maju mengikuti Saul dan mengikuti Samuel, lembu-lembunya akan diperlakukan juga demikian.\" Lalu TUHAN mendatangkan ketakutan kepada bangsa itu, sehingga majulah mereka serentak.'),(7452,9,11,8,'Ketika Saul memeriksa barisan mereka di Bezek, maka ada tiga ratus ribu orang Israel dan tiga puluh ribu orang Yehuda.'),(7453,9,11,9,'Kepada para utusan yang datang itu dikatakan: \"Beginilah kamu katakan kepada orang-orang Yabesh-Gilead: Besok pada waktu panas teriknya matahari akan datang bagimu penyelamatan.\" Ketika para utusan kembali dan memberitahukan hal itu kepada orang-orang Yabesh, bersukacitalah mereka.'),(7454,9,11,10,'Lalu orang-orang Yabesh itu berkata kepada Nahas: \"Besok kami akan keluar menyerahkan diri kepadamu; maka bolehlah kamu lakukan terhadap kami apapun yang kamu pandang baik.\"'),(7455,9,11,11,'Keesokan harinya Saul membagi rakyat itu menjadi tiga pasukan. Mereka itu masuk ke tengah-tengah perkemahan musuh pada waktu kawal pagi dan memukul kalah orang-orang Amon sebelum hari panas; dan terserak-seraklah orang-orang yang lolos itu, sehingga di antara mereka tidak ada tinggal dua orang bersama-sama.'),(7456,9,11,12,'Lalu berkatalah bangsa itu kepada Samuel: \"Siapakah yang telah berkata: Masakan Saul menjadi raja atas kita? Serahkanlah orang-orang itu, supaya kami membunuhnya.\"'),(7457,9,11,13,'Tetapi kata Saul: \"Pada hari ini seorangpun tidak boleh dibunuh, sebab pada hari ini TUHAN telah mewujudkan keselamatan kepada Israel.\"'),(7458,9,11,14,'Dan Samuel berkata kepada bangsa itu: \"Marilah kita pergi ke Gilgal dan membaharui jabatan raja di sana.\"'),(7459,9,11,15,'Lalu pergilah seluruh bangsa itu ke Gilgal dan menjadikan Saul raja di sana di hadapan TUHAN di Gilgal, dan mereka mempersembahkan di sana korban keselamatan di hadapan TUHAN, dan bersukarialah di sana Saul dan semua orang Israel dengan sangat.'),(7460,9,12,1,'Berkatalah Samuel kepada seluruh orang Israel: \"Telah kudengarkan segala permintaanmu yang kamu sampaikan kepadaku, dan seorang raja telah kuangkat atasmu.'),(7461,9,12,2,'Maka sekarang raja itulah yang menjadi pemimpinmu; tetapi aku ini telah menjadi tua dan beruban, dan bukankah anak-anakku laki-laki ada di antara kamu? Akulah yang menjadi pemimpinmu dari sejak mudaku sampai hari ini.'),(7462,9,12,3,'Di sini aku berdiri. Berikanlah kesaksian menentang aku di hadapan TUHAN dan di hadapan orang yang diurapi-Nya: Lembu siapakah yang telah kuambil? Keledai siapakah yang telah kuambil? Siapakah yang telah kuperas? Siapakah yang telah kuperlakukan dengan kekerasan? Dari tangan siapakah telah kuterima sogok sehingga aku harus tutup mata? Aku akan mengembalikannya kepadamu.\"'),(7463,9,12,4,'Jawab mereka: \"Engkau tidak memeras kami dan engkau tidak memperlakukan kami dengan kekerasan dan engkau tidak menerima apa-apa dari tangan siapapun.\"'),(7464,9,12,5,'Lalu berkatalah ia kepada mereka: \"TUHAN menjadi saksi kepada kamu, dan orang yang diurapi-Nyapun menjadi saksi pada hari ini, bahwa kamu tidak mendapat apa-apa dalam tanganku.\" Jawab mereka: \"Dia menjadi saksi.\"'),(7465,9,12,6,'Lalu berkatalah Samuel kepada bangsa itu: \"Tuhanlah saksi, yang mengangkat Musa dan Harun dan yang menuntun nenek moyangmu keluar dari tanah Mesir.'),(7466,9,12,7,'Maka sebab itu, berdirilah supaya aku bersama-sama dengan kamu berhakim di hadapan TUHAN mengenai segala perbuatan keselamatan TUHAN yang telah dikerjakan-Nya kepadamu dan kepada nenek moyangmu.'),(7467,9,12,8,'Ketika Yakub datang ke Mesir dan nenek moyangmu berseru-seru kepada TUHAN, maka TUHAN mengutus Musa dan Harun, yang membawa nenek moyangmu keluar dari Mesir, dan membiarkan mereka diam di tempat ini.'),(7468,9,12,9,'Tetapi mereka melupakan TUHAN, Allah mereka, dan Ia menyerahkan mereka ke dalam tangan Sisera, panglima tentara di Hazor, dan ke dalam tangan orang Filistin dan raja Moab, yang berperang melawan mereka.'),(7469,9,12,10,'Mereka berseru-seru kepada TUHAN, katanya: Kami telah berdosa, sebab kami telah meninggalkan TUHAN dan beribadah kepada para Baal dan para Asytoret; maka sekarang lepaskanlah kami dari tangan musuh kami, maka kami akan beribadah kepada-Mu.'),(7470,9,12,11,'Sesudah itu TUHAN mengutus Yerubaal, Barak, Yefta dan Samuel, dan melepaskan kamu dari tangan musuh di sekelilingmu, sehingga kamu diam dengan tenteram.'),(7471,9,12,12,'Tetapi ketika kamu melihat, bahwa Nahas, raja bani Amon, mendatangi kamu, maka kamu berkata kepadaku: Tidak, seorang raja harus memerintah kami, padahal TUHAN, Allahmu, adalah rajamu.'),(7472,9,12,13,'Maka sebab itu, lihat itu raja yang telah kamu pilih, yang kamu minta. Sesungguhnya TUHAN telah mengangkat raja atasmu,'),(7473,9,12,14,'asal saja kamu takut akan TUHAN, beribadah kepada-Nya, mendengarkan firman-Nya dan tidak menentang titah TUHAN, dan baik kamu, maupun raja yang akan memerintah kamu itu mengikuti TUHAN, Allahmu!'),(7474,9,12,15,'Tetapi jika kamu tidak mendengarkan firman TUHAN dan kamu menentang titah TUHAN, maka tangan TUHAN akan melawan kamu dan melawan rajamu.'),(7475,9,12,16,'Sekarang tinggallah berdiri dan lihatlah perkara yang besar yang akan dilakukan TUHAN di depan matamu ini.'),(7476,9,12,17,'Bukankah sekarang musim menuai gandum? Aku akan berseru kepada TUHAN, supaya Ia memberikan guruh dan hujan. Lihatlah dan sadarlah, bahwa besar kejahatan yang telah kamu lakukan itu di mata TUHAN dengan meminta raja bagimu.\"'),(7477,9,12,18,'Lalu berserulah Samuel kepada TUHAN, maka TUHAN memberikan pada hari itu guruh dan hujan, sehingga sangat takutlah seluruh bangsa itu kepada TUHAN dan kepada Samuel.'),(7478,9,12,19,'Berkatalah seluruh bangsa itu kepada Samuel: \"Berdoalah untuk hamba-hambamu ini kepada TUHAN, Allahmu, supaya jangan kami mati, sebab dengan meminta raja bagi kami, kami menambah dosa kami dengan kejahatan ini.\"'),(7479,9,12,20,'Dan berkatalah Samuel kepada bangsa itu: \"Jangan takut; memang kamu telah melakukan segala kejahatan ini, tetapi janganlah berhenti mengikuti TUHAN, melainkan beribadahlah kepada TUHAN dengan segenap hatimu.'),(7480,9,12,21,'Janganlah menyimpang untuk mengejar dewa kesia-siaan yang tidak berguna dan tidak dapat menolong karena semuanya itu adalah kesia-siaan belaka.'),(7481,9,12,22,'Sebab TUHAN tidak akan membuang umat-Nya, sebab nama-Nya yang besar. Bukankah TUHAN telah berkenan untuk membuat kamu menjadi umat-Nya?'),(7482,9,12,23,'Mengenai aku, jauhlah dari padaku untuk berdosa kepada TUHAN dengan berhenti mendoakan kamu; aku akan mengajarkan kepadamu jalan yang baik dan lurus.'),(7483,9,12,24,'Hanya takutlah akan TUHAN dan setialah beribadah kepada-Nya dengan segenap hatimu, sebab ketahuilah, betapa besarnya hal-hal yang dilakukan-Nya di antara kamu.'),(7484,9,12,25,'Tetapi jika kamu terus berbuat jahat, maka kamu akan dilenyapkan, baik kamu maupun rajamu itu.\"'),(7485,9,13,1,'Saul berumur sekian tahun ketika ia menjadi raja; dua tahun ia memerintah atas Israel.'),(7486,9,13,2,'Saul memilih tiga ribu orang dari antara orang Israel; dua ribu orang ada bersama-sama dengan Saul di Mikhmas dan di pegunungan Betel, sedang seribu orang ada bersama-sama dengan Yonatan di Gibea Benyamin, tetapi selebihnya dari rakyat itu disuruhnya pulang, masing-masing ke kemahnya.'),(7487,9,13,3,'Yonatan memukul kalah pasukan pendudukan orang Filistin yang ada di Geba; dan hal itu terdengar oleh orang Filistin. Karena itu Saul menyuruh meniup sangkakala di seluruh negeri, sebab pikirnya: \"Biarlah orang Ibrani mendengarnya.\"'),(7488,9,13,4,'Demikianlah seluruh orang Israel mendengar kabar, bahwa Saul telah memukul kalah pasukan pendudukan orang Filistin dan dengan demikian orang Israel dibenci oleh orang Filistin. Kemudian dikerahkanlah rakyat itu untuk mengikuti Saul ke Gilgal.'),(7489,9,13,5,'Adapun orang Filistin telah berkumpul untuk berperang melawan orang Israel. Dengan tiga ribu kereta, enam ribu orang pasukan berkuda dan pasukan berjalan kaki sebanyak pasir di tepi laut mereka bergerak maju dan berkemah di Mikhmas, di sebelah timur Bet-Awen.'),(7490,9,13,6,'Ketika dilihat orang-orang Israel, bahwa mereka terjepit--sebab rakyat memang terdesak--maka larilah rakyat bersembunyi di gua, keluk batu, bukit batu, liang batu dan perigi;'),(7491,9,13,7,'malah ada orang Ibrani yang menyeberangi arungan sungai Yordan menuju tanah Gad dan Gilead, sedang Saul masih di Gilgal dan seluruh rakyat mengikutinya dengan gemetar.'),(7492,9,13,8,'Ia menunggu tujuh hari lamanya sampai waktu yang ditentukan Samuel. Tetapi ketika Samuel tidak datang ke Gilgal, mulailah rakyat itu berserak-serak meninggalkan dia.'),(7493,9,13,9,'Sebab itu Saul berkata: \"Bawalah kepadaku korban bakaran dan korban keselamatan itu.\" Lalu ia mempersembahkan korban bakaran.'),(7494,9,13,10,'Baru saja ia habis mempersembahkan korban bakaran, maka tampaklah Samuel datang. Saul pergi menyongsongnya untuk memberi salam kepadanya.'),(7495,9,13,11,'Tetapi kata Samuel: \"Apa yang telah kauperbuat?\" Jawab Saul: \"Karena aku melihat rakyat itu berserak-serak meninggalkan aku dan engkau tidak datang pada waktu yang telah ditentukan, padahal orang Filistin telah berkumpul di Mikhmas,'),(7496,9,13,12,'maka pikirku: Sebentar lagi orang Filistin akan menyerang aku di Gilgal, padahal aku belum memohonkan belas kasihan TUHAN; sebab itu aku memberanikan diri, lalu mempersembahkan korban bakaran.\"'),(7497,9,13,13,'Kata Samuel kepada Saul: \"Perbuatanmu itu bodoh. Engkau tidak mengikuti perintah TUHAN, Allahmu, yang diperintahkan-Nya kepadamu; sebab sedianya TUHAN mengokohkan kerajaanmu atas orang Israel untuk selama-lamanya.'),(7498,9,13,14,'Tetapi sekarang kerajaanmu tidak akan tetap. TUHAN telah memilih seorang yang berkenan di hati-Nya dan TUHAN telah menunjuk dia menjadi raja atas umat-Nya, karena engkau tidak mengikuti apa yang diperintahkan TUHAN kepadamu.\"'),(7499,9,13,15,'Kemudian berangkatlah Samuel dan pergi dari Gilgal ke Gibea Benyamin. Tetapi Saul memeriksa barisan rakyat yang ada bersama-sama dengan dia itu: kira-kira enam ratus orang banyaknya.'),(7500,9,13,16,'Saul dan Yonatan, anaknya, dan rakyat yang ada bersama-sama dengan mereka, tinggal di Geba-Benyamin, sedang orang Filistin berkemah di Mikhmas.'),(7501,9,13,17,'Maka keluarlah orang-orang penjarah dari perkemahan orang Filistin dalam tiga gerombolan: gerombolan yang satu mengambil jalan ke Ofra, ke daerah Syual;'),(7502,9,13,18,'gerombolan yang kedua mengambil jalan ke Bet-Horon, dan gerombolan yang satu lagi mengambil jalan ke perbatasan yang menghadap ke lembah Zeboim arah ke padang gurun.'),(7503,9,13,19,'Seorang tukang besi tidak terdapat di seluruh negeri Israel, sebab orang Filistin berkata: \"Jangan-jangan orang Ibrani membuat pedang atau tombak.\"'),(7504,9,13,20,'Jadi semua orang Israel harus pergi kepada orang Filistin untuk mengasah mata bajaknya, beliungnya, kapaknya atau aritnya masing-masing--'),(7505,9,13,21,'adapun bayarannya ialah dua pertiga syikal untuk mata bajak dan beliung, dan sepertiga syikal untuk mengasah kapak dan untuk memasang kusa--'),(7506,9,13,22,'sehingga pada hari pertempuran itu sebilah pedang atau lembingpun tidak terdapat pada seluruh rakyat yang ada bersama Saul dan Yonatan. Tetapi Saul dan Yonatan, anaknya itu, masih mempunyainya.'),(7507,9,13,23,'Dan suatu pasukan pengawal orang Filistin telah keluar ke pelintasan gunung di Mikhmas.'),(7508,9,14,1,'Pada suatu hari Yonatan bin Saul berkata kepada bujang pembawa senjatanya: \"Mari kita menyeberang ke dekat pasukan pengawal orang Filistin yang di sebelah sana.\" Tetapi tidak diberitahukannya hal itu kepada ayahnya.'),(7509,9,14,2,'Adapun Saul duduk di ujung Gibea di bawah pohon delima yang di Migron. Dan rakyat yang ada bersama-sama dengan dia itu, kira-kira enam ratus orang banyaknya.'),(7510,9,14,3,'Ahia, anak Ahitub, saudara Ikabod, anak Pinehas, anak Eli, imam TUHAN di Silo, dialah yang memakai baju efod pada waktu itu. Tetapi rakyat tidak tahu tentang perginya Yonatan itu.'),(7511,9,14,4,'Di antara pelintasan-pelintasan bukit, yang dicoba Yonatan menyeberanginya ke arah pasukan pengawal orang Filistin, ada ujung bukit batu di sebelah sini dan ada ujung bukit batu di sebelah sana: yang satu bernama Bozes, yang lain bernama Sene.'),(7512,9,14,5,'Ujung yang satu berdiri di sebelah utara di tentangan Mikhmas, yang lain di sebelah selatan di tentangan Geba.'),(7513,9,14,6,'Berkatalah Yonatan kepada bujang pembawa senjatanya itu: \"Mari kita menyeberang ke dekat pasukan pengawal orang-orang yang tidak bersunat ini. Mungkin TUHAN akan bertindak untuk kita, sebab bagi TUHAN tidak sukar untuk menolong, baik dengan banyak orang maupun dengan sedikit orang.\"'),(7514,9,14,7,'Lalu jawab pembawa senjatanya itu kepadanya: \"Lakukanlah niat hatimu itu; sungguh, aku sepakat.\"'),(7515,9,14,8,'Kata Yonatan: \"Perhatikan, kita menyeberang ke dekat orang-orang itu dan memperlihatkan diri kepada mereka.'),(7516,9,14,9,'Apabila kata mereka kepada kita begini: Berhentilah, sampai kami datang padamu, maka kita tinggal berdiri di tempat kita dan tidak naik mendapatkan mereka,'),(7517,9,14,10,'tetapi apabila kata mereka begini: Naiklah ke mari, maka kita akan naik, sebab kalau demikian TUHAN telah menyerahkan mereka ke dalam tangan kita. Itulah tandanya bagi kita.\"'),(7518,9,14,11,'Ketika mereka keduanya memperlihatkan diri kepada pasukan pengawal orang Filistin, berkatalah orang Filistin itu: \"Lihat, orang-orang Ibrani keluar dari lobang-lobang tempat mereka bersembunyi.\"'),(7519,9,14,12,'Orang-orang dari pasukan pengawal itu berseru kepada Yonatan dan pembawa senjatanya, katanya: \"Naiklah ke mari, maka kami akan menghajar kamu.\" Lalu kata Yonatan kepada pembawa senjatanya: \"Naiklah mengikuti aku, sebab TUHAN telah menyerahkan mereka ke dalam tangan orang Israel.\"'),(7520,9,14,13,'Maka naiklah Yonatan merangkak ke atas, dengan diikuti oleh pembawa senjatanya. Orang-orang itu tewas terparang oleh Yonatan, sedang pembawa senjatanya membunuh mereka dari belakangnya.'),(7521,9,14,14,'Kekalahan yang pertama ini, yang ditimbulkan Yonatan dan pembawa senjatanya itu, besarnya kira-kira dua puluh orang dalam jarak kira-kira setengah alur dari sepembajakan ladang.'),(7522,9,14,15,'Lalu timbullah kegentaran di perkemahan, di padang dan di antara seluruh rakyat. Juga pasukan pengawal dan penjarah-penjarah itu gentar, dan bumi gemetar, sehingga menjadi kegentaran yang dari Allah.'),(7523,9,14,16,'Ketika peninjau-peninjau Saul di Gibea Benyamin melihat hal itu--dan sesungguhnya, orang ramai seperti ombak berjalan ke sana ke mari--'),(7524,9,14,17,'berkatalah Saul kepada tentara yang bersama-sama dengan dia itu: \"Periksalah barisan dan lihatlah siapa yang pergi dari pada kita.\" Mereka memeriksa barisan, dan ternyata Yonatan dan pembawa senjatanya tidak ada.'),(7525,9,14,18,'Lalu kata Saul kepada Ahia: \"Bawalah baju efod ke mari.\" Karena pada waktu itu dialah yang memakai baju efod di antara orang Israel.'),(7526,9,14,19,'Tetapi sedang Saul berbicara kepada imam itu, maka kian lama kian bertambahlah keributan di perkemahan orang Filistin, sehingga Saul berkata pula kepada imam itu: \"Biarlah!\"'),(7527,9,14,20,'Kemudian berkumpullah Saul dan seluruh rakyat yang bersama-sama dengan dia itu; dan ketika mereka sampai ke tempat pertempuran, tampaklah setiap orang menikam temannya dengan pedang, suatu huru-hara yang sangat besar.'),(7528,9,14,21,'Lagipula orang-orang Ibrani yang telah lama tinggal pada orang Filistin dan yang telah ikut maju dalam tentara mereka, mereka juga berbalik untuk bergabung dengan orang-orang Israel yang ada bersama-sama dengan Saul dan Yonatan.'),(7529,9,14,22,'Bahkan, ketika semua orang Israel yang telah bersembunyi di pegunungan Efraim, mendengar bahwa orang Filistin telah lari, orang-orang itupun bergabung dengan mereka dalam pertempuran.'),(7530,9,14,23,'Demikianlah TUHAN menyelamatkan orang Israel pada hari itu. Pertempuran itu meluas sampai lewat Bet-Awen.'),(7531,9,14,24,'Ketika orang-orang Israel terdesak pada hari itu, Saul menyuruh rakyat mengucapkan kutuk, katanya: \"Terkutuklah orang yang memakan sesuatu sebelum matahari terbenam dan sebelum aku membalas dendam terhadap musuhku.\" Sebab itu tidak ada seorangpun dari rakyat yang memakan sesuatu.'),(7532,9,14,25,'Dan seluruh orang itu sampailah ke suatu hutan dan di sana ada madu di tanah.'),(7533,9,14,26,'Ketika rakyat sampai ke hutan itu, tampaklah ada di sana madu meleleh, tetapi tidak ada seorangpun yang mencedoknya ke mulutnya dengan tangan, sebab rakyat takut kepada sumpah itu.'),(7534,9,14,27,'Tetapi Yonatan tidak mendengar, bahwa ayahnya telah menyuruh rakyat bersumpah. Ia mengulurkan tongkat yang ada di tangannya dan mencelupkan ujungnya ke dalam sarang madu; kemudian ia mencedoknya ke mulutnya dengan tangan, lalu matanya menjadi terang lagi.'),(7535,9,14,28,'Dan seorang dari rakyat berbicara, katanya: \"Ayahmu telah menyuruh rakyat bersumpah dengan bersungguh-sungguh, katanya: Terkutuklah orang yang memakan sesuatu pada hari ini; sebab itu rakyat letih lesu.\"'),(7536,9,14,29,'Lalu kata Yonatan: \"Ayahku mencelakakan negeri; coba lihat, bagaimana terangnya mataku, setelah aku merasai sedikit dari madu ini.'),(7537,9,14,30,'Apalagi, jika sekiranya rakyat pada hari ini boleh makan dengan bebas dari jarahan musuhnya, yang telah didapatnya! Tetapi sekarang tidaklah besar kekalahan di antara orang Filistin.\"'),(7538,9,14,31,'Dan pada hari itu mereka memukul kalah orang Filistin dari Mikhmas sampai ke Ayalon. Rakyat sudah sangat letih lesu,'),(7539,9,14,32,'sebab itu rakyat menyambar jarahan; mereka mengambil kambing domba, lembu dan anak lembu, menyembelihnya begitu saja di atas tanah, dan memakannya dengan darahnya.'),(7540,9,14,33,'Lalu diberitahukanlah kepada Saul, demikian: \"Lihat, rakyat berdosa terhadap TUHAN dengan memakannya dengan darahnya.\" Dan ia berkata: \"Kamu berbuat khianat; gulingkanlah sekarang juga sebuah batu besar ke mari.\"'),(7541,9,14,34,'Kata Saul pula: \"Berserak-seraklah di antara rakyat dan katakan kepada mereka: Setiap orang harus membawa lembunya atau dombanya kepadaku; sembelihlah itu di sini, maka kamu boleh memakannya. Tetapi janganlah berdosa terhadap TUHAN dengan memakannya dengan darahnya.\" Lalu setiap orang dari seluruh rakyat membawa serta pada malam itu lembunya, dan mereka menyembelihnya di sana.'),(7542,9,14,35,'Saul mendirikan mezbah bagi TUHAN; inilah mezbah yang mula-mula sekali didirikannya bagi TUHAN.'),(7543,9,14,36,'Lagi kata Saul: \"Marilah kita pada malam ini mengejar orang Filistin dan menjarahi mereka sampai fajar menyingsing dan janganlah kita biarkan hidup seorangpun dari mereka.\" Jawab mereka itu: \"Perbuatlah apa yang kaupandang baik.\" Tetapi imam berkata: \"Marilah kita dahulu tampil menghadap Allah di sini.\"'),(7544,9,14,37,'Saul bertanya kepada Allah: \"Bolehkah aku mengejar orang Filistin itu? Akan Kauserahkankah mereka ke dalam tangan orang Israel?\" Tetapi pada hari itu Ia tidak menjawab Saul.'),(7545,9,14,38,'Lalu kata Saul: \"Datanglah ke mari, kamu segala pemuka rakyat; berusahalah mengetahui apa sebab dosa ini terjadi pada hari ini.'),(7546,9,14,39,'Sebab demi TUHAN yang hidup, yang menyelamatkan orang Israel, sekalipun itu disebabkan oleh Yonatan, anakku, maka ia pasti akan mati.\" Tetapi seorangpun dari seluruh rakyat tidak ada yang menjawabnya.'),(7547,9,14,40,'Kemudian berkatalah ia kepada seluruh orang Israel: \"Kamu berdiri di sebelah yang satu dan aku serta anakku Yonatan akan berdiri di sebelah yang lain.\" Lalu jawab rakyat kepada Saul: \"Perbuatlah apa yang kaupandang baik.\"'),(7548,9,14,41,'Lalu berkatalah Saul: \"Ya, TUHAN, Allah Israel, mengapa Engkau tidak menjawab hamba-Mu pada hari ini? Jika kesalahan itu ada padaku atau pada anakku Yonatan, ya TUHAN, Allah Israel, tunjukkanlah kiranya Urim; tetapi jika kesalahan itu ada pada umat-Mu Israel, tunjukkanlah Tumim.\" Lalu didapati Yonatan dan Saul, tetapi rakyat itu terluput.'),(7549,9,14,42,'Kata Saul: \"Buanglah undi antara aku dan anakku Yonatan.\" Lalu didapati Yonatan.'),(7550,9,14,43,'Kata Saul kepada Yonatan: \"Beritahukanlah kepadaku apa yang telah kauperbuat.\" Lalu Yonatan memberitahukan kepadanya, katanya: \"Memang, aku telah merasai sedikit madu dengan ujung tongkat yang ada di tanganku. Aku bersedia untuk mati.\"'),(7551,9,14,44,'Kata Saul: \"Beginilah kiranya Allah menghukum aku, bahkan lebih lagi dari pada itu. Sesungguhnya, Yonatan, engkau harus mati.\"'),(7552,9,14,45,'Tetapi rakyat berkata kepada Saul: \"Masakan Yonatan harus mati, dia yang telah mendapat kemenangan yang besar ini di Israel? Jauhlah yang demikian! Demi TUHAN yang hidup, sehelai rambutpun dari kepalanya takkan jatuh ke bumi! Sebab dengan pertolongan Allah juga dilakukannya hal itu pada hari ini.\" Demikianlah rakyat membebaskan Yonatan, sehingga ia tidak harus mati.'),(7553,9,14,46,'Maka pulanglah Saul setelah mengejar orang Filistin, dan orang Filistin itupun kembali ke tempat kediamannya.'),(7554,9,14,47,'Setelah Saul mendapat jabatan raja atas Israel, maka berperanglah ia ke segala penjuru melawan segala musuhnya: melawan Moab, bani Amon, Edom, raja-raja negeri Zoba dan orang Filistin. Dan ke manapun ia pergi, ia selalu mendapat kemenangan.'),(7555,9,14,48,'Ia melakukan perbuatan-perbuatan yang gagah perkasa, memukul kalah orang Amalek, dan melepaskan Israel dari tangan orang-orang yang merampasi mereka.'),(7556,9,14,49,'Anak-anak lelaki Saul ialah Yonatan, Yiswi dan Malkisua. Nama kedua anaknya yang perempuan: yang tertua bernama Merab, yang termuda bernama Mikhal.'),(7557,9,14,50,'Isteri Saul bernama Ahinoam, anak Ahimaas. Panglima tentaranya bernama Abner, anak Ner, paman Saul.'),(7558,9,14,51,'Kish, ayah Saul, dan Ner, ayah Abner, adalah anak-anak Abiel.'),(7559,9,14,52,'Hebat peperangan melawan orang Filistin selama zaman Saul. Dan semua pahlawan dan orang gagah perkasa, yang dilihat Saul, dikumpulkannya kepadanya.'),(7560,9,15,1,'Berkatalah Samuel kepada Saul: \"Aku telah diutus oleh TUHAN untuk mengurapi engkau menjadi raja atas Israel, umat-Nya; oleh sebab itu, dengarkanlah bunyi firman TUHAN.'),(7561,9,15,2,'Beginilah firman TUHAN semesta alam: Aku akan membalas apa yang dilakukan orang Amalek kepada orang Israel, karena orang Amalek menghalang-halangi mereka, ketika orang Israel pergi dari Mesir.'),(7562,9,15,3,'Jadi pergilah sekarang, kalahkanlah orang Amalek, tumpaslah segala yang ada padanya, dan janganlah ada belas kasihan kepadanya. Bunuhlah semuanya, laki-laki maupun perempuan, kanak-kanak maupun anak-anak yang menyusu, lembu maupun domba, unta maupun keledai.\"'),(7563,9,15,4,'Lalu Saul memanggil rakyat berkumpul dan memeriksa barisan mereka di Telaim: ada dua ratus ribu orang pasukan berjalan kaki dan sepuluh ribu orang Yehuda.'),(7564,9,15,5,'Setelah Saul sampai ke kota orang Amalek, disuruhnyalah orang-orang menghadang di lembah.'),(7565,9,15,6,'Berkatalah Saul kepada orang Keni: \"Berangkatlah, menjauhlah, pergilah dari tengah-tengah orang Amalek, supaya jangan kulenyapkan kamu bersama-sama dengan mereka. Bukankah kamu telah menunjukkan persahabatanmu kepada semua orang Israel, ketika mereka pergi dari Mesir?\" Sesudah itu menjauhlah orang Keni dari tengah-tengah orang Amalek.'),(7566,9,15,7,'Lalu Saul memukul kalah orang Amalek mulai dari Hawila sampai ke Syur, yang di sebelah timur Mesir.'),(7567,9,15,8,'Agag, raja orang Amalek, ditangkapnya hidup-hidup, tetapi segenap rakyatnya ditumpasnya dengan mata pedang.'),(7568,9,15,9,'Tetapi Saul dan rakyat itu menyelamatkan Agag dan kambing domba dan lembu-lembu yang terbaik dan tambun, pula anak domba dan segala yang berharga: tidak mau mereka menumpas semuanya itu. Tetapi segala hewan yang tidak berharga dan yang buruk, itulah yang ditumpas mereka.'),(7569,9,15,10,'Lalu datanglah firman TUHAN kepada Samuel, demikian:'),(7570,9,15,11,'Aku menyesal, karena Aku telah menjadikan Saul raja, sebab ia telah berbalik dari pada Aku dan tidak melaksanakan firman-Ku.'),(7571,9,15,12,'Lalu Samuel bangun pagi-pagi untuk bertemu dengan Saul, tetapi diberitahukan kepada Samuel, demikian: \"Saul telah ke Karmel tadi dan telah didirikannya baginya suatu tanda peringatan; kemudian ia balik dan mengambil jurusan ke Gilgal.\"'),(7572,9,15,13,'Ketika Samuel sampai kepada Saul, berkatalah Saul kepadanya: \"Diberkatilah kiranya engkau oleh TUHAN; aku telah melaksanakan firman TUHAN.\"'),(7573,9,15,14,'Tetapi kata Samuel: \"Kalau begitu apakah bunyi kambing domba, yang sampai ke telingaku, dan bunyi lembu-lembu yang kudengar itu?\"'),(7574,9,15,15,'Jawab Saul: \"Semuanya itu dibawa dari pada orang Amalek, sebab rakyat menyelamatkan kambing domba dan lembu-lembu yang terbaik dengan maksud untuk mempersembahkan korban kepada TUHAN, Allahmu; tetapi selebihnya telah kami tumpas.\"'),(7575,9,15,16,'Lalu berkatalah Samuel kepada Saul: \"Sudahlah! Aku akan memberitahukan kepadamu apa yang difirmankan TUHAN kepadaku tadi malam.\" Kata Saul kepadanya: \"Katakanlah.\"'),(7576,9,15,17,'Sesudah itu berkatalah Samuel: \"Bukankah engkau, walaupun engkau kecil pada pemandanganmu sendiri, telah menjadi kepala atas suku-suku Israel? Dan bukankah TUHAN telah mengurapi engkau menjadi raja atas Israel?'),(7577,9,15,18,'TUHAN telah menyuruh engkau pergi, dengan pesan: Pergilah, tumpaslah orang-orang berdosa itu, yakni orang Amalek, berperanglah melawan mereka sampai engkau membinasakan mereka.'),(7578,9,15,19,'Mengapa engkau tidak mendengarkan suara TUHAN? Mengapa engkau mengambil jarahan dan melakukan apa yang jahat di mata TUHAN?\"'),(7579,9,15,20,'Lalu kata Saul kepada Samuel: \"Aku memang mendengarkan suara TUHAN dan mengikuti jalan yang telah disuruh TUHAN kepadaku dan aku membawa Agag, raja orang Amalek, tetapi orang Amalek itu sendiri telah kutumpas.'),(7580,9,15,21,'Tetapi rakyat mengambil dari jarahan itu kambing domba dan lembu-lembu yang terbaik dari yang dikhususkan untuk ditumpas itu, untuk mempersembahkan korban kepada TUHAN, Allahmu, di Gilgal.\"'),(7581,9,15,22,'Tetapi jawab Samuel: \"Apakah TUHAN itu berkenan kepada korban bakaran dan korban sembelihan sama seperti kepada mendengarkan suara TUHAN? Sesungguhnya, mendengarkan lebih baik dari pada korban sembelihan, memperhatikan lebih baik dari pada lemak domba-domba jantan.'),(7582,9,15,23,'Sebab pendurhakaan adalah sama seperti dosa bertenung dan kedegilan adalah sama seperti menyembah berhala dan terafim. Karena engkau telah menolak firman TUHAN, maka Ia telah menolak engkau sebagai raja.\"'),(7583,9,15,24,'Berkatalah Saul kepada Samuel: \"Aku telah berdosa, sebab telah kulangkahi titah TUHAN dan perkataanmu; tetapi aku takut kepada rakyat, karena itu aku mengabulkan permintaan mereka.'),(7584,9,15,25,'Maka sekarang, ampunilah kiranya dosaku; kembalilah bersama-sama dengan aku, maka aku akan sujud menyembah kepada TUHAN.\"'),(7585,9,15,26,'Tetapi jawab Samuel kepada Saul: \"Aku tidak akan kembali bersama-sama dengan engkau, sebab engkau telah menolak firman TUHAN; sebab itu TUHAN telah menolak engkau, sebagai raja atas Israel.\"'),(7586,9,15,27,'Ketika Samuel berpaling hendak pergi, maka Saul memegang punca jubah Samuel, tetapi terkoyak.'),(7587,9,15,28,'Kemudian berkatalah Samuel kepadanya: \"TUHAN telah mengoyakkan dari padamu jabatan raja atas Israel pada hari ini dan telah memberikannya kepada orang lain yang lebih baik dari padamu.'),(7588,9,15,29,'Lagi Sang Mulia dari Israel tidak berdusta dan Ia tidak tahu menyesal; sebab Ia bukan manusia yang harus menyesal.\"'),(7589,9,15,30,'Tetapi kata Saul: \"Aku telah berdosa; tetapi tunjukkanlah juga hormatmu kepadaku sekarang di depan para tua-tua bangsaku dan di depan orang Israel. Kembalilah bersama-sama dengan aku, maka aku akan sujud menyembah kepada TUHAN, Allahmu.\"'),(7590,9,15,31,'Sesudah itu kembalilah Samuel mengikuti Saul. Dan Saul sujud menyembah kepada TUHAN.'),(7591,9,15,32,'Lalu berkatalah Samuel: \"Bawa ke mari Agag, raja Amalek itu.\" Dengan gembira Agag pergi kepadanya, sebab pikirnya: \"Sesungguhnya, kepahitan maut telah lewat.\"'),(7592,9,15,33,'Tetapi kata Samuel: \"Seperti pedangmu membuat perempuan-perempuan kehilangan anak, demikianlah ibumu akan kehilangan anak di antara perempuan-perempuan.\" Sesudah itu Samuel mencincang Agag di hadapan TUHAN di Gilgal.'),(7593,9,15,34,'Kemudian Samuel pergi ke Rama, tetapi Saul pergi ke rumahnya, di Gibea-Saul.'),(7594,9,15,35,'Sampai hari matinya Samuel tidak melihat Saul lagi, tetapi Samuel berdukacita karena Saul. Dan TUHAN menyesal, karena Ia menjadikan Saul raja atas Israel.'),(7595,9,16,1,'Berfirmanlah TUHAN kepada Samuel: \"Berapa lama lagi engkau berdukacita karena Saul? Bukankah ia telah Kutolak sebagai raja atas Israel? Isilah tabung tandukmu dengan minyak dan pergilah. Aku mengutus engkau kepada Isai, orang Betlehem itu, sebab di antara anak-anaknya telah Kupilih seorang raja bagi-Ku.\"'),(7596,9,16,2,'Tetapi Samuel berkata: \"Bagaimana mungkin aku pergi? Jika Saul mendengarnya, ia akan membunuh aku.\" Firman TUHAN: \"Bawalah seekor lembu muda dan katakan: Aku datang untuk mempersembahkan korban kepada TUHAN.'),(7597,9,16,3,'Kemudian undanglah Isai ke upacara pengorbanan itu, lalu Aku akan memberitahukan kepadamu apa yang harus kauperbuat. Urapilah bagi-Ku orang yang akan Kusebut kepadamu.\"'),(7598,9,16,4,'Samuel berbuat seperti yang difirmankan TUHAN dan tibalah ia di kota Betlehem. Para tua-tua di kota itu datang mendapatkannya dengan gemetar dan berkata: \"Adakah kedatanganmu ini membawa selamat?\"'),(7599,9,16,5,'Jawabnya: \"Ya, benar! Aku datang untuk mempersembahkan korban kepada TUHAN. Kuduskanlah dirimu, dan datanglah dengan daku ke upacara pengorbanan ini.\" Kemudian ia menguduskan Isai dan anak-anaknya yang laki-laki dan mengundang mereka ke upacara pengorbanan itu.'),(7600,9,16,6,'Ketika mereka itu masuk dan Samuel melihat Eliab, lalu pikirnya: \"Sungguh, di hadapan TUHAN sekarang berdiri yang diurapi-Nya.\"'),(7601,9,16,7,'Tetapi berfirmanlah TUHAN kepada Samuel: \"Janganlah pandang parasnya atau perawakan yang tinggi, sebab Aku telah menolaknya. Bukan yang dilihat manusia yang dilihat Allah; manusia melihat apa yang di depan mata, tetapi TUHAN melihat hati.\"'),(7602,9,16,8,'Lalu Isai memanggil Abinadab dan menyuruhnya lewat di depan Samuel, tetapi Samuel berkata: \"Orang inipun tidak dipilih TUHAN.\"'),(7603,9,16,9,'Kemudian Isai menyuruh Syama lewat, tetapi Samuel berkata: \"Orang inipun tidak dipilih TUHAN.\"'),(7604,9,16,10,'Demikianlah Isai menyuruh ketujuh anaknya lewat di depan Samuel, tetapi Samuel berkata kepada Isai: \"Semuanya ini tidak dipilih TUHAN.\"'),(7605,9,16,11,'Lalu Samuel berkata kepada Isai: \"Inikah anakmu semuanya?\" Jawabnya: \"Masih tinggal yang bungsu, tetapi sedang menggembalakan kambing domba.\" Kata Samuel kepada Isai: \"Suruhlah memanggil dia, sebab kita tidak akan duduk makan, sebelum ia datang ke mari.\"'),(7606,9,16,12,'Kemudian disuruhnyalah menjemput dia. Ia kemerah-merahan, matanya indah dan parasnya elok. Lalu TUHAN berfirman: \"Bangkitlah, urapilah dia, sebab inilah dia.\"'),(7607,9,16,13,'Samuel mengambil tabung tanduk yang berisi minyak itu dan mengurapi Daud di tengah-tengah saudara-saudaranya. Sejak hari itu dan seterusnya berkuasalah Roh TUHAN atas Daud. Lalu berangkatlah Samuel menuju Rama.'),(7608,9,16,14,'Tetapi Roh TUHAN telah mundur dari pada Saul, dan sekarang ia diganggu oleh roh jahat yang dari pada TUHAN.'),(7609,9,16,15,'Lalu berkatalah hamba-hamba Saul kepadanya: \"Ketahuilah, roh jahat yang dari pada Allah mengganggu engkau;'),(7610,9,16,16,'baiklah tuanku menitahkan hamba-hambamu yang di depanmu ini mencari seorang yang pandai main kecapi. Apabila roh jahat yang dari pada Allah itu hinggap padamu, haruslah ia main kecapi, maka engkau merasa nyaman.\"'),(7611,9,16,17,'Berkatalah Saul kepada hamba-hambanya itu: \"Carilah bagiku seorang yang dapat main kecapi dengan baik, dan bawalah dia kepadaku.\"'),(7612,9,16,18,'Lalu jawab salah seorang hamba itu, katanya: \"Sesungguhnya, aku telah melihat salah seorang anak laki-laki Isai, orang Betlehem itu, yang pandai main kecapi. Ia seorang pahlawan yang gagah perkasa, seorang prajurit, yang pandai bicara, elok perawakannya; dan TUHAN menyertai dia.\"'),(7613,9,16,19,'Kemudian Saul mengirim suruhan kepada Isai dengan pesan: \"Suruhlah kepadaku anakmu Daud, yang ada pada kambing domba itu.\"'),(7614,9,16,20,'Lalu Isai mengambil seekor keledai yang dimuati roti, sekirbat anggur dan seekor anak kambing, maka dikirimkannyalah itu kepada Saul dengan perantaraan Daud, anaknya.'),(7615,9,16,21,'Demikianlah Daud sampai kepada Saul dan menjadi pelayannya. Saul sangat mengasihinya, dan ia menjadi pembawa senjatanya.'),(7616,9,16,22,'Sebab itu Saul menyuruh orang kepada Isai mengatakan: \"Biarkanlah Daud tetap menjadi pelayanku, sebab aku suka kepadanya.\"'),(7617,9,16,23,'Dan setiap kali apabila roh yang dari pada Allah itu hinggap pada Saul, maka Daud mengambil kecapi dan memainkannya; Saul merasa lega dan nyaman, dan roh yang jahat itu undur dari padanya.'),(7618,9,17,1,'Orang Filistin mengumpulkan tentaranya untuk berperang; mereka berkumpul di Sokho yang di tanah Yehuda dan berkemah antara Sokho dan Azeka di Efes-Damim.'),(7619,9,17,2,'Saul dan orang-orang Israel juga berkumpul dan berkemah di Lembah Tarbantin; mereka mengatur barisan perangnya berhadapan dengan orang Filistin.'),(7620,9,17,3,'Orang Filistin berdiri di bukit sebelah sini dan orang Israel berdiri di bukit sebelah sana, dan lembah ada di antara mereka.'),(7621,9,17,4,'Lalu tampillah keluar seorang pendekar dari tentara orang Filistin. Namanya Goliat, dari Gat. Tingginya enam hasta sejengkal.'),(7622,9,17,5,'Ketopong tembaga ada di kepalanya, dan ia memakai baju zirah yang bersisik; berat baju zirah ini lima ribu syikal tembaga.'),(7623,9,17,6,'Dia memakai penutup kaki dari tembaga, dan di bahunya ia memanggul lembing tembaga.'),(7624,9,17,7,'Gagang tombaknya seperti pesa tukang tenun, dan mata tombaknya itu enam ratus syikal besi beratnya. Dan seorang pembawa perisai berjalan di depannya.'),(7625,9,17,8,'Ia berdiri dan berseru kepada barisan Israel, katanya kepada mereka: \"Mengapa kamu keluar untuk mengatur barisan perangmu? Bukankah aku seorang Filistin dan kamu adalah hamba Saul? Pilihlah bagimu seorang, dan biarlah ia turun mendapatkan daku.'),(7626,9,17,9,'Jika ia dapat berperang melawan aku dan mengalahkan aku, maka kami akan menjadi hambamu; tetapi jika aku dapat mengungguli dia dan mengalahkannya, maka kamu akan menjadi hamba kami dan takluk kepada kami.\"'),(7627,9,17,10,'Pula kata orang Filistin itu: \"Aku menantang hari ini barisan Israel; berikanlah kepadaku seorang, supaya kami berperang seorang lawan seorang.\"'),(7628,9,17,11,'Ketika Saul dan segenap orang Israel mendengar perkataan orang Filistin itu, maka cemaslah hati mereka dan sangat ketakutan.'),(7629,9,17,12,'Daud adalah anak seorang dari Efrata, dari Betlehem-Yehuda, yang bernama Isai. Isai mempunyai delapan anak laki-laki. Pada zaman Saul orang itu telah tua dan lanjut usianya.'),(7630,9,17,13,'Ketiga anak Isai yang besar-besar telah pergi berperang mengikuti Saul; nama ketiga anaknya yang pergi berperang itu ialah Eliab, anak sulung, anak yang kedua ialah Abinadab, dan anak yang ketiga adalah Syama.'),(7631,9,17,14,'Daudlah yang bungsu. Jadi ketiga anak yang besar-besar itu pergi mengikuti Saul.'),(7632,9,17,15,'Tetapi Daud selalu pulang dari pada Saul untuk menggembalakan domba ayahnya di Betlehem.'),(7633,9,17,16,'Orang Filistin itu maju mendekat pada pagi hari dan pada petang hari. Demikianlah ia tampil ke depan empat puluh hari lamanya.'),(7634,9,17,17,'Isai berkata kepada Daud, anaknya: \"Ambillah untuk kakak-kakakmu bertih gandum ini seefa dan roti yang sepuluh ini; bawalah cepat-cepat ke perkemahan, kepada kakak-kakakmu.'),(7635,9,17,18,'Dan baiklah sampaikan keju yang sepuluh ini kepada kepala pasukan seribu. Tengoklah apakah kakak-kakakmu selamat dan bawalah pulang suatu tanda dari mereka.'),(7636,9,17,19,'Saul dan mereka itu dan semua orang Israel ada di Lembah Tarbantin tengah berperang melawan orang Filistin.\"'),(7637,9,17,20,'Lalu Daud bangun pagi-pagi, ditinggalkannyalah kambing dombanya pada seorang penjaga, lalu mengangkat muatan dan pergi, seperti yang diperintahkan Isai kepadanya. Sampailah ia ke perkemahan, ketika tentara keluar untuk mengatur barisannya dan mengangkat sorak perang.'),(7638,9,17,21,'Orang Israel dan orang Filistin itu mengatur barisannya, barisan berhadapan dengan barisan.'),(7639,9,17,22,'Lalu Daud menurunkan barang-barangnya dan meninggalkannya di tangan penjaga barang-barang tentara. Berlari-larilah Daud ke tempat barisan; sesampai di sana, bertanyalah ia kepada kakak-kakaknya apakah mereka selamat.'),(7640,9,17,23,'Sedang ia berbicara dengan mereka, tampillah maju pendekar itu. Namanya Goliat, orang Filistin dari Gat, dari barisan orang Filistin. Ia mengucapkan kata-kata yang tadi juga, dan Daud mendengarnya.'),(7641,9,17,24,'Ketika semua orang Israel melihat orang itu, larilah mereka dari padanya dengan sangat ketakutan.'),(7642,9,17,25,'Berkatalah orang-orang Israel itu: \"Sudahkah kamu lihat orang yang maju itu? Sesungguhnya ia maju untuk mencemoohkan orang Israel! Orang yang mengalahkan dia akan dianugerahi raja kekayaan yang besar, raja akan memberikan anaknya yang perempuan kepadanya dan kaum keluarganya akan dibebaskannya dari pajak di Israel.\"'),(7643,9,17,26,'Lalu berkatalah Daud kepada orang-orang yang berdiri di dekatnya: \"Apakah yang akan dilakukan kepada orang yang mengalahkan orang Filistin itu dan yang menghindarkan cemooh dari Israel? Siapakah orang Filistin yang tak bersunat ini, sampai ia berani mencemoohkan barisan dari pada Allah yang hidup?\"'),(7644,9,17,27,'Rakyat itupun menjawabnya dengan perkataan tadi: \"Begitulah akan dilakukan kepada orang yang mengalahkan dia.\"'),(7645,9,17,28,'Ketika Eliab, kakaknya yang tertua, mendengar perkataan Daud kepada orang-orang itu, bangkitlah amarah Eliab kepada Daud sambil berkata: \"Mengapa engkau datang? Dan pada siapakah kautinggalkan kambing domba yang dua tiga ekor itu di padang gurun? Aku kenal sifat pemberanimu dan kejahatan hatimu: engkau datang ke mari dengan maksud melihat pertempuran.\"'),(7646,9,17,29,'Tetapi jawab Daud: \"Apa yang telah kuperbuat? Hanya bertanya saja!\"'),(7647,9,17,30,'Lalu berpalinglah ia dari padanya kepada orang lain dan menanyakan yang sama. Dan rakyat memberi jawab kepadanya seperti tadi.'),(7648,9,17,31,'Terdengarlah kepada orang perkataan yang diucapkan oleh Daud, lalu diberitahukanlah kepada Saul. Dan Saul menyuruh memanggil dia.'),(7649,9,17,32,'Berkatalah Daud kepada Saul: \"Janganlah seseorang menjadi tawar hati karena dia; hambamu ini akan pergi melawan orang Filistin itu.\"'),(7650,9,17,33,'Tetapi Saul berkata kepada Daud: \"Tidak mungkin engkau dapat menghadapi orang Filistin itu untuk melawan dia, sebab engkau masih muda, sedang dia sejak dari masa mudanya telah menjadi prajurit.\"'),(7651,9,17,34,'Tetapi Daud berkata kepada Saul: \"Hambamu ini biasa menggembalakan kambing domba ayahnya. Apabila datang singa atau beruang, yang menerkam seekor domba dari kawanannya,'),(7652,9,17,35,'maka aku mengejarnya, menghajarnya dan melepaskan domba itu dari mulutnya. Kemudian apabila ia berdiri menyerang aku, maka aku menangkap janggutnya lalu menghajarnya dan membunuhnya.'),(7653,9,17,36,'Baik singa maupun beruang telah dihajar oleh hambamu ini. Dan orang Filistin yang tidak bersunat itu, ia akan sama seperti salah satu dari pada binatang itu, karena ia telah mencemooh barisan dari pada Allah yang hidup.\"'),(7654,9,17,37,'Pula kata Daud: \"TUHAN yang telah melepaskan aku dari cakar singa dan dari cakar beruang, Dia juga akan melepaskan aku dari tangan orang Filistin itu.\" Kata Saul kepada Daud: \"Pergilah! TUHAN menyertai engkau.\"'),(7655,9,17,38,'Lalu Saul mengenakan baju perangnya kepada Daud, ditaruhnya ketopong tembaga di kepalanya dan dikenakannya baju zirah kepadanya.'),(7656,9,17,39,'Lalu Daud mengikatkan pedangnya di luar baju perangnya, kemudian ia berikhtiar berjalan, sebab belum pernah dicobanya. Maka berkatalah Daud kepada Saul: \"Aku tidak dapat berjalan dengan memakai ini, sebab belum pernah aku mencobanya.\" Kemudian ia menanggalkannya.'),(7657,9,17,40,'Lalu Daud mengambil tongkatnya di tangannya, dipilihnya dari dasar sungai lima batu yang licin dan ditaruhnya dalam kantung gembala yang dibawanya, yakni tempat batu-batu, sedang umbannya dipegangnya di tangannya. Demikianlah ia mendekati orang Filistin itu.'),(7658,9,17,41,'Orang Filistin itu kian dekat menghampiri Daud dan di depannya orang yang membawa perisainya.'),(7659,9,17,42,'Ketika orang Filistin itu menujukan pandangnya ke arah Daud serta melihat dia, dihinanya Daud itu karena ia masih muda, kemerah-merahan dan elok parasnya.'),(7660,9,17,43,'Orang Filistin itu berkata kepada Daud: \"Anjingkah aku, maka engkau mendatangi aku dengan tongkat?\" Lalu demi para allahnya orang Filistin itu mengutuki Daud.'),(7661,9,17,44,'Pula orang Filistin itu berkata kepada Daud: \"Hadapilah aku, maka aku akan memberikan dagingmu kepada burung-burung di udara dan kepada binatang-binatang di padang.\"'),(7662,9,17,45,'Tetapi Daud berkata kepada orang Filistin itu: \"Engkau mendatangi aku dengan pedang dan tombak dan lembing, tetapi aku mendatangi engkau dengan nama TUHAN semesta alam, Allah segala barisan Israel yang kautantang itu.'),(7663,9,17,46,'Hari ini juga TUHAN akan menyerahkan engkau ke dalam tanganku dan aku akan mengalahkan engkau dan memenggal kepalamu dari tubuhmu; hari ini juga aku akan memberikan mayatmu dan mayat tentara orang Filistin kepada burung-burung di udara dan kepada binatang-binatang liar, supaya seluruh bumi tahu, bahwa Israel mempunyai Allah,'),(7664,9,17,47,'dan supaya segenap jemaah ini tahu, bahwa TUHAN menyelamatkan bukan dengan pedang dan bukan dengan lembing. Sebab di tangan Tuhanlah pertempuran dan Iapun menyerahkan kamu ke dalam tangan kami.\"'),(7665,9,17,48,'Ketika orang Filistin itu bergerak maju untuk menemui Daud, maka segeralah Daud berlari ke barisan musuh untuk menemui orang Filistin itu;'),(7666,9,17,49,'lalu Daud memasukkan tangannya dalam kantungnya, diambilnyalah sebuah batu dari dalamnya, diumbannya, maka kenalah dahi orang Filistin itu, sehingga batu itu terbenam ke dalam dahinya, dan terjerumuslah ia dengan mukanya ke tanah.'),(7667,9,17,50,'Demikianlah Daud mengalahkan orang Filistin itu dengan umban dan batu; ia mengalahkan orang Filistin itu dan membunuhnya, tanpa pedang di tangan.'),(7668,9,17,51,'Daud berlari mendapatkan orang Filistin itu, lalu berdiri di sebelahnya; diambilnyalah pedangnya, dihunusnya dari sarungnya, lalu menghabisi dia. Dipancungnyalah kepalanya dengan pedang itu. Ketika orang-orang Filistin melihat, bahwa pahlawan mereka telah mati, maka larilah mereka.'),(7669,9,17,52,'Maka bangkitlah orang-orang Israel dan Yehuda, mereka bersorak-sorak lalu mengejar orang-orang Filistin sampai dekat Gat dan sampai pintu gerbang Ekron. Dan orang-orang yang terbunuh dari orang Filistin bergelimpangan di jalan ke Saaraim, sampai Gat dan sampai Ekron.'),(7670,9,17,53,'Kemudian pulanglah orang Israel dari pemburuan hebat atas orang Filistin, lalu menjarah perkemahan mereka.'),(7671,9,17,54,'Dan Daud mengambil kepala orang Filistin yang dipancungnya itu dan membawanya ke Yerusalem, tetapi senjata-senjata orang itu ditaruhnya dalam kemahnya.'),(7672,9,17,55,'Ketika Saul melihat Daud pergi menemui orang Filistin itu, berkatalah ia kepada Abner, panglima tentaranya: \"Anak siapakah orang muda itu, Abner?\" Jawab Abner: \"Demi tuanku hidup, ya raja, sesungguhnya aku tidak tahu.\"'),(7673,9,17,56,'Kemudian raja berkata: \"Tanyakanlah, anak siapakah orang muda itu.\"'),(7674,9,17,57,'Ketika Daud kembali sesudah mengalahkan orang Filistin itu, maka Abner memanggilnya dan membawanya menghadap Saul, sedang kepala orang Filistin itu masih ada di tangannya.'),(7675,9,17,58,'Kata Saul kepadanya: \"Anak siapakah engkau, ya orang muda?\" Jawab Daud: \"Anak hamba tuanku, Isai, orang Betlehem itu.\"'),(7676,9,18,1,'Ketika Daud habis berbicara dengan Saul, berpadulah jiwa Yonatan dengan jiwa Daud; dan Yonatan mengasihi dia seperti jiwanya sendiri.'),(7677,9,18,2,'Pada hari itu Saul membawa dia dan tidak membiarkannya pulang ke rumah ayahnya.'),(7678,9,18,3,'Yonatan mengikat perjanjian dengan Daud, karena ia mengasihi dia seperti dirinya sendiri.'),(7679,9,18,4,'Yonatan menanggalkan jubah yang dipakainya, dan memberikannya kepada Daud, juga baju perangnya, sampai pedangnya, panahnya dan ikat pinggangnya.'),(7680,9,18,5,'Daud maju berperang dan selalu berhasil ke mana juga Saul menyuruhnya, sehingga Saul mengangkat dia mengepalai para prajurit. Hal ini dipandang baik oleh seluruh rakyat dan juga oleh pegawai-pegawai Saul.'),(7681,9,18,6,'Tetapi pada waktu mereka pulang, ketika Daud kembali sesudah mengalahkan orang Filistin itu, keluarlah orang-orang perempuan dari segala kota Israel menyongsong raja Saul sambil menyanyi dan menari-nari dengan memukul rebana, dengan bersukaria dan dengan membunyikan gerincing;'),(7682,9,18,7,'dan perempuan yang menari-nari itu menyanyi berbalas-balasan, katanya: \"Saul mengalahkan beribu-ribu musuh, tetapi Daud berlaksa-laksa.\"'),(7683,9,18,8,'Lalu bangkitlah amarah Saul dengan sangat; dan perkataan itu menyebalkan hatinya, sebab pikirnya: \"Kepada Daud diperhitungkan mereka berlaksa-laksa, tetapi kepadaku diperhitungkannya beribu-ribu; akhir-akhirnya jabatan raja itupun jatuh kepadanya.\"'),(7684,9,18,9,'Sejak hari itu maka Saul selalu mendengki Daud.'),(7685,9,18,10,'Keesokan harinya roh jahat yang dari pada Allah itu berkuasa atas Saul, sehingga ia kerasukan di tengah-tengah rumah, sedang Daud main kecapi seperti sehari-hari. Adapun Saul ada tombak di tangannya.'),(7686,9,18,11,'Saul melemparkan tombak itu, karena pikirnya: \"Baiklah aku menancapkan Daud ke dinding.\" Tetapi Daud mengelakkannya sampai dua kali.'),(7687,9,18,12,'Saul menjadi takut kepada Daud, karena TUHAN menyertai Daud, sedang dari pada Saul Ia telah undur.'),(7688,9,18,13,'Sebab itu Saul menjauhkan Daud dari dekatnya dan mengangkat dia menjadi kepala pasukan seribu, sehingga ia berada di depan dalam segala gerakan tentara.'),(7689,9,18,14,'Daud berhasil di segala perjalanannya, sebab TUHAN menyertai dia.'),(7690,9,18,15,'Ketika dilihat Saul, bahwa Daud sangat berhasil, makin takutlah ia kepadanya;'),(7691,9,18,16,'tetapi seluruh orang Israel dan orang Yehuda mengasihi Daud, karena ia memimpin segala gerakan mereka.'),(7692,9,18,17,'Berkatalah Saul kepada Daud: \"Ini dia anakku perempuan yang tertua, Merab; dia akan kuberikan kepadamu menjadi isterimu, hanya jadilah bagiku seorang yang gagah perkasa dan lakukanlah perang TUHAN.\" Sebab pikir Saul: \"Janganlah tanganku memukul dia, tetapi biarlah ia dipukul oleh tangan orang Filistin.\"'),(7693,9,18,18,'Tetapi Daud berkata kepada Saul: \"Siapakah aku dan siapakah sanak saudaraku, kaum ayahku, di antara orang Israel, sehingga aku menjadi menantu raja?\"'),(7694,9,18,19,'Tetapi ketika tiba waktunya untuk memberikan Merab, anak Saul itu, kepada Daud, maka anak perempuan itu diberikan kepada Adriel, orang Mehola, menjadi isterinya.'),(7695,9,18,20,'Tetapi Mikhal, anak perempuan Saul, jatuh cinta kepada Daud; ketika hal itu diberitahukan kepada Saul, maka iapun menyetujuinya;'),(7696,9,18,21,'sebab pikir Saul: \"Baiklah Mikhal kuberikan kepadanya; biarlah ia menjadi jerat bagi Daud, dan biarlah tangan orang Filistin memukul dia!\" Lalu berkatalah Saul kepada Daud untuk kedua kalinya: \"Pada hari ini engkau boleh menjadi menantuku.\"'),(7697,9,18,22,'Lagi Saul memerintahkan kepada para pegawainya: \"Katakanlah kepada Daud dengan diam-diam, demikian: Sesungguhnya, raja suka kepadamu dan para pegawainya mengasihi engkau; maka sebab itu, jadilah engkau menantu raja.\"'),(7698,9,18,23,'Lalu para pegawai Saul menyampaikan perkataan itu kepada Daud, tetapi Daud menjawab: \"Perkara ringankah pada pemandanganmu menjadi menantu raja? Bukankah aku seorang yang miskin dan rendah?\"'),(7699,9,18,24,'Para pegawai Saul memberitahukan kepada raja, katanya: \"Demikianlah jawab yang diberi Daud.\"'),(7700,9,18,25,'Kemudian berkatalah Saul: \"Beginilah kamu katakan kepada Daud: Raja tidak menghendaki mas kawin selain dari seratus kulit khatan orang Filistin sebagai pembalasan kepada musuh raja.\" Saul bermaksud untuk menjatuhkan Daud dengan perantaraan orang Filistin.'),(7701,9,18,26,'Ketika para pegawainya memberitahukan perkataan itu kepada Daud, maka setujulah Daud menjadi menantu raja. Waktunya belum genap,'),(7702,9,18,27,'tetapi Daud sudah bersiap, ia pergi dengan orang-orangnya dan menewaskan dari orang Filistin itu dua ratus orang serta membawa kulit khatan mereka; dan dalam jumlah yang genap diberikan merekalah semuanya itu kepada raja, supaya Daud menjadi menantu raja. Kemudian Saul memberikan Mikhal, anaknya, kepadanya menjadi isterinya.'),(7703,9,18,28,'Lalu mengertilah Saul dan tahulah ia, bahwa TUHAN menyertai Daud, dan bahwa seluruh orang Israel mengasihi Daud.'),(7704,9,18,29,'Maka makin takutlah Saul kepada Daud. Saul tetap menjadi musuh Daud seumur hidupnya.'),(7705,9,18,30,'Apabila raja-raja orang Filistin maju berperang, setiap kali mereka maju berperang, maka Daud lebih berhasil dari semua pegawai Saul, sehingga namanya sangat masyhur.'),(7706,9,19,1,'Saul mengatakan kepada Yonatan, anaknya, dan kepada semua pegawainya, bahwa Daud harus dibunuh. Tetapi Yonatan, anak Saul, sangat suka kepada Daud,'),(7707,9,19,2,'sehingga Yonatan memberitahukan kepada Daud: \"Ayahku Saul berikhtiar untuk membunuh engkau; oleh sebab itu, hati-hatilah besok pagi, duduklah di suatu tempat perlindungan dan bersembunyilah di sana.'),(7708,9,19,3,'Aku akan keluar dan berdiri di sisi ayahku di padang tempatmu itu. Maka aku akan berbicara dengan ayahku perihalmu; aku akan melihat bagaimana keadaannya, lalu memberitahukannya kepadamu.\"'),(7709,9,19,4,'Lalu Yonatan mengatakan yang baik tentang Daud kepada Saul, ayahnya, katanya: \"Janganlah raja berbuat dosa terhadap Daud, hambanya, sebab ia tidak berbuat dosa terhadapmu; bukankah apa yang diperbuatnya sangat baik bagimu!'),(7710,9,19,5,'Ia telah mempertaruhkan nyawanya dan telah mengalahkan orang Filistin itu, dan TUHAN telah memberikan kemenangan yang besar kepada seluruh Israel. Engkau sudah melihatnya dan bersukacita karenanya. Mengapa engkau hendak berbuat dosa terhadap darah orang yang tidak bersalah dengan membunuh Daud tanpa alasan?\"'),(7711,9,19,6,'Saul mendengarkan perkataan Yonatan dan Saul bersumpah: \"Demi TUHAN yang hidup, ia tidak akan dibunuh.\"'),(7712,9,19,7,'Lalu Yonatan memanggil Daud dan Yonatan memberitahukan kepadanya segala perkataan itu. Yonatan membawa Daud kepada Saul dan ia bekerja padanya seperti dahulu.'),(7713,9,19,8,'Ketika perang pecah pula, maka majulah Daud dan berperang melawan orang Filistin; ia menimbulkan kekalahan besar di antara mereka, sehingga mereka melarikan diri dari depannya.'),(7714,9,19,9,'Tetapi roh jahat yang dari pada TUHAN hinggap pada Saul, ketika ia duduk di rumahnya, dengan tombaknya di tangannya; dan Daud sedang main kecapi.'),(7715,9,19,10,'Lalu Saul berikhtiar menancapkan Daud ke dinding dengan tombaknya, tetapi Daud mengelakkan tikaman Saul, sehingga Saul mengenai dinding dengan tombak itu. Sesudah itu Daud melarikan diri dan luputlah ia pada malam itu.'),(7716,9,19,11,'Kemudian Saul mengirim orang-orang suruhan ke rumah Daud untuk mengamat-amatinya dan untuk membunuh dia pada waktu pagi. Tetapi Mikhal, isteri Daud, memberitahukan kepadanya, demikian: \"Jika engkau tidak dapat meluputkan dirimu malam ini, maka besok engkau akan dibunuh.\"'),(7717,9,19,12,'Lalu Mikhal menurunkan Daud dari jendela, ia pergi melarikan diri dan luputlah ia.'),(7718,9,19,13,'Sesudah itu Mikhal mengambil terafim dan menaruhnya di tempat tidur; ditaruhnya sehelai tenunan bulu kambing di bagian kepala dan ditutupinya dengan selimut.'),(7719,9,19,14,'Lalu Saul mengirim orang-orang suruhan untuk mengambil Daud, tetapi perempuan itu berkata: \"Ia sakit.\"'),(7720,9,19,15,'Sesudah itu Saul mengirim orang-orang suruhan itu untuk melihat Daud, katanya: \"Bawalah dia di atas tempat tidur itu ke mari, supaya ia dibunuh.\"'),(7721,9,19,16,'Lalu masuklah para suruhan itu ke dalam, dan tampaklah ada terafim di tempat tidur dengan sehelai tenunan bulu kambing di bagian kepala.'),(7722,9,19,17,'Berkatalah Saul kepada Mikhal: \"Mengapa engkau menipu aku demikian itu dan melepas musuhku pergi, sehingga ia luput?\" Tetapi jawab Mikhal kepada Saul: \"Ia berkata kepadaku: Biarkanlah aku pergi, apa perlunya aku membunuh engkau?\"'),(7723,9,19,18,'Setelah Daud melarikan diri dan luput, sampailah ia kepada Samuel di Rama dan memberitahukan kepadanya segala yang dilakukan Saul kepadanya. Kemudian pergilah ia bersama-sama dengan Samuel dan tinggallah mereka di Nayot.'),(7724,9,19,19,'Ketika diberitahukan kepada Saul, demikian: \"Ketahuilah, Daud ada di Nayot, dekat Rama,\"'),(7725,9,19,20,'maka Saul mengirim orang-orang suruhan untuk mengambil Daud. Tetapi orang-orang ini melihat sekumpulan nabi kepenuhan, dengan dikepalai oleh Samuel. Dan Roh Allah hinggap pada orang-orang suruhan Saul, sehingga merekapun kepenuhan seperti nabi.'),(7726,9,19,21,'Lalu hal itu diberitahukan kepada Saul; ia mengirim orang-orang suruhan yang lain, tetapi orang-orang itupun juga kepenuhan seperti nabi. Saul mengirim sekali lagi orang-orang suruhan, rombongan yang ketiga, dan orang-orang inipun juga kepenuhan.'),(7727,9,19,22,'Lalu ia sendiri pergi ke Rama. Sesampainya ke dekat perigi besar yang di Sekhu, bertanyalah ia, katanya: \"Di mana Samuel dan Daud?\" Jawab orang: \"Ada di Nayot, dekat Rama.\"'),(7728,9,19,23,'Lalu pergilah ia ke sana, ke Nayot, dekat Rama dan pada diapun hinggaplah Roh Allah, dan selama ia melanjutkan perjalanannya ia kepenuhan seperti nabi, hingga ia sampai ke Nayot dekat Rama.'),(7729,9,19,24,'Iapun menanggalkan pakaiannya, dan iapun juga kepenuhan di depan Samuel. Ia rebah terhantar dengan telanjang sehari-harian dan semalam-malaman itu. Itulah sebabnya orang berkata: \"Apakah juga Saul termasuk golongan nabi?\"'),(7730,9,20,1,'Maka larilah Daud dari Nayot, dekat Rama; sampailah ia kepada Yonatan, lalu berkata: \"Apakah yang telah kuperbuat? Apakah kesalahanku dan apakah dosaku terhadap ayahmu, sehingga ia ingin mencabut nyawaku?\"'),(7731,9,20,2,'Tetapi Yonatan berkata kepadanya: \"Jauhlah yang demikian itu! engkau tidak akan mati dibunuh. Ingatlah, ayahku tidak berbuat sesuatu, baik perkara besar maupun perkara kecil, dengan tidak menyatakannya kepadaku. Mengapa ayahku harus menyembunyikan perkara ini kepadaku? Tidak mungkin!\"'),(7732,9,20,3,'Tetapi Daud menjawab, katanya: \"Ayahmu tahu benar, bahwa engkau suka kepadaku. Sebab itu pikirnya: Tidak boleh Yonatan mengetahui hal ini, nanti ia bersusah hati. Namun, demi TUHAN yang hidup dan demi hidupmu, hanya satu langkah jaraknya antara aku dan maut.\"'),(7733,9,20,4,'Yonatan berkata kepada Daud: \"Apapun kehendak hatimu, aku akan melakukannya bagimu.\"'),(7734,9,20,5,'Lalu kata Daud kepada Yonatan: \"Kautahu, besok bulan baru, maka sebenarnya aku harus duduk makan bersama-sama dengan raja. Jika engkau membiarkan aku pergi, maka aku akan bersembunyi di padang sampai lusa petang.'),(7735,9,20,6,'Apabila ayahmu menanyakan aku, haruslah kaukatakan: Daud telah meminta dengan sangat kepadaku untuk pergi dengan segera ke Betlehem, kotanya, karena di sana ada upacara pengorbanan tahunan bagi segenap kaumnya.'),(7736,9,20,7,'Jika begini dikatakannya: Baiklah! maka hambamu ini selamat. Tetapi jika amarahnya bangkit dengan segera, ketahuilah, bahwa ia telah mengambil keputusan untuk mendatangkan celaka.'),(7737,9,20,8,'Jika demikian, tunjukkanlah kesetiaanmu kepada hambamu ini, sebab engkau telah mengikat perjanjian di hadapan TUHAN dengan hambamu ini. Tetapi jika ada kesalahan padaku, engkau sendirilah membunuh aku. Mengapa engkau harus menyerahkan aku kepada ayahmu?\"'),(7738,9,20,9,'Tetapi jawab Yonatan: \"Jauhlah yang demikian itu! Sebab jika kuketahui dengan pasti, bahwa ayahku telah mengambil keputusan untuk mendatangkan celaka kepadamu, masakan aku tidak memberitahukannya kepadamu?\"'),(7739,9,20,10,'Lalu bertanyalah Daud kepada Yonatan: \"Siapakah yang akan memberitahukan kepadaku, apabila ayahmu menjawab engkau dengan keras?\"'),(7740,9,20,11,'Kata Yonatan kepada Daud: \"Marilah kita keluar ke padang.\" Maka keluarlah keduanya ke padang.'),(7741,9,20,12,'Lalu berkatalah Yonatan kepada Daud: \"Demi TUHAN, Allah Israel, besok atau lusa kira-kira waktu ini aku akan memeriksa perasaan ayahku. Apabila baik keadaannya bagi Daud, masakan aku tidak akan menyuruh orang kepadamu dan memberitahukannya kepadamu?'),(7742,9,20,13,'Tetapi apabila ayahku memandang baik untuk mendatangkan celaka kepadamu, beginilah kiranya TUHAN menghukum Yonatan, bahkan lebih lagi dari pada itu, sekiranya aku tidak menyatakannya kepadamu dan membiarkan engkau pergi, sehingga engkau dapat berjalan dengan selamat. TUHAN kiranya menyertai engkau, seperti Ia menyertai ayahku dahulu.'),(7743,9,20,14,'Jika aku masih hidup, bukankah engkau akan menunjukkan kepadaku kasih setia TUHAN? Tetapi jika aku sudah mati,'),(7744,9,20,15,'janganlah engkau memutuskan kasih setiamu terhadap keturunanku sampai selamanya. Dan apabila TUHAN melenyapkan setiap orang dari musuh Daud dari muka bumi,'),(7745,9,20,16,'janganlah nama Yonatan terhapus dari keturunan Daud, melainkan kiranya TUHAN menuntut balas dari pada musuh-musuh Daud.\"'),(7746,9,20,17,'Dan Yonatan menyuruh Daud sekali lagi bersumpah demi kasihnya kepadanya, sebab ia mengasihi Daud seperti dirinya sendiri.'),(7747,9,20,18,'Kemudian berkatalah Yonatan kepadanya: \"Besok bulan baru; maka engkau nanti akan ditanyakan, sebab tempat dudukmu akan tinggal kosong.'),(7748,9,20,19,'Tetapi lusa engkau pasti akan dicari; engkau harus datang ke tempat engkau bersembunyi pada hari peristiwa itu, dan duduklah dekat bukit batu.'),(7749,9,20,20,'Maka aku akan memanahkan tiga anak panah ke samping batu itu, seolah-olah aku membidik suatu sasaran.'),(7750,9,20,21,'Dan ketahuilah, aku akan menyuruh bujangku: Pergilah mencari anak-anak panah itu. Jika tegas kukatakan kepada bujang itu: Lihat anak-anak panah itu lebih ke mari, ambillah! --maka datanglah, sebab, demi TUHAN yang hidup, engkau selamat dan tidak ada bahaya apa-apa.'),(7751,9,20,22,'Tetapi jika begini kukatakan kepada orang muda itu: Lihat anak-anak panah itu lebih ke sana! --maka pergilah, sebab TUHAN menyuruh engkau pergi.'),(7752,9,20,23,'Tentang hal yang kita janjikan itu, antara aku dan engkau, sesungguhnya, TUHAN ada di antara aku dan engkau sampai selamanya.\"'),(7753,9,20,24,'Sesudah itu bersembunyilah Daud di padang. Ketika bulan baru tiba, duduklah raja pada meja untuk makan.'),(7754,9,20,25,'Raja duduk di tempatnya seperti biasa, dekat dinding. Yonatan berhadapan dengan dia, Abner duduk di sisi Saul, tetapi tempat Daud tinggal kosong.'),(7755,9,20,26,'Dan Saul tidak berkata apa-apa pada hari itu, sebab pikirnya: \"Barangkali ada sesuatu yang terjadi kepadanya; mungkin ia tidak tahir; ya, tentu ia tidak tahir.\"'),(7756,9,20,27,'Tetapi pada hari sesudah bulan baru itu, pada hari yang kedua, ketika tempat Daud masih tinggal kosong, bertanyalah Saul kepada Yonatan, anaknya: \"Mengapa anak Isai tidak datang makan, baik kemarin maupun hari ini?\"'),(7757,9,20,28,'Jawab Yonatan kepada Saul: \"Daud telah meminta dengan sangat kepadaku untuk pergi ke Betlehem,'),(7758,9,20,29,'katanya: Biarkanlah aku pergi, sebab ada upacara pengorbanan bagi kaum kami di kota, dan saudara-saudaraku sendirilah yang memanggil aku. Oleh sebab itu, jika engkau mengasihi aku, berilah izin kepadaku untuk menengok saudara-saudaraku. Itulah sebabnya ia tidak datang ke perjamuan raja.\"'),(7759,9,20,30,'Lalu bangkitlah amarah Saul kepada Yonatan, katanya kepadanya: \"Anak sundal yang kurang ajar! Bukankah aku tahu, bahwa engkau telah memilih pihak anak Isai dan itu noda bagi kau sendiri dan bagi perut ibumu?'),(7760,9,20,31,'Sebab sesungguhnya selama anak Isai itu hidup di muka bumi, engkau dan kerajaanmu tidak akan kokoh. Dan sekarang suruhlah orang memanggil dan membawa dia kepadaku, sebab ia harus mati.\"'),(7761,9,20,32,'Tetapi Yonatan menjawab Saul, ayahnya itu, katanya kepadanya: \"Mengapa ia harus dibunuh? Apa yang dilakukannya?\"'),(7762,9,20,33,'Lalu Saul melemparkan tombaknya kepada Yonatan untuk membunuhnya. Maka tahulah Yonatan, bahwa ayahnya telah mengambil keputusan untuk membunuh Daud.'),(7763,9,20,34,'Sebab itu Yonatan bangkit dan meninggalkan perjamuan itu dengan kemarahan yang bernyala-nyala. Pada hari yang kedua bulan baru itu ia tidak makan apa-apa, sebab ia bersusah hati karena Daud, sebab ayahnya telah menghina Daud.'),(7764,9,20,35,'Pada waktu pagi keluarlah Yonatan ke padang bersama-sama seorang budak kecil sesuai dengan janjinya kepada Daud.'),(7765,9,20,36,'Berkatalah ia kepada budaknya: \"Larilah, carilah anak-anak panah yang kupanahkan.\" Baru saja budak itu berlari, maka Yonatan melepaskan sebatang anak panah lewat kepala budak itu.'),(7766,9,20,37,'Ketika budak itu sampai ke tempat letaknya anak panah yang dilepaskan Yonatan itu, maka berserulah Yonatan dari belakang budak itu, katanya: \"Bukankah anak panah itu lebih ke sana?\"'),(7767,9,20,38,'Kemudian berserulah Yonatan dari belakang budak itu: \"Ayo, cepat, jangan berdiri saja!\" Lalu budak Yonatan memungut anak panah itu dan kembali kepada tuannya.'),(7768,9,20,39,'Tetapi budak itu tidak tahu apa-apa, hanya Yonatan dan Daudlah yang mengetahui hal itu.'),(7769,9,20,40,'Sesudah itu Yonatan memberikan senjatanya kepada budak yang menyertai dia, dan berkata kepadanya: \"Pergilah, bawalah ke kota.\"'),(7770,9,20,41,'Maka pulanglah budak itu, lalu tampillah Daud dari sebelah bukit batu; ia sujud dengan mukanya ke tanah dan menyembah tiga kali. Mereka bercium-ciuman dan bertangis-tangisan. Akhirnya Daud dapat menahan diri.'),(7771,9,20,42,'Kemudian berkatalah Yonatan kepada Daud: \"Pergilah dengan selamat; bukankah kita berdua telah bersumpah demi nama TUHAN, demikian: TUHAN akan ada di antara aku dan engkau serta di antara keturunanku dan keturunanmu sampai selamanya.\" (20-43) Setelah itu bangunlah Daud dan pergi; dan Yonatanpun pulang ke kota.'),(7772,9,21,1,'Sampailah Daud ke Nob kepada Ahimelekh, imam itu. Dengan gemetar Ahimelekh pergi menemui Daud dan berkata kepadanya: \"Mengapa engkau seorang diri dan tidak ada orang bersama-sama dengan engkau?\"'),(7773,9,21,2,'Jawab Daud kepada imam Ahimelekh: \"Raja menugaskan sesuatu kepadaku, katanya kepadaku: Siapapun juga tidak boleh mengetahui sesuatu dari hal yang kusuruh kepadamu dan yang kutugaskan kepadamu ini. Sebab itu orang-orangku telah kusuruh pergi ke suatu tempat.'),(7774,9,21,3,'Maka sekarang, apa yang ada padamu? Berikanlah kepadaku lima roti atau apapun yang ada.\"'),(7775,9,21,4,'Lalu jawab imam itu kepada Daud: \"Tidak ada roti biasa padaku, hanya roti kudus yang ada; asal saja orang-orangmu itu menjaga diri terhadap perempuan.\"'),(7776,9,21,5,'Daud menjawab imam itu, katanya kepadanya: \"Memang, kami tidak diperbolehkan bergaul dengan perempuan, seperti sediakala apabila aku maju berperang. Tubuh orang-orangku itu tahir, sekalipun pada perjalanan biasa, apalagi pada hari ini, masing-masing mereka tahir tubuhnya.\"'),(7777,9,21,6,'Lalu imam itu memberikan kepadanya roti kudus itu, karena tidak ada roti di sana kecuali roti sajian; roti itu biasa diangkat orang dari hadapan TUHAN, supaya pada hari roti itu diambil, ditaruh lagi roti baru.'),(7778,9,21,7,'Maka pada hari itu juga ada di sana salah seorang pegawai Saul, yang dikhususkan melayani TUHAN; namanya Doeg, seorang Edom, pengawas atas gembala-gembala Saul.'),(7779,9,21,8,'Berkatalah Daud kepada Ahimelekh: \"Tidak adakah padamu di sini tombak atau pedang? Sebab baik pedangku maupun senjataku, tidak dapat kubawa, karena perintah raja itu mendesak.\"'),(7780,9,21,9,'Kemudian berkatalah imam itu: \"Pedang Goliat, orang Filistin, yang kaupukul kalah di Lembah Tarbantin, itulah yang ada di sini, terbungkus dalam kain di belakang efod itu. Jika engkau hendak mengambilnya, ambillah; yang lain tidak ada, hanya ini.\" Kata Daud: \"Tidak ada yang seperti itu; berikanlah itu kepadaku.\"'),(7781,9,21,10,'Kemudian bersiaplah Daud dan larilah ia pada hari itu juga dari Saul; sampailah ia kepada Akhis, raja kota Gat.'),(7782,9,21,11,'Pegawai-pegawai Akhis berkata kepada tuannya: \"Bukankah ini Daud raja negeri itu? Bukankah tentang dia orang-orang menyanyi berbalas-balasan sambil menari-nari, demikian: Saul mengalahkan beribu-ribu musuh, tetapi Daud berlaksa-laksa?\"'),(7783,9,21,12,'Daud memperhatikan perkataan itu, dan dia menjadi takut sekali kepada Akhis, raja kota Gat itu.'),(7784,9,21,13,'Sebab itu ia berlaku seperti orang yang sakit ingatan di depan mata mereka dan berbuat pura-pura gila di dekat mereka; ia menggores-gores pintu gerbang dan membiarkan ludahnya meleleh ke janggutnya.'),(7785,9,21,14,'Lalu berkatalah Akhis kepada para pegawainya: \"Tidakkah kamu lihat, bahwa orang itu gila? Mengapa kamu membawa dia kepadaku?'),(7786,9,21,15,'Kekurangan orang gilakah aku, maka kamu bawa orang ini kepadaku supaya ia menunjukkan gilanya dekat aku? Patutkah orang yang demikian masuk ke rumahku?\"'),(7787,9,22,1,'Lalu Daud pergi dari sana dan melarikan diri ke gua Adulam. Ketika saudara-saudaranya dan seluruh keluarganya mendengar hal itu, pergilah mereka ke sana mendapatkan dia.'),(7788,9,22,2,'Berhimpunlah juga kepadanya setiap orang yang dalam kesukaran, setiap orang yang dikejar-kejar tukang piutang, setiap orang yang sakit hati, maka ia menjadi pemimpin mereka. Bersama-sama dengan dia ada kira-kira empat ratus orang.'),(7789,9,22,3,'Dari sana Daud pergi ke Mizpa di Moab dan berkata kepada raja negeri Moab: \"Izinkanlah ayahku dan ibuku tinggal padamu, sampai aku tahu, apa yang dilakukan Allah kepadaku.\"'),(7790,9,22,4,'Lalu diantarkannyalah mereka kepada raja negeri Moab, dan mereka tinggal bersama dia selama Daud ada di kubu gunung.'),(7791,9,22,5,'Tetapi Gad, nabi itu, berkata kepada Daud: \"Janganlah tinggal di kubu gunung itu, pergilah dan pulanglah ke tanah Yehuda.\" Lalu pergilah Daud dan masuk ke hutan Keret.'),(7792,9,22,6,'Hal itu terdengar oleh Saul, sebab Daud dan orang-orang yang bersama-sama dengan dia telah diketahui tempatnya. Adapun Saul ada di Gibea, sedang duduk di bawah pohon tamariska di bukit, dengan tombaknya di tangan dan semua pegawainya berdiri di dekatnya.'),(7793,9,22,7,'Lalu berkatalah Saul kepada para pegawainya yang berdiri di dekatnya: \"Cobalah dengar, ya orang-orang Benyamin! Apakah anak Isai itu juga akan memberikan kepada kamu sekalian ladang dan kebun anggur, apakah ia akan mengangkat kamu sekalian menjadi kepala atas pasukan seribu dan atas pasukan seratus,'),(7794,9,22,8,'sehingga kamu sekalian mengadakan persepakatan melawan aku dan tidak ada seorangpun yang menyatakan kepadaku, bahwa anakku mengikat diri dengan anak Isai itu? Tidak ada seorangpun dari kamu yang cemas karena aku, atau yang menyatakan kepadaku, bahwa anakku telah menghasut pegawaiku melawan aku menjadi penghadang seperti sekarang ini.\"'),(7795,9,22,9,'Lalu menjawablah Doeg, orang Edom itu, yang berdiri dekat para pegawai Saul, katanya: \"Telah kulihat, bahwa anak Isai itu datang ke Nob, kepada Ahimelekh bin Ahitub.'),(7796,9,22,10,'Ia menanyakan TUHAN bagi Daud dan memberikan bekal kepadanya; juga pedang Goliat, orang Filistin itu, diberikannya kepadanya.\"'),(7797,9,22,11,'Lalu raja menyuruh memanggil Ahimelekh bin Ahitub, imam itu, bersama-sama dengan seluruh keluarganya, para imam yang di Nob; dan datanglah sekaliannya menghadap raja.'),(7798,9,22,12,'Kata Saul: \"Cobalah dengar, ya anak Ahitub!\" Jawabnya: \"Ya, tuanku.\"'),(7799,9,22,13,'Kemudian bertanyalah Saul kepadanya: \"Mengapa kamu mengadakan persepakatan melawan aku, engkau dengan anak Isai itu, dengan memberikan roti dan pedang kepadanya, menanyakan Allah baginya, sehingga ia bangkit melawan aku menjadi penghadang seperti sekarang ini?\"'),(7800,9,22,14,'Lalu Ahimelekh menjawab raja: \"Tetapi siapakah di antara segala pegawaimu yang dapat dipercaya seperti Daud, apalagi ia menantu raja dan kepala para pengawalmu, dan dihormati dalam rumahmu?'),(7801,9,22,15,'Bukan ini pertama kali aku menanyakan Allah bagi dia. Sekali-kali tidak! Janganlah kiranya raja melontarkan tuduhan kepada hambamu ini, bahkan kepada seluruh keluargaku, sebab hambamu ini tidak tahu apa-apa tentang semuanya itu, baik tentang perkara kecil maupun perkara besar.\"'),(7802,9,22,16,'Tetapi raja berkata: \"Engkau mesti dibunuh, Ahimelekh, engkau dan seluruh keluargamu.\"'),(7803,9,22,17,'Lalu raja memerintahkan kepada bentara yang berdiri di dekatnya: \"Majulah dan bunuhlah para imam TUHAN itu sebab mereka membantu Daud; sebab walaupun mereka tahu, bahwa ia melarikan diri, mereka tidak memberitahukan hal itu kepadaku.\" Tetapi para pegawai raja tidak mau mengangkat tangannya untuk memarang imam-imam TUHAN itu.'),(7804,9,22,18,'Lalu berkatalah raja kepada Doeg: \"Majulah engkau dan paranglah para imam itu.\" Maka majulah Doeg, orang Edom itu, lalu memarang para imam itu. Ia membunuh pada hari itu delapan puluh lima orang, yang memakai baju efod dari kain lenan.'),(7805,9,22,19,'Juga penduduk Nob, kota imam itu, dibunuh raja dengan mata pedang; laki-laki maupun perempuan, kanak-kanak maupun anak yang menyusu, pula lembu, keledai dan domba dibunuhnya dengan mata pedang.'),(7806,9,22,20,'Tetapi seorang anak Ahimelekh bin Ahitub, namanya Abyatar luput; ia melarikan diri menjadi pengikut Daud.'),(7807,9,22,21,'Ketika Abyatar memberitahukan kepada Daud, bahwa Saul telah membunuh para imam TUHAN,'),(7808,9,22,22,'berkatalah Daud kepada Abyatar: \"Memang pada hari itu juga ketika Doeg, orang Edom itu, ada di sana, aku telah tahu, bahwa pasti ia akan memberitahukannya kepada Saul. Akulah sebab utama dari pada kematian seluruh keluargamu.'),(7809,9,22,23,'Tinggallah padaku, janganlah takut; sebab siapa yang ingin mencabut nyawamu, ia juga ingin mencabut nyawaku; di dekatku engkau aman.\"'),(7810,9,23,1,'Diberitahukanlah kepada Daud, begini: \"Ketahuilah, orang Filistin berperang melawan kota Kehila dan menjarah tempat-tempat pengirikan.\"'),(7811,9,23,2,'Lalu bertanyalah Daud kepada TUHAN: \"Apakah aku akan pergi mengalahkan orang Filistin itu?\" Jawab TUHAN kepada Daud: \"Pergilah, kalahkanlah orang Filistin itu dan selamatkanlah Kehila.\"'),(7812,9,23,3,'Tetapi orang-orang Daud berkata kepadanya: \"Ingatlah, sedangkan di sini di Yehuda kita sudah dalam ketakutan, apalagi kalau kita pergi ke Kehila, melawan barisan perang orang Filistin.\"'),(7813,9,23,4,'Lalu bertanya pulalah Daud kepada TUHAN, maka TUHAN menjawab dia, firman-Nya: \"Bersiaplah, pergilah ke Kehila, sebab Aku akan menyerahkan orang Filistin itu ke dalam tanganmu.\"'),(7814,9,23,5,'Kemudian pergilah Daud dengan orang-orangnya ke Kehila; ia berperang melawan orang Filistin itu, dihalaunya ternak mereka dan ditimbulkannya kekalahan besar di antara mereka. Demikianlah Daud menyelamatkan penduduk Kehila.'),(7815,9,23,6,'Ketika Abyatar bin Ahimelekh melarikan diri kepada Daud ke Kehila, ia turun dengan membawa efod di tangannya.'),(7816,9,23,7,'Kepada Saul diberitahukan, bahwa Daud telah masuk Kehila. Lalu berkatalah Saul: \"Allah telah menyerahkan dia ke dalam tanganku, sebab dengan masuk ke dalam kota yang berpintu dan berpalang ia telah mengurung dirinya.\"'),(7817,9,23,8,'Maka Saul memanggil seluruh rakyat pergi berperang ke Kehila dan mengepung Daud dengan orang-orangnya.'),(7818,9,23,9,'Ketika diketahui Daud, bahwa Saul berniat jahat terhadap dia, berkatalah ia kepada imam Abyatar: \"Bawalah efod itu ke mari.\"'),(7819,9,23,10,'Berkatalah Daud: \"TUHAN, Allah Israel, hamba-Mu ini telah mendengar kabar pasti, bahwa Saul berikhtiar untuk datang ke Kehila dan memusnahkan kota ini oleh karena aku.'),(7820,9,23,11,'Akan diserahkan oleh warga-warga kota Kehila itukah aku ke dalam tangannya? Akan datangkah Saul seperti yang telah didengar oleh hamba-Mu ini? TUHAN, Allah Israel, beritahukanlah kiranya kepada hamba-Mu ini.\" Jawab TUHAN: \"Ia akan datang.\"'),(7821,9,23,12,'Kemudian bertanyalah Daud: \"Akan diserahkan oleh warga-warga kota Kehila itukah aku dengan orang-orangku ke dalam tangan Saul?\" Firman TUHAN: \"Akan mereka serahkan.\"'),(7822,9,23,13,'Lalu bersiaplah Daud dan orang-orangnya, kira-kira enam ratus orang banyaknya, mereka keluar dari Kehila dan pergi ke mana saja mereka dapat pergi. Apabila kepada Saul diberitahukan, bahwa Daud telah meluputkan diri dari Kehila, maka tidak jadilah ia maju berperang.'),(7823,9,23,14,'Maka Daud tinggal di padang gurun, di tempat-tempat perlindungan. Ia tinggal di pegunungan, di padang gurun Zif. Dan selama waktu itu Saul mencari dia, tetapi Allah tidak menyerahkan dia ke dalam tangannya.'),(7824,9,23,15,'Daud takut, karena Saul telah keluar dengan maksud mencabut nyawanya. Ketika Daud ada di padang gurun Zif di Koresa,'),(7825,9,23,16,'maka bersiaplah Yonatan, anak Saul, lalu pergi kepada Daud di Koresa. Ia menguatkan kepercayaan Daud kepada Allah'),(7826,9,23,17,'dan berkata kepadanya: \"Janganlah takut, sebab tangan ayahku Saul tidak akan menangkap engkau; engkau akan menjadi raja atas Israel, dan aku akan menjadi orang kedua di bawahmu. Juga ayahku Saul telah mengetahui yang demikian itu.\"'),(7827,9,23,18,'Kemudian kedua orang itu mengikat perjanjian di hadapan TUHAN. Dan Daud tinggal di Koresa, tetapi Yonatan pulang ke rumahnya.'),(7828,9,23,19,'Tetapi beberapa orang Zif pergi menghadap Saul di Gibea dan berkata: \"Daud menyembunyikan diri dekat kami di kubu-kubu gunung dekat Koresa, di bukit Hakhila, di sebelah selatan padang belantara.'),(7829,9,23,20,'Oleh sebab itu, jika tuanku raja berkenan datang, silakanlah datang; tanggungan kamilah untuk menyerahkan dia ke dalam tangan raja.\"'),(7830,9,23,21,'Berkatalah Saul: \"Diberkatilah kiranya kamu oleh TUHAN, karena kamu menunjukkan sayangmu kepadaku.'),(7831,9,23,22,'Baiklah pergi, carilah kepastian lagi, berusahalah mengetahui di mana ia berada dan siapa yang telah melihat dia di sana; sebab telah dikatakan orang kepadaku, bahwa ia sangat cerdik.'),(7832,9,23,23,'Berusahalah mengetahui segala tempat persembunyiannya. Kemudian datanglah kembali kepadaku dengan kabar yang pasti; dan aku akan pergi bersama-sama dengan kamu. Sesungguhnya, jika ia ada di dalam negeri, maka aku akan meneliti dia di antara segala ribuan orang Yehuda.\"'),(7833,9,23,24,'Lalu berkemaslah mereka pergi ke Zif, mendahului Saul. Daud dan orang-orangnya ada di padang gurun Maon, di dataran di sebelah selatan padang belantara.'),(7834,9,23,25,'Ketika Saul dengan orang-orangnya pergi mencari Daud, diberitahukanlah hal itu kepada Daud, lalu pergilah ia ke gunung batu dan tinggal di padang gurun Maon. Saul mendengar hal itu, lalu mengejar Daud di padang gurun Maon;'),(7835,9,23,26,'Saul berjalan dari sisi gunung sebelah sini dan Daud dengan orang-orangnya dari sisi gunung sebelah sana. Daud cepat-cepat mengelakkan Saul; tetapi Saul dengan orang-orangnya sudah hampir mengepung Daud serta orang-orangnya untuk menangkap mereka,'),(7836,9,23,27,'ketika seorang suruhan datang kepada Saul dengan pesan: \"Segeralah undur, sebab orang Filistin telah menyerbu negeri.\"'),(7837,9,23,28,'Maka berhentilah Saul mengejar Daud dan pergi menghadapi orang Filistin. Itulah sebabnya orang menyebut tempat itu: Gunung Batu Keluputan.'),(7838,9,23,29,'(24-1) Daud pergi dari sana, lalu tinggal di kubu-kubu gunung di En-Gedi.'),(7839,9,24,1,'(24-2) Ketika Saul pulang sesudah memburu orang Filistin itu, diberitahukanlah kepadanya, demikian: \"Ketahuilah, Daud ada di padang gurun En-Gedi.\"'),(7840,9,24,2,'(24-3) Kemudian Saul mengambil tiga ribu orang yang terpilih dari seluruh orang Israel, lalu pergi mencari Daud dan orang-orangnya di gunung batu Kambing Hutan.'),(7841,9,24,3,'(24-4) Ia sampai ke kandang-kandang domba di tepi jalan. Di sana ada gua dan Saul masuk ke dalamnya untuk membuang hajat, tetapi Daud dan orang-orangnya duduk di bagian belakang gua itu.'),(7842,9,24,4,'(24-5) Lalu berkatalah orang-orangnya kepada Daud: \"Telah tiba hari yang dikatakan TUHAN kepadamu: Sesungguhnya, Aku menyerahkan musuhmu ke dalam tanganmu, maka perbuatlah kepadanya apa yang kaupandang baik.\" Maka Daud bangun, lalu memotong punca jubah Saul dengan diam-diam.'),(7843,9,24,5,'(24-6) Kemudian berdebar-debarlah hati Daud, karena ia telah memotong punca Saul;'),(7844,9,24,6,'(24-7) lalu berkatalah ia kepada orang-orangnya: \"Dijauhkan Tuhanlah kiranya dari padaku untuk melakukan hal yang demikian kepada tuanku, kepada orang yang diurapi TUHAN, yakni menjamah dia, sebab dialah orang yang diurapi TUHAN.\"'),(7845,9,24,7,'(24-8) Dan Daud mencegah orang-orangnya dengan perkataan itu; ia tidak mengizinkan mereka bangkit menyerang Saul. Sementara itu Saul telah bangun meninggalkan gua itu hendak melanjutkan perjalanannya.'),(7846,9,24,8,'(24-9) Kemudian bangunlah Daud, ia keluar dari dalam gua itu dan berseru kepada Saul dari belakang, katanya: \"Tuanku raja!\" Saul menoleh ke belakang, lalu Daud berlutut dengan mukanya ke tanah dan sujud menyembah.'),(7847,9,24,9,'(24-10) Lalu berkatalah Daud kepada Saul: \"Mengapa engkau mendengarkan perkataan orang-orang yang mengatakan: Sesungguhnya Daud mengikhtiarkan celakamu?'),(7848,9,24,10,'(24-11) Ketahuilah, pada hari ini matamu sendiri melihat, bahwa TUHAN sekarang menyerahkan engkau ke dalam tanganku dalam gua itu; ada orang yang telah menyuruh aku membunuh engkau, tetapi aku merasa sayang kepadamu karena pikirku: Aku tidak akan menjamah tuanku itu, sebab dialah orang yang diurapi TUHAN.'),(7849,9,24,11,'(24-12) Lihatlah dahulu, ayahku, lihatlah kiranya punca jubahmu dalam tanganku ini! Sebab dari kenyataan bahwa aku memotong punca jubahmu dengan tidak membunuh engkau, dapatlah kauketahui dan kaulihat, bahwa tanganku bersih dari pada kejahatan dan pengkhianatan, dan bahwa aku tidak berbuat dosa terhadap engkau, walaupun engkau ini mengejar-ngejar aku untuk mencabut nyawaku.'),(7850,9,24,12,'(24-13) TUHAN kiranya menjadi hakim di antara aku dan engkau, TUHAN kiranya membalaskan aku kepadamu, tetapi tanganku tidak akan memukul engkau;'),(7851,9,24,13,'(24-14) seperti peribahasa orang tua-tua mengatakan: Dari orang fasik timbul kefasikan. Tetapi tanganku tidak akan memukul engkau.'),(7852,9,24,14,'(24-15) Terhadap siapakah raja Israel keluar berperang? Siapakah yang kaukejar? Anjing mati! Seekor kutu saja!'),(7853,9,24,15,'(24-16) Sebab itu TUHAN kiranya menjadi hakim yang memutuskan antara aku dan engkau; Dia kiranya memperhatikannya, memperjuangkan perkaraku dan memberi keadilan kepadaku dengan melepaskan aku dari tanganmu.\"'),(7854,9,24,16,'(24-17) Setelah Daud selesai menyampaikan perkataan itu kepada Saul, berkatalah Saul: \"Suaramukah itu, ya anakku Daud?\" Sesudah itu dengan suara nyaring menangislah Saul.'),(7855,9,24,17,'(24-18) Katanya kepada Daud: \"Engkau lebih benar dari pada aku, sebab engkau telah melakukan yang baik kepadaku, padahal aku melakukan yang jahat kepadamu.'),(7856,9,24,18,'(24-19) Telah kautunjukkan pada hari ini, betapa engkau telah melakukan yang baik kepadaku: walaupun TUHAN telah menyerahkan aku ke dalam tanganmu, engkau tidak membunuh aku.'),(7857,9,24,19,'(24-20) Apabila seseorang mendapat musuhnya, masakan dilepaskannya dia berjalan dengan selamat? TUHAN kiranya membalaskan kepadamu kebaikan ganti apa yang kaulakukan kepadaku pada hari ini.'),(7858,9,24,20,'(24-21) Oleh karena itu, sesungguhnya aku tahu, bahwa engkau pasti menjadi raja dan jabatan raja Israel akan tetap kokoh dalam tanganmu.'),(7859,9,24,21,'(24-22) Oleh sebab itu, bersumpahlah kepadaku demi TUHAN, bahwa engkau tidak akan melenyapkan keturunanku dan tidak akan menghapuskan namaku dari kaum keluargaku.\"'),(7860,9,24,22,'(24-23) Lalu bersumpahlah Daud kepada Saul. Kemudian pulanglah Saul ke rumahnya, sedang Daud dan orang-orangnya pergi ke kubu gunung.'),(7861,9,25,1,'Dan matilah Samuel; seluruh orang Israel berkumpul meratapi dia dan menguburkan dia di rumahnya di Rama. Dan Daud berkemas, lalu pergi ke padang gurun Paran.'),(7862,9,25,2,'Ketika itu ada seorang laki-laki di Maon, yang mempunyai perusahaan di Karmel. Orang itu sangat kaya: ia mempunyai tiga ribu ekor domba dan seribu ekor kambing. Ia ada di Karmel pada pengguntingan bulu domba-dombanya.'),(7863,9,25,3,'Nama orang itu Nabal dan nama isterinya Abigail. Perempuan itu bijak dan cantik, tetapi laki-laki itu kasar dan jahat kelakuannya. Ia seorang keturunan Kaleb.'),(7864,9,25,4,'Ketika didengar Daud di padang gurun, bahwa Nabal sedang menggunting bulu domba-dombanya,'),(7865,9,25,5,'maka Daud menyuruh sepuluh orang dan kepada orang-orang itu Daud berkata: \"Pergilah ke Karmel dan temuilah Nabal. Tanyakanlah keselamatannya atas namaku'),(7866,9,25,6,'dan sampaikanlah salam ini kepadanya: Selamat! Selamatlah engkau, selamatlah keluargamu, selamatlah segala yang ada padamu.'),(7867,9,25,7,'Baru-baru ini aku mendengar bahwa engkau mengadakan pengguntingan bulu domba. Adapun gembala-gembalamu yang ada dengan kami, tidak kami ganggu dan tidak ada sesuatu yang hilang dari pada mereka selama mereka ada di Karmel.'),(7868,9,25,8,'Tanyakanlah kepada orang-orangmu, mereka tentu akan memberitahukan kepadamu. Sebab itu biarlah orang-orang ini mendapat belas kasihanmu; bukankah kami ini datang pada hari raya? Berikanlah kepada hamba-hambamu ini dan kepada anakmu Daud apa yang ada padamu.\"'),(7869,9,25,9,'Ketika orang-orang Daud sampai ke sana, berkatalah mereka kepada Nabal atas nama Daud tepat seperti yang dikatakan kepada mereka, kemudian mereka menanti.'),(7870,9,25,10,'Tetapi Nabal menjawab anak buah Daud itu, katanya: \"Siapakah Daud? Siapakah anak Isai itu? Pada waktu sekarang ini ada banyak hamba-hamba yang lari dari tuannya.'),(7871,9,25,11,'Masakan aku mengambil rotiku, air minumku dan hewan bantaian yang kubantai bagi orang-orang pengguntingku untuk memberikannya kepada orang-orang yang aku tidak tahu dari mana mereka datang?\"'),(7872,9,25,12,'Lalu orang-orang Daud itu berbalik pulang dan setelah sampai, mereka memberitahukan kepadanya tepat seperti yang dikatakan kepada mereka.'),(7873,9,25,13,'Kemudian berkatalah Daud kepada orang-orangnya: \"Kamu masing-masing, sandanglah pedang!\" Lalu mereka masing-masing menyandang pedangnya; Daud sendiripun menyandang pedangnya. Sesudah itu kira-kira empat ratus orang maju mengikuti Daud, sedang dua ratus orang tinggal menjaga barang-barang.'),(7874,9,25,14,'Tetapi kepada Abigail, isteri Nabal, telah diberitahukan oleh salah seorang bujangnya, katanya: \"Ketahuilah, Daud menyuruh orang dari padang gurun untuk memberi salam kepada tuan kita, tetapi ia memaki-maki mereka.'),(7875,9,25,15,'Padahal orang-orang itu sangat baik kepada kami; mereka tidak mengganggu kami dan kami tidak kehilangan apa-apa selama kami lalu-lalang di dekat mereka, ketika kami ada di ladang.'),(7876,9,25,16,'Mereka seperti pagar tembok sekeliling kami siang malam, selama kami menggembalakan domba-domba di dekat mereka.'),(7877,9,25,17,'Oleh sebab itu, pikirkanlah dan pertimbangkanlah apa yang harus kauperbuat, sebab telah diputuskan bahwa celaka akan didatangkan kepada tuan kita dan kepada seisi rumahnya, dan ia seorang yang dursila, sehingga orang tidak dapat berbicara dengan dia.\"'),(7878,9,25,18,'Lalu segeralah Abigail mengambil dua ratus roti, dua buyung anggur, lima domba yang telah diolah, lima sukat bertih gandum, seratus buah kue kismis dan dua ratus kue ara, dimuatnyalah semuanya ke atas keledai,'),(7879,9,25,19,'lalu berkata kepada bujang-bujangnya: \"Berjalanlah mendahului aku; aku segera menyusul kamu.\" Tetapi Nabal, suaminya, tidaklah diberitahunya.'),(7880,9,25,20,'Ketika perempuan itu dengan menunggang keledainya, turun dengan terlindung oleh gunung, tampaklah Daud dan orang-orangnya turun ke arahnya, dan perempuan itu bertemu dengan mereka.'),(7881,9,25,21,'Daud tadinya telah berkata: \"Sia-sialah aku melindungi segala kepunyaan orang ini di padang gurun, sehingga tidak ada sesuatupun yang hilang dari segala kepunyaannya; ia membalas kebaikanku dengan kejahatan.'),(7882,9,25,22,'Beginilah kiranya Allah menghukum Daud, bahkan lebih lagi dari pada itu, jika kutinggalkan hidup sampai pagi seorang laki-laki sajapun dari semua yang ada padanya.\"'),(7883,9,25,23,'Ketika Abigail melihat Daud, segeralah ia turun dari atas keledainya, lalu sujud menyembah di depan Daud dengan mukanya sampai ke tanah.'),(7884,9,25,24,'Ia sujud pada kaki Daud serta berkata: \"Aku sajalah, ya tuanku, yang menanggung kesalahan itu. Izinkanlah hambamu ini berbicara kepadamu, dan dengarkanlah perkataan hambamu ini.'),(7885,9,25,25,'Janganlah kiranya tuanku mengindahkan Nabal, orang yang dursila itu, sebab seperti namanya demikianlah ia: Nabal namanya dan bebal orangnya. Tetapi aku, hambamu ini, tidak melihat orang-orang yang tuanku suruh.'),(7886,9,25,26,'Oleh sebab itu, tuanku, demi TUHAN yang hidup dan demi hidupmu yang dicegah TUHAN dari pada melakukan hutang darah dan dari pada bertindak sendiri dalam mencari keadilan, biarlah menjadi sama seperti Nabal musuhmu dan orang yang bermaksud jahat terhadap tuanku!'),(7887,9,25,27,'Oleh sebab itu, pemberian yang dibawa kepada tuanku oleh budakmu ini, biarlah diberikan kepada orang-orang yang mengikuti tuanku.'),(7888,9,25,28,'Ampunilah kiranya kecerobohan hambamu ini, sebab pastilah TUHAN akan membangun bagi tuanku keturunan yang teguh, karena tuanku ini melakukan perang TUHAN dan tidak ada yang jahat terdapat padamu selama hidupmu.'),(7889,9,25,29,'Jika sekiranya ada seorang bangkit mengejar engkau dan ingin mencabut nyawamu, maka nyawa tuanku akan terbungkus dalam bungkusan tempat orang-orang hidup pada TUHAN, Allahmu, tetapi nyawa para musuhmu akan diumbankan-Nya dari dalam salang umban.'),(7890,9,25,30,'Apabila TUHAN melakukan kepada tuanku sesuai dengan segala kebaikan yang difirmankan-Nya kepadamu dan menunjuk engkau menjadi raja atas Israel,'),(7891,9,25,31,'maka tak usahlah tuanku bersusah hati dan menyesal karena menumpahkan darah tanpa alasan, dan karena tuanku bertindak sendiri dalam mencari keadilan. Dan apabila TUHAN berbuat baik kepada tuanku, ingatlah kepada hambamu ini.\"'),(7892,9,25,32,'Lalu berkatalah Daud kepada Abigail: \"Terpujilah TUHAN, Allah Israel, yang mengutus engkau menemui aku pada hari ini;'),(7893,9,25,33,'terpujilah kebijakanmu dan terpujilah engkau sendiri, bahwa engkau pada hari ini menahan aku dari pada melakukan hutang darah dan dari pada bertindak sendiri dalam mencari keadilan.'),(7894,9,25,34,'Tetapi demi TUHAN, Allah Israel yang hidup, yang mencegah aku dari pada berbuat jahat kepadamu--jika engkau tadinya tidak segera datang menemui aku, pasti tidak akan ada seorang laki-lakipun tinggal hidup pada Nabal sampai fajar menyingsing.\"'),(7895,9,25,35,'Lalu Daud menerima dari perempuan itu apa yang dibawanya untuk dia, dan berkata kepadanya: \"Pulanglah dengan selamat ke rumahmu; lihatlah, aku mendengarkan perkataanmu dan menerima permintaanmu dengan baik.\"'),(7896,9,25,36,'Sampailah Abigail kepada Nabal dan tampaklah, Nabal mengadakan perjamuan di rumahnya, seperti perjamuan raja-raja. Nabal riang gembira dan mabuk sekali. Sebab itu tidaklah diceriterakan perempuan itu sepatah katapun kepadanya, sampai fajar menyingsing.'),(7897,9,25,37,'Tetapi pada waktu pagi, ketika sudah hilang mabuk Nabal itu, diceriterakanlah kepadanya oleh isterinya segala perkara itu. Lalu terhentilah jantungnya dalam dada dan ia membatu.'),(7898,9,25,38,'Dan kira-kira sepuluh hari sesudah itu TUHAN memukul Nabal, sehingga ia mati.'),(7899,9,25,39,'Ketika didengar Daud, bahwa Nabal telah mati, berkatalah ia: \"Terpujilah TUHAN, yang membela aku dalam perkara penghinaan Nabal terhadap aku dan yang mencegah hamba-Nya dari pada berbuat jahat. TUHAN telah membalikkan kejahatan Nabal ke atas kepalanya sendiri.\" Kemudian Daud menyuruh orang untuk berbicara dengan Abigail tentang mengambil dia menjadi isterinya.'),(7900,9,25,40,'Para hamba Daud datang kepada Abigail di Karmel dan berkata kepadanya, demikian: \"Daud menyuruh kami kepadamu untuk mengambil engkau menjadi isterinya.\"'),(7901,9,25,41,'Lalu bangkitlah perempuan itu berdiri, sujudlah ia menyembah dengan mukanya ke tanah sambil berkata: \"Sesungguhnya, hambamu ini ingin menjadi budak yang membasuh kaki para hamba tuanku itu.\"'),(7902,9,25,42,'Kemudian berkemaslah Abigail dengan segera; ia menunggang keledainya, dengan diiringi lima orang pelayan perempuan. Ia mengikuti suruhan Daud itu dan menjadi isteri Daud.'),(7903,9,25,43,'Juga Ahinoam dari Yizreel telah diambil Daud menjadi isterinya; kedua perempuan itu menjadi isterinya.'),(7904,9,25,44,'Tetapi Saul telah memberikan Mikhal, anaknya perempuan, isteri Daud, kepada Palti bin Lais, yang dari Galim itu.'),(7905,9,26,1,'Datanglah orang Zif kepada Saul di Gibea serta berkata: \"Daud menyembunyikan diri di bukit Hakhila di padang belantara.\"'),(7906,9,26,2,'Lalu berkemaslah Saul dan turun ke padang gurun Zif dengan tiga ribu orang yang terpilih dari orang Israel untuk mencari Daud di padang gurun Zif.'),(7907,9,26,3,'Berkemahlah Saul di bukit Hakhila yang di tepi jalan di padang belantara, sedang Daud tinggal di padang gurun. Ketika diketahui Daud, bahwa Saul datang mengikuti dia ke padang gurun,'),(7908,9,26,4,'disuruhnyalah pengintai-pengintai, maka diketahuinyalah, bahwa Saul benar-benar datang.'),(7909,9,26,5,'Berkemaslah Daud, lalu sampai ke tempat Saul berkemah. Waktu Daud melihat tempat Saul berbaring dengan Abner bin Ner, panglima tentaranya, --Saul berbaring di tengah-tengah perkemahan, sedang rakyat berkemah sekelilingnya--'),(7910,9,26,6,'berbicaralah Daud kepada Ahimelekh, orang Het itu, dan kepada Abisai, anak Zeruya, saudara Yoab, katanya: \"Siapa turun bersama-sama dengan aku kepada Saul ke tempat perkemahan itu?\" Jawab Abisai: \"Aku turun bersama-sama dengan engkau.\"'),(7911,9,26,7,'Datanglah Daud dengan Abisai kepada rakyat itu pada waktu malam, dan tampaklah di sana Saul berbaring tidur di tengah-tengah perkemahan, dengan tombaknya terpancung di tanah pada sebelah kepalanya, sedang Abner dan rakyat itu berbaring sekelilingnya.'),(7912,9,26,8,'Lalu berkatalah Abisai kepada Daud: \"Pada hari ini Allah telah menyerahkan musuhmu ke dalam tanganmu, oleh sebab itu izinkanlah kiranya aku menancapkan dia ke tanah dengan tombak ini, dengan satu tikaman saja, tidak usah dia kutancapkan dua kali.\"'),(7913,9,26,9,'Tetapi kata Daud kepada Abisai: \"Jangan musnahkan dia, sebab siapakah yang dapat menjamah orang yang diurapi TUHAN, dan bebas dari hukuman?\"'),(7914,9,26,10,'Lagi kata Daud: \"Demi TUHAN yang hidup, niscaya TUHAN akan membunuh dia: entah karena sampai ajalnya dan ia mati, entah karena ia pergi berperang dan hilang lenyap di sana.'),(7915,9,26,11,'Kiranya TUHAN menjauhkan dari padaku untuk menjamah orang yang diurapi TUHAN. Ambillah sekarang tombak yang ada di sebelah kepalanya dan kendi itu, dan marilah kita pergi.\"'),(7916,9,26,12,'Kemudian Daud mengambil tombak dan kendi itu dari sebelah kepala Saul, lalu mereka pergi. Tidak ada yang melihatnya, tidak ada yang mengetahuinya, tidak ada yang terbangun, sebab sekaliannya tidur, karena TUHAN membuat mereka tidur nyenyak.'),(7917,9,26,13,'Setelah Daud sampai ke seberang, berdirilah ia jauh-jauh di puncak gunung, sehingga ada jarak yang besar antara mereka.'),(7918,9,26,14,'Dan berserulah Daud kepada tentara itu dan kepada Abner bin Ner, katanya: \"Tidakkah engkau menjawab, Abner?\" Maka jawab Abner, katanya: \"Siapakah engkau ini yang berseru-seru kepada raja?\"'),(7919,9,26,15,'Kemudian berkatalah Daud kepada Abner: \"Apakah engkau ini bukan laki-laki? Siapakah yang seperti engkau di antara orang Israel? Mengapa engkau tidak mengawal tuanmu raja? Sebab ada seorang dari rakyat yang datang untuk memusnahkan raja, tuanmu itu.'),(7920,9,26,16,'Tidak baik hal yang kauperbuat itu. Demi TUHAN yang hidup, kamu ini harus mati, karena kamu tidak mengawal tuanmu, orang yang diurapi TUHAN itu. Sekarang, lihatlah, di mana tombak raja dan kendi yang ada di sebelah kepalanya?\"'),(7921,9,26,17,'Saul mengenal suara Daud, lalu ia berkata: \"Suaramukah itu, anakku Daud?\" Jawab Daud: \"Suaraku, tuanku raja.\"'),(7922,9,26,18,'Lalu berkatalah ia: \"Mengapa pula tuanku mengejar hambanya ini? Apa yang telah kuperbuat? Apakah kejahatan yang melekat pada tanganku?'),(7923,9,26,19,'Oleh sebab itu, kiranya tuanku raja mendengarkan perkataan hambanya ini. Jika TUHAN yang membujuk engkau melawan aku, maka biarlah Ia mencium bau korban persembahan; tetapi jika itu anak-anak manusia, terkutuklah mereka di hadapan TUHAN, karena mereka sekarang mengusir aku, sehingga aku tidak mendapat bagian dari pada milik TUHAN, dengan berkata: Pergilah, beribadahlah kepada allah lain.'),(7924,9,26,20,'Sebab itu, janganlah kiranya darahku tertumpah ke tanah, jauh dari hadapan TUHAN. Sebab raja Israel keluar untuk mencabut nyawaku, seperti orang memburu seekor ayam hutan di gunung-gunung.\"'),(7925,9,26,21,'Lalu berkatalah Saul: \"Aku telah berbuat dosa, pulanglah, anakku Daud, sebab aku tidak akan berbuat jahat lagi kepadamu, karena nyawaku pada hari ini berharga di matamu. Sesungguhnya, perbuatanku itu bodoh dan aku sesat sama sekali.\"'),(7926,9,26,22,'Tetapi Daud menjawab: \"Inilah tombak itu, ya tuanku raja! Baiklah salah seorang dari orang-orangmu menyeberang untuk mengambilnya.'),(7927,9,26,23,'TUHAN akan membalas kebenaran dan kesetiaan setiap orang, sebab TUHAN menyerahkan engkau pada hari ini ke dalam tanganku, tetapi aku tidak mau menjamah orang yang diurapi TUHAN.'),(7928,9,26,24,'Dan sesungguhnya, seperti nyawamu pada hari ini berharga di mataku, demikianlah hendaknya nyawaku berharga di mata TUHAN, dan hendaknya Ia melepaskan aku dari segala kesusahan.\"'),(7929,9,26,25,'Lalu berkatalah Saul kepada Daud: \"Diberkatilah kiranya engkau, anakku Daud. Apa juapun yang kauperbuat, pastilah engkau sanggup melakukannya.\" Lalu pergilah Daud meneruskan perjalanannya dan pulanglah Saul ke tempatnya.'),(7930,9,27,1,'Tetapi Daud berpikir dalam hatinya: \"Bagaimanapun juga pada suatu hari aku akan binasa oleh tangan Saul. Jadi tidak ada yang lebih baik bagiku selain meluputkan diri dengan segera ke negeri orang Filistin; maka tidak ada harapan bagi Saul untuk mencari aku lagi di seluruh daerah Israel dan aku akan terluput dari tangannya.\"'),(7931,9,27,2,'Bersiaplah Daud, lalu berjalan ke sana, ia dan keenam ratus orang yang bersama-sama dengan dia itu, kepada Akhis bin Maokh, raja kota Gat.'),(7932,9,27,3,'Daud dan semua orangnya menetap pada Akhis di Gat, masing-masing dengan rumah tangganya; Daud dengan kedua orang isterinya, yakni Ahinoam, perempuan Yizreel, dan Abigail, bekas isteri Nabal, perempuan Karmel.'),(7933,9,27,4,'Setelah diberitahukan kepada Saul, bahwa Daud telah melarikan diri ke Gat, ia tidak lagi mencarinya.'),(7934,9,27,5,'Berkatalah Daud kepada Akhis: \"Jika kiranya aku mendapat belas kasihanmu, biarlah diberikan kepadaku tempat di salah satu kota di tanah datar, supaya aku tinggal di sana. Mengapa hambamu ini tinggal padamu di kota kerajaan ini?\"'),(7935,9,27,6,'Maka pada hari itu Akhis memberikan Ziklag kepadanya; itulah sebabnya Ziklag menjadi kepunyaan raja-raja Yehuda sampai sekarang.'),(7936,9,27,7,'Dan lamanya Daud tinggal di daerah orang Filistin adalah satu tahun empat bulan.'),(7937,9,27,8,'Maka Daud dan orang-orangnya bergerak maju dan menyerbu orang Gesur, orang Girzi dan orang Amalek; sebab orang-orang inilah penduduk negeri itu yang membentang dari Telam ke arah Syur sampai tanah Mesir.'),(7938,9,27,9,'Apabila Daud memusnahkan negeri itu, seorangpun tidak dibiarkannya hidup, baik laki-laki maupun perempuan; ia merampas kambing domba, lembu, keledai, unta dan pakaian, kemudian pulanglah ia dan kembali kepada Akhis.'),(7939,9,27,10,'Jika Akhis bertanya: \"Di mana kamu menyerbu pada hari ini?\" maka jawab Daud: \"Di Tanah Negeb Yehuda,\" atau: \"Di Tanah Negeb orang Yerahmeel,\" atau: \"Di Tanah Negeb orang Keni.\"'),(7940,9,27,11,'Daud tidak membiarkan hidup seorangpun, baik laki-laki maupun perempuan, untuk dibawa ke Gat, sebab pikirnya: \"Jangan-jangan mereka mengabarkan tentang kami, dengan berkata: Beginilah dilakukan Daud.\" Itulah kebiasaannya, selama ia tinggal di daerah orang Filistin.'),(7941,9,27,12,'Tetapi Akhis mempercayai Daud, sebab pikirnya: \"Tentulah ia telah membuat diri dibenci di antara orang Israel, bangsanya; ia akan menjadi hambaku sampai selamanya.\"'),(7942,9,28,1,'Pada waktu itu orang Filistin mengerahkan tentaranya untuk berperang melawan orang Israel. Lalu berkatalah Akhis kepada Daud: \"Ketahuilah baik-baik, bahwa engkau beserta orang-orangmu harus maju berperang bersama-sama dengan aku dalam tentara.\"'),(7943,9,28,2,'Jawab Daud kepada Akhis: \"Baik, engkau akan tahu, apa yang dapat diperbuat hambamu ini.\" Lalu Akhis berkata kepada Daud: \"Sebab itu aku mengangkat engkau menjadi pengawalku sendiri sampai selamanya.\"'),(7944,9,28,3,'Adapun Samuel sudah mati. Seluruh orang Israel sudah meratapi dia dan mereka telah menguburkan dia di Rama, di kotanya. Dan Saul telah menyingkirkan dari dalam negeri para pemanggil arwah dan roh peramal.'),(7945,9,28,4,'Orang Filistin itu berkumpul, lalu bergerak maju, dan berkemah dekat Sunem. Saul mengumpulkan seluruh orang Israel, lalu mereka berkemah di Gilboa.'),(7946,9,28,5,'Ketika Saul melihat tentara Filistin itu, maka takutlah ia dan hatinya sangat gemetar.'),(7947,9,28,6,'Dan Saul bertanya kepada TUHAN, tetapi TUHAN tidak menjawab dia, baik dengan mimpi, baik dengan Urim, baik dengan perantaraan para nabi.'),(7948,9,28,7,'Lalu berkatalah Saul kepada para pegawainya: \"Carilah bagiku seorang perempuan yang sanggup memanggil arwah; maka aku hendak pergi kepadanya dan meminta petunjuk kepadanya.\" Para pegawainya menjawab dia: \"Di En-Dor ada seorang perempuan yang sanggup memanggil arwah.\"'),(7949,9,28,8,'Lalu menyamarlah Saul, ia mengenakan pakaian lain dan pergilah ia dengan dua orang. Ketika mereka pada waktu malam sampai kepada perempuan itu, berkatalah Saul: \"Cobalah engkau menenung bagiku dengan perantaraan arwah, dan panggillah supaya muncul kepadaku orang yang akan kusebut kepadamu.\"'),(7950,9,28,9,'Tetapi perempuan itu menjawabnya: \"Tentu engkau mengetahui apa yang diperbuat Saul, bahwa ia telah melenyapkan dari dalam negeri para pemanggil arwah dan roh peramal. Mengapa engkau memasang jerat terhadap nyawaku untuk membunuh aku?\"'),(7951,9,28,10,'Lalu bersumpahlah Saul kepadanya demi TUHAN, katanya: \"Demi TUHAN yang hidup, tidak akan ada kesalahan tertimpa kepadamu karena perkara ini.\"'),(7952,9,28,11,'Sesudah itu bertanyalah perempuan itu: \"Siapakah yang harus kupanggil supaya muncul kepadamu?\" Jawabnya: \"Panggillah Samuel supaya muncul kepadaku.\"'),(7953,9,28,12,'Ketika perempuan itu melihat Samuel, berteriaklah ia dengan suara nyaring. Lalu perempuan itu berkata kepada Saul, demikian: \"Mengapa engkau menipu aku? Engkau sendirilah Saul!\"'),(7954,9,28,13,'Maka berbicaralah raja kepadanya: \"Janganlah takut; tetapi apakah yang kaulihat?\" Perempuan itu menjawab Saul: \"Aku melihat sesuatu yang ilahi muncul dari dalam bumi.\"'),(7955,9,28,14,'Kemudian bertanyalah ia kepada perempuan itu: \"Bagaimana rupanya?\" Jawabnya: \"Ada seorang tua muncul, berselubungkan jubah.\" Maka tahulah Saul, bahwa itulah Samuel, lalu berlututlah ia dengan mukanya sampai ke tanah dan sujud menyembah.'),(7956,9,28,15,'Sesudah itu berbicaralah Samuel kepada Saul: \"Mengapa engkau mengganggu aku dengan memanggil aku muncul?\" Kata Saul: \"Aku sangat dalam keadaan terjepit: orang Filistin berperang melawan aku, dan Allah telah undur dari padaku. Ia tidak menjawab aku lagi, baik dengan perantaraan nabi maupun dengan mimpi. Sebab itu aku memanggil engkau, supaya engkau memberitahukan kepadaku, apa yang harus kuperbuat.\"'),(7957,9,28,16,'Lalu berbicaralah Samuel: \"Mengapa engkau bertanya kepadaku, padahal TUHAN telah undur dari padamu dan telah menjadi musuhmu?'),(7958,9,28,17,'TUHAN telah melakukan kepadamu seperti yang difirmankan-Nya dengan perantaraanku, yakni TUHAN telah mengoyakkan kerajaan dari tanganmu dan telah memberikannya kepada orang lain, kepada Daud.');
insert  into `kitab_detail`(`id`,`id_kitab`,`pasal`,`ayat`,`teks`) values (7959,9,28,18,'Karena engkau tidak mendengarkan suara TUHAN dan tidak melaksanakan murka-Nya yang bernyala-nyala itu atas Amalek, itulah sebabnya TUHAN melakukan hal itu kepadamu pada hari ini.'),(7960,9,28,19,'Juga orang Israel bersama-sama dengan engkau akan diserahkan TUHAN ke dalam tangan orang Filistin, dan besok engkau serta anak-anakmu sudah ada bersama-sama dengan daku. Juga tentara Israel akan diserahkan TUHAN ke dalam tangan orang Filistin.\"'),(7961,9,28,20,'Pada saat itu juga rebahlah Saul memanjang ke tanah sebab ia sangat ketakutan oleh karena perkataan Samuel itu. Juga tidak ada lagi kekuatannya, karena sehari semalam itu ia tidak makan apa-apa.'),(7962,9,28,21,'Perempuan itu mendekati Saul lalu melihat, bahwa Saul sangat terkejut. Kemudian berkatalah perempuan itu kepadanya: \"Lihat, budakmu ini telah mendengarkan permintaanmu; aku telah mempertaruhkan nyawaku dan mendengarkan perkataan yang kaukatakan kepadaku.'),(7963,9,28,22,'Oleh sebab itu, kiranya engkaupun mendengarkan permintaan budakmu ini. Izinkanlah aku menyajikan kepadamu sepotong roti; makanlah, supaya ada kekuatanmu, apabila engkau berjalan pula.\"'),(7964,9,28,23,'Tetapi Saul menolak dan berkata: \"Aku tidak mau makan.\" Tetapi ketika para pegawainya serta perempuan itu juga mendesak, maka didengarkannyalah permintaan mereka, lalu bangkitlah ia dari tanah dan duduk di balai-balai.'),(7965,9,28,24,'Perempuan itu mempunyai seekor anak lembu tambun di rumahnya maka segeralah ia menyembelih itu. Ia mengambil tepung, diremasnya dan dibakarnya menjadi roti yang tidak beragi.'),(7966,9,28,25,'Dihidangkannyalah semuanya itu ke depan Saul dan ke depan para pegawainya, lalu mereka makan. Kemudian bangkitlah mereka dan pergi pada malam itu juga.'),(7967,9,29,1,'Orang Filistin mengumpulkan segala tentara mereka ke Afek, sedang orang Israel berkemah dekat mata air yang di Yizreel.'),(7968,9,29,2,'Maka ketika raja-raja kota orang Filistin berjalan lewat dalam pasukan-pasukan seratus dan seribu, dan ketika juga Daud beserta orang-orangnya berjalan lewat di belakangnya bersama-sama dengan Akhis,'),(7969,9,29,3,'berkatalah para panglima orang Filistin itu: \"Apa gunanya orang-orang Ibrani ini?\" Jawab Akhis kepada para panglima orang Filistin itu: \"Bukankah dia itu Daud, hamba Saul, raja Israel, yang sudah satu dua tahun bersama-sama dengan aku, tanpa kudapati sesuatupun kesalahan padanya sejak saat ia membelot sampai hari ini?\"'),(7970,9,29,4,'Tetapi para panglima orang Filistin itu menjadi marah kepadanya; serta berkata kepadanya: \"Suruhlah orang itu pulang, supaya ia kembali ke tempat, yang kautunjukkan kepadanya, dan janganlah ia pergi berperang, bersama-sama dengan kita, supaya jangan ia menjadi lawan kita dalam peperangan. Sebab dengan apakah orang ini dapat menyukakan hati tuannya, kecuali dengan memberi kepala-kepala orang-orang ini?'),(7971,9,29,5,'Bukankah dia ini Daud yang dinyanyikan orang secara berbalas-balasan sambil menari-nari, demikian: Saul mengalahkan beribu-ribu musuh, tetapi Daud berlaksa-laksa?\"'),(7972,9,29,6,'Lalu Akhis memanggil Daud, dan berkata kepadanya: \"Demi TUHAN yang hidup, engkau ini orang jujur dan aku memandang baik, jika engkau keluar masuk bersama-sama dengan aku dalam tentara, sebab aku tidak mendapati sesuatu kejahatan padamu, sejak saat engkau datang kepadaku sampai hari ini; tetapi engkau ini tidak disukai oleh raja-raja kota.'),(7973,9,29,7,'Sebab itu, pulanglah, pergilah dengan selamat dan jangan lakukan apa yang jahat di mata raja-raja kota orang Filistin itu.\"'),(7974,9,29,8,'Tetapi Daud berkata kepada Akhis: \"Apa yang telah kuperbuat? Dan kesalahan apa yang kaudapati pada hambamu ini, sejak saat aku menjadi hamba kepadamu, sampai hari ini, sehingga aku tidak boleh ikut pergi berperang melawan musuh tuanku raja?\"'),(7975,9,29,9,'Lalu Akhis menjawab Daud: \"Aku tahu, engkau ini memang kusukai seperti utusan Allah. Hanya, para panglima orang Filistin telah berkata: Ia tidak boleh pergi berperang bersama-sama dengan kita.'),(7976,9,29,10,'Jadi, bangunlah pagi-pagi beserta orang-orang tuanmu ini yang datang bersama-sama dengan engkau; bangunlah kamu pagi-pagi, segera sesudah hari cukup terang bagimu, dan pergilah.\"'),(7977,9,29,11,'Lalu bangunlah Daud dan orang-orangnya pagi-pagi untuk berjalan pulang ke negeri orang Filistin, sedang orang Filistin itu bergerak maju ke Yizreel.'),(7978,9,30,1,'Ketika Daud serta orang-orangnya sampai ke Ziklag pada hari yang ketiga, orang Amalek telah menyerbu Tanah Negeb dan Ziklag; Ziklag telah dikalahkan oleh mereka dan dibakar habis.'),(7979,9,30,2,'Perempuan-perempuan dan semua orang yang ada di sana, tua dan muda, telah ditawan mereka, dengan tidak membunuh seorangpun; mereka menggiring sekaliannya, kemudian meneruskan perjalanannya.'),(7980,9,30,3,'Ketika Daud dan orang-orangnya sampai ke kota itu, tampaklah kota itu terbakar habis, dan isteri mereka serta anak mereka yang laki-laki dan perempuan telah ditawan.'),(7981,9,30,4,'Lalu menangislah Daud dan rakyat yang bersama-sama dengan dia itu dengan nyaring, sampai mereka tidak kuat lagi menangis.'),(7982,9,30,5,'Juga kedua isteri Daud ditawan, yakni Ahinoam, perempuan Yizreel, dan Abigail, bekas isteri Nabal, orang Karmel itu.'),(7983,9,30,6,'Dan Daud sangat terjepit, karena rakyat mengatakan hendak melempari dia dengan batu. Seluruh rakyat itu telah pedih hati, masing-masing karena anaknya laki-laki dan perempuan. Tetapi Daud menguatkan kepercayaannya kepada TUHAN, Allahnya.'),(7984,9,30,7,'Lalu Daud memberi perintah kepada imam Abyatar bin Ahimelekh: \"Bawalah efod itu kepadaku.\" Maka Abyatar membawa efod itu kepada Daud.'),(7985,9,30,8,'Kemudian bertanyalah Daud kepada TUHAN, katanya: \"Haruskah aku mengejar gerombolan itu? Akan dapatkah mereka kususul?\" Dan Ia berfirman kepadanya: \"Kejarlah, sebab sesungguhnya, engkau akan dapat menyusul mereka dan melepaskan para tawanan.\"'),(7986,9,30,9,'Lalu pergilah Daud beserta keenam ratus orang yang bersama-sama dengan dia, dan sampailah mereka ke sungai Besor. Sementara orang-orang yang mau tinggal di belakang berhenti di sana,'),(7987,9,30,10,'maka Daud melanjutkan pengejaran itu beserta empat ratus orang. Dua ratus orang yang terlalu lelah untuk menyeberangi sungai Besor itu, berhenti di sana.'),(7988,9,30,11,'Kemudian mereka menemui seorang Mesir di padang lalu membawanya kepada Daud. Mereka memberi dia roti, lalu makanlah ia, kemudian mereka memberi dia minum air,'),(7989,9,30,12,'dan memberikan kepadanya sepotong kue ara dan dua buah kue kismis, dan setelah dimakannya, ia segar kembali, sebab ia tidak makan dan minum selama tiga hari tiga malam.'),(7990,9,30,13,'Kemudian bertanyalah Daud kepadanya: \"Budak siapakah engkau dan dari manakah engkau?\" Jawabnya: \"Aku ini seorang pemuda Mesir, budak kepunyaan seorang Amalek. Tuanku meninggalkan aku, karena tiga hari yang lalu aku jatuh sakit.'),(7991,9,30,14,'Kami telah menyerbu Tanah Negeb orang Kreti dan daerah Yehuda dan Tanah Negeb Kaleb, dan Ziklag telah kami bakar habis.\"'),(7992,9,30,15,'Daud bertanya kepadanya: \"Dapatkah engkau menunjuk jalan kepadaku ke gerombolan itu?\" Katanya: \"Bersumpahlah kepadaku demi Allah, bahwa engkau tidak akan membunuh aku, dan tidak akan menyerahkan aku ke dalam tangan tuanku itu, maka aku akan menunjuk jalan kepadamu ke gerombolan itu.\"'),(7993,9,30,16,'Ia menunjuk jalan kepada Daud ke sana, dan tampaklah orang-orang itu berpencar-pencar di atas seluruh daerah itu, sambil makan, minum dan mengadakan perayaan karena jarahan yang besar, yang telah dirampas mereka dari tanah orang Filistin dan dari tanah Yehuda.'),(7994,9,30,17,'Dan pada keesokan harinya Daud menghancurkan mereka dari pagi-pagi buta sampai matahari terbenam; tidak ada seorangpun dari mereka yang lolos, kecuali empat ratus orang muda yang melarikan diri dengan menunggang unta.'),(7995,9,30,18,'Daud melepaskan semua apa yang dirampas oleh orang Amalek itu; juga kedua isterinya dapat dilepaskan Daud.'),(7996,9,30,19,'Tidak ada yang hilang pada mereka, dari hal yang kecil sampai hal yang besar, sampai anak laki-laki dan anak perempuan, dan dari jarahan sampai segala sesuatu yang telah dirampas mereka; semuanya itu dibawa Daud kembali.'),(7997,9,30,20,'Daud mengambil segala kambing domba dan lembu; semuanya itu digiring mereka di hadapannya, serta berkata: \"Inilah jarahan Daud.\"'),(7998,9,30,21,'Ketika Daud sampai kepada kedua ratus orang yang telah terlalu lelah untuk mengikuti Daud, yang telah dibiarkannya tinggal di dekat sungai Besor, maka keluarlah orang-orang ini menyongsong Daud dan menyongsong rakyat yang bersama-sama dengan dia. Daud mendekati orang-orang itu dan memberi salam kepada mereka.'),(7999,9,30,22,'Kemudian mulailah berbicara semua orang jahat dan orang dursila di antara orang-orang, yang ikut pergi bersama-sama dengan Daud itu, katanya: \"Karena mereka tidak ikut pergi bersama-sama dengan kita, janganlah kita berikan kepada mereka apa-apa dari jarahan yang kita selamatkan itu, kecuali kepada masing-masing mereka isterinya dan anak-anaknya. Itu boleh mereka bawa, dan biarlah mereka pergi!\"'),(8000,9,30,23,'Tetapi Daud berkata: \"Janganlah kamu, saudara-saudaraku, berbuat demikian, dengan apa yang diberikan TUHAN kepada kita; sebab Ia telah melindungi kita, dan menyerahkan ke dalam tangan kita gerombolan yang menyerang kita.'),(8001,9,30,24,'Siapa yang mau mendengarkan kamu dalam perkara ini? Sebab, bagian orang yang tinggal di dekat barang-barang adalah sama seperti bagian orang yang pergi berperang; itu akan dibagi sama-sama.\"'),(8002,9,30,25,'Dan demikianlah halnya sejak hari itu dan seterusnya; hal itu ditentukannya menjadi ketetapan dan peraturan bagi orang Israel sampai sekarang.'),(8003,9,30,26,'Ketika Daud sampai ke Ziklag, dikirimnyalah sebagian dari jarahan itu kepada para tua-tua di Yehuda, kepada teman-temannya, dengan pesan: \"Inilah pemberian kepadamu dari jarahan yang dirampas dari musuh TUHAN,\"'),(8004,9,30,27,'yakni kepada yang di Betel, kepada yang di Ramot di Tanah Selatan, kepada yang di Yatir,'),(8005,9,30,28,'kepada yang di Aroer, kepada yang di Sifmot, kepada yang di Estemoa,'),(8006,9,30,29,'kepada yang di Rakhal, kepada yang di kota-kota orang Yerahmeel, kepada yang di kota-kota orang Keni,'),(8007,9,30,30,'kepada yang di Horma, kepada yang di Bor-Asan, kepada yang di Atakh,'),(8008,9,30,31,'kepada yang di Hebron dan kepada segala tempat di mana Daud dengan orang-orangnya mengembara.'),(8009,9,31,1,'Sementara itu orang Filistin berperang melawan orang Israel. Orang-orang Israel melarikan diri dari hadapan orang Filistin dan banyak yang mati terbunuh di pegunungan Gilboa.'),(8010,9,31,2,'Orang Filistin terus mengejar Saul dan anak-anaknya dan menewaskan Yonatan, Abinadab dan Malkisua, anak-anak Saul.'),(8011,9,31,3,'Kemudian makin beratlah pertempuran itu bagi Saul; para pemanah menjumpainya, dan melukainya dengan parah.'),(8012,9,31,4,'Lalu berkatalah Saul kepada pembawa senjatanya: \"Hunuslah pedangmu dan tikamlah aku, supaya jangan datang orang-orang yang tidak bersunat ini menikam aku dan memperlakukan aku sebagai permainan.\" Tetapi pembawa senjatanya tidak mau, karena ia sangat segan. Kemudian Saul mengambil pedang itu dan menjatuhkan dirinya ke atasnya.'),(8013,9,31,5,'Ketika pembawa senjatanya melihat, bahwa Saul telah mati, iapun menjatuhkan dirinya ke atas pedangnya, lalu mati bersama-sama dengan Saul.'),(8014,9,31,6,'Jadi Saul, ketiga anaknya dan pembawa senjatanya, dan seluruh tentaranya sama-sama mati pada hari itu.'),(8015,9,31,7,'Ketika dilihat orang-orang Israel, yang di seberang lembah dan yang di seberang sungai Yordan, bahwa tentara Israel telah melarikan diri, dan bahwa Saul serta anak-anaknya sudah mati, maka mereka meninggalkan kota-kota mereka lalu melarikan diri juga; kemudian datanglah orang Filistin dan menetap di sana.'),(8016,9,31,8,'Ketika keesokan harinya orang Filistin datang merampasi orang-orang yang mati terbunuh itu, didapati mereka Saul dan ketiga anaknya tergelimpang di pegunungan Gilboa.'),(8017,9,31,9,'Mereka memancung kepala Saul, merampas senjata-senjatanya dan menyuruh orang berkeliling di negeri orang Filistin untuk menyampaikan kabar itu di kuil berhalanya dan kepada rakyat.'),(8018,9,31,10,'Kemudian mereka menaruh senjata-senjata Saul di kuil Asytoret, dan mayatnya dipakukan mereka di tembok kota Bet-Sean.'),(8019,9,31,11,'Ketika penduduk Yabesh-Gilead mendengar tentang apa yang telah dilakukan orang Filistin kepada Saul,'),(8020,9,31,12,'maka bersiaplah segenap orang gagah perkasa, mereka berjalan terus semalam-malaman, lalu mengambil mayat Saul dan mayat anak-anaknya dari tembok kota Bet-Sean. Kemudian pulanglah mereka ke Yabesh dan membakar mayat-mayat itu di sana.'),(8021,9,31,13,'Mereka mengambil tulang-tulangnya lalu menguburkannya di bawah pohon tamariska di Yabesh. Sesudah itu berpuasalah mereka tujuh hari lamanya.'),(8022,42,1,1,'Di mula ni mulana ditompa Debata langit dohot tano on.'),(8023,42,1,2,'Dung i tarulang ma tano i gabe halongonan jala holom di atas lung i, dung i mangareapreap ma Tondi ni Debata di atas ni angka aek.'),(8024,42,1,3,'Dung i mangkuling ma Debata ninna: Jadi ma ho na tiur! Jadi tiur ma tutu.'),(8025,42,1,4,'Nunga denggan na tiur i diida Debata, jadi dibahen Debata ma parsirangan ni na tiur sian na holom.'),(8026,42,1,5,'Dung i digoar Debata ma na tiur i arian, goar ni na holom i borngin. Dung i bot ma ari, torang ma ari muse, i ma ari parjolo.'),(8027,42,1,6,'Dung i mangkuling muse ma Debata: Jadi ma ho hajangkajang di parsitongaan ni angka aek bahen parsirangan ni aek tu aek.'),(8028,42,1,7,'Jadi dipauli Debata ma hajangkajang i, asa disirang aek angka na di toru ni hajangkajang sian aek angka na di ginjang ni hajangkajang i, gabe manjadi ma songon i.'),(8029,42,1,8,'Dung i digoar Debata ma hajangkajang i langit. Dung i bot ma ari, torang ma ari muse, i ma ari paduahon.'),(8030,42,1,9,'Dung i ninna Debata ma: Marpungu ma hamu aek, angka na di toru ni langit i tu sada inganan, asa tarida na mahiang! Gabe manjadi ma songon i.'),(8031,42,1,10,'Dung i digoar Debata ma na mahiang i tano, ia parpunguan ni angka aek laut digoar. Jadi nunga denggan diida Debata.'),(8032,42,1,11,'Dung i ninna Debata ma: Dipantumburhon tano i ma duhutduhut dohot suansuanan, na marparbuehon boni, hau angka na marparbuehon bonina di bagasan tudos tu mansamna be di tano on, gabe manjadi ma songon i.'),(8033,42,1,12,'Jadi dipantubuhon tano i ma duhutduhut dohot suansuanan, na marparbuehon bonina tudos tu mansamna dohot hau na marparbuehon bonina di bagasan, tudos tu mansamna. Dung i nunga denggan diida Debata.'),(8034,42,1,13,'Jadi bot ma ari, torang ma ari muse, i ma ari patoluhon.'),(8035,42,1,14,'Dung i mangkuling muse ma Debata: Jadi ma hamu angka panondang di hajangkajang ni langit i, bahen parsirangan ni arian sian borngin, gabe tanda paboa titik ni padan dohot angka ari dohot angka taon.'),(8036,42,1,15,'Jala gabe panondang angka i di hajangkajang ni langit i, asa dipatiur tano on; dung i manjadi ma songon i.'),(8037,42,1,16,'Dung i dipauli Debata ma dua panondang angka na bolon; panondang na bolon, pangguruan ni arian, dohot panondang na metmet, pangguruan ni borngin, ro di angka bintang.'),(8038,42,1,17,'Dung i dibahen Debata ma nasida tu hajangkajang ni langit i, asa disondangi tano on.'),(8039,42,1,18,'Jala manggomgomi arian dohot borngin, jala bahen parsirangan ni na tiur sian na holom. Dung i nunga denggan diida Debata.'),(8040,42,1,19,'Jadi bot ma ari, torang ma ari muse, i ma ari paopathon.'),(8041,42,1,20,'Dung i mangkuling muse ma Debata: Dipantubuhon angka aek i ma gulokgulok na marhosa, angka na manjirir, jala marhabangan ma angka pidong di atas tano on martongatonga langit.'),(8042,42,1,21,'Dung i ditompa Debata ma angka dengke na balga dohot nasa na marhosa, angka na manjiriri di bagasan aek, masiboan mansamna ro di nasa pidong angka na masiboan mansamna. Jadi nunga denggan diida Debata.'),(8043,42,1,22,'Dung i dipasupasu Debata ma nasida, ninna ma: Marpinompari ma hamu, asa lam torop jala gohi hamu ma angka aek ni laut, lam torop ma dohot angka pidong di atas tano on.'),(8044,42,1,23,'Jadi bot ma ari, torang ma ari muse; i ma ari palimahon.'),(8045,42,1,24,'Dung i mangkuling muse ma Debata: Dipantubuhon tano i ma hosa angka na mangolu masiboan mansamna: Angka dorbia dohot gulokgulok dohot binatang masiboan mansamna, gabe manjadi ma songon i.'),(8046,42,1,25,'Dung i dipauli Debata ma angka binatang ni tano on masiboan mansamna, dohot angka dorbia masiboan mansamna, dohot gulokgulok ni tano on masiboan mansamna. Jadi nunga denggan diida Debata.'),(8047,42,1,26,'Dung i mangkuling muse ma Debata: Tole ma, itatompa ma jolma, tumiru rupanta, tudos tu pangalahonta, asa dirajai dengke angka na di laut, dohot pidong angka na martongatonga langit, dohot angka dorbia, ro di sandok tano on nang sude gulokgulok, angka na manjirir di atas tano on.'),(8048,42,1,27,'Jadi ditompa Debata ma jolma i tumiru rupana, rupana ditiruanhon Debata laho manompa ibana, baoa dohot boruboru nasida ditompa.'),(8049,42,1,28,'Dung i dipasupasu Debata ma nasida, ninna Debata ma tu nasida: Marpinompari ma hamu, asa sopar hamu, jala gohi hamu sandok tano on, jala patunduk hamu; jala raja i hamu dengke angka na di laut dohot pidong angka na martongatonga langit ro di sude binatang, angka na manjirir di atas tano on.'),(8050,42,1,29,'Dung i ninna Debata ma: Boti ma i, nunga hulehon tu hamu nasa suansuanan na marparbuehon boni di sandok tano on, ro di sude hau angka na marparbue sisuanon, bahen panganonmuna ma i.'),(8051,42,1,30,'Alai anggo tu sude angka binatang ni tano on dohot tu angka pidong na martongatonga langit, ro di sude na manjirir di atas tano on, angka na marhosa di bagasan: Bulung ni angka suansuanan (hulehon) bahen panganonna, gabe manjadi ma songon i.'),(8052,42,1,31,'Dung i ditilik Debata ma sude na tinompana i, jadi nunga denggan situtu. Dung i bot ma ari, torang ma ari muse, i ma ari paonomhon.'),(8053,42,2,1,'Asa sun ma langit dohot tano on ro di isina sudena.'),(8054,42,2,2,'Jadi dung sun dibahen Debata jadijadianna i di ari papituhon, maradian ma Ibana di ari papituhon sian nasa ulaonna, na binahenna i.'),(8055,42,2,3,'Dung i dipasupasu Debata ma ari papituhon i, jala diparbadiai, ala na maradian Ibana disi sian nasa jadijadianna, na tinompa ni Debata laho pasunhon.'),(8056,42,2,4,'Songon i ma panjadijadi ni langit dohot tano, di na tartompa angka i, di ari panompaon ni Debata Jahowa di tano dohot langit.'),(8057,42,2,5,'Tagan so adong dope di tano on nasa ramba, jala tagan so martumbur dope nasa bulungbulung di ladang, ai ndang dope diparo Debata Jahowa udan tu tano on, jala ndang adong dope jolma na mangula tano i.'),(8058,42,2,6,'Alai martimpul ma uap sian tano on, gabe dibornohi sandok sisik ni tano on.'),(8059,42,2,7,'Dung i dijadihon Debata Jahowa ma jolma i sian orbuk ni tano on jala diullushon tu bagasan bungbung ni igungna hosa hangoluan i, gabe martondi na mangolu jolma i.'),(8060,42,2,8,'Dung i dipungka Debata Jahowa ma sada porlak di Eden tartungkan habinsaran, jadi tusi ma dibahen jolma na jinadihonna i.'),(8061,42,2,9,'Dung i dipantubuhon Debata Jahowa ma sian tano i nasa hau, angka na denggan idaon, jala tabo panganon, ro di hau hangoluan na di parsitongaan ni porlak i, nang hau parbinotoan ni na denggan dohot na roa.'),(8062,42,2,10,'Dung i ruar ma sada batang aek sian Eden i, asa dibornohi porlak i, manirpang ma sian i gabe opat batang aek bolon.'),(8063,42,2,11,'Goar ni parjolo: Pison, na mangaliati sandok tano Hevila, panombangan sere disi.'),(8064,42,2,12,'Jala meram situtu sere ni na saluat i. Jumpang do huhut disi landus dohot batu soham.'),(8065,42,2,13,'Ia goar ni batang aek paduahon: Gihon, na mangaliati sandok tano Kus.'),(8066,42,2,14,'Ia goar ni batang aek patoluhon: Hidekel (Tigris), na mamolus tungkan habinsaran sian Assur. Ia batang aek paopathon, i ma Euprat.'),(8067,42,2,15,'Dung i ditogihon Debata Jahowa ma jolma i paojakkon ibana tu porlak Eden i, asa dipaulaula jala diramoti.'),(8068,42,2,16,'Dung i dipatik Debata Jahowa ma jolma i, ninna ma: Jadi do panganonmu sian nasa hau di bagasan porlak on.'),(8069,42,2,17,'Alai anggo sian hau parbinotoan ni na denggan dohot na roa, ndang jadi panganonmu; ai sadari dipangan ho sian i, sai na mate do ho.'),(8070,42,2,18,'Dung i ninna Debata Jahowa ma: Ndang jadi punjung jolma i; hubahen ma di ibana sada pangurupina bahen donganna.'),(8071,42,2,19,'Ai dung sun dijadihon Debata Jahowa sian tano i nasa binatang di ladang ro di sude pidong angka na martongatonga langit, ditogihon ma i tu si Adam, asa diida manang beha goaranna; jala ia i dibahen si Adam goar ni nasa na marhosa laos i do goarna.'),(8072,42,2,20,'Jadi dibahen si Adam ma goar ni nasa pahanpahanan ro di pidong angka na martongatonga langit dohot nasa na manggulmit di tano on. Alai anggo di si Adam ndang adong jumpang pangurupi bahen donganna.'),(8073,42,2,21,'Dung i tarlalap modom ma si Adam dibahen Debata Jahowa, jadi dienet ma sada sian rusukna, jala dilangkophon sibuk tu pambuatanna i.'),(8074,42,2,22,'Dung i dipauli Debata Jahowa rusuk na binuatna i sian si Adam, bahen boruboru, jala ditogu ibana tu si Adam.'),(8075,42,2,23,'Dung i ninna si Adam ma: On do sahali holiholi sian angka holiholingku, jala sibuk sian sibukku; goaron ma ibana: Jolma ni baoa, ala sian baoa ibana binuat.'),(8076,42,2,24,'Dibahen i tadingkonon ni baoa do amana dohot inana mandongani jolmana, jadi sada sibuk nasida.'),(8077,42,2,25,'Marsaemara do nasida duansa, si Adam dohot jolmana i, jala ndang maila nasida.'),(8078,42,3,1,'Alai ulok do na umbisuk sian nasa na manggulmit di ladang, angka na jinadihon ni Debata Jahowa, jadi didok ma mandok boruboru i: Atehe tongon do didok Debata, na so jadi panganonmuna sian saluhut hau angka na di porlak on?'),(8079,42,3,2,'Dung i ninna boruboru i ma mandok ulok i: Jadi do panganonnami sian parbue ni hau angka na di porlak on.'),(8080,42,3,3,'Alai anggo sian parbue ni hau, na di parsitongaan ni porlak i didok Debata do: Unang pangan hamu sian i, jala unang jama hamu, asa unang mate hamu.'),(8081,42,3,4,'Dung i ninna ulok i ma mandok boruboru i: Ndang na tutu hamu mate.'),(8082,42,3,5,'Ai diboto Debata do, sadari dipangan hamu sian i, tonggor ma matamuna, gabe doshon Debata hamu umboto na denggan dohot na roa.'),(8083,42,3,6,'Dung i sai dipanotnoti boruboru i ma parbue ni hau i dibahen dengganna panganon jala pia matana marnida, jala tau manunggul roha tu hapistaran hau i, jadi diputikkon sian parbuena i jala dipangan, dilehon ma dohot tu sinondukna sian i, gabe dipangan.'),(8084,42,3,7,'Dung i tonggor ma matanasida na dua, diboto ma na marsaemara nasida, gabe disomoti ma bulung ni hau ara bahen sabuknasida.'),(8085,42,3,8,'Dung i dibege nasida ma soara ni Debata Jahowa, na medangedang di porlak i di haroro ni alogo mareak bot ari, gabe martabuni ma si Adam dohot jolmana i di holangkolang ni hau angka na di porlak i maradophon bohi ni Debata Jahowa.'),(8086,42,3,9,'Dung i joujou ma Debata Jahowa di si Adam, ninna ma mandok ibana: Didia do ho?'),(8087,42,3,10,'Jadi ninna si Adam ma: Hubege nian suaram di porlak i, gabe mabiar ahu, ai marsaemara do ahu, umbahen na martabuni ahu.'),(8088,42,3,11,'Dung i ninna Debata ma: Ise na paboahon tu ho, na marsaemara i ho? Nda tung dipangan ho sian hau na hupatikkon i mangorai panganonmu?'),(8089,42,3,12,'Dung i ninna si Adam ma: Boruboru na nilehonmu bahen donganku, i do na mangalehon tu ahu sian hau i, gabe hupangan.'),(8090,42,3,13,'Dung i ninna Debata Jahowa ma tu boruboru i: Boasa songon i bahenonmu? Jadi ninna boruboru i ma: Ulok i do paotooto ahu, gabe hupangan.'),(8091,42,3,14,'Dung i ninna Debata Jahowa ma tu ulok i: Ala dibahen ho songon i gumodang ma torungku tu ho sian tu nasa pahanpahanan dohot sian tu nasa na manggulmit di ladang. Butuham na ma jururhononmu, jala orbuk panganonmu saleleng di ngolum.'),(8092,42,3,15,'Jala bahenonku ma parbadaanmu dohot boruboru i, ro di pinomparmu tu Pinomparna. Ibana mangalisathon ulum, gabe pargutonmu tambihulna.'),(8093,42,3,16,'Dung i ninna ma mandok boruboru i: Ganda situtu bahenonku parniahapanmu saleleng gabegabean ho; bernit taononmu laho manubuhon anakkonmu, sinondukmu na ma ihutanmu, jala ibana mangarajai ho.'),(8094,42,3,17,'Jala didok ma tu si Adam: Ala ditangihon ho soara ni jolmam, gabe dipangan ho sian hau na hupatikkon i, mandok: Unang pangan sian i, hona torungku ma tano on ala ho, porsuk ma ahaponmu manganhon gogona saleleng di ngolum.'),(8095,42,3,18,'Pantubuhononna ma huhut di ho angka duriduri dohot duhutduhut na marsuga, angka ingkau rata ni ladang i na ma panganonmu.'),(8096,42,3,19,'Ingkon hir hodokmu manganhon sipanganonmu, paima mulak ho gabe tano, pambuatanmu hian, ai tano do ho, jala tu tano do ho mulak sogot.'),(8097,42,3,20,'Dung i dibahen si Adam ma goar ni jolmana i: Hawa (hangoluan), ala ibana ina ni nasa na mangolu.'),(8098,42,3,21,'Dung i dipauli Debata Jahowa ma bobak bahen abit ni si Adam dohot jolmana i, jala dialithon tu nasida.'),(8099,42,3,22,'Dung i ninna Debata Jahowa ma: Ida ma, nunga doshon Sada sian Hami si Adam, umboto na denggan dohot na roa. On pe, asa unang didungdungkon tanganna mamutikkon huhut sian hau hangoluan jala dipangan gabe mangolu salelenglelengna.'),(8100,42,3,23,'Jadi dipabali Debata Jahowa ibana sian porlak Eden, asa diula tano pambuatanna i.'),(8101,42,3,24,'Jadi dilele ma si Adam i, jala dipaipahon angka kerubim tungkan habinsaran ni porlak Eden i marpodang sidua baba marjimburjimbur, asa dijaga dalan tu hau hangoluan.'),(8102,42,4,1,'Dung i sampur ma si Adam dohot si Hawa, jolmana i, jadi gabegabean ma ibana, ditubuhon ma si Kain, jala ninna ma: Nunga maruli ahu sada baoa sian Jahowa.'),(8103,42,4,2,'Dung i diulahi ma manubuhon si Abel anggina i; gabe parmahan birubiru ma si Abel, alai siula hauma anggo si Kain.'),(8104,42,4,3,'Dung i di laonlaon ni ari, diboan si Kain ma sian parbue ni haumana bahen pelean tu Jahowa.'),(8105,42,4,4,'Jadi dohot ma si Abel mamboan sian buhabaju ni birubiruna dohot sian tabotabona. Jadi lomo ma roha ni Jahowa mamereng si Abel dohot na pinelehonna i.'),(8106,42,4,5,'Alai anggo si Kain dohot na pinelehonna i, ndang dibereng. Jadi pusu ma roha ni si Kain jala rupi bohina.'),(8107,42,4,6,'Dung i ninna Jahowa ma mandok si Kain: Boasa tung pusu roham, jala boasa rupi bohim?'),(8108,42,4,7,'Nda songon on do? Molo na denggan diula ho i ma hadirgakon ni bohi; alai molo so diula ho na denggan, mangonggop dosa i di lambung pintu mangkalungunhon ho, alai ingkon ho mangarajaisa.'),(8109,42,4,8,'Dung i mangkatai ma si Kain dohot si Abel, anggina i. Alai dung ro di hauma nasida, hehe ma si Kain dompak si Abel anggina i, jala dibunu ibana.'),(8110,42,4,9,'Jadi ninna Jahowa ma tu si Kain: Didia do si Abel anggim i? Ndang huboto, ninna, tung ahu ma siparorot anggingki?'),(8111,42,4,10,'Alai ninna Jahowa ma: Aha do na binahenmi? Maungaung do sian tano i mudar ni anggim sahat tu Ahu.'),(8112,42,4,11,'Onpe gumodang ma torungku tu ho asa tu tano i, na pangangang babana jumalo mudar ni anggim sian tanganmi.'),(8113,42,4,12,'Molo diula ho tano i, ndang be lehononna gogona tu ho, mompasompas jala madoado na ma ho di tano on.'),(8114,42,4,13,'Dung i ninna si Kain ma mandok Jahowa: Ndang be tarsesa dosangki dibahen balgana.'),(8115,42,4,14,'Ndi, nunga dipabali Ho ahu sadari on sian tano on, jadi martabuni na ma ahu maradophon Bohim, gabe mompasompas jala madoado na ma ahu di tano on. Jadi manang ise na jumpangan ahu, bunuonna ma ahu.'),(8116,42,4,15,'Alai ninna Jahowa ma mandok ibana: Antong manang ise pe na mambunu si Kain, pitu lompit abulonna. Dung i dibahen Jahowa ma sada tanda tu si Kain, asa unang dibunu manang ise na jumpangan ibana.'),(8117,42,4,16,'Dung i manginsurut ma si Kain sian Bohi ni Jahowa laho maringan tu tano Nod, na tungkan habinsaran sian porlak Eden.'),(8118,42,4,17,'Dung i sampur ma si Kain dohot jolmana, jadi gabegabean ma ibana, jala ditubuhon si Hanok. Dung i dipungka ma sada sosor dibahen ma goar ni sosor i Hanok, mangihuthon goar ni anakna i.'),(8119,42,4,18,'Dung i ditubuhon si Hanok ma si Irad; si Irad manubuhon si Mehuyael; si Mehuyael manubuhon si Metusael; si Metusael manubuhon si Lamek.'),(8120,42,4,19,'Alai anggo si Lamek dua do jolmana dibuat, goar ni na sada si Ada, goar ni paduahon si Zilla.'),(8121,42,4,20,'Dung i ditubuhon si Ada ma si Jabal, ibana do ompu ni sude angka na mangingani undungundung dohot na marpinahan.'),(8122,42,4,21,'Ia goar ni anggina i si Jubal, ibana do ompu ni sude angka sipiltik hasapi dohot sordam.'),(8123,42,4,22,'Si Zilla pe ditubuhon do si Tubal Kain, pande ni sude angka sipauli tombaga dohot bosi; ia iboto ni si Tubal Kain si Naema do.'),(8124,42,4,23,'Dung i ninna si Lamek ma tu jolmana na dua i, si Ada dohot si Zilla: Bege hamu ma soarangku, hamu ale jolma ni si Lamek! Pinggol hamu ma torsangkon! Sai na bunuonku do sada baoa ala ni bugangki, jala sada dolidoli ala ni bosarhon.'),(8125,42,4,24,'Ai molo pitu hali abulon ia si Kain, ingkon pitupulu hali pitu abulon anggo si Lamek.'),(8126,42,4,25,'Dung i sampur ma si Adam muse dohot jolmana i, jadi ditubuhon ma anak sada, dibahen ma goarna si Set, ai nunga disolukkon Debata tu ahu sada tubu na asing, singkat ni si Abel, na binunu ni si Kain, ninna.'),(8127,42,4,26,'Tubu ma dohot anak ni si Set sada, dibahen ma goarna si Enos. Uju i ma dipungka halak pajoujou goar ni Jahowa.'),(8128,42,5,1,'On ma buku ni angka sundut ni si Adam, di ari panompaon ni Debata di si Adam, tudos tu rupa ni Debata ibana ditompa.'),(8129,42,5,2,'Baoa dohot boruboru nasida ditompa, jala dipasupasu nasida, jala dibahen goarnasida jolma, di ari panompaonnasida i.'),(8130,42,5,3,'Jadi dung saratus tolupulu taon mangolu si Adam, tubu ma anakna, tudos tu rupana, songon pangalahona, dibahen ma goarna si Set.'),(8131,42,5,4,'Ia umur ni si Adam, dungkonsa ditubuhon si Set ualu ratus taon do, jala ditubuhon anak dohot boru.'),(8132,42,5,5,'Jadi sia ratus tolupulu taon ma sandok umur ni si Adam mangolu, dung i mate ma ibana.'),(8133,42,5,6,'Dung saratus lima taon leleng ni si Set mangolu, ditubuhon ma si Enos.'),(8134,42,5,7,'Ia leleng ni si Set mangolu, dung ditubuhon si Enos, ualu ratus pitu taon do, jala ditubuhon anak dohot boru.'),(8135,42,5,8,'Jadi sia ratus sampulu dua taon ma sandok umur ni si Set, dung i mate ma ibana.'),(8136,42,5,9,'Dung siapulu taon leleng ni si Enos mangolu, ditubuhon ma si Kenan.'),(8137,42,5,10,'Ia leleng ni si Enos mangolu, dung ditubuhon si Kenan, ualu ratus sampulu lima taon do, jala ditubuhon anak dohot boru.'),(8138,42,5,11,'Jadi sia ratus lima taon sandok umur ni si Enos, dung i mate ma ibana.'),(8139,42,5,12,'Dung pitupulu taon leleng ni si Kenan mangolu, ditubuhon ma si Mahalaleel.'),(8140,42,5,13,'Ia leleng ni si Kenan mangolu dung ditubuhon si Mahalaleel ualu ratus opatpulu taon do, jala ditubuhon anak dohot boru.'),(8141,42,5,14,'Jadi sia ratus sampulu taon ma sandok umur ni si Kenan, dung i mate ma ibana.'),(8142,42,5,15,'Dung onompulu lima taon leleng ni si Mahalaleel mangolu, ditubuhon ma si Jared.'),(8143,42,5,16,'Ia leleng ni si Mahalaleel mangolu, dung ditubuhon si Jared, ualu ratus tolupulu taon, jala ditubuhon anak dohot boru.'),(8144,42,5,17,'Jadi ualu ratus siapulu lima taon sandok umur ni si Mahalaleel, dung i mate ma ibana.'),(8145,42,5,18,'Dung saratus onompulu dua taon leleng ni si Jared mangolu, ditubuhon ma si Henok.'),(8146,42,5,19,'Ia leleng ni si Jared mangolu, dung ditubuhon si Henok, ualu ratus taon, jala ditubuhon anak dohot boru.'),(8147,42,5,20,'Jadi sia ratus onompulu dua taon sandok umur ni si Jared, dung i mate ma ibana.'),(8148,42,5,21,'Dung onompulu lima taon leleng ni si Henok mangolu, ditubuhon ma si Metusalah.'),(8149,42,5,22,'Jadi dung ditubuhon si Metusalah i, sai marparange, mardonganhon Debata ma si Henok tolu ratus taon lelengna, jala ditubuhon anak dohot boru.'),(8150,42,5,23,'Jadi tolu ratus onompulu lima taon sandok umur ni si Henok.'),(8151,42,5,24,'Jala sai marparange, mardonganhon Debata ma si Henok, dung i ndang disi be ibana, ai dialap Debata do ibana.'),(8152,42,5,25,'Dung saratus ualupulu pitu taon leleng ni si Metusalah mangolu, ditubuhon ma si Lamek.'),(8153,42,5,26,'Ia leleng ni si Metusalah mangolu, dung ditubuhon si Lamek, pitu ratus ualupulu dua taon do, jala ditubuhon anak dohot boru.'),(8154,42,5,27,'Jadi sia ratus onompulu sia taon sandok umur ni si Metusalah, dung i mate ma ibana.'),(8155,42,5,28,'Dung saratus ualupulu dua taon leleng ni si Lamek mangolu, ditubuhon ma sada anak.'),(8156,42,5,29,'Dung i dibahen ma goarna si Noak, ninna ma: On ma mangapul hita di ulaonta dohot di haloloja ni tanganta di tano on, na hona toru ni Jahowa.'),(8157,42,5,30,'Ia leleng ni si Lamek mangolu, dung ditubuhon si Noak, lima ratus siapulu lima taon do, jala ditubuhon anak dohot boru.'),(8158,42,5,31,'Jadi pitu ratus pitupulu pitu taon sandok umur ni si Lamek, dung i mate ma ibana.'),(8159,42,5,32,'Dung lima ratus taon umur ni si Noak, ditubuhon ma si Sem, si Ham dohot si Japet.'),(8160,42,6,1,'Jadi dung mamungka lam torop jolma i di tano on, jala tubuan boru nasida.'),(8161,42,6,2,'Diririt angka anak ni Debata ma boru ni jolma, ala na uli do nasida, jala dibuat bahen jolmanasida guru pamillitnasida.'),(8162,42,6,3,'Jadi ninna Jahowa ma: Ndang be uhum on ni Tondingku jolma i di haliluonnasida, ai roha daging do ibana, jala saratus duapulu taon na ma umurna.'),(8163,42,6,4,'Adong ma uju i angka Nepilim (pandobo) di tano on. Ai dung ditopot angka anak ni Debata angka boru ni jolma, adong ma huhut tubunasida, i ma angka na begu, angka baoa na tarbarita sian na robi.'),(8164,42,6,5,'Alai dung diida Jahowa, nunga lam deak hajahaton ni jolma di tano on, jala sai na jat na ma sude tubutubu dohot pingkiran ni rohanasida gonop ari.'),(8165,42,6,6,'Jadi disolsoli Jahowa ma rohana, ala tinompana i jolma di tano on, jala lungun rohana.'),(8166,42,6,7,'Dung i ninna Jahowa ma: Ripashononku ma sian tano on jolma na hutompa i, ro di pinahan dohot na manjirir, ro di pidong angka na martongatonga langit, ai nunga husolsoli bagingku ala na hutompa i nasida.'),(8167,42,6,8,'Alai asi do roha ni Jahowa ianggo marnida si Noak.'),(8168,42,6,9,'On ma pinompar ni si Noak: Jolma na bonar jala na daulat roha do si Noak i maradophon donganna magodang, sai marparange mardonganhon Debata do si Noak i.'),(8169,42,6,10,'Dung i ditubuhon si Noak ma anak tolu, si Sem, si Ham dohot si Japet.'),(8170,42,6,11,'Alai nunga marjea ianggo tano i maradophon Debata, jala nunga gok hajahaton tano i.'),(8171,42,6,12,'Dung i ditingkir Debata ma tano i, jala ida ma, nunga marjea: Ai tung saluhut na mardaging paroa pangalahona di tano i.'),(8172,42,6,13,'Jadi ninna Debata ma tu si Noak: Nunga ro ajal ni nasa na mardaging maradophon Ahu, ai nunga gok hajahaton tano on dibahen nasida. Onpe ripashononku ma nasida rap dohot tano on.'),(8173,42,6,14,'Bahen ma di ho sada parau sian hau goper (marantibane), bahen ma marbilut parau i jala puli sian bagasan dohot sian balian.'),(8174,42,6,15,'Songon on ma singana bahenonmu: Tolu ratus elak unjur ni parau i, limapulu elak bohangna, jala tolupulu elak timbona.'),(8175,42,6,16,'Bahen tartungkan ginjang sada pandiloan ni parau i, saelak timbona: Alai anggo pintu ni parau i tu lambungna bahen, jala tolu lampis bilutna bahen, sada di toru, sada di tongatonga, jala sada di ginjang.'),(8176,42,6,17,'Ai ida, paroonku ma tu tano on aek na sumar laho mangaripashon nasa na mardaging, na marhosa hangoluan di bagasan, angka na di toru ni langit, asa tos hosa ni saluhut na di tano on.'),(8177,42,6,18,'Alai pajongjongonku ma padanhu dohot ho, bongotanmu ma parau i, ho dohot angka anakmu dohot jolmam dohot jolma ni angka anakmi rap dohot ho.'),(8178,42,6,19,'Jala pamasuk ma tu bagasan parau i sapasang be sian nasa na mangolu, sian nasa na mardaging, tunggal dohot boruboru, asa mangolu rap dohot ho.'),(8179,42,6,20,'Sian pidong masiboan mansamna, sian pinahan pe masiboan mansamna, sian nasa na manjirir di tano i, masiboan mansamna; sapasang be sian luhutna ro tu ho, asa mangolu.'),(8180,42,6,21,'Alai buat di ho sian nasa sipanganon, angka na tarpangan, jala papungu tu ho bahen bohalmu dohot bohalnasida.'),(8181,42,6,22,'Dung i dibahen si Noak ma sude, songon na tinonahon ni Debata tu ibana, songon i dibahen.'),(8182,42,7,1,'Dung i ninna Jahowa ma tu si Noak: Bongot i ma parau i, ho dohot sude isi ni bagasmu, ai ho do huida na bonar maradophon Ahu di na sansundut on.'),(8183,42,7,2,'Buat ma di ho pitu pasang be, tunggal dohot boruboruna sian nasa pinahan na ganjing; alai anggo sian pinahan na so ganjing sapasang be, tunggal dohot boruboruna.'),(8184,42,7,3,'Sian pidong pe, angka na martonga langit, pitu pasang be, tunggal dohot boruboruna, asa adong tinggal boni mangolu di sandok tano on.'),(8185,42,7,4,'Ai pitu ari nari paroonku ma udan tu tano on opatpulu ari opatpulu borngin lelengna, asa huripashon sian tano on nasa na mangolu, na hutompa i.'),(8186,42,7,5,'Jadi dibahen si Noak ma sude, songon na tinonahon ni Jahowa tu ibana.'),(8187,42,7,6,'Alai onom ratus taon ma umur ni si Noak di na ro i aek na sumar i tu tano on.'),(8188,42,7,7,'Dung i dibongoti si Noak ma parau i rap dohot anakna dohot jolmana, ro di jolma ni angka anakna i di jolo ni aek na sumar i.'),(8189,42,7,8,'Sian pinahan angka na ganjing dohot sian pinahan na so ganjing dohot sian pidong dohot sian nasa na manjirir di tano on.'),(8190,42,7,9,'Angka ro be marpasangpasang mandapothon si Noak, tu bagasan parau i, tunggal dohot boruboru, songon na tinonahon ni Debata tu si Noak i.'),(8191,42,7,10,'Jadi dung salpu na pitu ari i, ro ma aek na sumar tu tano on.'),(8192,42,7,11,'Paonomratus taonhon di ngolu ni si Noak i, di bulan sipahadua di ari sampulupitu, apala di na sadari i marbuhalbuhal ma sude angka mual ni lung godang, mungkap ma dohot angka pandiloan ni langit.'),(8193,42,7,12,'Jadi masa ma udan tipotipo tu tano on opatpulu ari opatpulu borngin lelengna.'),(8194,42,7,13,'Apala di na sadari i do bongot si Noak tu parau i rap dohot angka anakna, si Sem, si Ham dohot si Japet, dohot jolmana dohot na tolu jolma ni angka anakna i.'),(8195,42,7,14,'Nasida dohot binatang marragam masiboan mansamna, dohot angka pinahan masiboan mansamna dohot sude angka na manjirir di tano on masiboan mansamna, dohot sude angka pidong masiboan mansamna, sude pidong na marende dohot na marhabong.'),(8196,42,7,15,'Angka ro be marpasangpasang tu si Noak, tu bagasan parau i, sian nasa na mardaging, angka na marhosa hangoluan di bagasan.'),(8197,42,7,16,'Angka na bongot i, sai marpasang be do ro sian nasa na mardaging, songon na tinonahon ni Debata tu ibana. Dung i dihinsu Jahowa ma sian pudina.'),(8198,42,7,17,'Jadi ro ma aek na sumar tu tano on opatpulu ari, gabe lam binsat ma aek i, jala dihindathon parau i sian tano on tu ginjang.'),(8199,42,7,18,'Dung i lam mangalantap ma aek i jala sumar situtu di tano on, gabe meapeap parau i di atas aek i.'),(8200,42,7,19,'Jadi lam binsat situtu ma aek i di tano on gabe disalpui sude dolok, angka na timbo na di toru ni sandok langit i.'),(8201,42,7,20,'Sampululima elak aek i tumimbo sian dolok, angka na sinalpuanna.'),(8202,42,7,21,'Jadi tos ma hosa ni nasa na mardaging, na manjirir di tano on, ro di pidong dohot pinahan dohot binatang, ro di sude na manggulmit di tano on, ro di jolma sudena.'),(8203,42,7,22,'Mate ma sude na marhosa hangoluan di bagasan igungna, sian sude angka na di darat.'),(8204,42,7,23,'Jadi ripas ma nasa na adong di tano on, sian jolma ro di pinahan, ro di gulokgulok ro di pidong na martongatonga langit, ripas do saluhutna sian tano on; holan si Noak tading angkup ni na rap dohot ibana di bagasan parau i.'),(8205,42,7,24,'Dung i sai mangalantap ma aek i di tano on maningtingi saratus limapulu ari lelengna.'),(8206,42,8,1,'Dung i diingot Debata ma si Noak dohot sude binatang ro di sude pinahan, angka na rap dohot ibana di bagasan parau i. Jadi dipabolushon Debata ma alogo tu tano i, asa moru aek i.'),(8207,42,8,2,'Dipolpol ma dohot angka mual ni lung i dohot angka pandiloan ni langit, jala dihomat udan tipotipo sian langit i.'),(8208,42,8,3,'Jadi lam moru ma aek i sian tano on, salpu saratus limapulu borngin, nunga lam moru angka aek i.'),(8209,42,8,4,'Asa dung jumpang ari sampulu pitu di bulan sipahapitu, sangkot ma parau i di punsu ni dolok Ararat.'),(8210,42,8,5,'Dung i sai lam tu moruna aek i ro di bulan sipahasampulu; di artia di bulan sipahasampulu tarida ma angka punsu ni dolok.'),(8211,42,8,6,'Salpu muse opatpulu borngin, diungkap si Noak ma pandiloan ni parau na binahenna i.'),(8212,42,8,7,'Dipalua ma sada sigak, sai mulahulak do i, paima marsik aek i sian tano on.'),(8213,42,8,8,'Dung i dipalua ma sian ibana sada darapati, asa diida manang naung marsik aek i sian sisik ni tano on.'),(8214,42,8,9,'Alai ndang jumpang darapati hasonggopan ni palak ni patna, jadi mulak ma tu ibana, tu bagasan parau i, ai mangalantap dope aek i di sisik ni sandok tano on. Jadi disurdukkon si Noak ma tanganna, jala ditangkup darapati i manait tu ibana, tu bagasan parau i.'),(8215,42,8,10,'Dung i dipaima pitu borngin nari, diulahi ma paluahon sada darapati sian parau i.'),(8216,42,8,11,'Mareak bot ari mulak ma darapati i tu ibana, jala ida ma, sada bulung ni hau jetun na rata di tuktukna. Jadi diboto si Noak ma naung marsik aek i sian tano on.'),(8217,42,8,12,'Dung i dipaima pitu borngin nari, asa dipalua muse sada darapati, alai ndang be mulak tu ibana.'),(8218,42,8,13,'Dung i paonomratussadataonhon, di artia di bulan sipahasada, marsik situtu ma aek i sian tano on. Jadi diungkap si Noak ma tarup ni parau i, gabe diida ma naung mahiang sisik ni tano on.'),(8219,42,8,14,'Jumpang ari duapulu pitu di bulan sipahadua, mahiang ma tano i.'),(8220,42,8,15,'Jadi ninna Debata ma mandok si Noak:'),(8221,42,8,16,'Ruar ma ho sian parau i, ho dohot jolmam dohot angka anakmu dohot jolma ni anakmu rap dohot ho.'),(8222,42,8,17,'Paruar ma rap dohot ho nasa binatang sian nasa na mardaging, angka pidong dohot pinahan dohot nasa na manjirir di tano i, asa rodot nasida, jala lam torop di tano on.'),(8223,42,8,18,'Jadi ruar ma si Noak rap dohot anakna dohot jolmana dohot jolma ni angka anakna i.'),(8224,42,8,19,'Ruar ma sian parau i dohot sude binatang, sude gulokgulok dohot sude pidong, sude na manjirir di tano on masiboan mansamna be.'),(8225,42,8,20,'Dung i dipajok si Noak ma sada langgatan di Jahowa, jala dibuat sian nasa pinahan na ganjing dohot sian nasa pidong na ganjing bahen pelean situtungon, pelehononna di langgatan i.'),(8226,42,8,21,'Jadi dung dianggo Jahowa uap na hushus i, ninna Jahowa ma di bagasan rohana: Ndang be ulahanku manorui tano on ala jolma i, ai na jat do pingkiran ni roha ni jolma sian sietehetehonna; jala ndang be ulahanku mangaragehon nasa na mangolu, songon na hubahen i.'),(8227,42,8,22,'Onpe, saleleng adong tano on, ndang jadi mansohot saburon dohot gotilon, ari ngali dohot ari las, ari logo dohot rondo ni ari, arian dohot borngin.'),(8228,42,9,1,'Dung i dipasupasu Debata ma si Noak dohot angka anakna, ninna ma mandok nasida: Gabe ma hamu, asa sopar, jala gohi hamu ma tano on.'),(8229,42,9,2,'Marhabiaran jala jimbolangon ma marnida hamu sude binatang di tano on, ro di sude pidong angka na martongatonga langit dohot nasa na manjirir di tano on, ro di dengke ni laut sudena, sahat tu tanganmu ma nasida.'),(8230,42,9,3,'Nasa na manggulmit, na mangolu bahen panganonmuna, sahat tu hamu ma i sudena hulehon, songon bulung rata.'),(8231,42,9,4,'Alai unang pangan hamu juhut, molo di bagasan dope hosana, mandok mudarna i.'),(8232,42,9,5,'Tongon do, sai luluhononku do mudarmuna marguru tu hosamuna i; sai luluhononku do sian tangan ni nasa binatang dohot sian tangan ni jolma; sian tangan ni baoa donganna i luluhononku hosa ni jolma i.'),(8233,42,9,6,'Manang ise na mandurushon mudar ni jolma, durushonon ni jolma ma mudarna; ai tudos tu rupa ni Debata jolma i ditompa Debata.'),(8234,42,9,7,'Alai gabe ma anggo hamu asa sopar, lam rarat ma hamu di tano on, jala lam sopar disi.'),(8235,42,9,8,'Dung i ninna Debata ma mandok si Noak dohot tu anakna angka na rap dohot ibana, botima i:'),(8236,42,9,9,'Ida ma anggo Ahu, pahotonku ma padanhu tu hamu ro di pinomparmuna sogot.'),(8237,42,9,10,'Dohot tu nasa na marhosa na mangolu angka na rap dohot hamu, angka pidong, pinahan dohot nasa binatang ni tano on, angka na rap dohot hamu, sian luhut angka na ruar sian parau ro di sude pahanpahanan ni tano on.'),(8238,42,9,11,'Pahotonku ma padanhu dohot hamu, paboa na so jadi be ripashonon ni aek na sumar nasa na mardaging, huhut paboa na so masa be aek na sumar i mangaripashon tano on.'),(8239,42,9,12,'Dung i ninna Debata ma: On ma tanda ni padan na hubahen i tu hamu dohot tu nasa na marhosa na mangolu, angka na rap dohot hamu ro di salelenglelengna:'),(8240,42,9,13,'Halibutonganki, na hubahen i di ombun, i ma tanda ni padanhu dohot tano on.'),(8241,42,9,14,'Onpe, molo huparo ombun tu tano on, sai tarida ma halibutongan i di ombun.'),(8242,42,9,15,'Jadi ingotonku ma padan na hubahen i tu hamu dohot tu nasa na marhosa na mangolu, ro di sude na mardaging, paboa na so pamasaonku be aek na sumar i mangaripashon nasa na mardaging.'),(8243,42,9,16,'Sai bahenonku do halibutongan i tu ombun, asa hubereng ibana marningot padan salelenglelengna binahen ni Debata tu nasa na marhosa na mangolu, tu nasa na mardaging, na di tano on.'),(8244,42,9,17,'Dung i ninna Debata ma tu si Noak: On ma tanda ni padan na hupahot tu nasa na mardaging, na di tano on.'),(8245,42,9,18,'Ianggo anak ni si Noak, angka na ruar sian parau i, on do: Si Sem, si Ham dohot si Japet. Alai anggo si Ham ama ni si Kanaan do i.'),(8246,42,9,19,'Na tolu i do anak ni si Noak, jala sian nasida do rarat jolma i tu sandok tano on.'),(8247,42,9,20,'Dung i dipungka si Noak ma sada porlak anggur, ai paruma do ibana.'),(8248,42,9,21,'Dung i diinum ma deba anggur i jadi mabuk, gabe dipasalang ibana di tongatonga ni undungundungna.'),(8249,42,9,22,'Jadi diida si Ham, ama ni si Kanaan, parsaemaraon ni amana, gabe dipaboa ma tu hahana na dua i na di balian.'),(8250,42,9,23,'Dung i dibuat si Sem dohot si Japet sada ulos, jala diampehon tu abaranasida na dua, dung i mardalan pudi ma nasida laho manghungkupi parsaemaraon ni amanasida, bohinasida pe tundal do, asa unang diida parsaemaraon ni amanasida i.'),(8251,42,9,24,'Dung tarjolma si Noak sian mabukna jala diboto na binahen ni anakna sianggian i tu ibana.'),(8252,42,9,25,'Jadi ninna ma: Hona bura ma si Kanaan, jampurut ni angka naposo ni angka hahana ma ibana.'),(8253,42,9,26,'Angkup ni didok ma: Pinuji ma Jahowa, Debata ni si Sem, jala naposona ma anggo si Kanaan.'),(8254,42,9,27,'Dipararat Debata ma ia si Japet, asa maringan ibana di bagasan undungundung ni si Sem, jala naposona ma anggo si Kanaan i.'),(8255,42,9,28,'Ia leleng ni si Noak mangolu, dung aek na sumar i tolu ratus limapulu taon do.'),(8256,42,9,29,'Jadi sia ratus limapulu taon ma sandok umur ni si Noak, dung i mate ma ibana.'),(8257,42,10,1,'On do angka sundut ni anak ni si Noak: Si Sem, si Ham dohot si Japet jala tubuan anak be do nasida, dungkonsa aek na sumar i.'),(8258,42,10,2,'Angka anak ni si Japet: Si Gomer, si Magog, si Madai, si Javan, si Tubal, si Mesek dohot si Tiras.'),(8259,42,10,3,'Ia anak ni si Gomer i: Si Askenas, si Ripat dohot si Togarma.'),(8260,42,10,4,'Ia anak ni si Javan i: Si Elisa, si Tarsis, si Kittim dohot si Dodanim.'),(8261,42,10,5,'Sian nasida rarat angka bangso tu pulopulo mangihuthon luatna be dohot pangkulingna dohot margana dohot bangsona.'),(8262,42,10,6,'Ia anak ni si Ham: Si Kus, si Mizraim, Put dohot si Kanaan.'),(8263,42,10,7,'Ia anak ni si Kus i: Si Seba, Hevila, Sapta, Raama dohot Saptika. Ia anak ni si Raama i: Si Seba dohot si Dedan.'),(8264,42,10,8,'Alai ia si Kus manubuhon si Nimrod, na mamungka marsiajar ulubalang di tano on.'),(8265,42,10,9,'Parburu na begu ibana maradophon Jahowa. Ala ni do didok halak: Parburu na begu maradophon Jahowa, doshon si Nimrod.'),(8266,42,10,10,'Ia mula ni harajaonna i, i ma Babel, Erik, Akkad dohot Kabne, angka na di luat Sinear.'),(8267,42,10,11,'Sian luat i borhat muse si Assur, jala dipungka huta Ninive dohot Rehobot Ir dohot Kala.'),(8268,42,10,12,'Angkup ni Resen, di holangkolang ni Ninive dohot Kala, i ma huta na bolon.'),(8269,42,10,13,'Dung i ditubuhon si Mizraim ma halak Lud, halak Anam, halak Lehab dohot halak Naptu.'),(8270,42,10,14,'Dohot halak Patrus, dohot halak Kaslu, haroroan ni halak Palistim dohot Kaptor.'),(8271,42,10,15,'Ia si Kanaan ditubuhon ma si Sidon, anak buhabajuna i dohot si Het.'),(8272,42,10,16,'Dohot si Jebusi, si Amori, si Girgasi.'),(8273,42,10,17,'Dohot si Hevi, si Arki dohot si Sini.'),(8274,42,10,18,'Dohot si Arvadi, si Zemari, si Hamati, dung salpu i marserak ma angka marga ni halak Kanaan.'),(8275,42,10,19,'Ia parbolatan ni halak Kanaan, olat ni Sidon ro di Gerar tolhas tu Gaza, ro di Sodom dohot Gomorra dohot Adma dohot Zeboim, tolhas tu Lasa.'),(8276,42,10,20,'I ma anak ni si Ham mangihuthon margana dohot pangkulingna, di angka luatna dohot bangsonasida be.'),(8277,42,10,21,'Jadi tubuan anak ma dohot si Sem, ibana do ama ni sude anak ni si Heber, anggi ni si Japet, hahana i.'),(8278,42,10,22,'Ia anak ni si Sem: Si Elam, si Assur, si Arpaksad, si Lud dohot si Ram.'),(8279,42,10,23,'Ia anak ni si Ram i: Si Us, si Hul, si Geter dohot si Mas.'),(8280,42,10,24,'Ia si Arpaksad manubuhon si Sela; ia si Sela manubuhon si Heber.'),(8281,42,10,25,'Ia di si Heber tubu dua anak; goar ni na sada Peleg, ala di angka arina sirang tano on; ia goar ni anggina i si Joktan.'),(8282,42,10,26,'Ia si Joktan manubuhon si Almodad, si Salep, si Hazarmavet, si Jerah.'),(8283,42,10,27,'Dohot si Hadoram, si Usal, si Dikla.'),(8284,42,10,28,'Dohot si Hobal, si Abimael, si Seba.'),(8285,42,10,29,'Dohot si Opir, si Hevila dohot si Jobab. I ma sude anak ni si Joktan.'),(8286,42,10,30,'Ia inganannasida olat ni Mesa sahat ro di Separ, tu dolok na tungkan habinsaran.'),(8287,42,10,31,'I ma angka anak ni si Sem mangihuthon margana dohot pangkulingna di angka luatna dohot bangsonasida be.'),(8288,42,10,32,'I ma pinompar ni anak ni si Noak mangihuthon marganasida di bangsona be; jala sian nasida marserak angka bangso di tano on, dungkonsa aek na sumar i.'),(8289,42,11,1,'Saragam do pangkuling masa di sandok tano on jala sada do hata.'),(8290,42,11,2,'2Jadi di na marbungkasan nasida sian habinsaran, jumpang nasida ma tano pea di luat Sinear, gabe maringan nasida disi.'),(8291,42,11,3,'Dung i masipandohan ma nasida: Tole, tatopa ma tano bahen tano takko, jala masak situtu itatutung i. Jadi tano na minasakna i dipangke bahen batu jala barerang dipangke nasida bahen rapot.'),(8292,42,11,4,'Dung i angka dipandok nasida ma: Tole, tapungka ma sada hutanta, jala tapajokkon sada tungkup, sungkot ro di langit punsuna, tabahen ma baritanta, asa unang marserak hita tu liat portibi on.'),(8293,42,11,5,'Jadi tuat ma Jahowa marnida huta dohot tungkup i, na pinauli ni angka anak ni jolma.'),(8294,42,11,6,'Dung i ninna Jahowa ma: Ida ma sada do bangso jala sada pangkuling di nasida sude, jala nunga dipungka nasida paulihon angka i. Onpe tung so hasundatan nasida paulihon saluhut na sinangkapnasida i.'),(8295,42,11,7,'Tole, tuat ma Hita jala tagaor pangkulingnasida disi, asa unang be diantusi sahalak pangkuling ni donganna.'),(8296,42,11,8,'Jadi dipaserak Jahowa ma nasida sian i tu liat portibi on, gabe mansohot nasida paulihon huta i.'),(8297,42,11,9,'Dibahen i didok goarna Babel, ala disi do digaor Jahowa pangkuling ni sandok isi ni tano on, jala sian i dipaserak Jahowa nasida tu liat portibi on.'),(8298,42,11,10,'On do angka sundut ni si Sem. Marumur saratus taon ma si Sem, jadi ditubuhon ma si Arpaksad, dua taon dung salpu aek na sumar i.'),(8299,42,11,11,'Ia leleng ni si Sem mangolu, dung ditubuhon si Arpaksad i lima ratus taon do, jala ditubuhon anak dohot boru.'),(8300,42,11,12,'Marumur tolupulu lima taon si Arpaksad, jadi ditubuhon ma si Sela.'),(8301,42,11,13,'Ia leleng ni si Arpaksad mangolu, dung ditubuhon si Sela opat ratus tolu taon do, jala ditubuhon anak dohot boru.'),(8302,42,11,14,'Marumur tolupulu taon si Sela, jadi ditubuhon ma si Heber.'),(8303,42,11,15,'Jadi dung ditubuhon si Heber, mangolu dope si Sela opat ratus tolu taon, jadi ditubuhon anak dohot boru.'),(8304,42,11,16,'Marumur tolupulu opat taon si Heber, jadi ditubuhon ma si Peleg.'),(8305,42,11,17,'Jadi dung ditubuhon si Peleg mangolu dope si Heber opat ratus tolupulu taon, jala ditubuhon anak dohot boru.'),(8306,42,11,18,'Marumur tolupulu taon si Peleg, jadi ditubuhon ma si Reu.'),(8307,42,11,19,'Jadi dung ditubuhon si Reu mangolu dope si Peleg dua ratus sia taon, jala ditubuhon anak dohot boru.'),(8308,42,11,20,'Marumur tolupulu dua taon si Reu, jadi ditubuhon ma si Serug.'),(8309,42,11,21,'Jadi dung ditubuhon si Serug, mangolu dope si Reu dua ratus pitu taon, jala ditubuhon anak dohot boru.'),(8310,42,11,22,'Marumur tolupulu taon si Serug, jadi ditubuhon ma si Nahor.'),(8311,42,11,23,'Jadi dung ditubuhon si Nahor, mangolu dope si Serug dua ratus taon, jala ditubuhon anak dohot boru.'),(8312,42,11,24,'Marumur duapulu sia taon si Nahor, jadi ditubuhon ma si Tara.'),(8313,42,11,25,'Jadi dung ditubuhon si Tara mangolu dope si Nahor saratus sampulu sia taon, jala ditubuhon anak dohot boru.'),(8314,42,11,26,'Marumur pitupulu taon si Tara, jadi ditubuhon ma si Abram, si Nahor dohot si Haran.'),(8315,42,11,27,'On do angka sundut ni si Tara: Ditubuhon si Tara do si Abram, si Nahor dohot si Haran. Alai si Haran manubuhon si Lot.'),(8316,42,11,28,'Alai mate do si Haran jumolo sian si Tara amana i, di tano hatubuanna i, di Ur di luat Kasdim.'),(8317,42,11,29,'Dung i mambuat boru ma si Abram dohot si Nahor; goar ni jolma ni si Abram si Sarai, ia goar ni jolma ni si Nahor si Milka, boru ni si Haran, ama ni si Milka dohot si Jiska.'),(8318,42,11,30,'Alai na hol do anggo si Sarai, ndang adong tubuna.'),(8319,42,11,31,'Dung i diarahon si Tara ma si Abram, anakna i dohot si Lot, anak ni si Haran, pahompuna i, dohot si Sarai, parumaenna, jolma ni si Abram anakna i, jadi bungkas ma nasida sian Ur di luat Kasdim, asa laho tu tano Kanaan, jadi sahat ma nasida tu Haran, laos maringan disi.'),(8320,42,11,32,'Jadi dung dua ratus lima taon umur ni si Tara, mate ma ibana di Haran.'),(8321,42,12,1,'Dung i ninna Jahowa ma mandok si Abram: Bungkas ma ho sian tano hagodanganmu dohot sian hatubuanmu dohot sian bagas ni amam tu tano sipatuduhononku tu ho.'),(8322,42,12,2,'Hubahen pe ho gabe bangso na bolon, jala pasupasuonku ma ho, jala pabalgaonku goarmu, asa gabe pasupasu ho.'),(8323,42,12,3,'Jala pasupasuonku angka na mamasumasu ho, jala toruanku angka na mamburai ho, jala dapotan pasupasu marhitehite ho saluhut angka bangso ni tano on.'),(8324,42,12,4,'Jadi bungkas ma si Abram, songon na nidok ni Jahowa tu ibana, bungkas ma dohot si Lot udur dohot ibana. Marumur pitupulu lima taon si Abram, di na bungkas i ibana sian Haran.'),(8325,42,12,5,'Dung i diarahon si Abram ma si Sarai, jolmana i dohot si Lot, anak ni hahana i ro di sude na sinamotsamotnasida, dohot jolma, angka na tinuhornasida di Haran, jadi bungkas ma nasida laho tu tano Kanaan. Jadi dung tolhas nasida ro di tano Kanaan.'),(8326,42,12,6,'Diliati si Abram ma tano i sahat tu inganan ni si Sikem, ro di tombak More, nunga di tano i hian halak Kanaan i, didapot.'),(8327,42,12,7,'Dung i ro ma Jahowa, diida si Abram, didok ma: Tu pinomparmu ma lehononku tano on. Jadi dipajok disi sada langgatan di Jahowa na niidana i.'),(8328,42,12,8,'Dung i borhat ma ibana sian i tu sada dolok na tungkan habinsaran ni Betel, jala dipagohon undungundungna, tungkan hasundutan ni Betel, jala tungkan habinsaran ni Ai; jadi dipauli ma disi sada langgatan di Jahowa, jala dipajoujou goar ni Jahowa.'),(8329,42,12,9,'Dung i bungkas muse si Abram laho dompak dangsina.'),(8330,42,12,10,'Alai masa ma haleon di tano i, jadi tuat ma si Abram tu tano Misir, asa maisolat ibana disi, ai haleon potir do di tano i.'),(8331,42,12,11,'Jadi mandapothon tano Misir i ibana, ninna ma mandok si Sarai, jolmana i: Huboto do, boruboru na uli rupa do ho.'),(8332,42,12,12,'Onpe, molo diida halak Misir ho, dohononnasida ma: Jolmana do on, gabe bunuonnasida ahu, ianggo ho pangoluonnasida do.'),(8333,42,12,13,'Ua dok ma, ibotongku ho, asa sonang ahu ala ho, jala mangolu hosangku ala ho.'),(8334,42,12,14,'Jadi dung tolhas si Abram ro di tano Misir, dipaberengbereng halak Misir ma boruboru i, ai na uli situtu do ibana.'),(8335,42,12,15,'Dohot angka panuturi ni raja Firaun paberengbereng ibana, jala dipapujipuji ibana tu raja Firaun; jadi diboan ma boruboru i tu bagas ni raja Firaun.'),(8336,42,12,16,'Dipadenggan ma uhumna tu si Abram, ala ni angkora i, gabe adong ma di ibana angka birubiru, lombu, halode, angka naposo baoa dohot boruboru, dohot halode boruboru dohot angka unto.'),(8337,42,12,17,'Alai dihonahon Jahowa ma angka haporsuhon bolon tu raja Firaun dohot tu donganna sajabu ala hata ni si Sarai, jolma ni si Abram i.'),(8338,42,12,18,'Jadi dijou raja Firaun ma si Abram, didok ma; dia do na binahenmi tu ahu? Boasa so dipaboa ho tu ahu jolmam ibana hape?'),(8339,42,12,19,'Boasa dohononmu: Ibotongku ibana? Jadi naeng buatonku ibana bahen jolmangku. On pe, na, jolmam, buat ma, dung i laho ma ho.'),(8340,42,12,20,'Dung i didokkon raja Firaun ma angka naposona bahen donganna pataruhon ibana dohot jolmana dohot sude na di ibana i.'),(8341,42,13,1,'Jadi nangkok ma si Abram sian Misir, ibana dohot jolmana dohot sude na di ibana rap dohot si Lot tu Kanaan pardangsina.'),(8342,42,13,2,'Alai mamora situtu do si Abram di pinahan, perak dohot di sere.'),(8343,42,13,3,'Dung i didatdati ma dalanna sian dangsina sahat ro di Betel, ro di hajongjongan ni undungundungna di mulana, di holangkolang ni Betel dohot Ai.'),(8344,42,13,4,'Tu hajongjongan ni langgatan na binahenna disi parjolo, jala dipajoujou si Abram disi goar ni Jahowa.'),(8345,42,13,5,'Alai si Lot pe, na udur mardalan dohot si Abram, marbirubiru do, jala marlombu jala marundungundung.'),(8346,42,13,6,'Jadi ndang tarparmudu tano i nasida, ianggo tung tampak maringan, ai tung na godang do artanasida, jadi ndang tarbahen be top nasida maringan.'),(8347,42,13,7,'Dung i sai adong bada di tongatonga ni sipormahan pinahan ni si Abram dohot sipormahan pinahan ni si Lot. Halak Kanaan i pe dohot halak Perisi maringan uju i di tano i.'),(8348,42,13,8,'Jadi ninna si Abram ma tu si Lot: Ua unang adong bada di tongatonganta dohot di tongatonga ni parmahan ni pinahanta be, ai na sabutuha do hita.'),(8349,42,13,9,'Nda di jolom do sandok tano on? Ua sirang ma ho sian ahu! Ia tu hambirang ho, tu siamun ahu; ia tu siamun ho tu hambirang ahu.'),(8350,42,13,10,'Jadi manatapnatap ma si Lot manangkasi sude angka luat humaliang Jordan, ai angka tano tangkasan do i luhut, tagan so diripashon Jahowa dope Sodom dohot Gomorra, sobokkon porlak ni Jahowa, sobokkon tano Misir tolhas ro di Zoar.'),(8351,42,13,11,'Jadi dipillit si Lot ma di ibana sude angka luat humaliang Jordan, borhat ma ibana tungkan habinsaran. Asa sirang ma nasida na marsabutuha i.'),(8352,42,13,12,'Si Abram maringan di tano Kanaan, ianggo si Lot maringan di angka huta ni luat i, jala dibahen undungundungna tolhas ro di Sodom.'),(8353,42,13,13,'Alai halak na jahat jala pardosa situtu isi ni Sodom i maradophon Jahowa.'),(8354,42,13,14,'Jadi ninna Jahowa ma mandok si Abram, dung sirang si Lot i sian ibana: Ua tatap ma sian ingananmon dompak utara dohot dompak dangsina dohot dompak habinsaran dohot dompak hasundutan.'),(8355,42,13,15,'Ai sandok tano na tinatapmi lehononku ma tu ho dohot tu pinomparmu ro di salelenglelengna.'),(8356,42,13,16,'Jala bahenonku do pinomparmu sobokkon orbuk ni tano toropna, molo tung habilangan jolma orbuk ni tano on, habilangansa ma dohot pinomparmi.'),(8357,42,13,17,'Borhat ma ho, liati ma tano i mangihuthon unjurna dohot bohangna, ai tu ho do lehononku.'),(8358,42,13,18,'Jadi dipaunsat si Abram ma undungundungna laho ma ibana maringan tu tombak Mamre na di Hebron, jala dipajokkon disi sada langgatan di Jahowa.'),(8359,42,14,1,'Dung i di angka ari uju si Amrapel raja ni Sinear, Ariok raja ni Ellasar, Kedor-Laomor raja ni Elam dohot Tidal raja ni angka bangso parbegu.'),(8360,42,14,2,'Dipungka nasida ma hamusuon tu si Bera raja ni Sodom, dohot tu si Birsa raja ni Gomorra, si Sinab raja ni Adma, dohot tu si Semeber raja ni Zeboim, dohot tu raja sian Bela, i ma Zoar.'),(8361,42,14,3,'Mardomu ma nasida luhut tu rura Siddim, i ma laut sira nuaeng.'),(8362,42,14,4,'Nunga pola sampulu dua taon lelengna dioloi nasida patu tu si Kedor-Laomor, alai pasampulutolutaonhon mangose ma nasida.'),(8363,42,14,5,'Jadi pasampuluopattaonhon ro ma si Kedor-Laomor dohot angka raja donganna i dipantaluhon ma halak Repaim di luat Asterot-Karnaim, dohot halak Susim di luat Ham dohot halak Emim, na di adaran Kiriataim.'),(8364,42,14,6,'Dohot halak Hori di Seir doloknasida tolhas ro di adaran Elaparan, na marbalokkon halongonan.'),(8365,42,14,7,'Dung i mulak ma nasida ro di mual Eumispat, i ma Kades, jala dipapatu sandok luat ni halak Amalek ro di halak Amori, na maringan di Hazezon-Tamar.'),(8366,42,14,8,'Dung i borhat ma raja ni Sodom dohot raja ni Gomorra dohot raja ni Adma dohot raja ni Zeboim dohot raja ni Bela, i ma Zoar, marodor be ma nasida laho marmusu di rura Siddim.'),(8367,42,14,9,'Dompak si Kedor-Laomor, raja ni Elam, dohot si Tidal, raja ni angka bangso parbegu, dohot si Amrapel raja ni Sinear, dohot si Ariok raja ni Ellasar; opat raja alo ni na lima i.'),(8368,42,14,10,'Alai mansai lan do angka lupaklupak barerang di rura Siddim i. Jadi maporus ma raja ni Sodom dohot Gomorra, angka margunopan ma tusi, ianggo angka na teba i maporus do tu dolok.'),(8369,42,14,11,'Jadi dibuat nasida ma sandok ugasan ni Sodom dohot Gomorra, ro di sude sipanganonna, laho ma nasida.'),(8370,42,14,12,'Diboan nasida ma dohot si Lot, anak ni haha ni si Abram, ro di ugasanna, ai di Sodom do ibana maringan, laos laho ma nasida.'),(8371,42,14,13,'Dung i ro sada halak na maporus i, dipaboa ma barita i tu si Abram, halak Heber i, na maringan di tombak Mamre, ginomgom ni si Amori anggi ni si Eskol dohot Aner, angka janji ni si Abram do nasida.'),(8372,42,14,14,'Jadi dung dibege si Abram barita i naung tarboan anakna i, diparsinjatai ma naposona, angka na somal, tolu ratus sampulu ualu halak, angka na tubu di bagasan jabuna, dieak ma musu i tolhas ro di Dan.'),(8373,42,14,15,'Dung i disagi ma angka naposona mangarampok musu i bornginna i, jala ditaluhon nasida dung i dilele nasida tolhas ro di Hoba, na tungkan hambirang ni Damaskus.'),(8374,42,14,16,'Jadi mulak ma diboan saluhut ugasan, dohot do si Lot anakna i, ro di ugasanna, angka boruboru dohot halak na torop i mulak diboan.'),(8375,42,14,17,'Jadi dung mulak si Abram sian na marporang i dompak si Kedor-Laomor dohot angka raja donganna i, ruar ma raja ni Sodom manomunomu ibana tu rura Sawe i ma rura ni raja i.'),(8376,42,14,18,'Alai anggo si Melkisedek, raja ni Salem diboan sagusagu dohot anggur; malim ni Debata na mamunjungi do ibana.'),(8377,42,14,19,'Dung i dipasupasu ma si Abram, ninna ma: Na pinasupasu do ho, ale Abram, ditumpak Debata na mamunjungi, sigomgom langit dohot tano on.'),(8378,42,14,20,'Jala pinuji ma Debata na mamunjungi, na pasahathon angka musum tu tanganmu, jadi dilehon si Abram ma tu ibana saparsampulu sian luhutna.'),(8379,42,14,21,'Dung i ninna raja ni Sodom i ma mandok si Abram: Nanggo jolma i lehon di ahu, ianggo ugasan i buat ma di ho!'),(8380,42,14,22,'Alai ninna si Abram ma tu raja ni Sodom i: Nunga pola maruari ahu tu Jahowa, Debata na mamunjungi, sigomgom langit dohot tano on:'),(8381,42,14,23,'Molo tung hubuat sian nasa na di ho nanggo sada tali ro di ihot ni sipatu, asa unang tardok ho sogot: Ahu do pamora si Abram.'),(8382,42,14,24,'Holan na pinangan ni angka dolidoli dohot jambar ni si Aner Eskol dohot Mamre, angka na mandongani ahu, itenang dibuat nasida turpuknasida i.'),(8383,42,15,1,'Dung salpu angka jamita ondeng ro ma hata ni Jahowa tu si Abram marhitehite alatan, ninna ma: Unang ho mabiar ale Abram. Ahu do lombulombum, upam mansai balga.'),(8384,42,15,2,'Alai ninna si Abram ma: Oe ale Tuhan Jahowa, aha ma lehononmu di ahu? Sai na punu do ahu, si Elieser na sian Damaskus na ma sitean bagashu.'),(8385,42,15,3,'Angkup ni ninna si Abram: Ndi, ndang adong pinomparhu dilehon ho, jala ida, anak na tubu di bagasan jabungku na ma sitean ahu.'),(8386,42,15,4,'Dung i ro ma hata ni Jahowa mandok tu ibana: Ndang ibana tumean ho, na ruar sian butuham, ibana tumean ho.'),(8387,42,15,5,'Dung i ditogihon Tuhan i ibana ruar tu balian, jala ninna: Ua tailihon ma dompak langit i jala bilangi angka bintang, molo habilangan ho angka i. Dung i didok muse tu ibana: Songon i ma pinomparmu sogot.'),(8388,42,15,6,'Jadi porsea ma si Abram di Jahowa, gabe hatigoranna do i dietong Jahowa.'),(8389,42,15,7,'Dung i didok ma tu ibana: Ahu do Jahowa, na paruarhon ho sian Ur ni halak Kasdim, mangalehon tu ho tano on bahen teanteananmu.'),(8390,42,15,8,'Alai ninna si Abram ma: Ale Tuhan Jahowa, di aha ma tandaonku paboa ahu tumeansa.'),(8391,42,15,9,'Dung i ninna Tuhan i ma mandok ibana: Buat ma di Ahu sada lombu na marumur tolu taon dohot sada hambing na marumur tolu taon dohot sada birubiru tunggal na marumur tolu taon dohot anduhur sada dohot anak ni darapati sada.'),(8392,42,15,10,'Jadi diboan ma i sude di Tuhan i, angka dipardostonga ma i, dung i martoradan be ma dibahen; alai anggo pidong i ndang dibola.'),(8393,42,15,11,'Dung i marsonggopan ma angka pidong panoro tu angka juhut pelean i, alai diosikkon si Abram ma angka pidong i.'),(8394,42,15,12,'Jadi dung manihir mata ni ari, tarnenep modom ma si Abram, gabe songgop ma tu ibana hasonggoton dohot haholomon bolon.'),(8395,42,15,13,'Dung i ninna ma mandok si Abram: Unang so diboto ho, paisolat do sogot pinomparmi di tano na so golatna, jala parhatobanonna nasida, jala paporsukporsuhonna arga opat ratus taon.'),(8396,42,15,14,'Alai bangso na marhatobanhon nasida pe uhumonku do; dung i ruar ma nasida mantat ugasan godang.'),(8397,42,15,15,'Ianggo ho topotonmu do angka ompum mardamedame, tanomon do ho dung sarimatua.'),(8398,42,15,16,'Dung i paopatsunduthon mulak ma nasida, ai ndang dope sun hinajahat ni halak Amori.'),(8399,42,15,17,'Jadi dung mate mata ni ari, jala holom marimpotimpot, ida ma, martimus ma sada pamurunan, jala disola sada sipusipu na gara holangkolang ni angka tanggotanggo.'),(8400,42,15,18,'Di ari na sasada i marpadan ma Jahowa dohot si Abram, ninna ma: Tu pinomparmu do lehononku tano on olat ni batang aek Misir sahat ro di batang aek na bolon, i ma batang aek Euprat.'),(8401,42,15,19,'Tano na niinganan ni halak Keni dohot halak Kenizi dohot halak Kadmoni.'),(8402,42,15,20,'Dohot halak Het dohot halak Perisi dohot halak Repaim.'),(8403,42,15,21,'Dohot halak Amori dohot halak Kanaan i dohot halak Girgasi dohot halak Jebusi.'),(8404,42,16,1,'Alai anggo si Sarai, jolma ni si Abram, ndang adong ditubuhon di ibana; alai adong do sada hatobanna, boru Misir, na margoar si Hagar.'),(8405,42,16,2,'Dung i ninna si Sarai ma mandok si Abram, boti ma i: Nunga disundati Jahowa ahu mangintubu, ua topot ma hatobanku, anggiat maruli ahu sian ibana. Jadi ditangihon si Abram ma soara ni si Sarai.'),(8406,42,16,3,'Dung i dibuat si Sarai, jolma ni si Abram i, si Hagar boru Misir i, dilehon ma tu si Abram, sinondukna i bahen jolmana, dung diingani si Abram tano Kanaan i sampulu taon lelengna.'),(8407,42,16,4,'Dung i ditopot ma si Hagar i, jadi gabegabean ma ibana. Dung diida naung gabegabean ibana, tingka ma rohana mida soranganna i.'),(8408,42,16,5,'Dung i ninna si Sarai ma tu si Abram: Marsapata tu ho do ahu! Hulehon hatobanku parjolmaonmu, hape dung diida naung gabegabean ibana, gabe pis matana mida ahu. Jahowa ma manguhum hita.'),(8409,42,16,6,'Alai ninna si Abram ma mandok si Sarai: Ida ma, guru di ho do hatobanmu, lomo ni roham bahen tu ibana. Jadi dipaposi si Sarai uhumna tu hatobanna i, gabe laho lintun sian jolona.'),(8410,42,16,7,'Alai jumpang suruan ni Jahowa ibana di lambung ni sada mual di halongonan, di lambung mual di dalan laho tu Sur.'),(8411,42,16,8,'Didok suruan i ma: Sian dia ho, jala tu dia ho ale Hagar, hatoban ni si Sarai? Na lintun do ahu sian jolo ni si Sarai, soranganki, ninna.'),(8412,42,16,9,'Dung i ninna suruan ni Jahowa ma mandok ibana: Mulak ma ho tu soranganmu, paunduk ma roham mangoloi ibana.'),(8413,42,16,10,'Angkup ni didok suruan ni Jahowa tu ibana: Patoroponku situtu ma pinomparmu, gabe ndang habilangan sogot dibahen hinatoropna.'),(8414,42,16,11,'Dung i ninna suruan ni Jahowa muse tu ibana: Ida ma, naung gabegabean ho, jala tubuan anak ho, bahenonmu ma goarna si Ismael, ai nunga ditangihon Jahowa hinadangolmu.'),(8415,42,16,12,'Jala jolma sobokkon hije ibana sogot. Tanganna mangalo sude jolma jala tangan ni sude jolma mangalo ibana; jala marpagaran ibana di jolo ni angka donganna sabutuha sasudena.'),(8416,42,16,13,'Jadi dibahen si Hagar ma goar ni Jahowa, na mangkatai dohot ibana: Ho do Debata na manatap ahu, ai ninna do: Marnida dope ahu tagamon, dung huida Ibana?'),(8417,42,16,14,'Ala ni digoar ma sumur i: Sumur Lakhai-Roi, na di holangkolang ni Kades dohot Bared.'),(8418,42,16,15,'Dung i ditubuhon si Hagar ma anak sada di si Abram, jadi dibahen si Abram ma goar ni anakna, na tinubuhon ni si Hagar i, Ismael.'),(8419,42,16,16,'Marumur ualupulu onom taon si Abram di na tinubuhon ni si Hagar si Ismael i di si Abram.'),(8420,42,17,1,'Jadi dung marumur siapulu sia taon si Abram, diida ma ro Jahowa na mandok tu ibana: Ahu do Debata, na sun hinagogo; sai marparange di jolongku ma ho, jala daulat roham!'),(8421,42,17,2,'Asa hupahot padanhu dohot ho, jala hupatorop situtu ho.'),(8422,42,17,3,'Jadi manungkap ma si Abram. Dung i mangkatai Debata muse dohot ibana:'),(8423,42,17,4,'Ianggo Ahu, ida ma, nunga hot padanhu dohot ho, jala torop angka bangso marompu tu ho sogot.'),(8424,42,17,5,'Ala ni ndang be si Abram goarmu dohonon, si Abraham ma goarmu, ai nunga hupabangkit ho gabe ompu ni torop angka bangso.'),(8425,42,17,6,'Jala sopar situtu ho tumpahanku, jala pabangkitonku ho gabe ompu parsadaan ni angka bangso, jala marroan sian ho angka raja.'),(8426,42,17,7,'Sai pahotonku do padanhu tu ho dohot tu pinomparmu sogot marsundutsundut, gabe padan salelenglelengna, paboa Ahu gabe Debatam dohot Debata ni pinomparmu sogot.'),(8427,42,17,8,'Jala lehononku tu ho dohot tu pinomparmu sogot tano paisolatanmu nuaeng, i ma sandok tano Kanaan bahen partohapan salelenglelengna, jala Debatanasida Ahu.'),(8428,42,17,9,'Angkup ni didok Debata tu si Abraham: Ho pe, sai ingot ma padan na hubahen i, ho dohot pinomparmu sogot marsundutsundut.'),(8429,42,17,10,'On do padan siingotonmuna, na hubahen i tu ho dohot tu pinomparmu sogot: Ingkon sunaton do nasa baoa sian hamu.'),(8430,42,17,11,'Ingkon sunatonmuna do sibuk ni sibursiburmuna bahen tanda ni padanhu dohot hamu.'),(8431,42,17,12,'ia anak sunatonmuna ma molo marumur ualu borngin, nasa baoa pinomparmuna na tubu di jabum dohot na tinuhor ni hepeng sian nasa halak sileban na so sian pinomparmu.'),(8432,42,17,13,'Ingkon sunaton do na tubu di jabum dohot na tinuhor ni hepengmu, asa padan na salelengnilelengna do padanhu na di sibukmuna i.'),(8433,42,17,14,'Ia baoa parhulup, na so tarsunat sibuk ni sibursiburna ripashononhon do ibana sian bangsona, ala diose padanhu.'),(8434,42,17,15,'Dung i didok Debata muse tu si Abraham: Ndang be si Sarai dohononmu goar ni jolmam, si Sarah ma goarna.'),(8435,42,17,16,'Ai pasupasuonku ibana, jala lehononku ma di ho anak sada nang sian ibana, jala pasupasuonku ibana gabe angka bangso; gari raja ni angka bangso marroan sian ibana sogot.'),(8436,42,17,17,'Jadi manungkap ma si Abraham jala mengkel, didok ma di bagasan rohana: Tung tubuan anak ma jolma sisaratus taon jala manubuhon ma tagamon si Sarah, na marumur siapulu taon?'),(8437,42,17,18,'Dung i ninna si Abraham ma tu Debata: Asal mangolu si Ismael maradophon Ho!'),(8438,42,17,19,'Jadi ninna Debata ma: Tongon do, ingkon tubuhonon ni si Sarah, jolmam anak sada di ho, bahen ma goarna si Isak; pahotonku do padanhu dohot ibana, padan na salelenglelengna, ro di pinomparna sogot.'),(8439,42,17,20,'Taringot tu si Ismael pe, nunga hutangihon ho, ida ma, nunga hupasupasu ibana, tumpahanku ma ibana gabe, asa torop situtu ibana. Sampulu dua raja tubuna, jala bangso na bolon ibana bahenonku.'),(8440,42,17,21,'Alai anggo padanhu pahotonku dohot si Isak, situbuhonon ni si Sarah di ho, taon i songon on.'),(8441,42,17,22,'Jadi dung sun Debata mangkatai dohot ibana, manaek ma Debata sian si Abraham.'),(8442,42,17,23,'Dung i dibuat si Abraham ma si Ismael, anakna i dohot sude anak angka na tubu di jabuna ro di sude na tinuhor ni hepengna, nasa baoa, pangisi ni jabu ni si Abraham, jala disunati sibuk ni sibursiburnasida, laos di ari pangkataianna, songon na nidok ni Debata tu ibana.'),(8443,42,17,24,'Ia umur ni si Abraham di na tarsunat i sibuk ni sibursiburna i siapulu sia taon do.'),(8444,42,17,25,'Alai anggo umur ni si Ismael, anakna i, sampulu tolu taon do, di na tarsunat i sibuk ni sibursiburna i.'),(8445,42,17,26,'Laos di ari na sasada i do tarsunat si Abraham dohot si Ismael anakna i.'),(8446,42,17,27,'Ro di nasa baoa, pangisi ni jabuna, na tubu di jabuna dohot na tinuhor ni hepeng sian halak sileban, tarsunat do i rap dohot ibana.'),(8447,42,18,1,'Dung i diida si Abraham ma Jahowa di tombak Mamre, tongon hundul ibana di baba pintu ni undungundungna, di parngiar ni ari.'),(8448,42,18,2,'Dung i manaili ma ibana, gabe diida ma tolu halak jongjong di jolona. Jadi dung diida marlojong ma ibana manomunomu nasida sian baba pintu ni undungundungna, jala dipatungki tu tano.'),(8449,42,18,3,'Dung i ninna ma: Molo asi roham marnida ahu, ale Tuhanku, sai unang laos i naposomon.'),(8450,42,18,4,'Ua binoan aek i otik, asa diburi hamu totmuna, jala mangunsande hamu di toru ni hau on.'),(8451,42,18,5,'Asa huboan sagusagu santopik, gabe togar rohamuna, dung i tulus hamu, ai ala ni do umbahen na ro hamu tu lambung ni naposomuna on. Jadi ninna nasida ma: Bahen ma songon na nidokmu.'),(8452,42,18,6,'Dung i humalaput ma si Abraham tu undungundungna, mandapothon si Sarah, ninna ma: Halaputi ma ne niitak arga tolu bale, dogili bahen sagusagu!'),(8453,42,18,7,'Marlojong ma ianggo si Abraham manopot inganan ni lombu, jala dibuat anak ni lombu sada na mokmok, jala na uli, dilehon ma tu naposo, na humalaput paulihon i.'),(8454,42,18,8,'Dung i diboan si Abraham ma susu na nialhotan dohot susu na tata dohot anak ni lombu na pinauli i, jala dipampeakkon tu jolonasida; jongjong ma ianggo ibana di lambungnasida, di toru ni hau i, jadi mangan ma nasida.'),(8455,42,18,9,'Dung i ninna nasida ma tu ibana: Didia do si Sarah, jolmami? Di undungundung indi do, ninna.'),(8456,42,18,10,'Dung i didok ma: Sai na mulak do Ahu tu ho taon i songon on, ida adong ma anak ni si Sarah, jolmami. Dohot ma si Sarah umbegesa sian balik ni pintu ni undungundung, hajongjonganna i.'),(8457,42,18,11,'Alai nunga pola sarimatua be si Abraham dohot si Sarah, nunga pola montok si Sarah marningot hasomalan ni boruboru hian.'),(8458,42,18,12,'Jadi mengkel ma si Sarah di bagasan rohana, ninna ma: Dung matua pe ahu hape, asa adong di ahu silas ni roha, ubanon dohot rajangku?'),(8459,42,18,13,'Dung i ninna Jahowa ma tu si Abraham: Boasa ma tung parengkelan ni si Sarah hatangki ninna do: Tung na tutu ma ulaning ahu manubuhon, dung matua ahu?'),(8460,42,18,14,'Adong do huroha, na so tarpatupa Jahowa? Sai na mulak do Ahu manopot ho taon i songon on, jala adong ma anak sada di si Sarah.'),(8461,42,18,15,'Jadi ninna si Sarah ma mansoadahon: Ndang na mengkel ahu, ai na mabiar do ibana; alai ninna Jahowa ma: Ndangana, ai na mengkel do ho tutu.'),(8462,42,18,16,'Dung i hehe ma sian i na tolu baoa i, ditondong ma dompak Sodom; jadi dipataru si Abraham ma nasida.'),(8463,42,18,17,'Dung i ninna Jahowa ma: Tung pamorso ma Ahu maradophon si Abraham taringot tu na naeng bahenonku?'),(8464,42,18,18,'Sai na gabe bangso na balga jala na sangap do si Abraham sogot, jala dapotan pasupasu marhitehite ibana nasa bangso di tano on.'),(8465,42,18,19,'Ai hutanda do ibana, sai na tonaanna muse angka anakna dohot pinomparna di pudina, asa diingot nasida dalan ni Jahowa mangulahon hatigoran dohot uhum, asa dipatolhas Jahowa tu si Abraham na pinarbagabagana tu ibana.'),(8466,42,18,20,'Dung i didok Jahowa ma: Maungaung do angguhangguk badar sian Sodom dohot Gomorra, ai manahatang do dosanasida i.'),(8467,42,18,21,'Na tuat ma Ahu, asa huida manang na sun hinajahat ni na niulanasida i, hombar tu angguhangguk na tolhas i tu Ahu, molo ndang, gabe huboto.'),(8468,42,18,22,'Dung i manirpang ma sian i na dua baoa i mardalan dompak Sodom; alai jongjong dope ianggo si Abraham di jolo ni Jahowa.'),(8469,42,18,23,'Dung i dipajonok si Abraham, jala ninna ma: Tung ripashononmu ma na tigor rap dohot parjahat?'),(8470,42,18,24,'Atek tung adong limapulu halak, angka na tigor di bagasan huta i, tung ripashononmu ma i, ndang olananmu huta i ala na limapulu na tigor i, angka na di bagasan i.'),(8471,42,18,25,'Dao ma i sian Ho mangulahon sisongon i: mambunu na tigor rap dohot parjahat; tung sobokkon parjahat ma na tigor i? Palias ma i sian Ho, tung na so uhum ma bahenon ni Siuhumi liat portibi on?'),(8472,42,18,26,'Jadi ninna Jahowa ma: Molo tung jumpang Ahu di bagasan huta Sodom limapulu halak, angka na tigor, sai ma olanangku na saluat i ala nasida.'),(8473,42,18,27,'Dung i ninna si Abraham ma mangalusi: Sombangku ale Tuhan, nunga sapala hum ahu mamungka mangkatai dohot Ho; atek pe orbuk jala sirabun ahu.'),(8474,42,18,28,'Ua tung moru lima halak sian na limapulu angka na pintor i, tung ripashononmu ma luhut huta i ala ni na lima i? Jadi ninna ma: Ndang ripashononku molo tung jumpang Ahu disi opatpulu lima.'),(8475,42,18,29,'Dung i diulahi si Abraham muse dope mandok tu Ibana, ninna ma: Ua tung opatpulu jumpang disi! Jadi ninna Jahowa ma: Ndang hahuaonku nasida ala ni na opatpulu i.'),(8476,42,18,30,'Dung i ninna si Abraham ma: Ua unang tung tarrimas Ho, ale Tuhan, ala hudatdati mandok: Atek tung jumpang disi tolupulu! Jadi ninna Jahowa ma: Ndang hahuaonku nasida, molo tung jumpang Ahu tolupulu disi.'),(8477,42,18,31,'Dung i ninna si Abraham ma: Ida ma, nunga sapala hum ahu mamungka mandok tu Tuhanki: Ua tung jumpang disi duapulu. Jadi ninna Jahowa ma: Ndang ripashononku ala ni na duapulu i.'),(8478,42,18,32,'Jadi ninna si Abraham ma: Unang tung tarrimas Ho, ale Tuhanki, ala na mandok ahu dope holan sahali on: Atek tung jumpang disi sampulu? Jadi ninna Jahowa ma: Ndang ripashononku nasida ala ni na sampulu i.'),(8479,42,18,33,'Jadi dung sun Jahowa mangkatai dohot si Abraham, laho ma Ibana, mulak ma dohot si Abraham tu ingananna.'),(8480,42,19,1,'Ianggo surusuruan na dua halak i tolhas ma bodarina i tu huta Sodom; alai hundulhundul di harbangan ni Sodom i si Lot. Jadi dung diida si Lot nasida, hehe ma ibana manomunomu nasida, dipatungkap ma bohina tu tano.'),(8481,42,19,2,'Dung i ninna ma: Ua tuli ma hamu angka tuangku tu bagas ni naposomuna on, marborngin ma hamu jala buri hamu patmuna, sogot pe manogot i tulusi hamu dalanmuna i. Alai ninna nasida ma: Ndangana, sai marborngin di alaman on do hami.'),(8482,42,19,3,'Dung i sai diparajarajai si Lot ma nasida, jadi tuli ma nasida sian ibana, dibongoti ma bagasna. Dung i dipauli ma sipanganonnasida, dilompa ma dohot sagusagu na so niigaran, jadi mangan nasida.'),(8483,42,19,4,'Alai ndang dope laho modom nasida, nunga dihaliangi pangisi ni huta Sodom jabu i, tole dakdanak ro di na matua, tung tumpu do nasida sude.'),(8484,42,19,5,'Sai joujou nasida mandok tu si Lot: Didia do baoa i angka na ro tu ho saborngin on? Togihon nasida ruar tu hami on, asa hutanda hami nasida.'),(8485,42,19,6,'Dung i ruar ma si Lot mandapothon nasida tu balian pintu, jala dihinsu pintu i di pudina.'),(8486,42,19,7,'Dung i ninna ma: Sombangku di hamu, ale angka dongan, unang bahen hamu na so uhum.'),(8487,42,19,8,'Ida ma adong do dua borungku, angka na so habubuhan dope, ua i ma hutogihon tu ruar, hulehon tu hamu, bahen hamu ma tu nasida lomo ni rohamuna, asal unang dibahen hamu manang aha tu halak on, ai ala ni do umbahen na marlinggom nasida tu bagashu.'),(8488,42,19,9,'Alai ninna nasida ma: Pajonok ma tuson! Angkup ni didok nasida ma: Sasada ibana do na ro maisolat, jala sai naeng manguhumi ibana. On pe, umposi ma bahenonnami tu ho, asa tu nasida. Jadi dionjar nasida ma halak si Lot i, ditahopi ma naeng mangaloha pintu i.'),(8489,42,19,10,'Jadi disurdukkon angka baoa i tangannasida manait si Lot i tu nasida, masuk tu jabu, jala dihinsu ma pintu i.'),(8490,42,19,11,'Ianggo baoa angka na di jolo pintu ni bagas i, dipapitung surusuruan i do, tole dakdanak ro di na magodang, gabe loja nasida mandadapi pintu i.'),(8491,42,19,12,'Dung i ninna na dua baoa i ma mandok si Lot: Adong dope dison manang ise helam, manang anakmu, manang borum? Manang na songon diham di huta on, togihon ma nasida ruar sian inganan on.'),(8492,42,19,13,'Ai naeng mangaripashon huta on do hami, ala na gogo angguhangguknasida tu jolo ni Jahowa, jala disuru Jahowa do hami mangaripashon huta i.'),(8493,42,19,14,'Jadi ruar ma si Lot i, asa mangkatai dohot helana i, angka na mangoro boruna i, ninna ma: Hehe ma hamu ruar sian huta on, ai ripashonon ni Jahowa ma huta on. Alai diengkelengkel angka helana i do ibana.'),(8494,42,19,15,'Dung i mamungka buhabuha ijuk ari manogot i, didasdasi angka suruan i ma si Lot, ninna ma: Hehe ma ho, boan ma jolmam dohot borum na dua i naung disi, asa unang ripas dohot ho binahen ni hajahaton ni huta on.'),(8495,42,19,16,'Alai sai dipagelegele langkana, jadi ditangkup baoa i ma tanganna dohot tangan ni jolmana dohot tangan ni boruna na dua i dibahen pangolanion ni Jahowa di ibana, ditogihon nasida ma ibana jala dipaulon tu balian ni huta i.'),(8496,42,19,17,'Jadi dung ro di balian tinogihonnasida i, ninna na sahalak ma: Palua ma hosam, unang bereng dompak pudim, jala unang ngongong ho di sandok rura on, tu dolok ma ho maporus, asa unang ripas ho.'),(8497,42,19,18,'Alai ninna si Lot ma mandok nasida: Ndangana, manomba ahu di Ho ale Tuhanku.'),(8498,42,19,19,'Ida ma, nunga sapala jumpangan asi ni roha naposomon maradophon Ho, sai pagodang ma asi ni roham na binahenmu tu ahu laho pangoluhon hosangku. Alai ndang tarbahen ahu malua ianggo tung tu dolok an, atek tung dapot hamagoan i ahu, tung mate ahu maon.'),(8499,42,19,20,'Ua bereng, jonok do huta an bahen haporusan, jala metmet do; ua tusi ma ahu loas maporus, nda na metmet do i nian? Asal mangolu hosangkon.'),(8500,42,19,21,'Dung i ninna suruan i ma mandok ibana: Taringot tu hata ondeng pe tataponku do ho, ndang ripashononku huta na nidokmi.'),(8501,42,19,22,'Halaputi ma ne maporus tusi, ai ndang tarbahen Ahu mambahen manang dia, paima tolhas ho tusi. Ala ni do umbahen Zoar huta i digoar halak.'),(8502,42,19,23,'Nunga pola binsar mata ni ari di tano on, di na sahat i si Lot tu Zoar.'),(8503,42,19,24,'Dung i diparo Jahowa ma udan barerang dohot api tu Sodom dohot tu Gomorra, sian Jahowa, sian banua ginjang.'),(8504,42,19,25,'Jadi dipabalik Tuhan i ma angka huta i dohot na saluat i humaliang, ro di sude pangisi ni angka huta i dohot suansuanan ni tano i.'),(8505,42,19,26,'Dung i manaili ma jolmana i dompak pudi, gabe batu sira ibana.'),(8506,42,19,27,'Alai borhat do anggo si Abraham sogot manogot i tumopot hajongjonganna i di jolo ni Jahowa.'),(8507,42,19,28,'Dung i ditatap ma dompak Sodom dohot Gomorra dohot dompak sude tano na humaliang i. Jadi diida ma martimpul timus sian tano i, songon timus ni pamurunan.'),(8508,42,19,29,'Dung i laho mangaripashon Debata di angka huta ni rura i, diingot Debata ma si Abraham, ditogihon ma si Lot sian parsitongaan ni hamagoan laho mangago angka huta na niinganan ni si Lot najolo.'),(8509,42,19,30,'Dung i nangkok ma si Lot sian Zoar laho maringan tu dolok rap dohot boruna na dua i, ai mabiar do ibana maringan di Zoar, gabe diingani ma sada liang, ibana dohot boruna na dua i.'),(8510,42,19,31,'Dung i ninna boruna sihahaan i mandok sianggian i: Nunga matua amanta, jala ndang adong be baoa di tano on na ro tu hita, songon hasomalan di liat portibi on.'),(8511,42,19,32,'Tole, tapainumhon ma anggur tu amanta, jala taparangkupi ibana modom, asa tapangolu pinompar sian amanta.'),(8512,42,19,33,'Jadi dipainumhon nasida ma anggur tu amanasida i di na saborngin i. Dung i ro sihahaan i diparangkupi ma amana i, ndang pamotoanna laho modom dohot laho hehe boruna i.'),(8513,42,19,34,'Manogotna i ninna sihahaan i ma tu sianggian i: Nunga huparangkupi damang i na borngin, talehon ma dohot saborngin on anggur inumonna, asa laho ho mangangkupi ibana, asa tapangolu pinompar sian amanta.'),(8514,42,19,35,'Jadi dilehon nasida ma di na saborngin i anggur inumon ni amanasida. Dung i hehe ma sianggian i diparangkupi ma ibana, ndang pamotoanna laho modom dohot laho hehe boruna i.'),(8515,42,19,36,'Jadi gabegabean be ma boru ni si Lot i duansa sian amanasida.'),(8516,42,19,37,'Dung i ditubuhon sihahaan i ma anak sada, dibahen ma goarna Moab, ibana do ompu ni halak Moab rasirasa nuaeng.'),(8517,42,19,38,'Dohot sianggian i manubuhon anak sada, dibahen ma goarna Ben-Ammi, ibana do ompu ni halak Amon rasirasa nuaeng.'),(8518,42,20,1,'Dung i bungkas ma si Abraham sian i tu tano na tungkan dangsina laho maringan tu holangkolang ni Kades dohot Sur, gabe maisolat ibana di Gerar.'),(8519,42,20,2,'Jadi didok si Abraham ma taringot tu si Sarah, jolmana i: Ibotongku ibana. Jadi disuru si Abimelek, raja ni Gerar i mangalap si Sarah.'),(8520,42,20,3,'Alai ro ma Debata paipihon tu si Abimelek bornginna i, ninna ma tu ibana: Ida ma, marutang hosa ma ho ala boruboru na binuatmi, ai naung marharajaan do ibana.'),(8521,42,20,4,'Alai ndang dope jumonok si Abimelek tu ibana, jadi ninna ma: Ale Tuhan, tung bunuonmu ma dohot halak na tigor?'),(8522,42,20,5,'Nda didok ibana do tu ahu: Ibotongku boruboru i? Boruboru ipe didok do: Ibotongku ibana. Asa ia hubahen pe songon i, tobok ni rohangku do, jala bontor ni tanganku.'),(8523,42,20,6,'Dung i ninna Debata ma paipihon tu ibana: Ahu pe, huboto do sian tobok ni roham do dibahen ho songon i, umbahen huambat ho antong unang mardosa dompak Ahu; ala ni do umbahen so huloas ho jumama boruboru i.'),(8524,42,20,7,'On pe, paulak ma jolma ni baoa i, ai panurirang do ibana, jala tangiangkononna do ho, asa mangolu ho. Ala molo so dipaulak ho, boto ma, sai na mate do ho dohot sude na di ho i.'),(8525,42,20,8,'Jadi dipasogot si Abimelek ma hehe manogotna i, dijou ma sude angka naposona, jala dipajojor ma sude hata ondeng tu pinggolnasida, jadi mabiar situtu ma angka jolma i.'),(8526,42,20,9,'Dung i dijou si Abimelek ma si Abraham i, ninna ma tu ibana: Aha do na binahenmu tu hami, jala aha dosangku tu ho, umbahen diparo ho dosa bolon tu ahu dohot tu na hugomgomi? Angka pambahenan, na so jadi bahenon, dibahen ho tu ahu.'),(8527,42,20,10,'Angkup ni didok si Abimelek tu si Abraham: Aha ma diida ho, ala dibahen ho songon i?'),(8528,42,20,11,'Jadi ninna si Abraham ma: Ala ninna rohangku do, atek tung so adong biar mida Debata di huta on, gabe bunuonnasida ahu maon ala hata jolmangki.'),(8529,42,20,12,'Jala tongon huhut ibotongku ibana, boru ni damang, alai ndada tubu ni dainang, gabe jolmangku ibana.'),(8530,42,20,13,'Alai dung sai maotaot ahu sian bagas ni damang, didokkon Debata, jadi hudok ma tu ibana: On ma tanda ni holong ni roham, sibahenonmu tu ahu: Manang tu dia pe hita sahat, dok ma taringot tu ahu: Ibotongku ibana.'),(8531,42,20,14,'Jadi dipambuat si Abimelek ma birubiru dohot lombu dohot angka naposo baoa dohot boruboru, jala diparlehon tu si Abraham, dipaulak tu ibana dohot si Sarah, jolmana i.'),(8532,42,20,15,'Dung i ninna si Abimelek ma: Ida ma tanongki di jolomi, ingani ma lomo ni roham!'),(8533,42,20,16,'Alai anggo tu si Sarah ninna do: Ida ma nunga hulehon tu ibotom saribu rupia, i ma pardengganan tu ho dohot tu sude donganmu, asa lias ho maradophon sudena i.'),(8534,42,20,17,'Dung i martangiang ma si Abraham tu Debata. Jadi dipamalum Debata ma si Abimelek dohot jolmana dohot angka naposona boruboru, gabe tubuan anak ma nasida.'),(8535,42,20,18,'Ai tarhinsu situtu dibahen Jahowa bortian ni isi ni bagas ni si Abimelek sudena, ala hata si Sarah jolma ni si Abraham.'),(8536,42,21,1,'Dung i ditatap Jahowa ma si Sarah, songon na nidokna i, jala dibahen Jahowa tu si Sarah songon na pinarbagana i.'),(8537,42,21,2,'Ai gabegabean ma si Sarah, jala ditubuhon anak sada di si Abraham, di hatutuana i, tongon di tingki, na nidok ni Debata tu ibana.'),(8538,42,21,3,'Dung i dibahen si Abraham ma goar ni anakna i, na tubu di ibana, tinubuhon ni si Sarah, si Isak.'),(8539,42,21,4,'Dung i paualubornginhon, disunat si Abraham ma si Isak, anakna i, songon na tinonahon ni Debata tu ibana.'),(8540,42,21,5,'Marumur saratus taon do si Abraham di na tubu i si Isak anakna i.'),(8541,42,21,6,'Jadi ninna si Sarah ma: Siparengkelon do dibahen Debata tu ahu; nasa na umbegesa parengkelanna do ahu.'),(8542,42,21,7,'Angkup ni didok do: Tung ise ma na hum marbagahon di si Abraham, si Sarah pasusuhon anak? Ai nunga hutubuhon anak sada di hatutuana i.'),(8543,42,21,8,'Jadi dung lam balga posoposo i, jala sirang susu, marharoan bolon ma si Abraham, di ari na sirang susu i si Isak.'),(8544,42,21,9,'Dung i sai diida si Sarah ma anak ni si Hagar, boru Misir i, na tinubuhonna di si Abraham, mangarehei.'),(8545,42,21,10,'Jadi didok ma tu si Abraham: Pabali ma hatoban on rap dohot anakna i, ai ndang manean anak ni hatoban on rap dohot si Isak anakki.'),(8546,42,21,11,'Alai sogo situtu do roha ni si Abraham di hata i, ala ni anakna i.'),(8547,42,21,12,'Alai ninna Debata ma tu si Abraham: Unang sogo roham taringot tu dakdanak i dohot taringot tu hatobanmu; tangihon ma sude na nidok ni soara ni si Sarah tu ho, ai marhitehite si Isak do targoar di ho pinompar.'),(8548,42,21,13,'Anak ni hatobanmi pe bahenonku do gabe bangso, ala pinomparmu ibana.'),(8549,42,21,14,'Jadi dipasogot si Abraham ma manogotna i hehe, dibuat ma sagusagu dohot aek satabutabu, dilehon ma i tu si Hagar, dipasandangkon ma tu abarana, dung i dipalaho ibana rap dohot dakdanak i. Jadi laho ma ibana, hape lilu do di halongonan Berseba.'),(8550,42,21,15,'Jadi dung suda aek sian tabutabu i dipapeak ma dakdanak i tu toru ni ramba metmet.'),(8551,42,21,16,'Dung i dipaholang ibana laho hundul arga sansinior sian i, ai ninna do: Ndang tarpaidaida ahu dakdanak i laho mate. Jadi sai hundul ibana tondong sian i, huhut dipangiar soarana jala tumatangis.'),(8552,42,21,17,'Jadi ditangihon Debata ma soara ni dakdanak i, joujou ma suruan ni Debata sian banua ginjang di si Hagar, ninna ma mandok ibana; aha di ho ale Hagar? Unang ho mabiar, ai nunga ditangihon Debata soara ni dakdanak i sian hapeahanna i.'),(8553,42,21,18,'Hehe ma ho, pahehe ma dakdanak i jala togu ibana dohot tanganmu ai bahenonku do ibana gabe bangso na bolon.'),(8554,42,21,19,'Dung i dipatonggor Debata ma mata ni si Hagar, gabe diida ma sada mual. Jadi laho ma ibana manggohi tabutabu i dohot aek, jala dipainum dakdanak i.'),(8555,42,21,20,'Dung i ditumpak Debata ma dakdanak i magodang, laos maringan ibana di halongonan, diula ma parsioron.'),(8556,42,21,21,'Jadi maringan ibana di halongonan Elaparan, gabe dibuat inana i ma sada boru Misir bahen jolmana.'),(8557,42,21,22,'Dung i ro ma uju i hata ni si Abimelek dohot si Pikol, partogi ni angka paranganna i mandok tu si Abraham: Sai didongani Debata do ho di sude na niulam.'),(8558,42,21,23,'On pe, mangmangkon ma dison tu Debata: Molo tung marmihimmihim ho tu ahu, manang tu angka anakku, ro di pinomparhu sogot! Nda maruhum na denggan ho tu ahu dohot tu tano paisolatanmu, marningot uhum na denggan na hubahen i tu ho.'),(8559,42,21,24,'Jadi ninna si Abraham ma: Humangmangkon pe.'),(8560,42,21,25,'Alai disarita si Abraham do si Abimelek ala sumur, na nirampas ni angka naposo ni si Abimelek.'),(8561,42,21,26,'Dung i ninna si Abimelek ma: Ndang pamotoanku manang ise na mambahen songon i; ho pe antong, ndang dialualuhon ho tu ahu, jala ndang dung hubege, nda sadari on.'),(8562,42,21,27,'Jadi dipambuat si Abraham ma birubiru dohot lombu, jala di parlehon tu si Abimelek, jadi marbulan be ma nasida na dua.'),(8563,42,21,28,'Dung i dipapulik si Abraham ma pitu anak ni birubiru.'),(8564,42,21,29,'Jadi ninna si Abimelek ma tu si Abraham: Tu aha ma anak ni birubiru na pitu on, angka na pinapulikmon?'),(8565,42,21,30,'Dung i ninna si Abraham ma: Sai jalo ma na pitu anak ni birubiru on sian tanganku bahen sitindangingku, paboa ahu sihali sumur on.'),(8566,42,21,31,'Ala ni do umbahen margoar Berseba (sumur parbulanan) huta i, ala disi marbulan nasida na dua i.'),(8567,42,21,32,'Asa marpadan ma nasida di Berseba. Dung i hehe ma si Abimelek dohot si Pikol, partogi ni paranganna i, asa mulak nasida tu tano ni halak Palistim.'),(8568,42,21,33,'Dung i disuan si Abraham ma angka hau ara di Berseba, jala dipajoujou disi goar ni Jahowa, Debata saleleng ni lelengna.'),(8569,42,21,34,'Jadi na nia leleng si Abraham maisolat di tano ni halak Palistim.'),(8570,42,22,1,'Jadi dung salpu angka jamita ondeng, diunjuni Debata ma si Abraham, ninna ma mandok ibana: Jo Abraham! Dison do ahu, ninna.'),(8571,42,22,2,'Dung i ninna ma: Buat ma anakmu na sasada i, haholongan ni roham, si Isak i, jala togihon ibana tu tano Moria, jala pelehon ibana disi bahen pelean situtungon tu sada dolok, sipaboaonku tu ho.'),(8572,42,22,3,'Jadi dipasogot si Abraham ma hehe manogotna i, dipalanoi halodena, jala diarahon dua halak naposona rap dohot si Isak, anakna i, jala ditahai soban manutungi pelean i, dung i borhat ma ibana laho tu dolok, na nidok ni Debata tu ibana.'),(8573,42,22,4,'Patoluarihon manaili ma si Abraham, jadi ditatap ma dolok i.'),(8574,42,22,5,'Dung i ninna si Abraham ma tu angka naposona i: Dison ma hamu mangulon rap dohot halode i, alai anggo ahu dohot dakdanak i, laho ma hami tu sadu i, jala ia dung martangiang hami, mulak hami tu hamu on.'),(8575,42,22,6,'Jadi dibuat si Abraham ma soban tu pelean situtungon i diampehon ma tu si Isak, anakna i. Alai ibana do maniop api dohot raut sada, jadi rap mardalan ma nasida na dua i.'),(8576,42,22,7,'Dung i ninna si Isak ma tu si Abraham amana i: Atehe, ale amang, ninna! Dison do ahu ale anaha, ninna. Dung i ninna si Isak ma: Nunga dison api dohot soban, alai didia do birubiru bahen pelean situtungon i?'),(8577,42,22,8,'Jadi ninna si Abraham ma: Sai pilliton ni Debata di Ibana sada birubiru bahen pelean situtungon, ale anaha. Jadi rap mardalan ma nasida na dua i.'),(8578,42,22,9,'Jadi dung sahat nasida tu dolok, na nidok ni Debata tu ibana, dipauli si Abraham ma disi sada langgatan, jala disusun soban i, ditali dohot si Isak anakna i, jala diampehon ibana tu ginjang ni langgatan i, tu atas soban i.'),(8579,42,22,10,'Dung i dipatoltol si Abraham ma tanganna mambuat raut i laho maneat anakna i.'),(8580,42,22,11,'Dung i dijou suruan ni Jahowa ma ibana sian banua ginjang, ninna ma: Jo Abraham, Abraham! Dison do ahu, ninna.'),(8581,42,22,12,'Jadi ninna suruan i ma: Unang laho tanganmu tu dakdanak i, jala unang bahen manang aha tu ibana, ai nunga huboto dihabiari ho do Debata, jala ndang diolani ho anakmu na sasada i maradophon Ahu.'),(8582,42,22,13,'Jadi manaili ma si Abraham, gabe diida ma birubiru tunggal sada di pudina, rais tandukna di dangka dupang ni hau metmet; jadi laho ma si Abraham dibuat ma birubiru tunggal i, jala dipelehon bahen pelean situtungon, singkat ni anakna i.'),(8583,42,22,14,'Dung i dibahen si Abraham ma goar ni pamelean i: Jahowa Jireh (Jahowa manatap); ala ni didok halak rasirasa nuaeng: Di dolok ni Jahowa tataponna.'),(8584,42,22,15,'Dung i dijou suruan ni Jahowa ma si Abraham paduahalihon sian banua ginjang.'),(8585,42,22,16,'Didok ma: Nunga pola huuarihon diringku, ninna Jahowa, ala dibahen ho songon i, jala ndang diolani ho anakmu na sasada i:'),(8586,42,22,17,'Sai pasupasuonku ma ho, jala patoroponku situtu pinomparmu, sobokkon bintang angka na di langit, jala sobokkon rihit, angka na di topi ni laut, jala teanon ni pinomparmu sogot huta ni angka musuna.'),(8587,42,22,18,'Jala martua sogot saluhut bangso ni tano on marhitehite pinomparmu, ala ditangihon ho soarangku.'),(8588,42,22,19,'Dung i mulak ma si Abraham tu angka naposona, jadi borhat ma nasida, asa tampak laho tu Berseba, ai maringan di Berseba do si Abraham.'),(8589,42,22,20,'Jadi dung salpu angka jamita ondeng, ro ma barita mandok tu si Abraham: Ida ma, nunga tubuan anak dohot si Milka di si Nahor, anggim i.'),(8590,42,22,21,'Si Us, buhabajuna dohot si Bus anggina i dohot si Kemuel, ompu ni halak Ram.'),(8591,42,22,22,'Dohot si Kesed dohot si Haso dohot si Pildas dohot si Jidlap dohot si Betuel.'),(8592,42,22,23,'Alai anggo si Betuel tumubuhon si Rebekka. Na ualu halak i do ditubuhon si Milka di si Nahor, anggi ni si Abraham i.'),(8593,42,22,24,'Dohot hatobanna, pinarjolmana i, na margoar si Reuma do tubuan anak: Si Teba, Gaham, Tahas dohot Maaka.'),(8594,42,23,1,'Ia umur ni si Sarah saratus duapulu pitu taon do, i ma leleng ni si Sarah mangolu.'),(8595,42,23,2,'Dung i mate ma si Sarah di Kiryat-Arba, i ma Hebron di tano Kanaan. Jadi ro ma si Abraham mangandungi si Sarah, jala ditatangisi.'),(8596,42,23,3,'Dung i hehe ma si Abraham sian na paadopadop na matena i, dipangkulingi ma angka anak ni si Het ninna:'),(8597,42,23,4,'Halak na hinomit jala paisolat do ahu di tongatongamuna on, sai lehon hamu ma di ahu parbandaan hapunjungan di tongatongamuna on, asa hutanom na matengki, na hupaadopadop on.'),(8598,42,23,5,'Jadi ninna angka anak ni si Het ma mangalusi si Abraham:'),(8599,42,23,6,'Tangihon ma hatanami on, ale tuan, raja na pinabangkit ni Debata do ho di tongatonganami on, tanom ma na matem tu parbandaannami, tinodo ni roham. Ndang adong manang ise hami on na mangorai ho mananom na matem tu parbandaanna.'),(8600,42,23,7,'Dung i hehe ma si Abraham, jala disomba bangso ni luat i, i ma angka anak ni si Het.'),(8601,42,23,8,'Jadi mangkatai ma ibana dohot nasida, ninna ma: Molo lomo rohamuna tanomonku na matengku, na hupaadopadop i, tangihon hamu ma hatangkon, jala ondihon hamu ahu tu si Epron, anak ni si Zohar.'),(8602,42,23,9,'Asa dilehon tu ahu liangna Makpelah na di duru ni haumana, sai dilehon ma i tu ahu manang sadia pe argana, bahen parbandaan hapunjungan di tongatongamuna on.'),(8603,42,23,10,'Ai maringan di tongatonga ni angka anak ni si Het do si Epron i. Jadi ninna si Epron, marga Het i ma mangalusi si Abraham, patubegehon tu angka anak ni si Het i, tu sude angka na marmeang muli sian bahal ni hutana i, botima:'),(8604,42,23,11,'Ndang songon i, ale tuangku, tangihon ma hatangkon: Hulehon ma tu ho hauma i ro di liang na di bagasan, tu ho ma hulehon maradophon angka anak ni bangsongku hulehon tu ho; tanom ma na matem tusi.'),(8605,42,23,12,'Jadi marsomba ma si Abraham maradophon isi ni luat i.'),(8606,42,23,13,'Dung i ninna ma tu si Epron, dibege angka isi ni luat i: Aut ni olo ho tumangihon hatangkon, sai jalo ma sian ahu hepeng silehononku tuhor ni hauma i, asa hutanom tusi na matengku.'),(8607,42,23,14,'Alai ninna si Epron ma mangalusi si Abraham:'),(8608,42,23,15,'Sai tangihon ma hatangkon, ale tuangku: Hauma paropatratus ringgitan, sadia ma i di hita? Sai tanom ma na matem tusi.'),(8609,42,23,16,'Jadi ditangihon si Abraham ma si Epron i, didasingkon ma hepeng na nidok ni si Epron i, binege ni angka anak ni si Het, siopatratus ringgit hian.'),(8610,42,23,17,'Jadi saut ma hauma ni si Epron, na di Makpelah, tondong ni Mamre, hauma i dohot liang i di bagasan ro di sude hau, angka na di hauma i, angka na hinangkamna i humaliang.'),(8611,42,23,18,'Gabe si Abraham nampunasa di jolo ni angka anak ni si Het ro di sude angka na marmeang muli sian bahal ni hutana i.'),(8612,42,23,19,'Dung i ditanom si Abraham ma si Sarah, jolmana i tu liang ni hauma Makpelah, tondong ni Mamre, i ma Hebron na di tano Kanaan.'),(8613,42,23,20,'Asa sahat ma tu si Abraham hauma i dohot liang na di bagasan bahen parbandaan hapunjungan sian angka anak ni si Het.'),(8614,42,24,1,'Alai nunga ubanon si Abraham, nunga sahat sarimatua ibana, jala na pinasupasu ni Jahowa do si Abraham di sasudena i.'),(8615,42,24,2,'Dung i didok si Abraham ma mandok naposona, sintua ni bagasna i, siramoti sasude na di ibana: Ua ampehon ma tanganmu tu toru ni gontingkon.'),(8616,42,24,3,'Asa hupamangmangkon ho tu Jahowa, Debata sigomgom langit dohot tano on, paboa na so tupa buatonmu sian boru ni halak Kanaan, na humomit ahu bahen jolma ni anakki.'),(8617,42,24,4,'Ingkon laho ho tu tano hagodanganki dohot hatubuanki mangaririt bahen jolma ni anakki, si Isak.'),(8618,42,24,5,'Jadi ninna naposona i ma mandok ibana: Beha ia so ra boruboru i mangihuthon ahu tu tano on, jadi do togihononku anakmi mulak tu tano, tininggalhonmi?'),(8619,42,24,6,'Dung i didok si Abraham ma mandok ibana; jaga ho, so tung ditogihon ho anakki mulak tusi.'),(8620,42,24,7,'Sai suruon ni Jahowa, Debata, sigomgom langit i suruanna tu jolom laho tusi mangaririt bahen jolma ni anakki, ai Ibana do manogihon ahu ruar sian bagas ni damang dohot sian tano hatubuanki, jala didok mandok ahu, pola diuarihon tu ahu, ninna: Tu pinomparmi do lehononku tano on.'),(8621,42,24,8,'Alai molo tung so ra boruboru i mangihuthon ho, malua do ho sian uarimon na tu ahu i, asal unang ditogihon ho anakki mulak tusi.'),(8622,42,24,9,'Jadi diampehon naposo i ma tanganna tu toru gonting ni si Abraham, indukna i, jala dimangmangkon hata i tu ibana.'),(8623,42,24,10,'Dung i dibuat naposo i ma sampulu unto sian unto ni rajana i, jala laho ibana; diboan dohot ugasan ni rajana i marragamragam di na borhat i ibana laho tu Mesopotamia tu huta ni si Nahor.'),(8624,42,24,11,'Dung i dipaulon ma angka unto i di balian ni huta i, di lambung ni sada sumur mareak bodari, di tingki haroro ni angka partuaek.'),(8625,42,24,12,'Jadi ninna ma: Ale Jahowa Debata ni si Abraham, indukhi, sai tomutomuhon ma tu ahu sadari on, sai bahen ma asi roha tu si Abraham, indukhi.'),(8626,42,24,13,'Ida ma, jongjong do ahu di lambung sumur on, jala ruar ma angka boru ni isi ni huta on laho manahui aek.'),(8627,42,24,14,'Onpe, ia hudok mandok sada boruboru i: Ua paeleng hudonmi, asa minum ahu, gabe dohononna: Inum ma na, hupasobur pe dohot angka untom, i ma nian na tinurpukkonmu tu naposom si Isak, asa hutanda disi, paboa naung dibahen Ho asi ni roha tu rajangki.'),(8628,42,24,15,'Jadi ndang dope tolhas hatana i didok, ida ma nunga ruar si Rebekka, tubu ni si Betuel, anak ni si Milka, jolma ni si Nahor, haha ni si Abraham, jala sumandang panguhatanna.'),(8629,42,24,16,'Jala na uli utus rupa ni boruboru i, namarbaju, jala na so dung tumanda baoa; mijur ma ibana tu sumur i manggohi panguhatanna, dung i nangkok ibana.'),(8630,42,24,17,'Jadi marlojong ma naposo i mandapothon ibana, ninna ma: Ua lehon aek i otik inumonku sian panguhatanmu.'),(8631,42,24,18,'Jadi didok boruboru i ma: Inum ma, ale rajanami na! Dung i humalaput ma ibana paijurhon panguhutanna i tu tanganna, painumhon ibana.'),(8632,42,24,19,'Jadi dung sun ibana dipainum, ninna ma: Hutahui pe dohot di angka untom, paima sombu uasna.'),(8633,42,24,20,'Jadi humalaput ma ibana mangusehon panguhatanna i tu parsoburan i, jala diulahi marlojong tu sumur i manahui singkop, di sude angka untona i ditahui.'),(8634,42,24,21,'Alai longang ma bajoi mida ibana, sai sip do, paima ditanda manang na ditumpak Jahowa langkana i, manang ndang.'),(8635,42,24,22,'Jadi dung sun marsobur angka unto i, dibuat bajoi ma sada tintin sere, satonga hati dokdokna, jala dua leang sere tu tanganna parsampulutailan.'),(8636,42,24,23,'Dung i didok ma: Atehe, boru ni ise do ho? Ua paboa di ahu; siat do hami di bagas ni amanta i marborngin?'),(8637,42,24,24,'Jadi ninna ma mandok ibana: Boru ni si Betuel do ahu, anak ni si Milka, tinubuhonna i di si Nahor.'),(8638,42,24,25,'Angkup ni didok boruboru i tu ibana: Galagala ni eme pe dohot gagaton, godang do di hami, parbornginan pe adong do.'),(8639,42,24,26,'Jadi dipaunduk bajoi ma ibana, huhut marsomba tu Jahowa.'),(8640,42,24,27,'Jala ninna ma: Pinuji ma Jahowa, Debata ni si Abraham, rajangki, ndang dialang asina dohot hasongtion ni rohana tu rajangki; Jahowa do tongon manogihon ahu di dalan tu bagas ni haha ni rajangki.'),(8641,42,24,28,'Dung i maringkat ma boruboru i laho paboahon angka hata ondeng tu bagas ni inana.'),(8642,42,24,29,'Adong do huhut sada iboto ni si Rebekka, si Laban goarna. Jadi marlojong ma si Laban tu balian manjumpangkon bajoi tu sumur i.'),(8643,42,24,30,'Dung diida tintin i dohot leang angka na di pargolangan ni ibotona i, jala dung dibege angka hata ni si Rebekka, ibotona i, na mandok: Songon on do hata ni bajoi tu ahu, jadi tolhas ma ibana tu bajoi, ida ma jongjong dope ibana di lambung ni angka unto, mardonokkon sumur i.'),(8644,42,24,31,'Dung i didok ma: Beta hita muli, ho na pinasupasu ni Jahowa, boasa tung jongjong di balian on ho? Nunga pola hupadiri bagas i bahen inganan ni angka unto i.'),(8645,42,24,32,'Jadi ditogihon ma bajoi tu jabu, diharhari ma hohos ni angka unto i, jala dilehon galagala ni eme dohot gagaton tu angka unto i, dohot aek pamuri ni patna dohot pat ni angka donganna i.'),(8646,42,24,33,'Dung i dibahen ma sipanganon tu jolona. Alai ninna ibana ma: Ndang na mangan ahu, nda jolo tolhas hudok angka hatangkon. Antong dok ma, ninna.'),(8647,42,24,34,'Jadi ninna ma: Naposo ni si Abraham do ahu.'),(8648,42,24,35,'Jala mansai ditumpak Jahowa rajangki, gabe timbul ibana, jala dilehon tu ibana birubiru dohot lombu, perak dohot sere, naposo baoa dohot naposo boruboru, angka unto dohot halode.'),(8649,42,24,36,'Angkup ni ditubuhon si Sarah, jolma ni rajangki sada anak di ibana di hatutuanasida i, tusi do dilehon sude na di ibana i.'),(8650,42,24,37,'Dung i moruari ahu dibahen rajangki, ninna do: Ndang na jadi buatonmu sian boru ni halak Kanaan, na humomit ahu, bahen jolma ni anakki.'),(8651,42,24,38,'Ingkon laho ho tu bagas ni damang, tumopot donganku samarga, mambuat jolma ni anakki.'),(8652,42,24,39,'Alai ningku do nian tu rajangki: Beha ia so olo boruboru i mangihuthon ahu?'),(8653,42,24,40,'Jadi ninna ibana mandok ahu: Sai suruon ni Jahowa, na hupaolooloi i suruanna mandongani ho manumpak langkam, asa dialap ho jolma ni anakki sian donganku dohot sian bagas ni damang.'),(8654,42,24,41,'Ia dung tolhas ho tu donganki, hape ndang olo nasida mangalehon boruboru i tu ho, disi pe asa malua ho sian na niuarihonmu tu ahu.'),(8655,42,24,42,'Jadi tolhas ma ahu sadari on tu sumur on, gabe hudok ma: Ale Jahowa, Debata ni rajangki, si Abraham, aut ni olo Ho manumpak langkangkon, na hulangkahon i!'),(8656,42,24,43,'Ida ma ahu na jongjong di lambung sumur on, patupa ma songon on: Ia ruar sada namarbaju manahui, gabe hudok mandok ibana: Ua lehon di ahu otik aek inumonku sian panguhatanmu.'),(8657,42,24,44,'Jadi didok ibana nian tu ahu: Inum ma na, dohot pe di untom hutahui, i ma nian jolma na tinurpukkon ni Jahowa tu anak ni rajangki.'),(8658,42,24,45,'Ndang dope tolhas hudok hata i di bagasan rohangku, ida ma, nunga ruar si Rebekka sumandang panguhatanna, jala mijur ibana tu sumur i manahui, jadi hudok ma tu ibana: Ua lehon ahu minum.'),(8659,42,24,46,'Jadi humalaput ma ibana paijurhon panguhatanna i, sian abarana, ninna ma: Minum ma ho, hupasobur pe dohot angka untomon. Asa minum ma ahu, jala dohot do angka unto i dipasobur.'),(8660,42,24,47,'Dung i, husungkun ma ibana, ningku do: Atehe boru ni ise do ho? Jadi ninna ibana ma: Boru ni si Betuel do, anak ni si Nahor, tinubuhon ni si Milka di ibana. Dung i hubahen ma sada tintin tu igungna dohot angka leang tu pargolanganna.'),(8661,42,24,48,'Dung i hupaunduk ahu marsomba tu Jahowa, jala hupuji Jahowa, Debata ni si Abraham, rajangki, na patongonhon langkangkon laho mambuat boru ni haha ni rajangki di anakna i.'),(8662,42,24,49,'Onpe molo olo hamu mambahen asi dohot burju ni roha tu rajangki, ba, paboa hamu di ahu, molo ndang, antong paboa hamu di ahu, asa hutondong tu siamun manang tu hambirang.'),(8663,42,24,50,'Jadi ninna si Laban dohot si Betuel ma mangalusi: Na ro sian Jahowa do hata i, ndang tardok hami tu ho na roa manang na denggan.'),(8664,42,24,51,'Ndi, di jolomi do si Rebekka i, buat ma ibana jala langka, asa gabe jolma ni anak ni rajam ibana, songon na nidok ni Jahowa.'),(8665,42,24,52,'Jadi dung dibege naposo ni si Abraham hatanasida i, marsomba ma ibana tu Jahowa.'),(8666,42,24,53,'Dung i diparungkar naposo i ma angka ugasan perak dohot sere dohot angka ulos, jala diparlehon tu si Rebekka, tu ibotona dohot tu inana i pe dilehon do angka ugasan na arga.'),(8667,42,24,54,'Dung i mangan jala minum nasida, ibana rap dohot angka donganna sauduran, jala marborngin nasida. Dung i hehe ma nasida manogotna i, jala ninna ibana ma: Paborhat hamu ma ahu tu rajangki.'),(8668,42,24,55,'Alai ninna ibotona dohot inana i ma: Ua tading nian si betet i di hami, arga sampulu borngin nari, salpu ni jadi ho mardalan.'),(8669,42,24,56,'Jadi ninna ibana ma tu nasida: Unang tiopi hamu ahu, nunga ditumpak Jahowa langkangku; paborhat hamu ahu, asa laho ahu tumopot rajangki.'),(8670,42,24,57,'Dung i ninna nasida ma: Antong hujou hami ma si betet i, asa itasungkun hatana i.'),(8671,42,24,58,'Jadi dijou nasida ma si Rebekka, didok ma tu ibana: Atehe olo do ho mandongani bayo on? Jadi ninna ibana ma: Laho pe ahu.'),(8672,42,24,59,'Asa dipaborhat nasida ma si Rebekka, ibotonasida i rap dohot na parorot ibana dohot naposo ni si Abraham, ro di angka donganna sauduran.'),(8673,42,24,60,'Jadi dipasupasu nasida ma si Rebekka, jala didok tu ibana: Gabe ma ho ale ito marriburibu loksa, jala mandapot di pinomparmu ma bahal ni angka musuna.'),(8674,42,24,61,'Dung i borhat ma si Rebekka dohot angka radingna, marsihundul be ma nasida di unto mangihuthon baoa i. Asa laho ma naposo i mamboan si Rebekka.'),(8675,42,24,62,'Alai ro muli do ianggo si Isak sian na manopot sumur Lakhai-Roi, ai tano na tungkan dangsina do diingani.'),(8676,42,24,63,'Dung i marhira bodari ruar do si Isak tu ladang martangiang, gabe manatapnatap ibana, jadi diida ma ro angka unto.'),(8677,42,24,64,'Manatapnatap ma dohot si Rebekka, jadi diida ma si Isak, gabe manimbung ibana sian unto i.'),(8678,42,24,65,'Dung i ninna ma mandok naposo i: Ise do halak an na mardalandalan di ladang i manjumpangkon hita? I ma rajangki, ninna naposo i. Jadi dibuat nasida ma tujungtujung manghungkupi bohina i.'),(8679,42,24,66,'Dung i dipajojor naposo i ma tu si Isak sude hata angka na niulana i.'),(8680,42,24,67,'Jadi ditogihon si Isak ma si Rebekka tu undungundung ni si Sarah, inana i, dibuat ma si Rebekka bahen jolmana, jala holong rohana di ibana. Jadi sabam ma roha ni si Isak di panading ni inana i.'),(8681,42,25,1,'Dung i diulakkon si Abraham ma mambuat boru, na margoar si Ketura.'),(8682,42,25,2,'Na manubuhon di ibana: Si Zimran dohot si Joksan dohot si Medan dohot si Midian, si Isbak dohot si Sua.'),(8683,42,25,3,'Ia si Joksan manubuhon: Si Seba dohot si Dedan. Alai anggo angka anak ni si Dedan: halak Assusim dohot si Letusim dohot si Leummim.'),(8684,42,25,4,'Ia angka anak ni si Midian: Si Epah, si Eper, si Hanok, si Abida dohot si Elda. I ma angka anak ni si Ketura sudena.'),(8685,42,25,5,'Dung i dilehon si Abraham ma sude na di ibana i tu si Isak.'),(8686,42,25,6,'Alai anggo tu angka anak ni jolmana panoroni i, dilehon si Abraham angka silehonlehon, dung i dipajae nasida sian si Isak anakna i tu tano na tungkan habinsaran, tagan mangolu dope ibana.'),(8687,42,25,7,'Ia leleng ni si Abraham mangolu saratus pitupulu lima taon do.'),(8688,42,25,8,'Dung i tos ma hosana, jala mate sarimatua si Abraham, dung mahap rohana di hangoluan on, jadi dipapungu ibana tu angka ompuna.'),(8689,42,25,9,'Dung i ditanom si Isak dohot si Ismael, angka anakna i, ibana tu liang Makpelah, tu hauma ni si Epron, anak ni si Zohar, marga Het, na tungkan habinsaran ni Mamre.'),(8690,42,25,10,'Tu hauma, na tinuhor ni si Abraham sian angka anak ni si Het; disi do tartanom si Abraham dohot si Sarah jolmana i.'),(8691,42,25,11,'Jadi dung mate si Abraham, dipasupasu Debata ma si Isak, anakna i; alai maringan di lambung sumur Lakhai-Roi do si Isak i.'),(8692,42,25,12,'Angka on do pinompar ni si Ismael, anak ni si Abraham, tinubuhon ni si Hagar, boru Misir, hatoban ni si Sarah, di si Abraham:'),(8693,42,25,13,'Jala on do goar ni angka anak ni si Ismael mangihuthon partubunasida: Nabaiot buhabaju ni si Ismael, dung i Kedar, Abdeel, Mibsam.'),(8694,42,25,14,'Misma, Duma, Masa.'),(8695,42,25,15,'Hadad, Tema, Jetur, Napis dohot Kademah.'),(8696,42,25,16,'I ma angka anak ni si Ismael, jala i ma angka goarnasida mangihuthon hutana dohot parsaroanna be, sampulu dua raja, parserahan ni marganasida.'),(8697,42,25,17,'Ia leleng ni si Ismael mangolu saratus tolupulu pitu taon do, dung i tos ma hosana, jala mate ibana, jadi dipapungu ma ibana tu angka ompuna.'),(8698,42,25,18,'Olat ni huta Hevila sahat ro di Sur na marbalokkon Misir dohot Assur diingani nasida; tungkan habinsaran ni angka donganna i ibana ojak.'),(8699,42,25,19,'On ma angka pinompar ni si Isak, anak ni si Abraham. Si Abraham tumubuhon si Isak.'),(8700,42,25,20,'Jala marumur opatpulu taon si Isak di na binuatna i si Rebekka, boru ni si Betuel, isi ni Ram i, iboto ni si Laban, isi ni Ram i, bahen jolmana.'),(8701,42,25,21,'Dung i digogo si Isak ma tumangiangkon jolmana i tu Jahowa, ai na hol do ibana; jadi ditangihon Jahowa tangiangna, gabegabean ma si Rebekka jolmana i.'),(8702,42,25,22,'Dung i masitunjangan ma posoposo i di bortianna. Jadi ninna si Rebekka ma: Molo songon i hape boasa songon on ahu? Dung i laho ma ibana manungkunhonsa tu Jahowa.'),(8703,42,25,23,'Jadi ninna Jahowa ma tu ibana: Dua do bangso di bortianmi, jala dua marga sirang sian boltokmi, jala gumogo na samarga martimbangkon na sada nari, jala mandapot di siampudan do sihahaan i.'),(8704,42,25,24,'Jadi dung jumpang hasasangana i manubuhon, ida ma, linduat do di boltokna i.'),(8705,42,25,25,'Jadi ruar ma na parjolo i, rara do ibana, hira na marulos imbulu sandok dagingna i, jadi dibahen ma goarna si Esau.'),(8706,42,25,26,'Dung songon i, ruar ma anggina i, ditiop ma tambihul ni si Esau, jadi dibahen ma goarna si Jakkob. Onompulu taon ma umur ni si Isak di na tubu i nasida.'),(8707,42,25,27,'Jadi dung balga dakdanak i, marsiajar parburuon dohot parumaon ia si Esau; alai anggo si Jakkob jolma na daulat marroha do, maringan di angka undungundung.'),(8708,42,25,28,'Dihasiani si Isak do si Esau i, ala giot pamanganna mangan na binuru; alai hasian ni si Rebekka do ianggo si Jakkob.'),(8709,42,25,29,'Dung i mangalompa sipanganon ma si Jakkob, jadi ro ma si Esau, sian harangan, jala mansai loja ibana.'),(8710,42,25,30,'Jadi ninna si Esau ma mandok si Jakkob: Ua husohali ma sian na rara i, ai nunga mansai loja ahu. Ala ni do umbahen margoar Edom ibana.'),(8711,42,25,31,'Alai ninna si Jakkob ma: Gadis ma tu ahu sadari on sahala sihahaan!'),(8712,42,25,32,'Jadi ninna si Esau ma: Antong na laho mate na ma ahu, tu dihangku sahala sihahaan i?'),(8713,42,25,33,'Dung i ninna si Jakkob ma: Antong mangmangkon ma tu ahu sadari on! Jadi dimangmangkon tu ibana; jala digadis sahalana sihahaan i tu si Jakkob.'),(8714,42,25,34,'Dung i dilehon si Jakkob ma tu si Esau sagusagu dohot hadio na nilompana i; jadi mangan jala minum ibana, dung i hehe ma ibana laho muli. Asa marsineang ma roha ni si Esau di sahala sihahaan i.'),(8715,42,26,1,'Dung i masa ma muse haleon di tano i, di balik ni haleon parjolo i, na masa uju di ngolu ni si Abraham, jadi bungkas ma si Isak tu Gerar mandapothon si Abimelek, raja ni halak Palistim.'),(8716,42,26,2,'Dung i ditopot Jahowa ma ibana, didok ma: Unang ho tuat tu tano Misir, sai ingani ma tano sipaboaonku tu ho.'),(8717,42,26,3,'Maisolat ma ho di tano on, asa hudongani ho, jala hupasupasu ho; ai tu ho dohot tu pinomparmu lehononku sogot sandok tano on, jala pahotonku ma uari, na huuarihon i tu si Abraham, amami.'),(8718,42,26,4,'Jala patoroponku do pinomparmi, songon bintang angka na di langit, jala lehononku ma tu pinomparmi sandok tano on, jala dapotan pasupasu sogot saluhut bangso ni tano on marhitehite Pinomparmi.'),(8719,42,26,5,'Ala na tinangihon ni si Abraham soarangki, jala diradoti uhumhu, angka tonangku, angka patikku dohot podangku.'),(8720,42,26,6,'Asa maringan di Gerar ma si Isak i.'),(8721,42,26,7,'Alai ia usouso halak, isi ni luat i taringot tu jolmana, gabe didok ma: Ibotongku do ibana, ai mabiar do ibana mandok, jolmangku do i, asa unang dibunu isi ni luat i ibana, ala ni si Rebekka, ai na uli rupa do ibana.'),(8722,42,26,8,'Hape dung na nia leleng ibana disi, mandulo ma si Abimelek, raja ni halak Palistim sian pandiloan, gabe diida ma si Isak margait dohot si Rebekka jolmana i.'),(8723,42,26,9,'Jadi dijou si Abimelek ma si Isak, ninna ma: Antong jolmam do ibana hape, boasa ma tung dohononmu: Ibotongku ibana? Jadi ninna si Isak ma mandok ibana: Atek tung mate ahu maon humongkop ibana, ninna rohangku.'),(8724,42,26,10,'Dung i ninna si Abimelek ma: Boasa ma tung songon i bahenonmu tu hami? Otiknari diparangkupi sada sian bangso i jolmam modom, gabe parohon dosa ho tu hami.'),(8725,42,26,11,'Dung i ditingtingkon si Abimelek ma tu saluhut parripena i, ninna ma: Manang ise na mangkaori bayo on dohot jolmana i, ingkon marutang hosa ibana.'),(8726,42,26,12,'Dung i mangordang ma si Isak di tano i, jadi jumpangsa ma di na sataon i lompit saratus, ai dipasupasu Jahowa do ibana.'),(8727,42,26,13,'Jadi jolma na sangap ibana, gabe lam timbul situtu.'),(8728,42,26,14,'Torop ma birubiruna dohot lombuna, torop dohot naposona. Jadi mangiburu ma halak Palistim mida ibana.'),(8729,42,26,15,'Disosopi halak Palistim ma sude sumur, angka na hinali ni naposo ni amana, di ngolu ni si Abraham, amana i, jala digohi dohot tano.'),(8730,42,26,16,'Pola dohot si Abimelek mandok tu si Isak: Bungkas ma ho sian lambungnami on, ai sumangap situtu do ho sian hami.'),(8731,42,26,17,'Jadi bungkas ma si Isak sian i, mangulon ma ibana di rura Gerar, laos disi ibana maringan.'),(8732,42,26,18,'Dung i diulahi si Isak mangkali sumur angka na hinali uju di ngolu ni si Abraham, amana i, angka na sinosopan ni halak Palistim, dung mate si Abraham, jala dibahen goarna mangihuthon goar, angka na binahen ni amana i nahinan.'),(8733,42,26,19,'Mangkali dohot angka naposo ni si Isak di rura i, gabe jumpang nasida disi sada sumur (aek mual) na mangolu.'),(8734,42,26,20,'Alai dibadai angka parmahan na sian Gerar ma angka parmahan ni si Isak, ninna ma: Hami do nampuna aek on. Jadi dibahen ma goar ni Sumur i: Esek (parbadaan), ala na dibadai nasida ibana disi.'),(8735,42,26,21,'Dung i dihali nasida ma sada sumur na asing, ipe diguluti nasida do, jala dibahen ma goarna Sitna (pargulutan).'),(8736,42,26,22,'Jadi bungkas ma ibana sian i, dihali muse sada sumur na asing, ndang marbadai nasida ala ni, jadi dibahen ma goarna Rehobot (inganan na lambas), ai nunga dipalambas Jahowa di hita ninna, jala sopar hita di tano on tumpahanna.'),(8737,42,26,23,'Dung i bungkas ibana sian i nangkok tu Berseba.'),(8738,42,26,24,'Bornginna i ro ma Jahowa manopot ibana, didok ma: Ahu do Debata ni si Abraham, amami, unang ho mabiar, ai dongananku jala pasupasuonku do ho, jala patoroponku pinomparmu ala ni si Abraham naposongki.'),(8739,42,26,25,'Jadi dipauli ma disi sada langgatan, jala dipajoujou goar ni Jahowa, dipahembang ma disi undungundungna, jala dihali naposo ni si Isak disi sada sumur.'),(8740,42,26,26,'Dung i diebati si Abimelek ibana sian Gerar dohot si Ahuzat, alealena i dohot si Pikol, partogi ni paranganna.'),(8741,42,26,27,'Alai ninna si Isak ma mandok nasida: Boasa ro hamu manopot ahu, hasomoran ni rohamuna, jala dipabungkas hamu ahu sian lambungmuna i.'),(8742,42,26,28,'Jadi ninna nasida ma: Tandap do huida hami, didongani Jahowa ho, dibahen i hudok hami: Ua marbulan be ma hita, tabahen ma janjinta.'),(8743,42,26,29,'Na so tupa bahenonmu tu hami na jat, songon hami, ndang huhahua hami ho, holan uhum na denggan hubahen hami tu ho, jala mardamedame ho bungkas hupasombu hami. On pe na pinasupasu ni Jahowa do ho nuaeng.'),(8744,42,26,30,'Dung i dihobas si Isak ma panganonnasida, jadi mangan jala minum nasida.'),(8745,42,26,31,'Dung i manogotna i sogot be ma nasida hehe, jala masipabulanan nasida. Dung i dipaborhat si Isak ma nasida, gabe laho ma nasida mardamedame.'),(8746,42,26,32,'Sadarina i ro ma angka naposo ni si Isak paboahon tu ibana taringot tu sumur na hinalinasida i, angka ninna ma tu ibana: Nunga jumpangan aek hami.'),(8747,42,26,33,'Jadi dibahen ma goarna: Seba (parbulanan), ala ni do umbahen margoar huta i Berseba (sumur parbulanan) rasirasa nuaeng.'),(8748,42,26,34,'Jadi dung marumur opatpulu taon si Esau, dibuat ma si Judit, boru ni si Beeri, marga Het dohot si Basemat, boru ni si Elon marga Het, bahen jolmana.'),(8749,42,26,35,'Alai marsak do roha ni si Isak dohot si Rebekka dibahen nasida.'),(8750,42,27,1,'Jadi dung lam matua si Isak, rambon na matana, gabe dijou ma si Esau, anakna sihahaan i, ninna ma mandok ibana: Ale amang! Dison do ahu, ninna mangalusi ibana.'),(8751,42,27,2,'Dung i ninna si Isak ma: Ida ma, nunga matua ahu, ndang huboto ari hamamatengku.'),(8752,42,27,3,'On pe, buat ma ulaulam, parnangkatanmu dohot siormu, jala laho ma ho tu harangan, buru ma di ahu sada juhut ramba.'),(8753,42,27,4,'Jala pauli di ahu tipak sipanganon lomo ni rohangki, dung i boan tu ahu, asa hupangan, asa dipasupasu tondingku ho, tagan so mate dope ahu.'),(8754,42,27,5,'Alai dibege si Rebekka do pandok ni si Isak tu si Esau anakna i. Jadi borhat ma si Esau tu harangan marburu juhut ramba, siboanonna.'),(8755,42,27,6,'Dung i ninna si Rebekka ma tu si Jakkob anakna i: Boti ma i, nunga hubege pandok ni amam tu si Esau, hahami, ninna do:'),(8756,42,27,7,'Boan di ahu sada juhut ramba, jala pauli tipak sipanganon lomo ni rohangki, asa hupangan, asa hupasupasu ho di jolo ni Jahowa, tagan so mate dope ahu.'),(8757,42,27,8,'On pe, ale amang, tangihon ma soarangkon taringot tu na hudokkon tu ho.'),(8758,42,27,9,'Laho ma ne ho tu jampalan ni birubiru, jala alap di ahu sian i anak ni hambing tunggal dua, angka na halaeanna, asa hupauli bahen sipanganon ni amam, silomo ni rohana.'),(8759,42,27,10,'Ho ma mamboan i tu amam, asa dipangan, asa ho dipasupasu, tagan so mate dope ibana.'),(8760,42,27,11,'Alai ninna si Jakkob ma tu si Rebekka, inana i: Ida ma, rabuton do dahahang, si Esau i, ianggo ahu jolma na limus do.'),(8761,42,27,12,'Atek tung dijama damang ahu maon, jadi songon na paotootohon ibana ahu di rohana, gabe parohon torutoru ahu tu ahu, ndada pasupasu.'),(8762,42,27,13,'Dung i ninna inana i ma mandok ibana: Tu ahu pe torutoru na tu ho i, ale amang, asal ditangihon ho soarangki, laho ma ho, sai alap ma di ahu.'),(8763,42,27,14,'Jadi laho ma ibana mangalap, jala diboan tu inana i. Dung i dipauli inana i ma sipanganon, silomo ni roha ni amana i.'),(8764,42,27,15,'Dung i dipambuat si Rebekka ma angka ulos ni si Esau, anakna sihahaan i, angka na ummuli, angka na pineopna di jabu, jala diuloshon tu si Jakkob, anakna siampudan i.'),(8765,42,27,16,'Alai anggo tu tanganna dohot tu rungkungna, na so mapol i, dilangkophon do bobak ni anak ni hambing tunggal i.'),(8766,42,27,17,'Dung i dilehon ma sipanganon na tabo i dohot sagusagu na pinaulina i tipak tu tangan ni si Jakkob, anakna i.'),(8767,42,27,18,'Jadi ditopot ma amana i, didok ma: O ba, ale amang! Dison do ahu ninna. Ise do ho ale anaha?'),(8768,42,27,19,'Dung i ninna si Jakkob ma mandok amana i: Ahu ma si Esau, buhabajum, nunga hupauli songon na nidokmi tu ahu; ua hehe ma ho hundul, jala pangan sian na huburu on, asa ditumpak tondim ahu.'),(8769,42,27,20,'ai ninna si Isak ma mandok anakna i: Beha do pambahenmi, ale anaha, umbahen na sai tibu dapotan ho? Jahowa Debatam do manjumpangkon i tu ahu, ninna.'),(8770,42,27,21,'Jadi ninna si Isak ma mandok si Jakkob: Pajonok ma tuson, ale anaha, asa hudadapi ho, manang na tutu anakku si Esau ho, manang ndang.'),(8771,42,27,22,'Dung i dipajonok si Jakkob ma tu si Isak amana i. Jadi dung dijama ibana, didok ma: Ianggo soara i, soara ni Jakkob do, alai anggo tangan i, tangan ni si Esau do.'),(8772,42,27,23,'Laos so ditanda do ibana, ai jabuton do tanganna i, songon tangan ni si Esau, hahana i, jadi dipasupasu ma ibana.'),(8773,42,27,24,'Dung i ninna si Isak ma: Ho do tutu anakku, si Esau i? Ahu ma tutu, ninna.'),(8774,42,27,25,'Dung i ninna si Isak ma: Antong boan ma tu lambungkon, ale anaha, asa hupangan sian na binurum, asa ditumpak tondingku ho. Jadi dipajonok ma tu lambungna, gabe mangan ibana, diboan dohot anggur tu ibana, gabe diinum.'),(8775,42,27,26,'Dung i ninna si Isak, amana i ma mandok ibana: Ua pajonok ma, jala umma ahu, ale anaha!'),(8776,42,27,27,'Jadi dipajonok ma, jala diumma amana i; jadi dianggo si Isak ma uap ni angka ulosna i, jala dipasupasu ibana, ninna ma: Ida ma, sobokkon uap ni jampalan na pinasupasu ni Jahowa, uap ni anakki.'),(8777,42,27,28,'Sai dilehon Debata ma tu ho nambur na sian banua ginjang, dohot tano na marmiak, asa godang emem dohot anggur.'),(8778,42,27,29,'Ingkon mangoloi tu ho ma angka bangso jala marsomba tu ho angka marga. Sai ho ma raja ni angka haham, jala marsomba tu ho angka anak ni inam. Hona bura ma angka na mamburai ho, jala dapotan pasupasu angka na mamasumasu ho.'),(8779,42,27,30,'Jadi dung sun si Isak mamasumasu si Jakkob i, jala ipe ruar ibana sian lambung ni si Isak, amana i, ro ma soluk si Esau hahana i sian parburuonna.'),(8780,42,27,31,'Ibana pe, tipak ma dipauli sipanganon, jala diboan tu amana, jala didok mandok amana i: Hehe ma ho, ale amang, jala pangan sian na binuru ni anakmi, asa ditumpak tondim ahu.'),(8781,42,27,32,'Jadi ninna si Isak, amana i ma mandok ibana: Ise do ho? Si Esau, anak buhabajum do ahu, ninna.'),(8782,42,27,33,'Jadi tarsonggot situtu ma si Isak, didok ma: Antong ise do parburu ondeng, na mamboan binuruna i tu ahu, gabe hupangan i, tagan so ro ho, jala nunga hupasupasu ibana? Nunga sahat tu ibana pasupasu i.'),(8783,42,27,34,'Jadi dung dibege si Esau angka hata ni amana i, dipangiar ma soarana mangangguk badar, jala habot situtu rohana, didok ma mandok amana i: Sai pasupasu ma dohot ahu, ale amang!'),(8784,42,27,35,'Alai ninna amana i ma: Nunga ro anggim, angkal dibahen mambuat pasupasum.'),(8785,42,27,36,'Leak ma dibahen goarna si Jakkob, ai nunga dua hali ahu dirupa: Nunga dibuat hian sahalangku sihahaan, pasupasu na di ahu hian pe, nunga dibuat. Dung i didok ma: Beha, ndang adong be dipeop ho pasupasu di ahu?'),(8786,42,27,37,'Dung i ninna si Isak ma mangalusi si Esau: Ida ma, nunga hupabangkit ibana gabe rajam, jala nunga hupadapot tu ibana sude angka hahana bahen naposona; nunga hutumpakhon eme dohot anggur tu ibana. Aha dope bahenonku tu ho, ale amang?'),(8787,42,27,38,'Dung i ninna si Esau ma mandok amana i: Ai holan sasada i do pasupasum, ale amang? Sai pasupasu ma dohot ahu, ale amang! Jadi dipangiar si Esau ma soarana tumatangis.'),(8788,42,27,39,'Dung i ninna si Isak, amana i ma mangalusi ibana: Antong, ndang marmiak tano ingananmi sogot, jala ndang dapotan nambur sian banua ginjang.'),(8789,42,27,40,'Jala podangmu hangoluhononmu, jala mandapot di anggim ho. Alai molo sai marungkil ho, sai na runtunonmu do augana sian rungkungmu sogot.'),(8790,42,27,41,'Dung i sogo situtu ma roha ni si Esau mida si Jakkob i, ala pasupasu, na pinasupasuhon ni amana i tu ibana, gabe ninna si Esau ma di bagasan rohana: Lam jonok na ma angka ari parandungan ni damang, ai bunuonku ma anggingku, si Jakkob i.'),(8791,42,27,42,'Hape tarbortik ma tu si Rebekka angka hata ni si Esau, anakna sihahaan i, jadi disuru ma manjou si Jakkob, anakna siampudan i, ninna ma mandok ibana: Ida ma, nunga ditahi haham si Esau i martubolhon pambahenmi, naeng bunuonna ho.'),(8792,42,27,43,'On pe, ale anaha, sai tangihon ma soarangkon: Borhat ma ho laho lintun tu Haran, manopot si Laban, ibotongki.'),(8793,42,27,44,'Sorang tu ibana ma ho manang piga ari on lelengna, paima mombun rimas ni haham.'),(8794,42,27,45,'Paima mulak rohana mida ho, jala dihalupahon na binahenmi tu ibana; dung i husuru pe mangalapi ho sian i. Boasa ma tung agoan sinuantunas na dua simardung ahu di na sadari?'),(8795,42,27,46,'Dungi  i ninna si Rebekka ma mandok si Isak: Marmahapmahap ahu di hangoluan, dibahen angka boru Het i; molo dohot muse si Jakkob mambuat sian angka boru Het i bahen jolmana, songon angka indi on, boru ni isi ni tano on, boasa ingkon di hangoluan on be ahu?'),(8796,42,28,1,'Dung i dijou si Isak ma si Jakkob, jala dipasupasu ibana, didok ma manonai ibana: Unang buat sian angka boru Kanaan bahen jolmam.'),(8797,42,28,2,'Borhat ma ho laho tu Padang-Aram, tu jabu ni si Betuel, ama ni inam, sian i ma buat bahen jolmam, sian angka boru ni si Laban, iboto ni inam.'),(8798,42,28,3,'Sai gabe ma ho jala torop dipasupasu Debata na sun hinagogo i, asa torop bangso marompu tu ho.'),(8799,42,28,4,'Sai dilehon ma tu ho dohot tu pinomparmu pasupasu na tu si Abraham i, asa ho manean tano paisolatanmu, na nilehon ni Debata tu si Abraham i.'),(8800,42,28,5,'Jadi diparbohat si Isak ma si Jakkob i, asa laho ibana tu Padang-Aram, tu si Laban, anak ni si Betuel, halak Ram i, iboto ni si Rebekka, ina ni si Jakkob dohot si Esau.'),(8801,42,28,6,'Jadi dung diida si Esau, naung dipasupasu si Isak si Jakkob i, jala dipaborhat ibana tu Padang-Aram, mangalap jolmana sian i, jala na nioraanna i, di na pinasupasuna i ibana, mambuat sian boru Kanaan, bahen jolmana.'),(8802,42,28,7,'Jala naung ditangihon si Jakkob hata ni amana dohot inana laho tu Padang-Aram.'),(8803,42,28,8,'Diida si Esau huhut sogo do roha ni si Isak, amana i mida boru Kanaan.'),(8804,42,28,9,'Jadi laho ma si Esau tumopot si Ismael, jala dibuat si Mahalat, boru ni si Ismael, anak ni si Abraham, iboto ni si Nabaiot bahen jolmana, imbang ni angka jolmana hian.'),(8805,42,28,10,'Alai borhat do anggo si Jakkob sian Berseba laho tu Haran.'),(8806,42,28,11,'Jadi tolhas ma ibana tu sada parbornginan, gabe marborngin ibana disi, ai nunga mate mata ni ari. Dung i dipambuat ma angka batu na disini bahen halang ni uluna, laos modom ibana disi.'),(8807,42,28,12,'Dung i marnipi ma ibana, diida ma sada tangga, na pinagohon tu tano, tolhas ro di langit ia ujungna, jadi ida ma, sai marnaek mijur sian i angka surusuruan ni Debata.'),(8808,42,28,13,'Jongjong di ginjangna anggo Jahowa, jala ninna do: Ahu do Jahowa, Debata ni ompum si Abraham dohot Debata ni si Isak, ia tano pinodomanmon lehononku ma tu ho dohot tu pinomparmu.'),(8809,42,28,14,'Jala sobokkon orbuk ni tano on sogot torop ni pinomparmu, jala angka rarat ma hamu tu hasundutan dohot tu habinsaran dohot tu utara dohot tu dangsina, jala dapotan pasupasu sogot nasa marga na di tano on marhitehite ho do pinomparmi.'),(8810,42,28,15,'Ida ma, sai dongananku jala ramotanku do ho manang tu dia pe ho mangalangka, jala togihononku do ho mulak tu tano on; ai ndang tadingkononku ho, nda jolo sahat hubahen na huparbaga i tu ho.'),(8811,42,28,16,'Dung i tarsunggul ma si Jakkob sian na modom i, jadi didok ma: Inganan ni Jahowa do on hape, so pamotoanku.'),(8812,42,28,17,'Jadi mabiar ma ibana, ninna ma: Na songkal situtu do inganan on, ndang tarbahen so on bagas ni Debata, jala baba pintu ni langit.'),(8813,42,28,18,'Dung i hehe ma si Jakkob sogot manogotna i, dibuat ma batu pinarhalanguluna i, jala dipajongjong bahen partinandaan, jala diusehon miak tu ginjangna.'),(8814,42,28,19,'Dung i dibahen ma goar ni inganan i Betel (bagas ni Debata), Lus do goar ni huta i anggo na sai laon.'),(8815,42,28,20,'Dung i marbagabaga ma si Jakkob, ninna do: Molo didongani jala diramoti Debata ahu di dalan na hudalani on, jala dilehon di ahu sagusagu sipanganon dohot ulos siporuloshononhon.'),(8816,42,28,21,'Jala marsonangsonang ahu mulak ditogihon tu bagas ni damang, sai Jahowa ma Debatangku.'),(8817,42,28,22,'Jala batu na hupajongjong on bahen partinandaan, i ma gabe bagas ni Debata, jala sude na nilehonmu tu ahu parsampuluonku ma i tu Ho.'),(8818,42,29,1,'Dung i dilangkahon si Jakkob ma patna laho tu tano ni halak habinsaran.'),(8819,42,29,2,'Dung i manatapnatap ma ibana, gabe diida ma sada sumur di jampalan, jala mangulon di lambungna angka birubiru tolu parduan, ai sian sumur i do dipasobur halak angka birubiru i. Alai batu bolon langkop ni sumur i.'),(8820,42,29,3,'Sai marpungu tusi somal sude angka parduan i, asa digulang nasida batu langkop ni sumur i pasoburhon angka birubiru i, dung i dipasuang muse batu i tu baba ni sumur i.'),(8821,42,29,4,'Dung i ninna si Jakkob ma mandok nasida: Sian dia hamu, ale dongan? Jadi ninna nasida ma: Sian Haran do hami.'),(8822,42,29,5,'Dung i didok ma mandok nasida: Ditanda hamu do si Laban, anak ni si Nahor? Hutanda hami do, ninna nasida.'),(8823,42,29,6,'Dung i ninna ibana mandok nasida: Mardamedame do ibana? Jadi ninna nasida ma: Marsonangsonang do; jala ida. Si Rahel, boruna i ma na mangadu birubiru an.'),(8824,42,29,7,'Dung i didok si Jakkob ma: Ida ma, ginjang dope ari, ndang dope jumpang tingki papunguhon pinahan; pasobur hamu birubiru i, dung i aduadu hamu pajampalhonsa.'),(8825,42,29,8,'Jadi ninna nasida ma: Nda jolo marpungu sude angka parduan i, ndang tarbahen hami manggulang batu, langkop ni sumur i laho pasoburhon birubiru i.'),(8826,42,29,9,'Mangkatai dope ibana dohot nasida, nunga ro si Rahel mangadu birubiru ni amana i, ai ibana do parmahan.'),(8827,42,29,10,'Alai dung diida si Jakkob si Rahel i, boru ni si Laban, iboto ni inana i, ro di birubiru ni si Laban, tulangna i, dipajonok si Jakkob ma, jala digulang batu, langkop ni sumur i, dung i dipasobur birubiru ni tulangna i.'),(8828,42,29,11,'Dung i diumma si Jakkob ma si Rahel i, jala dipangiar soarana tumatangis.'),(8829,42,29,12,'Dung i dipaboa si Jakkob tu si Rahel, bere ni amana i ibana, anak ni si Rebekka. Jadi marlojong ma si Rahel paboahon tu amana.'),(8830,42,29,13,'Asa dung dibege si Laban barita ni si Jakkob, anak ni ibotona i, marlojong ma ibana manomunomu, dihaol ma ibana jala diumma, dung i ditogihon ibana tu jabuna. Jadi dipajojor si Jakkob ma tu si Laban sude angka hata ondeng.'),(8831,42,29,14,'Dung i ninna si Laban ma mandok ibana: Antong holiholingku jala sibukku do ho hape! Jadi disi ma ibana arga sabulan lelengna.'),(8832,42,29,15,'Dung i ninna si Laban ma mandok si Jakkob: Tung sibasabasam ma mangula di ahu, ala dung berengku ho? Paboa ma di ahu manang aha upam!'),(8833,42,29,16,'Alai dua do boru ni si Laban, goar ni sihahaan si Lea, goar ni sianggian si Rahel.'),(8834,42,29,17,'Alai odapon do mata ni si Lea, uli do anggo bohi dohot rupa ni si Rahel i.'),(8835,42,29,18,'Jadi solo ma roha ni si Jakkob mida si Rahel i, didok ma: Hupangulahon pe ho pitu taon on mambuat si Rahel, borum siampudan i.'),(8836,42,29,19,'Jadi ninna si Laban ma: Tagonan tu ho ibana hulehon, asa tu halak na asing, inganhon ma ahu.'),(8837,42,29,20,'Asa mangula ma si Jakkob pitu taon mambuat si Rahel, jala sobokkon pitu ari do i di rohana, dibahen holong ni rohana mida ibana.'),(8838,42,29,21,'Dung i ninna si Jakkob ma mandok si Laban: Lehon ma tu ahu jolmangki, ai nunga jumpang padanta i, asa hutopot ibana.'),(8839,42,29,22,'Jadi dipapungu si Laban ma sude isi ni huta i mangalehon mangan.'),(8840,42,29,23,'Alai dung bodari, dibuat ma boruna si Lea, jala diboan tu si Jakkob, gabe i ma ditopot.'),(8841,42,29,24,'Dung i dilehon si Laban ma tu ibana si Zilpa, naposona boruboru bahen rading ni si Lea boruna i.'),(8842,42,29,25,'Alai manogotna i, ida ma, si Lea do hape, jadi ninna si Jakkob mandok si Laban: Boasa tung songon i bahenonmu tu ahu? Nda si Rahel do na hubuat umbahen hupangulahon ho, boasa tung paotootoonmu ahu?'),(8843,42,29,26,'Jadi ninna si Laban ma: Ndang songon i hasomalan di luatnami on, tung jumolo pamulihon sianggian sian sihahaan.'),(8844,42,29,27,'Jabuhon maon pitu borngin on, asa hulehon di ho dohot sianggian i, molo dipangulahon ho ahu pitu taonnari.'),(8845,42,29,28,'Jadi dibahen si Jakkob ma songon i, dijabuhon ma pitu borngin. Dung i dilehon si Laban ma tu ibana boruna si Rahel i, bahen jolmana.'),(8846,42,29,29,'Dung i dilehon si Laban ma tu si Rahel, boruna i, si Bilha, naposona i, bahen radingna.'),(8847,42,29,30,'Dung i ditopot si Jakkob ma dohot si Rahel, jala humolong rohana di si Rahel, asa di si Lea, jala dipangulahon si Laban pitu taonnari.'),(8848,42,29,31,'Alai dung diida Jahowa, tung na so hahua si Lea i, marhangoluan ma ibana dibahen, alai na hol do anggo si Rahel i.'),(8849,42,29,32,'Jadi gabegabean ma si Lea, ditubuhon ma anak sada, dibahen ma goarna si Ruben; ai ninna do: Ala nunga ditatap Jahowa dangolhi, ai holong ma nuaeng roha ni sinondukki di ahu.'),(8850,42,29,33,'Dung i tumagam haroan muse ibana, jala ditubuhon anak sada, jala ninna: Ala naung ditangihon Jahowa, tung na so hahua ahu, dibahen jadi dilehon tu ahu dohot Anak on, gabe dibahen ma goarna si Simeon.'),(8851,42,29,34,'Dung i tumagam haroan muse ibana, jala ditubuhon anak sada, ninna ma: Bali tu ahu ma nuaeng sinondukki, ai nunga tolu anakna hutubuhon; ala ni dibahen ma goarna si Lepi.'),(8852,42,29,35,'Dung i tumagam haroan muse ibana, jala ditubuhon anak sada, gabe ninna ma: Pujionku ma Jahowa anggo sahali on; ala ni dibahen ma goarna si Juda (sipujion); dung i mansohot ma ibana mangintubu.'),(8853,42,30,1,'Alai dung diida si Rahel, na so tubuan anak ibana di si Jakkob, mangiburu ma ibana di hahana i, didok ma tu si Jakkob: Patupa anak di ahu, molo so adong, mate ma ahu.'),(8854,42,30,2,'Jadi tarrimas ma si Jakkob mida si Rahel i, ai ahu do huroha singkat ni Debata, nda Ibana do tumiop parbue ni bortianmi?'),(8855,42,30,3,'Alai ninna si Rahel ma, ida ma, ndi radingku si Bilha, ibana ma topot, asa ditubuhon tu ampuanku, gabe maruli dohot ahu marhite sian ibana.'),(8856,42,30,4,'Jadi dilehon ma tu ibana si Bilha, radingna i, bahen jolmana, gabe ditopot si Jakkob ma.'),(8857,42,30,5,'Jadi gabegabean ma si Bilha, jala ditubuhon anak sada di si Jakkob.'),(8858,42,30,6,'Dung i ninna si Rahel ma nunga dapotan uhum ahu dibahen Debata, ditangihon dohot soarangki, jala dilehon di ahu anak sada; ala ni dibahen ma goarna si Dan.'),(8859,42,30,7,'Dung i tumagam haroan muse si Bilha, rading ni si Rahel i, jala ditubuhon anak paduahon di si Jakkob.'),(8860,42,30,8,'Jadi ninna si Rahel ma: Angka parsirangguton ni Debata huparsirangguthon dohot dahahang i, gabe monang ahu, jadi dibahen ma goarna si Naptali.'),(8861,42,30,9,'Jadi dung diida si Lea, naung mansohot ibana mangintubu, dibuat ma si Zilpa radingna i, mangalehon ibana tu si Jakkob bahen jolmana.'),(8862,42,30,10,'Asa ditubuhon si Zilpa, rading ni si Lea i anak sada di si Jakkob.'),(8863,42,30,11,'Dung i ninna si Lea ma: Nunga ro tua i, jadi dibahen ma goarna si Gad.'),(8864,42,30,12,'Dung i ditubuhon si Zilpa, rading ni si Lea i anak paduahon di si Jakkob.'),(8865,42,30,13,'Jadi ninna si Lea ma: Tuangki antong! Ai parhatuaon ni angka boruboru i ma ahu, jadi dibahen ma goarna si Aser.'),(8866,42,30,14,'Dung i di na masa gotilon laho ma si Ruben tu balian, gabe jumpangsa ma angka dudaim (bahen dorma) di tonga ni hauma, jala diboan tu si Lea inana i. Dung i ninna si Rahel ma mandok si Lea: Ua lehon ahu deba sian angka dudaim ni anakmu.'),(8867,42,30,15,'Jadi ninna si Lea ma mandok ibana: Na hurang dope huroha di roham binuatmi roha ni sinondukki, dohot do angka dudaim ni anakki naeng buatonmu? Jadi ninna si Rahel ma: Antong di lambungmu ma ibana modom saborngin on ali ni angka dudaim ni anakmi.'),(8868,42,30,16,'Jadi dung muli si Jakkob sian hauma bodarina i, ditomutomu si Lea ma ibana, ninna ma: Tu ahu do ho ro, ai nunga hutuhor ho ali ni angka dudaim ni anakki. Jadi rap modom nasida di na saborngin i.'),(8869,42,30,17,'Dung i ditangihon Debata ma si Lea, gabegabean ma ibana, jala ditubuhon anak palimahon di si Jakkob i.'),(8870,42,30,18,'Jadi ninna si Lea ma: Nunga dilehon Debata upangku, ala na hulehon i radingku tu sinondukki, jadi dibahen ma goarna si Isasar.'),(8871,42,30,19,'Dung i tumagam haroan muse si Lea i, jala ditubuhon anak paonomhon di si Jakkob.'),(8872,42,30,20,'Jadi ninna si Lea ma: Silua na denggan disiluahon Debata tu ahu. Inganhonon ni sinondukku ma ahu sahali on, ai nunga hutubuhon di ibana onom anak, jadi dibahen ma goarna si Zebulon.'),(8873,42,30,21,'Dung i ditubuhon ma boru sada, jala dibahen goarna si Dina.'),(8874,42,30,22,'Alai diingot Debata muse si Rahel, jala ditangihon pangidoanna, dibungka ma bortianna i.'),(8875,42,30,23,'Jadi tumagam haroan ibana, jala ditubuhon anak sada, ninna ma: Nunga didaek Debata haurahonku.'),(8876,42,30,24,'Jadi dibahen ma goarna si Josep, ninna ma: Sai ditambai Jahowa ma tu ahu anak sada nari!'),(8877,42,30,25,'Jadi dung ditubuhon si Rahel si Josep i, ninna si Jakkob ma mandok si Laban: Paborhat ma ahu, asa laho ahu tumopot ingananku dohot tanongku.'),(8878,42,30,26,'Lehon ma angka jolmangki dohot angka tubungku, angka na husamothon sian hamu, asa laho ahu, ai diboto hamu do parulangku, na hupangulahon i hamu.'),(8879,42,30,27,'Jadi ninna si Laban ma mandok ibana: Sai maruli ma ahu mangido asi ni roham! Ai patar do ditanda rohangku naung dipasupasu Jahowa ahu ala ho.'),(8880,42,30,28,'Bohoi tu ahu upam silehononku, ninna.'),(8881,42,30,29,'Dung i didok si Jakkob ma mandok ibana: Hamu pe, diboto hamu do parulangku di hamu dohot panjadi ni pinahanmuna saleleng ahu patureturehon.'),(8882,42,30,30,'Ai otik do adong di hamu, tagan so ro ahu, nunga manjarar gabe deak, jala dipasupasu Jahowa hamu marhite sian ahu. On pe, sadihari pe pangulahononku dohot isi ni bagashi?'),(8883,42,30,31,'Jadi ninna si Laban ma: Antong aha ma lehononku tu ho? Dung i ninna si Jakkob ma: Ndang manang aha lehononmu di ahu, molo dipatupa hamu hatangkon tu ahu, ulahanku ma marmahan pinahanmuna i.'),(8884,42,30,32,'Bolusonku ma sadari on sude punguan ni birubirumuna, ba papulik hamu ma sian i sude pinahan na marrintik dohot siganding, ro di sude na birong sian angka anak ni birubiru i; na marrintik dohot sibolang sian angka hambing pe, i ma gabe upangku.'),(8885,42,30,33,'Asa hatigoranki tumindangi ahu sogot, molo ro hamu tumingkir upangki: Sude na so marrintik, jala na so sibolang sian angka hambing i, jala na so birong sian angka anak ni birubiru i, na tinangko ma i molo di ahu.'),(8886,42,30,34,'Jadi ninna si Laban ma: Antong songon na nidokmi ma tutu!'),(8887,42,30,35,'Dung i dipapulik ma di na sadari i hambing tunggal angka na marrintik dohot angka sibolang, ro di sude hambing boruboru angka na marrintik dohot angka sibolang, sude angka na marsoar na bontar dohot sude na birong sian angka birubiru i, dung i dipasahat ma tu tangan ni angka anakna i.'),(8888,42,30,36,'Dung i dibahen si Laban ma partoluarian holangkolangna dohot si Jakkob. Asa diparmahan si Jakkob ma punguan birubiru ni si Laban angka na tading i.'),(8889,42,30,37,'Alai dibuat si Jakkob ma dangka ni hau meang dohot anturmangan dohot andulpak, jala dilangkati margollotgollot.'),(8890,42,30,38,'Dung i dipampeakkon ma dangka angka na nilangkatanna i tu parsoburan ni angka birubiru i, asa dipaadopadop, asa marbaringin nasida di na marsobur i.'),(8891,42,30,39,'Jadi marlambe ma angka birubiru maradophon angka dangka i, gabe ditubuhon ma na marrintik angka sibolang dohot siganding.'),(8892,42,30,40,'Dung i dipapulik si Jakkob ma angka anak ni birubiru i, jala didompakkon angka birubiru i tu na marrintik dohot na birong sian pinahan ni si Laban. Dung i pulik ma dibahen inganan ni pinahanna i, ndang be saor dibahen tu pinahan ni si Laban.'),(8893,42,30,41,'Alai molo rumogon birubiru, angka na umbolon i, dipampeakkon si Jakkob ma muse angka dangka i tu angka parsoburan i, gabe dipaadopadop laho marjantan.'),(8894,42,30,42,'Alai molo ro birubiru, angka na marpengpeng, ndang dibahen. Jadi di si Laban ma angka na marpengpeng i, alai angka na bolon i di si Jakkob do.'),(8895,42,30,43,'Jadi mamora bulung ma si Jakkob, torop ma angka birubiruna dohot naposona boruboru dohot baoa, angka unto dohot halode.'),(8896,42,31,1,'Dung i tarbortik ma tu si Jakkob hatahata ni angka anak ni si Laban, angka na mandok: Nunga diahuti si Jakkob sinamot ni amanta sasudena, jala marharoroan sian na di amanta i do hamoraonna on sudena.'),(8897,42,31,2,'Bohi ni si Laban pe, ndang songon na sai laon be dompak ibana, diida si Jakkob.'),(8898,42,31,3,'Dung i ninna Jahowa ma mandok si Jakkob: Mulak ma ho tu tano ni angka ompum, tu tano hagodanganmu, hudongani pe ho.'),(8899,42,31,4,'Jadi disuru si Jakkob ma manjou si Rahel dohot si Lea tu panjampalan punguan ni birubiruna i.'),(8900,42,31,5,'Jala didok ma mandok nasida: Nunga huida bohi ni amanta i, ndang be songon na sai laon dompak ahu; alai didongani Debata ni damang i do ahu.'),(8901,42,31,6,'Jala diboto hamu do, nasa gogongku hubahen mangulahon amanta i.'),(8902,42,31,7,'Alai sai dipaotooto amanta i do ahu, jala sampulu hali diuba upangki; alai ndang diloas Debata ibana mangago ahu.'),(8903,42,31,8,'Molo didok ibana songon on: Angka na marrintik ma upam, jadi angka na marrintik i dipantubuhon sude birubiru i. Alai molo didok songon on: Angka sibolang i ma upam, jadi angka sibolang dipantubuhon, sude birubiru i.'),(8904,42,31,9,'Asa Debata do mangarampas sinamot ni amanta i, jala dilehon tu ahu.'),(8905,42,31,10,'Ai huida do di bagasan nipingku, di tingki harorongom ni angka pinahan i, angka tunggal na marrintik dohot sibolang dohot siganding mangangkati angka birubiru i.'),(8906,42,31,11,'Jadi didok surusuruan ni Debata ma mandok ahu di bagasan nipingku: Jo Jakkob! Dison do ahu, ningku.'),(8907,42,31,12,'Dung i ninna ibana ma: Ua pameong jolo matam jala ida, angka na marrintik, jala sibolang, jala siganding sude birubiru tunggal angka na mangangkati angka birubiru boruboru, ai nunga huida sude na binahen ni si Laban i tu ho.'),(8908,42,31,13,'Ahu do Debata uju na di Betel i, uju na miniahanmu disi partinandaan i, jala dibagabaga i ho Ahu. On pe, borhat ma ho, bungkas sian tano on, jala mulak tu tano hagodanganmi.'),(8909,42,31,14,'Dung i ninna si Rahel dohot si Lea ma mangalusi ibana: Apala adong be tagamon jambar, manang ugasan siteanonnami di jabu ni amanta i.'),(8910,42,31,15,'Nda sobokkon halak na pulik marga na ma hami di rohana? Ai digadis do hami, jala nunga sun dipangan dohot tuhornami.'),(8911,42,31,16,'Ai nasa hamoraon na nirampas ni Debata sian amanta i, hita dohot anakta i hian do nampunasa. On pe, bahen ma sude na tinonahon ni Debata tu ho.'),(8912,42,31,17,'Jadi hehe ma si Jakkob, dipahundul ma angka anakna dohot jolmana tu angka unto.'),(8913,42,31,18,'Diboan ma dohot pinahanna luhut ro di nasa ugasanna, na niomona i, pinahan hapunjunganna pe, na sinamotsamotna i di Padang-Aram laho tumopot si Isak, amana i tu tano Kanaan.'),(8914,42,31,19,'Alai laho manggustingi imput ni birubiruna i anggo si Laban, jadi ditangko si Rahel ma angka terapim (Debata hidup) ni amana i.'),(8915,42,31,20,'Asa dipalungun si Jakkob ma roha ni si Laban, halak Ram i, ala so dipaboa tu ibana na laho lintun i ibana.'),(8916,42,31,21,'Asa lintun ma si Jakkob, diboan dohot sude na di ibana i, borhat ma ibana laho manaripari batang aek i, dung i ditondong ma dompak dolok Gilead.'),(8917,42,31,22,'Patolubornginhon adong ma na mamaritahon tu si Laban naung lintun i si Jakkob.'),(8918,42,31,23,'Jadi diarahon si Laban ma angka hahaanggina i mangeahi ibana, parpituarian pe, asa dapotsa ibana di dolok Gilead.'),(8919,42,31,24,'Alai ro ma Debata bornginna i paipihon tu si Laban, halak Ram i, ninna ma mandok ibana: Jaga ho! So tung ditongkari ho si Jakkob i.'),(8920,42,31,25,'Asa dapot si Laban ma si Jakkob i, ai dipagohon si Jakkob do undungundungna di dolok i; si Laban pe dipagohon do undungundungna di dolok Gilead rap dohot angka hahaanggina i.'),(8921,42,31,26,'Dung i ninna si Laban ma mandok si Jakkob: Dia do na binahenmi? Tole dipalungun ho rohangki, tole diboan ho angka borungku, hira tabantaban do nasida na dapot podang?'),(8922,42,31,27,'Boasa tung marsibuni ho laho lintun, so martinading hata ho tu ahu, asa hupataru ho nian, hugondanggondangi hamu nian, paboa godang ni rohangku.'),(8923,42,31,28,'So manang diloas ho ahu mangumma angka pahompungku dohot angka borungku; asa na oto do pambahenmi.'),(8924,42,31,29,'Tuk do nian gogo ni tanganku mambahen uhum na roa tu hamu; alai na ro do na borngin nantuari hata ni Debata ni amamuna tu ahu, ninna do: Jaga ho! So tung ditongkari ho si Jakkob i.'),(8925,42,31,30,'On pe, ia na naeng laho ho ala na malungun ho di bagas ni amamu, boasa ma tung tangkoonmu angka debatangku?'),(8926,42,31,31,'Dung i ninna si Jakkob ma mangalusi si Laban: Dibahen na mabiar do ahu, ai ninna rohangku do: Atek tung rampasonmu maon angka borumi sian ahu.'),(8927,42,31,32,'Alai molo tung jumpang ho angka debatami, di manang ise, marutang hosa ma ibana; asa sungkapi hamu ma di jolo ni angka donganta on; molo adong manang aha ugasanmu di ahu, buat di ho. Alai ndang pamotoan ni si Jakkob na tinangko ni si Rahel do i hape.'),(8928,42,31,33,'Jadi dibongoti si Laban ma undungundung ni si Jakkob dohot undungundung ni si Lea dohot undungundung ni rading na dua i, alai ndang adong jumpangsa. Dung i ruar ma ibana sian undungundung ni si Lea mambongoti undungundung ni si Rahel.'),(8929,42,31,34,'Alai nunga dibuat si Rahel hian angka terapim i, jala dibahen tu toru ni palano ni unto, asa dihunduli. Jadi disungkapi si Laban ma undungundung i luhutna, hape ndang adong jumpangsa.'),(8930,42,31,35,'Jadi ninna si Rahel ma tu amana i: Unang tung tarrimas roha ni tuangku, ala so tarbahen ahu hehe maradophon ho, ai masa do di ahu hasomalan ni boruboru. Asa didiori pe, laos so jumpangsa angka terapim i.'),(8931,42,31,36,'Jadi tarrimas ma si Jakkob, dibadai ma si Laban i, ninna si Jakkob ma mangalusi si Laban i: Aha ma pangalaosingku, aha ma dosangku umbahen piripirion hamu mangeahi ahu?'),(8932,42,31,37,'Nunga disungkapi hami ugasanku saluhutna, aha ma jumpang hamu sian nasa ugasan ni jabum? Peakkon ma tu jolo ni angka donganki dohot donganmi, asa diuhumi nasida hita na dua.'),(8933,42,31,38,'Nunga pola duapulu taon, sai di lambungmu do ahu, ndang dung tarposo birubirumu, manang hambingmu, jala ndang dung hupangan hambing tunggal ni birubirumuna.'),(8934,42,31,39,'Ndang dung hutaruhon tu ho na sinoro ni binatang sai hualii do i; sai dipangido ho do sian ahu na tinangko ni halak nang arian nang borngin.'),(8935,42,31,40,'Ia arian las ni ari manganhon ahu, ia borngin ngali ni ari do, jala ndang tarnono matangku.'),(8936,42,31,41,'Na duapulu taon on sai di jabum do ahu mangulahon hamu, sampulu opat taon mangomo borum na dua i, jala onom taon mangomo angka pinahanmu; alai sampulu hali dipauba hamu upangki.'),(8937,42,31,42,'Aut unang didongani Debata ni damang i ahu, Debata ni si Abraham, jala na hinabiaran ni si Isak, ra tung palahoonmu boti ahu; alai ditatap Debata do pardangolanki dohot halojaon ni tanganku, gabe dipinsang hamu na borngin nantuari.'),(8938,42,31,43,'Jadi ninna si Laban ma mangalusi si Jakkob: Borungku do angka boruboru i, jala anakku do angka anak i, jala pinahanku angka pinahan i, jala sude na niidam, ahu nampunasa; asa hahuaonku ma sadari on angka borungkon, manang anaknasida, angka na tinubuhonnasida i.'),(8939,42,31,44,'On pe, tole tabahen ma padan, ahu dohot ho, i ma sitindanginta sogot.'),(8940,42,31,45,'Jadi dibuat si Jakkob ma sada batu, jala dipajongjong bahen partinandaan.'),(8941,42,31,46,'Dung i ninna si Jakkob ma mandok angka donganna i: Papungu hamu ma angka batu. Jadi dipambuat nasida ma angka batu mambahen punggupunggu, laos mangan nasida disi di atas punggupunggu i.'),(8942,42,31,47,'Dung i dibahen si Laban ma goarna: Jegar-Sahaduta; alai Galed goarna dibahen si Jakkob.'),(8943,42,31,48,'Dung i ninna si Laban ma: Punggupunggu on ma sitindanginta sadari on, ala ni do umbahen margoar Galed;'),(8944,42,31,49,'Dohot Mispa, ai ninna do: Sai ditatap Jahowa ma hita, ia dung sirang hita!'),(8945,42,31,50,'Molo diarsahi ho angka borungki, manang mambuat boru bahen imbang ni angka borungki, ndang adong jolma di lambungta, alai Debata do sitindanginta.'),(8946,42,31,51,'Angkup ni didok si Laban mandok si Jakkob: Ida ma, punggupunggu on dohot partinandaan on na huojakkon i parbolatanta.'),(8947,42,31,52,'Punggupunggu on dohot partinandaan on sitindangi, paboa na so jadi laosanku punggupunggu on mandapothon ho, hamu pe, ndang jadi laosanmu punggupunggu on dohot partinandaan on mandapothon ahu laho mangago.'),(8948,42,31,53,'Debata ni si Abraham dohot Debata ni si Nahor, Debata ni amanasida pe manguhum hita. Jadi marbulan ma si Jakkob tu Na hinabiaran ni amana si Isak i.'),(8949,42,31,54,'Dung i dipelehon si Jakkob ma sada pelean di dolok i, dipio ma dohot angka donganna i mangan sagusagu. Jadi dung sun nasida mangan sagusagu, laos marborngin di dolok i nasida.'),(8950,42,31,55,'Torang ni arina i dipasogot si Laban ma hehe, jala diumma angka pahompuna dohot angka boruna i, jala dipasupasu nasida; dung i laho muli ma si Laban tu ingananna hian.'),(8951,42,32,1,'Alai si Jakkob pe ditulus ma dalanna, jadi dijumpangkon angka surusuruan ni Debata ibana.'),(8952,42,32,2,'Asa dung diida si Jakkob nasida, ninna ma: Parduan ni parangan ni Debata do on, jadi dibahen ma goar ni adian i Mahanaim.'),(8953,42,32,3,'Dung i disuru si Jakkob ma angka suruanna jumolo manopot si Esau, hahana i, tu tano Seir, tu luat Edom.'),(8954,42,32,4,'Ditonai ma nasida, ninna do: Songon on ma dok hamu tu rajangki. Si Esau i: Songon on do hata ni si Jakkob, naposomi: Na hinomit di lambung ni si Laban do ahu na sai laon rasirasa nuaeng.'),(8955,42,32,5,'Jadi marlombu ahu, marhalode, marbirubiru, jala marnaposo baoa dohot boruboru, gabe husuru paboahon i tu rajangki, asa asi roham marnida ahu.'),(8956,42,32,6,'Dung i mulak ma angka suruan i tu si Jakkob, angka didok ma: Nunga sahat hami tu haham, tu si Esau i, ibana pe, nunga ro manjumpangkon ho, jala opat ratus halak donganna.'),(8957,42,32,7,'Jadi mabiar situtu ma si Jakkob i, jala tahuton rohana, dibagi ma angka donganna i dohot angka birubiru i, angka lombu dohot angka halode mambahen dua parduan.'),(8958,42,32,8,'Ai ninna rohana do: Molo pajumpang si Esau dohot parduan na sada, jala dibunu, gabe malua parduan na tading i.'),(8959,42,32,9,'Dung i ninna si Jakkob ma: Iale Jahowa, Debata ni daompung si Abraham, dohot Debata ni damang si Isak! Didok Ho do mandok ahu: Mulak ma ho tu tano hatubuanmu, asa hutumpak ho.'),(8960,42,32,10,'Mametmethu do ahu nian manjalo sude angka asi ni roham dohot sude hasintongan, angka na binahenmi tu naposomon. Ai mardongan tungkothon hutaripari batang aek Jordan on na jolo, ia nuaeng nunga gabe dua parduan ahu.'),(8961,42,32,11,'Sai palua ma ahu sian tangan ni dahahang, si Esau i, ai mabiar do ahu mida ibana, atek tung ro ibana maon mambunu ahu, ina i ro di angka dakdanak.'),(8962,42,32,12,'Alai didok Ho do: Sai tumpahanku do ho jala sobokkon horsik ni laut bahenonku pinomparmu, angka na so habilangan dibahen hinadeakna.'),(8963,42,32,13,'Laos marborngin disi do ibana sabornginna i; dung i dipambuat ma nasa na dapot tanganna bahen sileanlean tu si Esau, hahana i:'),(8964,42,32,14,'Dua ratus hambing boruboru, jala duapulu hambing tunggal, dua ratus birubiru boruboru, jala duapulu birubiru tunggal.'),(8965,42,32,15,'Tolupulu unto panusuan rap dohot anakna, opatpulu lombu jala sampulu jonggi, duapulu halode boruboru rap dohot anakna sampulu.'),(8966,42,32,16,'Dung i dipasahat ma tu tangan ni angka naposona, pulik be saparduan, jala didok ma mandok angka naposona i: Pajolo hamu ma taripar, jala marholangkolang bahen hamu saparduan.'),(8967,42,32,17,'Dung i ditonai ma parjolo i, ninna: Molo pajumpang ho dohot dahahang si Esau i, jala disungkun ho, naposo ni ise do ho, jala tu dia ho laho, jala ise nampuna on angka na di jolomon?'),(8968,42,32,18,'Dohononmu ma: Si Jakkob naposomi do nampunasa, silehonlehonna do i, tinongosna tu rajangku, si Esau i, jala ida, ihut dohot ibana di pudinami.'),(8969,42,32,19,'Suang songon i ditonahon dohot tu na paduahon i dohot tu na patoluhon i dohot tu sude angka na mangadu angka parduan i, ninna ma: Hatangkon ondeng ma dok hamu tu si Esau, molo tung pajumpang hamu dohot ibana:'),(8970,42,32,20,'Ihut di pudinami dohot si Jakkob naposomi, i ma dok hamu! Ai ninna rohana do: Padengganonku do rohana marhitehite silehonlehon, na niadu di jolongku, dung i pe asa idaonku bohina, anggiat mira bohina marnida ahu.'),(8971,42,32,21,'Jadi taripar ma silehonlehon i di jolona; marborngin di parsaroan ianggo ibana sabornginna i.'),(8972,42,32,22,'Dung i hehe ma ibana bornginna i, dipangarahon ma jolmana na dua i dohot naposona boruboru na dua i dohot anakna na sampulu sada i, laho manaripari hatariparan Jabok.'),(8973,42,32,23,'Jadi ditogihon ma nasida patariparhon di batang aek i, dipataripar ma nasa na di ibana i.'),(8974,42,32,24,'Tading ma holan si Jakkob, jadi ro ma sada halak marsiranggut dohot ibana ro di buha siang ari.'),(8975,42,32,25,'Alai dung diida, ndang leak tunduksa si Jakkob, disoro ma soitna, jadi ngendul ma soit ni si Jakkob i dibahen parsiranggutonnasidai.'),(8976,42,32,26,'Dung i ninna Ibana ma: Palua ma Ahu, ai nunga buha siang ari. Alai ninna si Jakkob ma: Ndang hupalua Ho, nda jolo dipasupasu Ho ahu.'),(8977,42,32,27,'Dung i ninna Baoa i mandok Ibana: Ise do goarmu? Si Jakkob do, ninna.'),(8978,42,32,28,'Jadi ninna Baoa i ma: Ndang be si Jakkob dohonon goarmu, si Israel na ma, ai dialo ho do Debata dohot jolma, jala monang ho do.'),(8979,42,32,29,'Dung i manungkun ma si Jakkob, jala ninna: Ua paboa ma goarmu? Alai ninna Ibana ma: Boasa ma tung sungkunsungkunanmu goarhu? Dung i dipasupasu ibana disi.'),(8980,42,32,30,'Jadi dibahen si Jakkob ma goar ni parsiranggutan i Peniel; ai nunga tangkas huida apala Bohi ni Debata, jala malua tondingki.'),(8981,42,32,31,'Dung i di na sinalpuanna i Peniel i, binsar ma mata ni ari di ibana, hape mengkatengkat ibana dibahen soitna.'),(8982,42,32,32,'Ala ni do umbahen so dipangan halak Israel urat doldol rasirasa nuaeng, ala disoro pardomuan ni soit ni si Jakkob i.'),(8983,42,33,1,'Dung i manatapnatap ma si Jakkob, jadi diida ma ro si Esau, jala opat ratus halak donganna sauduran. Jadi disagihon ma angka anakna tu si Lea dohot tu si Rahel dohot tu naposo boruboru na dua i.'),(8984,42,33,2,'Tu jolo ma dibahen boruboru na dua i rap dohot anaknasida i, tu pudinasida i si Lea dohot anakkonna, parpudi sahali anggo si Rahel rap dohot si Josep.'),(8985,42,33,3,'Dung i mardalan di jolonasida ma anggo ibana, jala dipatungkaptungkap ibana tu tano pitu hali, paima tolhas ibana tu hahana i.'),(8986,42,33,4,'Dung i marlojong ma si Esau manjumpangkon ibana, dihaol ma rungkungna i, jala diumma ibana, jadi tangis be nasida.'),(8987,42,33,5,'Dung i manaili ma si Esau, gabe diida ma angka ina i rap dohot angka dakdanak i, gabe didok ma: Ise do angka donganmu an? I ma angka dakdanak, angka na niasihon ni Debata tu naposomon, ninna.'),(8988,42,33,6,'Dung i jumonok ma naposo boruboru na dua i, nasida dohot angka anakkonnasida, manungkapnungkap ma nasida marsomba.'),(8989,42,33,7,'Jumonok ma dohot si Lea rap dohot anakkonna i angka na marsomba be; dung i jumonok ma si Josep dohot si Rahel marsomba be.'),(8990,42,33,8,'Dung i ninna si Esau ma: Tu aha bahenonmu sude punguan angka na jumpang ahu nangkin? Mangalap asi ni roha ni rajangku do i tu ahu, ninna.'),(8991,42,33,9,'Alai ninna si Esau ma: Godang do anggo i di ahu, ale anggia, di ho ma na di ho hian.'),(8992,42,33,10,'Dung i ninna si Jakkob ma: Ndangana, molo sapala asi roham mida ahu, sai jalo ma silehonlehonki sian tanganku; ai songon na marnida bohi ni Debata do ahu, na marnida bohim ahu, jala sorta ho tu ahu.'),(8993,42,33,11,'Sai jalo ma pasupasungku, na binoan i tu ho, ai asi do roha ni Debata di ahu, jala singkop do sude di ahu. Jadi dung sai dielehelekkon tu ibana, gabe dijalo ma.'),(8994,42,33,12,'Dung i didok si Esau ma: Beta ma hita mardalan, ahu pe di jolom.'),(8995,42,33,13,'Alai ninna si Jakkob ma mandok ibana: Diboto rajangku do, mardongan posoposo dohot birubiru dohot lombu angka panusuan ahu, molo mahudushu binahen, nanggo sadari, angka mate ma sude pinahan i.'),(8996,42,33,14,'Sai jumolo ma rajangku mardalan Sian naposona on; ianggo ahu, hupanangetnanget pe marguru tu pangalangka ni pinahan angka na di jolongki dohot marguru tu langka ni angka dakdanak i, paima tolhas ahu tu Seir tumopot rajangki.'),(8997,42,33,15,'Jadi ninna si Esau ma: Beha ia hutadingkon deba sian angka dongankon bahen donganmu? Alai ninna si Jakkob ma: Tu aha ma i pola? Asal jumpangan asi ni roha ahu, ale rajanami.'),(8998,42,33,16,'Jadi mulak ma si Esau di na sadari i tu Seir.'),(8999,42,33,17,'Alai anggo si Jakkob borhat laho tu Sukot, dipauli ma jabuna sada, jala dibahen angka bara ni pinahanna, ala ni do umbahen margoar Sukot adian i.'),(9000,42,33,18,'Jadi tolhas ma si Jakkob mardamedame tu huta Sikem, na di tano Kanaan, di na ro i ibana sian Padang-Aram, di adopan ni huta i ibana marsaro.'),(9001,42,33,19,'Dung i dituhor ma hauma santurpuk, hajongjongan ni undungundungna i, sian angka anak ni si Hemor, ama ni si Sikem mararga saratus rupia.'),(9002,42,33,20,'Dung i dipajongjong disi langgatan sada, dibahen ma goarna: Di Debata ni halak Israel na gogo i.'),(9003,42,34,1,'Alai laho ma ianggo si Dina, boru ni si Lea, na tinubuhonna di si Jakkob, martandang tu angka boru, isi ni banua i.'),(9004,42,34,2,'Jadi diida si Sikem, anak ni si Hemor, marga Hevi, raja ni luat i ibana, jala digogoi.'),(9005,42,34,3,'Solo situtu rohana di si Dina, boru ni si Jakkob i, jala lomo rohana mida boruboru na bajar i, gabe didok ma hatana mambuat roha ni boruboru na bajar i.'),(9006,42,34,4,'Dung i ninna si Sikem na mandok si Hemor, amana i: Sai buat ma di ahu namarbaju on bahen jolmangku.'),(9007,42,34,5,'Dung i tarbortik ma tu si Jakkob, naung pinarotakrotak ni si Sikem si Dina, boruna i, jala di ladang angka anakna i marmahan pinahanna, jadi sip ma si Jakkob, paima ro nasida.'),(9008,42,34,6,'Dung i ruar ma si Hemor, ama ni si Sikem i tumopot si Jakkob, naeng mangkatai dohot ibana.'),(9009,42,34,7,'Ro ma dohot angka anak ni si Jakkob sian ladang. Jadi marsak jala tarrimas situtu angka baoa i, dung dibege nasida barita ni haurahon, binahen ni si Sikem tu Israel, naung pinarotakrotakna i boru ni si Jakkob, tung na so jadi do bahenon songon i.'),(9010,42,34,8,'Dung i mangkatai ma si Hemor dohot nasida, ninna ma: Solo do roha ni anakku, si Sikem di borumuna i; ua lehon hamu nasida tu ibana bahen jolmana!'),(9011,42,34,9,'Martondong ma hita: Lehon hamu borumuna di hami, borunami pe buat hamu di hamu.'),(9012,42,34,10,'Di lambungnami on ma hamu maringanan; guru di hamu ma tano na di jolomuna i; ingani jala liati hamu marboniaga mambuat laba di hamu.'),(9013,42,34,11,'Dung i didok si Sikem ma mandok ama ni si Dina dohot tu angka ibotona i: Sai asi ma rohamuna di ahu hulehon pe gurudokmuna.'),(9014,42,34,12,'Gumodang pe pangido hamu tuhorna dohot silehonlehon, sai na lehononku do, gurudokmuna i, asal dilehon hamu boru i tu ahu bahen jolmangku.'),(9015,42,34,13,'Jadi tanding ma dibahen angka anak ni si Jakkob mangalusi si Sikem dohot si Hemor amana i, ala na pinarotakrotakna i si Dina, ibotonasida i.'),(9016,42,34,14,'Didok nasida ma mandok ibana: Ndang haoloan hami hata i, mangalehon ibotonami tu halak na marhulup, ai haurahon do i di hami.'),(9017,42,34,15,'Alai huoloi hami pe hamu, molo olo hamu sunaton nasa baoa di hamu, songon hami.'),(9018,42,34,16,'Hulehon hami pe borunami tu hamu, hubuat hami pe dohot borumuna di hami, gabe tampak ingananta, jala sada bangso hita.'),(9019,42,34,17,'Alai molo so diunduk hamu hatanami, mangoloi sunaton, buatonnami ma borunami, dung i bungkas hami.'),(9020,42,34,18,'Jadi lomo ma roha ni si Hemor dohot si Sikem, anakna i umbege hatanasida i.'),(9021,42,34,19,'Ndang pola marpaimaan dolidoli i patupahon hata i, ai solo do rohana di boru ni si Jakkob, jala ibana na sumangap sian sude pinompar ni amana.'),(9022,42,34,20,'Jadi ditopot si Hemor dohot si Sikem anakna i partungkoan ni hutanasida i, asa marhata dohot angka baoa, isi ni hutanasida i, ninna ma:'),(9023,42,34,21,'Naeng maraleale angka halak on dohot hita italoas ma nasida mangingani tano on, asa diliati marboniaga, ai lambas do tano i di jolonasida humaliang; tabuat pe borunasida bahen jolmanta, talehon dohot borunta di nasida.'),(9024,42,34,22,'Alai ingkon marsunat hita nasa baoa, songon nasida angka na tarsunat be, asa olo nasida maringan di lambungta on, jala sada bangso dohot hita.'),(9025,42,34,23,'Nda guru di hita ma pinahan dohot sude sinamotnasida i? Antong sai itaoloi ma pinangido ni rohanasida i, asa maringan nasida di lambungta on.'),(9026,42,34,24,'Jadi diunduk nasida ma hata ni si Hemor dohot si Sikem, anakna i, sude nasida angka na marmeang muli sian bahal ni hutana i; marsunat ma sude baoa, isi ni huta i.'),(9027,42,34,25,'Dung i patoluarihon, di na bernit i diae nasida, masiboan podangna ma na dua anak ni si Jakkob, si Simeon dohot si Lepi, iboto ni si Dina i, dirampok ma huta i na so pangkiriman, jala dibunui nasa baoa.'),(9028,42,34,26,'Dibunu ma dohot si Hemor dohot si Sikem, anakna i, ditampuli dohot podang, dung i diboan ma si Dina sian bagas ni si Sikem, jadi laho nasida.'),(9029,42,34,27,'Dung i ditahopi angka anak ni si Jakkob ma angka na tartihami, jala ditaban duduk huta i, ala na pinarotakrotakna i ibotonasida i.'),(9030,42,34,28,'Didaek ma angka birubiru dohot lombu dohot halodenasida i ro di sude na di bagasan huta dohot na di balian.'),(9031,42,34,29,'Ro di sude ugasannasida dohot sude posoposo, tole jolmanasida diboan, jala dipupusi nasa ugasan na di bagasan jabu.'),(9032,42,34,30,'Dung i didok si Jakkob ma mandok si Simeon dohot si Lepi: Dilitohi hamu ma antong parnidaanku pabauhon baritangku tu isi ni tano on, tu halak Kanaan dohot Perisi, so manang piga bilanganta; molo marhara nasida di donganna mangalo hita, gabe bunuonna ma ahu, jadi ripur ahu dohot na hupompar.'),(9033,42,34,31,'Alai ninna nasida ma: Tung tama ma doshon sibabijalang ibotonami bahenonna?'),(9034,42,35,1,'Dung i ninna Debata ma mandok si Jakkob: Borhat ma ho laho nangkok tu Betel, ingani ma disi, jala pauli disi sada langgatan di Debata, na niidam, di na maporus i ho maradophon si Esau, hahami.'),(9035,42,35,2,'Jadi didok si Jakkob ma mandok donganna sabagas dohot tu sude donganna i: Pambolongkon hamu ma debata sileban, angka na pineopmuna i, manguras ma hamu jala salini hamu ulosmuna i.'),(9036,42,35,3,'Beta ma hita nangkok tu Betel, asa hupauli disi sada langgatan di Debata, na mangalusi ahu di ari hagogotanki, jala didongani ahu di dalan na hudalani i.'),(9037,42,35,4,'Jadi diparlehon be ma tu si Jakkob sude debata sileban, angka na di tangannasida i, ro di antinganting angka na di pinggolnasida i, dung i ditabunihon si Jakkob ma i tu toru ni hau ara na di lambung ni Sikem.'),(9038,42,35,5,'Dung i borhat ma nasida, ditahuti Debata ma angka huta na humaliang nasida i, asa unang dilele angka anak ni si Jakkob.'),(9039,42,35,6,'Jadi tolhas ma si Jakkob tu huta Lus na di tano Kanaan i, i ma Betel i, ibana dohot sude donganna sauduran.'),(9040,42,35,7,'Dung i dipauli ma disi langgatan sada, dibahen ma goar ni hajongjonganna: Elbetel, ala na niidana i disi Debata, di na maporus i ibana maradophon hahana i.'),(9041,42,35,8,'Dung i mate ma si Debora panarusan ni si Rebekka na jolo; jadi ditanom ma ibana di toru ni hariara, tungkan dangsina ni Betel, gabe dibahen ma goarna; hariara Bahut (parandungan).'),(9042,42,35,9,'Dung i diulahi Debata pataridahon Ibana tu si Jakkob, dung ro ibana sian Padang-Aram, jala dipasupasu ibana.'),(9043,42,35,10,'Jadi didok Debata ma mandok ibana: Si Jakkob do goarmu, alai ndang be si Jakkob, si Israel na ma dohonon goarmu. Jadi margoar si Israel na ma ibana.'),(9044,42,35,11,'Dung i ninna Debata ma mandok ibana: Ahu do Debata, pargogo na so hatudosan! Gabe ma ho jala matorop mabue; bangso sada, jala parluhutan ni angka bangso marharoroan sian ho, jala angka raja ruar sian gontingmu.'),(9045,42,35,12,'Jala tano naung hulehon tu si Abraham dohot tu si Isak, lehononku ma tu ho dohot tu pinomparmu sogot lehononku tano i.'),(9046,42,35,13,'Dung i manaek ma Debata sian lambungna, sian inganan pangkataianna i dohot si Jakkob.'),(9047,42,35,14,'Jadi dipajongjong si Jakkob ma sada partinandaan sian batu di inganan pangkataianna i dohot Debata, diusehon ma tu atasna pelean siinumon, diusehon dohot miak tu ginjangna.'),(9048,42,35,15,'Dung i dibahen si Jakkob ma goar ni inganan pangkataianna i dohot Debata, Betel.'),(9049,42,35,16,'Dung i borhat ma nasida sian Betel, adong dope arga sangombasnari mardalan asa sahat tu Eprata, mangintubu ma disi si Rahel, mansai maol ibana mangintubu.'),(9050,42,35,17,'Jadi di na maol i ibana mangintubu, ninna sibaso i ma mandok ibana: Unang ho mabiar, ai sai gomgomonmu do nang anakmon.'),(9051,42,35,18,'Alai di na laho tos hosana, ai mate do ibana, dibahen ma goarna Benoni; alai anggo amana manggoar ibana si Benjamin.'),(9052,42,35,19,'Asa mate ma si Rahel, jala tartanom di dalan laho tu Eprata, i ma Betlehem.'),(9053,42,35,20,'Dung i dipajongjong si Jakkob ma partinandaan sada di atas tanomanna i. I ma partinandaan di tanoman ni si Rahel rasirasa nuaeng.'),(9054,42,35,21,'Dung i borhat ma si Israel, jala dipahembang undungundungna i di bariba ni Migdol-Eder.'),(9055,42,35,22,'Jadi dung diingani si Israel tano i, laho ma si Ruben marjolmahon si Bilha, jolma ni amana i, jala tarbortik do i tu si Israel. Alai sampulu dua do anak ni si Jakkob.'),(9056,42,35,23,'Ia anak ni si Lea: Si Ruben, buhabaju ni Jakkob, si Simeon, si Lepi, si Juda, si Isasar dohot si Zebulon.'),(9057,42,35,24,'AIa anak ni si Rahel: Si Josep dohot si Benjamin.'),(9058,42,35,25,'Ia anak ni si Bilha, rading ni si Rahel: Si Dan dohot si Naptali.'),(9059,42,35,26,'Ia anak ni si Zilpa, rading ni si Lea: Si Gad dohot si Aser. I ma anak ni si Jakkob, angka na tubu di ibana di Padang-Aram.'),(9060,42,35,27,'Dung i sahat ma si Jakkob tu si Isak, amana i, tu Mamre, tu Kiryat-Arba, i ma Hebron, paisolatan ni si Abraham dohot si Isak.'),(9061,42,35,28,'Ia umur ni si Isak saratus ualupulu taon do.'),(9062,42,35,29,'Dung i tos ma hosa ni si Isak, jala mate ibana saur matua, jala mahap di hangoluan on, gabe dipapungu ma ibana tu angka ompuna jala ditanom si Esau dohot si Jakkob, angka anakna i, ibana.'),(9063,42,36,1,'On ma angka pinompar ni si Esau, i ma si Edom.'),(9064,42,36,2,'Dibuat si Esau ma angka jolmana sian boru Kanaan: Si Ada, boru ni si Elon, marga Het dohot si Aholibama, boru ni si Ana, pahompu ni si Zibeon, marga Hevi.'),(9065,42,36,3,'Dohot si Basemat, boru ni si Ismael, iboto ni si Nabaiot.'),(9066,42,36,4,'Dung i ditubuhon si Ada ma di si Esau si Elipas, ia si Basemat manubuhon si Reguel.'),(9067,42,36,5,'Ditubuhon si Aholibama ma si Jehus, si Jalam dohot si Kora. I ma anak ni si Esau, angka na tubu di ibana di tano Kanaan.'),(9068,42,36,6,'Dung i diboan si Esau ma angka jolmana, angka anakna dohot angka boruna dohot sude isi ni bagasna i ro di sude ugasanna dohot pinahanna dohot sinamotna, angka na sinamotsamotna i di tano Kanaan, gabe bungkas ibana tu sada tano paholangkon sian si Jakkob anggina i.'),(9069,42,36,7,'Ai magodangku do sinamotnasida i, ianggo tung tampak nasida maringan, jala ndang tarparmudu tano paisolatanna i nasida, dibahen hinadeak ni pinahannasida.'),(9070,42,36,8,'Jadi maringan di dolok Seir ia si Esau, i ma si Edom.'),(9071,42,36,9,'On ma angka pinompar ni si Esau, ompu ni halak Edom di dolok Seir.'),(9072,42,36,10,'On ma goar ni angka anak ni si Esau: Elipas, anak ni si Ada, jolma ni si Esau; Reguel, anak ni si Basemat, jolma ni si Esau.'),(9073,42,36,11,'Ia angka anak ni si Elipas, on ma: Teman, Omar, Zepo, Gatam dohot si Kenas.'),(9074,42,36,12,'Dohot do si Timna diparjolma si Elipas, anak ni si Esau i, gabe ditubuhon ma si Amalek di si Elipas. I ma angka anak ni si Ada, jolma ni si Esau.'),(9075,42,36,13,'Alai angka anak ni si Reguel on do: Si Nahat, si Zerah, si Samma dohot si Missa. I ma angka anak ni si Basemat, jolma ni si Esau.'),(9076,42,36,14,'Ia angka anak ni si Aholibama, boru ni si Ana, pahompu ni si Zibeon, jolma ni si Esau, angka na tinubuhonna di si Esau: Si Jehus, si Jalam dohot si Kora.'),(9077,42,36,15,'On do angka induk sian angka anak ni si Esau. Sian angka anak ni si Elipas, buhabaju ni si Esau: Induk Teman, induk Omar, induk Zepo, induk Kenas.'),(9078,42,36,16,'Induk Kora, induk Gatam, induk Amalek. I ma angka induk sian si Elipas di tano Edom, i ma angka anak ni si Ada.'),(9079,42,36,17,'Jala on ma angka anak ni si Reguel, anak ni si Esau: Induk Nahat, induk Zerah, induk Samma, induk Missa. I ma angka induk sian si Reguel di tano Edom. I ma angka anak ni si Basemat, jolma ni si Esau.'),(9080,42,36,18,'On ma angka anak ni si Aholibama, jolma ni si Esau: induk Jehus, induk Jalam, induk Kora. I ma angka induk sian Aholibama, boru ni si Ana, jolma ni si Esau i.'),(9081,42,36,19,'I ma angka anak ni si Esau, jala i ma angka induknasida. Ibana ma Edom i.'),(9082,42,36,20,'On ma angka anak ni si Seir, marga Hori, angka isi ni tano i hian: Si Lotan, si Sobal, si Zibeon, si Ana.'),(9083,42,36,21,'Dison, si Ezer dohot si Disan. I ma angka induk ni halak Hori, angka anak ni si Seir ni tano Edom.'),(9084,42,36,22,'Alai angka anak ni si Lotan, on do: Si Hori dohot si Heman; ia iboto ni si Lotan si Timna do.'),(9085,42,36,23,'Jala on do angka anak ni si Sobal: Alpan, Manahat, Ebal, Sepo dohot si Onam.'),(9086,42,36,24,'Ia angka anak ni si Zibeon, on do: Aia dohot Ana. I ma si Ana, na jumpangan angka mual aek rangat di halongonan, di na pinarmahanna i angka halode ni si Zibeon, amana i.'),(9087,42,36,25,'Jala on do angka anak ni si Ana: Si Dison dohot si Aholibama, boru ni si Ana.'),(9088,42,36,26,'Jala on do angka anak ni si Dison: Hemdan, Esban, Itran dohot Heran.'),(9089,42,36,27,'Ia angka anak ni si Ezer on do: Bilhan, Sapan dohot Akan.'),(9090,42,36,28,'Ia angka anak ni si Disan, on do: Us dohot Aran.'),(9091,42,36,29,'ma angka induk ni halak Hori: Induk Lotan, induk Sobal, induk Zibeon, induk Ana.'),(9092,42,36,30,'Induk Dison, induk Ezer, induk Disan. I ma angka induk ni halak Hori, mangihuthon hainduhonnasida be di tano Seir.'),(9093,42,36,31,'Jala on do angka raja, na mangarajai di tano Edom, tagan so adong dope raja na gumomgom halak Israel:'),(9094,42,36,32,'Si Bela, anak ni si Beor gumomgom Edom, ia goar ni hutana Dinhaba.'),(9095,42,36,33,'Jadi dung mate si Bela, bangkit raja singkatna si Jobab, anak ni si Zerah sian Bozra.'),(9096,42,36,34,'Jadi dung mate si Jobab, bangkit raja singkatna si Husam sian tano ni halak Teman i.'),(9097,42,36,35,'Jadi dung mate si Husam soluk ma mangarajai si Hadad, anak ni Bedad, na manaluhon halak Midian di ladang Midian ni halak Moab, ia goar ni hutana Apit.'),(9098,42,36,36,'Jadi dung mate si Hadad, soluk ma raja singkatna si Samla sian huta Masreka.'),(9099,42,36,37,'Jadi dung mate si Samla, soluk ma raja singkatna si Saul sian Rehobot, na martopihon batang aek i.'),(9100,42,36,38,'Jadi dung mate si Saul, soluk ma raja singkatna si Baal-Hanan, anak ni si Akbor.'),(9101,42,36,39,'Jadi dung mate si Baal-Hanan, anak ni si Akbor i, soluk ma raja singkatna si Hadad, ia goar ni hutana Pahu, ia goar ni jolmana Mehetabel, boru ni si Matret, tubu ni si Mezahap.'),(9102,42,36,40,'Jala on do goar ni angka induk sian si Esau mangihuthon sundutnasida dohot inganannasida dohot goarnasida: induk Timna, induk Alpa, induk Jete.'),(9103,42,36,41,'Induk Aholibama, induk Ela, induk Pinon.'),(9104,42,36,42,'Induk Kenas, induk Teman, induk Mibzar.'),(9105,42,36,43,'Induk Magdiel, induk Iram. I ma angka induk ni halak Edom mangihuthon inganannasida di tano ginomgomnasida i. I ma si Esau ompu ni halak Edom.'),(9106,42,37,1,'Alai anggo si Jakkob maringan di tano, paisolatan ni amana na jolo, di tano Kanaan i.'),(9107,42,37,2,'On do angka jamita ni pinompar ni si Jakkob: Marumur sampulu pitu taon si Josep, asa paihutihut pinahan ibana rap dohot angka hahana i, sai dongan ni angka anak ni si Bilha dohot si Zilpa, angka jolma ni amana i dolidoli i, sai dipatolhas si Josep do baritanasida angka na roa tu amanasida i.'),(9108,42,37,3,'Alai holongan do roha ni si Israel di si Josep, asa di angka anakna sasudena, ai dung pe matua ibana, asa ditubuhon, jala dibahen di ibana sada baju sampuborna na ganjang.'),(9109,42,37,4,'Jadi dung diida angka hahana i, holongan do hape roha ni amanasida mida ibana, asa mida sude angka hahana i, jadi sogo rohanasida mida ibana, ndang apala basar be pangkulingnasida tu ibana.'),(9110,42,37,5,'Angkup ni marnipi ma si Josep, jala dipaboa nipina i tu angka hahana i; jadi lam ganda sogo ni rohanasida i mida ibana.'),(9111,42,37,6,'Ai didok si Josep do mandok nasida; ua tangihon hamu jolo nipingkon:'),(9112,42,37,7,'Huipi mangarahuti tibalan hita di tonga ni hauma, jadi hehe ma tibalanku, laos jongjong, jomba be do angka tibalanmuna humaliang marsomba tu tibalanku.'),(9113,42,37,8,'Jadi ninna angka hahana i mandok ibana: Tung ho ma bangkit rajanami di roham, mangarajai hami? Jadi lam tamba muse sogo ni rohanasida i mida ibana, ala ni nipina dohot ala ni hatana i.'),(9114,42,37,9,'Dung i diipi muse sada nipi na asing, ipe dipaboa do tu angka hahana i, ninna ma: Ida ma, huipi dope sada nipinari; ai ida ma, mata ni ari dohot bulan dohot bintang sampulu sada marsomba tu ahu.'),(9115,42,37,10,'Alai dung dipaboa nipina tu amana dohot tu angka hahana i, hona hata ma ibana dibahen amana, ninna ma mandok ibana: Nipi aha ma nipimi? Ingkon ro ahu dohot inam dohot angka haham, manungkap tu tano marsomba tu ho, di roham?'),(9116,42,37,11,'Jadi hosom ma roha ni angka hahana i mida ibana; alai anggo amana i sai dipeop rohana do hata i.'),(9117,42,37,12,'Dung i laho ma angka hahana i marmahan birubiru ni amanasida i di lambung Sikem.'),(9118,42,37,13,'Jadi ninna si Israel ma tu si Josep: Nda di lambung ni Sikem do angka hahami marmahan? Ro ma ho, asa husuru ho manopot nasida. Jadi ninna si Josep ma: Dison do ahu.'),(9119,42,37,14,'Dung i didok si Jakkob ma mandok ibana: Antong laho ma ho maningkir angka hahami manang na sonang nasida, sonang dohot angka pinahan i, dung i mulak ho mamboan barita i tu ahu. Jadi disuru si Jakkob ma ibana sian rura Hebron, asa ditopot tu Sikem.'),(9120,42,37,15,'Jadi jumpang na sahalak ma ibana, di na lilu ibana di ladang, gabe disungkun baoa i ibana, ninna ma: Aha do na niluluanmu?'),(9121,42,37,16,'Angka dahahang i do na hulului, ninna; ua paboa di ahu, manang didia nasida marmahan!'),(9122,42,37,17,'Jadi ninna baoa i ma: Nunga munsat nasida sian on, ai hubege hatanasida: Beta ma hita laho tu Dotan. Jadi diihuthon si Josep ma angka hahana i, gabe jumpangsa nasida di Dotan.'),(9123,42,37,18,'Jadi dung ditatap nasida ibana, tagan so sahat dope ibana tu nasida, nunga martuptup hian nasida, mambunu ibana.'),(9124,42,37,19,'Asa masipandohan ma nasida: Ida hamu ma, nunga ro indi an, pangguruan ni angka nipi.'),(9125,42,37,20,'On pe, beta ma hita, asa itabunu ibana, dung i itadabu ibana tu godung sada, tadok pe, binatang manoro ibana, asa taida lapatan ni angka nipina i.'),(9126,42,37,21,'Alai dung dibege si Ruben hata i, dipalua ma ibana sian tangannasida, ninna ma: Unang ma tabunu hosana.'),(9127,42,37,22,'Angkup ni ninna si Ruben mandok nasida: Unang durushon hamu mudar, dabu hamu ibana tagonan tu godung, na di halongonan on, unang mangonai tangan hamu tu ibana. Alai naeng paluaonna ibana sian tangannasida, asa dipaulak ibana tu amana.'),(9128,42,37,23,'Jadi dung tolhas si Josep tu lambung ni angka hahana i, diosari nasida ma bajuna, baju sampuborna na ganjang i, na pinangkena i.'),(9129,42,37,24,'Dung i ditangkup nasida ibana, jala didabu ibana tu godung, alai rumar godung i ndang marisi aek.'),(9130,42,37,25,'Dung i hundul ma anggo nasida mangan sagusagu. Dung i manatapnatap ma nasida, gabe diida ma marudurudur halak Ismael ro sian Gilead rap dohot untonasida, angka na mangusung pamurpuri, miak na hushus dohot haminjon, angka na laho tuat tu tano Misir palangkahonsa.'),(9131,42,37,26,'Jadi ninna si Juda ma tu angka hahaanggina i: Aha ma labanta, molo itabunu angginta i, jala itaporso mudarna i?'),(9132,42,37,27,'Beta, itagadis ma ibana tu halak Ismael an, asa unang mangonai tangan hita tu ibana, ai angginta jala sibukta ibana, jadi ditangihon angka hahaanggina ibana.'),(9133,42,37,28,'Dung i di na mamolus angka partigatiga, halak Midian i, dienet nasida ma si Josep sian godung i tu ginjang, jala digadis si Josep arga duapulu rupia tu halak Ismael, angka na mamboan si Josep tu tano Misir.'),(9134,42,37,29,'Dung i mulak ma si Ruben tu godung i, hape ndang be di bagasan godung i didapot si Josep, jadi disansani ma angka ulosna.'),(9135,42,37,30,'Dung i mulak ma ibana tu angka anggina i, ninna ma: Ndang be disi dakdanak i, tu dia na ma ahu?'),(9136,42,37,31,'Dung i dibuat nasida ma baju ni si Josep i, diseat nasida ma sada hambing tunggal, jala diguluhon bajubaju i tu mudar i.'),(9137,42,37,32,'Dung i ditongoshon ma baju sampuborna na ganjang i, asa diboan tu amanasida, angka ninna ma manonahon: Na jumpang hami do on, ua tanda, manang baju ni anakmu do i, manang ndang.'),(9138,42,37,33,'Alai ditanda ibana ma i, gabe didok ma: Baju ni anakku do i! Nunga dipolgak binatang ibana, nunga tutu disoro si Josep i.'),(9139,42,37,34,'Dung i disansani si Jakkob ma angka ulosna i, jala dibahen ulos hori tu gontingna, sai tumatangis ibana sipatsipat ni ari marningot anakna i.'),(9140,42,37,35,'Dung i hehe be ma sude angka anakna dohot boruna, mangapul ibana nian; alai ndang olo ibana apulon nasida, ninna ma: Na laho na ma ahu manurun tumopot anakki tu Sheol (parbeguan) ala ni arsakkon; jadi sai ditatangisi amana i ibana.'),(9141,42,37,36,'Alai digadis halak Midian i do ibana tu tano Misir, tu si Potipar, naposo ni si Firaun, sintua ni angka hupas.'),(9142,42,38,1,'Tuat ma uju i si Juda tumadingkon angka hahaanggina i, dipagohon ma undungundungna i jonok tu lambung ni sada halak Adullam margoar si Hira.'),(9143,42,38,2,'Jadi diida si Juda ma disi sada boru ni halak Kanaan, si Sua goarna, dibuat ma i jala ditopot.'),(9144,42,38,3,'Jadi marhangoluan ma ibana, jala ditubuhon anak sada, dibahen ma goarna si Er.'),(9145,42,38,4,'Dung i gabegabean muse ibana, jala ditubuhon anak sada, dibahen ma goarna si Onan.'),(9146,42,38,5,'Dung i gabegabean muse ibana jala ditubuhon anak sada, dibahen ma goarna si Sela. Alai di huta Aksib do si Juda di hatutubuna i.'),(9147,42,38,6,'Dung i dibuat si Juda ma boru bahen jolma ni si Er, buhabajuna i, si Tamar do goarna.'),(9148,42,38,7,'Alai na jahat do si Er, buhabaju ni si Juda i, maradophon Jahowa, jadi dibunu Jahowa ibana.'),(9149,42,38,8,'Dung i ninna si Juda ma mandok si Onan: Topot ma jolma ni haham, jala pagodang, mambahen rindang di haham.'),(9150,42,38,9,'Alai dung diboto si Onan, ndada ibana nampuna pinompar, ia ditopot pe jolma ni hahana i, jadi tu tano ma gogo ni dagingna dibolongkon, asa unang marrindang hahana i dibahen.'),(9151,42,38,10,'Alai sogo do roha ni Jahowa di pambahenna i, jadi dibunu ma dohot ibana.'),(9152,42,38,11,'Dung i didok si Juda ma tu si Tamar, parumaenna i: Songon i ma hamu mabalu tinggal di bagas ni amanta, paima magodang anakku, si Sela i, ai ninna rohana do, atek tung mate dohot ibana, songon angka hahana i. Jadi laho ma si Tamar, jala diingani bagas ni amana i.'),(9153,42,38,12,'Dung i di laonlaon ni ari, mate ma boru ni si Sua, jolma ni si Juda. Jadi dung sabam roha ni si Juda, nangkok ma ibana tumopot angka sigusting imbulu ni birubiruna, tu Timna, ibana dohot si Hira alealena sian Adullam.'),(9154,42,38,13,'Jadi dibaritahon halak ma tu si Tamar, ninna do: Ida ma, na laho do simatuam doli nangkok tu Timna manggusting imbulu ni birubiruna.'),(9155,42,38,14,'Dung i diosari boruboru i ma angka ulos habaluonna i, disaongsaongkon ma abit tujung i laho hundul tu harbangan ni huta Enaim, na di lambung dalan tu Timna, ai nunga balga diida si Sela, hape ndang dilehon ibana tu si Sela bahen jolmana.'),(9156,42,38,15,'Jadi dung diida si Juda ibana, diambang ma boruboru sibabijalang ibana, ai disaongsaongi do bohina.'),(9157,42,38,16,'Dung i didapothon si Juda ma boruboru i tu pinggol ni dalan i, ninna ma: Ua loas ma ahu ro tu ho, ai ndang diboto parumaenna hape nasida. Jadi ninna nasida ma: Aha ma ugasanmu lehononmu tu ahu, asa ro ho tu ahu?'),(9158,42,38,17,'Jadi ninna ibana ma: Hutongos pe di ho sada hambing tunggal sian pagaran. Antong lehon ma tu on sindorna, paima ditongos ho, ninna nasida.'),(9159,42,38,18,'Jadi ninna ibana ma: Aha ma sindor lehononku tu ho? Jadi ninna nasida ma: Ba, tintin pijormi dohot horungkorungmi dohot tungkot na di tanganmi. Jadi dilehon ma i tu nasida, dung i ditopot ma nasida, jadi gabegabean ma nasida sian ibana.'),(9160,42,38,19,'Dung i hehe ma nasida laho muli, dipasae ma abit tujung i, jala dialithon muse angka ulos habaluonna i.'),(9161,42,38,20,'Dung i ditongoshon si Juda ma hambing tunggal i marhite sian alealena, halak Adullam i, tumobus sindor sian tangan ni boruboru i, hape ndang didapot disi.'),(9162,42,38,21,'Jadi disungkuni ma isi ni luat i: Atehe hamu, didia do boruboru sibabijalang i, na hundulhundul di Enaim, di pinggol ni dalan i? Alai ninna nasida ma: Ndang adong boruboru sibabijalang, huida hami dison.'),(9163,42,38,22,'Dung i mulak ma ibana tu si Juda, ninna ma: Ndang jumpang ahu nasida; jala didok angka isi ni luat i huhut: Ndang adong dison boruboru sibabijalang.'),(9164,42,38,23,'Jadi ninna si Juda ma: Di ibana ma i tusi, asal unang gabe haurahonta; ida ma, hutongos do nian hambing tunggal on, alai ndang jumpang ho angkora i.'),(9165,42,38,24,'Hape salpu arga tolu bulan, tarbortik ma tu si Juda: Tardege pinggol ni dalan do si Tamar, parumaenmi, nunga pola gabegabean ibana dibahen sibabijalangonna i. Jadi didok si Juda ma: Boan hamu nasida tu on, asa sinoksohan nasida.'),(9166,42,38,25,'Jadi dung diboan halak nasida, marsuruan ma nasida manopot simatuana doli mandok: Sian baoa, nampuna angka indi on ahu gabegabean, ua tanda hamu, manang ise nampuna tintin pijor on dohot horungkorung on dohot tungkot on!'),(9167,42,38,26,'Jadi ditanda si Juda ma ugasan i, gabe didok ma: Pintoran do hape nasida, asa ahu; ai ala na so hulehon i nasida tu si Sela, anakki. Alai ndang be diparjolma ibana nasida.'),(9168,42,38,27,'Jadi dung jumpang hatotoras ni emena, gabe linduat do di bortianna.'),(9169,42,38,28,'Tole manubuhon nasida, surduk na sada tangan, gabe ditangkup sibaso i, jala dirahuti dohot bonang na rara, ninna ma: On do na parjolo ruar.'),(9170,42,38,29,'Hape dung ditait tanganna i mulak, ruar ma anggina i; jadi didok sibaso i ma: Boasa ma tung songon i pamuntasmu? Jadi dibahen ma goarna si Peres.'),(9171,42,38,30,'Dung i ruar ma anggina i, na martali bonang na rara i di tanganna, jadi dibahen ma goarna si Zerah.'),(9172,42,39,1,'Alai anggo si Josep tarboan do tuat tu Misir, gabe ro ma si Potipar, wakil ni raja Firaun, sirajai angka naposona halak Misir, manuhor ibana sian halak Ismael, angka na patuathon ibana tusi.'),(9173,42,39,2,'ai didongani Jahowa do si Josep i, gabe jolma na martua ibana, sai di bagas ni rajana, halak Misir i ibana.'),(9174,42,39,3,'Jadi dung diida rajana i na nidonganan ni Jahowa ibana, jala ditumpak Jahowa sude mula ni tanganna i.'),(9175,42,39,4,'Jadi lomo ma roha ni rajana i mida si Josep, gabe parhaladona ibana; jala dipabangkit ibana gabe siramoti jabuna, jala sude na di ibana i, dipasahat do tu tangan ni si Josep.'),(9176,42,39,5,'Jadi olat ni na pinabangkitna i ibana bahen siramoti jabuna, ro di sude na di ibana i, dipasupasu Jahowa ma bagas ni halak Misir i ala ni si Josep, manjadi do ditumpak Jahowa sasude na di ibana i, di jabu dohot di balian.'),(9177,42,39,6,'Tung sude na di ibana i dipasahat do tu tangan ni si Josep, agia aha pe ndang dipabotoboto rohana, nda holan sagusagu na pinanganna i. Alai uli do anggo rupa dohot bohi ni si Josep i.'),(9178,42,39,7,'Jadi dung salpu angka jamita ondeng solo ma roha ni jolma ni rajana i mida si Josep, didok ma: Angkupi ma ahu modom.'),(9179,42,39,8,'Alai manjua ma si Josep, didok ma mandok jolma ni rajana i: Ida ma, ndang agia aha dibotoboto rajangku di jabu on, dungkon ni ahu, jala sude na di ibana i, tu tanganku do dipasahat.'),(9180,42,39,9,'Ndang adong manang ise na umbalga di jabu on sian ahu, jala ndang adong na so dipadapot tu ahu, dungkon ni ho, ala jolmana ho; tung olo ma ahu mambahen jea na sai godang, asa mardosa ahu maradophon Debata?'),(9181,42,39,10,'Alai sai songon i do hatana tu si Josep ariari, na so ditangihon ibana do modom di lambungna manang rap dohot nasida.'),(9182,42,39,11,'Hape laho ma si Josep di na sadari tu jabu mangula ulaonna, tongon ndang adong manang ise isi ni jabu i disi.'),(9183,42,39,12,'Jadi disoro boruboru i ma ulosna i, ninna ma: Angkupi ma ahu modom! Alai ditadingkon si Josep ma ulosna di tangannasida, laos maporus ibana tu balian.'),(9184,42,39,13,'Jadi dung diida nasida naung tinadingkonna ulosna di tangannasida laos maporus ibana tu balian.'),(9185,42,39,14,'Gabe dijou nasida ma angka isi ni bagasna i, ninna ma mandok angka nasida: Ida hamu ma, diboan ibana do halak Heber i tu hita, mambahen haurahon di hita. Ro do ibana tu ahu, naeng angkupanna ahu modom, gabe hupangiar soarangku joujou.'),(9186,42,39,15,'Jadi dung dibege ibana, hupangiar soarangku joujou, gabe ditadingkon ma ulosna i di lambungki, laho maporus ibana tu balian.'),(9187,42,39,16,'Dung i dipasombu nasida ma ulos ni si Josep di lambungnasida, paima ro rajana i muli.'),(9188,42,39,17,'Dung i dihatahon ma hatanasida ondeng tu ibana, ninna ma: Ro do nangkin tu ahu hatoban halak Heber i, na binoanmi tu hami naeng mambahen haurahon di ahu.'),(9189,42,39,18,'Alai dung hupangiar soarangki joujou, gabe ditadingkon ma ulosna i di lambungki, jala maporus ibana tu balian.'),(9190,42,39,19,'Jadi dung dibege raja ni si Josep hata, ni jolmana i, angka na pinaboana i tu ibana mandok: Songon na hudok i dibahen naposom tu ahu, gabe tarrimas situtu ma ibana.'),(9191,42,39,20,'Jadi ditangkup raja ni si Josep i ma ibana, dipasahat ma ibana tu bagas hurungan ni angka na hinurungkon ni raja i, asa laos di hurungan i ibana.'),(9192,42,39,21,'Alai didongani Jahowa do si Josep i, jala asi rohana mida ibana, dipadenggan dohot roha ni siramoti hurungan i mida ibana.'),(9193,42,39,22,'Dipasahat siramoti hurungan i ma tu tangan ni si Josep sasude angka na tarhurung di bagas hurungan i, ro di sude sibahenon disi, ingkon si Josep paturehon.'),(9194,42,39,23,'Ndang agia aha be diradoti siramoti hurungan i, naung pinasahatna i tu tangan ni si Josep, ala didongani Jahowa ibana, jala ditumpak manjadi na niulana i.'),(9195,42,40,1,'Jadi dung salpu angka jamita ondeng, mardosa ma sitahu anggur ni raja ni Misir i dohot silompa sagusagu dompak tuannasida, raja ni Misir i.'),(9196,42,40,2,'Jadi muruk situtu raja Firaun mida naposona na dua i, mida sintua ni angka sitahu anggur dohot mida sintua ni angka silompa sagusagu.'),(9197,42,40,3,'Gabe dipasahat nasida tu hurungan, tu jabu ni sintua ni angka hupas pangaramoti, tu hurungan panahatan ni si Josep.'),(9198,42,40,4,'Dung i dipasahat sintua ni angka hupas i ma nasida tu si Josep, asa diramoti nasida.'),(9199,42,40,5,'Jadi dung na nia leleng nasida di hurungan i, marnipi ma nasida duansa di na saborngin, pulik be do nipinasida i; marlapatan be do nipinasida i, nipi ni sitahu anggur dohot silompa sagusagu ni raja ni Misir i, angka na di hurungan i.'),(9200,42,40,6,'Dung i manogotna i ditopot si Josep ma nasida, gabe diida ma nasida margondok ni roha.'),(9201,42,40,7,'Jadi disungkun si Josep ma naposo ni raja Firaun i, angka na rap dohot ibana di hurungan di jabu ni indukna i, ninna ma: Boasa songon na surbit bohimuna idaon sadari on?'),(9202,42,40,8,'Jadi ninna nasida ma tu ibana: Marnipi do hami, hape ndang adong sijujursa. Jadi ninna si Josep ma tu nasida: Nda mandapot di Debata do angka panjujuron? Ua pajojor hamu ma tu ahu.'),(9203,42,40,9,'Dung i dipajojor sintua ni angka sitahu anggur nipina i tu si Josep, ninna mandok ibana: Huida di bagasan nipingku, hau anggur sangkambona di jolongku.'),(9204,42,40,10,'Tolu do ranting ni na sangkambona i, angka na martunas i, jala mangerbangi bungana, matoras dohot parbuena.'),(9205,42,40,11,'Jala di tanganku do panginuman ni raja Firaun i, jala hupambuat angka parbue i, jala huporoi tu panginuman ni raja Firaun, dung i hulehon panginuman i tu tangan ni raja i.'),(9206,42,40,12,'Jadi ninna si Josep ma tu ibana: On do lapatanna: Na tolu ranting i, tolu ari do i.'),(9207,42,40,13,'Di bagasan tolu ari on dope, pabangkiton ni raja Firaun ho, jala pasuongonna ho tu tohonanmi hian, jala lehononmu muse panginuman i tu tangan ni raja i, songon na somal na jolo uju sitahu anggurna ho.'),(9208,42,40,14,'Alai ia dung sonang ho sai ingot ahu, asi roham di ahu, taringothon ahu tu raja Firaun, asa diparuar ahu sian bagas on.'),(9209,42,40,15,'Ai na tarpangus do ahu sian tano ni halak Heber, jala dison pe, ndang adong hubahen agia aha, alana umbahen dabuhononna ahu tu hurungan on.'),(9210,42,40,16,'Jadi dung diida sintua ni angka silompa sagusagu, denggan do panjujurna i, gabe didok ma tu si Josep: Ahu pe, huhunti do di bagasan nipingku tolu hirang martinditindi marisi sagusagu di atas ulungku.'),(9211,42,40,17,'Ianggo hirang parginjang i marisi ragamragam ni sipanganon ni raja Firaun, pinajadijadi ni pangalompa, hape ro angka pidong manurbingi sian hirang na di atas ulungki.'),(9212,42,40,18,'Jadi ninna si Josep ma mangalusi: On do lapatanna: Na tolu hirang i, tolu ari do i.'),(9213,42,40,19,'Di bagasan tolu ari on dope, pisikkonon ni raja Firaun ma ulum sian ho, jala gantungonna badanmu tu hau, gabe panganon ni angka pidong sibukmu sian ho.'),(9214,42,40,20,'Tole patoluarihon, jumpang ma ari hatutubu ni raja Firaun, gabe dibahen ma sipanganon di saluhut naposona, dipanangkok ma ulu ni sintua ni angka sitahu anggur dohot ulu ni sintua ni angka silompa sagusagu tu tongatonga ni angka naposona i.'),(9215,42,40,21,'Jadi dipasuang ma sintua ni angka sitahu anggur tu ulaonna hian, asa dilehon muse panginuman i tu tangan ni raja Firaun.'),(9216,42,40,22,'Alai anggo sintua ni angka silompa sagusagu, digantung do, songon na jinujur ni si Josep tu nasida.'),(9217,42,40,23,'Alai ndang diingot sintua ni angka sitahu anggur i be si Josep, tung dihalupahon do ibana.'),(9218,42,41,1,'Jadi dung salpu boho dua taon, marnipi ma raja Firaun jongjong ibana di pasir ni batang aek.'),(9219,42,41,2,'Dung i diida ma nangkok sian batang aek i pitu lombu, angka na uli rupa jala na mokmok, angka na manjampali di arung.'),(9220,42,41,3,'Dung i soluk ma nangkok pitu lombu na asing sian batang aek i, angka na roa rupa, jala na marniang, didapothon ma tu lambung ni lombu, angka na di pasir ni batang aek i.'),(9221,42,41,4,'Dung i dipolgak lombu angka na roa rupa, jala na marniang i ma na pitu lombu angka na uli rupa, jala na mokmok i. Dung i tarsunggul ma raja Firaun i.'),(9222,42,41,5,'Dung i tarnono muse matana, gabe marnipi paduahalihon, diida ma tubu pitu kambiur sangkambona, angka na porngis jala na denggan.'),(9223,42,41,6,'Dung i soluk ma diida tubu pitu kambiur angka na lambang, jala antinganoon dibahen alogo sian habinsaran.'),(9224,42,41,7,'Jadi dipolgak kambiur angka na lambang i ma na pitu kambiur, angka na balga, jala na porngis i. Jadi tarsunggul ma raja Firaun i, jala diida ma, nipi do i hape.'),(9225,42,41,8,'Manogotna i holso situtu ma rohana i. Jadi disuru ma mangalapi saluhut pangarunding dohot paralamat angka na di tano Misir i; dung i diaturhon raja Firaun ma nipina i tu nasida: Alai ndang adong sian nasida na malo manjujursa di raja Firaun i.'),(9226,42,41,9,'Dung i mangkuling ma sintua ni angka sitahu anggur, ninna ma mandok raja Firaun i: Marningot ma ahu sadari on di dosangku.'),(9227,42,41,10,'Uju na tarrimas i raja Firaun mida hami naposona, gabe dipasahat ahu rap dohot sintua ni angka silompa sagusagu tu hurungan, na di jabu ni sintua angka hupas.'),(9228,42,41,11,'Jadi marnipi ma hami di na saborngin, ahu dohot ibana, pulik be do lapatan ni nipinami.'),(9229,42,41,12,'Jadi adong ma disi dongannami sada dolidoli, marga Heber, naposo ni sintua ni angka hupas i, tu ibana ma huaturhon hami, gabe dijujur ma nipinami be, marguru tu nipinami be lapatanna.'),(9230,42,41,13,'Jadi ia songon i panjujurna tu hami, laos songon i do songgop: Ia ahu mulak tu tohonanku hian, targantung do anggo donganku.'),(9231,42,41,14,'Dung i disuru raja Firaun ma mangalapi si Josep, jadi hatop ma dipalua nasida ibana sian hurungan i, dinabung ma misena, jala disingkati angka ulosna, gabe ditopot ma raja Firaun i.'),(9232,42,41,15,'Dung i ninna raja Firaun ma mandok si Josep: Adong do sada nipingku, hape ndang adong sijujursa. Alai hubege baritam, didok halak: Ia adong dibege ho nipi, malo do ho manjujursa.'),(9233,42,41,16,'Dung i ninna si Josep ma mangalusi raja Firaun: Ndada ahu nian, Debata do paboahon hasonangan tu raja Firaun i!'),(9234,42,41,17,'Dung i ninna raja Firaun ma mandok si Josep: Ida ma, huipi do, ahu jongjong di pasir ni batang aek i.'),(9235,42,41,18,'Jadi huida ma rungkar sian batang aek i pitu lombu, angka na mokmok, jala na uli rupa, angka na manggagat tu arung.'),(9236,42,41,19,'Dung i soluk ma rungkar huida pitu lombu na asing, angka na marniang jala na roa rupa situtu, ndang adong tudosan ni roana i huida di sandok tano Misir.'),(9237,42,41,20,'Dung i dipolgak na pitu lombu angka na marniang jala na roa i ma na pitu lombu parjolo, angka na mokmok i.'),(9238,42,41,21,'Sahat pe angka i tu butuhana i dipolgak, laos so tanda do naung ro di butuhana i, laos roa do rupanasida, songon na di mulana i. Jadi tarsunggul ma ahu.'),(9239,42,41,22,'Dung i huida muse di bagasan nipingku, tubu pitu kambiur sangkambona, angka na porngis jala na denggan.'),(9240,42,41,23,'Dung i soluk ma tubu huida pitu kambiur na asing angka na lambang, jala na rohor dibahen alogo sian purba.'),(9241,42,41,24,'Jadi dipolgak na pitu kambiur, angka na lambang i ma na pitu kambiur angka na denggan i. Nunga nian hupaboa luhutna tu angka pangarunding, alai ndang adong nanggo sada na malo manjujur di ahu.'),(9242,42,41,25,'Dung i ninna si Josep ma tu raja Firaun: Sada do pandok ni na dua nipi ni raja Firaun; dipaboa Debata do tu raja Firaun, na naeng pamasaonna.'),(9243,42,41,26,'Na pitu lombu angka na denggan i, pitu taon do i; na pitu kambiur, angka na denggan i, laos pitu taon do i; sada do tahe lapatan ni nipi i.'),(9244,42,41,27,'Na pitu lombu angka na marniang, jala na roa i, angka na rungkar i ihut tu bongka ondeng, pitu taon do i; na pitu kambiur, angka na lambang jala na rohor binahen ni alogo sian purba, boaboa ni pitu taon haleon do i.'),(9245,42,41,28,'I ma hata na hupaboa i tu raja Firaun: Dipalumba Debata do tu raja Firaun, na naeng pamasaonna.'),(9246,42,41,29,'Ida ma, masa ma pitu taon hadumaon bolon di sandok tano Misir.'),(9247,42,41,30,'Ihut ma tusi muse masa pitu taon haleon, gabe lupa ma sude hadumaon i di tano Misir; jadi pansing ma tano i bahenon ni haleon i.'),(9248,42,41,31,'Ndang be botoon ni halak hadumaon ni tano i bahenon ni haleon na soluk i, ai tung na pitor situtu do i.'),(9249,42,41,32,'Angkup ni, umbahen na pola dua hali marnipi raja Firaun, paboa gomos ni boaboa na sian Debata, jala tibuna pamasaon ni Debata.'),(9250,42,41,33,'On pe, didiori raja Firaun ma sada halak na pantas jala na pistar marroha, sipabangkitonna patotahon tano Misir i.'),(9251,42,41,34,'Didokkon raja i ma ibana pabangkithon angka sintua di tano i mangidohon saparlima sian tano Misir di na pitu taon hadumaon i.'),(9252,42,41,35,'Asa dipapungu nasida sude sipanganon saleleng angka taon hadumaon, angka na ro on, asa dipagukguk hian eme marpandapotan tu raja Firaun, pajopon ni angka huta bahen sipanganon sogot.'),(9253,42,41,36,'Asa adong hian bahen bohal ni tano i tumagam na pitu taon haleon i, angka na songgop sogot tu tano Misir, asa unang mate so mangan, isi ni tano i.'),(9254,42,41,37,'Denggan ma hata i di roha ni raja Firaun dohot di roha ni angka naposona sudena.'),(9255,42,41,38,'Jadi ninna raja Firaun ma mandok angka naposona i: Tung tagamon ma jumpang hita jolma songon ibana, na niinganan ni Tondi ni Debata?'),(9256,42,41,39,'Dung i didok raja Firaun ma tu si Josep: Dung dipabotohon Debata tu ho sudena i, ndang adong jolma na umpantas marroha jala na umpistar sian ho.'),(9257,42,41,40,'Sahat tu ho ma bagashu, guru dokmu ma sandok bangsongki; holan habangsa harajaon i ma husurunganku sian ho.'),(9258,42,41,41,'Angkup ni ninna raja Firaun mandok si Josep: Ida ma, nunga hupasahat tu ho sandok tano Misir on.'),(9259,42,41,42,'Dung i diumpat raja Firaun ma tintinna sian tanganna, jala disolukkon tu tangan ni si Josep, dilehon dohot surta na bontar bahen abitna dohot sampesampena, dibahen dohot horungkorung sere tu rungkungna.'),(9260,42,41,43,'Dung i dipahundul ibana tu huretana paidua huhut martingting i di jolona: Marsomba ma hamu! Asa sahat tu ibana ma sandok tano Misir.'),(9261,42,41,44,'Dung i ninna raja Firaun ma mandok si Josep: Ahu do nian raja Firaun, alai ndang na jadi hindaton ni manang ise tanganna manang patna di sandok tano Misir, nda lomo roham.'),(9262,42,41,45,'Jadi dibahen raja Firaun ma goar ni si Josep: Zopenat Paneah (sipangolu portibi on), jala dilehon tu ibana si Asnat, boru ni si Potipera, malim di huta On, bahen jolmana. Dung i borhat ma si Josep mangaliati tano Misir i.'),(9263,42,41,46,'Tolupulu taon do umur ni si Josep di na jongjong i ibana di jolo ni si Firaun, raja ni Misir i. Asa borhat sian jolo ni raja Firaun ma si Josep i, laho mangaliati sandok tano Misir.'),(9264,42,41,47,'Jadi mansai gukguk parbue ni tano i di na pitu taon hadumaon i.'),(9265,42,41,48,'Dung i dipapungu si Josep ma nasa goargoar ni sipanganon ni na pitu taon i, angka na di tano Misir i, jala disimpan sipanganon i tu angka huta, gogo ni hauma na humaliang ganup huta, dibahen tu bagasan.'),(9266,42,41,49,'Jadi sobokkon rihit na di pasir ni laut hagogodang ni eme na pinagukguk ni si Josep i, pola dipasohot muse mamilangi, ai ndang be habilangansa.'),(9267,42,41,50,'Dung i tubu ma dua anak di si Josep, tagan so ro dope taon haleon i, angka na tinubuhon ni si Asnat, boru ni si Potipera, malim di huta On.'),(9268,42,41,51,'Dibahen si Josep ma goar ni buhabajuna i: Si Manasse, ai lupa do ahu, dibahen Debata di sude pardangolanki, ro di bagas ni damang i sudena, ninna.'),(9269,42,41,52,'Dibahen goar ni na paduahon i: Si Epraim: Ai gabe do ahu ditumpak Debata di tano pardangolanki, ninna.'),(9270,42,41,53,'Jadi dung salpu na pitu taon hadumaon i di tano Misir.'),(9271,42,41,54,'Mamungka ma ro na pitu taon haleon, songon na nidok ni si Josep hian, gabe haleonon ma saluhut luat ni halak; alai anggo di sandok tano Misir, adong do sipanganon.'),(9272,42,41,55,'Dung i haleonon ma muse dohot sandok tano Misir i, jadi mangalualu tu raja Firaun ma na torop i mangido sipanganon. Alai ninna si Firaun ma mandok sude isi ni Misir: Si Josep ma topot hamu, na nidokna i ma bahen hamu.'),(9273,42,41,56,'Jadi dung haleonon saluhut luat ni halak, diungkap si Josep ma sude sopo parpeopan eme, digadisi ma i tu halak Misir, ai tung na potir situtu do haleon i di tano Misir.'),(9274,42,41,57,'Dung i angka ro ma isi ni sude luat tu tano Misir, manuhori sipanganon sian si Josep; ai tung na potir situtu do haleon i di sandok tano on.'),(9275,42,42,1,'Alai dung diida si Jakkob, adong eme di tano Misir, ninna ma mandok angka anakna i: Boasa sai masiberengan hamu?'),(9276,42,42,2,'Dung i didok ma: Adong hubege eme di tano Misir, tuat ma hamu tusi margalas di hita sian i, asa mangolu hita, gabe unang mate.'),(9277,42,42,3,'Jadi tuat ma angka haha ni si Josep na sampulu halak i laho margalas eme sian tano Misir.'),(9278,42,42,4,'Alai anggo si Benjamin, anggi ni si Josep i, ndang dipaborhat si Jakkob rap dohot angka hahana i, ai ninna do: Atek tung beha adong marana.'),(9279,42,42,5,'Dung i sahat ma angka anak ni si Israel margalas rap dohot angka pargalas na asing, ai di tano Kanaan pe nunga masa haleon.'),(9280,42,42,6,'Alai si Josep do pangarajai di luat i, ibana do sigadisi eme tu sude isi ni tano i. Asa dung sahat angka haha ni si Josep i, manungkap ma nasida tu tano, marsomba tu ibana.'),(9281,42,42,7,'Jadi dung diida si Josep angka hahana i, gabe ditanda ma nasida, alai mangindung ma ibana maradophon nasida, jala didok hata na horas tu nasida, ninna ma mandok nasida: Sian dia hamu ro? Jadi didok nasida ma: Sian tano Kanaan do, naeng manuhor sipanganon.'),(9282,42,42,8,'Asa ditanda si Josep do angka hahana i, alai anggo ibana ndang ditanda nasida.'),(9283,42,42,9,'Jadi diingot si Josep ma nipi, angka na niipina taringot tu nasida, gabe didok ma mandok nasida: Pangkahap do hamu naeng mangarigop bungbung ni tano on; umbahen na ro hamu.'),(9284,42,42,10,'Alai ninna nasida ma mandok ibana: Ndangana ale rajanami! Na ro manuhor sipanganon do angka naposomon.'),(9285,42,42,11,'Anak ni sahalak do hami sudena, na burju do hami, ndang dung pangkahap angka naposomon.'),(9286,42,42,12,'Alai ninna si Josep ma mandok nasida: Ndangana, naeng mangarigop bungbung ni tano on do hamu umbahen na ro.'),(9287,42,42,13,'Jadi ninna nasida ma: Sampulu dua do nian hami angka naposomon, angka na martinodohon, anak ni sada halak di tano Kanaan, tading di lambung ni amanta ianggo siampudan i sadari on, alai anggo na sada ndang disi be.'),(9288,42,42,14,'Dung i ninna si Josep ma mandok nasida: I ma na hudok i tu hamu: Pangkahap do hamu!'),(9289,42,42,15,'On ma pangujianku tu hamu: Songon sintongna mangolu raja Firaun, molo tagamon meret hamu sian on, nda jolo ro anggimuna siampudan i tuson.'),(9290,42,42,16,'Suru hamu ma sahalak sian hamu, mangalap anggimuna i, tu hurungan ma anggo hamu, asa huuji hatamuna i, manang na adong haburjuon di hamu; alai molo soada i, songon sintongna mangolu raja Firaun, pangkahap do hamu.'),(9291,42,42,17,'Jadi dipatop ma nasida tu sada hurungan tolu ari lelengna.'),(9292,42,42,18,'Alai jumpang patoluarihon, ninna si Josep ma mandok nasida: Boti ma bahen hamu, asa mangolu hamu, ai huhabiari do Debata.'),(9293,42,42,19,'Molo halak na burju hamu, sahalak ma sian hamu tading tarhurung di hurunganmuna on, laho ma ianggo hamu, manaruhon eme i, sipanganon ni hamu na sapanganan.'),(9294,42,42,20,'Dung i boan hamu ma anggimuna siampudan i tu ahu, asa hinahaporseaan hatamuna i, jala unang mate hamu. Jadi dibahen nasida ma songon i.'),(9295,42,42,21,'Alai masisolsolan ma nasida: Tongon do, pardosa do hita tu angginta i, ai itaida do ponjot ni rohana i, uju diparajarajai hita, alai ndada itatangihon; ala ni do umbahen na ro tu hita hagogotan on.'),(9296,42,42,22,'Jadi ninna si Ruben ma mangalusi nasida: Nda hudok hian do tu hamu: Unang mardosa hamu tu dakdanak i, alai ndang ditangihon hamu hatangki; asa sapata ni mudarna i huhut manginona tu hita.'),(9297,42,42,23,'Alai ndang diboto nasida tung diantusi si Josep hata i, ai marsipadas hata do si Josep mangangkupi nasida.'),(9298,42,42,24,'Dung i mangkatai ditundali si Josep ma nasida, laos tangis diulahi muse mandompakkon nasida, mangkulingkon nasida, ditangkup ma sian nasida si Simeon marningot ibana di adopannasida.'),(9299,42,42,25,'Dung i didokkon si Josep ma ision panuhuhannasida i dohot eme, jala paulahon peraknasida tu panuhuhanna be, jala lehonon tu nasida bohal di dalan, jadi dibahen ma songon i tu nasida.'),(9300,42,42,26,'Dung i dipampeakkon nasida ma emenasida i tu halodenasida i, jadi borhat ma nasida sian i.'),(9301,42,42,27,'Hape diungkap na sahalak ma panuhuhanna di parbornginan mangalehon gagaton tu halodena, gabe diida ma perakna i, talpe di siboanonna i.'),(9302,42,42,28,'Jadi didok ma mandok angka donganna i: Mulak do hepengku, ida hamu ma huhut di siboanonkon, gabe marseto ma rohanasida jala mabiar situtu, angka didok be ma tu donganna: Aha do on ulaning, na binahen ni Debata tu hita.'),(9303,42,42,29,'Dung i tolhas ma nasida tu tano Kanaan, tu si Jakkob, amanasida i, dialualuhon ma tu ibana luhutna na masa i tu nasida, angka ninna ma:'),(9304,42,42,30,'Poran di pangkuling ni raja ni tano i tu hami, songon pangkahap mangkahapi tano i hami, di rohana.'),(9305,42,42,31,'Alai na burju do hami, i do hudok hami tu ibana, ndang dung pangkahap hami.'),(9306,42,42,32,'Sampulu dua do hami, anak ni amanta, angka na martinodohon; ndang disi be ianggo na sada, di lambung ni amanta dope ianggo siampudan i, di tano Kanaan.'),(9307,42,42,33,'Jadi didok raja ni tano i ma mandok hami: Botima i, asa huboto manang na burju hamu: Tading ma sada sian hamu di lambungkon, boan hamu ma panganon ni na di huta, mardalan ma hamu!'),(9308,42,42,34,'Dung i boan hamu ma anggimuna siampudan i tu ahu, asa huboto, ndada pangkahap hamu, na burju do hamu hape; hulehon pe donganmuna on tu hamu, datdati hamu margalasi tu tano on.'),(9309,42,42,35,'Tole nasida mangarungkari panuhuhannasida i, gabe diida be ma puro parhepenganna di panuhuhanna be. Jadi dung diida nasida dohot amanasida i angka parhepengannasida i, mabiar ma nasida.'),(9310,42,42,36,'Dung i ninna si Jakkob, amanasida i, mandok nasida: Na punu ma ahu bahenonmuna! Si Josep ndang be disi, si Simeon pe, ndang be disi, dohot do si Benjamin naeng buatonmuna! Luhutna i marpungu tu ahu.'),(9311,42,42,37,'Alai ninna si Ruben ma mandok amana i: Anakku na dua i ma bunu, molo tung so mulak ibana huboan tu ho; tu tanganku ma ibana pasahat, sai na mulak ibana tu ho boanonku.'),(9312,42,42,38,'Alai ninna ibana ma: Ndang jadi tuat anakku rap dohot hamu, ai nunga mate hahana i, sasada ibana na ma tading; atek tung beha adong parmaraanna di dalan sibolusonmuna i, jadi tu toru ni situmandok na ma ahu na ubanon on taruhononmuna, bahenon ni dokdok ni roha. (42-39) Alai lam marpotir ma haleon di tano i.'),(9313,42,43,1,'Jadi dung suda dipangan nasida eme,'),(9314,42,43,2,'na binoannasida i sian tano Misir, mangkuling ma amanasida mandok tu nasida: Sai ulahi hamu ma margalas di hita otik sipanganon.'),(9315,42,43,3,'Jadi ninna si Juda ma mangalusi ibana: Mansai gomos situtu do hata ni raja i tu hami: Na so tupa idaonmuna be bohingkon, nda udur hamu dohot anggimuna i, ninna.'),(9316,42,43,4,'Asa ia dipaborhat ho angginami rap dohot hami, tuat pe hami margalas sipanganon di ho.'),(9317,42,43,5,'Alai molo so dipaborhat ho ibana, ndang tuat hami, ai nunga pola didok raja i mandok hami: Na so tupa idaonmuna be bohingkon, nda udur anggimuna i dohot hamu.'),(9318,42,43,6,'Dung i ninna si Israel ma: Boasa dibahen hamu na roa tu ahu, pola paboaonmuna tu raja i, adong dope anggimuna i?'),(9319,42,43,7,'Jadi didok nasida ma: Sai disungkunsungkuni raja i do taringot tu hami dohot taringot tu pomparannami, ninna do: Mangolu dope amamuna? Adong dope anggimuna i? Jadi hupajojor hami tu ibana, marguru tu panungkunna i; sian dia do botoonnami dohononna boanonnami angginami tuat?'),(9320,42,43,8,'Dung i ninna si Juda ma mandok si Israel amana i: Loas ma dakdanak i borhat rap dohot ahu, asa borhat hami mardalan, jala mangolu hita, unang mate, nang hami, nang ho, nang angka anakkonnami.'),(9321,42,43,9,'Ahu ma mangkangkungi ibana, sian tanganku pangidoonmu ibana; marsapata tu ahu ma ho saleleng ahu mangolu, molo tung so mulak ibana huboan tu ho, jala hupajongjong tu jolom.'),(9322,42,43,10,'Ai unang togang hami, nunga pola mulak hami dua hali tagamon.'),(9323,42,43,11,'Dung i ninna si Israel, amanasida i, mandok nasida: Molo boti, bahen hamu songon on: Buat hamu ma parbue angka na ummarga, tubu ni tano on mambahen tu panuhuhanmuna, bahen silehonlehon tu raja i: Saotik miak na hushus, dohot situak ni loba saotik dohot pamurpuri dohot haminjon dohot barangan dohot tapus.'),(9324,42,43,12,'Silompit dua ma boan hamu ianggo hepeng i, padohot hepeng na peak talpe di siboanonmuna i: Atek tung beha lilu ibana.'),(9325,42,43,13,'Boan hamu ma dohot anggimuna i, borhat ma hamu mangulahi manopot raja i.'),(9326,42,43,14,'Sai asi ma roha ni Debata, pargogo na so hatudosan i di hamu maradophon raja i, asa diloas borhat hahamuna na tading i dohot si Benjamin rap dohot hamu. Alai anggo ahu, songon halak na punu na ma ahu.'),(9327,42,43,15,'Asa dibuat nasida ma angka silua ondeng dohot hepeng i silompit, angkup ni si Benjamin; dung i borhat ma nasida laho tuat tu tano Misir manopot tu jolo ni si Josep.'),(9328,42,43,16,'Jadi dung diida si Josep udur do nasida dohot si Benjamin, gabe didokkon ma siramoti bagasna i: Togihon ma halak i tu bagas, jala potongi sipotongon i, jala hobas i, ai ingkon mangan halak on rap dohot ahu di hos ni ari.'),(9329,42,43,17,'Dung i dipatupa naposo i ma, songon na nidok ni si Josep i, ditogihon ma halak i tu jabu ni si Josep.'),(9330,42,43,18,'Alai mabiar ma halak i, ala tinogihonna i nasida tu jabu ni si Josep, angka ninna ma: Hata hepeng, na tarihut tu siboanonta na jolo do ulaning, umbahen ditogihon hita tu jabu on, asa manggiling hata tu hita dohot mandobo hita, marhatobanhon hita dohot mambuat halodenta i.'),(9331,42,43,19,'Dung i dipajonok nasida ma tu baoa siramoti bagas ni si Josep i, angka dipangkulingi nasida ma ibana di baba pintu ni bagas i.'),(9332,42,43,20,'Angka ninna nasida ma: Santabi ale rajanami, na tuat do hami nenganon margalas sipanganon.'),(9333,42,43,21,'Hape dung sahat hami tu parbornginan, jala huungkap hami siboanonnami, gabe disi do sintong hepengnami be, talpe di ginjang, jadi mulak do i huboan hami.'),(9334,42,43,22,'Huboan hami dohot hepeng na asing manuhor sipanganon; ndang huboto hami manang ise na mambahen hepengnami tu siboanonnami.'),(9335,42,43,23,'Alai ninna baoa i ma: Dame ma di hamu, unang hamu mabiar! Debatamuna dohot Debata ni amamuna mangalehon arta na buni tu siboanonmuna i; sahat tu ahu do hepengmuna i. Dung i diparuar ma si Simeon tu nasida.'),(9336,42,43,24,'Dung i ditogihon ma nasida tu jabu ni si Josep, dilehon ma dohot aek, asa diburi nasida patnasida i, jala didabu gagaton tu halodenasida i.'),(9337,42,43,25,'Dung i diparade nasida ma silehonlehon i tumagam haroro ni si Josep di hos ni ari, ai nunga tarbortik tu nasida, mangan sagusagu nasida disi.'),(9338,42,43,26,'Jadi dung ro si Josep tu bagas, diboan nasida ma tu ibana silehonlehon na di tangannasida i tu jabu, manungkap ma nasida tu tano, marsomba ibana.'),(9339,42,43,27,'Dung i disungkun si Josep ma manang na sonang do nasida, jala ninna ma: Hipas do amanta, na matua i, na nidokmuna i? Mangolu dope ibana?'),(9340,42,43,28,'Jadi didok nasida ma: Hipas do naposom, amanta i, mangolu dope ibana. Jadi manungkap ma nasida marsomba ibana.'),(9341,42,43,29,'Dung i manaili ma si Josep, gabe diida ma si Benjamin, anggina i, donganna saina, didok ma: On do anggimuna siampudan na nidokmuna i tu ahu? Dung i ninna si Josep muse: Asi ma roha ni Debata di ho, ale anaha!'),(9342,42,43,30,'Dung i humalaput ma si Josep laho, dibahen sihol ni rohana mida anggina i, jadi marhor ma iluna, gabe laho ma ibana tu bilutna, asa tangis disi.'),(9343,42,43,31,'Dung marsuap ibana, ruar ma ibana muse dipatomos ma rohana, didok ma: Boan hamu ma sagusagu i!'),(9344,42,43,32,'Dung i diboan nasida ma pulik be di ibana dohot di nasida, dohot di halak Misir, angka na mangan rap dohot ibana. Ai ndang na jadi mangan sagusagu halak Misir rap dohot halak Heber, ai pantang do i di halak Misir.'),(9345,42,43,33,'Dung i dipatording ma parhundulnasida di adopan ni si Josep, asa tanda sihahaan dohot siampudan, gabe longang be ma nasida.'),(9346,42,43,34,'Dung i dihindati parhobas i ma angka sipanganon hasurungan sian jolo ni si Josep tu jolonasida. Alai lima hali nasa jambar ni donganna sudena, jambar ni si Benjamin, jadi minum ma nasida, jala bosur rap dohot ibana.'),(9347,42,44,1,'Dung i didokkon si Josep ma siramoti bagasna i, ninna ma: Gohi ma panuhuhan ni angka halak on dohot sipanganon, marguru tu na tarusung nasida jala bahen ma hepeng nasida be, talpe tu bibir ni panuhuhanna be.'),(9348,42,44,2,'Jala bahen ma panginumanki, panginuman perak i, talpe di panuhuhan ni siampudan i rap dohot hepeng tuhor ni eme i. Jadi dibahen ma, songon na nidok ni si Josep.'),(9349,42,44,3,'Torang ari manogotna i, dipaborhat ma nasida rap dohot halodenasida i.'),(9350,42,44,4,'Jadi dung ruar nasida sian huta i, ndang sadia holang dope nasida, gabe didok si Josep ma mandok siramoti bagasna i: Borhat ma ho, haru eahi ma ne halak ondeng, jala ia dung dapot ho nasida, dok ma tu nasida: Boasa tung dibahen hamu na jat balos ni na denggan?'),(9351,42,44,5,'Nda dibuat hamu panginuman dohot partondungan ni tuangku? Na roa do na binahenmuna i.'),(9352,42,44,6,'Jadi dung dapotsa nasida, gabe dipandok ma tu nasida angka hata ondeng.'),(9353,42,44,7,'Jadi ninna nasida ma mandok ibana: Boasa ma tung songon i angka pangkulingmi, ale rajanami? Sitongka do ianggo di angka naposomon mangulahon, songon na nidokmu ondeng.'),(9354,42,44,8,'Ida ma, gari hepeng na jumpang hami, talpe di bibir ni panuhuhannami, huboan hami do mulak tu ho sian tano Kanaan; songon dia ma antong panangkonami di perak manang sere sian bagas ni rajanta i?'),(9355,42,44,9,'Ia tung jumpang panginuman di manang ise sian angka naposomon, ingkon mate ibana; angkup ni, dohot do hami gabe hatoban ni raja i.'),(9356,42,44,10,'Alai ninna ibana ma: Antong songon na nidokmuna i ma tutu: Ia tung jumpang di manang ise, i ma gabe hatobanhu, alai lias do ianggo hamu.'),(9357,42,44,11,'Jadi hapogan ma nasida paijurhon siboanonna be tu tano, asa diungkap be siboanonna.'),(9358,42,44,12,'Dung i didiori ma, mamungka di sihahaan, jojor sahat ro di sianggian; gabe jumpang ma panginuman i di siboanon ni si Benjamin.'),(9359,42,44,13,'Jadi diparribak nasida ma ulosnasida i, dipausungkon be ma harungna tu halodena i, asa mulak nasida tu huta.'),(9360,42,44,14,'Dung i ditopot si Juda dohot angka hahaanggina i tu bagas ni si Josep, ai disi dope ibana, jadi manungkap be ma nasida tu tano di jolona.'),(9361,42,44,15,'Alai ninna si Josep ma mandok nasida: Apala dia do na binahenmuna on? Nda diboto hamu hian na malo martondung do jolma songon ahu on.'),(9362,42,44,16,'Jadi didok si Juda ma: Aha ma dohononnami di ho, ale rajanami? Beha ma pandoknami, mamintori dirinami? Nunga jumpang Debata hajahaton ni angka naposomon. Ida ma, hatoban ni raja i na ma hami luhutna, hami ro di sibuat panginuman i.'),(9363,42,44,17,'Alai ninna si Josep ma: Sitongka do di ahu mangulahon sisongon i; baoa sibuat panginuman i, i ma hatobanku; ianggo hamu, nangkok ma hamu tu amamuna mardamedame.'),(9364,42,44,18,'Dung i jumonok ma si Juda tu lambungna, didok ma: Sombangku di ho, ala rajanami, loas ma naposomon mandok hata sada tu pinggol ni raja i, sai unang marpiarpiar rimasmu tu naposom, ai doshon raja Firaun i do ho!'),(9365,42,44,19,'Sungkunsungkun do raja i tu angka naposom, didok do: Adong dope amamuna, manang dongan sabutuha.'),(9366,42,44,20,'Jadi hudok hami ma tongon tu raja i: Adong dope amanami naung matua, jala sada anak siampudanna, tubuna dung matua, jala nunga mate hahana i, jala sasadasa tinggal na saina, jala haholongan ni amana ibana.'),(9367,42,44,21,'Dung i nimmu do mandok angka naposom: Boan hamu ma ibana tuat tu ahu, asa dinolnol matangku ibana.'),(9368,42,44,22,'Jadi hudok hami do nian tu raja i: Ra ndang tartadingkon dakdanak i amana i: Atek tung mate amana maon, tung sura ditadingkon.'),(9369,42,44,23,'Alai nimmu do mandok angka naposom: Molo so tuat anggimuna siampudan i, rap dohot hamu, ndang be jadi idaonmuna bohingkon.'),(9370,42,44,24,'Jadi nangkok ma hami tu naposom, damang i, gabe hupaboa hami ma tu ibana angka hata ni raja i.'),(9371,42,44,25,'Dung i ninna damang ma: Ulahi hamu ma margalas otik sipanganonta.'),(9372,42,44,26,'Alai hudok hami ma: Ndang tarbahen hami tuat; alai molo angginami siampudan i dongannami, tuat pe hami; ai ndang tarbereng hami bohi ni raja i, molo so angginami siampudan i dongannami.'),(9373,42,44,27,'Jadi ninna naposom, damang i, mandok hami: Diboto hamu do, dua do tubu ni jolmangku.'),(9374,42,44,28,'Ruar do anggo na sada sian ahu, gabe hudok: Tongon tahe nunga diparsisiga binatang ibana, jala rasirasa nuaeng, ndang huida ibana.'),(9375,42,44,29,'Molo dibuat hamu dohot on annon sian jolongku, gabe adong marana, jadi tu toru ni situmandok na ma ahu na ubanon i taruhononmuna, bahenon ni hinamago.'),(9376,42,44,30,'On pe, molo sahat ahu tu naposom, damang i, hape ndang dohot dakdanak i, jala sisadari do hosanasida na dua i.'),(9377,42,44,31,'Tongon tahe molo diida, ndang disi dakdanak i, mate ma ibana; alai hami angka naposomon do patuathon naposom, amanta na ubanon i, tu toru ni situmandok, bahenon ni dokdok ni roha.'),(9378,42,44,32,'Ai ahu naposomon do mangkangkungi dakdanak i tu damang i, ningku do: Molo so hupaulak ibana tu ho, sai mardosa ma ahu tu damang i saleleng mangolu.'),(9379,42,44,33,'On pe, ua ahu, naposomon ma tading dison, gabe hatoban ni raja i, singkat ni dakdanak i, asa nangkok dakdanak i, rap dohot angka hahana i.'),(9380,42,44,34,'Ai beha ma pambahenku nangkok tu damang i, so udur dohot dakdanak i? Ndang hapanotnotan ahu hinamago na songgop tu damang i.'),(9381,42,45,1,'Jadi ndang tartaon si Josep be sihol ni rohana maradophon sude angka na jongjong di lambungna, gabe dijouhon ma: Paruar hamu ma sude halak sian lambungkon! Jadi ndang adong manang ise na jongjong di lambungna, di na pinatandahon ni si Josep ibana tu angka hahana i.'),(9382,42,45,2,'Dung i dipangiar ma soarana tumatangis, pola dibege halak Misir, ro di isi ni bagas ni raja Firaun umbegesa.'),(9383,42,45,3,'Dung i ninna si Josep ma mandok angka hahana i: Ahu do si Josep, mangolu dope amanta i? Jadi ndang haalusan angka hahana i ibana, ai tarhatotong do rohanasida mida bohina i.'),(9384,42,45,4,'Dung i ninna si Josep ma mandok angka hahana i: Ua pajonok hamu ma tu lambungkon; gabe dipajonok nasida ma. Jadi ninna ibana ma: Ahu do si Josep, anggimuna, na ginadismuna i tu tano Misir.'),(9385,42,45,5,'On pe, unang mabiar hamu, jala unang serbeng matamuna, ala ginadismuna i ahu tuson, ai mangarade hangoluanmuna, umbahen disuru Debata ahu parjolomuna.'),(9386,42,45,6,'Ai padua taon on dope masa haleon di tano on, alai lima taonnari dope na so masa saburon manang gotilon.'),(9387,42,45,7,'Alai disuru Debata do ahu parjolomuna, pajophon di hamu ebaeba di tano on dohot pangoluhon hamu, mambahen haluaon bolon.'),(9388,42,45,8,'On pe, ndada hamu marsuru ahu tuson, Debata do, dipabangkit do ahu gabe ama ni raja Firaun, dohot gabe induk ni sandok bagasna, jala pangarajai di sandok tano Misir.'),(9389,42,45,9,'Asa pahatop hamu ma ne nangkok tu damang i, paboa hamu ma tu ibana: Songon on do tona ni anakmu, si Josep: Dipabangkit Debata do ahu gabe raja ni sandok tano Misir; tuat ma ho mandapothon ahu, unang togangtogangon.'),(9390,42,45,10,'Maringan di tano Gosen ma ho, asa jonok di lambungku, ho dohot anakmu dohot angka pahompum, ro di birubirum dohot lombum dohot nasa na di ho.'),(9391,42,45,11,'Ahu do marmuduhon ho disi, ai lima taonnari dope haleon on, asa unang raparan ho dohot isi ni bagasmu, ro di sude na di ho.'),(9392,42,45,12,'Antong matamuna dohot mata ni anggingki, si Benjamin i, marnidasa, paboa apala pamanganku manonahon hata i tu hamu.'),(9393,42,45,13,'Paboa hamu ma tu damang i sude hasangaponku di tano Misir, ro di sude na niidamuna i. Pahatop hamu ma ne tuat tuson rap dohot amanta.'),(9394,42,45,14,'Dung i dihaol ma rungkung ni si Benjamin, anggina i huhut tangis, si Benjamin i pe, sai tumatangis do mangkaol rungkungna.'),(9395,42,45,15,'Dung i diumma si Josep ma sude angka hahana i huhut tumatangis be; dung pe salpu i, asa mangkatai angka hahana i dohot ibana.'),(9396,42,45,16,'Dung i tarbortik ma barita i tu bagas ni raja Firaun, angka ninna do: Nunga ro angka haha ni si Josep; jadi denggan ma roha ni raja Firaun dohot angka naposona i.'),(9397,42,45,17,'Dung i ninna raja Firaun ma mandok si Josep: Dok ma tu angka hahami, songon on ma bahen hamu: Sorati hamu ma angka halodemuna i, borhat ma hamu, asa gira sahat hamu tu tano Kanaan.'),(9398,42,45,18,'Dung i boan hamu ma amamuna dohot ripemuna mandapothon ahu, asa hulehon tu hamu na dumenggan di tano Misir, asa dipangan hamu miak ni tano on.'),(9399,42,45,19,'Antong sahat tu ho ma i, bahen hamu ma songon on: Buat hamu ma sian tano Misir angka hureta hundulan ni anakkonmuna dohot jolmamuna i, boan hamu dohot amamuna ro tuson.'),(9400,42,45,20,'Jala unang habot rohamuna mida ugasanmuna i, ai gok di hamu do na denggan ni sandok tano Misir on.'),(9401,42,45,21,'Jadi dibahen angka anak ni si Israel ma songon i, diparlehon si Josep ma angka hureta tu nasida marningot pandok ni raja Firaun, dilehon tu nasida dohot bohal di dalan.'),(9402,42,45,22,'Diganupi nasida abit sasalin be, alai anggo tu si Benjamin tolu ratus rupia dohot lima salin abit dilehon.'),(9403,42,45,23,'Ditongoshon ma angkup ni di amana i sorat sampulu halode tunggal, angka na mangusung na dumenggan i sian tano Misir, dohot sampulu halode boruboru, angka siusung eme, sagusagu dohot sipanganon di amana i di tongan dalan.'),(9404,42,45,24,'Asa dipaborhat ma angka hahana i, gabe laho nasida; alai ninna do mandok nasida: Unang marbadai hamu di tongan dalan.'),(9405,42,45,25,'Jadi nangkok ma nasida sian tano Misir, gabe sahat nasida tu tano Kanaan, tu si Jakkob, amanasida i.'),(9406,42,45,26,'Jadi dibaritahon nasida ma tu ibana, angka ninna ma: Mangolu dope si Josep, jala ibana do sigomgom saluhut tano Misir; alai diparotootoi roha ni si Jakkob do i, ai ndang dihaporseai hatanasida i.'),(9407,42,45,27,'Alai dung dihatahon nasida tu ibana sude hata ni si Josep, angka na tinonahonna i tu nasida, diida dohot hureta, angka na tinongoshon ni si Josep mangusung ibana, gabe tarhilas ma tondi ni si Jakkob, amanasida i.'),(9408,42,45,28,'Dung i ninna si Israel ma: Taho ma i, mangolu dope hape si Josep anakki! Borhat ma ahu, asa huida ibana, tagan so mate dope ahu.'),(9409,42,46,1,'Asa borhat ma si Israel dohot sude na di ibana i. Jadi dung sahat ibana ro di Berseba, gabe dipelehon ma angka pelean tu Debata ni amana si Isak i.'),(9410,42,46,2,'Dung i didok Debata ma tu si Israel marhitehite pangungkapon bornginna i, ninna ma: Jo, ale Jakkob, Jakkob! Dison do ahu ninna ibana.'),(9411,42,46,3,'Dung i ninna ma: Ahu do Debata. Debata ni amam, unang ho mabiar tuat tu tano Misir; ai gabe bangso na bolon do ho tumpahanku disi.'),(9412,42,46,4,'Hudongani pe ho tuat tu tano Misir, jala sai na togihononku do ho nangkok sogot, ingkon si Josep do mangampehon tanganna tu matam.'),(9413,42,46,5,'Dung i borhat ma si Jakkob sian Berseba, jala ditogihon angka anak ni si Israel ma si Jakkob, amanasida i, dohot angka anakkonnasida ro di jolmanasida, di hureta, angka na tinongos ni raja Firaun mamboan ibana.'),(9414,42,46,6,'Diboan ma dohot pinahannasida, ro di sinamotnasida, angka na sinamotsamotnasida i di tano Kanaan, jadi sahat ma nasida tu tano Misir. Si Jakkob ro di sude pinomparna i.'),(9415,42,46,7,'Angka anakna dohot angka pahompuna, angka boruna dohot angka pahompuna boruboru, ro di sude pinomparna, diboan tu tano Misir rap dohot ibana.'),(9416,42,46,8,'Jala on ma angka goar ni anak ni si Israel, angka na tolhas tu tano Misir i: Si Jakkob dohot angka anakna i: Buhabaju ni si Jakkob, i ma si Ruben.'),(9417,42,46,9,'Angka anak ni si Ruben: Si Hanok, si Pallu, si Hezron dohot si Karmi.'),(9418,42,46,10,'Angka anak ni si Simeon: Si Jemuel, si Jamin, si Ohad, si Jakin, si Zohar dohot si Saul, tubu ni boru Kanaan.'),(9419,42,46,11,'Angka anak ni si Lepi: Si Gerson, si Kohat dohot si Merari.'),(9420,42,46,12,'Angka anak ni si Juda: Si Er, si Onan, si Sela, si Peres dohot si Jara. Alai anggo si Er dohot si Onan mate do di tano Kanaan. Ia anak ni si Peres: Si Hezron dohot si Hamul.'),(9421,42,46,13,'Angka anak ni si Isasar: Si Tola, si Pua, si Job dohot si Simron.'),(9422,42,46,14,'Angka anak ni si Zebulon: Si Sered, si Elon dohot si Jalel.'),(9423,42,46,15,'I ma pinompar ni si Lea, angka na tinubuhonna di si Jakkob di Padang-Aram, angkup ni si Dina, boruna i. Sude nasida, angka anakna dohot boruna, tolupulu tolu halak do.'),(9424,42,46,16,'Angka anak ni si Gad: Si Sipion, si Haggi, si Suni, si Esbon, si Eri, si Arodi dohot si Areli.'),(9425,42,46,17,'Angka anak ni si Aser: Si Innah, si Isha, si Ishi, si Beeria dohot si Jara, ibotonasida i; ia angka anak ni si Beeria: Si Heber dohot si Malkiel.'),(9426,42,46,18,'I ma pinompar ni si Zilpa, na nilehon ni si Laban tu si Lea, boruna i, asa na sampulu onom halak i do ditubuhon ibana di si Jakkob.'),(9427,42,46,19,'Angka anak ni si Rahel, jolma ni si Jakkob: Si Josep dohot si Benjamin.'),(9428,42,46,20,'Ia tubu ni si Josep di tano Misir, angka na tinubuhon ni si Asnat, boru ni si Potipera, malim di huta On di ibana: Si Manasse dohot si Epraim.'),(9429,42,46,21,'Angka anak ni si Benjamin: Si Bela, si Beker, si Asbel, si Gera, si Naaman, si Ehi, si Roseh, si Muppin, si Hupim dohot si Ared.'),(9430,42,46,22,'I ma pinompar ni si Rahel, angka na tinubuhonna di si Jakkob, sampulu opat halak saluhutna.'),(9431,42,46,23,'Ia anak ni si Dan: Si Husim.'),(9432,42,46,24,'Angka anak ni si Naptali: Si Jazeel, si Guni, si Jeser dohot si Shillem.'),(9433,42,46,25,'I ma pinompar ni si Bilha, na nilehon ni si Laban tu si Rahel, boruna i, asa na pitu halak i tinubuhonna di si Jakkob.'),(9434,42,46,26,'Sude jolma, angka na tolhas tu tano Misir rap dohot si Jakkob, angka na ruar sian gontingna, so padohot jolma ni angka anak ni si Jakkob, onompulu onom halak saluhutna.'),(9435,42,46,27,'Ia anak ni si Josep angka na tubu di ibana di tano Misir, dua halak do, asa boho pitupulu halak pinompar ni si Jakkob saluhut, angka na tolhas i tu tano Misir.'),(9436,42,46,28,'Dung i disuru si Jakkob ma si Juda pajolohon manopot si Josep, asa didokkon ro tu Gosen; jadi tolhas ma nasida tu tano Gosen.'),(9437,42,46,29,'Dung i dipasang si Josep ma huretana laho nangkok tu Gosen manjumpangkon si Israel, amana i. Jadi dung masipaidaan nasida, dihaol si Josep ma rungkungna, huhut sai tumatangis.'),(9438,42,46,30,'Dung i ninna si Israel ma mandok si Josep: Agia tung nuaeng ahu mate, asal dung huida bohim, laos mangolu dope ho hape.'),(9439,42,46,31,'Alai ninna si Josep ma mandok angka hahana i dohot tu isi ni bagas ni amana i: Nangkok ma ahu paboahonsa tu raja Firaun, jala dohononku ma tu ibana: Nunga ro angka hahaanggingku dohot isi ni bagas ni damang i, na di tano Kanaan na sai laon, mandapoti ahu.'),(9440,42,46,32,'Angka halak sipormahan birubiru do nasida, ai pardorbiaon do diula nasida, jala diboan nasida do birubirunasida dohot lombunasida, ro di sude na di nasida i.'),(9441,42,46,33,'On pe, molo dijou raja Firaun i hamu, jala didok: Aha do ulaonmuna i?'),(9442,42,46,34,'Songon on ma dok hamu: Halak pardorbia do angka naposomon sian sietehetehonnami rasirasa nuaeng, songon i hami, nang ompunami, asa anggiat dipaian hamu di tano Gosen, ai marbiasbias do halak Misir mida halak sipormahan birubiru.'),(9443,42,47,1,'Dung i laho ma si Josep mamaritahon tu raja Firaun, ninna ma: Nunga ro damang dohot angka hahaanggingku, ro di birubiru dohot lombunasida, ro di sude na di nasida i sian tano Kanaan, nunga pola sahat nasida di tano Gosen.'),(9444,42,47,2,'Dung i diarahon si Josep ma lima halak sian hahaanggina i, tu jolo ni raja Firaun.'),(9445,42,47,3,'Jadi didok raja Firaun i ma mandok hahaanggina i: Aha do ulaonmuna i? Jadi ninna nasida ma mandok raja Firaun: Siparmahan birubiru do angka naposomon, songon i hami, nang sian ompunami.'),(9446,42,47,4,'Angkup ni, didok nasida tu raja Firaun: Naeng maisolat hami di tano on, umbahen na ro, ai ndang adong jampalan ni birubiru ni angka naposom, ala posi ni haleon di tano Kanaan. On pe, sai loas ma angka naposom mian di tano Gosen.'),(9447,42,47,5,'Jadi ninna raja Firaun ma mandok si Josep: Nunga ro hape amanta dohot hahaanggim mandapoti ho.'),(9448,42,47,6,'Di jolomi do tano Misir on, di tano na dumenggan i ma paojak amanta dohot angka hahaanggim; di tano Gosen ma nasida mian. Ia adong diboto ho sian nasida jolma na pantas, pabangkit ma nasida gabe induk ni angka siramoti pinahanku sudena.'),(9449,42,47,7,'Dung i diarahon si Josep ma si Jakkob, amana i, mamboan ibana tujolo ni raja Firaun, jadi dipasupasu si Jakkob ma raja Firaun i.'),(9450,42,47,8,'Dung i ninna raja Firaun i ma mandok si Jakkob: Piga taon ma lelengmu mangolu?'),(9451,42,47,9,'Jadi ninna si Jakkob ma mandok raja Firaun: Saratus tolupulu taon ma leleng ni paisolatanki. Otik jala porsuk do ariari ni ngolungku, jala ndang dieak ariari ni ngolu ni angka daompung, di paisolatannasida i.'),(9452,42,47,10,'Dung i dipasupasu si Jakkob ma raja Firaun i, dung i ruar ma ibana sian jolo ni raja i.'),(9453,42,47,11,'Dung i dipahot si Josep ma inganan ni amana dohot angka hahana i, jala dilehon partaliannasida di tano Misir, di tano na dumenggan sahali di luat Ramses, songon na tinonahon ni raja Firaun i.'),(9454,42,47,12,'Jadi diparmudu si Josep ma amana dohot angka hahana i ro di sude pangisi ni bagas ni amana i, marguru tu torop ni anakkonna be.'),(9455,42,47,13,'Alai ndang adong sipanganon di sandok tano i, ai na potir situtu do haleon i, malas be do isi ni tano Misir dohot tano Kanaan, dibahen haleon i.'),(9456,42,47,14,'Asa dipagukguk si Josep ma nasa hepeng na jumpang ni tano Misir dohot ni tano Kanaan, tuhor ni eme, na ginalasnasida i; dung i ditaruhon si Josep ma hepeng i tu bagas ni raja Firaun.'),(9457,42,47,15,'Jadi dung suda hepeng di tano Misir dohot di tano Kanaan, ro be ma sude isi ni Misir manopot si Josep, angka ninna ma: Nia sipanganon di hami on! Boasa ma tagonan mate hami di jolomon, ala naung suda hepeng?'),(9458,42,47,16,'Jadi ninna si Josep ma: Boan hamu ma pahanpahananmuna tuson, asa hulehon tu hamu eme ali ni pinahanmuna i, molo naung suda hepeng i.'),(9459,42,47,17,'Jadi dipamboan nasida ma pinahannasida i tu si Josep, gabe dilehon si Josep ma tu nasida sipanganon ali ni angka hoda dohot birubiru dohot lombu dohot halode. Asa diparmudu ibana nasida na sataon i, ali ni saluhut pinahannasida i.'),(9460,42,47,18,'Jadi dung salpu na sataon i, ro ma nasida tu ibana paduataonhon angka ninna ma mandok ibana: Ndang tarporso hami tu ho ale rajanami, ndada holan perak i naung suda, pinahannami pe, nunga sahat ro di raja i sudena, ndang adong be tading, nda holan dagingnami dohot golatnami.'),(9461,42,47,19,'Boasa na tagonan mate hami paidaidaonmu, hami dohot golatnami? Ambati ma hami sandiri dohot golatnami ali ni sipanganon, asa mangadop di raja Firaun hami dohot golatnami, jala lehon boni, asa mangolu hami, unang mate, jala unang tarulang tano i.'),(9462,42,47,20,'Asa dituhor si Josep ma sandok tano Misir di raja Firaun, ai digadisi halak Misir do haumana be, dibahen potir ni haleon i, gabe raja Firaun ma nampuna tano i.'),(9463,42,47,21,'Dung i disagihon ma angka situan natorop i tu angka huta sian partalpahan ni tano Misir na sada tu na sada nari.'),(9464,42,47,22,'Holan hauma ni angka malim, ndang dituhor ibana, ai partalian ni malim do i sian raja Firaun, asa mangan nasida sian partaliannasida, na nilehon ni raja Firaun tu nasida; ala ni do umbahen so targadis nasida golatnasida i.'),(9465,42,47,23,'Dung i ninna si Josep ma mandok angka na torop i: Ida ma, nunga huambat hamu dohot golatmuna i sadari on di raja Firaun; na! Boni on di hamu, bonihon hamu ma i tu tano i.'),(9466,42,47,24,'Alai songon on ma taringot tu gogona i: Lehon hamu ma saparlima di raja Firaun, na opat bagian nari ma di hamu, bahen boni tu hauma i dohot bahen panganonmuna, ro di angka isi ni jabumuna, bahen sipanganon ni anakkonmuna huhut.'),(9467,42,47,25,'Jadi ninna nasida ma: Ho do na pangolu hami, sai asi ma roham, ale rajanami, marnida hami, sai hatoban ni raja Firaun i ma hami luhutna.'),(9468,42,47,26,'Laos patik ma i, dibahen si Josep rasirasa nuaeng, taringot tu tano ni halak Misir, saparlima di raja Firaun; holan tano ni angka malim sambing na so raja Firaun nampunasa.'),(9469,42,47,27,'Asa maringan di tano Misir ma Israel, di luat Gosen, mandapot di nasida ma i, jadi marpinompari ma nasida, gabe sopar situtu.'),(9470,42,47,28,'Dung i mangolu si Jakkob sampulu pitu taonnari di tano Misir, asa saratus opatpulu pitu taon ma umur ni si Jakkob.'),(9471,42,47,29,'Jadi dung marhira jumpang ajal ni si Israel, ditonahon ma anakna. Si Josep, ninna ma mandok ibana: Molo asi roham mida ahu ampehon ma tanganmu tu toru ni soithon, paboa na olo ho mambahen asi ni roha dohot hasintongan tu ahu, na so tupa tanomonmu ahu di tano Misir on.'),(9472,42,47,30,'Ingkon rap ahu modom dohot angka daompung, dibahen i ingkon boanonmu ahu sian tano Misir on, jala tanomonmu ahu di porbandaannasida. Jadi ninna si Josep ma: Hubahen pe songon na nidokmi.'),(9473,42,47,31,'Alai ninna ibana ma: Antong uarihon ma tu ahu! Jadi diuarihon ma tu ibana. Dung i marsomba ma si Israel dompak julu on ni podomanna i.'),(9474,42,48,1,'Jadi dung salpu angka jamita ondeng, tarbortik ma tu si Josep: Ida ma, marsahit do amanta. Jadi diarahon ma anakna na dua i: Si Manasse dohot si Epraim.'),(9475,42,48,2,'Jadi dung tarbortik tu si Jakkob, adong na mandok: Ida ma, nunga ro anakmu si Josep manopot ho, gabe dipatogar si Israel ma dirina, hundul ma ibana di podomanna.'),(9476,42,48,3,'Dung i ninna si Jakkob ma mandok si Josep, boti ma i: Huida do Debata, pargogo na so hatudosan di huta Lus, di tano Kanaan, jala dipasupasu ahu.'),(9477,42,48,4,'Jala ninna do mandok ahu: Ida ma, hutumpahi pe ho marpinompar, jala hupatorop pe ho, hubahen pe ho gabe abalan ni angka bangso, jala lehononku do tano on tu pinomparmu sogot, bahen partohapannasida salelenglelengna.'),(9478,42,48,5,'Nuaeng pe, anakmu na dua i, na tubu di ho di tano Misir, tagan so ro dope ahu mandapoti ho tu tano Misir on: Si Epraim dohot si Manasse, ahu ma nampuna nasida, sobokkon si Ruben dohot si Simeon.'),(9479,42,48,6,'Alai anakmu angka na tubu dungkon ni, ho ma nampunasa, goar ni angka hahanasida i ma panggoarhononna di partaliannasida.'),(9480,42,48,7,'Ai di na ro i ahu sian Padang-Aram, mate do si Rahel di tano Kanaan, di dalan mardonokkon huta Eprata, laos hutanom do ibana disi, di dalan laho tu Eprata, i ma Betlehem.'),(9481,42,48,8,'Dung i dibereng si Israel ma angka anak ni si Josep, jala didok ma: Ise do angka nasida on?'),(9482,42,48,9,'Jadi ninna si Josep ma mandok amana i; anakku do nasida, angka na nilehon ni Debata di ahu dison. Jadi didok si Jakkob ma: Ua togu nasida tu lambungkon, asa hupasupasu nasida.'),(9483,42,48,10,'Ai nunga rambon mata ni si Israel dibahen torasna, ndang be torang ibana marnida. Jadi dipajonok ma na dua i tu ibana, gabe diumma ma nasida, jala dihaol.'),(9484,42,48,11,'Dung i ninna si Israel ma mandok si Josep: Nunga huida bohim, so hirimhirim ni rohangku jala ida ma, dohot do pinomparmu dipaidahon Debata tu ahu.'),(9485,42,48,12,'Dung i ditogu si Josep ma nasida sian ampuanna, jala manungkap ibana tu tano di jolona.'),(9486,42,48,13,'Dung i dibuat si Josep ma nasida duansa, si Epraim di siamunna, tondong ni sihambirang ni si Israel, jala si Manasse di sihambirangna, tondong ni siamun ni si Israel, jala dipajonok nasida tu ibana.'),(9487,42,48,14,'Alai dipatoltol si Israel ma tanganna siamun laho mangampehon tu ulu ni si Epraim, atek pe ibana siampudan, jala tanganna sihambirang tu ulu ni si Manasse, dituntun rohana do mambahen songon i tanganna i, atek pe si Manasse sihahaan.'),(9488,42,48,15,'Dung i dipasupasu si Israel ma si Josep, ninna ma: Debata, ni hinabiaran ni angka daompung, si Abraham dohot si Isak, angka na marparange di jolona, Debata siparmahan ahu saleleng ahu adong rasirasa sadari on.'),(9489,42,48,16,'Suruan, na palua ahu sian nasa parmaraan, i ma mamasumasu dakdanak na dua on, sai tarbarita ma goarhu marhitehite nasida, ro di goar ni angka daompung si Abraham dohot si Isak, sai sobokkon dengke ma hinadeaknasida di pusoran ni tano on.'),(9490,42,48,17,'Alai dung diida si Josep, na niampehon ni amana tanganna siamun i tu ulu ni si Epraim, sogo ma rohana marnida, gabe ditiop ma tangan ni amana i paunsathon sian ulu ni si Epraim tu ulu ni si Manasse.'),(9491,42,48,18,'Jadi didok si Josep ma mandok amana i: Ndang songon i, ale amang, ai on do sihahaan, ampehon ma tanganmu siamun tu uluna.'),(9492,42,48,19,'Alai manjua do amana i, ninna ma: Tangkas do i huboto, ale anaha! Ibana pe gabe bangso do sogot, balga do nang ibana; alai sai na umbalga do anggina i sogot sian ibana, jala pinomparna i sogot abalan ni angka bangso.'),(9493,42,48,20,'Jadi dipasupasu ma nasida di na sadari i, ninna ma: Marhitehite ho do mamasumasu si Israel, dohonon ni halak ma: Dibahen Debata ma ho, songon si Epraim dohot songon si Manasse! Jadi jumolo ma si Epraim dibahen maradophon si Manasse.'),(9494,42,48,21,'Dung i ninna si Israel ma mandok si Josep: Ida ma, na mate ma anggo ahu, alai sai donganan ni Debata do hamu, sai pasuangonna do hamu tu tano ni ompumuna.'),(9495,42,48,22,'Ianggo ahu, nunga hulehon di ho tano i santurpuk hasurunganmu sian angka haham, na hubuat sian halak Amori marhitehite podangku dohot siorhu.'),(9496,42,49,1,'Dung i dijou si Jakkob ma angka anakna i, ninna ma: Marluhut ma hamu, asa hupaboa tu hamu na masa sogotsogot ni ari tu hamu.'),(9497,42,49,2,'Sange ma hamu marpungu, jala tangihon hamu ma, ale angka anak ni si Jakkob! Tangihon hamu ma hata ni si Israel, amamuna i.'),(9498,42,49,3,'Ale Ruben, ho do buhabajungku, gogongku jala bungabunga ni hagabeonku, parhasurungan di hamuliaon dohot parhasurungan di gogo nian.'),(9499,42,49,4,'Alai songon parbullak ni aek do ho ala ni; ndang be marhasurungan ho, ai dipanaek ho do podoman ni amam, diramuni ho do i uju na pinanaekmi tongon podomanki.'),(9500,42,49,5,'Ale Simeon dohot Lepi, angka na martinodohon; sinjata hajungkaton do podangnasida i.'),(9501,42,49,6,'Sai unang ma saor rohangku tu tahitahinasida, sai unang sauduran hasahalonku (tondingku) tu parluhutannasida i; ai mangasahon piripirinasida dibunui do angka jolma i, jala ditangkai angka lombu mangasahon jungkat ni rohanasida.'),(9502,42,49,7,'Marjea ma muruknasida i, ala mambahen uhum gogo dohot piripirina, ala jorbutna. Saginonku ma nasida tu tongatonga ni pinompar ni Jakkob, jala paserahonku nasida tu tongatonga ni pinompar ni Israel.'),(9503,42,49,8,'Ale Juda, ho do tongon sipujion ni angka hahaanggim! Pampe tu tangkuhuk ni angka musum sogot tanganmu, marsomba tu ho do angka anak ni amam.'),(9504,42,49,9,'Anak ni singa do si Juda. Manaek binahen ni sorosoro do ho ale anaha! Manggompang do ibana jala dipapodom, songon singa tunggal dohot songon singa boruboru; tung ise ma na hum manunggul ibana?'),(9505,42,49,10,'Ndang na meret tungkot harajaon sian si Juda, manang paruhum sian holangkolang ni patna paima ro si Shilo, jala Ibana do ihuthonon ni angka bangso.'),(9506,42,49,11,'Tambatonna ma halodena i tu bona ni hau anggur dohot anak ni halodena boruboru tu ranting ni hau anggur na denggan; taptaponna ma ulosna tu anggur, jala tu gota ni hau anggur sampesampena.'),(9507,42,49,12,'Rumara sian anggur do matana, jala sumaksak sian susu ia ngingina.'),(9508,42,49,13,'Ianggo si Zebulon, di topi laut do ibana maringan, di labuan ni angka parau, marbalokkon Sidon.'),(9509,42,49,14,'Ianggo si Isasar halode sipir holiholi do, na manggompang tu holangkolang ni angka parduan.'),(9510,42,49,15,'Dung diida denggan do hape pangulonan i jala lomak tano gabe dipaunduk ma abarana laho mamorsan, jadi halak parrodi ma ibana.'),(9511,42,49,16,'Ianggo si Dan manguhum bangsona sogot, sobokkon marga ni Israel i sadasada.'),(9512,42,49,17,'Doshon ulok di pinggol ni dalan do si Dan sogot, doshon ulok dari tahe di topi ni dalan balobung, mamarguti angka tambihul ni hoda, asa tunggaling tu pudi jolma na mangkunduli.'),(9513,42,49,18,'Hatuaonmi do na huhalungunhon, ale Jahowa!'),(9514,42,49,19,'Ianggo si Gad onjaron ni sapanggungan parbajo sogot, alai leleonna do nasida muse.'),(9515,42,49,20,'Sian si Aser ro sagusagu hataboanna, jala ibana mangalehon sipanganon hasoloan ni raja.'),(9516,42,49,21,'Ursa tini na gompis ianggo si Naptali, na mangalehon bagabaga na denggan.'),(9517,42,49,22,'Anak ni hau na marparbue ianggo si Josep, anak ni hau na marparbue di topi mual, dijangkit angka rantingna do dorpi.'),(9518,42,49,23,'Digitihi angka partembak ibana, jala disiorhon angka sumbia dompak ibana, jala dipaburuburu ibana.'),(9519,42,49,24,'Alai sai toltol do anggo siorna i, jala gogo do botohon ni tanganna i, pinargogo ni tangan ni Ulubalang ni si Jakkob, marhitehite goar ni Sipormahan, Partanobatoan ni si Israel.'),(9520,42,49,25,'Sian Debata ni amam, sai tumpahanna do ho, jala sian Pargogo na so hatudosan, sai pasupasuonna do ho, marhitehite pasupasu sian banua ginjang, marhitehite pasupasu sian lung, na gistang di toru, marhitehite pasupasu sian bagot panusuan dohot hagabeon ni bortian.'),(9521,42,49,26,'Disalpui angka pasupasu ni amam do angka pasupasu ni angka daompung sian na robi sahat ro di partalpahan ni angka dolokdolok sian mulamula; sai songgop ma angka i tu ulu ni si Josep dohot tu sisigatan ni Nasir (na pinillit) ni angka hahaanggina.'),(9522,42,49,27,'Tudoshon gompul panoro do ianggo si Benjamin; manganhon sorosoro ibana manogotna i, jala marbagi tabantaban ibana bodarina i.'),(9523,42,49,28,'I ma sude marga ni Israel na sampulu dua i, jala i do na pinaboa ni amanasida tu nasida laho mamasumasu nasida; pulik be do pamasumasuna i laho mamasumasu nasida be.'),(9524,42,49,29,'Dung i martona ma ibana, didok ma mandok nasida: Papunguon tu bangsongku nama anggo ahu; tanom hamu ma ahu tu lambung ni angka daompung, tu liang na di hauma ni si Epron, marga Het i.'),(9525,42,49,30,'Tu liang na di hauma Makpelah, tondong ni Mamre di tano Kanaan, na tinuhor ni si Abraham rap dohot hauma na sian si Epron, marga Het i, bahen parbandaan hapunjungan.'),(9526,42,49,31,'Tusi do tartanom si Abraham dohot si Sarah, jolmana i; tusi do tartanom si Isak dohot si Rebekka, jolmana i; jala tusi do hutanom dohot si Lea.'),(9527,42,49,32,'Hauma i dohot liang i na di bagasan, na tinuhor do i sian anak ni si Het.'),(9528,42,49,33,'Jadi dung sahat angka tona ni si Jakkob tu angka anakna i, dipapungu ma patna tu podomanna i, jadi tos ma hosana, gabe pinapungu ibana tu bangsona.'),(9529,42,50,1,'Dung i tinggang ma si Josep tu bohi ni amana i, sai ditatangisi ibana huhut diumma.'),(9530,42,50,2,'Dung i ditonai si Josep ma angka naposona, angka datu i, asa dihapuri amana i. Jadi dihapuri angka datu i ma si Israel.'),(9531,42,50,3,'Jadi opatpulu ari ma lelengna diula nasida, ai na sai leleng do somal ariari pangkapuron. Jadi sai ditatangisi halak Misir ibana pitupulu borngin lelengna.'),(9532,42,50,4,'Jadi dung salpu angka ari pangandunganna i gabe didok si Josep ma tu isi ni bagas ni raja Firaun: Molo tung asi rohamuna mida ahu, sai patolhas hamu ma hatangkon tu pinggol ni raja Firaun:'),(9533,42,50,5,'Dipatolon damang i do ahu, ninna: Ida ma, na mate ma anggo ahu; ingkon di tanoman, na huhali di ahu, di tano Kanaan i, tusi ma tanomonmu ahu. On pe, loas nangkok, asa hutanom damang, dung i mulak ahu.'),(9534,42,50,6,'Dung i ninna raja Firaun ma: Nangkok ma ho, tanom ma amanta i, songon naung niuarihonmu.'),(9535,42,50,7,'Jadi nangkok ma si Josep laho mananom amana i; nangkok dohot sude naposo ni raja Firaun rap dohot ibana, ro di sintua ni bagasna i dohot sude angka sintua ni tano Misir i.'),(9536,42,50,8,'Ro di sude isi ni bagas ni si Josep dohot angka hahaanggina i, ro di isi ni bagas ni amana i; holan angka anakkonnasida dohot birubirunasida dohot lombunasida tading di tano Gosen.'),(9537,42,50,9,'Nangkok ma udur dohot ibana nang angka hureta, nang angka parsihundul, tung na torop situtu do nasida na sauduran i.'),(9538,42,50,10,'Jadi dung sahat nasida ro di pardegean Atad, na di bariba ni Jordan, digogo nasida ma disi mangondasi, arga pitu borngin lelengna diandungi si Josep amana i.'),(9539,42,50,11,'Jadi dung diida isi ni tano i, halak Kanaan i, pangondason na di pardegean Atad i, angka ninna ma: Hinamago bolon maon di halak Misir. Ala ni do umbahen Abel Misraim goar ni pangulonan i didok halak, na di bariba ni Jordan.'),(9540,42,50,12,'Dung i dipatupa angka anak ni si Israel ma tu ibana, songon naung tinonahonna i tu nasida.'),(9541,42,50,13,'Diusung angka anakna i ma ibana tu tano Kanaan, jala ditanom ibana di liang ni hauma Makpelah, tondong ni Mamre, na tinuhor ni si Abraham rap dohot hauma i sian si Epron, marga Het i, bahen parbandaan hapunjungan.'),(9542,42,50,14,'Jadi dung ditanom nasida amana i, mulak ma si Josep tu tano Misir, ibana dohot angka hahaanggina i, dohot sude angka na nangkok rap dohot ibana mananom amana i.'),(9543,42,50,15,'Alai mabiar do angka hahaanggi ni si Josep, dung mate amanasida i, angka ninna nasida ma: Atek tung beha dihosom si Josep hita maon, gabe dipasudol tu hita sude na roa, na itabahen i tu ibana.'),(9544,42,50,16,'Jadi martona ma nasida tu si Josep, ninna: Hata ni amanta tu hami, tagan so mate dope ibana:'),(9545,42,50,17,'Songon on ma dok hamu tu si Josep: Sai marpamuati ma roham di hajahaton ni hahaanggim dohot di dosanasida i, ai tung na roa do na binahennasida i tu ho; on pe, sai marpamuati ma roham di hajahaton ni angka naposo ni Debata ni amanta i! Alai tangis do anggo si Josep, dung didok nasida songon i tu ibana.'),(9546,42,50,18,'Dung i laho dohot angka hahaanggina i, marsinggang ma nasida tu jolona, angka ninna ma: Ida ma hami on, naposom ma hami.'),(9547,42,50,19,'Dung i ninna si Josep ma mandok nasida: Unang hamu mabiar, ai nda guru di Debata do nang ahu?'),(9548,42,50,20,'Na jat do nian tutu tahi ni rohamuna dompak ahu, alai na denggan do hapatean ni dibahen Debata, patupahon songon na niidamuna sadari on, laho pangoluhon bangso na bolon.'),(9549,42,50,21,'On pe, unang mabiar hamu, ahu ma marmuduhon hamu dohot anakkonmuna i. Dung i diapul ma nasida pasabamhon rohanasida.'),(9550,42,50,22,'Jadi maringan di tano Misir ma si Josep, ibana dohot isi ni jabu ni amana i, jala mangolu si Josep saratus sampulu taon lelengna.'),(9551,42,50,23,'Jadi sanga diida si Josep pinompar ni si Epraim ro di sundut patoluhon; angka anak ni si Mahir pe, tubu ni si Manasse, tu ampuan ni si Josep do ditubuhon.'),(9552,42,50,24,'Dung i didok si Josep ma tu angka hahaanggina i: Na mate ma anggo ahu, alai sai tingkiron ni Debata do hamu sogot, jala togihononna hamu ruar sian tano on tu tano na niuarihonna i tu si Abraham, si Isak dohot tu si Jakkob.'),(9553,42,50,25,'Dung i dipatolon si Josep ma angka anak ni si Israel, ninna ma: Sai na tingkiron ni Debata do hamu sogot, ingkon boanonmu ma dohot holiholingku sian on!'),(9554,42,50,26,'Jadi mate ma si Josep marumur saratus sampulu taon, gabe dihapuri nasida ibana, jala dipamasuk ibana tu bagasan batang di tano Misir.'),(9555,43,1,1,'On ma goargoar ni halak Israel, angka na ro tu tano Misir udur dohot si Jakkob; ro be do nasida dohot donganna sajabu.'),(9556,43,1,2,'Si Ruben, Simeon, Lepi dohot Juda.'),(9557,43,1,3,'Isasar, Zebulon dohot Benjamin.'),(9558,43,1,4,'Dan dohot Naptali, Gad dohot Aser.'),(9559,43,1,5,'Jadi saluhut hosa angka na ruar sian gonting ni si Jakkob pitupulu halak do; alai anggo si Josep, nunga di tano Misir hian.'),(9560,43,1,6,'Dung i mate ma si Josep dohot sude hahaanggina i dohot sandok na sansundut i.'),(9561,43,1,7,'Dung i lam torop mabue ma anak ni Israel, marpinompari nasida, jala lam deak, jala lam marhuaso situtu, pola gok tano i dibahen nasida.'),(9562,43,1,8,'Alai bangkit ma muse sada raja na imbaru gumomgom Misir, na so tumanda si Josep.'),(9563,43,1,9,'Jadi didok ma tu bangsona: Ida ma, nunga gabe sada bangso halak Israel, tumorop jala gumogo sian hita.'),(9564,43,1,10,'Tole ma, itabisuki ma nasida, asa unang be lam ganda; atek tung beha tubu hamusuon, jadi dohot nasida maon marbalik gabe satahi dohot musunta jala mangalo hita, jala haruar sian tano on.'),(9565,43,1,11,'Jadi dipabangkit ma angka mandur mangarodi nasida, asa dipaporsukporsuk nasida marhitehite ulaon na dokdok; gabe dipauli ma huta Pitom dohot Ramses bahen parpeopan ni ugasan ni raja Firaun.'),(9566,43,1,12,'Hape lam dipaporsukporsuk nasida, lam torop nasida jala rarat, gabe lam tahuton nasida mida halak Israel.'),(9567,43,1,13,'Jadi gogo ma dibahen halak Misir marhatobanhon halak Israel.'),(9568,43,1,14,'Jadi marsingkor ma diahap nasida dibahen renge ni ulaon, pauli tano liat bahen tano takko dohot dibahen nasa ulaon di balian ro di sude ulaonnasida na asing, na niulanasida sian utang na so tarjua.'),(9569,43,1,15,'Dung i ninna raja ni Misir ma tu angka sibaso ni halak Heber, goar ni na sada Sipra, goar ni na sada nari Pua.'),(9570,43,1,16,'Ninna ma: Molo diurupi hamu boru ni halak Heber laho mangintubu, jala diida hamu di atas sapa paridian, molo tung baoa bunu hamu, alai molo boruboru atenang pangolu hamu.'),(9571,43,1,17,'Alai mabiar do angka sibaso i mida Debata, jadi ndang dibahen nasida songon na nidok ni raja ni Misir i tu nasida, dipangolu nasida do angka posoposo baoa.'),(9572,43,1,18,'Jadi ditonahon raja ni Misir i ma angka sibaso, didok ma tu nasida: Boasa tung songon i bahenonmuna, tung dipangolu hamu angka posoposo baoa?'),(9573,43,1,19,'Jadi ninna angka sibaso i ma tu raja Firaun: Ndang songon boru Misir anggo boru Heber, ai togap do nasida, ndang dope ro sibaso i tumopot nasida, nunga sahat ditubuhon.'),(9574,43,1,20,'Jadi ala ni ditumpak Debata ma angka sibaso i, lam tamba ma bangso i, jala tongam situtu.'),(9575,43,1,21,'Jadi ala dihabiari angka sibaso i Debata, gabe dibahen ma angka bagas di nasida.'),(9576,43,1,22,'Dung i dipatik raja Firaun ma sandok na nirajaanna, ninna ma: Nasa posoposo baoa tu batang aek Nil ma danggurhon hamu, alai nasa parompuan pangolu hamu.'),(9577,43,2,1,'Dung i adong ma sada halak sian pomparan ni si Lepi jala dibuat sada boru Lepi bahen jolmana.'),(9578,43,2,2,'Dung i gabegabean ma jolmana i, jala ditubuhon anak sada. Jadi dung diida mansai uli rupana, ditabunihon ma ibana tolu bulan lelengna.'),(9579,43,2,3,'Alai dung so tarbunihonsa be, dibuat ma sada poti sian arung, jala dipauli huhut didamari, dung i dipeakkon posoposo i tu bagasan, ditaruhon ma i tu bagasan arung na di pasir ni batang aek i.'),(9580,43,2,4,'Alai jongjong ia ibotona i manatapnatap, asa diboto manang beha bahenonna tu ibana.'),(9581,43,2,5,'Dung i tuat ma boru ni raja Firaun naeng maridi di bagasan batang aek i: Sai mardalandalan di pasir ni aek i anggo angka naposona boruboru. Jadi dung diida poti na metmet i di tongatonga ni arung i, gabe disuru ma naposona mangalap.'),(9582,43,2,6,'Jadi dung diungkap, diida ma posoposo i jala ida ma sai tumatangis posoposo i, jadi asi ma rohana mida ibana, ninna ma: Sian angka posoposo ni halak Heber do on.'),(9583,43,2,7,'Dung i ninna ibotona i ma tu boru ni raja Firaun: Beha ia laho ahu manjou sada inaina panusuan sian boru ni halak Heber, asa dipasusu posoposo i di ho?'),(9584,43,2,8,'Jadi ninna boru ni raja Firaun ma tu ibana: Antong laho ma ho. Jadi laho ma angkora i, jala dijou ina ni posoposo i.'),(9585,43,2,9,'Dung i ninna boru ni raja Firaun i ma tu ibana: Boan ma posoposo on jala pasusu di ahu, hulehon pe upam. Jadi dibuat inaina i ma posoposo i, jala dipasusu.'),(9586,43,2,10,'Jadi dung magodang posoposo i, ditaruhon ma tu boru ni raja Firaun gabe diain ma i bahen anakna, jala dibahen'),(9587,43,2,11,'Dung i di angka ari i, dung magodang si Musa, ruar ma ibana tumopot angka hahaanggina i, asa diida pardangolan'),(9588,43,2,12,'Dung i dibereng ma humaliang, jadi dung diida, ndang adong jolma disi, gabe dibunu ma halak Misir i, jala dipa'),(9589,43,2,13,'Ruar muse ibana di ari paduahon, jadi ida ma, dua halak Heber marbadai. Jadi ninna ma tu parsala i: Boasa balb'),(9590,43,2,14,'Alai ninna bayo i ma: Ise pabangkit ho bahen sintua dohot panguhum di hami? Naeng bunuonmu ahu huroha, songon'),(9591,43,2,15,'Sar ma barita i tu raja Firaun, jadi dilului ma si Musa naeng bunuonna. Alai lintun ma si Musa sian jolo ni ra'),(9592,43,2,16,'Alai pitu do boru ni malim di Midian, angka na ro manahui, asa digohi parsoburan, laho pasoburhon birubiru ni'),(9593,43,2,17,'Alai ro ma angka parmahan mangonjaronjar nasida. Jadi hehe ma si Musa mangurupi nasida jala dipasobur birubiru'),(9594,43,2,18,'Ia dung sahat nasida tu si Reguel, amanasida i, ninna ibana ma: Boasa tung songon i tibu hamu muli sadari on?'),(9595,43,2,19,'Jadi didok nasida ma: Adong tahe sada halak Misir, na palua hami sian tangan ni angka parmahan i, huhut sai di'),(9596,43,2,20,'Dung i didok ma tu angka boruna i; alai didia ibana? Boasa tung tading baoa i bahenonmuna? Jou hamu ibana, asa'),(9597,43,2,21,'Jadi mangoloi ma si Musa maisolat di halak i, gabe dilean ma boruna si Zippora bahen jolma ni si Musa.'),(9598,43,2,22,'Ditubuhon ma sada anak, dibahen ma goarna si Gerson, ai paisolat do ahu di tano na pulik, ninna.'),(9599,43,2,23,'Dung i di laonlaon ni ari, mate ma raja ni Misir. Alai sai mangangguhi ma halak Israel dibahen renge ni ulaonn'),(9600,43,2,24,'Dung i ditangihon Debata ma angguhangguknasida i, jala diingot padanna tu si Abraham, si Isak dohot si Jakkob.'),(9601,43,2,25,'Jadi ditatap Debata ma halak Israel i, jala ditanda nasida.'),(9602,43,3,1,'Alai diparmahan si Musa do birubiru ni si Jetro, simatuana i, malim di Midian, jala diadu angka birubiru i salpu halongonan i, sahat ro di dolok ni Debata, i ma dolok Horeb.'),(9603,43,3,2,'Dung i diida ma suruan ni Jahowa di bagasan api na marnalanala di tongatonga ni ramba. Dung i dibereng ma, jadi diida marsigorgor api di bagasan ramba i, alai ndang olo suda ramba i dipangan.'),(9604,43,3,3,'Jadi ninna si Musa ma: Ua laho ma ahu jumonok tahe, asa huida halongangan bolon on, ala ni aha umbahen so mosok ramba i.'),(9605,43,3,4,'Alai dung diida Jahowa ibana ro naeng manilik, joujou ma Debata sian tongatonga ni ramba i, ninna ma mandok ibana: Jo Musa, Musa! Dison do ahu, ninna.'),(9606,43,3,5,'Dung i ninna ma: Unang jumonok ho tuson, tanggali ma sipatum sian patmu, ai tano na badia do hajongjonganmon.'),(9607,43,3,6,'Dung i ninna muse: Ahu do Debata ni ompum, Debata ni si Abraham, Debata ni si Isak dohot Debata ni si Jakkob. Jadi dihungkupi si Musa ma bohina, ai mabiar do ibana mamereng Debata.'),(9608,43,3,7,'Dung i ninna Jahowa ma: Nunga tangkas huida pardangolan ni bangsongku na di tano Misir i, jala nunga hutangihon angguhangguknasida i maradophon angka na pasosaksosak nasida; ai nunga huboto pa'),(9609,43,3,8,'Ala ni umbahen tuat Ahu paluahon nasida sian tangan ni halak Misir, jala paruarhon nasida sian tano on tu tano na denggan jala na lambas, tu tano habaoran ni susu dohot situak ni loba, tu tano'),(9610,43,3,9,'On pe, ida ma, nunga sahat angguhangguk ni halak Israel tu Ahu, nunga huida huhut pangarupaon, na binahen ni halak Misir tu nasida.'),(9611,43,3,10,'On pe, laho ma ho, suruonku ma ho tumopot raja Firaun, asa di paruar ho bangsongku, halak Israel i sian Misir.'),(9612,43,3,11,'Alai ninna si Musa ma mandok Debata: Ise ma ahu pola, tung dapothononku raja Firaun i, tung paruaronku halak Israel sian Misir?'),(9613,43,3,12,'Alai ninna Ibana ma: Hudongani pe ho, jala on ma tandam paboa Ahu marsuru ho: Ia dung diparuar ho bangso i sian Misir, oloanmuna ma Debata di lambung dolok on.'),(9614,43,3,13,'Dung i ninna si Musa ma mandok Debata: Ida ma, ia dung sahat ahu di halak Israel, jala hudok tu nasida: Debata ni ompumuna marsuru ahu tu hamu, gabe didok nasida maon tu ahu: Ise do goarna? Beha ma dohononku'),(9615,43,3,14,'Dung i ninna Debata ma tu si Musa: Ahu nuaeng Ahu sogot. Dung i ninna muse: Songon on do dohononmu tu halak Israel: Jahowa marsuru ahu tu hamu.'),(9616,43,3,15,'Angkup ni didok Debata tu si Musa: Songon on do dohononmu tu halak Israel: Jahowa, Debata ni ompumuna, Debata ni si Abraham, Debata ni si Isak dohot Debata ni si Jakkob marsuru ahu tu hamu; i do goarhu ro di'),(9617,43,3,16,'Laho ma ho, paluhut ma angka sintua ni Israel, jala dok ma tu nasida: Jahowa, Debata ni ompumuna na huida i, Debata ni si Abraham, Isak dohot Jakkob, mandok: Nunga ditatap rohangku hamu, dohot na binahenna tu hamu di Misir'),(9618,43,3,17,'Nunga pola hudok: Sai paruaronku do hamu sian pardangolan Misir, tu tano ni halak Kanaan, dohot Het i, Amori. Perisi, Hevi, dohot Jebusi, tu tano habaoran ni susu dohot situak ni loba.'),(9619,43,3,18,'Sai tangihononnasida do soaram, dung i laho ma ho dohot angka sintua ni halak Israel tumopot raja ni Misir, jala dohononmuna tu ibana: Jahowa, Debata ni halak Heber manjumpangkon hami. On pe, ua loas ma hami mardalan tu halongonan par toluarian asa hupele hami jahowa Debatanami'),(9620,43,3,19,'Alai nunga huboto hian, ndang loason ni raja sian Misir hamu mardalan, nang marhitehite tangan na gogo.'),(9621,43,3,20,'Alai patoltolonku ma tanganku, asa huhonahon tu Misir sude halongangan, angka sibahenonku di tongatonganasida, jala dung songon i paluaonna ma hamu.'),(9622,43,3,21,'Dapotan asi ni roha pe bangso on sian halak Misir bahenonku, asa ia ruar nasida, unang ruar lumang boti.'),(9623,43,3,22,'Mangido ma ganup boruboru sian hombar bagasna dohot sian soranganna angka ulaula perak dohot ulaula sere dohot ulos, angka siampehononmuna tu anakmuna dohot tu borumuna, sintahanmuna sian Misir ma i.'),(9624,43,4,1,'Dung i ninna si Musa ma mangalusi: Ida ma, ra tung ndang porsea nasida di ahu, jala ndang tangihononna soarangki, dohononnasida ma tagamon: Ndang tutu diida ho Jahowa.'),(9625,43,4,2,'Jadi ninna Jahowa ma tu ibana: Aha do i na di tanganmi? Tungkot do i, ninna.'),(9626,43,4,3,'Dung i ninna Jahowa ma: Reongkon ma i tu tano! Jadi direongkon ma tu tano, gabe ulok ma i, jadi maporus ma si Musa maradophon ulok i.'),(9627,43,4,4,'Alai ninna Jahowa ma tu si Musa: Surdukkon ma tanganmi, jala tangkup ihurna. Jadi disurdukkon ma tanganna manangkupsa, jadi sumuang gabe tungkot di bagasan tanganna.'),(9628,43,4,5,'Porsea ma nasida bahenon ni, paboa naung diida ho Jahowa, Debata ni ompunasida, Debata ni si Abraham, Debata ni si Isak dohot Debata ni si Jakkob.'),(9629,43,4,6,'Angkup ni didok Jahowa tu ibana: Ua surukkon tanganmi tu bagasan gedekgedekmi. Jadi disurukkon ma tanganna tu bagasan gedekgedekna, jala dienet muse, ida ma, nunga puruon tanganna, mamontar songon hapur'),(9630,43,4,7,'Dung i ninna Jahowa muse: Ulahi surukkon tanganmi tu gedekgedekmi, jadi diulahi tanganna tu gedekgedekna, jala dung dienet muse sian gedekgedekna, ida ma, nunga sumuang, songon sibukna hian'),(9631,43,4,8,'Molo so porsea nasida di ho, jala ndang ditangihon soara ni tanda parjolo i, sai porsea do nasida bahenon ni soara ni tanda paduahon i.'),(9632,43,4,9,'Alai molo so porsea nasida dohot di tanda paduahon on jala ndang ditangihon soaram, tahui ma aek sian batang aek i, jala usehon tu tano na mahiang, gabe mudar ma aek na binuatmi sian batang aek i dung  rodi na mahiang'),(9633,43,4,10,'Alai ninna si Musa ma tu Jahowa: Iale Tuhanku, ianggo ahu ndang jolma parhata ahu, nang olat ni nantuari, nang olat ni nantuarisada, nang olat ni dung mangkatai Ho dohot napos om, ai dokdok do pamangan ku jala hallang dilangku'),(9634,43,4,11,'Dung i ninna Jahowa ma tu ibana: Tung ise ma mambahen marpamangan jolma i? Tung ise mambahen na ngungu manang na nengel, manang na marnida, manang na mapitung? Nda Ahu, Jahowa do?'),(9635,43,4,12,'On pe, laho ma ho, hudongani pe pamanganmi, hupodahon pe tu ho sidohononmu.'),(9636,43,4,13,'Dung i ninna si Musa muse: Iale Tuhan, ua suru ma manang ise lomo ni roham!'),(9637,43,4,14,'Jadi tarrimas situtu ma Jahowa mida si Musa, didok ma: Nda disi do si Aron, hahami, marga Lepi, huboto do pande ibana mangkatai, ida ma huhut, ipe ruar ibana manjumpangkon ho, ia dung ho diida lasma rohana di bagasan'),(9638,43,4,15,'Dung i pangkulingi ma ibana, jala tuturhon angka hata i tu pamanganna, hudongani pe pamanganmu dohot pamanganna, hupodahon pe tu hamu sibahenonmuna.'),(9639,43,4,16,'Ibana pe singkatmu mandok tu bangso i, ibana hira pamanganmu, jala ho hira Debatana.'),(9640,43,4,17,'Tiop ma dohot tungkot on di bagasan tanganmu, hitehitem mambahen angka tanda i muse.'),(9641,43,4,18,'Dung i laho ma si Musa, mulak tu si Jetro, simatuana i, didok ma tu ibana: Ua loas ma ahu laho, asa mulak ahu tu hahaanggingku, angka na di Misir, asa huida, manang na mangolu dope nasida. Dungi ninna si Jetro ma tusi Musa: Laho ma ho mardamedame.'),(9642,43,4,19,'Dung i ninna Jahowa ma tu si Musa di Midian: Laho ma ho mulak tu Misir, ai nunga mate sude halak, angka na tumahi hosam.'),(9643,43,4,20,'Jadi diboan si Musa ma jolmana dohot angka anakna, jala dipahundul nasida tu halode, asa mulak tu tano Misir. Ditiop si Musa huhut tungkot ni Debata di bagasan tanganna.'),(9644,43,4,21,'Dung i ninna Jahowa muse tu si Musa: Ingot ma di hamumulakmu tu tano Misir patupahon luhut halongangan, di jolo ni raja Firaun, angka na hupasahat tu tanganmu. Alai anggo Ahu patangkangonku ma rohana asa unang diloas bangso i laho'),(9645,43,4,22,'Dung i dohononmu ma tu raja Firaun: Songon on do hata ni Jahowa: Anakku sihahaan do Israel.'),(9646,43,4,23,'Alai hatangku tu ho: Loas ma anakku laho, asa dioloi Ahu. Molo tung manjua ho mangaloas ibana, ida ma, sai bunuonku do anak buhabajum.'),(9647,43,4,24,'Hape di tongan dalan dung sahat tu parbornginan, ro ma Jahowa manoro ibana, naeng mambunu.'),(9648,43,4,25,'Jadi dibuat si Zippora ma sada batu na tajom, jala disunat sibursibur ni anakna, jala direongkon tu lambung patna, jala ninna ma: Pangoli mudar do ho di ahu.'),(9649,43,4,26,'Jadi dipalua Jahowa ibana. Alai didok pe: Pangoli mudar, ala parsunaton i do.'),(9650,43,4,27,'Dung i ninna Jahowa ma tu si Aron: Laho ma ho manjumpangkon si Musa tu halongonan; jadi laho ma ibana, gabe pajumpang nasida di lambung dolok ni Debata, jala diumma ibana.'),(9651,43,4,28,'Jadi dipaboa si Musa ma tu si Aron sude hata ni Jahowa, na marsuru ibana dohot sude tanda, angka na tinonahonna tu ibana.'),(9652,43,4,29,'Jadi laho ma si Musa dohot si Aron, jala dipaluhut nasida ma sude angka sintua ni halak Israel.'),(9653,43,4,30,'Dung i dihatahon si Aron ma sude hata, angka na pinaboa ni Jahowa tu si Musa, jala dipatupa ma angka tanda di jolo ni bangso i.'),(9654,43,4,31,'Jadi porsea ma bangso i; jala dung dibege nasida panatap ni Jahowa di halak Israel, na tinatapna i pardangolannasida, mangunduk be ma nasida jala marsomba.'),(9655,43,5,1,'Dung i ditopot si Musa dohot si Aron ma raja Firaun didok ma: Boti tona ni Jahowa, Debata ni Israel: Loas ma laho bangsongki, asa marpesta nasida di Ahu di halongonan.'),(9656,43,5,2,'Dung i ninna raja Firaun ma: Ise do Jahowa, tung ingkon tangihononku soarana mangaloas laho Israel? Ndang hupabotoboto Jahowa, Israel pe ndang huloas laho.'),(9657,43,5,3,'Dung i ninna nasida ma: Na pajumpang do hami dohot Debata ni halak Heber, ua loas ma hami laho partoluarian tu halongonan, asa mamele hami tu Jahowa, Debatanami, asa unang dihonahon tu hami begu antuk manang podang.'),(9658,43,5,4,'Dung i ninna raja ni Misir i ma tu nasida: Ia ho ale Musa dohot Aron, boasa tung lolaanmuna bangso i sian ulaonnasida? Laho ma hamu tumopot rodimuna.'),(9659,43,5,5,'Angkup ni didok raja Firaun ma: Ida ma madeakku gariada bangso i di tano on, lam pasadionmuna nasida sian rodinasida.'),(9660,43,5,6,'Ala ni dilehon raja Firaun ma di na sadari i parenta tu angka mandur dohot tu angka juaratulis di tongatonga ni bangso i, ninna ma:'),(9661,43,5,7,'Ndang jadi be lehononmuna durame tu bangso i mambahen tano takko, songon siap ari; nasida sandiri ma laho papungupungu durame.'),(9662,43,5,8,'Alai anggo bilangan ni tano takko, angka na binahennasida songon siap ari, tong do pangidoonmuna sian nasida, unang orui hamu sian i, ai angka sigurbak ulu do nasida, umbahen mangangguhi nasida mandok: Laho ma hami mamele tu Debatanami.'),(9663,43,5,9,'Ingkon parengeon ulaon tu nasida, asa adong siulaonnasida, asa unang dipatangitangi hatahata gapgap.'),(9664,43,5,10,'Jadi ruar ma angka mandur ni bangso i dohot angka juaratulisna, angka didok nasida ma tu natorop i: Songon on do parenta ni raja Firaun: Ndang jadi be lehonon durame tu hamu.'),(9665,43,5,11,'Hamu ma laho mangalului durame di hamu, manang didia jumpang hamu; alai ndang jadi moru anggo sian ulaonmuna.'),(9666,43,5,12,'Jadi mampar ma na torop i tu sandok tano Misir papungupungu panabian singkat ni durame.'),(9667,43,5,13,'Jadi sai disosaksosak angka mandur i nasida, angka ninna ma: Pasun hamu ulaonmuna songon siap ari, songon uju na adong durame.'),(9668,43,5,14,'Jadi hona balbal ma angka sintua ni halak Israel, angka na pinabangkit ni mandur ni raja Firaun tu nasida, angka ninna ma: Boasa so dipasidung hamu bilanganmuna tano takko songon siap ari, nang nantuari nang sadari on?'),(9669,43,5,15,'Jadi ditopot angka sintua ni halak Israel ma raja Firaun, mangangguhi ma nasida ninna: Boasa ma tung songon i bahenonmu tu angka naposom?'),(9670,43,5,16,'Ndang be maruli durame angka naposom, sai bahen hamu tano takko, angka ninna mandok hami! Jala ida ma, hona linsing angka naposom, gabe dapotan dosa bangsom.'),(9671,43,5,17,'Alai ninna raja i ma: Angka sigurbak ulu do hamu tongon, umbahen sai didok hamu: Loas ma hami laho mamele tu Jahowa.'),(9672,43,5,18,'On pe, laho ma hamu, mangula ma hamu, ianggo durame ndang be lehonon tu hamu, alai sai lehon hamu bilangan ni tano takko i.'),(9673,43,5,19,'Jadi diida angka sintua ni halak Israel ma sorimagonasida i, ala didok: Ndang jadi oruanmuna sian tano takko pinaulimuna songon siap ari.'),(9674,43,5,20,'Dung i di na ruar i nasida sian jolo ni raja Firaun, didapot nasida ma si Musa dohot si Aron, angka na jongjong mandapothon nasida.'),(9675,43,5,21,'Angka didok ma tu nasida: Sai Jahowa ma marlulu dompak hamu dohot manguhum, ala binahenmuna hami hasomoran ni raja Firaun dohot angka naposona i mangalehon podang tu tangannasida mambunu hami.'),(9676,43,5,22,'Jadi mulak ma si Musa tumopot Jahowa, jala didok ma: Ale Tuhan, boasa ma tung paporsuhonmu bangso on? Boasa ma tung suruhononmu ahu tuson?'),(9677,43,5,23,'Ai olat ni dung bongot ahu tumopot raja Firaun mangkatai marhitehite goarmi, ganda do jahatna tu bangso on, jala ndang dipalua Ho bangsom.'),(9678,43,6,1,'Dung i ninna Jahowa ma tu si Musa: Idaonmu ma annon sibahenonku tu raja Firaun, ai marhitehite tangan na gogo loasonna ma hamu laho, jala marhitehite tangan na gogo onjaronna hamu sian tanona.'),(9679,43,6,2,'Angkup ni didok Debata ma tu si Musa: Ahu do Jahowa, ninna.'),(9680,43,6,3,'Jala hupapatar Ahu tu si Abraham, tu si Isak, dohot tu si Jakkob, margoar Debata na sun hinagogo, alai ndang dipabotoboto nasida ianggo goarhu Jahowa.'),(9681,43,6,4,'Hupahot do huhut padanhu tu nasida, mangalehon tu nasida tano Kanaan, tano paisolatannasida.'),(9682,43,6,5,'Jala nunga hutangihon angguhangguk ni halak Israel, angka na pinarhatoban ni halak Misir, jala marningot Ahu di padanhu.'),(9683,43,6,6,'Dibahen i paboa ma tu halak Israel: Ahu do Jahowa, jala paruaronku do hamu sian angka ulaon rodi ni halak Misir, jala paluaonku do hamu sian parhatobanonnasida, jala tobusonku do hamu marhitehite tangan na toltol dohot marhitehite angka uhum godang.'),(9684,43,6,7,'Jala ainonku do hamu gabe bangsongku, Ahu gabe Debatamuna; asa diboto hamu, Ahu do Jahowa, Debatamuna, na paruar hamu sian ulaon rodi ni Misir.'),(9685,43,6,8,'Asa hutogihon hamu tu tano naung humangmangkon lehononku tu si Abraham, si Isak dohot tu si Jakkob. Lehononku ma i bahen partalianmuna, Ahu do Jahowa.'),(9686,43,6,9,'Dung i dipaboa si Musa ma songon i tu halak Israel. Alai ndang ditangihon nasida si Musa dibahen hagogotan ni tondi dohot dibahen ulaon na renge i.'),(9687,43,6,10,'Jadi didok Jahowa ma tu si Musa, ninna ma:'),(9688,43,6,11,'Topot ma, dok ma tu Firaun, raja ni Misir, asa diloas halak Israel ruar sian tanona.'),(9689,43,6,12,'Alai ninna si Musa ma di jolo ni Jahowa: Ida ma, gari halak Israel ndang ditangihon ahu, didia ma tagamon tangihonon ni raja Firaun ahu? Jala pardila na hallang do ahu.'),(9690,43,6,13,'Jadi didok Jahowa ma tu si Musa dohot tu si Aron jala dilehon parenta dohononnasida tu halak Israel dohot tu Firaun, raja ni Misir, asa diparuar halak Israel sian tano Misir.'),(9691,43,6,14,'On ma angka horongna marompuompu. Angka anak ni si Ruben, buhabaju ni si Israel: si Hanok dohot si Pallu, Hezron dohot Karmi; i ma angka ompu Sian si Ruben.'),(9692,43,6,15,'Ia angka anak ni si Simeon: Jemuel, Jamin, Ohad, Jakin, Zohar dohot Saul, anak ni boru Kanaan; i ma angka ompu sian si Simeon.'),(9693,43,6,16,'Ia angka goar ni anak ni si Lepi mangihuthon ompunasida be: si Gerson, Kohat dohot Merari; ia umur ni si Lepi saratus tolupulu pitu taon lelengna.'),(9694,43,6,17,'Ia angka anak ni si Gerson: Libui dohot Simei mangihuthon ompunasida be.'),(9695,43,6,18,'Ia angka anak ni si Kohat: Amram, Isebar, Hebron dohot Usiel; ia umur ni si Kohat saratus tolupulu tolu taon lelengna.'),(9696,43,6,19,'Ia angka anak ni si Merari: Mahli dohot Musi. I ma angka ompu ni si Lepi mangihuthon partubuna.'),(9697,43,6,20,'Alai dibuat si Amram ma si Jokhebed, namboruna i bahen jolmana, gabe ditubuhon ma di ibana si Aron dohot si Musa. Ia umur ni si Amram saratus tolupulu pitu taon lelengna.'),(9698,43,6,21,'Ia angka anak ni si Isebar: Kora, Nepeg dohot Sikri.'),(9699,43,6,22,'Ia angka anak ni si Usiel: Misael, Elzapan dohot Sitri.'),(9700,43,6,23,'Alai dibuat si Aron ma si Eliseba, boru ni si Aminadab, iboto ni si Nahason bahen jolmana; jala ditubuhon di ibana si Nadab dohot si Abihu, si Eleasar dohot si Itamar.'),(9701,43,6,24,'Ia angka anak ni si Kora: Asir, Elkana dohot Abiasap; i ma angka ompu ni halak Kora.'),(9702,43,6,25,'Alai anggo si Eleasar, anak ni si Aron, dibuat do sada sian angka boru ni si Putiel bahen jolmana, jala ditubuhon di ibana si Pinehas; i ma angka horong ni halak Lepi marompuompu mangihuthon marganasida be.'),(9703,43,6,26,'I ma si Aron dohot si Musa, na pinangkulingan ni Jahowa mandok tu nasida: Togihon hamu ma halak Israel ruar sian tano Misir mangihuthon punguan nasida be.'),(9704,43,6,27,'Nasida ma angka na mandok tu Firaun, raja sian Misir, asa ditogihon nasida halak Israel ruar sian Misir, i ma si Musa dohot si Aron.'),(9705,43,6,28,'Jadi di na sadari i dipangkulingi Jahowa si Musa di tano Misir.'),(9706,43,6,29,'Didok Jahowa ma tu si Musa: Ahu do Jahowa, patolhas ma tu Firaun, raja ni Misir, sude hata na hutonahon tu ho.'),(9707,43,6,30,'Alai ninna si Musa ma di jolo ni Jahowa: Ida ma, pardila na hallang do ahu, didia ma tagamon tangihonon ni Firaun i ahu.'),(9708,43,7,1,'Alai ninna Jahowa ma tu si Musa: Ida ma, nunga hupabangkit ho gabe Debata ni raja Firaun, ia si Aron, hahami gabe panurirangmu.'),(9709,43,7,2,'Hatahononmu ma sude hata na hutonahon tu ho, jala si Aron hahami paboahon tu raja Firaun, asa diloas halak Israel ruar sian tanona.'),(9710,43,7,3,'Alai anggo Ahu, patangkangonku ma roha ni raja Firaun, asa hupatorop angka tanda dohot halongangan di tano Misir.'),(9711,43,7,4,'Jala ndang tangihonon ni raja Firaun hamu, asa huampehon tanganku tu Misir, jala huparuar angka punguanku, i ma bangsongku, halak Israel i sian tano Misir marhitehite uhum angka na balga.'),(9712,43,7,5,'Asa tung diboto halak Misir, Ahu do Jahowa, molo hupatoltol tanganku tu atas Misir, jala huparuar halak Israel sian tongatonganasida.'),(9713,43,7,6,'Jadi dibahen si Musa dohot si Aron ma, songon na tinonahon ni Jahowa tu nasida, songon i dibahen nasida.'),(9714,43,7,7,'Alai ualupulu taon ma umur ni si Musa, ia umur ni si Aron ualupulu tolu taon, di na mangkatai nasida dohot raja Firaun.'),(9715,43,7,8,'Dung i ninna Jahowa ma tu si Musa dohot tu si Aron songon on:'),(9716,43,7,9,'Molo didok raja Firaun mandok hamu: Patupa hamu sada halongangan, jadi dohononmu ma tu si Aron: Buat ma tungkotmi, jala timpalhon tu jolo ni raja Firaun, gabe ulok ma i.'),(9717,43,7,10,'Dung i ditopot si Musa dohot si Aron ma raja Firaun, jala dibahen nasida ma songon na tinonahon ni Jahowa, ditimpalhon si Aron ma tungkotna i tu jolo ni raja Firaun dohot tu jolo ni angka naposona, gabe ulok ma i.'),(9718,43,7,11,'Dung i ditonahon raja Firaun ma ro dohot angka partondung dohot angka parsibalik mata. Jadi dohot ma nasida, angka parsibalik mata ni Misir mambahen songon i marhitehite tabasnasida.'),(9719,43,7,12,'Dipantimpalhon be ma tungkotna, gabe ulok ma angka i; alai dibondut tungkot ni si Aron ma tungkotnasida i.'),(9720,43,7,13,'Jadi tangkang ma roha ni raja Firaun, jala ndang ditangihon nasida, songon na nidok ni Jahowa hian.'),(9721,43,7,14,'Dung i ninna Jahowa ma tu si Musa: Na tangkang do roha ni raja Firaun, dijua do mangaloas laho bangso i.'),(9722,43,7,15,'Laho ma ho sogot manogot i tumopot raja Firaun; ida ma na ruar do ibana tu aek, dapothon ma ibana tu pasir ni batang aek i, jala tiop ma di bagasan tanganmu tungkot na gabe ulok i.'),(9723,43,7,16,'Jala dok ma tu ibana: Jahowa, Debata ni halak Heber marsuru ahu tumopot ho mandok tu ho: Loas ma bangsongku laho, asa disomba Ahu di halongonan; alai ida ma, ndang ditangihon ho rasirasa nuaeng.'),(9724,43,7,17,'Boti didok Jahowa: Botoonmu ma sian tanda on, paboa Ahu do Jahowa: Ida ma, lipathononku ma tungkot na di bagasan tanganku tu aek na di bagasan batang aek i, jadi muba ma i gabe mudar.'),(9725,43,7,18,'Jadi mate ma dengke na di bagasan batang aek i, jala bau batang aek i, jala gialan halak Misir manginum aek sian batang aek i.'),(9726,43,7,19,'Dung i ninna Jahowa ma tu si Musa: Dok ma tu si Aron: Buat ma tungkotmi, jala patoltol tanganmi tu atas aek angka na di tano Misir, tu atas ni angka batang aek di nasida, tu atas ni angka halian aek, tu atas ni angka ambar dohot tu atas ni saluhut angka parpunguan aek, asa gabe mudar saluhut, gabe mudar di sandok tano Misir, nang di bagasan poting, nang di bagasan panguhatan.'),(9727,43,7,20,'Jadi dibahen si Musa dohot si Aron ma, songon na tinonahon ni Jahowa, dihindat ma tungkot i tu ginjang, jala dilipathon tu aek, na di bagasan batang aek i di jolo ni raja Firaun dohot di jolo ni angka naposona, jadi muba ma sude aek na di bagasan batang aek i, gabe mudar.'),(9728,43,7,21,'Jadi rage ma dengke, angka na di bagasan batang aek i jala bau batang aek i, jala ndang tarinum halak Misir aek na sian batang aek i, ai gabe mudar do i di sandok tano Misir.'),(9729,43,7,22,'Alai angka parsibalik mata ni Misir pe dibahen do songon i marhitehite tabasnasida, jadi laos tangkang ma roha ni raja Firaun, jala ndang ditangihon nasida, songon na nidok ni Jahowa hian.'),(9730,43,7,23,'Dung i humusor ma raja Firaun laho muli tu jabuna, laos so mangilala rohana, nang disi.'),(9731,43,7,24,'Alai saluhut halak Misir mangkali aek siinumon humaliang batang aek i, ai ndang tarinum nasida aek na sian batang i.'),(9732,43,7,25,'Pola gok pitu borngin lelengna, dung dilipati Jahowa badan i.'),(9733,43,8,1,'Dung i ninna Jahowa ma tu si Musa: Laho ma ho tumopot raja Firaun, jala dok ma tu ibana: Boti tona ni Jahowa: Loas ma laho bangsongki, asa disomba nasida Ahu.'),(9734,43,8,2,'Alai molo dijua ho mangaloas laho, ida ma, toruanku ma sandok gomgomanmu marhitehite angka tohuk.'),(9735,43,8,3,'Asa gumamang batang aek i dibahen tohuk, angka na manjangkiti jala bongot tu jabum dohot tu bilut podomanmu ro di hasurmu dohot tu bagas ni angka naposom dohot tu bangsom dohot tu tataringmi dohot tu pangananmu.'),(9736,43,8,4,'Jangkitan ni angka tohuk ma ho dohot bangsom ro di angka naposom.'),(9737,43,8,5,'Dung i ninna Jahowa ma tu si Musa: Dok ma tu si Aron: Patoltol ma tanganmu dohot tungkotmu tu ginjang ni angka batang aek, angka halian aek dohot tu ginjang ni angka ambar panangkokkon angka tohuk tu tano Misir.'),(9738,43,8,6,'Jadi dipatoltol si Aron ma tanganna tu ginjang ni aek angka na di Misir, gabe nangkok ma angka tohuk jala ditutup tano Misir.'),(9739,43,8,7,'Dung i dibahen angka parsibalik mata ma huhut songon i marhitehite tabasnasida, gabe dipanangkok ma angka tohuk di tano Misir.'),(9740,43,8,8,'Dung i dijou raja Firaun ma si Musa dohot si Aron, didok ma: Martangiang ma hamu tu Jahowa, asa dipadao angka tohuk i sian ahu dohot sian bangsongku, huloas bangso i, asa mamele nasida tu Jahowa.'),(9741,43,8,9,'Dung i ninna si Musa ma tu raja Firaun: Guru dokmu ahu, andigan ma ondihononku ho dohot angka naposom dohot bangsom asa diragehon angka tohuk i sian ho dohot sian angka jabum, holan di bagasan batang aek i tading.'),(9742,43,8,10,'Jadi ninna raja i ma: Antong marsogot ma. Songon na nidokmi ma ninna si Musa, asa tung ditanda ho, ndang adong manang ise songon Jahowa Debatanami.'),(9743,43,8,11,'Ingkon munsat angka tohuk i sian ho dohot sian angka bagasmu dohot sian angka naposom dohot sian bangsom; holan di bagasan batang aek i tading.'),(9744,43,8,12,'Jadi ruar ma si Musa dohot si Aron sian jolo ni raja Firaun, dung i mangangguhi ma si Musa tu Jahowa ala hata tohuk, angka na pinarona tu raja Firaun.'),(9745,43,8,13,'Jadi dibahen Jahowa ma songon na nidok ni si Musa, marragean ma angka tohuk i sian angka bagas, sian angka alaman dohot sian angka ladang.'),(9746,43,8,14,'Jadi dipagugun nasida ma angka bangke i margugungugun, gabe bau ma tano i.'),(9747,43,8,15,'Alai dung diida raja Firaun nunga lumumbang, gabe dipatangkang ma rohana, jala ndang ditangihon hatanasida, songon na nidok ni Jahowa hian.'),(9748,43,8,16,'Dung i ninna Jahowa ma tu si Musa: Dok ma tu si Aron: Patoltol ma tungkotmi, jala lipathon tu orbuk ni tano i, asa gabe rongit ranggas ma i di sandok tano Misir.'),(9749,43,8,17,'Jadi dibahen nasida ma songon i, dipatoltol si Aron ma tanganna tumiop tungkotna, jala dilipathon tu orbuk ni tano i, gabe rongit ranggas ma i di jolma dohot di pinahan; nasa orbuk ni tano i manjadi rongit ranggas di sandok tano Misir.'),(9750,43,8,18,'Angka parsibalik mata pe dibahen ma songon i marhitehite tabasnasida, parohon angka rongit ranggas nian, hape ndang tarjadisa. Jadi disonggopi angka rongit ranggas i ma jolma dohot pinahan.'),(9751,43,8,19,'Jadi ninna angka parsibalik mata ma tu raja Firaun: Jadijadian ni Debata do i. Alai laos tangkang do anggo roha ni raja Firaun, jala ndang ditangihon hatanasida, songon na nidok ni Jahowa hian.'),(9752,43,8,20,'Dung i ninna Jahowa ma tu si Musa: Pasogot ma manogot i hehe mandapothon tu jolo ni raja Firaun; ida ma, ruar do ibana laho tu aek, dok ma tu ibana: Songon on do tona ni Jahowa: Loas ma bangsongku laho, asa disomba nasida Ahu.'),(9753,43,8,21,'Ai molo so loasonmu bangsongku laho, ida ma paroonku ma dompak ho dohot dompak angka naposom dohot dompak bangsom dohot dompak angka bagasmu angka gulomitgulomit, asa gok gulomitgulomit angka bagas ni halak Misir dohot tano inganannasida i.'),(9754,43,8,22,'Marhasurungan ma bahenonku di ari na sasada i anggo tano Gosen inganan ni bangsongki, na so tupa adong disi gulomitgulomit, asa ditanda ho, paboa Ahu, Jahowa di tongatonga ni tano i.'),(9755,43,8,23,'Patindangonku ma haluaon di holangkolang ni bangsongku dohot bangsomu; marsogot ma masa tanda i.'),(9756,43,8,24,'Jadi dibahen Jahowa ma songon i; ro ma marhulalan angka gulomitgulomit tu bagas ni raja Firaun dohot tu bagas ni angka naposona, ro di sandok tona Misir; tung siap do tano i dibahen angka gulomitgulomit i.'),(9757,43,8,25,'Jadi dijou raja Firaun ma si Musa dohot si Aron, jala didok ma: Laho ma hamu mamele tu Debatamuna di tano on.'),(9758,43,8,26,'Alai ninna si Musa ma: Ndang jadi songon i bahenon, ai pantang ni halak Misir pelehononnami tu Jahowa Debatanami; ida ma, ia hupelehon hami pantang ni halak Misir di jolonasida, nda dangguran nasida ma hami dohot batu?'),(9759,43,8,27,'Loas ma hami mardalan tu halongonan partoluarian mamele tu Jahowa, Debatanami, songon na nidokna i tu hami.'),(9760,43,8,28,'Jadi ninna raja Firaun ma: Antong huloas pe hamu laho, asa mamele hamu tu Jahowa, Debatamuna, di halongonan, asal unang madaohu hamu laho, jala tangiangkon hamu ahu.'),(9761,43,8,29,'Dung i ninna si Musa ma: Ida ma, ruar ma ahu sian lambungmon, jala martangiang pe ahu tu Jahowa, asa dipadao marsogot angka gulomitgulomit i sian Firaun, sian angka naposona dohot sian bangsona, asal unang be dipaotooto Firaun hami, so loasonna bangso i laho mamele tu Jahowa.'),(9762,43,8,30,'Jadi ruar ma si Musa sian lambung ni Firaun, jala martangiang ibana tu Jahowa.'),(9763,43,8,31,'Jadi dibahen Jahowa ma songon na nidok ni si Musa, dipadao ma gulomitgulomit i sian Firaun, sian angka naposona dohot sian bangsona, ndang tading nanggo sada.'),(9764,43,8,32,'Hape dipatangkang raja Firaun do rohana, nang sahali on, jala ndang diloas laho bangso i.'),(9765,43,9,1,'Dung i ninna Jahowa ma tu si Musa: Laho ma ho tumopot Firaun jala dok ma tu ibana: Songon on do tona ni Jahowa, Debata ni halak Heber: Loas ma bangsongku laho, asa disomba nasida Ahu.'),(9766,43,9,2,'Ai molo manjua ho mangaloas nasida laho, sai gogo bahenonmu maniopi nasida.'),(9767,43,9,3,'Ida ma, mangonai ma tangan ni Jahowa tu pinahanmu na di ladang tu angka hoda, halode, unto, lombu dohot tu angka birubiru, hona bondil na rahat situtu.'),(9768,43,9,4,'Jala marhasurungan pamelok ni Jahowa di pinahan ni halak Israel sian pinahan ni halak Misir, ndang adong mate sian sandok pinahan ni halak Israel, nanggo sada.'),(9769,43,9,5,'Dung i dibuhul Jahowa ma tingkina, marsogot ma, ninna bahenon ni Jahowa na nidokna ondeng tu tano i.'),(9770,43,9,6,'Jadi marsogotna i dibahen Jahowa ma songon i: Marragean ma sandok pinahan ni halak Misir, alai sian pinahan ni halak Israel, ndang adong mate nanggo sada.'),(9771,43,9,7,'Jadi disuru Firaun ma maningkir, jala ida ma, ndang adong mate sian pinahan ni Israel, nanggo sada. Alai laos tangkang anggo roha ni Firaun, jala ndang diloas laho bangso i.'),(9772,43,9,8,'Dung i ninna Jahowa ma tu si Musa dohot tu si Aron: Gomaki hamu ma sirabun sian tataring, gok tanganmuna, jala disampakkon si Musa ma i dompak langit di jolo ni Firaun.'),(9773,43,9,9,'Asa gabe orbuk do i tu sandok tano Misir, jala songgop tu jolma dohot tu pinahan, gabe baro na mapultak, gabe ngenge na birong di sandok tano Misir.'),(9774,43,9,10,'Jadi digomahi nasida ma sirabun sian tataring, jala didapothon tu jolo ni Firaun, gabe disampakkon si Musa ma i dompak langit, manjadi baro ma i, ngenge na birong angka na mapultak di jolma dohot di pinahan.'),(9775,43,9,11,'Jadi ndang tarbahen angka parsibalik mata jongjong di jolo ni si Musa, dibahen angka baro i, ai baro on do nang angka parsibalik mata dohot sude halak Misir.'),(9776,43,9,12,'Alai dipatangkang Jahowa do anggo roha ni Firaun; jadi ndang ditangihon hatanasida, songon na nidok ni Jahowa hian tu si Musa.'),(9777,43,9,13,'Dung i ninna Jahowa ma tu si Musa: Pasogot ma manogot mandapothon tu jolo ni Firaun, jala dok ma tu ibana: Songon on do tona ni Jahowa, Debata ni halak Heber: Loas ma bangsongku laho, asa disomba nasida Ahu.'),(9778,43,9,14,'Ai anggo sahali on tongosonku ma saluhut torutorungku tu roham dohot tu angka naposom dohot tu bangsom, asa tung ditanda ho, na so adong songon Ahu on di sandok tano on.'),(9779,43,9,15,'Ai patoltolonku ma nuaeng tanganku jala honahononku ma tu ho dohot tu bangsom begu antuk, asa tung siap ho sian tano on.'),(9780,43,9,16,'Ala ni do antong umbahen na hupatindang ho, asa hupataridahon tu ho gogongku, jala asa tung tarbarita Goarhu di liat portibi on.'),(9781,43,9,17,'Sai datdatanmu dope mangalo bangsongki, ndang loasonmu nasida laho?'),(9782,43,9,18,'Ida ma, marsogot songon on paroonku ma udan ambolas na por situtu, na so dung masa songon i di tano Misir, olat ni ari haoojakna rasirasa nuaeng.'),(9783,43,9,19,'On pe suru ma papunguhon pinahanmu ro di sude ugasanmu na di ladang; nasa jolma dohot pinahan na jumpang di ladang, so pinapungu tu jabu, ia dapot ditinggang ambolas i nasida, rage ma i.'),(9784,43,9,20,'Manang ise sian angka naposo ni Firaun na mabiar mida hata ni Jahowa diluahon ma angka naposona dohot pinahanna tu jabu.'),(9785,43,9,21,'Alai manang ise na so marateatehon hata ni Jahowa, dipasombu ma angka naposona dohot pinahanna di ladang.'),(9786,43,9,22,'Dung i ninna Jahowa ma tu si Musa: Patoltol ma tanganmu dompak langit, asa ro udan ambolas tu sandok tano Misir, hona jolma dohot pinahan ro di nasa suansuanan di tano Misir.'),(9787,43,9,23,'Jadi dipatoltol si Musa ma tungkotna dompak langit, gabe diparo Jahowa ma ronggur dohot udan ambolas, sumiksaksiksak huhut hilap tu tano on. Jadi diparo Jahowa ma udan ambolas tu tano Misir.'),(9788,43,9,24,'Alai ro udan ambolas mardongan api na marhimpalhimpal di tonga ni ambolas i, na por situtu, ndang dung songon i masa di sandok tano Misir, olat ni dung gabe bangso Misir i.'),(9789,43,9,25,'Jadi ditinggang udan ambolas i ma sandok na di ladang di sandok tano Misir, sian jolma ro di pinahan ro di nasa suansuanan di ladang ditinggang udan ambolas i jala digashon ma sude hau di ladang.'),(9790,43,9,26,'Holan di tano Gosen, na niinganan ni halak Israel, ndang masa udan ambolas.'),(9791,43,9,27,'Dung i disuru Firaun ma manjou si Musa dohot si Aron, jala didok tu nasida: Nunga mardosa ahu anggo sahali on; na tigor do Jahowa, alai anggo ahu dohot bangsongki halak parjahat do.'),(9792,43,9,28,'Martangiang ma hamu tu Jahowa, nunga sae masa angka soara ronggur ni Debata dohot udan ambolas i, huloas pe hamu laho, asa unang be hamu tinggal dison.'),(9793,43,9,29,'Jadi didok si Musa ma tu ibana: Disi ruar ahu sian huta i, paherbangonku ma tanganku dompak Jahowa; mansohot ma angka soara ronggur, jala ndang be ro udan ambolas, asa tung ditanda ho, gok di Jahowa do tano on.'),(9794,43,9,30,'Alai anggo ho dohot angka naposom, huboto hian do na so mabiar hamu maradophon Debata Jahowa.'),(9795,43,9,31,'Ianggo hori dohot eme dapotsa ditinggang, ai boltok do ia eme, bibi ia hori.'),(9796,43,9,32,'Alai anggo singkoru eme dohot hadio ndang ditinggang, ai umpudi do i.'),(9797,43,9,33,'Dung i ruar ma si Musa sian lambung ni Firaun tu balian huta, jala dipaherbang tanganna dompak Jahowa, jadi mansohot ma angka soara ni ronggur dohot ambolas, udan i pe ndang be use tu tano i.'),(9798,43,9,34,'Alai dung diida Firaun naung mansohot udan dohot ambolas dohot angka soara ni ronggur dipatangkang rohana, ibana nang naposona.'),(9799,43,9,35,'Jadi laos tangkang roha ni Firaun, jala ndang diloas laho halak Israel songon na nidok ni Jahowa hian marhitehite si Musa.'),(9800,43,10,1,'Dung i ninna Jahowa ma tu si Musa: Laho ma ho tumopot Firaun, ai nunga hupatangkang rohana dohot roha ni angka naposona, asa hupatupa angka tandangkon di tongatongana.'),(9801,43,10,2,'Asa dipajojor ho sogot tu pinggol ni anakmu dohot pahompum, na hubahen i di Misir dohot tandangku angka na hupatindang di tongatonganasida, asa tung diboto hamu, Ahu do Jahowa.'),(9802,43,10,3,'Jadi laho ma si Musa dohot si Aron tumopot Firaun, jala didok ma tu ibana: Songon on do tona ni Jahowa, Debata ni halak Heber: Sadia leleng on juaonmu paundukkon roham maradophon Ahu? Loas ma laho bangsongku, asa disomba nasida Ahu.'),(9803,43,10,4,'Alai molo manjua ho mangaloas bangsongku laho, ida ma paroonku ma marsogot angka sihapor tu gomgomanmi.'),(9804,43,10,5,'Asa ditutup sihapor i sisik ni tano i, pola ndang tarida tano i jala panganonna ma ebaeba ni na malua, na tading di hamu sian udan ambolas i, jala gagatonna ma saluhut hau, angka na martumbur di hamu di ladang.'),(9805,43,10,6,'Jala gohan ni sihapor ma bagasmu dohot angka bagas ni saluhut naposom dohot angka bagas ni sandok halak Misir; na so dung songon i diida angka amam dohot angka ompum olat ni ari, dung adong nasida di tano on rasirasa sadari on. Dung i humusor ma si Musa jala ruar sian lambung ni Firaun.'),(9806,43,10,7,'Dung i didok angka naposo ni Firaun ma tu ibana: Sadia leleng on sambil on ni bayo on hita? Loas ma laho halak i, asa disomba nasida Jahowa, Debata nasida i; ndang dope mamoto roham naung mago Misir?'),(9807,43,10,8,'Jadi mulak ma si Musa dohot si Aron diboan tu jolo ni Firaun, gabe didok raja i ma tu nasida: Laho ma hamu, somba hamu ma Jahowa, Debatamuna i! Ise do tahe angka na laho i?'),(9808,43,10,9,'Dung i didok si Musa ma: Saluhut hami naeng laho angka dakdanaknami dohot na tuatuanami, udur dohot anaknami dohot borunami, birubirunami dohot lombunami, ai pesta ni Jahowa masa di hami.'),(9809,43,10,10,'Jadi didok raja i ma tu nasida: Songon pangaloashu di hamu dohot di posoposomuna laho, songon i ma pandongani ni Jahowa di hamu. Ida ma, nda na jat do tahi ni rohamuna?'),(9810,43,10,11,'Ndang songon i ua hamu angka baoa ma laho jala marsomba tu Jahowa, ai i do na pinangidomuna hian. Jadi dipabali ma nasida sian adopan ni Firaun.'),(9811,43,10,12,'Dung i ninna Jahowa ma tu si Musa: Patoltol ma tanganmu dompak tano Misir ala angka sihapor, asa manaek nasida tu tano Misir, jala digagat nasa suansuanan ni tano i, nasa na tinebahon ni udan ambolas i.'),(9812,43,10,13,'Jadi dipatoltol si Musa ma tungkotna tu ginjang ni tano Misir, gabe diparo Jahowa ma alogo sian purba tu tano i manipat sadari saborngin lelengna; tole manogotna i diboan alogo sian purba ma angka sihapor.'),(9813,43,10,14,'Jadi manaek ma angka sihapor tu sandok tano Misir jala songgop tu sandok gomgoman Misir mansai torop, ndang dung songon i tudosanna sihapor na sai laon, nang sogot.'),(9814,43,10,15,'Ai tung ditutup do sisik ni sandok tano i, pola holom tano i dibahen, gabe digagat ma sude suansuanan ni tano i ro di sude parbue ni hau, angka na tinebahon ni ambolas i, jadi ndang adong tading agia aha na rata di hau dohot di suansuanan ni ladang di sandok tano Misir.'),(9815,43,10,16,'Jadi humalaput ma Firaun manjou si Musa dohot si Aron, jala didok ma: Nunga mardosa ahu dompak Jahowa, Debatamuna i dohot dompak hamu.'),(9816,43,10,17,'On pe, sai marpamuati ma roham di dosangki sahali on dope, jala martangiang ma hamu tu Jahowa, Debatamuna, asa dipadao sian ahu holan hamatean on na ma.'),(9817,43,10,18,'Jadi ruar ma si Musa sian lambung ni Firaun, jala martangiang tu Jahowa.'),(9818,43,10,19,'Jadi, dipasuhar Jahowa ma alogo sian hasundutan mansai pur, gabe dilualua ma angka sihapor jala direongkon tu laut arung, ndang adong tading sihapor nanggo sada di sandok gomgoman Misir.'),(9819,43,10,20,'Alai dipatangkang Jahowa do roha ni Firaun, jala ndang diloas laho halak Israel i.'),(9820,43,10,21,'Dung i didok Jahowa ma tu si Musa: Patoltol ma tanganmu dompak langit asa masa na holom marimpotimpot tu tano Misir, pola dadapdadap jolma dibahen na holom.'),(9821,43,10,22,'Jadi dipatoltol si Musa ma tanganna dompak langit, gabe masa ma na holom marimpotimpot di sandok tano Misir tolu borngin lelengna.'),(9822,43,10,23,'Ndang diida manang ise donganna, jala ndang hehe manang ise sian ingananna, na tolu borngin i. Alai anggo di sandok halak Israel torang do di inganannasida.'),(9823,43,10,24,'Jadi dijou Firaun ma si Musa, jala didok ma: Laho ma hamu, marsomba ma hamu tu Jahowa, holan birubirumuna dohot lombumuna tading di bara, alai anggo anakkonmuna itenang mardalan rap dohot hamu.'),(9824,43,10,25,'Alai ninna si Musa ma: Ingkon lehononmu do tu tangannami dohot angka pelean sipotongon dohot pelean situtungon, sipelehononnami tu Jahowa, Debatanami.'),(9825,43,10,26,'Asa ingkon dohot do pinahannami mardalan rap dohot hami, ndang tading nanggo sada tambirik, ai sian i do buatonnami laho marsomba tu Jahowa, Debatanami; jala ndang huboto hami manang aha sisombahononnami tu Jahowa, paima sahat hami ro disi.'),(9826,43,10,27,'Alai dipatangkang Jahowa do roha ni Firaun, gabe ndang olo ibana mangaloas nasida laho.'),(9827,43,10,28,'Dung i ninna Firaun i ma tu si Musa: Laho ma ho sian lambungkon, jaga ho, unang be huida ho, ai sadari idaonmu bohingkon, ingkon mate ho bunuon.'),(9828,43,10,29,'Jadi didok si Musa ma: Songon na nidokmi ma, ndang be idaonku bohim.'),(9829,43,11,1,'Dung i didok Jahowa ma tu si Musa: Sada nari dope uhum i paroonku tu Firaun dohot tu Misir, dung i loasonna ma hamu laho sian i; ndada asal loasonna sudena, lam onjaronjaronna do hamu sian i.'),(9830,43,11,2,'Antong dok ma tu pinggol ni bangso i, asa dipangido be baoa dohot boruboru sian alealena be angka ulaula perak dohot ulaula sere.'),(9831,43,11,3,'Nunga disosoi Jahowa hian halak Misir marasi ni roha di bangso i. Si Musa pe halak na sangap situtu do di tano Misir, diida angka naposo ni Firaun diida na torop i.'),(9832,43,11,4,'Dung i ninna si Musa ma: Songon on do hata ni Jahowa: Marhira tonga borngin ruar ma Ahu umbolus tongatonga ni Misir.'),(9833,43,11,5,'Jadi mate ma nasa buhabaju di tano Misir, sian buhabaju ni Firaun, na hundul di habangsana hian, ro di buhabaju ni hatoban boruboru na di pudi ni pandudaan dohot sude buhabaju ni pinahan.'),(9834,43,11,6,'Jadi masa ma angguk badar di sandok tano Misir, na so dung masa songon i na sai laon, nang sogot pe.'),(9835,43,11,7,'Alai anggo di sude halak Israel ndang mahua nanggo biangna sada, songon i jolma ro di pinahan, asa tung ditanda hamu pamelok ni Jahowa di halak Israel sian halak Misir.'),(9836,43,11,8,'Dung i tuat ma saluhut angka naposomon tumopot ahu, jala sombaonnasida ma ahu mandok: Ruar ma ho dohot sandok bangso na sahat tu ho; dung salpu songon i ruar ma ahu. Jadi ruar ma si Musa sian lambung ni Firaun piripirion situtu.'),(9837,43,11,9,'Alai ninna Jahowa hian ma tu si Musa; ndang tagamon tangihonon ni Firaun hamu, asa tung torop halongangan angka sibahenonku di tano Misir.'),(9838,43,11,10,'Jadi nunga dibahen si Musa dohot si Aron sude angka halongangan on di jolo ni Firaun. Alai dipatangkang Jahowa do roha ni Firaun, gabe ndang diloas halak Israel laho sian tanona.'),(9839,43,12,1,'Dung i didok Jahowa hian ma tu si Musa dohot tu si Aron di tano Misir songon on.'),(9840,43,12,2,'Bulan on ma parmulaan manjujur angka bulan di hamu, i ma sipahasada di hamu sian angka bulan ni na sataon:'),(9841,43,12,3,'Dok hamu ma tu sandok luhutan ni Israel songon on: Di ari sampulu di bulan on, dibuat be ma sada anak ni birubiru marguru tu torop ni ripe na marsaompu, sada birubiru ganup ripe.'),(9842,43,12,4,'Alai molo maotikku na saripe so sudasa anak ni birubiru sada, dibuat ma i rap dohot hombar bagasna, na jumonok tu jabuna, marguru tu bilangan ni jolma, etongan tu sada birubiru marguru tu na tarpangansa be.'),(9843,43,12,5,'Alai ingkon anak ni birubiru tunggal na so marlindang, marumur sataon. Sian anak ni birubiru dohot sian hambing buatonmuna.'),(9844,43,12,6,'Jala ingkon paradeonmuna do i ro di ari sampuluopat di bulan on. Dung i seaton ni sandok luhutan ni Israel ma i mareak bot ari.'),(9845,43,12,7,'Dung i buatonnasida ma sian mudar i jala daishononna tu na dua tiang parhinsuhinsuan dohot tu tohang ni angka bagas, parmanganonnasida i.'),(9846,43,12,8,'Asa panganonnasida ma juhut na pinanggang tu api di na saborngin i, mardongan sagusagu na so niigaran dohot ingkau paet panganonnasida.'),(9847,43,12,9,'Na so tupa tata panganonmuna i, manang na nilompa di bagasan aek, ingkon panggangon tu api do, uluna ro di haehaena dohot butuhana.'),(9848,43,12,10,'Jala ndang jadi tebahononmuna sian i ro di manogotna i. Tu api do mosokkononhon ebaebana ro di manogotna i.'),(9849,43,12,11,'Songon on do pangalahomuna laho manganhon: Gontingmuna marhohos, patmuna marsipatu, jala tungkotmuna di bagasan tanganmuna, marpanilu ma hamu manganhon; ai Paska ni Jahowa do i.'),(9850,43,12,12,'Ai bolusonku ma di na saborngin i tano Misir, jala bunuonku sandok buhabaju di tano Misir, sian jolma ro di pinahan, jala bahenonku ma angka uhum tu saluhut debata ni Misir, Ahu, Jahowa i.'),(9851,43,12,13,'Jadi mudar i ma gabe partinandaanmuna di bagas angka na niingananmuna i, molo huida mudar i, laosanku ma hamu, asa unang masa tu hamu uhum hamagoan, di na huhonahon tu tano Misir.'),(9852,43,12,14,'Bahen parningotanmuna ma ari sadari on jala pestahononmuna ma i marulaon tu Jahowa, gabe patik salelenglelengna ma i ro di pinomparmuna parulahononmuna.'),(9853,43,12,15,'Panganonmuna ma sagusagu na so niigaran manipat pitu ari lelengna, di ari parjolo daehonmuna igarigar sian bagasmuna ai sai na siaphononhon do sian Israel manang ise na manganhon na niigaran sian ari parjolo ro di ari papituhon.'),(9854,43,12,16,'Pansangon ma di ari parjolo luhutan na badia, di ari papituhon pe marluhutan na badia ma hamu: Ndang jadi bahenon agia aha ulaon disi, nda holan sipanganonna be na jadi hobason di hamu.'),(9855,43,12,17,'Sai ingot hamu ma angka sagusagu na so niigaran, ai di ari na sasada i do hutogihon angka punguanmuna ruar sian tano Misir; asa sai ingot hamu ari na sasada i bahen patik salelenglelengna ro di pinomparmuna.'),(9856,43,12,18,'Di bulan sipahasada, di ari sampulu opat mareak bot ari, panganonmuna ma sagusagu na so niigaran ro di ari duapulu sada i bodarina i.'),(9857,43,12,19,'Manipat na pitu ari lelengna, unang ma jumpang igarigar di bagasmuna. Ai sai siaphononhon do sian luhutan Israel manang ise na manganhon na niigaran, manang halak na buro manang isi ni tano i ibana.'),(9858,43,12,20,'Sandok na so jadi hamu manganhon sagusagu na niigaran; pangan hamu ma di sandok ingananmuna sagusagu na so niigaran.'),(9859,43,12,21,'Dung i dipio si Musa ma saluhut sintua ni Israel jala didok tu nasida: Pillit jala togu hamu ma di hamu angka anak ni birubiru i marguru tu torop ni ripemuna jala seat hamu Paska i.'),(9860,43,12,22,'Dung i buat hamu ma sarahutan sanggul, jala sornophon hamu tu mudar na di parbontaran i, jala daishon hamu tu tohang dohot tu na dua tiang sian mudar na di parbontaran i; alai unang ruar agia ise sian hamu sian pintu ni jabuna paima torang ari.'),(9861,43,12,23,'Ai mamolus ma Jahowa laho mambunu halak Misir. Jadi ia dung diida mudar di tohang dohot di tiang na dua i, gabe laosan ni Jahowa ma pintu i, ndang loasonna pangago i bongot tu jabumuna laho mambunu.'),(9862,43,12,24,'Antong ingot hamu ma hata i, gabe patik di ho dohot di angka anakmu ro di salelenglelengna.'),(9863,43,12,25,'Jala ia dung sahat hamu tu tano silehonon ni Jahowa tu hamu, songon na nidokna i, sai radoti hamu ma ulaon on.'),(9864,43,12,26,'Jala ia didok angka anakmuna tu hamu: Ulaon aha do on di hamu?'),(9865,43,12,27,'Dohononmuna ma: Pelean Paska do i di Jahowa, na nilaosanna i angka bagas ni halak Israel di Misir, di na binununa i halak Misir, alai dipalua anggo bagasta. Jadi dipaunduk bangso i ma nasida jala marsomba.'),(9866,43,12,28,'Dung i laho ma halak Israel jala dibahen nasida ma songon na tinonahon ni Jahowa tu si Musa dohot tu si Aron, songon i ma dibahen nasida.'),(9867,43,12,29,'Jadi marhira tonga borngin, dibunu Jahowa ma nasa buhabaju di tano Misir, sian buhabaju ni Firaun na hundul di habangsana hian, ro di buhabaju ni na tarhurung, na di bagas hurungan dohot nasa buhabaju ni pinahan.'),(9868,43,12,30,'Jadi hehe ma Firaun bornginna i, ibana dohot nasa naposona ro di sude halak Misir, gabe masa ma andung bolon di Misir, ai ndang adong bagas na so adong na mate di bagasan.'),(9869,43,12,31,'Jadi dijou raja i ma si Musa dohot si Aron laos bornginna i, ninna ma: Borhat ma hamu ruar sian tongatonga ni bangsongki, nang hamu, nang halak Israel; laho ma hamu marsomba tu Jahowa, songon na nidokmuna i.'),(9870,43,12,32,'Boan hamu ma dohot birubirumuna dohot lombumuna, songon na nidokmuna i, laho ma hamu, jala pasupasu hamu ma dohot ahu.'),(9871,43,12,33,'Jadi dionjaronjar halak Misir ma bangso i pahatophon ruar nasida sian tano i, ai angka ninna do: Mate hami maon luhutna.'),(9872,43,12,34,'Jadi diusung bangso i ma na niitakna, tagan so diigari, ditompi be balanga panggorengan.'),(9873,43,12,35,'Jadi dibahen halak Israel ma songon na nidok ni si Musa, dipangido hian ma sian halak Misir angka ulaula perak dohot ulaula sere dohot angka ulos.'),(9874,43,12,36,'Alai nunga disosoi Jahowa hian halak Misir marasi ni roha di bangso i gabe dilehon pinangidonasida i hira tabantaban do i diboan nasida sian halak Misir.'),(9875,43,12,37,'Asa borhat ma halak Israel sian Ramses tu Sukot arga onom ratus ribu halak toropna mardalan pat, so padohot angka dakdanak.'),(9876,43,12,38,'Torop ma dohot halak sileban nangkok rap dohot nasida, ro di angka birubiru dohot lombu dohot pinahan mansai godang.'),(9877,43,12,39,'Dung i dilompa nasida ma na niitak, na binoannasida sian Misir bahen sagusagu na so niigaran, ai ndang niigaran hian, ai na nionjaronjar do nasida sian Misir, jadi ndang sanga nasida, sipanganon na asing pe ndang adong dihobas.'),(9878,43,12,40,'Ia leleng ni halak Israel maringan di Misir, opat ratus tolupulu taon do.'),(9879,43,12,41,'Jadi dung salpu ma opat ratus tolupulu taon i, boho ma di ari na sasada i, ruar ma sandok punguan ni Jahowa sian tano Misir.'),(9880,43,12,42,'Parulahononhon ma borngin na sasada on di Jahowa, ala tinogihonna i nasida ruar sian tano Misir; parningotan di Jahowa ma borngin na sasada i parulahonon ni sandok halak Israel ro di pinomparnasida sogot.'),(9881,43,12,43,'Dung i ninna Jahowa ma tu si Musa dohot tu si Aron: On ma patik ni Paska: Na so tupa halak na buro manganhon sian i.'),(9882,43,12,44,'Alai nasa hatoban, na tinuhor ni hepeng, dung disunati hamu, jadi ma panganonna sian i.'),(9883,43,12,45,'Halak paisolat dohot sipangan gaji pe, ndang jadi manganhon sian i.'),(9884,43,12,46,'Di bagasan jabu ma i panganon; ndang jadi boanonmu sian juhut i ruar sian jabu tu alaman, jala ndang jadi ponggolhononhon holiholina nanggo sada.'),(9885,43,12,47,'Ingkon sandok luhutan Israel mambahen songon i.'),(9886,43,12,48,'Alai molo adong halak na buro sorang tu ho, jala naeng radotanna Paska i tu Jahowa, ingkon sunatonna jolo nasa baoa na sorang tu ibana; dung pe i asa jadi jumonok ibana laho mangaradoti, asa sobokkon isi ni tano i hian ibana; alai nasa na so sinunat ndang jadi manganhon sian i.'),(9887,43,12,49,'Sada patik ma di isi ni tano hian dohot di halak na buro, na manginsur di tongatongamuna.'),(9888,43,12,50,'Jadi saluhut halak Israel mambahen songon i, hombar tu na tinonahon ni Jahowa tu si Musa dohot tu si Aron, songon i dibahen nasida.'),(9889,43,12,51,'Asa di ari na sasada i ma ditogihon Jahowa halak Israel ruar sian tano Misir, marguru tu punguannasida.'),(9890,43,13,1,'Dung i didok Jahowa ma tu si Musa, songon on:'),(9891,43,13,2,'Parbadiai di Ahu nasa buhabaju, na parjolo ruar sian bortian di halak Israel, di jolma dohot di pinahan, Ahu do nampunasa.'),(9892,43,13,3,'Jadi didok si Musa ma tu bangso i: Ingot hamu ma ari sadari on, haruruarmuna sian Misir, sian bagas parhatobanan, ai marhitehite tangan na gogo diparuar Jahowa hamu sian i, so tung dipangan hamu na niigaran.'),(9893,43,13,4,'Sadari on do hamu ruar di bulan Abib.'),(9894,43,13,5,'Ia dung sahat hamu ditogihon Jahowa tu tano ni halak Kanaan dohot Het i dohot Amori dohot Hevi dohot Jebusi tano naung minangmangkonna tu angka ompum mangalehon tu hamu tano habaoran ni susu dohot situak ni loba, sai radoti hamu ma ulaon i di bulan on.'),(9895,43,13,6,'Pitu ari ma panganonmuna sagusagu na so niigaran di ari papituhon i ma pesta di Jahowa.'),(9896,43,13,7,'Sagusagu na so niigaran ma panganonmuna na pitu ari i; ndang jadi dapot idaon di hamu igarigar, jala ndang jadi dapot idaon di hamu igarigar di sandok gomgomanmu.'),(9897,43,13,8,'Paboaonmu ma tu anakmu di ari na sasada i songon on: Ala ni na binahen ni Jahowa di ahu, di na ruar i ahu sian Misir.'),(9898,43,13,9,'Asa bahen partinandaan di bagasan tanganmu ma i, jala bahen parningotan di adopan ni matam, asa tung di bagasan pamanganmu patik ni Jahowa, paboa Jahowa manogihon ho ruar sian tano Misir marhitehite tangan na gogo.'),(9899,43,13,10,'Dibahen i radoti ma patik on di hajujumpang ni tingkina martaontaon.'),(9900,43,13,11,'Ia dung sahat ho diboan Jahowa tu tano ni halak Kanaan, songon na minangmangkonna tu ho dohot tu angka ompum, jala nunga sahat dilehon tu ho.'),(9901,43,13,12,'Pasahatonmu ma tu Jahowa, nasa anak na parjolo ruar sian bortian ro di nasa buhabaju di pinahan, angka na di ho, ia tunggal, Jahowa do nampunasa.'),(9902,43,13,13,'Alai nasa buhabaju sian halode, tobusonmu ma dohot anak ni birubiru; alai molo so ditobus ho, punokkon rungkungna; nasa buhabaju sian jolma pe di angka anakmi, tobusonmu do.'),(9903,43,13,14,'Jala molo disungkun anakmu ho sogot, aha do on? Dohononmu ma tu ibana: Marhitehite tangan na gogo ditogihon Jahowa hami ruar sian Misir, sian bagas parhatobanan.'),(9904,43,13,15,'Ai uju dipatangkang Firaun rohana manjua mangaloas hami laho, jadi dibunu Jahowa ma nasa buhabaju ni tano Misir, sian buhabaju ni jolma ro di buhabaju ni pinahan: Ala ni do hupelehon tu Jahowa nasa buhabaju tunggal; alai nasa buhabaju di angka anakku hutobus do.'),(9905,43,13,16,'Jala bahen partinandaan di bagasan tanganmu do i, jala bahen parningotan di holangkolang ni matam, paboa naung marhitehite tangan na gogo Jahowa paruarhon hami sian Misir.'),(9906,43,13,17,'Jadi dung diloas Firaun bangso i laho, ndada ditogihon Debata nasida sian dalan tu tano ni halak Palistim, atek pe jumonok nian sian i, ai ninna roha ni Debata do: Atek tung sumolsol dirina maon bangso i, ia diida nasida hamusuon, gabe mulak nasida maon tu Misir.'),(9907,43,13,18,'Dibahen i mangaliat bangso i ditogihon Debata sian dalan tu halongonan mangihutihut laut arung. Jadi marsinjata be halak Israel nangkok sian tano Misir.'),(9908,43,13,19,'Diboan si Musa huhut holiholi ni si Josep, ai pola do dipatolon halak Israel hian, ninna do: Sai na tingkiron ni Debata hamu; boan hamu ma dohot holiholingku sian on.'),(9909,43,13,20,'Jadi borhat ma nasida sian Sukot, jala marsaro di Etham, di partalpahan ni halongonan.'),(9910,43,13,21,'Jala Jahowa mardalan di jolonasida, ia arian marhitehite ombun na marsijongjong manogihon nasida di dalan i, ia bornginna i marhitehite api na marsijongjong manulu nasida, asa tarbahen nasida mardalan arian dohot borngin.'),(9911,43,13,22,'Ndang dung morot sian jolo ni bangso i ombun na marsijongjong arian dohot api na marsijongjong bornginna i.'),(9912,43,14,1,'Dung i ninna Jahowa ma tu si Musa songon on:'),(9913,43,14,2,'Dok ma tu halak Israel, asa mamedu nasida jala marsaro tondong ni Pi-Hakirot, di holangkolang ni Migdol dohot laut, tondong ni Baal Zephon, apala tondong ni ma nasida marsaro mardonokkon laut.'),(9914,43,14,3,'Jadi dohonon ni Firaun ma taringot tu halak Israel: Nunga lilu nasida di tano i, nunga dihapit halongonan i nasida.'),(9915,43,14,4,'Jadi patangkangonku ma roha ni Firaun, asa dilele nasida, asa marmulia Ahu di Firaun dohot di sandok paranganna, asa ditanda halak Misir, Ahu do Jahowa. Jadi dibahen nasida ma songon i.'),(9916,43,14,5,'Jadi dung tarbortik tu raja ni Misir naung lintun bangso i, gabe muba ma roha ni Firaun dohot roha ni angka naposona i maradophon Israel, angka didok nasida ma: Boasa tung tabahen songon i tung italoas halak Israel laho malua sian rodinta.'),(9917,43,14,6,'Jadi dipasang ma huretana, jala diarahon bangsona.'),(9918,43,14,7,'Dipambuat ma huhut onom ratus hureta angka na pinillit dohot nasa hureta na di Misir dohot tolu halak be parmusu di atas ni saluhutna.'),(9919,43,14,8,'Ai dipatangkang Jahowa do roha ni Firaun, raja ni Misir, asa dilele halak Israel, angka naung ruar marhitehite tangan na timbul.'),(9920,43,14,9,'Jadi dilele halak Misir ma nasida, gabe dapotsa ma nasida marsaro mardonokkon laut; nasa hoda, hureta ni Firaun ro di parsihundulna dohot paranganna mardonokkon Pi-Hakirot do, tondong ni Baal Zephon.'),(9921,43,14,10,'Jadi dung jumonok Firaun, gabe manatap ma halak Israel, jadi ida ma, halak Misir mangeahi nasida, gabe mabiar situtu nasida. Jadi mangangguhi ma halak Israel tu Jahowa.'),(9922,43,14,11,'Angka didok nasida ma tu si Musa: Dibahen na so adong tanoman di Misir huroha, umbahen ditogihon ho hami laho mate di halongonan? Boasa tung songon i bahenonmu tu hami, tung togihononmu hami ruar sian Misir?'),(9923,43,14,12,'Nda hudok hami hian tu ho di Misir songon on: Pasombu ma hami diparhatoban halak Misir, ai dengganan diparhatoban halak Misir hami, sian na mate di halongonan.'),(9924,43,14,13,'Jadi didok si Musa ma tu bangso i: Unang hamu mabiar, marsihohot ma rohamuna, jala ida hamu hamuliaon ni Jahowa, sibahenonna tu hamu sadari on; ai halak Misir, angka na niidamuna sadari on, ndang be idaonmuna nasida ro di salelenglelengna.'),(9925,43,14,14,'Jahowa do marporang humongkop hamu, asal hohom hamu.'),(9926,43,14,15,'Dung i didok Jahowa ma tu si Musa: Boasa sai mangangguhi ho tu Ahu? Dokkon ma halak Israel, asa borhat nasida.'),(9927,43,14,16,'Alai anggo ho hindat ma tungkotmi, jala patoltol tanganmu tu atas laut, jala patolak, asa dibolus halak Israel tongatonga ni laut i, mardalan di na mahiang.'),(9928,43,14,17,'Alai anggo Ahu ida ma patangkangonku ma roha ni halak Misir, asa dieahi nasida halak Israel; asa marmulia Ahu di Firaun dohot di sandok paranganna, di huretana dohot di angka parsihundulna.'),(9929,43,14,18,'Asa tung ditanda halak Misir, Ahu do Jahowa, ia dung marmulia Ahu di Firaun, di huretana dohot di angka parsihundulna.'),(9930,43,14,19,'Dung i borhat ma suruan ni Debata, na mardalan di jolo ni punguan ni Israel, jala laho ibana tu pudinasida, hehe ma dohot ombun na marsijongjong sian jolonasida, jala jongjong tu pudinasida.'),(9931,43,14,20,'Jadi sahat ma tu holangkolang ni parsaroan ni halak Misir dohot parsaroan ni Israel. Jadi holom ma ombun i sambariba, jala manondangi borngin na sambariba nari, gabe ndang masipadonohan be nasida manipat saborngin i.'),(9932,43,14,21,'Dung i dipatoltol si Musa ma tanganna tu atas laut, gabe mabaor laut i dibahen Jahowa marhitehite alogo na pur sian purba manipat saborngin i pahiang laut i, gabe tolak ma angka aek i.'),(9933,43,14,22,'Jadi bongot ma halak Israel tu tongatonga ni laut di na mahiang i, jadi hira dorpi angka aek i di siamun dohot di hambirangnasida.'),(9934,43,14,23,'Dung i dieahi halak Misir i nasida, jala bongot sian pudinasida, nasa hoda ni Firaun, angka huretana dohot angka parsihundulna tu tongatonga ni laut.'),(9935,43,14,24,'Jadi marhira torang ari, dibereng Jahowa ma tu parangan ni halak Misir sian api dohot ombun na marsijongjong, gabe guntur ma parangan ni halak Misir i.'),(9936,43,14,25,'Jala diantuk angka galapang ni huretanasida, gabe maol ditogu. Dung i didok halak Misir ma: Maporus ma hita maradophon Israel, ai Jahowa dongannasida mangalo halak Misir.'),(9937,43,14,26,'Dung i didok Jahowa ma tu si Musa: Patoltol ma tanganmu tu atas laut, asa sumuang angka aek i lingkup tu halak Misir, tu angka huretana dohot tu angka parsihundulna.'),(9938,43,14,27,'Jadi dipatoltol si Musa ma tanganna tu atas laut, gabe sumuang ma laut i tu dalanna hian mareak torang ari, gabe maporus ma halak Misir manjumpangkon aek i gabe ditunggalingkon Jahowa ma halak Misir tu tongatonga ni laut.'),(9939,43,14,28,'Ai sumuang ma angka aek i jala dilungkupi angka hureta dohot angka parsihundul ro di sude parangan ni Firaun, angka na mangihuthon nasida tu bagasan laut, ndang adong teba sian nasida nanggo sada.'),(9940,43,14,29,'Alai anggo halak Israel mardalan di na mahiang, di tongatonga ni laut i jala gabe dorpi di nasida angka aek i di siamun dohot di sihambirangnasida.'),(9941,43,14,30,'Asa dipalua Jahowa di na sadari i halak Israel sian tangan ni halak Misir, gabe diida halak Israel ma mate halak Misir i di topi laut i.'),(9942,43,14,31,'Jadi dung diida halak Israel tangan na timbul, na binahen ni Jahowa tu halak Misir, gabe mabiar ma bangso i mida Jahowa, jala porsea nasida di Jahowa dohot di si Musa, naposona i.'),(9943,43,15,1,'Dung i diendehon si Musa dohot halak Israel ma ende on di Jahowa, angka didok nasida ma songon on: Marende ma ahu tu Jahowa, ai marmulia situtu Ibana; hoda dohot parsihundulna diusophon tu bagasan laut.'),(9944,43,15,2,'Jahowa do hatogaonku dohot pujipujianku jala manjadi hatuaonku. Ibana do Debatangku; pasangaponku ma Ibana. Debata ni damang do Ibana, patimbulonku ma Ibana.'),(9945,43,15,3,'Jahowa do Ulubalang bolon, Jahowa do Goarna.'),(9946,43,15,4,'Angka hureta ni Firaun ro di paranganna diusophon do tu bagasan laut. Parsihundul angka na pinillitna dilonongkon do tu bagasan laut arung.'),(9947,43,15,5,'Angka lung do manghungkupi nasida; lonong do angka nasida tu toru, songon batu.'),(9948,43,15,6,'Tangan siamunmi, ale Jahowa, marmulia di bagasan hagogoon; tangan siamunmu, ale Jahowa, mangaropuhi musu i.'),(9949,43,15,7,'Jala marhitehite hinatimbul ni hasangaponmu disiaphon Ho angka na mangalo Ho; dilaluhon Ho parpiarpiar ni rimasmu mansudahon nasida songon durame.'),(9950,43,15,8,'Marhitehite pallongos ni igungmu marlintonglintong angka aek i; marsijongjong angka galumbang, songon dorpi; mangibulibul ma angka lung di tongatonga ni laut.'),(9951,43,15,9,'Dung i ninna musu i ma: Leleonku, tangkuponku ma nasida, bagionku ma taban; sombu ma rohangku mida nasida, umpatonku ma podangku, siaphonon ni tanganku ma nasida.'),(9952,43,15,10,'Diombushon Ho hosam, gabe dilungkupi laut nasida, angka lonong ma nasida songon simbora tu bagasan aek angka na tongam.'),(9953,43,15,11,'Ise ma songon Ho, ale Jahowa di tongatonga ni angka debata? Ise ma songon Ho marmulia di bagasan habadiaon, na songkal marpujipujian, na mambahen halongangan?'),(9954,43,15,12,'Dung dipatoltol Ho tanganmu siamun, dibondut tano i nasida.'),(9955,43,15,13,'Ditogihon Ho marhitehite asiasim bangso na pinaluam on; dipatolhas Ho nasida marhitehite hatogaonmu tu bagasan inganan ni habadiaonmu.'),(9956,43,15,14,'Dibege angka bangso barita i, gabe angka dotdot nasida, manimbasnimbas ma halak Palistim.'),(9957,43,15,15,'Tarsonggot ma uju i angka raja ni Edom, angka hitir ma raja ni Moab; tahuton ma sude isi ni Kanaan.'),(9958,43,15,16,'Sai madabu ma tu nasida songgotsonggot dohot hatahuton; tarhatotong ma nasida, songon batu dibahen hinatimbul ni tanganmu, paima taripar bangsom, ale Jahowa; paima taripar bangsom, na tinobusmu.'),(9959,43,15,17,'Sai pamasuk ma nasida, jala lopokkon nasida tu dolok partalianmi, haojahan ni ingananmi na pinaulim, ale Jahowa; tu habadiaon, na pinajok ni tanganmu, ale Tuhan.'),(9960,43,15,18,'Jahowa do Raja ro di salelenglelengna.'),(9961,43,15,19,'Ai bongot tu bagasan laut do hoda ni Firaun dohot angka huretana ro di parsihundulna, jadi dipasuang Jahowa ma angka aek ni laut i mangalungkupi nasida; alai anggo halak Israel mardalan di na mahiang do nasida di tongatonga ni laut.'),(9962,43,15,20,'Dung i ditiop si Meryam boru panurirang, iboto ni si Aron odap sada di bagasan tanganna, jala ruar sude angka boruboru marudurudur mangihuthon ibana masitiop odapna be.'),(9963,43,15,21,'Jadi marende taripar ma si Meryam tu nasida; marende ma hamu tu Jahowa, ai marmulia situtu do Ibana; hoda dohot parsihundulna diusophon tu bagasan laut.'),(9964,43,15,22,'Dung i didokkon si Musa ma halak Israel borhat sian laut arung, asa mardalan nasida tu halongonan Sur; jadi mardalan ma nasida tolu ari di bagasan halongonan i, ndang jumpangan aek nasida.'),(9965,43,15,23,'Dung i sahat ma nasida tu Mara; alai ndang tarinum nasida aek sian Mara, ai na paet do i; ala ni do umbahen margoar Mara.'),(9966,43,15,24,'Jadi marmungutmungut ma bangso i dompak si Musa, ninna ma: Aha na ma siinumonnami?'),(9967,43,15,25,'Dung i mangangguhi ma si Musa tu Jahowa, gabe dipatuduhon Jahowa ma tu ibana sada hau, na nireongkonna tu aek, jadi tonggi ma aek i. Disi, ma dibahen tu nasida sada patik dohot uhum, jala disi ma nasida diunjuni.'),(9968,43,15,26,'Jala didok ma: Molo burju ditangihon ho soara ni Jahowa Debatam, jala diulahon ho na tigor di rohana, jala dipinggol ho angka patikna, jala diradoti ho sude angka uhumna: Ndang pamasaonku tu ho nanggo sada sahit, angka na hupamasa tu Misir, ai Ahu do Jahowa pandaonim.'),(9969,43,15,27,'Dung i sahat ma nasida tu Elim, adong do disi mual sampulu dua, jala pitupulu harambir, gabe marsaro ma nasida disi di lambung aek i.'),(9970,43,16,1,'Dung i borhat ma nasida sian Elim, gabe sahat ma sandok luhutan ni halak Israel tu halongonan Sin na di holangkolang ni Elim dohot Sinai di ari sampulu lima di bulan sipahadua, dung ruar nasida sian tano Misir.'),(9971,43,16,2,'Jadi marmungutmungut ma sandok luhutan ni halak Israel dompak si Musa dohot dompak si Aron di halongonan i.'),(9972,43,16,3,'Didok halak Israel ma tu nasida: Tung so mate hami dibunu tangan ni Jahowa di tano Misir, di na hundul hami di lambung hudon angka na marisi juhut, di na bosur hami mangan sagusagu; ai ditogihon hamu hami ruar tu halongonan on laho mambunu sandok luhutan on anturaparon.'),(9973,43,16,4,'Dung i ninna Jahowa ma tu si Musa: Ida ma paroonku ma tu hamu udan sagusagu sian banua ginjang, asa ruar bangso i jala dipapungu sae sadarina i, asa huunjuni nasida manang turuton nasida patikki manang ndang.'),(9974,43,16,5,'Alai jumpang paonomarihon, paulionnasida ma na binoannasida i, gabe silompit ma i martimbangkon na pinapungunasida siap ari.'),(9975,43,16,6,'Dung i didok si Musa dohot si Aron ma tu sude halak Israel: Bodari on ma botoonmuna Jahowa paruarhon hamu sian tano Misir.'),(9976,43,16,7,'Sogot manogot idaonmuna ma hasangapon ni Jahowa, ai nunga di bege Ibana parungutungutonmuna dompak Jahowa. Ai tung ise ma hami pola umbahen tung marmungutmungut hamu dompak hami?'),(9977,43,16,8,'Angkup ni didok si Musa ma: Molo dilehon Jahowa juhut panganonmuna bodari on jala butongan mangan sagusagu manogotna i, ala naung dibege Jahowa parungutungutonmuna, na marmungutmungut i hamu dompak Ibana, ai tung ise ma anggo hami pola? Ndada dompak hami hamu marmungutmungut, dompak Jahowa do.'),(9978,43,16,9,'Dung i didok si Musa ma tu si Aron: Dok ma tu sandok luhutan ni halak Israel: Pajonok hamu ma tu jolo ni Jahowa, ai nunga dibege parungutungutonmuna.'),(9979,43,16,10,'Jadi di na mangkatai si Aron tu sandok luhutan ni halak Israel, humusor ma nasida mamereng dompak halongonan, jala ida ma, tarida ma hasangapon ni Jahowa di bagasan ombun i.'),(9980,43,16,11,'Dung i ninna Jahowa ma tu si Musa songon on:'),(9981,43,16,12,'Nunga hubege parungutunguton ni halak Israel, dok ma tu nasida songon on: Marhira bodari panganonmuna ma juhut, jala manogotna i butongan hamu mangan sagusagu, asa tung diboto hamu, Ahu do Jahowa Debatamuna i.'),(9982,43,16,13,'Jadi dung bodari, ro ma marhulalan angka lote, tung ditutup do parsaroan i, jala manogotna i marpeahan nambur humaliang parsaroan i.'),(9983,43,16,14,'Jadi dung manaek nambur na marpeahan i, ida ma adong ma rarak di sisik ni halongonan i songon batu ni botik na tata metmetna dohot hibulna.'),(9984,43,16,15,'Jadi dung diida halak Israel, angka didok be ma tu donganna; Manhu? (Aha do i?) Ai ndang diboto nasida manang aha i. Alai ninna si Musa ma tu nasida: I ma sagusagu na nilehon ni Jahowa tu hamu bahen sipanganon.'),(9985,43,16,16,'Boti ma hata na tinonahon ni Jahowa: Papungu hamu sian i ganup marguru tu tukna panganonna, sasolup sasahalak, marguru tu bilangan ni toropmuna; ganup mambuat marguru tu isi ni undungundungna be.'),(9986,43,16,17,'Jadi dibahen halak Israel ma songon i, adong na papungu godang, adong na papungu otik.'),(9987,43,16,18,'Alai dung disolupi, gabe ndang adong lobian na papungu godang, jala ndang hurangan na papungu otik; ganup marguru tu na tarpangansa, dipapungu nasida.'),(9988,43,16,19,'Dung i didok si Musa ma tu nasida: Manang ise pe, unang dihirahon sian i tu marsogot.'),(9989,43,16,20,'Alai ndang ditangihon nasida hata ni si Musa, adong do deba marhirahon sian i tu marsogot, jadi gulokon ma i jala bau, gabe tarrimas ma si Musa tu nasida.'),(9990,43,16,21,'Alai dipapungu nasida ma sian i ganup sogot, marguru tu na tarpangansa be; alai disi las ari, gabe malala ma.'),(9991,43,16,22,'Jumpang paonomarihon, silompit ma dipapungu nasida sagusagu i, dua solup tu na sahalak. Jadi ro ma sude induk ni bangso i laho paboahon tu si Musa.'),(9992,43,16,23,'Jadi didok si Musa ma tu nasida: I do tona ni Jahowa antong. Marsogot do ari Sabbat, paradianan siparbadiaan di Jahowa: Pamasak hamu hian ma sipamasahonmuna, jala lompa hamu hian silompaonmuna; alai na lobi, pasimpan hamu ma bahen sipanganon marsogot.'),(9993,43,16,24,'Jadi dipasimpan nasida ma i tu marsogot, songon na tinonahon ni si Musa, gabe ndang olo bau manang gulokon di bagasan.'),(9994,43,16,25,'Dung i ninna si Musa ma: Pangan hamu ma i sadari on, ai ari paradianan ni Jahowa do sadari on; ndang adong jumpang hamu di ladang.'),(9995,43,16,26,'Onom ari lelengna papunguonmuna ma i, alai ari papituhon i ari paradianan do i, ndang adong jumpang disi.'),(9996,43,16,27,'Alai tole papituarihon ruar do deba sian bangso i naeng papunguhon, hape ndang adong jumpangsa.'),(9997,43,16,28,'Jadi ninna Jahowa ma tu si Musa: Sadia leleng on juaonmuna mangaradoti angka tonangku dohot patikku?'),(9998,43,16,29,'Ida hamu ma, nunga dilehon Jahowa ari paradianan i tu hamu; ala ni Ibana mangalehon tu hamu di ari paonomhon sagusagu sidua ari; asa mian hamu di ingananmuna be; unang adong ruar nanggo sahalak sian ingananna di ari papituhon i.'),(9999,43,16,30,'Jadi maradian ma bangso i di ari papituhon i.'),(10000,43,16,31,'Jadi Manna ma goarna dibahen halak Israel, ianggo rupana songon boni ni hatumbar, saksak, jala daina songon sagusagu na tinanggulean.'),(10001,43,16,32,'Dung i didok si Musa ma: On do hata na tinonahon ni Jahowa: Gohi hamu ma sasolup sian i sipajopon tu pinomparmuna sogot, asa diida nasida sagusagu na hupapanganhon tu hamu di halongonan, di na huparuar hamu sian tano Misir.'),(10002,43,16,33,'Jadi ninna si Musa ma tu si Aron: Buat ma sada tapongan, jala bahen tusi Manna gok sasolup, jala pajongjong tu jolo ni Jahowa, sipajopon di pinomparmuna.'),(10003,43,16,34,'Songon na tinonahon ni Jahowa tu si Musa, dipajongjong si Aron ma i tu jolo ni poti parpadanan i bahen parningotan.'),(10004,43,16,35,'Leleng ni opatpulu taon ma dipangan halak Israel Manna i, paima sahat nasida tu tano siinganannasida: Paima sahat nasida tu parbalohan ni tano Kanaan, sai Manna do dipangan nasida.'),(10005,43,16,36,'Ia sasolup (homer), saparsampulu ni saampang (epah) ma i.'),(10006,43,17,1,'Dung i borhat ma sandok luhutan ni halak Israel sian halongonan Sin, marguru tu pandokkon ni Jahowa pardalanonnasida i, gabe marsaro nasida di Rapidim; alai ndang adong aek siinumon ni bangso i disi.'),(10007,43,17,2,'Jadi dibadai bangso i ma si Musa, angka didok ma: Lehon hamu aek siinumonnami! Jadi didok si Musa ma tu nasida: Boasa ahu badaanmuna? Boasa unjunanmuna Jahowa?'),(10008,43,17,3,'Alai uasan di aek bangso i disi, gabe marmungutmungut nasida dompak si Musa, didok ma: Boasa tung paruaronmu hami sian Misir laho mambunu hami dohot anakkonnami ro di pinahannami dibahen uas?'),(10009,43,17,4,'Jadi mangangguhi ma si Musa tu Jahowa, ninna ma: Aha ma bahenonku tu bangso on? Otik nari dangguronnasida ma ahu dohot batu.'),(10010,43,17,5,'Jadi didok Jahowa ma tu si Musa: Bolus ma sian jolo ni bangso i, jala arahon deba sian angka sintua ni Israel bahen donganmu, jala boan ma tungkotmu, na nilipathonmu tu batang aek i, dung i laho ma ho.'),(10011,43,17,6,'Ida ma, jongjong ma Ahu disi di jolom di atas ni sada dolok batu di Horeb, lipati ma dolok batu i, gabe ruar ma sian i aek siinumon ni bangso i. Jadi dibahen si Musa ma songon i di adopan ni angka sintua ni Israel.'),(10012,43,17,7,'Jadi dibahen ma goar ni hajongjongan i Massa (pangunjunan) dohot Meriba (parbadaan), ala ni bada ni halak Israel, jala ala ni niunjunannasida Jahowa, na nidokna i: Di tongatonganta ulaning do Jahowa manang ndang?'),(10013,43,17,8,'Dung i ro ma Amalek, jala marmusu dompak Israel di Rapidim.'),(10014,43,17,9,'Dung i didok si Musa ma tu si Josua: Pilliti ma di hita angka baoa, jala borhat ma ho mandopangi Amalek; jongjong ma ianggo Ahu marsogot di punsu ni dolokdolok, patioptiop tungkot ni Debata di bagasan tanganku.'),(10015,43,17,10,'Jadi dibahen si Josua ma, songon na nidok ni si Musa tu ibana laho mandopangi Amalek; alai anggo si Musa dohot si Aron dohot si Hur nangkok do nasida tu punsu ni dolokdolok i.'),(10016,43,17,11,'Jadi saleleng dipaherbang si Musa tanganna tu ginjang, monang ma Israel, alai molo dipalenduk tanganna, monang ma Amalek.'),(10017,43,17,12,'Alai ngaolan ma tangan ni si Musa, gabe dibuat nasida ma batu sada, dibahen ma tu toruna, asa hundul ibana tusi; ro ma si Aron dohot si Hur tumungkol tanganna sambariba be; gabe hot ma tanganna i, paima mate mata ni ari.'),(10018,43,17,13,'Jadi dipatunduk si Josua ma Amalek dohot bangsona marhite sian baba ni podang.'),(10019,43,17,14,'Dung i didok Jahowa ma tu si Musa: Surathon ma i tu sada buku bahen parningotan, jala pasingothon ma tu pinggol ni si Josua, ai sai na siaphononku ma barita ni Amalek sian toru ni langit.'),(10020,43,17,15,'Dung i dipauli si Musa ma sada langgatan, dibahen ma goarna: Jahowa Nissi (Jahowa partonggolhu).'),(10021,43,17,16,'Angkup ni didok ma: Sintak tangan di atas habangsa ni Jahowa: Hamusuon tihus ma di Jahowa dompak Amalek marsundutsundut.'),(10022,43,18,1,'Jadi dung dibege si Jetro, malim di Midian, simatua doli ni si Musa, sude na binahen ni Debata tu si Musa dohot tu Israel bangsona i, na pinaruar ni Jahowa halak Israel sian Misir.'),(10023,43,18,2,'Jadi diboan si Jetro, simatua doli ni si Musa, ma si Zippora, jolma ni si Musa, na sinuruna i mulak.'),(10024,43,18,3,'Rap dohot anakna na dua i, goar ni na sada si Gerson, ai ninna do: Paisolat do ahu di tano ni halak na pulik.'),(10025,43,18,4,'Goar ni na sada nari si Elieser, ai Debata ni damang do pangurupingku, jala dipalua ahu sian podang ni Firaun.'),(10026,43,18,5,'Jadi sahat ma si Jetro, simatua doli ni si Musa dohot angka anakna dohot jolmana tu si Musa, tu halongonan, tu parsaroanna di lambung dolok ni Debata.'),(10027,43,18,6,'Dung i ditonahon ma tu si Musa: Nunga ro ahu, simatuam doli si Jetro tumopot ho udur dohot jolmam dohot anakna na dua i.'),(10028,43,18,7,'Jadi ruar ma si Musa manjumpangkon simatuana i, dipaunduk ma ibana di jolona, jala diumma ibana; jadi dung masisungkunan nasida taringot tu sonangna, gabe masuk ma nasida tu undungundung i.'),(10029,43,18,8,'Dung i dipajojor si Musa ma tu simatuana sude na binahen ni Jahowa tu Firaun dohot tu halak Misir ala ni halak Israel, ro di sude hasusaan na mangonai tu nasida di tongan dalan dohot na pinalua ni Jahowa nasida.'),(10030,43,18,9,'Jadi las ma roha ni si Jetro ala sude na denggan i, na binahen ni Jahowa tu Israel, na pinaluana i nasida sian tangan ni halak Misir.'),(10031,43,18,10,'Dung i ninna si Jetro ma: Pinuji ma Jahowa, na paluahon hamu sian tangan ni halak Misir dohot sian tangan ni Firaun; tung dipalua bangsona sian pandondon ni tangan ni halak Misir.'),(10032,43,18,11,'Nuaeng pe huboto, tumongam hape Jahowa sian nasa angka debata, ai hata na hinajungkathonnasida lingkup tu nasida.'),(10033,43,18,12,'Dung i dibuat si Jetro, simatua doli ni si Musa ma sada pelean situtungon dohot angka pelean sipotongon di Debata. Jadi ro ma si Aron dohot saluhut sintua ni Israel laho mangan sagusagu rap dohot simatua doli ni si Musa di jolo ni Debata.'),(10034,43,18,13,'Tole marsogotna i hundul ma si Musa laho manguhum bangso i, gabe jongjong ma bangso i di lambung ni si Musa sian manogotna ro di botari.'),(10035,43,18,14,'Alai dung diida simatua doli ni si Musa sude na binahenna i tu bangso i, ninna ma: Hata dia do i na binahenmu tu bangso i? Boasa sasada ho hundul, jala sandok bangso i jongjong di jolomu sian manogot ro di botari?'),(10036,43,18,15,'Jadi didok si Musa ma tu simatuana doli: Na ro do bangso i tumopot ahu laho manungkun uhum sian Debata.'),(10037,43,18,16,'Ai molo adong parhataannasida, ro do nasida tu ahu, gabe huuhumi nasida be jala hupabotohon angka patik ni Debata dohot angka podana.'),(10038,43,18,17,'Jadi didok simatua doli ni si Musa ma tu ibana: Ndang denggan na binahenmi.'),(10039,43,18,18,'Mandate ho maon, nang ho, nang bangso on na di jolomon, ai madokdokhu ulaon on di ho, ndang tarula anggo sasada ho.'),(10040,43,18,19,'On pe tangihon ma soarangkon, huparrohai pe ho, asa didongani Debata ho. Ho ma mandapothon Debata singkat ni bangso i, jala ho ma pasahathon angka hata i tu Debata.'),(10041,43,18,20,'Jala pasingothon tu nasida angka patik dohot angka poda, jala pabotohon tu nasida dalan sidalanannasida i dohot ulaon siulaonnasida i.'),(10042,43,18,21,'Alai anggo ho lulu i ma sian sandok bangso i baoa angka na marsihohot marroha, angka na mangkabiari Debata, baoa parroha na sintong, angka na mangkagigihon pangomoan na so turut, i ma pabangkit bahen induk gumomgom nasida marsasaribu, marsasaratus, marlimalimapulu, jala marsasampulu.'),(10043,43,18,22,'Asa nasida tongtong manguhumi bangso i; alai nasa hata na balga, tu ho ma diboan nasida, anggo nasa hata metmet nasida ma manguhumi, asa paneang siulaonmu, molo nasida donganmu mangusung.'),(10044,43,18,23,'Molo dibahen ho songon i, jala ro tona ni Debata tu ho manolopi, jadi manahan ma ho mangkajongjongkon, muli ma dohot sandok bangso on tu ingananna be mardamedame.'),(10045,43,18,24,'Jadi ditangihon si Musa ma soara ni simatuana doli, jala dipatupa sude na nidokna i.'),(10046,43,18,25,'Dung i dipillit si Musa ma baoa angka na marsihohot sian sandok Israel, jala dipabangkit nasida bahen induk ni bangso i gumomgom marsasaribu, marsasaratus, marlimalimapulu jala marsasampulu.'),(10047,43,18,26,'Asa nasida tongtong manguhumi bangso i, anggo hata na maol, tu si Musa ma boanonnasida, alai anggo hata metmet, nasida do manguhumisa.'),(10048,43,18,27,'Dung i diloas si Musa ma simatuana doli laho, gabe mulak ma ibana tu tanona.'),(10049,43,19,1,'Patolubulanhon dung ruar halak Israel sian tano Misir, di ari na sasada i sahat ma nasida tu halongonan Sinai.'),(10050,43,19,2,'Ai borhat do nasida sian Rapidim, gabe sahat nasida tu halongonan Sinai, laos marsaro nasida di halongonan i; disi do marsaro Israel tondong ni dolok i.'),(10051,43,19,3,'Dung i nangkok ma si Musa tumopot Debata. Jadi dijouhon Jahowa ma tu ibana sian dolok i, ninna ma: Songon on ma dohononmu tu pinompar ni si Jakkob, paboaonmu tu halak Israel.'),(10052,43,19,4,'Nunga diida hamu, na hubahen i tu halak Misir, na huboan i hamu hira di atas ni habong ni pidong raja wali pasahat hamu tu Ahu.'),(10053,43,19,5,'On pe molo ditangihon hamu soarangku, jala diradoti hamu padan na hubahen i, jadi hasianku ma hamu sian nasa bangso, ai gok di Ahu do liat portibi on.'),(10054,43,19,6,'Manjadi harajaon malimmalim ma hamu di Ahu, jala bangso na badia. I ma angka hata sidohononmu tu halak Israel.'),(10055,43,19,7,'Jadi ro ma si Musa, dijou ma angka sintua ni bangso i, jala diaturhon tu jolonasida sude angka hata ondeng, angka na tinonahon ni Jahowa tu ibana.'),(10056,43,19,8,'Jadi sahata ma sandok bangso i mangalusi, angka didok nasida ma: Saluhut na pinaboa ni Jahowa, bahenonnami ma. Jadi mulak ma si Musa mamboan angka hata ni bangso i tu Jahowa.'),(10057,43,19,9,'Dung i didok Jahowa ma tu si Musa: Ida ma, na ro ma Ahu tumopot ho di bagasan ombun manorop, asa dibege bangso i molo mangkatai Ahu dohot ho, asa porsea nasida dohot di ho ro di salelenglelengna. Jadi dipaboa si Musa ma angka hata ni bangso i tu Jahowa.'),(10058,43,19,10,'Dung i didok Jahowa ma tu si Musa: Laho ma ho tumopot bangso i, jala urasi nasida sadari on dohot marsogot asa ditaptap nasida ulosnasida.'),(10059,43,19,11,'Asa diri nasida di ari patoluhon, ai patoluarihon tuat ma Jahowa di jolo ni sandok bangso i tu punsu ni dolok Sinai.'),(10060,43,19,12,'Jala bahen parbolatan ni bangso i humaliang, nimmu ma: Jaga hamu, so tung dinangkohi hamu dolok i, manang manjama bonana i. Manang ise na jumama dolok i, ingkon mate ibana.'),(10061,43,19,13,'Ndang jadi dais tusi tangan ni manang ise, ingkon dangguron manang tullangon ibana mate; nang pinahan, nang jolma, ndang jadi pangoluon. Alai molo marsaringar soara ni sarune, jadi ma nangkohannasida dolok i.'),(10062,43,19,14,'Dung i tuat ma si Musa sian dolok i tumopot bangso i, jala diurasi bangso i, jala ditaptap nasida ma ulosnasida be.'),(10063,43,19,15,'Dung i ninna si Musa ma tu bangso i: Diri ma hamu tumagam ari patoluhon, unang ma jumonok manang ise tu jolmana.'),(10064,43,19,16,'Tole patoluarihon, disi torang ari, masa ma angka soara ronggur dohot hilap dohot ombun manorop di atas dolok i, jala soara ni sarune mansai gogo, gabe tarsonggot ma sandok bangso na di parsaroan i.'),(10065,43,19,17,'Dung i ditogihon si Musa ma bangso i ruar sian parsaroan laho manjumpangkon Debata, jala angka jongjong nasida solhot di bona ni dolok i.'),(10066,43,19,18,'Jadi martimus ma sandok dolok Sinai maradophon Jahowa na tuat tu atasna di bagasan api, jala manaek timusna i songon timus ni pamurunan, gabe humuntal situtu sandok dolok i.'),(10067,43,19,19,'Jadi dung lam ganda situtu gogo ni soara ni sarune, mangkatai ma si Musa, gabe dialusi Debata ibana marsoara ronggur.'),(10068,43,19,20,'Alai dung tuat Jahowa tu dolok Sinai, tu punsu ni dolok i, dijou Jahowa ma si Musa tu punsu ni dolok i, gabe nangkok ma si Musa.'),(10069,43,19,21,'Dung i didok Jahowa ma tu si Musa: Tuat ma ho, tingtingkon ma tu bangso i, asa unang ditahopi nasida handang i laho mandapothon Jahowa, naeng idaonna, gabe marmatean maon torop sian nasida.'),(10070,43,19,22,'Angka malim pe, angka na jumonok tu Jahowa, ingkon manguras nasida, asa unang diragehon Jahowa nasida.'),(10071,43,19,23,'Alai ninna si Musa ma tu Jahowa: Ndang tarbahen bangso i nangkok tu dolok Sinai, ai nunga dihatindangkon Ho tu hami, nimmu do: Handang i dolok i jala parbadiai ibana.'),(10072,43,19,24,'Dung i didok Jahowa ma tu ibana: Laho ma ho tuat; alai nangkok ho muse rap dohot si Aron; alai anggo angka malim dohot bangso i, unang ditahopi nasida handang i laho mandapothon Jahowa, asa unang rage nasida.'),(10073,43,19,25,'Jadi tuat ma si Musa mandapothon bangso i, jala dipaboa ma tu nasida.'),(10074,43,20,1,'Dung i diaturhon Debata ma sude angka hata on, ninna ma:'),(10075,43,20,2,'Ahu do Jahowa Debatam, na paruarhon ho sian tano Misir, sian bagas parhatobananmu.'),(10076,43,20,3,'Ndang jadi marangkup Ahu adong Debatam.'),(10077,43,20,4,'Ndang jadi bahenonmu di ho ganaganaan, manang sumansuman dia na di banua ginjang nang di tano on, ro di na di bagasan aek na di toru ni tano.'),(10078,43,20,5,'Ndang jadi ho marsomba tu nasida, manang oloanmu nasida, ai Ahu do Jahowa Debatam, Debata parrimas, na mangaluluhon hajahaton ni amaama tu angka anakna ro di sundut patoluhon dohot paopathon, angka na sogo roha di Ahu.'),(10079,43,20,6,'Alai asi do rohangku di angka na marriburibu, angka na mangkaholongi Ahu, angka na mangaradoti angka patikku.'),(10080,43,20,7,'Ndang jadi dohononmu goar ni Jahowa Debatam, anggo sian neang ni roha, ai sai na hona toru ni Jahowa do manang ise na mandok goarna sian neang ni rohana.'),(10081,43,20,8,'Ingot hamu ma ari Sabbat laho pabadiahonsa.'),(10082,43,20,9,'Onom ari ma hamu mulaulaon, jala bahenonmu nasa ulaonmu.'),(10083,43,20,10,'Alai anggo ari papituhon i, ari paradianan ni Jahowa Debatam do i. Ndang jadi bahenonmu disi agia aha ulaon, nang ho manang anakmu, manang borum, manang naposom lahilahi, manang naposom boruboru, ro di pinahanmu, nang na sorang tu ho, di bagasan hutam.'),(10084,43,20,11,'Ai di na onom ari do ditompa Jahowa langit dohot tano dohot laut dohot nasa isina, dung i maradian ma Ibana di ari papituhon i. Dibahen i do umbahen na dipasupasu Jahowa ari paradiananna i jala diparbadiai.'),(10085,43,20,12,'Ingkon pasangaponmu natorasmu, asa leleng ho mangolu di tano, na nilehon ni Jahowa Debatam tu ho.'),(10086,43,20,13,'Unang ho mambunu.'),(10087,43,20,14,'Unang ho mangalangkup.'),(10088,43,20,15,'Unang ho manangko.'),(10089,43,20,16,'Unang ho olo sitindangkon gabus maralohon donganmu.'),(10090,43,20,17,'Ndang jadi haliangan ni roham bagas ni donganmu. Ndang jadi haliangan ni roham jolma ni donganmu, manang naposona baoa manang naposona boruboru, manang lombuna, manang hodana, manang dia pe ugasan ni donganmu.'),(10091,43,20,18,'Jadi sandok bangso i marnida dohot umbege angka soara ronggur dohot hilap dohot soara ni sarune i dohot dolok na martimus i. Jadi dung diida bangso i, lumindak ma nasida jongjong mandaodao.'),(10092,43,20,19,'Angka didok ma tu si Musa: Ho ma angkupnami mangkatai, hutangihon hami pe, unang Debata mangkulingi hami, atek tung mate hami maon.'),(10093,43,20,20,'Alai ninna si Musa ma tu bangso i: Unang hamu mabiar, ai mangunjuni hamu do Debata umbahen na ro, asa di jolomuna biar mida Ibana, asa unang mardosa hamu.'),(10094,43,20,21,'Jadi jongjong ma bangso i mandaodao; alai anggo si Musa didapothon do tu na holom, na niinganan ni Debata.'),(10095,43,20,22,'Dung i ninna Jahowa ma tu si Musa: Songon on do dohononmu tu halak Israel: Nunga diida hamu, na hupangkulingi i hamu sian banua ginjang.'),(10096,43,20,23,'Dibahen i ndang jadi marangkup Ahu bahenonmu debatam, manang angka debata sian perak manang angka debata sian sere pe.'),(10097,43,20,24,'Langgatan sian tano sada bahenonmu di Ahu, bahen pamelean ni angka peleanmu situtungon dohot pelean hamauliateon, angka birubirumu dohot angka lombumu pelehononmu disi. Ai ia disi hupajok inganan parningotan di goarhu, tusi do topotonku ho jala pasupasuonku ho.'),(10098,43,20,25,'Alai molo naeng bahenonmu di Ahu sada langgatan sian batu, unang pauli sian batu na nidakdak, ai molo mangonai bosim tusi, gabe ramun maon.'),(10099,43,20,26,'Jala unang martangga ho manaek tu langgatanku, asa unang mullop na niabitanmu maradophon ibana.'),(10100,43,21,1,'Jala on do uhum, angka siaturhononmu tu jolonasida.'),(10101,43,21,2,'Molo dituhor ho sada hatoban baoa marga Heber, onom taon ma lelengna mangadop, alai anggo papitutaonhon ingkon malua boti ibana.'),(10102,43,21,3,'Molo panangkarnangkar ibana ro panangkarnangkar ibana ruar; molo marripe ibana ro, rap malua ibana dohot ripena.'),(10103,43,21,4,'Alai molo marripe ibana dibahen indukna, gabe ditubuhon anak manang boru di ibana, indukna i ma nampuna ripena dohot tubuna, holan ibana sandiri malua.'),(10104,43,21,5,'Alai molo sada hata ni hatoban i mandok: Holong do rohangku di indukhu, di jolmangku dohot di angka anakkonku, tagonan unang malua ahu.'),(10105,43,21,6,'Togihonon ni indukna ma ibana tu Debata patindangonna ma ibana tu pintu manang tu tiang, dung i turaon ni indukna ma pinggolna dohot turatura, jadi hatobanna ma ibana salelenglelengna.'),(10106,43,21,7,'Alai molo digadis manang ise boruna bahen hatoban, ndang jadi malua, songon halulua ni hatoban baoa.'),(10107,43,21,8,'Molo sogo roha ni indukna mida ibana, jala ndang marsinonduk mangan dibahen, ingkon tobuson ma i; alai anggo tung tu bangso sileban ndang guru rohana manggadis, ala naung diose padanna tu nasida.'),(10108,43,21,9,'Alai molo tu anakna nasida maroroan dibahen, uhum parboruon na ma bahenonna tu nasida.'),(10109,43,21,10,'Molo boruboru na asing dibuat bahen imbangna, ndang jadi oruanna sipanganonna, parulosonna dohot pargansionna.'),(10110,43,21,11,'Molo so dibahen uhum na tolu ondeng tu nasida, malua boti nasida, ndang sitobus.'),(10111,43,21,12,'Molo adong manang ise na mangompong jolma, pola mate, ingkon marutang hosa ibana.'),(10112,43,21,13,'Alai molo so diburu hian, tung Debata tardok patongonhon ibana tu tanganna so pangkirimanna, Ahu pe patuduhon haporusanna tu ho.'),(10113,43,21,14,'Alai molo piripirion manang ise dompak donganna, marbisuk pamununa di ibana, saratonmu ma ibana sian lambung ni langgatanku laho mambunu ibana.'),(10114,43,21,15,'Manang ise na mambunu amana manang inana, ingkon marutang hosa ibana.'),(10115,43,21,16,'Manang ise na mamangus jolma jala digadis, manang jumpang di tanganna dope, ingkon marutang hosa ibana.'),(10116,43,21,17,'Manang ise na mamburai amana manang inana, ingkon marutang hosa ibana.'),(10117,43,21,18,'Molo marbadai angka baoa, gabe dipungkulhon ma sada batu tu alona manang ditinju ibana, alai ndang pola mate, asal peak di podomanna.'),(10118,43,21,19,'Ia dung hehe muse ibana gabe mardalani di balian martungkottungkot, jadi lias ma na mambunu i, asal digarar halolahanna dohot upa pandaoni paima malum situtu.'),(10119,43,21,20,'Manang ise na mangompongi hatobanna baoa manang boruboru, gabe mate na niomponganna i, ingkon abulonna do.'),(10120,43,21,21,'Alai molo tung mangolu sadari manang dua ari nari ndang abulonna, ai hepengna na ma i.'),(10121,43,21,22,'Molo marbadai angka baoa, gabe tarsamar boruboru na humunti pagar, pola hatektehan sanggul, alai ndang nian mahua anggo hosana, ingkon marutang hepeng ma ibana, marguru tu pandok ni harajaan ni boruboru i tu ibana, jala lehononna tu angka panguhum.'),(10122,43,21,23,'Alai molo sanga mate, ingkon hosa do abul ni hosa.'),(10123,43,21,24,'Mata do abul ni mata, ipon do abul ni ipon, tangan do abul ni tangan, pat do abul ni pat.'),(10124,43,21,25,'Mabortu ma abul ni na mabortu, bugang do abul ni bugang, na punil abul ni na punil.'),(10125,43,21,26,'Molo dilipat manang ise mata ni hatobanna baoa manang boruboru, pola sega matana, ingkon paluaonna boti, abul ni matana i.'),(10126,43,21,27,'Suang songon i molo diropangkon ngingi ni hatobanna baoa manang boruboru, ingkon paluaonna boti abul ni iponna i.'),(10127,43,21,28,'Molo adong baoa manang boruboru ditambing sada jonggi, pola mate ibana, ingkon mate dangguron do jonggi i dohot batu, jala ndang jadi panganon sibukna, alai lias ma ianggo nampuna lombu i.'),(10128,43,21,29,'Alai molo panambing hian jonggi i sian na sai laon, diboto nampunasa i huhut, hape ndang diramoti, gabe dibunu jonggi ma baoa manang boruboru, ingkon mate dangguron jonggi i dohot batu, bunuon ma dohot nampunasa.'),(10129,43,21,30,'Alai molo pardengganan uhum marobo tu ibana, ingkon lehononna do partobus singkat ni hosana, marguru tu pangarobo ni uhum tu ibana godangna.'),(10130,43,21,31,'Laos uhum bongka ondeng do bahenon tu ibana, molo anak manang boru ditambing jonggi i.'),(10131,43,21,32,'Alai molo hatoban baoa manang boruboru ditambing lombu i, tolupulu ringgit ma lehonon ni nampunasa tu induk somangnasida i, jala ingkon mate dangguron ia jonggi i.'),(10132,43,21,33,'Molo adong manang ise patalak sada godung, manang tung dihali sada godung, hape ndang dilangkopi, gabe madabu ma tusi sada lombu manang halode.'),(10133,43,21,34,'Ingkon abulon ni nampuna godung i do, hepeng ma pasuangonna tu nampunasa, alai di ibana ma ianggo bangkena.'),(10134,43,21,35,'Molo ditambing jonggi ni na sahalak jonggi ni donganna, pola mate, gadisonnasida ma jonggi na mangolu i, jala parduaonna tuhorna, jonggi na mate i pe parduaonnasida do.'),(10135,43,21,36,'Alai anggo binoto hian na sai laon panambing jonggi i, hape ndang diramoti nampunasa, ingkon alianna do jonggi abul ni jonggi, di ibana ma anggo na mate i.'),(10136,43,22,1,'Molo ditangko manang ise sada jonggi manang birubiru, laos dipotong manang digadis, ingkon uhum pangalimai ma gararonna ia jonggi, uhum pangopati gararonna ia birubiru.'),(10137,43,22,2,'Molo dapot tartangkup panangko i laho mangarut nian, gabe hona balbal ibana pola mate, ndang siabulon mudarna.'),(10138,43,22,3,'Alai molo naung sanga hatorangan ari panangko i, abulon ma mudarna. Ingkon gararon ni panangko i luhutna; molo so adong di ibana gadison ma ibana ala tinangkona i.'),(10139,43,22,4,'Molo dapot di tanganna na tinangko i laos mangolu, silompit ma gararonna sian lombu ro di halode dohot birubiru.'),(10140,43,22,5,'Molo dipagagathon manang ise pinahanna tu hauma manang tu porlak anggur ni na asing, tung dipalua songon i pinahanna tusi, ingkon na dumenggan ni haumana dohot porlak anggurna gararhononna.'),(10141,43,22,6,'Molo rarat api gabe dapotsa handang, laos dipangan bunbunan manang eme na jongjong dope, ingkon gararon ni na pagalak api i do.'),(10142,43,22,7,'Molo dipalumehon manang ise tu donganna hepengna manang barangbarangna, gabe diarut sian jabuna, silompit ma gararon ni panangko, molo tung dapot.'),(10143,43,22,8,'Alai molo so dapot panangko i, boanon ma nampuna bagas i tu jolo ni Debata, atik tung beha laos ibana paganjang tanganna tu ugasan ni donganna.'),(10144,43,22,9,'Taringot tu nasa hata jahajaha, ala jonggi, ala halode, ala birubiru, ala ulos, ala manang aha pe na mago, gabe didok na mandok: Ahu do nampuna on, tu jolo ni angka panguhum boanon hatanasida be, silompit dua ma gararon ni panjoha tu alona ginorahon ni panguhum.'),(10145,43,22,10,'Molo dipalumehon manang ise tu donganna sada halode manang jonggi, manang birubiru, manang pinahan dia pe, gabe mate ma i, manang mabugang, manang diolat halak, ndang adong na marnidasa.'),(10146,43,22,11,'Ingkon mangmang tu Jahowa ma uhum di tongatonganasida na dua, atik tung beha tanganna laho tu ugasan ni donganna; ingkon jaloon ni nampunasa do, jala ndang gararon ni alona i.'),(10147,43,22,12,'Alai molo tung ditangko panangko sian ibana, ingkon gararonna do i tu nampunasa.'),(10148,43,22,13,'Molo tung digotil binatang, ingkon boanonna do tandana bahen sitindangi, ndang gararonna anggo na ginotil i.'),(10149,43,22,14,'Molo diinjam sahalak sian donganna manang aha, gabe mabugang manang mate, ndang diida nampunasa, ingkon gararonna do i.'),(10150,43,22,15,'Alai molo tung diida nampunasa, ndang gararonna; molo tung paneoa ibana disi, seoana i do singkatna.'),(10151,43,22,16,'Molo didormai manang ise namarbaju na so oroan dope jala diangkupi modom, ingkon pintor lehononna bolina tusi jala buatonna bahen jolmana.'),(10152,43,22,17,'Alai molo manjua situtu amana mangalehon boruna tu ibana, timbangonna ma hepeng marguru tu godang ni boli ni namarbaju.'),(10153,43,22,18,'Ndang jadi pangoluon boruboru na marhapandean.'),(10154,43,22,19,'Ingkon bunuon do manang ise na pasaorsaor ibana modom tu pinahan.'),(10155,43,22,20,'Ingkon siaphononhon do manang ise na mamele debata sileban, nda holan tu Jahowa ibana mamele.'),(10156,43,22,21,'Ndang jadi sosahanmu halak dagang manang rupaonmu ibana; ai halak dagang do nang hamu na jolo di tano Misir.'),(10157,43,22,22,'Ndang jadi rupaon boruboru na mabalu manang anak lumanglumang.'),(10158,43,22,23,'Molo tung dirupa ho nasida, gabe mangangguhi nasida tu Ahu, sai na tangihononku do angguhangguknasida i.'),(10159,43,22,24,'Jadi marpiarpiar ma rimashu, jala bunuonku hamu marhitehite podang, gabe mabalu jolmamuna, gabe anak lumanglumang ia angka anakmuna.'),(10160,43,22,25,'Molo dipasali ho hepeng tu bangsongki, tu na pogos di lambungmi, unang ho songon halak sijalo bunga ni hepeng sian ibana, unang bahen marbunga hepengmu tu ibana.'),(10161,43,22,26,'Molo sindor dijalo ho ulos ni donganmu, ingkon paulahonmu tu ibana jumolo asa sundut mata ni ari.'),(10162,43,22,27,'Ai ulosna do bulusanna manangkas tu hulingkulingna, tung tudia ma ibana modom? Molo tung mangangguhi ibana tu Ahu, sai tangihononku di ibana, ai parasi ni roha do Ahu.'),(10163,43,22,28,'Ndang jadi buraanmu Debata, jala ndang jadi pulasanmu induk di bangsom.'),(10164,43,22,29,'Ndang jadi tiopanmu lobilobi ni emem manang anggurmu. Ingkon tu Ahu lehononmu anak buhabajum.'),(10165,43,22,30,'Suang songon i bahenonmu taringot tu lombum dohot angka birubirum. Pitu ari lelengna di lambung ni inana; di ari paualuhon i tu Ahu ma i lehononmu.'),(10166,43,22,31,'Ingkon halak na badia ma hamu di Ahu. Dibahen i ndang jadi panganonmuna juhut na ginotil ni binatang di ladang, ingkon tu jolo ni biang do timpalhononhon.'),(10167,43,23,1,'Ndang jadi pararatonmu barita gapgap, jala unang paherbang tanganmu rap dohot parjahat, gabe sitindangkon hageduhon.'),(10168,43,23,2,'Ndang jadi paihutonmu tahi torop laho manjahati, jala ndang jadi parpanguluanmu hata bada, manang manolopi hata torop laho papeolhon uhum.'),(10169,43,23,3,'Na pogos pe ndang jadi ampinanmu di pargulutanna.'),(10170,43,23,4,'Molo jumpang ho lombu ni musum, manang halodena lilu, ingkon mulak togihononmu tu ibana.'),(10171,43,23,5,'Molo diida ho halode ni halak na mangkagigihon ho mandate dibahen soratna, ndang jadi pasombuonmu peak, ingkon paheheonmu do i rap dohot binoanna.'),(10172,43,23,6,'Ndang jadi pailingonmu uhum tu halak na pogos hasolhotonmu di pargulutanna i.'),(10173,43,23,7,'Sai paholang ma ho sian hata gapgap, jala ndang jadi mate bunuonmu halak na lidang dohot na tigor, ai ndang olo Ahu mamintori halak parjahat.'),(10174,43,23,8,'Ndang jadi jaloonmu silehonlehon, ai papitung mata ni halak angka na tonggor hian nian anggo silehonlehon, jala dipapeol parhataan ni halak partigor.'),(10175,43,23,9,'Ndang jadi hilangonmu halak dagang, ai diboto hamu do roha ni halak dagang ala halak dagang nang hamu na jolo di tano Misir.'),(10176,43,23,10,'Onom taon padodot saburanmu tanomi, jala papunguonmu gogona.'),(10177,43,23,11,'Alai anggo papitutaonhon ingkon ulangonmu ma, jala patalunonmu, asa panganon ni angka na pogos sian bangsom, jala na tinebahonnasida jadi ma panganon ni binatang di ladang. Suang songon i do bahenonmu tu porlak anggurmu dohot tu hau jetunmu.'),(10178,43,23,12,'Onom ari ma lelengmu mangula ulaonmu, alai anggo di ari papituhon i ingkon maradian ma ho, asa maradian jonggim dohot halodem, maradian dohot anak ni hatobanmu dohot halak dagang pahosahon.'),(10179,43,23,13,'Sai radoti hamu ma sude na hupaboa i tu hamu. Ndang jadi paingotingotonmuna goar ni angka debata sileban, jala ndang jadi begeon sian pamanganmu.'),(10180,43,23,14,'Tolu hali di na sataon ho marari raya di Ahu.'),(10181,43,23,15,'Ingkon radotanmu ma ari raya ni sagusagu angka na so niasoman, pitu ari ma lelengna panganonmu sagusagu na so niasoman, songon naung hutonahon tu ho, pola hubuhu bulan Abib, ai di bulan i do ho ruar sian Misir. Alai ndang jadi idaonmu bohingkon so marisi pelean tanganmu.'),(10182,43,23,16,'Nang ari raya gotilon ni angka patumonaan ni na niulam, na sinaburmu di ladang. Nang ari raya hasisimpul ni suansuanan di ujung ni taon, ia dung dipapungu ho na niulam sian ladang.'),(10183,43,23,17,'Tolu hali di na sataon ingkon ro nasa baoa sian ho tu jolo ni Tuhan Jahowa.'),(10184,43,23,18,'Ndang jadi pelehononmu mudar ni pelean tu Ahu mardongan sagusagu na niasoman, jala ndang jadi peopon tu manogot tabotabo sian pelean ari raya tu Ahu.'),(10185,43,23,19,'Ingkon boanonmu do patumonaan sian tanomi masuk tu bagasan jabu ni Jahowa, Debatam. Ndang jadi lompaonmu anak ni hambingmu di bagasan aek ni susu ni inana.'),(10186,43,23,20,'Ida ma, sai suruonku do Suruan sada di jolom, asa diramoti ho di tongan dalan, jala ditogihon ho sahat tu inganan naung hupadiri di ho.'),(10187,43,23,21,'Sai jamoti ma dirim di adopanna, jala tangihon ma soarana, unang tung alo hamu ibana; ai ndang marpamuati rohana di angka pangalaosionmuna i, ai di bagasan dirina do Goarhu.'),(10188,43,23,22,'Alai molo ditangihon ho soarana sian nasa roham, jala dipatupa ho sude na hudok i, jadi Ahu mangalo angka alom, jala Ahu manosak angka na manosaknosak ho.'),(10189,43,23,23,'Molo mardalan Suruanki di jolom, jala ditogihon ho masuk tu tano ni halak Amori, Het i, Perisi, Kanaan, Hevi dohot Jebusi, jala Ahu mangaripashon nasida.'),(10190,43,23,24,'Ndang jadi ho marsomba tu debatanasida, jala ndang jadi oloanmu nasida, jala ndang jadi bahenonmu, songon na binahennasida i, ingkon rendephononmu situtu nasida, jala ropuhanmu situtu ganaganaan angka na pinajoknasida.'),(10191,43,23,25,'Alai ingkon oloanmuna do Jahowa Debatamuna i, jadi pasupasuonna ma sagusagum dohot tapianmu, jala pasalpuonku angka sahitsahit sian tongatongamuna.'),(10192,43,23,26,'Ndang jadi adong na hatektehan sanggul, nang na hol di tanomu; sai na gohanku do bilangan ni ariarim.'),(10193,43,23,27,'Sai suruonku do panonggotingku di jolom, jala godoranku do nasa bangso sitopotonmi, jala bahenonku do maporus nasa musum maradophon ho.'),(10194,43,23,28,'Jala suruonku ma angka uhum hamagoan di jolomi, asa dipabali halak Hevi, Kanaan dohot Het sian jolom.'),(10195,43,23,29,'Ndang di bagasan sataon nasida pabalionku sian jolom, asa unang gabe halongonan tano i, ganda maon dohot binatang di harangan pasarisarionmu.'),(10196,43,23,30,'Di laonlaon ni ari pe pabalionku nasida sian jolom, paima lam sopar ho, jala disoluk ho tano i.'),(10197,43,23,31,'Jala bahenonku ma parbalohan ni tanomu sian laut arung sahat ro di laut ni halak Palistim, jala sian halongonan sahat ro di batang aek i (Euprat). Ai pasahatonku do angka pangisi ni tano i tu tanganmuna, asa dipabali ho nasida sian jolom.'),(10198,43,23,32,'Ndang jadi angkupanmu nasida manang debatanasida marpadan.'),(10199,43,23,33,'Ndang jadi loasonmu nasida maringan di tanomu, asa unang mardosa ho dompak Ahu elaonnasida; ai molo tung oloanmu debatanasida i, sai na gabe jorgong do i di ho.'),(10200,43,24,1,'Dung i didok Jahowa ma tu si Musa: Nangkok ma hamu mandapothon Jahowa, ho dohot si Aron, Nadab dohot Abihu, ro di na pitupulu halak sintua ni Israel, mandaodao ma hamu marsomba Ahu.'),(10201,43,24,2,'Sasada si Musa pe jumonok mandapothon Jahowa, alai anggo nasida ndang jadi jumonok, bangso i pe ndang jadi nangkok rap dohot ibana.'),(10202,43,24,3,'Dung i ro ma si Musa, jala dipajojor tu bangso i saluhut hata ni Jahowa dohot sude angka uhum. Jadi sahata ma sandok bangso i mangalusi, didok nasida ma: Sude hata angka na pinaboa ni Jahowa, sai bahenonnami do.'),(10203,43,24,4,'Jadi disurathon si Musa ma sude hata ni Jahowa, jala dipasogot manogotna i, dipauli ma sada langgatan di bona ni dolok i dohot sampuludua tiang parningotan marguru sian na sampuludua marga ni Israel.'),(10204,43,24,5,'Dung i disuru ma angka dolidoli sian halak Israel, asa dipelehon nasida angka pelean situtungon dohot mamotongi lombu bahen angka pelean hamauliateon di Jahowa.'),(10205,43,24,6,'Dung i dibuat si Musa ma mudar i satonga, jala dibahen tu bagasan angka saoan; alai anggo mudar na satonganari, tu langgatan i do dipispishon.'),(10206,43,24,7,'Dung i dibuat ma pustaha parpadanan i, jala dijaha tu pinggol ni natorop i. Jadi didok nasida ma: Sude na nidok ni Jahowa, sai bahenonnami do, jala tangihononnami do.'),(10207,43,24,8,'Dung i dibuat si Musa ma mudar i, jala dipispishon tu na torop i, jala ninna ma: Ida ma, on ma mudar ni parpadanan i, naung pinahot ni Jahowa tu hamu ala saluhut hata ondeng.'),(10208,43,24,9,'Jadi nangkok ma si Musa dohot si Aron, si Nadab dohot Abihu ro di na pitupulu sintua ni halak Israel i.'),(10209,43,24,10,'Jadi diida nasida ma Debata ni Israel; ia sidegean ni patna, songon mata intan na rata riongna, jala songon rupa ni langit jumpa puasa bintang.'),(10210,43,24,11,'Jala ndang disurdukkon tanganna mangkahua angka sintua ni halak Israel. Jadi dung diida nasida Debata mangan dohot minum ma nasida.'),(10211,43,24,12,'Dung i ninna Jahowa ma tu si Musa: Nangkok ma ho mandapothon Ahu tu dolok on, disi ma ho, asa hulehon tu ho angka batu na marsurat dohot patik ro di uhum, angka naung husurati, sipodahononmu tu nasida.'),(10212,43,24,13,'Jadi hehe ma si Musa dohot si Josua parhaladona i, gabe nangkok ma si Musa tu dolok ni Debata.'),(10213,43,24,14,'Jala didok ma tu angka sintua i: Tading dison ma anggo hamu, paima mulak hami tu hamu. Antong si Aron dohot si Hur do di lambungmuna on; manang ise na marparkaro, tu nasida ma diboan.'),(10214,43,24,15,'Jadi dung sahat si Musa tu punsu ni dolok i, gabe dihungkupi ombun i ma dolok i.'),(10215,43,24,16,'Jadi mangulon ma hamuliaon ni Jahowa di punsu ni dolok Sinai, jala dihungkupi ombun i dolok i onom ari lelengna; tole papituarihon dijou ma si Musa sian bagasan ni ombun i.'),(10216,43,24,17,'Jala songon api na marjilam rupa ni hamuliaon ni Jahowa di punsu ni dolok i, diida halak Israel.'),(10217,43,24,18,'Jadi masuk ma si Musa tu bagasan ombun i laho nangkok tu dolok i gabe didolok i ma si Musa opatpulu ari opatpulu borngin lelengna.'),(10218,43,25,1,'Dung i ro ma hata ni Jahowa tu si Musa, ninna ma.'),(10219,43,25,2,'Paboa ma tu halak Israel mamboan tu Ahu silehonlehon, bahen pelean; sian ganup baoa na lomo roha, buatonmuna silehonlehon tu Ahu.'),(10220,43,25,3,'Alai on do silehonlehon, angka sibuatonmuna sian nasida: sere, perak dohot tombaga.'),(10221,43,25,4,'Tole abit sian bonang na rata langit dohot na rara dohot na merham, nang hain bontar na halus dohot imbulu ni hambing.'),(10222,43,25,5,'Tole angka bobak ni birubiru tunggal, angka na tinubar, tole bobak ni pitudar dohot hau pinasa.'),(10223,43,25,6,'Tole miak tu parpalitoan, miak na hushus pangkeon laho mamiahi dohot manutung daupa.'),(10224,43,25,7,'Tole angka mata intan simata unam dohot landus pangkeon tu baju epod dohot tu tutup andora.'),(10225,43,25,8,'Jala ingkon bahenonnasida di Ahu sada inganan na badia, asa maringan Ahu di tongatonganasida.'),(10226,43,25,9,'Sandok songon na hupatuduhon i tu ho, tiruan ni inganan dohot tiruan ni sude angka parhohasna, ingkon songon i bahenonmuna.'),(10227,43,25,10,'Alai bahenonnasida ma sada poti sian hau pinasa, tonga tolu asta unjurna, jala tonga dua asta bohangna, laos tonga dua asta ia timbona.'),(10228,43,25,11,'Ingkon lampisonmu ma i dohot sere na pita, sian bagasan dohot sian ruar lampisonmu, jala bahenonmu do bibirna marlaislais sere humaliang.'),(10229,43,25,12,'Jala tompaonmu ma tusi opat langgu sere, angka sibahenonmu tu suhina na opat i; dua langgu di na sambariba be.'),(10230,43,25,13,'Jala bahen ma haluhalu sian hau pinasa, ipe lampisonmu do i dohot sere.'),(10231,43,25,14,'Jala surakkon ma haluhalu i tu bagasan langgu, angka na lohot di poti i laho mangusung poti i.'),(10232,43,25,15,'Alai ingkon pasombuon do angka haluhalu i di bagasan angka langgu ni poti i, ndang jadi eneton sian i.'),(10233,43,25,16,'Jala pamasuhonmu tu bagasan poti i surat parjanjian, silehononku tu ho.'),(10234,43,25,17,'Ingkon bahenonmu huhut sada langkop pardengganan sian sere na pita; tonga tolu asta unjurna, jala tonga dua asta bohangna.'),(10235,43,25,18,'Ingkon bahenonmu huhut dua kerubim sian sere sangkimpal na tinopa, bahenonmu do i tu na dua tampahan ni langkop pardengganan i.'),(10236,43,25,19,'Jala bahen ma kerubim na sada tu tampahan na sada jala kerubim na sada nari tu tampahanna na sada nari; tubu sian bagasan langkop pardengganan i tolpus tu duru bahenonmuna angka kerubim i di na dua tampahanna.'),(10237,43,25,20,'Jala paherbangon ni angka kerubim habongna na dua i sian ginjang asa dihungkupi habongna i langkop pardengganan i, jala padompak be bohinasida, dompak langkop pardengganan i bohi ni angka kerubim mamereng.'),(10238,43,25,21,'Dung i bahenonmu ma langkop pardengganan i tu ginjang ni poti i, jala pamasuhonmu ma tu bagasan poti i surat parjanjian silehononku tu ho.'),(10239,43,25,22,'Jadi disi ma topotonku ho, asa mangkatai Ahu dohot ho sian ginjang ni langkop pardengganan i sian holangholang ni kerubim na dua i, angka na di ginjang ni poti parpadanan i taringot tu sude sitonahononku tu ho, paboaonmu tu halak Israel.'),(10240,43,25,23,'Ingkon bahenonmu dohot sada meja sian hau pinasa; dua asta unjurna, jala saasta bohangna, jala tonga dua asta timbona.'),(10241,43,25,24,'Jala lampisonmu ma i dohot sere na pita, jala bahenonmu martopi sere humaliang.'),(10242,43,25,25,'Jala bahenonmu tusi sada lais santopap bidangna humaliang, jala bahen laisna i martopi sere pinariponipon humaliang.'),(10243,43,25,26,'Jala bahen tusi opat langgu sere, sibahenonmu tu suhina na opat i, angka na di patna na opat i.'),(10244,43,25,27,'Ingkon jonok di toru ni lais bahenon angka langgu i bahen panuruhan ni haluhalu laho mangusung meja i.'),(10245,43,25,28,'Sian hau pinasa ma bahenonmu haluhalu i, jala lampisonmu dohot sere, i ma pangkeon mangusung meja i.'),(10246,43,25,29,'Jala bahenonmu ma pingganna dohot saoanna dohot hondina dohot mangkukna, bahen pangusean ni tuak tangkasan ma i; sian sere na pita ma i paulionmu.'),(10247,43,25,30,'Jala ingkon peakkononmu tongtong di meja i sagusagu na marhadohoan maradophon Ahu.'),(10248,43,25,31,'Ingkon bahenonmu huhut sada parpalitoan sian sere na pita, sangkimpal parpalitoan i topaonmu, ro di badanna, dangkana dohot butuhana, hansingna dohot bungana bahenon sian i.'),(10249,43,25,32,'Jala ingkon onom dangka ruar sian lambungna, tolu dangka sian na sambariba ni parpalitoan i, jala tolu dangka sian na sambariba nari.'),(10250,43,25,33,'Martolu butuha, songon bunga ni buapala ganup dangkana i bahenon, mardongan hansing dohot bunga; laos songon i do bahenon tu na onom dangka i, angka na ruar sian parpalitoan i.'),(10251,43,25,34,'Alai ingkon opat do butuha ni parpalitoan i bahenon, songon bunga ni buapala mardongan hansing dohot bunga.'),(10252,43,25,35,'Jala sahansing be di toru ni na dua dangkana, angka na ruar sian i; suang songon i sahansing be di toru ni dangkana na dua, angka na ruar sian i; suang muse sahansing be di toru ni dangkana na dua, angka na ruar sian i, gabe onom dangka ruar sian badan ni parpalitoan i.'),(10253,43,25,36,'Ai ingkon ruar sian i do angka hansingna dohot dangkana; luhutna i sian sangkimpal sere na pita do i.'),(10254,43,25,37,'Jala bahenonmu do pitu palitona, angka sibahenon tu ginjang, asa marsinondang angka i tu jolo.'),(10255,43,25,38,'Panurturna pe dohot pangintopina sian sere na pita do.'),(10256,43,25,39,'Sian satalenta sere na pita bahenonmu luhutna i, ro di parhohasna sudena i.'),(10257,43,25,40,'Jala ingot ma mambahensa tudos tu tiruan na niidam di dolok i.'),(10258,43,26,1,'Ia undungundung i ingkon bahenonmu do sian sampulu rimberimbe, sian bonang na nidua, jala na rata langit, jala na rara jala na merham, margombaran kerubim bahenonmu sitonunhonon ni parpunsa.'),(10259,43,26,2,'Ia unjur ni sada rimberimbe ingkon duapulu ualu asta do, ia bidangna opat asta; sisada suhat do saluhut angka rimberimbe i.'),(10260,43,26,3,'Limalima ma angka rimberimbe i padomuon, nasada tu donganna.'),(10261,43,26,4,'Jala bahenonmu do angka tali sangkutan na rata tu bibir ni rimberimbe parjolo i apala di pardomuanna, laos songon i do bahenonmu di bibir ni rimberimbe paduahon i bahen pardomuan paduahon.'),(10262,43,26,5,'Ingkon limapulu tali sangkutan bahenonmu tu rimberimbe na sada, jala limapulu tali sangkutan bahenonmu di ujung ni rimberimbe i, apala di pardomuanna paduahon; ingkon masijaloan do angka tali sangkutan, na sada tu donganna.'),(10263,43,26,6,'Jala bahenonmu do limapulu haithait sere laho patunggomhon angka rimberimbe i, na sada tu donganna, asa manjadi undungundung sada.'),(10264,43,26,7,'Bahenonmu ma huhut rimberimbe sian imbulu ni hambing bahen langitlangit di atas ni undungundung i; sampulu sada rimberimbe sisongon i bahenonmu.'),(10265,43,26,8,'Ia unjur ni sada rimberimbe tolupulu asta do, jala bidangna opat asta; sisada suhat do rimberimbe na sampulu sada i.'),(10266,43,26,9,'Jala padomuonmu pulik na lima rimberimbe, laos songon i pulik na onom rimberimbe, jala lompitonmu rimberimbe paonomhon, tongon di jolo ni undungundung i.'),(10267,43,26,10,'Jala bahenonmu limapulu tali sangkutan di bibir ni rimberimbe na sada parduru apala di pardomuanna, jala limapulu tali sangkutan di bibir ni rimberimbe na sada nari, di pardomuanna paduahon.'),(10268,43,26,11,'Jala bahenonmu do limapulu haithait tombaga, jala haithononhon ma angka haithait i tu angka tali sangkutan i laho padomuhon undungundung i gabe sada.'),(10269,43,26,12,'Alai anggo lobilobi ni rimberimbe sian langitlangit ni undungundung i, na satonga lobilobi ni rimberimbe i, laos gantung di pudi ni undungundung i.'),(10270,43,26,13,'Jala saasta di na sambariba, jala saasta di na sambariba nari sian lobilobi di unjur ni angka rimberimbe ni undungundung i; ingkon duansa gantung di lambung ni undungundung i bahen pangondingina angkup be di lambung.'),(10271,43,26,14,'Jala bahenonmu do di atas langitlangit ni undungundung i salampis sian bobak ni birubiru tunggal na tinubar, di atas ni muse salampis sian bobak ni pitudar.'),(10272,43,26,15,'Bahenonmu ma huhut angka papan sian hau pinasa bahen dorpi ni undungundung i, angka sipajongjongon.'),(10273,43,26,16,'Sampulu asta ganjang ni papan i, jala tonga dua asta bidang ni sada papan.'),(10274,43,26,17,'Ingkon mardua pasi ganup papan, baris be tu donganna; ingkon songon i ma bahenonmu saluhut papan angka na tu undungundung i.'),(10275,43,26,18,'Jala songon on do bahenonmu papan tu undungundung i: Ingkon duapulu papan sambariba tungkan dangsina.'),(10276,43,26,19,'Alai ingkon bahenonmu do opatpulu ojahan perak di toru ni na duapulu papan i, dua be ojahan di toru ni sada papan, marguru tu pasina na dua i.'),(10277,43,26,20,'Di na sambariba nari ni undungundung i pe, tungkan utara laos bahenon do duapulu papan.'),(10278,43,26,21,'Nang na opatpulu ojahanna perak, dua be ojahan di toru ni sada papan.'),(10279,43,26,22,'Alai di tampahan ni undungundung tungkan hasundutan, onom papan bahenonmu.'),(10280,43,26,23,'Jala dua papan bahenonmu di na dua suhi ni undungundung, di tampahanna parpudi.'),(10281,43,26,24,'Ingkon dua lampis ma i sian toru mardomu di ginjang marhitehite sadu langgu, songon i ma duansa di na dua suhi i.'),(10282,43,26,25,'Jadi ualu papan disi mardongan ojahanna perak sampulu onom toropna, dua be di toru ni sada papan.'),(10283,43,26,26,'Jala bahenonmu do angka ransangna sian hau pinasa, lima tu angka papan di na sambariba ni undungundung i.'),(10284,43,26,27,'Jala lima ransang tu angka papan di na sambariba nari ni undungundung i, jala lima ransang tu angka papan ni undungundung di tampahan parpudi ni tungkan hasundutan.'),(10285,43,26,28,'Jala ransang partonga na di dos tonga ni angka papan, ingkon torus sian suhi na sada tu suhi na sada nari.'),(10286,43,26,29,'Jala lampisonmu do angka papan i dohot sere, sabongna pe sian sere do bahenonmu, asa tusi pinabongot angka ransang; angka ransang i pe lampisonmu dohot sere.'),(10287,43,26,30,'Dung i pajongjongonmu ma undungundung i tudos tu tiruan na niidam di dolok i.'),(10288,43,26,31,'Dung i bahenonmu ma sada rimberimbe sian bonang na rata langit, jala na tinubar, jala na merham dohot sian hain na halus na nidua, margombaran kerubim bahenonmu sitonunhonon ni parpunsa tusi.'),(10289,43,26,32,'Dung i hirehononmu ma i tu opat tiang sian hau pinasa angka na nilampis dohot sere, jala marhaithait sere, jongjong di atas opat ojahan perak.'),(10290,43,26,33,'Alai bahenonmu do rimberimbe i tu toru ni haithait. Dung i pamasuk hamu ma tu bagasan rimberimbe i poti parpadanan, gabe rimberimbe i ma parholangan di hamu, di holangholang ni na badia dohot na songkal sahali.'),(10291,43,26,34,'Ingkon langkophononmu ma langkop pardengganan i tu atas poti parpadanan di bagasan na songkal sahali.'),(10292,43,26,35,'Alai anggo meja i tu balian ni rimberimbe bahenonmu, jala tondong ni meja i parpalitoan i di na sambariba ni undungundung i tungkan dangsina, asa tungkan utara ma bahenonmu meja i.'),(10293,43,26,36,'Bahenonmu ma huhut sada rimberimbe tutup ni pintu ni undungundung i sian bonang na rata langit dohot na tinubar dohot na merham dohot sian bonang na bontar na nidua sitonunon ni parpunsa.'),(10294,43,26,37,'Bahenonmu ma dohot lima tiang sian hau pinasa tu rimberimbe i, angka silampisonmu dohot sere jala marhaithait sere; tumpaonna ma huhut lima ojahanna sian tombaga.'),(10295,43,27,1,'Dung i bahenonmu ma sada langgatan sian hau pinasa, lima asta unjurna jala lima asta bohangna, asa maropat suhi langgatan i jala tolu asta timbona.'),(10296,43,27,2,'Martanduk ma bahenonmu di suhina na opat i; songon na tubu sian i angka tandukna jala lampisonmu do i dohot tombaga.'),(10297,43,27,3,'Bahenonmu ma dohot angka parsirabunanna dohot siuksiukna dohot mangkuk pamispisan dohot garpuna dohot parapiapianna; sian tombaga ma paulionmu saluhut ulaulana i.'),(10298,43,27,4,'Bahenonmu ma huhut sada handanghandangna sian tombaga marpangalaho songon na niangit, jala ingkon bahenonmu huhut tu jala i opat langgu sian tombaga tu suhina na opat i.'),(10299,43,27,5,'Ingkon bahenonmu do handanghandang i tu toru ni sidegedegean humaliang langgatan i sahat ro di parsitongaan ni langgatan i.'),(10300,43,27,6,'Dung i bahenonmu ma angka haluhalu tu langgatan i, haluhalu sian hau pinasa angka silampisonmu dohot tombaga.'),(10301,43,27,7,'Dung i pabongotonmu ma angka haluhalu i tu bagasan angka langgu, gabe marhaluhalu ma langgatan i angkup be laho mangusung.'),(10302,43,27,8,'Rumar di bagasan bahenonmu langgatan i sian angka papan, songon naung pinatuduhon ni Jahowa tu ho di dolok, songon i do bahenonmu.'),(10303,43,27,9,'Ingkon bahenonmu ma dohot sada alaman ni undungundung i di na sambariba tungkan dangsina, marrimberimbe sian bonang na bontar na nidua, saratus asta ganjangna di na sambariba.'),(10304,43,27,10,'Jala duapulu tiangna jala duapulu ojahanna sian tombaga, ianggo haithait ni angka tiang ro di ransangransangna perak do.'),(10305,43,27,11,'Songon i dohot di na sambariba tungkan utara ingkon marrimberimbe do, saratus asta unjurna jala duapulu tiangna jala ojahanna na duapulu i sian tombaga alai anggo haithait ni tiang i ro di ransangransangna perak do.'),(10306,43,27,12,'Alai anggo bohang ni alaman i tungkan hasundutan marrimberimbe silimapulu asta be ganjangna, sampulu tiangna jala sampulu ojahanna.'),(10307,43,27,13,'Ia bohang ni alaman di na sambariba tungkan habinsaran laos limapulu asta do.'),(10308,43,27,14,'Jadi sampulu lima asta rimberimbe di na sambariba haroroan i, tolu tiangna jala tolu ojahanna.'),(10309,43,27,15,'Di na sambariba nari pe laos sampulu lima asta rimberimbe i, tolu tiangna jala tolu ojahanna.'),(10310,43,27,16,'Alai di pintu tungkan alaman pe, ingkon adong do sada rimberimbe, duapulu asta ganjangna sian bonang na rata langit na rara tinubar dohot na merham dohot sian hain na halus na nidua sitonunhonon ni parpunsa tusi mardongan opat tiang dohot opat pat ojahanna.'),(10311,43,27,17,'Luhut angka tiang ni alaman humaliang ingkon mardomu do marhite sian ransangransang perak dohot haithait sian perak, alai anggo patna ingkon sian tombaga do.'),(10312,43,27,18,'Ia unjur ni alaman i ingkon saratus asta do, bohangna limapulu asta, timbona lima asta, paulion sian bonang na nidua, alai anggo pat ni tiang i ingkon tombaga do.'),(10313,43,27,19,'Luhut ulaula angka na pinangke di undungundung i mangaradoti ulaon marragamragam, ro di sude angka labangna dohot labang tu alaman i, ingkon sian tombaga do.'),(10314,43,27,20,'Alai anggo ho dohononmu huhut halak Israel asa diboan be tu ho miak jetun na niduda jala na tinapis bahen miak palito asa tongtong galak palito i.'),(10315,43,27,21,'Ingkon di bagasan undungundung parluhutan, di ruar ni rimberimbe, na mangolat poti parpadanan i pasangon ni Aron dohot angka anakna palito i olat ni bodari sahat ro di manogotna i di adopan ni Jahowa. I ma aturan na hot salelenglelengna di halak Israel ro di pinomparnasida i.'),(10316,43,28,1,'Dung i dohononmu ma Aron hahami, rap dohot angka anakna ruar sian tongatonga ni halak Israel ro jumonok tu ho, asa ibana mangaradoti tohonan malim di Ahu, i ma Aron dohot angka anakna: Nadab, Abihu, si Eleasar dohot si Itamar.'),(10317,43,28,2,'Dung i bahenonmu ma pangkean angka na badia di Aron hahami bahen hamuliaon dohot pajagarhon.'),(10318,43,28,3,'Ingkon dohononmu ma tu sude halak angka na pistar marroha, angka naung Ahu manggohi rohana dohot tondi hapistaran, asa nasida paulihon angka pangkean di Aron laho pabadiahon ibana, asa ibana gabe malim di Ahu.'),(10319,43,28,4,'Alai on do pangkean angka sipaulionnasida: sada tutup andora, sada baju epod, sada baju parbalian, sada baju parbagasan na niangit, sada tumpal dohot sada hohos; ingkon nasida paulihon pangkean angka na badia di Aron hahami dohot di angka anakna, asa ibana gabe malim di Ahu.'),(10320,43,28,5,'Ingkon buatonnasida sere dohot bonang na rata langit, na tinubar jala na merham dohot hain na halus.'),(10321,43,28,6,'Ia baju epod i ingkon paulionnasida do sian sere dohot sian bonang na rata langit, na tinubar jala na merham dohot sian hain na halus na nidua sitonunhonon ni parpunsa.'),(10322,43,28,7,'Ingkon mardua pangabaraan epod i bahenonna sipatunggomon di tampahanna be.'),(10323,43,28,8,'Hohos ni epod i pe ingkon sisada hapandean do dohot baju i jala mardomu tusi, paulion sian sere dohot sian bonang na rata langit, na tinubar jala na merham dohot sian hain na halus na nidua.'),(10324,43,28,9,'Dung i ingkon buatonmu ma dua mata intan Unam jala uhirhononmu tusi goargoar ni anak ni Israel na sampuludua i.'),(10325,43,28,10,'Onom goarnasida di mata intan na sada, jala goar na onom nari tu mata intan paduahon mangihuthon partubunasida.'),(10326,43,28,11,'Songon panguhirhon ni halak pandakdak batu, angka na manguhir tintin sahap panguhironna di goar ni angka anak ni Israel tu mata intan na dua i, jala marbibir sere humaliang.'),(10327,43,28,12,'Dung i bahenonmu ma na dua mata intan i tu na dua pangabaraan ni epod i bahen batu parningotan di angka anak ni Israel, asa usungon ni Aron di atas abarana na dua i goargoarnasida di adopan ni Jahowa bahen parningotan.'),(10328,43,28,13,'Ingkon bahenonmu huhut angka haithait sere.'),(10329,43,28,14,'Paulionmu muse dua rante sian sere na pita, dos ganjangna, ingkon songon tali na nidua hapandeanna masijaloan jala haithononhon tu haithait i.'),(10330,43,28,15,'Angkup ni bahenonmu ma sada tutup andora paruhuman, suang songon hapandean ni baju epod bahenonmu tonunhonon ni parpunsa tusi sere, bonang na rata langit, na tinubar jala na merham dohot hain na halus na nidua.'),(10331,43,28,16,'Ingkon maropat suhi do i bahenon, jala dua lampis, sanjongkal ginjangna jala sanjongkal bidangna.'),(10332,43,28,17,'Jala ingkon gohanmu do i dohot opat rinte batu intan. Di rinte parjolo: Batu intan sardis, topasion, samarados.'),(10333,43,28,18,'Di rinte paduahon: Batu intan rubin, sapire, diaman.'),(10334,43,28,19,'Di rinte patoluhon: Batu intan opal, akat, ametist.'),(10335,43,28,20,'Di rinte paopathon: Batu intan krisolit, Unam dohot jaspis; jala ingkon marihot sere angka rintena i bahenon.'),(10336,43,28,21,'Jala ingkon jojor angka batu i mangihuthon partording ni na sampulu dua goar ni anak ni Israel, angka siuhirhonon ni halak pandakdak batu, pulik be goarna i mangihuthon na sampuludua marga ni Israel.'),(10337,43,28,22,'Ingkon bahenonmu huhut tu tutup andora angka rante dos ganjangna, songon tali na nidua hapandeanna sian sere na pita.'),(10338,43,28,23,'Bahenonmu huhut tu tutup andora i dua tintin sere jala bahenonmu na dua tintin i tu na dua tampahan ni tutup andora i.'),(10339,43,28,24,'Ingkon bahenonmu na dua rante na nidua sian sere tu bagasan tintin na dua i angka na di tampahan ni tutup andora i.'),(10340,43,28,25,'Alai anggo na dua ujung ni na dua rante na nidua i ingkon haithononmu do i tu na dua haithait jala patunggomonmu do i tu pangabaraan ni baju epod patondong be.'),(10341,43,28,26,'Ingkon bahenonmu muse dua tintin sere na asing jala lohothononmu do i tu na dua tampahan ni tutup andora, apala di pangkilimanna na tungkan baju epod i dingkan bagasan.'),(10342,43,28,27,'Bahenonmu muse dua tintin sere na asing jala lohothononmu do i tu na dua pangabaraan ni baju epod tungkan toru tongon di pardomuanna, di atas hohos ni baju epod.'),(10343,43,28,28,'Ingkon pudunhononhon muse tutup andora i rap dohot tintinna marhite sian bonang na rata langit tu tintin ni baju epod, asa hopos tu hohos ni baju epod jala unang tarbahen sirang tutup andora sian baju epod.'),(10344,43,28,29,'Jadi songon i ma usungon ni Aron goargoar ni anak ni Israel di atas parateateanna di bagasan tutup andora ni paruhuman, molo laho masuk ibana tu bagasan inganan na badia i bahen parningotan tongtong di adopan ni Jahowa.'),(10345,43,28,30,'Dung i bahenonmu ma tu bagasan tutup andora paruhuman: Urim dohot Tummim (hatiuron dohot hasintongan), asa di atas parateatean ni Aron do i laho masuk ibana tu adopan ni Jahowa; ingkon songon i tongtong paboanboanon ni Aron uhum ni halak Israel di atas parateateanna di adopan ni Jahowa.'),(10346,43,28,31,'Ingkon bahenonmu huhut baju parbalian di atas baju epod, sandok sian bonang na rata langit do i ditonun.'),(10347,43,28,32,'Jala parrungkunganna i di parsitongaanna, ingkon martopi na tinonun do i humaliang, songon topi ni baju partahanan, asa unang olo maribak.'),(10348,43,28,33,'Jala bahenonmu do tu rambuna humaliang angka parbue ni dalimo sian bonang na rata langit, na tinubar jala na merham, bahenon huhut marsolotan angka giringgiring sere humaliang.'),(10349,43,28,34,'Sai marsorinsorin do sada giringgiring sere dung i sada parbue ni dalimo humaliang di rambu ni baju parbalian i.'),(10350,43,28,35,'Ingkon solukkonon ni Aron bajubaju i laho marulaon ibana, asa tarbege soara ni giringgiring, molo masuk ibana tu bagasan inganan na badia i di adopan ni Jahowa dohot molo ruar ibana muse asa unang mate ibana.'),(10351,43,28,36,'Ingkon bahenonmu huhut sada sangkalia sian sere na pita, jala uhirhononmu tu bagasan songon panguhiron sahap: Habadiaon di Jahowa!'),(10352,43,28,37,'Jala palohotonmu do i marhite sian tali bonang na rata langit tu tumpal i, asa lohot di jolo ni tumpal ma i.'),(10353,43,28,38,'Asa di pardompahan ni Aron ma sangkalia i, asa Aron mamboan pardengganan ni nasa ugasan na badia, angka naung pinarbadiaan ni halak Israel marhite sian peleannasida, ingkon tongtong sangkalia i di pardompahanna, asa jumpangan asi ni roha halak Israel di adopan ni Jahowa.'),(10354,43,28,39,'Alai anggo baju parbagasan i ingkon paulionmu do i sian hain na halus, bahenonmu muse sada tumpal sian hain na halus; alai anggo hohosna ingkon angiton ni parpunsa do i.'),(10355,43,28,40,'Di angka anak ni Aron pe ingkon paulionmu do angka baju parbagasan, tole angka hohos dohot tumpal bahen hamuliaon dohot pajagarhon.'),(10356,43,28,41,'Ingkon solukkononmu do i tu Aron hahami dohot tu angka anakna, dung i miahanmu ma nasida jala gohanmu tangannasida pabadiahon nasida, asa nasida gabe malim di Ahu.'),(10357,43,28,42,'Angkupni bahen ma di nasida saraoal sian hain na bontar manghungkupi dagingna unang manangkas, olat ni ulu ni tot sahat ro di gonting ganjangna.'),(10358,43,28,43,'Ingkon pangkeon ni si Aron dohot angka anakna saraoal i laho masuk nasida tu undungundung parluhutan i, nang molo jumonok nasida tu langgatan laho marhalado di inganan na badia i, asa unang mangonai torutoru tu nasida gabe ingkon mate nasida. I ma aturan na hot salelenglelengna di halak Israel ro di pinomparnasida i.'),(10359,43,29,1,'Jala on do parturena bahenonmu tu nasida laho pasahathon tohonan tu nasida mangaradoti hamalimon di Ahu: Ingkon buatonmu sada jonggi naposo dohot dua birubiru tunggal na so marlindang.'),(10360,43,29,2,'Tole muse roti na so niasoman dohot sagusagu na so niasoman na marsaor miak dohot juada na so niasoman na marsapu miak; sian itak na lamot bahenonmu luhutna i.'),(10361,43,29,3,'Dung i bahenonmu ma angka i tu bagasan sada hirang, laos di bagasan hirang do angka i pelehononmu rap dohot jonggi naposo i dohot na dua birubiru tunggal i.'),(10362,43,29,4,'Dung i dohononmu ma si Aron dohot angka anakna ro jumonok tu pintu ni undungundung parluhutan i, laos didionmu ma nasida dohot aek.'),(10363,43,29,5,'Dung i buatonmu ma angka pangkean i jala solukkononmu tu Aron, baju parbagasan, baju parbalian, baju epod dohot tutup andora, jala ingkon hohosanmu do ibana dohot hohos ni baju epod.'),(10364,43,29,6,'Dung i bahenonmu ma tumpal i tu atas uluna, jala lohothononmu sangkalia habadiaon tu tumpal i.'),(10365,43,29,7,'Dung i buatonmu ma miak pamiahion, jala usehononmu tu atas uluna asa mamiahi ibana.'),(10366,43,29,8,'Dokkononmu ma huhut angka anakna ro jumonok, jala solukkononmu tu nasida baju parbagasan i.'),(10367,43,29,9,'Jala ingkon hohoshononmu tu nasida hohos i, tu Aron dohot tu angka anakna i, jala palohotonmu tumpal i tu ulunasida, i ma tanda paboa nasida mangaradoti hamalimon ro di salelenglelengna; ingkon laos gohanmu ma tangan ni Aron dohot angka anakna i.'),(10368,43,29,10,'Dung i dohononmu ma mamboan jonggi na poso i jumonok tu adopan ni undungundung parluhutan i, asa diampehon Aron dohot anakna i tangannasida tu atas ulu ni jonggi i.'),(10369,43,29,11,'Dung i seatonmu ma jonggi i di adopan ni Jahowa mardonokkon pintu ni undungundung parluhutan i.'),(10370,43,29,12,'Dung i buatonmu ma deba sian mudar ni jonggi i jala daishononmu do i marhite sian jarijarim tu angka tanduk ni langgatan i; alai anggo sandok mudar na tinggal i usehononmu do i tu pantar ni langgatan i.'),(10371,43,29,13,'Dung i buatonmu ma nasa pultahan panaputi ni raoanna dohot hirehire na ampe tu ateate dohot tu pia na dua i ro di pultahan na lohot tusi, ingkon laos tutungonmu ma i bahen daupa di atas langgatan i.'),(10372,43,29,14,'Alai anggo sibuk ni jonggi i, hulingkulingna dohot napana ingkon suda do i tutungonmu dohot api di balian ni parsaroan i, i ma gabe pelean pardosaon.'),(10373,43,29,15,'Dung i buatonmu ma sada birubiru tunggal, jala dohononmu si Aron dohot angka anakna mangampehon tangannasida tu atas ulu ni birubiru tunggal i.'),(10374,43,29,16,'Dung i seatonmu ma birubiru tunggal i jala buatonmu mudarna, laos pispishononmu ma i tu langgatan i humaliang.'),(10375,43,29,17,'Dung i saheanmu ma birubiru tunggal i, jala burionmu raoanna dohot patna, jala patoponmu ma i tu na margoarna dohot tu uluna.'),(10376,43,29,18,'Jala tutungonmu di atas langgatan sandok birubiru tunggal i bahen daupa; pelean situtungon ma i di Jahowa, uap na hushus ni pelean api tu Jahowa ma i.'),(10377,43,29,19,'Dung i buatonmu ma birubiru tunggal na sada nari, jala ia dung diampehon Aron dohot angka anakna i tangannasida tu atas ulu ni birubiru tunggal i.'),(10378,43,29,20,'Seatonmu ma birubiru tunggal i, jala buatonmu deba sian mudarna jala bahenonmu ma i tu pinggol ni Aron dohot tu pinggol siamun ni angka anakna, dohot tu ina ni tanganna siamun, dohot tu ina ni patna siamun; dung i pispishononmu ma mudar i tu langgatan i humaliang.'),(10379,43,29,21,'Dung i buatonmu ma deba sian mudar na di atas langgatan i dohot sian miak pamiahion i, jala pispishononmu ma i tu Aron dohot tu angka pangkeanna, tu angka anakna dohot tu angka pangkean ni anakna, asa badia Aron dohot angka pangkeanna, badia dohot angka anakna ro di pangkean ni angka anakna rap dohot ibana.'),(10380,43,29,22,'Dung i buatonmu ma pultahan sian birubiru tunggal i dohot ihurna ro di pultahan panaputi ni raoanna, nang hirehire na ampe tu ateate dohot pia na dua i rap dohot pultahan na lohot tusi ro di hae jolo dingkan siamun; ai birubiru tunggal sahatsahat ni tohonan ma i.'),(10381,43,29,23,'Angkupni buatonmu ma sangkimpal roti dohot sada sagusagu na marsaor miak dohot sada timpatimpa sian bagasan hirang isian roti na so niasoman, na di adopan ni Jahowa.'),(10382,43,29,24,'Jala ampehononmu ma luhutna i tu tangan ni Aron dohot tu tangan ni angka anakna i jala tatingtatingonmu ma i bahen pelean tatingan di adopan ni Jahowa.'),(10383,43,29,25,'Dung i buatonmu ma i sian tangannasida, jala tutungonmu bahen daupa di atas langgatan dongan ni pelean situtungon bahen uap na hushus di adopan ni Jahowa, bahen pelean api tu Jahowa ma i.'),(10384,43,29,26,'Dung i buatonmu ma andora sian birubiru tunggal sahatsahat ni tohonan tu Aron jala tatingtatingonmu ma i bahen pelean tatingan di adopan ni Jahowa, ingkon i ma gabe parjambaranmu.'),(10385,43,29,27,'Ingkon songon i urasonmu andora pelean tatingan dohot hae jolo bahen pelean tatingan, na tinating jala na hinindat sian birubiru sahatsahat ni tohonan na di Aron dohot di angka anakna i.'),(10386,43,29,28,'Ingkon manjadi uhum na hot ma i di Aron dohot di angka anakna ro di salelenglelengna turpukna sian halak Israel, ai pelean tatingan do on; ingkon pelehonon ni halak Israel do pelean tatingan i sian angka pelean hamauliateon, pinelehonnasida i bahen pelean tatingan di Jahowa.'),(10387,43,29,29,'Ia pangkean ni Aron angka na badia i, ingkon sahat tu angka anakna ma i dung salpu ibana, pangkeonnasida ma i uju laho mamiahi nasida pasahathon tohonan i tu nasida.'),(10388,43,29,30,'Ingkon pitu ari lelengna pangkeonna pangkean i manang ise sian angka anak ni Aron bangkit malim singkatna, asa jadi ibana masuk tu undungundung parluhutan i laho marhalado di inganan na badia i.'),(10389,43,29,31,'Ingkon buatonmu ma birubiru tunggal sahatsahat ni tohonan i, jala lompaonmu juhutna di bagasan inganan na badia i.'),(10390,43,29,32,'Dung i ingkon panganon ni Aron dohot angka anakna i ma juhut ni birubiru tunggal i rap dohot roti angka na di bagasan hirang i mardonokkon pintu ni undungundung parluhutan i.'),(10391,43,29,33,'Asa ingkon laos panganonnasida do pelean na mambahen pardengganan di nasida laho pasahathon tohonan tu nasida dohot pabadiahon dirinasida i; alai na so tupa anggo halak ruar manganhon sian i, ai na badia do i.'),(10392,43,29,34,'Molo tung adong na lobi sian juhut sahatsahat ni tohonan i manang sian roti sahat ro di manogotna i, ingkon tutungonmu do na lobi i di bagasan api; ndang jadi panganon ni manang ise, ai na badia do i.'),(10393,43,29,35,'Ingkon songon i do bahenonmu tu Aron dohot tu angka anakna i, sandok songon na hutonahon i tu ho: Ingkon pitu ari lelengna pasahatonmu tohonan i tu nasida.'),(10394,43,29,36,'Angkup ni ingkon ganup ari seaton sada lombu bahen pelean pardosaon pasimpul pardengganan i; ingkon urasonmu huhut langgatan pameleanna marhite sian pelean pardengganan pinelehonmu di atasna, dung i miahanmu ma langgatan i laho pabadiahonsa.'),(10395,43,29,37,'Ingkon pitu ari lelengna pelehononmu pelean pardengganan di langgatan i pabadiahonsa, asa tung manjadi na badia sahali; manang ise ma naeng jumonok tu langgatan i, ingkon na badia do.'),(10396,43,29,38,'Alai ingkon on do pelehononmu di atas langgatan i: dua anak ni birubiru sisataon be tongtong ganup ari.'),(10397,43,29,39,'Sada birubiru pelehononmu manogotna i, jala birubiru na sada nari pelehononmu marhira bodari.'),(10398,43,29,40,'Jala dongan ni anak ni birubiru parjolo i bahenonmu sasolup itak marsaor miak na tinutu arga sabale; angkup ni sapansung tuak anggur bahen pelean siinumon.'),(10399,43,29,41,'Ia anak ni birubiru na sada nari sipelehononmu marhira bot ari, suang songon pamelehonmi di pelean sipanganon dohot siinumon manogotna i pamelehonmi disi bahen uap na hushus dohot pelean situtungon di Jahowa.'),(10400,43,29,42,'I ma pelean situtungon na tongtong di pinomparmuna mardonokkon pintu ni undungundung parluhutan i di adopan ni Jahowa, tusi do huhut ro Ahu monopot hamu asa mangkatai ahu dohot ho disi.'),(10401,43,29,43,'Tusi ma Ahu ro marluhut rap dohot halak Israel jala hamuliaonki pabadiahon inganan i.'),(10402,43,29,44,'Ahu huhut pabadiahon undungundung parluhutan i rap dohot langgatan i; Ahu huhut pabadiahon si Aron dohot angka anakna gabe malim di Ahu.'),(10403,43,29,45,'Ahu huhut maringan di tongatonga ni halak Israel, asa Ahu gabe Debatanasida.'),(10404,43,29,46,'Asa tung diboto nasida, Ahu do Jahowa, Debatanasida i, naung manogihon nasida ruar sian tano Misir, asa maringan Ahu di tongatonganasida; Ahu, Jahowa, Debatanasida i.'),(10405,43,30,1,'Ingkon paulionmu huhut sada langgatan panutungan daupa; ingkon sian hau pinasa paulionmu i.'),(10406,43,30,2,'Saasta unjurna jala saasta bohangna, maropatsuhi tahe, jala dua asta timbona; ingkon ruar sian i huhut angka tandukna.'),(10407,43,30,3,'Jala ingkon lampisonmu do i dohot sere na pita, nang tutupna, nang dorpina humaliang dohot angka tandukna; ingkon bahenonmu huhut marbibir humaliang sian sere na pita.'),(10408,43,30,4,'Ingkon bahenonmu muse dua langgu sere di toru ni bibirna i; ingkon bahenonmu do i tahe di suhina na dua i angkup be, bahen panuruhan ni haluhalu, asa dapot usungon ni halak langgatan i.'),(10409,43,30,5,'Angka haluhalu i pe ingkon bahenonmu do sian hau pinasa jala lampisonmu dohot sere.'),(10410,43,30,6,'Jala pajongjongonmu do langgatan i di adopan ni rimberimbe, na mangolat poti panindangion i, di adopan ni langkop pardengganan i na di atas ni panindangion i, asa tusi Ahu ro marluhut rap dohot ho.'),(10411,43,30,7,'Ingkon manutung daupa angka na hushus di atasna Aron ganup sogot, ganup mangisi palito i ibana ingkon manutung daupa ibana.'),(10412,43,30,8,'Nang laho mamasang palito i Aron mareak bot ari, ingkon tongtong manutung daupa ibana di adopan ni Jahowa paingotingoton nang pinomparmuna.'),(10413,43,30,9,'Na so tupa hamu mamelehon di atasna daupa sileban, manang pelean situtungon, manang pelean sipanganon, manang mangusehon pelean siinumon tusi.'),(10414,43,30,10,'Ingkon Aron mambahen pardengganan di atas tandukna sahali sataon marhite sian mudar ni pelean pardosaon; i ma tutu ingkon sahali sataon ibana mambahen pardengganan di atasna paingotingoton ni pinomparmuna; ai na badia sahali do ibana di Jahowa.'),(10415,43,30,11,'Angkup ni didok Jahowa tu si Musa songon on:'),(10416,43,30,12,'Molo dibilangi ho torop ni sandok halak Israel mangihuthon bilangannasida i, ingkon ganup nasida mangalehon partobus ni hosana di Jahowa uju na binilangan nasida, asa unang songgop tu nasida torutoru uju na binilangan nasida.'),(10417,43,30,13,'On do tama lehonon ni ganup na masuk tu bilangan: satonga sekkel lehononna mangihuthon sekkel di inganan na badia; sasekkel mararga duapulu gera; na satonga sekkel i ingkon gabe pelean tatingan di Jahowa.'),(10418,43,30,14,'Manang ise naung binilangan olat ni na marumur duapulu taon tu ginjang, ingkon lehononna do pelean tatingan tu Jahowa.'),(10419,43,30,15,'Halak na mora ndang jadi gandaanna, jala halak na pogos ndang jadi oruanna mangalehon sisatonga sekkel i bahen pelean tatingan tu Jahowa bahen partobus ni hosanasida i.'),(10420,43,30,16,'Ingkon buatonmu perak partobus i sian halak Israel, asa lehononmu muse pature ulaon di undungundung parluhutan i, bahen parningotan di halak Israel di adopan ni Jahowa bahen pardengganan di tondimuna i.'),(10421,43,30,17,'Dung i ro muse ma hata ni Jahowa tu Musa songon on:'),(10422,43,30,18,'Ingkon bahenonmu huhut sada tong tombaga, ojahanna pe ingkon tombaga do bahen parburian, jala bahenonmu do i di holangholang ni undungundung parluhutan i dohot langgatan, jala bahenonmu do aek tu bagasan.'),(10423,43,30,19,'Asa anggo Aron dohot angka anakna i ingkon aek sian i buatonna mamburi tangan dohot patnasida.'),(10424,43,30,20,'Molo masuk nasida tu bagasan undungundung parluhutan i, ingkon jolo marburi nasida, asa unang mate nasida; suang songon i molo jumonok nasida tu langgatan i naeng marhalado laho manutung pelean situtungon bahen daupa tu Jahowa.'),(10425,43,30,21,'Ingkon jolo burianna tanganna dohot patna, asa unang mate nasida. I ma gabe uhum na hot di nasida ro di saleleng ni lelengna paingotingoton ni pinomparnasida huhut marsundutsundut.'),(10426,43,30,22,'Dung i ro muse hata ni Jahowa tu Musa songon on:'),(10427,43,30,23,'Alai anggo ho, buat ma di ho pamurpuri na dumenggan i, i ma: Pultak ni haminjon lima ratus sekkel boratna dohot ulumamis satongana, i ma duaratus limapulu sekkel dohot hasior laos dua ratus limapulu sekkel boratna.'),(10428,43,30,24,'Tole muse hau teja lima ratus sekkel mangihuthon sekkel di inganan na badia i dohot miak sian hau Jetun sabale.'),(10429,43,30,25,'Ingkon bahenonmu sian i miak pamiahion na badia, miak pulungpulungan na sinaor mangihuthon hapandean ni pande daon, miak pamiahion na badia ma i.'),(10430,43,30,26,'Ingkon miakkononmu ma i tu undungundung parluhutan i dohot tu poti parpadanan i.'),(10431,43,30,27,'Nang meja i ro di ulaulana sudena, tole parpalitoan rap dohot ulaulana, nang langgatan pardaupaan.'),(10432,43,30,28,'Tole muse langgatan pamelean ni pelean situtungon ro di ulaulana sudena dohot tong parburian ro di ojahanna.'),(10433,43,30,29,'Ingkon songon i bahenonmu pabadiahon angka i, asa gabe sun badia, ganup na manjama angka i ingkon gabe badia do.'),(10434,43,30,30,'Aron pe ro di angka anakna ingkon miahanmu do jala urasonmu nasida gabe malim di Ahu.'),(10435,43,30,31,'Dung i dohononmu ma tu halak Israel, songon on: Ingkon miak pamiahion na badia maon di Ahu ingoton ni pinomparmuna.'),(10436,43,30,32,'Ndang jadi usehononhon miak i tu daging ni manang ise jolma hian, parmasakna pe ndang jadi pasumanonmu, ai miak na badia do i, dibahen i ingkon badia do i di hamu.'),(10437,43,30,33,'Manang ise na pasuman parmasakna, manang tung diusehon tu halak sileban ingkon siap ibana bahenon sian bangsona.'),(10438,43,30,34,'Dung i ro muse hata ni Jahowa tu Musa: Buat ma di ho pamurpuri angka na hushus, gota ni haminjon, bungalawang, meang dohot daupa mamolin dos be do boratna.'),(10439,43,30,35,'Dung i bahenonmu ma sian i bahen miak daupa mangihuthon hapandean ni pande daon, saor ansim, pita dohot badia.'),(10440,43,30,36,'Dung i tutuonmu ma sian i mansai lamot jala bahenonmu sian i tu adopan ni poti parpadanan i di bagasan undungundung parluhutan i, asa ro Ahu manopot ho tusi; i ma na badia sahali di hamu.'),(10441,43,30,37,'Ia daupa sibahenonmu i, ndang jadi tiruonmu parmasakna di hamu sandiri; ingkon badia do i di ho lehonon tu Jahowa.'),(10442,43,30,38,'Manang ise na manuman parmasakna, asa paanggoanggoonna, ingkon siap do ibana bahenon sian bangsona.'),(10443,43,31,1,'Dung i ro muse ma hata ni Jahowa tu si Musa songon on:'),(10444,43,31,2,'Ida ma, nunga hujou sahalak na margoar Bezaleel, anak ni si Uri, i ma anak ni si Hur sian marga Juda.'),(10445,43,31,3,'Nunga pola hugoki rohana dohot Tondi ni Debata marhapistaran, marhapandean dohot marparbinotoan di bagasan saluhut ulaon i.'),(10446,43,31,4,'Mamingkiri hapandean na sumurung mamandei sere, perak dohot tombaga.'),(10447,43,31,5,'Huhut manguhir dohot mamijor batu intan, mamandei hau pe mambahen hapandean marragamragam.'),(10448,43,31,6,'Ianggo Ahu, ida ma nunga pola hubahen donganna, i ma Oholiab, anak ni si Ahisamakh, sian marga Dan; tu bagasan roha ni saluhut halak na pande, nunga hulehon roha hapandean, asa nasida mangulahon sude na hutonahon tu ho:'),(10449,43,31,7,'Tole undungundung parluhutan i, tole poti parpadanan i, ro di langkop pardengganan na di atasna i dohot sude ulaula di undungundung i.'),(10450,43,31,8,'Tole muse meja i ro di ulaulana, tole parpalitoan sian sere na pita ro di ulaulana sudena dohot langgatan panutungan daupa.'),(10451,43,31,9,'Tole langgatan pamelean ni pelean situtungon ro di ulaulana sudena, nang tong parburian dohot ojahanna.'),(10452,43,31,10,'Tole angka pangkean tohonan dohot pangkean angka na badia di malim Aron, tole pangkean ni angka anakna laho mangaradoti hamalimon.'),(10453,43,31,11,'Tole muse miak pamiahion i dohot daupa sian pamurpuri na hushus situtungon di inganan na badia; hombar tu sandok na hutonahon i tu ho bahenonnasida.'),(10454,43,31,12,'Dung i ro muse ma hata ni Jahowa tu si Musa songon on:'),(10455,43,31,13,'Dok ma tu halak Israel: Ingkon ingotonmuna do ari paradiananku: Ai i do tanda ni parpadanan di Ahu dohot di hamu ro di pinomparmuna, asa tung diboto hamu, Ahu do Jahowa na pabadiahon hamu.'),(10456,43,31,14,'Dibahen i sai ingot hamu ma ari Sabbat i, ai na badia do i di hamu; manang ise na mangaramuni ari Sabbat i, ingkon marutang hosa ibana: Ai manang ise na mangula ulaon disi, ingkon siap bahenon hosana sian tongatonga ni bangsona.'),(10457,43,31,15,'Ingkon onom ari lelengna mangula halak; alai anggo ari papituhon i, ari Sabbat paradianan na badia ma i di Jahowa; manang ise na mangula ulaon di ari Sabbat, ingkon marutang hosa ibana.'),(10458,43,31,16,'Dibahen i ingkon ingoton ni halak Israel ari Sabbat i, nang pinomparnasida laho pabadiahon ari Sabbat i, padan na hot ma i ro di salelenglelengna.'),(10459,43,31,17,'Ari Sabbat i ma tutu manjadi tanda di Ahu dohot di halak Israel ro di salelenglelengna. Ai di bagasan na onom ari ditompa Jahowa langit dohot tano on; alai anggo di ari papituhon i maradian ma Ibana jala dipasonang dirina.'),(10460,43,31,18,'Jadi dung sun Jahowa mangkatai dohot si Musa di atas dolok Sinai, dilehon ma tu ibana dua lei batu panuratan ni panindangion naung tarsurat marhite sian jarijari ni Debata.'),(10461,43,32,1,'Alai dung diida bangso i lelengna, asa tuat Musa sian dolok i, marluhut ma bangso i manopot Aron, ninna ma mandok ibana: Hehe ma ho, bahen ma di hami Debata, angka na manguluhon hami; ai ndang huboto na marhua hami manang si Musa on, baoa na manogihon hami ruar sian tano Misir.'),(10462,43,32,2,'Dung i didok Aron ma mandok nasida: Tanggali hamu ma antinganting sere, angka na di pinggol ni jolmamuna, anakmuna dohot borumuna, jala pamboan hamu ma angka i tu ahu.'),(10463,43,32,3,'Jadi sandok bangso i masitanggal antingantingna sere, angka na di pinggolnasida be, jala dipamboan do i tu Aron.'),(10464,43,32,4,'Dung i dijalo ibana ma angka i sian tangannasida, laos disinga ma rupana dohot panurat, i ma disuman mambahen sada lombu tuangan. Dung i didok nasida ma: On ma Debatamu, ale Israel! Naung manogihon ho ruar sian tano Misir.'),(10465,43,32,5,'Jadi dung diida Aron lombu i laos dipajok ma sada langgatan di adopanna, jala martingting ma Aron mandok: Marsogot ma ari raya di Jahowa.'),(10466,43,32,6,'Dung i dipasogot nasida ma manogotna i hehe, laos dipelehon ma angka pelean situtungon, diboan huhut angka pelean hamauliateon, dung i hundul be ma bangso i marmanganan dohot marminuman, hehe muse nasida laho manontori.'),(10467,43,32,7,'Dung i ro ma hata ni Jahowa tu Musa: Laho ma ho tuat! Ai nunga marroha mago bangsomi, na tinogihonmu ruar sian tano Misir.'),(10468,43,32,8,'Tibuna i nasida manimbil sian dalan naung hupatuduhon tu nasida! Nunga dibahen nasida di nasida sada lombu tuangan, nunga pola marsomba dohot mamele nasida tusi, jala nunga pola didok: On ma Debatamu, ale Israel! Angka naung manogihon ho ruar sian tano Misir.'),(10469,43,32,9,'Dung i ro muse hata ni Jahowa tu Musa: Nunga mananda rohangku di bangso on, jala ida ma, tung na tangkang do roha ni bangso on.'),(10470,43,32,10,'Nuaeng pe, loas ma Ahu, asa marsigorgor rimashu mangonai tu nasida jala huripashon nasida, asa ho hubahen gabe bangso na bolon.'),(10471,43,32,11,'Alai marhuraja ma anggo Musa di adopan ni Jahowa, Debatana, ninna ma: Boasa ma tung marsigorgor rimasmu, ale Jahowa mida bangsom naung tinogihonmu ruar sian tano Misir marhite sian hagogoon godang dohot marhite sian tangan na marhuaso?'),(10472,43,32,12,'Boasa ma tung dohonon ni halak Misir songon on: Mambahen nasida marhinamago, umbahen ditogihon nasida ruar, asa bunuonna nasida di dolok, jala ripashononna nasida sian atas tano on? Marpangulahi ma roham sian parpiarpiar ni rimasmu, sai marpamuati ma roham taringot tu jea na naeng pasonggoponmu tu bangsom.'),(10473,43,32,13,'Sai marningot ma roham di Abraham, di Isak dohot di Jakkob, angka naposomi, nunga pola maruari Ho tu dirim marbagahon tu nasida: Ahu patorophon sogot pinomparmuna, songon bintang angka na di langit, jala sandok tano on naung huparbagabaga i, ingkon lehononku do i tu pinomparmuna, bahen partalianna ro di salelenglelengna.'),(10474,43,32,14,'Jadi marpamuati ma roha ni Jahowa taringot tu hinamago naung nilumbahonna naeng bahenonna tu bangsona.'),(10475,43,32,15,'Dung i humusor ma Musa jala tuat sian dolok i huhut patioptiop di bagasan tanganna na dua lei panindangion i, angka na tarsurat angkup be di bulungna na dua i.'),(10476,43,32,16,'Jadijadian ni Debata do lei na dua i, surat i pe, surat ni Debata sandiri do i, na niuhirhonna tu lei na dua i.'),(10477,43,32,17,'Jadi dung dibege Josua soara ni bangso i marsuraksurak, ninna ma mandok Musa: Soara ni halak na tongtang do i di bagasan parsaroan i.'),(10478,43,32,18,'Alai ninna Musa ma: Ndada soara ni suraksurak boaboa ni hamonangan manang hataluan, soara ni halak na marende taripar do i anggo na hubege.'),(10479,43,32,19,'Alai dung jonok ibana tu parsaroan i, jala diida lombu i dohot tortor marhaliang, marsigorgor situtu ma rimas ni Musa, laos ditimpalhon ma sian tanganna lei na dua i, gabe mabola do i di bonandolok i.'),(10480,43,32,20,'Dung i dibuat ma lombu naung pinaulinasida i, jala ditutung di bagasan api, laos diduda mansai lamot, jala disirsirhon tu aek, jala didokkon halak Israel minum sian i.'),(10481,43,32,21,'Dung i ninna Musa ma mandok Aron: Aha huroha na binahen ni bangso on tu ho, umbahen paroonmu dosa na sai balga tu atasna?'),(10482,43,32,22,'Dung i ninna Aron ma: Unang tung marsigorgor rimas ni Tuanku tu ahu! Ai ditanda ho do roa ni bangso on marharohaon.'),(10483,43,32,23,'Angka didok nasida do mandok ahu: Bahen di hami debata, angka na manguluhon hami, ai ndang huboto hami manang na marhua si Musa on, baoa na manogihon hami ruar sian tano Misir.'),(10484,43,32,24,'Jadi ningku ma tutu mandok nasida: Manang ise na marsere, ditanggali ma i, jala dilehon tu ahu, dung i hutimpalhon ma i tu bagasan api, gabe ruar ma lombu on.'),(10485,43,32,25,'Jadi dung diida Musa naung gabe hijang bangso i, ai nunga dipahijong si Aron bahen haurahon maradophon halak alonasida i.'),(10486,43,32,26,'Jadi jongjong ma Musa di bahal ni parsaroan, laos ninna ma: Ro be ma tu ahu manang ise na mangalehon dirina tu Jahowa! Jadi marpungu ma tu lambungna saluhut halak Lepi.'),(10487,43,32,27,'Dung i ninna Musa ma tu nasida: Songon on do hata ni Jahowa, Debata ni Israel: Dibahen be ma podangna tu gontingna, dung i ulahulak hamu na sian harbangan on tu harbangan adui di parsaroan i, ingkon dibunu be ma ganup donganna dohot alealena dohot tondongna.'),(10488,43,32,28,'Jadi dibahen halak Lepi ma hombar tu hata ni si Musa, gabe marragean ma sian bangso i di na sadari i arga tolu ribu halak.'),(10489,43,32,29,'Ai nunga didok si Musa hian: Ingkon urasonmuna dirimuna sadari on di Jahowa! Ingkon ganup mangalo anakna, nang donganna, asa maruli pasupasu hamu sadari on sian Jahowa.'),(10490,43,32,30,'Tole manogotna i, didok Musa ma mandok bangso i: Mansai balga do dosa na binahenmuna i! On pe nangkok ma anggo ahu manopot Jahowa, anggiat tung dapot ahu pardengganan taringot tu dosamuna i.'),(10491,43,32,31,'Jadi dung mulak si Musa tu lambung ni Jahowa, ninna ma: Iale isuang! Nunga mardosa godang bangso on, ala naung binahennasida di nasida angka debata sian sere.'),(10492,43,32,32,'Nuaeng pe, sai marpamuati ma roham mida dosanasida i, alai molo so tarbahen, haru sesa nama dohot goarhu sian pustaha naung sinuratmi.'),(10493,43,32,33,'Alai ro ma hata ni Jahowa tu Musa: Manang ise naung mardosa dompak Ahu, goar ni do sesaonku sian pustahangku.'),(10494,43,32,34,'On pe laho ma ho, togihon ma bangso i tu tano naung hupaboa i tu ho; ida ma, ingkon Suruanki mardalan manguluhon ho! Alai ia dung jumpang ari pangaluluhonku, luluhononku ma tu nasida dosanasida i.'),(10495,43,32,35,'Jadi ditorui Jahowa ma bangso i ala naung binahennasida lombu i na pinauli ni Aron.'),(10496,43,33,1,'Dung i ro muse hata ni Jahowa tu Musa: Laho ma ho, borhat sian on, ho dohot bangso naung tinogihonmu ruar sian tano Misir laho tu tano naung pola huuarihon tu Abraham, Isak dohot Jakkob, uju hudok: Tu pinomparmu tano i lehononku.'),(10497,43,33,2,'Husuru pe Suruanku manguluhon ho, hupabali pe anggo halak Kanaan, Amori, Het i, Perisi, Hevi dohot Jebusi:'),(10498,43,33,3,'Ibana pe manogihon hamu tu tano habaoran ni susu dohot situak ni loba; ndang tarbahen anggo Ahu nangkok di tongatongamuna, ai bangso na tangkang marroha do ho, atik tung siap ho maon bahenonku di tongan dalan.'),(10499,43,33,4,'Jadi dung dibege bangso i hata na horas i, gondok ma rohanasida, jala ndang adong nanggo sahalak na mamangke pangkeanna hian.'),(10500,43,33,5,'Alai nunga didok Jahowa hian hatana anggo tu Musa, patolhasonna tu halak Israel: Bangso na tangkang marroha do hamu, nanggo satongkin Ahu nangkok di tongatongamuna, ingkon siaphononku do hamu. On pe ditanggali be ma pangkeanna sian ibana, asa huboto manang aha sibahenonku tu ho.'),(10501,43,33,6,'Jadi ditanggali halak Israel ma pangkeannasida be mandaodao sian dolok Horeb.'),(10502,43,33,7,'Alai anggo Musa dibuat ma undungundung i, jala dipajongjong ma i di balian holang sian parsaroan i, jala digoar ma i undungundung parluhutan. Dung i manang ise na naeng mangalului Jahowa, ingkon ruar Ibana tu undungundung parluhutan i na di balian ni parsaroan i.'),(10503,43,33,8,'Jadi molo jumpa ruar Musa laho manopot undungundung i, hehe be sandok bangso i, laos jongjong ganup di pintu ni undungundungna be, dituluthon nasida ma Musa sian pudi, paima masuk ibana tu bagasan undungundung i.'),(10504,43,33,9,'Ia dung masuk Musa tu bagasan undungundung i, tuat ma ombun na marsijongjong i, laos jongjong di bagasan pintu ni undungundung, jala mangkatai Jahowa dohot Musa.'),(10505,43,33,10,'Jadi sandok bangso i marnida ombun na marsijongjong i jongjong di bagasan pintu ni undungundung i, gabe hehe be ma sandok bangso i, laos marsomba nasida ganup di pintu ni undungundungna be.'),(10506,43,33,11,'Dung i mangkatai ma Jahowa dohot Musa adop bohi be, songon sada baoa na mangkatai dohot alealena; dung i mulak ma Musa tu parsaroan i, alai anggo parhaladona Josua, anak ni Nun dolidoli i, ndang morot sian tongatonga ni undungundung i.'),(10507,43,33,12,'Dung i ninna Musa ma tu Jahowa: Ida ma, nunga didok Ho mandok ahu: Togihon ma bangso on nangkok, alai ndang dipabotohon Ho tu ahu manang ise na naeng suruhononmu donganku, nunga nian didok Ho: Ahu mananda ho pola ro di goar! Jala nunga jumpangan asi ni roha ho maradophon Ahu.'),(10508,43,33,13,'Nuaeng pe, molo tutu naung jumpangan asi ni roha ahu maradophon Ho, ua pabotohon ma tu ahu dalanmu, asa tung tangkas Ho hutanda, paboa naung jumpangan asi ni roha ahu maradophon Ho; jala ida ma parripem do bangso on.'),(10509,43,33,14,'Dung i ninna Jahowa ma: Ingkon Bohingku donganmu mardalan, asa hutogihon ho tu hasonangan.'),(10510,43,33,15,'Alai ninna Musa ma tu Jahowa: Molo so tongtong Bohimi manguluhon hami, unang nian togihon hami nangkok sian on.'),(10511,43,33,16,'Ai tung marhite sian aha ma dapot tandaon, paboa naung jumpangan asi ni roha ahu maradophon Ho, ahu dohot parripem on? Nda marhite sian na nidongananmu hami mardalan, paboa marhasurungan ahu dohot parripem on maradophon nasa bangso angka na di atas tano on?'),(10512,43,33,17,'Dung i ninna Jahowa ma tu Musa: Nang hata on na nidokmu ondeng, bahenonku do i, ala naung jumpangan asi ni roha ho maradophon Ahu, jala Ahu mananda ho pola ro di goar.'),(10513,43,33,18,'Dung i ninna Musa ma: Ua pataridahon ma tu ahu hamuliaonmi!'),(10514,43,33,19,'Alai ninna Jahowa ma: Ahu pabolushon saluhut hatuaonku sian jolo ni bohim, jala Ahu manjouhon Goar ni Jahowa di adopanmu; na martalup ni roha do Ahu sogot di halak naung tinalupan ni rohangku; jala na marasi ni roha do Ahu sogot di halak naung niasian ni rohangku.'),(10515,43,33,20,'Angkup ni didok Jahowa: Ndang tarbahen ho anggo tung marnida apala Bohingkon, ai ndang tarbahen manang ise jolma hian marnida Ahu, laos mangolu ibana.'),(10516,43,33,21,'Angkup ni didok Jahowa: Ida ma, adong sada hajongjongan mardonokkon Ahu, ingkon disi ho jongjong di atas dolok batu!'),(10517,43,33,22,'Jadi dung mamolus hamuliaonku, bahenonku ma ho tu bagasan sada rigat ni dolok batu i, laos hungkuphononku tanganku tu ho paima salpu Ahu mamolus.'),(10518,43,33,23,'Ia dung hupatait tanganku, idaonmu ma Ahu sian pudi; alai anggo apala Bohingkon ndang jadi idaon ni manang ise!'),(10519,43,34,1,'Dung i ninna Jahowa ma tu Musa: Dakdak ma di ho dua lei batu angka na suman tu parjolo i, asa Ahu manurathon tusi hata angka naung tarsurat di lei parjolo i, angka binolahonmi.'),(10520,43,34,2,'Ingkon hobas ma ho sogot manogot i, asa sogot ho nangkok tu dolok Sinai, laos jongjong disi ho di adopanku, di punsu ni dolok i.'),(10521,43,34,3,'Jala ndang jadi nanggo sahalak nangkok rap dohot ho, jala ndang jadi tarida nanggo sahalak di atas ni sandok dolok i; birubiru pe manang lombu ndang jadi manjampal mardonokkon dolok on.'),(10522,43,34,4,'Dung i didakdak Musa ma dua lei batu, na suman tu parjolo i, jala dipasogot ibana manogotna i hehe asa nangkok tu dolok Sinai, songon na tinonahon ni Jahowa tu ibana, laos diboan ma na dua lei batu di bagasan tanganna.'),(10523,43,34,5,'Jadi tuat ma Jahowa di bagasan sada ombun, laos jongjong Ibana disi di lambungna, huhut joujou marhitehite Goar ni Jahowa.'),(10524,43,34,6,'Jadi mamolus ma Jahowa sian adopan ni Musa huhut Ibana joujou: Jahowa, Jahowa do Debata na talup jala na asi roha, lambat tarrimas, jala godang denggan basana dohot habonaronna.'),(10525,43,34,7,'Na pahothon denggan basana i pola marriburibu sundut jala marpamuati rohana mida hajahaton, pangalaosion dohot dosa, alai ndang loasonna malua sian uhum anggo halak parsala, sai na luluhononna do hajahaton ni amaama tu angka anakna dohot pahompuna ro di sundut patoluhon dohot paopathon.'),(10526,43,34,8,'Jadi hapogan ma Musa manungkap tu tano laos marsomba.'),(10527,43,34,9,'Huhut mandok: Molo sapala jumpangan asi ni roha ahu maradophon Ho, ale Tuhan, ua sauduran ma Tuhan i mardalan di tongatonganami! Ai bangso na tangkang marroha do bangso on, alai sai marpamuati ma roham di hajahatonnami dohot di dosanami, sai bahen ma hami parjambaranmu.'),(10528,43,34,10,'Dung i ninna Jahowa ma: Ida ma, Ahu mambahen padan di adopan ni sandok bangso on; Ahu mambahen halongangan, angka na so dung masa di sandok tano on dohot di tongatonga ni saluhut angka bangso parbegu, ingkon sandok bangso donganmi marnida jadijadian ni Jahowa, ai na songkal sahali do i na naeng bahenonku tu ho.'),(10529,43,34,11,'Antong pajop ma di bagasan roham na hutonahon i tu ho sadari on. Ida ma, Ahu pe pabalihon maradophon ho halak Amori, Kanaan, Het i, Perisi, Hevi dohot Jebusi.'),(10530,43,34,12,'Sai jaga ma dirim, na so tupa marpadanpadan ho tu halak isi ni tano sisoluhonmu, asa unang gabe jorgong nasida di tongatongami.'),(10531,43,34,13,'Ingkon lohaonmuna do langgatannasida i, ingkon segaanmuna ajiajiannasida, jala tabaonmuna sombaonnasida i.'),(10532,43,34,14,'Ai ndang jadi ho marsomba tu Debata sileban; ai targoar do Jahowa Parlulu, ala Debata Parlulu do Ibana.'),(10533,43,34,15,'Na so tupa bahenonmu padan tu halak isi ni tano i, ia marmainan nasida mangihuthon debatanasida i, manang mamele nasida tu debatanasida i, gabe diela ho asa panganonmu sian pelean na pinelehonna i.'),(10534,43,34,16,'Ndang jadi buatonmu sian borunasida bahen jolma ni anakmu, atik tung marmainan borunasida maon mangihuthon debatanasida i, gabe tarela dohot anakmu maon marmainan mangihuthon debatanasida.'),(10535,43,34,17,'Ndang jadi bahenonmu di ho angka debata tuangan.'),(10536,43,34,18,'Ingkon tongtong ho marari raya sagusagu na so niasoman. Ingkon pitu ari lelengna panganonmu sagusagu na so niasoman, songon na hutonahon i tu ho di tingki naung binuhul bulan Abib; ai di bulan Abib do ho ruar sian Misir.'),(10537,43,34,19,'Nasa buhabaju Ahu do nampunasa, nang nasa buhabaju tunggal sian pinahanmu, nang lombu, nang birubiru.'),(10538,43,34,20,'Alai anggo buhabaju ni halode ingkon tobusonmu do i marhite sian birubiru; alai molo so ditobus ho ingkon ponggolhononmu do rungkungna. Nasa buhabaju ni angka anakmu ingkon tobusonmu do i. Alai ndang jadi idaonmu Bohingkon so marisi pelean tanganmu.'),(10539,43,34,21,'Ingkon onom ari lelengmu mangula, alai anggo di ari papituhon i ingkon maradian ho; di tingki masa ni paninggalaon pe nang di gotilon, ingkon maradian ho.'),(10540,43,34,22,'Ari raya sipitu borngin i pe ingkon ingotonmu do, boanonmu ma disi patumonaan ni na niulam; tole muse ari raya hasisimpul ni suansuanan di ujung ni taon.'),(10541,43,34,23,'Tolu hali di na sataon, ingkon ro nasa baoa sian ho tu adopan ni Tuhan Jahowa, Debata ni Israel.'),(10542,43,34,24,'Ia dung hupabali angka bangso parbegu maradophon ho, jala hupalambas tano ingananmu, ndang jadi nanggo sahalak mangkaliangi tanom saleleng ho nangkok naeng marnida Bohi ni Jahowa Debatam sitolu hali i di bagasan sataon.'),(10543,43,34,25,'Ndang jadi durushononmu mudar ni pelean tu Ahu mardongan sagusagu na niasoman; jala ndang jadi marborngin peopon tu manogot anggo pelean di ari raya Paska.'),(10544,43,34,26,'Ingkon taruhononmu do patumonaan sian tanomi tu jabu ni Jahowa, Debatam. Ndang jadi lompaonmu anak ni hambingmu di bagasan aek ni susu ni inana.'),(10545,43,34,27,'Dung i ninna Jahowa ma tu Musa: Surathon ma angka hata on, ai marguru tu angka hata on do padan na hubahen i tu ho dohot tu Israel.'),(10546,43,34,28,'Jadi sai disi ma Musa rap dohot Jahowa pola opatpulu ari opatpulu borngin lelengna, ndang mangan sagusagu ibana jala ndang minum aek. Dung i disurathon Ibana ma sude hata ni padan i, hata na sampulu i tu lei batu.'),(10547,43,34,29,'Jadi di na tuat i Musa sian dolok Sinai, huhut patioptiop na dua lei panindangion di bagasan tanganna di na tuat i ibana sian dolok i, alai ndang diboto Musa naung marsinondang sisik ni bohina i, ala naung mangkatai ibana dohot Jahowa.'),(10548,43,34,30,'Jadi dibereng Aron dohot sude halak Israel ma Musa, hape ida ma, nunga marsinondang sisik ni bohina, gabe mabiar nasida ro jumonok tu ibana.'),(10549,43,34,31,'Jadi dijou Musa ma nasida, gabe mulak nasida tu ibana, Aron dohot sude induk ni luhutan i, laos mangkatai ma Musa dohot nasida.'),(10550,43,34,32,'Jadi dung songon i ro ma jumonok sude halak Israel; gabe dipatolhas Musa ma tu nasida sude hata na pinaboa ni Jahowa tu ibana di dolok Sinai.'),(10551,43,34,33,'Jadi dung tolhas hata i dihatahon Musa tu nasida, dibahen ma sada pangkuphupi tu bohina.'),(10552,43,34,34,'Alai molo masuk muse Musa tu adopan ni Jahowa naeng mangkatai dohot Ibana dipasisi ma pangkuphupi i paima ruar ibana muse. Jadi dung ruar ibana dihatahon ma tu halak Israel hata na tinonahon tu ibana.'),(10553,43,34,35,'Jadi diida halak Israel ma bohi ni Musa, pola marsinondang sisik ni bohi ni Musa, gabe dipasuang Musa ma pangkuphupi i tu bohina, paima masuk ibana muse mangkatai dohot Jahowa.'),(10554,43,35,1,'Dung i dipaluhut Musa ma sandok luhutan ni halak Israel, jala ninna ma mandok nasida: On do hata angka na tinonahon ni Jahowa siulahononmuna.'),(10555,43,35,2,'Ingkon onom ari lelengmu mangula ulaon, alai anggo ari papituhon i, ari Sabbat paradianan na badia ma i di Jahowa. Manang ise na mangula ulaon di ari na sasada i, ingkon marutang hosa ibana.'),(10556,43,35,3,'Ndang jadi hamu mangopan api di bagasan sude ingananmuna anggo di ari Sabbat i.'),(10557,43,35,4,'Dung i didok Musa ma tu sandok luhutan ni halak Israel songon on: On do hata na tinonahon ni Jahowa tu hamu:'),(10558,43,35,5,'Jalo hamu na sian hamu silehonlehon bahen pelean tatingan di Jahowa, ganup sian lomo ni rohana mamboan pelean tatingan di Jahowa: sere, perak dohot tombaga.'),(10559,43,35,6,'Tole abit sian bonang na rata langit dohot na rara dohot na merham, nang hain bontar na halus dohot imbulu ni hambing.'),(10560,43,35,7,'Tole bobak ni birubiru tunggal angka na tinubar, tole bobak ni pitudar dohot hau pinasa.'),(10561,43,35,8,'Tole miak tu parpalitoan, miak na hushus pangkeon laho mamiahi dohot manutung daupa.'),(10562,43,35,9,'Tole angka mata intan unam dohot simata landus bahenon tu baju epod dohot tu tutup andora.'),(10563,43,35,10,'Jala manang ise na pantas marroha di tongatongamuna, ingkon ro be mangulahon sandok na tinonahon ni Jahowa:'),(10564,43,35,11,'Pauli inganan na badia i rap dohot undungundungna, langitlangitna, haithaitna, papanna, ransangransangna, angka tiangna dohot ojahanna.'),(10565,43,35,12,'Tole poti parpadanan i rap dohot haluhaluna, langkop pardengganan i dohot rimberimbe.'),(10566,43,35,13,'Tole meja i rap dohot haluhaluna ro di sandok ulaulana ro di roti na marhadohoan.'),(10567,43,35,14,'Tole parpalitoan i bahen panondang i ro di ulaulana, angka palitona dohot miak tu palito i.'),(10568,43,35,15,'Tole muse langgatan pardaupaan i rap dohot haluhaluna, nang miak pamiahion dohot pamurpuri bahen daupa na hushus ro di rimberimbe tutup ni pintu laho masuk tu inganan na badia i.'),(10569,43,35,16,'Tole langgatan pamelean ni pelean situtungon rap dohot handanghandangna sian tombaga, angka haluhaluna dohot sandok ulaulana, tong parburian dohot ojahanna.'),(10570,43,35,17,'Tole rimberimbe tu alaman i rap dohot tiangna dohot ojahanna; nang rimberimbe tutup ni bahal haroroan tu alaman i.'),(10571,43,35,18,'Tole angka labang tu inganan na badia i dohot tu alaman i rap dohot angka talina.'),(10572,43,35,19,'Tole angka pangkean tohonan laho marhalado di inganan na badia i, angka pangkean ni malim Aron na badia i rap dohot pangkean ni angka anakna laho mangaradoti hamalimon.'),(10573,43,35,20,'Dung i ruar ma sandok luhutan ni halak Israel sian adopan ni Musa.'),(10574,43,35,21,'Jadi ro be ma ganup na tarsunggul rohana dohot ganup na martondi na girgir, diboan be ma pelean tatingan tu Jahowa pangkeon tu ulaon pauli undungundung parluhutan i dohot tu sandok ulaon siradotan disi dohot tu pangkean angka na badia i.'),(10575,43,35,22,'Jadi ro be ma, angka baoa dongan ni angka parompuan, sude tahe angka na girgir marroha, diboan be ma leangna dohot antingantingna dohot tintinna dohot horunghorungna, ro di ulaula sere marragamragam, jala ganup mamelehon sere bahen pelean tatingan tu Jahowa.'),(10576,43,35,23,'Manang ise pe na umpeop abit sian bonang na rata langit dohot na rara dohot na merham, nang hain bontar na halus dohot imbulu ni hambing, tole bobak ni birubiru tunggal angka na tinubar dohot bobak ni pitudar, diboan be ma i.'),(10577,43,35,24,'Manang ise na umpeop perak manang tombaga diboan be ma i guguanna bahen pelean tatingan tu Jahowa; jala manang ise na umpeop hau pinasa ditaruhon be ma i pangkeon tu sandok ulaon tohonan marragamragam.'),(10578,43,35,25,'Nang saluhut inaina angka na torang marroha mangganti be do dohot tanganna sandiri, jala diboan be na ginantina i, abit sian bonang na rata langit dohot na rara dohot na merham, nang hain bontar na halus.'),(10579,43,35,26,'Jala nasa parompuan angka na tarsunggul rohana jala na marhapistaran, mangganti imbulu ni hambing ma nasida.'),(10580,43,35,27,'Alai anggo angka induk i, diboan be ma mata intan unam dohot simata landus bahenon tu baju epod dohot tu tutup andora.'),(10581,43,35,28,'Tole muse parmurpuri dohot miak tu parpalitoan dohot miak na hushus pangkeon laho mamiahi dohot manutung daupa.'),(10582,43,35,29,'Nasa baoa dohot parompuan sian halak Israel angka na tarsunggul rohana be gabe girgir mamboan silehonlehon manumpak ulaon marragamragam, na tinonahon ni Jahowa marhite sian si Musa, sibahenon ni halak Israel bahen pelean tu Jahowa.'),(10583,43,35,30,'Dung i ro ma hata ni Musa tu halak Israel: Ida hamu ma, nunga pola dijou Jahowa sahalak na margoar Bezaleel, anak ni si Uri, i ma anak ni si Hur sian marga Juda.'),(10584,43,35,31,'Nunga pola digohi Tondi ni Debata rohana i marhapistaran, marhapandean dohot marparbinotoan mangaradoti saluhut ulaon i.'),(10585,43,35,32,'Asa dipingkiri rohana hapandean na sumurung laho mamandei sere, perak dohot tombaga.'),(10586,43,35,33,'Huhut manguhir dohot mamijor batu intan, mamandei hau pe mambahen hapandean marragamragam.'),(10587,43,35,34,'Angkup ni nunga dibasabasahon Jahowa hamaloon tu bagasan rohana mangajari halak na asing, ibana dohot si Oholiab, anak ni si Ahisamakh sian marga Dan.'),(10588,43,35,35,'Nunga digohi Jahowa rohanasida dohot hapistaran laho mambahen hapandean marragamragam, mamandei hau, mamunsa dohot mangangit abit sian bonang na rata langit, na rara dohot na merham, nang hain bontar na halus, tole hapandean ni partonun, asa dibahen nasida hapandean marragamragam, jala dipingkirpingkiri rohanasida hapandean angka na sumurung.'),(10589,43,36,1,'Jadi tole ma Bezaleel dohot Oholiab ro di sude baoa angka na pistar marroha, angka naung maruli rohana hapistaran dohot hapandean sian Jahowa, marmulahon mambahen hapandean marragamragam mangaradoti tohonan di inganan na badia i, hombar tu saluhutna na tinonahon ni Jahowa.'),(10590,43,36,2,'Ai nunga dijou Musa hian Bezaleel dohot Oholiab ro di sude baoa angka na pistar marroha, angka naung maruli rohana hapistaran sian Jahowa, nang sude naung tarsunggul rohana ro jumonok mangaradoti ulaon i.'),(10591,43,36,3,'Jadi dibuat nasida ma sian adopan ni Musa saluhut pelean tatingan, angka naung binoan ni halak Israel manumpak ulaon mangaradoti tohonan di inganan na badia i, asa paulionna; alai sai didatdati nasida dope ganup sogot mamboan silehonlehon guguan lomo ni rohana i tu Musa.'),(10592,43,36,4,'Jadi ro be ma sude pande, angka na mangula sandok ulaon di inganan na badia i, ganup sian ulaon na niulanasida be.'),(10593,43,36,5,'Didok nasida be ma tu Musa songon on: Magodangku do guguan na binoan ni bangso i, lobi sian na tarpangke hasea tu ulaon tohonan i, naung tinonahon ni Jahowa sibahenon i.'),(10594,43,36,6,'Jadi didokkon Musa ma martingting tu sandok parsaroan i, songon on: Unang be didatdati hita on, nang baoa nang parompuan mangalehon guguan manumpak ulaon pauli inganan na badia i! Jadi dipasohot bangso i ma mamboan.'),(10595,43,36,7,'Ai nunga tung singkop ulaula tu sandok ulaon sipaulion i, marlobilobi gariada.'),(10596,43,36,8,'Jadi tole ma sude halak na pistar marroha, angka siula ulaon i pauli undungundung i, i ma sampulu rimberimbe sian bonang na nidua, na rata langit, na rara jala na merham, margombaran kerubim, hapandean na binahen ni parpunsa.'),(10597,43,36,9,'Ia unjur ni rimberimbe i sadasada duapulu ualu asta do, ia bidangna opat asta; sisada suhat do saluhut angka rimberimbe i.'),(10598,43,36,10,'Limalima angka rimberimbe i dipadomu, na sada tu donganna.'),(10599,43,36,11,'Dibahen huhut angka tali sangkutan na rata tu bibir ni rimberimbe parjolo i apala di pardomuanna, laos songon i muse dibahen di bibir ni rimberimbe paduahon i bahen pardomuan paduahon.'),(10600,43,36,12,'Limapulu tali sangkutan dibahen tu rimberimbe na sada, jala limapulu tali sangkutan muse dibahen di ujung ni rimberimbe i, apala di pardomuanna paduahon; jadi masijaloan ma angka tali sangkutan, na sada tu donganna.'),(10601,43,36,13,'Dibahen muse ma limapulu haithait sere laho patunggomhon angka rimberimbe i, na sada tu donganna, i ma manjadi sada undungundung.'),(10602,43,36,14,'Dibahen muse ma angka rimberimbe sian imbulu ni hambing bahen langitlangit di atas ni undungundung i; sampulu sada rimberimbe sisongon i dibahen.'),(10603,43,36,15,'Ia unjur ni rimberimbe sadasada tolupulu asta do jala bidangna opat asta; sisada suhat do rimberimbe na sampulu sada i.'),(10604,43,36,16,'Jala dipatunggom pulik na lima rimberimbe i, laos songon i pulik rimberimbe na onom nari.'),(10605,43,36,17,'Dibahen muse ma limapulu tali sangkutan di bibir ni rimberimbe na sada parduru apala di pardomuanna, jala limapulu tali sangkutan di bibir ni rimberimbe na sada nari, di pardomuanna paduahon.'),(10606,43,36,18,'Dibahen muse ma limapulu haithait tombaga laho padomuhon undungundung i gabe sada.'),(10607,43,36,19,'Dibahen muse ma di atas langitlangit ni undungundung i salampis sian bobak ni birubiru tunggal na tinubar, di atas ni muse salampis sian bobak ni pitudar.'),(10608,43,36,20,'Dibahen muse ma angka papan sian hau pinasa bahen dorpi ni undungundung i, angka sipajongjongon do.'),(10609,43,36,21,'Sampulu asta ganjang ni papan i, jala tonga dua asta ia bidang ni sada papan.'),(10610,43,36,22,'Mardua pasi ganup papan, baris be tu donganna, songon i do dibahen saluhut papan angka na masuk tu undungundung i.'),(10611,43,36,23,'Jala songon on do dibahen parmasuk ni angka papan tu undungundung i: Duapulu papan sambariba tungkan dangsina.'),(10612,43,36,24,'Dibahen do huhut opatpulu ojahan perak di toru ni na duapulu papan i, dua be ojahan di toru ni sada papan, marguru tu pasina na dua i.'),(10613,43,36,25,'Di na sambariba nari ni undungundung i pe, tungkan utara dibahen muse duapulu papan.'),(10614,43,36,26,'Nang na opatpulu ojahanna perak, dua be ojahan di toru ni sada papan.'),(10615,43,36,27,'Alai di tampahan ni undungundung tungkan hasundutan, onom papan dibahen.'),(10616,43,36,28,'Dibahen muse papan dua nari di na dua suhi ni undungundung i, di tampahanna parpudi.'),(10617,43,36,29,'Jadi dua lampis ma i sian toru mardomu di ginjang pinadomu ni sada langgu; songon i ma dibahen duansa di na dua suhi i.'),(10618,43,36,30,'Jadi ualu papan ma disi mardongan ojahanna perak sampulu onom toropna, dua be di toru ni sada papan.'),(10619,43,36,31,'Dibahen muse ma angka ransangransang sian hau pinasa, lima tu angka papan di na sambariba ni undungundung i.'),(10620,43,36,32,'Laos lima ransangransang tu angka papan di na sambariba nari ni undungundung i, laos lima ransangransang tu angka papan ni undungundung i di tampahanna parpudi tungkan hasundutan.'),(10621,43,36,33,'Dibahen ma huhut ransangransang partonga na di dos tonga ni angka papan, torus sian suhi na sada tu suhi na sada nari.'),(10622,43,36,34,'Dilampis muse ma angka papan i dohot sere, sabongna pe sian sere do dibahen, asa tusi pabongoton angka ransangransang; angka ransang i pe dilampis do dohot sere.'),(10623,43,36,35,'Dipauli muse ma sada rimberimbe sian bonang na rata langit, jala na tinubar, jala na merham dohot sian hain na halus na nidua, margombaran kerubim do i dibahen, tinonunhon ni parpunsa tusi.'),(10624,43,36,36,'Dung i dihirehon ma i tu opat tiang sian hau pinasa angka na nilampis dohot sere, jala marhaithait sere, jala dituang tusi opat ojahan perak.'),(10625,43,36,37,'Dipauli muse ma sada rimberimbe bahen tutup ni pintu ni undungundung i sian bonang na rata langit dohot na tinubar dohot na merham dohot sian bonang na bontar na nidua, tinonunhon ni parpunsa.'),(10626,43,36,38,'Dibahen muse ma lima tiang rap dohot haithaitna; dilampis muse ma angka uluna nang rungkungna dohot sere, jala ditumpa lima ojahanna sian tombaga.'),(10627,43,37,1,'Dung i Bezaleel sandiri ma paulihon poti i sian hau pinasa, tonga tolu asta unjurna, jala tonga dua asta bohangna, laos tonga dua asta dohot timbona.'),(10628,43,37,2,'Dung i dilampis ma poti i dohot sere na pita, sian bagasan dohot sian ruar, jala dibahen huhut bibirna marlaislais sere humaliang.'),(10629,43,37,3,'Ditumpa huhut tusi opat langgu sere tu suhina na opat i, dua langgu di na sambariba be.'),(10630,43,37,4,'Dibahen muse ma haluhalu sian hau pinasa, jala dilampis do i dohot sere.'),(10631,43,37,5,'Disurukkon ma haluhalu i tu bagasan langgu angka na lohot di poti i, laho mangusung poti i.'),(10632,43,37,6,'Dibahen muse ma sada langkop pardengganan sian sere na pita, tonga tolu asta unjurna, jala tonga dua asta bidangna.'),(10633,43,37,7,'Dibahen muse ma dua kerubim sian sere sangkimpal na tinopa, dibahen do i tu na dua tampahan ni langkop parpadanan i.'),(10634,43,37,8,'Kerubim na sada tu tampahan na sada, jala kerubim na sada nari tu tampahanna na sada nari; tubu sian bagasan langkop pardengganan i tolpus tu duru dibahen angka kerubim i di na dua tampahanna.'),(10635,43,37,9,'Jala dipaherbang angka kerubim i ma habongna na dua i sian ginjang, asa dihungkupi habongnasida langkop pardengganan i, jala padompak be bohinasida, dompak langkop pardengganan i bohi ni angka kerubim i mamereng.'),(10636,43,37,10,'Dipauli muse ma sada meja sian hau pinasa, dua asta unjurna, jala saasta bidangna, jala tonga dua asta timbona.'),(10637,43,37,11,'Dilampis ma i muse dohot sere na pita, jala dibahen martopi sere humaliang.'),(10638,43,37,12,'Dibahen muse ma tusi sada lais santopap bidangna humaliang, dibahen muse laisna i martopi sere pinariponipon humaliang.'),(10639,43,37,13,'Jala ditumpa tusi opat langgu sere, angka na binahenna tu suhina na opat i, angka na di patna na opat i.'),(10640,43,37,14,'Jonok di toru ni lais i do angka langgu i bahen panuruhan ni haluhalu laho mangusung meja i.'),(10641,43,37,15,'Sian hau pinasa ma angka haluhalu i dibahen, jala dilampis ma i dohot sere, i ma pangkeon mangusung meja i.'),(10642,43,37,16,'Dipauli ma huhut ulaula sipangkeon di atas meja i: angka pingganna, saoanna, hondina dohot mangkukna, bahen pangusean ni tuak tangkasan ma i; sian sere na pita do i dipauli.'),(10643,43,37,17,'Dipauli muse ma sada parpalitoan sian sere na pita, sangkimpal parpalitoan i ditopa, ro di badanna, dangkana dohot butuhana, hansingna dohot bungana.'),(10644,43,37,18,'Onom dangka ruar sian lambungna, tolu dangka, sian na sambariba ni parpalitoan i, jala tolu dangka sian na sambariba nari.'),(10645,43,37,19,'Martolu butuha, songon bunga ni buapala ganup dangkana i, mardongan angka hansing dohot bunga; laos songon i do dibahen tu na onom dangka i, angka na ruar sian parpalitoan i.'),(10646,43,37,20,'Alai opat do butuha ni parpalitoan i, songon bunga ni buapala mardongan hansing dohot bunga.'),(10647,43,37,21,'Jala sahansing be di toru ni na dua dangkana angka na ruar sian i; suang songon i sahansing be di toru ni dangkana na dua, angka na ruar sian i; suang muse sahansing be di toru ni dangkana na dua, angka na ruar sian i; gabe onom dangka ruar sian badan ni parpalitoan i.'),(10648,43,37,22,'Ruar do sian i angka hansingna dohot dangkana, luhutna i sian sangkimpal sere na pita i do dipauli.'),(10649,43,37,23,'Dipauli muse ma pitu palitona rap dohot panurturna dohot pangintopina sian sere na pita.'),(10650,43,37,24,'Sian satalenta sere na pita parpalitoan i dipauli, ro di parhohasna i sudena.'),(10651,43,37,25,'Dipauli muse ma langgatan pardaupaan sian hau pinasa, saasta unjurna, jala saasta bohangna, maropat suhi tahe, jala dua asta timbona, ruar huhut sian i angka tandukna.'),(10652,43,37,26,'Jala dilampis do i dohot sere na pita, nang tutupna, nang dorpina humaliang dohot angka tandukna; dibahen do i huhut marbibir humaliang sian sere na pita.'),(10653,43,37,27,'Dibahen muse dua langgu sere di toru ni bibirna i di lambungna na dua i angkup be, bahen panuruhan ni haluhalu, asa dapot usungon ni halak langgatan i.'),(10654,43,37,28,'Alai sian hau pinasa haluhalu i dipauli, jala dilampis do i dohot sere.'),(10655,43,37,29,'Dipamasak huhut miak pamiahion na badia i dohot daupa sian pamurpuri na so sinaor, mangihuthon hapandean ni pande daon.'),(10656,43,38,1,'Dung i dipauli muse ma langgatan pamelean ni pelean situtungon sian hau pinasa, lima asta unjurna, laos lima asta bohangna, asa maropat suhi do, jala tolu asta timbona.'),(10657,43,38,2,'Dibahen ma huhut opat tandukna di suhina na opat i, songon na tubu sian i angka tandukna, jala dilampis do i dohot tombaga.'),(10658,43,38,3,'Dipauli ma huhut sude ulaula tu langgatan i: parsirabunanna, siuksiukna, mangkuk pamispisanna, garpuna dohot parapiapianna; sian tombaga do dipauli saluhut ulaulana i.'),(10659,43,38,4,'Dibahen ma huhut tu langgatan i sada handanghandang sian tombaga, marpangalaho songon jala na niangit di toru ni sidegedegean humaliang langgatan i sahat ro di parsitongaan ni langgatan i.'),(10660,43,38,5,'Ditumpa huhut opat langgu tu suhina na opat i mardomu tu handanghandang tombaga i bahen panuruhan ni angka haluhalu.'),(10661,43,38,6,'Dipauli ma angka haluhalu i sian hau pinasa, jala dilampis do i dohot tombaga.'),(10662,43,38,7,'Dung i dipabongot ma angka haluhalu i tu bagasan langgu angka na di lambung ni langgatan i, asa dapot usungon ni halak langgatan i marhite sian i; rumar do langgatan i dibahen sian angka papan.'),(10663,43,38,8,'Dipauli muse ma tong parburian i sian tombaga, ojahanna pe tombaga do, sian angka sorminan pinangke ni parompuan angka na marpungu di lambung pintu ni undungundung parluhutan i marhaladoi.'),(10664,43,38,9,'Dipauli muse ma alaman ni undungundung i di na sambariba tungkan dangsina, tole muse angka rimberimbe ni alaman i sian bonang na bontar na nidua, saratus asta ganjangna.'),(10665,43,38,10,'Tole tiangna na duapulu i dohot ojahanna na duapulu i sian tombaga; alai anggo haithait ni angka tiang i ro di ransangransangna perak do.'),(10666,43,38,11,'Suang songon i di na sambariba tungkan utara laos marrimberimbe do, saratus asta unjurna, tole tiangna dohot ojahanna duapulu be do sian tombaga; alai anggo haithait ni angka tiang i ro di ransangransangna perak do.'),(10667,43,38,12,'Alai anggo bohang ni alaman i tungkan hasundutan, limapulu asta ganjang ni rimberimbe i, sampulu tiangna, laos sampulu ojahanna; alai anggo haithait ni angka tiang i ro di ransangransangna perak do.'),(10668,43,38,13,'Ia bohang ni alaman i na tungkan habinsaran laos limapulu asta do.'),(10669,43,38,14,'Jadi sampulu lima asta rimberimbe i di na sambariba haroroan i, tolu tiangna laos tolu ojahanna.'),(10670,43,38,15,'Di na sambariba nari pe laos suang songon i do; gabe dos di na dua bariba ni pintu haroroan tu alaman i ganjang ni rimberimbe i sampulu lima asta be, tolu tiangna laos tolu ojahanna.'),(10671,43,38,16,'Saluhut rimberimbe ni alaman i humaliang sian hain na halus na nidua do.'),(10672,43,38,17,'Ia ojahan ni angka tiang i tombaga do, alai anggo haithait ni angka tiang i dohot ransangransangna perak do; ia angka uluna na nilampis do dohot perak; alai anggo ransangransangna perak do, angka na mardomu tu tiang ni alaman i.'),(10673,43,38,18,'Alai anggo rimberimbe tutup ni pintu ni alaman i hapandean ni parpunsa do i pinaulina sian bonang na rata langit, na tinubar dohot na merham dohot sian hain na halus na nidua, duapulu asta ganjangna, lima asta timbona dohot bidangna mangihuthon suhatsuhat ni angka rimberimbe ni alaman i.'),(10674,43,38,19,'Tiangna na opat i dohot ojahanna na opat i sian tombaga do; alai anggo haithaitna dohot ransangransangna sian perak, anggo ulu ni angka tiang i nilampis do dohot perak.'),(10675,43,38,20,'Saluhut labang tu inganan na badia i dohot tu alaman i humaliang sian tombaga do.'),(10676,43,38,21,'On do aturan ni saluhut na pinalaho pauli inganan na badia i, undungundung parluhutan i, na binilangan, nidokkon ni Musa, binilangan ni halak Lepi angka na marpandapotan tu Itamar, anak ni malim Aron.'),(10677,43,38,22,'Asa si Bezaleel, anak ni Uri, i ma anak ni Hur, marga Juda patupahon saluhutna na tinonahon ni Jahowa tu Musa.'),(10678,43,38,23,'Ia donganna mangula i ma Oholiab, anak ni Ahisamakh, marga Dan; pande do ibana mandakdak batu, manguhir dohot mamunsa bonang na rata langit, na tinubar dohot na merham, nang pauli hain na halus.'),(10679,43,38,24,'Saluhut sere na suda pinangkena tu sandok ulaon pauli inganan na badia i, i ma sere guguan bahen pelean tatingan duapulu sia talenta do dohot pitu ratus tolupulu sekkel mangihuthon sekkel di inganan na badia.'),(10680,43,38,25,'Ia godang ni perak na ginugu ni luhutan na binilangan saratus talenta dohot saribu pitu ratus pitupulu lima sekkel, mangihuthon sekkel di inganan na badia.'),(10681,43,38,26,'Sabeka sasahalak, i ma satonga sekkel mangihuthon sekkel di inganan na badia sian saluhut jolma na masuk bilangan olat ni na marumur duapulu taon tu ginjang, i ma onom ratus ribu dohot tolu ribu lima ratus limapulu halak toropna.'),(10682,43,38,27,'Suda do saratus talenta perak laho manuang angka ojahan ni inganan na badia dohot angka ojahan ni rimberimbe, boho saratus talenta, sasatalenta tu sada ojahan.'),(10683,43,38,28,'Alai sian na saribu pitu ratus pitupulu lima sekkel perak i dipauli do angka haithait tu angka tiang i, jala dilampis angka uluna tole angka langguna ditumpa sian i.'),(10684,43,38,29,'Ia godang ni guguan tombaga bahen pelean tatingan pitupulu talenta dohot dua ribu opat ratus sekkel dokdokna.'),(10685,43,38,30,'Dipauli sian i angka ojahan ni pintu undungundung parluhutan i, tole langgatan tombaga dohot handanghandang tombaga na masuk tusi ro di saluhut ulaula tu langgatan i.'),(10686,43,38,31,'Tole muse angka ojahan ni alaman i humaliang dohot ojahan ni bahal ni alaman i ro di sude labang tu inganan na badia i dohot sude labang tu alaman i humaliang.'),(10687,43,39,1,'Dipauli nasida huhut angka pangkean tohonan laho marhalado di inganan na badia i sian bonang na rata langit, na tinubar dohot na merham; dipauli huhut pangkean na badia angka sisolukkonon ni si Aron, songon na tinonahon ni Jahowa hian tu Musa.'),(10688,43,39,2,'Dipauli ma baju epod i sian sere dohot sian bonang na rata langit, na tinubar jala na merham dohot sian hain na halus na nidua.'),(10689,43,39,3,'Dinanggari nasida ma sere i gabe purada laos diirisi ma i manjadi bonang, asa dapot tonunhonon ni parpunsa saor tu bonang na rata langit, na tinubar dohot na merham, saor muse tu hain na halus.'),(10690,43,39,4,'Dibahen nasida huhut dua pangabaraan di baju epod i, jala dipatunggom di tampahanna be.'),(10691,43,39,5,'Tole muse hohos ni epod i, sisada hapandean do i dohot baju i laos mardomu tusi, jadijadian sian sere dohot sian bonang na rata langit, na tinubar jala na merham dohot sian hain na halus na nidua songon na tinonahon ni Jahowa tu si Musa.'),(10692,43,39,6,'Dung i dipabongot nasida ma angka batu intan unam angka na nirusuk dohot tali sere, jala diuhirhon tusi songon uhiran sahap goargoar ni anak ni Israel na sampuludua i.'),(10693,43,39,7,'Dung i dibahen nasida ma angka batu intan i tu na dua pangabaraan ni baju epod i, bahen batu parningotan di angka anak ni Israel, songon na tinonahon ni Jahowa tu si Musa.'),(10694,43,39,8,'Dibahen nasida huhut tutup andora i, suang songon hapandean ni baju epod ditonunhon parpunsa tusi sere, bonang na rata langit, na tinubar jala na merham dohot hain na halus na nidua.'),(10695,43,39,9,'Maropat suhi do i dibahen jala dua lampis, sanjongkal ganjangna jala sanjongkal bidangna.'),(10696,43,39,10,'Jala digohi nasida do i dohot opat rinte batu intan. Di rinte parjolo: batu intan sardis, topasion dohot samarados.'),(10697,43,39,11,'Di rinte paduahon: batu intan rubin, sapire dohot diaman.'),(10698,43,39,12,'Di rinte patoluhon: batu intan opal, akat, dohot ametist.'),(10699,43,39,13,'Di rinte paopathon: batu intan krisolit, Unam dohot jaspis; jala marihot sere angka rinte i dibahen humaliang.'),(10700,43,39,14,'Jala jojor angka batu i mangihuthon partording ni na sampuludua goar ni anak ni Israel, angka na niuhirhon ni halak pandakdak batu, pulik be goarna i, mangihuthon na sampuludua marga ni Israel.'),(10701,43,39,15,'Dung i dibahen nasida huhut tu tutup andora i angka rante dos ganjangna, songon tali nidua hapandeanna sian sere na pita.'),(10702,43,39,16,'Dibahen nasida huhut ihot sere dohot dua tintin sere, dibahen muse na dua tintin i tu na dua tampahan ni tutup andora i.'),(10703,43,39,17,'Jala dibahen nasida do na dua rante na nidua sian sere tu bagasan tintin na dua i, angka na di tampahan ni tutup andora i.'),(10704,43,39,18,'Alai anggo na dua ujung ni na dua rante na nidua i dihaithon nasida do i tu haithait na dua i jala dipatunggom do i tu pangabaraan ni baju epod i patondong be.'),(10705,43,39,19,'Dibahen nasida muse dua tintin sere na asing, jala dilohothon do i tu na dua tampahan ni tutup andora i, apala di pangkilimanna na tungkan baju epod i dingkan bagasan.'),(10706,43,39,20,'Dibahen muse dope dua tintin sere na asing, jala dilohothon do i tu na dua pangabaraan ni baju epod i tungkan toru tongon di pardomuanna, di atas hohos ni baju epod i.'),(10707,43,39,21,'Jadi dipudunhon nasida ma tutup andora i rap dohot tintinna marhite sian bonang na rata langit tu tintin ni baju epod, asa hopos tu hohos ni baju epod i, jala unang tarbahen sirang tutup andora sian baju epod, songon na tinonahon ni Jahowa tu Musa.'),(10708,43,39,22,'Dipauli muse ma baju parbalian di toru ni baju epod i, sandok sian bonang na rata langit do i ditonun.'),(10709,43,39,23,'Jala parrungkunganna i di parsitongaanna, jala martopi na tinonun parrungkungan i humaliang, songon topi ni baju partohonan, asa unang olo maribak.'),(10710,43,39,24,'Dibahen nasida huhut tu rambuna humaliang angka parbue ni dalimo sian bonang na rata langit, na tinubar jala na merham huhut na nidua.'),(10711,43,39,25,'Dibahen nasida huhut angka giringgiring sian sere na pita, i ma disolothon tu holangholang ni angka parbue ni dalimo humaliang di rambu ni baju parbalian i.'),(10712,43,39,26,'Sai marsorinsorin do sada parbue ni dalimo dung i sada giringgiring tuk humaliang, sisolukkononhon do i laho marulaon na badia songon na tinonahon ni Jahowa tu Musa.'),(10713,43,39,27,'Dipauli nasida huhut angka baju parbagasan sian hain na halus, sipangkeon ni Aron dohot angka anakna i.'),(10714,43,39,28,'Tole muse tumpal ni sintua ni malim sian hain na bontar ro di angka talitali ni malim sian hain na bontar do, saraoalna pe sian hain na bontar na nidua do.'),(10715,43,39,29,'Hohos i pe dipauli do i sian hain na nidua dohot sian bonang na rata langit, na tinubar dohot na merham, hapandean ni parpunsa do i, songon na tinonahon ni Jahowa tu Musa.'),(10716,43,39,30,'Dibahen nasida huhut sangkalia i, tumpal habadiaon i sian sere na pita jala diuhirhon tusi, songon panguhiron sahap: Habadiaon di Jahowa!'),(10717,43,39,31,'Jala dilohothon tusi tali bonang na rata langit, asa dipadomu tu tumpal i sian ginjang songon na tinonahon ni Jahowa tu Musa.'),(10718,43,39,32,'Jadi songon i ma hasusun ni sandok ulaon pauliuli inganan na badia i, undungundung parluhutan i; dibahen halak Israel do antong sude hombar tu na tinonahon ni Jahowa, songon i dibahen nasida.'),(10719,43,39,33,'Dung i diboan nasida ma inganan na badia i tu Musa, i ma: undungundung i ro di sude ulaulana, angka haithaitna, papanna, ransangransangna, tiangna dohot ojahanna.'),(10720,43,39,34,'Tole muse langitlangitna sian bobak ni birubiru tunggal na tinubar, tole muse sian bobak ni pitudar, tole muse rimberimbe bahen tutupna i.'),(10721,43,39,35,'Tole muse poti panindangion i ro di haluhaluna dohot langkop pardengganan i.'),(10722,43,39,36,'Tole muse meja i ro di sude ulaulana dohot sagusagu na marhadohoan.'),(10723,43,39,37,'Tole muse parpalitoan sian sere na pita i ro di angka lampuna, aturanna ro di sude ulaulana dohot miak tu palito i.'),(10724,43,39,38,'Tole muse langgatan sere i, miak pamiahion i, daupa na hushus i ro di rimberimbe bahen tutup ni pintu ni undungundung i.'),(10725,43,39,39,'Tole muse langgatan tombaga i ro di handanghandang tombaga angka na masuk tusi, haluhaluna ro di sude parhohasna, tong parburian i ro di ojahanna.'),(10726,43,39,40,'Tole muse angka rimberimbe ni alaman ro di angka tiangna dohot ojahanna, nang rimberimbe tutup ni bahal ni alaman i, ro di angka talina, labangna dohot sude ulaula mangaradoti ulaon di bagasan inganan na badia i, undungundung parluhutan i.'),(10727,43,39,41,'Tole muse angka pangkean tohonan laho marhalado di bagasan inganan na badia i, angka pangkean na badia di malim Aron dohot angka pangkean di angka anakna laho mangaradoti hamalimon.'),(10728,43,39,42,'Luhutna i hombar tu hata na tinonahon ni Jahowa tu si Musa, laos songon i dipasidung halak Israel sandok ulaon i.'),(10729,43,39,43,'Dung i ditingkir Musa ma sandok ulaon i, gabe ida ma, nunga sun dibahen nasida; songon na tinonahon ni Jahowa tu Musa laos songon i dibahen nasida. Dung i dipasupasu Musa ma nasida.'),(10730,43,40,1,'Dung i ro ma hata ni Jahowa tu si Musa, songon on:'),(10731,43,40,2,'Di artia bulan sipahasada ingkon pajongjongonmu inganan na badia, undungundung parluhutan i.'),(10732,43,40,3,'Ingkon bahenonmu tu bagasan ni poti panindangion i, jala hungkupanmu poti i dohot rimberimbe.'),(10733,43,40,4,'Ingkon boanonmu huhut meja i tu bagasan, jala patordingonmu sipatordingon hian di atasna; boanonmu huhut parpalitoan i tu bagasan jala pasangonmu angka palitona.'),(10734,43,40,5,'Ingkon pajongjongonmu huhut langgatan pardaupaan sian sere di adopan ni poti panindangion i, jala tipak gantungonmu rimberimbe tutup ni pintu ni inganan na badia i.'),(10735,43,40,6,'Alai anggo langgatan pamelean ni pelean situtungon ingkon pajongjongonmu do di jolo pintu ni inganan na badia i, undungundung parluhutan i.'),(10736,43,40,7,'Ia tong parburian i ingkon pajongjongonmu do di holangholang ni undungundung parluhutan i dohot langgatan i, laos bahenonmu aek tu bagasan.'),(10737,43,40,8,'Dung i pajongjongonmu ma rimberimbe ni alaman i humaliang, gantungonmu dohot rimberimbe tutup ni bahal ni alaman i.'),(10738,43,40,9,'Dung i buatonmu ma miak pamiahion i jala miakkon tu inganan na badia i dohot tu sude ugasan na di bagasanna i, jala urasonmu do i ro di sude ulaulana, asa gabe na badia angka i.'),(10739,43,40,10,'Ingkon miahanmu dohot langgatan pamelean ni pelean situtungon ro di sude ulaulana, jala urasonmu langgatan i, asa langgatan i gabe na badia sahali.'),(10740,43,40,11,'Ingkon miahanmu dohot tong parburian i ro di ojahanna jala urasonmu do i.'),(10741,43,40,12,'Dung i boanonmu ma Aron dohot angka anakna i ro jumonok tu pintu ni undungundung parluhutan i, jala didionmu nasida dohot aek.'),(10742,43,40,13,'Dung i solukkononmu ma tu Aron angka pangkean na badia i, jala miahanmu ibana, jala urasonmu, asa ibana mangaradoti hamalimon di Ahu.'),(10743,43,40,14,'Boanonmu huhut angka anakna ro jumonok, jala solukkononmu tu nasida angka baju na marhadohoan.'),(10744,43,40,15,'Laos miahanmu ma dohot nasida, songon na miniahanmu amanasida i, asa nasida mangaradoti hamalimon di Ahu; ia pamiahion na tu nasida i ingkon manjadi tanda hamalimon do i salelenglelengna ro di pinomparnasida sogot.'),(10745,43,40,16,'Jadi dipatupa si Musa ma sude songon na tinonahon ni Jahowa tu ibana songon i do dibahen.'),(10746,43,40,17,'Saut ma di artia bulan sipahasada paduataonhon dung ruar sian Misir dipajongjong nasida inganan na badia i.'),(10747,43,40,18,'Jadi dung dipajongjong Musa sandiri inganan na badia i, dibahen dohot angka ojahanna jala dipamasuk tusi angka papanna, dibahen muse ransangransangna jala dipajongjong angka tiangna.'),(10748,43,40,19,'Jala dipaherbang angka rimberimbe ni undungundung i tu atas inganan na badia i, jala dipeakkon tutup ni undungundung i tu atasna, songon na tinonahon ni Jahowa tu si Musa.'),(10749,43,40,20,'Dung i dibuat ma lei panindangion i jala dipeakkon tu bagasan poti i, disurukkon ma dohot haluhalu i tu poti i dibahen muse ma langkop pardengganan i tu atas ni poti i.'),(10750,43,40,21,'Dung i diboan ma poti i tu bagasan inganan na badia i jala digantungkon rimberimbe i bahen tutup ni poti panindangion i, songon na tinonahon ni Jahowa tu Musa.'),(10751,43,40,22,'Dung i dipajongjong ma meja i tu bagasan undungundung parluhutan i di lambung inganan na badia i tungkan utara di balian ni rimberimbe i.'),(10752,43,40,23,'Jala dipatording di atasna sipatordingon hian, i ma sagusagu di adopan ni Jahowa, songon na tinonahon ni Jahowa tu Musa.'),(10753,43,40,24,'Dipajongjong ma dohot parpalitoan i tu bagasan undungundung na badia i tondong ni meja i di lambung inganan na badia i tungkan dangsina.'),(10754,43,40,25,'Dipagalak ma dohot angka palito i di adopan ni Jahowa, songon na tinonahon ni Jahowa hian tu Musa.'),(10755,43,40,26,'Dipajongjong muse ma langgatan sere i tu bagasan undungundung parluhutan i di jolo ni rimberimbe i.'),(10756,43,40,27,'Jala di tutung di atasna daupa na hushus marragamragam songon na tinonahon ni Jahowa hian tu Musa.'),(10757,43,40,28,'Digantungkon ma dohot rimberimbe tutup ni pintu laho tu inganan na badia i.'),(10758,43,40,29,'Alai anggo langgatan pamelean ni pelean situtungon dipajongjong do i di pintu haroroan tu inganan na badia i, undungundung parluhutan i, jala dipelehon di atasna pelean situtungon dohot pelean sipanganon, songon na tinonahon ni Jahowa hian tu si Musa.'),(10759,43,40,30,'Dipajongjong dohot tong parburian i di holangholang ni undungundung parluhutan i dohot langgatan i, laos dibahen tusi aek parburian.'),(10760,43,40,31,'Asa laos aek sian i buaton ni si Musa. Si Aron dohot angka anakna i mamburi tangannasida dohot patnasida.'),(10761,43,40,32,'Molo masuk nasida tu bagasan undungundung parluhutan i, jala molo ro nasida jumonok tu langgatan i, ingkon jolo marburi nasida, songon na tinonahon ni Jahowa hian tu si Musa.'),(10762,43,40,33,'Dipajongjong muse ma rimberimbe ni alaman i humaliang inganan na badia i dohot langgatan i, jala digantungkon rimberimbe tutup ni bahal ni alaman i. Jadi sun ma sandok ulaon i dipasidung si Musa.'),(10763,43,40,34,'Dung i ro ma ombun i manghungkupi undungundung parluhutan i, jala hamuliaon ni Jahowa manggohi inganan na badia i.'),(10764,43,40,35,'Pola ndang tarbahen si Musa laho bongot tu bagasan undungundung parluhutan i, ai sai mangulon do ombun i di atasna, jala hamuliaon ni Jahowa manggohi inganan na badia i.'),(10765,43,40,36,'Molo tung manaek ombun i sian atas inganan na badia i, borhat ma halak Israel i mardalan, manulus sandok dalannasida i.'),(10766,43,40,37,'Alai molo so manaek ombun i, ndang borhat nasida, sahat ro di ari hananaekna i.'),(10767,43,40,38,'Ai sai di atas ni inganan na badia i ombun ni Jahowa ia arian jala api do di atasna ia bornginna i dipaidaida sandok pinompar ni Israel di sude dalannasida i.');

